INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(0, 1, 1, 'بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيْمِ', 'bismillāhir-raḥmānir-raḥīm'),
(1, 1, 2, 'اَلْحَمْدُ لِلّٰهِ رَبِّ الْعٰلَمِيْنَۙ', 'al-ḥamdu lillāhi rabbil-\'ālamīn'),
(2, 1, 3, 'الرَّحْمٰنِ الرَّحِيْمِۙ', 'ar-raḥmānir-raḥīm'),
(3, 1, 4, 'مٰلِكِ يَوْمِ الدِّيْنِۗ', 'māliki yaumid-dīn'),
(4, 1, 5, 'اِيَّاكَ نَعْبُدُ وَاِيَّاكَ نَسْتَعِيْنُۗ', 'iyyāka na\'budu wa iyyāka nasta\'īn'),
(5, 1, 6, 'اِهْدِنَا الصِّرَاطَ الْمُسْتَقِيْمَ ۙ', 'ihdinaṣ-ṣirāṭal-mustaqīm'),
(6, 1, 7, 'صِرَاطَ الَّذِيْنَ اَنْعَمْتَ عَلَيْهِمْ ەۙ غَيْرِ الْمَغْضُوْبِ عَلَيْهِمْ وَلَا الضَّاۤلِّيْنَ', 'ṣirāṭallażīna an\'amta \'alaihim gairil-magḍụbi \'alaihim wa laḍ-ḍāllīn'),
(7, 2, 1, 'الۤمّۤ ۚ', 'alif lām mīm'),
(8, 2, 2, 'ذٰلِكَ الْكِتٰبُ لَا رَيْبَ ۛ فِيْهِ ۛ هُدًى لِّلْمُتَّقِيْنَۙ', 'żālikal-kitābu lā raiba fīh, hudal lil-muttaqīn'),
(9, 2, 3, 'الَّذِيْنَ يُؤْمِنُوْنَ بِالْغَيْبِ وَيُقِيْمُوْنَ الصَّلٰوةَ وَمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَ ۙ', 'allażīna yu`minụna bil-gaibi wa yuqīmụnaṣ-ṣalāta wa mimmā razaqnāhum yunfiqụn'),
(10, 2, 4, 'وَالَّذِيْنَ يُؤْمِنُوْنَ بِمَآ اُنْزِلَ اِلَيْكَ وَمَآ اُنْزِلَ مِنْ قَبْلِكَ ۚ وَبِالْاٰخِرَةِ هُمْ يُوْقِنُوْنَۗ', 'wallażīna yu`minụna bimā unzila ilaika wa mā unzila ming qablik, wa bil-ākhirati hum yụqinụn'),
(11, 2, 5, 'اُولٰۤىِٕكَ عَلٰى هُدًى مِّنْ رَّبِّهِمْ ۙ وَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ', 'ulā`ika \'alā hudam mir rabbihim wa ulā`ika humul-mufliḥụn'),
(12, 2, 6, 'اِنَّ الَّذِيْنَ كَفَرُوْا سَوَاۤءٌ عَلَيْهِمْ ءَاَنْذَرْتَهُمْ اَمْ لَمْ تُنْذِرْهُمْ لَا يُؤْمِنُوْنَ', 'innallażīna kafarụ sawā`un \'alaihim a anżartahum am lam tunżir-hum lā yu`minụn'),
(13, 2, 7, 'خَتَمَ اللّٰهُ عَلٰى قُلُوْبِهِمْ وَعَلٰى سَمْعِهِمْ ۗ وَعَلٰٓى اَبْصَارِهِمْ غِشَاوَةٌ وَّلَهُمْ عَذَابٌ عَظِيْمٌ', 'khatamallāhu \'alā qulụbihim wa \'alā sam\'ihim, wa \'alā abṣārihim gisyāwatuw wa lahum \'ażābun \'aẓīm'),
(14, 2, 8, 'وَمِنَ النَّاسِ مَنْ يَّقُوْلُ اٰمَنَّا بِاللّٰهِ وَبِالْيَوْمِ الْاٰخِرِ وَمَا هُمْ بِمُؤْمِنِيْنَۘ', 'wa minan-nāsi may yaqụlu āmannā billāhi wa bil-yaumil-ākhiri wa mā hum bimu`minīn'),
(15, 2, 9, 'يُخٰدِعُوْنَ اللّٰهَ وَالَّذِيْنَ اٰمَنُوْا ۚ وَمَا يَخْدَعُوْنَ اِلَّآ اَنْفُسَهُمْ وَمَا يَشْعُرُوْنَۗ', 'yukhādi\'ụnallāha wallażīna āmanụ, wa mā yakhda\'ụna illā anfusahum wa mā yasy\'urụn'),
(16, 2, 10, 'فِيْ قُلُوْبِهِمْ مَّرَضٌۙ فَزَادَهُمُ اللّٰهُ مَرَضًاۚ وَلَهُمْ عَذَابٌ اَلِيْمٌ ۢ ەۙ بِمَا كَانُوْا يَكْذِبُوْنَ', 'fī qulụbihim maraḍun fa zādahumullāhu maraḍā, wa lahum \'ażābun alīmum bimā kānụ yakżibụn'),
(17, 2, 11, 'وَاِذَا قِيْلَ لَهُمْ لَا تُفْسِدُوْا فِى الْاَرْضِۙ قَالُوْٓا اِنَّمَا نَحْنُ مُصْلِحُوْنَ', 'wa iżā qīla lahum lā tufsidụ fil arḍi qālū innamā naḥnu muṣliḥụn'),
(18, 2, 12, 'اَلَآ اِنَّهُمْ هُمُ الْمُفْسِدُوْنَ وَلٰكِنْ لَّا يَشْعُرُوْنَ', 'alā innahum humul-mufsidụna wa lākil lā yasy\'urụn'),
(19, 2, 13, 'وَاِذَا قِيْلَ لَهُمْ اٰمِنُوْا كَمَآ اٰمَنَ النَّاسُ قَالُوْٓا اَنُؤْمِنُ كَمَآ اٰمَنَ السُّفَهَاۤءُ ۗ اَلَآ اِنَّهُمْ هُمُ السُّفَهَاۤءُ وَلٰكِنْ لَّا يَعْلَمُوْنَ', 'wa iżā qīla lahum āminụ kamā āmanan-nāsu qālū a nu`minu kamā āmanas-sufahā`, alā innahum humus-sufahā`u wa lākil lā ya\'lamụn'),
(20, 2, 14, 'وَاِذَا لَقُوا الَّذِيْنَ اٰمَنُوْا قَالُوْٓا اٰمَنَّا ۚ وَاِذَا خَلَوْا اِلٰى شَيٰطِيْنِهِمْ ۙ قَالُوْٓا اِنَّا مَعَكُمْ ۙاِنَّمَا نَحْنُ مُسْتَهْزِءُوْنَ', 'wa iżā laqullażīna āmanụ qālū āmannā, wa iżā khalau ilā syayāṭīnihim qālū innā ma\'akum innamā naḥnu mustahzi`ụn'),
(21, 2, 15, ' اَللّٰهُ يَسْتَهْزِئُ بِهِمْ وَيَمُدُّهُمْ فِيْ طُغْيَانِهِمْ يَعْمَهُوْنَ', 'allāhu yastahzi`u bihim wa yamudduhum fī ṭugyānihim ya\'mahụn'),
(22, 2, 16, 'اُولٰۤىِٕكَ الَّذِيْنَ اشْتَرَوُا الضَّلٰلَةَ بِالْهُدٰىۖ فَمَا رَبِحَتْ تِّجَارَتُهُمْ وَمَا كَانُوْا مُهْتَدِيْنَ', 'ulā`ikallażīnasytarawuḍ-ḍalālata bil-hudā fa mā rabiḥat tijāratuhum wa mā kānụ muhtadīn'),
(23, 2, 17, 'مَثَلُهُمْ كَمَثَلِ الَّذِى اسْتَوْقَدَ نَارًا ۚ فَلَمَّآ اَضَاۤءَتْ مَا حَوْلَهٗ ذَهَبَ اللّٰهُ بِنُوْرِهِمْ وَتَرَكَهُمْ فِيْ ظُلُمٰتٍ لَّا يُبْصِرُوْنَ', 'maṡaluhum kamaṡalillażistauqada nārā, fa lammā aḍā`at mā ḥaulahụ żahaballāhu binụrihim wa tarakahum fī ẓulumātil lā yubṣirụn'),
(24, 2, 18, 'صُمٌّ ۢ بُكْمٌ عُمْيٌ فَهُمْ لَا يَرْجِعُوْنَۙ', 'ṣummum bukmun \'umyun fa hum lā yarji\'ụn'),
(25, 2, 19, 'اَوْ كَصَيِّبٍ مِّنَ السَّمَاۤءِ فِيْهِ ظُلُمٰتٌ وَّرَعْدٌ وَّبَرْقٌۚ يَجْعَلُوْنَ اَصَابِعَهُمْ فِيْٓ اٰذَانِهِمْ مِّنَ الصَّوَاعِقِ حَذَرَ الْمَوْتِۗ وَاللّٰهُ مُحِيْطٌۢ بِالْكٰفِرِيْنَ', 'au kaṣayyibim minas-samā`i fīhi ẓulumātuw wa ra\'duw wa barq, yaj\'alụna aṣābi\'ahum fī āżānihim minaṣ-ṣawā\'iqi ḥażaral-maụt, wallāhu muḥīṭum bil-kāfirīn'),
(26, 2, 20, 'يَكَادُ الْبَرْقُ يَخْطَفُ اَبْصَارَهُمْ ۗ كُلَّمَآ اَضَاۤءَ لَهُمْ مَّشَوْا فِيْهِ ۙ وَاِذَآ اَظْلَمَ عَلَيْهِمْ قَامُوْا ۗوَلَوْ شَاۤءَ اللّٰهُ لَذَهَبَ بِسَمْعِهِمْ وَاَبْصَارِهِمْ ۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'yakādul-barqu yakhṭafu abṣārahum, kullamā aḍā`a lahum masyau fīhi wa iżā aẓlama \'alaihim qāmụ, walau syā`allāhu lażahaba bisam\'ihim wa abṣārihim, innallāha \'alā kulli syai`ing qadīr'),
(27, 2, 21, ' يٰٓاَيُّهَا النَّاسُ اعْبُدُوْا رَبَّكُمُ الَّذِيْ خَلَقَكُمْ وَالَّذِيْنَ مِنْ قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُوْنَۙ', 'yā ayyuhan-nāsu\'budụ rabbakumullażī khalaqakum wallażīna ming qablikum la\'allakum tattaqụn'),
(28, 2, 22, ' الَّذِيْ جَعَلَ لَكُمُ الْاَرْضَ فِرَاشًا وَّالسَّمَاۤءَ بِنَاۤءً ۖوَّاَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً فَاَخْرَجَ بِهٖ مِنَ الثَّمَرٰتِ رِزْقًا لَّكُمْ ۚ فَلَا تَجْعَلُوْا لِلّٰهِ اَنْدَادًا وَّاَنْتُمْ تَعْلَمُوْنَ', 'allażī ja\'ala lakumul-arḍa firāsyaw was-samā`a binā`aw wa anzala minas-samā`i mā`an fa akhraja bihī minaṡ-ṡamarāti rizqal lakum, fa lā taj\'alụ lillāhi andādaw wa antum ta\'lamụn'),
(29, 2, 23, 'وَاِنْ كُنْتُمْ فِيْ رَيْبٍ مِّمَّا نَزَّلْنَا عَلٰى عَبْدِنَا فَأْتُوْا بِسُوْرَةٍ مِّنْ مِّثْلِهٖ ۖ وَادْعُوْا شُهَدَاۤءَكُمْ مِّنْ دُوْنِ اللّٰهِ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'wa ing kuntum fī raibim mimmā nazzalnā \'alā \'abdinā fa`tụ bisụratim mim miṡlihī wad\'ụ syuhadā`akum min dụnillāhi ing kuntum ṣādiqīn'),
(30, 2, 24, 'فَاِنْ لَّمْ تَفْعَلُوْا وَلَنْ تَفْعَلُوْا فَاتَّقُوا النَّارَ الَّتِيْ وَقُوْدُهَا النَّاسُ وَالْحِجَارَةُ ۖ اُعِدَّتْ لِلْكٰفِرِيْنَ', 'fa il lam taf\'alụ wa lan taf\'alụ fattaqun-nārallatī waqụduhan-nāsu wal-ḥijāratu u\'iddat lil-kāfirīn'),
(31, 2, 25, 'وَبَشِّرِ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ اَنَّ لَهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ ۗ كُلَّمَا رُزِقُوْا مِنْهَا مِنْ ثَمَرَةٍ رِّزْقًا ۙ قَالُوْا هٰذَا الَّذِيْ رُزِقْنَا مِنْ قَبْلُ وَاُتُوْا بِهٖ مُتَشَابِهًا ۗوَلَهُمْ فِيْهَآ اَزْوَاجٌ مُّطَهَّرَةٌ وَّهُمْ فِيْهَا خٰلِدُوْنَ', 'wa basysyirillażīna āmanụ wa \'amiluṣ-ṣāliḥāti anna lahum jannātin tajrī min taḥtihal-an-hār, kullamā ruziqụ min-hā min ṡamaratir rizqang qālụ hāżallażī ruziqnā ming qablu wa utụ bihī mutasyābihā, wa lahum fīhā azwājum muṭahharatuw wa hum fīhā khālidụn'),
(32, 2, 26, '۞ اِنَّ اللّٰهَ لَا يَسْتَحْيٖٓ اَنْ يَّضْرِبَ مَثَلًا مَّا بَعُوْضَةً فَمَا فَوْقَهَا ۗ فَاَمَّا الَّذِيْنَ اٰمَنُوْا فَيَعْلَمُوْنَ اَنَّهُ الْحَقُّ مِنْ رَّبِّهِمْ ۚ وَاَمَّا الَّذِيْنَ كَفَرُوْا فَيَقُوْلُوْنَ مَاذَآ اَرَادَ اللّٰهُ بِهٰذَا مَثَلًا ۘ يُضِلُّ بِهٖ كَثِيْرًا وَّيَهْدِيْ بِهٖ كَثِيْرًا ۗ وَمَا يُضِلُّ بِهٖٓ اِلَّا الْفٰسِقِيْنَۙ', 'innallāha lā yastaḥyī ay yaḍriba maṡalam mā ba\'ụḍatan fa mā fauqahā, fa ammallażīna āmanụ fa ya\'lamụna annahul-ḥaqqu mir rabbihim, wa ammallażīna kafarụ fa yaqụlụna māżā arādallāhu bihāżā maṡalā, yuḍillu bihī kaṡīraw wa yahdī bihī kaṡīrā, wa mā yuḍillu bihī illal-fāsiqīn'),
(33, 2, 27, 'الَّذِيْنَ يَنْقُضُوْنَ عَهْدَ اللّٰهِ مِنْۢ بَعْدِ مِيْثَاقِهٖۖ وَيَقْطَعُوْنَ مَآ اَمَرَ اللّٰهُ بِهٖٓ اَنْ يُّوْصَلَ وَيُفْسِدُوْنَ فِى الْاَرْضِۗ اُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ', 'allażīna yangquḍụna \'ahdallāhi mim ba\'di mīṡāqihī wa yaqṭa\'ụna mā amarallāhu bihī ay yụṣala wa yufsidụna fil-arḍ, ulā`ika humul-khāsirụn'),
(34, 2, 28, 'كَيْفَ تَكْفُرُوْنَ بِاللّٰهِ وَكُنْتُمْ اَمْوَاتًا فَاَحْيَاكُمْۚ ثُمَّ يُمِيْتُكُمْ ثُمَّ يُحْيِيْكُمْ ثُمَّ اِلَيْهِ تُرْجَعُوْنَ', 'kaifa takfurụna billāhi wa kuntum amwātan fa aḥyākum, ṡumma yumītukum ṡumma yuḥyīkum ṡumma ilaihi turja\'ụn'),
(35, 2, 29, 'هُوَ الَّذِيْ خَلَقَ لَكُمْ مَّا فِى الْاَرْضِ جَمِيْعًا ثُمَّ اسْتَوٰٓى اِلَى السَّمَاۤءِ فَسَوّٰىهُنَّ سَبْعَ سَمٰوٰتٍ ۗ وَهُوَ بِكُلِّ شَيْءٍ عَلِيْمٌ', 'huwallażī khalaqa lakum mā fil-arḍi jamī\'an ṡummastawā ilas-samā`i fa sawwāhunna sab\'a samāwāt, wa huwa bikulli syai`in \'alīm'),
(36, 2, 30, 'وَاِذْ قَالَ رَبُّكَ لِلْمَلٰۤىِٕكَةِ ِانِّيْ جَاعِلٌ فِى الْاَرْضِ خَلِيْفَةً ۗ قَالُوْٓا اَتَجْعَلُ فِيْهَا مَنْ يُّفْسِدُ فِيْهَا وَيَسْفِكُ الدِّمَاۤءَۚ وَنَحْنُ نُسَبِّحُ بِحَمْدِكَ وَنُقَدِّسُ لَكَ ۗ قَالَ اِنِّيْٓ اَعْلَمُ مَا لَا تَعْلَمُوْنَ', 'wa iż qāla rabbuka lil-malā`ikati innī jā\'ilun fil-arḍi khalīfah, qālū a taj\'alu fīhā may yufsidu fīhā wa yasfikud-dimā`, wa naḥnu nusabbiḥu biḥamdika wa nuqaddisu lak, qāla innī a\'lamu mā lā ta\'lamụn'),
(37, 2, 31, 'وَعَلَّمَ اٰدَمَ الْاَسْمَاۤءَ كُلَّهَا ثُمَّ عَرَضَهُمْ عَلَى الْمَلٰۤىِٕكَةِ فَقَالَ اَنْۢبِـُٔوْنِيْ بِاَسْمَاۤءِ هٰٓؤُلَاۤءِ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'wa \'allama ādamal-asmā`a kullahā ṡumma \'araḍahum \'alal-malā`ikati fa qāla ambi`ụnī bi`asmā`i hā`ulā`i ing kuntum ṣādiqīn'),
(38, 2, 32, 'قَالُوْا سُبْحٰنَكَ لَا عِلْمَ لَنَآ اِلَّا مَا عَلَّمْتَنَا ۗاِنَّكَ اَنْتَ الْعَلِيْمُ الْحَكِيْمُ', 'qālụ sub-ḥānaka lā \'ilma lanā illā mā \'allamtanā, innaka antal-\'alīmul-ḥakīm'),
(39, 2, 33, 'قَالَ يٰٓاٰدَمُ اَنْۢبِئْهُمْ بِاَسْمَاۤىِٕهِمْ ۚ فَلَمَّآ اَنْۢبَاَهُمْ بِاَسْمَاۤىِٕهِمْۙ قَالَ اَلَمْ اَقُلْ لَّكُمْ اِنِّيْٓ اَعْلَمُ غَيْبَ السَّمٰوٰتِ وَالْاَرْضِۙ وَاَعْلَمُ مَا تُبْدُوْنَ وَمَا كُنْتُمْ تَكْتُمُوْنَ', 'qāla yā ādamu ambi`hum bi`asmā`ihim, fa lammā amba`ahum bi`asmā`ihim qāla a lam aqul lakum innī a\'lamu gaibas-samāwāti wal-arḍi wa a\'lamu mā tubdụna wa mā kuntum taktumụn'),
(40, 2, 34, 'وَاِذْ قُلْنَا لِلْمَلٰۤىِٕكَةِ اسْجُدُوْا لِاٰدَمَ فَسَجَدُوْٓا اِلَّآ اِبْلِيْسَۗ اَبٰى وَاسْتَكْبَرَۖ وَكَانَ مِنَ الْكٰفِرِيْنَ', 'wa iż qulnā lil-malā`ikatisjudụ li`ādama fa sajadū illā iblīs, abā wastakbara wa kāna minal-kāfirīn'),
(41, 2, 35, 'وَقُلْنَا يٰٓاٰدَمُ اسْكُنْ اَنْتَ وَزَوْجُكَ الْجَنَّةَ وَكُلَا مِنْهَا رَغَدًا حَيْثُ شِئْتُمَاۖ وَلَا تَقْرَبَا هٰذِهِ الشَّجَرَةَ فَتَكُوْنَا مِنَ الظّٰلِمِيْنَ', 'wa qulnā yā ādamuskun anta wa zaujukal-jannata wa kulā min-hā ragadan ḥaiṡu syi`tumā wa lā taqrabā hāżihisy-syajarata fa takụnā minaẓ-ẓālimīn'),
(42, 2, 36, 'فَاَزَلَّهُمَا الشَّيْطٰنُ عَنْهَا فَاَخْرَجَهُمَا مِمَّا كَانَا فِيْهِ ۖ وَقُلْنَا اهْبِطُوْا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ ۚ وَلَكُمْ فِى الْاَرْضِ مُسْتَقَرٌّ وَّمَتَاعٌ اِلٰى حِيْنٍ', 'fa azallahumasy-syaiṭānu \'an-hā fa akhrajahumā mimmā kānā fīhi wa qulnahbiṭụ ba\'ḍukum liba\'ḍin \'aduww, wa lakum fil-arḍi mustaqarruw wa matā\'un ilā ḥīn'),
(43, 2, 37, 'فَتَلَقّٰٓى اٰدَمُ مِنْ رَّبِّهٖ كَلِمٰتٍ فَتَابَ عَلَيْهِ ۗ اِنَّهٗ هُوَ التَّوَّابُ الرَّحِيْمُ', 'fa talaqqā ādamu mir rabbihī kalimātin fa tāba \'alaīh, innahụ huwat-tawwābur-raḥīm'),
(44, 2, 38, 'قُلْنَا اهْبِطُوْا مِنْهَا جَمِيْعًا ۚ فَاِمَّا يَأْتِيَنَّكُمْ مِّنِّيْ هُدًى فَمَنْ تَبِعَ هُدَايَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ', 'qulnahbiṭụ min-hā jamī\'ā, fa immā ya`tiyannakum minnī hudan fa man tabi\'a hudāya fa lā khaufun \'alaihim wa lā hum yaḥzanụn'),
(45, 2, 39, 'وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَآ اُولٰۤىِٕكَ اَصْحٰبُ النَّارِ ۚ هُمْ فِيْهَا خٰلِدُوْنَ', 'wallażīna kafarụ wa każżabụ bi`āyātinā ulā`ika aṣ-ḥābun-nār, hum fīhā khālidụn'),
(46, 2, 40, 'يٰبَنِيْٓ اِسْرَاۤءِيْلَ اذْكُرُوْا نِعْمَتِيَ الَّتِيْٓ اَنْعَمْتُ عَلَيْكُمْ وَاَوْفُوْا بِعَهْدِيْٓ اُوْفِ بِعَهْدِكُمْۚ وَاِيَّايَ فَارْهَبُوْنِ', 'yā banī isrā`īlażkurụ ni\'matiyallatī an\'amtu \'alaikum wa aufụ bi\'ahdī ụfi bi\'ahdikum, wa iyyāya far-habụn'),
(47, 2, 41, 'وَاٰمِنُوْا بِمَآ اَنْزَلْتُ مُصَدِّقًا لِّمَا مَعَكُمْ وَلَا تَكُوْنُوْٓا اَوَّلَ كَافِرٍۢ بِهٖ ۖ وَلَا تَشْتَرُوْا بِاٰيٰتِيْ ثَمَنًا قَلِيْلًا ۖوَّاِيَّايَ فَاتَّقُوْنِ', 'wa āminụ bimā anzaltu muṣaddiqal limā ma\'akum wa lā takụnū awwala kāfirim bihī wa lā tasytarụ bi`āyātī ṡamanang qalīlaw wa iyyāya fattaqụn'),
(48, 2, 42, 'وَلَا تَلْبِسُوا الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُوا الْحَقَّ وَاَنْتُمْ تَعْلَمُوْنَ', 'wa lā talbisul-ḥaqqa bil-bāṭili wa taktumul-ḥaqqa wa antum ta\'lamụn'),
(49, 2, 43, 'وَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَارْكَعُوْا مَعَ الرَّاكِعِيْنَ', 'wa aqīmuṣ-ṣalāta wa ātuz-zakāta warka\'ụ ma\'ar-rāki\'īn'),
(50, 2, 44, '۞ اَتَأْمُرُوْنَ النَّاسَ بِالْبِرِّ وَتَنْسَوْنَ اَنْفُسَكُمْ وَاَنْتُمْ تَتْلُوْنَ الْكِتٰبَ ۗ اَفَلَا تَعْقِلُوْنَ', 'a ta`murụnan-nāsa bil-birri wa tansauna anfusakum wa antum tatlụnal-kitāb, a fa lā ta\'qilụn'),
(51, 2, 45, 'وَاسْتَعِيْنُوْا بِالصَّبْرِ وَالصَّلٰوةِ ۗ وَاِنَّهَا لَكَبِيْرَةٌ اِلَّا عَلَى الْخٰشِعِيْنَۙ', 'wasta\'īnụ biṣ-ṣabri waṣ-ṣalāh, wa innahā lakabīratun illā \'alal-khāsyi\'īn'),
(52, 2, 46, 'الَّذِيْنَ يَظُنُّوْنَ اَنَّهُمْ مُّلٰقُوْا رَبِّهِمْ وَاَنَّهُمْ اِلَيْهِ رٰجِعُوْنَ', 'allażīna yaẓunnụna annahum mulāqụ rabbihim wa annahum ilaihi rāji\'ụn'),
(53, 2, 47, 'يٰبَنِيْٓ اِسْرَاۤءِيْلَ اذْكُرُوْا نِعْمَتِيَ الَّتِيْٓ اَنْعَمْتُ عَلَيْكُمْ وَاَنِّيْ فَضَّلْتُكُمْ عَلَى الْعٰلَمِيْنَ', 'yā banī isrā`īlażkurụ ni\'matiyallatī an\'amtu \'alaikum wa annī faḍḍaltukum \'alal-\'ālamīn'),
(54, 2, 48, 'وَاتَّقُوْا يَوْمًا لَّا تَجْزِيْ نَفْسٌ عَنْ نَّفْسٍ شَيْـًٔا وَّلَا يُقْبَلُ مِنْهَا شَفَاعَةٌ وَّلَا يُؤْخَذُ مِنْهَا عَدْلٌ وَّلَا هُمْ يُنْصَرُوْنَ', 'wattaqụ yaumal lā tajzī nafsun \'an nafsin syai`aw wa lā yuqbalu min-hā syafā\'atuw wa lā yu`khażu min-hā \'adluw wa lā hum yunṣarụn'),
(55, 2, 49, 'وَاِذْ نَجَّيْنٰكُمْ مِّنْ اٰلِ فِرْعَوْنَ يَسُوْمُوْنَكُمْ سُوْۤءَ الْعَذَابِ يُذَبِّحُوْنَ اَبْنَاۤءَكُمْ وَيَسْتَحْيُوْنَ نِسَاۤءَكُمْ ۗ وَفِيْ ذٰلِكُمْ بَلَاۤءٌ مِّنْ رَّبِّكُمْ عَظِيْمٌ', 'wa iż najjainākum min āli fir\'auna yasụmụnakum sū`al-\'ażābi yużabbiḥụna abnā`akum wa yastaḥyụna nisā`akum, wa fī żālikum balā`um mir rabbikum \'aẓīm'),
(56, 2, 50, 'وَاِذْ فَرَقْنَا بِكُمُ الْبَحْرَ فَاَنْجَيْنٰكُمْ وَاَغْرَقْنَآ اٰلَ فِرْعَوْنَ وَاَنْتُمْ تَنْظُرُوْنَ', 'wa iż faraqnā bikumul-baḥra fa anjainākum wa agraqnā āla fir\'auna wa antum tanẓurụn'),
(57, 2, 51, 'وَاِذْ وٰعَدْنَا مُوْسٰىٓ اَرْبَعِيْنَ لَيْلَةً ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِنْۢ بَعْدِهٖ وَاَنْتُمْ ظٰلِمُوْنَ', 'wa iż wā\'adnā mụsā arba\'īna lailatan ṡummattakhażtumul-\'ijla mim ba\'dihī wa antum ẓālimụn'),
(58, 2, 52, 'ثُمَّ عَفَوْنَا عَنْكُمْ مِّنْۢ بَعْدِ ذٰلِكَ لَعَلَّكُمْ تَشْكُرُوْنَ', 'ṡumma \'afaunā \'angkum mim ba\'di żālika la\'allakum tasykurụn'),
(59, 2, 53, 'وَاِذْ اٰتَيْنَا مُوْسَى الْكِتٰبَ وَالْفُرْقَانَ لَعَلَّكُمْ تَهْتَدُوْنَ', 'wa iż ātainā mụsal-kitāba wal-furqāna la\'allakum tahtadụn'),
(60, 2, 54, 'وَاِذْ قَالَ مُوْسٰى لِقَوْمِهٖ يٰقَوْمِ اِنَّكُمْ ظَلَمْتُمْ اَنْفُسَكُمْ بِاتِّخَاذِكُمُ الْعِجْلَ فَتُوْبُوْٓا اِلٰى بَارِىِٕكُمْ فَاقْتُلُوْٓا اَنْفُسَكُمْۗ ذٰلِكُمْ خَيْرٌ لَّكُمْ عِنْدَ بَارِىِٕكُمْۗ فَتَابَ عَلَيْكُمْ ۗ اِنَّهٗ هُوَ التَّوَّابُ الرَّحِيْمُ', 'wa iż qāla mụsā liqaumihī yā qaumi innakum ẓalamtum anfusakum bittikhāżikumul-\'ijla fa tụbū ilā bāri`ikum faqtulū anfusakum, żālikum khairul lakum \'inda bāri`ikum, fa tāba \'alaikum, innahụ huwat-tawwābur-raḥīm'),
(61, 2, 55, 'وَاِذْ قُلْتُمْ يٰمُوْسٰى لَنْ نُّؤْمِنَ لَكَ حَتّٰى نَرَى اللّٰهَ جَهْرَةً فَاَخَذَتْكُمُ الصّٰعِقَةُ وَاَنْتُمْ تَنْظُرُوْنَ', 'wa iż qultum yā mụsā lan nu`mina laka ḥattā narallāha jahratan fa akhażatkumuṣ-ṣā\'iqatu wa antum tanẓurụn'),
(62, 2, 56, 'ثُمَّ بَعَثْنٰكُمْ مِّنْۢ بَعْدِ مَوْتِكُمْ لَعَلَّكُمْ تَشْكُرُوْنَ', 'ṡumma ba\'aṡnākum mim ba\'di mautikum la\'allakum tasykurụn'),
(63, 2, 57, 'وَظَلَّلْنَا عَلَيْكُمُ الْغَمَامَ وَاَنْزَلْنَا عَلَيْكُمُ الْمَنَّ وَالسَّلْوٰى ۗ كُلُوْا مِنْ طَيِّبٰتِ مَا رَزَقْنٰكُمْ ۗ وَمَا ظَلَمُوْنَا وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَ', 'wa ẓallalnā \'alaikumul-gamāma wa anzalnā \'alaikumul-manna was-salwā, kulụ min ṭayyibāti mā razaqnākum, wa mā ẓalamụnā wa lāking kānū anfusahum yaẓlimụn'),
(64, 2, 58, 'وَاِذْ قُلْنَا ادْخُلُوْا هٰذِهِ الْقَرْيَةَ فَكُلُوْا مِنْهَا حَيْثُ شِئْتُمْ رَغَدًا وَّادْخُلُوا الْبَابَ سُجَّدًا وَّقُوْلُوْا حِطَّةٌ نَّغْفِرْ لَكُمْ خَطٰيٰكُمْ ۗ وَسَنَزِيْدُ الْمُحْسِنِيْنَ', 'wa iż qulnadkhulụ hāżihil-qaryata fa kulụ min-hā ḥaiṡu syi`tum ragadaw wadkhulul-bāba sujjadaw wa qụlụ ḥiṭṭatun nagfir lakum khaṭāyākum, wa sanazīdul-muḥsinīn'),
(65, 2, 59, 'فَبَدَّلَ الَّذِيْنَ ظَلَمُوْا قَوْلًا غَيْرَ الَّذِيْ قِيْلَ لَهُمْ فَاَنْزَلْنَا عَلَى الَّذِيْنَ ظَلَمُوْا رِجْزًا مِّنَ السَّمَاۤءِ بِمَا كَانُوْا يَفْسُقُوْنَ', 'fa baddalallażīna ẓalamụ qaulan gairallażī qīla lahum fa anzalnā \'alallażīna ẓalamụ rijzam minas-samā`i bimā kānụ yafsuqụn'),
(66, 2, 60, '۞ وَاِذِ اسْتَسْقٰى مُوْسٰى لِقَوْمِهٖ فَقُلْنَا اضْرِبْ بِّعَصَاكَ الْحَجَرَۗ فَانْفَجَرَتْ مِنْهُ اثْنَتَا عَشْرَةَ عَيْنًا ۗ قَدْ عَلِمَ كُلُّ اُنَاسٍ مَّشْرَبَهُمْ ۗ كُلُوْا وَاشْرَبُوْا مِنْ رِّزْقِ اللّٰهِ وَلَا تَعْثَوْا فِى الْاَرْضِ مُفْسِدِيْنَ', 'wa iżistasqā mụsā liqaumihī fa qulnaḍrib bi\'aṣākal-ḥajar, fanfajarat min-huṡnatā \'asyrata \'ainā, qad \'alima kullu unāsim masyrabahum, kulụ wasyrabụ mir rizqillāhi wa lā ta\'ṡau fil-arḍi mufsidīn'),
(67, 2, 61, 'وَاِذْ قُلْتُمْ يٰمُوْسٰى لَنْ نَّصْبِرَ عَلٰى طَعَامٍ وَّاحِدٍ فَادْعُ لَنَا رَبَّكَ يُخْرِجْ لَنَا مِمَّا تُنْۢبِتُ الْاَرْضُ مِنْۢ بَقْلِهَا وَقِثَّاۤىِٕهَا وَفُوْمِهَا وَعَدَسِهَا وَبَصَلِهَا ۗ قَالَ اَتَسْتَبْدِلُوْنَ الَّذِيْ هُوَ اَدْنٰى بِالَّذِيْ هُوَ خَيْرٌ ۗ اِهْبِطُوْا مِصْرًا فَاِنَّ لَكُمْ مَّا سَاَلْتُمْ ۗ وَضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ وَالْمَسْكَنَةُ وَبَاۤءُوْ بِغَضَبٍ مِّنَ اللّٰهِ ۗ ذٰلِكَ بِاَنَّهُمْ كَانُوْا يَكْفُرُوْنَ بِاٰيٰتِ اللّٰهِ وَيَقْتُلُوْنَ النَّبِيّٖنَ بِغَيْرِ الْحَقِّ ۗ ذٰلِكَ بِمَا عَصَوْا وَّكَانُوْا يَعْتَدُوْنَ', 'wa iż qultum yā mụsā lan naṣbira \'alā ṭa\'āmiw wāḥidin fad\'u lanā rabbaka yukhrij lanā mimmā tumbitul-arḍu mim baqlihā wa qiṡṡā`ihā wa fụmihā wa \'adasihā wa baṣalihā, qāla a tastabdilụnallażī huwa adnā billażī huwa khaīr, ihbiṭụ miṣran fa inna lakum mā sa`altum, wa ḍuribat \'alaihimuż-żillatu wal-maskanatu wa bā`ụ bigaḍabim minallāh, żālika bi`annahum kānụ yakfurụna bi`āyātillāhi wa yaqtulụnan-nabiyyīna bigairil-ḥaqq, żālika bimā \'aṣaw wa kānụ ya\'tadụn'),
(68, 2, 62, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَالَّذِيْنَ هَادُوْا وَالنَّصٰرٰى وَالصَّابِــِٕيْنَ مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَعَمِلَ صَالِحًا فَلَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ', 'innallażīna āmanụ wallażīna hādụ wan-naṣārā waṣ-ṣābi`īna man āmana billāhi wal-yaumil-ākhiri wa \'amila ṣāliḥan fa lahum ajruhum \'inda rabbihim, wa lā khaufun \'alaihim wa lā hum yaḥzanụn'),
(69, 2, 63, 'وَاِذْ اَخَذْنَا مِيْثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّوْرَۗ خُذُوْا مَآ اٰتَيْنٰكُمْ بِقُوَّةٍ وَّاذْكُرُوْا مَا فِيْهِ لَعَلَّكُمْ تَتَّقُوْنَ', 'wa iż akhażnā mīṡāqakum wa rafa\'nā fauqakumuṭ-ṭụr, khużụ mā ātainākum biquwwatiw ważkurụ mā fīhi la\'allakum tattaqụn'),
(70, 2, 64, 'ثُمَّ تَوَلَّيْتُمْ مِّنْۢ بَعْدِ ذٰلِكَ فَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ لَكُنْتُمْ مِّنَ الْخٰسِرِيْنَ', 'ṡumma tawallaitum mim ba\'di żālika falau lā faḍlullāhi \'alaikum wa raḥmatuhụ lakuntum minal-khāsirīn'),
(71, 2, 65, 'وَلَقَدْ عَلِمْتُمُ الَّذِيْنَ اعْتَدَوْا مِنْكُمْ فِى السَّبْتِ فَقُلْنَا لَهُمْ كُوْنُوْا قِرَدَةً خَاسِـِٕيْنَ', 'wa laqad \'alimtumullażīna\'tadau mingkum fis-sabti fa qulnā lahum kụnụ qiradatan khāsi`īn'),
(72, 2, 66, ' فَجَعَلْنٰهَا نَكَالًا لِّمَا بَيْنَ يَدَيْهَا وَمَا خَلْفَهَا وَمَوْعِظَةً لِّلْمُتَّقِيْنَ', 'fa ja\'alnāhā nakālal limā baina yadaihā wa mā khalfahā wa mau\'iẓatal lil-muttaqīn'),
(73, 2, 67, 'وَاِذْ قَالَ مُوْسٰى لِقَوْمِهٖٓ اِنَّ اللّٰهَ يَأْمُرُكُمْ اَنْ تَذْبَحُوْا بَقَرَةً ۗ قَالُوْٓا اَتَتَّخِذُنَا هُزُوًا ۗ قَالَ اَعُوْذُ بِاللّٰهِ اَنْ اَكُوْنَ مِنَ الْجٰهِلِيْنَ', 'wa iż qāla mụsā liqaumihī innallāha ya`murukum an tażbaḥụ baqarah, qālū a tattakhiżunā huzuwā, qāla a\'ụżu billāhi an akụna minal-jāhilīn'),
(74, 2, 68, 'قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَّنَا مَا هِيَ ۗ قَالَ اِنَّهٗ يَقُوْلُ اِنَّهَا بَقَرَةٌ لَّا فَارِضٌ وَّلَا بِكْرٌۗ عَوَانٌۢ بَيْنَ ذٰلِكَ ۗ فَافْعَلُوْا مَا تُؤْمَرُوْنَ', 'qālud\'u lanā rabbaka yubayyil lanā mā hiy, qāla innahụ yaqụlu innahā baqaratul lā fāriḍuw wa lā bikr, \'awānum baina żālik, faf\'alụ mā tu`marụn'),
(75, 2, 69, 'قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَّنَا مَا لَوْنُهَا ۗ قَالَ اِنَّهٗ يَقُوْلُ اِنَّهَا بَقَرَةٌ صَفْرَاۤءُ فَاقِعٌ لَّوْنُهَا تَسُرُّ النّٰظِرِيْنَ', 'qālud\'u lanā rabbaka yubayyil lanā mā launuhā, qāla innahụ yaqụlu innahā baqaratun ṣafrā`u fāqi\'ul launuhā tasurrun-nāẓirīn'),
(76, 2, 70, 'قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَّنَا مَا هِيَۙ اِنَّ الْبَقَرَ تَشٰبَهَ عَلَيْنَاۗ وَاِنَّآ اِنْ شَاۤءَ اللّٰهُ لَمُهْتَدُوْنَ', 'qālud\'u lanā rabbaka yubayyil lanā mā hiya innal-baqara tasyābaha \'alainā, wa innā in syā`allāhu lamuhtadụn'),
(77, 2, 71, 'قَالَ اِنَّهٗ يَقُوْلُ اِنَّهَا بَقَرَةٌ لَّا ذَلُوْلٌ تُثِيْرُ الْاَرْضَ وَلَا تَسْقِى الْحَرْثَۚ مُسَلَّمَةٌ لَّاشِيَةَ فِيْهَا ۗ قَالُوا الْـٰٔنَ جِئْتَ بِالْحَقِّ فَذَبَحُوْهَا وَمَا كَادُوْا يَفْعَلُوْنَ', 'qāla innahụ yaqụlu innahā baqaratul lā żalụlun tuṡīrul-arḍa wa lā tasqil-ḥarṡ, musallamatul lā syiyata fīhā, qālul-āna ji`ta bil-ḥaqqi fa żabaḥụhā wa mā kādụ yaf\'alụn'),
(78, 2, 72, 'وَاِذْ قَتَلْتُمْ نَفْسًا فَادّٰرَءْتُمْ فِيْهَا ۗ وَاللّٰهُ مُخْرِجٌ مَّا كُنْتُمْ تَكْتُمُوْنَ ۚ', 'wa iż qataltum nafsan faddāra\'tum fīhā, wallāhu mukhrijum mā kuntum taktumụn'),
(79, 2, 73, 'فَقُلْنَا اضْرِبُوْهُ بِبَعْضِهَاۗ كَذٰلِكَ يُحْيِ اللّٰهُ الْمَوْتٰى وَيُرِيْكُمْ اٰيٰتِهٖ لَعَلَّكُمْ تَعْقِلُوْنَ', 'fa qulnaḍribụhu biba\'ḍihā, każālika yuḥyillāhul-mautā wa yurīkum āyātihī la\'allakum ta\'qilụn'),
(80, 2, 74, 'ثُمَّ قَسَتْ قُلُوْبُكُمْ مِّنْۢ بَعْدِ ذٰلِكَ فَهِيَ كَالْحِجَارَةِ اَوْ اَشَدُّ قَسْوَةً ۗ وَاِنَّ مِنَ الْحِجَارَةِ لَمَا يَتَفَجَّرُ مِنْهُ الْاَنْهٰرُ ۗ وَاِنَّ مِنْهَا لَمَا يَشَّقَّقُ فَيَخْرُجُ مِنْهُ الْمَاۤءُ ۗوَاِنَّ مِنْهَا لَمَا يَهْبِطُ مِنْ خَشْيَةِ اللّٰهِ ۗوَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ', 'ṡumma qasat qulụbukum mim ba\'di żālika fa hiya kal-ḥijārati au asyaddu qaswah, wa inna minal-ḥijārati lamā yatafajjaru min-hul-an-hār, wa inna min-hā lamā yasysyaqqaqu fa yakhruju min-hul-mā`, wa inna min-hā lamā yahbiṭu min khasy-yatillāh, wa mallāhu bigāfilin \'ammā ta\'malụn'),
(81, 2, 75, '۞ اَفَتَطْمَعُوْنَ اَنْ يُّؤْمِنُوْا لَكُمْ وَقَدْ كَانَ فَرِيْقٌ مِّنْهُمْ يَسْمَعُوْنَ كَلَامَ اللّٰهِ ثُمَّ يُحَرِّفُوْنَهٗ مِنْۢ بَعْدِ مَا عَقَلُوْهُ وَهُمْ يَعْلَمُوْنَ', 'a fa taṭma\'ụna ay yu`minụ lakum wa qad kāna farīqum min-hum yasma\'ụna kalāmallāhi ṡumma yuḥarrifụnahụ mim ba\'di mā \'aqalụhu wa hum ya\'lamụn'),
(82, 2, 76, 'وَاِذَا لَقُوا الَّذِيْنَ اٰمَنُوْا قَالُوْٓا اٰمَنَّاۚ وَاِذَا خَلَا بَعْضُهُمْ اِلٰى بَعْضٍ قَالُوْٓا اَتُحَدِّثُوْنَهُمْ بِمَا فَتَحَ اللّٰهُ عَلَيْكُمْ لِيُحَاۤجُّوْكُمْ بِهٖ عِنْدَ رَبِّكُمْ ۗ اَفَلَا تَعْقِلُوْنَ', 'wa iżā laqullażīna āmanụ qālū āmannā, wa iżā khalā ba\'ḍuhum ilā ba\'ḍing qālū a tuḥaddiṡụnahum bimā fataḥallāhu \'alaikum liyuḥājjụkum bihī \'inda rabbikum, a fa lā ta\'qilụn'),
(83, 2, 77, 'اَوَلَا يَعْلَمُوْنَ اَنَّ اللّٰهَ يَعْلَمُ مَا يُسِرُّوْنَ وَمَا يُعْلِنُوْنَ', 'a wa lā ya\'lamụna annallāha ya\'lamu mā yusirrụna wa mā yu\'linụn'),
(84, 2, 78, ' وَمِنْهُمْ اُمِّيُّوْنَ لَا يَعْلَمُوْنَ الْكِتٰبَ اِلَّآ اَمَانِيَّ وَاِنْ هُمْ اِلَّا يَظُنُّوْنَ', 'wa min-hum ummiyyụna lā ya\'lamụnal-kitāba illā amāniyya wa in hum illā yaẓunnụn'),
(85, 2, 79, 'فَوَيْلٌ لِّلَّذِيْنَ يَكْتُبُوْنَ الْكِتٰبَ بِاَيْدِيْهِمْ ثُمَّ يَقُوْلُوْنَ هٰذَا مِنْ عِنْدِ اللّٰهِ لِيَشْتَرُوْا بِهٖ ثَمَنًا قَلِيْلًا ۗفَوَيْلٌ لَّهُمْ مِّمَّا كَتَبَتْ اَيْدِيْهِمْ وَوَيْلٌ لَّهُمْ مِّمَّا يَكْسِبُوْنَ', 'fa wailul lillażīna yaktubụnal-kitāba bi`aidīhim ṡumma yaqụlụna hāżā min \'indillāhi liyasytarụ bihī ṡamanang qalīlā, fa wailul lahum mimmā katabat aidīhim wa wailul lahum mimmā yaksibụn'),
(86, 2, 80, 'وَقَالُوْا لَنْ تَمَسَّنَا النَّارُ اِلَّآ اَيَّامًا مَّعْدُوْدَةً ۗ قُلْ اَتَّخَذْتُمْ عِنْدَ اللّٰهِ عَهْدًا فَلَنْ يُّخْلِفَ اللّٰهُ عَهْدَهٗٓ اَمْ تَقُوْلُوْنَ عَلَى اللّٰهِ مَا لَا تَعْلَمُوْنَ', 'wa qālụ lan tamassanan-nāru illā ayyāmam ma\'dụdah, qul attakhażtum \'indallāhi \'ahdan fa lay yukhlifallāhu \'ahdahū am taqụlụna \'alallāhi mā lā ta\'lamụn'),
(87, 2, 81, 'بَلٰى مَنْ كَسَبَ سَيِّئَةً وَّاَحَاطَتْ بِهٖ خَطِيْۤـَٔتُهٗ فَاُولٰۤىِٕكَ اَصْحٰبُ النَّارِ ۚ هُمْ فِيْهَا خٰلِدُوْنَ', 'balā mang kasaba sayyi`ataw wa `aḥāṭat bihī khaṭī`atuhụ fa ulā`ika aṣ-ḥābun-nār, hum fīhā khālidụn'),
(88, 2, 82, 'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ اُولٰۤىِٕكَ اَصْحٰبُ الْجَنَّةِ ۚ هُمْ فِيْهَا خٰلِدُوْنَ', 'wallażīna āmanụ wa \'amiluṣ-ṣāliḥāti ulā`ika aṣ-ḥābul-jannah, hum fīhā khālidụn'),
(89, 2, 83, 'وَاِذْ اَخَذْنَا مِيْثَاقَ بَنِيْٓ اِسْرَاۤءِيْلَ لَا تَعْبُدُوْنَ اِلَّا اللّٰهَ وَبِالْوَالِدَيْنِ اِحْسَانًا وَّذِى الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنِ وَقُوْلُوْا لِلنَّاسِ حُسْنًا وَّاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَۗ ثُمَّ تَوَلَّيْتُمْ اِلَّا قَلِيْلًا مِّنْكُمْ وَاَنْتُمْ مُّعْرِضُوْنَ', 'wa iż akhażnā mīṡāqa banī isrā`īla lā ta\'budụna illallāha wa bil-wālidaini iḥsānaw wa żil-qurbā wal-yatāmā wal-masākīni wa qụlụ lin-nāsi ḥusnaw wa aqīmuṣ-ṣalāta wa ātuz-zakāh, ṡumma tawallaitum illā qalīlam mingkum wa antum mu\'riḍụn'),
(90, 2, 84, 'وَاِذْ اَخَذْنَا مِيْثَاقَكُمْ لَا تَسْفِكُوْنَ دِمَاۤءَكُمْ وَلَا تُخْرِجُوْنَ اَنْفُسَكُمْ مِّنْ دِيَارِكُمْ ۖ ثُمَّ اَقْرَرْتُمْ وَاَنْتُمْ تَشْهَدُوْنَ', 'wa iż akhażnā mīṡāqakum lā tasfikụna dimā`akum wa lā tukhrijụna anfusakum min diyārikum ṡumma aqrartum wa antum tasy-hadụn'),
(91, 2, 85, 'ثُمَّ اَنْتُمْ هٰٓؤُلَاۤءِ تَقْتُلُوْنَ اَنْفُسَكُمْ وَتُخْرِجُوْنَ فَرِيْقًا مِّنْكُمْ مِّنْ دِيَارِهِمْۖ تَظٰهَرُوْنَ عَلَيْهِمْ بِالْاِثْمِ وَالْعُدْوَانِۗ وَاِنْ يَّأْتُوْكُمْ اُسٰرٰى تُفٰدُوْهُمْ وَهُوَ مُحَرَّمٌ عَلَيْكُمْ اِخْرَاجُهُمْ ۗ اَفَتُؤْمِنُوْنَ بِبَعْضِ الْكِتٰبِ وَتَكْفُرُوْنَ بِبَعْضٍۚ فَمَا جَزَاۤءُ مَنْ يَّفْعَلُ ذٰلِكَ مِنْكُمْ اِلَّا خِزْيٌ فِى الْحَيٰوةِ الدُّنْيَا ۚوَيَوْمَ الْقِيٰمَةِ يُرَدُّوْنَ اِلٰٓى اَشَدِّ الْعَذَابِۗ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ', 'ṡumma antum hā`ulā`i taqtulụna anfusakum wa tukhrijụna farīqam mingkum min diyārihim taẓāharụna \'alaihim bil-iṡmi wal-\'udwān, wa iy ya`tụkum usārā tufādụhum wa huwa muḥarramun \'alaikum ikhrājuhum, a fa tu`minụna biba\'ḍil-kitābi wa takfurụna biba\'ḍ, fa mā jazā`u may yaf\'alu żālika mingkum illā khizyun fil-ḥayātid-dun-yā, wa yaumal-qiyāmati yuraddụna ilā asyaddil-\'ażāb, wa mallāhu bigāfilin \'ammā ta\'malụn'),
(92, 2, 86, ' اُولٰۤىِٕكَ الَّذِيْنَ اشْتَرَوُا الْحَيٰوةَ الدُّنْيَا بِالْاٰخِرَةِ ۖ فَلَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنْصَرُوْنَ', 'ulā`ikallażīnasytarawul-ḥayātad-dun-yā bil-ākhirati fa lā yukhaffafu \'an-humul-\'ażābu wa lā hum yunṣarụn'),
(93, 2, 87, 'وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ وَقَفَّيْنَا مِنْۢ بَعْدِهٖ بِالرُّسُلِ ۖ وَاٰتَيْنَا عِيْسَى ابْنَ مَرْيَمَ الْبَيِّنٰتِ وَاَيَّدْنٰهُ بِرُوْحِ الْقُدُسِۗ اَفَكُلَّمَا جَاۤءَكُمْ رَسُوْلٌۢ بِمَا لَا تَهْوٰىٓ اَنْفُسُكُمُ اسْتَكْبَرْتُمْ ۚ فَفَرِيْقًا كَذَّبْتُمْ وَفَرِيْقًا تَقْتُلُوْنَ', 'wa laqad ātainā mụsal-kitāba wa qaffainā mim ba\'dihī bir-rusuli wa ātainā \'īsabna maryamal-bayyināti wa ayyadnāhu birụḥil-qudus, a fa kullamā jā`akum rasụlum bimā lā tahwā anfusukumustakbartum, fa farīqang każżabtum wa farīqan taqtulụn'),
(94, 2, 88, 'وَقَالُوْا قُلُوْبُنَا غُلْفٌ ۗ بَلْ لَّعَنَهُمُ اللّٰهُ بِكُفْرِهِمْ فَقَلِيْلًا مَّا يُؤْمِنُوْنَ', 'wa qālụ qulụbunā gulf, bal la\'anahumullāhu bikufrihim fa qalīlam mā yu`minụn'),
(95, 2, 89, 'وَلَمَّا جَاۤءَهُمْ كِتٰبٌ مِّنْ عِنْدِ اللّٰهِ مُصَدِّقٌ لِّمَا مَعَهُمْۙ وَكَانُوْا مِنْ قَبْلُ يَسْتَفْتِحُوْنَ عَلَى الَّذِيْنَ كَفَرُوْاۚ فَلَمَّا جَاۤءَهُمْ مَّا عَرَفُوْا كَفَرُوْا بِهٖ ۖ فَلَعْنَةُ اللّٰهِ عَلَى الْكٰفِرِيْنَ', 'wa lammā jā`ahum kitābum min \'indillāhi muṣaddiqul limā ma\'ahum wa kānụ ming qablu yastaftiḥụna \'alallażīna kafarụ, fa lammā jā`ahum mā \'arafụ kafarụ bihī fa la\'natullāhi \'alal-kāfirīn'),
(96, 2, 90, ' بِئْسَمَا اشْتَرَوْا بِهٖٓ اَنْفُسَهُمْ اَنْ يَّكْفُرُوْا بِمَآ اَنْزَلَ اللّٰهُ بَغْيًا اَنْ يُّنَزِّلَ اللّٰهُ مِنْ فَضْلِهٖ عَلٰى مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ ۚ فَبَاۤءُوْ بِغَضَبٍ عَلٰى غَضَبٍۗ وَلِلْكٰفِرِيْنَ عَذَابٌ مُّهِيْنٌ', 'bi`samasytarau bihī anfusahum ay yakfurụ bimā anzalallāhu bagyan ay yunazzilallāhu min faḍlihī \'alā may yasyā`u min \'ibādih, fa bā`ụ bigaḍabin \'alā gaḍab, wa lil-kāfirīna \'ażābum muhīn'),
(97, 2, 91, 'وَاِذَا قِيْلَ لَهُمْ اٰمِنُوْا بِمَآ اَنْزَلَ اللّٰهُ قَالُوْا نُؤْمِنُ بِمَآ اُنْزِلَ عَلَيْنَا وَيَكْفُرُوْنَ بِمَا وَرَاۤءَهٗ وَهُوَ الْحَقُّ مُصَدِّقًا لِّمَا مَعَهُمْ ۗ قُلْ فَلِمَ تَقْتُلُوْنَ اَنْۢبِيَاۤءَ اللّٰهِ مِنْ قَبْلُ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ', 'wa iżā qīla lahum āminụ bimā anzalallāhu qālụ nu`minu bimā unzila \'alainā wa yakfurụna bimā warā`ahụ wa huwal-ḥaqqu muṣaddiqal limā ma\'ahum, qul fa lima taqtulụna ambiyā`allāhi ming qablu ing kuntum mu`minīn'),
(98, 2, 92, '۞ وَلَقَدْ جَاۤءَكُمْ مُّوْسٰى بِالْبَيِّنٰتِ ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِنْۢ بَعْدِهٖ وَاَنْتُمْ ظٰلِمُوْنَ', 'wa laqad jā`akum mụsā bil-bayyināti ṡummattakhażtumul-\'ijla mim ba\'dihī wa antum ẓālimụn'),
(99, 2, 93, 'وَاِذْ اَخَذْنَا مِيْثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّوْرَۗ خُذُوْا مَآ اٰتَيْنٰكُمْ بِقُوَّةٍ وَّاسْمَعُوْا ۗ قَالُوْا سَمِعْنَا وَعَصَيْنَا وَاُشْرِبُوْا فِيْ قُلُوْبِهِمُ الْعِجْلَ بِكُفْرِهِمْ ۗ قُلْ بِئْسَمَا يَأْمُرُكُمْ بِهٖٓ اِيْمَانُكُمْ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ', 'wa iż akhażnā mīṡāqakum wa rafa\'nā fauqakumuṭ-ṭụr, khużụ mā ātainākum biquwwatiw wasma\'ụ, qālụ sami\'nā wa \'aṣainā wa usyribụ fī qulụbihimul-\'ijla bikufrihim, qul bi`samā ya`murukum bihī īmānukum ing kuntum mu`minīn'),
(100, 2, 94, ' قُلْ اِنْ كَانَتْ لَكُمُ الدَّارُ الْاٰخِرَةُ عِنْدَ اللّٰهِ خَالِصَةً مِّنْ دُوْنِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'qul ing kānat lakumud-dārul-ākhiratu \'indallāhi khāliṣatam min dụnin-nāsi fa tamannawul-mauta ing kuntum ṣādiqīn'),
(101, 2, 95, 'وَلَنْ يَّتَمَنَّوْهُ اَبَدًاۢ بِمَا قَدَّمَتْ اَيْدِيْهِمْ ۗ وَاللّٰهُ عَلِيْمٌ ۢ بِالظّٰلِمِيْنَ', 'wa lay yatamannauhu abadam bimā qaddamat aidīhim, wallāhu \'alīmum biẓ-ẓālimīn'),
(102, 2, 96, 'وَلَتَجِدَنَّهُمْ اَحْرَصَ النَّاسِ عَلٰى حَيٰوةٍ ۛوَمِنَ الَّذِيْنَ اَشْرَكُوْا ۛيَوَدُّ اَحَدُهُمْ لَوْ يُعَمَّرُ اَلْفَ سَنَةٍۚ وَمَا هُوَ بِمُزَحْزِحِهٖ مِنَ الْعَذَابِ اَنْ يُّعَمَّرَۗ وَاللّٰهُ بَصِيْرٌۢ بِمَا يَعْمَلُوْنَ', 'wa latajidannahum aḥraṣan-nāsi \'alā ḥayāh, wa minallażīna asyrakụ yawaddu aḥaduhum lau yu\'ammaru alfa sanah, wa mā huwa bimuzaḥziḥihī minal-\'ażābi ay yu\'ammar, wallāhu baṣīrum bimā ya\'malụn'),
(103, 2, 97, ' قُلْ مَنْ كَانَ عَدُوًّا لِّجِبْرِيْلَ فَاِنَّهٗ نَزَّلَهٗ عَلٰى قَلْبِكَ بِاِذْنِ اللّٰهِ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ وَهُدًى وَّبُشْرٰى لِلْمُؤْمِنِيْنَ', 'qul mang kāna \'aduwwal lijibrīla fa innahụ nazzalahụ \'alā qalbika bi`iżnillāhi muṣaddiqal limā baina yadaihi wa hudaw wa busyrā lil-mu`minīn'),
(104, 2, 98, 'مَنْ كَانَ عَدُوًّا لِّلّٰهِ وَمَلٰۤىِٕكَتِهٖ وَرُسُلِهٖ وَجِبْرِيْلَ وَمِيْكٰىلَ فَاِنَّ اللّٰهَ عَدُوٌّ لِّلْكٰفِرِيْنَ', 'mang kāna \'aduwwal lillāhi wa malā`ikatihī wa rusulihī wa jibrīla wa mīkāla fa innallāha \'aduwwul lil-kāfirīn'),
(105, 2, 99, 'وَلَقَدْ اَنْزَلْنَآ اِلَيْكَ اٰيٰتٍۢ بَيِّنٰتٍۚ وَمَا يَكْفُرُ بِهَآ اِلَّا الْفٰسِقُوْنَ', 'wa laqad anzalnā ilaika āyātim bayyināt, wa mā yakfuru bihā illal-fāsiqụn'),
(106, 2, 100, 'اَوَكُلَّمَا عٰهَدُوْا عَهْدًا نَّبَذَهٗ فَرِيْقٌ مِّنْهُمْ ۗ بَلْ اَكْثَرُهُمْ لَا يُؤْمِنُوْنَ', 'a wa kullamā \'āhadụ \'ahdan nabażahụ farīqum min-hum, bal akṡaruhum lā yu`minụn'),
(107, 2, 101, ' وَلَمَّا جَاۤءَهُمْ رَسُوْلٌ مِّنْ عِنْدِ اللّٰهِ مُصَدِّقٌ لِّمَا مَعَهُمْ نَبَذَ فَرِيْقٌ مِّنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَۙ كِتٰبَ اللّٰهِ وَرَاۤءَ ظُهُوْرِهِمْ كَاَنَّهُمْ لَا يَعْلَمُوْنَۖ', 'wa lammā jā`ahum rasụlum min \'indillāhi muṣaddiqul limā ma\'ahum nabaża farīqum minallażīna ụtul-kitāba kitāballāhi warā`a ẓuhụrihim ka`annahum lā ya\'lamụn'),
(108, 2, 102, 'وَاتَّبَعُوْا مَا تَتْلُوا الشَّيٰطِيْنُ عَلٰى مُلْكِ سُلَيْمٰنَ ۚ وَمَا كَفَرَ سُلَيْمٰنُ وَلٰكِنَّ الشَّيٰطِيْنَ كَفَرُوْا يُعَلِّمُوْنَ النَّاسَ السِّحْرَ وَمَآ اُنْزِلَ عَلَى الْمَلَكَيْنِ بِبَابِلَ هَارُوْتَ وَمَارُوْتَ ۗ وَمَا يُعَلِّمٰنِ مِنْ اَحَدٍ حَتّٰى يَقُوْلَآ اِنَّمَا نَحْنُ فِتْنَةٌ فَلَا تَكْفُرْ ۗ فَيَتَعَلَّمُوْنَ مِنْهُمَا مَا يُفَرِّقُوْنَ بِهٖ بَيْنَ الْمَرْءِ وَزَوْجِهٖ ۗ وَمَا هُمْ بِضَاۤرِّيْنَ بِهٖ مِنْ اَحَدٍ اِلَّا بِاِذْنِ اللّٰهِ ۗ وَيَتَعَلَّمُوْنَ مَا يَضُرُّهُمْ وَلَا يَنْفَعُهُمْ ۗ وَلَقَدْ عَلِمُوْا لَمَنِ اشْتَرٰىهُ مَا لَهٗ فِى الْاٰخِرَةِ مِنْ خَلَاقٍ ۗ وَلَبِئْسَ مَاشَرَوْا بِهٖٓ اَنْفُسَهُمْ ۗ لَوْ كَانُوْا يَعْلَمُوْنَ', 'wattaba\'ụ mā tatlusy-syayāṭīnu \'alā mulki sulaimān, wa mā kafara sulaimānu wa lākinnasy-syayāṭīna kafarụ yu\'allimụnan-nāsas-siḥra wa mā unzila \'alal-malakaini bibābila hārụta wa mārụt, wa mā yu\'allimāni min aḥadin ḥattā yaqụlā innamā naḥnu fitnatun fa lā takfur, fa yata\'allamụna min-humā mā yufarriqụna bihī bainal-mar\'i wa zaujih, wa mā hum biḍārrīna bihī min aḥadin illā bi`iżnillāh, wa yata\'allamụna mā yaḍurruhum wa lā yanfa\'uhum, wa laqad \'alimụ lamanisytarāhu mā lahụ fil-ākhirati min khalāq, wa labi`sa mā syarau bihī anfusahum, lau kānụ ya\'lamụn'),
(109, 2, 103, 'وَلَوْ اَنَّهُمْ اٰمَنُوْا وَاتَّقَوْا لَمَثُوْبَةٌ مِّنْ عِنْدِ اللّٰهِ خَيْرٌ ۗ لَوْ كَانُوْا يَعْلَمُوْنَ', 'walau annahum āmanụ wattaqau lamaṡụbatum min \'indillāhi khaīr, lau kānụ ya\'lamụn'),
(110, 2, 104, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَقُوْلُوْا رَاعِنَا وَقُوْلُوا انْظُرْنَا وَاسْمَعُوْا وَلِلْكٰفِرِيْنَ عَذَابٌ اَلِيْمٌ', 'yā ayyuhallażīna āmanụ lā taqụlụ rā\'inā wa qụlunẓurnā wasma\'ụ wa lil-kāfirīna \'ażābun alīm'),
(111, 2, 105, 'مَا يَوَدُّ الَّذِيْنَ كَفَرُوْا مِنْ اَهْلِ الْكِتٰبِ وَلَا الْمُشْرِكِيْنَ اَنْ يُّنَزَّلَ عَلَيْكُمْ مِّنْ خَيْرٍ مِّنْ رَّبِّكُمْ ۗ وَاللّٰهُ يَخْتَصُّ بِرَحْمَتِهٖ مَنْ يَّشَاۤءُ ۗ وَاللّٰهُ ذُو الْفَضْلِ الْعَظِيْمِ', 'mā yawaddullażīna kafarụ min ahlil-kitābi wa lal-musyrikīna ay yunazzala \'alaikum min khairim mir rabbikum, wallāhu yakhtaṣṣu biraḥmatihī may yasyā`, wallāhu żul-faḍlil-\'aẓīm'),
(112, 2, 106, '۞ مَا نَنْسَخْ مِنْ اٰيَةٍ اَوْ نُنْسِهَا نَأْتِ بِخَيْرٍ مِّنْهَآ اَوْ مِثْلِهَا ۗ اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'mā nansakh min āyatin au nunsihā na`ti bikhairim min-hā au miṡlihā, a lam ta\'lam annallāha \'alā kulli syai`ing qadīr'),
(113, 2, 107, 'اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ ۗ وَمَا لَكُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ', 'a lam ta\'lam annallāha lahụ mulkus-samāwāti wal-arḍ, wa mā lakum min dụnillāhi miw waliyyiw wa lā naṣīr'),
(114, 2, 108, 'اَمْ تُرِيْدُوْنَ اَنْ تَسْـَٔلُوْا رَسُوْلَكُمْ كَمَا سُىِٕلَ مُوْسٰى مِنْ قَبْلُ ۗوَمَنْ يَّتَبَدَّلِ الْكُفْرَ بِالْاِيْمَانِ فَقَدْ ضَلَّ سَوَاۤءَ السَّبِيْلِ', 'am turīdụna an tas`alụ rasụlakum kamā su`ila mụsā ming qabl, wa may yatabaddalil-kufra bil-īmāni fa qad ḍalla sawā`as-sabīl'),
(115, 2, 109, 'وَدَّ كَثِيْرٌ مِّنْ اَهْلِ الْكِتٰبِ لَوْ يَرُدُّوْنَكُمْ مِّنْۢ بَعْدِ اِيْمَانِكُمْ كُفَّارًاۚ حَسَدًا مِّنْ عِنْدِ اَنْفُسِهِمْ مِّنْۢ بَعْدِ مَا تَبَيَّنَ لَهُمُ الْحَقُّ ۚ فَاعْفُوْا وَاصْفَحُوْا حَتّٰى يَأْتِيَ اللّٰهُ بِاَمْرِهٖ ۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'wadda kaṡīrum min ahlil-kitābi lau yaruddụnakum mim ba\'di īmānikum kuffārā, ḥasadam min \'indi anfusihim mim ba\'di mā tabayyana lahumul-ḥaqq, fa\'fụ waṣfaḥụ ḥattā ya`tiyallāhu bi`amrih, innallāha \'alā kulli syai`ing qadīr'),
(116, 2, 110, ' وَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ ۗ وَمَا تُقَدِّمُوْا لِاَنْفُسِكُمْ مِّنْ خَيْرٍ تَجِدُوْهُ عِنْدَ اللّٰهِ ۗ اِنَّ اللّٰهَ بِمَا تَعْمَلُوْنَ بَصِيْرٌ', 'wa aqīmuṣ-ṣalāta wa ātuz-zakāh, wa mā tuqaddimụ li`anfusikum min khairin tajidụhu \'indallāh, innallāha bimā ta\'malụna baṣīr'),
(117, 2, 111, 'وَقَالُوْا لَنْ يَّدْخُلَ الْجَنَّةَ اِلَّا مَنْ كَانَ هُوْدًا اَوْ نَصٰرٰى ۗ تِلْكَ اَمَانِيُّهُمْ ۗ قُلْ هَاتُوْا بُرْهَانَكُمْ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'wa qālụ lay yadkhulal-jannata illā mang kāna hụdan au naṣārā, tilka amāniyyuhum, qul hātụ bur-hānakum ing kuntum ṣādiqīn'),
(118, 2, 112, 'بَلٰى مَنْ اَسْلَمَ وَجْهَهٗ لِلّٰهِ وَهُوَ مُحْسِنٌ فَلَهٗٓ اَجْرُهٗ عِنْدَ رَبِّهٖۖ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ', 'balā man aslama waj-hahụ lillāhi wa huwa muḥsinun fa lahū ajruhụ \'inda rabbihī wa lā khaufun \'alaihim wa lā hum yaḥzanụn'),
(119, 2, 113, 'وَقَالَتِ الْيَهُوْدُ لَيْسَتِ النَّصٰرٰى عَلٰى شَيْءٍۖ وَّقَالَتِ النَّصٰرٰى لَيْسَتِ الْيَهُوْدُ عَلٰى شَيْءٍۙ وَّهُمْ يَتْلُوْنَ الْكِتٰبَۗ كَذٰلِكَ قَالَ الَّذِيْنَ لَا يَعْلَمُوْنَ مِثْلَ قَوْلِهِمْ ۚ فَاللّٰهُ يَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كَانُوْا فِيْهِ يَخْتَلِفُوْنَ', 'wa qālatil-yahụdu laisatin-naṣārā \'alā syai`iw wa qālatin-naṣārā laisatil-yahụdu \'alā syai`iw wa hum yatlụnal-kitāb, każālika qālallażīna lā ya\'lamụna miṡla qaulihim, fallāhu yaḥkumu bainahum yaumal-qiyāmati fīmā kānụ fīhi yakhtalifụn'),
(120, 2, 114, 'وَمَنْ اَظْلَمُ مِمَّنْ مَّنَعَ مَسٰجِدَ اللّٰهِ اَنْ يُّذْكَرَ فِيْهَا اسْمُهٗ وَسَعٰى فِيْ خَرَابِهَاۗ اُولٰۤىِٕكَ مَا كَانَ لَهُمْ اَنْ يَّدْخُلُوْهَآ اِلَّا خَاۤىِٕفِيْنَ ەۗ لَهُمْ فِى الدُّنْيَا خِزْيٌ وَّلَهُمْ فِى الْاٰخِرَةِ عَذَابٌ عَظِيْمٌ', 'wa man aẓlamu mim mam mana\'a masājidallāhi ay yużkara fīhasmuhụ wa sa\'ā fī kharābihā, ulā`ika mā kāna lahum ay yadkhulụhā illā khā`ifīn, lahum fid-dun-yā khizyuw wa lahum fil-ākhirati \'ażābun \'aẓīm'),
(121, 2, 115, 'وَلِلّٰهِ الْمَشْرِقُ وَالْمَغْرِبُ فَاَيْنَمَا تُوَلُّوْا فَثَمَّ وَجْهُ اللّٰهِ ۗ اِنَّ اللّٰهَ وَاسِعٌ عَلِيْمٌ', 'wa lillāhil-masyriqu wal-magribu fa ainamā tuwallụ fa ṡamma waj-hullāh, innallāha wāsi\'un \'alīm'),
(122, 2, 116, 'وَقَالُوا اتَّخَذَ اللّٰهُ وَلَدًا ۙسُبْحٰنَهٗ ۗ بَلْ لَّهٗ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ كُلٌّ لَّهٗ قَانِتُوْنَ', 'wa qāluttakhażallāhu waladan sub-ḥānah, bal lahụ mā fis-samāwāti wal-arḍ, kullul lahụ qānitụn'),
(123, 2, 117, 'بَدِيْعُ السَّمٰوٰتِ وَالْاَرْضِۗ وَاِذَا قَضٰٓى اَمْرًا فَاِنَّمَا يَقُوْلُ لَهٗ كُنْ فَيَكُوْنُ', 'badī\'us-samāwāti wal-arḍ, wa iżā qaḍā amran fa innamā yaqụlu lahụ kun fa yakụn'),
(124, 2, 118, 'وَقَالَ الَّذِيْنَ لَا يَعْلَمُوْنَ لَوْلَا يُكَلِّمُنَا اللّٰهُ اَوْ تَأْتِيْنَآ اٰيَةٌ ۗ كَذٰلِكَ قَالَ الَّذِيْنَ مِنْ قَبْلِهِمْ مِّثْلَ قَوْلِهِمْ ۗ تَشَابَهَتْ قُلُوْبُهُمْ ۗ قَدْ بَيَّنَّا الْاٰيٰتِ لِقَوْمٍ يُّوْقِنُوْنَ', 'wa qālallażīna lā ya\'lamụna lau lā yukallimunallāhu au ta`tīnā āyah, każālika qālallażīna ming qablihim miṡla qaulihim, tasyābahat qulụbuhum, qad bayyannal-āyāti liqaumiy yụqinụn'),
(125, 2, 119, 'اِنَّآ اَرْسَلْنٰكَ بِالْحَقِّ بَشِيْرًا وَّنَذِيْرًاۙ وَّلَا تُسْـَٔلُ عَنْ اَصْحٰبِ الْجَحِيْمِ', 'innā arsalnāka bil-ḥaqqi basyīraw wa nażīraw wa lā tus`alu \'an aṣ-ḥābil-jaḥīm'),
(126, 2, 120, 'وَلَنْ تَرْضٰى عَنْكَ الْيَهُوْدُ وَلَا النَّصٰرٰى حَتّٰى تَتَّبِعَ مِلَّتَهُمْ ۗ قُلْ اِنَّ هُدَى اللّٰهِ هُوَ الْهُدٰى ۗ وَلَىِٕنِ اتَّبَعْتَ اَهْوَاۤءَهُمْ بَعْدَ الَّذِيْ جَاۤءَكَ مِنَ الْعِلْمِ ۙ مَا لَكَ مِنَ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ', 'wa lan tarḍā \'angkal-yahụdu wa lan-naṣārā ḥattā tattabi\'a millatahum, qul inna hudallāhi huwal-hudā, wa la`inittaba\'ta ahwā`ahum ba\'dallażī jā`aka minal-\'ilmi mā laka minallāhi miw waliyyiw wa lā naṣīr'),
(127, 2, 121, 'اَلَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يَتْلُوْنَهٗ حَقَّ تِلَاوَتِهٖۗ اُولٰۤىِٕكَ يُؤْمِنُوْنَ بِهٖ ۗ وَمَنْ يَّكْفُرْ بِهٖ فَاُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ', 'allażīna ātaināhumul-kitāba yatlụnahụ ḥaqqa tilāwatih, ulā`ika yu`minụna bih, wa may yakfur bihī fa ulā`ika humul-khāsirụn'),
(128, 2, 122, 'يٰبَنِيْٓ اِسْرَاۤءِيْلَ اذْكُرُوْا نِعْمَتِيَ الَّتِيْٓ اَنْعَمْتُ عَلَيْكُمْ وَاَنِّيْ فَضَّلْتُكُمْ عَلَى الْعٰلَمِيْنَ', 'yā banī isrā`īlażkurụ ni\'matiyallatī an\'amtu \'alaikum wa annī faḍḍaltukum \'alal-\'ālamīn'),
(129, 2, 123, 'وَاتَّقُوْا يَوْمًا لَّا تَجْزِيْ نَفْسٌ عَنْ نَّفْسٍ شَيْـًٔا وَّلَا يُقْبَلُ مِنْهَا عَدْلٌ وَّلَا تَنْفَعُهَا شَفَاعَةٌ وَّلَا هُمْ يُنْصَرُوْنَ', 'wattaqụ yaumal lā tajzī nafsun \'an nafsin syai`aw wa lā yuqbalu min-hā \'adluw wa lā tanfa\'uhā syafā\'atuw wa lā hum yunṣarụn'),
(130, 2, 124, '۞ وَاِذِ ابْتَلٰٓى اِبْرٰهٖمَ رَبُّهٗ بِكَلِمٰتٍ فَاَتَمَّهُنَّ ۗ قَالَ اِنِّيْ جَاعِلُكَ لِلنَّاسِ اِمَامًا ۗ قَالَ وَمِنْ ذُرِّيَّتِيْ ۗ قَالَ لَا يَنَالُ عَهْدِى الظّٰلِمِيْنَ', 'wa iżibtalā ibrāhīma rabbuhụ bikalimātin fa atammahunn, qāla innī jā\'iluka lin-nāsi imāmā, qāla wa min żurriyyatī, qāla lā yanālu \'ahdiẓ-ẓālimīn'),
(131, 2, 125, 'وَاِذْ جَعَلْنَا الْبَيْتَ مَثَابَةً لِّلنَّاسِ وَاَمْنًاۗ وَاتَّخِذُوْا مِنْ مَّقَامِ اِبْرٰهٖمَ مُصَلًّىۗ وَعَهِدْنَآ اِلٰٓى اِبْرٰهٖمَ وَاِسْمٰعِيْلَ اَنْ طَهِّرَا بَيْتِيَ لِلطَّاۤىِٕفِيْنَ وَالْعٰكِفِيْنَ وَالرُّكَّعِ السُّجُوْدِ', 'wa iż ja\'alnal-baita maṡābatal lin-nāsi wa amnā, wattakhiżụ mim maqāmi ibrāhīma muṣallā, wa \'ahidnā ilā ibrāhīma wa ismā\'īla an ṭahhirā baitiya liṭ-ṭā`ifīna wal-\'ākifīna war-rukka\'is-sujụd'),
(132, 2, 126, 'وَاِذْ قَالَ اِبْرٰهٖمُ رَبِّ اجْعَلْ هٰذَا بَلَدًا اٰمِنًا وَّارْزُقْ اَهْلَهٗ مِنَ الثَّمَرٰتِ مَنْ اٰمَنَ مِنْهُمْ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۗ قَالَ وَمَنْ كَفَرَ فَاُمَتِّعُهٗ قَلِيْلًا ثُمَّ اَضْطَرُّهٗٓ اِلٰى عَذَابِ النَّارِ ۗ وَبِئْسَ الْمَصِيْرُ', 'wa iż qāla ibrāhīmu rabbij\'al hāżā baladan āminaw warzuq ahlahụ minaṡ-ṡamarāti man āmana min-hum billāhi wal-yaumil-ākhir, qāla wa mang kafara fa umatti\'uhụ qalīlan ṡumma aḍṭarruhū ilā \'ażābin-nār, wa bi`sal-maṣīr'),
(133, 2, 127, 'وَاِذْ يَرْفَعُ اِبْرٰهٖمُ الْقَوَاعِدَ مِنَ الْبَيْتِ وَاِسْمٰعِيْلُۗ رَبَّنَا تَقَبَّلْ مِنَّا ۗ اِنَّكَ اَنْتَ السَّمِيْعُ الْعَلِيْمُ', 'wa iż yarfa\'u ibrāhīmul-qawā\'ida minal-baiti wa ismā\'īl, rabbanā taqabbal minnā, innaka antas-samī\'ul-\'alīm'),
(134, 2, 128, 'رَبَّنَا وَاجْعَلْنَا مُسْلِمَيْنِ لَكَ وَمِنْ ذُرِّيَّتِنَآ اُمَّةً مُّسْلِمَةً لَّكَۖ وَاَرِنَا مَنَاسِكَنَا وَتُبْ عَلَيْنَا ۚ اِنَّكَ اَنْتَ التَّوَّابُ الرَّحِيْمُ', 'rabbanā waj\'alnā muslimaini laka wa min żurriyyatinā ummatam muslimatal laka wa arinā manāsikanā wa tub \'alainā, innaka antat-tawwābur-raḥīm'),
(135, 2, 129, 'رَبَّنَا وَابْعَثْ فِيْهِمْ رَسُوْلًا مِّنْهُمْ يَتْلُوْا عَلَيْهِمْ اٰيٰتِكَ وَيُعَلِّمُهُمُ الْكِتٰبَ وَالْحِكْمَةَ وَيُزَكِّيْهِمْ ۗ اِنَّكَ اَنْتَ الْعَزِيْزُ الْحَكِيْمُ', 'rabbanā wab\'aṡ fīhim rasụlam min-hum yatlụ \'alaihim āyātika wa yu\'allimuhumul-kitāba wal-ḥikmata wa yuzakkīhim, innaka antal-\'azīzul-ḥakīm'),
(136, 2, 130, 'وَمَنْ يَّرْغَبُ عَنْ مِّلَّةِ اِبْرٰهٖمَ اِلَّا مَنْ سَفِهَ نَفْسَهٗ ۗوَلَقَدِ اصْطَفَيْنٰهُ فِى الدُّنْيَا ۚوَاِنَّهٗ فِى الْاٰخِرَةِ لَمِنَ الصّٰلِحِيْنَ', 'wa may yargabu \'am millati ibrāhīma illā man safiha nafsah, wa laqadiṣṭafaināhu fid-dun-yā, wa innahụ fil-ākhirati laminaṣ-ṣāliḥīn'),
(137, 2, 131, 'اِذْ قَالَ لَهٗ رَبُّهٗٓ اَسْلِمْۙ قَالَ اَسْلَمْتُ لِرَبِّ الْعٰلَمِيْنَ', 'iż qāla lahụ rabbuhū aslim qāla aslamtu lirabbil-\'ālamīn'),
(138, 2, 132, 'وَوَصّٰى بِهَآ اِبْرٰهٖمُ بَنِيْهِ وَيَعْقُوْبُۗ يٰبَنِيَّ اِنَّ اللّٰهَ اصْطَفٰى لَكُمُ الدِّيْنَ فَلَا تَمُوْتُنَّ اِلَّا وَاَنْتُمْ مُّسْلِمُوْنَ ۗ', 'wa waṣṣā bihā ibrāhīmu banīhi wa ya\'qụb, yā baniyya innallāhaṣṭafā lakumud-dīna fa lā tamụtunna illā wa antum muslimụn'),
(139, 2, 133, 'اَمْ كُنْتُمْ شُهَدَاۤءَ اِذْ حَضَرَ يَعْقُوْبَ الْمَوْتُۙ اِذْ قَالَ لِبَنِيْهِ مَا تَعْبُدُوْنَ مِنْۢ بَعْدِيْۗ قَالُوْا نَعْبُدُ اِلٰهَكَ وَاِلٰهَ اٰبَاۤىِٕكَ اِبْرٰهٖمَ وَاِسْمٰعِيْلَ وَاِسْحٰقَ اِلٰهًا وَّاحِدًاۚ وَنَحْنُ لَهٗ مُسْلِمُوْنَ', 'am kuntum syuhadā`a iż ḥaḍara ya\'qụbal-mautu iż qāla libanīhi mā ta\'budụna mim ba\'dī, qālụ na\'budu ilāhaka wa ilāha ābā`ika ibrāhīma wa ismā\'īla wa is-ḥāqa ilāhaw wāḥidā, wa naḥnu lahụ muslimụn'),
(140, 2, 134, 'تِلْكَ اُمَّةٌ قَدْ خَلَتْ ۚ لَهَا مَا كَسَبَتْ وَلَكُمْ مَّا كَسَبْتُمْ ۚ وَلَا تُسْـَٔلُوْنَ عَمَّا كَانُوْا يَعْمَلُوْنَ', 'tilka ummatung qad khalat, lahā mā kasabat wa lakum mā kasabtum, wa lā tus`alụna \'ammā kānụ ya\'malụn'),
(141, 2, 135, 'وَقَالُوْا كُوْنُوْا هُوْدًا اَوْ نَصٰرٰى تَهْتَدُوْا ۗ قُلْ بَلْ مِلَّةَ اِبْرٰهٖمَ حَنِيْفًا ۗوَمَا كَانَ مِنَ الْمُشْرِكِيْنَ', 'wa qālụ kụnụ hụdan au naṣārā tahtadụ, qul bal millata ibrāhīma ḥanīfā, wa mā kāna minal-musyrikīn'),
(142, 2, 136, 'قُوْلُوْٓا اٰمَنَّا بِاللّٰهِ وَمَآ اُنْزِلَ اِلَيْنَا وَمَآ اُنْزِلَ اِلٰٓى اِبْرٰهٖمَ وَاِسْمٰعِيْلَ وَاِسْحٰقَ وَيَعْقُوْبَ وَالْاَسْبَاطِ وَمَآ اُوْتِيَ مُوْسٰى وَعِيْسٰى وَمَآ اُوْتِيَ النَّبِيُّوْنَ مِنْ رَّبِّهِمْۚ لَا نُفَرِّقُ بَيْنَ اَحَدٍ مِّنْهُمْۖ وَنَحْنُ لَهٗ مُسْلِمُوْنَ', 'qụlū āmannā billāhi wa mā unzila ilainā wa mā unzila ilā ibrāhīma wa ismā\'īla wa is-ḥāqa wa ya\'qụba wal-asbāṭi wa mā ụtiya mụsā wa \'īsā wa mā ụtiyan-nabiyyụna mir rabbihim, lā nufarriqu baina aḥadim min-hum wa naḥnu lahụ muslimụn'),
(143, 2, 137, 'فَاِنْ اٰمَنُوْا بِمِثْلِ مَآ اٰمَنْتُمْ بِهٖ فَقَدِ اهْتَدَوْا ۚوَاِنْ تَوَلَّوْا فَاِنَّمَا هُمْ فِيْ شِقَاقٍۚ فَسَيَكْفِيْكَهُمُ اللّٰهُ ۚوَهُوَ السَّمِيْعُ الْعَلِيْمُ ۗ', 'fa in āmanụ bimiṡli mā āmantum bihī fa qadihtadau, wa in tawallau fa innamā hum fī syiqāq, fa sayakfīkahumullāh, wa huwas-samī\'ul-\'alīm'),
(144, 2, 138, 'صِبْغَةَ اللّٰهِ ۚ وَمَنْ اَحْسَنُ مِنَ اللّٰهِ صِبْغَةً ۖ وَّنَحْنُ لَهٗ عٰبِدُوْنَ', 'ṣibgatallāh, wa man aḥsanu minallāhi ṣibgataw wa naḥnu lahụ \'ābidụn'),
(145, 2, 139, 'قُلْ اَتُحَاۤجُّوْنَنَا فِى اللّٰهِ وَهُوَ رَبُّنَا وَرَبُّكُمْۚ وَلَنَآ اَعْمَالُنَا وَلَكُمْ اَعْمَالُكُمْۚ وَنَحْنُ لَهٗ مُخْلِصُوْنَ ۙ', 'qul a tuḥājjụnanā fillāhi wa huwa rabbunā wa rabbukum, wa lanā a\'mālunā wa lakum a\'mālukum, wa naḥnu lahụ mukhliṣụn'),
(146, 2, 140, 'اَمْ تَقُوْلُوْنَ اِنَّ اِبْرٰهٖمَ وَاِسْمٰعِيْلَ وَاِسْحٰقَ وَيَعْقُوْبَ وَالْاَسْبَاطَ كَانُوْا هُوْدًا اَوْ نَصٰرٰى ۗ قُلْ ءَاَنْتُمْ اَعْلَمُ اَمِ اللّٰهُ ۗ وَمَنْ اَظْلَمُ مِمَّنْ كَتَمَ شَهَادَةً عِنْدَهٗ مِنَ اللّٰهِ ۗ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ', 'am taqụlụna inna ibrāhīma wa ismā\'īla wa is-ḥāqa wa ya\'qụba wal-asbāṭa kānụ hụdan au naṣārā, qul a antum a\'lamu amillāh, wa man aẓlamu mim mang katama syahādatan \'indahụ minallāh, wa mallāhu bigāfilin \'ammā ta\'malụn'),
(147, 2, 141, 'تِلْكَ اُمَّةٌ قَدْ خَلَتْ ۚ لَهَا مَا كَسَبَتْ وَلَكُمْ مَّا كَسَبْتُمْ ۚ وَلَا تُسْـَٔلُوْنَ عَمَّا كَانُوْا يَعْمَلُوْنَ ۔', 'tilka ummatung qad khalat, lahā mā kasabat wa lakum mā kasabtum, wa lā tus`alụna \'ammā kānụ ya\'malụn'),
(148, 2, 142, '۞ سَيَقُوْلُ السُّفَهَاۤءُ مِنَ النَّاسِ مَا وَلّٰىهُمْ عَنْ قِبْلَتِهِمُ الَّتِيْ كَانُوْا عَلَيْهَا ۗ قُلْ لِّلّٰهِ الْمَشْرِقُ وَالْمَغْرِبُۗ يَهْدِيْ مَنْ يَّشَاۤءُ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ', 'sayaqụlus-sufahā`u minan-nāsi mā wallāhum \'ang qiblatihimullatī kānụ \'alaihā, qul lillāhil-masyriqu wal-magrib, yahdī may yasyā`u ilā ṣirāṭim mustaqīm'),
(149, 2, 143, 'وَكَذٰلِكَ جَعَلْنٰكُمْ اُمَّةً وَّسَطًا لِّتَكُوْنُوْا شُهَدَاۤءَ عَلَى النَّاسِ وَيَكُوْنَ الرَّسُوْلُ عَلَيْكُمْ شَهِيْدًا ۗ وَمَا جَعَلْنَا الْقِبْلَةَ الَّتِيْ كُنْتَ عَلَيْهَآ اِلَّا لِنَعْلَمَ مَنْ يَّتَّبِعُ الرَّسُوْلَ مِمَّنْ يَّنْقَلِبُ عَلٰى عَقِبَيْهِۗ وَاِنْ كَانَتْ لَكَبِيْرَةً اِلَّا عَلَى الَّذِيْنَ هَدَى اللّٰهُ ۗوَمَا كَانَ اللّٰهُ لِيُضِيْعَ اِيْمَانَكُمْ ۗ اِنَّ اللّٰهَ بِالنَّاسِ لَرَءُوْفٌ رَّحِيْمٌ', 'wa każālika ja\'alnākum ummataw wasaṭal litakụnụ syuhadā`a \'alan-nāsi wa yakụnar-rasụlu \'alaikum syahīdā, wa mā ja\'alnal-qiblatallatī kunta \'alaihā illā lina\'lama may yattabi\'ur-rasụla mim may yangqalibu \'alā \'aqibaīh, wa ing kānat lakabīratan illā \'alallażīna hadallāh, wa mā kānallāhu liyuḍī\'a īmānakum, innallāha bin-nāsi lara`ụfur raḥīm'),
(150, 2, 144, 'قَدْ نَرٰى تَقَلُّبَ وَجْهِكَ فِى السَّمَاۤءِۚ فَلَنُوَلِّيَنَّكَ قِبْلَةً تَرْضٰىهَا ۖ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ ۗ وَحَيْثُ مَا كُنْتُمْ فَوَلُّوْا وُجُوْهَكُمْ شَطْرَهٗ ۗ وَاِنَّ الَّذِيْنَ اُوْتُوا الْكِتٰبَ لَيَعْلَمُوْنَ اَنَّهُ الْحَقُّ مِنْ رَّبِّهِمْ ۗ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا يَعْمَلُوْنَ', 'qad narā taqalluba waj-hika fis-samā`, fa lanuwalliyannaka qiblatan tarḍāhā fa walli waj-haka syaṭral-masjidil-ḥarām, wa ḥaiṡu mā kuntum fa wallụ wujụhakum syaṭrah, wa innallażīna ụtul-kitāba laya\'lamụna annahul-ḥaqqu mir rabbihim, wa mallāhu bigāfilin \'ammā ya\'malụn'),
(151, 2, 145, 'وَلَىِٕنْ اَتَيْتَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ بِكُلِّ اٰيَةٍ مَّا تَبِعُوْا قِبْلَتَكَ ۚ وَمَآ اَنْتَ بِتَابِعٍ قِبْلَتَهُمْ ۚ وَمَا بَعْضُهُمْ بِتَابِعٍ قِبْلَةَ بَعْضٍۗ وَلَىِٕنِ اتَّبَعْتَ اَهْوَاۤءَهُمْ مِّنْۢ بَعْدِ مَاجَاۤءَكَ مِنَ الْعِلْمِ ۙ اِنَّكَ اِذًا لَّمِنَ الظّٰلِمِيْنَ ۘ', 'wa la`in ataitallażīna ụtul-kitāba bikulli āyatim mā tabi\'ụ qiblatak, wa mā anta bitābi\'ing qiblatahum, wa mā ba\'ḍuhum bitābi\'ing qiblata ba\'ḍ, wa la`inittaba\'ta ahwā`ahum mim ba\'di mā jā`aka minal-\'ilmi innaka iżal laminaẓ-ẓālimīn'),
(152, 2, 146, 'اَلَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يَعْرِفُوْنَهٗ كَمَا يَعْرِفُوْنَ اَبْنَاۤءَهُمْ ۗ وَاِنَّ فَرِيْقًا مِّنْهُمْ لَيَكْتُمُوْنَ الْحَقَّ وَهُمْ يَعْلَمُوْنَ', 'allażīna ātaināhumul-kitāba ya\'rifụnahụ kamā ya\'rifụna abnā`ahum, wa inna farīqam min-hum layaktumụnal-ḥaqqa wa hum ya\'lamụn'),
(153, 2, 147, 'اَلْحَقُّ مِنْ رَّبِّكَ فَلَا تَكُوْنَنَّ مِنَ الْمُمْتَرِيْنَ', 'al-ḥaqqu mir rabbika fa lā takụnanna minal-mumtarīn'),
(154, 2, 148, 'وَلِكُلٍّ وِّجْهَةٌ هُوَ مُوَلِّيْهَا فَاسْتَبِقُوا الْخَيْرٰتِۗ اَيْنَ مَا تَكُوْنُوْا يَأْتِ بِكُمُ اللّٰهُ جَمِيْعًا ۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'wa likulliw wij-hatun huwa muwallīhā fastabiqul-khairāt, aina mā takụnụ ya`ti bikumullāhu jamī\'ā, innallāha \'alā kulli syai`ing qadīr'),
(155, 2, 149, 'وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ ۗ وَاِنَّهٗ لَلْحَقُّ مِنْ رَّبِّكَ ۗ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ', 'wa min ḥaiṡu kharajta fa walli waj-haka syaṭral-masjidil-ḥarām, wa innahụ lal-ḥaqqu mir rabbik, wa mallāhu bigāfilin \'ammā ta\'malụn'),
(156, 2, 150, 'وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ ۗ وَحَيْثُ مَا كُنْتُمْ فَوَلُّوْا وُجُوْهَكُمْ شَطْرَهٗ ۙ لِئَلَّا يَكُوْنَ لِلنَّاسِ عَلَيْكُمْ حُجَّةٌ اِلَّا الَّذِيْنَ ظَلَمُوْا مِنْهُمْ فَلَا تَخْشَوْهُمْ وَاخْشَوْنِيْ وَلِاُتِمَّ نِعْمَتِيْ عَلَيْكُمْ وَلَعَلَّكُمْ تَهْتَدُوْنَۙ', 'wa min ḥaiṡu kharajta fa walli waj-haka syaṭral-masjidil-ḥarām, wa ḥaiṡu mā kuntum fa wallụ wujụhakum syaṭrahụ li`allā yakụna lin-nāsi \'alaikum ḥujjatun illallażīna ẓalamụ min-hum fa lā takhsyauhum wakhsyaunī wa li`utimma ni\'matī \'alaikum wa la\'allakum tahtadụn'),
(157, 2, 151, 'كَمَآ اَرْسَلْنَا فِيْكُمْ رَسُوْلًا مِّنْكُمْ يَتْلُوْا عَلَيْكُمْ اٰيٰتِنَا وَيُزَكِّيْكُمْ وَيُعَلِّمُكُمُ الْكِتٰبَ وَالْحِكْمَةَ وَيُعَلِّمُكُمْ مَّا لَمْ تَكُوْنُوْا تَعْلَمُوْنَۗ', 'kamā arsalnā fīkum rasụlam mingkum yatlụ \'alaikum āyātinā wa yuzakkīkum wa yu\'allimukumul-kitāba wal-ḥikmata wa yu\'allimukum mā lam takụnụ ta\'lamụn'),
(158, 2, 152, 'فَاذْكُرُوْنِيْٓ اَذْكُرْكُمْ وَاشْكُرُوْا لِيْ وَلَا تَكْفُرُوْنِ', 'fażkurụnī ażkurkum wasykurụ lī wa lā takfurụn');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(159, 2, 153, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اسْتَعِيْنُوْا بِالصَّبْرِ وَالصَّلٰوةِ ۗ اِنَّ اللّٰهَ مَعَ الصّٰبِرِيْنَ', 'yā ayyuhallażīna āmanusta\'īnụ biṣ-ṣabri waṣ-ṣalāh, innallāha ma\'aṣ-ṣābirīn'),
(160, 2, 154, 'وَلَا تَقُوْلُوْا لِمَنْ يُّقْتَلُ فِيْ سَبِيْلِ اللّٰهِ اَمْوَاتٌ ۗ بَلْ اَحْيَاۤءٌ وَّلٰكِنْ لَّا تَشْعُرُوْنَ', 'wa lā taqụlụ limay yuqtalu fī sabīlillāhi amwāt, bal aḥyā`uw wa lākil lā tasy\'urụn'),
(161, 2, 155, 'وَلَنَبْلُوَنَّكُمْ بِشَيْءٍ مِّنَ الْخَوْفِ وَالْجُوْعِ وَنَقْصٍ مِّنَ الْاَمْوَالِ وَالْاَنْفُسِ وَالثَّمَرٰتِۗ وَبَشِّرِ الصّٰبِرِيْنَ', 'wa lanabluwannakum bisyai`im minal-khaufi wal-jụ\'i wa naqṣim minal-amwāli wal-anfusi waṡ-ṡamarāt, wa basysyiriṣ-ṣābirīn'),
(162, 2, 156, 'اَلَّذِيْنَ اِذَآ اَصَابَتْهُمْ مُّصِيْبَةٌ ۗ قَالُوْٓا اِنَّا لِلّٰهِ وَاِنَّآ اِلَيْهِ رٰجِعُوْنَۗ', 'allażīna iżā aṣābat-hum muṣībah, qālū innā lillāhi wa innā ilaihi rāji\'ụn'),
(163, 2, 157, 'اُولٰۤىِٕكَ عَلَيْهِمْ صَلَوٰتٌ مِّنْ رَّبِّهِمْ وَرَحْمَةٌ ۗوَاُولٰۤىِٕكَ هُمُ الْمُهْتَدُوْنَ', 'ulā`ika \'alaihim ṣalawātum mir rabbihim wa raḥmah, wa ulā`ika humul-muhtadụn'),
(164, 2, 158, '۞ اِنَّ الصَّفَا وَالْمَرْوَةَ مِنْ شَعَاۤىِٕرِ اللّٰهِ ۚ فَمَنْ حَجَّ الْبَيْتَ اَوِ اعْتَمَرَ فَلَا جُنَاحَ عَلَيْهِ اَنْ يَّطَّوَّفَ بِهِمَا ۗ وَمَنْ تَطَوَّعَ خَيْرًاۙ فَاِنَّ اللّٰهَ شَاكِرٌ عَلِيْمٌ', 'innaṣ-ṣafā wal-marwata min sya\'ā`irillāh, fa man ḥajjal-baita awi\'tamara fa lā junāḥa \'alaihi ay yaṭṭawwafa bihimā, wa man taṭawwa\'a khairan fa innallāha syākirun \'alīm'),
(165, 2, 159, 'اِنَّ الَّذِيْنَ يَكْتُمُوْنَ مَآ اَنْزَلْنَا مِنَ الْبَيِّنٰتِ وَالْهُدٰى مِنْۢ بَعْدِ مَا بَيَّنّٰهُ لِلنَّاسِ فِى الْكِتٰبِۙ اُولٰۤىِٕكَ يَلْعَنُهُمُ اللّٰهُ وَيَلْعَنُهُمُ اللّٰعِنُوْنَۙ', 'innallażīna yaktumụna mā anzalnā minal-bayyināti wal-hudā mim ba\'di mā bayyannāhu lin-nāsi fil-kitābi ulā`ika yal\'anuhumullāhu wa yal\'anuhumul-lā\'inụn'),
(166, 2, 160, 'اِلَّا الَّذِيْنَ تَابُوْا وَاَصْلَحُوْا وَبَيَّنُوْا فَاُولٰۤىِٕكَ اَتُوْبُ عَلَيْهِمْ ۚ وَاَنَا التَّوَّابُ الرَّحِيْمُ', 'illallażīna tābụ wa aṣlaḥụ wa bayyanụ fa ulā`ika atụbu \'alaihim, wa anat-tawwābur-raḥīm'),
(167, 2, 161, 'اِنَّ الَّذِيْنَ كَفَرُوْا وَمَاتُوْا وَهُمْ كُفَّارٌ اُولٰۤىِٕكَ عَلَيْهِمْ لَعْنَةُ اللّٰهِ وَالْمَلٰۤىِٕكَةِ وَالنَّاسِ اَجْمَعِيْنَۙ', 'innallażīna kafarụ wa mātụ wa hum kuffārun ulā`ika \'alaihim la\'natullāhi wal-malā`ikati wan-nāsi ajma\'īn'),
(168, 2, 162, 'خٰلِدِيْنَ فِيْهَا ۚ لَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنْظَرُوْنَ', 'khālidīna fīhā, lā yukhaffafu \'an-humul-\'ażābu wa lā hum yunẓarụn'),
(169, 2, 163, 'وَاِلٰهُكُمْ اِلٰهٌ وَّاحِدٌۚ لَآاِلٰهَ اِلَّا هُوَ الرَّحْمٰنُ الرَّحِيْمُ', 'wa ilāhukum ilāhuw wāḥid, lā ilāha illā huwar-raḥmānur-raḥīm'),
(170, 2, 164, 'اِنَّ فِيْ خَلْقِ السَّمٰوٰتِ وَالْاَرْضِ وَاخْتِلَافِ الَّيْلِ وَالنَّهَارِ وَالْفُلْكِ الَّتِيْ تَجْرِيْ فِى الْبَحْرِ بِمَا يَنْفَعُ النَّاسَ وَمَآ اَنْزَلَ اللّٰهُ مِنَ السَّمَاۤءِ مِنْ مَّاۤءٍ فَاَحْيَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَا وَبَثَّ فِيْهَا مِنْ كُلِّ دَاۤبَّةٍ ۖ وَّتَصْرِيْفِ الرِّيٰحِ وَالسَّحَابِ الْمُسَخَّرِ بَيْنَ السَّمَاۤءِ وَالْاَرْضِ لَاٰيٰتٍ لِّقَوْمٍ يَّعْقِلُوْنَ', 'inna fī khalqis-samāwāti wal-arḍi wakhtilāfil-laili wan-nahāri wal-fulkillatī tajrī fil-baḥri bimā yanfa\'un-nāsa wa mā anzalallāhu minas-samā`i mim mā`in fa aḥyā bihil-arḍa ba\'da mautihā wa baṡṡa fīhā ming kulli dābbatiw wa taṣrīfir-riyāḥi was-saḥābil-musakhkhari bainas-samā`i wal-arḍi la`āyātil liqaumiy ya\'qilụn'),
(171, 2, 165, 'وَمِنَ النَّاسِ مَنْ يَّتَّخِذُ مِنْ دُوْنِ اللّٰهِ اَنْدَادًا يُّحِبُّوْنَهُمْ كَحُبِّ اللّٰهِ ۗ وَالَّذِيْنَ اٰمَنُوْٓا اَشَدُّ حُبًّا لِّلّٰهِ ۙوَلَوْ يَرَى الَّذِيْنَ ظَلَمُوْٓا اِذْ يَرَوْنَ الْعَذَابَۙ اَنَّ الْقُوَّةَ لِلّٰهِ جَمِيْعًا ۙوَّاَنَّ اللّٰهَ شَدِيْدُ الْعَذَابِ', 'wa minan-nāsi may yattakhiżu min dụnillāhi andāday yuḥibbụnahum kaḥubbillāh, wallażīna āmanū asyaddu ḥubbal lillāhi walau yarallażīna ẓalamū iż yaraunal-\'ażāba annal-quwwata lillāhi jamī\'aw wa annallāha syadīdul-\'ażāb'),
(172, 2, 166, 'اِذْ تَبَرَّاَ الَّذِيْنَ اتُّبِعُوْا مِنَ الَّذِيْنَ اتَّبَعُوْا وَرَاَوُا الْعَذَابَ وَتَقَطَّعَتْ بِهِمُ الْاَسْبَابُ', 'iż tabarra`allażīnattubi\'ụ minallażīnattaba\'ụ wa ra`awul-\'ażāba wa taqaṭṭa\'at bihimul-asbāb'),
(173, 2, 167, 'وَقَالَ الَّذِيْنَ اتَّبَعُوْا لَوْ اَنَّ لَنَا كَرَّةً فَنَتَبَرَّاَ مِنْهُمْ ۗ كَمَا تَبَرَّءُوْا مِنَّا ۗ كَذٰلِكَ يُرِيْهِمُ اللّٰهُ اَعْمَالَهُمْ حَسَرٰتٍ عَلَيْهِمْ ۗ وَمَا هُمْ بِخَارِجِيْنَ مِنَ النَّارِ', 'wa qālallażīnattaba\'ụ lau anna lanā karratan fa natabarra`a min-hum, kamā tabarra`ụ minnā, każālika yurīhimullāhu a\'mālahum ḥasarātin \'alaihim, wa mā hum bikhārijīna minan-nār'),
(174, 2, 168, 'يٰٓاَيُّهَا النَّاسُ كُلُوْا مِمَّا فِى الْاَرْضِ حَلٰلًا طَيِّبًا ۖوَّلَا تَتَّبِعُوْا خُطُوٰتِ الشَّيْطٰنِۗ اِنَّهٗ لَكُمْ عَدُوٌّ مُّبِيْنٌ', 'yā ayyuhan-nāsu kulụ mimmā fil-arḍi ḥalālan ṭayyibaw wa lā tattabi\'ụ khuṭuwātisy-syaiṭān, innahụ lakum \'aduwwum mubīn'),
(175, 2, 169, 'اِنَّمَا يَأْمُرُكُمْ بِالسُّوْۤءِ وَالْفَحْشَاۤءِ وَاَنْ تَقُوْلُوْا عَلَى اللّٰهِ مَا لَا تَعْلَمُوْنَ', 'innamā ya`murukum bis-sū`i wal-faḥsyā`i wa an taqụlụ \'alallāhi mā lā ta\'lamụn'),
(176, 2, 170, 'وَاِذَا قِيْلَ لَهُمُ اتَّبِعُوْا مَآ اَنْزَلَ اللّٰهُ قَالُوْا بَلْ نَتَّبِعُ مَآ اَلْفَيْنَا عَلَيْهِ اٰبَاۤءَنَا ۗ اَوَلَوْ كَانَ اٰبَاۤؤُهُمْ لَا يَعْقِلُوْنَ شَيْـًٔا وَّلَا يَهْتَدُوْنَ', 'wa iżā qīla lahumuttabi\'ụ mā anzalallāhu qālụ bal nattabi\'u mā alfainā \'alaihi ābā`anā, a walau kāna ābā`uhum lā ya\'qilụna syai`aw wa lā yahtadụn'),
(177, 2, 171, 'وَمَثَلُ الَّذِيْنَ كَفَرُوْا كَمَثَلِ الَّذِيْ يَنْعِقُ بِمَا لَا يَسْمَعُ اِلَّا دُعَاۤءً وَّنِدَاۤءً ۗ صُمٌّ ۢ بُكْمٌ عُمْيٌ فَهُمْ لَا يَعْقِلُوْنَ', 'wa maṡalullażīna kafarụ kamaṡalillażī yan\'iqu bimā lā yasma\'u illā du\'ā`aw wa nidā`ā, ṣummum bukmun \'umyun fa hum lā ya\'qilụn'),
(178, 2, 172, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُلُوْا مِنْ طَيِّبٰتِ مَا رَزَقْنٰكُمْ وَاشْكُرُوْا لِلّٰهِ اِنْ كُنْتُمْ اِيَّاهُ تَعْبُدُوْنَ', 'yā ayyuhallażīna āmanụ kulụ min ṭayyibāti mā razaqnākum wasykurụ lillāhi ing kuntum iyyāhu ta\'budụn'),
(179, 2, 173, 'اِنَّمَا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَالدَّمَ وَلَحْمَ الْخِنْزِيْرِ وَمَآ اُهِلَّ بِهٖ لِغَيْرِ اللّٰهِ ۚ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَّلَا عَادٍ فَلَآ اِثْمَ عَلَيْهِ ۗ اِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'innamā ḥarrama \'alaikumul-maitata wad-dama wa laḥmal-khinzīri wa mā uhilla bihī ligairillāh, fa maniḍṭurra gaira bāgiw wa lā \'ādin fa lā iṡma \'alaīh, innallāha gafụrur raḥīm'),
(180, 2, 174, 'اِنَّ الَّذِيْنَ يَكْتُمُوْنَ مَآ اَنْزَلَ اللّٰهُ مِنَ الْكِتٰبِ وَيَشْتَرُوْنَ بِهٖ ثَمَنًا قَلِيْلًاۙ اُولٰۤىِٕكَ مَا يَأْكُلُوْنَ فِيْ بُطُوْنِهِمْ اِلَّا النَّارَ وَلَا يُكَلِّمُهُمُ اللّٰهُ يَوْمَ الْقِيٰمَةِ وَلَا يُزَكِّيْهِمْ ۚوَلَهُمْ عَذَابٌ اَلِيْمٌ', 'innallażīna yaktumụna mā anzalallāhu minal-kitābi wa yasytarụna bihī ṡamanang qalīlan ulā`ika mā ya`kulụna fī buṭụnihim illan-nāra wa lā yukallimuhumullāhu yaumal-qiyāmati wa lā yuzakkīhim, wa lahum \'ażābun alīm'),
(181, 2, 175, 'اُولٰۤىِٕكَ الَّذِيْنَ اشْتَرَوُا الضَّلٰلَةَ بِالْهُدٰى وَالْعَذَابَ بِالْمَغْفِرَةِ ۚ فَمَآ اَصْبَرَهُمْ عَلَى النَّارِ', 'ulā`ikallażīnasytarawuḍ-ḍalālata bil-hudā wal-\'ażāba bil-magfirah, fa mā aṣbarahum \'alan-nār'),
(182, 2, 176, 'ذٰلِكَ بِاَنَّ اللّٰهَ نَزَّلَ الْكِتٰبَ بِالْحَقِّ ۗ وَاِنَّ الَّذِيْنَ اخْتَلَفُوْا فِى الْكِتٰبِ لَفِيْ شِقَاقٍۢ بَعِيْدٍ', 'żālika bi`annallāha nazzalal-kitāba bil-ḥaqq, wa innallażīnakhtalafụ fil-kitābi lafī syiqāqim ba\'īd'),
(183, 2, 177, '۞ لَيْسَ الْبِرَّاَنْ تُوَلُّوْا وُجُوْهَكُمْ قِبَلَ الْمَشْرِقِ وَالْمَغْرِبِ وَلٰكِنَّ الْبِرَّ مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَالْمَلٰۤىِٕكَةِ وَالْكِتٰبِ وَالنَّبِيّٖنَ ۚ وَاٰتَى الْمَالَ عَلٰى حُبِّهٖ ذَوِى الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنَ وَابْنَ السَّبِيْلِۙ وَالسَّاۤىِٕلِيْنَ وَفىِ الرِّقَابِۚ وَاَقَامَ الصَّلٰوةَ وَاٰتَى الزَّكٰوةَ ۚ وَالْمُوْفُوْنَ بِعَهْدِهِمْ اِذَا عَاهَدُوْا ۚ وَالصّٰبِرِيْنَ فِى الْبَأْسَاۤءِ وَالضَّرَّاۤءِ وَحِيْنَ الْبَأْسِۗ اُولٰۤىِٕكَ الَّذِيْنَ صَدَقُوْا ۗوَاُولٰۤىِٕكَ هُمُ الْمُتَّقُوْنَ', 'laisal-birra an tuwallụ wujụhakum qibalal-masyriqi wal-magribi wa lākinnal-birra man āmana billāhi wal-yaumil-ākhiri wal-malā`ikati wal-kitābi wan-nabiyyīn, wa ātal-māla \'alā ḥubbihī żawil-qurbā wal-yatāmā wal-masākīna wabnas-sabīli was-sā`ilīna wa fir-riqāb, wa aqāmaṣ-ṣalāta wa ātaz-zakāh, wal-mụfụna bi\'ahdihim iżā \'āhadụ, waṣ-ṣābirīna fil-ba`sā`i waḍ-ḍarrā`i wa ḥīnal-ba`s, ulā`ikallażīna ṣadaqụ, wa ulā`ika humul-muttaqụn'),
(184, 2, 178, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُتِبَ عَلَيْكُمُ الْقِصَاصُ فِى الْقَتْلٰىۗ اَلْحُرُّ بِالْحُرِّ وَالْعَبْدُ بِالْعَبْدِ وَالْاُنْثٰى بِالْاُنْثٰىۗ فَمَنْ عُفِيَ لَهٗ مِنْ اَخِيْهِ شَيْءٌ فَاتِّبَاعٌ ۢبِالْمَعْرُوْفِ وَاَدَاۤءٌ اِلَيْهِ بِاِحْسَانٍ ۗ ذٰلِكَ تَخْفِيْفٌ مِّنْ رَّبِّكُمْ وَرَحْمَةٌ ۗفَمَنِ اعْتَدٰى بَعْدَ ذٰلِكَ فَلَهٗ عَذَابٌ اَلِيْمٌ', 'yā ayyuhallażīna āmanụ kutiba \'alaikumul-qiṣāṣu fil-qatlā, al-ḥurru bil-ḥurri wal-\'abdu bil-\'abdi wal-unṡā bil-unṡā, fa man \'ufiya lahụ min akhīhi syai`un fattibā\'um bil-ma\'rụfi wa adā`un ilaihi bi`iḥsān, żālika takhfīfum mir rabbikum wa raḥmah, fa mani\'tadā ba\'da żālika fa lahụ \'ażābun alīm'),
(185, 2, 179, 'وَلَكُمْ فِى الْقِصَاصِ حَيٰوةٌ يّٰٓاُولِى الْاَلْبَابِ لَعَلَّكُمْ تَتَّقُوْنَ', 'wa lakum fil-qiṣāṣi ḥayātuy yā ulil-albābi la\'allakum tattaqụn'),
(186, 2, 180, 'كُتِبَ عَلَيْكُمْ اِذَا حَضَرَ اَحَدَكُمُ الْمَوْتُ اِنْ تَرَكَ خَيْرًا ۖ ۨالْوَصِيَّةُ لِلْوَالِدَيْنِ وَالْاَقْرَبِيْنَ بِالْمَعْرُوْفِۚ حَقًّا عَلَى الْمُتَّقِيْنَ ۗ', 'kutiba \'alaikum iżā ḥaḍara aḥadakumul-mautu in taraka khairanil-waṣiyyatu lil-wālidaini wal-aqrabīna bil-ma\'rụf, ḥaqqan \'alal-muttaqīn'),
(187, 2, 181, 'فَمَنْۢ بَدَّلَهٗ بَعْدَمَا سَمِعَهٗ فَاِنَّمَآ اِثْمُهٗ عَلَى الَّذِيْنَ يُبَدِّلُوْنَهٗ ۗ اِنَّ اللّٰهَ سَمِيْعٌ عَلِيْمٌ ۗ', 'fa mam baddalahụ ba\'da mā sami\'ahụ fa innamā iṡmuhụ \'alallażīna yubaddilụnah, innallāha samī\'un \'alīm'),
(188, 2, 182, 'فَمَنْ خَافَ مِنْ مُّوْصٍ جَنَفًا اَوْ اِثْمًا فَاَصْلَحَ بَيْنَهُمْ فَلَآ اِثْمَ عَلَيْهِ ۗ اِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'fa man khāfa mim mụṣin janafan au iṡman fa aṣlaḥa bainahum fa lā iṡma \'alaīh, innallāha gafụrur raḥīm'),
(189, 2, 183, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُتِبَ عَلَيْكُمُ الصِّيَامُ كَمَا كُتِبَ عَلَى الَّذِيْنَ مِنْ قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُوْنَۙ', 'yā ayyuhallażīna āmanụ kutiba \'alaikumuṣ-ṣiyāmu kamā kutiba \'alallażīna ming qablikum la\'allakum tattaqụn'),
(190, 2, 184, 'اَيَّامًا مَّعْدُوْدٰتٍۗ فَمَنْ كَانَ مِنْكُمْ مَّرِيْضًا اَوْ عَلٰى سَفَرٍ فَعِدَّةٌ مِّنْ اَيَّامٍ اُخَرَ ۗ وَعَلَى الَّذِيْنَ يُطِيْقُوْنَهٗ فِدْيَةٌ طَعَامُ مِسْكِيْنٍۗ فَمَنْ تَطَوَّعَ خَيْرًا فَهُوَ خَيْرٌ لَّهٗ ۗ وَاَنْ تَصُوْمُوْا خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ', 'ayyāmam ma\'dụdāt, fa mang kāna mingkum marīḍan au \'alā safarin fa \'iddatum min ayyāmin ukhar, wa \'alallażīna yuṭīqụnahụ fidyatun ṭa\'āmu miskīn, fa man taṭawwa\'a khairan fa huwa khairul lah, wa an taṣụmụ khairul lakum ing kuntum ta\'lamụn'),
(191, 2, 185, 'شَهْرُ رَمَضَانَ الَّذِيْٓ اُنْزِلَ فِيْهِ الْقُرْاٰنُ هُدًى لِّلنَّاسِ وَبَيِّنٰتٍ مِّنَ الْهُدٰى وَالْفُرْقَانِۚ فَمَنْ شَهِدَ مِنْكُمُ الشَّهْرَ فَلْيَصُمْهُ ۗ وَمَنْ كَانَ مَرِيْضًا اَوْ عَلٰى سَفَرٍ فَعِدَّةٌ مِّنْ اَيَّامٍ اُخَرَ ۗ يُرِيْدُ اللّٰهُ بِكُمُ الْيُسْرَ وَلَا يُرِيْدُ بِكُمُ الْعُسْرَ ۖ وَلِتُكْمِلُوا الْعِدَّةَ وَلِتُكَبِّرُوا اللّٰهَ عَلٰى مَا هَدٰىكُمْ وَلَعَلَّكُمْ تَشْكُرُوْنَ', 'syahru ramaḍānallażī unzila fīhil-qur`ānu hudal lin-nāsi wa bayyinātim minal-hudā wal-furqān, fa man syahida mingkumusy-syahra falyaṣum-h, wa mang kāna marīḍan au \'alā safarin fa \'iddatum min ayyāmin ukhar, yurīdullāhu bikumul-yusra wa lā yurīdu bikumul-\'usra wa litukmilul-\'iddata wa litukabbirullāha \'alā mā hadākum wa la\'allakum tasykurụn'),
(192, 2, 186, 'وَاِذَا سَاَلَكَ عِبَادِيْ عَنِّيْ فَاِنِّيْ قَرِيْبٌ ۗ اُجِيْبُ دَعْوَةَ الدَّاعِ اِذَا دَعَانِۙ فَلْيَسْتَجِيْبُوْا لِيْ وَلْيُؤْمِنُوْا بِيْ لَعَلَّهُمْ يَرْشُدُوْنَ', 'wa iżā sa`alaka \'ibādī \'annī fa innī qarīb, ujību da\'watad-dā\'i iżā da\'āni falyastajībụ lī walyu`minụ bī la\'allahum yarsyudụn'),
(193, 2, 187, 'اُحِلَّ لَكُمْ لَيْلَةَ الصِّيَامِ الرَّفَثُ اِلٰى نِسَاۤىِٕكُمْ ۗ هُنَّ لِبَاسٌ لَّكُمْ وَاَنْتُمْ لِبَاسٌ لَّهُنَّ ۗ عَلِمَ اللّٰهُ اَنَّكُمْ كُنْتُمْ تَخْتَانُوْنَ اَنْفُسَكُمْ فَتَابَ عَلَيْكُمْ وَعَفَا عَنْكُمْ ۚ فَالْـٰٔنَ بَاشِرُوْهُنَّ وَابْتَغُوْا مَا كَتَبَ اللّٰهُ لَكُمْ ۗ وَكُلُوْا وَاشْرَبُوْا حَتّٰى يَتَبَيَّنَ لَكُمُ الْخَيْطُ الْاَبْيَضُ مِنَ الْخَيْطِ الْاَسْوَدِ مِنَ الْفَجْرِۖ ثُمَّ اَتِمُّوا الصِّيَامَ اِلَى الَّيْلِۚ وَلَا تُبَاشِرُوْهُنَّ وَاَنْتُمْ عَاكِفُوْنَۙ فِى الْمَسٰجِدِ ۗ تِلْكَ حُدُوْدُ اللّٰهِ فَلَا تَقْرَبُوْهَاۗ كَذٰلِكَ يُبَيِّنُ اللّٰهُ اٰيٰتِهٖ لِلنَّاسِ لَعَلَّهُمْ يَتَّقُوْنَ', 'uḥilla lakum lailataṣ-ṣiyāmir-rafaṡu ilā nisā`ikum, hunna libāsul lakum wa antum libāsul lahunn, \'alimallāhu annakum kuntum takhtānụna anfusakum fa tāba \'alaikum wa \'afā \'angkum, fal-āna bāsyirụhunna wabtagụ mā kataballāhu lakum, wa kulụ wasyrabụ ḥattā yatabayyana lakumul-khaiṭul-abyaḍu minal-khaiṭil-aswadi minal-fajr, ṡumma atimmuṣ-ṣiyāma ilal-laīl, wa lā tubāsyirụhunna wa antum \'ākifụna fil-masājid, tilka ḥudụdullāhi fa lā taqrabụhā, każālika yubayyinullāhu āyātihī lin-nāsi la\'allahum yattaqụn'),
(194, 2, 188, 'وَلَا تَأْكُلُوْٓا اَمْوَالَكُمْ بَيْنَكُمْ بِالْبَاطِلِ وَتُدْلُوْا بِهَآ اِلَى الْحُكَّامِ لِتَأْكُلُوْا فَرِيْقًا مِّنْ اَمْوَالِ النَّاسِ بِالْاِثْمِ وَاَنْتُمْ تَعْلَمُوْنَ', 'wa lā ta`kulū amwālakum bainakum bil-bāṭili wa tudlụ bihā ilal-ḥukkāmi lita`kulụ farīqam min amwālin-nāsi bil-iṡmi wa antum ta\'lamụn'),
(195, 2, 189, '۞ يَسـَٔلُوْنَكَ عَنِ الْاَهِلَّةِ ۗ قُلْ هِيَ مَوَاقِيْتُ لِلنَّاسِ وَالْحَجِّ ۗ وَلَيْسَ الْبِرُّ بِاَنْ تَأْتُوا الْبُيُوْتَ مِنْ ظُهُوْرِهَا وَلٰكِنَّ الْبِرَّ مَنِ اتَّقٰىۚ وَأْتُوا الْبُيُوْتَ مِنْ اَبْوَابِهَا ۖ وَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُفْلِحُوْنَ', 'yas`alụnaka \'anil-ahillah, qul hiya mawāqītu lin-nāsi wal-ḥajj, wa laisal-birru bi`an ta`tul-buyụta min ẓuhụrihā wa lākinnal-birra manittaqā, wa`tul-buyụta min abwābihā wattaqullāha la\'allakum tufliḥụn'),
(196, 2, 190, 'وَقَاتِلُوْا فِيْ سَبِيْلِ اللّٰهِ الَّذِيْنَ يُقَاتِلُوْنَكُمْ وَلَا تَعْتَدُوْا ۗ اِنَّ اللّٰهَ لَا يُحِبُّ الْمُعْتَدِيْنَ', 'wa qātilụ fī sabīlillāhillażīna yuqātilụnakum wa lā ta\'tadụ, innallāha lā yuḥibbul-mu\'tadīn'),
(197, 2, 191, 'وَاقْتُلُوْهُمْ حَيْثُ ثَقِفْتُمُوْهُمْ وَاَخْرِجُوْهُمْ مِّنْ حَيْثُ اَخْرَجُوْكُمْ وَالْفِتْنَةُ اَشَدُّ مِنَ الْقَتْلِ ۚ وَلَا تُقَاتِلُوْهُمْ عِنْدَ الْمَسْجِدِ الْحَرَامِ حَتّٰى يُقٰتِلُوْكُمْ فِيْهِۚ فَاِنْ قٰتَلُوْكُمْ فَاقْتُلُوْهُمْۗ كَذٰلِكَ جَزَاۤءُ الْكٰفِرِيْنَ', 'waqtulụhum ḥaiṡu ṡaqiftumụhum wa akhrijụhum min ḥaiṡu akhrajụkum wal-fitnatu asyaddu minal-qatl, wa lā tuqātilụhum \'indal-masjidil-ḥarāmi ḥattā yuqātilụkum fīh, fa ing qātalụkum faqtulụhum, każālika jazā`ul-kāfirīn'),
(198, 2, 192, 'فَاِنِ انْتَهَوْا فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'fa inintahau fa innallāha gafụrur raḥīm'),
(199, 2, 193, 'وَقٰتِلُوْهُمْ حَتّٰى لَا تَكُوْنَ فِتْنَةٌ وَّيَكُوْنَ الدِّيْنُ لِلّٰهِ ۗ فَاِنِ انْتَهَوْا فَلَا عُدْوَانَ اِلَّا عَلَى الظّٰلِمِيْنَ', 'wa qātilụhum ḥattā lā takụna fitnatuw wa yakụnad-dīnu lillāh, fa inintahau fa lā \'udwāna illā \'alaẓ-ẓālimīn'),
(200, 2, 194, 'اَلشَّهْرُ الْحَرَامُ بِالشَّهْرِ الْحَرَامِ وَالْحُرُمٰتُ قِصَاصٌۗ فَمَنِ اعْتَدٰى عَلَيْكُمْ فَاعْتَدُوْا عَلَيْهِ بِمِثْلِ مَا اعْتَدٰى عَلَيْكُمْ ۖ وَاتَّقُوا اللّٰهَ وَاعْلَمُوْٓا اَنَّ اللّٰهَ مَعَ الْمُتَّقِيْنَ', 'asy-syahrul-ḥarāmu bisy-syahril-ḥarāmi wal-ḥurumātu qiṣāṣ, fa mani\'tadā \'alaikum fa\'tadụ \'alaihi bimiṡli ma\'tadā \'alaikum wattaqullāha wa\'lamū annallāha ma\'al-muttaqīn'),
(201, 2, 195, 'وَاَنْفِقُوْا فِيْ سَبِيْلِ اللّٰهِ وَلَا تُلْقُوْا بِاَيْدِيْكُمْ اِلَى التَّهْلُكَةِ ۛ وَاَحْسِنُوْا ۛ اِنَّ اللّٰهَ يُحِبُّ الْمُحْسِنِيْنَ', 'wa anfiqụ fī sabīlillāhi wa lā tulqụ bi`aidīkum ilat-tahlukati wa aḥsinụ, innallāha yuḥibbul-muḥsinīn'),
(202, 2, 196, 'وَاَتِمُّوا الْحَجَّ وَالْعُمْرَةَ لِلّٰهِ ۗ فَاِنْ اُحْصِرْتُمْ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِۚ وَلَا تَحْلِقُوْا رُءُوْسَكُمْ حَتّٰى يَبْلُغَ الْهَدْيُ مَحِلَّهٗ ۗ فَمَنْ كَانَ مِنْكُمْ مَّرِيْضًا اَوْ بِهٖٓ اَذًى مِّنْ رَّأْسِهٖ فَفِدْيَةٌ مِّنْ صِيَامٍ اَوْ صَدَقَةٍ اَوْ نُسُكٍ ۚ فَاِذَآ اَمِنْتُمْ ۗ فَمَنْ تَمَتَّعَ بِالْعُمْرَةِ اِلَى الْحَجِّ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِۚ فَمَنْ لَّمْ يَجِدْ فَصِيَامُ ثَلٰثَةِ اَيَّامٍ فِى الْحَجِّ وَسَبْعَةٍ اِذَا رَجَعْتُمْ ۗ تِلْكَ عَشَرَةٌ كَامِلَةٌ ۗذٰلِكَ لِمَنْ لَّمْ يَكُنْ اَهْلُهٗ حَاضِرِى الْمَسْجِدِ الْحَرَامِ ۗ وَاتَّقُوا اللّٰهَ وَاعْلَمُوْٓا اَنَّ اللّٰهَ شَدِيْدُ الْعِقَابِ', 'wa atimmul-ḥajja wal-\'umrata lillāh, fa in uḥṣirtum fa mastaisara minal-hady, wa lā taḥliqụ ru`ụsakum ḥattā yablugal-hadyu maḥillah, fa mang kāna mingkum marīḍan au bihī ażam mir ra`sihī fa fidyatum min ṣiyāmin au ṣadaqatin au nusuk, fa iżā amintum, fa man tamatta\'a bil-\'umrati ilal-ḥajji fa mastaisara minal-hady, fa mal lam yajid fa ṣiyāmu ṡalāṡati ayyāmin fil-ḥajji wa sab\'atin iżā raja\'tum, tilka \'asyaratung kāmilah, żālika limal lam yakun ahluhụ ḥāḍiril-masjidil-ḥarām, wattaqullāha wa\'lamū annallāha syadīdul-\'iqāb'),
(203, 2, 197, 'اَلْحَجُّ اَشْهُرٌ مَّعْلُوْمٰتٌ ۚ فَمَنْ فَرَضَ فِيْهِنَّ الْحَجَّ فَلَا رَفَثَ وَلَا فُسُوْقَ وَلَا جِدَالَ فِى الْحَجِّ ۗ وَمَا تَفْعَلُوْا مِنْ خَيْرٍ يَّعْلَمْهُ اللّٰهُ ۗ وَتَزَوَّدُوْا فَاِنَّ خَيْرَ الزَّادِ التَّقْوٰىۖ وَاتَّقُوْنِ يٰٓاُولِى الْاَلْبَابِ', 'al-ḥajju asy-hurum ma\'lụmāt, fa man faraḍa fīhinnal-ḥajja fa lā rafaṡa wa lā fusụqa wa lā jidāla fil-ḥajj, wa mā taf\'alụ min khairiy ya\'lam-hullāh, wa tazawwadụ fa inna khairaz-zādit-taqwā wattaqụni yā ulil-albāb'),
(204, 2, 198, 'لَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَبْتَغُوْا فَضْلًا مِّنْ رَّبِّكُمْ ۗ فَاِذَآ اَفَضْتُمْ مِّنْ عَرَفَاتٍ فَاذْكُرُوا اللّٰهَ عِنْدَ الْمَشْعَرِ الْحَرَامِ ۖ وَاذْكُرُوْهُ كَمَا هَدٰىكُمْ ۚ وَاِنْ كُنْتُمْ مِّنْ قَبْلِهٖ لَمِنَ الضَّاۤلِّيْنَ', 'laisa \'alaikum junāḥun an tabtagụ faḍlam mir rabbikum, fa iżā afaḍtum min \'arafātin fażkurullāha \'indal-masy\'aril-ḥarāmi ważkurụhu kamā hadākum, wa ing kuntum ming qablihī laminaḍ-ḍāllīn'),
(205, 2, 199, 'ثُمَّ اَفِيْضُوْا مِنْ حَيْثُ اَفَاضَ النَّاسُ وَاسْتَغْفِرُوا اللّٰهَ ۗ اِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'ṡumma afīḍụ min ḥaiṡu afāḍan-nāsu wastagfirullāh, innallāha gafụrur raḥīm'),
(206, 2, 200, 'فَاِذَا قَضَيْتُمْ مَّنَاسِكَكُمْ فَاذْكُرُوا اللّٰهَ كَذِكْرِكُمْ اٰبَاۤءَكُمْ اَوْ اَشَدَّ ذِكْرًا ۗ فَمِنَ النَّاسِ مَنْ يَّقُوْلُ رَبَّنَآ اٰتِنَا فِى الدُّنْيَا وَمَا لَهٗ فِى الْاٰخِرَةِ مِنْ خَلَاقٍ', 'fa iżā qaḍaitum manasikakum fażkurullāha każikrikum ābā`akum au asyadda żikrā, fa minan-nāsi may yaqụlu rabbanā ātinā fid-dun-yā wa mā lahụ fil-ākhirati min khalāq'),
(207, 2, 201, 'وَمِنْهُمْ مَّنْ يَّقُوْلُ رَبَّنَآ اٰتِنَا فِى الدُّنْيَا حَسَنَةً وَّفِى الْاٰخِرَةِ حَسَنَةً وَّقِنَا عَذَابَ النَّارِ', 'wa min-hum may yaqụlu rabbanā ātinā fid-dun-yā ḥasanataw wa fil-ākhirati ḥasanataw wa qinā \'ażāban-nār'),
(208, 2, 202, 'اُولٰۤىِٕكَ لَهُمْ نَصِيْبٌ مِّمَّا كَسَبُوْا ۗ وَاللّٰهُ سَرِيْعُ الْحِسَابِ', 'ulā`ika lahum naṣībum mimmā kasabụ, wallāhu sarī\'ul-ḥisāb'),
(209, 2, 203, '۞ وَاذْكُرُوا اللّٰهَ فِيْٓ اَيَّامٍ مَّعْدُوْدٰتٍ ۗ فَمَنْ تَعَجَّلَ فِيْ يَوْمَيْنِ فَلَآ اِثْمَ عَلَيْهِ ۚوَمَنْ تَاَخَّرَ فَلَآ اِثْمَ عَلَيْهِۙ لِمَنِ اتَّقٰىۗ وَاتَّقُوا اللّٰهَ وَاعْلَمُوْٓا اَنَّكُمْ اِلَيْهِ تُحْشَرُوْنَ', 'ważkurullāha fī ayyāmim ma\'dụdāt, fa man ta\'ajjala fī yaumaini fa lā iṡma \'alaīh, wa man ta`akhkhara fa lā iṡma \'alaihi limanittaqā, wattaqullāha wa\'lamū annakum ilaihi tuḥsyarụn'),
(210, 2, 204, 'وَمِنَ النَّاسِ مَنْ يُّعْجِبُكَ قَوْلُهٗ فِى الْحَيٰوةِ الدُّنْيَا وَيُشْهِدُ اللّٰهَ عَلٰى مَا فِيْ قَلْبِهٖ ۙ وَهُوَ اَلَدُّ الْخِصَامِ', 'wa minan-nāsi may yu\'jibuka qauluhụ fil-ḥayātid-dun-yā wa yusy-hidullāha \'alā mā fī qalbihī wa huwa aladdul-khiṣām'),
(211, 2, 205, 'وَاِذَا تَوَلّٰى سَعٰى فِى الْاَرْضِ لِيُفْسِدَ فِيْهَا وَيُهْلِكَ الْحَرْثَ وَالنَّسْلَ ۗ وَ اللّٰهُ لَا يُحِبُّ الْفَسَادَ', 'wa iżā tawallā sa\'ā fil-arḍi liyufsida fīhā wa yuhlikal-ḥarṡa wan-nasl, wallāhu lā yuḥibbul-fasād'),
(212, 2, 206, 'وَاِذَا قِيْلَ لَهُ اتَّقِ اللّٰهَ اَخَذَتْهُ الْعِزَّةُ بِالْاِثْمِ فَحَسْبُهٗ جَهَنَّمُ ۗ وَلَبِئْسَ الْمِهَادُ', 'wa iżā qīla lahuttaqillāha akhażat-hul-\'izzatu bil-iṡmi fa ḥasbuhụ jahannam, wa labi`sal-mihād'),
(213, 2, 207, 'وَمِنَ النَّاسِ مَنْ يَّشْرِيْ نَفْسَهُ ابْتِغَاۤءَ مَرْضَاتِ اللّٰهِ ۗوَاللّٰهُ رَءُوْفٌۢ بِالْعِبَادِ', 'wa minan-nāsi may yasyrī nafsahubtigā`a marḍātillāh, wallāhu ra`ụfum bil-\'ibād'),
(214, 2, 208, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا ادْخُلُوْا فِى السِّلْمِ كَاۤفَّةً ۖوَّلَا تَتَّبِعُوْا خُطُوٰتِ الشَّيْطٰنِۗ اِنَّهٗ لَكُمْ عَدُوٌّ مُّبِيْنٌ', 'yā ayyuhallażīna āmanudkhulụ fis-silmi kāffataw wa lā tattabi\'ụ khuṭuwātisy-syaiṭān, innahụ lakum \'aduwwum mubīn'),
(215, 2, 209, 'فَاِنْ زَلَلْتُمْ مِّنْۢ بَعْدِ مَا جَاۤءَتْكُمُ الْبَيِّنٰتُ فَاعْلَمُوْٓا اَنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ', 'fa in zalaltum mim ba\'di mā jā`atkumul-bayyinātu fa\'lamū annallāha \'azīzun ḥakīm'),
(216, 2, 210, 'هَلْ يَنْظُرُوْنَ اِلَّآ اَنْ يَّأْتِيَهُمُ اللّٰهُ فِيْ ظُلَلٍ مِّنَ الْغَمَامِ وَالْمَلٰۤىِٕكَةُ وَقُضِيَ الْاَمْرُ ۗ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ', 'hal yanẓurụna illā ay ya`tiyahumullāhu fī ẓulalim minal-gamāmi wal-malā`ikatu wa quḍiyal-amr, wa ilallāhi turja\'ul-umụr'),
(217, 2, 211, 'سَلْ بَنِيْٓ اِسْرَاۤءِيْلَ كَمْ اٰتَيْنٰهُمْ مِّنْ اٰيَةٍ ۢ بَيِّنَةٍ ۗ وَمَنْ يُّبَدِّلْ نِعْمَةَ اللّٰهِ مِنْۢ بَعْدِ مَا جَاۤءَتْهُ فَاِنَّ اللّٰهَ شَدِيْدُ الْعِقَابِ', 'sal banī isrā`īla kam ātaināhum min āyatim bayyinah, wa may yubaddil ni\'matallāhi mim ba\'di mā jā`at-hu fa innallāha syadīdul-\'iqāb'),
(218, 2, 212, 'زُيِّنَ لِلَّذِيْنَ كَفَرُوا الْحَيٰوةُ الدُّنْيَا وَيَسْخَرُوْنَ مِنَ الَّذِيْنَ اٰمَنُوْا ۘ وَالَّذِيْنَ اتَّقَوْا فَوْقَهُمْ يَوْمَ الْقِيٰمَةِ ۗ وَاللّٰهُ يَرْزُقُ مَنْ يَّشَاۤءُ بِغَيْرِ حِسَابٍ', 'zuyyina lillażīna kafarul-ḥayātud-dun-yā wa yaskharụna minallażīna āmanụ, wallażīnattaqau fauqahum yaumal-qiyāmah, wallāhu yarzuqu may yasyā`u bigairi ḥisāb'),
(219, 2, 213, 'كَانَ النَّاسُ اُمَّةً وَّاحِدَةً ۗ فَبَعَثَ اللّٰهُ النَّبِيّٖنَ مُبَشِّرِيْنَ وَمُنْذِرِيْنَ ۖ وَاَنْزَلَ مَعَهُمُ الْكِتٰبَ بِالْحَقِّ لِيَحْكُمَ بَيْنَ النَّاسِ فِيْمَا اخْتَلَفُوْا فِيْهِ ۗ وَمَا اخْتَلَفَ فِيْهِ اِلَّا الَّذِيْنَ اُوْتُوْهُ مِنْۢ بَعْدِ مَا جَاۤءَتْهُمُ الْبَيِّنٰتُ بَغْيًا ۢ بَيْنَهُمْ ۚ فَهَدَى اللّٰهُ الَّذِيْنَ اٰمَنُوْا لِمَا اخْتَلَفُوْا فِيْهِ مِنَ الْحَقِّ بِاِذْنِهٖ ۗ وَاللّٰهُ يَهْدِيْ مَنْ يَّشَاۤءُ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ', 'kānan-nāsu ummataw wāḥidah, fa ba\'aṡallāhun-nabiyyīna mubasysyirīna wa munżirīna wa anzala ma\'ahumul-kitāba bil-ḥaqqi liyaḥkuma bainan-nāsi fīmakhtalafụ fīh, wa makhtalafa fīhi illallażīna ụtụhu mim ba\'di mā jā`at-humul-bayyinātu bagyam bainahum, fa hadallāhullażīna āmanụ limakhtalafụ fīhi minal-ḥaqqi bi`iżnih, wallāhu yahdī may yasyā`u ilā ṣirāṭim mustaqīm'),
(220, 2, 214, 'اَمْ حَسِبْتُمْ اَنْ تَدْخُلُوا الْجَنَّةَ وَلَمَّا يَأْتِكُمْ مَّثَلُ الَّذِيْنَ خَلَوْا مِنْ قَبْلِكُمْ ۗ مَسَّتْهُمُ الْبَأْسَاۤءُ وَالضَّرَّاۤءُ وَزُلْزِلُوْا حَتّٰى يَقُوْلَ الرَّسُوْلُ وَالَّذِيْنَ اٰمَنُوْا مَعَهٗ مَتٰى نَصْرُ اللّٰهِ ۗ اَلَآ اِنَّ نَصْرَ اللّٰهِ قَرِيْبٌ', 'am ḥasibtum an tadkhulul-jannata wa lammā ya`tikum maṡalullażīna khalau ming qablikum, massat-humul-ba`sā`u waḍ-ḍarrā`u wa zulzilụ ḥattā yaqụlar-rasụlu wallażīna āmanụ ma\'ahụ matā naṣrullāh, alā inna naṣrallāhi qarīb'),
(221, 2, 215, 'يَسْـَٔلُوْنَكَ مَاذَا يُنْفِقُوْنَ ۗ قُلْ مَآ اَنْفَقْتُمْ مِّنْ خَيْرٍ فَلِلْوَالِدَيْنِ وَالْاَقْرَبِيْنَ وَالْيَتٰمٰى وَالْمَسٰكِيْنِ وَابْنِ السَّبِيْلِ ۗ وَمَا تَفْعَلُوْا مِنْ خَيْرٍ فَاِنَّ اللّٰهَ بِهٖ عَلِيْمٌ', 'yas`alụnaka māżā yunfiqụn, qul mā anfaqtum ming kairin fa lil-wālidaini wal-aqrabīna wal-yatāmā wal-masākīni wabnis-sabīl, wa mā taf\'alụ min khairin fa innallāha bihī \'alīm'),
(222, 2, 216, 'كُتِبَ عَلَيْكُمُ الْقِتَالُ وَهُوَ كُرْهٌ لَّكُمْ ۚ وَعَسٰٓى اَنْ تَكْرَهُوْا شَيْـًٔا وَّهُوَ خَيْرٌ لَّكُمْ ۚ وَعَسٰٓى اَنْ تُحِبُّوْا شَيْـًٔا وَّهُوَ شَرٌّ لَّكُمْ ۗ وَاللّٰهُ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُوْنَ', 'kutiba \'alaikumul-qitālu wa huwa kur-hul lakum, wa \'asā an takrahụ syai`aw wa huwa khairul lakum, wa \'asā an tuḥibbụ syai`aw wa huwa syarrul lakum, wallāhu ya\'lamu wa antum lā ta\'lamụn'),
(223, 2, 217, 'يَسْـَٔلُوْنَكَ عَنِ الشَّهْرِ الْحَرَامِ قِتَالٍ فِيْهِۗ قُلْ قِتَالٌ فِيْهِ كَبِيْرٌ ۗ وَصَدٌّ عَنْ سَبِيْلِ اللّٰهِ وَكُفْرٌۢ بِهٖ وَالْمَسْجِدِ الْحَرَامِ وَاِخْرَاجُ اَهْلِهٖ مِنْهُ اَكْبَرُ عِنْدَ اللّٰهِ ۚ وَالْفِتْنَةُ اَكْبَرُ مِنَ الْقَتْلِ ۗ وَلَا يَزَالُوْنَ يُقَاتِلُوْنَكُمْ حَتّٰى يَرُدُّوْكُمْ عَنْ دِيْنِكُمْ اِنِ اسْتَطَاعُوْا ۗ وَمَنْ يَّرْتَدِدْ مِنْكُمْ عَنْ دِيْنِهٖ فَيَمُتْ وَهُوَ كَافِرٌ فَاُولٰۤىِٕكَ حَبِطَتْ اَعْمَالُهُمْ فِى الدُّنْيَا وَالْاٰخِرَةِ ۚ وَاُولٰۤىِٕكَ اَصْحٰبُ النَّارِۚ هُمْ فِيْهَا خٰلِدُوْنَ', 'yas`alụnaka \'anisy-syahril-ḥarāmi qitālin fīh, qul qitālun fīhi kabīr, wa ṣaddun \'an sabīlillāhi wa kufrum bihī wal-masjidil-ḥarāmi wa ikhrāju ahlihī min-hu akbaru \'indallāh, wal-fitnatu akbaru minal-qatl, wa lā yazālụna yuqātilụnakum ḥattā yaruddụkum \'an dīnikum inistaṭā\'ụ, wa may yartadid mingkum \'an dīnihī fa yamut wa huwa kāfirun fa ulā`ika ḥabiṭat a\'māluhum fid-dun-yā wal-ākhirah, wa ulā`ika aṣ-ḥābun-nār, hum fīhā khālidụn'),
(224, 2, 218, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَالَّذِيْنَ هَاجَرُوْا وَجَاهَدُوْا فِيْ سَبِيْلِ اللّٰهِ ۙ اُولٰۤىِٕكَ يَرْجُوْنَ رَحْمَتَ اللّٰهِ ۗوَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ', 'innallażīna āmanụ wallażīna hājarụ wa jāhadụ fī sabīlillāhi ulā`ika yarjụna raḥmatallāh, wallāhu gafụrur raḥīm'),
(225, 2, 219, '۞ يَسْـَٔلُوْنَكَ عَنِ الْخَمْرِ وَالْمَيْسِرِۗ قُلْ فِيْهِمَآ اِثْمٌ كَبِيْرٌ وَّمَنَافِعُ لِلنَّاسِۖ وَاِثْمُهُمَآ اَكْبَرُ مِنْ نَّفْعِهِمَاۗ وَيَسْـَٔلُوْنَكَ مَاذَا يُنْفِقُوْنَ ەۗ قُلِ الْعَفْوَۗ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيٰتِ لَعَلَّكُمْ تَتَفَكَّرُوْنَۙ', 'yas`alụnaka \'anil-khamri wal-maisir, qul fīhimā iṡmung kabīruw wa manafi\'u lin-nāsi wa iṡmuhumā akbaru min-naf\'ihimā, wa yas`alụnaka māżā yunfiqụn, qulil-\'afw, każālika yubayyinullāhu lakumul-āyāti la\'allakum tatafakkarụn'),
(226, 2, 220, 'فِى الدُّنْيَا وَالْاٰخِرَةِ ۗ وَيَسْـَٔلُوْنَكَ عَنِ الْيَتٰمٰىۗ قُلْ اِصْلَاحٌ لَّهُمْ خَيْرٌ ۗ وَاِنْ تُخَالِطُوْهُمْ فَاِخْوَانُكُمْ ۗ وَاللّٰهُ يَعْلَمُ الْمُفْسِدَ مِنَ الْمُصْلِحِ ۗ وَلَوْ شَاۤءَ اللّٰهُ لَاَعْنَتَكُمْ اِنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ', 'fid-dun-yā wal-ākhirah, wa yas`alụnaka \'anil-yatāmā, qul iṣlāḥul lahum khaīr, wa in tukhāliṭụhum fa ikhwānukum, wallāhu ya\'lamul-mufsida minal-muṣliḥ, walau syā`allāhu la`a\'natakum, innallāha \'azīzun ḥakīm'),
(227, 2, 221, 'وَلَا تَنْكِحُوا الْمُشْرِكٰتِ حَتّٰى يُؤْمِنَّ ۗ وَلَاَمَةٌ مُّؤْمِنَةٌ خَيْرٌ مِّنْ مُّشْرِكَةٍ وَّلَوْ اَعْجَبَتْكُمْ ۚ وَلَا تُنْكِحُوا الْمُشْرِكِيْنَ حَتّٰى يُؤْمِنُوْا ۗ وَلَعَبْدٌ مُّؤْمِنٌ خَيْرٌ مِّنْ مُّشْرِكٍ وَّلَوْ اَعْجَبَكُمْ ۗ اُولٰۤىِٕكَ يَدْعُوْنَ اِلَى النَّارِ ۖ وَاللّٰهُ يَدْعُوْٓا اِلَى الْجَنَّةِ وَالْمَغْفِرَةِ بِاِذْنِهٖۚ وَيُبَيِّنُ اٰيٰتِهٖ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُوْنَ', 'wa lā tangkiḥul-musyrikāti ḥattā yu`minn, wa la`amatum mu`minatun khairum mim musyrikatiw walau a\'jabatkum, wa lā tungkiḥul-musyrikīna ḥattā yu`minụ, wa la\'abdum mu`minun khairum mim musyrikiw walau a\'jabakum, ulā`ika yad\'ụna ilan-nāri wallāhu yad\'ū ilal-jannati wal-magfirati bi`iżnih, wa yubayyinu āyātihī lin-nāsi la\'allahum yatażakkarụn'),
(228, 2, 222, 'وَيَسْـَٔلُوْنَكَ عَنِ الْمَحِيْضِ ۗ قُلْ هُوَ اَذًىۙ فَاعْتَزِلُوا النِّسَاۤءَ فِى الْمَحِيْضِۙ وَلَا تَقْرَبُوْهُنَّ حَتّٰى يَطْهُرْنَ ۚ فَاِذَا تَطَهَّرْنَ فَأْتُوْهُنَّ مِنْ حَيْثُ اَمَرَكُمُ اللّٰهُ ۗ اِنَّ اللّٰهَ يُحِبُّ التَّوَّابِيْنَ وَيُحِبُّ الْمُتَطَهِّرِيْنَ', 'wa yas`alụnaka \'anil-maḥīḍ, qul huwa ażan fa\'tazilun-nisā`a fil-maḥīḍi wa lā taqrabụhunna ḥattā yaṭ-hurn, fa iżā taṭahharna fa`tụhunna min ḥaiṡu amarakumullāh, innallāha yuḥibbut-tawwābīna wa yuḥibbul-mutaṭahhirīn'),
(229, 2, 223, 'نِسَاۤؤُكُمْ حَرْثٌ لَّكُمْ ۖ فَأْتُوْا حَرْثَكُمْ اَنّٰى شِئْتُمْ ۖ وَقَدِّمُوْا لِاَنْفُسِكُمْ ۗ وَاتَّقُوا اللّٰهَ وَاعْلَمُوْٓا اَنَّكُمْ مُّلٰقُوْهُ ۗ وَبَشِّرِ الْمُؤْمِنِيْنَ', 'nisā`ukum ḥarṡul lakum fa`tụ ḥarṡakum annā syi`tum wa qaddimụ li`anfusikum, wattaqullāha wa\'lamū annakum mulāqụh, wa basysyiril-mu`minīn'),
(230, 2, 224, 'وَلَا تَجْعَلُوا اللّٰهَ عُرْضَةً لِّاَيْمَانِكُمْ اَنْ تَبَرُّوْا وَتَتَّقُوْا وَتُصْلِحُوْا بَيْنَ النَّاسِۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌ', 'wa lā taj\'alullāha \'urḍatal li`aimānikum an tabarrụ wa tattaqụ wa tuṣliḥụ bainan-nās, wallāhu samī\'un \'alīm'),
(231, 2, 225, 'لَا يُؤَاخِذُكُمُ اللّٰهُ بِاللَّغْوِ فِيْٓ اَيْمَانِكُمْ وَلٰكِنْ يُّؤَاخِذُكُمْ بِمَا كَسَبَتْ قُلُوْبُكُمْ ۗ وَاللّٰهُ غَفُوْرٌ حَلِيْمٌ', 'lā yu`ākhiżukumullāhu bil-lagwi fī aimānikum wa lākiy yu`ākhiżukum bimā kasabat qulụbukum, wallāhu gafụrun ḥalīm'),
(232, 2, 226, 'لِلَّذِيْنَ يُؤْلُوْنَ مِنْ نِّسَاۤىِٕهِمْ تَرَبُّصُ اَرْبَعَةِ اَشْهُرٍۚ فَاِنْ فَاۤءُوْ فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'lillażīna yu`lụna min nisā`ihim tarabbuṣu arba\'ati asy-hur, fa in fā`ụ fa innallāha gafụrur raḥīm'),
(233, 2, 227, 'وَاِنْ عَزَمُوا الطَّلَاقَ فَاِنَّ اللّٰهَ سَمِيْعٌ عَلِيْمٌ', 'wa in \'azamuṭ-ṭalāqa fa innallāha samī\'un \'alīm'),
(234, 2, 228, 'وَالْمُطَلَّقٰتُ يَتَرَبَّصْنَ بِاَنْفُسِهِنَّ ثَلٰثَةَ قُرُوْۤءٍۗ وَلَا يَحِلُّ لَهُنَّ اَنْ يَّكْتُمْنَ مَا خَلَقَ اللّٰهُ فِيْٓ اَرْحَامِهِنَّ اِنْ كُنَّ يُؤْمِنَّ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۗ وَبُعُوْلَتُهُنَّ اَحَقُّ بِرَدِّهِنَّ فِيْ ذٰلِكَ اِنْ اَرَادُوْٓا اِصْلَاحًا ۗوَلَهُنَّ مِثْلُ الَّذِيْ عَلَيْهِنَّ بِالْمَعْرُوْفِۖ وَلِلرِّجَالِ عَلَيْهِنَّ دَرَجَةٌ ۗ وَاللّٰهُ عَزِيْزٌ حَكِيْمٌ', 'wal-muṭallaqātu yatarabbaṣna bi`anfusihinna ṡalāṡata qurū`, wa lā yaḥillu lahunna ay yaktumna mā khalaqallāhu fī ar-ḥāmihinna ing kunna yu`minna billāhi wal-yaumil-ākhir, wa bu\'ụlatuhunna aḥaqqu biraddihinna fī żālika in arādū iṣlāḥā, wa lahunna miṡlullażī \'alaihinna bil-ma\'rụfi wa lir-rijāli \'alaihinna darajah, wallāhu \'azīzun ḥakīm'),
(235, 2, 229, 'اَلطَّلَاقُ مَرَّتٰنِ ۖ فَاِمْسَاكٌۢ بِمَعْرُوْفٍ اَوْ تَسْرِيْحٌۢ بِاِحْسَانٍ ۗ وَلَا يَحِلُّ لَكُمْ اَنْ تَأْخُذُوْا مِمَّآ اٰتَيْتُمُوْهُنَّ شَيْـًٔا اِلَّآ اَنْ يَّخَافَآ اَلَّا يُقِيْمَا حُدُوْدَ اللّٰهِ ۗ فَاِنْ خِفْتُمْ اَلَّا يُقِيْمَا حُدُوْدَ اللّٰهِ ۙ فَلَا جُنَاحَ عَلَيْهِمَا فِيْمَا افْتَدَتْ بِهٖ ۗ تِلْكَ حُدُوْدُ اللّٰهِ فَلَا تَعْتَدُوْهَا ۚوَمَنْ يَّتَعَدَّ حُدُوْدَ اللّٰهِ فَاُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ', 'aṭ-ṭalāqu marratāni fa imsākum bima\'rụfin au tasrīḥum bi`iḥsān, wa lā yaḥillu lakum an ta`khużụ mimmā ātaitumụhunna syai`an illā ay yakhāfā allā yuqīmā ḥudụdallāh, fa in khiftum allā yuqīmā ḥudụdallāhi fa lā junāḥa \'alaihimā fīmaftadat bih, tilka ḥudụdullāhi fa lā ta\'tadụhā, wa may yata\'adda ḥudụdallāhi fa ulā`ika humuẓ-ẓālimụn'),
(236, 2, 230, 'فَاِنْ طَلَّقَهَا فَلَا تَحِلُّ لَهٗ مِنْۢ بَعْدُ حَتّٰى تَنْكِحَ زَوْجًا غَيْرَهٗ ۗ فَاِنْ طَلَّقَهَا فَلَا جُنَاحَ عَلَيْهِمَآ اَنْ يَّتَرَاجَعَآ اِنْ ظَنَّآ اَنْ يُّقِيْمَا حُدُوْدَ اللّٰهِ ۗ وَتِلْكَ حُدُوْدُ اللّٰهِ يُبَيِّنُهَا لِقَوْمٍ يَّعْلَمُوْنَ', 'fa in ṭallaqahā fa lā taḥillu lahụ mim ba\'du ḥattā tangkiḥa zaujan gairah, fa in ṭallaqahā fa lā junāḥa \'alaihimā ay yatarāja\'ā in ẓannā ay yuqīmā ḥudụdallāh, wa tilka ḥudụdullāhi yubayyinuhā liqaumiy ya\'lamụn'),
(237, 2, 231, 'وَاِذَا طَلَّقْتُمُ النِّسَاۤءَ فَبَلَغْنَ اَجَلَهُنَّ فَاَمْسِكُوْهُنَّ بِمَعْرُوْفٍ اَوْ سَرِّحُوْهُنَّ بِمَعْرُوْفٍۗ وَلَا تُمْسِكُوْهُنَّ ضِرَارًا لِّتَعْتَدُوْا ۚ وَمَنْ يَّفْعَلْ ذٰلِكَ فَقَدْ ظَلَمَ نَفْسَهٗ ۗ وَلَا تَتَّخِذُوْٓا اٰيٰتِ اللّٰهِ هُزُوًا وَّاذْكُرُوْا نِعْمَتَ اللّٰهِ عَلَيْكُمْ وَمَآ اَنْزَلَ عَلَيْكُمْ مِّنَ الْكِتٰبِ وَالْحِكْمَةِ يَعِظُكُمْ بِهٖ ۗوَاتَّقُوا اللّٰهَ وَاعْلَمُوْٓا اَنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ', 'wa iżā ṭallaqtumun-nisā`a fa balagna ajalahunna fa amsikụhunna bima\'rụfin au sarriḥụhunna bima\'rụf, wa lā tumsikụhunna ḍirāral lita\'tadụ, wa may yaf\'al żālika fa qad ẓalama nafsah, wa lā tattakhiżū āyātillāhi huzuwaw ważkurụ ni\'matallāhi \'alaikum wa mā anzala \'alaikum minal-kitābi wal-ḥikmati ya\'iẓukum bih, wattaqullāha wa\'lamū annallāha bikulli syai`in \'alīm'),
(238, 2, 232, 'وَاِذَا طَلَّقْتُمُ النِّسَاۤءَ فَبَلَغْنَ اَجَلَهُنَّ فَلَا تَعْضُلُوْهُنَّ اَنْ يَّنْكِحْنَ اَزْوَاجَهُنَّ اِذَا تَرَاضَوْا بَيْنَهُمْ بِالْمَعْرُوْفِ ۗ ذٰلِكَ يُوْعَظُ بِهٖ مَنْ كَانَ مِنْكُمْ يُؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ ۗ ذٰلِكُمْ اَزْكٰى لَكُمْ وَاَطْهَرُ ۗ وَاللّٰهُ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُوْنَ', 'wa iżā ṭallaqtumun-nisā`a fa balagna ajalahunna fa lā ta\'ḍulụhunna ay yangkiḥna azwājahunna iżā tarāḍau bainahum bil-ma\'rụf, żālika yụ\'aẓu bihī mang kāna mingkum yu`minu billāhi wal-yaumil-ākhir, żālikum azkā lakum wa aṭ-har, wallāhu ya\'lamu wa antum lā ta\'lamụn'),
(239, 2, 233, '۞ وَالْوَالِدٰتُ يُرْضِعْنَ اَوْلَادَهُنَّ حَوْلَيْنِ كَامِلَيْنِ لِمَنْ اَرَادَ اَنْ يُّتِمَّ الرَّضَاعَةَ ۗ وَعَلَى الْمَوْلُوْدِ لَهٗ رِزْقُهُنَّ وَكِسْوَتُهُنَّ بِالْمَعْرُوْفِۗ لَا تُكَلَّفُ نَفْسٌ اِلَّا وُسْعَهَا ۚ لَا تُضَاۤرَّ وَالِدَةٌ ۢبِوَلَدِهَا وَلَا مَوْلُوْدٌ لَّهٗ بِوَلَدِهٖ وَعَلَى الْوَارِثِ مِثْلُ ذٰلِكَ ۚ فَاِنْ اَرَادَا فِصَالًا عَنْ تَرَاضٍ مِّنْهُمَا وَتَشَاوُرٍ فَلَا جُنَاحَ عَلَيْهِمَا ۗوَاِنْ اَرَدْتُّمْ اَنْ تَسْتَرْضِعُوْٓا اَوْلَادَكُمْ فَلَا جُنَاحَ عَلَيْكُمْ اِذَا سَلَّمْتُمْ مَّآ اٰتَيْتُمْ بِالْمَعْرُوْفِۗ وَاتَّقُوا اللّٰهَ وَاعْلَمُوْٓا اَنَّ اللّٰهَ بِمَا تَعْمَلُوْنَ بَصِيْرٌ', 'wal-wālidātu yurḍi\'na aulādahunna ḥaulaini kāmilaini liman arāda ay yutimmar-raḍā\'ah, wa \'alal-maulụdi lahụ rizquhunna wa kiswatuhunna bil-ma\'rụf, lā tukallafu nafsun illā wus\'ahā, lā tuḍārra wālidatum biwaladihā wa lā maulụdul lahụ biwaladihī wa \'alal-wāriṡi miṡlu żālik, fa in arādā fiṣālan \'an tarāḍim min-humā wa tasyāwurin fa lā junāḥa \'alaihimā, wa in arattum an tastarḍi\'ū aulādakum fa lā junāḥa \'alaikum iżā sallamtum mā ātaitum bil-ma\'rụf, wattaqullāha wa\'lamū annallāha bimā ta\'malụna baṣīr'),
(240, 2, 234, 'وَالَّذِيْنَ يُتَوَفَّوْنَ مِنْكُمْ وَيَذَرُوْنَ اَزْوَاجًا يَّتَرَبَّصْنَ بِاَنْفُسِهِنَّ اَرْبَعَةَ اَشْهُرٍ وَّعَشْرًا ۚ فاِذَا بَلَغْنَ اَجَلَهُنَّ فَلَا جُنَاحَ عَلَيْكُمْ فِيْمَا فَعَلْنَ فِيْٓ اَنْفُسِهِنَّ بِالْمَعْرُوْفِۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ', 'wallażīna yutawaffauna mingkum wa yażarụna azwājay yatarabbaṣna bi`anfusihinna arba\'ata asy-huriw wa \'asyrā, fa iżā balagna ajalahunna fa lā junāḥa \'alaikum fīmā fa\'alna fī anfusihinna bil-ma\'rụf, wallāhu bimā ta\'malụna khabīr'),
(241, 2, 235, 'وَلَا جُنَاحَ عَلَيْكُمْ فِيْمَا عَرَّضْتُمْ بِهٖ مِنْ خِطْبَةِ النِّسَاۤءِ اَوْ اَكْنَنْتُمْ فِيْٓ اَنْفُسِكُمْ ۗ عَلِمَ اللّٰهُ اَنَّكُمْ سَتَذْكُرُوْنَهُنَّ وَلٰكِنْ لَّا تُوَاعِدُوْهُنَّ سِرًّا اِلَّآ اَنْ تَقُوْلُوْا قَوْلًا مَّعْرُوْفًا ەۗ وَلَا تَعْزِمُوْا عُقْدَةَ النِّكَاحِ حَتّٰى يَبْلُغَ الْكِتٰبُ اَجَلَهٗ ۗوَاعْلَمُوْٓا اَنَّ اللّٰهَ يَعْلَمُ مَا فِيْٓ اَنْفُسِكُمْ فَاحْذَرُوْهُ ۚوَاعْلَمُوْٓا اَنَّ اللّٰهَ غَفُوْرٌ حَلِيْمٌ', 'wa lā junāḥa \'alaikum fīmā \'arraḍtum bihī min khiṭbatin-nisā`i au aknantum fī anfusikum, \'alimallāhu annakum satażkurụnahunna wa lākil lā tuwā\'idụhunna sirran illā an taqụlụ qaulam ma\'rụfā, wa lā ta\'zimụ \'uqdatan-nikāḥi ḥattā yablugal-kitābu ajalah, wa\'lamū annallāha ya\'lamu mā fī anfusikum faḥżarụh, wa\'lamū annallāha gafụrun ḥalīm'),
(242, 2, 236, 'لَا جُنَاحَ عَلَيْكُمْ اِنْ طَلَّقْتُمُ النِّسَاۤءَ مَا لَمْ تَمَسُّوْهُنَّ اَوْ تَفْرِضُوْا لَهُنَّ فَرِيْضَةً ۖ وَّمَتِّعُوْهُنَّ عَلَى الْمُوْسِعِ قَدَرُهٗ وَعَلَى الْمُقْتِرِ قَدَرُهٗ ۚ مَتَاعًا ۢبِالْمَعْرُوْفِۚ حَقًّا عَلَى الْمُحْسِنِيْنَ', 'lā junāḥa \'alaikum in ṭallaqtumun-nisā`a mā lam tamassụhunna au tafriḍụ lahunna farīḍataw wa matti\'ụhunna \'alal-mụsi\'i qadaruhụ wa \'alal-muqtiri qadaruh, matā\'am bil-ma\'rụf, ḥaqqan \'alal-muḥsinīn'),
(243, 2, 237, 'وَاِنْ طَلَّقْتُمُوْهُنَّ مِنْ قَبْلِ اَنْ تَمَسُّوْهُنَّ وَقَدْ فَرَضْتُمْ لَهُنَّ فَرِيْضَةً فَنِصْفُ مَا فَرَضْتُمْ اِلَّآ اَنْ يَّعْفُوْنَ اَوْ يَعْفُوَا الَّذِيْ بِيَدِهٖ عُقْدَةُ النِّكَاحِ ۗ وَاَنْ تَعْفُوْٓا اَقْرَبُ لِلتَّقْوٰىۗ وَلَا تَنْسَوُا الْفَضْلَ بَيْنَكُمْ ۗ اِنَّ اللّٰهَ بِمَا تَعْمَلُوْنَ بَصِيْرٌ', 'wa in ṭallaqtumụhunna ming qabli an tamassụhunna wa qad faraḍtum lahunna farīḍatan fa niṣfu mā faraḍtum illā ay ya\'fụna au ya\'fuwallażī biyadihī \'uqdatun-nikāḥ, wa an ta\'fū aqrabu lit-taqwā, wa lā tansawul-faḍla bainakum, innallāha bimā ta\'malụna baṣīr'),
(244, 2, 238, 'حَافِظُوْا عَلَى الصَّلَوٰتِ وَالصَّلٰوةِ الْوُسْطٰى وَقُوْمُوْا لِلّٰهِ قٰنِتِيْنَ', 'ḥāfiẓụ \'alaṣ-ṣalawāti waṣ-ṣalātil-wusṭā wa qụmụ lillāhi qānitīn'),
(245, 2, 239, 'فَاِنْ خِفْتُمْ فَرِجَالًا اَوْ رُكْبَانًا ۚ فَاِذَآ اَمِنْتُمْ فَاذْكُرُوا اللّٰهَ كَمَا عَلَّمَكُمْ مَّا لَمْ تَكُوْنُوْا تَعْلَمُوْنَ', 'fa in khiftum fa rijālan au rukbānā, fa iżā amintum fażkurullāha kamā \'allamakum mā lam takụnụ ta\'lamụn'),
(246, 2, 240, 'وَالَّذِيْنَ يُتَوَفَّوْنَ مِنْكُمْ وَيَذَرُوْنَ اَزْوَاجًاۖ وَّصِيَّةً لِّاَزْوَاجِهِمْ مَّتَاعًا اِلَى الْحَوْلِ غَيْرَ اِخْرَاجٍ ۚ فَاِنْ خَرَجْنَ فَلَا جُنَاحَ عَلَيْكُمْ فِيْ مَا فَعَلْنَ فِيْٓ اَنْفُسِهِنَّ مِنْ مَّعْرُوْفٍۗ وَاللّٰهُ عَزِيْزٌ حَكِيْمٌ', 'wallażīna yutawaffauna mingkum wa yażarụna azwājaw waṣiyyatal li`azwājihim matā\'an ilal-ḥauli gaira ikhrāj, fa in kharajna fa lā junāḥa \'alaikum fī mā fa\'alna fī anfusihinna mim ma\'rụf, wallāhu \'azīzun ḥakīm'),
(247, 2, 241, 'وَلِلْمُطَلَّقٰتِ مَتَاعٌ ۢبِالْمَعْرُوْفِۗ حَقًّا عَلَى الْمُتَّقِيْنَ', 'wa lil-muṭallaqāti matā\'um bil-ma\'rụf, ḥaqqan \'alal-muttaqīn'),
(248, 2, 242, 'كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيٰتِهٖ لَعَلَّكُمْ تَعْقِلُوْنَ', 'każālika yubayyinullāhu lakum āyātihī la\'allakum ta\'qilụn'),
(249, 2, 243, '۞ اَلَمْ تَرَ اِلَى الَّذِيْنَ خَرَجُوْا مِنْ دِيَارِهِمْ وَهُمْ اُلُوْفٌ حَذَرَ الْمَوْتِۖ فَقَالَ لَهُمُ اللّٰهُ مُوْتُوْا ۗ ثُمَّ اَحْيَاهُمْ ۗ اِنَّ اللّٰهَ لَذُوْ فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَشْكُرُوْنَ', 'a lam tara ilallażīna kharajụ min diyārihim wa hum ulụfun ḥażaral-mauti fa qāla lahumullāhu mụtụ, ṡumma aḥyāhum, innallāha lażụ faḍlin \'alan-nāsi wa lākinna akṡaran-nāsi lā yasykurụn'),
(250, 2, 244, 'وَقَاتِلُوْا فِيْ سَبِيْلِ اللّٰهِ وَاعْلَمُوْٓا اَنَّ اللّٰهَ سَمِيْعٌ عَلِيْمٌ', 'wa qātilụ fī sabīlillāhi wa\'lamū annallāha samī\'un \'alīm'),
(251, 2, 245, 'مَنْ ذَا الَّذِيْ يُقْرِضُ اللّٰهَ قَرْضًا حَسَنًا فَيُضٰعِفَهٗ لَهٗٓ اَضْعَافًا كَثِيْرَةً ۗوَاللّٰهُ يَقْبِضُ وَيَبْصُۣطُۖ وَاِلَيْهِ تُرْجَعُوْنَ', 'man żallażī yuqriḍullāha qarḍan ḥasanan fa yuḍā\'ifahụ lahū aḍ\'āfang kaṡīrah, wallāhu yaqbiḍu wa yabṣuṭu wa ilaihi turja\'ụn'),
(252, 2, 246, 'اَلَمْ تَرَ اِلَى الْمَلَاِ مِنْۢ بَنِيْٓ اِسْرَاۤءِيْلَ مِنْۢ بَعْدِ مُوْسٰىۘ اِذْ قَالُوْا لِنَبِيٍّ لَّهُمُ ابْعَثْ لَنَا مَلِكًا نُّقَاتِلْ فِيْ سَبِيْلِ اللّٰهِ ۗ قَالَ هَلْ عَسَيْتُمْ اِنْ كُتِبَ عَلَيْكُمُ الْقِتَالُ اَلَّا تُقَاتِلُوْا ۗ قَالُوْا وَمَا لَنَآ اَلَّا نُقَاتِلَ فِيْ سَبِيْلِ اللّٰهِ وَقَدْاُخْرِجْنَا مِنْ دِيَارِنَا وَاَبْنَاۤىِٕنَا ۗ فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتَالُ تَوَلَّوْا اِلَّا قَلِيْلًا مِّنْهُمْ ۗوَاللّٰهُ عَلِيْمٌ ۢبِالظّٰلِمِيْنَ', 'a lam tara ilal-mala`i mim banī isrā`īla mim ba\'di mụsā, iż qālụ linabiyyil lahumub\'aṡ lanā malikan-nuqātil fī sabīlillāh, qāla hal \'asaitum ing kutiba \'alaikumul-qitālu allā tuqātilụ, qālụ wa mā lanā allā nuqātila fī sabīlillāhi wa qad ukhrijnā min diyārinā wa abnā`inā, fa lammā kutiba \'alaihimul-qitālu tawallau illā qalīlam min-hum, wallāhu \'alīmum biẓ-ẓālimīn'),
(253, 2, 247, 'وَقَالَ لَهُمْ نَبِيُّهُمْ اِنَّ اللّٰهَ قَدْ بَعَثَ لَكُمْ طَالُوْتَ مَلِكًا ۗ قَالُوْٓا اَنّٰى يَكُوْنُ لَهُ الْمُلْكُ عَلَيْنَا وَنَحْنُ اَحَقُّ بِالْمُلْكِ مِنْهُ وَلَمْ يُؤْتَ سَعَةً مِّنَ الْمَالِۗ قَالَ اِنَّ اللّٰهَ اصْطَفٰىهُ عَلَيْكُمْ وَزَادَهٗ بَسْطَةً فِى الْعِلْمِ وَالْجِسْمِ ۗ وَاللّٰهُ يُؤْتِيْ مُلْكَهٗ مَنْ يَّشَاۤءُ ۗ وَاللّٰهُ وَاسِعٌ عَلِيْمٌ', 'wa qāla lahum nabiyyuhum innallāha qad ba\'aṡa lakum ṭālụta malikā, qālū annā yakụnu lahul-mulku \'alainā wa naḥnu aḥaqqu bil-mulki min-hu wa lam yu`ta sa\'atam minal-māl, qāla innallāhaṣṭafāhu \'alaikum wa zādahụ basṭatan fil \'ilmi wal-jism, wallāhu yu`tī mulkahụ may yasyā`, wallāhu wāsi\'un \'alīm'),
(254, 2, 248, 'وَقَالَ لَهُمْ نَبِيُّهُمْ اِنَّ اٰيَةَ مُلْكِهٖٓ اَنْ يَّأْتِيَكُمُ التَّابُوْتُ فِيْهِ سَكِيْنَةٌ مِّنْ رَّبِّكُمْ وَبَقِيَّةٌ مِّمَّا تَرَكَ اٰلُ مُوْسٰى وَاٰلُ هٰرُوْنَ تَحْمِلُهُ الْمَلٰۤىِٕكَةُ ۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لَّكُمْ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ', 'wa qāla lahum nabiyyuhum inna āyata mulkihī ay ya`tiyakumut-tābụtu fīhi sakīnatum mir rabbikum wa baqiyyatum mimmā taraka ālu mụsā wa ālu hārụna taḥmiluhul-malā`ikah, inna fī żālika la`āyatal lakum ing kuntum mu`minīn'),
(255, 2, 249, 'فَلَمَّا فَصَلَ طَالُوْتُ بِالْجُنُوْدِ قَالَ اِنَّ اللّٰهَ مُبْتَلِيْكُمْ بِنَهَرٍۚ فَمَنْ شَرِبَ مِنْهُ فَلَيْسَ مِنِّيْۚ وَمَنْ لَّمْ يَطْعَمْهُ فَاِنَّهٗ مِنِّيْٓ اِلَّا مَنِ اغْتَرَفَ غُرْفَةً ۢبِيَدِهٖ ۚ فَشَرِبُوْا مِنْهُ اِلَّا قَلِيْلًا مِّنْهُمْ ۗ فَلَمَّا جَاوَزَهٗ هُوَ وَالَّذِيْنَ اٰمَنُوْا مَعَهٗۙ قَالُوْا لَا طَاقَةَ لَنَا الْيَوْمَ بِجَالُوْتَ وَجُنُوْدِهٖ ۗ قَالَ الَّذِيْنَ يَظُنُّوْنَ اَنَّهُمْ مُّلٰقُوا اللّٰهِ ۙ كَمْ مِّنْ فِئَةٍ قَلِيْلَةٍ غَلَبَتْ فِئَةً كَثِيْرَةً ۢبِاِذْنِ اللّٰهِ ۗ وَاللّٰهُ مَعَ الصّٰبِرِيْنَ', 'fa lammā faṣala ṭālụtu bil-junụdi qāla innallāha mubtalīkum binahar, fa man syariba min-hu fa laisa minnī, wa mal lam yaṭ\'am-hu fa innahụ minnī illā manigtarafa gurfatam biyadih, fa syaribụ min-hu illā qalīlam min-hum, fa lammā jāwazahụ huwa wallażīna āmanụ ma\'ahụ qālụ lā ṭāqata lanal-yauma bijālụta wa junụdih, qālallażīna yaẓunnụna annahum mulāqullāhi kam min fi`ating qalīlatin galabat fi`atang kaṡīratam bi`iżnillāh, wallāhu ma\'aṣ-ṣābirīn'),
(256, 2, 250, 'وَلَمَّا بَرَزُوْا لِجَالُوْتَ وَجُنُوْدِهٖ قَالُوْا رَبَّنَآ اَفْرِغْ عَلَيْنَا صَبْرًا وَّثَبِّتْ اَقْدَامَنَا وَانْصُرْنَا عَلَى الْقَوْمِ الْكٰفِرِيْنَ ۗ', 'wa lammā barazụ lijālụta wa junụdihī qālụ rabbanā afrig \'alainā ṣabraw wa ṡabbit aqdāmanā wanṣurnā \'alal-qaumil-kāfirīn'),
(257, 2, 251, 'فَهَزَمُوْهُمْ بِاِذْنِ اللّٰهِ ۗوَقَتَلَ دَاوٗدُ جَالُوْتَ وَاٰتٰىهُ اللّٰهُ الْمُلْكَ وَالْحِكْمَةَ وَعَلَّمَهٗ مِمَّا يَشَاۤءُ ۗ وَلَوْلَا دَفْعُ اللّٰهِ النَّاسَ بَعْضَهُمْ بِبَعْضٍ لَّفَسَدَتِ الْاَرْضُ وَلٰكِنَّ اللّٰهَ ذُوْ فَضْلٍ عَلَى الْعٰلَمِيْنَ', 'fa hazamụhum bi`iżnillāh, wa qatala dāwụdu jālụta wa ātāhullāhul-mulka wal-ḥikmata wa \'allamahụ mimmā yasyā`, walau lā daf\'ullāhin-nāsa ba\'ḍahum biba\'ḍil lafasadatil-arḍu wa lākinnallāha żụ faḍlin \'alal-\'ālamīn'),
(258, 2, 252, 'تِلْكَ اٰيٰتُ اللّٰهِ نَتْلُوْهَا عَلَيْكَ بِالْحَقِّ ۗ وَاِنَّكَ لَمِنَ الْمُرْسَلِيْنَ ۔', 'tilka āyātullāhi natlụhā \'alaika bil-ḥaqq, wa innaka laminal-mursalīn'),
(259, 2, 253, 'تِلْكَ الرُّسُلُ فَضَّلْنَا بَعْضَهُمْ عَلٰى بَعْضٍۘ مِنْهُمْ مَّنْ كَلَّمَ اللّٰهُ وَرَفَعَ بَعْضَهُمْ دَرَجٰتٍۗ وَاٰتَيْنَا عِيْسَى ابْنَ مَرْيَمَ الْبَيِّنٰتِ وَاَيَّدْنٰهُ بِرُوْحِ الْقُدُسِۗ وَلَوْ شَاۤءَ اللّٰهُ مَا اقْتَتَلَ الَّذِيْنَ مِنْۢ بَعْدِهِمْ مِّنْۢ بَعْدِ مَا جَاۤءَتْهُمُ الْبَيِّنٰتُ وَلٰكِنِ اخْتَلَفُوْا فَمِنْهُمْ مَّنْ اٰمَنَ وَمِنْهُمْ مَّنْ كَفَرَ ۗوَلَوْ شَاۤءَ اللّٰهُ مَا اقْتَتَلُوْاۗ وَلٰكِنَّ اللّٰهَ يَفْعَلُ مَا يُرِيْدُ', 'tilkar-rusulu faḍḍalnā ba\'ḍahum \'alā ba\'ḍ, min-hum mang kallamallāhu wa rafa\'a ba\'ḍahum darajāt, wa ātainā \'īsabna maryamal-bayyināti wa ayyadnāhu birụḥil-qudus, walau syā`allāhu maqtatalallażīna mim ba\'dihim mim ba\'di mā jā`at-humul-bayyinātu wa lākinikhtalafụ fa min-hum man āmana wa min-hum mang kafar, walau syā`allāhu maqtatalụ, wa lākinnallāha yaf\'alu mā yurīd'),
(260, 2, 254, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَنْفِقُوْا مِمَّا رَزَقْنٰكُمْ مِّنْ قَبْلِ اَنْ يَّأْتِيَ يَوْمٌ لَّا بَيْعٌ فِيْهِ وَلَا خُلَّةٌ وَّلَا شَفَاعَةٌ ۗوَالْكٰفِرُوْنَ هُمُ الظّٰلِمُوْنَ', 'yā ayyuhallażīna āmanū anfiqụ mimmā razaqnākum ming qabli ay ya`tiya yaumul lā bai\'un fīhi wa lā khullatuw wa lā syafā\'ah, wal-kāfirụna humuẓ-ẓālimụn'),
(261, 2, 255, 'اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَۚ اَلْحَيُّ الْقَيُّوْمُ ەۚ لَا تَأْخُذُهٗ سِنَةٌ وَّلَا نَوْمٌۗ لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ مَنْ ذَا الَّذِيْ يَشْفَعُ عِنْدَهٗٓ اِلَّا بِاِذْنِهٖۗ يَعْلَمُ مَا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْۚ وَلَا يُحِيْطُوْنَ بِشَيْءٍ مِّنْ عِلْمِهٖٓ اِلَّا بِمَا شَاۤءَۚ وَسِعَ كُرْسِيُّهُ السَّمٰوٰتِ وَالْاَرْضَۚ وَلَا يَـُٔوْدُهٗ حِفْظُهُمَاۚ وَهُوَ الْعَلِيُّ الْعَظِيْمُ', 'allāhu lā ilāha illā huw, al-ḥayyul-qayyụm, lā ta`khużuhụ sinatuw wa lā na`ụm, lahụ mā fis-samāwāti wa mā fil-arḍ, man żallażī yasyfa\'u \'indahū illā bi`iżnih, ya\'lamu mā baina aidīhim wa mā khalfahum, wa lā yuḥīṭụna bisyai`im min \'ilmihī illā bimā syā`, wasi\'a kursiyyuhus-samāwāti wal-arḍ, wa lā ya`ụduhụ ḥifẓuhumā, wa huwal-\'aliyyul-\'aẓīm'),
(262, 2, 256, 'لَآ اِكْرَاهَ فِى الدِّيْنِۗ قَدْ تَّبَيَّنَ الرُّشْدُ مِنَ الْغَيِّ ۚ فَمَنْ يَّكْفُرْ بِالطَّاغُوْتِ وَيُؤْمِنْۢ بِاللّٰهِ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقٰى لَا انْفِصَامَ لَهَا ۗوَاللّٰهُ سَمِيْعٌ عَلِيْمٌ', 'lā ikrāha fid-dīn, qat tabayyanar-rusydu minal-gayy, fa may yakfur biṭ-ṭāgụti wa yu`mim billāhi fa qadistamsaka bil-\'urwatil-wuṡqā lanfiṣāma lahā, wallāhu samī\'un \'alīm'),
(263, 2, 257, 'اَللّٰهُ وَلِيُّ الَّذِيْنَ اٰمَنُوْا يُخْرِجُهُمْ مِّنَ الظُّلُمٰتِ اِلَى النُّوْرِۗ وَالَّذِيْنَ كَفَرُوْٓا اَوْلِيَاۤؤُهُمُ الطَّاغُوْتُ يُخْرِجُوْنَهُمْ مِّنَ النُّوْرِ اِلَى الظُّلُمٰتِۗ اُولٰۤىِٕكَ اَصْحٰبُ النَّارِۚ هُمْ فِيْهَا خٰلِدُوْنَ', 'allāhu waliyyullażīna āmanụ yukhrijuhum minaẓ-ẓulumāti ilan-nụr, wallażīna kafarū auliyā`uhumuṭ-ṭāgụtu yukhrijụnahum minan-nụri ilaẓ-ẓulumāt, ulā`ika aṣ-ḥābun-nār, hum fīhā khālidụn'),
(264, 2, 258, 'اَلَمْ تَرَ اِلَى الَّذِيْ حَاۤجَّ اِبْرٰهٖمَ فِيْ رَبِّهٖٓ اَنْ اٰتٰىهُ اللّٰهُ الْمُلْكَ ۘ اِذْ قَالَ اِبْرٰهٖمُ رَبِّيَ الَّذِيْ يُحْيٖ وَيُمِيْتُۙ قَالَ اَنَا۠ اُحْيٖ وَاُمِيْتُ ۗ قَالَ اِبْرٰهٖمُ فَاِنَّ اللّٰهَ يَأْتِيْ بِالشَّمْسِ مِنَ الْمَشْرِقِ فَأْتِ بِهَا مِنَ الْمَغْرِبِ فَبُهِتَ الَّذِيْ كَفَرَ ۗوَاللّٰهُ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَۚ', 'a lam tara ilallażī ḥājja ibrāhīma fī rabbihī an ātāhullāhul-mulk, iż qāla ibrāhīmu rabbiyallażī yuḥyī wa yumītu qāla ana uḥyī wa umīt, qāla ibrāhīmu fa innallāha ya`tī bisy-syamsi minal-masyriqi fa`ti bihā minal-magribi fa buhitallażī kafar, wallāhu lā yahdil-qaumaẓ-ẓālimīn'),
(265, 2, 259, 'اَوْ كَالَّذِيْ مَرَّ عَلٰى قَرْيَةٍ وَّهِيَ خَاوِيَةٌ عَلٰى عُرُوْشِهَاۚ قَالَ اَنّٰى يُحْيٖ هٰذِهِ اللّٰهُ بَعْدَ مَوْتِهَا ۚ فَاَمَاتَهُ اللّٰهُ مِائَةَ عَامٍ ثُمَّ بَعَثَهٗ ۗ قَالَ كَمْ لَبِثْتَ ۗ قَالَ لَبِثْتُ يَوْمًا اَوْ بَعْضَ يَوْمٍۗ قَالَ بَلْ لَّبِثْتَ مِائَةَ عَامٍ فَانْظُرْ اِلٰى طَعَامِكَ وَشَرَابِكَ لَمْ يَتَسَنَّهْ ۚ وَانْظُرْ اِلٰى حِمَارِكَۗ وَلِنَجْعَلَكَ اٰيَةً لِّلنَّاسِ وَانْظُرْ اِلَى الْعِظَامِ كَيْفَ نُنْشِزُهَا ثُمَّ نَكْسُوْهَا لَحْمًا ۗ فَلَمَّا تَبَيَّنَ لَهٗ ۙ قَالَ اَعْلَمُ اَنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'au kallażī marra \'alā qaryatiw wa hiya khāwiyatun \'alā \'urụsyihā, qāla annā yuḥyī hāżihillāhu ba\'da mautihā, fa amātahullāhu mi`ata \'āmin ṡumma ba\'aṡah, qāla kam labiṡt, qāla labiṡtu yauman au ba\'ḍa yaụm, qāla bal labiṡta mi`ata \'āmin fanẓur ilā ṭa\'āmika wa syarābika lam yatasannah, wanẓur ilā ḥimārik, wa linaj\'alaka āyatal lin-nāsi wanẓur ilal-\'iẓāmi kaifa nunsyizuhā ṡumma naksụhā laḥmā, fa lammā tabayyana lahụ qāla a\'lamu annallāha \'alā kulli syai`ing qadīr'),
(266, 2, 260, 'وَاِذْ قَالَ اِبْرٰهٖمُ رَبِّ اَرِنِيْ كَيْفَ تُحْيِ الْمَوْتٰىۗ قَالَ اَوَلَمْ تُؤْمِنْ ۗقَالَ بَلٰى وَلٰكِنْ لِّيَطْمَىِٕنَّ قَلْبِيْ ۗقَالَ فَخُذْ اَرْبَعَةً مِّنَ الطَّيْرِفَصُرْهُنَّ اِلَيْكَ ثُمَّ اجْعَلْ عَلٰى كُلِّ جَبَلٍ مِّنْهُنَّ جُزْءًا ثُمَّ ادْعُهُنَّ يَأْتِيْنَكَ سَعْيًا ۗوَاعْلَمْ اَنَّ اللّٰهَ عَزِيْزٌحَكِيْمٌ', 'wa iż qāla ibrāhīmu rabbi arinī kaifa tuḥyil-mautā, qāla a wa lam tu`min, qāla balā wa lākil liyaṭma`inna qalbī, qāla fakhuż arba\'atam minaṭ-ṭairi fa ṣur-hunna ilaika ṡummaj\'al \'alā kulli jabalim min-hunna juz`an ṡummad\'uhunna ya`tīnaka sa\'yā, wa\'lam annallāha \'azīzun ḥakīm'),
(267, 2, 261, 'مَثَلُ الَّذِيْنَ يُنْفِقُوْنَ اَمْوَالَهُمْ فِيْ سَبِيْلِ اللّٰهِ كَمَثَلِ حَبَّةٍ اَنْۢبَتَتْ سَبْعَ سَنَابِلَ فِيْ كُلِّ سُنْۢبُلَةٍ مِّائَةُ حَبَّةٍ ۗ وَاللّٰهُ يُضٰعِفُ لِمَنْ يَّشَاۤءُ ۗوَاللّٰهُ وَاسِعٌ عَلِيْمٌ', 'maṡalullażīna yunfiqụna amwālahum fī sabīlillāhi kamaṡali ḥabbatin ambatat sab\'a sanābila fī kulli sumbulatim mi`atu ḥabbah, wallāhu yuḍā\'ifu limay yasyā`, wallāhu wāsi\'un \'alīm'),
(268, 2, 262, 'اَلَّذِيْنَ يُنْفِقُوْنَ اَمْوَالَهُمْ فِيْ سَبِيْلِ اللّٰهِ ثُمَّ لَا يُتْبِعُوْنَ مَآ اَنْفَقُوْا مَنًّا وَّلَآ اَذًىۙ لَّهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ', 'allażīna yunfiqụna amwālahum fī sabīlillāhi ṡumma lā yutbi\'ụna mā anfaqụ mannaw wa lā ażal lahum ajruhum \'inda rabbihim, wa lā khaufun \'alaihim wa lā hum yaḥzanụn'),
(269, 2, 263, '۞ قَوْلٌ مَّعْرُوْفٌ وَّمَغْفِرَةٌ خَيْرٌ مِّنْ صَدَقَةٍ يَّتْبَعُهَآ اَذًى ۗ وَاللّٰهُ غَنِيٌّ حَلِيْمٌ', 'qaulum ma\'rụfuw wa magfiratun khairum min ṣadaqatiy yatba\'uhā ażā, wallāhu ganiyyun ḥalīm'),
(270, 2, 264, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تُبْطِلُوْا صَدَقٰتِكُمْ بِالْمَنِّ وَالْاَذٰىۙ كَالَّذِيْ يُنْفِقُ مَالَهٗ رِئَاۤءَ النَّاسِ وَلَا يُؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۗ فَمَثَلُهٗ كَمَثَلِ صَفْوَانٍ عَلَيْهِ تُرَابٌ فَاَصَابَهٗ وَابِلٌ فَتَرَكَهٗ صَلْدًا ۗ لَا يَقْدِرُوْنَ عَلٰى شَيْءٍ مِّمَّا كَسَبُوْا ۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الْكٰفِرِيْنَ', 'yā ayyuhallażīna āmanụ lā tubṭilụ ṣadaqātikum bil-manni wal-ażā kallażī yunfiqu mālahụ ri`ā`an-nāsi wa lā yu`minu billāhi wal-yaumil-ākhir, fa maṡaluhụ kamaṡali ṣafwānin \'alaihi turābun fa aṣābahụ wābilun fa tarakahụ ṣaldā, lā yaqdirụna \'alā syai`im mimmā kasabụ, wallāhu lā yahdil-qaumal-kāfirīn'),
(271, 2, 265, 'وَمَثَلُ الَّذِيْنَ يُنْفِقُوْنَ اَمْوَالَهُمُ ابْتِغَاۤءَ مَرْضَاتِ اللّٰهِ وَتَثْبِيْتًا مِّنْ اَنْفُسِهِمْ كَمَثَلِ جَنَّةٍۢ بِرَبْوَةٍ اَصَابَهَا وَابِلٌ فَاٰتَتْ اُكُلَهَا ضِعْفَيْنِۚ فَاِنْ لَّمْ يُصِبْهَا وَابِلٌ فَطَلٌّ ۗوَاللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرٌ', 'wa maṡalullażīna yunfiqụna amwālahumubtigā`a marḍātillāhi wa taṡbītam min anfusihim kamaṡali jannatim birabwatin aṣābahā wābilun fa ātat ukulahā ḍi\'faīn, fa il lam yuṣib-hā wābilun fa ṭall, wallāhu bimā ta\'malụna baṣīr');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(272, 2, 266, 'اَيَوَدُّ اَحَدُكُمْ اَنْ تَكُوْنَ لَهٗ جَنَّةٌ مِّنْ نَّخِيْلٍ وَّاَعْنَابٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۙ لَهٗ فِيْهَا مِنْ كُلِّ الثَّمَرٰتِۙ وَاَصَابَهُ الْكِبَرُ وَلَهٗ ذُرِّيَّةٌ ضُعَفَاۤءُۚ فَاَصَابَهَآ اِعْصَارٌ فِيْهِ نَارٌ فَاحْتَرَقَتْ ۗ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيٰتِ لَعَلَّكُمْ تَتَفَكَّرُوْنَ', 'a yawaddu aḥadukum an takụna lahụ jannatum min nakhīliw wa a\'nābin tajrī min taḥtihal-an-hāru lahụ fīhā ming kulliṡ-ṡamarāti wa aṣābahul-kibaru wa lahụ żurriyyatun ḍu\'afā`, fa aṣābahā i\'ṣārun fīhi nārun faḥtaraqat, każālika yubayyinullāhu lakumul-āyāti la\'allakum tatafakkarụn'),
(273, 2, 267, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَنْفِقُوْا مِنْ طَيِّبٰتِ مَا كَسَبْتُمْ وَمِمَّآ اَخْرَجْنَا لَكُمْ مِّنَ الْاَرْضِ ۗ وَلَا تَيَمَّمُوا الْخَبِيْثَ مِنْهُ تُنْفِقُوْنَ وَلَسْتُمْ بِاٰخِذِيْهِ اِلَّآ اَنْ تُغْمِضُوْا فِيْهِ ۗ وَاعْلَمُوْٓا اَنَّ اللّٰهَ غَنِيٌّ حَمِيْدٌ', 'yā ayyuhallażīna āmanū anfiqụ min ṭayyibāti mā kasabtum wa mimmā akhrajnā lakum minal-arḍ, wa lā tayammamul-khabīṡa min-hu tunfiqụna wa lastum bi`ākhiżīhi illā an tugmiḍụ fīh, wa\'lamū annallāha ganiyyun ḥamīd'),
(274, 2, 268, 'اَلشَّيْطٰنُ يَعِدُكُمُ الْفَقْرَ وَيَأْمُرُكُمْ بِالْفَحْشَاۤءِ ۚ وَاللّٰهُ يَعِدُكُمْ مَّغْفِرَةً مِّنْهُ وَفَضْلًا ۗ وَاللّٰهُ وَاسِعٌ عَلِيْمٌ ۖ', 'asy-syaiṭānu ya\'idukumul-faqra wa ya`murukum bil-faḥsyā`, wallāhu ya\'idukum magfiratam min-hu wa faḍlā, wallāhu wāsi\'un \'alīm'),
(275, 2, 269, 'يُّؤْتِى الْحِكْمَةَ مَنْ يَّشَاۤءُ ۚ وَمَنْ يُّؤْتَ الْحِكْمَةَ فَقَدْ اُوْتِيَ خَيْرًا كَثِيْرًا ۗ وَمَا يَذَّكَّرُ اِلَّآ اُولُوا الْاَلْبَابِ', 'yu`til-ḥikmata may yasyā`, wa may yu`tal-ḥikmata fa qad ụtiya khairang kaṡīrā, wa mā yażżakkaru illā ulul-albāb'),
(276, 2, 270, 'وَمَآ اَنْفَقْتُمْ مِّنْ نَّفَقَةٍ اَوْ نَذَرْتُمْ مِّنْ نَّذْرٍ فَاِنَّ اللّٰهَ يَعْلَمُهٗ ۗ وَمَا لِلظّٰلِمِيْنَ مِنْ اَنْصَارٍ', 'wa mā anfaqtum min nafaqatin au nażartum min nażrin fa innallāha ya\'lamuh, wa mā liẓ-ẓālimīna min anṣār'),
(277, 2, 271, 'اِنْ تُبْدُوا الصَّدَقٰتِ فَنِعِمَّا هِيَۚ وَاِنْ تُخْفُوْهَا وَتُؤْتُوْهَا الْفُقَرَاۤءَ فَهُوَ خَيْرٌ لَّكُمْ ۗ وَيُكَفِّرُ عَنْكُمْ مِّنْ سَيِّاٰتِكُمْ ۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ', 'in tubduṣ-ṣadaqāti fa ni\'immā hiy, wa in tukhfụhā wa tu`tụhal-fuqarā`a fa huwa khairul lakum, wa yukaffiru \'angkum min sayyi`ātikum, wallāhu bimā ta\'malụna khabīr'),
(278, 2, 272, '۞ لَيْسَ عَلَيْكَ هُدٰىهُمْ وَلٰكِنَّ اللّٰهَ يَهْدِيْ مَنْ يَّشَاۤءُ ۗوَمَا تُنْفِقُوْا مِنْ خَيْرٍ فَلِاَنْفُسِكُمْ ۗوَمَا تُنْفِقُوْنَ اِلَّا ابْتِغَاۤءَ وَجْهِ اللّٰهِ ۗوَمَا تُنْفِقُوْا مِنْ خَيْرٍ يُّوَفَّ اِلَيْكُمْ وَاَنْتُمْ لَا تُظْلَمُوْنَ', 'laisa \'alaika hudāhum wa lākinnallāha yahdī may yasyā`, wa mā tunfiqụ min khairin fa li`anfusikum, wa mā tunfiqụna illabtigā`a waj-hillāh, wa mā tunfiqụ min khairiy yuwaffa ilaikum wa antum lā tuẓlamụn'),
(279, 2, 273, 'لِلْفُقَرَاۤءِ الَّذِيْنَ اُحْصِرُوْا فِيْ سَبِيْلِ اللّٰهِ لَا يَسْتَطِيْعُوْنَ ضَرْبًا فِى الْاَرْضِۖ يَحْسَبُهُمُ الْجَاهِلُ اَغْنِيَاۤءَ مِنَ التَّعَفُّفِۚ تَعْرِفُهُمْ بِسِيْمٰهُمْۚ لَا يَسْـَٔلُوْنَ النَّاسَ اِلْحَافًا ۗوَمَا تُنْفِقُوْا مِنْ خَيْرٍ فَاِنَّ اللّٰهَ بِهٖ عَلِيْمٌ', 'lil-fuqarā`illażīna uḥṣirụ fī sabīlillāhi lā yastaṭī\'ụna ḍarban fil-arḍi yaḥsabuhumul-jāhilu agniyā`a minat-ta\'affuf, ta\'rifuhum bisīmāhum, lā yas`alụnan-nāsa il-ḥāfā, wa mā tunfiqụ min khairin fa innallāha bihī \'alīm'),
(280, 2, 274, 'اَلَّذِيْنَ يُنْفِقُوْنَ اَمْوَالَهُمْ بِالَّيْلِ وَالنَّهَارِ سِرًّا وَّعَلَانِيَةً فَلَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ', 'allażīna yunfiqụna amwālahum bil-laili wan-nahāri sirraw wa \'alāniyatan fa lahum ajruhum \'inda rabbihim, wa lā khaufun \'alaihim wa lā hum yaḥzanụn'),
(281, 2, 275, 'اَلَّذِيْنَ يَأْكُلُوْنَ الرِّبٰوا لَا يَقُوْمُوْنَ اِلَّا كَمَا يَقُوْمُ الَّذِيْ يَتَخَبَّطُهُ الشَّيْطٰنُ مِنَ الْمَسِّۗ ذٰلِكَ بِاَنَّهُمْ قَالُوْٓا اِنَّمَا الْبَيْعُ مِثْلُ الرِّبٰواۘ وَاَحَلَّ اللّٰهُ الْبَيْعَ وَحَرَّمَ الرِّبٰواۗ فَمَنْ جَاۤءَهٗ مَوْعِظَةٌ مِّنْ رَّبِّهٖ فَانْتَهٰى فَلَهٗ مَا سَلَفَۗ وَاَمْرُهٗٓ اِلَى اللّٰهِ ۗ وَمَنْ عَادَ فَاُولٰۤىِٕكَ اَصْحٰبُ النَّارِ ۚ هُمْ فِيْهَا خٰلِدُوْنَ', 'allażīna ya`kulụnar-ribā lā yaqụmụna illā kamā yaqụmullażī yatakhabbaṭuhusy-syaiṭānu minal-mass, żālika bi`annahum qālū innamal-bai\'u miṡlur-ribā, wa aḥallallāhul-bai\'a wa ḥarramar-ribā, fa man jā`ahụ mau\'iẓatum mir rabbihī fantahā fa lahụ mā salaf, wa amruhū ilallāh, wa man \'āda fa ulā`ika aṣ-ḥābun-nār, hum fīhā khālidụn'),
(282, 2, 276, 'يَمْحَقُ اللّٰهُ الرِّبٰوا وَيُرْبِى الصَّدَقٰتِ ۗ وَاللّٰهُ لَا يُحِبُّ كُلَّ كَفَّارٍ اَثِيْمٍ', 'yam-ḥaqullāhur-ribā wa yurbiṣ-ṣadaqāt, wallāhu lā yuḥibbu kulla kaffārin aṡīm'),
(283, 2, 277, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَاَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ لَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ', 'innallażīna āmanụ wa \'amiluṣ-ṣāliḥāti wa aqāmuṣ-ṣalāta wa ātawuz-zakāta lahum ajruhum \'inda rabbihim, wa lā khaufun \'alaihim wa lā hum yaḥzanụn'),
(284, 2, 278, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَذَرُوْا مَا بَقِيَ مِنَ الرِّبٰوٓا اِنْ كُنْتُمْ مُّؤْمِنِيْنَ', 'yā ayyuhallażīna āmanuttaqullāha wa żarụ mā baqiya minar-ribā ing kuntum mu`minīn'),
(285, 2, 279, 'فَاِنْ لَّمْ تَفْعَلُوْا فَأْذَنُوْا بِحَرْبٍ مِّنَ اللّٰهِ وَرَسُوْلِهٖۚ وَاِنْ تُبْتُمْ فَلَكُمْ رُءُوْسُ اَمْوَالِكُمْۚ لَا تَظْلِمُوْنَ وَلَا تُظْلَمُوْنَ', 'fa il lam taf\'alụ fa`żanụ biḥarbim minallāhi wa rasụlih, wa in tubtum fa lakum ru`ụsu amwālikum, lā taẓlimụna wa lā tuẓlamụn'),
(286, 2, 280, 'وَاِنْ كَانَ ذُوْ عُسْرَةٍ فَنَظِرَةٌ اِلٰى مَيْسَرَةٍ ۗ وَاَنْ تَصَدَّقُوْا خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ', 'wa ing kāna żụ \'usratin fa naẓiratun ilā maisarah, wa an taṣaddaqụ khairul lakum ing kuntum ta\'lamụn'),
(287, 2, 281, 'وَاتَّقُوْا يَوْمًا تُرْجَعُوْنَ فِيْهِ اِلَى اللّٰهِ ۗثُمَّ تُوَفّٰى كُلُّ نَفْسٍ مَّا كَسَبَتْ وَهُمْ لَا يُظْلَمُوْنَ', 'wattaqụ yauman turja\'ụna fīhi ilallāh, ṡumma tuwaffā kullu nafsim mā kasabat wa hum lā yuẓlamụn'),
(288, 2, 282, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا تَدَايَنْتُمْ بِدَيْنٍ اِلٰٓى اَجَلٍ مُّسَمًّى فَاكْتُبُوْهُۗ وَلْيَكْتُبْ بَّيْنَكُمْ كَاتِبٌۢ بِالْعَدْلِۖ وَلَا يَأْبَ كَاتِبٌ اَنْ يَّكْتُبَ كَمَا عَلَّمَهُ اللّٰهُ فَلْيَكْتُبْۚ وَلْيُمْلِلِ الَّذِيْ عَلَيْهِ الْحَقُّ وَلْيَتَّقِ اللّٰهَ رَبَّهٗ وَلَا يَبْخَسْ مِنْهُ شَيْـًٔاۗ فَاِنْ كَانَ الَّذِيْ عَلَيْهِ الْحَقُّ سَفِيْهًا اَوْ ضَعِيْفًا اَوْ لَا يَسْتَطِيْعُ اَنْ يُّمِلَّ هُوَ فَلْيُمْلِلْ وَلِيُّهٗ بِالْعَدْلِۗ وَاسْتَشْهِدُوْا شَهِيْدَيْنِ مِنْ رِّجَالِكُمْۚ فَاِنْ لَّمْ يَكُوْنَا رَجُلَيْنِ فَرَجُلٌ وَّامْرَاَتٰنِ مِمَّنْ تَرْضَوْنَ مِنَ الشُّهَدَۤاءِ اَنْ تَضِلَّ اِحْدٰىهُمَا فَتُذَكِّرَ اِحْدٰىهُمَا الْاُخْرٰىۗ وَلَا يَأْبَ الشُّهَدَۤاءُ اِذَا مَا دُعُوْا ۗ وَلَا تَسْـَٔمُوْٓا اَنْ تَكْتُبُوْهُ صَغِيْرًا اَوْ كَبِيْرًا اِلٰٓى اَجَلِهٖۗ ذٰلِكُمْ اَقْسَطُ عِنْدَ اللّٰهِ وَاَقْوَمُ لِلشَّهَادَةِ وَاَدْنٰىٓ اَلَّا تَرْتَابُوْٓا اِلَّآ اَنْ تَكُوْنَ تِجَارَةً حَاضِرَةً تُدِيْرُوْنَهَا بَيْنَكُمْ فَلَيْسَ عَلَيْكُمْ جُنَاحٌ اَلَّا تَكْتُبُوْهَاۗ وَاَشْهِدُوْٓا اِذَا تَبَايَعْتُمْ ۖ وَلَا يُضَاۤرَّ كَاتِبٌ وَّلَا شَهِيْدٌ ەۗ وَاِنْ تَفْعَلُوْا فَاِنَّهٗ فُسُوْقٌۢ بِكُمْ ۗ وَاتَّقُوا اللّٰهَ ۗ وَيُعَلِّمُكُمُ اللّٰهُ ۗ وَاللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمٌ', 'yā ayyuhallażīna āmanū iżā tadāyantum bidainin ilā ajalim musamman faktubụh, walyaktub bainakum kātibum bil-\'adli wa lā ya`ba kātibun ay yaktuba kamā \'allamahullāhu falyaktub, walyumlilillażī \'alaihil-ḥaqqu walyattaqillāha rabbahụ wa lā yabkhas min-hu syai`ā, fa ing kānallażī \'alaihil-ḥaqqu safīhan au ḍa\'īfan au lā yastaṭī\'u ay yumilla huwa falyumlil waliyyuhụ bil-\'adl, wastasy-hidụ syahīdaini mir rijālikum, fa il lam yakụnā rajulaini fa rajuluw wamra`atāni mim man tarḍauna minasy-syuhadā`i an taḍilla iḥdāhumā fa tużakkira iḥdāhumal-ukhrā, wa lā ya`basy-syuhadā`u iżā mā du\'ụ, wa lā tas`amū an taktubụhu ṣagīran au kabīran ilā ajalih, żālikum aqsaṭu \'indallāhi wa aqwamu lisy-syahādati wa adnā allā tartābū illā an takụna tijāratan ḥāḍiratan tudīrụnahā bainakum fa laisa \'alaikum junāḥun allā taktubụhā, wa asy-hidū iżā tabāya\'tum wa lā yuḍārra kātibuw wa lā syahīd, wa in taf\'alụ fa innahụ fusụqum bikum, wattaqullāh, wa yu\'allimukumullāh, wallāhu bikulli syai`in \'alīm'),
(289, 2, 283, '۞ وَاِنْ كُنْتُمْ عَلٰى سَفَرٍ وَّلَمْ تَجِدُوْا كَاتِبًا فَرِهٰنٌ مَّقْبُوْضَةٌ ۗفَاِنْ اَمِنَ بَعْضُكُمْ بَعْضًا فَلْيُؤَدِّ الَّذِى اؤْتُمِنَ اَمَانَتَهٗ وَلْيَتَّقِ اللّٰهَ رَبَّهٗ ۗ وَلَا تَكْتُمُوا الشَّهَادَةَۗ وَمَنْ يَّكْتُمْهَا فَاِنَّهٗٓ اٰثِمٌ قَلْبُهٗ ۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ عَلِيْمٌ', 'wa ing kuntum \'alā safariw wa lam tajidụ kātiban fa rihānum maqbụḍah, fa in amina ba\'ḍukum ba\'ḍan falyu`addillażi`tumina amānatahụ walyattaqillāha rabbah, wa lā taktumusy-syahādah, wa may yaktum-hā fa innahū āṡimung qalbuh, wallāhu bimā ta\'malụna \'alīm'),
(290, 2, 284, ' لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ ۗ وَاِنْ تُبْدُوْا مَا فِيْٓ اَنْفُسِكُمْ اَوْ تُخْفُوْهُ يُحَاسِبْكُمْ بِهِ اللّٰهُ ۗ فَيَغْفِرُ لِمَنْ يَّشَاۤءُ وَيُعَذِّبُ مَنْ يَّشَاۤءُ ۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'lillāhi mā fis-samāwāti wa mā fil-arḍ, wa in tubdụ mā fī anfusikum au tukhfụhu yuḥāsibkum bihillāh, fa yagfiru limay yasyā`u wa yu\'ażżibu may yasyā`, wallāhu \'alā kulli syai`ing qadīr'),
(291, 2, 285, 'اٰمَنَ الرَّسُوْلُ بِمَآ اُنْزِلَ اِلَيْهِ مِنْ رَّبِّهٖ وَالْمُؤْمِنُوْنَۗ كُلٌّ اٰمَنَ بِاللّٰهِ وَمَلٰۤىِٕكَتِهٖ وَكُتُبِهٖ وَرُسُلِهٖۗ لَا نُفَرِّقُ بَيْنَ اَحَدٍ مِّنْ رُّسُلِهٖ ۗ وَقَالُوْا سَمِعْنَا وَاَطَعْنَا غُفْرَانَكَ رَبَّنَا وَاِلَيْكَ الْمَصِيْرُ', 'āmanar-rasụlu bimā unzila ilaihi mir rabbihī wal-mu`minụn, kullun āmana billāhi wa malā`ikatihī wa kutubihī wa rusulih, lā nufarriqu baina aḥadim mir rusulih, wa qālụ sami\'nā wa aṭa\'nā gufrānaka rabbanā wa ilaikal-maṣīr'),
(292, 2, 286, 'لَا يُكَلِّفُ اللّٰهُ نَفْسًا اِلَّا وُسْعَهَا ۗ لَهَا مَا كَسَبَتْ وَعَلَيْهَا مَا اكْتَسَبَتْ ۗ رَبَّنَا لَا تُؤَاخِذْنَآ اِنْ نَّسِيْنَآ اَوْ اَخْطَأْنَا ۚ رَبَّنَا وَلَا تَحْمِلْ عَلَيْنَآ اِصْرًا كَمَا حَمَلْتَهٗ عَلَى الَّذِيْنَ مِنْ قَبْلِنَا ۚ رَبَّنَا وَلَا تُحَمِّلْنَا مَا لَا طَاقَةَ لَنَا بِهٖۚ وَاعْفُ عَنَّاۗ وَاغْفِرْ لَنَاۗ وَارْحَمْنَا ۗ اَنْتَ مَوْلٰىنَا فَانْصُرْنَا عَلَى الْقَوْمِ الْكٰفِرِيْنَ', 'lā yukallifullāhu nafsan illā wus\'ahā, lahā mā kasabat wa \'alaihā maktasabat, rabbanā lā tu`ākhiżnā in nasīnā au akhṭa`nā, rabbanā wa lā taḥmil \'alainā iṣrang kamā ḥamaltahụ \'alallażīna ming qablinā, rabbanā wa lā tuḥammilnā mā lā ṭāqata lanā bih, wa\'fu \'annā, wagfir lanā, war-ḥamnā, anta maulānā fanṣurnā \'alal-qaumil-kāfirīn'),
(293, 3, 1, 'الۤمّۤ', 'alif lām mīm'),
(294, 3, 2, 'اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَ الْحَيُّ الْقَيُّوْمُۗ', 'allāhu lā ilāha illā huwal-ḥayyul-qayyụm'),
(295, 3, 3, 'نَزَّلَ عَلَيْكَ الْكِتٰبَ بِالْحَقِّ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ وَاَنْزَلَ التَّوْرٰىةَ وَالْاِنْجِيْلَۙ', 'nazzala \'alaikal-kitāba bil-ḥaqqi muṣaddiqal limā baina yadaihi wa anzalat-taurāta wal-injīl'),
(296, 3, 4, 'مِنْ قَبْلُ هُدًى لِّلنَّاسِ وَاَنْزَلَ الْفُرْقَانَ ەۗ اِنَّ الَّذِيْنَ كَفَرُوْا بِاٰيٰتِ اللّٰهِ لَهُمْ عَذَابٌ شَدِيْدٌ ۗوَاللّٰهُ عَزِيْزٌ ذُو انْتِقَامٍۗ', 'ming qablu hudal lin-nāsi wa anzalal-furqān, innallażīna kafarụ bi`āyātillāhi lahum \'ażābun syadīd, wallāhu \'azīzun żuntiqām'),
(297, 3, 5, 'اِنَّ اللّٰهَ لَا يَخْفٰى عَلَيْهِ شَيْءٌ فِى الْاَرْضِ وَلَا فِى السَّمَاۤءِ', 'innallāha lā yakhfā \'alaihi syai`un fil-arḍi wa lā fis-samā`'),
(298, 3, 6, 'هُوَ الَّذِيْ يُصَوِّرُكُمْ فِى الْاَرْحَامِ كَيْفَ يَشَاۤءُ ۗ لَآ اِلٰهَ اِلَّا هُوَ الْعَزِيْزُ الْحَكِيْمُ', 'huwallażī yuṣawwirukum fil-ar-ḥāmi kaifa yasyā`, lā ilāha illā huwal-\'azīzul-ḥakīm'),
(299, 3, 7, 'هُوَ الَّذِيْٓ اَنْزَلَ عَلَيْكَ الْكِتٰبَ مِنْهُ اٰيٰتٌ مُّحْكَمٰتٌ هُنَّ اُمُّ الْكِتٰبِ وَاُخَرُ مُتَشٰبِهٰتٌ ۗ فَاَمَّا الَّذِيْنَ فِيْ قُلُوْبِهِمْ زَيْغٌ فَيَتَّبِعُوْنَ مَا تَشَابَهَ مِنْهُ ابْتِغَاۤءَ الْفِتْنَةِ وَابْتِغَاۤءَ تَأْوِيْلِهٖۚ وَمَا يَعْلَمُ تَأْوِيْلَهٗٓ اِلَّا اللّٰهُ ۘوَالرَّاسِخُوْنَ فِى الْعِلْمِ يَقُوْلُوْنَ اٰمَنَّا بِهٖۙ كُلٌّ مِّنْ عِنْدِ رَبِّنَا ۚ وَمَا يَذَّكَّرُ اِلَّآ اُولُوا الْاَلْبَابِ', 'huwallażī anzala \'alaikal-kitāba min-hu āyātum muḥkamātun hunna ummul-kitābi wa ukharu mutasyābihāt, fa ammallażīna fī qulụbihim zaigun fayattabi\'ụna mā tasyābaha min-hubtigā`al-fitnati wabtigā`a ta`wīlih, wa mā ya\'lamu ta`wīlahū illallāh, war-rāsikhụna fil-\'ilmi yaqụlụna āmannā bihī kullum min \'indi rabbinā, wa mā yażżakkaru illā ulul-albāb'),
(300, 3, 8, 'رَبَّنَا لَا تُزِغْ قُلُوْبَنَا بَعْدَ اِذْ هَدَيْتَنَا وَهَبْ لَنَا مِنْ لَّدُنْكَ رَحْمَةً ۚاِنَّكَ اَنْتَ الْوَهَّابُ', 'rabbanā lā tuzig qulụbanā ba\'da iż hadaitanā wa hab lanā mil ladungka raḥmah, innaka antal-wahhāb'),
(301, 3, 9, 'رَبَّنَآ اِنَّكَ جَامِعُ النَّاسِ لِيَوْمٍ لَّا رَيْبَ فِيْهِ ۗاِنَّ اللّٰهَ لَا يُخْلِفُ الْمِيْعَادَ', 'rabbanā innaka jāmi\'un-nāsi liyaumil lā raiba fīh, innallāha lā yukhliful-mī\'ād'),
(302, 3, 10, 'اِنَّ الَّذِيْنَ كَفَرُوْا لَنْ تُغْنِيَ عَنْهُمْ اَمْوَالُهُمْ وَلَآ اَوْلَادُهُمْ مِّنَ اللّٰهِ شَيْـًٔا ۗوَاُولٰۤىِٕكَ هُمْ وَقُوْدُ النَّارِۗ', 'innallażīna kafarụ lan tugniya \'an-hum amwāluhum wa lā aulāduhum minallāhi syai`ā, wa ulā`ika hum waqụdun-nār'),
(303, 3, 11, 'كَدَأْبِ اٰلِ فِرْعَوْنَۙ وَالَّذِيْنَ مِنْ قَبْلِهِمْۗ كَذَّبُوْا بِاٰيٰتِنَاۚ فَاَخَذَهُمُ اللّٰهُ بِذُنُوْبِهِمْ ۗ وَاللّٰهُ شَدِيْدُ الْعِقَابِ', 'kada`bi āli fir\'auna wallażīna ming qablihim, każżabụ bi`āyātinā, fa akhażahumullāhu biżunụbihim, wallāhu syadīdul-\'iqāb'),
(304, 3, 12, 'قُلْ لِّلَّذِيْنَ كَفَرُوْا سَتُغْلَبُوْنَ وَتُحْشَرُوْنَ اِلٰى جَهَنَّمَ ۗ وَبِئْسَ الْمِهَادُ', 'qul lillażīna kafarụ satuglabụna wa tuḥsyarụna ilā jahannam, wa bi`sal-mihād'),
(305, 3, 13, 'قَدْ كَانَ لَكُمْ اٰيَةٌ فِيْ فِئَتَيْنِ الْتَقَتَا ۗفِئَةٌ تُقَاتِلُ فِيْ سَبِيْلِ اللّٰهِ وَاُخْرٰى كَافِرَةٌ يَّرَوْنَهُمْ مِّثْلَيْهِمْ رَأْيَ الْعَيْنِ ۗوَاللّٰهُ يُؤَيِّدُ بِنَصْرِهٖ مَنْ يَّشَاۤءُ ۗ اِنَّ فِيْ ذٰلِكَ لَعِبْرَةً لِّاُولِى الْاَبْصَارِ', 'qad kāna lakum āyatun fī fi`atainiltaqatā, fi`atun tuqātilu fī sabīlillāhi wa ukhrā kāfiratuy yaraunahum miṡlaihim ra`yal-\'aīn, wallāhu yu`ayyidu binaṣrihī may yasyā`, inna fī żālika la\'ibratal li`ulil-abṣār'),
(306, 3, 14, 'زُيِّنَ لِلنَّاسِ حُبُّ الشَّهَوٰتِ مِنَ النِّسَاۤءِ وَالْبَنِيْنَ وَالْقَنَاطِيْرِ الْمُقَنْطَرَةِ مِنَ الذَّهَبِ وَالْفِضَّةِ وَالْخَيْلِ الْمُسَوَّمَةِ وَالْاَنْعَامِ وَالْحَرْثِ ۗ ذٰلِكَ مَتَاعُ الْحَيٰوةِ الدُّنْيَا ۗوَاللّٰهُ عِنْدَهٗ حُسْنُ الْمَاٰبِ', 'zuyyina lin-nāsi ḥubbusy-syahawāti minan-nisā`i wal-banīna wal-qanaṭīril-muqanṭarati minaż-żahabi wal-fiḍḍati wal-khailil-musawwamati wal-an\'āmi wal-ḥarṡ, żālika matā\'ul-ḥayātid-dun-yā, wallāhu \'indahụ ḥusnul-ma`āb'),
(307, 3, 15, '۞ قُلْ اَؤُنَبِّئُكُمْ بِخَيْرٍ مِّنْ ذٰلِكُمْ ۗ لِلَّذِيْنَ اتَّقَوْا عِنْدَ رَبِّهِمْ جَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا وَاَزْوَاجٌ مُّطَهَّرَةٌ وَّرِضْوَانٌ مِّنَ اللّٰهِ ۗ وَاللّٰهُ بَصِيْرٌۢ بِالْعِبَادِۚ', 'qul a unabbi`ukum bikhairim min żālikum, lillażīnattaqau \'inda rabbihim jannātun tajrī min taḥtihal-an-hāru khālidīna fīhā wa azwājum muṭahharatuw wa riḍwānum minallāh, wallāhu baṣīrum bil-\'ibād'),
(308, 3, 16, 'اَلَّذِيْنَ يَقُوْلُوْنَ رَبَّنَآ اِنَّنَآ اٰمَنَّا فَاغْفِرْ لَنَا ذُنُوْبَنَا وَقِنَا عَذَابَ النَّارِۚ', 'allażīna yaqụlụna rabbanā innanā āmannā fagfir lanā żunụbanā wa qinā \'ażāban-nār'),
(309, 3, 17, 'اَلصّٰبِرِيْنَ وَالصّٰدِقِيْنَ وَالْقٰنِتِيْنَ وَالْمُنْفِقِيْنَ وَالْمُسْتَغْفِرِيْنَ بِالْاَسْحَارِ', 'aṣ-ṣābirīna waṣ-ṣādiqīna wal-qānitīna wal-munfiqīna wal-mustagfirīna bil-as-ḥār'),
(310, 3, 18, 'شَهِدَ اللّٰهُ اَنَّهٗ لَآ اِلٰهَ اِلَّا هُوَۙ وَالْمَلٰۤىِٕكَةُ وَاُولُوا الْعِلْمِ قَاۤىِٕمًاۢ بِالْقِسْطِۗ لَآ اِلٰهَ اِلَّا هُوَ الْعَزِيْزُ الْحَكِيْمُ', 'syahidallāhu annahụ lā ilāha illā huwa wal-malā`ikatu wa ulul-\'ilmi qā`imam bil-qisṭ, lā ilāha illā huwal-\'azīzul-ḥakīm'),
(311, 3, 19, 'اِنَّ الدِّيْنَ عِنْدَ اللّٰهِ الْاِسْلَامُ ۗ وَمَا اخْتَلَفَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ اِلَّا مِنْۢ بَعْدِ مَا جَاۤءَهُمُ الْعِلْمُ بَغْيًاۢ بَيْنَهُمْ ۗوَمَنْ يَّكْفُرْ بِاٰيٰتِ اللّٰهِ فَاِنَّ اللّٰهَ سَرِيْعُ الْحِسَابِ', 'innad-dīna \'indallāhil-islām, wa makhtalafallażīna ụtul-kitāba illā mim ba\'di mā jā`ahumul-\'ilmu bagyam bainahum, wa may yakfur bi`āyātillāhi fa innallāha sarī\'ul-ḥisāb'),
(312, 3, 20, 'فَاِنْ حَاۤجُّوْكَ فَقُلْ اَسْلَمْتُ وَجْهِيَ لِلّٰهِ وَمَنِ اتَّبَعَنِ ۗوَقُلْ لِّلَّذِيْنَ اُوْتُوا الْكِتٰبَ وَالْاُمِّيّٖنَ ءَاَسْلَمْتُمْ ۗ فَاِنْ اَسْلَمُوْا فَقَدِ اهْتَدَوْا ۚ وَاِنْ تَوَلَّوْا فَاِنَّمَا عَلَيْكَ الْبَلٰغُ ۗ وَاللّٰهُ بَصِيْرٌۢ بِالْعِبَادِ', 'fa in ḥājjụka fa qul aslamtu waj-hiya lillāhi wa manittaba\'an, wa qul lillażīna ụtul-kitāba wal-ummiyyīna a aslamtum, fa in aslamụ fa qadihtadau, wa in tawallau fa innamā \'alaikal-balāg, wallāhu baṣīrum bil-\'ibād'),
(313, 3, 21, 'اِنَّ الَّذِيْنَ يَكْفُرُوْنَ بِاٰيٰتِ اللّٰهِ وَيَقْتُلُوْنَ النَّبِيّٖنَ بِغَيْرِحَقٍّۖ وَّيَقْتُلُوْنَ الَّذِيْنَ يَأْمُرُوْنَ بِالْقِسْطِ مِنَ النَّاسِۙ فَبَشِّرْهُمْ بِعَذَابٍ اَلِيْمٍ', 'innallażīna yakfurụna bi`āyātillāhi wa yaqtulụnan-nabiyyīna bigairi ḥaqqiw wa yaqtulụnallażīna ya`murụna bil-qisṭi minan-nāsi fa basysyir-hum bi\'ażābin alīm'),
(314, 3, 22, 'اُولٰۤىِٕكَ الَّذِيْنَ حَبِطَتْ اَعْمَالُهُمْ فِى الدُّنْيَا وَالْاٰخِرَةِ ۖ وَمَا لَهُمْ مِّنْ نّٰصِرِيْنَ', 'ulā`ikallażīna ḥabiṭat a\'māluhum fid-dun-yā wal-ākhirati wa mā lahum min nāṣirīn'),
(315, 3, 23, 'اَلَمْ تَرَ اِلَى الَّذِيْنَ اُوْتُوْا نَصِيْبًا مِّنَ الْكِتٰبِ يُدْعَوْنَ اِلٰى كِتٰبِ اللّٰهِ لِيَحْكُمَ بَيْنَهُمْ ثُمَّ يَتَوَلّٰى فَرِيْقٌ مِّنْهُمْ وَهُمْ مُّعْرِضُوْنَ', 'a lam tara ilallażīna ụtụ naṣībam minal-kitābi yud\'auna ilā kitābillāhi liyaḥkuma bainahum ṡumma yatawallā farīqum min-hum wa hum mu\'riḍụn'),
(316, 3, 24, 'ذٰلِكَ بِاَنَّهُمْ قَالُوْا لَنْ تَمَسَّنَا النَّارُ اِلَّآ اَيَّامًا مَّعْدُوْدٰتٍ ۖ وَّغَرَّهُمْ فِيْ دِيْنِهِمْ مَّا كَانُوْا يَفْتَرُوْنَ', 'żālika bi`annahum qālụ lan tamassanan-nāru illā ayyāmam ma\'dụdātiw wa garrahum fī dīnihim mā kānụ yaftarụn'),
(317, 3, 25, 'فَكَيْفَ اِذَا جَمَعْنٰهُمْ لِيَوْمٍ لَّا رَيْبَ فِيْهِۗ وَوُفِّيَتْ كُلُّ نَفْسٍ مَّا كَسَبَتْ وَهُمْ لَا يُظْلَمُوْنَ', 'fa kaifa iżā jama\'nāhum liyaumil lā raiba fīh, wa wuffiyat kullu nafsim mā kasabat wa hum lā yuẓlamụn'),
(318, 3, 26, 'قُلِ اللهم مٰلِكَ الْمُلْكِ تُؤْتِى الْمُلْكَ مَنْ تَشَاۤءُ وَتَنْزِعُ الْمُلْكَ مِمَّنْ تَشَاۤءُۖ وَتُعِزُّ مَنْ تَشَاۤءُ وَتُذِلُّ مَنْ تَشَاۤءُ ۗ بِيَدِكَ الْخَيْرُ ۗ اِنَّكَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'qulillāhumma mālikal-mulki tu`til-mulka man tasyā`u wa tanzi\'ul-mulka mim man tasyā`u wa tu\'izzu man tasyā`u wa tużillu man tasyā`, biyadikal-khaīr, innaka \'alā kulli syai`ing qadīr'),
(319, 3, 27, 'تُوْلِجُ الَّيْلَ فِى النَّهَارِ وَتُوْلِجُ النَّهَارَ فِى الَّيْلِ وَتُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَتُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَتَرْزُقُ مَنْ تَشَاۤءُ بِغَيْرِ حِسَابٍ', 'tụlijul-laila fin-nahāri wa tụlijun-nahāra fil-laili wa tukhrijul-ḥayya minal-mayyiti wa tukhrijul-mayyita minal-ḥayyi wa tarzuqu man tasyā`u bigairi ḥisāb'),
(320, 3, 28, 'لَا يَتَّخِذِ الْمُؤْمِنُوْنَ الْكٰفِرِيْنَ اَوْلِيَاۤءَ مِنْ دُوْنِ الْمُؤْمِنِيْنَۚ وَمَنْ يَّفْعَلْ ذٰلِكَ فَلَيْسَ مِنَ اللّٰهِ فِيْ شَيْءٍ اِلَّآ اَنْ تَتَّقُوْا مِنْهُمْ تُقٰىةً ۗ وَيُحَذِّرُكُمُ اللّٰهُ نَفْسَهٗ ۗ وَاِلَى اللّٰهِ الْمَصِيْرُ', 'lā yattakhiżil-mu`minụnal-kāfirīna auliyā`a min dụnil-mu`minīn, wa may yaf\'al żālika fa laisa minallāhi fī syai`in illā an tattaqụ min-hum tuqāh, wa yuḥażżirukumullāhu nafsah, wa ilallāhil-maṣīr'),
(321, 3, 29, 'قُلْ اِنْ تُخْفُوْا مَا فِيْ صُدُوْرِكُمْ اَوْ تُبْدُوْهُ يَعْلَمْهُ اللّٰهُ ۗوَيَعْلَمُ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'qul in tukhfụ mā fī ṣudụrikum au tubdụhu ya\'lam-hullāh, wa ya\'lamu mā fis-samāwāti wa mā fil-arḍ, wallāhu \'alā kulli syai`ing qadīr'),
(322, 3, 30, 'يَوْمَ تَجِدُ كُلُّ نَفْسٍ مَّا عَمِلَتْ مِنْ خَيْرٍ مُّحْضَرًا ۛوَمَا عَمِلَتْ مِنْ سُوْۤءٍ ۛ تَوَدُّ لَوْ اَنَّ بَيْنَهَا وَبَيْنَهٗٓ اَمَدًاۢ بَعِيْدًا ۗوَيُحَذِّرُكُمُ اللّٰهُ نَفْسَهٗ ۗوَاللّٰهُ رَءُوْفٌۢ بِالْعِبَادِ', 'yauma tajidu kullu nafsim mā \'amilat min khairim muḥḍaraw wa mā \'amilat min sū`, tawaddu lau anna bainahā wa bainahū amadam ba\'īdā, wa yuḥażżirukumullāhu nafsah, wallāhu ra`ụfum bil-\'ibād'),
(323, 3, 31, 'قُلْ اِنْ كُنْتُمْ تُحِبُّوْنَ اللّٰهَ فَاتَّبِعُوْنِيْ يُحْبِبْكُمُ اللّٰهُ وَيَغْفِرْ لَكُمْ ذُنُوْبَكُمْ ۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ', 'qul ing kuntum tuḥibbụnallāha fattabi\'ụnī yuḥbibkumullāhu wa yagfir lakum żunụbakum, wallāhu gafụrur raḥīm'),
(324, 3, 32, 'قُلْ اَطِيْعُوا اللّٰهَ وَالرَّسُوْلَ ۚ فَاِنْ تَوَلَّوْا فَاِنَّ اللّٰهَ لَا يُحِبُّ الْكٰفِرِيْنَ', 'qul aṭī\'ullāha war-rasụl, fa in tawallau fa innallāha lā yuḥibbul-kāfirīn'),
(325, 3, 33, '۞ اِنَّ اللّٰهَ اصْطَفٰىٓ اٰدَمَ وَنُوْحًا وَّاٰلَ اِبْرٰهِيْمَ وَاٰلَ عِمْرَانَ عَلَى الْعٰلَمِيْنَۙ', 'innallāhaṣṭafā ādama wa nụḥaw wa āla ibrāhīma wa āla \'imrāna \'alal-\'ālamīn'),
(326, 3, 34, 'ذُرِّيَّةً ۢ بَعْضُهَا مِنْۢ بَعْضٍۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌۚ', 'żurriyyatam ba\'ḍuhā mim ba\'ḍ, wallāhu samī\'un \'alīm'),
(327, 3, 35, 'اِذْ قَالَتِ امْرَاَتُ عِمْرَانَ رَبِّ اِنِّيْ نَذَرْتُ لَكَ مَا فِيْ بَطْنِيْ مُحَرَّرًا فَتَقَبَّلْ مِنِّيْ ۚ اِنَّكَ اَنْتَ السَّمِيْعُ الْعَلِيْمُ', 'iż qālatimra`atu \'imrāna rabbi innī nażartu laka mā fī baṭnī muḥarraran fa taqabbal minnī, innaka antas-samī\'ul-\'alīm'),
(328, 3, 36, 'فَلَمَّا وَضَعَتْهَا قَالَتْ رَبِّ اِنِّيْ وَضَعْتُهَآ اُنْثٰىۗ وَاللّٰهُ اَعْلَمُ بِمَا وَضَعَتْۗ وَلَيْسَ الذَّكَرُ كَالْاُنْثٰى ۚ وَاِنِّيْ سَمَّيْتُهَا مَرْيَمَ وَاِنِّيْٓ اُعِيْذُهَا بِكَ وَذُرِّيَّتَهَا مِنَ الشَّيْطٰنِ الرَّجِيْمِ', 'fa lammā waḍa\'at-hā qālat rabbi innī waḍa\'tuhā unṡā, wallāhu a\'lamu bimā waḍa\'at, wa laisaż-żakaru kal-unṡā, wa innī sammaituhā maryama wa innī u\'īżuhā bika wa żurriyyatahā minasy-syaiṭānir-rajīm'),
(329, 3, 37, 'فَتَقَبَّلَهَا رَبُّهَا بِقَبُوْلٍ حَسَنٍ وَّاَنْۢبَتَهَا نَبَاتًا حَسَنًاۖ وَّكَفَّلَهَا زَكَرِيَّا ۗ كُلَّمَا دَخَلَ عَلَيْهَا زَكَرِيَّا الْمِحْرَابَۙ وَجَدَ عِنْدَهَا رِزْقًا ۚ قَالَ يٰمَرْيَمُ اَنّٰى لَكِ هٰذَا ۗ قَالَتْ هُوَ مِنْ عِنْدِ اللّٰهِ ۗ اِنَّ اللّٰهَ يَرْزُقُ مَنْ يَّشَاۤءُ بِغَيْرِ حِسَابٍ', 'fa taqabbalahā rabbuhā biqabụlin ḥasaniw wa ambatahā nabātan ḥasanaw wa kaffalahā zakariyyā, kullamā dakhala \'alaihā zakariyyal-miḥrāba wajada \'indahā rizqā, qāla yā maryamu annā laki hāżā, qālat huwa min \'indillāh, innallāha yarzuqu may yasyā`u bigairi ḥisāb'),
(330, 3, 38, 'هُنَالِكَ دَعَا زَكَرِيَّا رَبَّهٗ ۚ قَالَ رَبِّ هَبْ لِيْ مِنْ لَّدُنْكَ ذُرِّيَّةً طَيِّبَةً ۚ اِنَّكَ سَمِيْعُ الدُّعَاۤءِ', 'hunālika da\'ā zakariyyā rabbah, qāla rabbi hab lī mil ladungka żurriyyatan ṭayyibah, innaka samī\'ud-du\'ā`'),
(331, 3, 39, 'فَنَادَتْهُ الْمَلٰۤىِٕكَةُ وَهُوَ قَاۤىِٕمٌ يُّصَلِّيْ فِى الْمِحْرَابِۙ اَنَّ اللّٰهَ يُبَشِّرُكَ بِيَحْيٰى مُصَدِّقًاۢ بِكَلِمَةٍ مِّنَ اللّٰهِ وَسَيِّدًا وَّحَصُوْرًا وَّنَبِيًّا مِّنَ الصّٰلِحِيْنَ', 'fa nādat-hul-malā`ikatu wa huwa qā`imuy yuṣallī fil-miḥrābi annallāha yubasysyiruka biyaḥyā muṣaddiqam bikalimatim minallāhi wa sayyidaw wa ḥaṣụraw wa nabiyyam minaṣ-ṣāliḥīn'),
(332, 3, 40, 'قَالَ رَبِّ اَنّٰى يَكُوْنُ لِيْ غُلٰمٌ وَّقَدْ بَلَغَنِيَ الْكِبَرُ وَامْرَاَتِيْ عَاقِرٌ ۗ قَالَ كَذٰلِكَ اللّٰهُ يَفْعَلُ مَا يَشَاۤءُ', 'qāla rabbi annā yakụnu lī gulāmuw wa qad balaganiyal-kibaru wamra`atī \'āqir, qāla każālikallāhu yaf\'alu mā yasyā`'),
(333, 3, 41, 'قَالَ رَبِّ اجْعَلْ لِّيْٓ اٰيَةً ۗ قَالَ اٰيَتُكَ اَلَّا تُكَلِّمَ النَّاسَ ثَلٰثَةَ اَيَّامٍ اِلَّا رَمْزًا ۗ وَاذْكُرْ رَّبَّكَ كَثِيْرًا وَّسَبِّحْ بِالْعَشِيِّ وَالْاِبْكَارِ', 'qāla rabbij\'al lī āyah, qāla āyatuka allā tukalliman-nāsa ṡalāṡata ayyāmin illā ramzā, ważkur rabbaka kaṡīraw wa sabbiḥ bil-\'asyiyyi wal-ibkār'),
(334, 3, 42, 'وَاِذْ قَالَتِ الْمَلٰۤىِٕكَةُ يٰمَرْيَمُ اِنَّ اللّٰهَ اصْطَفٰىكِ وَطَهَّرَكِ وَاصْطَفٰىكِ عَلٰى نِسَاۤءِ الْعٰلَمِيْنَ', 'wa iż qālatil-malā`ikatu yā maryamu innallāhaṣṭafāki wa ṭahharaki waṣṭafāki \'alā nisā`il-\'ālamīn'),
(335, 3, 43, 'يٰمَرْيَمُ اقْنُتِيْ لِرَبِّكِ وَاسْجُدِيْ وَارْكَعِيْ مَعَ الرَّاكِعِيْنَ', 'yā maryamuqnutī lirabbiki wasjudī warka\'ī ma\'ar-rāki\'īn'),
(336, 3, 44, 'ذٰلِكَ مِنْ اَنْۢبَاۤءِ الْغَيْبِ نُوْحِيْهِ اِلَيْكَ ۗوَمَا كُنْتَ لَدَيْهِمْ اِذْ يُلْقُوْنَ اَقْلَامَهُمْ اَيُّهُمْ يَكْفُلُ مَرْيَمَۖ وَمَا كُنْتَ لَدَيْهِمْ اِذْ يَخْتَصِمُوْنَ', 'żālika min ambā`il-gaibi nụḥīhi ilaīk, wa mā kunta ladaihim iż yulqụna aqlāmahum ayyuhum yakfulu maryama wa mā kunta ladaihim iż yakhtaṣimụn'),
(337, 3, 45, 'اِذْ قَالَتِ الْمَلٰۤىِٕكَةُ يٰمَرْيَمُ اِنَّ اللّٰهَ يُبَشِّرُكِ بِكَلِمَةٍ مِّنْهُۖ اسْمُهُ الْمَسِيْحُ عِيْسَى ابْنُ مَرْيَمَ وَجِيْهًا فِى الدُّنْيَا وَالْاٰخِرَةِ وَمِنَ الْمُقَرَّبِيْنَۙ', 'iż qālatil-malā`ikatu yā maryamu innallāha yubasysyiruki bikalimatim min-husmuhul-masīḥu \'īsabnu maryama wajīhan fid-dun-yā wal-ākhirati wa minal-muqarrabīn'),
(338, 3, 46, 'وَيُكَلِّمُ النَّاسَ فِى الْمَهْدِ وَكَهْلًا وَّمِنَ الصّٰلِحِيْنَ', 'wa yukallimun-nāsa fil-mahdi wa kahlaw wa minaṣ-ṣāliḥīn'),
(339, 3, 47, 'قَالَتْ رَبِّ اَنّٰى يَكُوْنُ لِيْ وَلَدٌ وَّلَمْ يَمْسَسْنِيْ بَشَرٌ ۗ قَالَ كَذٰلِكِ اللّٰهُ يَخْلُقُ مَا يَشَاۤءُ ۗاِذَا قَضٰٓى اَمْرًا فَاِنَّمَا يَقُوْلُ لَهٗ كُنْ فَيَكُوْنُ', 'qālat rabbi annā yakụnu lī waladuw wa lam yamsasnī basyar, qāla każālikillāhu yakhluqu mā yasyā`, iżā qaḍā amran fa innamā yaqụlu lahụ kun fa yakụn'),
(340, 3, 48, 'وَيُعَلِّمُهُ الْكِتٰبَ وَالْحِكْمَةَ وَالتَّوْرٰىةَ وَالْاِنْجِيْلَۚ', 'wa yu\'allimuhul-kitāba wal-ḥikmata wat-taurāta wal-injīl'),
(341, 3, 49, 'وَرَسُوْلًا اِلٰى بَنِيْٓ اِسْرَاۤءِيْلَ ەۙ اَنِّيْ قَدْ جِئْتُكُمْ بِاٰيَةٍ مِّنْ رَّبِّكُمْ ۙاَنِّيْٓ اَخْلُقُ لَكُمْ مِّنَ الطِّيْنِ كَهَيْـَٔةِ الطَّيْرِ فَاَنْفُخُ فِيْهِ فَيَكُوْنُ طَيْرًاۢ بِاِذْنِ اللّٰهِ ۚوَاُبْرِئُ الْاَكْمَهَ وَالْاَبْرَصَ وَاُحْيِ الْمَوْتٰى بِاِذْنِ اللّٰهِ ۚوَاُنَبِّئُكُمْ بِمَا تَأْكُلُوْنَ وَمَا تَدَّخِرُوْنَ ۙفِيْ بُيُوْتِكُمْ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيَةً لَّكُمْ اِنْ كُنْتُمْ مُّؤْمِنِيْنَۚ', 'wa rasụlan ilā banī isrā`īla annī qad ji`tukum bi`āyatim mir rabbikum annī akhluqu lakum minaṭ-ṭīni kahai`atiṭ-ṭairi fa anfukhu fīhi fa yakụnu ṭairam bi`iżnillāh, wa ubri`ul-akmaha wal-abraṣa wa uḥyil-mautā bi`iżnillāh, wa unabbi`ukum bimā ta`kulụna wa mā taddakhirụna fī buyụtikum, inna fī żālika la`āyatal lakum ing kuntum mu`minīn'),
(342, 3, 50, 'وَمُصَدِّقًا لِّمَا بَيْنَ يَدَيَّ مِنَ التَّوْرٰىةِ وَلِاُحِلَّ لَكُمْ بَعْضَ الَّذِيْ حُرِّمَ عَلَيْكُمْ وَجِئْتُكُمْ بِاٰيَةٍ مِّنْ رَّبِّكُمْۗ فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ', 'wa muṣaddiqal limā baina yadayya minat-taurāti wa li`uḥilla lakum ba\'ḍallażī ḥurrima \'alaikum wa ji`tukum bi`āyatim mir rabbikum, fattaqullāha wa aṭī\'ụn'),
(343, 3, 51, 'اِنَّ اللّٰهَ رَبِّيْ وَرَبُّكُمْ فَاعْبُدُوْهُ ۗهٰذَا صِرَاطٌ مُّسْتَقِيْمٌ', 'innallāha rabbī wa rabbukum fa\'budụh, hāżā ṣirāṭum mustaqīm'),
(344, 3, 52, '۞ فَلَمَّآ اَحَسَّ عِيْسٰى مِنْهُمُ الْكُفْرَ قَالَ مَنْ اَنْصَارِيْٓ اِلَى اللّٰهِ ۗ قَالَ الْحَوَارِيُّوْنَ نَحْنُ اَنْصَارُ اللّٰهِ ۚ اٰمَنَّا بِاللّٰهِ ۚ وَاشْهَدْ بِاَنَّا مُسْلِمُوْنَ', 'fa lammā aḥassa \'īsā min-humul-kufra qāla man anṣārī ilallāh, qālal-ḥawāriyyụna naḥnu anṣārullāh, āmannā billāh, wasy-had bi`annā muslimụn'),
(345, 3, 53, 'رَبَّنَآ اٰمَنَّا بِمَآ اَنْزَلْتَ وَاتَّبَعْنَا الرَّسُوْلَ فَاكْتُبْنَا مَعَ الشّٰهِدِيْنَ', 'rabbanā āmannā bimā anzalta wattaba\'nar-rasụla faktubnā ma\'asy-syāhidīn'),
(346, 3, 54, 'وَمَكَرُوْا وَمَكَرَ اللّٰهُ ۗوَاللّٰهُ خَيْرُ الْمَاكِرِيْنَ', 'wa makarụ wa makarallāh, wallāhu khairul-mākirīn'),
(347, 3, 55, 'اِذْ قَالَ اللّٰهُ يٰعِيْسٰٓى اِنِّيْ مُتَوَفِّيْكَ وَرَافِعُكَ اِلَيَّ وَمُطَهِّرُكَ مِنَ الَّذِيْنَ كَفَرُوْا وَجَاعِلُ الَّذِيْنَ اتَّبَعُوْكَ فَوْقَ الَّذِيْنَ كَفَرُوْٓا اِلٰى يَوْمِ الْقِيٰمَةِ ۚ ثُمَّ اِلَيَّ مَرْجِعُكُمْ فَاَحْكُمُ بَيْنَكُمْ فِيْمَا كُنْتُمْ فِيْهِ تَخْتَلِفُوْنَ', 'iż qālallāhu yā \'īsā innī mutawaffīka wa rāfi\'uka ilayya wa muṭahhiruka minallażīna kafarụ wa jā\'ilullażīnattaba\'ụka fauqallażīna kafarū ilā yaumil-qiyāmah, ṡumma ilayya marji\'ukum fa aḥkumu bainakum fīmā kuntum fīhi takhtalifụn'),
(348, 3, 56, 'فَاَمَّا الَّذِيْنَ كَفَرُوْا فَاُعَذِّبُهُمْ عَذَابًا شَدِيْدًا فِى الدُّنْيَا وَالْاٰخِرَةِۖ وَمَا لَهُمْ مِّنْ نّٰصِرِيْنَ', 'fa ammallażīna kafarụ fa u\'ażżibuhum \'ażāban syadīdan fid-dun-yā wal-ākhirati wa mā lahum min nāṣirīn'),
(349, 3, 57, 'وَاَمَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَيُوَفِّيْهِمْ اُجُوْرَهُمْ ۗ وَاللّٰهُ لَا يُحِبُّ الظّٰلِمِيْنَ', 'wa ammallażīna āmanụ wa \'amiluṣ-ṣāliḥāti fa yuwaffīhim ujụrahum, wallāhu lā yuḥibbuẓ-ẓālimīn'),
(350, 3, 58, 'ذٰلِكَ نَتْلُوْهُ عَلَيْكَ مِنَ الْاٰيٰتِ وَالذِّكْرِ الْحَكِيْمِ', 'żālika natlụhu \'alaika minal-āyāti waż-żikril-ḥakīm'),
(351, 3, 59, 'اِنَّ مَثَلَ عِيْسٰى عِنْدَ اللّٰهِ كَمَثَلِ اٰدَمَ ۗ خَلَقَهٗ مِنْ تُرَابٍ ثُمَّ قَالَ لَهٗ كُنْ فَيَكُوْنُ', 'inna maṡala \'īsā \'indallāhi kamaṡali ādam, khalaqahụ min turābin ṡumma qāla lahụ kun fa yakụn'),
(352, 3, 60, 'اَلْحَقُّ مِنْ رَّبِّكَ فَلَا تَكُنْ مِّنَ الْمُمْتَرِيْنَ', 'al-ḥaqqu mir rabbika fa lā takum minal-mumtarīn'),
(353, 3, 61, 'فَمَنْ حَاۤجَّكَ فِيْهِ مِنْۢ بَعْدِ مَا جَاۤءَكَ مِنَ الْعِلْمِ فَقُلْ تَعَالَوْا نَدْعُ اَبْنَاۤءَنَا وَاَبْنَاۤءَكُمْ وَنِسَاۤءَنَا وَنِسَاۤءَكُمْ وَاَنْفُسَنَا وَاَنْفُسَكُمْۗ ثُمَّ نَبْتَهِلْ فَنَجْعَلْ لَّعْنَتَ اللّٰهِ عَلَى الْكٰذِبِيْنَ', 'fa man ḥājjaka fīhi mim ba\'di mā jā`aka minal-\'ilmi fa qul ta\'ālau nad\'u abnā`anā wa abnā`akum wa nisā`anā wa nisā`akum wa anfusanā wa anfusakum, ṡumma nabtahil fa naj\'al la\'natallāhi \'alal-kāżibīn'),
(354, 3, 62, 'اِنَّ هٰذَا لَهُوَ الْقَصَصُ الْحَقُّ ۚ وَمَا مِنْ اِلٰهٍ اِلَّا اللّٰهُ ۗوَاِنَّ اللّٰهَ لَهُوَ الْعَزِيْزُ الْحَكِيْمُ', 'inna hāżā lahuwal-qaṣaṣul-ḥaqq, wa mā min ilāhin illallāh, wa innallāha lahuwal-\'azīzul-ḥakīm'),
(355, 3, 63, 'فَاِنْ تَوَلَّوْا فَاِنَّ اللّٰهَ عَلِيْمٌ ۢبِالْمُفْسِدِيْنَ', 'fa in tawallau fa innallāha \'alīmum bil-mufsidīn'),
(356, 3, 64, 'قُلْ يٰٓاَهْلَ الْكِتٰبِ تَعَالَوْا اِلٰى كَلِمَةٍ سَوَاۤءٍۢ بَيْنَنَا وَبَيْنَكُمْ اَلَّا نَعْبُدَ اِلَّا اللّٰهَ وَلَا نُشْرِكَ بِهٖ شَيْـًٔا وَّلَا يَتَّخِذَ بَعْضُنَا بَعْضًا اَرْبَابًا مِّنْ دُوْنِ اللّٰهِ ۗ فَاِنْ تَوَلَّوْا فَقُوْلُوا اشْهَدُوْا بِاَنَّا مُسْلِمُوْنَ', 'qul yā ahlal-kitābi ta\'ālau ilā kalimatin sawā`im bainanā wa bainakum allā na\'buda illallāha wa lā nusyrika bihī syai`aw wa lā yattakhiża ba\'ḍunā ba\'ḍan arbābam min dụnillāh, fa in tawallau fa qụlusy-hadụ bi`annā muslimụn'),
(357, 3, 65, 'يٰٓاَهْلَ الْكِتٰبِ لِمَ تُحَاۤجُّوْنَ فِيْٓ اِبْرٰهِيْمَ وَمَآ اُنْزِلَتِ التَّوْرٰىةُ وَالْاِنْجِيْلُ اِلَّا مِنْۢ بَعْدِهٖۗ اَفَلَا تَعْقِلُوْنَ', 'yā ahlal-kitābi lima tuḥājjụna fī ibrāhīma wa mā unzilatit-taurātu wal-injīlu illā mim ba\'dih, a fa lā ta\'qilụn'),
(358, 3, 66, 'هٰٓاَنْتُمْ هٰٓؤُلَاۤءِ حَاجَجْتُمْ فِيْمَا لَكُمْ بِهٖ عِلْمٌ فَلِمَ تُحَاۤجُّوْنَ فِيْمَا لَيْسَ لَكُمْ بِهٖ عِلْمٌ ۗ وَاللّٰهُ يَعْلَمُ واَنْتُمْ لَا تَعْلَمُوْنَ', 'hā`antum hā`ulā`i ḥājajtum fīmā lakum bihī \'ilmun fa lima tuḥājjụna fīmā laisa lakum bihī \'ilm, wallāhu ya\'lamu wa antum lā ta\'lamụn'),
(359, 3, 67, 'مَاكَانَ اِبْرٰهِيْمُ يَهُوْدِيًّا وَّلَا نَصْرَانِيًّا وَّلٰكِنْ كَانَ حَنِيْفًا مُّسْلِمًاۗ وَمَا كَانَ مِنَ الْمُشْرِكِيْنَ', 'mā kāna ibrāhīmu yahụdiyyaw wa lā naṣrāniyyaw wa lāking kāna ḥanīfam muslimā, wa mā kāna minal-musyrikīn'),
(360, 3, 68, 'اِنَّ اَوْلَى النَّاسِ بِاِبْرٰهِيْمَ لَلَّذِيْنَ اتَّبَعُوْهُ وَهٰذَا النَّبِيُّ وَالَّذِيْنَ اٰمَنُوْا ۗ وَاللّٰهُ وَلِيُّ الْمُؤْمِنِيْنَ', 'inna aulan-nāsi bi`ibrāhīma lallażīnattaba\'ụhu wa hāżan-nabiyyu wallażīna āmanụ, wallāhu waliyyul-mu`minīn'),
(361, 3, 69, 'وَدَّتْ طَّاۤىِٕفَةٌ مِّنْ اَهْلِ الْكِتٰبِ لَوْ يُضِلُّوْنَكُمْۗ وَمَا يُضِلُّوْنَ اِلَّآ اَنْفُسَهُمْ وَمَا يَشْعُرُوْنَ', 'waddaṭ ṭā`ifatum min ahlil-kitābi lau yuḍillụnakum, wa mā yuḍillụna illā anfusahum wa mā yasy\'urụn'),
(362, 3, 70, 'يٰٓاَهْلَ الْكِتٰبِ لِمَ تَكْفُرُوْنَ بِاٰيٰتِ اللّٰهِ وَاَنْتُمْ تَشْهَدُوْنَ', 'yā ahlal-kitābi lima takfurụna bi`āyātillāhi wa antum tasy-hadụn'),
(363, 3, 71, 'يٰٓاَهْلَ الْكِتٰبِ لِمَ تَلْبِسُوْنَ الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُوْنَ الْحَقَّ وَاَنْتُمْ تَعْلَمُوْنَ', 'yā ahlal-kitābi lima talbisụnal-ḥaqqa bil-bāṭili wa taktumụnal-ḥaqqa wa antum ta\'lamụn'),
(364, 3, 72, 'وَقَالَتْ طَّاۤىِٕفَةٌ مِّنْ اَهْلِ الْكِتٰبِ اٰمِنُوْا بِالَّذِيْٓ اُنْزِلَ عَلَى الَّذِيْنَ اٰمَنُوْا وَجْهَ النَّهَارِ وَاكْفُرُوْٓا اٰخِرَهٗ لَعَلَّهُمْ يَرْجِعُوْنَۚ', 'wa qālaṭ ṭā`ifatum min ahlil-kitābi āminụ billażī unzila \'alallażīna āmanụ waj-han-nahāri wakfurū ākhirahụ la\'allahum yarji\'ụn'),
(365, 3, 73, 'وَلَا تُؤْمِنُوْٓا اِلَّا لِمَنْ تَبِعَ دِيْنَكُمْ ۗ قُلْ اِنَّ الْهُدٰى هُدَى اللّٰهِ ۙ اَنْ يُّؤْتٰىٓ اَحَدٌ مِّثْلَ مَآ اُوْتِيْتُمْ اَوْ يُحَاۤجُّوْكُمْ عِنْدَ رَبِّكُمْ ۗ قُلْ اِنَّ الْفَضْلَ بِيَدِ اللّٰهِ ۚ يُؤْتِيْهِ مَنْ يَّشَاۤءُ ۗوَاللّٰهُ وَاسِعٌ عَلِيْمٌ ۚ', 'wa lā tu`minū illā liman tabi\'a dīnakum, qul innal-hudā hudallāhi ay yu`tā aḥadum miṡla mā ụtītum au yuḥājjụkum \'inda rabbikum, qul innal-faḍla biyadillāh, yu`tīhi may yasyā`, wallāhu wāsi\'un \'alīm'),
(366, 3, 74, 'يَخْتَصُّ بِرَحْمَتِهٖ مَنْ يَّشَاۤءُ ۗوَاللّٰهُ ذُو الْفَضْلِ الْعَظِيْمِ', 'yakhtaṣṣu biraḥmatihī may yasyā`, wallāhu żul-faḍlil-\'aẓīm'),
(367, 3, 75, '۞ وَمِنْ اَهْلِ الْكِتٰبِ مَنْ اِنْ تَأْمَنْهُ بِقِنْطَارٍ يُّؤَدِّهٖٓ اِلَيْكَۚ وَمِنْهُمْ مَّنْ اِنْ تَأْمَنْهُ بِدِيْنَارٍ لَّا يُؤَدِّهٖٓ اِلَيْكَ اِلَّا مَا دُمْتَ عَلَيْهِ قَاۤىِٕمًا ۗ ذٰلِكَ بِاَنَّهُمْ قَالُوْا لَيْسَ عَلَيْنَا فِى الْاُمِّيّٖنَ سَبِيْلٌۚ وَيَقُوْلُوْنَ عَلَى اللّٰهِ الْكَذِبَ وَهُمْ يَعْلَمُوْنَ', 'wa min ahlil-kitābi man in ta`man-hu biqinṭāriy yu`addihī ilaīk, wa min-hum man in ta`man-hu bidīnāril lā yu`addihī ilaika illā mā dumta \'alaihi qā`imā, żālika bi`annahum qālụ laisa \'alainā fil-ummiyyīna sabīl, wa yaqụlụna \'alallāhil-każiba wa hum ya\'lamụn'),
(368, 3, 76, 'بَلٰى مَنْ اَوْفٰى بِعَهْدِهٖ وَاتَّقٰى فَاِنَّ اللّٰهَ يُحِبُّ الْمُتَّقِيْنَ', 'balā man aufā bi\'ahdihī wattaqā fa innallāha yuḥibbul-muttaqīn'),
(369, 3, 77, 'اِنَّ الَّذِيْنَ يَشْتَرُوْنَ بِعَهْدِ اللّٰهِ وَاَيْمَانِهِمْ ثَمَنًا قَلِيْلًا اُولٰۤىِٕكَ لَا خَلَاقَ لَهُمْ فِى الْاٰخِرَةِ وَلَا يُكَلِّمُهُمُ اللّٰهُ وَلَا يَنْظُرُ اِلَيْهِمْ يَوْمَ الْقِيٰمَةِ وَلَا يُزَكِّيْهِمْ ۖ وَلَهُمْ عَذَابٌ اَلِيْمٌ', 'innallażīna yasytarụna bi\'ahdillāhi wa aimānihim ṡamanang qalīlan ulā`ika lā khalāqa lahum fil-ākhirati wa lā yukallimuhumullāhu wa lā yanẓuru ilaihim yaumal-qiyāmati wa lā yuzakkīhim wa lahum \'ażābun alīm'),
(370, 3, 78, 'وَاِنَّ مِنْهُمْ لَفَرِيْقًا يَّلْوٗنَ اَلْسِنَتَهُمْ بِالْكِتٰبِ لِتَحْسَبُوْهُ مِنَ الْكِتٰبِ وَمَا هُوَ مِنَ الْكِتٰبِۚ وَيَقُوْلُوْنَ هُوَ مِنْ عِنْدِ اللّٰهِ وَمَا هُوَ مِنْ عِنْدِ اللّٰهِ ۚ وَيَقُوْلُوْنَ عَلَى اللّٰهِ الْكَذِبَ وَهُمْ يَعْلَمُوْنَ', 'wa inna min-hum lafarīqay yalwụna alsinatahum bil-kitābi litaḥsabụhu minal-kitābi wa mā huwa minal-kitāb, wa yaqụlụna huwa min \'indillāhi wa mā huwa min \'indillāh, wa yaqụlụna \'alallāhil-każiba wa hum ya\'lamụn'),
(371, 3, 79, 'مَا كَانَ لِبَشَرٍ اَنْ يُّؤْتِيَهُ اللّٰهُ الْكِتٰبَ وَالْحُكْمَ وَالنُّبُوَّةَ ثُمَّ يَقُوْلَ لِلنَّاسِ كُوْنُوْا عِبَادًا لِّيْ مِنْ دُوْنِ اللّٰهِ وَلٰكِنْ كُوْنُوْا رَبَّانِيّٖنَ بِمَا كُنْتُمْ تُعَلِّمُوْنَ الْكِتٰبَ وَبِمَا كُنْتُمْ تَدْرُسُوْنَ ۙ', 'mā kāna libasyarin ay yu`tiyahullāhul-kitāba wal-ḥukma wan-nubuwwata ṡumma yaqụla lin-nāsi kụnụ \'ibādal lī min dụnillāhi wa lāking kụnụ rabbāniyyīna bimā kuntum tu\'allimụnal-kitāba wa bimā kuntum tadrusụn'),
(372, 3, 80, 'وَلَا يَأْمُرَكُمْ اَنْ تَتَّخِذُوا الْمَلٰۤىِٕكَةَ وَالنَّبِيّٖنَ اَرْبَابًا ۗ اَيَأْمُرُكُمْ بِالْكُفْرِ بَعْدَ اِذْ اَنْتُمْ مُّسْلِمُوْنَ', 'wa lā ya`murakum an tattakhiżul-malā`ikata wan-nabiyyīna arbābā, a ya`murukum bil-kufri ba\'da iż antum muslimụn'),
(373, 3, 81, 'وَاِذْ اَخَذَ اللّٰهُ مِيْثَاقَ النَّبِيّٖنَ لَمَآ اٰتَيْتُكُمْ مِّنْ كِتٰبٍ وَّحِكْمَةٍ ثُمَّ جَاۤءَكُمْ رَسُوْلٌ مُّصَدِّقٌ لِّمَا مَعَكُمْ لَتُؤْمِنُنَّ بِهٖ وَلَتَنْصُرُنَّهٗ ۗ قَالَ ءَاَقْرَرْتُمْ وَاَخَذْتُمْ عَلٰى ذٰلِكُمْ اِصْرِيْ ۗ قَالُوْٓا اَقْرَرْنَا ۗ قَالَ فَاشْهَدُوْا وَاَنَا۠ مَعَكُمْ مِّنَ الشّٰهِدِيْنَ', 'wa iż akhażallāhu mīṡāqan-nabiyyīna lamā ātaitukum ming kitābiw wa ḥikmatin ṡumma jā`akum rasụlum muṣaddiqul limā ma\'akum latu`minunna bihī wa latanṣurunnah, qāla a aqrartum wa akhażtum \'alā żālikum iṣrī, qālū aqrarnā, qāla fasy-hadụ wa ana ma\'akum minasy-syāhidīn'),
(374, 3, 82, 'فَمَنْ تَوَلّٰى بَعْدَ ذٰلِكَ فَاُولٰۤىِٕكَ هُمُ الْفٰسِقُوْنَ', 'fa man tawallā ba\'da żālika fa ulā`ika humul-fāsiqụn'),
(375, 3, 83, 'اَفَغَيْرَ دِيْنِ اللّٰهِ يَبْغُوْنَ وَلَهٗ ٓ اَسْلَمَ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِ طَوْعًا وَّكَرْهًا وَّاِلَيْهِ يُرْجَعُوْنَ', 'a fa gaira dīnillāhi yabgụna wa lahū aslama man fis-samāwāti wal-arḍi ṭau\'aw wa kar-haw wa ilaihi yurja\'ụn'),
(376, 3, 84, 'قُلْ اٰمَنَّا بِاللّٰهِ وَمَآ اُنْزِلَ عَلَيْنَا وَمَآ اُنْزِلَ عَلٰٓى اِبْرٰهِيْمَ وَاِسْمٰعِيْلَ وَاِسْحٰقَ وَيَعْقُوْبَ وَالْاَسْبَاطِ وَمَآ اُوْتِيَ مُوْسٰى وَعِيْسٰى وَالنَّبِيُّوْنَ مِنْ رَّبِّهِمْۖ لَا نُفَرِّقُ بَيْنَ اَحَدٍ مِّنْهُمْۖ وَنَحْنُ لَهٗ مُسْلِمُوْنَ', 'qul āmannā billāhi wa mā unzila \'alainā wa mā unzila \'alā ibrāhīma wa ismā\'īla wa is-ḥāqa wa ya\'qụba wal-asbāṭi wa mā ụtiya mụsā wa \'īsā wan-nabiyyụna mir rabbihim lā nufarriqu baina aḥadim min-hum wa naḥnu lahụ muslimụn'),
(377, 3, 85, 'وَمَنْ يَّبْتَغِ غَيْرَ الْاِسْلَامِ دِيْنًا فَلَنْ يُّقْبَلَ مِنْهُۚ وَهُوَ فِى الْاٰخِرَةِ مِنَ الْخٰسِرِيْنَ', 'wa may yabtagi gairal-islāmi dīnan fa lay yuqbala min-h, wa huwa fil-ākhirati minal-khāsirīn'),
(378, 3, 86, 'كَيْفَ يَهْدِى اللّٰهُ قَوْمًا كَفَرُوْا بَعْدَ اِيْمَانِهِمْ وَشَهِدُوْٓا اَنَّ الرَّسُوْلَ حَقٌّ وَّجَاۤءَهُمُ الْبَيِّنٰتُ ۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ', 'kaifa yahdillāhu qaumang kafarụ ba\'da īmānihim wa syahidū annar-rasụla ḥaqquw wa jā`ahumul-bayyināt, wallāhu lā yahdil-qaumaẓ-ẓālimīn'),
(379, 3, 87, 'اُولٰۤىِٕكَ جَزَاۤؤُهُمْ اَنَّ عَلَيْهِمْ لَعْنَةَ اللّٰهِ وَالْمَلٰۤىِٕكَةِ وَالنَّاسِ اَجْمَعِيْنَۙ', 'ulā`ika jazā`uhum anna \'alaihim la\'natallāhi wal-malā`ikati wan-nāsi ajma\'īn'),
(380, 3, 88, 'خٰلِدِيْنَ فِيْهَا ۚ لَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنْظَرُوْنَۙ', 'khālidīna fīhā, lā yukhaffafu \'an-humul-\'ażābu wa lā hum yunẓarụn'),
(381, 3, 89, 'اِلَّا الَّذِيْنَ تَابُوْا مِنْۢ بَعْدِ ذٰلِكَ وَاَصْلَحُوْاۗ فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'illallażīna tābụ mim ba\'di żālika wa aṣlaḥụ, fa innallāha gafụrur raḥīm'),
(382, 3, 90, 'اِنَّ الَّذِيْنَ كَفَرُوْا بَعْدَ اِيْمَانِهِمْ ثُمَّ ازْدَادُوْا كُفْرًا لَّنْ تُقْبَلَ تَوْبَتُهُمْ ۚ وَاُولٰۤىِٕكَ هُمُ الضَّاۤلُّوْنَ', 'innallażīna kafarụ ba\'da īmānihim ṡummazdādụ kufral lan tuqbala taubatuhum, wa ulā`ika humuḍ-ḍāllụn'),
(383, 3, 91, 'اِنَّ الَّذِيْنَ كَفَرُوْا وَمَاتُوْا وَهُمْ كُفَّارٌ فَلَنْ يُّقْبَلَ مِنْ اَحَدِهِمْ مِّلْءُ الْاَرْضِ ذَهَبًا وَّلَوِ افْتَدٰى بِهٖۗ اُولٰۤىِٕكَ لَهُمْ عَذَابٌ اَلِيْمٌ وَّمَا لَهُمْ مِّنْ نّٰصِرِيْنَ ۔', 'innallażīna kafarụ wa mātụ wa hum kuffārun fa lay yuqbala min aḥadihim mil`ul-arḍi żahabaw wa lawiftadā bih, ulā`ika lahum \'ażābun alīmuw wa mā lahum min nāṣirīn'),
(384, 3, 92, 'لَنْ تَنَالُوا الْبِرَّ حَتّٰى تُنْفِقُوْا مِمَّا تُحِبُّوْنَ ۗوَمَا تُنْفِقُوْا مِنْ شَيْءٍ فَاِنَّ اللّٰهَ بِهٖ عَلِيْمٌ', 'lan tanālul-birra ḥattā tunfiqụ mimmā tuḥibbụn, wa mā tunfiqụ min syai`in fa innallāha bihī \'alīm'),
(385, 3, 93, '۞ كُلُّ الطَّعَامِ كَانَ حِلًّا لِّبَنِيْٓ اِسْرَاۤءِيْلَ اِلَّا مَا حَرَّمَ اِسْرَاۤءِيْلُ عَلٰى نَفْسِهٖ مِنْ قَبْلِ اَنْ تُنَزَّلَ التَّوْرٰىةُ ۗ قُلْ فَأْتُوْا بِالتَّوْرٰىةِ فَاتْلُوْهَآ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'kulluṭ-ṭa\'āmi kāna ḥillal libanī isrā`īla illā mā ḥarrama isrā`īlu \'alā nafsihī ming qabli an tunazzalat-taurāh, qul fa`tụ bit-taurāti fatlụhā ing kuntum ṣādiqīn'),
(386, 3, 94, 'فَمَنِ افْتَرٰى عَلَى اللّٰهِ الْكَذِبَ مِنْۢ بَعْدِ ذٰلِكَ فَاُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ', 'fa maniftarā \'alallāhil-każiba mim ba\'di żālika fa ulā`ika humuẓ-ẓālimụn'),
(387, 3, 95, 'قُلْ صَدَقَ اللّٰهُ ۗ فَاتَّبِعُوْا مِلَّةَ اِبْرٰهِيْمَ حَنِيْفًاۗ وَمَا كَانَ مِنَ الْمُشْرِكِيْنَ', 'qul ṣadaqallāh, fattabi\'ụ millata ibrāhīma ḥanīfā, wa mā kāna minal-musyrikīn'),
(388, 3, 96, 'اِنَّ اَوَّلَ بَيْتٍ وُّضِعَ لِلنَّاسِ لَلَّذِيْ بِبَكَّةَ مُبٰرَكًا وَّهُدًى لِّلْعٰلَمِيْنَۚ', 'inna awwala baitiw wuḍi\'a lin-nāsi lallażī bibakkata mubārakaw wa hudal lil-\'ālamīn'),
(389, 3, 97, 'فِيْهِ اٰيٰتٌۢ بَيِّنٰتٌ مَّقَامُ اِبْرٰهِيْمَ ەۚ وَمَنْ دَخَلَهٗ كَانَ اٰمِنًا ۗ وَلِلّٰهِ عَلَى النَّاسِ حِجُّ الْبَيْتِ مَنِ اسْتَطَاعَ اِلَيْهِ سَبِيْلًا ۗ وَمَنْ كَفَرَ فَاِنَّ اللّٰهَ غَنِيٌّ عَنِ الْعٰلَمِيْنَ', 'fīhi āyātum bayyinātum maqāmu ibrāhīm, wa man dakhalahụ kāna āminā, wa lillāhi \'alan-nāsi ḥijjul-baiti manistaṭā\'a ilaihi sabīlā, wa mang kafara fa innallāha ganiyyun \'anil-\'ālamīn'),
(390, 3, 98, 'قُلْ يٰٓاَهْلَ الْكِتٰبِ لِمَ تَكْفُرُوْنَ بِاٰيٰتِ اللّٰهِ وَاللّٰهُ شَهِيْدٌ عَلٰى مَا تَعْمَلُوْنَ', 'qul yā ahlal-kitābi lima takfurụna bi`āyātillāhi wallāhu syahīdun \'alā mā ta\'malụn'),
(391, 3, 99, 'قُلْ يٰٓاَهْلَ الْكِتٰبِ لِمَ تَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ مَنْ اٰمَنَ تَبْغُوْنَهَا عِوَجًا وَّاَنْتُمْ شُهَدَاۤءُ ۗ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ', 'qul yā ahlal-kitābi lima taṣuddụna \'an sabīlillāhi man āmana tabgụnahā \'iwajaw wa antum syuhadā`, wa mallāhu bigāfilin \'ammā ta\'malụn'),
(392, 3, 100, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنْ تُطِيْعُوْا فَرِيْقًا مِّنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ يَرُدُّوْكُمْ بَعْدَ اِيْمَانِكُمْ كٰفِرِيْنَ', 'yā ayyuhallażīna āmanū in tuṭī\'ụ farīqam minallażīna ụtul-kitāba yaruddụkum ba\'da īmānikum kāfirīn'),
(393, 3, 101, 'وَكَيْفَ تَكْفُرُوْنَ وَاَنْتُمْ تُتْلٰى عَلَيْكُمْ اٰيٰتُ اللّٰهِ وَفِيْكُمْ رَسُوْلُهٗ ۗ وَمَنْ يَّعْتَصِمْ بِاللّٰهِ فَقَدْ هُدِيَ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ', 'wa kaifa takfurụna wa antum tutlā \'alaikum āyātullāhi wa fīkum rasụluh, wa may ya\'taṣim billāhi fa qad hudiya ilā ṣirāṭim mustaqīm'),
(394, 3, 102, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ حَقَّ تُقٰىتِهٖ وَلَا تَمُوْتُنَّ اِلَّا وَاَنْتُمْ مُّسْلِمُوْنَ', 'yā ayyuhallażīna āmanuttaqullāha ḥaqqa tuqātihī wa lā tamụtunna illā wa antum muslimụn'),
(395, 3, 103, 'وَاعْتَصِمُوْا بِحَبْلِ اللّٰهِ جَمِيْعًا وَّلَا تَفَرَّقُوْا ۖوَاذْكُرُوْا نِعْمَتَ اللّٰهِ عَلَيْكُمْ اِذْ كُنْتُمْ اَعْدَاۤءً فَاَلَّفَ بَيْنَ قُلُوْبِكُمْ فَاَصْبَحْتُمْ بِنِعْمَتِهٖٓ اِخْوَانًاۚ وَكُنْتُمْ عَلٰى شَفَا حُفْرَةٍ مِّنَ النَّارِ فَاَنْقَذَكُمْ مِّنْهَا ۗ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيٰتِهٖ لَعَلَّكُمْ تَهْتَدُوْنَ', 'wa\'taṣimụ biḥablillāhi jamī\'aw wa lā tafarraqụ ważkurụ ni\'matallāhi \'alaikum iż kuntum a\'dā`an fa allafa baina qulụbikum fa aṣbaḥtum bini\'matihī ikhwānā, wa kuntum \'alā syafā ḥufratim minan-nāri fa angqażakum min-hā, każālika yubayyinullāhu lakum āyātihī la\'allakum tahtadụn'),
(396, 3, 104, 'وَلْتَكُنْ مِّنْكُمْ اُمَّةٌ يَّدْعُوْنَ اِلَى الْخَيْرِ وَيَأْمُرُوْنَ بِالْمَعْرُوْفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ ۗ وَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ', 'waltakum mingkum ummatuy yad\'ụna ilal-khairi wa ya`murụna bil-ma\'rụfi wa yan-hauna \'anil-mungkar, wa ulā`ika humul-mufliḥụn'),
(397, 3, 105, 'وَلَا تَكُوْنُوْا كَالَّذِيْنَ تَفَرَّقُوْا وَاخْتَلَفُوْا مِنْۢ بَعْدِ مَا جَاۤءَهُمُ الْبَيِّنٰتُ ۗ وَاُولٰۤىِٕكَ لَهُمْ عَذَابٌ عَظِيْمٌ ۙ', 'wa lā takụnụ kallażīna tafarraqụ wakhtalafụ mim ba\'di mā jā`ahumul-bayyināt, wa ulā`ika lahum \'ażābun \'aẓīm'),
(398, 3, 106, 'يَّوْمَ تَبْيَضُّ وُجُوْهٌ وَّتَسْوَدُّ وُجُوْهٌ ۚ فَاَمَّا الَّذِيْنَ اسْوَدَّتْ وُجُوْهُهُمْۗ اَ كَفَرْتُمْ بَعْدَ اِيْمَانِكُمْ فَذُوْقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُوْنَ', 'yauma tabyaḍḍu wujụhuw wa taswaddu wujụh, fa ammallażīnaswaddat wujụhuhum, a kafartum ba\'da īmānikum fa żụqul-\'ażāba bimā kuntum takfurụn'),
(399, 3, 107, 'وَاَمَّا الَّذِيْنَ ابْيَضَّتْ وُجُوْهُهُمْ فَفِيْ رَحْمَةِ اللّٰهِ ۗ هُمْ فِيْهَا خٰلِدُوْنَ', 'wa ammallażīnabyaḍḍat wujụhuhum fa fī raḥmatillāh, hum fīhā khālidụn'),
(400, 3, 108, 'تِلْكَ اٰيٰتُ اللّٰهِ نَتْلُوْهَا عَلَيْكَ بِالْحَقِّ ۗ وَمَا اللّٰهُ يُرِيْدُ ظُلْمًا لِّلْعٰلَمِيْنَ', 'tilka āyātullāhi natlụhā \'alaika bil-ḥaqq, wa mallāhu yurīdu ẓulmal lil-\'ālamīn'),
(401, 3, 109, 'وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ ۗوَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ', 'wa lillāhi mā fis-samāwāti wa mā fil-arḍ, wa ilallāhi turja\'ul-umụr'),
(402, 3, 110, 'كُنْتُمْ خَيْرَ اُمَّةٍ اُخْرِجَتْ لِلنَّاسِ تَأْمُرُوْنَ بِالْمَعْرُوْفِ وَتَنْهَوْنَ عَنِ الْمُنْكَرِ وَتُؤْمِنُوْنَ بِاللّٰهِ ۗ وَلَوْ اٰمَنَ اَهْلُ الْكِتٰبِ لَكَانَ خَيْرًا لَّهُمْ ۗ مِنْهُمُ الْمُؤْمِنُوْنَ وَاَكْثَرُهُمُ الْفٰسِقُوْنَ', 'kuntum khaira ummatin ukhrijat lin-nāsi ta`murụna bil-ma\'rụfi wa tan-hauna \'anil-mungkari wa tu`minụna billāh, walau āmana ahlul-kitābi lakāna khairal lahum, min-humul-mu`minụna wa akṡaruhumul-fāsiqụn'),
(403, 3, 111, 'لَنْ يَّضُرُّوْكُمْ اِلَّآ اَذًىۗ وَاِنْ يُّقَاتِلُوْكُمْ يُوَلُّوْكُمُ الْاَدْبَارَۗ ثُمَّ لَا يُنْصَرُوْنَ', 'lay yaḍurrụkum illā ażā, wa iy yuqātilụkum yuwallụkumul-adbār, ṡumma lā yunṣarụn'),
(404, 3, 112, 'ضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ اَيْنَ مَا ثُقِفُوْٓا اِلَّا بِحَبْلٍ مِّنَ اللّٰهِ وَحَبْلٍ مِّنَ النَّاسِ وَبَاۤءُوْ بِغَضَبٍ مِّنَ اللّٰهِ وَضُرِبَتْ عَلَيْهِمُ الْمَسْكَنَةُ ۗ ذٰلِكَ بِاَنَّهُمْ كَانُوْا يَكْفُرُوْنَ بِاٰيٰتِ اللّٰهِ وَيَقْتُلُوْنَ الْاَنْبِۢيَاۤءَ بِغَيْرِ حَقٍّۗ ذٰلِكَ بِمَا عَصَوْا وَّكَانُوْا يَعْتَدُوْنَ', 'ḍuribat \'alaihimuż-żillatu aina mā ṡuqifū illā biḥablim minallāhi wa ḥablim minan-nāsi wa bā`ụ bigaḍabim minallāhi wa ḍuribat \'alaihimul-maskanah, żālika bi`annahum kānụ yakfurụna bi`āyātillāhi wa yaqtulụnal-ambiyā`a bigairi ḥaqq, żālika bimā \'aṣaw wa kānụ ya\'tadụn'),
(405, 3, 113, '۞ لَيْسُوْا سَوَاۤءً ۗ مِنْ اَهْلِ الْكِتٰبِ اُمَّةٌ قَاۤىِٕمَةٌ يَّتْلُوْنَ اٰيٰتِ اللّٰهِ اٰنَاۤءَ الَّيْلِ وَهُمْ يَسْجُدُوْنَ', 'laisụ sawā`ā, min ahlil-kitābi ummatung qā`imatuy yatlụna āyātillāhi ānā`al-laili wa hum yasjudụn'),
(406, 3, 114, 'يُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَيَأْمُرُوْنَ بِالْمَعْرُوْفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ وَيُسَارِعُوْنَ فِى الْخَيْرٰتِۗ وَاُولٰۤىِٕكَ مِنَ الصّٰلِحِيْنَ', 'yu`minụna billāhi wal-yaumil-ākhiri wa ya`murụna bil-ma\'rụfi wa yan-hauna \'anil-mungkari wa yusāri\'ụna fil-khairāt, wa ulā`ika minaṣ-ṣāliḥīn'),
(407, 3, 115, 'وَمَا يَفْعَلُوْا مِنْ خَيْرٍ فَلَنْ يُّكْفَرُوْهُ ۗ وَاللّٰهُ عَلِيْمٌ ۢبِالْمُتَّقِيْنَ', 'wa mā yaf\'alụ min khairin fa lay yukfarụh, wallāhu \'alīmum bil-muttaqīn'),
(408, 3, 116, 'اِنَّ الَّذِيْنَ كَفَرُوْا لَنْ تُغْنِيَ عَنْهُمْ اَمْوَالُهُمْ وَلَآ اَوْلَادُهُمْ مِّنَ اللّٰهِ شَيْـًٔا ۗ وَاُولٰۤىِٕكَ اَصْحٰبُ النَّارِ ۚ هُمْ فِيْهَا خٰلِدُوْنَ', 'innallażīna kafarụ lan tugniya \'an-hum amwāluhum wa lā aulāduhum minallāhi syai`ā, wa ulā`ika aṣ-ḥābun-nār, hum fīhā khālidụn'),
(409, 3, 117, 'مَثَلُ مَا يُنْفِقُوْنَ فِيْ هٰذِهِ الْحَيٰوةِ الدُّنْيَا كَمَثَلِ رِيْحٍ فِيْهَا صِرٌّ اَصَابَتْ حَرْثَ قَوْمٍ ظَلَمُوْٓا اَنْفُسَهُمْ فَاَهْلَكَتْهُ ۗ وَمَا ظَلَمَهُمُ اللّٰهُ وَلٰكِنْ اَنْفُسَهُمْ يَظْلِمُوْنَ', 'maṡalu mā yunfiqụna fī hāżihil-ḥayātid-dun-yā kamaṡali rīḥin fīhā ṣirrun aṣābat ḥarṡa qaumin ẓalamū anfusahum fa ahlakat-h, wa mā ẓalamahumullāhu wa lākin anfusahum yaẓlimụn'),
(410, 3, 118, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوْا بِطَانَةً مِّنْ دُوْنِكُمْ لَا يَأْلُوْنَكُمْ خَبَالًاۗ وَدُّوْا مَا عَنِتُّمْۚ قَدْ بَدَتِ الْبَغْضَاۤءُ مِنْ اَفْوَاهِهِمْۖ وَمَا تُخْفِيْ صُدُوْرُهُمْ اَكْبَرُ ۗ قَدْ بَيَّنَّا لَكُمُ الْاٰيٰتِ اِنْ كُنْتُمْ تَعْقِلُوْنَ', 'yā ayyuhallażīna āmanụ lā tattakhiżụ biṭānatam min dụnikum lā ya`lụnakum khabālā, waddụ mā \'anittum, qad badatil-bagḍā`u min afwāhihim wa mā tukhfī ṣudụruhum akbar, qad bayyannā lakumul-āyāti ing kuntum ta\'qilụn'),
(411, 3, 119, 'هٰٓاَنْتُمْ اُولَاۤءِ تُحِبُّوْنَهُمْ وَلَا يُحِبُّوْنَكُمْ وَتُؤْمِنُوْنَ بِالْكِتٰبِ كُلِّهٖۚ وَاِذَا لَقُوْكُمْ قَالُوْٓا اٰمَنَّاۖ وَاِذَا خَلَوْا عَضُّوْا عَلَيْكُمُ الْاَنَامِلَ مِنَ الْغَيْظِ ۗ قُلْ مُوْتُوْا بِغَيْظِكُمْ ۗ اِنَّ اللّٰهَ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ', 'hā`antum ulā`i tuḥibbụnahum wa lā yuḥibbụnakum wa tu`minụna bil-kitābi kullih, wa iżā laqụkum qālū āmannā wa iżā khalau \'aḍḍụ \'alaikumul-anāmila minal-gaīẓ, qul mụtụ bigaiẓikum, innallāha \'alīmum biżātiṣ-ṣudụr'),
(412, 3, 120, 'اِنْ تَمْسَسْكُمْ حَسَنَةٌ تَسُؤْهُمْۖ وَاِنْ تُصِبْكُمْ سَيِّئَةٌ يَّفْرَحُوْا بِهَا ۗ وَاِنْ تَصْبِرُوْا وَتَتَّقُوْا لَا يَضُرُّكُمْ كَيْدُهُمْ شَيْـًٔا ۗ اِنَّ اللّٰهَ بِمَا يَعْمَلُوْنَ مُحِيْطٌ', 'in tamsaskum ḥasanatun tasu`hum wa in tuṣibkum sayyi`atuy yafraḥụ bihā, wa in taṣbirụ wa tattaqụ lā yaḍurrukum kaiduhum syai`ā, innallāha bimā ya\'malụna muḥīṭ'),
(413, 3, 121, 'وَاِذْ غَدَوْتَ مِنْ اَهْلِكَ تُبَوِّئُ الْمُؤْمِنِيْنَ مَقَاعِدَ لِلْقِتَالِ ۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌۙ', 'wa iż gadauta min ahlika tubawwi`ul-mu`minīna maqā\'ida lil-qitāl, wallāhu samī\'un \'alīm'),
(414, 3, 122, 'اِذْ هَمَّتْ طَّۤاىِٕفَتٰنِ مِنْكُمْ اَنْ تَفْشَلَاۙ وَاللّٰهُ وَلِيُّهُمَا ۗ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ', 'iż hammaṭ ṭā`ifatāni mingkum an tafsyalā wallāhu waliyyuhumā, wa \'alallāhi falyatawakkalil-mu`minụn'),
(415, 3, 123, 'وَلَقَدْ نَصَرَكُمُ اللّٰهُ بِبَدْرٍ وَّاَنْتُمْ اَذِلَّةٌ ۚ فَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تَشْكُرُوْنَ', 'wa laqad naṣarakumullāhu bibadriw wa antum ażillah, fattaqullāha la\'allakum tasykurụn'),
(416, 3, 124, 'اِذْ تَقُوْلُ لِلْمُؤْمِنِيْنَ اَلَنْ يَّكْفِيَكُمْ اَنْ يُّمِدَّكُمْ رَبُّكُمْ بِثَلٰثَةِ اٰلَافٍ مِّنَ الْمَلٰۤىِٕكَةِ مُنْزَلِيْنَۗ', 'iż taqụlu lil-mu`minīna a lay yakfiyakum ay yumiddakum rabbukum biṡalāṡati ālāfim minal-malā`ikati munzalīn'),
(417, 3, 125, 'بَلٰٓى ۙاِنْ تَصْبِرُوْا وَتَتَّقُوْا وَيَأْتُوْكُمْ مِّنْ فَوْرِهِمْ هٰذَا يُمْدِدْكُمْ رَبُّكُمْ بِخَمْسَةِ اٰلَافٍ مِّنَ الْمَلٰۤىِٕكَةِ مُسَوِّمِيْنَ', 'balā in taṣbirụ wa tattaqụ wa ya`tụkum min faurihim hāżā yumdidkum rabbukum bikhamsati ālāfim minal-malā`ikati musawwimīn'),
(418, 3, 126, 'وَمَا جَعَلَهُ اللّٰهُ اِلَّا بُشْرٰى لَكُمْ وَلِتَطْمَىِٕنَّ قُلُوْبُكُمْ بِهٖ ۗ وَمَا النَّصْرُ اِلَّا مِنْ عِنْدِ اللّٰهِ الْعَزِيْزِ الْحَكِيْمِۙ', 'wa mā ja\'alahullāhu illā busyrā lakum wa litaṭma`inna qulụbukum bih, wa man-naṣru illā min \'indillāhil-\'azīzil-ḥakīm'),
(419, 3, 127, 'لِيَقْطَعَ طَرَفًا مِّنَ الَّذِيْنَ كَفَرُوْٓا اَوْ يَكْبِتَهُمْ فَيَنْقَلِبُوْا خَاۤىِٕبِيْنَ', 'liyaqṭa\'a ṭarafam minallażīna kafarū au yakbitahum fa yangqalibụ khā`ibīn'),
(420, 3, 128, 'لَيْسَ لَكَ مِنَ الْاَمْرِ شَيْءٌ اَوْ يَتُوْبَ عَلَيْهِمْ اَوْ يُعَذِّبَهُمْ فَاِنَّهُمْ ظٰلِمُوْنَ', 'laisa laka minal-amri syai`un au yatụba \'alaihim au yu\'ażżibahum fa innahum ẓālimụn'),
(421, 3, 129, 'وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ يَغْفِرُ لِمَنْ يَّشَاۤءُ وَيُعَذِّبُ مَنْ يَّشَاۤءُ ۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ', 'wa lillāhi mā fis-samāwāti wa mā fil-arḍ, yagfiru limay yasyā`u wa yu\'ażżibu may yasyā`, wallāhu gafụrur raḥīm');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(422, 3, 130, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَأْكُلُوا الرِّبٰوٓا اَضْعَافًا مُّضٰعَفَةً ۖوَّاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُفْلِحُوْنَۚ', 'yā ayyuhallażīna āmanụ lā ta`kulur-ribā aḍ\'āfam muḍā\'afataw wattaqullāha la\'allakum tufliḥụn'),
(423, 3, 131, 'وَاتَّقُوا النَّارَ الَّتِيْٓ اُعِدَّتْ لِلْكٰفِرِيْنَ ۚ', 'wattaqun-nārallatī u\'iddat lil-kāfirīn'),
(424, 3, 132, 'وَاَطِيْعُوا اللّٰهَ وَالرَّسُوْلَ لَعَلَّكُمْ تُرْحَمُوْنَۚ', 'wa aṭī\'ullāha war-rasụla la\'allakum tur-ḥamụn'),
(425, 3, 133, '۞ وَسَارِعُوْٓا اِلٰى مَغْفِرَةٍ مِّنْ رَّبِّكُمْ وَجَنَّةٍ عَرْضُهَا السَّمٰوٰتُ وَالْاَرْضُۙ اُعِدَّتْ لِلْمُتَّقِيْنَۙ', 'wa sāri\'ū ilā magfiratim mir rabbikum wa jannatin \'arḍuhas-samāwātu wal-arḍu u\'iddat lil-muttaqīn'),
(426, 3, 134, 'الَّذِيْنَ يُنْفِقُوْنَ فِى السَّرَّۤاءِ وَالضَّرَّۤاءِ وَالْكَاظِمِيْنَ الْغَيْظَ وَالْعَافِيْنَ عَنِ النَّاسِۗ وَاللّٰهُ يُحِبُّ الْمُحْسِنِيْنَۚ', 'allażīna yunfiqụna fis-sarrā`i waḍ-ḍarrā`i wal-kāẓimīnal-gaiẓa wal-\'āfīna \'anin-nās, wallāhu yuḥibbul-muḥsinīn'),
(427, 3, 135, 'وَالَّذِيْنَ اِذَا فَعَلُوْا فَاحِشَةً اَوْ ظَلَمُوْٓا اَنْفُسَهُمْ ذَكَرُوا اللّٰهَ فَاسْتَغْفَرُوْا لِذُنُوْبِهِمْۗ وَمَنْ يَّغْفِرُ الذُّنُوْبَ اِلَّا اللّٰهُ ۗ وَلَمْ يُصِرُّوْا عَلٰى مَا فَعَلُوْا وَهُمْ يَعْلَمُوْنَ', 'wallażīna iżā fa\'alụ fāḥisyatan au ẓalamū anfusahum żakarullāha fastagfarụ liżunụbihim, wa may yagfiruż-żunụba illallāh, wa lam yuṣirrụ \'alā mā fa\'alụ wa hum ya\'lamụn'),
(428, 3, 136, 'اُولٰۤىِٕكَ جَزَاۤؤُهُمْ مَّغْفِرَةٌ مِّنْ رَّبِّهِمْ وَجَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا ۗ وَنِعْمَ اَجْرُ الْعٰمِلِيْنَۗ', 'ulā`ika jazā`uhum magfiratum mir rabbihim wa jannātun tajrī min taḥtihal-an-hāru khālidīna fīhā, wa ni\'ma ajrul-\'āmilīn'),
(429, 3, 137, 'قَدْ خَلَتْ مِنْ قَبْلِكُمْ سُنَنٌۙ فَسِيْرُوْا فِى الْاَرْضِ فَانْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِيْنَ', 'qad khalat ming qablikum sunanun fa sīrụ fil-arḍi fanẓurụ kaifa kāna \'āqibatul-mukażżibīn'),
(430, 3, 138, 'هٰذَا بَيَانٌ لِّلنَّاسِ وَهُدًى وَّمَوْعِظَةٌ لِّلْمُتَّقِيْنَ', 'hāżā bayānul lin-nāsi wa hudaw wa mau\'iẓatul lil-muttaqīn'),
(431, 3, 139, 'وَلَا تَهِنُوْا وَلَا تَحْزَنُوْا وَاَنْتُمُ الْاَعْلَوْنَ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ', 'wa lā tahinụ wa lā taḥzanụ wa antumul-a\'launa ing kuntum mu`minīn'),
(432, 3, 140, 'اِنْ يَّمْسَسْكُمْ قَرْحٌ فَقَدْ مَسَّ الْقَوْمَ قَرْحٌ مِّثْلُهٗ ۗوَتِلْكَ الْاَيَّامُ نُدَاوِلُهَا بَيْنَ النَّاسِۚ وَلِيَعْلَمَ اللّٰهُ الَّذِيْنَ اٰمَنُوْا وَيَتَّخِذَ مِنْكُمْ شُهَدَاۤءَ ۗوَاللّٰهُ لَا يُحِبُّ الظّٰلِمِيْنَۙ', 'iy yamsaskum qar-ḥun fa qad massal-qauma qar-ḥum miṡluh, wa tilkal-ayyāmu nudāwiluhā bainan-nās, wa liya\'lamallāhullażīna āmanụ wa yattakhiża mingkum syuhadā`, wallāhu lā yuḥibbuẓ-ẓālimīn'),
(433, 3, 141, 'وَلِيُمَحِّصَ اللّٰهُ الَّذِيْنَ اٰمَنُوْا وَيَمْحَقَ الْكٰفِرِيْنَ', 'wa liyumaḥḥiṣallāhullażīna āmanụ wa yam-ḥaqal-kāfirīn'),
(434, 3, 142, 'اَمْ حَسِبْتُمْ اَنْ تَدْخُلُوا الْجَنَّةَ وَلَمَّا يَعْلَمِ اللّٰهُ الَّذِيْنَ جَاهَدُوْا مِنْكُمْ وَيَعْلَمَ الصّٰبِرِيْنَ', 'am ḥasibtum an tadkhulul-jannata wa lammā ya\'lamillāhullażīna jāhadụ mingkum wa ya\'lamaṣ-ṣābirīn'),
(435, 3, 143, 'وَلَقَدْ كُنْتُمْ تَمَنَّوْنَ الْمَوْتَ مِنْ قَبْلِ اَنْ تَلْقَوْهُۖ فَقَدْ رَاَيْتُمُوْهُ وَاَنْتُمْ تَنْظُرُوْنَ', 'wa laqad kuntum tamannaunal-mauta ming qabli an talqauhu fa qad ra`aitumụhu wa antum tanẓurụn'),
(436, 3, 144, 'وَمَا مُحَمَّدٌ اِلَّا رَسُوْلٌۚ قَدْ خَلَتْ مِنْ قَبْلِهِ الرُّسُلُ ۗ اَفَا۟ىِٕنْ مَّاتَ اَوْ قُتِلَ انْقَلَبْتُمْ عَلٰٓى اَعْقَابِكُمْ ۗ وَمَنْ يَّنْقَلِبْ عَلٰى عَقِبَيْهِ فَلَنْ يَّضُرَّ اللّٰهَ شَيْـًٔا ۗوَسَيَجْزِى اللّٰهُ الشّٰكِرِيْنَ', 'wa mā muḥammadun illā rasụl, qad khalat ming qablihir-rusul, a fa im māta au qutilangqalabtum \'alā a\'qābikum, wa may yangqalib \'alā \'aqibaihi fa lay yaḍurrallāha syai`ā, wa sayajzillāhusy-syākirīn'),
(437, 3, 145, 'وَمَا كَانَ لِنَفْسٍ اَنْ تَمُوْتَ اِلَّا بِاِذْنِ اللّٰهِ كِتٰبًا مُّؤَجَّلًا ۗ وَمَنْ يُّرِدْ ثَوَابَ الدُّنْيَا نُؤْتِهٖ مِنْهَاۚ وَمَنْ يُّرِدْ ثَوَابَ الْاٰخِرَةِ نُؤْتِهٖ مِنْهَا ۗ وَسَنَجْزِى الشّٰكِرِيْنَ', 'wa mā kāna linafsin an tamụta illā bi`iżnillāhi kitābam mu`ajjalā, wa may yurid ṡawābad-dun-yā nu`tihī min-hā, wa may yurid ṡawābal-ākhirati nu`tihī min-hā, wa sanajzisy-syākirīn'),
(438, 3, 146, 'وَكَاَيِّنْ مِّنْ نَّبِيٍّ قَاتَلَۙ مَعَهٗ رِبِّيُّوْنَ كَثِيْرٌۚ فَمَا وَهَنُوْا لِمَآ اَصَابَهُمْ فِيْ سَبِيْلِ اللّٰهِ وَمَا ضَعُفُوْا وَمَا اسْتَكَانُوْا ۗ وَاللّٰهُ يُحِبُّ الصّٰبِرِيْنَ', 'wa ka`ayyim min nabiyying qātala ma\'ahụ ribbiyyụna kaṡīr, fa mā wahanụ limā aṣābahum fī sabīlillāhi wa mā ḍa\'ufụ wa mastakānụ, wallāhu yuḥibbuṣ-ṣābirīn'),
(439, 3, 147, 'وَمَا كَانَ قَوْلَهُمْ اِلَّآ اَنْ قَالُوْا رَبَّنَا اغْفِرْ لَنَا ذُنُوْبَنَا وَاِسْرَافَنَا فِيْٓ اَمْرِنَا وَثَبِّتْ اَقْدَامَنَا وَانْصُرْنَا عَلَى الْقَوْمِ الْكٰفِرِيْنَ', 'wa mā kāna qaulahum illā ang qālụ rabbanagfir lanā żunụbanā wa isrāfanā fī amrinā wa ṡabbit aqdāmanā wanṣurnā \'alal-qaumil-kāfirīn'),
(440, 3, 148, 'فَاٰتٰىهُمُ اللّٰهُ ثَوَابَ الدُّنْيَا وَحُسْنَ ثَوَابِ الْاٰخِرَةِ ۗ وَاللّٰهُ يُحِبُّ الْمُحْسِنِيْنَ', 'fa ātāhumullāhu ṡawābad-dun-yā wa ḥusna ṡawābil-ākhirah, wallāhu yuḥibbul-muḥsinīn'),
(441, 3, 149, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنْ تُطِيْعُوا الَّذِيْنَ كَفَرُوْا يَرُدُّوْكُمْ عَلٰٓى اَعْقَابِكُمْ فَتَنْقَلِبُوْا خٰسِرِيْنَ', 'yā ayyuhallażīna āmanū in tuṭī\'ullażīna kafarụ yaruddụkum \'alā a\'qābikum fa tangqalibụ khāsirīn'),
(442, 3, 150, 'بَلِ اللّٰهُ مَوْلٰىكُمْ ۚ وَهُوَ خَيْرُ النّٰصِرِيْنَ', 'balillāhu maulākum, wa huwa khairun nāṣirīn'),
(443, 3, 151, 'سَنُلْقِيْ فِيْ قُلُوْبِ الَّذِيْنَ كَفَرُوا الرُّعْبَ بِمَٓا اَشْرَكُوْا بِاللّٰهِ مَا لَمْ يُنَزِّلْ بِهٖ سُلْطٰنًا ۚ وَمَأْوٰىهُمُ النَّارُ ۗ وَبِئْسَ مَثْوَى الظّٰلِمِيْنَ', 'sanulqī fī qulụbillażīna kafarur-ru\'ba bimā asyrakụ billāhi mā lam yunazzil bihī sulṭānā, wa ma`wāhumun-nār, wa bi`sa maṡwaẓ-ẓālimīn'),
(444, 3, 152, 'وَلَقَدْ صَدَقَكُمُ اللّٰهُ وَعْدَهٗٓ اِذْ تَحُسُّوْنَهُمْ بِاِذْنِهٖ ۚ حَتّٰىٓ اِذَا فَشِلْتُمْ وَتَنَازَعْتُمْ فِى الْاَمْرِ وَعَصَيْتُمْ مِّنْۢ بَعْدِ مَآ اَرٰىكُمْ مَّا تُحِبُّوْنَ ۗ مِنْكُمْ مَّنْ يُّرِيْدُ الدُّنْيَا وَمِنْكُمْ مَّنْ يُّرِيْدُ الْاٰخِرَةَ ۚ ثُمَّ صَرَفَكُمْ عَنْهُمْ لِيَبْتَلِيَكُمْ ۚ وَلَقَدْ عَفَا عَنْكُمْ ۗ وَاللّٰهُ ذُوْ فَضْلٍ عَلَى الْمُؤْمِنِيْنَ', 'wa laqad ṣadaqakumullāhu wa\'dahū iż taḥussụnahum bi`iżnih, ḥattā iżā fasyiltum wa tanāza\'tum fil-amri wa \'aṣaitum mim ba\'di mā arākum mā tuḥibbụn, mingkum may yurīdud-dun-yā wa mingkum may yurīdul-ākhirah, ṡumma ṣarafakum \'an-hum liyabtaliyakum, wa laqad \'afā \'angkum, wallāhu żụ faḍlin \'alal-mu`minīn'),
(445, 3, 153, '۞ اِذْ تُصْعِدُوْنَ وَلَا تَلْوٗنَ عَلٰٓى اَحَدٍ وَّالرَّسُوْلُ يَدْعُوْكُمْ فِيْٓ اُخْرٰىكُمْ فَاَثَابَكُمْ غَمًّا ۢبِغَمٍّ لِّكَيْلَا تَحْزَنُوْا عَلٰى مَا فَاتَكُمْ وَلَا مَآ اَصَابَكُمْ ۗ وَاللّٰهُ خَبِيْرٌ ۢبِمَا تَعْمَلُوْنَ', 'iż tuṣ\'idụna wa lā talwụna \'alā aḥadiw war-rasụlu yad\'ụkum fī ukhrākum fa aṡābakum gammam bigammil likai lā taḥzanụ \'alā mā fātakum wa lā mā aṣābakum, wallāhu khabīrum bimā ta\'malụn'),
(446, 3, 154, 'ثُمَّ اَنْزَلَ عَلَيْكُمْ مِّنْۢ بَعْدِ الْغَمِّ اَمَنَةً نُّعَاسًا يَّغْشٰى طَۤاىِٕفَةً مِّنْكُمْ ۙ وَطَۤاىِٕفَةٌ قَدْ اَهَمَّتْهُمْ اَنْفُسُهُمْ يَظُنُّوْنَ بِاللّٰهِ غَيْرَ الْحَقِّ ظَنَّ الْجَاهِلِيَّةِ ۗ يَقُوْلُوْنَ هَلْ لَّنَا مِنَ الْاَمْرِ مِنْ شَيْءٍ ۗ قُلْ اِنَّ الْاَمْرَ كُلَّهٗ لِلّٰهِ ۗ يُخْفُوْنَ فِيْٓ اَنْفُسِهِمْ مَّا لَا يُبْدُوْنَ لَكَ ۗ يَقُوْلُوْنَ لَوْ كَانَ لَنَا مِنَ الْاَمْرِ شَيْءٌ مَّا قُتِلْنَا هٰهُنَا ۗ قُلْ لَّوْ كُنْتُمْ فِيْ بُيُوْتِكُمْ لَبَرَزَ الَّذِيْنَ كُتِبَ عَلَيْهِمُ الْقَتْلُ اِلٰى مَضَاجِعِهِمْ ۚ وَلِيَبْتَلِيَ اللّٰهُ مَا فِيْ صُدُوْرِكُمْ وَلِيُمَحِّصَ مَا فِيْ قُلُوْبِكُمْ ۗ وَاللّٰهُ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ', 'ṡumma anzala \'alaikum mim ba\'dil-gammi amanatan nu\'āsay yagsyā ṭā`ifatam mingkum wa ṭā`ifatung qad ahammat-hum anfusuhum yaẓunnụna billāhi gairal-ḥaqqi ẓannal-jāhiliyyah, yaqụlụna hal lanā minal-amri min syaī`, qul innal-amra kullahụ lillāh, yukhfụna fī anfusihim mā lā yubdụna lak, yaqụlụna lau kāna lanā minal-amri syai`um mā qutilnā hāhunā, qul lau kuntum fī buyụtikum labarazallażīna kutiba \'alaihimul-qatlu ilā maḍāji\'ihim, wa liyabtaliyallāhu mā fī ṣudụrikum wa liyumaḥḥiṣa mā fī qulụbikum, wallāhu \'alīmum biżātiṣ-ṣudụr'),
(447, 3, 155, 'اِنَّ الَّذِيْنَ تَوَلَّوْا مِنْكُمْ يَوْمَ الْتَقَى الْجَمْعٰنِۙ اِنَّمَا اسْتَزَلَّهُمُ الشَّيْطٰنُ بِبَعْضِ مَا كَسَبُوْا ۚ وَلَقَدْ عَفَا اللّٰهُ عَنْهُمْ ۗ اِنَّ اللّٰهَ غَفُوْرٌ حَلِيْمٌ', 'innallażīna tawallau mingkum yaumaltaqal-jam\'āni innamastazallahumusy-syaiṭānu biba\'ḍi mā kasabụ, wa laqad \'afallāhu \'an-hum, innallāha gafụrun ḥalīm'),
(448, 3, 156, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَكُوْنُوْا كَالَّذِيْنَ كَفَرُوْا وَقَالُوْا لِاِخْوَانِهِمْ اِذَا ضَرَبُوْا فِى الْاَرْضِ اَوْ كَانُوْا غُزًّى لَّوْ كَانُوْا عِنْدَنَا مَا مَاتُوْا وَمَا قُتِلُوْاۚ لِيَجْعَلَ اللّٰهُ ذٰلِكَ حَسْرَةً فِيْ قُلُوْبِهِمْ ۗ وَاللّٰهُ يُحْيٖ وَيُمِيْتُ ۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرٌ', 'yā ayyuhallażīna āmanụ lā takụnụ kallażīna kafarụ wa qālụ li`ikhwānihim iżā ḍarabụ fil-arḍi au kānụ guzzal lau kānụ \'indanā mā mātụ wa mā qutilụ, liyaj\'alallāhu żālika ḥasratan fī qulụbihim, wallāhu yuḥyī wa yumīt, wallāhu bimā ta\'malụna baṣīr'),
(449, 3, 157, 'وَلَىِٕنْ قُتِلْتُمْ فِيْ سَبِيْلِ اللّٰهِ اَوْ مُتُّمْ لَمَغْفِرَةٌ مِّنَ اللّٰهِ وَرَحْمَةٌ خَيْرٌ مِّمَّا يَجْمَعُوْنَ', 'wa la`ing qutiltum fī sabīlillāhi au muttum lamagfiratum minallāhi wa raḥmatun khairum mimmā yajma\'ụn'),
(450, 3, 158, 'وَلَىِٕنْ مُّتُّمْ اَوْ قُتِلْتُمْ لَاِلَى اللّٰهِ تُحْشَرُوْنَ', 'wa la`im muttum au qutiltum la`ilallāhi tuḥsyarụn'),
(451, 3, 159, 'فَبِمَا رَحْمَةٍ مِّنَ اللّٰهِ لِنْتَ لَهُمْ ۚ وَلَوْ كُنْتَ فَظًّا غَلِيْظَ الْقَلْبِ لَانْفَضُّوْا مِنْ حَوْلِكَ ۖ فَاعْفُ عَنْهُمْ وَاسْتَغْفِرْ لَهُمْ وَشَاوِرْهُمْ فِى الْاَمْرِۚ فَاِذَا عَزَمْتَ فَتَوَكَّلْ عَلَى اللّٰهِ ۗ اِنَّ اللّٰهَ يُحِبُّ الْمُتَوَكِّلِيْنَ', 'fa bimā raḥmatim minallāhi linta lahum, walau kunta faẓẓan galīẓal-qalbi lanfaḍḍụ min ḥaulika fa\'fu \'an-hum wastagfir lahum wa syāwir-hum fil-amr, fa iżā \'azamta fa tawakkal \'alallāh, innallāha yuḥibbul-mutawakkilīn'),
(452, 3, 160, 'اِنْ يَّنْصُرْكُمُ اللّٰهُ فَلَا غَالِبَ لَكُمْ ۚ وَاِنْ يَّخْذُلْكُمْ فَمَنْ ذَا الَّذِيْ يَنْصُرُكُمْ مِّنْۢ بَعْدِهٖ ۗ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ', 'iy yanṣurkumullāhu fa lā gāliba lakum, wa iy yakhżulkum fa man żallażī yanṣurukum mim ba\'dih, wa \'alallāhi falyatawakkalil-mu`minụn'),
(453, 3, 161, 'وَمَا كَانَ لِنَبِيٍّ اَنْ يَّغُلَّ ۗوَمَنْ يَّغْلُلْ يَأْتِ بِمَا غَلَّ يَوْمَ الْقِيٰمَةِ ۚ ثُمَّ تُوَفّٰى كُلُّ نَفْسٍ مَّا كَسَبَتْ وَهُمْ لَا يُظْلَمُوْنَ', 'wa mā kāna linabiyyin ay yagull, wa may yaglul ya`ti bimā galla yaumal-qiyāmah, ṡumma tuwaffā kullu nafsim mā kasabat wa hum lā yuẓlamụn'),
(454, 3, 162, 'اَفَمَنِ اتَّبَعَ رِضْوَانَ اللّٰهِ كَمَنْۢ بَاۤءَ بِسَخَطٍ مِّنَ اللّٰهِ وَمَأْوٰىهُ جَهَنَّمُ ۗ وَبِئْسَ الْمَصِيْرُ', 'a fa manittaba\'a riḍwānallāhi kamam bā`a bisakhaṭim minallāhi wa ma`wāhu jahannam, wa bi`sal-maṣīr'),
(455, 3, 163, 'هُمْ دَرَجٰتٌ عِنْدَ اللّٰهِ ۗ وَاللّٰهُ بَصِيْرٌ ۢبِمَا يَعْمَلُوْنَ', 'hum darajātun \'indallāh, wallāhu baṣīrum bimā ya\'malụn'),
(456, 3, 164, 'لَقَدْ مَنَّ اللّٰهُ عَلَى الْمُؤْمِنِيْنَ اِذْ بَعَثَ فِيْهِمْ رَسُوْلًا مِّنْ اَنْفُسِهِمْ يَتْلُوْا عَلَيْهِمْ اٰيٰتِهٖ وَيُزَكِّيْهِمْ وَيُعَلِّمُهُمُ الْكِتٰبَ وَالْحِكْمَةَۚ وَاِنْ كَانُوْا مِنْ قَبْلُ لَفِيْ ضَلٰلٍ مُّبِيْنٍ', 'laqad mannallāhu \'alal-mu`minīna iż ba\'aṡa fīhim rasụlam min anfusihim yatlụ \'alaihim āyātihī wa yuzakkīhim wa yu\'allimuhumul-kitāba wal-ḥikmah, wa ing kānụ ming qablu lafī ḍalālim mubīn'),
(457, 3, 165, 'اَوَلَمَّآ اَصَابَتْكُمْ مُّصِيْبَةٌ قَدْ اَصَبْتُمْ مِّثْلَيْهَاۙ قُلْتُمْ اَنّٰى هٰذَا ۗ قُلْ هُوَ مِنْ عِنْدِ اَنْفُسِكُمْ ۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'a wa lammā aṣābatkum muṣībatung qad aṣabtum miṡlaihā qultum annā hāżā, qul huwa min \'indi anfusikum, innallāha \'alā kulli syai`ing qadīr'),
(458, 3, 166, 'وَمَآ اَصَابَكُمْ يَوْمَ الْتَقَى الْجَمْعٰنِ فَبِاِذْنِ اللّٰهِ وَلِيَعْلَمَ الْمُؤْمِنِيْنَۙ', 'wa mā aṣābakum yaumaltaqal-jam\'āni fa bi`iżnillāhi wa liya\'lamal-mu`minīn'),
(459, 3, 167, 'وَلِيَعْلَمَ الَّذِيْنَ نَافَقُوْا ۖوَقِيْلَ لَهُمْ تَعَالَوْا قَاتِلُوْا فِيْ سَبِيْلِ اللّٰهِ اَوِ ادْفَعُوْا ۗ قَالُوْا لَوْ نَعْلَمُ قِتَالًا لَّاتَّبَعْنٰكُمْ ۗ هُمْ لِلْكُفْرِ يَوْمَىِٕذٍ اَقْرَبُ مِنْهُمْ لِلْاِيْمَانِ ۚ يَقُوْلُوْنَ بِاَفْوَاهِهِمْ مَّا لَيْسَ فِيْ قُلُوْبِهِمْ ۗ وَاللّٰهُ اَعْلَمُ بِمَا يَكْتُمُوْنَۚ', 'wa liya\'lamallażīna nāfaqụ wa qīla lahum ta\'ālau qātilụ fī sabīlillāhi awidfa\'ụ, qālụ lau na\'lamu qitālal lattaba\'nākum, hum lil-kufri yauma`iżin aqrabu min-hum lil-īmān, yaqụlụna bi`afwāhihim mā laisa fī qulụbihim, wallāhu a\'lamu bimā yaktumụn'),
(460, 3, 168, 'اَلَّذِيْنَ قَالُوْا لِاِخْوَانِهِمْ وَقَعَدُوْا لَوْ اَطَاعُوْنَا مَا قُتِلُوْا ۗ قُلْ فَادْرَءُوْا عَنْ اَنْفُسِكُمُ الْمَوْتَ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'allażīna qālụ li`ikhwānihim wa qa\'adụ lau aṭā\'ụnā mā qutilụ, qul fadra`ụ \'an anfusikumul-mauta ing kuntum ṣādiqīn'),
(461, 3, 169, 'وَلَا تَحْسَبَنَّ الَّذِيْنَ قُتِلُوْا فِيْ سَبِيْلِ اللّٰهِ اَمْوَاتًا ۗ بَلْ اَحْيَاۤءٌ عِنْدَ رَبِّهِمْ يُرْزَقُوْنَۙ', 'wa lā taḥsabannallażīna qutilụ fī sabīlillāhi amwātā, bal aḥyā`un \'inda rabbihim yurzaqụn'),
(462, 3, 170, 'فَرِحِيْنَ بِمَآ اٰتٰىهُمُ اللّٰهُ مِنْ فَضْلِهٖۙ وَيَسْتَبْشِرُوْنَ بِالَّذِيْنَ لَمْ يَلْحَقُوْا بِهِمْ مِّنْ خَلْفِهِمْ ۙ اَلَّا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَۘ', 'fariḥīna bimā ātāhumullāhu min faḍlihī wa yastabsyirụna billażīna lam yal-ḥaqụ bihim min khalfihim allā khaufun \'alaihim wa lā hum yaḥzanụn'),
(463, 3, 171, '۞ يَسْتَبْشِرُوْنَ بِنِعْمَةٍ مِّنَ اللّٰهِ وَفَضْلٍۗ وَاَنَّ اللّٰهَ لَا يُضِيْعُ اَجْرَ الْمُؤْمِنِيْنَ', 'yastabsyirụna bini\'matim minallāhi wa faḍl, wa annallāha lā yuḍī\'u ajral-mu`minīn'),
(464, 3, 172, 'اَلَّذِيْنَ اسْتَجَابُوْا لِلّٰهِ وَالرَّسُوْلِ مِنْۢ بَعْدِ مَآ اَصَابَهُمُ الْقَرْحُ ۖ لِلَّذِيْنَ اَحْسَنُوْا مِنْهُمْ وَاتَّقَوْا اَجْرٌ عَظِيْمٌۚ', 'allażīnastajābụ lillāhi war-rasụli mim ba\'di mā aṣābahumul-qar-ḥu lillażīna aḥsanụ min-hum wattaqau ajrun \'aẓīm'),
(465, 3, 173, 'اَلَّذِيْنَ قَالَ لَهُمُ النَّاسُ اِنَّ النَّاسَ قَدْ جَمَعُوْا لَكُمْ فَاخْشَوْهُمْ فَزَادَهُمْ اِيْمَانًاۖ وَّقَالُوْا حَسْبُنَا اللّٰهُ وَنِعْمَ الْوَكِيْلُ', 'allażīna qāla lahumun-nāsu innan-nāsa qad jama\'ụ lakum fakhsyauhum fa zādahum īmānaw wa qālụ ḥasbunallāhu wa ni\'mal-wakīl'),
(466, 3, 174, 'فَانْقَلَبُوْا بِنِعْمَةٍ مِّنَ اللّٰهِ وَفَضْلٍ لَّمْ يَمْسَسْهُمْ سُوْۤءٌۙ وَّاتَّبَعُوْا رِضْوَانَ اللّٰهِ ۗ وَاللّٰهُ ذُوْ فَضْلٍ عَظِيْمٍ', 'fangqalabụ bini\'matim minallāhi wa faḍlil lam yamsas-hum sū`uw wattaba\'ụ riḍwānallāh, wallāhu żụ faḍlin \'aẓīm'),
(467, 3, 175, 'اِنَّمَا ذٰلِكُمُ الشَّيْطٰنُ يُخَوِّفُ اَوْلِيَاۤءَهٗۖ فَلَا تَخَافُوْهُمْ وَخَافُوْنِ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ', 'innamā żālikumusy-syaiṭānu yukhawwifu auliyā`ahụ fa lā takhāfụhum wa khāfụni ing kuntum mu`minīn'),
(468, 3, 176, 'وَلَا يَحْزُنْكَ الَّذِيْنَ يُسَارِعُوْنَ فِى الْكُفْرِۚ اِنَّهُمْ لَنْ يَّضُرُّوا اللّٰهَ شَيْـًٔا ۗ يُرِيْدُ اللّٰهُ اَلَّا يَجْعَلَ لَهُمْ حَظًّا فِى الْاٰخِرَةِ وَلَهُمْ عَذَابٌ عَظِيْمٌۚ', 'wa lā yaḥzungkallażīna yusāri\'ụna fil-kufr, innahum lay yaḍurrullāha syai`ā, yurīdullāhu allā yaj\'ala lahum ḥaẓẓan fil-ākhirati wa lahum \'ażābun \'aẓīm'),
(469, 3, 177, 'اِنَّ الَّذِيْنَ اشْتَرَوُا الْكُفْرَ بِالْاِيْمَانِ لَنْ يَّضُرُّوا اللّٰهَ شَيْـًٔاۚ وَلَهُمْ عَذَابٌ اَلِيْمٌ', 'innallażīnasytarawul-kufra bil-īmāni lay yaḍurrullāha syai`ā, wa lahum \'ażābun alīm'),
(470, 3, 178, 'وَلَا يَحْسَبَنَّ الَّذِيْنَ كَفَرُوْٓا اَنَّمَا نُمْلِيْ لَهُمْ خَيْرٌ لِّاَنْفُسِهِمْ ۗ اِنَّمَا نُمْلِيْ لَهُمْ لِيَزْدَادُوْٓا اِثْمًا ۚ وَلَهُمْ عَذَابٌ مُّهِيْنٌ', 'wa lā yaḥsabannallażīna kafarū annamā numlī lahum khairul li`anfusihim, innamā numlī lahum liyazdādū iṡmā, wa lahum \'ażābum muhīn'),
(471, 3, 179, 'مَا كَانَ اللّٰهُ لِيَذَرَ الْمُؤْمِنِيْنَ عَلٰى مَآ اَنْتُمْ عَلَيْهِ حَتّٰى يَمِيْزَ الْخَبِيْثَ مِنَ الطَّيِّبِ ۗ وَمَا كَانَ اللّٰهُ لِيُطْلِعَكُمْ عَلَى الْغَيْبِ وَلٰكِنَّ اللّٰهَ يَجْتَبِيْ مِنْ رُّسُلِهٖ مَنْ يَّشَاۤءُ ۖ فَاٰمِنُوْا بِاللّٰهِ وَرُسُلِهٖ ۚ وَاِنْ تُؤْمِنُوْا وَتَتَّقُوْا فَلَكُمْ اَجْرٌ عَظِيْمٌ', 'mā kānallāhu liyażaral-mu`minīna \'alā mā antum \'alaihi ḥattā yamīzal-khabīṡa minaṭ-ṭayyib, wa mā kānallāhu liyuṭli\'akum \'alal-gaibi wa lākinnallāha yajtabī mir rusulihī may yasyā`u fa āminụ billāhi wa rusulih, wa in tu`minụ wa tattaqụ fa lakum ajrun \'aẓīm'),
(472, 3, 180, 'وَلَا يَحْسَبَنَّ الَّذِيْنَ يَبْخَلُوْنَ بِمَآ اٰتٰىهُمُ اللّٰهُ مِنْ فَضْلِهٖ هُوَ خَيْرًا لَّهُمْ ۗ بَلْ هُوَ شَرٌّ لَّهُمْ ۗ سَيُطَوَّقُوْنَ مَا بَخِلُوْا بِهٖ يَوْمَ الْقِيٰمَةِ ۗ وَلِلّٰهِ مِيْرَاثُ السَّمٰوٰتِ وَالْاَرْضِۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ', 'wa lā yaḥsabannallażīna yabkhalụna bimā ātāhumullāhu min faḍlihī huwa khairal lahum, bal huwa syarrul lahum, sayuṭawwaqụna mā bakhilụ bihī yaumal-qiyāmah, wa lillāhi mīrāṡus-samāwāti wal-arḍ, wallāhu bimā ta\'malụna khabīr'),
(473, 3, 181, 'لَقَدْ سَمِعَ اللّٰهُ قَوْلَ الَّذِيْنَ قَالُوْٓا اِنَّ اللّٰهَ فَقِيْرٌ وَّنَحْنُ اَغْنِيَاۤءُ ۘ سَنَكْتُبُ مَا قَالُوْا وَقَتْلَهُمُ الْاَنْۢبِيَاۤءَ بِغَيْرِ حَقٍّۙ وَّنَقُوْلُ ذُوْقُوْا عَذَابَ الْحَرِيْقِ', 'laqad sami\'allāhu qaulallażīna qālū innallāha faqīruw wa naḥnu agniyā`, sanaktubu mā qālụ wa qatlahumul-ambiyā`a bigairi ḥaqqiw wa naqụlu żụqụ \'ażābal-ḥarīq'),
(474, 3, 182, 'ذٰلِكَ بِمَا قَدَّمَتْ اَيْدِيْكُمْ وَاَنَّ اللّٰهَ لَيْسَ بِظَلَّامٍ لِّلْعَبِيْدِۚ', 'żālika bimā qaddamat aidīkum wa annallāha laisa biẓallāmil lil-\'abīd'),
(475, 3, 183, 'اَلَّذِيْنَ قَالُوْٓا اِنَّ اللّٰهَ عَهِدَ اِلَيْنَآ اَلَّا نُؤْمِنَ لِرَسُوْلٍ حَتّٰى يَأْتِيَنَا بِقُرْبَانٍ تَأْكُلُهُ النَّارُ ۗ قُلْ قَدْ جَاۤءَكُمْ رُسُلٌ مِّنْ قَبْلِيْ بِالْبَيِّنٰتِ وَبِالَّذِيْ قُلْتُمْ فَلِمَ قَتَلْتُمُوْهُمْ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'allażīna qālū innallāha \'ahida ilainā allā nu`mina lirasụlin ḥattā ya`tiyanā biqurbānin ta`kuluhun-nār, qul qad jā`akum rusulum ming qablī bil-bayyināti wa billażī qultum fa lima qataltumụhum ing kuntum ṣādiqīn'),
(476, 3, 184, 'فَاِنْ كَذَّبُوْكَ فَقَدْ كُذِّبَ رُسُلٌ مِّنْ قَبْلِكَ جَاۤءُوْ بِالْبَيِّنٰتِ وَالزُّبُرِ وَالْكِتٰبِ الْمُنِيْرِ', 'fa ing każżabụka fa qad kużżiba rusulum ming qablika jā`ụ bil-bayyināti waz-zuburi wal-kitābil-munīr'),
(477, 3, 185, 'كُلُّ نَفْسٍ ذَاۤىِٕقَةُ الْمَوْتِۗ وَاِنَّمَا تُوَفَّوْنَ اُجُوْرَكُمْ يَوْمَ الْقِيٰمَةِ ۗ فَمَنْ زُحْزِحَ عَنِ النَّارِ وَاُدْخِلَ الْجَنَّةَ فَقَدْ فَازَ ۗ وَمَا الْحَيٰوةُ الدُّنْيَآ اِلَّا مَتَاعُ الْغُرُوْرِ', 'kullu nafsin żā`iqatul maụt, wa innamā tuwaffauna ujụrakum yaumal-qiyāmah, fa man zuḥziḥa \'anin-nāri wa udkhilal-jannata fa qad fāz, wa mal-ḥayātud-dun-yā illā matā\'ul-gurụr'),
(478, 3, 186, '۞ لَتُبْلَوُنَّ فِيْٓ اَمْوَالِكُمْ وَاَنْفُسِكُمْۗ وَلَتَسْمَعُنَّ مِنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ مِنْ قَبْلِكُمْ وَمِنَ الَّذِيْنَ اَشْرَكُوْٓا اَذًى كَثِيْرًا ۗ وَاِنْ تَصْبِرُوْا وَتَتَّقُوْا فَاِنَّ ذٰلِكَ مِنْ عَزْمِ الْاُمُوْرِ', 'latublawunna fī amwālikum wa anfusikum, wa latasma\'unna minallażīna ụtul-kitāba ming qablikum wa minallażīna asyrakū ażang kaṡīrā, wa in taṣbirụ wa tattaqụ fa inna żālika min \'azmil-umụr'),
(479, 3, 187, 'وَاِذْ اَخَذَ اللّٰهُ مِيْثَاقَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ لَتُبَيِّنُنَّهٗ لِلنَّاسِ وَلَا تَكْتُمُوْنَهٗۖ فَنَبَذُوْهُ وَرَاۤءَ ظُهُوْرِهِمْ وَاشْتَرَوْا بِهٖ ثَمَنًا قَلِيْلًا ۗ فَبِئْسَ مَا يَشْتَرُوْنَ', 'wa iż akhażallāhu mīṡāqallażīna ụtul-kitāba latubayyinunnahụ lin-nāsi wa lā taktumụnahụ fa nabażụhu warā`a ẓuhụrihim wasytarau bihī ṡamanang qalīlā, fa bi`sa mā yasytarụn'),
(480, 3, 188, 'لَا تَحْسَبَنَّ الَّذِيْنَ يَفْرَحُوْنَ بِمَآ اَتَوْا وَّيُحِبُّوْنَ اَنْ يُّحْمَدُوْا بِمَا لَمْ يَفْعَلُوْا فَلَا تَحْسَبَنَّهُمْ بِمَفَازَةٍ مِّنَ الْعَذَابِۚ وَلَهُمْ عَذَابٌ اَلِيْمٌ', 'lā taḥsabannallażīna yafraḥụna bimā ataw wa yuḥibbụna ay yuḥmadụ bimā lam yaf\'alụ fa lā taḥsabannahum bimafāzatim minal-\'ażāb, wa lahum \'ażābun alīm'),
(481, 3, 189, 'وَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'wa lillāhi mulkus-samāwāti wal-arḍ, wallāhu \'alā kulli syai`ing qadīr'),
(482, 3, 190, 'اِنَّ فِيْ خَلْقِ السَّمٰوٰتِ وَالْاَرْضِ وَاخْتِلَافِ الَّيْلِ وَالنَّهَارِ لَاٰيٰتٍ لِّاُولِى الْاَلْبَابِۙ', 'inna fī khalqis-samāwāti wal-arḍi wakhtilāfil-laili wan-nahāri la`āyātil li`ulil-albāb'),
(483, 3, 191, 'الَّذِيْنَ يَذْكُرُوْنَ اللّٰهَ قِيَامًا وَّقُعُوْدًا وَّعَلٰى جُنُوْبِهِمْ وَيَتَفَكَّرُوْنَ فِيْ خَلْقِ السَّمٰوٰتِ وَالْاَرْضِۚ رَبَّنَا مَا خَلَقْتَ هٰذَا بَاطِلًاۚ سُبْحٰنَكَ فَقِنَا عَذَابَ النَّارِ', 'allażīna yażkurụnallāha qiyāmaw wa qu\'ụdaw wa \'alā junụbihim wa yatafakkarụna fī khalqis-samāwāti wal-arḍ, rabbanā mā khalaqta hāżā bāṭilā, sub-ḥānaka fa qinā \'ażāban-nār'),
(484, 3, 192, 'رَبَّنَآ اِنَّكَ مَنْ تُدْخِلِ النَّارَ فَقَدْ اَخْزَيْتَهٗ ۗ وَمَا لِلظّٰلِمِيْنَ مِنْ اَنْصَارٍ', 'rabbanā innaka man tudkhilin-nāra fa qad akhzaitah, wa mā liẓ-ẓālimīna min anṣār'),
(485, 3, 193, 'رَبَّنَآ اِنَّنَا سَمِعْنَا مُنَادِيًا يُّنَادِيْ لِلْاِيْمَانِ اَنْ اٰمِنُوْا بِرَبِّكُمْ فَاٰمَنَّا ۖرَبَّنَا فَاغْفِرْ لَنَا ذُنُوْبَنَا وَكَفِّرْ عَنَّا سَيِّاٰتِنَا وَتَوَفَّنَا مَعَ الْاَبْرَارِۚ', 'rabbanā innanā sami\'nā munādiyay yunādī lil-īmāni an āminụ birabbikum fa āmannā rabbanā fagfir lanā żunụbanā wa kaffir \'annā sayyi`ātinā wa tawaffanā ma\'al-abrār'),
(486, 3, 194, 'رَبَّنَا وَاٰتِنَا مَا وَعَدْتَّنَا عَلٰى رُسُلِكَ وَلَا تُخْزِنَا يَوْمَ الْقِيٰمَةِ ۗ اِنَّكَ لَا تُخْلِفُ الْمِيْعَادَ', 'rabbanā wa ātinā mā wa\'attanā \'alā rusulika wa lā tukhzinā yaumal-qiyāmah, innaka lā tukhliful-mī\'ād'),
(487, 3, 195, 'فَاسْتَجَابَ لَهُمْ رَبُّهُمْ اَنِّيْ لَآ اُضِيْعُ عَمَلَ عَامِلٍ مِّنْكُمْ مِّنْ ذَكَرٍ اَوْ اُنْثٰى ۚ بَعْضُكُمْ مِّنْۢ بَعْضٍ ۚ فَالَّذِيْنَ هَاجَرُوْا وَاُخْرِجُوْا مِنْ دِيَارِهِمْ وَاُوْذُوْا فِيْ سَبِيْلِيْ وَقٰتَلُوْا وَقُتِلُوْا لَاُكَفِّرَنَّ عَنْهُمْ سَيِّاٰتِهِمْ وَلَاُدْخِلَنَّهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۚ ثَوَابًا مِّنْ عِنْدِ اللّٰهِ ۗ وَاللّٰهُ عِنْدَهٗ حُسْنُ الثَّوَابِ', 'fastajāba lahum rabbuhum annī lā uḍī\'u \'amala \'āmilim mingkum min żakarin au unṡā, ba\'ḍukum mim ba\'ḍ, fallażīna hājarụ wa ukhrijụ min diyārihim wa ụżụ fī sabīlī wa qātalụ wa qutilụ la`ukaffiranna \'an-hum sayyi`ātihim wa la`udkhilannahum jannātin tajrī min taḥtihal-an-hār, ṡawābam min \'indillāh, wallāhu \'indahụ ḥusnuṡ-ṡawāb'),
(488, 3, 196, 'لَا يَغُرَّنَّكَ تَقَلُّبُ الَّذِيْنَ كَفَرُوْا فِى الْبِلَادِۗ', 'lā yagurrannaka taqallubullażīna kafarụ fil-bilād'),
(489, 3, 197, 'مَتَاعٌ قَلِيْلٌ ۗ ثُمَّ مَأْوٰىهُمْ جَهَنَّمُ ۗوَبِئْسَ الْمِهَادُ', 'matā\'ung qalīl, ṡumma ma`wāhum jahannam, wa bi`sal-mihād'),
(490, 3, 198, 'لٰكِنِ الَّذِيْنَ اتَّقَوْا رَبَّهُمْ لَهُمْ جَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا نُزُلًا مِّنْ عِنْدِ اللّٰهِ ۗ وَمَا عِنْدَ اللّٰهِ خَيْرٌ لِّلْاَبْرَارِ', 'lākinillażīnattaqau rabbahum lahum jannātun tajrī min taḥtihal-an-hāru khālidīna fīhā nuzulam min \'indillāh, wa mā \'indallāhi khairul lil-abrār'),
(491, 3, 199, 'وَاِنَّ مِنْ اَهْلِ الْكِتٰبِ لَمَنْ يُّؤْمِنُ بِاللّٰهِ وَمَآ اُنْزِلَ اِلَيْكُمْ وَمَآ اُنْزِلَ اِلَيْهِمْ خٰشِعِيْنَ لِلّٰهِ ۙ لَا يَشْتَرُوْنَ بِاٰيٰتِ اللّٰهِ ثَمَنًا قَلِيْلًا ۗ اُولٰۤىِٕكَ لَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْ ۗ اِنَّ اللّٰهَ سَرِيْعُ الْحِسَابِ', 'wa inna min ahlil-kitābi lamay yu`minu billāhi wa mā unzila ilaikum wa mā unzila ilaihim khāsyi\'īna lillāhi lā yasytarụna bi`āyātillāhi ṡamanang qalīlā, ulā`ika lahum ajruhum \'inda rabbihim, innallāha sarī\'ul-ḥisāb'),
(492, 3, 200, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اصْبِرُوْا وَصَابِرُوْا وَرَابِطُوْاۗ وَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُفْلِحُوْنَ', 'yā ayyuhallażīna āmanuṣbirụ wa ṣābirụ wa rābiṭụ, wattaqullāha la\'allakum tufliḥụn'),
(493, 4, 1, 'يٰٓاَيُّهَا النَّاسُ اتَّقُوْا رَبَّكُمُ الَّذِيْ خَلَقَكُمْ مِّنْ نَّفْسٍ وَّاحِدَةٍ وَّخَلَقَ مِنْهَا زَوْجَهَا وَبَثَّ مِنْهُمَا رِجَالًا كَثِيْرًا وَّنِسَاۤءً ۚ وَاتَّقُوا اللّٰهَ الَّذِيْ تَسَاۤءَلُوْنَ بِهٖ وَالْاَرْحَامَ ۗ اِنَّ اللّٰهَ كَانَ عَلَيْكُمْ رَقِيْبًا', 'yā ayyuhan-nāsuttaqụ rabbakumullażī khalaqakum min nafsiw wāḥidatiw wa khalaqa min-hā zaujahā wa baṡṡa min-humā rijālang kaṡīraw wa nisā`ā, wattaqullāhallażī tasā`alụna bihī wal-ar-ḥām, innallāha kāna \'alaikum raqībā'),
(494, 4, 2, 'وَاٰتُوا الْيَتٰمٰىٓ اَمْوَالَهُمْ وَلَا تَتَبَدَّلُوا الْخَبِيْثَ بِالطَّيِّبِ ۖ وَلَا تَأْكُلُوْٓا اَمْوَالَهُمْ اِلٰٓى اَمْوَالِكُمْ ۗ اِنَّهٗ كَانَ حُوْبًا كَبِيْرًا', 'wa ātul-yatāmā amwālahum wa lā tatabaddalul-khabīṡa biṭ-ṭayyibi wa lā ta`kulū amwālahum ilā amwālikum, innahụ kāna ḥụbang kabīrā'),
(495, 4, 3, 'وَاِنْ خِفْتُمْ اَلَّا تُقْسِطُوْا فِى الْيَتٰمٰى فَانْكِحُوْا مَا طَابَ لَكُمْ مِّنَ النِّسَاۤءِ مَثْنٰى وَثُلٰثَ وَرُبٰعَ ۚ فَاِنْ خِفْتُمْ اَلَّا تَعْدِلُوْا فَوَاحِدَةً اَوْ مَا مَلَكَتْ اَيْمَانُكُمْ ۗ ذٰلِكَ اَدْنٰٓى اَلَّا تَعُوْلُوْاۗ', 'wa in khiftum allā tuqsiṭụ fil-yatāmā fangkiḥụ mā ṭāba lakum minan-nisā`i maṡnā wa ṡulāṡa wa rubā\', fa in khiftum allā ta\'dilụ fa wāḥidatan au mā malakat aimānukum, żālika adnā allā ta\'ụlụ'),
(496, 4, 4, 'وَاٰتُوا النِّسَاۤءَ صَدُقٰتِهِنَّ نِحْلَةً ۗ فَاِنْ طِبْنَ لَكُمْ عَنْ شَيْءٍ مِّنْهُ نَفْسًا فَكُلُوْهُ هَنِيْۤـًٔا مَّرِيْۤـًٔا', 'wa ātun-nisā`a ṣaduqātihinna niḥlah, fa in ṭibna lakum \'an syai`im min-hu nafsan fa kulụhu hanī`am marī`ā'),
(497, 4, 5, 'وَلَا تُؤْتُوا السُّفَهَاۤءَ اَمْوَالَكُمُ الَّتِيْ جَعَلَ اللّٰهُ لَكُمْ قِيٰمًا وَّارْزُقُوْهُمْ فِيْهَا وَاكْسُوْهُمْ وَقُوْلُوْا لَهُمْ قَوْلًا مَّعْرُوْفًا', 'wa lā tu`tus-sufahā`a amwālakumullatī ja\'alallāhu lakum qiyāmaw warzuqụhum fīhā waksụhum wa qụlụ lahum qaulam ma\'rụfā'),
(498, 4, 6, 'وَابْتَلُوا الْيَتٰمٰى حَتّٰىٓ اِذَا بَلَغُوا النِّكَاحَۚ فَاِنْ اٰنَسْتُمْ مِّنْهُمْ رُشْدًا فَادْفَعُوْٓا اِلَيْهِمْ اَمْوَالَهُمْ ۚ وَلَا تَأْكُلُوْهَآ اِسْرَافًا وَّبِدَارًا اَنْ يَّكْبَرُوْا ۗ وَمَنْ كَانَ غَنِيًّا فَلْيَسْتَعْفِفْ ۚ وَمَنْ كَانَ فَقِيْرًا فَلْيَأْكُلْ بِالْمَعْرُوْفِ ۗ فَاِذَا دَفَعْتُمْ اِلَيْهِمْ اَمْوَالَهُمْ فَاَشْهِدُوْا عَلَيْهِمْ ۗ وَكَفٰى بِاللّٰهِ حَسِيْبًا', 'wabtalul-yatāmā ḥattā iżā balagun-nikāḥ, fa in ānastum min-hum rusydan fadfa\'ū ilaihim amwālahum, wa lā ta`kulụhā isrāfaw wa bidāran ay yakbarụ, wa mang kāna ganiyyan falyasta\'fif, wa mang kāna faqīran falya`kul bil-ma\'rụf, fa iżā dafa\'tum ilaihim amwālahum fa asy-hidụ \'alaihim, wa kafā billāhi ḥasībā'),
(499, 4, 7, 'لِلرِّجَالِ نَصِيْبٌ مِّمَّا تَرَكَ الْوَالِدٰنِ وَالْاَقْرَبُوْنَۖ وَلِلنِّسَاۤءِ نَصِيْبٌ مِّمَّا تَرَكَ الْوَالِدٰنِ وَالْاَقْرَبُوْنَ مِمَّا قَلَّ مِنْهُ اَوْ كَثُرَ ۗ نَصِيْبًا مَّفْرُوْضًا', 'lir-rijāli naṣībum mimmā tarakal-wālidāni wal-aqrabụna wa lin-nisā`i naṣībum mimmā tarakal-wālidāni wal-aqrabụna mimmā qalla min-hu au kaṡur, naṣībam mafrụḍā'),
(500, 4, 8, 'وَاِذَا حَضَرَ الْقِسْمَةَ اُولُوا الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنُ فَارْزُقُوْهُمْ مِّنْهُ وَقُوْلُوْا لَهُمْ قَوْلًا مَّعْرُوْفًا', 'wa iżā ḥaḍaral-qismata ulul-qurbā wal-yatāmā wal-masākīnu farzuqụhum min-hu wa qụlụ lahum qaulam ma\'rụfā'),
(501, 4, 9, 'وَلْيَخْشَ الَّذِيْنَ لَوْ تَرَكُوْا مِنْ خَلْفِهِمْ ذُرِّيَّةً ضِعٰفًا خَافُوْا عَلَيْهِمْۖ فَلْيَتَّقُوا اللّٰهَ وَلْيَقُوْلُوْا قَوْلًا سَدِيْدًا', 'walyakhsyallażīna lau tarakụ min khalfihim żurriyyatan ḍi\'āfan khāfụ \'alaihim falyattaqullāha walyaqụlụ qaulan sadīdā'),
(502, 4, 10, 'اِنَّ الَّذِيْنَ يَأْكُلُوْنَ اَمْوَالَ الْيَتٰمٰى ظُلْمًا اِنَّمَا يَأْكُلُوْنَ فِيْ بُطُوْنِهِمْ نَارًا ۗ وَسَيَصْلَوْنَ سَعِيْرًا', 'innallażīna ya`kulụna amwālal-yatāmā ẓulman innamā ya`kulụna fī buṭụnihim nārā, wa sayaṣlauna sa\'īrā'),
(503, 4, 11, 'يُوْصِيْكُمُ اللّٰهُ فِيْٓ اَوْلَادِكُمْ لِلذَّكَرِ مِثْلُ حَظِّ الْاُنْثَيَيْنِ ۚ فَاِنْ كُنَّ نِسَاۤءً فَوْقَ اثْنَتَيْنِ فَلَهُنَّ ثُلُثَا مَا تَرَكَ ۚ وَاِنْ كَانَتْ وَاحِدَةً فَلَهَا النِّصْفُ ۗ وَلِاَبَوَيْهِ لِكُلِّ وَاحِدٍ مِّنْهُمَا السُّدُسُ مِمَّا تَرَكَ اِنْ كَانَ لَهٗ وَلَدٌ ۚ فَاِنْ لَّمْ يَكُنْ لَّهٗ وَلَدٌ وَّوَرِثَهٗٓ اَبَوٰهُ فَلِاُمِّهِ الثُّلُثُ ۚ فَاِنْ كَانَ لَهٗٓ اِخْوَةٌ فَلِاُمِّهِ السُّدُسُ مِنْۢ بَعْدِ وَصِيَّةٍ يُّوْصِيْ بِهَآ اَوْ دَيْنٍ ۗ اٰبَاۤؤُكُمْ وَاَبْنَاۤؤُكُمْۚ لَا تَدْرُوْنَ اَيُّهُمْ اَقْرَبُ لَكُمْ نَفْعًا ۗ فَرِيْضَةً مِّنَ اللّٰهِ ۗ اِنَّ اللّٰهَ كَانَ عَلِيْمًا حَكِيْمًا', 'yụṣīkumullāhu fī aulādikum liż-żakari miṡlu ḥaẓẓil-unṡayaīn, fa ing kunna nisā`an fauqaṡnataini fa lahunna ṡuluṡā mā tarak, wa ing kānat wāḥidatan fa lahan-niṣf, wa li`abawaihi likulli wāḥidim min-humas-sudusu mimmā taraka ing kāna lahụ walad, fa il lam yakul lahụ waladuw wa wariṡahū abawāhu fa li`ummihiṡ-ṡuluṡ, fa ing kāna lahū ikhwatun fa li`ummihis-sudusu mim ba\'di waṣiyyatiy yụṣī bihā au daīn, ābā`ukum wa abnā`ukum, lā tadrụna ayyuhum aqrabu lakum naf\'ā, farīḍatam minallāh, innallāha kāna \'alīman ḥakīmā'),
(504, 4, 12, '۞ وَلَكُمْ نِصْفُ مَا تَرَكَ اَزْوَاجُكُمْ اِنْ لَّمْ يَكُنْ لَّهُنَّ وَلَدٌ ۚ فَاِنْ كَانَ لَهُنَّ وَلَدٌ فَلَكُمُ الرُّبُعُ مِمَّا تَرَكْنَ مِنْۢ بَعْدِ وَصِيَّةٍ يُّوْصِيْنَ بِهَآ اَوْ دَيْنٍ ۗ وَلَهُنَّ الرُّبُعُ مِمَّا تَرَكْتُمْ اِنْ لَّمْ يَكُنْ لَّكُمْ وَلَدٌ ۚ فَاِنْ كَانَ لَكُمْ وَلَدٌ فَلَهُنَّ الثُّمُنُ مِمَّا تَرَكْتُمْ مِّنْۢ بَعْدِ وَصِيَّةٍ تُوْصُوْنَ بِهَآ اَوْ دَيْنٍ ۗ وَاِنْ كَانَ رَجُلٌ يُّوْرَثُ كَلٰلَةً اَوِ امْرَاَةٌ وَّلَهٗٓ اَخٌ اَوْ اُخْتٌ فَلِكُلِّ وَاحِدٍ مِّنْهُمَا السُّدُسُۚ فَاِنْ كَانُوْٓا اَكْثَرَ مِنْ ذٰلِكَ فَهُمْ شُرَكَاۤءُ فِى الثُّلُثِ مِنْۢ بَعْدِ وَصِيَّةٍ يُّوْصٰى بِهَآ اَوْ دَيْنٍۙ غَيْرَ مُضَاۤرٍّ ۚ وَصِيَّةً مِّنَ اللّٰهِ ۗ وَاللّٰهُ عَلِيْمٌ حَلِيْمٌۗ', 'wa lakum niṣfu mā taraka azwājukum il lam yakul lahunna walad, fa ing kāna lahunna waladun fa lakumur-rubu\'u mimmā tarakna mim ba\'di waṣiyyatiy yụṣīna bihā au daīn, wa lahunnar-rubu\'u mimmā taraktum il lam yakul lakum walad, fa ing kāna lakum waladun fa lahunnaṡ-ṡumunu mimmā taraktum mim ba\'di waṣiyyatin tụṣụna bihā au daīn, wa ing kāna rajuluy yụraṡu kalālatan awimra`atuw wa lahū akhun au ukhtun fa likulli wāḥidim min-humas-sudus, fa ing kānū akṡara min żālika fa hum syurakā`u fiṡ-ṡuluṡi mim ba\'di waṣiyyatiy yụṣā bihā au dainin gaira muḍārr, waṣiyyatam minallāh, wallāhu \'alīmun ḥalīm'),
(505, 4, 13, 'تِلْكَ حُدُوْدُ اللّٰهِ ۗ وَمَنْ يُّطِعِ اللّٰهَ وَرَسُوْلَهٗ يُدْخِلْهُ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا ۗ وَذٰلِكَ الْفَوْزُ الْعَظِيْمُ', 'tilka ḥudụdullāh, wa may yuṭi\'illāha wa rasụlahụ yudkhil-hu jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā, wa żālikal-fauzul-\'aẓīm'),
(506, 4, 14, 'وَمَنْ يَّعْصِ اللّٰهَ وَرَسُوْلَهٗ وَيَتَعَدَّ حُدُوْدَهٗ يُدْخِلْهُ نَارًا خَالِدًا فِيْهَاۖ وَلَهٗ عَذَابٌ مُّهِيْنٌ', 'wa may ya\'ṣillāha wa rasụlahụ wa yata\'adda ḥudụdahụ yudkhil-hu nāran khālidan fīhā wa lahụ \'ażābum muḥīn'),
(507, 4, 15, 'وَالّٰتِيْ يَأْتِيْنَ الْفَاحِشَةَ مِنْ نِّسَاۤىِٕكُمْ فَاسْتَشْهِدُوْا عَلَيْهِنَّ اَرْبَعَةً مِّنْكُمْ ۚ فَاِنْ شَهِدُوْا فَاَمْسِكُوْهُنَّ فِى الْبُيُوْتِ حَتّٰى يَتَوَفّٰىهُنَّ الْمَوْتُ اَوْ يَجْعَلَ اللّٰهُ لَهُنَّ سَبِيْلًا', 'wallātī ya`tīnal-fāḥisyata min nisā`ikum fastasy-hidụ \'alaihinna arba\'atam mingkum, fa in syahidụ fa amsikụhunna fil-buyụti ḥattā yatawaffāhunnal-mautu au yaj\'alallāhu lahunna sabīlā'),
(508, 4, 16, 'وَالَّذٰنِ يَأْتِيٰنِهَا مِنْكُمْ فَاٰذُوْهُمَا ۚ فَاِنْ تَابَا وَاَصْلَحَا فَاَعْرِضُوْا عَنْهُمَا ۗ اِنَّ اللّٰهَ كَانَ تَوَّابًا رَّحِيْمًا', 'wallażāni ya`tiyānihā mingkum fa āżụhumā, fa in tābā wa aṣlaḥā fa a\'riḍụ \'an-humā, innallāha kāna tawwābar raḥīmā'),
(509, 4, 17, 'اِنَّمَا التَّوْبَةُ عَلَى اللّٰهِ لِلَّذِيْنَ يَعْمَلُوْنَ السُّوْۤءَ بِجَهَالَةٍ ثُمَّ يَتُوْبُوْنَ مِنْ قَرِيْبٍ فَاُولٰۤىِٕكَ يَتُوْبُ اللّٰهُ عَلَيْهِمْ ۗ وَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًا', 'innamat-taubatu \'alallāhi lillażīna ya\'malụnas-sū`a bijahālatin ṡumma yatụbụna ming qarībin fa ulā`ika yatụbullāhu \'alaihim, wa kānallāhu \'alīman ḥakīmā'),
(510, 4, 18, 'وَلَيْسَتِ التَّوْبَةُ لِلَّذِيْنَ يَعْمَلُوْنَ السَّيِّاٰتِۚ حَتّٰىٓ اِذَا حَضَرَ اَحَدَهُمُ الْمَوْتُ قَالَ اِنِّيْ تُبْتُ الْـٰٔنَ وَلَا الَّذِيْنَ يَمُوْتُوْنَ وَهُمْ كُفَّارٌ ۗ اُولٰۤىِٕكَ اَعْتَدْنَا لَهُمْ عَذَابًا اَلِيْمًا', 'wa laisatit-taubatu lillażīna ya\'malụnas-sayyi`āt, ḥattā iżā ḥaḍara aḥadahumul-mautu qāla innī tubtul-āna wa lallażīna yamụtụna wa hum kuffār, ulā`ika a\'tadnā lahum \'ażāban alīmā'),
(511, 4, 19, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا يَحِلُّ لَكُمْ اَنْ تَرِثُوا النِّسَاۤءَ كَرْهًا ۗ وَلَا تَعْضُلُوْهُنَّ لِتَذْهَبُوْا بِبَعْضِ مَآ اٰتَيْتُمُوْهُنَّ اِلَّآ اَنْ يَّأْتِيْنَ بِفَاحِشَةٍ مُّبَيِّنَةٍ ۚ وَعَاشِرُوْهُنَّ بِالْمَعْرُوْفِ ۚ فَاِنْ كَرِهْتُمُوْهُنَّ فَعَسٰٓى اَنْ تَكْرَهُوْا شَيْـًٔا وَّيَجْعَلَ اللّٰهُ فِيْهِ خَيْرًا كَثِيْرًا', 'yā ayyuhallażīna āmanụ lā yaḥillu lakum an tariṡun-nisā`a kar-hā, wa lā ta\'ḍulụhunna litaż-habụ biba\'ḍi mā ātaitumụhunna illā ay ya`tīna bifāḥisyatim mubayyinah, wa \'āsyirụhunna bil-ma\'rụf, fa ing karihtumụhunna fa \'asā an takrahụ syai`aw wa yaj\'alallāhu fīhi khairang kaṡīrā'),
(512, 4, 20, 'وَاِنْ اَرَدْتُّمُ اسْتِبْدَالَ زَوْجٍ مَّكَانَ زَوْجٍۙ وَّاٰتَيْتُمْ اِحْدٰىهُنَّ قِنْطَارًا فَلَا تَأْخُذُوْا مِنْهُ شَيْـًٔا ۗ اَتَأْخُذُوْنَهٗ بُهْتَانًا وَّاِثْمًا مُّبِيْنًا', 'wa in arattumustibdāla zaujim makāna zaujiw wa ātaitum iḥdāhunna qinṭāran fa lā ta`khużụ min-hu syai`ā, a ta`khużụnahụ buhtānaw wa iṡmam mubīnā'),
(513, 4, 21, 'وَكَيْفَ تَأْخُذُوْنَهٗ وَقَدْ اَفْضٰى بَعْضُكُمْ اِلٰى بَعْضٍ وَّاَخَذْنَ مِنْكُمْ مِّيْثَاقًا غَلِيْظًا', 'wa kaifa ta`khużụnahụ wa qad afḍā ba\'ḍukum ilā ba\'ḍiw wa akhażna mingkum mīṡāqan galīẓā'),
(514, 4, 22, 'وَلَا تَنْكِحُوْا مَا نَكَحَ اٰبَاۤؤُكُمْ مِّنَ النِّسَاۤءِ اِلَّا مَا قَدْ سَلَفَ ۗ اِنَّهٗ كَانَ فَاحِشَةً وَّمَقْتًاۗ وَسَاۤءَ سَبِيْلًا', 'wa lā tangkiḥụ mā nakaḥa ābā`ukum minan-nisā`i illā mā qad salaf, innahụ kāna fāḥisyataw wa maqtā, wa sā`a sabīlā'),
(515, 4, 23, 'حُرِّمَتْ عَلَيْكُمْ اُمَّهٰتُكُمْ وَبَنٰتُكُمْ وَاَخَوٰتُكُمْ وَعَمّٰتُكُمْ وَخٰلٰتُكُمْ وَبَنٰتُ الْاَخِ وَبَنٰتُ الْاُخْتِ وَاُمَّهٰتُكُمُ الّٰتِيْٓ اَرْضَعْنَكُمْ وَاَخَوٰتُكُمْ مِّنَ الرَّضَاعَةِ وَاُمَّهٰتُ نِسَاۤىِٕكُمْ وَرَبَاۤىِٕبُكُمُ الّٰتِيْ فِيْ حُجُوْرِكُمْ مِّنْ نِّسَاۤىِٕكُمُ الّٰتِيْ دَخَلْتُمْ بِهِنَّۖ فَاِنْ لَّمْ تَكُوْنُوْا دَخَلْتُمْ بِهِنَّ فَلَا جُنَاحَ عَلَيْكُمْ ۖ وَحَلَاۤىِٕلُ اَبْنَاۤىِٕكُمُ الَّذِيْنَ مِنْ اَصْلَابِكُمْۙ وَاَنْ تَجْمَعُوْا بَيْنَ الْاُخْتَيْنِ اِلَّا مَا قَدْ سَلَفَ ۗ اِنَّ اللّٰهَ كَانَ غَفُوْرًا رَّحِيْمًا ۔', 'ḥurrimat \'alaikum ummahātukum wa banatukum wa akhawātukum wa \'ammātukum wa khālātukum wa banatul-akhi wa banatul-ukhti wa ummahātukumullātī arḍa\'nakum wa akhawātukum minar-raḍā\'ati wa ummahātu nisā`ikum wa raba`ibukumullātī fī ḥujụrikum min-nisā`ikumullātī dakhaltum bihinna fa il lam takụnụ dakhaltum bihinna fa lā junāḥa \'alaikum wa ḥalā`ilu abnā`ikumullażīna min aṣlābikum wa an tajma\'ụ bainal-ukhtaini illā mā qad salaf, innallāha kāna gafụrar raḥīmā'),
(516, 4, 24, '۞ وَالْمُحْصَنٰتُ مِنَ النِّسَاۤءِ اِلَّا مَا مَلَكَتْ اَيْمَانُكُمْ ۚ كِتٰبَ اللّٰهِ عَلَيْكُمْ ۚ وَاُحِلَّ لَكُمْ مَّا وَرَاۤءَ ذٰلِكُمْ اَنْ تَبْتَغُوْا بِاَمْوَالِكُمْ مُّحْصِنِيْنَ غَيْرَ مُسَافِحِيْنَ ۗ فَمَا اسْتَمْتَعْتُمْ بِهٖ مِنْهُنَّ فَاٰتُوْهُنَّ اُجُوْرَهُنَّ فَرِيْضَةً ۗوَلَا جُنَاحَ عَلَيْكُمْ فِيْمَا تَرَاضَيْتُمْ بِهٖ مِنْۢ بَعْدِ الْفَرِيْضَةِۗ اِنَّ اللّٰهَ كَانَ عَلِيْمًا حَكِيْمًا', 'wal-muḥṣanātu minan-nisā`i illā mā malakat aimānukum, kitāballāhi \'alaikum, wa uḥilla lakum mā warā`a żālikum an tabtagụ bi`amwālikum muḥṣinīna gaira musāfiḥīn, fa mastamta\'tum bihī min-hunna fa ātụhunna ujụrahunna farīḍah, wa lā junāḥa \'alaikum fīmā tarāḍaitum bihī mim ba\'dil-farīḍah, innallāha kāna \'alīman ḥakīmā'),
(517, 4, 25, 'وَمَنْ لَّمْ يَسْتَطِعْ مِنْكُمْ طَوْلًا اَنْ يَّنْكِحَ الْمُحْصَنٰتِ الْمُؤْمِنٰتِ فَمِنْ مَّا مَلَكَتْ اَيْمَانُكُمْ مِّنْ فَتَيٰتِكُمُ الْمُؤْمِنٰتِۗ وَاللّٰهُ اَعْلَمُ بِاِيْمَانِكُمْ ۗ بَعْضُكُمْ مِّنْۢ بَعْضٍۚ فَانْكِحُوْهُنَّ بِاِذْنِ اَهْلِهِنَّ وَاٰتُوْهُنَّ اُجُوْرَهُنَّ بِالْمَعْرُوْفِ مُحْصَنٰتٍ غَيْرَ مُسٰفِحٰتٍ وَّلَا مُتَّخِذٰتِ اَخْدَانٍ ۚ فَاِذَآ اُحْصِنَّ فَاِنْ اَتَيْنَ بِفَاحِشَةٍ فَعَلَيْهِنَّ نِصْفُ مَا عَلَى الْمُحْصَنٰتِ مِنَ الْعَذَابِۗ ذٰلِكَ لِمَنْ خَشِيَ الْعَنَتَ مِنْكُمْ ۗ وَاَنْ تَصْبِرُوْا خَيْرٌ لَّكُمْ ۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ', 'wa mal lam yastaṭi\' mingkum ṭaulan ay yangkiḥal-muḥṣanātil-mu`mināti fa mimmā malakat aimānukum min fatayātikumul-mu`mināt, wallāhu a\'lamu bi`īmānikum, ba\'ḍukum mim ba\'ḍ, fangkiḥụhunna bi`iżni ahlihinna wa ātụhunna ujụrahunna bil-ma\'rụfi muḥṣanātin gaira musāfiḥātiw wa lā muttakhiżāti akhdān, fa iżā uḥṣinna fa in ataina bifāḥisyatin fa \'alaihinna niṣfu mā \'alal-muḥṣanāti minal-\'ażāb, żālika liman khasyiyal-\'anata mingkum, wa an taṣbirụ khairul lakum, wallāhu gafụrur raḥīm'),
(518, 4, 26, 'يُرِيْدُ اللّٰهُ لِيُبَيِّنَ لَكُمْ وَيَهْدِيَكُمْ سُنَنَ الَّذِيْنَ مِنْ قَبْلِكُمْ وَيَتُوْبَ عَلَيْكُمْ ۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ', 'yurīdullāhu liyubayyina lakum wa yahdiyakum sunanallażīna ming qablikum wa yatụba \'alaikum, wallāhu \'alīmun ḥakīm'),
(519, 4, 27, 'وَاللّٰهُ يُرِيْدُ اَنْ يَّتُوْبَ عَلَيْكُمْ ۗ وَيُرِيْدُ الَّذِيْنَ يَتَّبِعُوْنَ الشَّهَوٰتِ اَنْ تَمِيْلُوْا مَيْلًا عَظِيْمًا', 'wallāhu yurīdu ay yatụba \'alaikum, wa yurīdullażīna yattabi\'ụnasy-syahawāti an tamīlụ mailan \'aẓīmā'),
(520, 4, 28, 'يُرِيْدُ اللّٰهُ اَنْ يُّخَفِّفَ عَنْكُمْ ۚ وَخُلِقَ الْاِنْسَانُ ضَعِيْفًا', 'yurīdullāhu ay yukhaffifa \'angkum, wa khuliqal-insānu ḍa\'īfā'),
(521, 4, 29, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَأْكُلُوْٓا اَمْوَالَكُمْ بَيْنَكُمْ بِالْبَاطِلِ اِلَّآ اَنْ تَكُوْنَ تِجَارَةً عَنْ تَرَاضٍ مِّنْكُمْ ۗ وَلَا تَقْتُلُوْٓا اَنْفُسَكُمْ ۗ اِنَّ اللّٰهَ كَانَ بِكُمْ رَحِيْمًا', 'yā ayyuhallażīna āmanụ lā ta`kulū amwālakum bainakum bil-bāṭili illā an takụna tijāratan \'an tarāḍim mingkum, wa lā taqtulū anfusakum, innallāha kāna bikum raḥīmā'),
(522, 4, 30, 'وَمَنْ يَّفْعَلْ ذٰلِكَ عُدْوَانًا وَّظُلْمًا فَسَوْفَ نُصْلِيْهِ نَارًا ۗوَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرًا', 'wa may yaf\'al żālika \'udwānaw wa ẓulman fa saufa nuṣlīhi nārā, wa kāna żālika \'alallāhi yasīrā'),
(523, 4, 31, 'اِنْ تَجْتَنِبُوْا كَبَاۤىِٕرَ مَا تُنْهَوْنَ عَنْهُ نُكَفِّرْ عَنْكُمْ سَيِّاٰتِكُمْ وَنُدْخِلْكُمْ مُّدْخَلًا كَرِيْمًا', 'in tajtanibụ kabā`ira mā tun-hauna \'an-hu nukaffir \'angkum sayyi`ātikum wa nudkhilkum mudkhalang karīmā'),
(524, 4, 32, 'وَلَا تَتَمَنَّوْا مَا فَضَّلَ اللّٰهُ بِهٖ بَعْضَكُمْ عَلٰى بَعْضٍ ۗ لِلرِّجَالِ نَصِيْبٌ مِّمَّا اكْتَسَبُوْا ۗ وَلِلنِّسَاۤءِ نَصِيْبٌ مِّمَّا اكْتَسَبْنَ ۗوَسْـَٔلُوا اللّٰهَ مِنْ فَضْلِهٖ ۗ اِنَّ اللّٰهَ كَانَ بِكُلِّ شَيْءٍ عَلِيْمًا', 'wa lā tatamannau mā faḍḍalallāhu bihī ba\'ḍakum \'alā ba\'ḍ, lir-rijāli naṣībum mimmaktasabụ, wa lin-nisā`i naṣībum mimmaktasabn, was`alullāha min faḍlih, innallāha kāna bikulli syai`in \'alīmā'),
(525, 4, 33, 'وَلِكُلٍّ جَعَلْنَا مَوَالِيَ مِمَّا تَرَكَ الْوَالِدٰنِ وَالْاَقْرَبُوْنَ ۗ وَالَّذِيْنَ عَقَدَتْ اَيْمَانُكُمْ فَاٰتُوْهُمْ نَصِيْبَهُمْ ۗ اِنَّ اللّٰهَ كَانَ عَلٰى كُلِّ شَيْءٍ شَهِيْدًا', 'wa likullin ja\'alnā mawāliya mimmā tarakal-wālidāni wal-aqrabụn, wallażīna \'aqadat aimānukum fa ātụhum naṣībahum, innallāha kāna \'alā kulli syai`in syahīdā'),
(526, 4, 34, 'اَلرِّجَالُ قَوَّامُوْنَ عَلَى النِّسَاۤءِ بِمَا فَضَّلَ اللّٰهُ بَعْضَهُمْ عَلٰى بَعْضٍ وَّبِمَآ اَنْفَقُوْا مِنْ اَمْوَالِهِمْ ۗ فَالصّٰلِحٰتُ قٰنِتٰتٌ حٰفِظٰتٌ لِّلْغَيْبِ بِمَا حَفِظَ اللّٰهُ ۗوَالّٰتِيْ تَخَافُوْنَ نُشُوْزَهُنَّ فَعِظُوْهُنَّ وَاهْجُرُوْهُنَّ فِى الْمَضَاجِعِ وَاضْرِبُوْهُنَّ ۚ فَاِنْ اَطَعْنَكُمْ فَلَا تَبْغُوْا عَلَيْهِنَّ سَبِيْلًا ۗاِنَّ اللّٰهَ كَانَ عَلِيًّا كَبِيْرًا', 'ar-rijālu qawwāmụna \'alan-nisā`i bimā faḍḍalallāhu ba\'ḍahum \'alā ba\'ḍiw wa bimā anfaqụ min amwālihim, faṣ-ṣāliḥātu qānitātun ḥāfiẓātul lil-gaibi bimā ḥafiẓallāh, wallātī takhāfụna nusyụzahunna fa\'iẓụhunna wahjurụhunna fil-maḍāji\'i waḍribụhunn, fa in aṭa\'nakum fa lā tabgụ \'alaihinna sabīlā, innallāha kāna \'aliyyang kabīrā'),
(527, 4, 35, 'وَاِنْ خِفْتُمْ شِقَاقَ بَيْنِهِمَا فَابْعَثُوْا حَكَمًا مِّنْ اَهْلِهٖ وَحَكَمًا مِّنْ اَهْلِهَا ۚ اِنْ يُّرِيْدَآ اِصْلَاحًا يُّوَفِّقِ اللّٰهُ بَيْنَهُمَا ۗ اِنَّ اللّٰهَ كَانَ عَلِيْمًا خَبِيْرًا', 'wa in khiftum syiqāqa bainihimā fab\'aṡụ ḥakamam min ahlihī wa ḥakamam min ahlihā, iy yurīdā iṣlāḥay yuwaffiqillāhu bainahumā, innallāha kāna \'alīman khabīrā'),
(528, 4, 36, '۞ وَاعْبُدُوا اللّٰهَ وَلَا تُشْرِكُوْا بِهٖ شَيْـًٔا وَّبِالْوَالِدَيْنِ اِحْسَانًا وَّبِذِى الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنِ وَالْجَارِ ذِى الْقُرْبٰى وَالْجَارِ الْجُنُبِ وَالصَّاحِبِ بِالْجَنْۢبِ وَابْنِ السَّبِيْلِۙ وَمَا مَلَكَتْ اَيْمَانُكُمْ ۗ اِنَّ اللّٰهَ لَا يُحِبُّ مَنْ كَانَ مُخْتَالًا فَخُوْرًاۙ', 'wa\'budullāha wa lā tusyrikụ bihī syai`aw wa bil-wālidaini iḥsānaw wa biżil-qurbā wal-yatāmā wal-masākīni wal-jāri żil-qurbā wal-jāril-junubi waṣ-ṣāḥibi bil-jambi wabnis-sabīli wa mā malakat aimānukum, innallāha lā yuḥibbu mang kāna mukhtālan fakhụrā'),
(529, 4, 37, ' ۨالَّذِيْنَ يَبْخَلُوْنَ وَيَأْمُرُوْنَ النَّاسَ بِالْبُخْلِ وَيَكْتُمُوْنَ مَآ اٰتٰىهُمُ اللّٰهُ مِنْ فَضْلِهٖۗ وَاَعْتَدْنَا لِلْكٰفِرِيْنَ عَذَابًا مُّهِيْنًاۚ', 'allażīna yabkhalụna wa ya`murụnan-nāsa bil-bukhli wa yaktumụna mā ātāhumullāhu min faḍlih, wa a\'tadnā lil-kāfirīna \'ażābam muhīnā'),
(530, 4, 38, 'وَالَّذِيْنَ يُنْفِقُوْنَ اَمْوَالَهُمْ رِئَاۤءَ النَّاسِ وَلَا يُؤْمِنُوْنَ بِاللّٰهِ وَلَا بِالْيَوْمِ الْاٰخِرِ ۗ وَمَنْ يَّكُنِ الشَّيْطٰنُ لَهٗ قَرِيْنًا فَسَاۤءَ قَرِيْنًا', 'wallażīna yunfiqụna amwālahum ri`ā`an-nāsi wa lā yu`minụna billāhi wa lā bil-yaumil-ākhir, wa may yakunisy-syaiṭānu lahụ qarīnan fa sā`a qarīnā'),
(531, 4, 39, 'وَمَاذَا عَلَيْهِمْ لَوْ اٰمَنُوْا بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَاَنْفَقُوْا مِمَّا رَزَقَهُمُ اللّٰهُ ۗوَكَانَ اللّٰهُ بِهِمْ عَلِيْمًا', 'wa māżā \'alaihim lau āmanụ billāhi wal-yaumil-ākhiri wa anfaqụ mimmā razaqahumullāh, wa kānallāhu bihim \'alīmā'),
(532, 4, 40, 'اِنَّ اللّٰهَ لَا يَظْلِمُ مِثْقَالَ ذَرَّةٍ ۚوَاِنْ تَكُ حَسَنَةً يُّضٰعِفْهَا وَيُؤْتِ مِنْ لَّدُنْهُ اَجْرًا عَظِيْمًا', 'innallāha lā yaẓlimu miṡqāla żarrah, wa in taku ḥasanatay yuḍā\'if-hā wa yu`ti mil ladun-hu ajran \'aẓīmā'),
(533, 4, 41, 'فَكَيْفَ اِذَا جِئْنَا مِنْ كُلِّ اُمَّةٍۢ بِشَهِيْدٍ وَّجِئْنَا بِكَ عَلٰى هٰٓؤُلَاۤءِ شَهِيْدًاۗ', 'fa kaifa iżā ji`nā ming kulli ummatim bisyahīdiw wa ji`nā bika \'alā hā`ulā`i syahīdā'),
(534, 4, 42, 'يَوْمَىِٕذٍ يَّوَدُّ الَّذِيْنَ كَفَرُوْا وَعَصَوُا الرَّسُوْلَ لَوْ تُسَوّٰى بِهِمُ الْاَرْضُۗ وَلَا يَكْتُمُوْنَ اللّٰهَ حَدِيْثًا', 'yauma`iżiy yawaddullażīna kafarụ wa \'aṣawur-rasụla lau tusawwā bihimul-arḍ, wa lā yaktumụnallāha ḥadīṡā'),
(535, 4, 43, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَقْرَبُوا الصَّلٰوةَ وَاَنْتُمْ سُكَارٰى حَتّٰى تَعْلَمُوْا مَا تَقُوْلُوْنَ وَلَا جُنُبًا اِلَّا عَابِرِيْ سَبِيْلٍ حَتّٰى تَغْتَسِلُوْا ۗوَاِنْ كُنْتُمْ مَّرْضٰٓى اَوْ عَلٰى سَفَرٍ اَوْ جَاۤءَ اَحَدٌ مِّنْكُمْ مِّنَ الْغَاۤىِٕطِ اَوْ لٰمَسْتُمُ النِّسَاۤءَ فَلَمْ تَجِدُوْا مَاۤءً فَتَيَمَّمُوْا صَعِيْدًا طَيِّبًا فَامْسَحُوْا بِوُجُوْهِكُمْ وَاَيْدِيْكُمْ ۗ اِنَّ اللّٰهَ كَانَ عَفُوًّا غَفُوْرًا', 'yā ayyuhallażīna āmanụ lā taqrabuṣ-ṣalāta wa antum sukārā ḥattā ta\'lamụ mā taqụlụna wa lā junuban illā \'ābirī sabīlin ḥattā tagtasilụ, wa ing kuntum marḍā au \'alā safarin au jā`a aḥadum mingkum minal-gā`iṭi au lāmastumun-nisā`a fa lam tajidụ mā`an fa tayammamụ ṣa\'īdan ṭayyiban famsaḥụ biwujụhikum wa aidīkum, innallāha kāna \'afuwwan gafụrā'),
(536, 4, 44, 'اَلَمْ تَرَ اِلَى الَّذِيْنَ اُوْتُوْا نَصِيْبًا مِّنَ الْكِتٰبِ يَشْتَرُوْنَ الضَّلٰلَةَ وَيُرِيْدُوْنَ اَنْ تَضِلُّوا السَّبِيْلَۗ', 'a lam tara ilallażīna ụtụ naṣībam minal-kitābi yasytarụnaḍ-ḍalālata wa yurīdụna an taḍillus-sabīl'),
(537, 4, 45, 'وَاللّٰهُ اَعْلَمُ بِاَعْدَاۤىِٕكُمْ ۗوَكَفٰى بِاللّٰهِ وَلِيًّا ۙوَّكَفٰى بِاللّٰهِ نَصِيْرًا', 'wallāhu a\'lamu bi`a\'dā`ikum, wa kafā billāhi waliyyaw wa kafā billāhi naṣīrā'),
(538, 4, 46, 'مِنَ الَّذِيْنَ هَادُوْا يُحَرِّفُوْنَ الْكَلِمَ عَنْ مَّوَاضِعِهٖ وَيَقُوْلُوْنَ سَمِعْنَا وَعَصَيْنَا وَاسْمَعْ غَيْرَ مُسْمَعٍ وَّرَاعِنَا لَيًّاۢ بِاَلْسِنَتِهِمْ وَطَعْنًا فِى الدِّيْنِۗ وَلَوْ اَنَّهُمْ قَالُوْا سَمِعْنَا وَاَطَعْنَا وَاسْمَعْ وَانْظُرْنَا لَكَانَ خَيْرًا لَّهُمْ وَاَقْوَمَۙ وَلٰكِنْ لَّعَنَهُمُ اللّٰهُ بِكُفْرِهِمْ فَلَا يُؤْمِنُوْنَ اِلَّا قَلِيْلًا', 'minallażīna hādụ yuḥarrifụnal-kalima \'am mawāḍi\'ihī wa yaqụlụna sami\'nā wa \'aṣainā wasma\' gaira musma\'iw wa rā\'inā layyam bi`alsinatihim wa ṭa\'nan fid-dīn, walau annahum qālụ sami\'nā wa aṭa\'nā wasma\' wanẓurnā lakāna khairal lahum wa aqwama wa lākil la\'anahumullāhu bikufrihim fa lā yu`minụna illā qalīlā'),
(539, 4, 47, 'يٰٓاَيُّهَا الَّذِيْنَ اُوْتُوا الْكِتٰبَ اٰمِنُوْا بِمَا نَزَّلْنَا مُصَدِّقًا لِّمَا مَعَكُمْ مِّنْ قَبْلِ اَنْ نَّطْمِسَ وُجُوْهًا فَنَرُدَّهَا عَلٰٓى اَدْبَارِهَآ اَوْ نَلْعَنَهُمْ كَمَا لَعَنَّآ اَصْحٰبَ السَّبْتِ ۗ وَكَانَ اَمْرُ اللّٰهِ مَفْعُوْلًا', 'yā ayyuhallażīna ụtul-kitāba āminụ bimā nazzalnā muṣaddiqal limā ma\'akum ming qabli an naṭmisa wujụhan fa naruddahā \'alā adbārihā au nal\'anahum kamā la\'annā aṣ-ḥābas-sabt, wa kāna amrullāhi maf\'ụlā'),
(540, 4, 48, 'اِنَّ اللّٰهَ لَا يَغْفِرُ اَنْ يُّشْرَكَ بِهٖ وَيَغْفِرُ مَا دُوْنَ ذٰلِكَ لِمَنْ يَّشَاۤءُ ۚ وَمَنْ يُّشْرِكْ بِاللّٰهِ فَقَدِ افْتَرٰٓى اِثْمًا عَظِيْمًا', 'innallāha lā yagfiru ay yusyraka bihī wa yagfiru mā dụna żālika limay yasyā`, wa may yusyrik billāhi fa qadiftarā iṡman \'aẓīmā'),
(541, 4, 49, 'اَلَمْ تَرَ اِلَى الَّذِيْنَ يُزَكُّوْنَ اَنْفُسَهُمْ ۗ بَلِ اللّٰهُ يُزَكِّيْ مَنْ يَّشَاۤءُ وَلَا يُظْلَمُوْنَ فَتِيْلًا', 'a lam tara ilallażīna yuzakkụna anfusahum, balillāhu yuzakkī may yasyā`u wa lā yuẓlamụna fatīlā'),
(542, 4, 50, 'اُنْظُرْ كَيْفَ يَفْتَرُوْنَ عَلَى اللّٰهِ الْكَذِبَۗ وَكَفٰى بِهٖٓ اِثْمًا مُّبِيْنًا', 'unẓur kaifa yaftarụna \'alallāhil-każib, wa kafā bihī iṡmam mubīnā'),
(543, 4, 51, 'اَلَمْ تَرَ اِلَى الَّذِيْنَ اُوْتُوْا نَصِيْبًا مِّنَ الْكِتٰبِ يُؤْمِنُوْنَ بِالْجِبْتِ وَالطَّاغُوْتِ وَيَقُوْلُوْنَ لِلَّذِيْنَ كَفَرُوْا هٰٓؤُلَاۤءِ اَهْدٰى مِنَ الَّذِيْنَ اٰمَنُوْا سَبِيْلًا', 'a lam tara ilallażīna ụtụ naṣībam minal-kitābi yu`minụna bil-jibti waṭ-ṭāgụti wa yaqụlụna lillażīna kafarụ hā`ulā`i ahdā minallażīna āmanụ sabīlā'),
(544, 4, 52, 'اُولٰۤىِٕكَ الَّذِيْنَ لَعَنَهُمُ اللّٰهُ ۗوَمَنْ يَّلْعَنِ اللّٰهُ فَلَنْ تَجِدَ لَهٗ نَصِيْرًا', 'ulā`ikallażīna la\'anahumullāh, wa may yal\'anillāhu fa lan tajida lahụ naṣīrā'),
(545, 4, 53, 'اَمْ لَهُمْ نَصِيْبٌ مِّنَ الْمُلْكِ فَاِذًا لَّا يُؤْتُوْنَ النَّاسَ نَقِيْرًاۙ', 'am lahum naṣībum minal-mulki fa iżal lā yu`tụnan-nāsa naqīrā'),
(546, 4, 54, 'اَمْ يَحْسُدُوْنَ النَّاسَ عَلٰى مَآ اٰتٰىهُمُ اللّٰهُ مِنْ فَضْلِهٖۚ فَقَدْ اٰتَيْنَآ اٰلَ اِبْرٰهِيْمَ الْكِتٰبَ وَالْحِكْمَةَ وَاٰتَيْنٰهُمْ مُّلْكًا عَظِيْمًا', 'am yaḥsudụnan-nāsa \'alā mā ātāhumullāhu min faḍlih, fa qad ātainā āla ibrāhīmal-kitāba wal-ḥikmata wa ātaināhum mulkan \'aẓīmā'),
(547, 4, 55, 'فَمِنْهُمْ مَّنْ اٰمَنَ بِهٖ وَمِنْهُمْ مَّنْ صَدَّ عَنْهُ ۗ وَكَفٰى بِجَهَنَّمَ سَعِيْرًا', 'fa min-hum man āmana bihī wa min-hum man ṣadda \'an-h, wa kafā bijahannama sa\'īrā'),
(548, 4, 56, 'اِنَّ الَّذِيْنَ كَفَرُوْا بِاٰيٰتِنَا سَوْفَ نُصْلِيْهِمْ نَارًاۗ كُلَّمَا نَضِجَتْ جُلُوْدُهُمْ بَدَّلْنٰهُمْ جُلُوْدًا غَيْرَهَا لِيَذُوْقُوا الْعَذَابَۗ اِنَّ اللّٰهَ كَانَ عَزِيْزًا حَكِيْمًا', 'innallażīna kafarụ bi`āyātinā saufa nuṣlīhim nārā, kullamā naḍijat julụduhum baddalnāhum julụdan gairahā liyażụqul-\'ażāb, innallāha kāna \'azīzan ḥakīmā'),
(549, 4, 57, 'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ سَنُدْخِلُهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًاۗ لَهُمْ فِيْهَآ اَزْوَاجٌ مُّطَهَّرَةٌ ۙ وَّنُدْخِلُهُمْ ظِلًّا ظَلِيْلًا', 'wallażīna āmanụ wa \'amiluṣ-ṣāliḥāti sanudkhiluhum jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā abadā, lahum fīhā azwājum muṭahharatuw wa nudkhiluhum ẓillan ẓalīlā'),
(550, 4, 58, '۞ اِنَّ اللّٰهَ يَأْمُرُكُمْ اَنْ تُؤَدُّوا الْاَمٰنٰتِ اِلٰٓى اَهْلِهَاۙ وَاِذَا حَكَمْتُمْ بَيْنَ النَّاسِ اَنْ تَحْكُمُوْا بِالْعَدْلِ ۗ اِنَّ اللّٰهَ نِعِمَّا يَعِظُكُمْ بِهٖ ۗ اِنَّ اللّٰهَ كَانَ سَمِيْعًاۢ بَصِيْرًا', 'innallāha ya`murukum an tu`addul-amānāti ilā ahlihā wa iżā ḥakamtum bainan-nāsi an taḥkumụ bil-\'adl, innallāha ni\'immā ya\'iẓukum bih, innallāha kāna samī\'am baṣīrā'),
(551, 4, 59, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَطِيْعُوا اللّٰهَ وَاَطِيْعُوا الرَّسُوْلَ وَاُولِى الْاَمْرِ مِنْكُمْۚ فَاِنْ تَنَازَعْتُمْ فِيْ شَيْءٍ فَرُدُّوْهُ اِلَى اللّٰهِ وَالرَّسُوْلِ اِنْ كُنْتُمْ تُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۗ ذٰلِكَ خَيْرٌ وَّاَحْسَنُ تَأْوِيْلًا', 'yā ayyuhallażīna āmanū aṭī\'ullāha wa aṭī\'ur-rasụla wa ulil-amri mingkum, fa in tanāza\'tum fī syai`in fa ruddụhu ilallāhi war-rasụli ing kuntum tu`minụna billāhi wal-yaumil-ākhir, żālika khairuw wa aḥsanu ta`wīlā'),
(552, 4, 60, 'اَلَمْ تَرَ اِلَى الَّذِيْنَ يَزْعُمُوْنَ اَنَّهُمْ اٰمَنُوْا بِمَآ اُنْزِلَ اِلَيْكَ وَمَآ اُنْزِلَ مِنْ قَبْلِكَ يُرِيْدُوْنَ اَنْ يَّتَحَاكَمُوْٓا اِلَى الطَّاغُوْتِ وَقَدْ اُمِرُوْٓا اَنْ يَّكْفُرُوْا بِهٖ ۗوَيُرِيْدُ الشَّيْطٰنُ اَنْ يُّضِلَّهُمْ ضَلٰلًا ۢ بَعِيْدًا', 'a lam tara ilallażīna yaz\'umụna annahum āmanụ bimā unzila ilaika wa mā unzila ming qablika yurīdụna ay yataḥākamū ilaṭ-ṭāgụti wa qad umirū ay yakfurụ bih, wa yurīdusy-syaiṭānu ay yuḍillahum ḍalālam ba\'īdā'),
(553, 4, 61, 'وَاِذَا قِيْلَ لَهُمْ تَعَالَوْا اِلٰى مَآ اَنْزَلَ اللّٰهُ وَاِلَى الرَّسُوْلِ رَاَيْتَ الْمُنٰفِقِيْنَ يَصُدُّوْنَ عَنْكَ صُدُوْدًاۚ', 'wa iżā qīla lahum ta\'ālau ilā mā anzalallāhu wa ilar-rasụli ra`aital-munāfiqīna yaṣuddụna \'angka ṣudụdā'),
(554, 4, 62, 'فَكَيْفَ اِذَآ اَصَابَتْهُمْ مُّصِيْبَةٌ ۢبِمَا قَدَّمَتْ اَيْدِيْهِمْ ثُمَّ جَاۤءُوْكَ يَحْلِفُوْنَ بِاللّٰهِ ۖاِنْ اَرَدْنَآ اِلَّآ اِحْسَانًا وَّتَوْفِيْقًا', 'fa kaifa iżā aṣābat-hum muṣībatum bimā qaddamat aidīhim ṡumma jā`ụka yaḥlifụna billāhi in aradnā illā iḥsānaw wa taufīqā'),
(555, 4, 63, 'اُولٰۤىِٕكَ الَّذِيْنَ يَعْلَمُ اللّٰهُ مَا فِيْ قُلُوْبِهِمْ فَاَعْرِضْ عَنْهُمْ وَعِظْهُمْ وَقُلْ لَّهُمْ فِيْٓ اَنْفُسِهِمْ قَوْلًا ۢ بَلِيْغًا', 'ulā`ikallażīna ya\'lamullāhu mā fī qulụbihim fa a\'riḍ \'an-hum wa\'iẓ-hum wa qul lahum fī anfusihim qaulam balīgā'),
(556, 4, 64, 'وَمَآ اَرْسَلْنَا مِنْ رَّسُوْلٍ اِلَّا لِيُطَاعَ بِاِذْنِ اللّٰهِ ۗوَلَوْ اَنَّهُمْ اِذْ ظَّلَمُوْٓا اَنْفُسَهُمْ جَاۤءُوْكَ فَاسْتَغْفَرُوا اللّٰهَ وَاسْتَغْفَرَ لَهُمُ الرَّسُوْلُ لَوَجَدُوا اللّٰهَ تَوَّابًا رَّحِيْمًا', 'wa mā arsalnā mir rasụlin illā liyuṭā\'a bi`iżnillāh, walau annahum iż ẓalamū anfusahum jā`ụka fastagfarullāha wastagfara lahumur-rasụlu lawajadullāha tawwābar raḥīmā'),
(557, 4, 65, 'فَلَا وَرَبِّكَ لَا يُؤْمِنُوْنَ حَتّٰى يُحَكِّمُوْكَ فِيْمَا شَجَرَ بَيْنَهُمْ ثُمَّ لَا يَجِدُوْا فِيْٓ اَنْفُسِهِمْ حَرَجًا مِّمَّا قَضَيْتَ وَيُسَلِّمُوْا تَسْلِيْمًا', 'fa lā wa rabbika lā yu`minụna ḥattā yuḥakkimụka fīmā syajara bainahum ṡumma lā yajidụ fī anfusihim ḥarajam mimmā qaḍaita wa yusallimụ taslīmā');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(558, 4, 66, 'وَلَوْ اَنَّا كَتَبْنَا عَلَيْهِمْ اَنِ اقْتُلُوْٓا اَنْفُسَكُمْ اَوِ اخْرُجُوْا مِنْ دِيَارِكُمْ مَّا فَعَلُوْهُ اِلَّا قَلِيْلٌ مِّنْهُمْ ۗوَلَوْ اَنَّهُمْ فَعَلُوْا مَا يُوْعَظُوْنَ بِهٖ لَكَانَ خَيْرًا لَّهُمْ وَاَشَدَّ تَثْبِيْتًاۙ', 'walau annā katabnā \'alaihim aniqtulū anfusakum awikhrujụ min diyārikum mā fa\'alụhu illā qalīlum min-hum, walau annahum fa\'alụ mā yụ\'aẓụna bihī lakāna khairal lahum wa asyadda taṡbītā'),
(559, 4, 67, 'وَّاِذًا لَّاٰ تَيْنٰهُمْ مِّنْ لَّدُنَّآ اَجْرًا عَظِيْمًاۙ', 'wa iżal la`ātaināhum mil ladunnā ajran \'aẓīmā'),
(560, 4, 68, 'وَّلَهَدَيْنٰهُمْ صِرَاطًا مُّسْتَقِيْمًا', 'wa lahadaināhum ṣirāṭam mustaqīmā'),
(561, 4, 69, 'وَمَنْ يُّطِعِ اللّٰهَ وَالرَّسُوْلَ فَاُولٰۤىِٕكَ مَعَ الَّذِيْنَ اَنْعَمَ اللّٰهُ عَلَيْهِمْ مِّنَ النَّبِيّٖنَ وَالصِّدِّيْقِيْنَ وَالشُّهَدَاۤءِ وَالصّٰلِحِيْنَ ۚ وَحَسُنَ اُولٰۤىِٕكَ رَفِيْقًا', 'wa may yuṭi\'illāha war-rasụla fa ulā`ika ma\'allażīna an\'amallāhu \'alaihim minan-nabiyyīna waṣ-ṣiddīqīna wasy-syuhadā`i waṣ-ṣāliḥīn, wa ḥasuna ulā`ika rafīqā'),
(562, 4, 70, 'ذٰلِكَ الْفَضْلُ مِنَ اللّٰهِ ۗوَكَفٰى بِاللّٰهِ عَلِيْمًا', 'żālikal-faḍlu minallāh, wa kafā billāhi \'alīmā'),
(563, 4, 71, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا خُذُوْا حِذْرَكُمْ فَانْفِرُوْا ثُبَاتٍ اَوِ انْفِرُوْا جَمِيْعًا', 'yā ayyuhallażīna āmanụ khużụ ḥiżrakum fanfirụ ṡubātin awinfirụ jamī\'ā'),
(564, 4, 72, 'وَاِنَّ مِنْكُمْ لَمَنْ لَّيُبَطِّئَنَّۚ فَاِنْ اَصَابَتْكُمْ مُّصِيْبَةٌ قَالَ قَدْ اَنْعَمَ اللّٰهُ عَلَيَّ اِذْ لَمْ اَكُنْ مَّعَهُمْ شَهِيْدًا', 'wa inna mingkum lamal layubaṭṭi`ann, fa in aṣābatkum muṣībatung qāla qad an\'amallāhu \'alayya iż lam akum ma\'ahum syahīdā'),
(565, 4, 73, 'وَلَىِٕنْ اَصَابَكُمْ فَضْلٌ مِّنَ اللّٰهِ لَيَقُوْلَنَّ كَاَنْ لَّمْ تَكُنْۢ بَيْنَكُمْ وَبَيْنَهٗ مَوَدَّةٌ يّٰلَيْتَنِيْ كُنْتُ مَعَهُمْ فَاَفُوْزَ فَوْزًا عَظِيْمًا', 'wa la`in aṣābakum faḍlum minallāhi layaqụlanna ka`al lam takum bainakum wa bainahụ mawaddatuy yā laitanī kuntu ma\'ahum fa afụza fauzan \'aẓīmā'),
(566, 4, 74, '۞ فَلْيُقَاتِلْ فِيْ سَبِيْلِ اللّٰهِ الَّذِيْنَ يَشْرُوْنَ الْحَيٰوةَ الدُّنْيَا بِالْاٰخِرَةِ ۗ وَمَنْ يُّقَاتِلْ فِيْ سَبِيْلِ اللّٰهِ فَيُقْتَلْ اَوْ يَغْلِبْ فَسَوْفَ نُؤْتِيْهِ اَجْرًا عَظِيْمًا', 'falyuqātil fī sabīlillāhillażīna yasyrụnal-ḥayātad-dun-yā bil-ākhirah, wa may yuqātil fī sabīlillāhi fa yuqtal au yaglib fa saufa nu`tīhi ajran \'aẓīmā'),
(567, 4, 75, 'وَمَا لَكُمْ لَا تُقَاتِلُوْنَ فِيْ سَبِيْلِ اللّٰهِ وَالْمُسْتَضْعَفِيْنَ مِنَ الرِّجَالِ وَالنِّسَاۤءِ وَالْوِلْدَانِ الَّذِيْنَ يَقُوْلُوْنَ رَبَّنَآ اَخْرِجْنَا مِنْ هٰذِهِ الْقَرْيَةِ الظَّالِمِ اَهْلُهَاۚ وَاجْعَلْ لَّنَا مِنْ لَّدُنْكَ وَلِيًّاۚ وَاجْعَلْ لَّنَا مِنْ لَّدُنْكَ نَصِيْرًا', 'wa mā lakum lā tuqātilụna fī sabīlillāhi wal-mustaḍ\'afīna minar-rijāli wan-nisā`i wal-wildānillażīna yaqụlụna rabbanā akhrijnā min hāżihil-qaryatiẓ-ẓālimi ahluhā, waj\'al lanā mil ladungka waliyyā, waj\'al lanā mil ladungka naṣīrā'),
(568, 4, 76, 'اَلَّذِيْنَ اٰمَنُوْا يُقَاتِلُوْنَ فِيْ سَبِيْلِ اللّٰهِ ۚ وَالَّذِيْنَ كَفَرُوْا يُقَاتِلُوْنَ فِيْ سَبِيْلِ الطَّاغُوْتِ فَقَاتِلُوْٓا اَوْلِيَاۤءَ الشَّيْطٰنِ ۚ اِنَّ كَيْدَ الشَّيْطٰنِ كَانَ ضَعِيْفًا ۚ', 'allażīna āmanụ yuqātilụna fī sabīlillāh, wallażīna kafarụ yuqātilụna fī sabīliṭ-ṭāgụti fa qātilū auliyā`asy-syaiṭān, inna kaidasy-syaiṭāni kāna ḍa\'īfā'),
(569, 4, 77, 'اَلَمْ تَرَ اِلَى الَّذِيْنَ قِيْلَ لَهُمْ كُفُّوْٓا اَيْدِيَكُمْ وَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَۚ فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتَالُ اِذَا فَرِيْقٌ مِّنْهُمْ يَخْشَوْنَ النَّاسَ كَخَشْيَةِ اللّٰهِ اَوْ اَشَدَّ خَشْيَةً ۚ وَقَالُوْا رَبَّنَا لِمَ كَتَبْتَ عَلَيْنَا الْقِتَالَۚ لَوْلَآ اَخَّرْتَنَآ اِلٰٓى اَجَلٍ قَرِيْبٍۗ قُلْ مَتَاعُ الدُّنْيَا قَلِيْلٌۚ وَالْاٰخِرَةُ خَيْرٌ لِّمَنِ اتَّقٰىۗ وَلَا تُظْلَمُوْنَ فَتِيْلًا', 'a lam tara ilallażīna qīla lahum kuffū aidiyakum wa aqīmuṣ-ṣalāta wa ātuz-zakāh, fa lammā kutiba \'alaihimul-qitālu iżā farīqum min-hum yakhsyaunan-nāsa kakhasy-yatillāhi au asyadda khasy-yah, wa qālụ rabbanā lima katabta \'alainal-qitāl, lau lā akhkhartanā ilā ajaling qarīb, qul matā\'ud-dun-yā qalīl, wal-ākhiratu khairul limanittaqā, wa lā tuẓlamụna fatīlā'),
(570, 4, 78, 'اَيْنَمَا تَكُوْنُوْا يُدْرِكْكُّمُ الْمَوْتُ وَلَوْ كُنْتُمْ فِيْ بُرُوْجٍ مُّشَيَّدَةٍ ۗ وَاِنْ تُصِبْهُمْ حَسَنَةٌ يَّقُوْلُوْا هٰذِهٖ مِنْ عِنْدِ اللّٰهِ ۚ وَاِنْ تُصِبْهُمْ سَيِّئَةٌ يَّقُوْلُوْا هٰذِهٖ مِنْ عِنْدِكَ ۗ قُلْ كُلٌّ مِّنْ عِنْدِ اللّٰهِ ۗ فَمَالِ هٰٓؤُلَاۤءِ الْقَوْمِ لَا يَكَادُوْنَ يَفْقَهُوْنَ حَدِيْثًا', 'aina mā takụnụ yudrikkumul-mautu walau kuntum fī burụjim musyayyadah, wa in tuṣib-hum ḥasanatuy yaqụlụ hāżihī min \'indillāh, wa in tuṣib-hum sayyi`atuy yaqụlụ hāżihī min \'indik, qul kullum min \'indillāh, fa māli hā`ulā`il-qaumi lā yakādụna yafqahụna ḥadīṡā'),
(571, 4, 79, 'مَآ اَصَابَكَ مِنْ حَسَنَةٍ فَمِنَ اللّٰهِ ۖ وَمَآ اَصَابَكَ مِنْ سَيِّئَةٍ فَمِنْ نَّفْسِكَ ۗ وَاَرْسَلْنٰكَ لِلنَّاسِ رَسُوْلًا ۗ وَكَفٰى بِاللّٰهِ شَهِيْدًا', 'mā aṣābaka min ḥasanatin fa minallāhi wa mā aṣābaka min sayyi`atin fa min nafsik, wa arsalnāka lin-nāsi rasụlā, wa kafā billāhi syahīdā'),
(572, 4, 80, 'مَنْ يُّطِعِ الرَّسُوْلَ فَقَدْ اَطَاعَ اللّٰهَ ۚ وَمَنْ تَوَلّٰى فَمَآ اَرْسَلْنٰكَ عَلَيْهِمْ حَفِيْظًا ۗ', 'may yuṭi\'ir-rasụla fa qad aṭā\'allāh, wa man tawallā fa mā arsalnāka \'alaihim ḥafīẓā'),
(573, 4, 81, 'وَيَقُوْلُوْنَ طَاعَةٌ ۖ فَاِذَا بَرَزُوْا مِنْ عِنْدِكَ بَيَّتَ طَاۤىِٕفَةٌ مِّنْهُمْ غَيْرَ الَّذِيْ تَقُوْلُ ۗ وَاللّٰهُ يَكْتُبُ مَا يُبَيِّتُوْنَ ۚ فَاَعْرِضْ عَنْهُمْ وَتَوَكَّلْ عَلَى اللّٰهِ ۗ وَكَفٰى بِاللّٰهِ وَكِيْلًا', 'wa yaqụlụna ṭā\'atun fa iżā barazụ min \'indika bayyata ṭā`ifatum min-hum gairallażī taqụl, wallāhu yaktubu mā yubayyitụn, fa a\'riḍ \'an-hum wa tawakkal \'alallāh, wa kafā billāhi wakīlā'),
(574, 4, 82, 'اَفَلَا يَتَدَبَّرُوْنَ الْقُرْاٰنَ ۗ وَلَوْ كَانَ مِنْ عِنْدِ غَيْرِ اللّٰهِ لَوَجَدُوْا فِيْهِ اخْتِلَافًا كَثِيْرًا', 'a fa lā yatadabbarụnal-qur`ān, walau kāna min \'indi gairillāhi lawajadụ fīhikhtilāfang kaṡīrā'),
(575, 4, 83, 'وَاِذَا جَاۤءَهُمْ اَمْرٌ مِّنَ الْاَمْنِ اَوِ الْخَوْفِ اَذَاعُوْا بِهٖ ۗ وَلَوْ رَدُّوْهُ اِلَى الرَّسُوْلِ وَاِلٰٓى اُولِى الْاَمْرِ مِنْهُمْ لَعَلِمَهُ الَّذِيْنَ يَسْتَنْۢبِطُوْنَهٗ مِنْهُمْ ۗ وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ لَاتَّبَعْتُمُ الشَّيْطٰنَ اِلَّا قَلِيْلًا', 'wa iżā jā`ahum amrum minal-amni awil-khaufi ażā\'ụ bih, walau raddụhu ilar-rasụli wa ilā ulil-amri min-hum la\'alimahullażīna yastambiṭụnahụ min-hum, walau lā faḍlullāhi \'alaikum wa raḥmatuhụ lattaba\'tumusy-syaiṭāna illā qalīlā'),
(576, 4, 84, 'فَقَاتِلْ فِيْ سَبِيْلِ اللّٰهِ ۚ لَا تُكَلَّفُ اِلَّا نَفْسَكَ وَحَرِّضِ الْمُؤْمِنِيْنَ ۚ عَسَى اللّٰهُ اَنْ يَّكُفَّ بَأْسَ الَّذِيْنَ كَفَرُوْا ۗوَاللّٰهُ اَشَدُّ بَأْسًا وَّاَشَدُّ تَنْكِيْلًا', 'fa qātil fī sabīlillāh, lā tukallafu illā nafsaka wa ḥarriḍil-mu`minīn, \'asallāhu ay yakuffa ba`sallażīna kafarụ, wallāhu asyaddu ba`saw wa asyaddu tangkīlā'),
(577, 4, 85, 'مَنْ يَّشْفَعْ شَفَاعَةً حَسَنَةً يَّكُنْ لَّهٗ نَصِيْبٌ مِّنْهَا ۚ وَمَنْ يَّشْفَعْ شَفَاعَةً سَيِّئَةً يَّكُنْ لَّهٗ كِفْلٌ مِّنْهَا ۗ وَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ مُّقِيْتًا', 'may yasyfa\' syafā\'atan ḥasanatay yakul lahụ naṣībum min-hā, wa may yasyfa\' syafā\'atan sayyi`atay yakul lahụ kiflum min-hā, wa kānallāhu \'alā kulli syai`im muqītā'),
(578, 4, 86, 'وَاِذَا حُيِّيْتُمْ بِتَحِيَّةٍ فَحَيُّوْا بِاَحْسَنَ مِنْهَآ اَوْ رُدُّوْهَا ۗ اِنَّ اللّٰهَ كَانَ عَلٰى كُلِّ شَيْءٍ حَسِيْبًا', 'wa iżā ḥuyyītum bitaḥiyyatin fa ḥayyụ bi`aḥsana min-hā au ruddụhā, innallāha kāna \'alā kulli syai`in ḥasībā'),
(579, 4, 87, 'اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَۗ لَيَجْمَعَنَّكُمْ اِلٰى يَوْمِ الْقِيٰمَةِ لَا رَيْبَ فِيْهِ ۗ وَمَنْ اَصْدَقُ مِنَ اللّٰهِ حَدِيْثًا', 'allāhu lā ilāha illā huw, layajma\'annakum ilā yaumil-qiyāmati lā raiba fīh, wa man aṣdaqu minallāhi ḥadīṡā'),
(580, 4, 88, '۞ فَمَا لَكُمْ فِى الْمُنٰفِقِيْنَ فِئَتَيْنِ وَاللّٰهُ اَرْكَسَهُمْ بِمَا كَسَبُوْا ۗ اَتُرِيْدُوْنَ اَنْ تَهْدُوْا مَنْ اَضَلَّ اللّٰهُ ۗوَمَنْ يُّضْلِلِ اللّٰهُ فَلَنْ تَجِدَ لَهٗ سَبِيْلًا', 'fa mā lakum fil-munāfiqīna fi`ataini wallāhu arkasahum bimā kasabụ, a turīdụna an tahdụ man aḍallallāh, wa may yuḍlilillāhu fa lan tajida lahụ sabīlā'),
(581, 4, 89, 'وَدُّوْا لَوْ تَكْفُرُوْنَ كَمَا كَفَرُوْا فَتَكُوْنُوْنَ سَوَاۤءً فَلَا تَتَّخِذُوْا مِنْهُمْ اَوْلِيَاۤءَ حَتّٰى يُهَاجِرُوْا فِيْ سَبِيْلِ اللّٰهِ ۗ فَاِنْ تَوَلَّوْا فَخُذُوْهُمْ وَاقْتُلُوْهُمْ حَيْثُ وَجَدْتُّمُوْهُمْ ۖ وَلَا تَتَّخِذُوْا مِنْهُمْ وَلِيًّا وَّلَا نَصِيْرًاۙ', 'waddụ lau takfurụna kamā kafarụ fa takụnụna sawā`an fa lā tattakhiżụ min-hum auliyā`a ḥattā yuhājirụ fī sabīlillāh, fa in tawallau fa khużụhum waqtulụhum ḥaiṡu wajattumụhum wa lā tattakhiżụ min-hum waliyyaw wa lā naṣīrā'),
(582, 4, 90, 'اِلَّا الَّذِيْنَ يَصِلُوْنَ اِلٰى قَوْمٍۢ بَيْنَكُمْ وَبَيْنَهُمْ مِّيْثَاقٌ اَوْ جَاۤءُوْكُمْ حَصِرَتْ صُدُوْرُهُمْ اَنْ يُّقَاتِلُوْكُمْ اَوْ يُقَاتِلُوْا قَوْمَهُمْ ۗ وَلَوْ شَاۤءَ اللّٰهُ لَسَلَّطَهُمْ عَلَيْكُمْ فَلَقَاتَلُوْكُمْ ۚ فَاِنِ اعْتَزَلُوْكُمْ فَلَمْ يُقَاتِلُوْكُمْ وَاَلْقَوْا اِلَيْكُمُ السَّلَمَ ۙ فَمَا جَعَلَ اللّٰهُ لَكُمْ عَلَيْهِمْ سَبِيْلًا', 'illallażīna yaṣilụna ilā qaumim bainakum wa bainahum mīṡāqun au jā`ụkum ḥaṣirat ṣudụruhum ay yuqātilụkum au yuqātilụ qaumahum, walau syā`allāhu lasallaṭahum \'alaikum fa laqātalụkum, fa ini\'tazalụkum fa lam yuqātilụkum wa alqau ilaikumus-salama fa mā ja\'alallāhu lakum \'alaihim sabīlā'),
(583, 4, 91, 'سَتَجِدُوْنَ اٰخَرِيْنَ يُرِيْدُوْنَ اَنْ يَّأْمَنُوْكُمْ وَيَأْمَنُوْا قَوْمَهُمْ ۗ كُلَّ مَا رُدُّوْٓا اِلَى الْفِتْنَةِ اُرْكِسُوْا فِيْهَا ۚ فَاِنْ لَّمْ يَعْتَزِلُوْكُمْ وَيُلْقُوْٓا اِلَيْكُمُ السَّلَمَ وَيَكُفُّوْٓا اَيْدِيَهُمْ فَخُذُوْهُمْ وَاقْتُلُوْهُمْ حَيْثُ ثَقِفْتُمُوْهُمْ ۗ وَاُولٰۤىِٕكُمْ جَعَلْنَا لَكُمْ عَلَيْهِمْ سُلْطٰنًا مُّبِيْنًا', 'satajidụna ākharīna yurīdụna ay ya`manụkum wa ya`manụ qaumahum, kulla mā ruddū ilal-fitnati urkisụ fīhā, fa il lam ya\'tazilụkum wa yulqū ilaikumus-salama wa yakuffū aidiyahum fa khużụhum waqtulụhum ḥaiṡu ṡaqiftumụhum, wa ulā`ikum ja\'alnā lakum \'alaihim sulṭānam mubīnā'),
(584, 4, 92, 'وَمَا كَانَ لِمُؤْمِنٍ اَنْ يَّقْتُلَ مُؤْمِنًا اِلَّا خَطَـًٔا ۚ وَمَنْ قَتَلَ مُؤْمِنًا خَطَـًٔا فَتَحْرِيْرُ رَقَبَةٍ مُّؤْمِنَةٍ وَّدِيَةٌ مُّسَلَّمَةٌ اِلٰٓى اَهْلِهٖٓ اِلَّآ اَنْ يَّصَّدَّقُوْا ۗ فَاِنْ كَانَ مِنْ قَوْمٍ عَدُوٍّ لَّكُمْ وَهُوَ مُؤْمِنٌ فَتَحْرِيْرُ رَقَبَةٍ مُّؤْمِنَةٍ ۗوَاِنْ كَانَ مِنْ قَوْمٍۢ بَيْنَكُمْ وَبَيْنَهُمْ مِّيْثَاقٌ فَدِيَةٌ مُّسَلَّمَةٌ اِلٰٓى اَهْلِهٖ وَتَحْرِيْرُ رَقَبَةٍ مُّؤْمِنَةٍ ۚ فَمَنْ لَّمْ يَجِدْ فَصِيَامُ شَهْرَيْنِ مُتَتَابِعَيْنِۖ تَوْبَةً مِّنَ اللّٰهِ ۗوَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًا', 'wa mā kāna limu`minin ay yaqtula mu`minan illā khaṭa`ā, wa mang qatala mu`minan khaṭa`an fa taḥrīru raqabatim mu`minatiw wa diyatum musallamatun ilā ahlihī illā ay yaṣṣaddaqụ, fa ing kāna ming qaumin \'aduwwil lakum wa huwa mu`minun fa taḥrīru raqabatim mu`minah, wa ing kāna ming qaumim bainakum wa bainahum mīṡāqun fa diyatum musallamatun ilā ahlihī wa taḥrīru raqabatim mu`minah, fa mal lam yajid fa ṣiyāmu syahraini mutatābi\'aini taubatam minallāh, wa kānallāhu \'alīman ḥakīmā'),
(585, 4, 93, 'وَمَنْ يَّقْتُلْ مُؤْمِنًا مُّتَعَمِّدًا فَجَزَاۤؤُهٗ جَهَنَّمُ خَالِدًا فِيْهَا وَغَضِبَ اللّٰهُ عَلَيْهِ وَلَعَنَهٗ وَاَعَدَّ لَهٗ عَذَابًا عَظِيْمًا', 'wa may yaqtul mu`minam muta\'ammidan fa jazā`uhụ jahannamu khālidan fīhā wa gaḍiballāhu \'alaihi wa la\'anahụ wa a\'adda lahụ \'ażāban \'aẓīmā'),
(586, 4, 94, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا ضَرَبْتُمْ فِيْ سَبِيْلِ اللّٰهِ فَتَبَيَّنُوْا وَلَا تَقُوْلُوْا لِمَنْ اَلْقٰىٓ اِلَيْكُمُ السَّلٰمَ لَسْتَ مُؤْمِنًاۚ تَبْتَغُوْنَ عَرَضَ الْحَيٰوةِ الدُّنْيَا ۖفَعِنْدَ اللّٰهِ مَغَانِمُ كَثِيْرَةٌ ۗ كَذٰلِكَ كُنْتُمْ مِّنْ قَبْلُ فَمَنَّ اللّٰهُ عَلَيْكُمْ فَتَبَيَّنُوْاۗ اِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُوْنَ خَبِيْرًا', 'yā ayyuhallażīna āmanū iżā ḍarabtum fī sabīlillāhi fa tabayyanụ wa lā taqụlụ liman alqā ilaikumus-salāma lasta mu`minā, tabtagụna \'araḍal-ḥayātid-dun-yā fa \'indallāhi magānimu kaṡīrah, każālika kuntum ming qablu fa mannallāhu \'alaikum fa tabayyanụ, innallāha kāna bimā ta\'malụna khabīrā'),
(587, 4, 95, 'لَا يَسْتَوِى الْقَاعِدُوْنَ مِنَ الْمُؤْمِنِيْنَ غَيْرُ اُولِى الضَّرَرِ وَالْمُجَاهِدُوْنَ فِيْ سَبِيْلِ اللّٰهِ بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۗ فَضَّلَ اللّٰهُ الْمُجٰهِدِيْنَ بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ عَلَى الْقٰعِدِيْنَ دَرَجَةً ۗ وَكُلًّا وَّعَدَ اللّٰهُ الْحُسْنٰىۗ وَفَضَّلَ اللّٰهُ الْمُجٰهِدِيْنَ عَلَى الْقٰعِدِيْنَ اَجْرًا عَظِيْمًاۙ', 'lā yastawil-qā\'idụna minal-mu`minīna gairu uliḍ-ḍarari wal-mujāhidụna fī sabīlillāhi bi`amwālihim wa anfusihim, faḍḍalallāhul-mujāhidīna bi`amwālihim wa anfusihim \'alal-qā\'idīna darajah, wa kullaw wa\'adallāhul-ḥusnā, wa faḍḍalallāhul-mujāhidīna \'alal-qā\'idīna ajran \'aẓīmā'),
(588, 4, 96, 'دَرَجٰتٍ مِّنْهُ وَمَغْفِرَةً وَّرَحْمَةً ۗوَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا', 'darajātim min-hu wa magfirataw wa raḥmah, wa kānallāhu gafụrar raḥīmā'),
(589, 4, 97, 'اِنَّ الَّذِيْنَ تَوَفّٰىهُمُ الْمَلٰۤىِٕكَةُ ظَالِمِيْٓ اَنْفُسِهِمْ قَالُوْا فِيْمَ كُنْتُمْ ۗ قَالُوْا كُنَّا مُسْتَضْعَفِيْنَ فِى الْاَرْضِۗ قَالُوْٓا اَلَمْ تَكُنْ اَرْضُ اللّٰهِ وَاسِعَةً فَتُهَاجِرُوْا فِيْهَا ۗ فَاُولٰۤىِٕكَ مَأْوٰىهُمْ جَهَنَّمُ ۗ وَسَاۤءَتْ مَصِيْرًاۙ', 'innallażīna tawaffāhumul-malā`ikatu ẓālimī anfusihim qālụ fīma kuntum, qālụ kunnā mustaḍ\'afīna fil-arḍ, qālū a lam takun arḍullāhi wāsi\'atan fa tuhājirụ fīhā, fa ulā`ika ma`wāhum jahannam, wa sā`at maṣīrā'),
(590, 4, 98, 'اِلَّا الْمُسْتَضْعَفِيْنَ مِنَ الرِّجَالِ وَالنِّسَاۤءِ وَالْوِلْدَانِ لَا يَسْتَطِيْعُوْنَ حِيْلَةً وَّلَا يَهْتَدُوْنَ سَبِيْلًاۙ', 'illal-mustaḍ\'afīna minar-rijāli wan-nisā`i wal-wildāni lā yastaṭī\'ụna ḥīlataw wa lā yahtadụna sabīlā'),
(591, 4, 99, 'فَاُولٰۤىِٕكَ عَسَى اللّٰهُ اَنْ يَّعْفُوَ عَنْهُمْ ۗ وَكَانَ اللّٰهُ عَفُوًّا غَفُوْرًا', 'fa ulā`ika \'asallāhu ay ya\'fuwa \'an-hum, wa kānallāhu \'afuwwan gafụrā'),
(592, 4, 100, '۞ وَمَنْ يُّهَاجِرْ فِيْ سَبِيْلِ اللّٰهِ يَجِدْ فِى الْاَرْضِ مُرَاغَمًا كَثِيْرًا وَّسَعَةً ۗوَمَنْ يَّخْرُجْ مِنْۢ بَيْتِهٖ مُهَاجِرًا اِلَى اللّٰهِ وَرَسُوْلِهٖ ثُمَّ يُدْرِكْهُ الْمَوْتُ فَقَدْ وَقَعَ اَجْرُهٗ عَلَى اللّٰهِ ۗوَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا', 'wa may yuhājir fī sabīlillāhi yajid fil-arḍi murāgamang kaṡīraw wa sa\'ah, wa may yakhruj mim baitihī muhājiran ilallāhi wa rasụlihī ṡumma yudrik-hul-mautu fa qad waqa\'a ajruhụ \'alallāh, wa kānallāhu gafụrar raḥīmā'),
(593, 4, 101, 'وَاِذَا ضَرَبْتُمْ فِى الْاَرْضِ فَلَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَقْصُرُوْا مِنَ الصَّلٰوةِ ۖ اِنْ خِفْتُمْ اَنْ يَّفْتِنَكُمُ الَّذِيْنَ كَفَرُوْاۗ اِنَّ الْكٰفِرِيْنَ كَانُوْا لَكُمْ عَدُوًّا مُّبِيْنًا', 'wa iżā ḍarabtum fil-arḍi fa laisa \'alaikum junāḥun an taqṣurụ minaṣ-ṣalāti in khiftum ay yaftinakumullażīna kafarụ, innal-kāfirīna kānụ lakum \'aduwwam mubīnā'),
(594, 4, 102, 'وَاِذَا كُنْتَ فِيْهِمْ فَاَقَمْتَ لَهُمُ الصَّلٰوةَ فَلْتَقُمْ طَاۤىِٕفَةٌ مِّنْهُمْ مَّعَكَ وَلْيَأْخُذُوْٓا اَسْلِحَتَهُمْ ۗ فَاِذَا سَجَدُوْا فَلْيَكُوْنُوْا مِنْ وَّرَاۤىِٕكُمْۖ وَلْتَأْتِ طَاۤىِٕفَةٌ اُخْرٰى لَمْ يُصَلُّوْا فَلْيُصَلُّوْا مَعَكَ وَلْيَأْخُذُوْا حِذْرَهُمْ وَاَسْلِحَتَهُمْ ۗ وَدَّ الَّذِيْنَ كَفَرُوْا لَوْ تَغْفُلُوْنَ عَنْ اَسْلِحَتِكُمْ وَاَمْتِعَتِكُمْ فَيَمِيْلُوْنَ عَلَيْكُمْ مَّيْلَةً وَّاحِدَةً ۗوَلَا جُنَاحَ عَلَيْكُمْ اِنْ كَانَ بِكُمْ اَذًى مِّنْ مَّطَرٍ اَوْ كُنْتُمْ مَّرْضٰٓى اَنْ تَضَعُوْٓا اَسْلِحَتَكُمْ وَخُذُوْا حِذْرَكُمْ ۗ اِنَّ اللّٰهَ اَعَدَّ لِلْكٰفِرِيْنَ عَذَابًا مُّهِيْنًا', 'wa iżā kunta fīhim fa aqamta lahumuṣ-ṣalāta faltaqum ṭā`ifatum min-hum ma\'aka walya`khużū asliḥatahum, fa iżā sajadụ falyakụnụ miw warā`ikum walta`ti ṭā`ifatun ukhrā lam yuṣallụ falyuṣallụ ma\'aka walya`khużụ ḥiżrahum wa asliḥatahum, waddallażīna kafarụ lau tagfulụna \'an asliḥatikum wa amti\'atikum fa yamīlụna \'alaikum mailataw wāḥidah, wa lā junāḥa \'alaikum ing kāna bikum ażam mim maṭarin au kuntum marḍā an taḍa\'ū asliḥatakum, wa khużụ ḥiżrakum, innallāha a\'adda lil-kāfirīna \'ażābam muhīnā'),
(595, 4, 103, 'فَاِذَا قَضَيْتُمُ الصَّلٰوةَ فَاذْكُرُوا اللّٰهَ قِيَامًا وَّقُعُوْدًا وَّعَلٰى جُنُوْبِكُمْ ۚ فَاِذَا اطْمَأْنَنْتُمْ فَاَقِيْمُوا الصَّلٰوةَ ۚ اِنَّ الصَّلٰوةَ كَانَتْ عَلَى الْمُؤْمِنِيْنَ كِتٰبًا مَّوْقُوْتًا', 'fa iżā qaḍaitumuṣ-ṣalāta fażkurullāha qiyāmaw wa qu\'ụdaw wa \'alā junụbikum, fa iżaṭma`nantum fa aqīmuṣ-ṣalāh, innaṣ-ṣalāta kānat \'alal-mu`minīna kitābam mauqụtā'),
(596, 4, 104, 'وَلَا تَهِنُوْا فِى ابْتِغَاۤءِ الْقَوْمِ ۗ اِنْ تَكُوْنُوْا تَأْلَمُوْنَ فَاِنَّهُمْ يَأْلَمُوْنَ كَمَا تَأْلَمُوْنَ ۚوَتَرْجُوْنَ مِنَ اللّٰهِ مَا لَا يَرْجُوْنَ ۗوَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًا', 'wa lā tahinụ fibtigā`il-qaụm, in takụnụ ta`lamụna fa innahum ya`lamụna kamā ta`lamụn, wa tarjụna minallāhi mā lā yarjụn, wa kānallāhu \'alīman ḥakīmā'),
(597, 4, 105, 'اِنَّآ اَنْزَلْنَآ اِلَيْكَ الْكِتٰبَ بِالْحَقِّ لِتَحْكُمَ بَيْنَ النَّاسِ بِمَآ اَرٰىكَ اللّٰهُ ۗوَلَا تَكُنْ لِّلْخَاۤىِٕنِيْنَ خَصِيْمًا ۙ', 'innā anzalnā ilaikal-kitāba bil-ḥaqqi litaḥkuma bainan-nāsi bimā arākallāh, wa lā takul lil-khā`inīna khaṣīmā'),
(598, 4, 106, 'وَّاسْتَغْفِرِ اللّٰهَ ۗاِنَّ اللّٰهَ كَانَ غَفُوْرًا رَّحِيْمًاۚ', 'wastagfirillāh, innallāha kāna gafụrar raḥīmā'),
(599, 4, 107, 'وَلَا تُجَادِلْ عَنِ الَّذِيْنَ يَخْتَانُوْنَ اَنْفُسَهُمْ ۗ اِنَّ اللّٰهَ لَا يُحِبُّ مَنْ كَانَ خَوَّانًا اَثِيْمًاۙ', 'wa lā tujādil \'anillażīna yakhtānụna anfusahum, innallāha lā yuḥibbu mang kāna khawwānan aṡīmā'),
(600, 4, 108, 'يَّسْتَخْفُوْنَ مِنَ النَّاسِ وَلَا يَسْتَخْفُوْنَ مِنَ اللّٰهِ وَهُوَ مَعَهُمْ اِذْ يُبَيِّتُوْنَ مَا لَا يَرْضٰى مِنَ الْقَوْلِ ۗ وَكَانَ اللّٰهُ بِمَا يَعْمَلُوْنَ مُحِيْطًا', 'yastakhfụna minan-nāsi wa lā yastakhfụna minallāhi wa huwa ma\'ahum iż yubayyitụna mā lā yarḍā minal-qaụl, wa kānallāhu bimā ya\'malụna muḥīṭā'),
(601, 4, 109, 'هٰٓاَنْتُمْ هٰٓؤُلَاۤءِ جَادَلْتُمْ عَنْهُمْ فِى الْحَيٰوةِ الدُّنْيَاۗ فَمَنْ يُّجَادِلُ اللّٰهَ عَنْهُمْ يَوْمَ الْقِيٰمَةِ اَمْ مَّنْ يَّكُوْنُ عَلَيْهِمْ وَكِيْلًا', 'hā`antum hā`ulā`i jādaltum \'an-hum fil-ḥayātid-dun-yā, fa may yujādilullāha \'an-hum yaumal-qiyāmati am may yakụnu \'alaihim wakīlā'),
(602, 4, 110, 'وَمَنْ يَّعْمَلْ سُوْۤءًا اَوْ يَظْلِمْ نَفْسَهٗ ثُمَّ يَسْتَغْفِرِ اللّٰهَ يَجِدِ اللّٰهَ غَفُوْرًا رَّحِيْمًا', 'wa may ya\'mal sū`an au yaẓlim nafsahụ ṡumma yastagfirillāha yajidillāha gafụrar raḥīmā'),
(603, 4, 111, 'وَمَنْ يَّكْسِبْ اِثْمًا فَاِنَّمَا يَكْسِبُهٗ عَلٰى نَفْسِهٖ ۗ وَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًا', 'wa may yaksib iṡman fa innamā yaksibuhụ \'alā nafsih, wa kānallāhu \'alīman ḥakīmā'),
(604, 4, 112, 'وَمَنْ يَّكْسِبْ خَطِيْۤـَٔةً اَوْ اِثْمًا ثُمَّ يَرْمِ بِهٖ بَرِيْۤـًٔا فَقَدِ احْتَمَلَ بُهْتَانًا وَّاِثْمًا مُّبِيْنًا', 'wa may yaksib khaṭī`atan au iṡman ṡumma yarmi bihī barī`an fa qadiḥtamala buhtānaw wa iṡmam mubīnā'),
(605, 4, 113, 'وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكَ وَرَحْمَتُهٗ لَهَمَّتْ طَّاۤىِٕفَةٌ مِّنْهُمْ اَنْ يُّضِلُّوْكَۗ وَمَا يُضِلُّوْنَ اِلَّآ اَنْفُسَهُمْ وَمَا يَضُرُّوْنَكَ مِنْ شَيْءٍ ۗ وَاَنْزَلَ اللّٰهُ عَلَيْكَ الْكِتٰبَ وَالْحِكْمَةَ وَعَلَّمَكَ مَا لَمْ تَكُنْ تَعْلَمُۗ وَكَانَ فَضْلُ اللّٰهِ عَلَيْكَ عَظِيْمًا', 'walau lā faḍlullāhi \'alaika wa raḥmatuhụ lahammaṭ ṭā`ifatum min-hum ay yuḍillụk, wa mā yuḍillụna illā anfusahum wa mā yaḍurrụnaka min syaī`, wa anzalallāhu \'alaikal-kitāba wal-ḥikmata wa \'allamaka mā lam takun ta\'lam, wa kāna faḍlullāhi \'alaika \'aẓīmā'),
(606, 4, 114, '۞ لَا خَيْرَ فِيْ كَثِيْرٍ مِّنْ نَّجْوٰىهُمْ اِلَّا مَنْ اَمَرَ بِصَدَقَةٍ اَوْ مَعْرُوْفٍ اَوْ اِصْلَاحٍۢ بَيْنَ النَّاسِۗ وَمَنْ يَّفْعَلْ ذٰلِكَ ابْتِغَاۤءَ مَرْضَاتِ اللّٰهِ فَسَوْفَ نُؤْتِيْهِ اَجْرًا عَظِيْمًا', 'lā khaira fī kaṡīrim min najwāhum illā man amara biṣadaqatin au ma\'rụfin au iṣlāḥim bainan-nās, wa may yaf\'al żālikabtigā`a marḍātillāhi fa saufa nu`tīhi ajran \'aẓīmā'),
(607, 4, 115, 'وَمَنْ يُّشَاقِقِ الرَّسُوْلَ مِنْۢ بَعْدِ مَا تَبَيَّنَ لَهُ الْهُدٰى وَيَتَّبِعْ غَيْرَ سَبِيْلِ الْمُؤْمِنِيْنَ نُوَلِّهٖ مَا تَوَلّٰى وَنُصْلِهٖ جَهَنَّمَۗ وَسَاۤءَتْ مَصِيْرًا', 'wa may yusyāqiqir-rasụla mim ba\'di mā tabayyana lahul-hudā wa yattabi\' gaira sabīlil-mu`minīna nuwallihī mā tawallā wa nuṣlihī jahannam, wa sā`at maṣīrā'),
(608, 4, 116, 'اِنَّ اللّٰهَ لَا يَغْفِرُ اَنْ يُّشْرَكَ بِهٖ وَيَغْفِرُ مَا دُوْنَ ذٰلِكَ لِمَنْ يَّشَاۤءُ ۗ وَمَنْ يُّشْرِكْ بِاللّٰهِ فَقَدْ ضَلَّ ضَلٰلًا ۢ بَعِيْدًا', 'innallāha lā yagfiru ay yusyraka bihī wa yagfiru mā dụna żālika limay yasyā`, wa may yusyrik billāhi fa qad ḍalla ḍalālam ba\'īdā'),
(609, 4, 117, 'اِنْ يَّدْعُوْنَ مِنْ دُوْنِهٖٓ اِلَّآ اِنَاثًاۚ وَاِنْ يَّدْعُوْنَ اِلَّا شَيْطٰنًا مَّرِيْدًاۙ', 'iy yad\'ụna min dụnihī illā ināṡā, wa iy yad\'ụna illā syaiṭānam marīdā'),
(610, 4, 118, 'لَّعَنَهُ اللّٰهُ ۘ وَقَالَ لَاَتَّخِذَنَّ مِنْ عِبَادِكَ نَصِيْبًا مَّفْرُوْضًاۙ', 'la\'anahullāh, wa qāla la`attakhiżanna min \'ibādika naṣībam mafrụḍā'),
(611, 4, 119, 'وَّلَاُضِلَّنَّهُمْ وَلَاُمَنِّيَنَّهُمْ وَلَاٰمُرَنَّهُمْ فَلَيُبَتِّكُنَّ اٰذَانَ الْاَنْعَامِ وَلَاٰمُرَنَّهُمْ فَلَيُغَيِّرُنَّ خَلْقَ اللّٰهِ ۚ وَمَنْ يَّتَّخِذِ الشَّيْطٰنَ وَلِيًّا مِّنْ دُوْنِ اللّٰهِ فَقَدْ خَسِرَ خُسْرَانًا مُّبِيْنًا', 'wa la`uḍillannahum wa la`umanniyannahum wa la`āmurannahum fa layubattikunna āżānal-an\'āmi wa la`āmurannahum fa layugayyirunna khalqallāh, wa may yattakhiżisy-syaiṭāna waliyyam min dụnillāhi fa qad khasira khusrānam mubīnā'),
(612, 4, 120, 'يَعِدُهُمْ وَيُمَنِّيْهِمْۗ وَمَا يَعِدُهُمُ الشَّيْطٰنُ اِلَّا غُرُوْرًا', 'ya\'iduhum wa yumannīhim, wa mā ya\'iduhumusy-syaiṭānu illā gurụrā'),
(613, 4, 121, 'اُولٰۤىِٕكَ مَأْوٰىهُمْ جَهَنَّمُۖ وَلَا يَجِدُوْنَ عَنْهَا مَحِيْصًا', 'ulā`ika ma`wāhum jahannamu wa lā yajidụna \'an-hā mahīṣā'),
(614, 4, 122, 'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ سَنُدْخِلُهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًاۗ وَعْدَ اللّٰهِ حَقًّا ۗوَمَنْ اَصْدَقُ مِنَ اللّٰهِ قِيْلًا', 'wallażīna āmanụ wa \'amiluṣ-ṣāliḥāti sanudkhiluhum jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā abadā, wa\'dallāhi ḥaqqā, wa man aṣdaqu minallāhi qīlā'),
(615, 4, 123, 'لَيْسَ بِاَمَانِيِّكُمْ وَلَآ اَمَانِيِّ اَهْلِ الْكِتٰبِ ۗ مَنْ يَّعْمَلْ سُوْۤءًا يُّجْزَ بِهٖۙ وَلَا يَجِدْ لَهٗ مِنْ دُوْنِ اللّٰهِ وَلِيًّا وَّلَا نَصِيْرًا', 'laisa bi`amāniyyikum wa lā amāniyyi ahlil-kitāb, may ya\'mal sū`ay yujza bihī wa lā yajid lahụ min dụnillāhi waliyyaw wa lā naṣīrā'),
(616, 4, 124, 'وَمَنْ يَّعْمَلْ مِنَ الصّٰلِحٰتِ مِنْ ذَكَرٍ اَوْ اُنْثٰى وَهُوَ مُؤْمِنٌ فَاُولٰۤىِٕكَ يَدْخُلُوْنَ الْجَنَّةَ وَلَا يُظْلَمُوْنَ نَقِيْرًا', 'wa may ya\'mal minaṣ-ṣāliḥāti min żakarin au unṡā wa huwa mu`minun fa ulā`ika yadkhulụnal-jannata wa lā yuẓlamụna naqīrā'),
(617, 4, 125, 'وَمَنْ اَحْسَنُ دِيْنًا مِّمَّنْ اَسْلَمَ وَجْهَهٗ لِلّٰهِ وَهُوَ مُحْسِنٌ وَّاتَّبَعَ مِلَّةَ اِبْرٰهِيْمَ حَنِيْفًا ۗوَاتَّخَذَ اللّٰهُ اِبْرٰهِيْمَ خَلِيْلًا', 'wa man aḥsanu dīnam mim man aslama waj-hahụ lillāhi wa huwa muḥsinuw wattaba\'a millata ibrāhīma ḥanīfā, wattakhażallāhu ibrāhīma khalīlā'),
(618, 4, 126, 'وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَكَانَ اللّٰهُ بِكُلِّ شَيْءٍ مُّحِيْطًا', 'wa lillāhi mā fis-samāwāti wa mā fil-arḍ, wa kānallāhu bikulli syai`im muḥīṭā'),
(619, 4, 127, 'وَيَسْتَفْتُوْنَكَ فِى النِّسَاۤءِۗ قُلِ اللّٰهُ يُفْتِيْكُمْ فِيْهِنَّ ۙوَمَا يُتْلٰى عَلَيْكُمْ فِى الْكِتٰبِ فِيْ يَتٰمَى النِّسَاۤءِ الّٰتِيْ لَا تُؤْتُوْنَهُنَّ مَا كُتِبَ لَهُنَّ وَتَرْغَبُوْنَ اَنْ تَنْكِحُوْهُنَّ وَالْمُسْتَضْعَفِيْنَ مِنَ الْوِلْدَانِۙ وَاَنْ تَقُوْمُوْا لِلْيَتٰمٰى بِالْقِسْطِ ۗوَمَا تَفْعَلُوْا مِنْ خَيْرٍ فَاِنَّ اللّٰهَ كَانَ بِهٖ عَلِيْمًا', 'wa yastaftụnaka fin-nisā`, qulillāhu yuftīkum fīhinna wa mā yutlā \'alaikum fil-kitābi fī yatāman-nisā`illātī lā tu`tụnahunna mā kutiba lahunna wa targabụna an tangkiḥụhunna wal-mustaḍ\'afīna minal-wildāni wa an taqụmụ lil-yatāmā bil-qisṭ, wa mā taf\'alụ min khairin fa innallāha kāna bihī \'alīmā'),
(620, 4, 128, 'وَاِنِ امْرَاَةٌ خَافَتْ مِنْۢ بَعْلِهَا نُشُوْزًا اَوْ اِعْرَاضًا فَلَا جُنَاحَ عَلَيْهِمَآ اَنْ يُّصْلِحَا بَيْنَهُمَا صُلْحًا ۗوَالصُّلْحُ خَيْرٌ ۗوَاُحْضِرَتِ الْاَنْفُسُ الشُّحَّۗ وَاِنْ تُحْسِنُوْا وَتَتَّقُوْا فَاِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُوْنَ خَبِيْرًا', 'wa inimra`atun khāfat mim ba\'lihā nusyụzan au i\'rāḍan fa lā junāḥa \'alaihimā ay yuṣliḥā bainahumā ṣul-ḥā, waṣ-ṣul-ḥu khaīr, wa uḥḍiratil-anfususy-syuḥḥ, wa in tuḥsinụ wa tattaqụ fa innallāha kāna bimā ta\'malụna khabīrā'),
(621, 4, 129, 'وَلَنْ تَسْتَطِيْعُوْٓا اَنْ تَعْدِلُوْا بَيْنَ النِّسَاۤءِ وَلَوْ حَرَصْتُمْ فَلَا تَمِيْلُوْا كُلَّ الْمَيْلِ فَتَذَرُوْهَا كَالْمُعَلَّقَةِ ۗوَاِنْ تُصْلِحُوْا وَتَتَّقُوْا فَاِنَّ اللّٰهَ كَانَ غَفُوْرًا رَّحِيْمًا', 'wa lan tastaṭī\'ū an ta\'dilụ bainan-nisā`i walau ḥaraṣtum fa lā tamīlụ kullal-maili fa tażarụhā kal-mu\'allaqah, wa in tuṣliḥụ wa tattaqụ fa innallāha kāna gafụrar raḥīmā'),
(622, 4, 130, 'وَاِنْ يَّتَفَرَّقَا يُغْنِ اللّٰهُ كُلًّا مِّنْ سَعَتِهٖۗ وَكَانَ اللّٰهُ وَاسِعًا حَكِيْمًا', 'wa iy yatafarraqā yugnillāhu kullam min sa\'atih, wa kānallāhu wāsi\'an ḥakīmā'),
(623, 4, 131, 'وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَلَقَدْ وَصَّيْنَا الَّذِيْنَ اُوْتُوا الْكِتٰبَ مِنْ قَبْلِكُمْ وَاِيَّاكُمْ اَنِ اتَّقُوا اللّٰهَ ۗوَاِنْ تَكْفُرُوْا فَاِنَّ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَكَانَ اللّٰهُ غَنِيًّا حَمِيْدًا', 'wa lillāhi mā fis-samāwāti wa mā fil-arḍ, wa laqad waṣṣainallażīna ụtul-kitāba ming qablikum wa iyyākum anittaqullāh, wa in takfurụ fa inna lillāhi mā fis-samāwāti wa mā fil-arḍ, wa kānallāhu ganiyyan ḥamīdā'),
(624, 4, 132, 'وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ ۗوَكَفٰى بِاللّٰهِ وَكِيْلًا', 'wa lillāhi mā fis-samāwāti wa mā fil-arḍ, wa kafā billāhi wakīlā'),
(625, 4, 133, 'اِنْ يَّشَأْ يُذْهِبْكُمْ اَيُّهَا النَّاسُ وَيَأْتِ بِاٰخَرِيْنَۗ وَكَانَ اللّٰهُ عَلٰى ذٰلِكَ قَدِيْرًا', 'iy yasya` yuż-hibkum ayyuhan-nāsu wa ya`ti bi`ākharīn, wa kānallāhu \'alā żālika qadīrā'),
(626, 4, 134, 'مَنْ كَانَ يُرِيْدُ ثَوَابَ الدُّنْيَا فَعِنْدَ اللّٰهِ ثَوَابُ الدُّنْيَا وَالْاٰخِرَةِ ۗوَكَانَ اللّٰهُ سَمِيْعًاۢ بَصِيْرًا', 'mang kāna yurīdu ṡawābad-dun-yā fa \'indallāhi ṡawābud-dun-yā wal-ākhirah, wa kānallāhu samī\'am baṣīrā'),
(627, 4, 135, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُوْنُوْا قَوَّامِيْنَ بِالْقِسْطِ شُهَدَاۤءَ لِلّٰهِ وَلَوْ عَلٰٓى اَنْفُسِكُمْ اَوِ الْوَالِدَيْنِ وَالْاَقْرَبِيْنَ ۚ اِنْ يَّكُنْ غَنِيًّا اَوْ فَقِيْرًا فَاللّٰهُ اَوْلٰى بِهِمَاۗ فَلَا تَتَّبِعُوا الْهَوٰٓى اَنْ تَعْدِلُوْا ۚ وَاِنْ تَلْوٗٓا اَوْ تُعْرِضُوْا فَاِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُوْنَ خَبِيْرًا', 'yā ayyuhallażīna āmanụ kụnụ qawwāmīna bil-qisṭi syuhadā`a lillāhi walau \'alā anfusikum awil-wālidaini wal-aqrabīn, iy yakun ganiyyan au faqīran fallāhu aulā bihimā, fa lā tattabi\'ul-hawā an ta\'dilụ, wa in talwū au tu\'riḍụ fa innallāha kāna bimā ta\'malụna khabīrā'),
(628, 4, 136, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اٰمِنُوْا بِاللّٰهِ وَرَسُوْلِهٖ وَالْكِتٰبِ الَّذِيْ نَزَّلَ عَلٰى رَسُوْلِهٖ وَالْكِتٰبِ الَّذِيْٓ اَنْزَلَ مِنْ قَبْلُ ۗوَمَنْ يَّكْفُرْ بِاللّٰهِ وَمَلٰۤىِٕكَتِهٖ وَكُتُبِهٖ وَرُسُلِهٖ وَالْيَوْمِ الْاٰخِرِ فَقَدْ ضَلَّ ضَلٰلًا ۢ بَعِيْدًا', 'yā ayyuhallażīna āmanū āminụ billāhi wa rasụlihī wal-kitābillażī nazzala \'alā rasụlihī wal-kitābillażī anzala ming qabl, wa may yakfur billāhi wa malā`ikatihī wa kutubihī wa rusulihī wal-yaumil-ākhiri fa qad ḍalla ḍalālam ba\'īdā'),
(629, 4, 137, 'اِنَّ الَّذِيْنَ اٰمَنُوْا ثُمَّ كَفَرُوْا ثُمَّ اٰمَنُوْا ثُمَّ كَفَرُوْا ثُمَّ ازْدَادُوْا كُفْرًا لَّمْ يَكُنِ اللّٰهُ لِيَغْفِرَ لَهُمْ وَلَا لِيَهْدِيَهُمْ سَبِيْلًاۗ', 'innallażīna āmanụ ṡumma kafarụ ṡumma āmanụ ṡumma kafarụ ṡummazdādụ kufral lam yakunillāhu liyagfira lahum wa lā liyahdiyahum sabīlā'),
(630, 4, 138, 'بَشِّرِ الْمُنٰفِقِيْنَ بِاَنَّ لَهُمْ عَذَابًا اَلِيْمًاۙ', 'basysyiril-munāfiqīna bi`anna lahum \'ażāban alīmā'),
(631, 4, 139, ' ۨالَّذِيْنَ يَتَّخِذُوْنَ الْكٰفِرِيْنَ اَوْلِيَاۤءَ مِنْ دُوْنِ الْمُؤْمِنِيْنَ ۗ اَيَبْتَغُوْنَ عِنْدَهُمُ الْعِزَّةَ فَاِنَّ الْعِزَّةَ لِلّٰهِ جَمِيْعًاۗ', 'allażīna yattakhiżụnal-kāfirīna auliyā`a min dụnil-mu`minīn, a yabtagụna \'indahumul-\'izzata fa innal-\'izzata lillāhi jamī\'ā'),
(632, 4, 140, 'وَقَدْ نَزَّلَ عَلَيْكُمْ فِى الْكِتٰبِ اَنْ اِذَا سَمِعْتُمْ اٰيٰتِ اللّٰهِ يُكْفَرُ بِهَا وَيُسْتَهْزَاُ بِهَا فَلَا تَقْعُدُوْا مَعَهُمْ حَتّٰى يَخُوْضُوْا فِيْ حَدِيْثٍ غَيْرِهٖٓ ۖ اِنَّكُمْ اِذًا مِّثْلُهُمْ ۗ اِنَّ اللّٰهَ جَامِعُ الْمُنٰفِقِيْنَ وَالْكٰفِرِيْنَ فِيْ جَهَنَّمَ جَمِيْعًاۙ', 'wa qad nazzala \'alaikum fil-kitābi an iżā sami\'tum āyātillāhi yukfaru bihā wa yustahza`u bihā fa lā taq\'udụ ma\'ahum ḥattā yakhụḍụ fī ḥadīṡin gairihī innakum iżam miṡluhum, innallāha jāmi\'ul-munāfiqīna wal-kāfirīna fī jahannama jamī\'ā'),
(633, 4, 141, ' ۨالَّذِيْنَ يَتَرَبَّصُوْنَ بِكُمْۗ فَاِنْ كَانَ لَكُمْ فَتْحٌ مِّنَ اللّٰهِ قَالُوْٓا اَلَمْ نَكُنْ مَّعَكُمْ ۖ وَاِنْ كَانَ لِلْكٰفِرِيْنَ نَصِيْبٌ قَالُوْٓا اَلَمْ نَسْتَحْوِذْ عَلَيْكُمْ وَنَمْنَعْكُمْ مِّنَ الْمُؤْمِنِيْنَ ۗ فَاللّٰهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيٰمَةِ ۗ وَلَنْ يَّجْعَلَ اللّٰهُ لِلْكٰفِرِيْنَ عَلَى الْمُؤْمِنِيْنَ سَبِيْلًا', 'allażīna yatarabbaṣụna bikum, fa ing kāna lakum fat-ḥum minallāhi qālū a lam nakum ma\'akum wa ing kāna lil-kāfirīna naṣībung qālū a lam nastaḥwiż \'alaikum wa namna\'kum minal-mu`minīn, fallāhu yaḥkumu bainakum yaumal-qiyāmah, wa lay yaj\'alallāhu lil-kāfirīna \'alal-mu`minīna sabīlā'),
(634, 4, 142, 'اِنَّ الْمُنٰفِقِيْنَ يُخٰدِعُوْنَ اللّٰهَ وَهُوَ خَادِعُهُمْۚ وَاِذَا قَامُوْٓا اِلَى الصَّلٰوةِ قَامُوْا كُسَالٰىۙ يُرَاۤءُوْنَ النَّاسَ وَلَا يَذْكُرُوْنَ اللّٰهَ اِلَّا قَلِيْلًاۖ', 'innal-munāfiqīna yukhādi\'ụnallāha wa huwa khādi\'ụhum, wa iżā qāmū ilaṣ-ṣalāti qāmụ kusālā yurā`ụnan-nāsa wa lā yażkurụnallāha illā qalīlā'),
(635, 4, 143, 'مُّذَبْذَبِيْنَ بَيْنَ ذٰلِكَۖ لَآ اِلٰى هٰٓؤُلَاۤءِ وَلَآ اِلٰى هٰٓؤُلَاۤءِ ۗ وَمَنْ يُّضْلِلِ اللّٰهُ فَلَنْ تَجِدَ لَهٗ سَبِيْلًا', 'mużabżabīna baina żālika lā ilā hā`ulā`i wa lā ilā hā`ulā`, wa may yuḍlilillāhu fa lan tajida lahụ sabīlā'),
(636, 4, 144, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوا الْكٰفِرِيْنَ اَوْلِيَاۤءَ مِنْ دُوْنِ الْمُؤْمِنِيْنَ ۚ اَتُرِيْدُوْنَ اَنْ تَجْعَلُوْا لِلّٰهِ عَلَيْكُمْ سُلْطٰنًا مُّبِيْنًا', 'yā ayyuhallażīna āmanụ lā tattakhiżul-kāfirīna auliyā`a min dụnil-mu`minīn, a turīdụna an taj\'alụ lillāhi \'alaikum sulṭānam mubīnā'),
(637, 4, 145, 'اِنَّ الْمُنٰفِقِيْنَ فِى الدَّرْكِ الْاَسْفَلِ مِنَ النَّارِۚ وَلَنْ تَجِدَ لَهُمْ نَصِيْرًاۙ', 'innal-munāfiqīna fid-darkil-asfali minan-nār, wa lan tajida lahum naṣīrā'),
(638, 4, 146, 'اِلَّا الَّذِيْنَ تَابُوْا وَاَصْلَحُوْا وَاعْتَصَمُوْا بِاللّٰهِ وَاَخْلَصُوْا دِيْنَهُمْ لِلّٰهِ فَاُولٰۤىِٕكَ مَعَ الْمُؤْمِنِيْنَۗ وَسَوْفَ يُؤْتِ اللّٰهُ الْمُؤْمِنِيْنَ اَجْرًا عَظِيْمًا', 'illallażīna tābụ wa aṣlaḥụ wa\'taṣamụ billāhi wa akhlaṣụ dīnahum lillāhi fa ulā`ika ma\'al-mu`minīn, wa saufa yu`tillāhul-mu`minīna ajran \'aẓīmā'),
(639, 4, 147, 'مَا يَفْعَلُ اللّٰهُ بِعَذَابِكُمْ اِنْ شَكَرْتُمْ وَاٰمَنْتُمْۗ وَكَانَ اللّٰهُ شَاكِرًا عَلِيْمًا ۔', 'mā yaf\'alullāhu bi\'ażābikum in syakartum wa āmantum, wa kānallāhu syākiran \'alīmā'),
(640, 4, 148, '۞ لَا يُحِبُّ اللّٰهُ الْجَهْرَ بِالسُّوْۤءِ مِنَ الْقَوْلِ اِلَّا مَنْ ظُلِمَ ۗ وَكَانَ اللّٰهُ سَمِيْعًا عَلِيْمًا', 'lā yuḥibbullāhul-jahra bis-sū`i minal-qauli illā man ẓulim, wa kānallāhu samī\'an \'alīmā'),
(641, 4, 149, 'اِنْ تُبْدُوْا خَيْرًا اَوْ تُخْفُوْهُ اَوْ تَعْفُوْا عَنْ سُوْۤءٍ فَاِنَّ اللّٰهَ كَانَ عَفُوًّا قَدِيْرًا', 'in tubdụ khairan au tukhfụhu au ta\'fụ \'an sū`in fa innallāha kāna \'afuwwang qadīrā'),
(642, 4, 150, 'اِنَّ الَّذِيْنَ يَكْفُرُوْنَ بِاللّٰهِ وَرُسُلِهٖ وَيُرِيْدُوْنَ اَنْ يُّفَرِّقُوْا بَيْنَ اللّٰهِ وَرُسُلِهٖ وَيَقُوْلُوْنَ نُؤْمِنُ بِبَعْضٍ وَّنَكْفُرُ بِبَعْضٍۙ وَّيُرِيْدُوْنَ اَنْ يَّتَّخِذُوْا بَيْنَ ذٰلِكَ سَبِيْلًاۙ', 'innallażīna yakfurụna billāhi wa rusulihī wa yurīdụna ay yufarriqụ bainallāhi wa rusulihī wa yaqụlụna nu`minu biba\'ḍiw wa nakfuru biba\'ḍiw wa yurīdụna ay yattakhiżụ baina żālika sabīlā'),
(643, 4, 151, 'اُولٰۤىِٕكَ هُمُ الْكٰفِرُوْنَ حَقًّا ۚوَاَعْتَدْنَا لِلْكٰفِرِيْنَ عَذَابًا مُّهِيْنًا', 'ulā`ika humul-kāfirụna ḥaqqā, wa a\'tadnā lil-kāfirīna \'ażābam muhīnā'),
(644, 4, 152, 'وَالَّذِيْنَ اٰمَنُوْا بِاللّٰهِ وَرُسُلِهٖ وَلَمْ يُفَرِّقُوْا بَيْنَ اَحَدٍ مِّنْهُمْ اُولٰۤىِٕكَ سَوْفَ يُؤْتِيْهِمْ اُجُوْرَهُمْ ۗوَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا', 'wallażīna āmanụ billāhi wa rusulihī wa lam yufarriqụ baina aḥadim min-hum ulā`ika saufa yu`tīhim ujụrahum, wa kānallāhu gafụrar raḥīmā'),
(645, 4, 153, 'يَسْـَٔلُكَ اَهْلُ الْكِتٰبِ اَنْ تُنَزِّلَ عَلَيْهِمْ كِتٰبًا مِّنَ السَّمَاۤءِ فَقَدْ سَاَلُوْا مُوْسٰٓى اَكْبَرَ مِنْ ذٰلِكَ فَقَالُوْٓا اَرِنَا اللّٰهَ جَهْرَةً فَاَخَذَتْهُمُ الصَّاعِقَةُ بِظُلْمِهِمْۚ ثُمَّ اتَّخَذُوا الْعِجْلَ مِنْۢ بَعْدِ مَا جَاۤءَتْهُمُ الْبَيِّنٰتُ فَعَفَوْنَا عَنْ ذٰلِكَ ۚ وَاٰتَيْنَا مُوْسٰى سُلْطٰنًا مُّبِيْنًا', 'yas`aluka ahlul-kitābi an tunazzila \'alaihim kitābam minas-samā`i fa qad sa`alụ mụsā akbara min żālika fa qālū arinallāha jahratan fa akhażat-humuṣ-ṣā\'iqatu biẓulmihim, ṡummattakhażul-\'ijla mim ba\'di mā jā`at-humul-bayyinātu fa \'afaunā \'an żālik, wa ātainā mụsā sulṭānam mubīnā'),
(646, 4, 154, 'وَرَفَعْنَا فَوْقَهُمُ الطُّوْرَ بِمِيْثَاقِهِمْ وَقُلْنَا لَهُمُ ادْخُلُوا الْبَابَ سُجَّدًا وَّقُلْنَا لَهُمْ لَا تَعْدُوْا فِى السَّبْتِ وَاَخَذْنَا مِنْهُمْ مِّيْثَاقًا غَلِيْظًا', 'wa rafa\'nā fauqahumuṭ-ṭụra bimīṡāqihim wa qulnā lahumudkhulul-bāba sujjadaw wa qulnā lahum lā ta\'dụ fis-sabti wa akhażnā min-hum mīṡāqan galīẓā'),
(647, 4, 155, 'فَبِمَا نَقْضِهِمْ مِّيْثَاقَهُمْ وَكُفْرِهِمْ بِاٰيٰتِ اللّٰهِ وَقَتْلِهِمُ الْاَنْۢبِيَاۤءَ بِغَيْرِ حَقٍّ وَّقَوْلِهِمْ قُلُوْبُنَا غُلْفٌ ۗ بَلْ طَبَعَ اللّٰهُ عَلَيْهَا بِكُفْرِهِمْ فَلَا يُؤْمِنُوْنَ اِلَّا قَلِيْلًاۖ', 'fa bimā naqḍihim mīṡāqahum wa kufrihim bi`āyātillāhi wa qatlihimul-ambiyā`a bigairi ḥaqqiw wa qaulihim qulụbunā gulf, bal ṭaba\'allāhu \'alaihā bikufrihim fa lā yu`minụna illā qalīlā'),
(648, 4, 156, 'وَّبِكُفْرِهِمْ وَقَوْلِهِمْ عَلٰى مَرْيَمَ بُهْتَانًا عَظِيْمًاۙ', 'wa bikufrihim wa qaulihim \'alā maryama buhtānan \'aẓīmā'),
(649, 4, 157, 'وَّقَوْلِهِمْ اِنَّا قَتَلْنَا الْمَسِيْحَ عِيْسَى ابْنَ مَرْيَمَ رَسُوْلَ اللّٰهِۚ وَمَا قَتَلُوْهُ وَمَا صَلَبُوْهُ وَلٰكِنْ شُبِّهَ لَهُمْ ۗوَاِنَّ الَّذِيْنَ اخْتَلَفُوْا فِيْهِ لَفِيْ شَكٍّ مِّنْهُ ۗمَا لَهُمْ بِهٖ مِنْ عِلْمٍ اِلَّا اتِّبَاعَ الظَّنِّ وَمَا قَتَلُوْهُ يَقِيْنًاۢ ۙ', 'wa qaulihim innā qatalnal-masīḥa \'īsabna maryama rasụlallāh, wa mā qatalụhu wa mā ṣalabụhu wa lākin syubbiha lahum, wa innallażīnakhtalafụ fīhi lafī syakkim min-h, mā lahum bihī min \'ilmin illattibā\'aẓ-ẓanni wa mā qatalụhu yaqīnā'),
(650, 4, 158, 'بَلْ رَّفَعَهُ اللّٰهُ اِلَيْهِ ۗوَكَانَ اللّٰهُ عَزِيْزًا حَكِيْمًا', 'bal rafa\'ahullāhu ilaīh, wa kānallāhu \'azīzan ḥakīmā'),
(651, 4, 159, 'وَاِنْ مِّنْ اَهْلِ الْكِتٰبِ اِلَّا لَيُؤْمِنَنَّ بِهٖ قَبْلَ مَوْتِهٖ ۚوَيَوْمَ الْقِيٰمَةِ يَكُوْنُ عَلَيْهِمْ شَهِيْدًاۚ', 'wa im min ahlil-kitābi illā layu`minanna bihī qabla mautih, wa yaumal-qiyāmati yakụnu \'alaihim syahīdā'),
(652, 4, 160, 'فَبِظُلْمٍ مِّنَ الَّذِيْنَ هَادُوْا حَرَّمْنَا عَلَيْهِمْ طَيِّبٰتٍ اُحِلَّتْ لَهُمْ وَبِصَدِّهِمْ عَنْ سَبِيْلِ اللّٰهِ كَثِيْرًاۙ', 'fa biẓulmim minallażīna hādụ ḥarramnā \'alaihim ṭayyibātin uḥillat lahum wa biṣaddihim \'an sabīlillāhi kaṡīrā'),
(653, 4, 161, 'وَّاَخْذِهِمُ الرِّبٰوا وَقَدْ نُهُوْا عَنْهُ وَاَكْلِهِمْ اَمْوَالَ النَّاسِ بِالْبَاطِلِ ۗوَاَعْتَدْنَا لِلْكٰفِرِيْنَ مِنْهُمْ عَذَابًا اَلِيْمًا', 'wa akhżihimur-ribā wa qad nuhụ \'an-hu wa aklihim amwālan-nāsi bil-bāṭil, wa a\'tadnā lil-kāfirīna min-hum \'ażāban alīmā'),
(654, 4, 162, 'لٰكِنِ الرَّاسِخُوْنَ فِى الْعِلْمِ مِنْهُمْ وَالْمُؤْمِنُوْنَ يُؤْمِنُوْنَ بِمَآ اُنْزِلَ اِلَيْكَ وَمَآ اُنْزِلَ مِنْ قَبْلِكَ وَالْمُقِيْمِيْنَ الصَّلٰوةَ وَالْمُؤْتُوْنَ الزَّكٰوةَ وَالْمُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۗ اُولٰۤىِٕكَ سَنُؤْتِيْهِمْ اَجْرًا عَظِيْمًا', 'lākinir-rāsikhụna fil-\'ilmi min-hum wal-mu`minụna yu`minụna bimā unzila ilaika wa mā unzila ming qablika wal-muqīmīnaṣ-ṣalāta wal-mu`tụnaz-zakāta wal-mu`minụna billāhi wal-yaumil-ākhir, ulā`ika sanu`tīhim ajran \'aẓīmā'),
(655, 4, 163, '۞ اِنَّآ اَوْحَيْنَآ اِلَيْكَ كَمَآ اَوْحَيْنَآ اِلٰى نُوْحٍ وَّالنَّبِيّٖنَ مِنْۢ بَعْدِهٖۚ وَاَوْحَيْنَآ اِلٰٓى اِبْرٰهِيْمَ وَاِسْمٰعِيْلَ وَاِسْحٰقَ وَيَعْقُوْبَ وَالْاَسْبَاطِ وَعِيْسٰى وَاَيُّوْبَ وَيُوْنُسَ وَهٰرُوْنَ وَسُلَيْمٰنَ ۚوَاٰتَيْنَا دَاوٗدَ زَبُوْرًاۚ', 'innā auḥainā ilaika kamā auḥainā ilā nụḥiw wan-nabiyyīna mim ba\'dih, wa auḥainā ilā ibrāhīma wa ismā\'īla wa is-ḥāqa wa ya\'qụba wal-asbāṭi wa \'īsā wa ayyụba wa yụnusa wa hārụna wa sulaimān, wa ātainā dāwụda zabụrā'),
(656, 4, 164, 'وَرُسُلًا قَدْ قَصَصْنٰهُمْ عَلَيْكَ مِنْ قَبْلُ وَرُسُلًا لَّمْ نَقْصُصْهُمْ عَلَيْكَ ۗوَكَلَّمَ اللّٰهُ مُوْسٰى تَكْلِيْمًاۚ', 'wa rusulang qad qaṣaṣnāhum \'alaika ming qablu wa rusulal lam naqṣuṣ-hum \'alaīk, wa kallamallāhu mụsā taklīmā'),
(657, 4, 165, 'رُسُلًا مُّبَشِّرِيْنَ وَمُنْذِرِيْنَ لِئَلَّا يَكُوْنَ لِلنَّاسِ عَلَى اللّٰهِ حُجَّةٌ ۢ بَعْدَ الرُّسُلِ ۗوَكَانَ اللّٰهُ عَزِيْزًا حَكِيْمًا', 'rusulam mubasysyirīna wa munżirīna li`allā yakụna lin-nāsi \'alallāhi ḥujjatum ba\'dar-rusul, wa kānallāhu \'azīzan ḥakīmā'),
(658, 4, 166, 'لٰكِنِ اللّٰهُ يَشْهَدُ بِمَآ اَنْزَلَ اِلَيْكَ اَنْزَلَهٗ بِعِلْمِهٖ ۚوَالْمَلٰۤىِٕكَةُ يَشْهَدُوْنَ ۗوَكَفٰى بِاللّٰهِ شَهِيْدًاۗ', 'lākinillāhu yasy-hadu bimā anzala ilaika anzalahụ bi\'ilmih, wal-malā`ikatu yasy-hadụn, wa kafā billāhi syahīdā'),
(659, 4, 167, 'اِنَّ الَّذِيْنَ كَفَرُوْا وَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ قَدْ ضَلُّوْا ضَلٰلًا ۢ بَعِيْدًا', 'innallażīna kafarụ wa ṣaddụ \'an sabīlillāhi qad ḍallụ ḍalālam ba\'īdā'),
(660, 4, 168, 'اِنَّ الَّذِيْنَ كَفَرُوْا وَظَلَمُوْا لَمْ يَكُنِ اللّٰهُ لِيَغْفِرَ لَهُمْ وَلَا لِيَهْدِيَهُمْ طَرِيْقًاۙ', 'innallażīna kafarụ wa ẓalamụ lam yakunillāhu liyagfira lahum wa lā liyahdiyahum ṭarīqā'),
(661, 4, 169, 'اِلَّا طَرِيْقَ جَهَنَّمَ خٰلِدِيْنَ فِيْهَآ اَبَدًا ۗوَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرًا', 'illā ṭarīqa jahannama khālidīna fīhā abadā, wa kāna żālika \'alallāhi yasīrā'),
(662, 4, 170, 'يٰٓاَيُّهَا النَّاسُ قَدْ جَاۤءَكُمُ الرَّسُوْلُ بِالْحَقِّ مِنْ رَّبِّكُمْ فَاٰمِنُوْا خَيْرًا لَّكُمْ ۗوَاِنْ تَكْفُرُوْا فَاِنَّ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ وَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًا', 'yā ayyuhan-nāsu qad jā`akumur-rasụlu bil-ḥaqqi mir rabbikum fa āminụ khairal lakum, wa in takfurụ fa inna lillāhi mā fis-samāwāti wal-arḍ, wa kānallāhu \'alīman ḥakīmā'),
(663, 4, 171, 'يٰٓاَهْلَ الْكِتٰبِ لَا تَغْلُوْا فِيْ دِيْنِكُمْ وَلَا تَقُوْلُوْا عَلَى اللّٰهِ اِلَّا الْحَقَّۗ اِنَّمَا الْمَسِيْحُ عِيْسَى ابْنُ مَرْيَمَ رَسُوْلُ اللّٰهِ وَكَلِمَتُهٗ ۚ اَلْقٰهَآ اِلٰى مَرْيَمَ وَرُوْحٌ مِّنْهُ ۖفَاٰمِنُوْا بِاللّٰهِ وَرُسُلِهٖۗ وَلَا تَقُوْلُوْا ثَلٰثَةٌ ۗاِنْتَهُوْا خَيْرًا لَّكُمْ ۗ اِنَّمَا اللّٰهُ اِلٰهٌ وَّاحِدٌ ۗ سُبْحٰنَهٗٓ اَنْ يَّكُوْنَ لَهٗ وَلَدٌ ۘ لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَكَفٰى بِاللّٰهِ وَكِيْلًا', 'yā ahlal-kitābi lā taglụ fī dīnikum wa lā taqụlụ \'alallāhi illal-ḥaqq, innamal-masīḥu \'īsabnu maryama rasụlullāhi wa kalimatuh, alqāhā ilā maryama wa rụḥum min-hu fa āminụ billāhi wa rusulih, wa lā taqụlụ ṡalāṡah, intahụ khairal lakum, innamallāhu ilāhuw wāḥid, sub-ḥānahū ay yakụna lahụ walad, lahụ mā fis-samāwāti wa mā fil-arḍ, wa kafā billāhi wakīlā'),
(664, 4, 172, 'لَنْ يَّسْتَنْكِفَ الْمَسِيْحُ اَنْ يَّكُوْنَ عَبْدًا لِّلّٰهِ وَلَا الْمَلٰۤىِٕكَةُ الْمُقَرَّبُوْنَۗ وَمَنْ يَّسْتَنْكِفْ عَنْ عِبَادَتِهٖ وَيَسْتَكْبِرْ فَسَيَحْشُرُهُمْ اِلَيْهِ جَمِيْعًا', 'lay yastangkifal-masīḥu ay yakụna \'abdal lillāhi wa lal-malā`ikatul-muqarrabụn, wa may yastangkif \'an \'ibādatihī wa yastakbir fa sayaḥsyuruhum ilaihi jamī\'ā'),
(665, 4, 173, 'فَاَمَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَيُوَفِّيْهِمْ اُجُوْرَهُمْ وَيَزِيْدُهُمْ مِّنْ فَضْلِهٖۚ وَاَمَّا الَّذِيْنَ اسْتَنْكَفُوْا وَاسْتَكْبَرُوْا فَيُعَذِّبُهُمْ عَذَابًا اَلِيْمًاۙ وَّلَا يَجِدُوْنَ لَهُمْ مِّنْ دُوْنِ اللّٰهِ وَلِيًّا وَّلَا نَصِيْرًا', 'fa ammallażīna āmanụ wa \'amiluṣ-ṣāliḥāti fa yuwaffīhim ujụrahum wa yazīduhum min faḍlih, wa ammallażīnastangkafụ wastakbarụ fa yu\'ażżibuhum \'ażāban alīmaw wa lā yajidụna lahum min dụnillāhi waliyyaw wa lā naṣīrā'),
(666, 4, 174, 'يٰٓاَيُّهَا النَّاسُ قَدْ جَاۤءَكُمْ بُرْهَانٌ مِّنْ رَّبِّكُمْ وَاَنْزَلْنَآ اِلَيْكُمْ نُوْرًا مُّبِيْنًا', 'yā ayyuhan-nāsu qad jā`akum bur-hānum mir rabbikum wa anzalnā ilaikum nụram mubīnā'),
(667, 4, 175, 'فَاَمَّا الَّذِيْنَ اٰمَنُوْا بِاللّٰهِ وَاعْتَصَمُوْا بِهٖ فَسَيُدْخِلُهُمْ فِيْ رَحْمَةٍ مِّنْهُ وَفَضْلٍۙ وَّيَهْدِيْهِمْ اِلَيْهِ صِرَاطًا مُّسْتَقِيْمًاۗ', 'fa ammallażīna āmanụ billāhi wa\'taṣamụ bihī fa sayudkhiluhum fī raḥmatim min-hu wa faḍliw wa yahdīhim ilaihi ṣirāṭam mustaqīmā'),
(668, 4, 176, 'يَسْتَفْتُوْنَكَۗ قُلِ اللّٰهُ يُفْتِيْكُمْ فِى الْكَلٰلَةِ ۗاِنِ امْرُؤٌا هَلَكَ لَيْسَ لَهٗ وَلَدٌ وَّلَهٗٓ اُخْتٌ فَلَهَا نِصْفُ مَا تَرَكَۚ وَهُوَ يَرِثُهَآ اِنْ لَّمْ يَكُنْ لَّهَا وَلَدٌ ۚ فَاِنْ كَانَتَا اثْنَتَيْنِ فَلَهُمَا الثُّلُثٰنِ مِمَّا تَرَكَ ۗوَاِنْ كَانُوْٓا اِخْوَةً رِّجَالًا وَّنِسَاۤءً فَلِلذَّكَرِ مِثْلُ حَظِّ الْاُنْثَيَيْنِۗ يُبَيِّنُ اللّٰهُ لَكُمْ اَنْ تَضِلُّوْا ۗ وَاللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمٌ', 'yastaftụnak, qulillāhu yuftīkum fil-kalālah, inimru`un halaka laisa lahụ waladuw wa lahū ukhtun fa lahā niṣfu mā tarak, wa huwa yariṡuhā il lam yakul lahā walad, fa ing kānataṡnataini fa lahumaṡ-ṡuluṡāni mimmā tarak, wa ing kānū ikhwatar rijālaw wa nisā`an fa liż-żakari miṡlu ḥaẓẓil-unṡayaīn, yubayyinullāhu lakum an taḍillụ, wallāhu bikulli syai`in \'alīm'),
(669, 5, 1, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَوْفُوْا بِالْعُقُوْدِۗ اُحِلَّتْ لَكُمْ بَهِيْمَةُ الْاَنْعَامِ اِلَّا مَا يُتْلٰى عَلَيْكُمْ غَيْرَ مُحِلِّى الصَّيْدِ وَاَنْتُمْ حُرُمٌۗ اِنَّ اللّٰهَ يَحْكُمُ مَا يُرِيْدُ', 'yā ayyuhallażīna āmanū aufụ bil-\'uqụd, uḥillat lakum bahīmatul-an\'āmi illā mā yutlā \'alaikum gaira muḥilliṣ-ṣaidi wa antum ḥurum, innallāha yaḥkumu mā yurīd'),
(670, 5, 2, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تُحِلُّوْا شَعَاۤىِٕرَ اللّٰهِ وَلَا الشَّهْرَ الْحَرَامَ وَلَا الْهَدْيَ وَلَا الْقَلَاۤىِٕدَ وَلَآ اٰۤمِّيْنَ الْبَيْتَ الْحَرَامَ يَبْتَغُوْنَ فَضْلًا مِّنْ رَّبِّهِمْ وَرِضْوَانًا ۗوَاِذَا حَلَلْتُمْ فَاصْطَادُوْا ۗوَلَا يَجْرِمَنَّكُمْ شَنَاٰنُ قَوْمٍ اَنْ صَدُّوْكُمْ عَنِ الْمَسْجِدِ الْحَرَامِ اَنْ تَعْتَدُوْۘا وَتَعَاوَنُوْا عَلَى الْبِرِّ وَالتَّقْوٰىۖ وَلَا تَعَاوَنُوْا عَلَى الْاِثْمِ وَالْعُدْوَانِ ۖوَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ شَدِيْدُ الْعِقَابِ', 'yā ayyuhallażīna āmanụ lā tuḥillụ sya\'ā`irallāhi wa lasy-syahral-ḥarāma wa lal-hadya wa lal-qalā`ida wa lā āmmīnal-baital-ḥarāma yabtagụna faḍlam mir rabbihim wa riḍwānā, wa iżā ḥalaltum faṣṭādụ, wa lā yajrimannakum syana`ānu qaumin an ṣaddụkum \'anil-masjidil-ḥarāmi an ta\'tadụ, wa ta\'āwanụ \'alal-birri wat-taqwā wa lā ta\'āwanụ \'alal-iṡmi wal-\'udwāni wattaqullāh, innallāha syadīdul-\'iqāb'),
(671, 5, 3, 'حُرِّمَتْ عَلَيْكُمُ الْمَيْتَةُ وَالدَّمُ وَلَحْمُ الْخِنْزِيْرِ وَمَآ اُهِلَّ لِغَيْرِ اللّٰهِ بِهٖ وَالْمُنْخَنِقَةُ وَالْمَوْقُوْذَةُ وَالْمُتَرَدِّيَةُ وَالنَّطِيْحَةُ وَمَآ اَكَلَ السَّبُعُ اِلَّا مَا ذَكَّيْتُمْۗ وَمَا ذُبِحَ عَلَى النُّصُبِ وَاَنْ تَسْتَقْسِمُوْا بِالْاَزْلَامِۗ ذٰلِكُمْ فِسْقٌۗ اَلْيَوْمَ يَىِٕسَ الَّذِيْنَ كَفَرُوْا مِنْ دِيْنِكُمْ فَلَا تَخْشَوْهُمْ وَاخْشَوْنِۗ اَلْيَوْمَ اَكْمَلْتُ لَكُمْ دِيْنَكُمْ وَاَتْمَمْتُ عَلَيْكُمْ نِعْمَتِيْ وَرَضِيْتُ لَكُمُ الْاِسْلَامَ دِيْنًاۗ فَمَنِ اضْطُرَّ فِيْ مَخْمَصَةٍ غَيْرَ مُتَجَانِفٍ لِّاِثْمٍۙ فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'ḥurrimat \'alaikumul-maitatu wad-damu wa laḥmul-khinzīri wa mā uhilla ligairillāhi bihī wal-munkhaniqatu wal-mauqụżatu wal-mutaraddiyatu wan-naṭīḥatu wa mā akalas-sabu\'u illā mā żakkaitum, wa mā żubiḥa \'alan-nuṣubi wa an tastaqsimụ bil-azlām, żālikum fisq, al-yauma ya`isallażīna kafarụ min dīnikum fa lā takhsyauhum wakhsyaụn, al-yauma akmaltu lakum dīnakum wa atmamtu \'alaikum ni\'matī wa raḍītu lakumul-islāma dīnā, fa maniḍṭurra fī makhmaṣatin gaira mutajānifil li`iṡmin fa innallāha gafụrur raḥīm'),
(672, 5, 4, 'يَسْـَٔلُوْنَكَ مَاذَآ اُحِلَّ لَهُمْۗ قُلْ اُحِلَّ لَكُمُ الطَّيِّبٰتُۙ وَمَا عَلَّمْتُمْ مِّنَ الْجَوَارِحِ مُكَلِّبِيْنَ تُعَلِّمُوْنَهُنَّ مِمَّا عَلَّمَكُمُ اللّٰهُ فَكُلُوْا مِمَّآ اَمْسَكْنَ عَلَيْكُمْ وَاذْكُرُوا اسْمَ اللّٰهِ عَلَيْهِ ۖوَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ سَرِيْعُ الْحِسَابِ', 'yas`alụnaka māżā uḥilla lahum, qul uḥilla lakumuṭ-ṭayyibātu wa mā \'allamtum minal-jawāriḥi mukallibīna tu\'allimụnahunna mimmā \'allamakumullāhu fa kulụ mimmā amsakna \'alaikum ważkurusmallāhi \'alaihi wattaqullāh, innallāha sarī\'ul-ḥisāb'),
(673, 5, 5, 'اَلْيَوْمَ اُحِلَّ لَكُمُ الطَّيِّبٰتُۗ وَطَعَامُ الَّذِيْنَ اُوْتُوا الْكِتٰبَ حِلٌّ لَّكُمْ ۖوَطَعَامُكُمْ حِلٌّ لَّهُمْ ۖوَالْمُحْصَنٰتُ مِنَ الْمُؤْمِنٰتِ وَالْمُحْصَنٰتُ مِنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ مِنْ قَبْلِكُمْ اِذَآ اٰتَيْتُمُوْهُنَّ اُجُوْرَهُنَّ مُحْصِنِيْنَ غَيْرَ مُسَافِحِيْنَ وَلَا مُتَّخِذِيْٓ اَخْدَانٍۗ وَمَنْ يَّكْفُرْ بِالْاِيْمَانِ فَقَدْ حَبِطَ عَمَلُهٗ ۖوَهُوَ فِى الْاٰخِرَةِ مِنَ الْخٰسِرِيْنَ', 'al-yauma uḥilla lakumuṭ-ṭayyibāt, wa ṭa\'āmullażīna ụtul-kitāba ḥillul lakum wa ṭa\'āmukum ḥillul lahum wal-muḥṣanātu minal-mu`mināti wal-muḥṣanātu minallażīna ụtul-kitāba ming qablikum iżā ātaitumụhunna ujụrahunna muḥṣinīna gaira musāfiḥīna wa lā muttakhiżī akhdān, wa may yakfur bil-īmāni fa qad ḥabiṭa \'amaluhụ wa huwa fil-ākhirati minal-khāsirīn'),
(674, 5, 6, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا قُمْتُمْ اِلَى الصَّلٰوةِ فَاغْسِلُوْا وُجُوْهَكُمْ وَاَيْدِيَكُمْ اِلَى الْمَرَافِقِ وَامْسَحُوْا بِرُءُوْسِكُمْ وَاَرْجُلَكُمْ اِلَى الْكَعْبَيْنِۗ وَاِنْ كُنْتُمْ جُنُبًا فَاطَّهَّرُوْاۗ وَاِنْ كُنْتُمْ مَّرْضٰٓى اَوْ عَلٰى سَفَرٍ اَوْ جَاۤءَ اَحَدٌ مِّنْكُمْ مِّنَ الْغَاۤىِٕطِ اَوْ لٰمَسْتُمُ النِّسَاۤءَ فَلَمْ تَجِدُوْا مَاۤءً فَتَيَمَّمُوْا صَعِيْدًا طَيِّبًا فَامْسَحُوْا بِوُجُوْهِكُمْ وَاَيْدِيْكُمْ مِّنْهُ ۗمَا يُرِيْدُ اللّٰهُ لِيَجْعَلَ عَلَيْكُمْ مِّنْ حَرَجٍ وَّلٰكِنْ يُّرِيْدُ لِيُطَهِّرَكُمْ وَلِيُتِمَّ نِعْمَتَهٗ عَلَيْكُمْ لَعَلَّكُمْ تَشْكُرُوْنَ', 'yā ayyuhallażīna āmanū iżā qumtum ilaṣ-ṣalāti fagsilụ wujụhakum wa aidiyakum ilal-marāfiqi wamsaḥụ biru`ụsikum wa arjulakum ilal-ka\'baīn, wa ing kuntum junuban faṭṭahharụ, wa ing kuntum marḍā au \'alā safarin au jā`a aḥadum mingkum minal-gā`iṭi au lāmastumun-nisā`a fa lam tajidụ mā`an fa tayammamụ ṣa\'īdan ṭayyiban famsaḥụ biwujụhikum wa aidīkum min-h, mā yurīdullāhu liyaj\'ala \'alaikum min ḥarajiw wa lākiy yurīdu liyuṭahhirakum wa liyutimma ni\'matahụ \'alaikum la\'allakum tasykurụn'),
(675, 5, 7, 'وَاذْكُرُوْا نِعْمَةَ اللّٰهِ عَلَيْكُمْ وَمِيْثَاقَهُ الَّذِيْ وَاثَقَكُمْ بِهٖٓ ۙاِذْ قُلْتُمْ سَمِعْنَا وَاَطَعْنَا ۖوَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ', 'ważkurụ ni\'matallāhi \'alaikum wa mīṡāqahullażī wāṡaqakum bihī iż qultum sami\'nā wa aṭa\'nā wattaqullāh, innallāha \'alīmum biżātiṣ-ṣudụr'),
(676, 5, 8, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُوْنُوْا قَوَّامِيْنَ لِلّٰهِ شُهَدَاۤءَ بِالْقِسْطِۖ وَلَا يَجْرِمَنَّكُمْ شَنَاٰنُ قَوْمٍ عَلٰٓى اَلَّا تَعْدِلُوْا ۗاِعْدِلُوْاۗ هُوَ اَقْرَبُ لِلتَّقْوٰىۖ وَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ خَبِيْرٌۢ بِمَا تَعْمَلُوْنَ', 'yā ayyuhallażīna āmanụ kụnụ qawwāmīna lillāhi syuhadā`a bil-qisṭi wa lā yajrimannakum syana`ānu qaumin \'alā allā ta\'dilụ, i\'dilụ, huwa aqrabu lit-taqwā wattaqullāh, innallāha khabīrum bimā ta\'malụn'),
(677, 5, 9, 'وَعَدَ اللّٰهُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِۙ لَهُمْ مَّغْفِرَةٌ وَّاَجْرٌ عَظِيْمٌ', 'wa\'adallāhullażīna āmanụ wa \'amiluṣ-ṣāliḥāti lahum magfiratuw wa ajrun \'aẓīm'),
(678, 5, 10, 'وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَآ اُولٰۤىِٕكَ اَصْحٰبُ الْجَحِيْمِ', 'wallażīna kafarụ wa każżabụ bi`āyātinā ulā`ika aṣ-ḥābul-jaḥīm'),
(679, 5, 11, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اذْكُرُوْا نِعْمَتَ اللّٰهِ عَلَيْكُمْ اِذْ هَمَّ قَوْمٌ اَنْ يَّبْسُطُوْٓا اِلَيْكُمْ اَيْدِيَهُمْ فَكَفَّ اَيْدِيَهُمْ عَنْكُمْۚ وَاتَّقُوا اللّٰهَ ۗوَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ', 'yā ayyuhallażīna āmanużkurụ ni\'matallāhi \'alaikum iż hamma qaumun ay yabsuṭū ilaikum aidiyahum fa kaffa aidiyahum \'angkum, wattaqullāh, wa \'alallāhi falyatawakkalil-mu`minụn'),
(680, 5, 12, '۞ وَلَقَدْ اَخَذَ اللّٰهُ مِيْثَاقَ بَنِيْٓ اِسْرَاۤءِيْلَۚ وَبَعَثْنَا مِنْهُمُ اثْنَيْ عَشَرَ نَقِيْبًاۗ وَقَالَ اللّٰهُ اِنِّيْ مَعَكُمْ ۗ لَىِٕنْ اَقَمْتُمُ الصَّلٰوةَ وَاٰتَيْتُمُ الزَّكٰوةَ وَاٰمَنْتُمْ بِرُسُلِيْ وَعَزَّرْتُمُوْهُمْ وَاَقْرَضْتُمُ اللّٰهَ قَرْضًا حَسَنًا لَّاُكَفِّرَنَّ عَنْكُمْ سَيِّاٰتِكُمْ وَلَاُدْخِلَنَّكُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۚ فَمَنْ كَفَرَ بَعْدَ ذٰلِكَ مِنْكُمْ فَقَدْ ضَلَّ سَوَاۤءَ السَّبِيْلِ', 'wa laqad akhażallāhu mīṡāqa banī isrā`īl, wa ba\'aṡnā min-humuṡnai \'asyara naqībā, wa qālallāhu innī ma\'akum, la`in aqamtumuṣ-ṣalāta wa ātaitumuz-zakāta wa āmantum birusulī wa \'azzartumụhum wa aqraḍtumullāha qarḍan ḥasanal la`ukaffiranna \'angkum sayyi`ātikum wa la`udkhilannakum jannātin tajrī min taḥtihal-an-hār, fa mang kafara ba\'da żālika mingkum fa qad ḍalla sawā`as-sabīl'),
(681, 5, 13, 'فَبِمَا نَقْضِهِمْ مِّيْثَاقَهُمْ لَعَنّٰهُمْ وَجَعَلْنَا قُلُوْبَهُمْ قٰسِيَةً ۚ يُحَرِّفُوْنَ الْكَلِمَ عَنْ مَّوَاضِعِهٖۙ وَنَسُوْا حَظًّا مِّمَّا ذُكِّرُوْا بِهٖۚ وَلَا تَزَالُ تَطَّلِعُ عَلٰى خَاۤىِٕنَةٍ مِّنْهُمْ اِلَّا قَلِيْلًا مِّنْهُمْ ۖ فَاعْفُ عَنْهُمْ وَاصْفَحْ ۗاِنَّ اللّٰهَ يُحِبُّ الْمُحْسِنِيْنَ', 'fa bimā naqḍihim mīṡāqahum la\'annāhum wa ja\'alnā qulụbahum qāsiyah, yuḥarrifụnal-kalima \'am mawāḍi\'ihī wa nasụ ḥaẓẓam mimmā żukkirụ bih, wa lā tazālu taṭṭali\'u \'alā khā`inatim min-hum illā qalīlam min-hum fa\'fu \'an-hum waṣfaḥ, innallāha yuḥibbul-muḥsinīn'),
(682, 5, 14, 'وَمِنَ الَّذِيْنَ قَالُوْٓا اِنَّا نَصٰرٰٓى اَخَذْنَا مِيْثَاقَهُمْ فَنَسُوْا حَظًّا مِّمَّا ذُكِّرُوْا بِهٖۖ فَاَغْرَيْنَا بَيْنَهُمُ الْعَدَاوَةَ وَالْبَغْضَاۤءَ اِلٰى يَوْمِ الْقِيٰمَةِ ۗ وَسَوْفَ يُنَبِّئُهُمُ اللّٰهُ بِمَا كَانُوْا يَصْنَعُوْنَ', 'wa minallażīna qālū innā naṣārā akhażnā mīṡāqahum fa nasụ haẓẓam mimmā żukkirụ bihī fa agrainā bainahumul-\'adāwata wal-bagḍā`a ilā yaumil-qiyāmah, wa saufa yunabbi`uhumullāhu bimā kānụ yaṣna\'ụn'),
(683, 5, 15, 'يٰٓاَهْلَ الْكِتٰبِ قَدْ جَاۤءَكُمْ رَسُوْلُنَا يُبَيِّنُ لَكُمْ كَثِيْرًا مِّمَّا كُنْتُمْ تُخْفُوْنَ مِنَ الْكِتٰبِ وَيَعْفُوْا عَنْ كَثِيْرٍەۗ قَدْ جَاۤءَكُمْ مِّنَ اللّٰهِ نُوْرٌ وَّكِتٰبٌ مُّبِيْنٌۙ', 'yā ahlal-kitābi qad jā`akum rasụlunā yubayyinu lakum kaṡīram mimmā kuntum tukhfụna minal-kitābi wa ya\'fụ \'ang kaṡīr, qad jā`akum minallāhi nụruw wa kitābum mubīn'),
(684, 5, 16, 'يَّهْدِيْ بِهِ اللّٰهُ مَنِ اتَّبَعَ رِضْوَانَهٗ سُبُلَ السَّلٰمِ وَيُخْرِجُهُمْ مِّنَ الظُّلُمٰتِ اِلَى النُّوْرِ بِاِذْنِهٖ وَيَهْدِيْهِمْ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ', 'yahdī bihillāhu manittaba\'a riḍwānahụ subulas-salāmi wa yukhrijuhum minaẓ-ẓulumāti ilan-nụri bi`iżnihī wa yahdīhim ilā ṣirāṭim mustaqīm');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(685, 5, 17, 'لَقَدْ كَفَرَ الَّذِيْنَ قَالُوْٓا اِنَّ اللّٰهَ هُوَ الْمَسِيْحُ ابْنُ مَرْيَمَۗ قُلْ فَمَنْ يَّمْلِكُ مِنَ اللّٰهِ شَيْـًٔا اِنْ اَرَادَ اَنْ يُّهْلِكَ الْمَسِيْحَ ابْنَ مَرْيَمَ وَاُمَّهٗ وَمَنْ فِى الْاَرْضِ جَمِيْعًا ۗوَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا ۗ يَخْلُقُ مَا يَشَاۤءُ ۗوَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'laqad kafarallażīna qālū innallāha huwal-masīḥubnu maryam, qul fa may yamliku minallāhi syai`an in arāda ay yuhlikal-masīḥabna maryama wa ummahụ wa man fil-arḍi jamī\'ā, wa lillāhi mulkus-samāwāti wal-arḍi wa mā bainahumā, yakhluqu mā yasyā`, wallāhu \'alā kulli syai`ing qadīr'),
(686, 5, 18, 'وَقَالَتِ الْيَهُوْدُ وَالنَّصٰرٰى نَحْنُ اَبْنٰۤؤُ اللّٰهِ وَاَحِبَّاۤؤُهٗ ۗ قُلْ فَلِمَ يُعَذِّبُكُمْ بِذُنُوْبِكُمْ ۗ بَلْ اَنْتُمْ بَشَرٌ مِّمَّنْ خَلَقَۗ يَغْفِرُ لِمَنْ يَّشَاۤءُ وَيُعَذِّبُ مَنْ يَّشَاۤءُۗ وَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا ۖوَاِلَيْهِ الْمَصِيْرُ', 'wa qālatil-yahụdu wan-naṣārā naḥnu abnā`ullāhi wa aḥibbā`uh, qul fa lima yu\'ażżibukum biżunụbikum, bal antum basyarum mim man khalaq, yagfiru limay yasyā`u wa yu\'ażżibu may yasyā`, wa lillāhi mulkus-samāwāti wal-arḍi wa mā bainahumā wa ilaihil-maṣīr'),
(687, 5, 19, 'يٰٓاَهْلَ الْكِتٰبِ قَدْ جَاۤءَكُمْ رَسُوْلُنَا يُبَيِّنُ لَكُمْ عَلٰى فَتْرَةٍ مِّنَ الرُّسُلِ اَنْ تَقُوْلُوْا مَا جَاۤءَنَا مِنْۢ بَشِيْرٍ وَّلَا نَذِيْرٍۗ فَقَدْ جَاۤءَكُمْ بَشِيْرٌ وَّنَذِيْرٌ ۗوَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'yā ahlal-kitābi qad jā`akum rasụlunā yubayyinu lakum \'alā fatratim minar-rusuli an taqụlụ mā jā`anā mim basyīriw wa lā nażīr, fa qad jā`akum basyīruw wa nażīr, wallāhu \'alā kulli syai`ing qadīr'),
(688, 5, 20, 'وَاِذْ قَالَ مُوْسٰى لِقَوْمِهٖ يٰقَوْمِ اذْكُرُوْا نِعْمَةَ اللّٰهِ عَلَيْكُمْ اِذْ جَعَلَ فِيْكُمْ اَنْۢبِيَاۤءَ وَجَعَلَكُمْ مُّلُوْكًاۙ وَّاٰتٰىكُمْ مَّا لَمْ يُؤْتِ اَحَدًا مِّنَ الْعٰلَمِيْنَ', 'wa iż qāla mụsā liqaumihī yā qaumiżkurụ ni\'matallāhi \'alaikum iż ja\'ala fīkum ambiyā`a wa ja\'alakum mulụkaw wa ātākum mā lam yu`ti aḥadam minal-\'ālamīn'),
(689, 5, 21, 'يٰقَوْمِ ادْخُلُوا الْاَرْضَ الْمُقَدَّسَةَ الَّتِيْ كَتَبَ اللّٰهُ لَكُمْ وَلَا تَرْتَدُّوْا عَلٰٓى اَدْبَارِكُمْ فَتَنْقَلِبُوْا خٰسِرِيْنَ', 'yā qaumidkhulul-arḍal-muqaddasatallatī kataballāhu lakum wa lā tartaddụ \'alā adbārikum fa tangqalibụ khāsirīn'),
(690, 5, 22, 'قَالُوْا يٰمُوْسٰٓى اِنَّ فِيْهَا قَوْمًا جَبَّارِيْنَۖ وَاِنَّا لَنْ نَّدْخُلَهَا حَتّٰى يَخْرُجُوْا مِنْهَاۚ فَاِنْ يَّخْرُجُوْا مِنْهَا فَاِنَّا دٰخِلُوْنَ', 'qālụ yā mụsā inna fīhā qauman jabbārīna wa innā lan nadkhulahā ḥattā yakhrujụ min-hā, fa iy yakhrujụ min-hā fa innā dākhilụn'),
(691, 5, 23, 'قَالَ رَجُلَانِ مِنَ الَّذِيْنَ يَخَافُوْنَ اَنْعَمَ اللّٰهُ عَلَيْهِمَا ادْخُلُوْا عَلَيْهِمُ الْبَابَۚ فَاِذَا دَخَلْتُمُوْهُ فَاِنَّكُمْ غٰلِبُوْنَ ەۙ وَعَلَى اللّٰهِ فَتَوَكَّلُوْٓا اِنْ كُنْتُمْ مُّؤْمِنِيْنَ', 'qāla rajulāni minallażīna yakhāfụna an\'amallāhu \'alaihimadkhulụ \'alaihimul-bāb, fa iżā dakhaltumụhu fa innakum gālibụna wa \'alallāhi fa tawakkalū ing kuntum mu`minīn'),
(692, 5, 24, 'قَالُوْا يٰمُوْسٰٓى اِنَّا لَنْ نَّدْخُلَهَآ اَبَدًا مَّا دَامُوْا فِيْهَا ۖفَاذْهَبْ اَنْتَ وَرَبُّكَ فَقَاتِلَآ اِنَّا هٰهُنَا قٰعِدُوْنَ', 'qālụ yā mụsā innā lan nadkhulahā abadam mā dāmụ fīhā faż-hab anta wa rabbuka fa qātilā innā hāhunā qā\'idụn'),
(693, 5, 25, 'قَالَ رَبِّ اِنِّيْ لَآ اَمْلِكُ اِلَّا نَفْسِيْ وَاَخِيْ فَافْرُقْ بَيْنَنَا وَبَيْنَ الْقَوْمِ الْفٰسِقِيْنَ', 'qāla rabbi innī lā amliku illā nafsī wa akhī fafruq bainanā wa bainal-qaumil-fāsiqīn'),
(694, 5, 26, 'قَالَ فَاِنَّهَا مُحَرَّمَةٌ عَلَيْهِمْ اَرْبَعِيْنَ سَنَةً ۚيَتِيْهُوْنَ فِى الْاَرْضِۗ فَلَا تَأْسَ عَلَى الْقَوْمِ الْفٰسِقِيْنَ', 'qāla fa innahā muḥarramatun \'alaihim arba\'īna sanah, yatīhụna fil-arḍ, fa lā ta`sa \'alal-qaumil-fāsiqīn'),
(695, 5, 27, '۞ وَاتْلُ عَلَيْهِمْ نَبَاَ ابْنَيْ اٰدَمَ بِالْحَقِّۘ اِذْ قَرَّبَا قُرْبَانًا فَتُقُبِّلَ مِنْ اَحَدِهِمَا وَلَمْ يُتَقَبَّلْ مِنَ الْاٰخَرِۗ قَالَ لَاَقْتُلَنَّكَ ۗ قَالَ اِنَّمَا يَتَقَبَّلُ اللّٰهُ مِنَ الْمُتَّقِيْنَ', 'watlu \'alaihim naba`abnai ādama bil-ḥaqq, iż qarrabā qurbānan fa tuqubbila min aḥadihimā wa lam yutaqabbal minal-ākhar, qāla la`aqtulannak, qāla innamā yataqabbalullāhu minal-muttaqīn'),
(696, 5, 28, 'لَىِٕنْۢ بَسَطْتَّ اِلَيَّ يَدَكَ لِتَقْتُلَنِيْ مَآ اَنَا۠ بِبَاسِطٍ يَّدِيَ اِلَيْكَ لِاَقْتُلَكَۚ اِنِّيْٓ اَخَافُ اللّٰهَ رَبَّ الْعٰلَمِيْنَ', 'la`im basatta ilayya yadaka litaqtulanī mā ana bibāsiṭiy yadiya ilaika li`aqtulak, innī akhāfullāha rabbal-\'ālamīn'),
(697, 5, 29, 'اِنِّيْٓ اُرِيْدُ اَنْ تَبُوْۤاَ بِاِثْمِيْ وَاِثْمِكَ فَتَكُوْنَ مِنْ اَصْحٰبِ النَّارِۚ وَذٰلِكَ جَزَاۤؤُا الظّٰلِمِيْنَۚ', 'innī urīdu an tabū`a bi`iṡmī wa iṡmika fa takụna min aṣ-ḥābin-nār, wa żālika jazā`uẓ-ẓālimīn'),
(698, 5, 30, 'فَطَوَّعَتْ لَهٗ نَفْسُهٗ قَتْلَ اَخِيْهِ فَقَتَلَهٗ فَاَصْبَحَ مِنَ الْخٰسِرِيْنَ', 'fa ṭawwa\'at lahụ nafsuhụ qatla akhīhi fa qatalahụ fa aṣbaḥa minal-khāsirīn'),
(699, 5, 31, 'فَبَعَثَ اللّٰهُ غُرَابًا يَّبْحَثُ فِى الْاَرْضِ لِيُرِيَهٗ كَيْفَ يُوَارِيْ سَوْءَةَ اَخِيْهِ ۗ قَالَ يٰوَيْلَتٰٓى اَعَجَزْتُ اَنْ اَكُوْنَ مِثْلَ هٰذَا الْغُرَابِ فَاُوَارِيَ سَوْءَةَ اَخِيْۚ فَاَصْبَحَ مِنَ النّٰدِمِيْنَ ۛ', 'fa ba\'aṡallāhu gurābay yab-ḥaṡu fil-arḍi liyuriyahụ kaifa yuwārī sau`ata akhīh, qāla yā wailatā a \'ajaztu an akụna miṡla hāżal-gurābi fa uwāriya sau`ata akhī, fa aṣbaḥa minan-nādimīn'),
(700, 5, 32, 'مِنْ اَجْلِ ذٰلِكَ ۛ كَتَبْنَا عَلٰى بَنِيْٓ اِسْرَاۤءِيْلَ اَنَّهٗ مَنْ قَتَلَ نَفْسًاۢ بِغَيْرِ نَفْسٍ اَوْ فَسَادٍ فِى الْاَرْضِ فَكَاَنَّمَا قَتَلَ النَّاسَ جَمِيْعًاۗ وَمَنْ اَحْيَاهَا فَكَاَنَّمَآ اَحْيَا النَّاسَ جَمِيْعًا ۗوَلَقَدْ جَاۤءَتْهُمْ رُسُلُنَا بِالْبَيِّنٰتِ ثُمَّ اِنَّ كَثِيْرًا مِّنْهُمْ بَعْدَ ذٰلِكَ فِى الْاَرْضِ لَمُسْرِفُوْنَ', 'min ajli żālika katabnā \'alā banī isrā`īla annahụ mang qatala nafsam bigairi nafsin au fasādin fil-arḍi fa ka`annamā qatalan-nāsa jamī\'ā, wa man aḥyāhā fa ka`annamā aḥyan-nāsa jamī\'ā, wa laqad jā`at-hum rusulunā bil-bayyināti ṡumma inna kaṡīram min-hum ba\'da żālika fil-arḍi lamusrifụn'),
(701, 5, 33, 'اِنَّمَا جَزٰۤؤُا الَّذِيْنَ يُحَارِبُوْنَ اللّٰهَ وَرَسُوْلَهٗ وَيَسْعَوْنَ فِى الْاَرْضِ فَسَادًا اَنْ يُّقَتَّلُوْٓا اَوْ يُصَلَّبُوْٓا اَوْ تُقَطَّعَ اَيْدِيْهِمْ وَاَرْجُلُهُمْ مِّنْ خِلَافٍ اَوْ يُنْفَوْا مِنَ الْاَرْضِۗ ذٰلِكَ لَهُمْ خِزْيٌ فِى الدُّنْيَا وَلَهُمْ فِى الْاٰخِرَةِ عَذَابٌ عَظِيْمٌ', 'innamā jazā`ullażīna yuḥāribụnallāha wa rasụlahụ wa yas\'auna fil-arḍi fasādan ay yuqattalū au yuṣallabū au tuqaṭṭa\'a aidīhim wa arjuluhum min khilāfin au yunfau minal-arḍ, żālika lahum khizyun fid-dun-yā wa lahum fil-ākhirati \'ażābun \'aẓīm'),
(702, 5, 34, 'اِلَّا الَّذِيْنَ تَابُوْا مِنْ قَبْلِ اَنْ تَقْدِرُوْا عَلَيْهِمْۚ فَاعْلَمُوْٓا اَنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'illallażīna tābụ ming qabli an taqdirụ \'alaihim, fa\'lamū annallāha gafụrur raḥīm'),
(703, 5, 35, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَابْتَغُوْٓا اِلَيْهِ الْوَسِيْلَةَ وَجَاهِدُوْا فِيْ سَبِيْلِهٖ لَعَلَّكُمْ تُفْلِحُوْنَ', 'yā ayyuhallażīna āmanuttaqullāha wabtagū ilaihil-wasīlata wa jāhidụ fī sabīlihī la\'allakum tufliḥụn'),
(704, 5, 36, 'اِنَّ الَّذِيْنَ كَفَرُوْا لَوْ اَنَّ لَهُمْ مَّا فِى الْاَرْضِ جَمِيْعًا وَّمِثْلَهٗ مَعَهٗ لِيَفْتَدُوْا بِهٖ مِنْ عَذَابِ يَوْمِ الْقِيٰمَةِ مَا تُقُبِّلَ مِنْهُمْ ۚ وَلَهُمْ عَذَابٌ اَلِيْمٌ', 'innallażīna kafarụ lau anna lahum mā fil-arḍi jamī\'aw wa miṡlahụ ma\'ahụ liyaftadụ bihī min \'ażābi yaumil-qiyāmati mā tuqubbila min-hum, wa lahum \'ażābun alīm'),
(705, 5, 37, 'يُرِيْدُوْنَ اَنْ يَّخْرُجُوْا مِنَ النَّارِ وَمَا هُمْ بِخَارِجِيْنَ مِنْهَا ۖوَلَهُمْ عَذَابٌ مُّقِيمٌ', 'yurīdụna ay yakhrujụ minan-nāri wa mā hum bikhārijīna min-hā wa lahum \'ażābum muqīm'),
(706, 5, 38, 'وَالسَّارِقُ وَالسَّارِقَةُ فَاقْطَعُوْٓا اَيْدِيَهُمَا جَزَاۤءًۢ بِمَا كَسَبَا نَكَالًا مِّنَ اللّٰهِ ۗوَاللّٰهُ عَزِيْزٌ حَكِيْمٌ', 'was-sāriqu was-sāriqatu faqṭa\'ū aidiyahumā jazā`am bimā kasabā nakālam minallāh, wallāhu \'azīzun ḥakīm'),
(707, 5, 39, 'فَمَنْ تَابَ مِنْۢ بَعْدِ ظُلْمِهٖ وَاَصْلَحَ فَاِنَّ اللّٰهَ يَتُوْبُ عَلَيْهِ ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'fa man tāba mim ba\'di ẓulmihī wa aṣlaḥa fa innallāha yatụbu \'alaīh, innallāha gafụrur raḥīm'),
(708, 5, 40, 'اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ يُعَذِّبُ مَنْ يَّشَاۤءُ وَيَغْفِرُ لِمَنْ يَّشَاۤءُ ۗوَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'a lam ta\'lam annallāha lahụ mulkus-samāwāti wal-arḍ, yu\'ażżibu may yasyā`u wa yagfiru limay yasyā`, wallāhu \'alā kulli syai`ing qadīr'),
(709, 5, 41, '۞ يٰٓاَيُّهَا الرَّسُوْلُ لَا يَحْزُنْكَ الَّذِيْنَ يُسَارِعُوْنَ فِى الْكُفْرِ مِنَ الَّذِيْنَ قَالُوْٓا اٰمَنَّا بِاَفْوَاهِهِمْ وَلَمْ تُؤْمِنْ قُلُوْبُهُمْ ۛ وَمِنَ الَّذِيْنَ هَادُوْا ۛ سَمّٰعُوْنَ لِلْكَذِبِ سَمّٰعُوْنَ لِقَوْمٍ اٰخَرِيْنَۙ لَمْ يَأْتُوْكَ ۗ يُحَرِّفُوْنَ الْكَلِمَ مِنْۢ بَعْدِ مَوَاضِعِهٖۚ يَقُوْلُوْنَ اِنْ اُوْتِيْتُمْ هٰذَا فَخُذُوْهُ وَاِنْ لَّمْ تُؤْتَوْهُ فَاحْذَرُوْا ۗوَمَنْ يُّرِدِ اللّٰهُ فِتْنَتَهٗ فَلَنْ تَمْلِكَ لَهٗ مِنَ اللّٰهِ شَيْـًٔا ۗ اُولٰۤىِٕكَ الَّذِيْنَ لَمْ يُرِدِ اللّٰهُ اَنْ يُّطَهِّرَ قُلُوْبَهُمْ ۗ لَهُمْ فِى الدُّنْيَا خِزْيٌ ۖوَّلَهُمْ فِى الْاٰخِرَةِ عَذَابٌ عَظِيْمٌ', 'yā ayyuhar-rasụlu lā yaḥzungkallażīna yusāri\'ụna fil-kufri minallażīna qālū āmannā bi`afwāhihim wa lam tu`ming qulụbuhum, wa minallażīna hādụ sammā\'ụna lil-każibi sammā\'ụna liqaumin ākharīna lam ya`tụk, yuḥarrifụnal-kalima mim ba\'di mawāḍi\'ihī, yaqụlụna in ụtītum hāżā fa khużụhu wa il lam tu`tauhu faḥżarụ, wa may yuridillāhu fitnatahụ fa lan tamlika lahụ minallāhi syai`ā, ulā`ikallażīna lam yuridillāhu ay yuṭahhira qulụbahum, lahum fid-dun-yā khizyuw wa lahum fil-ākhirati \'ażābun \'aẓīm'),
(710, 5, 42, 'سَمّٰعُوْنَ لِلْكَذِبِ اَكّٰلُوْنَ لِلسُّحْتِۗ فَاِنْ جَاۤءُوْكَ فَاحْكُمْ بَيْنَهُمْ اَوْ اَعْرِضْ عَنْهُمْ ۚوَاِنْ تُعْرِضْ عَنْهُمْ فَلَنْ يَّضُرُّوْكَ شَيْـًٔا ۗ وَاِنْ حَكَمْتَ فَاحْكُمْ بَيْنَهُمْ بِالْقِسْطِۗ اِنَّ اللّٰهَ يُحِبُّ الْمُقْسِطِيْنَ', 'sammā\'ụna lil-każibi akkālụna lis-suḥt, fa in jā`ụka faḥkum bainahum au a\'riḍ \'an-hum, wa in tu\'riḍ \'an-hum fa lay yaḍurrụka syai`ā, wa in ḥakamta faḥkum bainahum bil-qisṭ, innallāha yuḥibbul-muqsiṭīn'),
(711, 5, 43, 'وَكَيْفَ يُحَكِّمُوْنَكَ وَعِنْدَهُمُ التَّوْرٰىةُ فِيْهَا حُكْمُ اللّٰهِ ثُمَّ يَتَوَلَّوْنَ مِنْۢ بَعْدِ ذٰلِكَ ۗوَمَآ اُولٰۤىِٕكَ بِالْمُؤْمِنِيْنَ', 'wa kaifa yuḥakkimụnaka wa \'indahumut-taurātu fīhā ḥukmullāhi ṡumma yatawallauna mim ba\'di żālik, wa mā ulā`ika bil-mu`minīn'),
(712, 5, 44, 'اِنَّآ اَنْزَلْنَا التَّوْرٰىةَ فِيْهَا هُدًى وَّنُوْرٌۚ يَحْكُمُ بِهَا النَّبِيُّوْنَ الَّذِيْنَ اَسْلَمُوْا لِلَّذِيْنَ هَادُوْا وَالرَّبَّانِيُّوْنَ وَالْاَحْبَارُ بِمَا اسْتُحْفِظُوْا مِنْ كِتٰبِ اللّٰهِ وَكَانُوْا عَلَيْهِ شُهَدَاۤءَۚ فَلَا تَخْشَوُا النَّاسَ وَاخْشَوْنِ وَلَا تَشْتَرُوْا بِاٰيٰتِيْ ثَمَنًا قَلِيْلًا ۗوَمَنْ لَّمْ يَحْكُمْ بِمَآ اَنْزَلَ اللّٰهُ فَاُولٰۤىِٕكَ هُمُ الْكٰفِرُوْنَ', 'innā anzalnat-taurāta fīhā hudaw wa nụr, yaḥkumu bihan-nabiyyụnallażīna aslamụ lillażīna hādụ war-rabbāniyyụna wal-aḥbāru bimastuḥfiẓụ ming kitābillāhi wa kānụ \'alaihi syuhadā`, fa lā takhsyawun-nāsa wakhsyauni wa lā tasytarụ bi`āyātī ṡamanang qalīlā, wa mal lam yaḥkum bimā anzalallāhu fa ulā`ika humul-kāfirụn'),
(713, 5, 45, 'وَكَتَبْنَا عَلَيْهِمْ فِيْهَآ اَنَّ النَّفْسَ بِالنَّفْسِ وَالْعَيْنَ بِالْعَيْنِ وَالْاَنْفَ بِالْاَنْفِ وَالْاُذُنَ بِالْاُذُنِ وَالسِّنَّ بِالسِّنِّۙ وَالْجُرُوْحَ قِصَاصٌۗ فَمَنْ تَصَدَّقَ بِهٖ فَهُوَ كَفَّارَةٌ لَّهٗ ۗوَمَنْ لَّمْ يَحْكُمْ بِمَآ اَنْزَلَ اللّٰهُ فَاُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ', 'wa katabnā \'alaihim fīhā annan-nafsa bin-nafsi wal-\'aina bil-\'aini wal-anfa bil-anfi wal-użuna bil-użuni was-sinna bis-sinni wal-jurụḥa qiṣāṣ, fa man taṣaddaqa bihī fa huwa kaffāratul lah, wa mal lam yaḥkum bimā anzalallāhu fa ulā`ika humuẓ-ẓālimụn'),
(714, 5, 46, 'وَقَفَّيْنَا عَلٰٓى اٰثَارِهِمْ بِعِيْسَى ابْنِ مَرْيَمَ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ مِنَ التَّوْرٰىةِ ۖواٰتَيْنٰهُ الْاِنْجِيْلَ فِيْهِ هُدًى وَّنُوْرٌۙ وَّمُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ مِنَ التَّوْرٰىةِ وَهُدًى وَّمَوْعِظَةً لِّلْمُتَّقِيْنَۗ', 'wa qaffainā \'alā āṡārihim bi\'īsabni maryama muṣaddiqal limā baina yadaihi minat-taurāti wa ātaināhul-injīla fīhi hudaw wa nụruw wa muṣaddiqal limā baina yadaihi minat-taurāti wa hudaw wa mau\'iẓatal lil-muttaqīn'),
(715, 5, 47, 'وَلْيَحْكُمْ اَهْلُ الْاِنْجِيْلِ بِمَآ اَنْزَلَ اللّٰهُ فِيْهِۗ وَمَنْ لَّمْ يَحْكُمْ بِمَآ اَنْزَلَ اللّٰهُ فَاُولٰۤىِٕكَ هُمُ الْفٰسِقُوْنَ', 'walyaḥkum ahlul-injīli bimā anzalallāhu fīh, wa mal lam yaḥkum bimā anzalallāhu fa ulā`ika humul-fāsiqụn'),
(716, 5, 48, 'وَاَنْزَلْنَآ اِلَيْكَ الْكِتٰبَ بِالْحَقِّ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ مِنَ الْكِتٰبِ وَمُهَيْمِنًا عَلَيْهِ فَاحْكُمْ بَيْنَهُمْ بِمَآ اَنْزَلَ اللّٰهُ وَلَا تَتَّبِعْ اَهْوَاۤءَهُمْ عَمَّا جَاۤءَكَ مِنَ الْحَقِّۗ لِكُلٍّ جَعَلْنَا مِنْكُمْ شِرْعَةً وَّمِنْهَاجًا ۗوَلَوْ شَاۤءَ اللّٰهُ لَجَعَلَكُمْ اُمَّةً وَّاحِدَةً وَّلٰكِنْ لِّيَبْلُوَكُمْ فِيْ مَآ اٰتٰىكُمْ فَاسْتَبِقُوا الْخَيْرٰتِۗ اِلَى اللّٰهِ مَرْجِعُكُمْ جَمِيْعًا فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ فِيْهِ تَخْتَلِفُوْنَۙ', 'wa anzalnā ilaikal-kitāba bil-ḥaqqi muṣaddiqal limā baina yadaihi minal-kitābi wa muhaiminan \'alaihi faḥkum bainahum bimā anzalallāhu wa lā tattabi\' ahwā`ahum \'ammā jā`aka minal-ḥaqq, likullin ja\'alnā mingkum syir\'ataw wa min-hājā, walau syā`allāhu laja\'alakum ummataw wāḥidataw wa lākil liyabluwakum fī mā ātākum fastabiqul-khairāt, ilallāhi marji\'ukum jamī\'an fa yunabbi`ukum bimā kuntum fīhi takhtalifụn'),
(717, 5, 49, 'وَاَنِ احْكُمْ بَيْنَهُمْ بِمَآ اَنْزَلَ اللّٰهُ وَلَا تَتَّبِعْ اَهْوَاۤءَهُمْ وَاحْذَرْهُمْ اَنْ يَّفْتِنُوْكَ عَنْۢ بَعْضِ مَآ اَنْزَلَ اللّٰهُ اِلَيْكَۗ فَاِنْ تَوَلَّوْا فَاعْلَمْ اَنَّمَا يُرِيْدُ اللّٰهُ اَنْ يُّصِيْبَهُمْ بِبَعْضِ ذُنُوْبِهِمْ ۗوَاِنَّ كَثِيْرًا مِّنَ النَّاسِ لَفٰسِقُوْنَ', 'wa aniḥkum bainahum bimā anzalallāhu wa lā tattabi\' ahwā`ahum waḥżar-hum ay yaftinụka \'am ba\'ḍi mā anzalallāhu ilaīk, fa in tawallau fa\'lam annamā yurīdullāhu ay yuṣībahum biba\'ḍi żunụbihim, wa inna kaṡīram minan-nāsi lafāsiqụn'),
(718, 5, 50, 'اَفَحُكْمَ الْجَاهِلِيَّةِ يَبْغُوْنَۗ وَمَنْ اَحْسَنُ مِنَ اللّٰهِ حُكْمًا لِّقَوْمٍ يُّوْقِنُوْنَ', 'a fa ḥukmal-jāhiliyyati yabgụn, wa man aḥsanu minallāhi ḥukmal liqaumiy yụqinụn'),
(719, 5, 51, '۞ يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوا الْيَهُوْدَ وَالنَّصٰرٰٓى اَوْلِيَاۤءَ ۘ بَعْضُهُمْ اَوْلِيَاۤءُ بَعْضٍۗ وَمَنْ يَّتَوَلَّهُمْ مِّنْكُمْ فَاِنَّهٗ مِنْهُمْ ۗ اِنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ', 'yā ayyuhallażīna āmanụ lā tattakhiżul-yahụda wan-naṣārā auliyā`, ba\'ḍuhum auliyā`u ba\'ḍ, wa may yatawallahum mingkum fa innahụ min-hum, innallāha lā yahdil-qaumaẓ-ẓālimīn'),
(720, 5, 52, 'فَتَرَى الَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ يُّسَارِعُوْنَ فِيْهِمْ يَقُوْلُوْنَ نَخْشٰٓى اَنْ تُصِيْبَنَا دَاۤىِٕرَةٌ ۗفَعَسَى اللّٰهُ اَنْ يَّأْتِيَ بِالْفَتْحِ اَوْ اَمْرٍ مِّنْ عِنْدِهٖ فَيُصْبِحُوْا عَلٰى مَآ اَسَرُّوْا فِيْٓ اَنْفُسِهِمْ نٰدِمِيْنَۗ', 'fa tarallażīna fī qulụbihim maraḍuy yusāri\'ụna fīhim yaqụlụna nakhsyā an tuṣībanā dā`irah, fa \'asallāhu ay ya`tiya bil-fat-ḥi au amrim min \'indihī fa yuṣbiḥụ \'alā mā asarrụ fī anfusihim nādimīn'),
(721, 5, 53, 'وَيَقُوْلُ الَّذِيْنَ اٰمَنُوْٓا اَهٰٓؤُلَاۤءِ الَّذِيْنَ اَقْسَمُوْا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْۙ اِنَّهُمْ لَمَعَكُمْۗ حَبِطَتْ اَعْمَالُهُمْ فَاَصْبَحُوْا خٰسِرِيْنَ', 'wa yaqụlullażīna āmanū a hā`ulā`illażīna aqsamụ billāhi jahda aimānihim innahum lama\'akum, ḥabiṭat a\'māluhum fa aṣbaḥụ khāsirīn'),
(722, 5, 54, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا مَنْ يَّرْتَدَّ مِنْكُمْ عَنْ دِيْنِهٖ فَسَوْفَ يَأْتِى اللّٰهُ بِقَوْمٍ يُّحِبُّهُمْ وَيُحِبُّوْنَهٗٓ ۙاَذِلَّةٍ عَلَى الْمُؤْمِنِيْنَ اَعِزَّةٍ عَلَى الْكٰفِرِيْنَۖ يُجَاهِدُوْنَ فِيْ سَبِيْلِ اللّٰهِ وَلَا يَخَافُوْنَ لَوْمَةَ لَاۤىِٕمٍ ۗذٰلِكَ فَضْلُ اللّٰهِ يُؤْتِيْهِ مَنْ يَّشَاۤءُۗ وَاللّٰهُ وَاسِعٌ عَلِيْمٌ', 'yā ayyuhallażīna āmanụ may yartadda mingkum \'an dīnihī fa saufa ya`tillāhu biqaumiy yuḥibbuhum wa yuḥibbụnahū ażillatin \'alal-mu`minīna a\'izzatin \'alal-kāfirīna yujāhidụna fī sabīlillāhi wa lā yakhāfụna laumata lā`im, żālika faḍlullāhi yu`tīhi may yasyā`, wallāhu wāsi\'un \'alīm'),
(723, 5, 55, 'اِنَّمَا وَلِيُّكُمُ اللّٰهُ وَرَسُوْلُهٗ وَالَّذِيْنَ اٰمَنُوا الَّذِيْنَ يُقِيْمُوْنَ الصَّلٰوةَ وَيُؤْتُوْنَ الزَّكٰوةَ وَهُمْ رَاكِعُوْنَ', 'innamā waliyyukumullāhu wa rasụluhụ wallażīna āmanullażīna yuqīmụnaṣ-ṣalāta wa yu`tụnaz-zakāta wa hum rāki\'ụn'),
(724, 5, 56, 'وَمَنْ يَّتَوَلَّ اللّٰهَ وَرَسُوْلَهٗ وَالَّذِيْنَ اٰمَنُوْا فَاِنَّ حِزْبَ اللّٰهِ هُمُ الْغٰلِبُوْنَ', 'wa may yatawallallāha wa rasụlahụ wallażīna āmanụ fa inna ḥizballāhi humul-gālibụn'),
(725, 5, 57, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوا الَّذِيْنَ اتَّخَذُوْا دِيْنَكُمْ هُزُوًا وَّلَعِبًا مِّنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ مِنْ قَبْلِكُمْ وَالْكُفَّارَ اَوْلِيَاۤءَۚ وَاتَّقُوا اللّٰهَ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ', 'yā ayyuhallażīna āmanụ lā tattakhiżullażīnattakhażụ dīnakum huzuwaw wa la\'ibam minallażīna ụtul-kitāba ming qablikum wal-kuffāra auliyā`, wattaqullāha ing kuntum mu`minīn'),
(726, 5, 58, 'وَاِذَا نَادَيْتُمْ اِلَى الصَّلٰوةِ اتَّخَذُوْهَا هُزُوًا وَّلَعِبًا ۗذٰلِكَ بِاَ نَّهُمْ قَوْمٌ لَّا يَعْقِلُوْنَ', 'wa iżā nādaitum ilaṣ-ṣalātittakhażụhā huzuwaw wa la\'ibā, żālika bi`annahum qaumul lā ya\'qilụn'),
(727, 5, 59, 'قُلْ يٰٓاَهْلَ الْكِتٰبِ هَلْ تَنْقِمُوْنَ مِنَّآ اِلَّآ اَنْ اٰمَنَّا بِاللّٰهِ وَمَآ اُنْزِلَ اِلَيْنَا وَمَآ اُنْزِلَ مِنْ قَبْلُۙ وَاَنَّ اَكْثَرَكُمْ فٰسِقُوْنَ', 'qul yā ahlal-kitābi hal tangqimụna minnā illā an āmannā billāhi wa mā unzila ilainā wa mā unzila ming qablu wa anna akṡarakum fāsiqụn'),
(728, 5, 60, 'قُلْ هَلْ اُنَبِّئُكُمْ بِشَرٍّ مِّنْ ذٰلِكَ مَثُوْبَةً عِنْدَ اللّٰهِ ۗمَنْ لَّعَنَهُ اللّٰهُ وَغَضِبَ عَلَيْهِ وَجَعَلَ مِنْهُمُ الْقِرَدَةَ وَالْخَنَازِيْرَ وَعَبَدَ الطَّاغُوْتَۗ اُولٰۤىِٕكَ شَرٌّ مَّكَانًا وَّاَضَلُّ عَنْ سَوَاۤءِ السَّبِيْلِ', 'qul hal unabbi`ukum bisyarrim min żālika maṡụbatan \'indallāh, mal la\'anahullāhu wa gaḍiba \'alaihi wa ja\'ala min-humul-qiradata wal-khanāzīra wa \'abadaṭ-ṭāgụt, ulā`ika syarrum makānaw wa aḍallu \'an sawā`is-sabīl'),
(729, 5, 61, 'وَاِذَا جَاۤءُوْكُمْ قَالُوْٓا اٰمَنَّا وَقَدْ دَّخَلُوْا بِالْكُفْرِ وَهُمْ قَدْ خَرَجُوْا بِهٖ ۗوَاللّٰهُ اَعْلَمُ بِمَا كَانُوْا يَكْتُمُوْنَ', 'wa iżā jā`ụkum qālū āmannā wa qad dakhalụ bil-kufri wa hum qad kharajụ bih, wallāhu a\'lamu bimā kānụ yaktumụn'),
(730, 5, 62, 'وَتَرٰى كَثِيْرًا مِّنْهُمْ يُسَارِعُوْنَ فِى الْاِثْمِ وَالْعُدْوَانِ وَاَكْلِهِمُ السُّحْتَۗ لَبِئْسَ مَا كَانُوْا يَعْمَلُوْنَ', 'wa tarā kaṡīram min-hum yusāri\'ụna fil-iṡmi wal-\'udwāni wa aklihimus-suḥt, labi`sa mā kānụ ya\'malụn'),
(731, 5, 63, 'لَوْلَا يَنْهٰىهُمُ الرَّبَّانِيُّوْنَ وَالْاَحْبَارُ عَنْ قَوْلِهِمُ الْاِثْمَ وَاَكْلِهِمُ السُّحْتَۗ لَبِئْسَ مَا كَانُوْا يَصْنَعُوْنَ', 'lau lā yan-hāhumur-rabbāniyyụna wal-aḥbāru \'ang qaulihimul-iṡma wa aklihimus-suḥt, labi`sa mā kānụ yaṣna\'ụn'),
(732, 5, 64, 'وَقَالَتِ الْيَهُوْدُ يَدُ اللّٰهِ مَغْلُوْلَةٌ ۗغُلَّتْ اَيْدِيْهِمْ وَلُعِنُوْا بِمَا قَالُوْا ۘ بَلْ يَدٰهُ مَبْسُوْطَتٰنِۙ يُنْفِقُ كَيْفَ يَشَاۤءُۗ وَلَيَزِيْدَنَّ كَثِيْرًا مِّنْهُمْ مَّآ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ طُغْيَانًا وَّكُفْرًاۗ وَاَلْقَيْنَا بَيْنَهُمُ الْعَدَاوَةَ وَالْبَغْضَاۤءَ اِلٰى يَوْمِ الْقِيٰمَةِۗ كُلَّمَآ اَوْقَدُوْا نَارًا لِّلْحَرْبِ اَطْفَاَهَا اللّٰهُ ۙوَيَسْعَوْنَ فِى الْاَرْضِ فَسَادًاۗ وَاللّٰهُ لَا يُحِبُّ الْمُفْسِدِيْنَ', 'wa qālatil-yahụdu yadullāhi maglụlah, gullat aidīhim wa lu\'inụ bimā qālụ, bal yadāhu mabsụṭatāni yunfiqu kaifa yasyā`, wa layazīdanna kaṡīram min-hum mā unzila ilaika mir rabbika ṭugyānaw wa kufrā, wa alqainā bainahumul-\'adāwata wal-bagḍā`a ilā yaumil-qiyāmah, kullamā auqadụ nāral lil-ḥarbi aṭfa`ahallāhu wa yas\'auna fil-arḍi fasādā, wallāhu lā yuḥibbul-mufsidīn'),
(733, 5, 65, 'وَلَوْ اَنَّ اَهْلَ الْكِتٰبِ اٰمَنُوْا وَاتَّقَوْا لَكَفَّرْنَا عَنْهُمْ سَيِّاٰتِهِمْ وَلَاَدْخَلْنٰهُمْ جَنّٰتِ النَّعِيْمِ', 'walau anna ahlal-kitābi āmanụ wattaqau lakaffarnā \'an-hum sayyi`ātihim wa la`adkhalnāhum jannātin-na\'īm'),
(734, 5, 66, 'وَلَوْ اَنَّهُمْ اَقَامُوا التَّوْرٰىةَ وَالْاِنْجِيْلَ وَمَآ اُنْزِلَ اِلَيْهِمْ مِّنْ رَّبِّهِمْ لَاَكَلُوْا مِنْ فَوْقِهِمْ وَمِنْ تَحْتِ اَرْجُلِهِمْۗ مِنْهُمْ اُمَّةٌ مُّقْتَصِدَةٌ ۗ وَكَثِيْرٌ مِّنْهُمْ سَاۤءَ مَا يَعْمَلُوْنَ', 'walau annahum aqāmut-taurāta wal-injīla wa mā unzila ilaihim mir rabbihim la`akalụ min fauqihim wa min taḥti arjulihim, min-hum ummatum muqtaṣidah, wa kaṡīrum min-hum sā`a mā ya\'malụn'),
(735, 5, 67, '۞ يٰٓاَيُّهَا الرَّسُوْلُ بَلِّغْ مَآ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ ۗوَاِنْ لَّمْ تَفْعَلْ فَمَا بَلَّغْتَ رِسٰلَتَهٗ ۗوَاللّٰهُ يَعْصِمُكَ مِنَ النَّاسِۗ اِنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الْكٰفِرِيْنَ', 'yā ayyuhar-rasụlu ballig mā unzila ilaika mir rabbik, wa il lam taf\'al fa mā ballagta risālatah, wallāhu ya\'ṣimuka minan-nās, innallāha lā yahdil-qaumal-kāfirīn'),
(736, 5, 68, 'قُلْ يٰٓاَهْلَ الْكِتٰبِ لَسْتُمْ عَلٰى شَيْءٍ حَتّٰى تُقِيْمُوا التَّوْرٰىةَ وَالْاِنْجِيْلَ وَمَآ اُنْزِلَ اِلَيْكُمْ مِّنْ رَّبِّكُمْ ۗوَلَيَزِيْدَنَّ كَثِيْرًا مِّنْهُمْ مَّآ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ طُغْيَانًا وَّكُفْرًاۚ فَلَا تَأْسَ عَلَى الْقَوْمِ الْكٰفِرِيْنَ', 'qul yā ahlal-kitābi lastum \'alā syai`in ḥattā tuqīmut-taurāta wal-injīla wa mā unzila ilaikum mir rabbikum, wa layazīdanna kaṡīram min-hum mā unzila ilaika mir rabbika ṭugyānaw wa kufrā, fa lā ta`sa \'alal-qaumil-kāfirīn'),
(737, 5, 69, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَالَّذِيْنَ هَادُوْا وَالصَّابِـُٔوْنَ وَالنَّصٰرٰى مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَعَمِلَ صَالِحًا فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ', 'innallażīna āmanụ wallażīna hādụ waṣ-ṣābi`ụna wan-naṣārā man āmana billāhi wal-yaumil-ākhiri wa \'amila ṣāliḥan fa lā khaufun \'alaihim wa lā hum yaḥzanụn'),
(738, 5, 70, 'لَقَدْ اَخَذْنَا مِيْثَاقَ بَنِيْٓ اِسْرَاۤءِيْلَ وَاَرْسَلْنَآ اِلَيْهِمْ رُسُلًا ۗ كُلَّمَا جَاۤءَهُمْ رَسُوْلٌۢ بِمَا لَا تَهْوٰٓى اَنْفُسُهُمْۙ فَرِيْقًا كَذَّبُوْا وَفَرِيْقًا يَّقْتُلُوْنَ', 'laqad akhażnā mīṡāqa banī isrā`īla wa arsalnā ilaihim rusulā, kullamā jā`ahum rasụlum bimā lā tahwā anfusuhum farīqang każżabụ wa farīqay yaqtulụn'),
(739, 5, 71, 'وَحَسِبُوْٓا اَلَّا تَكُوْنَ فِتْنَةٌ فَعَمُوْا وَصَمُّوْا ثُمَّ تَابَ اللّٰهُ عَلَيْهِمْ ثُمَّ عَمُوْا وَصَمُّوْا كَثِيْرٌ مِّنْهُمْۗ وَاللّٰهُ بَصِيْرٌۢ بِمَا يَعْمَلُوْنَ', 'wa ḥasibū allā takụna fitnatun fa \'amụ wa ṣammụ ṡumma tāballāhu \'alaihim ṡumma \'amụ wa ṣammụ kaṡīrum min-hum, wallāhu baṣīrum bimā ya\'malụn'),
(740, 5, 72, 'لَقَدْ كَفَرَ الَّذِيْنَ قَالُوْٓا اِنَّ اللّٰهَ هُوَ الْمَسِيْحُ ابْنُ مَرْيَمَ ۗوَقَالَ الْمَسِيْحُ يٰبَنِيْٓ اِسْرَاۤءِيْلَ اعْبُدُوا اللّٰهَ رَبِّيْ وَرَبَّكُمْ ۗاِنَّهٗ مَنْ يُّشْرِكْ بِاللّٰهِ فَقَدْ حَرَّمَ اللّٰهُ عَلَيْهِ الْجَنَّةَ وَمَأْوٰىهُ النَّارُ ۗوَمَا لِلظّٰلِمِيْنَ مِنْ اَنْصَارٍ', 'laqad kafarallażīna qālū innallāha huwal-masīḥubnu maryam, wa qālal-masīḥu yā banī isrā`īla\'budullāha rabbī wa rabbakum, innahụ may yusyrik billāhi fa qad ḥarramallāhu \'alaihil-jannata wa ma`wāhun-nār, wa mā liẓ-ẓālimīna min anṣār'),
(741, 5, 73, 'لَقَدْ كَفَرَ الَّذِيْنَ قَالُوْٓا اِنَّ اللّٰهَ ثَالِثُ ثَلٰثَةٍ ۘ وَمَا مِنْ اِلٰهٍ اِلَّآ اِلٰهٌ وَّاحِدٌ ۗوَاِنْ لَّمْ يَنْتَهُوْا عَمَّا يَقُوْلُوْنَ لَيَمَسَّنَّ الَّذِيْنَ كَفَرُوْا مِنْهُمْ عَذَابٌ اَلِيْمٌ', 'laqad kafarallażīna qālū innallāha ṡāliṡu ṡalāṡah, wa mā min ilāhin illā ilāhuw wāḥid, wa il lam yantahụ \'ammā yaqụlụna layamassannallażīna kafarụ min-hum \'ażābun alīm'),
(742, 5, 74, 'اَفَلَا يَتُوْبُوْنَ اِلَى اللّٰهِ وَيَسْتَغْفِرُوْنَهٗۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ', 'a fa lā yatụbụna ilallāhi wa yastagfirụnah, wallāhu gafụrur raḥīm'),
(743, 5, 75, 'مَا الْمَسِيْحُ ابْنُ مَرْيَمَ اِلَّا رَسُوْلٌۚ قَدْ خَلَتْ مِنْ قَبْلِهِ الرُّسُلُۗ وَاُمُّهٗ صِدِّيْقَةٌ ۗ كَانَا يَأْكُلَانِ الطَّعَامَ ۗ اُنْظُرْ كَيْفَ نُبَيِّنُ لَهُمُ الْاٰيٰتِ ثُمَّ انْظُرْ اَنّٰى يُؤْفَكُوْنَ', 'mal-masīḥubnu maryama illā rasụl, qad khalat ming qablihir-rusul, wa ummuhụ ṣiddīqah, kānā ya`kulāniṭ-ṭa\'ām, unẓur kaifa nubayyinu lahumul-āyāti ṡummanẓur annā yu`fakụn'),
(744, 5, 76, 'قُلْ اَتَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَا يَمْلِكُ لَكُمْ ضَرًّا وَّلَا نَفْعًا ۗوَاللّٰهُ هُوَ السَّمِيْعُ الْعَلِيْمُ', 'qul a ta\'budụna min dụnillāhi mā lā yamliku lakum ḍarraw wa lā naf\'ā, wallāhu huwas-samī\'ul-\'alīm'),
(745, 5, 77, 'قُلْ يٰٓاَهْلَ الْكِتٰبِ لَا تَغْلُوْا فِيْ دِيْنِكُمْ غَيْرَ الْحَقِّ وَلَا تَتَّبِعُوْٓا اَهْوَاۤءَ قَوْمٍ قَدْ ضَلُّوْا مِنْ قَبْلُ وَاَضَلُّوْا كَثِيْرًا وَّضَلُّوْا عَنْ سَوَاۤءِ السَّبِيْلِ', 'qul yā ahlal-kitābi lā taglụ fī dīnikum gairal-ḥaqqi wa lā tattabi\'ū ahwā`a qauming qad ḍallụ ming qablu wa aḍallụ kaṡīraw wa ḍallụ \'an sawā`is-sabīl'),
(746, 5, 78, 'لُعِنَ الَّذِيْنَ كَفَرُوْا مِنْۢ بَنِيْٓ اِسْرَاۤءِيْلَ عَلٰى لِسَانِ دَاوٗدَ وَعِيْسَى ابْنِ مَرْيَمَ ۗذٰلِكَ بِمَا عَصَوْا وَّكَانُوْا يَعْتَدُوْنَ', 'lu\'inallażīna kafarụ mim banī isrā`īla \'alā lisāni dāwụda wa \'īsabni maryam, żālika bimā \'aṣaw wa kānụ ya\'tadụn'),
(747, 5, 79, 'كَانُوْا لَا يَتَنَاهَوْنَ عَنْ مُّنْكَرٍ فَعَلُوْهُۗ لَبِئْسَ مَا كَانُوْا يَفْعَلُوْنَ', 'kānụ lā yatanāhauna \'am mungkarin fa\'alụh, labi`sa mā kānụ yaf\'alụn'),
(748, 5, 80, 'تَرٰى كَثِيْرًا مِّنْهُمْ يَتَوَلَّوْنَ الَّذِيْنَ كَفَرُوْا ۗ لَبِئْسَ مَا قَدَّمَتْ لَهُمْ اَنْفُسُهُمْ اَنْ سَخِطَ اللّٰهُ عَلَيْهِمْ وَفِى الْعَذَابِ هُمْ خٰلِدُوْنَ', 'tarā kaṡīram min-hum yatawallaunallażīna kafarụ, labi`sa mā qaddamat lahum anfusuhum an sakhiṭallāhu \'alaihim wa fil-\'ażābi hum khālidụn'),
(749, 5, 81, 'وَلَوْ كَانُوْا يُؤْمِنُوْنَ بِاللّٰهِ وَالنَّبِيِّ وَمَآ اُنْزِلَ اِلَيْهِ مَا اتَّخَذُوْهُمْ اَوْلِيَاۤءَ وَلٰكِنَّ كَثِيْرًا مِّنْهُمْ فٰسِقُوْنَ', 'walau kānụ yu`minụna billāhi wan-nabiyyi wa mā unzila ilaihi mattakhażụhum auliyā`a wa lākinna kaṡīram min-hum fāsiqụn'),
(750, 5, 82, '۞ لَتَجِدَنَّ اَشَدَّ النَّاسِ عَدَاوَةً لِّلَّذِيْنَ اٰمَنُوا الْيَهُوْدَ وَالَّذِيْنَ اَشْرَكُوْاۚ وَلَتَجِدَنَّ اَقْرَبَهُمْ مَّوَدَّةً لِّلَّذِيْنَ اٰمَنُوا الَّذِيْنَ قَالُوْٓا اِنَّا نَصٰرٰىۗ ذٰلِكَ بِاَنَّ مِنْهُمْ قِسِّيْسِيْنَ وَرُهْبَانًا وَّاَنَّهُمْ لَا يَسْتَكْبِرُوْنَ ۔', 'latajidanna asyaddan-nāsi \'adāwatal lillażīna āmanul-yahụda wallażīna asyrakụ, wa latajidanna aqrabahum mawaddatal lillażīna āmanullażīna qālū innā naṣārā, żālika bi`anna min-hum qissīsīna wa ruhbānaw wa annahum lā yastakbirụn'),
(751, 5, 83, 'وَاِذَا سَمِعُوْا مَآ اُنْزِلَ اِلَى الرَّسُوْلِ تَرٰٓى اَعْيُنَهُمْ تَفِيْضُ مِنَ الدَّمْعِ مِمَّا عَرَفُوْا مِنَ الْحَقِّۚ يَقُوْلُوْنَ رَبَّنَآ اٰمَنَّا فَاكْتُبْنَا مَعَ الشّٰهِدِيْنَ', 'wa iżā sami\'ụ mā unzila ilar-rasụli tarā a\'yunahum tafīḍu minad-dam\'i mimmā \'arafụ minal-ḥaqq, yaqụlụna rabbanā āmannā faktubnā ma\'asy-syāhidīn'),
(752, 5, 84, 'وَمَا لَنَا لَا نُؤْمِنُ بِاللّٰهِ وَمَا جَاۤءَنَا مِنَ الْحَقِّۙ وَنَطْمَعُ اَنْ يُّدْخِلَنَا رَبُّنَا مَعَ الْقَوْمِ الصّٰلِحِيْنَ', 'wa mā lanā lā nu`minu billāhi wa mā jā`anā minal-ḥaqqi wa naṭma\'u ay yudkhilanā rabbunā ma\'al-qaumiṣ-ṣāliḥīn'),
(753, 5, 85, 'فَاَثَابَهُمُ اللّٰهُ بِمَا قَالُوْا جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَاۗ وَذٰلِكَ جَزَاۤءُ الْمُحْسِنِيْنَ', 'fa aṡābahumullāhu bimā qālụ jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā, wa żālika jazā`ul-muḥsinīn'),
(754, 5, 86, 'وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَآ اُولٰۤىِٕكَ اَصْحٰبُ الْجَحِيْمِ', 'wallażīna kafarụ wa każżabụ bi`āyātinā ulā`ika aṣ-ḥābul-jaḥīm'),
(755, 5, 87, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تُحَرِّمُوْا طَيِّبٰتِ مَآ اَحَلَّ اللّٰهُ لَكُمْ وَلَا تَعْتَدُوْا ۗاِنَّ اللّٰهَ لَا يُحِبُّ الْمُعْتَدِيْنَ', 'yā ayyuhallażīna āmanụ lā tuḥarrimụ ṭayyibāti mā aḥallallāhu lakum wa lā ta\'tadụ, innallāha lā yuḥibbul-mu\'tadīn'),
(756, 5, 88, 'وَكُلُوْا مِمَّا رَزَقَكُمُ اللّٰهُ حَلٰلًا طَيِّبًا ۖوَّاتَّقُوا اللّٰهَ الَّذِيْٓ اَنْتُمْ بِهٖ مُؤْمِنُوْنَ', 'wa kulụ mimmā razaqakumullāhu ḥalālan ṭayyibaw wattaqullāhallażī antum bihī mu`minụn'),
(757, 5, 89, 'لَا يُؤَاخِذُكُمُ اللّٰهُ بِاللَّغْوِ فِيْٓ اَيْمَانِكُمْ وَلٰكِنْ يُّؤَاخِذُكُمْ بِمَا عَقَّدْتُّمُ الْاَيْمَانَۚ فَكَفَّارَتُهٗٓ اِطْعَامُ عَشَرَةِ مَسٰكِيْنَ مِنْ اَوْسَطِ مَا تُطْعِمُوْنَ اَهْلِيْكُمْ اَوْ كِسْوَتُهُمْ اَوْ تَحْرِيْرُ رَقَبَةٍ ۗفَمَنْ لَّمْ يَجِدْ فَصِيَامُ ثَلٰثَةِ اَيَّامٍ ۗذٰلِكَ كَفَّارَةُ اَيْمَانِكُمْ اِذَا حَلَفْتُمْ ۗوَاحْفَظُوْٓا اَيْمَانَكُمْ ۗ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيٰتِهٖ لَعَلَّكُمْ تَشْكُرُوْنَ', 'lā yu`ākhiżukumullāhu bil-lagwi fī aimānikum wa lākiy yu`ākhiżukum bimā \'aqqattumul-aimān, fa kaffāratuhū iṭ\'āmu \'asyarati masākīna min ausaṭi mā tuṭ\'imụna ahlīkum au kiswatuhum au taḥrīru raqabah, fa mal lam yajid fa ṣiyāmu ṡalāṡati ayyām, żālika kaffāratu aimānikum iżā ḥalaftum, waḥfaẓū aimānakum, każālika yubayyinullāhu lakum āyātihī la\'allakum tasykurụn'),
(758, 5, 90, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنَّمَا الْخَمْرُ وَالْمَيْسِرُ وَالْاَنْصَابُ وَالْاَزْلَامُ رِجْسٌ مِّنْ عَمَلِ الشَّيْطٰنِ فَاجْتَنِبُوْهُ لَعَلَّكُمْ تُفْلِحُوْنَ', 'yā ayyuhallażīna āmanū innamal-khamru wal-maisiru wal-anṣābu wal-azlāmu rijsum min \'amalisy-syaiṭāni fajtanibụhu la\'allakum tufliḥụn'),
(759, 5, 91, 'اِنَّمَا يُرِيْدُ الشَّيْطٰنُ اَنْ يُّوْقِعَ بَيْنَكُمُ الْعَدَاوَةَ وَالْبَغْضَاۤءَ فِى الْخَمْرِ وَالْمَيْسِرِ وَيَصُدَّكُمْ عَنْ ذِكْرِ اللّٰهِ وَعَنِ الصَّلٰوةِ فَهَلْ اَنْتُمْ مُّنْتَهُوْنَ', 'innamā yurīdusy-syaiṭānu ay yụqi\'a bainakumul-\'adāwata wal-bagḍā`a fil-khamri wal-maisiri wa yaṣuddakum \'an żikrillāhi wa \'aniṣ-ṣalāti fa hal antum muntahụn'),
(760, 5, 92, 'وَاَطِيْعُوا اللّٰهَ وَاَطِيْعُوا الرَّسُوْلَ وَاحْذَرُوْا ۚفَاِنْ تَوَلَّيْتُمْ فَاعْلَمُوْٓا اَنَّمَا عَلٰى رَسُوْلِنَا الْبَلٰغُ الْمُبِيْنُ', 'wa aṭī\'ullāha wa aṭī\'ur-rasụla waḥżarụ, fa in tawallaitum fa\'lamū annamā \'alā rasụlinal-balāgul-mubīn'),
(761, 5, 93, 'لَيْسَ عَلَى الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ جُنَاحٌ فِيْمَا طَعِمُوْٓا اِذَا مَا اتَّقَوْا وَّاٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ ثُمَّ اتَّقَوْا وَّاٰمَنُوْا ثُمَّ اتَّقَوْا وَّاَحْسَنُوْا ۗوَاللّٰهُ يُحِبُّ الْمُحْسِنِيْنَ', 'laisa \'alallażīna āmanụ wa \'amiluṣ-ṣāliḥāti junāḥun fīmā ṭa\'imū iżā mattaqaw wa āmanụ wa \'amiluṣ-ṣāliḥāti ṡummattaqaw wa āmanụ ṡummattaqaw wa aḥsanụ, wallāhu yuḥibbul-muḥsinīn'),
(762, 5, 94, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَيَبْلُوَنَّكُمُ اللّٰهُ بِشَيْءٍ مِّنَ الصَّيْدِ تَنَالُهٗٓ اَيْدِيْكُمْ وَرِمَاحُكُمْ لِيَعْلَمَ اللّٰهُ مَنْ يَّخَافُهٗ بِالْغَيْبِۚ فَمَنِ اعْتَدٰى بَعْدَ ذٰلِكَ فَلَهٗ عَذَابٌ اَلِيْمٌ', 'yā ayyuhallażīna āmanụ layabluwannakumullāhu bisyai`im minaṣ-ṣaidi tanāluhū aidīkum wa rimāḥukum liya\'lamallāhu may yakhāfuhụ bil-gaīb, fa mani\'tadā ba\'da żālika fa lahụ \'ażābun alīm'),
(763, 5, 95, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَقْتُلُوا الصَّيْدَ وَاَنْتُمْ حُرُمٌ ۗوَمَنْ قَتَلَهٗ مِنْكُمْ مُّتَعَمِّدًا فَجَزَۤاءٌ مِّثْلُ مَا قَتَلَ مِنَ النَّعَمِ يَحْكُمُ بِهٖ ذَوَا عَدْلٍ مِّنْكُمْ هَدْيًاۢ بٰلِغَ الْكَعْبَةِ اَوْ كَفَّارَةٌ طَعَامُ مَسٰكِيْنَ اَوْ عَدْلُ ذٰلِكَ صِيَامًا لِّيَذُوْقَ وَبَالَ اَمْرِهٖ ۗعَفَا اللّٰهُ عَمَّا سَلَفَ ۗوَمَنْ عَادَ فَيَنْتَقِمُ اللّٰهُ مِنْهُ ۗوَاللّٰهُ عَزِيْزٌ ذُو انْتِقَامٍ', 'yā ayyuhallażīna āmanụ lā taqtuluṣ-ṣaida wa antum ḥurum, wa mang qatalahụ mingkum muta\'ammidan fa jazā`um miṡlu mā qatala minan-na\'ami yaḥkumu bihī żawā \'adlim mingkum hadyam bāligal-ka\'bati au kaffāratun ṭa\'āmu masākīna au \'adlu żālika ṣiyāmal liyażụqa wa bāla amrih, \'afallāhu \'ammā salaf, wa man \'āda fa yantaqimullāhu min-h, wallāhu \'azīzun żuntiqām'),
(764, 5, 96, 'اُحِلَّ لَكُمْ صَيْدُ الْبَحْرِ وَطَعَامُهٗ مَتَاعًا لَّكُمْ وَلِلسَّيَّارَةِ ۚوَحُرِّمَ عَلَيْكُمْ صَيْدُ الْبَرِّ مَا دُمْتُمْ حُرُمًا ۗوَاتَّقُوا اللّٰهَ الَّذِيْٓ اِلَيْهِ تُحْشَرُوْنَ', 'uḥilla lakum ṣaidul-baḥri wa ṭa\'āmuhụ matā\'al lakum wa lis-sayyārah, wa ḥurrima \'alaikum ṣaidul-barri mā dumtum ḥurumā, wattaqullāhallażī ilaihi tuḥsyarụn'),
(765, 5, 97, '۞ جَعَلَ اللّٰهُ الْكَعْبَةَ الْبَيْتَ الْحَرَامَ قِيٰمًا لِّلنَّاسِ وَالشَّهْرَ الْحَرَامَ وَالْهَدْيَ وَالْقَلَاۤىِٕدَ ۗذٰلِكَ لِتَعْلَمُوْٓا اَنَّ اللّٰهَ يَعْلَمُ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۙ وَاَنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ', 'ja\'alallāhul-ka\'batal-baital-ḥarāma qiyāmal lin-nāsi wasy-syahral-ḥarāma wal-hadya wal-qalā`id, żālika lita\'lamū annallāha ya\'lamu mā fis-samāwāti wa mā fil-arḍi wa annallāha bikulli syai`in \'alīm'),
(766, 5, 98, 'اِعْلَمُوْٓا اَنَّ اللّٰهَ شَدِيْدُ الْعِقَابِۙ وَاَنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌۗ', 'i\'lamū annallāha syadīdul-\'iqābi wa annallāha gafụrur raḥīm'),
(767, 5, 99, 'مَا عَلَى الرَّسُوْلِ اِلَّا الْبَلٰغُ ۗوَاللّٰهُ يَعْلَمُ مَا تُبْدُوْنَ وَمَا تَكْتُمُوْنَ', 'mā \'alar-rasụli illal-balāg, wallāhu ya\'lamu mā tubdụna wa mā taktumụn'),
(768, 5, 100, 'قُلْ لَّا يَسْتَوِى الْخَبِيْثُ وَالطَّيِّبُ وَلَوْ اَعْجَبَكَ كَثْرَةُ الْخَبِيْثِۚ فَاتَّقُوا اللّٰهَ يٰٓاُولِى الْاَلْبَابِ لَعَلَّكُمْ تُفْلِحُوْنَ', 'qul lā yastawil-khabīṡu waṭ-ṭayyibu walau a\'jabaka kaṡratul khabīṡ, fattaqullāha yā ulil-albābi la\'allakum tufliḥụn'),
(769, 5, 101, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَسْـَٔلُوْا عَنْ اَشْيَاۤءَ اِنْ تُبْدَ لَكُمْ تَسُؤْكُمْ ۚوَاِنْ تَسْـَٔلُوْا عَنْهَا حِيْنَ يُنَزَّلُ الْقُرْاٰنُ تُبْدَ لَكُمْ ۗعَفَا اللّٰهُ عَنْهَا ۗوَاللّٰهُ غَفُوْرٌ حَلِيْمٌ', 'yā ayyuhallażīna āmanụ lā tas`alụ \'an asy-yā`a in tubda lakum tasu`kum, wa in tas`alụ \'an-hā ḥīna yunazzalul-qur`ānu tubda lakum, \'afallāhu \'an-hā, wallāhu gafụrun ḥalīm'),
(770, 5, 102, 'قَدْ سَاَلَهَا قَوْمٌ مِّنْ قَبْلِكُمْ ثُمَّ اَصْبَحُوْا بِهَا كٰفِرِيْنَ', 'qad sa`alahā qaumum ming qablikum ṡumma aṣbaḥụ bihā kāfirīn'),
(771, 5, 103, 'مَا جَعَلَ اللّٰهُ مِنْۢ بَحِيْرَةٍ وَّلَا سَاۤىِٕبَةٍ وَّلَا وَصِيْلَةٍ وَّلَا حَامٍ ۙوَّلٰكِنَّ الَّذِيْنَ كَفَرُوْا يَفْتَرُوْنَ عَلَى اللّٰهِ الْكَذِبَۗ وَاَكْثَرُهُمْ لَا يَعْقِلُوْنَ', 'mā ja\'alallāhu mim baḥīratiw wa lā sā`ibatiw wa lā waṣīlatiw wa lā hāmiw wa lākinnallażīna kafarụ yaftarụna \'alallāhil-każib, wa akṡaruhum lā ya\'qilụn'),
(772, 5, 104, 'وَاِذَا قِيْلَ لَهُمْ تَعَالَوْا اِلٰى مَآ اَنْزَلَ اللّٰهُ وَاِلَى الرَّسُوْلِ قَالُوْا حَسْبُنَا مَا وَجَدْنَا عَلَيْهِ اٰبَاۤءَنَا ۗ اَوَلَوْ كَانَ اٰبَاۤؤُهُمْ لَا يَعْلَمُوْنَ شَيْـًٔا وَّلَا يَهْتَدُوْنَ', 'wa iżā qīla lahum ta\'ālau ilā mā anzalallāhu wa ilar-rasụli qālụ ḥasbunā mā wajadnā \'alaihi ābā`anā, a walau kāna ābā`uhum lā ya\'lamụna syai`aw wa lā yahtadụn'),
(773, 5, 105, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا عَلَيْكُمْ اَنْفُسَكُمْ ۚ لَا يَضُرُّكُمْ مَّنْ ضَلَّ اِذَا اهْتَدَيْتُمْ ۗ اِلَى اللّٰهِ مَرْجِعُكُمْ جَمِيْعًا فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ', 'yā ayyuhallażīna āmanụ \'alaikum anfusakum, lā yaḍurrukum man ḍalla iżahtadaitum, ilallāhi marji\'ukum jamī\'an fa yunabbi`ukum bimā kuntum ta\'malụn'),
(774, 5, 106, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا شَهَادَةُ بَيْنِكُمْ اِذَا حَضَرَ اَحَدَكُمُ الْمَوْتُ حِيْنَ الْوَصِيَّةِ اثْنٰنِ ذَوَا عَدْلٍ مِّنْكُمْ اَوْ اٰخَرٰنِ مِنْ غَيْرِكُمْ اِنْ اَنْتُمْ ضَرَبْتُمْ فِى الْاَرْضِ فَاَصَابَتْكُمْ مُّصِيْبَةُ الْمَوْتِۗ تَحْبِسُوْنَهُمَا مِنْۢ بَعْدِ الصَّلٰوةِ فَيُقْسِمٰنِ بِاللّٰهِ اِنِ ارْتَبْتُمْ لَا نَشْتَرِيْ بِهٖ ثَمَنًا وَّلَوْ كَانَ ذَا قُرْبٰىۙ وَلَا نَكْتُمُ شَهَادَةَ اللّٰهِ اِنَّآ اِذًا لَّمِنَ الْاٰثِمِيْنَ', 'yā ayyuhallażīna āmanụ syahādatu bainikum iżā ḥaḍara aḥadakumul-mautu ḥīnal-waṣiyyatiṡnāni żawā \'adlim mingkum au ākharāni min gairikum in antum ḍarabtum fil-arḍi fa aṣābatkum muṣībatul-maụt, taḥbisụnahumā mim ba\'diṣ-ṣalāti fa yuqsimāni billāhi inirtabtum lā nasytarī bihī ṡamanaw walau kāna żā qurbā wa lā naktumu syahādatallāhi innā iżal laminal-āṡimīn'),
(775, 5, 107, 'فَاِنْ عُثِرَ عَلٰٓى اَنَّهُمَا اسْتَحَقَّآ اِثْمًا فَاٰخَرٰنِ يَقُوْمٰنِ مَقَامَهُمَا مِنَ الَّذِيْنَ اسْتَحَقَّ عَلَيْهِمُ الْاَوْلَيٰنِ فَيُقْسِمٰنِ بِاللّٰهِ لَشَهَادَتُنَآ اَحَقُّ مِنْ شَهَادَتِهِمَا وَمَا اعْتَدَيْنَآ ۖاِنَّآ اِذًا لَّمِنَ الظّٰلِمِيْنَ', 'fa in \'uṡira \'alā annahumastaḥaqqā iṡman fa ākharāni yaqụmāni maqāmahumā minallażīnastaḥaqqa \'alaihimul-aulayāni fa yuqsimāni billāhi lasyahādatunā aḥaqqu min syahādatihimā wa ma\'tadainā innā iżal laminaẓ-ẓālimīn'),
(776, 5, 108, 'ذٰلِكَ اَدْنٰٓى اَنْ يَّأْتُوْا بِالشَّهَادَةِ عَلٰى وَجْهِهَآ اَوْ يَخَافُوْٓا اَنْ تُرَدَّ اَيْمَانٌۢ بَعْدَ اَيْمَانِهِمْۗ وَاتَّقُوا اللّٰهَ وَاسْمَعُوْا ۗوَاللّٰهُ لَا يَهْدِى الْقَوْمَ الْفٰسِقِيْنَ', 'żālika adnā ay ya`tụ bisy-syahādati \'alā waj-hihā au yakhāfū an turadda aimānum ba\'da aimānihim, wattaqullāha wasma\'ụ, wallāhu lā yahdil-qaumal-fāsiqīn'),
(777, 5, 109, '۞ يَوْمَ يَجْمَعُ اللّٰهُ الرُّسُلَ فَيَقُوْلُ مَاذَٓا اُجِبْتُمْ ۗ قَالُوْا لَا عِلْمَ لَنَا ۗاِنَّكَ اَنْتَ عَلَّامُ الْغُيُوْبِ', 'yauma yajma\'ullāhur-rusula fa yaqụlu māżā ujibtum, qālụ lā \'ilma lanā, innaka anta \'allāmul-guyụb'),
(778, 5, 110, 'اِذْ قَالَ اللّٰهُ يٰعِيْسَى ابْنَ مَرْيَمَ اذْكُرْ نِعْمَتِيْ عَلَيْكَ وَعَلٰى وَالِدَتِكَ ۘاِذْ اَيَّدْتُّكَ بِرُوْحِ الْقُدُسِۗ تُكَلِّمُ النَّاسَ فِى الْمَهْدِ وَكَهْلًا ۚوَاِذْ عَلَّمْتُكَ الْكِتٰبَ وَالْحِكْمَةَ وَالتَّوْرٰىةَ وَالْاِنْجِيْلَ ۚوَاِذْ تَخْلُقُ مِنَ الطِّيْنِ كَهَيْـَٔةِ الطَّيْرِ بِاِذْنِيْ فَتَنْفُخُ فِيْهَا فَتَكُوْنُ طَيْرًاۢ بِاِذْنِيْ وَتُبْرِئُ الْاَكْمَهَ وَالْاَبْرَصَ بِاِذْنِيْ ۚوَاِذْ تُخْرِجُ الْمَوْتٰى بِاِذْنِيْ ۚوَاِذْ كَفَفْتُ بَنِيْٓ اِسْرَاۤءِيْلَ عَنْكَ اِذْ جِئْتَهُمْ بِالْبَيِّنٰتِ فَقَالَ الَّذِيْنَ كَفَرُوْا مِنْهُمْ اِنْ هٰذَآ اِلَّا سِحْرٌ مُّبِيْنٌ', 'iż qālallāhu yā \'īsabna maryamażkur ni\'matī \'alaika wa \'alā wālidatik, iż ayyattuka birụḥil-qudus, tukallimun-nāsa fil-mahdi wa kahlā, wa iż \'allamtukal-kitāba wal-ḥikmata wat-taurāta wal-injīl, wa iż takhluqu minaṭ-ṭīni kahai`atiṭ-ṭairi bi`iżnī fa tanfukhu fīhā fa takụnu ṭairam bi`iżnī wa tubri`ul-akmaha wal-abraṣa bi`iżnī, wa iż tukhrijul-mautā bi`iżnī, wa iż kafaftu banī isrā`īla \'angka iż ji`tahum bil-bayyināti fa qālallażīna kafarụ min-hum in hāżā illā siḥrum mubīn'),
(779, 5, 111, 'وَاِذْ اَوْحَيْتُ اِلَى الْحَوَارِيّٖنَ اَنْ اٰمِنُوْا بِيْ وَبِرَسُوْلِيْ ۚ قَالُوْٓا اٰمَنَّا وَاشْهَدْ بِاَنَّنَا مُسْلِمُوْنَ', 'wa iż auḥaitu ilal-ḥawāriyyīna an āminụ bī wa birasụlī, qālū āmannā wasy-had bi`annanā muslimụn'),
(780, 5, 112, 'اِذْ قَالَ الْحَوَارِيُّوْنَ يٰعِيْسَى ابْنَ مَرْيَمَ هَلْ يَسْتَطِيْعُ رَبُّكَ اَنْ يُّنَزِّلَ عَلَيْنَا مَاۤىِٕدَةً مِّنَ السَّمَاۤءِ ۗقَالَ اتَّقُوا اللّٰهَ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ', 'iż qālal-ḥawāriyyụna yā \'īsabna maryama hal yastaṭī\'u rabbuka ay yunazzila \'alainā mā`idatam minas-samā`, qālattaqullāha ing kuntum mu`minīn'),
(781, 5, 113, 'قَالُوْا نُرِيْدُ اَنْ نَّأْكُلَ مِنْهَا وَتَطْمَىِٕنَّ قُلُوْبُنَا وَنَعْلَمَ اَنْ قَدْ صَدَقْتَنَا وَنَكُوْنَ عَلَيْهَا مِنَ الشّٰهِدِيْنَ', 'qālụ nurīdu an na`kula min-hā wa taṭma`inna qulụbunā wa na\'lama ang qad ṣadaqtanā wa nakụna \'alaihā minasy-syāhidīn'),
(782, 5, 114, 'قَالَ عِيْسَى ابْنُ مَرْيَمَ اللهم رَبَّنَآ اَنْزِلْ عَلَيْنَا مَاۤىِٕدَةً مِّنَ السَّمَاۤءِ تَكُوْنُ لَنَا عِيْدًا لِّاَوَّلِنَا وَاٰخِرِنَا وَاٰيَةً مِّنْكَ وَارْزُقْنَا وَاَنْتَ خَيْرُ الرّٰزِقِيْنَ', 'qāla \'īsabnu maryamallāhumma rabbanā anzil \'alainā mā`idatam minas-samā`i takụnu lanā \'īdal li`awwalinā wa ākhirinā wa āyatam mingka warzuqnā wa anta khairur-rāziqīn'),
(783, 5, 115, 'قَالَ اللّٰهُ اِنِّيْ مُنَزِّلُهَا عَلَيْكُمْ ۚ فَمَنْ يَّكْفُرْ بَعْدُ مِنْكُمْ فَاِنِّيْٓ اُعَذِّبُهٗ عَذَابًا لَّآ اُعَذِّبُهٗٓ اَحَدًا مِّنَ الْعٰلَمِيْنَ', 'qālallāhu innī munazziluhā \'alaikum, fa may yakfur ba\'du mingkum fa innī u\'ażżibuhụ \'ażābal lā u\'ażżibuhū aḥadam minal-\'ālamīn'),
(784, 5, 116, 'وَاِذْ قَالَ اللّٰهُ يٰعِيْسَى ابْنَ مَرْيَمَ ءَاَنْتَ قُلْتَ لِلنَّاسِ اتَّخِذُوْنِيْ وَاُمِّيَ اِلٰهَيْنِ مِنْ دُوْنِ اللّٰهِ ۗقَالَ سُبْحٰنَكَ مَا يَكُوْنُ لِيْٓ اَنْ اَقُوْلَ مَا لَيْسَ لِيْ بِحَقٍّ ۗاِنْ كُنْتُ قُلْتُهٗ فَقَدْ عَلِمْتَهٗ ۗتَعْلَمُ مَا فِيْ نَفْسِيْ وَلَآ اَعْلَمُ مَا فِيْ نَفْسِكَ ۗاِنَّكَ اَنْتَ عَلَّامُ الْغُيُوْبِ', 'wa iż qālallāhu yā \'īsabna maryama a anta qulta lin-nāsittakhiżụnī wa ummiya ilāhaini min dụnillāh, qāla sub-ḥānaka mā yakụnu lī an aqụla mā laisa lī biḥaqq, ing kuntu qultuhụ fa qad \'alimtah, ta\'lamu mā fī nafsī wa lā a\'lamu mā fī nafsik, innaka anta \'allāmul-guyụb'),
(785, 5, 117, 'مَا قُلْتُ لَهُمْ اِلَّا مَآ اَمَرْتَنِيْ بِهٖٓ اَنِ اعْبُدُوا اللّٰهَ رَبِّيْ وَرَبَّكُمْ ۚوَكُنْتُ عَلَيْهِمْ شَهِيْدًا مَّا دُمْتُ فِيْهِمْ ۚ فَلَمَّا تَوَفَّيْتَنِيْ كُنْتَ اَنْتَ الرَّقِيْبَ عَلَيْهِمْ ۗوَاَنْتَ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ', 'mā qultu lahum illā mā amartanī bihī ani\'budullāha rabbī wa rabbakum, wa kuntu \'alaihim syahīdam mā dumtu fīhim, fa lammā tawaffaitanī kunta antar-raqība \'alaihim, wa anta \'alā kulli syai`in syahīd'),
(786, 5, 118, 'اِنْ تُعَذِّبْهُمْ فَاِنَّهُمْ عِبَادُكَ ۚوَاِنْ تَغْفِرْ لَهُمْ فَاِنَّكَ اَنْتَ الْعَزِيْزُ الْحَكِيْمُ', 'in tu\'ażżib-hum fa innahum \'ibāduk, wa in tagfir lahum fa innaka antal-\'azīzul-ḥakīm'),
(787, 5, 119, 'قَالَ اللّٰهُ هٰذَا يَوْمُ يَنْفَعُ الصّٰدِقِيْنَ صِدْقُهُمْ ۗ لَهُمْ جَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًا ۗرَضِيَ اللّٰهُ عَنْهُمْ وَرَضُوْا عَنْهُ ۗذٰلِكَ الْفَوْزُ الْعَظِيْمُ', 'qālallāhu hāżā yaumu yanfa\'uṣ-ṣādiqīna ṣidquhum, lahum jannātun tajrī min taḥtihal-an-hāru khālidīna fīhā abadā, raḍiyallāhu \'an-hum wa raḍụ \'an-h, żālikal-fauzul-\'aẓīm'),
(788, 5, 120, 'لِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ وَمَا فِيْهِنَّ ۗوَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'lillāhi mulkus-samāwāti wal-arḍi wa mā fīhinn, wa huwa \'alā kulli syai`ing qadīr'),
(789, 6, 1, 'اَلْحَمْدُ لِلّٰهِ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَجَعَلَ الظُّلُمٰتِ وَالنُّوْرَ ەۗ ثُمَّ الَّذِيْنَ كَفَرُوْا بِرَبِّهِمْ يَعْدِلُوْنَ', 'al-ḥamdu lillāhillażī khalaqas-samāwāti wal-arḍa wa ja\'alaẓ-ẓulumāti wan-nụr, ṡummallażīna kafarụ birabbihim ya\'dilụn'),
(790, 6, 2, 'هُوَ الَّذِيْ خَلَقَكُمْ مِّنْ طِيْنٍ ثُمَّ قَضٰٓى اَجَلًا ۗوَاَجَلٌ مُّسَمًّى عِنْدَهٗ ثُمَّ اَنْتُمْ تَمْتَرُوْنَ', 'huwallażī khalaqakum min ṭīnin ṡumma qaḍā ajalā, wa ajalum musamman \'indahụ ṡumma antum tamtarụn'),
(791, 6, 3, 'وَهُوَ اللّٰهُ فِى السَّمٰوٰتِ وَفِى الْاَرْضِۗ يَعْلَمُ سِرَّكُمْ وَجَهْرَكُمْ وَيَعْلَمُ مَا تَكْسِبُوْنَ', 'wa huwallāhu fis-samāwāti wa fil-arḍ, ya\'lamu sirrakum wa jahrakum wa ya\'lamu mā taksibụn'),
(792, 6, 4, 'وَمَا تَأْتِيْهِمْ مِّنْ اٰيَةٍ مِّنْ اٰيٰتِ رَبِّهِمْ اِلَّا كَانُوْا عَنْهَا مُعْرِضِيْنَ', 'wa mā ta`tīhim min āyatim min āyāti rabbihim illā kānụ \'an-hā mu\'riḍīn'),
(793, 6, 5, 'فَقَدْ كَذَّبُوْا بِالْحَقِّ لَمَّا جَاۤءَهُمْۗ فَسَوْفَ يَأْتِيْهِمْ اَنْۢبـٰۤؤُا مَا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ', 'fa qad każżabụ bil-ḥaqqi lammā jā`ahum, fa saufa ya`tīhim ambā`u mā kānụ bihī yastahzi`ụn'),
(794, 6, 6, 'اَلَمْ يَرَوْا كَمْ اَهْلَكْنَا مِنْ قَبْلِهِمْ مِّنْ قَرْنٍ مَّكَّنّٰهُمْ فِى الْاَرْضِ مَا لَمْ نُمَكِّنْ لَّكُمْ وَاَرْسَلْنَا السَّمَاۤءَ عَلَيْهِمْ مِّدْرَارًا ۖوَّجَعَلْنَا الْاَنْهٰرَ تَجْرِيْ مِنْ تَحْتِهِمْ فَاَهْلَكْنٰهُمْ بِذُنُوْبِهِمْ وَاَنْشَأْنَا مِنْۢ بَعْدِهِمْ قَرْنًا اٰخَرِيْنَ', 'a lam yarau kam ahlaknā ming qablihim ming qarnim makkannāhum fil-arḍi mā lam numakkil lakum wa arsalnas-samā`a \'alaihim midrāraw wa ja\'alnal-an-hāra tajrī min taḥtihim fa ahlaknāhum biżunụbihim wa ansya`nā mim ba\'dihim qarnan ākharīn'),
(795, 6, 7, 'وَلَوْ نَزَّلْنَا عَلَيْكَ كِتٰبًا فِيْ قِرْطَاسٍ فَلَمَسُوْهُ بِاَيْدِيْهِمْ لَقَالَ الَّذِيْنَ كَفَرُوْٓا اِنْ هٰذَآ اِلَّا سِحْرٌ مُّبِيْنٌ', 'walau nazzalnā \'alaika kitāban fī qirṭāsin fa lamasụhu bi`aidīhim laqālallażīna kafarū in hāżā illā siḥrum mubīn'),
(796, 6, 8, 'وَقَالُوْا لَوْلَآ اُنْزِلَ عَلَيْهِ مَلَكٌ ۗوَلَوْ اَنْزَلْنَا مَلَكًا لَّقُضِيَ الْاَمْرُ ثُمَّ لَا يُنْظَرُوْنَ', 'wa qālụ lau lā unzila \'alaihi malak, walau anzalnā malakal laquḍiyal-amru ṡumma lā yunẓarụn'),
(797, 6, 9, 'وَلَوْ جَعَلْنٰهُ مَلَكًا لَّجَعَلْنٰهُ رَجُلًا وَّلَلَبَسْنَا عَلَيْهِمْ مَّا يَلْبِسُوْنَ', 'walau ja\'alnāhu malakal laja\'alnāhu rajulaw wa lalabasnā \'alaihim mā yalbisụn'),
(798, 6, 10, 'وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِّنْ قَبْلِكَ فَحَاقَ بِالَّذِيْنَ سَخِرُوْا مِنْهُمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ', 'wa laqadistuhzi`a birusulim ming qablika fa ḥāqa billażīna sakhirụ min-hum mā kānụ bihī yastahzi`ụn'),
(799, 6, 11, 'قُلْ سِيْرُوْا فِى الْاَرْضِ ثُمَّ انْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِيْنَ', 'qul sīrụ fil-arḍi ṡummanẓurụ kaifa kāna \'āqibatul-mukażżibīn'),
(800, 6, 12, 'قُلْ لِّمَنْ مَّا فِى السَّمٰوٰتِ وَالْاَرْضِۗ قُلْ لِّلّٰهِ ۗ كَتَبَ عَلٰى نَفْسِهِ الرَّحْمَةَ ۗ لَيَجْمَعَنَّكُمْ اِلٰى يَوْمِ الْقِيٰمَةِ لَا رَيْبَ فِيْهِۗ اَلَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ فَهُمْ لَا يُؤْمِنُوْنَ', 'qul limam mā fis-samāwāti wal-arḍ, qul lillāh, kataba \'alā nafsihir-raḥmah, layajma\'annakum ilā yaumil-qiyāmati lā raiba fīh, allażīna khasirū anfusahum fa hum lā yu`minụn'),
(801, 6, 13, '۞ وَلَهٗ مَا سَكَنَ فِى الَّيْلِ وَالنَّهَارِ ۗوَهُوَ السَّمِيْعُ الْعَلِيْمُ', 'wa lahụ mā sakana fil-laili wan-nahār, wa huwas-samī\'ul-\'alīm'),
(802, 6, 14, 'قُلْ اَغَيْرَ اللّٰهِ اَتَّخِذُ وَلِيًّا فَاطِرِ السَّمٰوٰتِ وَالْاَرْضِ وَهُوَ يُطْعِمُ وَلَا يُطْعَمُ ۗ قُلْ اِنِّيْٓ اُمِرْتُ اَنْ اَكُوْنَ اَوَّلَ مَنْ اَسْلَمَ وَلَا تَكُوْنَنَّ مِنَ الْمُشْرِكِيْنَ', 'qul agairallāhi attakhiżu waliyyan fāṭiris-samāwāti wal-arḍi wa huwa yuṭ\'imu wa lā yuṭ\'am, qul innī umirtu an akụna awwala man aslama wa lā takụnanna minal-musyrikīn'),
(803, 6, 15, 'قُلْ اِنِّيْٓ اَخَافُ اِنْ عَصَيْتُ رَبِّيْ عَذَابَ يَوْمٍ عَظِيْمٍ', 'qul innī akhāfu in \'aṣaitu rabbī \'ażāba yaumin \'aẓīm'),
(804, 6, 16, 'مَنْ يُّصْرَفْ عَنْهُ يَوْمَىِٕذٍ فَقَدْ رَحِمَهٗ ۗوَذٰلِكَ الْفَوْزُ الْمُبِيْنُ', 'may yuṣraf \'an-hu yauma`iżin fa qad raḥimah, wa żālikal-fauzul-mubīn'),
(805, 6, 17, 'وَاِنْ يَّمْسَسْكَ اللّٰهُ بِضُرٍّ فَلَا كَاشِفَ لَهٗٓ اِلَّا هُوَ ۗوَاِنْ يَّمْسَسْكَ بِخَيْرٍ فَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'wa iy yamsaskallāhu biḍurrin fa lā kāsyifa lahū illā huw, wa iy yamsaska bikhairin fa huwa \'alā kulli syai`ing qadīr'),
(806, 6, 18, 'وَهُوَ الْقَاهِرُ فَوْقَ عِبَادِهٖۗ وَهُوَ الْحَكِيْمُ الْخَبِيْرُ', 'wa huwal-qāhiru fauqa \'ibādih, wa huwal-ḥakīmul-khabīr'),
(807, 6, 19, 'قُلْ اَيُّ شَيْءٍ اَكْبَرُ شَهَادَةً ۗ قُلِ اللّٰهُ ۗشَهِيْدٌۢ بَيْنِيْ وَبَيْنَكُمْ ۗوَاُوْحِيَ اِلَيَّ هٰذَا الْقُرْاٰنُ لِاُنْذِرَكُمْ بِهٖ وَمَنْۢ بَلَغَ ۗ اَىِٕنَّكُمْ لَتَشْهَدُوْنَ اَنَّ مَعَ اللّٰهِ اٰلِهَةً اُخْرٰىۗ قُلْ لَّآ اَشْهَدُ ۚ قُلْ اِنَّمَا هُوَ اِلٰهٌ وَّاحِدٌ وَّاِنَّنِيْ بَرِيْۤءٌ مِّمَّا تُشْرِكُوْنَ', 'qul ayyu syai`in akbaru syahādah, qulillāh, syahīdum bainī wa bainakum, wa ụḥiya ilayya hāżal-qur`ānu li`unżirakum bihī wa mam balag, a innakum latasy-hadụna anna ma\'allāhi ālihatan ukhrā, qul lā asy-had, qul innamā huwa ilāhuw wāḥiduw wa innanī barī`um mimmā tusyrikụn'),
(808, 6, 20, 'اَلَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يَعْرِفُوْنَهٗ كَمَا يَعْرِفُوْنَ اَبْنَاۤءَهُمْۘ اَلَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ فَهُمْ لَا يُؤْمِنُوْنَ', 'allażīna ātaināhumul-kitāba ya\'rifụnahụ kamā ya\'rifụna abnā`ahum, allażīna khasirū anfusahum fa hum lā yu`minụn'),
(809, 6, 21, 'وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا اَوْ كَذَّبَ بِاٰيٰتِهٖۗ اِنَّهٗ لَا يُفْلِحُ الظّٰلِمُوْنَ', 'wa man aẓlamu mim maniftarā \'alallāhi każiban au każżaba bi`āyātih, innahụ lā yufliḥuẓ-ẓālimụn'),
(810, 6, 22, 'وَيَوْمَ نَحْشُرُهُمْ جَمِيْعًا ثُمَّ نَقُوْلُ لِلَّذِيْنَ اَشْرَكُوْٓا اَيْنَ شُرَكَاۤؤُكُمُ الَّذِيْنَ كُنْتُمْ تَزْعُمُوْنَ', 'wa yauma naḥsyuruhum jamī\'an ṡumma naqụlu lillażīna asyrakū aina syurakā`ukumullażīna kuntum taz\'umụn'),
(811, 6, 23, 'ثُمَّ لَمْ تَكُنْ فِتْنَتُهُمْ اِلَّآ اَنْ قَالُوْا وَاللّٰهِ رَبِّنَا مَا كُنَّا مُشْرِكِيْنَ', 'ṡumma lam takun fitnatuhum illā ang qālụ wallāhi rabbinā mā kunnā musyrikīn'),
(812, 6, 24, 'اُنْظُرْ كَيْفَ كَذَبُوْا عَلٰٓى اَنْفُسِهِمْ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ', 'unẓur kaifa każabụ \'alā anfusihim wa ḍalla \'an-hum mā kānụ yaftarụn'),
(813, 6, 25, 'وَمِنْهُمْ مَّنْ يَّسْتَمِعُ اِلَيْكَ ۚوَجَعَلْنَا عَلٰى قُلُوْبِهِمْ اَكِنَّةً اَنْ يَّفْقَهُوْهُ وَفِيْٓ اٰذَانِهِمْ وَقْرًا ۗوَاِنْ يَّرَوْا كُلَّ اٰيَةٍ لَّا يُؤْمِنُوْا بِهَا ۗحَتّٰٓى اِذَا جَاۤءُوْكَ يُجَادِلُوْنَكَ يَقُوْلُ الَّذِيْنَ كَفَرُوْٓا اِنْ هٰذَآ اِلَّآ اَسَاطِيْرُ الْاَوَّلِيْنَ', 'wa min-hum may yastami\'u ilaīk, wa ja\'alnā \'alā qulụbihim akinnatan ay yafqahụhu wa fī āżānihim waqrā, wa iy yarau kulla āyatil lā yu`minụ bihā, ḥattā iżā jā`ụka yujādilụnaka yaqụlullażīna kafarū in hāżā illā asāṭīrul-awwalīn'),
(814, 6, 26, 'وَهُمْ يَنْهَوْنَ عَنْهُ وَيَنْـَٔوْنَ عَنْهُ ۚوَاِنْ يُّهْلِكُوْنَ اِلَّآ اَنْفُسَهُمْ وَمَا يَشْعُرُوْنَ', 'wa hum yan-hauna \'an-hu wa yan`auna \'an-h, wa iy yuhlikụna illā anfusahum wa mā yasy\'urụn'),
(815, 6, 27, 'وَلَوْ تَرٰٓى اِذْ وُقِفُوْا عَلَى النَّارِ فَقَالُوْا يٰلَيْتَنَا نُرَدُّ وَلَا نُكَذِّبَ بِاٰيٰتِ رَبِّنَا وَنَكُوْنَ مِنَ الْمُؤْمِنِيْنَ', 'walau tarā iż wuqifụ \'alan-nāri fa qālụ yā laitanā nuraddu wa lā nukażżiba bi`āyāti rabbinā wa nakụna minal-mu`minīn'),
(816, 6, 28, 'بَلْ بَدَا لَهُمْ مَّا كَانُوْا يُخْفُوْنَ مِنْ قَبْلُ ۗوَلَوْ رُدُّوْا لَعَادُوْا لِمَا نُهُوْا عَنْهُ وَاِنَّهُمْ لَكٰذِبُوْنَ', 'bal badā lahum mā kānụ yukhfụna ming qabl, walau ruddụ la\'ādụ limā nuhụ \'an-hu wa innahum lakāżibụn'),
(817, 6, 29, 'وَقَالُوْٓا اِنْ هِيَ اِلَّا حَيَاتُنَا الدُّنْيَا وَمَا نَحْنُ بِمَبْعُوْثِيْنَ', 'wa qālū in hiya illā ḥayātunad-dun-yā wa mā naḥnu bimab\'ụṡīn'),
(818, 6, 30, 'وَلَوْ تَرٰٓى اِذْ وُقِفُوْا عَلٰى رَبِّهِمْ ۗ قَالَ اَلَيْسَ هٰذَا بِالْحَقِّ ۗقَالُوْا بَلٰى وَرَبِّنَا ۗقَالَ فَذُوْقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُوْنَ', 'walau tarā iż wuqifụ \'alā rabbihim, qāla a laisa hāżā bil-ḥaqq, qālụ balā wa rabbinā, qāla fa żụqul-\'ażāba bimā kuntum takfurụn'),
(819, 6, 31, 'قَدْ خَسِرَ الَّذِيْنَ كَذَّبُوْا بِلِقَاۤءِ اللّٰهِ ۗحَتّٰٓى اِذَا جَاۤءَتْهُمُ السَّاعَةُ بَغْتَةً قَالُوْا يٰحَسْرَتَنَا عَلٰى مَا فَرَّطْنَا فِيْهَاۙ وَهُمْ يَحْمِلُوْنَ اَوْزَارَهُمْ عَلٰى ظُهُوْرِهِمْۗ اَلَا سَاۤءَ مَا يَزِرُوْنَ', 'qad khasirallażīna każżabụ biliqā`illāh, ḥattā iżā jā`at-humus-sā\'atu bagtatang qālụ yā ḥasratanā \'alā mā farraṭnā fīhā wa hum yaḥmilụna auzārahum \'alā ẓuhụrihim, alā sā`a mā yazirụn'),
(820, 6, 32, 'وَمَا الْحَيٰوةُ الدُّنْيَآ اِلَّا لَعِبٌ وَّلَهْوٌ ۗوَلَلدَّارُ الْاٰخِرَةُ خَيْرٌ لِّلَّذِيْنَ يَتَّقُوْنَۗ اَفَلَا تَعْقِلُوْنَ', 'wa mal-ḥayātud-dun-yā illā la\'ibuw wa lahw, wa lad-dārul-ākhiratu khairul lillażīna yattaqụn, a fa lā ta\'qilụn'),
(821, 6, 33, 'قَدْ نَعْلَمُ اِنَّهٗ لَيَحْزُنُكَ الَّذِيْ يَقُوْلُوْنَ فَاِنَّهُمْ لَا يُكَذِّبُوْنَكَ وَلٰكِنَّ الظّٰلِمِيْنَ بِاٰيٰتِ اللّٰهِ يَجْحَدُوْنَ', 'qad na\'lamu innahụ layaḥzunukallażī yaqụlụna fa innahum lā yukażżibụnaka wa lākinnaẓ-ẓālimīna bi`āyātillāhi yaj-ḥadụn');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(822, 6, 34, 'وَلَقَدْ كُذِّبَتْ رُسُلٌ مِّنْ قَبْلِكَ فَصَبَرُوْا عَلٰى مَا كُذِّبُوْا وَاُوْذُوْا حَتّٰٓى اَتٰىهُمْ نَصْرُنَا ۚوَلَا مُبَدِّلَ لِكَلِمٰتِ اللّٰهِ ۚوَلَقَدْ جَاۤءَكَ مِنْ نَّبَإِ۟ى الْمُرْسَلِيْنَ', 'wa laqad kużżibat rusulum ming qablika fa ṣabarụ \'alā mā kużżibụ wa ụżụ ḥattā atāhum naṣrunā, wa lā mubaddila likalimātillāh, wa laqad jā`aka min naba`il-mursalīn'),
(823, 6, 35, 'وَاِنْ كَانَ كَبُرَ عَلَيْكَ اِعْرَاضُهُمْ فَاِنِ اسْتَطَعْتَ اَنْ تَبْتَغِيَ نَفَقًا فِى الْاَرْضِ اَوْ سُلَّمًا فِى السَّمَاۤءِ فَتَأْتِيَهُمْ بِاٰيَةٍ ۗوَلَوْ شَاۤءَ اللّٰهُ لَجَمَعَهُمْ عَلَى الْهُدٰى فَلَا تَكُوْنَنَّ مِنَ الْجٰهِلِيْنَ', 'wa ing kāna kabura \'alaika i\'rāḍuhum fa inistaṭa\'ta an tabtagiya nafaqan fil-arḍi au sullaman fis-samā`i fa ta`tiyahum bi`āyah, walau syā`allāhu lajama\'ahum \'alal-hudā fa lā takụnanna minal-jāhilīn'),
(824, 6, 36, '۞ اِنَّمَا يَسْتَجِيْبُ الَّذِيْنَ يَسْمَعُوْنَ ۗوَالْمَوْتٰى يَبْعَثُهُمُ اللّٰهُ ثُمَّ اِلَيْهِ يُرْجَعُوْنَ', 'innamā yastajībullażīna yasma\'ụn, wal-mautā yab\'aṡuhumullāhu ṡumma ilaihi yurja\'ụn'),
(825, 6, 37, 'وَقَالُوْا لَوْلَا نُزِّلَ عَلَيْهِ اٰيَةٌ مِّنْ رَّبِّهٖۗ قُلْ اِنَّ اللّٰهَ قَادِرٌ عَلٰٓى اَنْ يُّنَزِّلَ اٰيَةً وَّلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ', 'wa qālụ lau lā nuzzila \'alaihi āyatum mir rabbih, qul innallāha qādirun \'alā ay yunazzila āyataw wa lākinna akṡarahum lā ya\'lamụn'),
(826, 6, 38, 'وَمَا مِنْ دَاۤبَّةٍ فِى الْاَرْضِ وَلَا طٰۤىِٕرٍ يَّطِيْرُ بِجَنَاحَيْهِ اِلَّآ اُمَمٌ اَمْثَالُكُمْ ۗمَا فَرَّطْنَا فِى الْكِتٰبِ مِنْ شَيْءٍ ثُمَّ اِلٰى رَبِّهِمْ يُحْشَرُوْنَ', 'wa mā min dābbatin fil-arḍi wa lā ṭā`iriy yaṭīru bijanāḥaihi illā umamun amṡālukum, mā farraṭnā fil-kitābi min syai`in ṡumma ilā rabbihim yuḥsyarụn'),
(827, 6, 39, 'وَالَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا صُمٌّ وَّبُكْمٌ فِى الظُّلُمٰتِۗ مَنْ يَّشَاِ اللّٰهُ يُضْلِلْهُ وَمَنْ يَّشَأْ يَجْعَلْهُ عَلٰى صِرَاطٍ مُّسْتَقِيْمٍ', 'wallażīna każżabụ bi`āyātinā ṣummuw wa bukmun fiẓ-ẓulumāt, may yasya`illāhu yuḍlil-hu wa may yasya` yaj\'al-hu \'alā ṣirāṭim mustaqīm'),
(828, 6, 40, 'قُلْ اَرَءَيْتَكُمْ اِنْ اَتٰىكُمْ عَذَابُ اللّٰهِ اَوْ اَتَتْكُمُ السَّاعَةُ اَغَيْرَ اللّٰهِ تَدْعُوْنَۚ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'qul a ra`aitakum in atākum \'ażābullāhi au atatkumus-sā\'atu a gairallāhi tad\'ụn, ing kuntum ṣādiqīn'),
(829, 6, 41, 'بَلْ اِيَّاهُ تَدْعُوْنَ فَيَكْشِفُ مَا تَدْعُوْنَ اِلَيْهِ اِنْ شَاۤءَ وَتَنْسَوْنَ مَا تُشْرِكُوْنَ', 'bal iyyāhu tad\'ụna fa yaksyifu mā tad\'ụna ilaihi in syā`a wa tansauna mā tusyrikụn'),
(830, 6, 42, 'وَلَقَدْ اَرْسَلْنَآ اِلٰٓى اُمَمٍ مِّنْ قَبْلِكَ فَاَخَذْنٰهُمْ بِالْبَأْسَاۤءِ وَالضَّرَّاۤءِ لَعَلَّهُمْ يَتَضَرَّعُوْنَ', 'wa laqad arsalnā ilā umamim ming qablika fa akhażnāhum bil-ba`sā`i waḍ-ḍarrā`i la\'allahum yataḍarra\'ụn'),
(831, 6, 43, 'فَلَوْلَآ اِذْ جَاۤءَهُمْ بَأْسُنَا تَضَرَّعُوْا وَلٰكِنْ قَسَتْ قُلُوْبُهُمْ وَزَيَّنَ لَهُمُ الشَّيْطٰنُ مَا كَانُوْا يَعْمَلُوْنَ', 'falau lā iż jā`ahum ba`sunā taḍarra\'ụ wa lāking qasat qulụbuhum wa zayyana lahumusy-syaiṭānu mā kānụ ya\'malụn'),
(832, 6, 44, 'فَلَمَّا نَسُوْا مَا ذُكِّرُوْا بِهٖ فَتَحْنَا عَلَيْهِمْ اَبْوَابَ كُلِّ شَيْءٍۗ حَتّٰٓى اِذَا فَرِحُوْا بِمَآ اُوْتُوْٓا اَخَذْنٰهُمْ بَغْتَةً فَاِذَا هُمْ مُّبْلِسُوْنَ', 'fa lammā nasụ mā żukkirụ bihī fataḥnā \'alaihim abwāba kulli syaī`, ḥattā iżā fariḥụ bimā ụtū akhażnāhum bagtatan fa iżā hum mublisụn'),
(833, 6, 45, 'فَقُطِعَ دَابِرُ الْقَوْمِ الَّذِيْنَ ظَلَمُوْاۗ وَالْحَمْدُ لِلّٰهِ رَبِّ الْعٰلَمِيْنَ', 'fa quṭi\'a dābirul-qaumillażīna ẓalamụ, wal-ḥamdu lillāhi rabbil-\'ālamīn'),
(834, 6, 46, 'قُلْ اَرَاَيْتُمْ اِنْ اَخَذَ اللّٰهُ سَمْعَكُمْ وَاَبْصَارَكُمْ وَخَتَمَ عَلٰى قُلُوْبِكُمْ مَّنْ اِلٰهٌ غَيْرُ اللّٰهِ يَأْتِيْكُمْ بِهٖۗ اُنْظُرْ كَيْفَ نُصَرِّفُ الْاٰيٰتِ ثُمَّ هُمْ يَصْدِفُوْنَ', 'qul a ra`aitum in akhażallāhu sam\'akum wa abṣārakum wa khatama \'alā qulụbikum man ilāhun gairullāhi ya`tīkum bih, unẓur kaifa nuṣarriful-āyāti ṡumma hum yaṣdifụn'),
(835, 6, 47, 'قُلْ اَرَاَيْتَكُمْ اِنْ اَتٰىكُمْ عَذَابُ اللّٰهِ بَغْتَةً اَوْ جَهْرَةً هَلْ يُهْلَكُ اِلَّا الْقَوْمُ الظّٰلِمُوْنَ', 'qul a ra`aitakum in atākum \'ażābullāhi bagtatan au jahratan hal yuhlaku illal-qaumuẓ-ẓālimụn'),
(836, 6, 48, 'وَمَا نُرْسِلُ الْمُرْسَلِيْنَ اِلَّا مُبَشِّرِيْنَ وَمُنْذِرِيْنَۚ فَمَنْ اٰمَنَ وَاَصْلَحَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ', 'wa mā nursilul-mursalīna illā mubasysyirīna wa munżirīn, fa man āmana wa aṣlaḥa fa lā khaufun \'alaihim wa lā hum yaḥzanụn'),
(837, 6, 49, 'وَالَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا يَمَسُّهُمُ الْعَذَابُ بِمَا كَانُوْا يَفْسُقُوْنَ', 'wallażīna każżabụ bi`āyātinā yamassuhumul-\'ażābu bimā kānụ yafsuqụn'),
(838, 6, 50, 'قُلْ لَّآ اَقُوْلُ لَكُمْ عِنْدِيْ خَزَاۤىِٕنُ اللّٰهِ وَلَآ اَعْلَمُ الْغَيْبَ وَلَآ اَقُوْلُ لَكُمْ اِنِّيْ مَلَكٌۚ اِنْ اَتَّبِعُ اِلَّا مَا يُوْحٰٓى اِلَيَّۗ قُلْ هَلْ يَسْتَوِى الْاَعْمٰى وَالْبَصِيْرُۗ اَفَلَا تَتَفَكَّرُوْنَ', 'qul lā aqụlu lakum \'indī khazā`inullāhi wa lā a\'lamul-gaiba wa lā aqụlu lakum innī malak, in attabi\'u illā mā yụḥā ilayy, qul hal yastawil-a\'mā wal-baṣīr, a fa lā tatafakkarụn'),
(839, 6, 51, 'وَاَنْذِرْ بِهِ الَّذِيْنَ يَخَافُوْنَ اَنْ يُّحْشَرُوْٓا اِلٰى رَبِّهِمْ لَيْسَ لَهُمْ مِّنْ دُوْنِهٖ وَلِيٌّ وَّلَا شَفِيْعٌ لَّعَلَّهُمْ يَتَّقُوْنَ', 'wa anżir bihillażīna yakhāfụna ay yuḥsyarū ilā rabbihim laisa lahum min dụnihī waliyyuw wa lā syafī\'ul la\'allahum yattaqụn'),
(840, 6, 52, 'وَلَا تَطْرُدِ الَّذِيْنَ يَدْعُوْنَ رَبَّهُمْ بِالْغَدٰوةِ وَالْعَشِيِّ يُرِيْدُوْنَ وَجْهَهٗ ۗمَا عَلَيْكَ مِنْ حِسَابِهِمْ مِّنْ شَيْءٍ وَّمَا مِنْ حِسَابِكَ عَلَيْهِمْ مِّنْ شَيْءٍ فَتَطْرُدَهُمْ فَتَكُوْنَ مِنَ الظّٰلِمِيْنَ', 'wa lā taṭrudillażīna yad\'ụna rabbahum bil-gadāti wal-\'asyiyyi yurīdụna waj-hah, mā \'alaika min ḥisābihim min syai`iw wa mā min ḥisābika \'alaihim min syai`in fa taṭrudahum fa takụna minaẓ-ẓālimīn'),
(841, 6, 53, 'وَكَذٰلِكَ فَتَنَّا بَعْضَهُمْ بِبَعْضٍ لِّيَقُوْلُوْٓا اَهٰٓؤُلَاۤءِ مَنَّ اللّٰهُ عَلَيْهِمْ مِّنْۢ بَيْنِنَاۗ اَلَيْسَ اللّٰهُ بِاَعْلَمَ بِالشّٰكِرِيْنَ', 'wa każālika fatannā ba\'ḍahum biba\'ḍil liyaqụlū a hā`ulā`i mannallāhu \'alaihim mim baininā, a laisallāhu bi`a\'lama bisy-syākirīn'),
(842, 6, 54, 'وَاِذَا جَاۤءَكَ الَّذِيْنَ يُؤْمِنُوْنَ بِاٰيٰتِنَا فَقُلْ سَلٰمٌ عَلَيْكُمْ كَتَبَ رَبُّكُمْ عَلٰى نَفْسِهِ الرَّحْمَةَۙ اَنَّهٗ مَنْ عَمِلَ مِنْكُمْ سُوْۤءًاۢ بِجَهَالَةٍ ثُمَّ تَابَ مِنْۢ بَعْدِهٖ وَاَصْلَحَ فَاَنَّهٗ غَفُوْرٌ رَّحِيْمٌ', 'wa iżā jā`akallażīna yu`minụna bi`āyātinā fa qul salāmun \'alaikum kataba rabbukum \'alā nafsihir-raḥmata annahụ man \'amila mingkum sū`am bijahālatin ṡumma tāba mim ba\'dihī wa aṣlaḥa fa annahụ gafụrur raḥīm'),
(843, 6, 55, 'وَكَذٰلِكَ نُفَصِّلُ الْاٰيٰتِ وَلِتَسْتَبِيْنَ سَبِيْلُ الْمُجْرِمِيْنَ', 'wa każālika nufaṣṣilul-āyāti wa litastabīna sabīlul-mujrimīn'),
(844, 6, 56, 'قُلْ اِنِّيْ نُهِيْتُ اَنْ اَعْبُدَ الَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ ۗ قُلْ لَّآ اَتَّبِعُ اَهْوَاۤءَكُمْۙ قَدْ ضَلَلْتُ اِذًا وَّمَآ اَنَا۠ مِنَ الْمُهْتَدِيْنَ', 'qul innī nuhītu an a\'budallażīna tad\'ụna min dụnillāh, qul lā attabi\'u ahwā`akum qad ḍalaltu iżaw wa mā ana minal-muhtadīn'),
(845, 6, 57, 'قُلْ اِنِّيْ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّيْ وَكَذَّبْتُمْ بِهٖۗ مَا عِنْدِيْ مَا تَسْتَعْجِلُوْنَ بِهٖۗ اِنِ الْحُكْمُ اِلَّا لِلّٰهِ ۗيَقُصُّ الْحَقَّ وَهُوَ خَيْرُ الْفَاصِلِيْنَ', 'qul innī \'alā bayyinatim mir rabbī wa każżabtum bih, mā \'indī mā tasta\'jilụna bih, inil-ḥukmu illā lillāh, yaquṣṣul-ḥaqqa wa huwa khairul-fāṣilīn'),
(846, 6, 58, 'قُلْ لَّوْ اَنَّ عِنْدِيْ مَا تَسْتَعْجِلُوْنَ بِهٖ لَقُضِيَ الْاَمْرُ بَيْنِيْ وَبَيْنَكُمْ ۗوَاللّٰهُ اَعْلَمُ بِالظّٰلِمِيْنَ', 'qul lau anna \'indī mā tasta\'jilụna bihī laquḍiyal-amru bainī wa bainakum, wallāhu a\'lamu biẓ-ẓālimīn'),
(847, 6, 59, '۞ وَعِنْدَهٗ مَفَاتِحُ الْغَيْبِ لَا يَعْلَمُهَآ اِلَّا هُوَۗ وَيَعْلَمُ مَا فِى الْبَرِّ وَالْبَحْرِۗ وَمَا تَسْقُطُ مِنْ وَّرَقَةٍ اِلَّا يَعْلَمُهَا وَلَا حَبَّةٍ فِيْ ظُلُمٰتِ الْاَرْضِ وَلَا رَطْبٍ وَّلَا يَابِسٍ اِلَّا فِيْ كِتٰبٍ مُّبِيْنٍ', 'wa \'indahụ mafātiḥul-gaibi lā ya\'lamuhā illā huw, wa ya\'lamu mā fil-barri wal-baḥr, wa mā tasquṭu miw waraqatin illā ya\'lamuhā wa lā ḥabbatin fī ẓulumātil-arḍi wa lā raṭbiw wa lā yābisin illā fī kitābim mubīn'),
(848, 6, 60, 'وَهُوَ الَّذِيْ يَتَوَفّٰىكُمْ بِالَّيْلِ وَيَعْلَمُ مَا جَرَحْتُمْ بِالنَّهَارِ ثُمَّ يَبْعَثُكُمْ فِيْهِ لِيُقْضٰٓى اَجَلٌ مُّسَمًّىۚ ثُمَّ اِلَيْهِ مَرْجِعُكُمْ ثُمَّ يُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ', 'wa huwallażī yatawaffākum bil-laili wa ya\'lamu mā jaraḥtum bin-nahāri ṡumma yab\'aṡukum fīhi liyuqḍā ajalum musammā, ṡumma ilaihi marji\'ukum ṡumma yunabbi`ukum bimā kuntum ta\'malụn'),
(849, 6, 61, 'وَهُوَ الْقَاهِرُ فَوْقَ عِبَادِهٖ وَيُرْسِلُ عَلَيْكُمْ حَفَظَةً ۗحَتّٰٓى اِذَا جَاۤءَ اَحَدَكُمُ الْمَوْتُ تَوَفَّتْهُ رُسُلُنَا وَهُمْ لَا يُفَرِّطُوْنَ', 'wa huwal-qāhiru fauqa \'ibādihī wa yursilu \'alaikum ḥafaẓah, ḥattā iżā jā`a aḥadakumul-mautu tawaffat-hu rusulunā wa hum lā yufarriṭụn'),
(850, 6, 62, 'ثُمَّ رُدُّوْٓا اِلَى اللّٰهِ مَوْلٰىهُمُ الْحَقِّۗ اَلَا لَهُ الْحُكْمُ وَهُوَ اَسْرَعُ الْحَاسِبِيْنَ', 'ṡumma ruddū ilallāhi maulāhumul-ḥaqq, alā lahul-ḥukmu wa huwa asra\'ul-ḥāsibīn'),
(851, 6, 63, 'قُلْ مَنْ يُّنَجِّيْكُمْ مِّنْ ظُلُمٰتِ الْبَرِّ وَالْبَحْرِ تَدْعُوْنَهٗ تَضَرُّعًا وَّخُفْيَةً ۚ لَىِٕنْ اَنْجٰىنَا مِنْ هٰذِهٖ لَنَكُوْنَنَّ مِنَ الشّٰكِرِيْنَ', 'qul may yunajjīkum min ẓulumātil-barri wal-baḥri tad\'ụnahụ taḍarru\'aw wa khufyah, la`in anjānā min hāżihī lanakụnanna minasy-syākirīn'),
(852, 6, 64, 'قُلِ اللّٰهُ يُنَجِّيْكُمْ مِّنْهَا وَمِنْ كُلِّ كَرْبٍ ثُمَّ اَنْتُمْ تُشْرِكُوْنَ', 'qulillāhu yunajjīkum min-hā wa ming kulli karbin ṡumma antum tusyrikụn'),
(853, 6, 65, 'قُلْ هُوَ الْقَادِرُ عَلٰٓى اَنْ يَّبْعَثَ عَلَيْكُمْ عَذَابًا مِّنْ فَوْقِكُمْ اَوْ مِنْ تَحْتِ اَرْجُلِكُمْ اَوْ يَلْبِسَكُمْ شِيَعًا وَّيُذِيْقَ بَعْضَكُمْ بَأْسَ بَعْضٍۗ اُنْظُرْ كَيْفَ نُصَرِّفُ الْاٰيٰتِ لَعَلَّهُمْ يَفْقَهُوْنَ', 'qul huwal-qādiru \'alā ay yab\'aṡa \'alaikum \'ażābam min fauqikum au min taḥti arjulikum au yalbisakum syiya\'aw wa yużīqa ba\'ḍakum ba`sa ba\'ḍ, unẓur kaifa nuṣarriful-āyāti la\'allahum yafqahụn'),
(854, 6, 66, 'وَكَذَّبَ بِهٖ قَوْمُكَ وَهُوَ الْحَقُّۗ قُلْ لَّسْتُ عَلَيْكُمْ بِوَكِيْلٍ ۗ', 'wa każżaba bihī qaumuka wa huwal-ḥaqq, qul lastu \'alaikum biwakīl'),
(855, 6, 67, 'لِكُلِّ نَبَاٍ مُّسْتَقَرٌّ وَّسَوْفَ تَعْلَمُوْنَ', 'likulli naba`im mustaqarruw wa saufa ta\'lamụn'),
(856, 6, 68, 'وَاِذَا رَاَيْتَ الَّذِيْنَ يَخُوْضُوْنَ فِيْٓ اٰيٰتِنَا فَاَعْرِضْ عَنْهُمْ حَتّٰى يَخُوْضُوْا فِيْ حَدِيْثٍ غَيْرِهٖۗ وَاِمَّا يُنْسِيَنَّكَ الشَّيْطٰنُ فَلَا تَقْعُدْ بَعْدَ الذِّكْرٰى مَعَ الْقَوْمِ الظّٰلِمِيْنَ', 'wa iżā ra`aitallażīna yakhụḍụna fī āyātinā fa a\'riḍ \'an-hum ḥattā yakhụḍụ fī ḥadīṡin gairih, wa immā yunsiyannakasy-syaiṭānu fa lā taq\'ud ba\'daż-żikrā ma\'al-qaumiẓ-ẓālimīn'),
(857, 6, 69, 'وَمَا عَلَى الَّذِيْنَ يَتَّقُوْنَ مِنْ حِسَابِهِمْ مِّنْ شَيْءٍ وَّلٰكِنْ ذِكْرٰى لَعَلَّهُمْ يَتَّقُوْنَ', 'wa mā \'alallażīna yattaqụna min ḥisābihim min syai`iw wa lākin żikrā la\'allahum yattaqụn'),
(858, 6, 70, 'وَذَرِ الَّذِيْنَ اتَّخَذُوْا دِيْنَهُمْ لَعِبًا وَّلَهْوًا وَّغَرَّتْهُمُ الْحَيٰوةُ الدُّنْيَا وَذَكِّرْ بِهٖٓ اَنْ تُبْسَلَ نَفْسٌۢ بِمَا كَسَبَتْۖ لَيْسَ لَهَا مِنْ دُوْنِ اللّٰهِ وَلِيٌّ وَّلَا شَفِيْعٌ ۚوَاِنْ تَعْدِلْ كُلَّ عَدْلٍ لَّا يُؤْخَذْ مِنْهَاۗ اُولٰۤىِٕكَ الَّذِيْنَ اُبْسِلُوْا بِمَا كَسَبُوْا لَهُمْ شَرَابٌ مِّنْ حَمِيْمٍ وَّعَذَابٌ اَلِيْمٌ ۢبِمَا كَانُوْا يَكْفُرُوْنَ', 'wa żarillażīnattakhażụ dīnahum la\'ibaw wa lahwaw wa garrat-humul-ḥayātud-dun-yā wa żakkir bihī an tubsala nafsum bimā kasabat laisa lahā min dụnillāhi waliyyuw wa lā syafī\', wa in ta\'dil kulla \'adlil lā yu`khaż min-hā, ulā`ikallażīna ubsilụ bimā kasabụ lahum syarābum min ḥamīmiw wa \'ażābun alīmum bimā kānụ yakfurụn'),
(859, 6, 71, 'قُلْ اَنَدْعُوْا مِنْ دُوْنِ اللّٰهِ مَا لَا يَنْفَعُنَا وَلَا يَضُرُّنَا وَنُرَدُّ عَلٰٓى اَعْقَابِنَا بَعْدَ اِذْ هَدٰىنَا اللّٰهُ كَالَّذِى اسْتَهْوَتْهُ الشَّيٰطِيْنُ فِى الْاَرْضِ حَيْرَانَ لَهٗٓ اَصْحٰبٌ يَّدْعُوْنَهٗٓ اِلَى الْهُدَى ائْتِنَا ۗ قُلْ اِنَّ هُدَى اللّٰهِ هُوَ الْهُدٰىۗ وَاُمِرْنَا لِنُسْلِمَ لِرَبِّ الْعٰلَمِيْنَۙ', 'qul a nad\'ụ min dụnillāhi mā lā yanfa\'unā wa lā yaḍurrunā wa nuraddu \'alā a\'qābinā ba\'da iż hadānallāhu kallażistahwat-husy-syayāṭīnu fil-arḍi ḥairāna lahū aṣ-ḥābuy yad\'ụnahū ilal-huda`tinā, qul inna hudallāhi huwal-hudā, wa umirnā linuslima lirabbil-\'ālamīn'),
(860, 6, 72, 'وَاَنْ اَقِيْمُوا الصَّلٰوةَ وَاتَّقُوْهُۗ وَهُوَ الَّذِيْٓ اِلَيْهِ تُحْشَرُوْنَ', 'wa an aqīmuṣ-ṣalāta wattaqụh, wa huwallażī ilaihi tuḥsyarụn'),
(861, 6, 73, 'وَهُوَ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّۗ وَيَوْمَ يَقُوْلُ كُنْ فَيَكُوْنُۚ قَوْلُهُ الْحَقُّۗ وَلَهُ الْمُلْكُ يَوْمَ يُنْفَخُ فِى الصُّوْرِۗ عٰلِمُ الْغَيْبِ وَالشَّهَادَةِ وَهُوَ الْحَكِيْمُ الْخَبِيْرُ', 'wa huwallażī khalaqas-samāwāti wal-arḍa bil-ḥaqq, wa yauma yaqụlu kun fa yakụn, qauluhul-ḥaqq, wa lahul-mulku yauma yunfakhu fiṣ-ṣụr, \'ālimul-gaibi wasy-syahādati wa huwal-ḥakīmul-khabīr'),
(862, 6, 74, '۞ وَاِذْ قَالَ اِبْرٰهِيْمُ لِاَبِيْهِ اٰزَرَ اَتَتَّخِذُ اَصْنَامًا اٰلِهَةً ۚاِنِّيْٓ اَرٰىكَ وَقَوْمَكَ فِيْ ضَلٰلٍ مُّبِيْنٍ', 'wa iż qāla ibrāhīmu li`abīhi āzara a tattakhiżu aṣnāman ālihah, innī arāka wa qaumaka fī ḍalālim mubīn'),
(863, 6, 75, 'وَكَذٰلِكَ نُرِيْٓ اِبْرٰهِيْمَ مَلَكُوْتَ السَّمٰوٰتِ وَالْاَرْضِ وَلِيَكُوْنَ مِنَ الْمُوْقِنِيْنَ', 'wa każālika nurī ibrāhīma malakụtas-samāwāti wal-arḍi wa liyakụna minal-mụqinīn'),
(864, 6, 76, 'فَلَمَّا جَنَّ عَلَيْهِ الَّيْلُ رَاٰ كَوْكَبًا ۗقَالَ هٰذَا رَبِّيْۚ فَلَمَّآ اَفَلَ قَالَ لَآ اُحِبُّ الْاٰفِلِيْنَ', 'fa lammā janna \'alaihil-lailu ra`ā kaukabā, qāla hāżā rabbī, fa lammā afala qāla lā uḥibbul-āfilīn'),
(865, 6, 77, 'فَلَمَّا رَاَ الْقَمَرَ بَازِغًا قَالَ هٰذَا رَبِّيْ ۚفَلَمَّآ اَفَلَ قَالَ لَىِٕنْ لَّمْ يَهْدِنِيْ رَبِّيْ لَاَكُوْنَنَّ مِنَ الْقَوْمِ الضَّاۤلِّيْنَ', 'fa lammā ra`al-qamara bāzigang qāla hāżā rabbī, fa lammā afala qāla la`il lam yahdinī rabbī la`akụnanna minal-qaumiḍ-ḍāllīn'),
(866, 6, 78, 'فَلَمَّا رَاَ الشَّمْسَ بَازِغَةً قَالَ هٰذَا رَبِّيْ هٰذَآ اَكْبَرُۚ فَلَمَّآ اَفَلَتْ قَالَ يٰقَوْمِ اِنِّيْ بَرِيْۤءٌ مِّمَّا تُشْرِكُوْنَ', 'fa lammā ra`asy-syamsa bāzigatang qāla hāżā rabbī hāżā akbar, fa lammā afalat qāla yā qaumi innī barī`um mimmā tusyrikụn'),
(867, 6, 79, 'اِنِّيْ وَجَّهْتُ وَجْهِيَ لِلَّذِيْ فَطَرَ السَّمٰوٰتِ وَالْاَرْضَ حَنِيْفًا وَّمَآ اَنَا۠ مِنَ الْمُشْرِكِيْنَۚ', 'innī wajjahtu waj-hiya lillażī faṭaras-samāwāti wal-arḍa ḥanīfaw wa mā ana minal-musyrikīn'),
(868, 6, 80, 'وَحَاۤجَّهٗ قَوْمُهٗ ۗقَالَ اَتُحَاۤجُّوْۤنِّيْ فِى اللّٰهِ وَقَدْ هَدٰىنِۗ وَلَآ اَخَافُ مَا تُشْرِكُوْنَ بِهٖٓ اِلَّآ اَنْ يَّشَاۤءَ رَبِّيْ شَيْـًٔا ۗوَسِعَ رَبِّيْ كُلَّ شَيْءٍ عِلْمًا ۗ اَفَلَا تَتَذَكَّرُوْنَ', 'wa ḥājjahụ qaumuh, qāla a tuḥājjūnnī fillāhi wa qad hadān, wa lā akhāfu mā tusyrikụna bihī illā ay yasyā`a rabbī syai`ā, wasi\'a rabbī kulla syai`in \'ilmā, a fa lā tatażakkarụn'),
(869, 6, 81, 'وَكَيْفَ اَخَافُ مَآ اَشْرَكْتُمْ وَلَا تَخَافُوْنَ اَنَّكُمْ اَشْرَكْتُمْ بِاللّٰهِ مَا لَمْ يُنَزِّلْ بِهٖ عَلَيْكُمْ سُلْطٰنًا ۗفَاَيُّ الْفَرِيْقَيْنِ اَحَقُّ بِالْاَمْنِۚ اِنْ كُنْتُمْ تَعْلَمُوْنَۘ', 'wa kaifa akhāfu mā asyraktum wa lā takhāfụna annakum asyraktum billāhi mā lam yunazzil bihī \'alaikum sulṭānā, fa ayyul-farīqaini aḥaqqu bil-amn, ing kuntum ta\'lamụn'),
(870, 6, 82, 'اَلَّذِيْنَ اٰمَنُوْا وَلَمْ يَلْبِسُوْٓا اِيْمَانَهُمْ بِظُلْمٍ اُولٰۤىِٕكَ لَهُمُ الْاَمْنُ وَهُمْ مُّهْتَدُوْنَ', 'allażīna āmanụ wa lam yalbisū īmānahum biẓulmin ulā`ika lahumul-amnu wa hum muhtadụn'),
(871, 6, 83, 'وَتِلْكَ حُجَّتُنَآ اٰتَيْنٰهَآ اِبْرٰهِيْمَ عَلٰى قَوْمِهٖۗ نَرْفَعُ دَرَجٰتٍ مَّنْ نَّشَاۤءُۗ اِنَّ رَبَّكَ حَكِيْمٌ عَلِيْمٌ', 'wa tilka ḥujjatunā ātaināhā ibrāhīma \'alā qaumih, narfa\'u darajātim man nasyā`, inna rabbaka ḥakīmun \'alīm'),
(872, 6, 84, 'وَوَهَبْنَا لَهٗٓ اِسْحٰقَ وَيَعْقُوْبَۗ كُلًّا هَدَيْنَا وَنُوْحًا هَدَيْنَا مِنْ قَبْلُ وَمِنْ ذُرِّيَّتِهٖ دَاوٗدَ وَسُلَيْمٰنَ وَاَيُّوْبَ وَيُوْسُفَ وَمُوْسٰى وَهٰرُوْنَ ۗوَكَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَۙ', 'wa wahabnā lahū is-ḥāqa wa ya\'qụb, kullan hadainā wa nụḥan hadainā ming qablu wa min żurriyyatihī dāwụda wa sulaimāna wa ayyụba wa yụsufa wa mụsā wa hārụn, wa każālika najzil-muḥsinīn'),
(873, 6, 85, 'وَزَكَرِيَّا وَيَحْيٰى وَعِيْسٰى وَاِلْيَاسَۗ كُلٌّ مِّنَ الصّٰلِحِيْنَۙ', 'wa zakariyyā wa yaḥyā wa \'īsā wa ilyās, kullum minaṣ-ṣāliḥīn'),
(874, 6, 86, 'وَاِسْمٰعِيْلَ وَالْيَسَعَ وَيُوْنُسَ وَلُوْطًاۗ وَكُلًّا فَضَّلْنَا عَلَى الْعٰلَمِيْنَۙ', 'wa ismā\'īla walyasa\'a wa yụnusa wa lụṭā, wa kullan faḍḍalnā \'alal-\'ālamīn'),
(875, 6, 87, 'وَمِنْ اٰبَاۤىِٕهِمْ وَذُرِّيّٰتِهِمْ وَاِخْوَانِهِمْ ۚوَاجْتَبَيْنٰهُمْ وَهَدَيْنٰهُمْ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ', 'wa min ābā`ihim wa żurriyyātihim wa ikhwānihim, wajtabaināhum wa hadaināhum ilā ṣirāṭim mustaqīm'),
(876, 6, 88, 'ذٰلِكَ هُدَى اللّٰهِ يَهْدِيْ بِهٖ مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ ۗوَلَوْ اَشْرَكُوْا لَحَبِطَ عَنْهُمْ مَّا كَانُوْا يَعْمَلُوْنَ', 'żālika hudallāhi yahdī bihī may yasyā`u min \'ibādih, walau asyrakụ laḥabiṭa \'an-hum mā kānụ ya\'malụn'),
(877, 6, 89, 'اُولٰۤىِٕكَ الَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ وَالْحُكْمَ وَالنُّبُوَّةَ ۚفَاِنْ يَّكْفُرْ بِهَا هٰٓؤُلَاۤءِ فَقَدْ وَكَّلْنَا بِهَا قَوْمًا لَّيْسُوْا بِهَا بِكٰفِرِيْنَ', 'ulā`ikallażīna ātaināhumul-kitāba wal-ḥukma wan-nubuwwah, fa iy yakfur bihā hā`ulā`i fa qad wakkalnā bihā qaumal laisụ bihā bikāfirīn'),
(878, 6, 90, 'اُولٰۤىِٕكَ الَّذِيْنَ هَدَى اللّٰهُ فَبِهُدٰىهُمُ اقْتَدِهْۗ قُلْ لَّآ اَسْـَٔلُكُمْ عَلَيْهِ اَجْرًاۗ اِنْ هُوَ اِلَّا ذِكْرٰى لِلْعٰلَمِيْنَ', 'ulā`ikallażīna hadallāhu fa bihudāhumuqtadih, qul lā as`alukum \'alaihi ajrā, in huwa illā żikrā lil-\'ālamīn'),
(879, 6, 91, 'وَمَا قَدَرُوا اللّٰهَ حَقَّ قَدْرِهٖٓ اِذْ قَالُوْا مَآ اَنْزَلَ اللّٰهُ عَلٰى بَشَرٍ مِّنْ شَيْءٍۗ قُلْ مَنْ اَنْزَلَ الْكِتٰبَ الَّذِيْ جَاۤءَ بِهٖ مُوْسٰى نُوْرًا وَّهُدًى لِّلنَّاسِ تَجْعَلُوْنَهٗ قَرَاطِيْسَ تُبْدُوْنَهَا وَتُخْفُوْنَ كَثِيْرًاۚ وَعُلِّمْتُمْ مَّا لَمْ تَعْلَمُوْٓا اَنْتُمْ وَلَآ اٰبَاۤؤُكُمْ ۗقُلِ اللّٰهُ ۙثُمَّ ذَرْهُمْ فِيْ خَوْضِهِمْ يَلْعَبُوْنَ', 'wa mā qadarullāha ḥaqqa qadrihī iż qālụ mā anzalallāhu \'alā basyarim min syaī`, qul man anzalal-kitāballażī jā`a bihī mụsā nụraw wa hudal lin-nāsi taj\'alụnahụ qarāṭīsa tubdụnahā wa tukhfụna kaṡīrā, wa \'ullimtum mā lam ta\'lamū antum wa lā ābā`ukum, qulillāhu ṡumma żar-hum fī khauḍihim yal\'abụn'),
(880, 6, 92, 'وَهٰذَا كِتٰبٌ اَنْزَلْنٰهُ مُبٰرَكٌ مُّصَدِّقُ الَّذِيْ بَيْنَ يَدَيْهِ وَلِتُنْذِرَ اُمَّ الْقُرٰى وَمَنْ حَوْلَهَاۗ وَالَّذِيْنَ يُؤْمِنُوْنَ بِالْاٰخِرَةِ يُؤْمِنُوْنَ بِهٖ وَهُمْ عَلٰى صَلَاتِهِمْ يُحٰفِظُوْنَ', 'wa hāżā kitābun anzalnāhu mubārakum muṣaddiqullażī baina yadaihi wa litunżira ummal-qurā wa man ḥaulahā, wallażīna yu`minụna bil-ākhirati yu`minụna bihī wa hum \'alā ṣalātihim yuḥāfiẓụn'),
(881, 6, 93, 'وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا اَوْ قَالَ اُوْحِيَ اِلَيَّ وَلَمْ يُوْحَ اِلَيْهِ شَيْءٌ وَّمَنْ قَالَ سَاُنْزِلُ مِثْلَ مَآ اَنْزَلَ اللّٰهُ ۗوَلَوْ تَرٰٓى اِذِ الظّٰلِمُوْنَ فِيْ غَمَرٰتِ الْمَوْتِ وَالْمَلٰۤىِٕكَةُ بَاسِطُوْٓا اَيْدِيْهِمْۚ اَخْرِجُوْٓا اَنْفُسَكُمْۗ اَلْيَوْمَ تُجْزَوْنَ عَذَابَ الْهُوْنِ بِمَا كُنْتُمْ تَقُوْلُوْنَ عَلَى اللّٰهِ غَيْرَ الْحَقِّ وَكُنْتُمْ عَنْ اٰيٰتِهٖ تَسْتَكْبِرُوْنَ', 'wa man aẓlamu mim maniftarā \'alallāhi każiban au qāla ụḥiya ilayya wa lam yụḥa ilaihi syai`uw wa mang qāla sa`unzilu miṡla mā anzalallāh, walau tarā iżiẓ-ẓālimụna fī gamarātil-mauti wal-malā`ikatu bāsiṭū aidīhim, akhrijū anfusakum, al-yauma tujzauna \'ażābal-hụni bimā kuntum taqụlụna \'alallāhi gairal-ḥaqqi wa kuntum \'an āyātihī tastakbirụn'),
(882, 6, 94, 'وَلَقَدْ جِئْتُمُوْنَا فُرَادٰى كَمَا خَلَقْنٰكُمْ اَوَّلَ مَرَّةٍ وَّتَرَكْتُمْ مَّا خَوَّلْنٰكُمْ وَرَاۤءَ ظُهُوْرِكُمْۚ وَمَا نَرٰى مَعَكُمْ شُفَعَاۤءَكُمُ الَّذِيْنَ زَعَمْتُمْ اَنَّهُمْ فِيْكُمْ شُرَكٰۤؤُا ۗ لَقَدْ تَّقَطَّعَ بَيْنَكُمْ وَضَلَّ عَنْكُمْ مَّا كُنْتُمْ تَزْعُمُوْنَ', 'wa laqad ji`tumụnā furādā kamā khalaqnākum awwala marratiw wa taraktum mā khawwalnākum warā`a ẓuhụrikum, wa mā narā ma\'akum syufa\'ā`akumullażīna za\'amtum annahum fīkum syurakā`, laqat taqaṭṭa\'a bainakum wa ḍalla \'angkum mā kuntum taz\'umụn'),
(883, 6, 95, '۞ اِنَّ اللّٰهَ فَالِقُ الْحَبِّ وَالنَّوٰىۗ يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَمُخْرِجُ الْمَيِّتِ مِنَ الْحَيِّ ۗذٰلِكُمُ اللّٰهُ فَاَنّٰى تُؤْفَكُوْنَ', 'innallāha fāliqul-ḥabbi wan-nawā, yukhrijul-ḥayya minal-mayyiti wa mukhrijul-mayyiti minal-ḥayy, żālikumullāhu fa annā tu`fakụn'),
(884, 6, 96, 'فَالِقُ الْاِصْبَاحِۚ وَجَعَلَ الَّيْلَ سَكَنًا وَّالشَّمْسَ وَالْقَمَرَ حُسْبَانًا ۗذٰلِكَ تَقْدِيْرُ الْعَزِيْزِ الْعَلِيْمِ', 'fāliqul-iṣbāḥ, wa ja\'alal-laila sakanaw wasy-syamsa wal-qamara ḥusbānā, żālika taqdīrul-\'azīzil-\'alīm'),
(885, 6, 97, 'وَهُوَ الَّذِيْ جَعَلَ لَكُمُ النُّجُوْمَ لِتَهْتَدُوْا بِهَا فِيْ ظُلُمٰتِ الْبَرِّ وَالْبَحْرِۗ قَدْ فَصَّلْنَا الْاٰيٰتِ لِقَوْمٍ يَّعْلَمُوْنَ', 'wa huwallażī ja\'ala lakumun-nujụma litahtadụ bihā fī ẓulumātil-barri wal-baḥr, qad faṣṣalnal-āyāti liqaumiy ya\'lamụn'),
(886, 6, 98, 'وَهُوَ الَّذِيْٓ اَنْشَاَكُمْ مِّنْ نَّفْسٍ وَّاحِدَةٍ فَمُسْتَقَرٌّ وَّمُسْتَوْدَعٌ ۗقَدْ فَصَّلْنَا الْاٰيٰتِ لِقَوْمٍ يَّفْقَهُوْنَ', 'wa huwallażī ansya`akum min nafsiw wāḥidatin fa mustaqarruw wa mustauda\', qad faṣṣalnal-āyāti liqaumiy yafqahụn'),
(887, 6, 99, 'وَهُوَ الَّذِيْٓ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءًۚ فَاَخْرَجْنَا بِهٖ نَبَاتَ كُلِّ شَيْءٍ فَاَخْرَجْنَا مِنْهُ خَضِرًا نُّخْرِجُ مِنْهُ حَبًّا مُّتَرَاكِبًاۚ وَمِنَ النَّخْلِ مِنْ طَلْعِهَا قِنْوَانٌ دَانِيَةٌ وَّجَنّٰتٍ مِّنْ اَعْنَابٍ وَّالزَّيْتُوْنَ وَالرُّمَّانَ مُشْتَبِهًا وَّغَيْرَ مُتَشَابِهٍۗ اُنْظُرُوْٓا اِلٰى ثَمَرِهٖٓ اِذَٓا اَثْمَرَ وَيَنْعِهٖ ۗاِنَّ فِيْ ذٰلِكُمْ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ', 'wa huwallażī anzala minas-samā`i mā`ā, fa akhrajnā bihī nabāta kulli syai`in fa akhrajnā min-hu khaḍiran nukhriju min-hu ḥabbam mutarākibā, wa minan-nakhli min ṭal\'ihā qinwānun dāniyatuw wa jannātim min a\'nābiw waz-zaitụna war-rummāna musytabihaw wa gaira mutasyābih, unẓurū ilā ṡamarihī iżā aṡmara wa yan\'ih, inna fī żālikum la`āyātil liqaumiy yu`minụn'),
(888, 6, 100, 'وَجَعَلُوْا لِلّٰهِ شُرَكَاۤءَ الْجِنَّ وَخَلَقَهُمْ وَخَرَقُوْا لَهٗ بَنِيْنَ وَبَنٰتٍۢ بِغَيْرِ عِلْمٍۗ سُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يَصِفُوْنَ', 'wa ja\'alụ lillāhi syurakā`al-jinna wa khalaqahum wa kharaqụ lahụ banīna wa banātim bigairi \'ilm, sub-ḥānahụ wa ta\'ālā \'ammā yaṣifụn'),
(889, 6, 101, 'بَدِيْعُ السَّمٰوٰتِ وَالْاَرْضِۗ اَنّٰى يَكُوْنُ لَهٗ وَلَدٌ وَّلَمْ تَكُنْ لَّهٗ صَاحِبَةٌ ۗوَخَلَقَ كُلَّ شَيْءٍۚ وَهُوَ بِكُلِّ شَيْءٍ عَلِيْمٌ', 'badī\'us-samāwāti wal-arḍ, annā yakụnu lahụ waladuw wa lam takul lahụ ṣāḥibah, wa khalaqa kulla syaī`, wa huwa bikulli syai`in \'alīm'),
(890, 6, 102, 'ذٰلِكُمُ اللّٰهُ رَبُّكُمْۚ لَآ اِلٰهَ اِلَّا هُوَۚ خَالِقُ كُلِّ شَيْءٍ فَاعْبُدُوْهُ ۚوَهُوَ عَلٰى كُلِّ شَيْءٍ وَّكِيْلٌ', 'żālikumullāhu rabbukum, lā ilāha illā huw, khāliqu kulli syai`in fa\'budụh, wa huwa \'alā kulli syai`iw wakīl'),
(891, 6, 103, 'لَا تُدْرِكُهُ الْاَبْصَارُ وَهُوَ يُدْرِكُ الْاَبْصَارَۚ وَهُوَ اللَّطِيْفُ الْخَبِيْرُ', 'lā tudrikuhul-abṣāru wa huwa yudrikul-abṣār, wa huwal-laṭīful-khabīr'),
(892, 6, 104, 'قَدْ جَاۤءَكُمْ بَصَاۤىِٕرُ مِنْ رَّبِّكُمْۚ فَمَنْ اَبْصَرَ فَلِنَفْسِهٖۚ وَمَنْ عَمِيَ فَعَلَيْهَاۗ وَمَآ اَنَا۠ عَلَيْكُمْ بِحَفِيْظٍ', 'qad jā`akum baṣā`iru mir rabbikum, fa man abṣara fa linafsih, wa man \'amiya fa \'alaihā, wa mā ana \'alaikum biḥafīẓ'),
(893, 6, 105, 'وَكَذٰلِكَ نُصَرِّفُ الْاٰيٰتِ وَلِيَقُوْلُوْا دَرَسْتَ وَلِنُبَيِّنَهٗ لِقَوْمٍ يَّعْلَمُوْنَ', 'wa każālika nuṣarriful-āyāti wa liyaqụlụ darasta wa linubayyinahụ liqaumiy ya\'lamụn'),
(894, 6, 106, 'اِتَّبِعْ مَآ اُوْحِيَ اِلَيْكَ مِنْ رَّبِّكَۚ لَآ اِلٰهَ اِلَّا هُوَۚ وَاَعْرِضْ عَنِ الْمُشْرِكِيْنَ', 'ittabi\' mā ụḥiya ilaika mir rabbik, lā ilāha illā huw, wa a\'riḍ \'anil-musyrikīn'),
(895, 6, 107, 'وَلَوْ شَاۤءَ اللّٰهُ مَآ اَشْرَكُوْاۗ وَمَا جَعَلْنٰكَ عَلَيْهِمْ حَفِيْظًاۚ وَمَآ اَنْتَ عَلَيْهِمْ بِوَكِيْلٍ', 'walau syā`allāhu mā asyrakụ, wa mā ja\'alnāka \'alaihim ḥafīẓā, wa mā anta \'alaihim biwakīl'),
(896, 6, 108, 'وَلَا تَسُبُّوا الَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ فَيَسُبُّوا اللّٰهَ عَدْوًاۢ بِغَيْرِ عِلْمٍۗ كَذٰلِكَ زَيَّنَّا لِكُلِّ اُمَّةٍ عَمَلَهُمْۖ ثُمَّ اِلٰى رَبِّهِمْ مَّرْجِعُهُمْ فَيُنَبِّئُهُمْ بِمَا كَانُوْا يَعْمَلُوْنَ', 'wa lā tasubbullażīna yad\'ụna min dụnillāhi fa yasubbullāha \'adwam bigairi \'ilm, każālika zayyannā likulli ummatin \'amalahum ṡumma ilā rabbihim marji\'uhum fa yunabbi`uhum bimā kānụ ya\'malụn'),
(897, 6, 109, 'وَاَقْسَمُوْا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْ لَىِٕنْ جَاۤءَتْهُمْ اٰيَةٌ لَّيُؤْمِنُنَّ بِهَاۗ قُلْ اِنَّمَا الْاٰيٰتُ عِنْدَ اللّٰهِ وَمَا يُشْعِرُكُمْ اَنَّهَآ اِذَا جَاۤءَتْ لَا يُؤْمِنُوْنَ', 'wa aqsamụ billāhi jahda aimānihim la`in jā`at-hum āyatul layu`minunna bihā, qul innamal-āyātu \'indallāhi wa mā yusy\'irukum annahā iżā jā`at lā yu`minụn'),
(898, 6, 110, 'وَنُقَلِّبُ اَفْـِٕدَتَهُمْ وَاَبْصَارَهُمْ كَمَا لَمْ يُؤْمِنُوْا بِهٖٓ اَوَّلَ مَرَّةٍ وَّنَذَرُهُمْ فِيْ طُغْيَانِهِمْ يَعْمَهُوْنَ ۔', 'wa nuqallibu af`idatahum wa abṣārahum kamā lam yu`minụ bihī awwala marratiw wa nażaruhum fī ṭugyānihim ya\'mahụn'),
(899, 6, 111, '۞ وَلَوْ اَنَّنَا نَزَّلْنَآ اِلَيْهِمُ الْمَلٰۤىِٕكَةَ وَكَلَّمَهُمُ الْمَوْتٰى وَحَشَرْنَا عَلَيْهِمْ كُلَّ شَيْءٍ قُبُلًا مَّا كَانُوْا لِيُؤْمِنُوْٓا اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ وَلٰكِنَّ اَكْثَرَهُمْ يَجْهَلُوْنَ', 'walau annanā nazzalnā ilaihimul-malā`ikata wa kallamahumul-mautā wa ḥasyarnā \'alaihim kulla syai`ing qubulam mā kānụ liyu`minū illā ay yasyā`allāhu wa lākinna akṡarahum yaj-halụn'),
(900, 6, 112, 'وَكَذٰلِكَ جَعَلْنَا لِكُلِّ نَبِيٍّ عَدُوًّا شَيٰطِيْنَ الْاِنْسِ وَالْجِنِّ يُوْحِيْ بَعْضُهُمْ اِلٰى بَعْضٍ زُخْرُفَ الْقَوْلِ غُرُوْرًا ۗوَلَوْ شَاۤءَ رَبُّكَ مَا فَعَلُوْهُ فَذَرْهُمْ وَمَا يَفْتَرُوْنَ', 'wa każālika ja\'alnā likulli nabiyyin \'aduwwan syayāṭīnal-insi wal-jinni yụḥī ba\'ḍuhum ilā ba\'ḍin zukhrufal-qauli gurụrā, walau syā`a rabbuka mā fa\'alụhu fa żar-hum wa mā yaftarụn'),
(901, 6, 113, 'وَلِتَصْغٰٓى اِلَيْهِ اَفْـِٕدَةُ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ وَلِيَرْضَوْهُ وَلِيَقْتَرِفُوْا مَا هُمْ مُّقْتَرِفُوْنَ', 'wa litaṣgā ilaihi af`idatullażīna lā yu`minụna bil-ākhirati wa liyarḍauhu wa liyaqtarifụ mā hum muqtarifụn'),
(902, 6, 114, 'اَفَغَيْرَ اللّٰهِ اَبْتَغِيْ حَكَمًا وَّهُوَ الَّذِيْٓ اَنْزَلَ اِلَيْكُمُ الْكِتٰبَ مُفَصَّلًا ۗوَالَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يَعْلَمُوْنَ اَنَّهٗ مُنَزَّلٌ مِّنْ رَّبِّكَ بِالْحَقِّ فَلَا تَكُوْنَنَّ مِنَ الْمُمْتَرِيْنَ', 'a fagairallāhi abtagī ḥakamaw wa huwallażī anzala ilaikumul-kitāba mufaṣṣalā, wallażīna ātaināhumul-kitāba ya\'lamụna annahụ munazzalum mir rabbika bil-ḥaqqi fa lā takụnanna minal-mumtarīn'),
(903, 6, 115, 'وَتَمَّتْ كَلِمَتُ رَبِّكَ صِدْقًا وَّعَدْلًاۗ لَا مُبَدِّلَ لِكَلِمٰتِهٖ ۚوَهُوَ السَّمِيْعُ الْعَلِيْمُ', 'wa tammat kalimatu rabbika ṣidqaw wa \'adlā, lā mubaddila likalimātih, wa huwas-samī\'ul-\'alīm'),
(904, 6, 116, 'وَاِنْ تُطِعْ اَكْثَرَ مَنْ فِى الْاَرْضِ يُضِلُّوْكَ عَنْ سَبِيْلِ اللّٰهِ ۗاِنْ يَّتَّبِعُوْنَ اِلَّا الظَّنَّ وَاِنْ هُمْ اِلَّا يَخْرُصُوْنَ', 'wa in tuṭi\' akṡara man fil-arḍi yuḍillụka \'an sabīlillāh, iy yattabi\'ụna illaẓ-ẓanna wa in hum illā yakhruṣụn'),
(905, 6, 117, 'اِنَّ رَبَّكَ هُوَ اَعْلَمُ مَنْ يَّضِلُّ عَنْ سَبِيْلِهٖۚ وَهُوَ اَعْلَمُ بِالْمُهْتَدِيْنَ', 'inna rabbaka huwa a\'lamu may yaḍillu \'an sabīlih, wa huwa a\'lamu bil-muhtadīn'),
(906, 6, 118, 'فَكُلُوْا مِمَّا ذُكِرَ اسْمُ اللّٰهِ عَلَيْهِ اِنْ كُنْتُمْ بِاٰيٰتِهٖ مُؤْمِنِيْنَ', 'fa kulụ mimmā żukirasmullāhi \'alaihi ing kuntum bi`āyātihī mu`minīn'),
(907, 6, 119, 'وَمَا لَكُمْ اَلَّا تَأْكُلُوْا مِمَّا ذُكِرَ اسْمُ اللّٰهِ عَلَيْهِ وَقَدْ فَصَّلَ لَكُمْ مَّا حَرَّمَ عَلَيْكُمْ اِلَّا مَا اضْطُرِرْتُمْ اِلَيْهِ ۗوَاِنَّ كَثِيرًا لَّيُضِلُّوْنَ بِاَهْوَاۤىِٕهِمْ بِغَيْرِ عِلْمٍ ۗاِنَّ رَبَّكَ هُوَ اَعْلَمُ بِالْمُعْتَدِيْنَ', 'wa mā lakum allā ta`kulụ mimmā żukirasmullāhi \'alaihi wa qad faṣṣala lakum mā ḥarrama \'alaikum illā maḍṭurirtum ilaīh, wa inna kaṡīral layuḍillụna bi`ahwā`ihim bigairi \'ilm, inna rabbaka huwa a\'lamu bil-mu\'tadīn'),
(908, 6, 120, 'وَذَرُوْا ظَاهِرَ الْاِثْمِ وَبَاطِنَهٗ ۗاِنَّ الَّذِيْنَ يَكْسِبُوْنَ الْاِثْمَ سَيُجْزَوْنَ بِمَا كَانُوْا يَقْتَرِفُوْنَ', 'wa żarụ ẓāhiral-iṡmi wa bāṭinah, innallażīna yaksibụnal-iṡma sayujzauna bimā kānụ yaqtarifụn'),
(909, 6, 121, 'وَلَا تَأْكُلُوْا مِمَّا لَمْ يُذْكَرِ اسْمُ اللّٰهِ عَلَيْهِ وَاِنَّهٗ لَفِسْقٌۗ وَاِنَّ الشَّيٰطِيْنَ لَيُوْحُوْنَ اِلٰٓى اَوْلِيَاۤىِٕهِمْ لِيُجَادِلُوْكُمْ ۚوَاِنْ اَطَعْتُمُوْهُمْ اِنَّكُمْ لَمُشْرِكُوْنَ', 'wa lā ta`kulụ mimmā lam yużkarismullāhi \'alaihi wa innahụ lafisq, wa innasy-syayāṭīna layụḥụna ilā auliyā`ihim liyujādilụkum, wa in aṭa\'tumụhum innakum lamusyrikụn'),
(910, 6, 122, 'اَوَمَنْ كَانَ مَيْتًا فَاَحْيَيْنٰهُ وَجَعَلْنَا لَهٗ نُوْرًا يَّمْشِيْ بِهٖ فِى النَّاسِ كَمَنْ مَّثَلُهٗ فِى الظُّلُمٰتِ لَيْسَ بِخَارِجٍ مِّنْهَاۗ كَذٰلِكَ زُيِّنَ لِلْكٰفِرِيْنَ مَا كَانُوْا يَعْمَلُوْنَ', 'a wa mang kāna maitan fa aḥyaināhu wa ja\'alnā lahụ nụray yamsyī bihī fin-nāsi kamam maṡaluhụ fiẓ-ẓulumāti laisa bikhārijim min-hā, każālika zuyyina lil-kāfirīna mā kānụ ya\'malụn'),
(911, 6, 123, 'وَكَذٰلِكَ جَعَلْنَا فِيْ كُلِّ قَرْيَةٍ اَكٰبِرَ مُجْرِمِيْهَا لِيَمْكُرُوْا فِيْهَاۗ وَمَا يَمْكُرُوْنَ اِلَّا بِاَنْفُسِهِمْ وَمَا يَشْعُرُوْنَ', 'wa każālika ja\'alnā fī kulli qaryatin akābira mujrimīhā liyamkurụ fīhā, wa mā yamkurụna illā bi`anfusihim wa mā yasy\'urụn'),
(912, 6, 124, 'وَاِذَا جَاۤءَتْهُمْ اٰيَةٌ قَالُوْا لَنْ نُّؤْمِنَ حَتّٰى نُؤْتٰى مِثْلَ مَآ اُوْتِيَ رُسُلُ اللّٰهِ ۘ اَللّٰهُ اَعْلَمُ حَيْثُ يَجْعَلُ رِسٰلَتَهٗۗ سَيُصِيْبُ الَّذِيْنَ اَجْرَمُوْا صَغَارٌ عِنْدَ اللّٰهِ وَعَذَابٌ شَدِيْدٌۢ بِمَا كَانُوْا يَمْكُرُوْنَ', 'wa iżā jā`at-hum āyatung qālụ lan nu`mina ḥattā nu`tā miṡla mā ụtiya rusulullāh, allāhu a\'lamu ḥaiṡu yaj\'alu risālatah, sayuṣībullażīna ajramụ ṣagārun \'indallāhi wa \'ażābun syadīdum bimā kānụ yamkurụn'),
(913, 6, 125, 'فَمَنْ يُّرِدِ اللّٰهُ اَنْ يَّهْدِيَهٗ يَشْرَحْ صَدْرَهٗ لِلْاِسْلَامِۚ وَمَنْ يُّرِدْ اَنْ يُّضِلَّهٗ يَجْعَلْ صَدْرَهٗ ضَيِّقًا حَرَجًا كَاَنَّمَا يَصَّعَّدُ فِى السَّمَاۤءِۗ كَذٰلِكَ يَجْعَلُ اللّٰهُ الرِّجْسَ عَلَى الَّذِيْنَ لَا يُؤْمِنُوْنَ', 'fa may yuridillāhu ay yahdiyahụ yasyraḥ ṣadrahụ lil-islām, wa may yurid ay yuḍillahụ yaj\'al ṣadrahụ ḍayyiqan ḥarajang ka`annamā yaṣṣa\'\'adu fis-samā`, każālika yaj\'alullāhur-rijsa \'alallażīna lā yu`minụn'),
(914, 6, 126, 'وَهٰذَا صِرَاطُ رَبِّكَ مُسْتَقِيْمًاۗ قَدْ فَصَّلْنَا الْاٰيٰتِ لِقَوْمٍ يَّذَّكَّرُوْنَ', 'wa hāżā ṣirāṭu rabbika mustaqīmā, qad faṣṣalnal-āyāti liqaumiy yażżakkarụn'),
(915, 6, 127, '۞ لَهُمْ دَارُ السَّلٰمِ عِنْدَ رَبِّهِمْ وَهُوَ وَلِيُّهُمْ بِمَا كَانُوْا يَعْمَلُوْنَ', 'lahum dārus-salāmi \'inda rabbihim wa huwa waliyyuhum bimā kānụ ya\'malụn'),
(916, 6, 128, 'وَيَوْمَ يَحْشُرُهُمْ جَمِيْعًاۚ يٰمَعْشَرَ الْجِنِّ قَدِ اسْتَكْثَرْتُمْ مِّنَ الْاِنْسِ ۚوَقَالَ اَوْلِيَاۤؤُهُمْ مِّنَ الْاِنْسِ رَبَّنَا اسْتَمْتَعَ بَعْضُنَا بِبَعْضٍ وَّبَلَغْنَآ اَجَلَنَا الَّذِيْٓ اَجَّلْتَ لَنَا ۗقَالَ النَّارُ مَثْوٰىكُمْ خٰلِدِيْنَ فِيْهَآ اِلَّا مَا شَاۤءَ اللّٰهُ ۗاِنَّ رَبَّكَ حَكِيْمٌ عَلِيْمٌ', 'wa yauma yaḥsyuruhum jamī\'ā, yā ma\'syaral-jinni qadistakṡartum minal-ins, wa qāla auliyā`uhum minal-insi rabbanastamta\'a ba\'ḍunā biba\'ḍiw wa balagnā ajalanallażī ajjalta lanā, qālan-nāru maṡwākum khālidīna fīhā illā mā syā`allāh, inna rabbaka ḥakīmun \'alīm'),
(917, 6, 129, 'وَكَذٰلِكَ نُوَلِّيْ بَعْضَ الظّٰلِمِيْنَ بَعْضًاۢ بِمَا كَانُوْا يَكْسِبُوْنَ', 'wa każālika nuwallī ba\'ḍaẓ-ẓālimīna ba\'ḍam bimā kānụ yaksibụn'),
(918, 6, 130, 'يٰمَعْشَرَ الْجِنِّ وَالْاِنْسِ اَلَمْ يَأْتِكُمْ رُسُلٌ مِّنْكُمْ يَقُصُّوْنَ عَلَيْكُمْ اٰيٰتِيْ وَيُنْذِرُوْنَكُمْ لِقَاۤءَ يَوْمِكُمْ هٰذَاۗ قَالُوْا شَهِدْنَا عَلٰٓى اَنْفُسِنَا وَغَرَّتْهُمُ الْحَيٰوةُ الدُّنْيَا وَشَهِدُوْا عَلٰٓى اَنْفُسِهِمْ اَنَّهُمْ كَانُوْا كٰفِرِيْنَ', 'yā ma\'syaral-jinni wal-insi a lam ya`tikum rusulum mingkum yaquṣṣụna \'alaikum āyātī wa yunżirụnakum liqā`a yaumikum hāżā, qālụ syahidnā \'alā anfusinā wa garrat-humul-ḥayātud-dun-yā wa syahidụ \'alā anfusihim annahum kānụ kāfirīn'),
(919, 6, 131, 'ذٰلِكَ اَنْ لَّمْ يَكُنْ رَّبُّكَ مُهْلِكَ الْقُرٰى بِظُلْمٍ وَّاَهْلُهَا غٰفِلُوْنَ', 'żālika al lam yakur rabbuka muhlikal-qurā biẓulmiw wa ahluhā gāfilụn'),
(920, 6, 132, 'وَلِكُلٍّ دَرَجٰتٌ مِّمَّا عَمِلُوْاۗ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا يَعْمَلُوْنَ', 'wa likullin darajātum mimmā \'amilụ, wa mā rabbuka bigāfilin \'ammā ya\'malụn'),
(921, 6, 133, 'وَرَبُّكَ الْغَنِيُّ ذُو الرَّحْمَةِ ۗاِنْ يَّشَأْ يُذْهِبْكُمْ وَيَسْتَخْلِفْ مِنْۢ بَعْدِكُمْ مَّا يَشَاۤءُ كَمَآ اَنْشَاَكُمْ مِّنْ ذُرِّيَّةِ قَوْمٍ اٰخَرِيْنَ', 'wa rabbukal-ganiyyu żur-raḥmah, iy yasya` yuż-hibkum wa yastakhlif mim ba\'dikum mā yasyā`u kamā ansya`akum min żurriyyati qaumin ākharīn'),
(922, 6, 134, 'اِنَّ مَا تُوْعَدُوْنَ لَاٰتٍۙ وَّمَآ اَنْتُمْ بِمُعْجِزِيْنَ', 'inna mā tụ\'adụna la`ātiw wa mā antum bimu\'jizīn'),
(923, 6, 135, 'قُلْ يٰقَوْمِ اعْمَلُوْا عَلٰى مَكَانَتِكُمْ اِنِّيْ عَامِلٌۚ فَسَوْفَ تَعْلَمُوْنَۙ مَنْ تَكُوْنُ لَهٗ عَاقِبَةُ الدَّارِۗ اِنَّهٗ لَا يُفْلِحُ الظّٰلِمُوْنَ', 'qul yā qaumi\'malụ \'alā makānatikum innī \'āmil, fa saufa ta\'lamụna man takụnu lahụ \'āqibatud-dār, innahụ lā yufliḥuẓ-ẓālimụn'),
(924, 6, 136, 'وَجَعَلُوْا لِلّٰهِ مِمَّا ذَرَاَ مِنَ الْحَرْثِ وَالْاَنْعَامِ نَصِيْبًا فَقَالُوْا هٰذَا لِلّٰهِ بِزَعْمِهِمْ وَهٰذَا لِشُرَكَاۤىِٕنَاۚ فَمَا كَانَ لِشُرَكَاۤىِٕهِمْ فَلَا يَصِلُ اِلَى اللّٰهِ ۚوَمَا كَانَ لِلّٰهِ فَهُوَ يَصِلُ اِلٰى شُرَكَاۤىِٕهِمْۗ سَاۤءَ مَا يَحْكُمُوْنَ', 'wa ja\'alụ lillāhi mimmā żara`a minal-ḥarṡi wal-an\'āmi naṣīban fa qālụ hāżā lillāhi biza\'mihim wa hāżā lisyurakā`inā, fa mā kāna lisyurakā`ihim fa lā yaṣilu ilallāh, wa mā kāna lillāhi fa huwa yaṣilu ilā syurakā`ihim, sā`a mā yaḥkumụn'),
(925, 6, 137, 'وَكَذٰلِكَ زَيَّنَ لِكَثِيْرٍ مِّنَ الْمُشْرِكِيْنَ قَتْلَ اَوْلَادِهِمْ شُرَكَاۤؤُهُمْ لِيُرْدُوْهُمْ وَلِيَلْبِسُوْا عَلَيْهِمْ دِيْنَهُمْۗ وَلَوْ شَاۤءَ اللّٰهُ مَا فَعَلُوْهُ فَذَرْهُمْ وَمَا يَفْتَرُوْنَ', 'wa każālika zayyana likaṡīrim minal-musyrikīna qatla aulādihim syurakā`uhum liyurdụhum wa liyalbisụ \'alaihim dīnahum, walau syā`allāhu mā fa\'alụhu fa żar-hum wa mā yaftarụn'),
(926, 6, 138, 'وَقَالُوْا هٰذِهٖٓ اَنْعَامٌ وَّحَرْثٌ حِجْرٌ لَّا يَطْعَمُهَآ اِلَّا مَنْ نَّشَاۤءُ بِزَعْمِهِمْ وَاَنْعَامٌ حُرِّمَتْ ظُهُوْرُهَا وَاَنْعَامٌ لَّا يَذْكُرُوْنَ اسْمَ اللّٰهِ عَلَيْهَا افْتِرَاۤءً عَلَيْهِۗ سَيَجْزِيْهِمْ بِمَا كَانُوْا يَفْتَرُوْنَ', 'wa qālụ hāżihī an\'āmuw wa ḥarṡun ḥijrul lā yaṭ\'amuhā illā man nasyā`u biza\'mihim wa an\'āmun ḥurrimat ẓuhụruhā wa an\'āmul lā yażkurụnasmallāhi \'alaihaftirā`an \'alaīh, sayajzīhim bimā kānụ yaftarụn'),
(927, 6, 139, 'وَقَالُوْا مَا فِيْ بُطُوْنِ هٰذِهِ الْاَنْعَامِ خَالِصَةٌ لِّذُكُوْرِنَا وَمُحَرَّمٌ عَلٰٓى اَزْوَاجِنَاۚ وَاِنْ يَّكُنْ مَّيْتَةً فَهُمْ فِيْهِ شُرَكَاۤءُ ۗسَيَجْزِيْهِمْ وَصْفَهُمْۗ اِنَّهٗ حَكِيْمٌ عَلِيْمٌ', 'wa qālụ mā fī buṭụni hāżihil-an\'āmi khāliṣatul liżukụrinā wa muḥarramun \'alā azwājinā, wa iy yakum maitatan fa hum fīhi syurakā`, sayajzīhim waṣfahum, innahụ ḥakīmun \'alīm'),
(928, 6, 140, 'قَدْ خَسِرَ الَّذِيْنَ قَتَلُوْٓا اَوْلَادَهُمْ سَفَهًاۢ بِغَيْرِ عِلْمٍ وَّحَرَّمُوْا مَا رَزَقَهُمُ اللّٰهُ افْتِرَاۤءً عَلَى اللّٰهِ ۗقَدْ ضَلُّوْا وَمَا كَانُوْا مُهْتَدِيْنَ', 'qad khasirallażīna qatalū aulādahum safaham bigairi \'ilmiw wa ḥarramụ mā razaqahumullāhuftirā`an \'alallāh, qad ḍallụ wa mā kānụ muhtadīn'),
(929, 6, 141, '۞ وَهُوَ الَّذِيْٓ اَنْشَاَ جَنّٰتٍ مَّعْرُوْشٰتٍ وَّغَيْرَ مَعْرُوْشٰتٍ وَّالنَّخْلَ وَالزَّرْعَ مُخْتَلِفًا اُكُلُهٗ وَالزَّيْتُوْنَ وَالرُّمَّانَ مُتَشَابِهًا وَّغَيْرَ مُتَشَابِهٍۗ كُلُوْا مِنْ ثَمَرِهٖٓ اِذَآ اَثْمَرَ وَاٰتُوْا حَقَّهٗ يَوْمَ حَصَادِهٖۖ وَلَا تُسْرِفُوْا ۗاِنَّهٗ لَا يُحِبُّ الْمُسْرِفِيْنَۙ', 'wa huwallażī ansya`a jannātim ma\'rụsyātiw wa gaira ma\'rụsyātiw wan-nakhla waz-zar\'a mukhtalifan ukuluhụ waz-zaitụna war-rummāna mutasyābihaw wa gaira mutasyābih, kulụ min ṡamarihī iżā aṡmara wa ātụ ḥaqqahụ yauma ḥaṣādihī wa lā tusrifụ, innahụ lā yuḥibbul-musrifīn'),
(930, 6, 142, 'وَمِنَ الْاَنْعَامِ حَمُوْلَةً وَّفَرْشًا ۗ كُلُوْا مِمَّا رَزَقَكُمُ اللّٰهُ وَلَا تَتَّبِعُوْا خُطُوٰتِ الشَّيْطٰنِۗ اِنَّهٗ لَكُمْ عَدُوٌّ مُّبِيْنٌۙ', 'wa minal-an\'āmi ḥamụlataw wa farsyā, kulụ mimmā razaqakumullāhu wa lā tattabi\'ụ khuṭuwātisy-syaiṭān, innahụ lakum \'aduwwum mubīn'),
(931, 6, 143, 'ثَمٰنِيَةَ اَزْوَاجٍۚ مِنَ الضَّأْنِ اثْنَيْنِ وَمِنَ الْمَعْزِ اثْنَيْنِۗ قُلْ ءٰۤالذَّكَرَيْنِ حَرَّمَ اَمِ الْاُنْثَيَيْنِ اَمَّا اشْتَمَلَتْ عَلَيْهِ اَرْحَامُ الْاُنْثَيَيْنِۗ نَبِّئُوْنِيْ بِعِلْمٍ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'ṡamāniyata azwāj, minaḍ-ḍa`niṡnaini wa minal-ma\'ziṡnaīn, qul āż-żakaraini ḥarrama amil-unṡayaini ammasytamalat \'alaihi ar-ḥāmul-unṡayaīn, nabbi`ụnī bi\'ilmin ing kuntum ṣādiqīn'),
(932, 6, 144, 'وَمِنَ الْاِبِلِ اثْنَيْنِ وَمِنَ الْبَقَرِ اثْنَيْنِۗ قُلْ ءٰۤالذَّكَرَيْنِ حَرَّمَ اَمِ الْاُنْثَيَيْنِ اَمَّا اشْتَمَلَتْ عَلَيْهِ اَرْحَامُ الْاُنْثَيَيْنِۗ اَمْ كُنْتُمْ شُهَدَاۤءَ اِذْ وَصّٰىكُمُ اللّٰهُ بِهٰذَاۚ فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا لِّيُضِلَّ النَّاسَ بِغَيْرِ عِلْمٍۗ اِنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ', 'wa minal-ibiliṡnaini wa minal-baqariṡnaīn, qul āż-żakaraini ḥarrama amil-unṡayaini ammasytamalat \'alaihi ar-ḥāmul-unṡayaīn, am kuntum syuhadā`a iż waṣṣākumullāhu bihāżā, fa man aẓlamu mim maniftarā \'alallāhi każibal liyuḍillan-nāsa bigairi \'ilm, innallāha lā yahdil-qaumaẓ-ẓālimīn'),
(933, 6, 145, 'قُلْ لَّآ اَجِدُ فِيْ مَآ اُوْحِيَ اِلَيَّ مُحَرَّمًا عَلٰى طَاعِمٍ يَّطْعَمُهٗٓ اِلَّآ اَنْ يَّكُوْنَ مَيْتَةً اَوْ دَمًا مَّسْفُوْحًا اَوْ لَحْمَ خِنْزِيْرٍ فَاِنَّهٗ رِجْسٌ اَوْ فِسْقًا اُهِلَّ لِغَيْرِ اللّٰهِ بِهٖۚ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَّلَا عَادٍ فَاِنَّ رَبَّكَ غَفُوْرٌ رَّحِيْمٌ', 'qul lā ajidu fī mā ụḥiya ilayya muḥarraman \'alā ṭā\'imiy yaṭ\'amuhū illā ay yakụna maitatan au damam masfụḥan au laḥma khinzīrin fa innahụ rijsun au fisqan uhilla ligairillāhi bih, fa maniḍṭurra gaira bāgiw wa lā \'ādin fa inna rabbaka gafụrur raḥīm'),
(934, 6, 146, 'وَعَلَى الَّذِيْنَ هَادُوْا حَرَّمْنَا كُلَّ ذِيْ ظُفُرٍۚ وَمِنَ الْبَقَرِ وَالْغَنَمِ حَرَّمْنَا عَلَيْهِمْ شُحُوْمَهُمَآ اِلَّا مَا حَمَلَتْ ظُهُوْرُهُمَآ اَوِ الْحَوَايَآ اَوْ مَا اخْتَلَطَ بِعَظْمٍۗ ذٰلِكَ جَزَيْنٰهُمْ بِبَغْيِهِمْۚ وَاِنَّا لَصٰدِقُوْنَ', 'wa \'alallażīna hādụ ḥarramnā kulla żī ẓufur, wa minal-baqari wal-ganami ḥarramnā \'alaihim syuḥụmahumā illā mā ḥamalat ẓuhụruhumā awil-ḥawāyā au makhtalaṭa bi\'aẓm, żālika jazaināhum bibagyihim, wa innā laṣādiqụn'),
(935, 6, 147, 'فَاِنْ كَذَّبُوْكَ فَقُلْ رَّبُّكُمْ ذُوْ رَحْمَةٍ وَّاسِعَةٍۚ وَلَا يُرَدُّ بَأْسُهٗ عَنِ الْقَوْمِ الْمُجْرِمِيْنَ', 'fa ing każżabụka fa qur rabbukum żụ raḥmatiw wāsi\'ah, wa lā yuraddu ba`suhụ \'anil-qaumil-mujrimīn'),
(936, 6, 148, 'سَيَقُوْلُ الَّذِيْنَ اَشْرَكُوْا لَوْ شَاۤءَ اللّٰهُ مَآ اَشْرَكْنَا وَلَآ اٰبَاۤؤُنَا وَلَا حَرَّمْنَا مِنْ شَيْءٍۗ كَذٰلِكَ كَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْ حَتّٰى ذَاقُوْا بَأْسَنَاۗ قُلْ هَلْ عِنْدَكُمْ مِّنْ عِلْمٍ فَتُخْرِجُوْهُ لَنَاۗ اِنْ تَتَّبِعُوْنَ اِلَّا الظَّنَّ وَاِنْ اَنْتُمْ اِلَّا تَخْرُصُوْنَ', 'sayaqụlullażīna asyrakụ lau syā`allāhu mā asyraknā wa lā ābā`unā wa lā ḥarramnā min syaī`, każālika każżaballażīna ming qablihim ḥattā żāqụ ba`sanā, qul hal \'indakum min \'ilmin fa tukhrijụhu lanā, in tattabi\'ụna illaẓ-ẓanna wa in antum illā takhruṣụn'),
(937, 6, 149, 'قُلْ فَلِلّٰهِ الْحُجَّةُ الْبَالِغَةُۚ فَلَوْ شَاۤءَ لَهَدٰىكُمْ اَجْمَعِيْنَ', 'qul falillāhil-ḥujjatul-bāligah, falau syā`a lahadākum ajma\'īn'),
(938, 6, 150, 'قُلْ هَلُمَّ شُهَدَاۤءَكُمُ الَّذِيْنَ يَشْهَدُوْنَ اَنَّ اللّٰهَ حَرَّمَ هٰذَاۚ فَاِنْ شَهِدُوْا فَلَا تَشْهَدْ مَعَهُمْۚ وَلَا تَتَّبِعْ اَهْوَاۤءَ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَالَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ وَهُمْ بِرَبِّهِمْ يَعْدِلُوْنَ', 'qul halumma syuhadā`akumullażīna yasy-hadụna annallāha ḥarrama hāżā, fa in syahidụ fa lā tasy-had ma\'ahum, wa lā tattabi\' ahwā`allażīna każżabụ bi`āyātinā wallażīna lā yu`minụna bil-ākhirati wa hum birabbihim ya\'dilụn'),
(939, 6, 151, '۞ قُلْ تَعَالَوْا اَتْلُ مَا حَرَّمَ رَبُّكُمْ عَلَيْكُمْ اَلَّا تُشْرِكُوْا بِهٖ شَيْـًٔا وَّبِالْوَالِدَيْنِ اِحْسَانًاۚ وَلَا تَقْتُلُوْٓا اَوْلَادَكُمْ مِّنْ اِمْلَاقٍۗ نَحْنُ نَرْزُقُكُمْ وَاِيَّاهُمْ ۚوَلَا تَقْرَبُوا الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَۚ وَلَا تَقْتُلُوا النَّفْسَ الَّتِيْ حَرَّمَ اللّٰهُ اِلَّا بِالْحَقِّۗ ذٰلِكُمْ وَصّٰىكُمْ بِهٖ لَعَلَّكُمْ تَعْقِلُوْنَ', 'qul ta\'ālau atlu mā ḥarrama rabbukum \'alaikum allā tusyrikụ bihī syai`aw wa bil-wālidaini iḥsānā, wa lā taqtulū aulādakum min imlāq, naḥnu narzuqukum wa iyyāhum, wa lā taqrabul-fawāḥisya mā ẓahara min-hā wa mā baṭan, wa lā taqtulun-nafsallatī ḥarramallāhu illā bil-ḥaqq, żālikum waṣṣākum bihī la\'allakum ta\'qilụn'),
(940, 6, 152, 'وَلَا تَقْرَبُوْا مَالَ الْيَتِيْمِ اِلَّا بِالَّتِيْ هِيَ اَحْسَنُ حَتّٰى يَبْلُغَ اَشُدَّهٗ ۚوَاَوْفُوا الْكَيْلَ وَالْمِيْزَانَ بِالْقِسْطِۚ لَا نُكَلِّفُ نَفْسًا اِلَّا وُسْعَهَاۚ وَاِذَا قُلْتُمْ فَاعْدِلُوْا وَلَوْ كَانَ ذَا قُرْبٰىۚ وَبِعَهْدِ اللّٰهِ اَوْفُوْاۗ ذٰلِكُمْ وَصّٰىكُمْ بِهٖ لَعَلَّكُمْ تَذَكَّرُوْنَۙ', 'wa lā taqrabụ mālal-yatīmi illā billatī hiya aḥsanu ḥattā yabluga asyuddah, wa auful-kaila wal-mīzāna bil-qisṭ, lā nukallifu nafsan illā wus\'ahā, wa iżā qultum fa\'dilụ walau kāna żā qurbā, wa bi\'ahdillāhi aufụ, żālikum waṣṣākum bihī la\'allakum tażakkarụn'),
(941, 6, 153, 'وَاَنَّ هٰذَا صِرَاطِيْ مُسْتَقِيْمًا فَاتَّبِعُوْهُ ۚوَلَا تَتَّبِعُوا السُّبُلَ فَتَفَرَّقَ بِكُمْ عَنْ سَبِيْلِهٖ ۗذٰلِكُمْ وَصّٰىكُمْ بِهٖ لَعَلَّكُمْ تَتَّقُوْنَ', 'wa anna hāżā ṣirāṭī mustaqīman fattabi\'ụh, wa lā tattabi\'us-subula fa tafarraqa bikum \'an sabīlih, żālikum waṣṣākum bihī la\'allakum tattaqụn'),
(942, 6, 154, 'ثُمَّ اٰتَيْنَا مُوْسَى الْكِتٰبَ تَمَامًا عَلَى الَّذِيْٓ اَحْسَنَ وَتَفْصِيْلًا لِّكُلِّ شَيْءٍ وَّهُدًى وَّرَحْمَةً لَّعَلَّهُمْ بِلِقَاۤءِ رَبِّهِمْ يُؤْمِنُوْنَ', 'ṡumma ātainā mụsal-kitāba tamāman \'alallażī aḥsana wa tafṣīlal likulli syai`iw wa hudaw wa raḥmatal la\'allahum biliqā`i rabbihim yu`minụn'),
(943, 6, 155, 'وَهٰذَا كِتٰبٌ اَنْزَلْنٰهُ مُبٰرَكٌ فَاتَّبِعُوْهُ وَاتَّقُوْا لَعَلَّكُمْ تُرْحَمُوْنَۙ', 'wa hāżā kitābun anzalnāhu mubārakun fattabi\'ụhu wattaqụ la\'allakum tur-ḥamụn'),
(944, 6, 156, 'اَنْ تَقُوْلُوْٓا اِنَّمَآ اُنْزِلَ الْكِتٰبُ عَلٰى طَاۤىِٕفَتَيْنِ مِنْ قَبْلِنَاۖ وَاِنْ كُنَّا عَنْ دِرَاسَتِهِمْ لَغٰفِلِيْنَۙ', 'an taqụlū innamā unzilal-kitābu \'alā ṭā`ifataini ming qablinā wa ing kunnā \'an dirāsatihim lagāfilīn'),
(945, 6, 157, 'اَوْ تَقُوْلُوْا لَوْ اَنَّآ اُنْزِلَ عَلَيْنَا الْكِتٰبُ لَكُنَّآ اَهْدٰى مِنْهُمْۚ فَقَدْ جَاۤءَكُمْ بَيِّنَةٌ مِّنْ رَّبِّكُمْ وَهُدًى وَّرَحْمَةٌ ۚفَمَنْ اَظْلَمُ مِمَّنْ كَذَّبَ بِاٰيٰتِ اللّٰهِ وَصَدَفَ عَنْهَا ۗسَنَجْزِى الَّذِيْنَ يَصْدِفُوْنَ عَنْ اٰيٰتِنَا سُوْۤءَ الْعَذَابِ بِمَا كَانُوْا يَصْدِفُوْنَ', 'au taqụlụ lau annā unzila \'alainal-kitābu lakunnā ahdā min-hum, fa qad jā`akum bayyinatum mir rabbikum wa hudaw wa raḥmah, fa man aẓlamu mim mang każżaba bi`āyātillāhi wa ṣadafa \'an-hā, sanajzillażīna yaṣdifụna \'an āyātinā sū`al-\'ażābi bimā kānụ yaṣdifụn'),
(946, 6, 158, 'هَلْ يَنْظُرُوْنَ اِلَّآ اَنْ تَأْتِيَهُمُ الْمَلٰۤىِٕكَةُ اَوْ يَأْتِيَ رَبُّكَ اَوْ يَأْتِيَ بَعْضُ اٰيٰتِ رَبِّكَ ۗيَوْمَ يَأْتِيْ بَعْضُ اٰيٰتِ رَبِّكَ لَا يَنْفَعُ نَفْسًا اِيْمَانُهَا لَمْ تَكُنْ اٰمَنَتْ مِنْ قَبْلُ اَوْ كَسَبَتْ فِيْٓ اِيْمَانِهَا خَيْرًاۗ قُلِ انْتَظِرُوْٓا اِنَّا مُنْتَظِرُوْنَ', 'hal yanẓurụna illā an ta`tiyahumul-malā`ikatu au ya`tiya rabbuka au ya`tiya ba\'ḍu āyāti rabbik, yauma ya`tī ba\'ḍu āyāti rabbika lā yanfa\'u nafsan īmānuhā lam takun āmanat ming qablu au kasabat fī īmānihā khairā, qulintaẓirū innā muntaẓirụn'),
(947, 6, 159, 'اِنَّ الَّذِيْنَ فَرَّقُوْا دِيْنَهُمْ وَكَانُوْا شِيَعًا لَّسْتَ مِنْهُمْ فِيْ شَيْءٍۗ اِنَّمَآ اَمْرُهُمْ اِلَى اللّٰهِ ثُمَّ يُنَبِّئُهُمْ بِمَا كَانُوْا يَفْعَلُوْنَ', 'innallażīna farraqụ dīnahum wa kānụ syiya\'al lasta min-hum fī syaī`, innamā amruhum ilallāhi ṡumma yunabbi`uhum bimā kānụ yaf\'alụn'),
(948, 6, 160, 'مَنْ جَاۤءَ بِالْحَسَنَةِ فَلَهٗ عَشْرُ اَمْثَالِهَا ۚوَمَنْ جَاۤءَ بِالسَّيِّئَةِ فَلَا يُجْزٰٓى اِلَّا مِثْلَهَا وَهُمْ لَا يُظْلَمُوْنَ', 'man jā`a bil-ḥasanati fa lahụ \'asyru amṡālihā, wa man jā`a bis-sayyi`ati fa lā yujzā illā miṡlahā wa hum lā yuẓlamụn'),
(949, 6, 161, 'قُلْ اِنَّنِيْ هَدٰىنِيْ رَبِّيْٓ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ەۚ دِيْنًا قِيَمًا مِّلَّةَ اِبْرٰهِيْمَ حَنِيْفًاۚ وَمَا كَانَ مِنَ الْمُشْرِكِيْنَ', 'qul innanī hadānī rabbī ilā ṣirāṭim mustaqīm, dīnang qiyamam millata ibrāhīma ḥanīfā, wa mā kāna minal-musyrikīn'),
(950, 6, 162, 'قُلْ اِنَّ صَلَاتِيْ وَنُسُكِيْ وَمَحْيَايَ وَمَمَاتِيْ لِلّٰهِ رَبِّ الْعٰلَمِيْنَۙ', 'qul inna ṣalātī wa nusukī wa maḥyāya wa mamātī lillāhi rabbil-\'ālamīn'),
(951, 6, 163, 'لَا شَرِيْكَ لَهٗ ۚوَبِذٰلِكَ اُمِرْتُ وَاَنَا۠ اَوَّلُ الْمُسْلِمِيْنَ', 'lā syarīka lah, wa biżālika umirtu wa ana awwalul-muslimīn'),
(952, 6, 164, 'قُلْ اَغَيْرَ اللّٰهِ اَبْغِيْ رَبًّا وَّهُوَ رَبُّ كُلِّ شَيْءٍۗ وَلَا تَكْسِبُ كُلُّ نَفْسٍ اِلَّا عَلَيْهَاۚ وَلَا تَزِرُ وَازِرَةٌ وِّزْرَ اُخْرٰىۚ ثُمَّ اِلٰى رَبِّكُمْ مَّرْجِعُكُمْ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ فِيْهِ تَخْتَلِفُوْنَ', 'qul a gairallāhi abgī rabbaw wa huwa rabbu kulli syaī`, wa lā taksibu kullu nafsin illā \'alaihā, wa lā taziru wāziratuw wizra ukhrā, ṡumma ilā rabbikum marji\'ukum fa yunabbi`ukum bimā kuntum fīhi takhtalifụn'),
(953, 6, 165, 'وَهُوَ الَّذِيْ جَعَلَكُمْ خَلٰۤىِٕفَ الْاَرْضِ وَرَفَعَ بَعْضَكُمْ فَوْقَ بَعْضٍ دَرَجٰتٍ لِّيَبْلُوَكُمْ فِيْ مَآ اٰتٰىكُمْۗ اِنَّ رَبَّكَ سَرِيْعُ الْعِقَابِۖ وَاِنَّهٗ لَغَفُوْرٌ رَّحِيْمٌ', 'wa huwallażī ja\'alakum khalā`ifal-arḍi wa rafa\'a ba\'ḍakum fauqa ba\'ḍin darajātil liyabluwakum fī mā ātākum, inna rabbaka sarī\'ul-\'iqābi wa innahụ lagafụrur raḥīm'),
(954, 7, 1, 'الۤمّۤصۤ ۚ', 'alif lām mīm shād'),
(955, 7, 2, 'كِتٰبٌ اُنْزِلَ اِلَيْكَ فَلَا يَكُنْ فِيْ صَدْرِكَ حَرَجٌ مِّنْهُ لِتُنْذِرَ بِهٖ وَذِكْرٰى لِلْمُؤْمِنِيْنَ', 'kitābun unzila ilaika fa lā yakun fī ṣadrika ḥarajum min-hu litunżira bihī wa żikrā lil-mu`minīn'),
(956, 7, 3, 'اِتَّبِعُوْا مَآ اُنْزِلَ اِلَيْكُمْ مِّنْ رَّبِّكُمْ وَلَا تَتَّبِعُوْا مِنْ دُوْنِهٖٓ اَوْلِيَاۤءَۗ قَلِيْلًا مَّا تَذَكَّرُوْنَ', 'ittabi\'ụ mā unzila ilaikum mir rabbikum wa lā tattabi\'ụ min dụnihī auliyā`, qalīlam mā tażakkarụn'),
(957, 7, 4, 'وَكَمْ مِّنْ قَرْيَةٍ اَهْلَكْنٰهَا فَجَاۤءَهَا بَأْسُنَا بَيَاتًا اَوْ هُمْ قَاۤىِٕلُوْنَ', 'wa kam ming qaryatin ahlaknāhā fa jā`ahā ba`sunā bayātan au hum qā`ilụn'),
(958, 7, 5, 'فَمَا كَانَ دَعْوٰىهُمْ اِذْ جَاۤءَهُمْ بَأْسُنَآ اِلَّآ اَنْ قَالُوْٓا اِنَّا كُنَّا ظٰلِمِيْنَ', 'fa mā kāna da\'wāhum iż jā`ahum ba`sunā illā ang qālū innā kunnā ẓālimīn'),
(959, 7, 6, 'فَلَنَسْـَٔلَنَّ الَّذِيْنَ اُرْسِلَ اِلَيْهِمْ وَلَنَسْـَٔلَنَّ الْمُرْسَلِيْنَۙ', 'fa lanas`alannallażīna ursila ilaihim wa lanas`alannal-mursalīn'),
(960, 7, 7, 'فَلَنَقُصَّنَّ عَلَيْهِمْ بِعِلْمٍ وَّمَا كُنَّا غَاۤىِٕبِيْنَ', 'fa lanaquṣṣanna \'alaihim bi\'ilmiw wa mā kunnā gā`ibīn'),
(961, 7, 8, 'وَالْوَزْنُ يَوْمَىِٕذِ ِۨالْحَقُّۚ فَمَنْ ثَقُلَتْ مَوَازِيْنُهٗ فَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ', 'wal-waznu yauma`iżinil-ḥaqq, fa man ṡaqulat mawāzīnuhụ fa ulā`ika humul-mufliḥụn'),
(962, 7, 9, 'وَمَنْ خَفَّتْ مَوَازِيْنُهٗ فَاُولٰۤىِٕكَ الَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ بِمَا كَانُوْا بِاٰيٰتِنَا يَظْلِمُوْنَ', 'wa man khaffat mawāzīnuhụ fa ulā`ikallażīna khasirū anfusahum bimā kānụ bi`āyātinā yaẓlimụn'),
(963, 7, 10, 'وَلَقَدْ مَكَّنّٰكُمْ فِى الْاَرْضِ وَجَعَلْنَا لَكُمْ فِيْهَا مَعَايِشَۗ قَلِيْلًا مَّا تَشْكُرُوْنَ', 'wa laqad makkannākum fil-arḍi wa ja\'alnā lakum fīhā ma\'āyisy, qalīlam mā tasykurụn'),
(964, 7, 11, 'وَلَقَدْ خَلَقْنٰكُمْ ثُمَّ صَوَّرْنٰكُمْ ثُمَّ قُلْنَا لِلْمَلٰۤىِٕكَةِ اسْجُدُوْا لِاٰدَمَ فَسَجَدُوْٓا اِلَّآ اِبْلِيْسَۗ لَمْ يَكُنْ مِّنَ السّٰجِدِيْنَ', 'wa laqad khalaqnākum ṡumma ṣawwarnākum ṡumma qulnā lil-malā`ikatisjudụ li`ādama fa sajadū illā iblīs, lam yakum minas-sājidīn'),
(965, 7, 12, 'قَالَ مَا مَنَعَكَ اَلَّا تَسْجُدَ اِذْ اَمَرْتُكَ ۗقَالَ اَنَا۠ خَيْرٌ مِّنْهُۚ خَلَقْتَنِيْ مِنْ نَّارٍ وَّخَلَقْتَهٗ مِنْ طِيْنٍ', 'qāla mā mana\'aka allā tasjuda iż amartuk, qāla ana khairum min-h, khalaqtanī min nāriw wa khalaqtahụ min ṭīn'),
(966, 7, 13, 'قَالَ فَاهْبِطْ مِنْهَا فَمَا يَكُوْنُ لَكَ اَنْ تَتَكَبَّرَ فِيْهَا فَاخْرُجْ اِنَّكَ مِنَ الصّٰغِرِيْنَ', 'qāla fahbiṭ min-hā fa mā yakụnu laka an tatakabbara fīhā fakhruj innaka minaṣ-ṣāgirīn'),
(967, 7, 14, 'قَالَ اَنْظِرْنِيْٓ اِلٰى يَوْمِ يُبْعَثُوْنَ', 'qāla anẓirnī ilā yaumi yub\'aṡụn'),
(968, 7, 15, 'قَالَ اِنَّكَ مِنَ الْمُنْظَرِيْنَ', 'qāla innaka minal-munẓarīn'),
(969, 7, 16, 'قَالَ فَبِمَآ اَغْوَيْتَنِيْ لَاَقْعُدَنَّ لَهُمْ صِرَاطَكَ الْمُسْتَقِيْمَۙ', 'qāla fa bimā agwaitanī la`aq\'udanna lahum ṣirāṭakal-mustaqīm'),
(970, 7, 17, 'ثُمَّ لَاٰتِيَنَّهُمْ مِّنْۢ بَيْنِ اَيْدِيْهِمْ وَمِنْ خَلْفِهِمْ وَعَنْ اَيْمَانِهِمْ وَعَنْ شَمَاۤىِٕلِهِمْۗ وَلَا تَجِدُ اَكْثَرَهُمْ شٰكِرِيْنَ', 'ṡumma la`ātiyannahum mim baini aidīhim wa min khalfihim wa \'an aimānihim wa \'an syamā`ilihim, wa lā tajidu akṡarahum syākirīn'),
(971, 7, 18, 'قَالَ اخْرُجْ مِنْهَا مَذْءُوْمًا مَّدْحُوْرًا ۗ لَمَنْ تَبِعَكَ مِنْهُمْ لَاَمْلَـَٔنَّ جَهَنَّمَ مِنْكُمْ اَجْمَعِيْنَ', 'qālakhruj min-hā maż`ụmam mad-ḥụrā, laman tabi\'aka min-hum la`amla`anna jahannama mingkum ajma\'īn'),
(972, 7, 19, 'وَيٰٓاٰدَمُ اسْكُنْ اَنْتَ وَزَوْجُكَ الْجَنَّةَ فَكُلَا مِنْ حَيْثُ شِئْتُمَا وَلَا تَقْرَبَا هٰذِهِ الشَّجَرَةَ فَتَكُوْنَا مِنَ الظّٰلِمِيْنَ', 'wa yā ādamuskun anta wa zaujukal-jannata fa kulā min ḥaiṡu syi`tumā wa lā taqrabā hāżihisy-syajarata fa takụnā minaẓ-ẓālimīn'),
(973, 7, 20, 'فَوَسْوَسَ لَهُمَا الشَّيْطٰنُ لِيُبْدِيَ لَهُمَا مَا وٗرِيَ عَنْهُمَا مِنْ سَوْاٰتِهِمَا وَقَالَ مَا نَهٰىكُمَا رَبُّكُمَا عَنْ هٰذِهِ الشَّجَرَةِ ِالَّآ اَنْ تَكُوْنَا مَلَكَيْنِ اَوْ تَكُوْنَا مِنَ الْخٰلِدِيْنَ', 'fa waswasa lahumasy-syaiṭānu liyubdiya lahumā mā wụriya \'an-humā min sau`ātihimā wa qāla mā nahākumā rabbukumā \'an hāżihisy-syajarati illā an takụnā malakaini au takụnā minal-khālidīn'),
(974, 7, 21, 'وَقَاسَمَهُمَآ اِنِّيْ لَكُمَا لَمِنَ النّٰصِحِيْنَۙ', 'wa qāsamahumā innī lakumā laminan-nāṣiḥīn'),
(975, 7, 22, 'فَدَلّٰىهُمَا بِغُرُورٍۚ فَلَمَّا ذَاقَا الشَّجَرَةَ بَدَتْ لَهُمَا سَوْاٰتُهُمَا وَطَفِقَا يَخْصِفٰنِ عَلَيْهِمَا مِنْ وَّرَقِ الْجَنَّةِۗ وَنَادٰىهُمَا رَبُّهُمَآ اَلَمْ اَنْهَكُمَا عَنْ تِلْكُمَا الشَّجَرَةِ وَاَقُلْ لَّكُمَآ اِنَّ الشَّيْطٰنَ لَكُمَا عَدُوٌّ مُّبِيْنٌ', 'fa dallāhumā bigurụr, fa lammā żāqasy-syajarata badat lahumā sau`ātuhumā wa ṭafiqā yakhṣifāni \'alaihimā miw waraqil-jannah, wa nādāhumā rabbuhumā a lam an-hakumā \'an tilkumasy-syajarati wa aqul lakumā innasy-syaiṭāna lakumā \'aduwwum mubīn');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(976, 7, 23, 'قَالَا رَبَّنَا ظَلَمْنَآ اَنْفُسَنَا وَاِنْ لَّمْ تَغْفِرْ لَنَا وَتَرْحَمْنَا لَنَكُوْنَنَّ مِنَ الْخٰسِرِيْنَ', 'qālā rabbanā ẓalamnā anfusana wa il lam tagfir lanā wa tar-ḥamnā lanakụnanna minal-khāsirīn'),
(977, 7, 24, 'قَالَ اهْبِطُوْا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ ۚوَلَكُمْ فِى الْاَرْضِ مُسْتَقَرٌّ وَّمَتَاعٌ اِلٰى حِيْنٍ', 'qālahbiṭụ ba\'ḍukum liba\'ḍin \'aduww, wa lakum fil-arḍi mustaqarruw wa matā\'un ilā ḥīn'),
(978, 7, 25, 'قَالَ فِيْهَا تَحْيَوْنَ وَفِيْهَا تَمُوْتُوْنَ وَمِنْهَا تُخْرَجُوْنَ', 'qāla fīhā taḥyauna wa fīhā tamụtụna wa min-hā tukhrajụn'),
(979, 7, 26, 'يَا بَنِيْٓ اٰدَمَ قَدْ اَنْزَلْنَا عَلَيْكُمْ لِبَاسًا يُّوَارِيْ سَوْاٰتِكُمْ وَرِيْشًاۗ وَلِبَاسُ التَّقْوٰى ذٰلِكَ خَيْرٌۗ ذٰلِكَ مِنْ اٰيٰتِ اللّٰهِ لَعَلَّهُمْ يَذَّكَّرُوْنَ', 'yā banī ādama qad anzalnā \'alaikum libāsay yuwārī sau`ātikum warīsyā, wa libāsut-taqwā żālika khaīr, żālika min āyātillāhi la\'allahum yażżakkarụn'),
(980, 7, 27, 'يٰبَنِيْٓ اٰدَمَ لَا يَفْتِنَنَّكُمُ الشَّيْطٰنُ كَمَآ اَخْرَجَ اَبَوَيْكُمْ مِّنَ الْجَنَّةِ يَنْزِعُ عَنْهُمَا لِبَاسَهُمَا لِيُرِيَهُمَا سَوْاٰتِهِمَا ۗاِنَّهٗ يَرٰىكُمْ هُوَ وَقَبِيْلُهٗ مِنْ حَيْثُ لَا تَرَوْنَهُمْۗ اِنَّا جَعَلْنَا الشَّيٰطِيْنَ اَوْلِيَاۤءَ لِلَّذِيْنَ لَا يُؤْمِنُوْنَ', 'yā banī ādama lā yaftinannakumusy-syaiṭānu kamā akhraja abawaikum minal-jannati yanzi\'u \'an-humā libāsahumā liyuriyahumā sau`ātihimā, innahụ yarākum huwa wa qabīluhụ min ḥaiṡu lā taraunahum, innā ja\'alnasy-syayāṭīna auliyā`a lillażīna lā yu`minụn'),
(981, 7, 28, 'وَاِذَا فَعَلُوْا فَاحِشَةً قَالُوْا وَجَدْنَا عَلَيْهَآ اٰبَاۤءَنَا وَاللّٰهُ اَمَرَنَا بِهَاۗ قُلْ اِنَّ اللّٰهَ لَا يَأْمُرُ بِالْفَحْشَاۤءِۗ اَتَقُوْلُوْنَ عَلَى اللّٰهِ مَا لَا تَعْلَمُوْنَ', 'wa iżā fa\'alụ fāḥisyatang qālụ wajadnā \'alaihā ābā`anā wallāhu amaranā bihā, qul innallāha lā ya`muru bil-faḥsyā`, a taqụlụna \'alallāhi mā lā ta\'lamụn'),
(982, 7, 29, 'قُلْ اَمَرَ رَبِّيْ بِالْقِسْطِۗ وَاَقِيْمُوْا وُجُوْهَكُمْ عِنْدَ كُلِّ مَسْجِدٍ وَّادْعُوْهُ مُخْلِصِيْنَ لَهُ الدِّيْنَ ەۗ كَمَا بَدَاَكُمْ تَعُوْدُوْنَۗ', 'qul amara rabbī bil-qisṭ, wa aqīmụ wujụhakum \'inda kulli masjidiw wad\'ụhu mukhliṣīna lahud-dīn, kamā bada`akum ta\'ụdụn'),
(983, 7, 30, 'فَرِيْقًا هَدٰى وَفَرِيْقًا حَقَّ عَلَيْهِمُ الضَّلٰلَةُ ۗاِنَّهُمُ اتَّخَذُوا الشَّيٰطِيْنَ اَوْلِيَاۤءَ مِنْ دُوْنِ اللّٰهِ وَيَحْسَبُوْنَ اَنَّهُمْ مُّهْتَدُوْنَ', 'farīqan hadā wa farīqan ḥaqqa \'alaihimuḍ-ḍalālah, innahumuttakhażusy-syayāṭīna auliyā`a min dụnillāhi wa yaḥsabụna annahum muhtadụn'),
(984, 7, 31, '۞ يٰبَنِيْٓ اٰدَمَ خُذُوْا زِيْنَتَكُمْ عِنْدَ كُلِّ مَسْجِدٍ وَّكُلُوْا وَاشْرَبُوْا وَلَا تُسْرِفُوْاۚ اِنَّهٗ لَا يُحِبُّ الْمُسْرِفِيْنَ', 'yā banī ādama khużụ zīnatakum \'inda kulli masjidiw wa kulụ wasyrabụ wa lā tusrifụ, innahụ lā yuḥibbul-musrifīn'),
(985, 7, 32, 'قُلْ مَنْ حَرَّمَ زِيْنَةَ اللّٰهِ الَّتِيْٓ اَخْرَجَ لِعِبَادِهٖ وَالطَّيِّبٰتِ مِنَ الرِّزْقِۗ قُلْ هِيَ لِلَّذِيْنَ اٰمَنُوْا فِى الْحَيٰوةِ الدُّنْيَا خَالِصَةً يَّوْمَ الْقِيٰمَةِۗ كَذٰلِكَ نُفَصِّلُ الْاٰيٰتِ لِقَوْمٍ يَّعْلَمُوْنَ', 'qul man ḥarrama zīnatallāhillatī akhraja li\'ibādihī waṭ-ṭayyibāti minar-rizq, qul hiya lillażīna āmanụ fil-ḥayātid-dun-yā khāliṣatay yaumal-qiyāmah, każālika nufaṣṣilul-āyāti liqaumiy ya\'lamụn'),
(986, 7, 33, 'قُلْ اِنَّمَا حَرَّمَ رَبِّيَ الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَ وَالْاِثْمَ وَالْبَغْيَ بِغَيْرِ الْحَقِّ وَاَنْ تُشْرِكُوْا بِاللّٰهِ مَا لَمْ يُنَزِّلْ بِهٖ سُلْطٰنًا وَّاَنْ تَقُوْلُوْا عَلَى اللّٰهِ مَا لَا تَعْلَمُوْنَ', 'qul innamā ḥarrama rabbiyal-fawāḥisya mā ẓahara min-hā wa mā baṭana wal-iṡma wal-bagya bigairil-ḥaqqi wa an tusyrikụ billāhi mā lam yunazzil bihī sulṭānaw wa an taqụlụ \'alallāhi mā lā ta\'lamụn'),
(987, 7, 34, 'وَلِكُلِّ اُمَّةٍ اَجَلٌۚ فَاِذَا جَاۤءَ اَجَلُهُمْ لَا يَسْتَأْخِرُوْنَ سَاعَةً وَّلَا يَسْتَقْدِمُوْنَ', 'wa likulli ummatin ajal, fa iżā jā`a ajaluhum lā yasta`khirụna sā\'ataw wa lā yastaqdimụn'),
(988, 7, 35, 'يٰبَنِيْٓ اٰدَمَ اِمَّا يَأْتِيَنَّكُمْ رُسُلٌ مِّنْكُمْ يَقُصُّوْنَ عَلَيْكُمْ اٰيٰتِيْۙ فَمَنِ اتَّقٰى وَاَصْلَحَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ', 'yā banī ādama immā ya`tiyannakum rusulum mingkum yaquṣṣụna \'alaikum āyātī fa manittaqā wa aṣlaḥa fa lā khaufun \'alaihim wa lā hum yaḥzanụn'),
(989, 7, 36, 'وَالَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَاسْتَكْبَرُوْا عَنْهَآ اُولٰۤىِٕكَ اَصْحٰبُ النَّارِۚ هُمْ فِيْهَا خٰلِدُوْنَ', 'wallażīna każżabụ bi`āyātinā wastakbarụ \'an-hā ulā`ika aṣ-ḥābun-nār, hum fīhā khālidụn'),
(990, 7, 37, 'فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا اَوْ كَذَّبَ بِاٰيٰتِهٖۗ اُولٰۤىِٕكَ يَنَالُهُمْ نَصِيْبُهُمْ مِّنَ الْكِتٰبِۗ حَتّٰٓى اِذَا جَاۤءَتْهُمْ رُسُلُنَا يَتَوَفَّوْنَهُمْۙ قَالُوْٓا اَيْنَ مَا كُنْتُمْ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ ۗقَالُوْا ضَلُّوْا عَنَّا وَشَهِدُوْا عَلٰٓى اَنْفُسِهِمْ اَنَّهُمْ كَانُوْا كٰفِرِيْنَ', 'fa man aẓlamu mim maniftarā \'alallāhi każiban au każżaba bi`āyātih, ulā`ika yanaluhum naṣībuhum minal-kitāb, ḥattā iżā jā`at-hum rusulunā yatawaffaunahum qālū aina mā kuntum tad\'ụna min dụnillāh, qālụ ḍallụ \'annā wa syahidụ \'alā anfusihim annahum kānụ kāfirīn'),
(991, 7, 38, 'قَالَ ادْخُلُوْا فِيْٓ اُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِكُمْ مِّنَ الْجِنِّ وَالْاِنْسِ فِى النَّارِۙ كُلَّمَا دَخَلَتْ اُمَّةٌ لَّعَنَتْ اُخْتَهَا ۗحَتّٰٓى اِذَا ادَّارَكُوْا فِيْهَا جَمِيْعًا ۙقَالَتْ اُخْرٰىهُمْ لِاُوْلٰىهُمْ رَبَّنَا هٰٓؤُلَاۤءِ اَضَلُّوْنَا فَاٰتِهِمْ عَذَابًا ضِعْفًا مِّنَ النَّارِ ەۗ قَالَ لِكُلٍّ ضِعْفٌ وَّلٰكِنْ لَّا تَعْلَمُوْنَ', 'qāladkhulụ fī umaming qad khalat ming qablikum minal-jinni wal-insi fin-nāri kullamā dakhalat ummatul la\'anat ukhtahā, ḥattā iżad dārakụ fīhā jamī\'ang qālat ukhrāhum li`ụlāhum rabbanā hā`ulā`i aḍallụnā fa ātihim \'ażāban ḍi\'fam minan-nār, qāla likullin ḍi\'fuw wa lākil lā ta\'lamụn'),
(992, 7, 39, 'وَقَالَتْ اُوْلٰىهُمْ لِاُخْرٰىهُمْ فَمَا كَانَ لَكُمْ عَلَيْنَا مِنْ فَضْلٍ فَذُوْقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْسِبُوْنَ', 'wa qālat ụlāhum li`ukhrāhum fa mā kāna lakum \'alainā min faḍlin fa żụqul-\'ażāba bimā kuntum taksibụn'),
(993, 7, 40, 'اِنَّ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَاسْتَكْبَرُوْا عَنْهَا لَا تُفَتَّحُ لَهُمْ اَبْوَابُ السَّمَاۤءِ وَلَا يَدْخُلُوْنَ الْجَنَّةَ حَتّٰى يَلِجَ الْجَمَلُ فِيْ سَمِّ الْخِيَاطِ ۗ وَكَذٰلِكَ نَجْزِى الْمُجْرِمِيْنَ', 'innallażīna każżabụ bi`āyātinā wastakbarụ \'an-hā lā tufattaḥu lahum abwābus-samā`i wa lā yadkhulụnal-jannata ḥattā yalijal-jamalu fī sammil-khiyāṭ, wa każālika najzil-mujrimīn'),
(994, 7, 41, 'لَهُمْ مِّنْ جَهَنَّمَ مِهَادٌ وَّمِنْ فَوْقِهِمْ غَوَاشٍۗ وَكَذٰلِكَ نَجْزِى الظّٰلِمِيْنَ', 'lahum min jahannama mihāduw wa min fauqihim gawāsy, wa każālika najziẓ-ẓālimīn'),
(995, 7, 42, 'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَا نُكَلِّفُ نَفْسًا اِلَّا وُسْعَهَآ اُولٰۤىِٕكَ اَصْحٰبُ الْجَنَّةِۚ هُمْ فِيْهَا خٰلِدُوْنَ', 'wallażīna āmanụ wa \'amiluṣ-ṣāliḥāti lā nukallifu nafsan illā wus\'ahā ulā`ika aṣ-ḥābul-jannah, hum fīhā khālidụn'),
(996, 7, 43, 'وَنَزَعْنَا مَا فِيْ صُدُوْرِهِمْ مِّنْ غِلٍّ تَجْرِيْ مِنْ تَحْتِهِمُ الْانْهٰرُۚ وَقَالُوا الْحَمْدُ لِلّٰهِ الَّذِيْ هَدٰىنَا لِهٰذَاۗ وَمَا كُنَّا لِنَهْتَدِيَ لَوْلَآ اَنْ هَدٰىنَا اللّٰهُ ۚ لَقَدْ جَاۤءَتْ رُسُلُ رَبِّنَا بِالْحَقِّۗ وَنُوْدُوْٓا اَنْ تِلْكُمُ الْجَنَّةُ اُوْرِثْتُمُوْهَا بِمَا كُنْتُمْ تَعْمَلُوْنَ', 'wa naza\'nā mā fī ṣudụrihim min gillin tajrī min taḥtihimul-an-hār, wa qālul-ḥamdu lillāhillażī hadānā lihāżā, wa mā kunnā linahtadiya lau lā an hadānallāh, laqad jā`at rusulu rabbinā bil-ḥaqq, wa nụdū an tilkumul-jannatu ụriṡtumụhā bimā kuntum ta\'malụn'),
(997, 7, 44, 'وَنَادٰٓى اَصْحٰبُ الْجَنَّةِ اَصْحٰبَ النَّارِ اَنْ قَدْ وَجَدْنَا مَا وَعَدَنَا رَبُّنَا حَقًّا فَهَلْ وَجَدْتُّمْ مَّا وَعَدَ رَبُّكُمْ حَقًّا ۗقَالُوْا نَعَمْۚ فَاَذَّنَ مُؤَذِّنٌۢ بَيْنَهُمْ اَنْ لَّعْنَةُ اللّٰهِ عَلَى الظّٰلِمِيْنَ', 'wa nādā aṣ-ḥābul-jannati aṣ-ḥāban-nāri ang qad wajadnā mā wa\'adanā rabbunā ḥaqqan fa hal wajattum mā wa\'ada rabbukum ḥaqqā, qālụ na\'am, fa ażżana mu`ażżinum bainahum al la\'natullāhi \'alaẓ-ẓālimīn'),
(998, 7, 45, 'اَلَّذِيْنَ يَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ وَيَبْغُوْنَهَا عِوَجًاۚ وَهُمْ بِالْاٰخِرَةِ كٰفِرُوْنَۘ', 'allażīna yaṣuddụna \'an sabīlillāhi wa yabgụnahā \'iwajā, wa hum bil-ākhirati kāfirụn'),
(999, 7, 46, 'وَبَيْنَهُمَا حِجَابٌۚ وَعَلَى الْاَعْرَافِ رِجَالٌ يَّعْرِفُوْنَ كُلًّا ۢ بِسِيْمٰىهُمْۚ وَنَادَوْا اَصْحٰبَ الْجَنَّةِ اَنْ سَلٰمٌ عَلَيْكُمْۗ لَمْ يَدْخُلُوْهَا وَهُمْ يَطْمَعُوْنَ', 'wa bainahumā ḥijāb, wa \'alal-a\'rāfi rijāluy ya\'rifụna kullam bisīmāhum, wa nādau aṣ-ḥābal-jannati an salāmun \'alaikum, lam yadkhulụhā wa hum yaṭma\'ụn'),
(1000, 7, 47, '۞ وَاِذَا صُرِفَتْ اَبْصَارُهُمْ تِلْقَاۤءَ اَصْحٰبِ النَّارِۙ قَالُوْا رَبَّنَا لَا تَجْعَلْنَا مَعَ الْقَوْمِ الظّٰلِمِيْنَ', 'wa iżā ṣurifat abṣāruhum tilqā`a aṣ-ḥābin-nāri qālụ rabbanā lā taj\'alnā ma\'al-qaumiẓ-ẓālimīn'),
(1001, 7, 48, 'وَنَادٰٓى اَصْحٰبُ الْاَعْرَافِ رِجَالًا يَّعْرِفُوْنَهُمْ بِسِيْمٰىهُمْ قَالُوْا مَآ اَغْنٰى عَنْكُمْ جَمْعُكُمْ وَمَا كُنْتُمْ تَسْتَكْبِرُوْنَ', 'wa nādā aṣ-ḥābul-a\'rāfi rijālay ya\'rifụnahum bisīmāhum qālụ mā agnā \'angkum jam\'ukum wa mā kuntum tastakbirụn'),
(1002, 7, 49, 'اَهٰٓؤُلَاۤءِ الَّذِيْنَ اَقْسَمْتُمْ لَا يَنَالُهُمُ اللّٰهُ بِرَحْمَةٍۗ اُدْخُلُوا الْجَنَّةَ لَا خَوْفٌ عَلَيْكُمْ وَلَآ اَنْتُمْ تَحْزَنُوْنَ', 'a hā`ulā`illażīna aqsamtum lā yanaluhumullāhu biraḥmah, udkhulul-jannata lā khaufun \'alaikum wa lā antum taḥzanụn'),
(1003, 7, 50, 'وَنَادٰٓى اَصْحٰبُ النَّارِ اَصْحٰبَ الْجَنَّةِ اَنْ اَفِيْضُوْا عَلَيْنَا مِنَ الْمَاۤءِ اَوْ مِمَّا رَزَقَكُمُ اللّٰهُ ۗقَالُوْٓا اِنَّ اللّٰهَ حَرَّمَهُمَا عَلَى الْكٰفِرِيْنَۙ', 'wa nādā aṣ-ḥābun-nāri aṣ-ḥābal-jannati an afīḍụ \'alainā minal-mā`i au mimmā razaqakumullāh, qālū innallāha ḥarramahumā \'alal-kāfirīn'),
(1004, 7, 51, 'الَّذِيْنَ اتَّخَذُوْا دِيْنَهُمْ لَهْوًا وَّلَعِبًا وَّغَرَّتْهُمُ الْحَيٰوةُ الدُّنْيَاۚ فَالْيَوْمَ نَنْسٰىهُمْ كَمَا نَسُوْا لِقَاۤءَ يَوْمِهِمْ هٰذَاۙ وَمَا كَانُوْا بِاٰيٰتِنَا يَجْحَدُوْنَ', 'allażīnattakhażụ dīnahum lahwaw wa la\'ibaw wa garrat-humul-ḥayātud-dun-yā, fal-yauma nansāhum kamā nasụ liqā`a yaumihim hāżā wa mā kānụ bi`āyātinā yaj-ḥadụn'),
(1005, 7, 52, 'وَلَقَدْ جِئْنٰهُمْ بِكِتٰبٍ فَصَّلْنٰهُ عَلٰى عِلْمٍ هُدًى وَّرَحْمَةً لِّقَوْمٍ يُّؤْمِنُوْنَ', 'wa laqad ji`nāhum bikitābin faṣṣalnāhu \'alā \'ilmin hudaw wa raḥmatal liqaumiy yu`minụn'),
(1006, 7, 53, 'هَلْ يَنْظُرُوْنَ اِلَّا تَأْوِيْلَهٗۗ يَوْمَ يَأْتِيْ تَأْوِيْلُهٗ يَقُوْلُ الَّذِيْنَ نَسُوْهُ مِنْ قَبْلُ قَدْ جَاۤءَتْ رُسُلُ رَبِّنَا بِالْحَقِّۚ فَهَلْ لَّنَا مِنْ شُفَعَاۤءَ فَيَشْفَعُوْا لَنَآ اَوْ نُرَدُّ فَنَعْمَلَ غَيْرَ الَّذِيْ كُنَّا نَعْمَلُۗ قَدْ خَسِرُوْٓا اَنْفُسَهُمْ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ', 'hal yanẓurụna illā ta`wīlah, yauma ya`tī ta`wīluhụ yaqụlullażīna nasụhu ming qablu qad jā`at rusulu rabbinā bil-ḥaqq, fa hal lanā min syufa\'ā`a fa yasyfa\'ụ lanā au nuraddu fa na\'mala gairallażī kunnā na\'mal, qad khasirū anfusahum wa ḍalla \'an-hum mā kānụ yaftarụn'),
(1007, 7, 54, 'اِنَّ رَبَّكُمُ اللّٰهُ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ فِيْ سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِۗ يُغْشِى الَّيْلَ النَّهَارَ يَطْلُبُهٗ حَثِيْثًاۙ وَّالشَّمْسَ وَالْقَمَرَ وَالنُّجُوْمَ مُسَخَّرٰتٍۢ بِاَمْرِهٖٓ ۙاَلَا لَهُ الْخَلْقُ وَالْاَمْرُۗ تَبٰرَكَ اللّٰهُ رَبُّ الْعٰلَمِيْنَ', 'inna rabbakumullāhullażī khalaqas-samāwāti wal-arḍa fī sittati ayyāmin ṡummastawā \'alal-\'arsy, yugsyil-lailan-nahāra yaṭlubuhụ ḥaṡīṡaw wasy-syamsa wal-qamara wan-nujụma musakhkharātim bi`amrihī alā lahul-khalqu wal-amr, tabārakallāhu rabbul-\'ālamīn'),
(1008, 7, 55, 'اُدْعُوْا رَبَّكُمْ تَضَرُّعًا وَّخُفْيَةً ۗاِنَّهٗ لَا يُحِبُّ الْمُعْتَدِيْنَۚ', 'ud\'ụ rabbakum taḍarru\'aw wa khufyah, innahụ lā yuḥibbul-mu\'tadīn'),
(1009, 7, 56, 'وَلَا تُفْسِدُوْا فِى الْاَرْضِ بَعْدَ اِصْلَاحِهَا وَادْعُوْهُ خَوْفًا وَّطَمَعًاۗ اِنَّ رَحْمَتَ اللّٰهِ قَرِيْبٌ مِّنَ الْمُحْسِنِيْنَ', 'wa lā tufsidụ fil-arḍi ba\'da iṣlāḥihā wad\'ụhu khaufaw wa ṭama\'ā, inna raḥmatallāhi qarībum minal-muḥsinīn'),
(1010, 7, 57, 'وَهُوَ الَّذِيْ يُرْسِلُ الرِّيٰحَ بُشْرًاۢ بَيْنَ يَدَيْ رَحْمَتِهٖۗ حَتّٰٓى اِذَآ اَقَلَّتْ سَحَابًا ثِقَالًا سُقْنٰهُ لِبَلَدٍ مَّيِّتٍ فَاَنْزَلْنَا بِهِ الْمَاۤءَ فَاَخْرَجْنَا بِهٖ مِنْ كُلِّ الثَّمَرٰتِۗ كَذٰلِكَ نُخْرِجُ الْمَوْتٰى لَعَلَّكُمْ تَذَكَّرُوْنَ', 'wa huwallażī yursilur-riyāḥa busyram baina yadai raḥmatih, ḥattā iżā aqallat saḥāban ṡiqālan suqnāhu libaladim mayyitin fa anzalnā bihil-mā`a fa akhrajnā bihī ming kulliṡ-ṡamarāt, każālika nukhrijul-mautā la\'allakum tażakkarụn'),
(1011, 7, 58, 'وَالْبَلَدُ الطَّيِّبُ يَخْرُجُ نَبَاتُهٗ بِاِذْنِ رَبِّهٖۚ وَالَّذِيْ خَبُثَ لَا يَخْرُجُ اِلَّا نَكِدًاۗ كَذٰلِكَ نُصَرِّفُ الْاٰيٰتِ لِقَوْمٍ يَّشْكُرُوْنَ', 'wal-baladuṭ-ṭayyibu yakhruju nabātuhụ bi`iżni rabbih, wallażī khabuṡa lā yakhruju illā nakidā, każālika nuṣarriful-āyāti liqaumiy yasykurụn'),
(1012, 7, 59, 'لَقَدْ اَرْسَلْنَا نُوْحًا اِلٰى قَوْمِهٖ فَقَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ اِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظِيْمٍ', 'laqad arsalnā nụḥan ilā qaumihī fa qāla yā qaumi\'budullāha mā lakum min ilāhin gairuh, innī akhāfu \'alaikum \'ażāba yaumin \'aẓīm'),
(1013, 7, 60, 'قَالَ الْمَلَاُ مِنْ قَوْمِهٖٓ اِنَّا لَنَرٰىكَ فِيْ ضَلٰلٍ مُّبِيْنٍ', 'qālal-mala`u ming qaumihī innā lanarāka fī ḍalālim mubīn'),
(1014, 7, 61, 'قَالَ يٰقَوْمِ لَيْسَ بِيْ ضَلٰلَةٌ وَّلٰكِنِّيْ رَسُوْلٌ مِّنْ رَّبِّ الْعٰلَمِيْنَ', 'qāla yā qaumi laisa bī ḍalālatuw wa lākinnī rasụlum mir rabbil-\'ālamīn'),
(1015, 7, 62, 'اُبَلِّغُكُمْ رِسٰلٰتِ رَبِّيْ وَاَنْصَحُ لَكُمْ وَاَعْلَمُ مِنَ اللّٰهِ مَا لَا تَعْلَمُوْنَ', 'uballigukum risālāti rabbī wa anṣaḥu lakum wa a\'lamu minallāhi mā lā ta\'lamụn'),
(1016, 7, 63, 'اَوَعَجِبْتُمْ اَنْ جَاۤءَكُمْ ذِكْرٌ مِّنْ رَّبِّكُمْ عَلٰى رَجُلٍ مِّنْكُمْ لِيُنْذِرَكُمْ وَلِتَتَّقُوْا وَلَعَلَّكُمْ تُرْحَمُوْنَ', 'a wa \'ajibtum an jā`akum żikrum mir rabbikum \'alā rajulim mingkum liyunżirakum wa litattaqụ wa la\'allakum tur-ḥamụn'),
(1017, 7, 64, 'فَكَذَّبُوْهُ فَاَنْجَيْنٰهُ وَالَّذِيْنَ مَعَهٗ فِى الْفُلْكِ وَاَغْرَقْنَا الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَاۗ اِنَّهُمْ كَانُوْا قَوْمًا عَمِيْنَ', 'fa każżabụhu fa anjaināhu wallażīna ma\'ahụ fil-fulki wa agraqnallażīna każżabụ bi`āyātinā, innahum kānụ qauman \'amīn'),
(1018, 7, 65, '۞ وَاِلٰى عَادٍ اَخَاهُمْ هُوْدًاۗ قَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ اَفَلَا تَتَّقُوْنَ', 'wa ilā \'ādin akhāhum hụdā, qāla yā qaumi\'budullāha mā lakum min ilāhin gairuh, a fa lā tattaqụn'),
(1019, 7, 66, 'قَالَ الْمَلَاُ الَّذِيْنَ كَفَرُوْا مِنْ قَوْمِهٖٓ اِنَّا لَنَرٰىكَ فِيْ سَفَاهَةٍ وَّاِنَّا لَنَظُنُّكَ مِنَ الْكٰذِبِيْنَ', 'qālal-mala`ullażīna kafarụ ming qaumihī innā lanarāka fī safāhatiw wa innā lanaẓunnuka minal-kāżibīn'),
(1020, 7, 67, 'قَالَ يٰقَوْمِ لَيْسَ بِيْ سَفَاهَةٌ وَّلٰكِنِّيْ رَسُوْلٌ مِّنْ رَّبِّ الْعٰلَمِيْنَ', 'qāla yā qaumi laisa bī safāhatuw wa lākinnī rasụlum mir rabbil-\'ālamīn'),
(1021, 7, 68, 'اُبَلِّغُكُمْ رِسٰلٰتِ رَبِّيْ وَاَنَا۠ لَكُمْ نَاصِحٌ اَمِيْنٌ', 'uballigukum risālāti rabbī wa ana lakum nāṣiḥun amīn'),
(1022, 7, 69, 'اَوَعَجِبْتُمْ اَنْ جَاۤءَكُمْ ذِكْرٌ مِّنْ رَّبِّكُمْ عَلٰى رَجُلٍ مِّنْكُمْ لِيُنْذِرَكُمْۗ وَاذْكُرُوْٓا اِذْ جَعَلَكُمْ خُلَفَاۤءَ مِنْۢ بَعْدِ قَوْمِ نُوْحٍ وَّزَادَكُمْ فِى الْخَلْقِ بَصْۣطَةً ۚفَاذْكُرُوْٓا اٰلَاۤءَ اللّٰهِ لَعَلَّكُمْ تُفْلِحُوْنَ', 'a wa \'ajibtum an jā`akum żikrum mir rabbikum \'alā rajulim mingkum liyunżirakum, ważkurū iż ja\'alakum khulafā`a mim ba\'di qaumi nụḥiw wa zādakum fil-khalqi baṣṭah, fażkurū ālā`allāhi la\'allakum tufliḥụn'),
(1023, 7, 70, 'قَالُوْٓا اَجِئْتَنَا لِنَعْبُدَ اللّٰهَ وَحْدَهٗ وَنَذَرَ مَا كَانَ يَعْبُدُ اٰبَاۤؤُنَاۚ فَأْتِنَا بِمَا تَعِدُنَآ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ', 'qālū a ji`tanā lina\'budallāha waḥdahụ wa nażara mā kāna ya\'budu ābā`unā, fa`tinā bimā ta\'idunā ing kunta minaṣ-ṣādiqīn'),
(1024, 7, 71, 'قَالَ قَدْ وَقَعَ عَلَيْكُمْ مِّنْ رَّبِّكُمْ رِجْسٌ وَّغَضَبٌۗ اَتُجَادِلُوْنَنِيْ فِيْٓ اَسْمَاۤءٍ سَمَّيْتُمُوْهَآ اَنْتُمْ وَاٰبَاۤؤُكُمْ مَّا نَزَّلَ اللّٰهُ بِهَا مِنْ سُلْطٰنٍۗ فَانْتَظِرُوْٓا اِنِّيْ مَعَكُمْ مِّنَ الْمُنْتَظِرِيْنَ', 'qāla qad waqa\'a \'alaikum mir rabbikum rijsuw wa gaḍab, a tujādilụnanī fī asmā`in sammaitumụhā antum wa ābā`ukum mā nazzalallāhu bihā min sulṭān, fantaẓirū innī ma\'akum minal-muntaẓirīn'),
(1025, 7, 72, 'فَاَنْجَيْنٰهُ وَالَّذِيْنَ مَعَهٗ بِرَحْمَةٍ مِّنَّا وَقَطَعْنَا دَابِرَ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَمَا كَانُوْا مُؤْمِنِيْنَ', 'fa anjaināhu wallażīna ma\'ahụ biraḥmatim minnā wa qaṭa\'nā dābirallażīna każżabụ bi`āyātinā wa mā kānụ mu`minīn'),
(1026, 7, 73, 'وَاِلٰى ثَمُوْدَ اَخَاهُمْ صٰلِحًاۘ قَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ قَدْ جَاۤءَتْكُمْ بَيِّنَةٌ مِّنْ رَّبِّكُمْۗ هٰذِهٖ نَاقَةُ اللّٰهِ لَكُمْ اٰيَةً فَذَرُوْهَا تَأْكُلْ فِيْٓ اَرْضِ اللّٰهِ وَلَا تَمَسُّوْهَا بِسُوْۤءٍ فَيَأْخُذَكُمْ عَذَابٌ اَلِيْمٌ', 'wa ilā ṡamụda akhāhum ṣāliḥā, qāla yā qaumi\'budullāha mā lakum min ilāhin gairuh, qad jā`atkum bayyinatum mir rabbikum, hāżihī nāqatullāhi lakum āyatan fa żarụhā ta`kul fī arḍillāhi wa lā tamassụhā bisū`in fa ya`khużakum \'ażābun alīm'),
(1027, 7, 74, 'وَاذْكُرُوْٓا اِذْ جَعَلَكُمْ خُلَفَاۤءَ مِنْۢ بَعْدِ عَادٍ وَّبَوَّاَكُمْ فِى الْاَرْضِ تَتَّخِذُوْنَ مِنْ سُهُوْلِهَا قُصُوْرًا وَّتَنْحِتُوْنَ الْجِبَالَ بُيُوْتًا ۚفَاذْكُرُوْٓا اٰلَاۤءَ اللّٰهِ وَلَا تَعْثَوْا فِى الْاَرْضِ مُفْسِدِيْنَ', 'ważkurū iż ja\'alakum khulafā`a mim ba\'di \'ādiw wa bawwa`akum fil-arḍi tattakhiżụna min suhụlihā quṣụraw wa tan-ḥitụnal-jibāla buyụtā, fażkurū ālā`allāhi wa lā ta\'ṡau fil-arḍi mufsidīn'),
(1028, 7, 75, 'قَالَ الْمَلَاُ الَّذِيْنَ اسْتَكْبَرُوْا مِنْ قَوْمِهٖ لِلَّذِيْنَ اسْتُضْعِفُوْا لِمَنْ اٰمَنَ مِنْهُمْ اَتَعْلَمُوْنَ اَنَّ صٰلِحًا مُّرْسَلٌ مِّنْ رَّبِّهٖۗ قَالُوْٓا اِنَّا بِمَآ اُرْسِلَ بِهٖ مُؤْمِنُوْنَ', 'qālal-mala`ullażīnastakbarụ ming qaumihī lillażīnastuḍ\'ifụ liman āmana min-hum a ta\'lamụna anna ṣāliḥam mursalum mir rabbih, qālū innā bimā ursila bihī mu`minụn'),
(1029, 7, 76, 'قَالَ الَّذِيْنَ اسْتَكْبَرُوْٓا اِنَّا بِالَّذِيْٓ اٰمَنْتُمْ بِهٖ كٰفِرُوْنَ', 'qālallażīnastakbarū innā billażī āmantum bihī kāfirụn'),
(1030, 7, 77, 'فَعَقَرُوا النَّاقَةَ وَعَتَوْا عَنْ اَمْرِ رَبِّهِمْ وَقَالُوْا يٰصٰلِحُ ائْتِنَا بِمَا تَعِدُنَآ اِنْ كُنْتَ مِنَ الْمُرْسَلِيْنَ', 'fa \'aqarun-nāqata wa \'atau \'an amri rabbihim wa qālụ yā ṣāliḥu`tinā bimā ta\'idunā ing kunta minal-mursalīn'),
(1031, 7, 78, 'فَاَخَذَتْهُمُ الرَّجْفَةُ فَاَصْبَحُوْا فِيْ دَارِهِمْ جٰثِمِيْنَ', 'fa akhażat-humur-rajfatu fa aṣbaḥụ fī dārihim jāṡimīn'),
(1032, 7, 79, 'فَتَوَلّٰى عَنْهُمْ وَقَالَ يٰقَوْمِ لَقَدْ اَبْلَغْتُكُمْ رِسَالَةَ رَبِّيْ وَنَصَحْتُ لَكُمْ وَلٰكِنْ لَّا تُحِبُّوْنَ النّٰصِحِيْنَ', 'fa tawallā \'an-hum wa qāla yā qaumi laqad ablagtukum risālata rabbī wa naṣaḥtu lakum wa lākil lā tuḥibbụnan-nāṣiḥīn'),
(1033, 7, 80, 'وَلُوْطًا اِذْ قَالَ لِقَوْمِهٖٓ اَتَأْتُوْنَ الْفَاحِشَةَ مَا سَبَقَكُمْ بِهَا مِنْ اَحَدٍ مِّنَ الْعٰلَمِيْنَ', 'wa lụṭan iż qāla liqaumihī a ta`tụnal-fāḥisyata mā sabaqakum bihā min aḥadim minal-\'ālamīn'),
(1034, 7, 81, 'اِنَّكُمْ لَتَأْتُوْنَ الرِّجَالَ شَهْوَةً مِّنْ دُوْنِ النِّسَاۤءِۗ بَلْ اَنْتُمْ قَوْمٌ مُّسْرِفُوْنَ', 'innakum lata`tụnar-rijāla syahwatam min dụnin-nisā`, bal antum qaumum musrifụn'),
(1035, 7, 82, 'وَمَا كَانَ جَوَابَ قَوْمِهٖٓ اِلَّآ اَنْ قَالُوْٓا اَخْرِجُوْهُمْ مِّنْ قَرْيَتِكُمْۚ اِنَّهُمْ اُنَاسٌ يَّتَطَهَّرُوْنَ', 'wa mā kāna jawāba qaumihī illā ang qālū akhrijụhum ming qaryatikum, innahum unāsuy yataṭahharụn'),
(1036, 7, 83, 'فَاَنْجَيْنٰهُ وَاَهْلَهٗٓ اِلَّا امْرَاَتَهٗ كَانَتْ مِنَ الْغٰبِرِيْنَ', 'fa anjaināhu wa ahlahū illamra`atahụ kānat minal-gābirīn'),
(1037, 7, 84, 'وَاَمْطَرْنَا عَلَيْهِمْ مَّطَرًاۗ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُجْرِمِيْنَ', 'wa amṭarnā \'alaihim maṭarā, fanẓur kaifa kāna \'āqibatul-mujrimīn'),
(1038, 7, 85, 'وَاِلٰى مَدْيَنَ اَخَاهُمْ شُعَيْبًاۗ قَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ قَدْ جَاۤءَتْكُمْ بَيِّنَةٌ مِّنْ رَّبِّكُمْ فَاَوْفُوا الْكَيْلَ وَالْمِيْزَانَ وَلَا تَبْخَسُوا النَّاسَ اَشْيَاۤءَهُمْ وَلَا تُفْسِدُوْا فِى الْاَرْضِ بَعْدَ اِصْلَاحِهَاۗ ذٰلِكُمْ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ مُّؤْمِنِيْنَۚ', 'wa ilā madyana akhāhum syu\'aibā, qāla yā qaumi\'budullāha mā lakum min ilāhin gairuh, qad jā`atkum bayyinatum mir rabbikum fa auful-kaila wal mīzāna wa lā tabkhasun-nāsa asy-yā`ahum wa lā tufsidụ fil-arḍi ba\'da iṣlāḥihā, żālikum khairul lakum ing kuntum mu`minīn'),
(1039, 7, 86, 'وَلَا تَقْعُدُوْا بِكُلِّ صِرَاطٍ تُوْعِدُوْنَ وَتَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ مَنْ اٰمَنَ بِهٖ وَتَبْغُوْنَهَا عِوَجًاۚ وَاذْكُرُوْٓا اِذْ كُنْتُمْ قَلِيْلًا فَكَثَّرَكُمْۖ وَانْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِيْنَ', 'wa lā taq\'udụ bikulli ṣirāṭin tụ\'idụna wa taṣuddụna \'an sabīlillāhi man āmana bihī wa tabgụnahā \'iwajā, ważkurū iż kuntum qalīlan fa kaṡṡarakum wanẓurụ kaifa kāna \'āqibatul-mufsidīn'),
(1040, 7, 87, 'وَاِنْ كَانَ طَاۤىِٕفَةٌ مِّنْكُمْ اٰمَنُوْا بِالَّذِيْٓ اُرْسِلْتُ بِهٖ وَطَاۤىِٕفَةٌ لَّمْ يُؤْمِنُوْا فَاصْبِرُوْا حَتّٰى يَحْكُمَ اللّٰهُ بَيْنَنَاۚ وَهُوَ خَيْرُ الْحٰكِمِيْنَ ۔', 'wa ing kāna ṭā`ifatum mingkum āmanụ billażī ursiltu bihī wa ṭā`ifatul lam yu`minụ faṣbirụ ḥattā yaḥkumallāhu bainanā, wa huwa khairul-ḥākimīn'),
(1041, 7, 88, 'قَالَ الْمَلَاُ الَّذِيْنَ اسْتَكْبَرُوْا مِنْ قَوْمِهٖ لَنُخْرِجَنَّكَ يٰشُعَيْبُ وَالَّذِيْنَ اٰمَنُوْا مَعَكَ مِنْ قَرْيَتِنَآ اَوْ لَتَعُوْدُنَّ فِيْ مِلَّتِنَاۗ قَالَ اَوَلَوْ كُنَّا كَارِهِيْنَ', 'qālal-mala`ullażīnastakbarụ ming qaumihī lanukhrijannaka yā syu\'aibu wallażīna āmanụ ma\'aka ming qaryatinā au lata\'ụdunna fī millatinā, qāla a walau kunnā kārihīn'),
(1042, 7, 89, 'قَدِ افْتَرَيْنَا عَلَى اللّٰهِ كَذِبًا اِنْ عُدْنَا فِيْ مِلَّتِكُمْ بَعْدَ اِذْ نَجّٰىنَا اللّٰهُ مِنْهَاۗ وَمَا يَكُوْنُ لَنَآ اَنْ نَّعُوْدَ فِيْهَآ اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ رَبُّنَاۗ وَسِعَ رَبُّنَا كُلَّ شَيْءٍ عِلْمًاۗ عَلَى اللّٰهِ تَوَكَّلْنَاۗ رَبَّنَا افْتَحْ بَيْنَنَا وَبَيْنَ قَوْمِنَا بِالْحَقِّ وَاَنْتَ خَيْرُ الْفَاتِحِيْنَ', 'qadiftarainā \'alallāhi każiban in \'udnā fī millatikum ba\'da iż najjānallāhu min-hā, wa mā yakụnu lanā an na\'ụda fīhā illā ay yasyā`allāhu rabbunā, wasi\'a rabbunā kulla syai`in \'ilmā, \'alallāhi tawakkalnā, rabbanaftaḥ bainanā wa baina qauminā bil-ḥaqqi wa anta khairul-fātiḥīn'),
(1043, 7, 90, 'وَقَالَ الْمَلَاُ الَّذِيْنَ كَفَرُوْا مِنْ قَوْمِهٖ لَىِٕنِ اتَّبَعْتُمْ شُعَيْبًا ِانَّكُمْ اِذًا لَّخٰسِرُوْنَ', 'wa qālal-mala`ullażīna kafarụ ming qaumihī la`inittaba\'tum syu\'aiban innakum iżal lakhāsirụn'),
(1044, 7, 91, 'فَاَخَذَتْهُمُ الرَّجْفَةُ فَاَصْبَحُوْا فِيْ دَارِهِمْ جٰثِمِيْنَۙ', 'fa akhażat-humur-rajfatu fa aṣbaḥụ fī dārihim jāṡimīn'),
(1045, 7, 92, 'الَّذِيْنَ كَذَّبُوْا شُعَيْبًا كَاَنْ لَّمْ يَغْنَوْا فِيْهَاۚ اَلَّذِيْنَ كَذَّبُوْا شُعَيْبًا كَانُوْا هُمُ الْخٰسِرِيْنَ', 'allażīna każżabụ syu\'aibang ka`al lam yagnau fīhā, allażīna każżabụ syu\'aibang kānụ humul-khāsirīn'),
(1046, 7, 93, 'فَتَوَلّٰى عَنْهُمْ وَقَالَ يٰقَوْمِ لَقَدْ اَبْلَغْتُكُمْ رِسٰلٰتِ رَبِّيْ وَنَصَحْتُ لَكُمْۚ فَكَيْفَ اٰسٰى عَلٰى قَوْمٍ كٰفِرِيْنَ', 'fa tawallā \'an-hum wa qāla yā qaumi laqad ablagtukum risālāti rabbī wa naṣaḥtu lakum, fa kaifa āsā \'alā qauming kāfirīn'),
(1047, 7, 94, 'وَمَآ اَرْسَلْنَا فِيْ قَرْيَةٍ مِّنْ نَّبِيٍّ اِلَّآ اَخَذْنَآ اَهْلَهَا بِالْبَأْسَاۤءِ وَالضَّرَّاۤءِ لَعَلَّهُمْ يَضَّرَّعُوْنَ', 'wa mā arsalnā fī qaryatim min nabiyyin illā akhażnā ahlahā bil-ba`sā`i waḍ-ḍarrā`i la\'allahum yaḍḍarra\'ụn'),
(1048, 7, 95, 'ثُمَّ بَدَّلْنَا مَكَانَ السَّيِّئَةِ الْحَسَنَةَ حَتّٰى عَفَوْا وَّقَالُوْا قَدْ مَسَّ اٰبَاۤءَنَا الضَّرَّاۤءُ وَالسَّرَّاۤءُ فَاَخَذْنٰهُمْ بَغْتَةً وَّهُمْ لَا يَشْعُرُوْنَ', 'ṡumma baddalnā makānas-sayyi`atil-ḥasanata ḥattā \'afaw wa qālụ qad massa ābā`anaḍ-ḍarrā`u was-sarrā`u fa akhażnāhum bagtataw wa hum lā yasy\'urụn'),
(1049, 7, 96, 'وَلَوْ اَنَّ اَهْلَ الْقُرٰٓى اٰمَنُوْا وَاتَّقَوْا لَفَتَحْنَا عَلَيْهِمْ بَرَكٰتٍ مِّنَ السَّمَاۤءِ وَالْاَرْضِ وَلٰكِنْ كَذَّبُوْا فَاَخَذْنٰهُمْ بِمَا كَانُوْا يَكْسِبُوْنَ', 'walau anna ahlal-qurā āmanụ wattaqau lafataḥnā \'alaihim barakātim minas-samā`i wal-arḍi wa lāking każżabụ fa akhażnāhum bimā kānụ yaksibụn'),
(1050, 7, 97, 'اَفَاَمِنَ اَهْلُ الْقُرٰٓى اَنْ يَّأْتِيَهُمْ بَأْسُنَا بَيَاتًا وَّهُمْ نَاۤىِٕمُوْنَۗ', 'a fa amina ahlul-qurā ay ya`tiyahum ba`sunā bayātaw wa hum nā`imụn'),
(1051, 7, 98, 'اَوَاَمِنَ اَهْلُ الْقُرٰٓى اَنْ يَّأْتِيَهُمْ بَأْسُنَا ضُحًى وَّهُمْ يَلْعَبُوْنَ', 'a wa amina ahlul-qurā ay ya`tiyahum ba`sunā ḍuḥaw wa hum yal\'abụn'),
(1052, 7, 99, 'اَفَاَمِنُوْا مَكْرَ اللّٰهِۚ فَلَا يَأْمَنُ مَكْرَ اللّٰهِ اِلَّا الْقَوْمُ الْخٰسِرُوْنَ', 'a fa aminụ makrallāh, fa lā ya`manu makrallāhi illal-qaumul-khāsirụn'),
(1053, 7, 100, 'اَوَلَمْ يَهْدِ لِلَّذِيْنَ يَرِثُوْنَ الْاَرْضَ مِنْۢ بَعْدِ اَهْلِهَآ اَنْ لَّوْ نَشَاۤءُ اَصَبْنٰهُمْ بِذُنُوْبِهِمْۚ وَنَطْبَعُ عَلٰى قُلُوْبِهِمْ فَهُمْ لَا يَسْمَعُوْنَ', 'a wa lam yahdi lillażīna yariṡụnal-arḍa mim ba\'di ahlihā al lau nasyā`u aṣabnāhum biżunụbihim, wa naṭba\'u \'alā qulụbihim fa hum lā yasma\'ụn'),
(1054, 7, 101, 'تِلْكَ الْقُرٰى نَقُصُّ عَلَيْكَ مِنْ اَنْۢبَاۤىِٕهَاۚ وَلَقَدْ جَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِۚ فَمَا كَانُوْا لِيُؤْمِنُوْا بِمَا كَذَّبُوْا مِنْ قَبْلُۗ كَذٰلِكَ يَطْبَعُ اللّٰهُ عَلٰى قُلُوْبِ الْكٰفِرِيْنَ', 'tilkal-qurā naquṣṣu \'alaika min ambā`ihā, wa laqad jā`at-hum rusuluhum bil-bayyināt, fa mā kānụ liyu`minụ bimā każżabụ ming qabl, każālika yaṭba\'ullāhu \'alā qulụbil-kāfirīn'),
(1055, 7, 102, 'وَمَا وَجَدْنَا لِاَكْثَرِهِمْ مِّنْ عَهْدٍۚ وَاِنْ وَّجَدْنَآ اَكْثَرَهُمْ لَفٰسِقِيْنَ', 'wa mā wajadnā li`akṡarihim min \'ahd, wa iw wajadnā akṡarahum lafāsiqīn'),
(1056, 7, 103, 'ثُمَّ بَعَثْنَا مِنْۢ بَعْدِهِمْ مُّوْسٰى بِاٰيٰتِنَآ اِلٰى فِرْعَوْنَ وَمَلَا۟ىِٕهٖ فَظَلَمُوْا بِهَاۚ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِيْنَ', 'ṡumma ba\'aṡnā mim ba\'dihim mụsā bi`āyātinā ilā fir\'auna wa mala`ihī fa ẓalamụ bihā, fanẓur kaifa kāna \'āqibatul-mufsidīn'),
(1057, 7, 104, 'وَقَالَ مُوْسٰى يٰفِرْعَوْنُ اِنِّيْ رَسُوْلٌ مِّنْ رَّبِّ الْعٰلَمِيْنَۙ', 'wa qāla mụsā yā fir\'aunu innī rasụlum mir rabbil-\'ālamīn'),
(1058, 7, 105, 'حَقِيْقٌ عَلٰٓى اَنْ لَّآ اَقُوْلَ عَلَى اللّٰهِ اِلَّا الْحَقَّۗ قَدْ جِئْتُكُمْ بِبَيِّنَةٍ مِّنْ رَّبِّكُمْ فَاَرْسِلْ مَعِيَ بَنِيْٓ اِسْرَاۤءِيْلَ ۗ', 'ḥaqīqun \'alā al lā aqụla \'alallāhi illal-ḥaqq, qad ji`tukum bibayyinatim mir rabbikum fa arsil ma\'iya banī isrā`īl'),
(1059, 7, 106, 'قَالَ اِنْ كُنْتَ جِئْتَ بِاٰيَةٍ فَأْتِ بِهَآ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ', 'qāla ing kunta ji`ta bi`āyatin fa`ti bihā ing kunta minaṣ-ṣādiqīn'),
(1060, 7, 107, 'فَاَلْقٰى عَصَاهُ فَاِذَا هِيَ ثُعْبَانٌ مُّبِيْنٌ ۖ', 'fa alqā \'aṣāhu fa iżā hiya ṡu\'bānum mubīn'),
(1061, 7, 108, 'وَّنَزَعَ يَدَهٗ فَاِذَا هِيَ بَيْضَاۤءُ لِلنّٰظِرِيْنَ', 'wa naza\'a yadahụ fa iżā hiya baiḍā`u lin-nāẓirīn'),
(1062, 7, 109, 'قَالَ الْمَلَاُ مِنْ قَوْمِ فِرْعَوْنَ اِنَّ هٰذَا لَسٰحِرٌ عَلِيْمٌۙ', 'qālal-mala`u ming qaumi fir\'auna inna hāżā lasāḥirun \'alīm'),
(1063, 7, 110, 'يُّرِيْدُ اَنْ يُّخْرِجَكُمْ مِّنْ اَرْضِكُمْ ۚ فَمَاذَا تَأْمُرُوْنَ', 'yurīdu ay yukhrijakum min arḍikum, fa māżā ta`murụn'),
(1064, 7, 111, 'قَالُوْآ اَرْجِهْ وَاَخَاهُ وَاَرْسِلْ فِى الْمَدَاۤىِٕنِ حٰشِرِيْنَۙ', 'qālū arjih wa akhāhu wa arsil fil-madā`ini ḥāsyirīn'),
(1065, 7, 112, 'يَأْتُوْكَ بِكُلِّ سٰحِرٍ عَلِيْمٍ', 'ya`tụka bikulli sāḥirin \'alīm'),
(1066, 7, 113, 'وَجَاۤءَ السَّحَرَةُ فِرْعَوْنَ قَالُوْٓا اِنَّ لَنَا لَاَجْرًا اِنْ كُنَّا نَحْنُ الْغٰلِبِيْنَ', 'wa jā`as-saḥaratu fir\'auna qālū inna lanā la`ajran ing kunnā naḥnul-gālibīn'),
(1067, 7, 114, 'قَالَ نَعَمْ وَاِنَّكُمْ لَمِنَ الْمُقَرَّبِيْنَ', 'qāla na\'am wa innakum laminal-muqarrabīn'),
(1068, 7, 115, 'قَالُوْا يٰمُوْسٰٓى اِمَّآ اَنْ تُلْقِيَ وَاِمَّآ اَنْ نَّكُوْنَ نَحْنُ الْمُلْقِيْنَ', 'qālụ yā mụsā immā an tulqiya wa immā an nakụna naḥnul-mulqīn'),
(1069, 7, 116, 'قَالَ اَلْقُوْاۚ فَلَمَّآ اَلْقَوْا سَحَرُوْٓا اَعْيُنَ النَّاسِ وَاسْتَرْهَبُوْهُمْ وَجَاۤءُوْ بِسِحْرٍ عَظِيْمٍ', 'qāla alqụ, fa lammā alqau saḥarū a\'yunan-nāsi wastar-habụhum wa jā`ụ bisiḥrin \'aẓīm'),
(1070, 7, 117, '۞ وَاَوْحَيْنَآ اِلٰى مُوْسٰٓى اَنْ اَلْقِ عَصَاكَۚ فَاِذَا هِيَ تَلْقَفُ مَا يَأْفِكُوْنَۚ', 'wa auḥainā ilā mụsā an alqi \'aṣāk, fa iżā hiya talqafu mā ya`fikụn'),
(1071, 7, 118, 'فَوَقَعَ الْحَقُّ وَبَطَلَ مَا كَانُوْا يَعْمَلُوْنَۚ', 'fa waqa\'al-ḥaqqu wa baṭala mā kānụ ya\'malụn'),
(1072, 7, 119, 'فَغُلِبُوْا هُنَالِكَ وَانْقَلَبُوْا صٰغِرِيْنَۚ', 'fa gulibụ hunālika wangqalabụ ṣāgirīn'),
(1073, 7, 120, 'وَاُلْقِيَ السَّحَرَةُ سٰجِدِيْنَۙ', 'wa ulqiyas-saḥaratu sājidīn'),
(1074, 7, 121, 'قَالُوْٓا اٰمَنَّا بِرَبِّ الْعٰلَمِيْنَۙ', 'qālū āmannā birabbil-\'ālamīn'),
(1075, 7, 122, 'رَبِّ مُوْسٰى وَهٰرُوْنَ', 'rabbi mụsā wa hārụn'),
(1076, 7, 123, 'قَالَ فِرْعَوْنُ اٰمَنْتُمْ بِهٖ قَبْلَ اَنْ اٰذَنَ لَكُمْۚ اِنَّ هٰذَا لَمَكْرٌ مَّكَرْتُمُوْهُ فِى الْمَدِيْنَةِ لِتُخْرِجُوْا مِنْهَآ اَهْلَهَاۚ فَسَوْفَ تَعْلَمُوْنَ', 'qāla fir\'aunu āmantum bihī qabla an āżana lakum, inna hāżā lamakrum makartumụhu fil-madīnati litukhrijụ min-hā ahlahā, fa saufa ta\'lamụn'),
(1077, 7, 124, 'لَاُقَطِّعَنَّ اَيْدِيَكُمْ وَاَرْجُلَكُمْ مِّنْ خِلَافٍ ثُمَّ لَاُصَلِّبَنَّكُمْ اَجْمَعِيْنَ', 'la`uqaṭṭi\'anna aidiyakum wa arjulakum min khilāfin ṡumma la`uṣallibannakum ajma\'īn'),
(1078, 7, 125, 'قَالُوْٓا اِنَّآ اِلٰى رَبِّنَا مُنْقَلِبُوْنَۙ', 'qālū innā ilā rabbinā mungqalibụn'),
(1079, 7, 126, 'وَمَا تَنْقِمُ مِنَّآ اِلَّآ اَنْ اٰمَنَّا بِاٰيٰتِ رَبِّنَا لَمَّا جَاۤءَتْنَا ۗرَبَّنَآ اَفْرِغْ عَلَيْنَا صَبْرًا وَّتَوَفَّنَا مُسْلِمِيْنَ', 'wa mā tangqimu minnā illā an āmannā bi`āyāti rabbinā lammā jā`atnā, rabbanā afrig \'alainā ṣabraw wa tawaffanā muslimīn'),
(1080, 7, 127, 'وَقَالَ الْمَلَاُ مِنْ قَوْمِ فِرْعَوْنَ اَتَذَرُ مُوْسٰى وَقَوْمَهٗ لِيُفْسِدُوْا فِى الْاَرْضِ وَيَذَرَكَ وَاٰلِهَتَكَۗ قَالَ سَنُقَتِّلُ اَبْنَاۤءَهُمْ وَنَسْتَحْيٖ نِسَاۤءَهُمْۚ وَاِنَّا فَوْقَهُمْ قَاهِرُوْنَ', 'wa qālal-mala`u ming qaumi fir\'auna a tażaru mụsā wa qaumahụ liyufsidụ fil-arḍi wa yażaraka wa ālihatak, qāla sanuqattilu abnā`ahum wa nastaḥyī nisā`ahum, wa innā fauqahum qāhirụn'),
(1081, 7, 128, 'قَالَ مُوْسٰى لِقَوْمِهِ اسْتَعِيْنُوْا بِاللّٰهِ وَاصْبِرُوْاۚ اِنَّ الْاَرْضَ لِلّٰهِ ۗيُوْرِثُهَا مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖۗ وَالْعَاقِبَةُ لِلْمُتَّقِيْنَ', 'qāla mụsā liqaumihista\'īnụ billāhi waṣbirụ, innal-arḍa lillāh, yụriṡuhā may yasyā`u min \'ibādih, wal-\'āqibatu lil-muttaqīn'),
(1082, 7, 129, 'قَالُوْٓا اُوْذِيْنَا مِنْ قَبْلِ اَنْ تَأْتِيَنَا وَمِنْۢ بَعْدِ مَا جِئْتَنَا ۗقَالَ عَسٰى رَبُّكُمْ اَنْ يُّهْلِكَ عَدُوَّكُمْ وَيَسْتَخْلِفَكُمْ فِى الْاَرْضِ فَيَنْظُرَ كَيْفَ تَعْمَلُوْنَ', 'qālū ụżīnā ming qabli an ta`tiyana wa mim ba\'di mā ji`tana, qāla \'asā rabbukum ay yuhlika \'aduwwakum wa yastakhlifakum fil-arḍi fa yanẓura kaifa ta\'malụn'),
(1083, 7, 130, 'وَلَقَدْ اَخَذْنَآ اٰلَ فِرْعَوْنَ بِالسِّنِيْنَ وَنَقْصٍ مِّنَ الثَّمَرٰتِ لَعَلَّهُمْ يَذَّكَّرُوْنَ', 'wa laqad akhażnā āla fir\'auna bis-sinīna wa naqṣim minaṡ-ṡamarāti la\'allahum yażżakkarụn'),
(1084, 7, 131, 'فَاِذَا جَاۤءَتْهُمُ الْحَسَنَةُ قَالُوْا لَنَا هٰذِهٖ ۚوَاِنْ تُصِبْهُمْ سَيِّئَةٌ يَّطَّيَّرُوْا بِمُوْسٰى وَمَنْ مَّعَهٗۗ اَلَآ اِنَّمَا طٰۤىِٕرُهُمْ عِنْدَ اللّٰهِ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ', 'fa iżā jā`at-humul-ḥasanatu qālụ lanā hāżih, wa in tuṣib-hum sayyi`atuy yaṭṭayyarụ bimụsā wa mam ma\'ah, alā innamā ṭā`iruhum \'indallāhi wa lākinna akṡarahum lā ya\'lamụn'),
(1085, 7, 132, 'وَقَالُوْا مَهْمَا تَأْتِنَا بِهٖ مِنْ اٰيَةٍ لِّتَسْحَرَنَا بِهَاۙ فَمَا نَحْنُ لَكَ بِمُؤْمِنِيْنَ', 'wa qālụ mahmā ta`tinā bihī min āyatil litas-ḥaranā bihā fa mā naḥnu laka bimu`minīn'),
(1086, 7, 133, 'فَاَرْسَلْنَا عَلَيْهِمُ الطُّوْفَانَ وَالْجَرَادَ وَالْقُمَّلَ وَالضَّفَادِعَ وَالدَّمَ اٰيٰتٍ مُّفَصَّلٰتٍۗ فَاسْتَكْبَرُوْا وَكَانُوْا قَوْمًا مُّجْرِمِيْنَ', 'fa arsalnā \'alaihimuṭ-ṭụfāna wal-jarāda wal-qummala waḍ-ḍafādi\'a wad-dama āyātim mufaṣṣalāt, fastakbarụ wa kānụ qaumam mujrimīn'),
(1087, 7, 134, 'وَلَمَّا وَقَعَ عَلَيْهِمُ الرِّجْزُ قَالُوْا يٰمُوْسَى ادْعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِنْدَكَۚ لَىِٕنْ كَشَفْتَ عَنَّا الرِّجْزَ لَنُؤْمِنَنَّ لَكَ وَلَنُرْسِلَنَّ مَعَكَ بَنِيْٓ اِسْرَاۤءِيْلَ ۚ', 'wa lammā waqa\'a \'alaihimur-rijzu qālụ yā mụsad\'u lanā rabbaka bimā \'ahida \'indak, la`ing kasyafta \'annar-rijza lanu`minanna laka wa lanursilanna ma\'aka banī isrā`īl'),
(1088, 7, 135, 'فَلَمَّا كَشَفْنَا عَنْهُمُ الرِّجْزَ اِلٰٓى اَجَلٍ هُمْ بَالِغُوْهُ اِذَا هُمْ يَنْكُثُوْنَ', 'fa lammā kasyafnā \'an-humur-rijza ilā ajalin hum bāligụhu iżā hum yangkuṡụn'),
(1089, 7, 136, 'فَانْتَقَمْنَا مِنْهُمْ فَاَغْرَقْنٰهُمْ فِى الْيَمِّ بِاَنَّهُمْ كَذَّبُوْا بِاٰيٰتِنَا وَكَانُوْا عَنْهَا غٰفِلِيْنَ', 'fantaqamnā min-hum fa agraqnāhum fil-yammi bi`annahum każżabụ bi`āyātinā wa kānụ \'an-hā gāfilīn'),
(1090, 7, 137, 'وَاَوْرَثْنَا الْقَوْمَ الَّذِيْنَ كَانُوْا يُسْتَضْعَفُوْنَ مَشَارِقَ الْاَرْضِ وَمَغَارِبَهَا الَّتِيْ بٰرَكْنَا فِيْهَاۗ وَتَمَّتْ كَلِمَتُ رَبِّكَ الْحُسْنٰى عَلٰى بَنِيْٓ اِسْرَاۤءِيْلَۙ بِمَا صَبَرُوْاۗ وَدَمَّرْنَا مَا كَانَ يَصْنَعُ فِرْعَوْنُ وَقَوْمُهٗ وَمَا كَانُوْا يَعْرِشُوْنَ', 'wa auraṡnal-qaumallażīna kānụ yustaḍ\'afụna masyāriqal-arḍi wa magāribahallatī bāraknā fīhā, wa tammat kalimatu rabbikal-ḥusnā \'alā banī isrā`īla bimā ṣabarụ, wa dammarnā mā kāna yaṣna\'u fir\'aunu wa qaumuhụ wa mā kānụ ya\'risyụn'),
(1091, 7, 138, 'وَجَاوَزْنَا بِبَنِيْٓ اِسْرَاۤءِيْلَ الْبَحْرَ فَاَتَوْا عَلٰى قَوْمٍ يَّعْكُفُوْنَ عَلٰٓى اَصْنَامٍ لَّهُمْ ۚقَالُوْا يٰمُوْسَى اجْعَلْ لَّنَآ اِلٰهًا كَمَا لَهُمْ اٰلِهَةٌ ۗقَالَ اِنَّكُمْ قَوْمٌ تَجْهَلُوْنَ', 'wa jāwaznā bibanī isrā`īlal-baḥra fa atau \'alā qaumiy ya\'kufụna \'alā aṣnāmil lahum, qālụ yā mụsaj\'al lanā ilāhang kamā lahum ālihah, qāla innakum qaumun taj-halụn'),
(1092, 7, 139, 'اِنَّ هٰٓؤُلَاۤءِ مُتَبَّرٌ مَّا هُمْ فِيْهِ وَبٰطِلٌ مَّا كَانُوْا يَعْمَلُوْنَ', 'inna hā`ulā`i mutabbarum mā hum fīhi wa bāṭilum mā kānụ ya\'malụn'),
(1093, 7, 140, 'قَالَ اَغَيْرَ اللّٰهِ اَبْغِيْكُمْ اِلٰهًا وَّهُوَ فَضَّلَكُمْ عَلَى الْعٰلَمِيْنَ', 'qāla a gairallāhi abgīkum ilāhaw wa huwa faḍḍalakum \'alal-\'ālamīn'),
(1094, 7, 141, 'وَاِذْ اَنْجَيْنٰكُمْ مِّنْ اٰلِ فِرْعَوْنَ يَسُوْمُوْنَكُمْ سُوْۤءَ الْعَذَابِۚ يُقَتِّلُوْنَ اَبْنَاۤءَكُمْ وَيَسْتَحْيُوْنَ نِسَاۤءَكُمْۗ وَفِيْ ذٰلِكُمْ بَلَاۤءٌ مِّنْ رَّبِّكُمْ عَظِيْمٌ', 'wa iż anjainākum min āli fir\'auna yasụmụnakum sū`al-\'ażāb, yuqattilụna abnā`akum wa yastaḥyụna nisā`akum, wa fī żālikum balā`um mir rabbikum \'aẓīm'),
(1095, 7, 142, '۞ وَوٰعَدْنَا مُوْسٰى ثَلٰثِيْنَ لَيْلَةً وَّاَتْمَمْنٰهَا بِعَشْرٍ فَتَمَّ مِيْقَاتُ رَبِّهٖٓ اَرْبَعِيْنَ لَيْلَةً ۚوَقَالَ مُوْسٰى لِاَخِيْهِ هٰرُوْنَ اخْلُفْنِيْ فِيْ قَوْمِيْ وَاَصْلِحْ وَلَا تَتَّبِعْ سَبِيْلَ الْمُفْسِدِيْنَ', 'wa wā\'adnā mụsā ṡalāṡīna lailataw wa atmamnāhā bi\'asyrin fa tamma mīqātu rabbihī arba\'īna lailah, wa qāla mụsā li`akhīhi hārụnakhlufnī fī qaumī wa aṣliḥ wa lā tattabi\' sabīlal-mufsidīn'),
(1096, 7, 143, 'وَلَمَّا جَاۤءَ مُوْسٰى لِمِيْقَاتِنَا وَكَلَّمَهٗ رَبُّهٗۙ قَالَ رَبِّ اَرِنِيْٓ اَنْظُرْ اِلَيْكَۗ قَالَ لَنْ تَرٰىنِيْ وَلٰكِنِ انْظُرْ اِلَى الْجَبَلِ فَاِنِ اسْتَقَرَّ مَكَانَهٗ فَسَوْفَ تَرٰىنِيْۚ فَلَمَّا تَجَلّٰى رَبُّهٗ لِلْجَبَلِ جَعَلَهٗ دَكًّا وَّخَرَّ مُوْسٰى صَعِقًاۚ فَلَمَّآ اَفَاقَ قَالَ سُبْحٰنَكَ تُبْتُ اِلَيْكَ وَاَنَا۠ اَوَّلُ الْمُؤْمِنِيْنَ', 'wa lammā jā`a mụsā limīqātinā wa kallamahụ rabbuhụ qāla rabbi arinī anẓur ilaīk, qāla lan tarānī wa lākininẓur ilal-jabali fa inistaqarra makānahụ fa saufa tarānī, fa lammā tajallā rabbuhụ lil-jabali ja\'alahụ dakkaw wa kharra mụsā ṣa\'iqā, fa lammā afāqa qāla sub-ḥānaka tubtu ilaika wa ana awwalul-mu`minīn'),
(1097, 7, 144, 'قَالَ يٰمُوْسٰٓى اِنِّى اصْطَفَيْتُكَ عَلَى النَّاسِ بِرِسٰلٰتِيْ وَبِكَلَامِيْ ۖفَخُذْ مَآ اٰتَيْتُكَ وَكُنْ مِّنَ الشّٰكِرِيْنَ', 'qāla yā mụsā inniṣṭafaituka \'alan-nāsi birisālātī wa bikalāmī fa khuż mā ātaituka wa kum minasy-syākirīn'),
(1098, 7, 145, 'وَكَتَبْنَا لَهٗ فِى الْاَلْوَاحِ مِنْ كُلِّ شَيْءٍ مَّوْعِظَةً وَّتَفْصِيْلًا لِّكُلِّ شَيْءٍۚ فَخُذْهَا بِقُوَّةٍ وَّأْمُرْ قَوْمَكَ يَأْخُذُوْا بِاَحْسَنِهَا ۗسَاُورِيْكُمْ دَارَ الْفٰسِقِيْنَ', 'wa katabnā lahụ fil-alwāḥi ming kulli syai`im mau\'iẓataw wa tafṣīlal likulli syaī`, fa khuż-hā biquwwatiw wa`mur qaumaka ya`khużụ bi`aḥsanihā, sa`urīkum dāral-fāsiqīn'),
(1099, 7, 146, 'سَاَصْرِفُ عَنْ اٰيٰتِيَ الَّذِيْنَ يَتَكَبَّرُوْنَ فِى الْاَرْضِ بِغَيْرِ الْحَقِّۗ وَاِنْ يَّرَوْا كُلَّ اٰيَةٍ لَّا يُؤْمِنُوْا بِهَاۚ وَاِنْ يَّرَوْا سَبِيْلَ الرُّشْدِ لَا يَتَّخِذُوْهُ سَبِيْلًاۚ وَاِنْ يَّرَوْا سَبِيْلَ الْغَيِّ يَتَّخِذُوْهُ سَبِيْلًاۗ ذٰلِكَ بِاَنَّهُمْ كَذَّبُوْا بِاٰيٰتِنَا وَكَانُوْا عَنْهَا غٰفِلِيْنَ', 'sa aṣrifu \'an āyātiyallażīna yatakabbarụna fil-arḍi bigairil-ḥaqq, wa iy yarau kulla āyatil lā yu`minụ bihā, wa iy yarau sabīlar-rusydi lā yattakhiżụhu sabīlā, wa iy yarau sabīlal-gayyi yattakhiżụhu sabīlā, żālika bi`annahum każżabụ bi`āyātinā wa kānụ \'an-hā gāfilīn'),
(1100, 7, 147, 'وَالَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَلِقَاۤءِ الْاٰخِرَةِ حَبِطَتْ اَعْمَالُهُمْۗ هَلْ يُجْزَوْنَ اِلَّا مَا كَانُوْا يَعْمَلُوْنَ', 'wallażīna każżabụ bi`āyātinā wa liqā`il-ākhirati ḥabiṭat a\'māluhum, hal yujzauna illā mā kānụ ya\'malụn'),
(1101, 7, 148, 'وَاتَّخَذَ قَوْمُ مُوْسٰى مِنْۢ بَعْدِهٖ مِنْ حُلِيِّهِمْ عِجْلًا جَسَدًا لَّهٗ خُوَارٌۗ اَلَمْ يَرَوْا اَنَّهٗ لَا يُكَلِّمُهُمْ وَلَا يَهْدِيْهِمْ سَبِيْلًاۘ اِتَّخَذُوْهُ وَكَانُوْا ظٰلِمِيْنَ', 'wattakhaża qaumu mụsā mim ba\'dihī min ḥuliyyihim \'ijlan jasadal lahụ khuwār, a lam yarau annahụ lā yukallimuhum wa lā yahdīhim sabīlā, ittakhażụhu wa kānụ ẓālimīn'),
(1102, 7, 149, 'وَلَمَّا سُقِطَ فِيْٓ اَيْدِيْهِمْ وَرَاَوْا اَنَّهُمْ قَدْ ضَلُّوْاۙ قَالُوْا لَىِٕنْ لَّمْ يَرْحَمْنَا رَبُّنَا وَيَغْفِرْ لَنَا لَنَكُوْنَنَّ مِنَ الْخٰسِرِيْنَ', 'wa lammā suqiṭa fī aidīhim wa ra`au annahum qad ḍallụ qālụ la`il lam yar-ḥamnā rabbunā wa yagfir lanā lanakụnanna minal-khāsirīn'),
(1103, 7, 150, 'وَلَمَّا رَجَعَ مُوْسٰٓى اِلٰى قَوْمِهٖ غَضْبَانَ اَسِفًاۙ قَالَ بِئْسَمَا خَلَفْتُمُوْنِيْ مِنْۢ بَعْدِيْۚ اَعَجِلْتُمْ اَمْرَ رَبِّكُمْۚ وَاَلْقَى الْاَلْوَاحَ وَاَخَذَ بِرَأْسِ اَخِيْهِ يَجُرُّهٗٓ اِلَيْهِ ۗقَالَ ابْنَ اُمَّ اِنَّ الْقَوْمَ اسْتَضْعَفُوْنِيْ وَكَادُوْا يَقْتُلُوْنَنِيْۖ فَلَا تُشْمِتْ بِيَ الْاَعْدَاۤءَ وَلَا تَجْعَلْنِيْ مَعَ الْقَوْمِ الظّٰلِمِيْنَ', 'wa lammā raja\'a mụsā ilā qaumihī gaḍbāna asifang qāla bi`samā khalaftumụnī mim ba\'dī, a \'ajiltum amra rabbikum, wa alqal-alwāḥa wa akhaża bira`si akhīhi yajurruhū ilaīh, qālabna umma innal-qaumastaḍ\'afụnī wa kādụ yaqtulụnanī fa lā tusymit biyal-a\'dā`a wa lā taj\'alnī ma\'al qaumiẓ-ẓālimīn'),
(1104, 7, 151, 'قَالَ رَبِّ اغْفِرْ لِيْ وَلِاَخِيْ وَاَدْخِلْنَا فِيْ رَحْمَتِكَ ۖوَاَنْتَ اَرْحَمُ الرّٰحِمِيْنَ', 'qāla rabbigfir lī wa li`akhī wa adkhilnā fī raḥmatika wa anta ar-ḥamur-rāḥimīn'),
(1105, 7, 152, 'اِنَّ الَّذِيْنَ اتَّخَذُوا الْعِجْلَ سَيَنَالُهُمْ غَضَبٌ مِّنْ رَّبِّهِمْ وَذِلَّةٌ فِى الْحَيٰوةِ الدُّنْيَاۗ وَكَذٰلِكَ نَجْزِى الْمُفْتَرِيْنَ', 'innallażīnattakhażul-\'ijla sayanāluhum gaḍabum mir rabbihim wa żillatun fil-ḥayātid-dun-yā, wa każālika najzil-muftarīn'),
(1106, 7, 153, 'وَالَّذِيْنَ عَمِلُوا السَّيِّاٰتِ ثُمَّ تَابُوْا مِنْۢ بَعْدِهَا وَاٰمَنُوْٓا اِنَّ رَبَّكَ مِنْۢ بَعْدِهَا لَغَفُوْرٌ رَّحِيْمٌ', 'wallażīna \'amilus-sayyi`āti ṡumma tābụ mim ba\'dihā wa āmanū inna rabbaka mim ba\'dihā lagafụrur raḥīm'),
(1107, 7, 154, 'وَلَمَّا سَكَتَ عَنْ مُّوْسَى الْغَضَبُ اَخَذَ الْاَلْوَاحَۖ وَفِيْ نُسْخَتِهَا هُدًى وَّرَحْمَةٌ لِّلَّذِيْنَ هُمْ لِرَبِّهِمْ يَرْهَبُوْنَ', 'wa lammā sakata \'am mụsal-gaḍabu akhażal-alwāḥa wa fī nuskhatihā hudaw wa raḥmatul lillażīna hum lirabbihim yar-habụn'),
(1108, 7, 155, 'وَاخْتَارَ مُوْسٰى قَوْمَهٗ سَبْعِيْنَ رَجُلًا لِّمِيْقَاتِنَا ۚفَلَمَّآ اَخَذَتْهُمُ الرَّجْفَةُ قَالَ رَبِّ لَوْ شِئْتَ اَهْلَكْتَهُمْ مِّنْ قَبْلُ وَاِيَّايَۗ اَتُهْلِكُنَا بِمَا فَعَلَ السُّفَهَاۤءُ مِنَّاۚ اِنْ هِيَ اِلَّا فِتْنَتُكَۗ تُضِلُّ بِهَا مَنْ تَشَاۤءُ وَتَهْدِيْ مَنْ تَشَاۤءُۗ اَنْتَ وَلِيُّنَا فَاغْفِرْ لَنَا وَارْحَمْنَا وَاَنْتَ خَيْرُ الْغَافِرِيْنَ', 'wakhtāra mụsā qaumahụ sab\'īna rajulal limīqātinā, fa lammā akhażat-humur-rajfatu qāla rabbi lau syi`ta ahlaktahum ming qablu wa iyyāy, a tuhlikunā bimā fa\'alas-sufahā`u minnā, in hiya illā fitnatuk, tuḍillu bihā man tasyā`u wa tahdī man tasyā`, anta waliyyunā fagfir lanā war-ḥamnā wa anta khairul-gāfirīn'),
(1109, 7, 156, '۞ وَاكْتُبْ لَنَا فِيْ هٰذِهِ الدُّنْيَا حَسَنَةً وَّفِى الْاٰخِرَةِ اِنَّا هُدْنَآ اِلَيْكَۗ قَالَ عَذَابِيْٓ اُصِيْبُ بِهٖ مَنْ اَشَاۤءُۚ وَرَحْمَتِيْ وَسِعَتْ كُلَّ شَيْءٍۗ فَسَاَكْتُبُهَا لِلَّذِيْنَ يَتَّقُوْنَ وَيُؤْتُوْنَ الزَّكٰوةَ وَالَّذِيْنَ هُمْ بِاٰيٰتِنَا يُؤْمِنُوْنَۚ', 'waktub lana fī hāżihid-dun-yā ḥasanataw wa fil-ākhirati innā hudnā ilaīk, qāla \'ażābī uṣību bihī man asyā`, wa raḥmatī wasi\'at kulla syaī`, fa sa`aktubuhā lillażīna yattaqụna wa yu`tụnaz-zakāta wallażīna hum bi`āyātinā yu`minụn'),
(1110, 7, 157, 'اَلَّذِيْنَ يَتَّبِعُوْنَ الرَّسُوْلَ النَّبِيَّ الْاُمِّيَّ الَّذِيْ يَجِدُوْنَهٗ مَكْتُوْبًا عِنْدَهُمْ فِى التَّوْرٰىةِ وَالْاِنْجِيْلِ يَأْمُرُهُمْ بِالْمَعْرُوْفِ وَيَنْهٰىهُمْ عَنِ الْمُنْكَرِ وَيُحِلُّ لَهُمُ الطَّيِّبٰتِ وَيُحَرِّمُ عَلَيْهِمُ الْخَبٰۤىِٕثَ وَيَضَعُ عَنْهُمْ اِصْرَهُمْ وَالْاَغْلٰلَ الَّتِيْ كَانَتْ عَلَيْهِمْۗ فَالَّذِيْنَ اٰمَنُوْا بِهٖ وَعَزَّرُوْهُ وَنَصَرُوْهُ وَاتَّبَعُوا النُّوْرَ الَّذِيْٓ اُنْزِلَ مَعَهٗٓ ۙاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ', 'allażīna yattabi\'ụnar-rasụlan-nabiyyal-ummiyyallażī yajidụnahụ maktụban \'indahum fit-taurāti wal-injīli ya`muruhum bil-ma\'rụfi wa yan-hāhum \'anil-mungkari wa yuḥillu lahumuṭ-ṭayyibāti wa yuḥarrimu \'alaihimul-khabā`iṡa wa yaḍa\'u \'an-hum iṣrahum wal-aglālallatī kānat \'alaihim, fallażīna āmanụ bihī wa \'azzarụhu wa naṣarụhu wattaba\'un-nụrallażī unzila ma\'ahū ulā`ika humul-mufliḥụn'),
(1111, 7, 158, 'قُلْ يٰٓاَيُّهَا النَّاسُ اِنِّيْ رَسُوْلُ اللّٰهِ اِلَيْكُمْ جَمِيْعًا ۨالَّذِيْ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۚ لَآ اِلٰهَ اِلَّا هُوَ يُحْيٖ وَيُمِيْتُۖ فَاٰمِنُوْا بِاللّٰهِ وَرَسُوْلِهِ النَّبِيِّ الْاُمِّيِّ الَّذِيْ يُؤْمِنُ بِاللّٰهِ وَكَلِمٰتِهٖ وَاتَّبِعُوْهُ لَعَلَّكُمْ تَهْتَدُوْنَ', 'qul yā ayyuhan-nāsu innī rasụlullāhi ilaikum jamī\'anillażī lahụ mulkus-samāwāti wal-arḍ, lā ilāha illā huwa yuḥyī wa yumītu fa āminụ billāhi wa rasụlihin-nabiyyil-ummiyyillażī yu`minu billāhi wa kalimātihī wattabi\'ụhu la\'allakum tahtadụn'),
(1112, 7, 159, 'وَمِنْ قَوْمِ مُوْسٰٓى اُمَّةٌ يَّهْدُوْنَ بِالْحَقِّ وَبِهٖ يَعْدِلُوْنَ', 'wa ming qaumi mụsā ummatuy yahdụna bil-ḥaqqi wa bihī ya\'dilụn'),
(1113, 7, 160, 'وَقَطَّعْنٰهُمُ اثْنَتَيْ عَشْرَةَ اَسْبَاطًا اُمَمًاۗ وَاَوْحَيْنَآ اِلٰى مُوْسٰٓى اِذِ اسْتَسْقٰىهُ قَوْمُهٗٓ اَنِ اضْرِبْ بِّعَصَاكَ الْحَجَرَۚ فَانْۢبَجَسَتْ مِنْهُ اثْنَتَا عَشْرَةَ عَيْنًاۗ قَدْ عَلِمَ كُلُّ اُنَاسٍ مَّشْرَبَهُمْۗ وَظَلَّلْنَا عَلَيْهِمُ الْغَمَامَ وَاَنْزَلْنَا عَلَيْهِمُ الْمَنَّ وَالسَّلْوٰىۗ كُلُوْا مِنْ طَيِّبٰتِ مَا رَزَقْنٰكُمْۗ وَمَا ظَلَمُوْنَا وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَ', 'wa qaṭṭa\'nāhumuṡnatai \'asyrata asbāṭan umamā, wa auḥainā ilā mụsā iżistasqāhu qaumuhū aniḍrib bi\'aṣākal-ḥajar, fambajasat min-huṡnatā \'asyrata \'ainā, qad \'alima kullu unāsim masyrabahum, wa ẓallalnā \'alaihimul-gamāma wa anzalnā \'alaihimul-manna was-salwā, kulụ min ṭayyibāti mā razaqnākum, wa mā ẓalamụnā wa lāking kānū anfusahum yaẓlimụn'),
(1114, 7, 161, 'وَاِذْ قِيْلَ لَهُمُ اسْكُنُوْا هٰذِهِ الْقَرْيَةَ وَكُلُوْا مِنْهَا حَيْثُ شِئْتُمْ وَقُوْلُوْا حِطَّةٌ وَّادْخُلُوا الْبَابَ سُجَّدًا نَّغْفِرْ لَكُمْ خَطِيْۤـٰٔتِكُمْۗ سَنَزِيْدُ الْمُحْسِنِيْنَ', 'wa iż qīla lahumuskunụ hāżihil-qaryata wa kulụ min-hā ḥaiṡu syi`tum wa qụlụ hiṭṭatuw wadkhulul-bāba sujjadan nagfir lakum khaṭī`ātikum, sanazīdul-muḥsinīn'),
(1115, 7, 162, 'فَبَدَّلَ الَّذِيْنَ ظَلَمُوْا مِنْهُمْ قَوْلًا غَيْرَ الَّذِيْ قِيْلَ لَهُمْ فَاَرْسَلْنَا عَلَيْهِمْ رِجْزًا مِّنَ السَّمَاۤءِ بِمَا كَانُوْا يَظْلِمُوْنَ', 'fa baddalallażīna ẓalamụ min-hum qaulan gairallażī qīla lahum fa arsalnā \'alaihim rijzam minas-samā`i bimā kānụ yaẓlimụn'),
(1116, 7, 163, 'وَسْـَٔلْهُمْ عَنِ الْقَرْيَةِ الَّتِيْ كَانَتْ حَاضِرَةَ الْبَحْرِۘ اِذْ يَعْدُوْنَ فِى السَّبْتِ اِذْ تَأْتِيْهِمْ حِيْتَانُهُمْ يَوْمَ سَبْتِهِمْ شُرَّعًا وَّيَوْمَ لَا يَسْبِتُوْنَۙ لَا تَأْتِيْهِمْ ۛ كَذٰلِكَ ۛنَبْلُوْهُمْ بِمَا كَانُوْا يَفْسُقُوْنَ', 'was`al-hum \'anil-qaryatillatī kānat hāḍiratal-baḥr, iż ya\'dụna fis-sabti iż ta`tīhim ḥītānuhum yauma sabtihim syurra\'aw wa yauma lā yasbitụna lā ta`tīhim, każālika nablụhum bimā kānụ yafsuqụn'),
(1117, 7, 164, 'وَاِذْ قَالَتْ اُمَّةٌ مِّنْهُمْ لِمَ تَعِظُوْنَ قَوْمًاۙ ۨاللّٰهُ مُهْلِكُهُمْ اَوْ مُعَذِّبُهُمْ عَذَابًا شَدِيْدًاۗ قَالُوْا مَعْذِرَةً اِلٰى رَبِّكُمْ وَلَعَلَّهُمْ يَتَّقُوْنَ', 'wa iż qālat ummatum min-hum lima ta\'iẓụna qaumanillāhu muhlikuhum au mu\'ażżibuhum \'ażāban syadīdā, qālụ ma\'żiratan ilā rabbikum wa la\'allahum yattaqụn'),
(1118, 7, 165, 'فَلَمَّا نَسُوْا مَا ذُكِّرُوْا بِهٖٓ اَنْجَيْنَا الَّذِيْنَ يَنْهَوْنَ عَنِ السُّوْۤءِ وَاَخَذْنَا الَّذِيْنَ ظَلَمُوْا بِعَذَابٍۢ بَـِٔيْسٍۢ بِمَا كَانُوْا يَفْسُقُوْنَ', 'fa lammā nasụ mā żukkirụ bihī anjainallażīna yan-hauna \'anis-sū`i wa akhażnallażīna ẓalamụ bi\'ażābim ba`īsim bimā kānụ yafsuqụn'),
(1119, 7, 166, 'فَلَمَّا عَتَوْا عَنْ مَّا نُهُوْا عَنْهُ قُلْنَا لَهُمْ كُوْنُوْا قِرَدَةً خَاسِـِٕيْنَ', 'fa lammā \'atau \'am mā nuhụ \'an-hu qulnā lahum kụnụ qiradatan khāsi`īn'),
(1120, 7, 167, 'وَاِذْ تَاَذَّنَ رَبُّكَ لَيَبْعَثَنَّ عَلَيْهِمْ اِلٰى يَوْمِ الْقِيٰمَةِ مَنْ يَّسُوْمُهُمْ سُوْۤءَ الْعَذَابِۗ اِنَّ رَبَّكَ لَسَرِيْعُ الْعِقَابِۖ وَاِنَّهٗ لَغَفُوْرٌ رَّحِيْمٌ', 'wa iż ta`ażżana rabbuka layab\'aṡanna \'alaihim ilā yaumil-qiyāmati may yasụmuhum sū`al-\'ażāb, inna rabbaka lasarī\'ul-\'iqābi wa innahụ lagafụrur raḥīm'),
(1121, 7, 168, 'وَقَطَّعْنٰهُمْ فِى الْاَرْضِ اُمَمًاۚ مِنْهُمُ الصّٰلِحُوْنَ وَمِنْهُمْ دُوْنَ ذٰلِكَ ۖوَبَلَوْنٰهُمْ بِالْحَسَنٰتِ وَالسَّيِّاٰتِ لَعَلَّهُمْ يَرْجِعُوْنَ', 'wa qaṭṭa\'nāhum fil-arḍi umamā, min-humuṣ-ṣāliḥụna wa min-hum dụna żālika wa balaunāhum bil-ḥasanāti was-sayyi`āti la\'allahum yarji\'ụn'),
(1122, 7, 169, 'فَخَلَفَ مِنْۢ بَعْدِهِمْ خَلْفٌ وَّرِثُوا الْكِتٰبَ يَأْخُذُوْنَ عَرَضَ هٰذَا الْاَدْنٰى وَيَقُوْلُوْنَ سَيُغْفَرُ لَنَاۚ وَاِنْ يَّأْتِهِمْ عَرَضٌ مِّثْلُهٗ يَأْخُذُوْهُۗ اَلَمْ يُؤْخَذْ عَلَيْهِمْ مِّيْثَاقُ الْكِتٰبِ اَنْ لَّا يَقُوْلُوْا عَلَى اللّٰهِ اِلَّا الْحَقَّ وَدَرَسُوْا مَا فِيْهِۗ وَالدَّارُ الْاٰخِرَةُ خَيْرٌ لِّلَّذِيْنَ يَتَّقُوْنَۗ اَفَلَا تَعْقِلُوْنَ', 'fa khalafa mim ba\'dihim khalfuw wariṡul-kitāba ya`khużụna \'araḍa hāżal-adnā wa yaqụlụna sayugfaru lanā, wa iy ya`tihim \'araḍum miṡluhụ ya`khużụh, a lam yu`khaż \'alaihim mīṡāqul-kitābi al lā yaqụlụ \'alallāhi illal-ḥaqqa wa darasụ mā fīh, wad-dārul-ākhiratu khairul lillażīna yattaqụn, a fa lā ta\'qilụn'),
(1123, 7, 170, 'وَالَّذِيْنَ يُمَسِّكُوْنَ بِالْكِتٰبِ وَاَقَامُوا الصَّلٰوةَۗ اِنَّا لَا نُضِيْعُ اَجْرَ الْمُصْلِحِيْنَ', 'wallażīna yumassikụna bil-kitābi wa aqāmuṣ-ṣalāh, innā lā nuḍī\'u ajral-muṣliḥīn'),
(1124, 7, 171, '۞ وَاِذْ نَتَقْنَا الْجَبَلَ فَوْقَهُمْ كَاَنَّهٗ ظُلَّةٌ وَّظَنُّوْٓا اَنَّهٗ وَاقِعٌۢ بِهِمْۚ خُذُوْا مَآ اٰتَيْنٰكُمْ بِقُوَّةٍ وَّاذْكُرُوْا مَا فِيْهِ لَعَلَّكُمْ تَتَّقُوْنَ', 'wa iż nataqnal-jabala fauqahum ka`annahụ ẓullatuw wa ẓannū annahụ wāqi\'um bihim, khużụ mā ātainākum biquwwatiw ważkurụ mā fīhi la\'allakum tattaqụn'),
(1125, 7, 172, 'وَاِذْ اَخَذَ رَبُّكَ مِنْۢ بَنِيْٓ اٰدَمَ مِنْ ظُهُوْرِهِمْ ذُرِّيَّتَهُمْ وَاَشْهَدَهُمْ عَلٰٓى اَنْفُسِهِمْۚ اَلَسْتُ بِرَبِّكُمْۗ قَالُوْا بَلٰىۛ شَهِدْنَا ۛاَنْ تَقُوْلُوْا يَوْمَ الْقِيٰمَةِ اِنَّا كُنَّا عَنْ هٰذَا غٰفِلِيْنَۙ', 'wa iż akhaża rabbuka mim banī ādama min ẓuhụrihim żurriyyatahum wa asy-hadahum \'alā anfusihim, a lastu birabbikum, qālụ balā syahidnā, an taqụlụ yaumal-qiyāmati innā kunnā \'an hāżā gāfilīn'),
(1126, 7, 173, 'اَوْ تَقُوْلُوْٓا اِنَّمَآ اَشْرَكَ اٰبَاۤؤُنَا مِنْ قَبْلُ وَكُنَّا ذُرِّيَّةً مِّنْۢ بَعْدِهِمْۚ اَفَتُهْلِكُنَا بِمَا فَعَلَ الْمُبْطِلُوْنَ', 'au taqụlū innamā asyraka ābā`unā ming qablu wa kunnā żurriyyatam mim ba\'dihim, a fa tuhlikunā bimā fa\'alal-mubṭilụn'),
(1127, 7, 174, 'وَكَذٰلِكَ نُفَصِّلُ الْاٰيٰتِ وَلَعَلَّهُمْ يَرْجِعُوْنَ', 'wa każālika nufaṣṣilul-āyāti wa la\'allahum yarji\'ụn'),
(1128, 7, 175, 'وَاتْلُ عَلَيْهِمْ نَبَاَ الَّذِيْٓ اٰتَيْنٰهُ اٰيٰتِنَا فَانْسَلَخَ مِنْهَا فَاَتْبَعَهُ الشَّيْطٰنُ فَكَانَ مِنَ الْغٰوِيْنَ', 'watlu \'alaihim naba`allażī ātaināhu āyātinā fansalakha min-hā fa atba\'ahusy-syaiṭānu fa kāna minal-gāwīn'),
(1129, 7, 176, 'وَلَوْ شِئْنَا لَرَفَعْنٰهُ بِهَا وَلٰكِنَّهٗٓ اَخْلَدَ اِلَى الْاَرْضِ وَاتَّبَعَ هَوٰىهُۚ فَمَثَلُهٗ كَمَثَلِ الْكَلْبِۚ اِنْ تَحْمِلْ عَلَيْهِ يَلْهَثْ اَوْ تَتْرُكْهُ يَلْهَثْۗ ذٰلِكَ مَثَلُ الْقَوْمِ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَاۚ فَاقْصُصِ الْقَصَصَ لَعَلَّهُمْ يَتَفَكَّرُوْنَ', 'walau syi`nā larafa\'nāhu bihā wa lākinnahū akhlada ilal-arḍi wattaba\'a hawāh, fa maṡaluhụ kamaṡalil-kalb, in taḥmil \'alaihi yal-haṡ au tatruk-hu yal-haṡ, żālika maṡalul-qaumillażīna każżabụ bi`āyātinā, faqṣuṣil-qaṣaṣa la\'allahum yatafakkarụn'),
(1130, 7, 177, 'سَاۤءَ مَثَلًا ۨالْقَوْمُ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَاَنْفُسَهُمْ كَانُوْا يَظْلِمُوْنَ', 'sā`a maṡalanil-qaumullażīna każżabụ bi`āyātinā wa anfusahum kānụ yaẓlimụn'),
(1131, 7, 178, 'مَنْ يَّهْدِ اللّٰهُ فَهُوَ الْمُهْتَدِيْۚ وَمَنْ يُّضْلِلْ فَاُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ', 'may yahdillāhu fa huwal-muhtadī, wa may yuḍlil fa ulā`ika humul-khāsirụn'),
(1132, 7, 179, 'وَلَقَدْ ذَرَأْنَا لِجَهَنَّمَ كَثِيْرًا مِّنَ الْجِنِّ وَالْاِنْسِۖ لَهُمْ قُلُوْبٌ لَّا يَفْقَهُوْنَ بِهَاۖ وَلَهُمْ اَعْيُنٌ لَّا يُبْصِرُوْنَ بِهَاۖ وَلَهُمْ اٰذَانٌ لَّا يَسْمَعُوْنَ بِهَاۗ اُولٰۤىِٕكَ كَالْاَنْعَامِ بَلْ هُمْ اَضَلُّ ۗ اُولٰۤىِٕكَ هُمُ الْغٰفِلُوْنَ', 'wa laqad żara`nā lijahannama kaṡīram minal-jinni wal-insi lahum qulụbul lā yafqahụna bihā wa lahum a\'yunul lā yubṣirụna bihā wa lahum āżānul lā yasma\'ụna bihā, ulā`ika kal-an\'āmi bal hum aḍall, ulā`ika humul-gāfilụn'),
(1133, 7, 180, 'وَلِلّٰهِ الْاَسْمَاۤءُ الْحُسْنٰى فَادْعُوْهُ بِهَاۖ وَذَرُوا الَّذِيْنَ يُلْحِدُوْنَ فِيْٓ اَسْمَاۤىِٕهٖۗ سَيُجْزَوْنَ مَا كَانُوْا يَعْمَلُوْنَ ۖ', 'wa lillāhil-asmā`ul-ḥusnā fad\'ụhu bihā wa żarullażīna yul-ḥidụna fī asmā`ih, sayujzauna mā kānụ ya\'malụn'),
(1134, 7, 181, 'وَمِمَّنْ خَلَقْنَآ اُمَّةٌ يَّهْدُوْنَ بِالْحَقِّ وَبِهٖ يَعْدِلُوْنَ', 'wa mim man khalaqnā ummatuy yahdụna bil-ḥaqqi wa bihī ya\'dilụn'),
(1135, 7, 182, 'وَالَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا سَنَسْتَدْرِجُهُمْ مِّنْ حَيْثُ لَا يَعْلَمُوْنَ', 'wallażīna każżabụ bi`āyātinā sanastadrijuhum min ḥaiṡu lā ya\'lamụn'),
(1136, 7, 183, 'وَاُمْلِيْ لَهُمْ ۗاِنَّ كَيْدِيْ مَتِيْنٌ', 'wa umlī lahum, inna kaidī matīn'),
(1137, 7, 184, 'اَوَلَمْ يَتَفَكَّرُوْا مَا بِصَاحِبِهِمْ مِّنْ جِنَّةٍۗ اِنْ هُوَ اِلَّا نَذِيْرٌ مُّبِيْنٌ', 'a wa lam yatafakkarụ mā biṣāḥibihim min jinnah, in huwa illā nażīrum mubīn');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(1138, 7, 185, 'اَوَلَمْ يَنْظُرُوْا فِيْ مَلَكُوْتِ السَّمٰوٰتِ وَالْاَرْضِ وَمَا خَلَقَ اللّٰهُ مِنْ شَيْءٍ وَّاَنْ عَسٰٓى اَنْ يَّكُوْنَ قَدِ اقْتَرَبَ اَجَلُهُمْۖ فَبِاَيِّ حَدِيْثٍۢ بَعْدَهٗ يُؤْمِنُوْنَ', 'a wa lam yanẓurụ fī malakụtis-samāwāti wal-arḍi wa mā khalaqallāhu min syai`iw wa an \'asā ay yakụna qadiqtaraba ajaluhum fa bi`ayyi ḥadīṡim ba\'dahụ yu`minụn'),
(1139, 7, 186, 'مَنْ يُّضْلِلِ اللّٰهُ فَلَا هَادِيَ لَهٗ ۖوَيَذَرُهُمْ فِيْ طُغْيَانِهِمْ يَعْمَهُوْنَ', 'may yuḍlilillāhu fa lā hādiya lahụ wa yażaruhum fī ṭugyānihim ya\'mahụn'),
(1140, 7, 187, 'يَسْـَٔلُوْنَكَ عَنِ السَّاعَةِ اَيَّانَ مُرْسٰىهَاۗ قُلْ اِنَّمَا عِلْمُهَا عِنْدَ رَبِّيْۚ لَا يُجَلِّيْهَا لِوَقْتِهَآ اِلَّا هُوَۘ ثَقُلَتْ فِى السَّمٰوٰتِ وَالْاَرْضِۗ لَا تَأْتِيْكُمْ اِلَّا بَغْتَةً ۗيَسْـَٔلُوْنَكَ كَاَنَّكَ حَفِيٌّ عَنْهَاۗ قُلْ اِنَّمَا عِلْمُهَا عِنْدَ اللّٰهِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ', 'yas`alụnaka \'anis-sā\'ati ayyāna mursāhā, qul innamā \'ilmuhā \'inda rabbī, lā yujallīhā liwaqtihā illā huw, ṡaqulat fis-samāwāti wal-arḍ, lā ta`tīkum illā bagtah, yas`alụnaka ka`annaka ḥafiyyun \'an-hā, qul innamā \'ilmuhā \'indallāhi wa lākinna akṡaran-nāsi lā ya\'lamụn'),
(1141, 7, 188, 'قُلْ لَّآ اَمْلِكُ لِنَفْسِيْ نَفْعًا وَّلَا ضَرًّا اِلَّا مَا شَاۤءَ اللّٰهُ ۗوَلَوْ كُنْتُ اَعْلَمُ الْغَيْبَ لَاسْتَكْثَرْتُ مِنَ الْخَيْرِۛ وَمَا مَسَّنِيَ السُّوْۤءُ ۛاِنْ اَنَا۠ اِلَّا نَذِيْرٌ وَّبَشِيْرٌ لِّقَوْمٍ يُّؤْمِنُوْنَ', 'qul lā amliku linafsī naf\'aw wa lā ḍarran illā mā syā`allāh, walau kuntu a\'lamul-gaiba lastakṡartu minal-khaīr, wa mā massaniyas-sū`u in ana illā nażīruw wa basyīrul liqaumiy yu`minụn'),
(1142, 7, 189, '۞ هُوَ الَّذِيْ خَلَقَكُمْ مِّنْ نَّفْسٍ وَّاحِدَةٍ وَّجَعَلَ مِنْهَا زَوْجَهَا لِيَسْكُنَ اِلَيْهَاۚ فَلَمَّا تَغَشّٰىهَا حَمَلَتْ حَمْلًا خَفِيْفًا فَمَرَّتْ بِهٖ ۚفَلَمَّآ اَثْقَلَتْ دَّعَوَا اللّٰهَ رَبَّهُمَا لَىِٕنْ اٰتَيْتَنَا صَالِحًا لَّنَكُوْنَنَّ مِنَ الشّٰكِرِيْنَ', 'huwallażī khalaqakum min nafsiw wāḥidatiw wa ja\'ala min-hā zaujahā liyaskuna ilaihā, fa lammā tagasysyāhā ḥamalat ḥamlan khafīfan fa marrat bih, fa lammā aṡqalad da\'awallāha rabbahumā la`in ātaitanā ṣāliḥal lanakụnanna minasy-syākirīn'),
(1143, 7, 190, 'فَلَمَّآ اٰتٰىهُمَا صَالِحًا جَعَلَا لَهٗ شُرَكَاۤءَ فِيْمَآ اٰتٰىهُمَا ۚفَتَعٰلَى اللّٰهُ عَمَّا يُشْرِكُوْنَ', 'fa lammā ātāhumā ṣāliḥan ja\'alā lahụ syurakā`a fīmā ātāhumā, fa ta\'ālallāhu \'ammā yusyrikụn'),
(1144, 7, 191, 'اَيُشْرِكُوْنَ مَا لَا يَخْلُقُ شَيْـًٔا وَّهُمْ يُخْلَقُوْنَۖ', 'a yusyrikụna mā lā yakhluqu syai`aw wa hum yukhlaqụn'),
(1145, 7, 192, 'وَلَا يَسْتَطِيْعُوْنَ لَهُمْ نَصْرًا وَّلَآ اَنْفُسَهُمْ يَنْصُرُوْنَ', 'wa lā yastaṭī\'ụna lahum naṣraw wa lā anfusahum yanṣurụn'),
(1146, 7, 193, 'وَاِنْ تَدْعُوْهُمْ اِلَى الْهُدٰى لَا يَتَّبِعُوْكُمْۗ سَوَۤاءٌ عَلَيْكُمْ اَدَعَوْتُمُوْهُمْ اَمْ اَنْتُمْ صَامِتُوْنَ', 'wa in tad\'ụhum ilal-hudā lā yattabi\'ụkum, sawā`un \'alaikum a da\'autumụhum am antum ṣāmitụn'),
(1147, 7, 194, 'اِنَّ الَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ عِبَادٌ اَمْثَالُكُمْ فَادْعُوْهُمْ فَلْيَسْتَجِيْبُوْا لَكُمْ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'innallażīna tad\'ụna min dụnillāhi \'ibādun amṡālukum fad\'ụhum falyastajībụ lakum ing kuntum ṣādiqīn'),
(1148, 7, 195, 'اَلَهُمْ اَرْجُلٌ يَّمْشُوْنَ بِهَآ ۖ اَمْ لَهُمْ اَيْدٍ يَّبْطِشُوْنَ بِهَآ ۖ اَمْ لَهُمْ اَعْيُنٌ يُّبْصِرُوْنَ بِهَآ ۖ اَمْ لَهُمْ اٰذَانٌ يَّسْمَعُوْنَ بِهَاۗ قُلِ ادْعُوْا شُرَكَاۤءَكُمْ ثُمَّ كِيْدُوْنِ فَلَا تُنْظِرُوْنِ', 'a lahum arjuluy yamsyụna bihā am lahum aidiy yabṭisyụna bihā am lahum a\'yunuy yubṣirụna bihā am lahum āżānuy yasma\'ụna bihā, qulid\'ụ syurakā`akum ṡumma kīdụni fa lā tunẓirụn'),
(1149, 7, 196, 'اِنَّ وَلِيِّ َۧ اللّٰهُ الَّذِيْ نَزَّلَ الْكِتٰبَۖ وَهُوَ يَتَوَلَّى الصّٰلِحِيْنَ', 'inna waliyyiyallāhullażī nazzalal-kitāba wa huwa yatawallaṣ-ṣāliḥīn'),
(1150, 7, 197, 'وَالَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِهٖ لَا يَسْتَطِيْعُوْنَ نَصْرَكُمْ وَلَآ اَنْفُسَهُمْ يَنْصُرُوْنَ', 'wallażīna tad\'ụna min dụnihī lā yastaṭī\'ụna naṣrakum wa lā anfusahum yanṣurụn'),
(1151, 7, 198, 'وَاِنْ تَدْعُوْهُمْ اِلَى الْهُدٰى لَا يَسْمَعُوْاۗ وَتَرٰىهُمْ يَنْظُرُوْنَ اِلَيْكَ وَهُمْ لَا يُبْصِرُوْنَ', 'wa in tad\'ụhum ilal-hudā lā yasma\'ụ, wa tarāhum yanẓurụna ilaika wa hum lā yubṣirụn'),
(1152, 7, 199, 'خُذِ الْعَفْوَ وَأْمُرْ بِالْعُرْفِ وَاَعْرِضْ عَنِ الْجَاهِلِيْنَ', 'khużil-\'afwa wa`mur bil-\'urfi wa a\'riḍ \'anil-jāhilīn'),
(1153, 7, 200, 'وَاِمَّا يَنْزَغَنَّكَ مِنَ الشَّيْطٰنِ نَزْغٌ فَاسْتَعِذْ بِاللّٰهِ ۗاِنَّهٗ سَمِيْعٌ عَلِيْمٌ', 'wa immā yanzagannaka minasy-syaiṭāni nazgun fasta\'iż billāh, innahụ samī\'un \'alīm'),
(1154, 7, 201, 'اِنَّ الَّذِيْنَ اتَّقَوْا اِذَا مَسَّهُمْ طٰۤىِٕفٌ مِّنَ الشَّيْطٰنِ تَذَكَّرُوْا فَاِذَا هُمْ مُّبْصِرُوْنَۚ', 'innallażīnattaqau iżā massahum ṭā`ifum minasy-syaiṭāni tażakkarụ fa iżā hum mubṣirụn'),
(1155, 7, 202, 'وَاِخْوَانُهُمْ يَمُدُّوْنَهُمْ فِى الْغَيِّ ثُمَّ لَا يُقْصِرُوْنَ', 'wa ikhwānuhum yamuddụnahum fil-gayyi ṡumma lā yuqṣirụn'),
(1156, 7, 203, 'وَاِذَا لَمْ تَأْتِهِمْ بِاٰيَةٍ قَالُوْا لَوْلَا اجْتَبَيْتَهَاۗ قُلْ اِنَّمَآ اَتَّبِعُ مَا يُوْحٰٓى اِلَيَّ مِنْ رَّبِّيْۗ هٰذَا بَصَاۤىِٕرُ مِنْ رَّبِّكُمْ وَهُدًى وَّرَحْمَةٌ لِّقَوْمٍ يُّؤْمِنُوْنَ', 'wa iżā lam ta`tihim bi`āyating qālụ lau lajtabaitahā, qul innamā attabi\'u mā yụḥā ilayya mir rabbī, hāżā baṣā`iru mir rabbikum wa hudaw wa raḥmatul liqaumiy yu`minụn'),
(1157, 7, 204, 'وَاِذَا قُرِئَ الْقُرْاٰنُ فَاسْتَمِعُوْا لَهٗ وَاَنْصِتُوْا لَعَلَّكُمْ تُرْحَمُوْنَ', 'wa iżā quri`al-qur`ānu fastami\'ụ lahụ wa anṣitụ la\'allakum tur-ḥamụn'),
(1158, 7, 205, 'وَاذْكُرْ رَّبَّكَ فِيْ نَفْسِكَ تَضَرُّعًا وَّخِيْفَةً وَّدُوْنَ الْجَهْرِ مِنَ الْقَوْلِ بِالْغُدُوِّ وَالْاٰصَالِ وَلَا تَكُنْ مِّنَ الْغٰفِلِيْنَ', 'ważkur rabbaka fī nafsika taḍarru\'aw wa khīfataw wa dụnal-jahri minal-qauli bil-guduwwi wal-āṣāli wa lā takum minal-gāfilīn'),
(1159, 7, 206, 'اِنَّ الَّذِيْنَ عِنْدَ رَبِّكَ لَا يَسْتَكْبِرُوْنَ عَنْ عِبَادَتِهٖ وَيُسَبِّحُوْنَهٗ وَلَهٗ يَسْجُدُوْنَ ۩', 'innallażīna \'inda rabbika lā yastakbirụna \'an \'ibādatihī wa yusabbiḥụnahụ wa lahụ yasjudụn'),
(1160, 8, 1, 'يَسْـَٔلُوْنَكَ عَنِ الْاَنْفَالِۗ قُلِ الْاَنْفَالُ لِلّٰهِ وَالرَّسُوْلِۚ فَاتَّقُوا اللّٰهَ وَاَصْلِحُوْا ذَاتَ بَيْنِكُمْ ۖوَاَطِيْعُوا اللّٰهَ وَرَسُوْلَهٗٓ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ', 'yas`alụnaka \'anil-anfāl, qulil-anfālu lillāhi war-rasụl, fattaqullāha wa aṣliḥụ żāta bainikum wa aṭī\'ullāha wa rasụlahū ing kuntum mu`minīn'),
(1161, 8, 2, 'اِنَّمَا الْمُؤْمِنُوْنَ الَّذِيْنَ اِذَا ذُكِرَ اللّٰهُ وَجِلَتْ قُلُوْبُهُمْ وَاِذَا تُلِيَتْ عَلَيْهِمْ اٰيٰتُهٗ زَادَتْهُمْ اِيْمَانًا وَّعَلٰى رَبِّهِمْ يَتَوَكَّلُوْنَۙ', 'innamal-mu`minụnallażīna iżā żukirallāhu wajilat qulụbuhum wa iżā tuliyat \'alaihim āyātuhụ zādat-hum īmānaw wa \'alā rabbihim yatawakkalụn'),
(1162, 8, 3, 'الَّذِيْنَ يُقِيْمُوْنَ الصَّلٰوةَ وَمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَۗ', 'allażīna yuqīmụnaṣ-ṣalāta wa mimmā razaqnāhum yunfiqụn'),
(1163, 8, 4, 'اُولٰۤىِٕكَ هُمُ الْمُؤْمِنُوْنَ حَقًّاۗ لَهُمْ دَرَجٰتٌ عِنْدَ رَبِّهِمْ وَمَغْفِرَةٌ وَّرِزْقٌ كَرِيْمٌۚ', 'ulā`ika humul-mu`minụna ḥaqqā, lahum darajātun \'inda rabbihim wa magfiratuw wa rizqung karīm'),
(1164, 8, 5, 'كَمَآ اَخْرَجَكَ رَبُّكَ مِنْۢ بَيْتِكَ بِالْحَقِّۖ وَاِنَّ فَرِيْقًا مِّنَ الْمُؤْمِنِيْنَ لَكٰرِهُوْنَ', 'kamā akhrajaka rabbuka mim baitika bil-ḥaqqi wa inna farīqam minal-mu`minīna lakārihụn'),
(1165, 8, 6, 'يُجَادِلُوْنَكَ فِى الْحَقِّ بَعْدَمَا تَبَيَّنَ كَاَنَّمَا يُسَاقُوْنَ اِلَى الْمَوْتِ وَهُمْ يَنْظُرُوْنَ ۗ', 'yujādilụnaka fil-ḥaqqi ba\'da mā tabayyana ka`annamā yusāqụna ilal-mauti wa hum yanẓurụn'),
(1166, 8, 7, 'وَاِذْ يَعِدُكُمُ اللّٰهُ اِحْدَى الطَّاۤىِٕفَتَيْنِ اَنَّهَا لَكُمْ وَتَوَدُّوْنَ اَنَّ غَيْرَ ذَاتِ الشَّوْكَةِ تَكُوْنُ لَكُمْ وَيُرِيْدُ اللّٰهُ اَنْ يُّحِقَّ الْحَقَّ بِكَلِمٰتِهٖ وَيَقْطَعَ دَابِرَ الْكٰفِرِيْنَۙ', 'wa iż ya\'idukumullāhu iḥdaṭ-ṭā`ifataini annahā lakum wa tawaddụna anna gaira żātisy-syaukati takụnu lakum wa yurīdullāhu ay yuḥiqqal-ḥaqqa bikalimātihī wa yaqṭa\'a dābiral-kāfirīn'),
(1167, 8, 8, 'لِيُحِقَّ الْحَقَّ وَيُبْطِلَ الْبَاطِلَ وَلَوْ كَرِهَ الْمُجْرِمُوْنَۚ', 'liyuḥiqqal-ḥaqqa wa yubṭilal-bāṭila walau karihal-mujrimụn'),
(1168, 8, 9, 'اِذْ تَسْتَغِيْثُوْنَ رَبَّكُمْ فَاسْتَجَابَ لَكُمْ اَنِّيْ مُمِدُّكُمْ بِاَلْفٍ مِّنَ الْمَلٰۤىِٕكَةِ مُرْدِفِيْنَ', 'iż tastagīṡụna rabbakum fastajāba lakum annī mumiddukum bi`alfim minal-malā`ikati murdifīn'),
(1169, 8, 10, 'وَمَا جَعَلَهُ اللّٰهُ اِلَّا بُشْرٰى وَلِتَطْمَىِٕنَّ بِهٖ قُلُوْبُكُمْۗ وَمَا النَّصْرُ اِلَّا مِنْ عِنْدِ اللّٰهِ ۗاِنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ', 'wa mā ja\'alahullāhu illā busyrā wa litaṭma`inna bihī qulụbukum, wa man-naṣru illā min \'indillāh, innallāha \'azīzun ḥakīm'),
(1170, 8, 11, 'اِذْ يُغَشِّيْكُمُ النُّعَاسَ اَمَنَةً مِّنْهُ وَيُنَزِّلُ عَلَيْكُمْ مِّنَ السَّمَاۤءِ مَاۤءً لِّيُطَهِّرَكُمْ بِهٖ وَيُذْهِبَ عَنْكُمْ رِجْزَ الشَّيْطٰنِ وَلِيَرْبِطَ عَلٰى قُلُوْبِكُمْ وَيُثَبِّتَ بِهِ الْاَقْدَامَۗ', 'iż yugasysyīkumun-nu\'āsa amanatam min-hu wa yunazzilu \'alaikum minas-samā`i mā`al liyuṭahhirakum bihī wa yuż-hiba \'angkum rijzasy-syaiṭāni wa liyarbiṭa \'alā qulụbikum wa yuṡabbita bihil-aqdām'),
(1171, 8, 12, 'اِذْ يُوْحِيْ رَبُّكَ اِلَى الْمَلٰۤىِٕكَةِ اَنِّيْ مَعَكُمْ فَثَبِّتُوا الَّذِيْنَ اٰمَنُوْاۗ سَاُلْقِيْ فِيْ قُلُوْبِ الَّذِيْنَ كَفَرُوا الرُّعْبَ فَاضْرِبُوْا فَوْقَ الْاَعْنَاقِ وَاضْرِبُوْا مِنْهُمْ كُلَّ بَنَانٍۗ', 'iż yụḥī rabbuka ilal-malā`ikati annī ma\'akum fa ṡabbitullażīna āmanụ, sa`ulqī fī qulụbillażīna kafarur-ru\'ba faḍribụ fauqal-a\'nāqi waḍribụ min-hum kulla banān'),
(1172, 8, 13, 'ذٰلِكَ بِاَنَّهُمْ شَاۤقُّوا اللّٰهَ وَرَسُوْلَهٗۚ وَمَنْ يُّشَاقِقِ اللّٰهَ وَرَسُوْلَهٗ فَاِنَّ اللّٰهَ شَدِيْدُ الْعِقَابِ', 'żālika bi`annahum syāqqullāha wa rasụlah, wa may yusyāqiqillāha wa rasụlahụ fa innallāha syadīdul-\'iqāb'),
(1173, 8, 14, 'ذٰلِكُمْ فَذُوْقُوْهُ وَاَنَّ لِلْكٰفِرِيْنَ عَذَابَ النَّارِ', 'żālikum fa żụqụhu wa anna lil-kāfirīna \'ażāban-nār'),
(1174, 8, 15, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا لَقِيْتُمُ الَّذِيْنَ كَفَرُوْا زَحْفًا فَلَا تُوَلُّوْهُمُ الْاَدْبَارَۚ', 'yā ayyuhallażīna āmanū iżā laqītumullażīna kafarụ zaḥfan fa lā tuwallụhumul-adbār'),
(1175, 8, 16, 'وَمَنْ يُّوَلِّهِمْ يَوْمَىِٕذٍ دُبُرَهٗٓ اِلَّا مُتَحَرِّفًا لِّقِتَالٍ اَوْ مُتَحَيِّزًا اِلٰى فِئَةٍ فَقَدْ بَاۤءَ بِغَضَبٍ مِّنَ اللّٰهِ وَمَأْوٰىهُ جَهَنَّمُ ۗ وَبِئْسَ الْمَصِيْرُ', 'wa may yuwallihim yauma`iżin duburahū illā mutaḥarrifal liqitālin au mutaḥayyizan ilā fi`atin fa qad bā`a bigaḍabim minallāhi wa ma`wāhu jahannam, wa bi`sal-maṣīr'),
(1176, 8, 17, 'فَلَمْ تَقْتُلُوْهُمْ وَلٰكِنَّ اللّٰهَ قَتَلَهُمْۖ وَمَا رَمَيْتَ اِذْ رَمَيْتَ وَلٰكِنَّ اللّٰهَ رَمٰىۚ وَلِيُبْلِيَ الْمُؤْمِنِيْنَ مِنْهُ بَلَاۤءً حَسَنًاۗ اِنَّ اللّٰهَ سَمِيْعٌ عَلِيْمٌ', 'fa lam taqtulụhum wa lākinnallāha qatalahum wa mā ramaita iż ramaita wa lākinnallāha ramā, wa liyubliyal-mu`minīna min-hu balā`an ḥasanā, innallāha samī\'un \'alīm'),
(1177, 8, 18, 'ذٰلِكُمْ وَاَنَّ اللّٰهَ مُوْهِنُ كَيْدِ الْكٰفِرِيْنَ', 'żālikum wa annallāha mụhinu kaidil-kāfirīn'),
(1178, 8, 19, 'اِنْ تَسْتَفْتِحُوْا فَقَدْ جَاۤءَكُمُ الْفَتْحُۚ وَاِنْ تَنْتَهُوْا فَهُوَ خَيْرٌ لَّكُمْۚ وَاِنْ تَعُوْدُوْا نَعُدْۚ وَلَنْ تُغْنِيَ عَنْكُمْ فِئَتُكُمْ شَيْـًٔا وَّلَوْ كَثُرَتْۙ وَاَنَّ اللّٰهَ مَعَ الْمُؤْمِنِيْنَ', 'in tastaftiḥụ fa qad jā`akumul-fat-ḥ, wa in tantahụ fa huwa khairul lakum, wa in ta\'ụdụ na\'ud, wa lan tugniya \'angkum fi`atukum syai`aw walau kaṡurat wa annallāha ma\'al-mu`minīn'),
(1179, 8, 20, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَطِيْعُوا اللّٰهَ وَرَسُوْلَهٗ وَلَا تَوَلَّوْا عَنْهُ وَاَنْتُمْ تَسْمَعُوْنَ', 'yā ayyuhallażīna āmanū aṭī\'ullāha wa rasụlahụ wa lā tawallau \'an-hu wa antum tasma\'ụn'),
(1180, 8, 21, 'وَلَا تَكُوْنُوْا كَالَّذِيْنَ قَالُوْا سَمِعْنَا وَهُمْ لَا يَسْمَعُوْنَۚ', 'wa lā takụnụ kallażīna qālụ sami\'nā wa hum lā yasma\'ụn'),
(1181, 8, 22, '۞ اِنَّ شَرَّ الدَّوَاۤبِّ عِنْدَ اللّٰهِ الصُّمُّ الْبُكْمُ الَّذِيْنَ لَا يَعْقِلُوْنَ', 'inna syarrad-dawābbi \'indallāhiṣ-ṣummul-bukmullażīna lā ya\'qilụn'),
(1182, 8, 23, 'وَلَوْ عَلِمَ اللّٰهُ فِيْهِمْ خَيْرًا لَّاَسْمَعَهُمْۗ وَلَوْ اَسْمَعَهُمْ لَتَوَلَّوْا وَّهُمْ مُّعْرِضُوْنَ', 'walau \'alimallāhu fīhim khairal la`asma\'ahum, walau asma\'ahum latawallaw wa hum mu\'riḍụn'),
(1183, 8, 24, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اسْتَجِيْبُوْا لِلّٰهِ وَلِلرَّسُوْلِ اِذَا دَعَاكُمْ لِمَا يُحْيِيْكُمْۚ وَاعْلَمُوْٓا اَنَّ اللّٰهَ يَحُوْلُ بَيْنَ الْمَرْءِ وَقَلْبِهٖ وَاَنَّهٗٓ اِلَيْهِ تُحْشَرُوْنَ', 'yā ayyuhallażīna āmanustajībụ lillāhi wa lir-rasụli iżā da\'ākum limā yuḥyīkum, wa\'lamū annallāha yaḥụlu bainal-mar`i wa qalbihī wa annahū ilaihi tuḥsyarụn'),
(1184, 8, 25, 'وَاتَّقُوْا فِتْنَةً لَّا تُصِيْبَنَّ الَّذِيْنَ ظَلَمُوْا مِنْكُمْ خَاۤصَّةً ۚوَاعْلَمُوْٓا اَنَّ اللّٰهَ شَدِيْدُ الْعِقَابِ', 'wattaqụ fitnatal lā tuṣībannallażīna ẓalamụ mingkum khāṣṣah, wa\'lamū annallāha syadīdul-\'iqāb'),
(1185, 8, 26, 'وَاذْكُرُوْٓا اِذْ اَنْتُمْ قَلِيْلٌ مُّسْتَضْعَفُوْنَ فِى الْاَرْضِ تَخَافُوْنَ اَنْ يَّتَخَطَّفَكُمُ النَّاسُ فَاٰوٰىكُمْ وَاَيَّدَكُمْ بِنَصْرِهٖ وَرَزَقَكُمْ مِّنَ الطَّيِّبٰتِ لَعَلَّكُمْ تَشْكُرُوْنَ', 'ważkurū iż antum qalīlum mustaḍ\'afụna fil-arḍi takhāfụna ay yatakhaṭṭafakumun-nāsu fa āwākum wa ayyadakum binaṣrihī wa razaqakum minaṭ-ṭayyibāti la\'allakum tasykurụn'),
(1186, 8, 27, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَخُوْنُوا اللّٰهَ وَالرَّسُوْلَ وَتَخُوْنُوْٓا اَمٰنٰتِكُمْ وَاَنْتُمْ تَعْلَمُوْنَ', 'yā ayyuhallażīna āmanụ lā takhụnullāha war-rasụla wa takhụnū amānātikum wa antum ta\'lamụn'),
(1187, 8, 28, 'وَاعْلَمُوْٓا اَنَّمَآ اَمْوَالُكُمْ وَاَوْلَادُكُمْ فِتْنَةٌ ۙوَّاَنَّ اللّٰهَ عِنْدَهٗٓ اَجْرٌ عَظِيْمٌ', 'wa\'lamū annamā amwālukum wa aulādukum fitnatuw wa annallāha \'indahū ajrun \'aẓīm'),
(1188, 8, 29, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنْ تَتَّقُوا اللّٰهَ يَجْعَلْ لَّكُمْ فُرْقَانًا وَّيُكَفِّرْ عَنْكُمْ سَيِّاٰتِكُمْ وَيَغْفِرْ لَكُمْۗ وَاللّٰهُ ذُو الْفَضْلِ الْعَظِيْمِ', 'yā ayyuhallażīna āmanū in tattaqullāha yaj\'al lakum furqānaw wa yukaffir \'angkum sayyi`ātikum wa yagfir lakum, wallāhu żul-faḍlil-\'aẓīm'),
(1189, 8, 30, 'وَاِذْ يَمْكُرُ بِكَ الَّذِيْنَ كَفَرُوْا لِيُثْبِتُوْكَ اَوْ يَقْتُلُوْكَ اَوْ يُخْرِجُوْكَۗ وَيَمْكُرُوْنَ وَيَمْكُرُ اللّٰهُ ۗوَاللّٰهُ خَيْرُ الْمَاكِرِيْنَ', 'wa iż yamkuru bikallażīna kafarụ liyuṡbitụka au yaqtulụka au yukhrijụk, wa yamkurụna wa yamkurullāh, wallāhu khairul-mākirīn'),
(1190, 8, 31, 'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا قَالُوْا قَدْ سَمِعْنَا لَوْ نَشَاۤءُ لَقُلْنَا مِثْلَ هٰذَآ ۙاِنْ هٰذَآ اِلَّآ اَسَاطِيْرُ الْاَوَّلِيْنَ', 'wa iżā tutlā \'alaihim āyātunā qālụ qad sami\'nā lau nasyā`u laqulnā miṡla hāżā in hāżā illā asāṭīrul-awwalīn'),
(1191, 8, 32, 'وَاِذْ قَالُوا اللهم اِنْ كَانَ هٰذَا هُوَ الْحَقَّ مِنْ عِنْدِكَ فَاَمْطِرْ عَلَيْنَا حِجَارَةً مِّنَ السَّمَاۤءِ اَوِ ائْتِنَا بِعَذَابٍ اَلِيْمٍ', 'wa iż qālullāhumma ing kāna hāżā huwal-ḥaqqa min \'indika fa amṭir \'alainā ḥijāratam minas-samā`i awi`tinā bi\'ażābin alīm'),
(1192, 8, 33, 'وَمَا كَانَ اللّٰهُ لِيُعَذِّبَهُمْ وَاَنْتَ فِيْهِمْۚ وَمَا كَانَ اللّٰهُ مُعَذِّبَهُمْ وَهُمْ يَسْتَغْفِرُوْنَ', 'wa mā kānallāhu liyu\'ażżibahum wa anta fīhim, wa mā kānallāhu mu\'ażżibahum wa hum yastagfirụn'),
(1193, 8, 34, 'وَمَا لَهُمْ اَلَّا يُعَذِّبَهُمُ اللّٰهُ وَهُمْ يَصُدُّوْنَ عَنِ الْمَسْجِدِ الْحَرَامِ وَمَا كَانُوْٓا اَوْلِيَاۤءَهٗۗ اِنْ اَوْلِيَاۤؤُهٗٓ اِلَّا الْمُتَّقُوْنَ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ', 'wa mā lahum allā yu\'ażżibahumullāhu wa hum yaṣuddụna \'anil-masjidil-ḥarāmi wa mā kānū auliyā`ah, in auliyā`uhū illal-muttaqụna wa lākinna akṡarahum lā ya\'lamụn'),
(1194, 8, 35, 'وَمَا كَانَ صَلَاتُهُمْ عِنْدَ الْبَيْتِ اِلَّا مُكَاۤءً وَّتَصْدِيَةًۗ فَذُوْقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُوْنَ', 'wa mā kāna ṣalātuhum \'indal-baiti illā mukā`aw wa taṣdiyah, fa żụqul-\'ażāba bimā kuntum takfurụn'),
(1195, 8, 36, 'اِنَّ الَّذِيْنَ كَفَرُوْا يُنْفِقُوْنَ اَمْوَالَهُمْ لِيَصُدُّوْا عَنْ سَبِيْلِ اللّٰهِ ۗفَسَيُنْفِقُوْنَهَا ثُمَّ تَكُوْنُ عَلَيْهِمْ حَسْرَةً ثُمَّ يُغْلَبُوْنَ ەۗ وَالَّذِيْنَ كَفَرُوْٓا اِلٰى جَهَنَّمَ يُحْشَرُوْنَۙ', 'innallażīna kafarụ yunfiqụna amwālahum liyaṣuddụ \'an sabīlillāh, fa sayunfiqụnahā ṡumma takụnu \'alaihim ḥasratan ṡumma yuglabụn, wallażīna kafarū ilā jahannama yuḥsyarụn'),
(1196, 8, 37, 'لِيَمِيْزَ اللّٰهُ الْخَبِيْثَ مِنَ الطَّيِّبِ وَيَجْعَلَ الْخَبِيْثَ بَعْضَهٗ عَلٰى بَعْضٍ فَيَرْكُمَهٗ جَمِيْعًا فَيَجْعَلَهٗ فِيْ جَهَنَّمَۗ اُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ', 'liyamīzallāhul-khabīṡa minaṭ-ṭayyibi wa yaj\'alal-khabīṡa ba\'ḍahụ \'alā ba\'ḍin fa yarkumahụ jamī\'an fa yaj\'alahụ fī jahannam, ulā`ika humul-khāsirụn'),
(1197, 8, 38, 'قُلْ لِّلَّذِيْنَ كَفَرُوْٓا اِنْ يَّنْتَهُوْا يُغْفَرْ لَهُمْ مَّا قَدْ سَلَفَۚ وَاِنْ يَّعُوْدُوْا فَقَدْ مَضَتْ سُنَّتُ الْاَوَّلِيْنَ', 'qul lillażīna kafarū iy yantahụ yugfar lahum mā qad salaf, wa iy ya\'ụdụ fa qad maḍat sunnatul-awwalīn'),
(1198, 8, 39, 'وَقَاتِلُوْهُمْ حَتّٰى لَا تَكُوْنَ فِتْنَةٌ وَّيَكُوْنَ الدِّيْنُ كُلُّهٗ لِلّٰهِۚ فَاِنِ انْتَهَوْا فَاِنَّ اللّٰهَ بِمَا يَعْمَلُوْنَ بَصِيْرٌ', 'wa qātilụhum ḥattā lā takụna fitnatuw wa yakụnad-dīnu kulluhụ lillāh, fa inintahau fa innallāha bimā ya\'malụna baṣīr'),
(1199, 8, 40, 'وَاِنْ تَوَلَّوْا فَاعْلَمُوْٓا اَنَّ اللّٰهَ مَوْلٰىكُمْ ۗنِعْمَ الْمَوْلٰى وَنِعْمَ النَّصِيْرُ ۔', 'wa in tawallau fa\'lamū annallāha maulākum, ni\'mal-maulā wa ni\'man-naṣīr'),
(1200, 8, 41, '۞ وَاعْلَمُوْٓا اَنَّمَا غَنِمْتُمْ مِّنْ شَيْءٍ فَاَنَّ لِلّٰهِ خُمُسَهٗ وَلِلرَّسُوْلِ وَلِذِى الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنِ وَابْنِ السَّبِيْلِ اِنْ كُنْتُمْ اٰمَنْتُمْ بِاللّٰهِ وَمَآ اَنْزَلْنَا عَلٰى عَبْدِنَا يَوْمَ الْفُرْقَانِ يَوْمَ الْتَقَى الْجَمْعٰنِۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'wa\'lamū annamā ganimtum min syai`in fa anna lillāhi khumusahụ wa lir-rasụli wa liżil-qurbā wal-yatāmā wal-masākīni wabnis-sabīli ing kuntum āmantum billāhi wa mā anzalnā \'alā \'abdinā yaumal-furqāni yaumaltaqal jam\'ān, wallāhu \'alā kulli syai`ing qadīr'),
(1201, 8, 42, 'اِذْ اَنْتُمْ بِالْعُدْوَةِ الدُّنْيَا وَهُمْ بِالْعُدْوَةِ الْقُصْوٰى وَالرَّكْبُ اَسْفَلَ مِنْكُمْۗ وَلَوْ تَوَاعَدْتُّمْ لَاخْتَلَفْتُمْ فِى الْمِيْعٰدِۙ وَلٰكِنْ لِّيَقْضِيَ اللّٰهُ اَمْرًا كَانَ مَفْعُوْلًا ەۙ لِّيَهْلِكَ مَنْ هَلَكَ عَنْۢ بَيِّنَةٍ وَّيَحْيٰى مَنْ حَيَّ عَنْۢ بَيِّنَةٍۗ وَاِنَّ اللّٰهَ لَسَمِيْعٌ عَلِيْمٌۙ', 'iż antum bil-\'udwatid-dun-yā wa hum bil-\'udwatil-quṣwā war-rakbu asfala mingkum, walau tawā\'attum lakhtalaftum fil-mī\'ādi wa lākil liyaqḍiyallāhu amrang kāna maf\'ụlal liyahlika man halaka \'am bayyinatiw wa yaḥyā man ḥayya \'am bayyinah, wa innallāha lasamī\'un \'alīm'),
(1202, 8, 43, 'اِذْ يُرِيْكَهُمُ اللّٰهُ فِيْ مَنَامِكَ قَلِيْلًاۗ وَلَوْ اَرٰىكَهُمْ كَثِيْرًا لَّفَشِلْتُمْ وَلَتَنَازَعْتُمْ فِى الْاَمْرِ وَلٰكِنَّ اللّٰهَ سَلَّمَۗ اِنَّهٗ عَلِيْمٌۢ بِذَاتِ الصُّدُوْرِ', 'iż yurīkahumullāhu fī manāmika qalīlā, walau arākahum kaṡīral lafasyiltum wa latanāza\'tum fil-amri wa lākinnallāha sallam, innahụ \'alīmum biżātiṣ-ṣudụr'),
(1203, 8, 44, 'وَاِذْ يُرِيْكُمُوْهُمْ اِذِ الْتَقَيْتُمْ فِيْٓ اَعْيُنِكُمْ قَلِيْلًا وَّيُقَلِّلُكُمْ فِيْٓ اَعْيُنِهِمْ لِيَقْضِيَ اللّٰهُ اَمْرًا كَانَ مَفْعُوْلًا ۗوَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ', 'wa iż yurīkumụhum iżiltaqaitum fī a\'yunikum qalīlaw wa yuqallilukum fī a\'yunihim liyaqḍiyallāhu amrang kāna maf\'ụlā, wa ilallāhi turja\'ul-umụr'),
(1204, 8, 45, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا لَقِيْتُمْ فِئَةً فَاثْبُتُوْا وَاذْكُرُوا اللّٰهَ كَثِيْرًا لَّعَلَّكُمْ تُفْلِحُوْنَۚ', 'yā ayyuhallażīna āmanū iżā laqītum fi`atan faṡbutụ ważkurullāha kaṡīral la\'allakum tufliḥụn'),
(1205, 8, 46, 'وَاَطِيْعُوا اللّٰهَ وَرَسُوْلَهٗ وَلَا تَنَازَعُوْا فَتَفْشَلُوْا وَتَذْهَبَ رِيْحُكُمْ وَاصْبِرُوْاۗ اِنَّ اللّٰهَ مَعَ الصّٰبِرِيْنَۚ', 'wa aṭī\'ullāha wa rasụlahụ wa lā tanāza\'ụ fa tafsyalụ wa taż-haba rīḥukum waṣbirụ, innallāha ma\'aṣ-ṣābirīn'),
(1206, 8, 47, 'وَلَا تَكُوْنُوْا كَالَّذِيْنَ خَرَجُوْا مِنْ دِيَارِهِمْ بَطَرًا وَّرِئَاۤءَ النَّاسِ وَيَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ ۗوَاللّٰهُ بِمَايَعْمَلُوْنَ مُحِيْطٌ', 'wa lā takụnụ kallażīna kharajụ min diyārihim baṭaraw wa ri`ā`an-nāsi wa yaṣuddụna \'an sabīlillāh, wallāhu bimā ya\'malụna muḥīṭ'),
(1207, 8, 48, 'وَاِذْ زَيَّنَ لَهُمُ الشَّيْطٰنُ اَعْمَالَهُمْ وَقَالَ لَا غَالِبَ لَكُمُ الْيَوْمَ مِنَ النَّاسِ وَاِنِّيْ جَارٌ لَّكُمْۚ فَلَمَّا تَرَاۤءَتِ الْفِئَتٰنِ نَكَصَ عَلٰى عَقِبَيْهِ وَقَالَ اِنِّيْ بَرِيْۤءٌ مِّنْكُمْ اِنِّيْٓ اَرٰى مَا لَا تَرَوْنَ اِنِّيْٓ اَخَافُ اللّٰهَ ۗوَاللّٰهُ شَدِيْدُ الْعِقَابِ', 'wa iż zayyana lahumusy-syaiṭānu a\'mālahum wa qāla lā gāliba lakumul-yauma minan-nāsi wa innī jārul lakum, fa lammā tarā`atil-fi`atāni nakaṣa \'alā \'aqibaihi wa qāla innī barī`um mingkum innī arā mā lā tarauna innī akhāfullāh, wallāhu syadīdul-\'iqāb'),
(1208, 8, 49, 'اِذْ يَقُوْلُ الْمُنٰفِقُوْنَ وَالَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ غَرَّ هٰٓؤُلَاۤءِ دِيْنُهُمْۗ وَمَنْ يَّتَوَكَّلْ عَلَى اللّٰهِ فَاِنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ', 'iż yaqụlul-munāfiqụna wallażīna fī qulụbihim maraḍun garra hā`ulā`i dīnuhum, wa may yatawakkal \'alallāhi fa innallāha \'azīzun ḥakīm'),
(1209, 8, 50, 'وَلَوْ تَرٰٓى اِذْ يَتَوَفَّى الَّذِيْنَ كَفَرُوا الْمَلٰۤىِٕكَةُ يَضْرِبُوْنَ وُجُوْهَهُمْ وَاَدْبَارَهُمْۚ وَذُوْقُوْا عَذَابَ الْحَرِيْقِ', 'walau tarā iż yatawaffallażīna kafarul-malā`ikatu yaḍribụna wujụhahum wa adbārahum, wa żụqụ \'ażābal-ḥarīq'),
(1210, 8, 51, 'ذٰلِكَ بِمَا قَدَّمَتْ اَيْدِيْكُمْ وَاَنَّ اللّٰهَ لَيْسَ بِظَلَّامٍ لِّلْعَبِيْدِۙ', 'żālika bimā qaddamat aidīkum wa annallāha laisa biẓallāmil lil-\'abīd'),
(1211, 8, 52, 'كَدَأْبِ اٰلِ فِرْعَوْنَۙ وَالَّذِيْنَ مِنْ قَبْلِهِمْۗ كَفَرُوْا بِاٰيٰتِ اللّٰهِ فَاَخَذَهُمُ اللّٰهُ بِذُنُوْبِهِمْۗ اِنَّ اللّٰهَ قَوِيٌّ شَدِيْدُ الْعِقَابِ', 'kada`bi āli fir\'auna wallażīna ming qablihim, kafarụ bi`āyātillāhi fa akhażahumullāhu biżunụbihim, innallāha qawiyyun syadīdul-\'iqāb'),
(1212, 8, 53, 'ذٰلِكَ بِاَنَّ اللّٰهَ لَمْ يَكُ مُغَيِّرًا نِّعْمَةً اَنْعَمَهَا عَلٰى قَوْمٍ حَتّٰى يُغَيِّرُوْا مَا بِاَنْفُسِهِمْۙ وَاَنَّ اللّٰهَ سَمِيْعٌ عَلِيْمٌۙ', 'żālika bi`annallāha lam yaku mugayyiran ni\'matan an\'amahā \'alā qaumin ḥattā yugayyirụ mā bi`anfusihim wa annallāha samī\'un \'alīm'),
(1213, 8, 54, 'كَدَأْبِ اٰلِ فِرْعَوْنَۙ وَالَّذِيْنَ مِنْ قَبْلِهِمْۚ كَذَّبُوْا بِاٰيٰتِ رَبِّهِمْ فَاَهْلَكْنٰهُمْ بِذُنُوْبِهِمْ وَاَغْرَقْنَآ اٰلَ فِرْعَوْنَۚ وَكُلٌّ كَانُوْا ظٰلِمِيْنَ', 'kada`bi āli fir\'auna wallażīna ming qablihim, każżabụ bi`āyāti rabbihim fa ahlaknāhum biżunụbihim wa agraqnā āla fir\'aụn, wa kullung kānụ ẓālimīn'),
(1214, 8, 55, 'اِنَّ شَرَّ الدَّوَاۤبِّ عِنْدَ اللّٰهِ الَّذِيْنَ كَفَرُوْا فَهُمْ لَا يُؤْمِنُوْنَۖ', 'inna syarrad-dawābbi \'indallāhillażīna kafarụ fa hum lā yu`minụn'),
(1215, 8, 56, 'الَّذِيْنَ عَاهَدْتَّ مِنْهُمْ ثُمَّ يَنْقُضُوْنَ عَهْدَهُمْ فِيْ كُلِّ مَرَّةٍ وَّهُمْ لَا يَتَّقُوْنَ', 'allażīna \'āhatta min-hum ṡumma yangquḍụna \'ahdahum fī kulli marratiw wa hum lā yattaqụn'),
(1216, 8, 57, 'فَاِمَّا تَثْقَفَنَّهُمْ فِى الْحَرْبِ فَشَرِّدْ بِهِمْ مَّنْ خَلْفَهُمْ لَعَلَّهُمْ يَذَّكَّرُوْنَ', 'fa immā taṡqafannahum fil-ḥarbi fa syarrid bihim man khalfahum la\'allahum yażżakkarụn'),
(1217, 8, 58, 'وَاِمَّا تَخَافَنَّ مِنْ قَوْمٍ خِيَانَةً فَانْۢبِذْ اِلَيْهِمْ عَلٰى سَوَاۤءٍۗ اِنَّ اللّٰهَ لَا يُحِبُّ الْخَاۤىِٕنِيْنَ', 'wa immā takhāfanna ming qaumin khiyānatan fambiż ilaihim \'alā sawā`, innallāha lā yuḥibbul-khā`inīn'),
(1218, 8, 59, 'وَلَا يَحْسَبَنَّ الَّذِيْنَ كَفَرُوْا سَبَقُوْاۗ اِنَّهُمْ لَا يُعْجِزُوْنَ', 'wa lā yaḥsabannallażīna kafarụ sabaqụ, innahum lā yu\'jizụn'),
(1219, 8, 60, 'وَاَعِدُّوْا لَهُمْ مَّا اسْتَطَعْتُمْ مِّنْ قُوَّةٍ وَّمِنْ رِّبَاطِ الْخَيْلِ تُرْهِبُوْنَ بِهٖ عَدُوَّ اللّٰهِ وَعَدُوَّكُمْ وَاٰخَرِيْنَ مِنْ دُوْنِهِمْۚ لَا تَعْلَمُوْنَهُمْۚ اَللّٰهُ يَعْلَمُهُمْۗ وَمَا تُنْفِقُوْا مِنْ شَيْءٍ فِيْ سَبِيْلِ اللّٰهِ يُوَفَّ اِلَيْكُمْ وَاَنْتُمْ لَا تُظْلَمُوْنَ', 'wa a\'iddụ lahum mastaṭa\'tum ming quwwatiw wa mir ribāṭil-khaili tur-hibụna bihī \'aduwwallāhi wa \'aduwwakum wa ākharīna min dụnihim, lā ta\'lamụnahum, allāhu ya\'lamuhum, wa mā tunfiqụ min syai`in fī sabīlillāhi yuwaffa ilaikum wa antum lā tuẓlamụn'),
(1220, 8, 61, '۞ وَاِنْ جَنَحُوْا لِلسَّلْمِ فَاجْنَحْ لَهَا وَتَوَكَّلْ عَلَى اللّٰهِ ۗاِنَّهٗ هُوَ السَّمِيْعُ الْعَلِيْمُ', 'wa in janaḥụ lis-salmi fajnaḥ lahā wa tawakkal \'alallāh, innahụ huwas-samī\'ul-\'alīm'),
(1221, 8, 62, 'وَاِنْ يُّرِيْدُوْٓا اَنْ يَّخْدَعُوْكَ فَاِنَّ حَسْبَكَ اللّٰهُ ۗهُوَ الَّذِيْٓ اَيَّدَكَ بِنَصْرِهٖ وَبِالْمُؤْمِنِيْنَۙ', 'wa iy yurīdū ay yakhda\'ụka fa inna ḥasbakallāh, huwallażī ayyadaka binaṣrihī wa bil-mu`minīn'),
(1222, 8, 63, 'وَاَلَّفَ بَيْنَ قُلُوْبِهِمْۗ لَوْاَنْفَقْتَ مَا فِى الْاَرْضِ جَمِيْعًا مَّآ اَلَّفْتَ بَيْنَ قُلُوْبِهِمْ وَلٰكِنَّ اللّٰهَ اَلَّفَ بَيْنَهُمْۗ اِنَّهٗ عَزِيْزٌ حَكِيْمٌ', 'wa allafa baina qulụbihim, lau anfaqta mā fil-arḍi jamī\'am mā allafta baina qulụbihim wa lākinnallāha allafa bainahum, innahụ \'azīzun ḥakīm'),
(1223, 8, 64, 'يٰٓاَيُّهَا النَّبِيُّ حَسْبُكَ اللّٰهُ وَمَنِ اتَّبَعَكَ مِنَ الْمُؤْمِنِيْنَ', 'yā ayyuhan-nabiyyu ḥasbukallāhu wa manittaba\'aka minal-mu`minīn'),
(1224, 8, 65, 'يٰٓاَيُّهَا النَّبِيُّ حَرِّضِ الْمُؤْمِنِيْنَ عَلَى الْقِتَالِۗ اِنْ يَّكُنْ مِّنْكُمْ عِشْرُوْنَ صَابِرُوْنَ يَغْلِبُوْا مِائَتَيْنِۚ وَاِنْ يَّكُنْ مِّنْكُمْ مِّائَةٌ يَّغْلِبُوْٓا اَلْفًا مِّنَ الَّذِيْنَ كَفَرُوْا بِاَنَّهُمْ قَوْمٌ لَّا يَفْقَهُوْنَ', 'yā ayyuhan-nabiyyu ḥarriḍil-mu`minīna \'alal-qitāl, iy yakum mingkum \'isyrụna ṣābirụna yaglibụ mi`ataīn, wa iy yakum mingkum mi`atuy yaglibū alfam minallażīna kafarụ bi`annahum qaumul lā yafqahụn'),
(1225, 8, 66, 'اَلْـٰٔنَ خَفَّفَ اللّٰهُ عَنْكُمْ وَعَلِمَ اَنَّ فِيْكُمْ ضَعْفًاۗ فَاِنْ يَّكُنْ مِّنْكُمْ مِّائَةٌ صَابِرَةٌ يَّغْلِبُوْا مِائَتَيْنِۚ وَاِنْ يَّكُنْ مِّنْكُمْ اَلْفٌ يَّغْلِبُوْٓا اَلْفَيْنِ بِاِذْنِ اللّٰهِ ۗوَاللّٰهُ مَعَ الصّٰبِرِيْنَ', 'al-āna khaffafallāhu \'angkum wa \'alima anna fīkum ḍa\'fā, fa iy yakum mingkum mi`atun ṣābiratuy yaglibụ mi`ataīn, wa iy yakum mingkum alfuy yaglibū alfaini bi`iżnillāh, wallāhu ma\'aṣ-ṣābirīn'),
(1226, 8, 67, 'مَاكَانَ لِنَبِيٍّ اَنْ يَّكُوْنَ لَهٗٓ اَسْرٰى حَتّٰى يُثْخِنَ فِى الْاَرْضِۗ تُرِيْدُوْنَ عَرَضَ الدُّنْيَاۖ وَاللّٰهُ يُرِيْدُ الْاٰخِرَةَۗ وَاللّٰهُ عَزِيْزٌحَكِيْمٌ', 'mā kāna linabiyyin ay yakụna lahū asrā ḥattā yuṡkhina fil-arḍ, turīdụna \'araḍad-dun-yā wallāhu yurīdul-ākhirah, wallāhu \'azīzun ḥakīm'),
(1227, 8, 68, 'لَوْلَاكِتٰبٌ مِّنَ اللّٰهِ سَبَقَ لَمَسَّكُمْ فِيْمَآ اَخَذْتُمْ عَذَابٌ عَظِيْمٌ', 'lau lā kitābum minallāhi sabaqa lamassakum fīmā akhażtum \'ażābun \'aẓīm'),
(1228, 8, 69, 'فَكُلُوْا مِمَّاغَنِمْتُمْ حَلٰلًا طَيِّبًاۖ وَّاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'fa kulụ mimmā ganimtum ḥalālan ṭayyibaw wattaqullāh, innallāha gafụrur raḥīm'),
(1229, 8, 70, 'يٰٓاَيُّهَا النَّبِيُّ قُلْ لِّمَنْ فِيْٓ اَيْدِيْكُمْ مِّنَ الْاَسْرٰٓىۙ اِنْ يَّعْلَمِ اللّٰهُ فِيْ قُلُوْبِكُمْ خَيْرًا يُّؤْتِكُمْ خَيْرًا مِّمَّآ اُخِذَ مِنْكُمْ وَيَغْفِرْ لَكُمْۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ', 'yā ayyuhan-nabiyyu qul liman fī aidīkum minal-asrā iy ya\'lamillāhu fī qulụbikum khairay yu`tikum khairam mimmā ukhiża mingkum wa yagfir lakum, wallāhu gafụrur raḥīm'),
(1230, 8, 71, 'وَاِنْ يُّرِيْدُوْا خِيَانَتَكَ فَقَدْ خَانُوا اللّٰهَ مِنْ قَبْلُ فَاَمْكَنَ مِنْهُمْ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ', 'wa iy yurīdụ khiyānataka fa qad khānullāha ming qablu fa amkana min-hum wallāhu \'alīmun ḥakīm'),
(1231, 8, 72, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَهَاجَرُوْا وَجَاهَدُوْا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ فِيْ سَبِيْلِ اللّٰهِ وَالَّذِيْنَ اٰوَوْا وَّنَصَرُوْٓا اُولٰۤىِٕكَ بَعْضُهُمْ اَوْلِيَاۤءُ بَعْضٍۗ وَالَّذِيْنَ اٰمَنُوْا وَلَمْ يُهَاجِرُوْا مَا لَكُمْ مِّنْ وَّلَايَتِهِمْ مِّنْ شَيْءٍ حَتّٰى يُهَاجِرُوْاۚ وَاِنِ اسْتَنْصَرُوْكُمْ فِى الدِّيْنِ فَعَلَيْكُمُ النَّصْرُ اِلَّا عَلٰى قَوْمٍۢ بَيْنَكُمْ وَبَيْنَهُمْ مِّيْثَاقٌۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرٌ', 'innallażīna āmanụ wa hājarụ wa jāhadụ bi`amwālihim wa anfusihim fī sabīlillāhi wallażīna āwaw wa naṣarū ulā`ika ba\'ḍuhum auliyā`u ba\'ḍ, wallażīna āmanụ wa lam yuhājirụ mā lakum miw walāyatihim min syai`in ḥattā yuhājirụ, wa inistanṣarụkum fid-dīni fa \'alaikumun-naṣru illā \'alā qaumim bainakum wa bainahum mīṡāq, wallāhu bimā ta\'malụna baṣīr'),
(1232, 8, 73, 'وَالَّذِيْنَ كَفَرُوْا بَعْضُهُمْ اَوْلِيَاۤءُ بَعْضٍۗ اِلَّا تَفْعَلُوْهُ تَكُنْ فِتْنَةٌ فِى الْاَرْضِ وَفَسَادٌ كَبِيْرٌۗ', 'wallażīna kafarụ ba\'ḍuhum auliyā`u ba\'ḍ, illā taf\'alụhu takun fitnatun fil-arḍi wa fasādung kabīr'),
(1233, 8, 74, 'وَالَّذِيْنَ اٰمَنُوْا وَهَاجَرُوْا وَجَاهَدُوْا فِيْ سَبِيْلِ اللّٰهِ وَالَّذِيْنَ اٰوَوْا وَّنَصَرُوْٓا اُولٰۤىِٕكَ هُمُ الْمُؤْمِنُوْنَ حَقًّاۗ لَهُمْ مَّغْفِرَةٌ وَّرِزْقٌ كَرِيْمٌ', 'wallażīna āmanụ wa hājarụ wa jāhadụ fī sabīlillāhi wallażīna āwaw wa naṣarū ulā`ika humul-mu`minụna ḥaqqā, lahum magfiratuw wa rizqung karīm'),
(1234, 8, 75, 'وَالَّذِيْنَ اٰمَنُوْا مِنْۢ بَعْدُ وَهَاجَرُوْا وَجَاهَدُوْا مَعَكُمْ فَاُولٰۤىِٕكَ مِنْكُمْۗ وَاُولُوا الْاَرْحَامِ بَعْضُهُمْ اَوْلٰى بِبَعْضٍ فِيْ كِتٰبِ اللّٰهِ ۗاِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ', 'wallażīna āmanụ mim ba\'du wa hājarụ wa jāhadụ ma\'akum fa ulā`ika mingkum, wa ulul-ar-ḥāmi ba\'ḍuhum aulā biba\'ḍin fī kitābillāh, innallāha bikulli syai`in \'alīm'),
(1235, 9, 1, 'بَرَاۤءَةٌ مِّنَ اللّٰهِ وَرَسُوْلِهٖٓ اِلَى الَّذِيْنَ عَاهَدْتُّمْ مِّنَ الْمُشْرِكِيْنَۗ', 'barā`atum minallāhi wa rasụlihī ilallażīna \'āhattum minal-musyrikīn'),
(1236, 9, 2, 'فَسِيْحُوْا فِى الْاَرْضِ اَرْبَعَةَ اَشْهُرٍ وَّاعْلَمُوْٓا اَنَّكُمْ غَيْرُ مُعْجِزِى اللّٰهِ ۙوَاَنَّ اللّٰهَ مُخْزِى الْكٰفِرِيْنَ', 'fa sīḥụ fil-arḍi arba\'ata asy-huriw wa\'lamū annakum gairu mu\'jizillāhi wa annallāha mukhzil-kāfirīn'),
(1237, 9, 3, 'وَاَذَانٌ مِّنَ اللّٰهِ وَرَسُوْلِهٖٓ اِلَى النَّاسِ يَوْمَ الْحَجِّ الْاَكْبَرِ اَنَّ اللّٰهَ بَرِيْۤءٌ مِّنَ الْمُشْرِكِيْنَ ەۙ وَرَسُوْلُهٗ ۗفَاِنْ تُبْتُمْ فَهُوَ خَيْرٌ لَّكُمْۚ وَاِنْ تَوَلَّيْتُمْ فَاعْلَمُوْٓا اَنَّكُمْ غَيْرُ مُعْجِزِى اللّٰهِ ۗوَبَشِّرِ الَّذِيْنَ كَفَرُوْا بِعَذَابٍ اَلِيْمٍۙ', 'wa ażānum minallāhi wa rasụlihī ilan-nāsi yaumal-ḥajjil-akbari annallāha barī`um minal-musyrikīna wa rasụluh, fa in tubtum fa huwa khairul lakum, wa in tawallaitum fa\'lamū annakum gairu mu\'jizillāh, wa basysyirillażīna kafarụ bi\'ażābin alīm'),
(1238, 9, 4, 'اِلَّا الَّذِيْنَ عَاهَدْتُّمْ مِّنَ الْمُشْرِكِيْنَ ثُمَّ لَمْ يَنْقُصُوْكُمْ شَيْـًٔا وَّلَمْ يُظَاهِرُوْا عَلَيْكُمْ اَحَدًا فَاَتِمُّوْٓا اِلَيْهِمْ عَهْدَهُمْ اِلٰى مُدَّتِهِمْۗ اِنَّ اللّٰهَ يُحِبُّ الْمُتَّقِيْنَ', 'illallażīna \'āhattum minal-musyrikīna ṡumma lam yangquṣụkum syai`aw wa lam yuẓāhirụ \'alaikum aḥadan fa atimmū ilaihim \'ahdahum ilā muddatihim, innallāha yuḥibbul muttaqīn'),
(1239, 9, 5, 'فَاِذَا انْسَلَخَ الْاَشْهُرُ الْحُرُمُ فَاقْتُلُوا الْمُشْرِكِيْنَ حَيْثُ وَجَدْتُّمُوْهُمْ وَخُذُوْهُمْ وَاحْصُرُوْهُمْ وَاقْعُدُوْا لَهُمْ كُلَّ مَرْصَدٍۚ فَاِنْ تَابُوْا وَاَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ فَخَلُّوْا سَبِيْلَهُمْۗ اِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'fa iżansalakhal-asy-hurul-ḥurumu faqtulul-musyrikīna ḥaiṡu wajattumụhum wa khużụhum waḥṣurụhum waq\'udụ lahum kulla marṣad, fa in tābụ wa aqāmuṣ-ṣalāta wa ātawuz-zakāta fa khallụ sabīlahum, innallāha gafụrur raḥīm'),
(1240, 9, 6, 'وَاِنْ اَحَدٌ مِّنَ الْمُشْرِكِيْنَ اسْتَجَارَكَ فَاَجِرْهُ حَتّٰى يَسْمَعَ كَلٰمَ اللّٰهِ ثُمَّ اَبْلِغْهُ مَأْمَنَهٗ ۗذٰلِكَ بِاَنَّهُمْ قَوْمٌ لَّا يَعْلَمُوْنَ', 'wa in aḥadum minal-musyrikīnastajāraka fa ajir-hu ḥattā yasma\'a kalāmallāhi ṡumma ablig-hu ma`manah, żālika bi`annahum qaumul lā ya\'lamụn'),
(1241, 9, 7, 'كَيْفَ يَكُوْنُ لِلْمُشْرِكِيْنَ عَهْدٌ عِنْدَ اللّٰهِ وَعِنْدَ رَسُوْلِهٖٓ اِلَّا الَّذِيْنَ عَاهَدْتُّمْ عِنْدَ الْمَسْجِدِ الْحَرَامِۚ فَمَا اسْتَقَامُوْا لَكُمْ فَاسْتَقِيْمُوْا لَهُمْ ۗاِنَّ اللّٰهَ يُحِبُّ الْمُتَّقِيْنَ', 'kaifa yakụnu lil-musyrikīna \'ahdun \'indallāhi wa \'inda rasụlihī illallażīna \'āhattum \'indal-masjidil-ḥarām, famastaqāmụ lakum fastaqīmụ lahum, innallāha yuḥibbul-muttaqīn'),
(1242, 9, 8, 'كَيْفَ وَاِنْ يَّظْهَرُوْا عَلَيْكُمْ لَا يَرْقُبُوْا فِيْكُمْ اِلًّا وَّلَا ذِمَّةً ۗيُرْضُوْنَكُمْ بِاَفْوَاهِهِمْ وَتَأْبٰى قُلُوْبُهُمْۚ وَاَكْثَرُهُمْ فٰسِقُوْنَۚ', 'kaifa wa iy yaẓ-harụ \'alaikum lā yarqubụ fīkum illaw wa lā żimmah, yurḍụnakum bi`afwāhihim wa ta`bā qulụbuhum, wa akṡaruhum fāsiqụn'),
(1243, 9, 9, 'اِشْتَرَوْا بِاٰيٰتِ اللّٰهِ ثَمَنًا قَلِيْلًا فَصَدُّوْا عَنْ سَبِيْلِهٖۗ اِنَّهُمْ سَاۤءَ مَاكَانُوْا يَعْمَلُوْنَ', 'isytarau bi`āyātillāhi ṡamanang qalīlan fa ṣaddụ \'an sabīlih, innahum sā`a mā kānụ ya\'malụn'),
(1244, 9, 10, 'لَا يَرْقُبُوْنَ فِيْ مُؤْمِنٍ اِلًّا وَّلَا ذِمَّةً ۗوَاُولٰۤىِٕكَ هُمُ الْمُعْتَدُوْنَ', 'lā yarqubụna fī mu`minin illaw wa lā żimmah, wa ulā`ika humul-mu\'tadụn'),
(1245, 9, 11, 'فَاِنْ تَابُوْا وَاَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ فَاِخْوَانُكُمْ فِى الدِّيْنِ ۗوَنُفَصِّلُ الْاٰيٰتِ لِقَوْمٍ يَّعْلَمُوْنَ', 'fa in tābụ wa aqāmuṣ-ṣalāta wa ātawuz-zakāta fa ikhwānukum fid-dīn, wa nufaṣṣilul-āyāti liqaumiy ya\'lamụn'),
(1246, 9, 12, 'وَاِنْ نَّكَثُوْٓا اَيْمَانَهُمْ مِّنْۢ بَعْدِ عَهْدِهِمْ وَطَعَنُوْا فِيْ دِيْنِكُمْ فَقَاتِلُوْٓا اَىِٕمَّةَ الْكُفْرِۙ اِنَّهُمْ لَآ اَيْمَانَ لَهُمْ لَعَلَّهُمْ يَنْتَهُوْنَ', 'wa in nakaṡū aimānahum mim ba\'di \'ahdihim wa ṭa\'anụ fī dīnikum fa qātilū a`immatal-kufri innahum lā aimāna lahum la\'allahum yantahụn'),
(1247, 9, 13, 'اَلَا تُقَاتِلُوْنَ قَوْمًا نَّكَثُوْٓا اَيْمَانَهُمْ وَهَمُّوْا بِاِخْرَاجِ الرَّسُوْلِ وَهُمْ بَدَءُوْكُمْ اَوَّلَ مَرَّةٍۗ اَتَخْشَوْنَهُمْ ۚفَاللّٰهُ اَحَقُّ اَنْ تَخْشَوْهُ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ', 'alā tuqātilụna qauman nakaṡū aimānahum wa hammụ bi`ikhrājir-rasụli wa hum bada`ụkum awwala marrah, a takhsyaunahum, fallāhu aḥaqqu an takhsyauhu ing kuntum mu`minīn'),
(1248, 9, 14, 'قَاتِلُوْهُمْ يُعَذِّبْهُمُ اللّٰهُ بِاَيْدِيْكُمْ وَيُخْزِهِمْ وَيَنْصُرْكُمْ عَلَيْهِمْ وَيَشْفِ صُدُوْرَ قَوْمٍ مُّؤْمِنِيْنَۙ', 'qātilụhum yu\'ażżib-humullāhu bi`aidīkum wa yukhzihim wa yanṣurkum \'alaihim wa yasyfi ṣudụra qaumim mu`minīn'),
(1249, 9, 15, 'وَيُذْهِبْ غَيْظَ قُلُوْبِهِمْۗ وَيَتُوْبُ اللّٰهُ عَلٰى مَنْ يَّشَاۤءُۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ', 'wa yuż-hib gaiẓa qulụbihim, wa yatụbullāhu \'alā may yasyā`, wallāhu \'alīmun ḥakīm'),
(1250, 9, 16, 'اَمْ حَسِبْتُمْ اَنْ تُتْرَكُوْا وَلَمَّا يَعْلَمِ اللّٰهُ الَّذِيْنَ جَاهَدُوْا مِنْكُمْ وَلَمْ يَتَّخِذُوْا مِنْ دُوْنِ اللّٰهِ وَلَا رَسُوْلِهٖ وَلَا الْمُؤْمِنِيْنَ وَلِيْجَةً ۗوَاللّٰهُ خَبِيْرٌۢ بِمَا تَعْمَلُوْنَ', 'am ḥasibtum an tutrakụ wa lammā ya\'lamillāhullażīna jāhadụ mingkum wa lam yattakhiżụ min dụnillāhi wa lā rasụlihī wa lal-mu`minīna walījah, wallāhu khabīrum bimā ta\'malụn'),
(1251, 9, 17, 'مَا كَانَ لِلْمُشْرِكِيْنَ اَنْ يَّعْمُرُوْا مَسٰجِدَ اللّٰهِ شٰهِدِيْنَ عَلٰٓى اَنْفُسِهِمْ بِالْكُفْرِۗ اُولٰۤىِٕكَ حَبِطَتْ اَعْمَالُهُمْۚ وَ فِى النَّارِ هُمْ خٰلِدُوْنَ', 'mā kāna lil-musyrikīna ay ya\'murụ masājidallāhi syāhidīna \'alā anfusihim bil-kufr, ulā`ika ḥabiṭat a\'māluhum, wa fin-nāri hum khālidụn'),
(1252, 9, 18, 'اِنَّمَا يَعْمُرُ مَسٰجِدَ اللّٰهِ مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَاَقَامَ الصَّلٰوةَ وَاٰتَى الزَّكٰوةَ وَلَمْ يَخْشَ اِلَّا اللّٰهَ ۗفَعَسٰٓى اُولٰۤىِٕكَ اَنْ يَّكُوْنُوْا مِنَ الْمُهْتَدِيْنَ', 'innamā ya\'muru masājidallāhi man āmana billāhi wal-yaumil-ākhiri wa aqāmaṣ-ṣalāta wa ātaz-zakāta wa lam yakhsya illallāh, fa \'asā ulā`ika ay yakụnụ minal-muhtadīn'),
(1253, 9, 19, '۞ اَجَعَلْتُمْ سِقَايَةَ الْحَاۤجِّ وَعِمَارَةَ الْمَسْجِدِ الْحَرَامِ كَمَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَجَاهَدَ فِيْ سَبِيْلِ اللّٰهِ ۗ لَا يَسْتَوٗنَ عِنْدَ اللّٰهِ ۗوَاللّٰهُ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَۘ', 'a ja\'altum siqāyatal-ḥājji wa \'imāratal-masjidil-ḥarāmi kaman āmana billāhi wal-yaumil-ākhiri wa jāhada fī sabīlillāh, lā yastawụna \'indallāh, wallāhu lā yahdil-qaumaẓ-ẓālimīn'),
(1254, 9, 20, 'اَلَّذِيْنَ اٰمَنُوْا وَهَاجَرُوْا وَجَاهَدُوْا فِيْ سَبِيْلِ اللّٰهِ بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۙ اَعْظَمُ دَرَجَةً عِنْدَ اللّٰهِ ۗوَاُولٰۤىِٕكَ هُمُ الْفَاۤىِٕزُوْنَ', 'allażīna āmanụ wa hājarụ wa jāhadụ fī sabīlillāhi bi`amwālihim wa anfusihim a\'ẓamu darajatan \'indallāh, wa ulā`ika humul-fā`izụn'),
(1255, 9, 21, 'يُبَشِّرُهُمْ رَبُّهُمْ بِرَحْمَةٍ مِّنْهُ وَرِضْوَانٍ وَّجَنّٰتٍ لَّهُمْ فِيْهَا نَعِيْمٌ مُّقِيْمٌۙ', 'yubasysyiruhum rabbuhum biraḥmatim min-hu wa riḍwāniw wa jannātil lahum fīhā na\'īmum muqīm'),
(1256, 9, 22, 'خٰلِدِيْنَ فِيْهَآ اَبَدًا ۗاِنَّ اللّٰهَ عِنْدَهٗٓ اَجْرٌ عَظِيْمٌ', 'khālidīna fīhā abadā, innallāha \'indahū ajrun \'aẓīm'),
(1257, 9, 23, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوْٓا اٰبَاۤءَكُمْ وَاِخْوَانَكُمْ اَوْلِيَاۤءَ اِنِ اسْتَحَبُّوا الْكُفْرَ عَلَى الْاِيْمَانِۗ وَمَنْ يَّتَوَلَّهُمْ مِّنْكُمْ فَاُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ', 'yā ayyuhallażīna āmanụ lā tattakhiżū ābā`akum wa ikhwānakum auliyā`a inistaḥabbul-kufra \'alal-īmān, wa may yatawallahum mingkum fa ulā`ika humuẓ-ẓālimụn'),
(1258, 9, 24, 'قُلْ اِنْ كَانَ اٰبَاۤؤُكُمْ وَاَبْنَاۤؤُكُمْ وَاِخْوَانُكُمْ وَاَزْوَاجُكُمْ وَعَشِيْرَتُكُمْ وَاَمْوَالُ ِۨاقْتَرَفْتُمُوْهَا وَتِجَارَةٌ تَخْشَوْنَ كَسَادَهَا وَمَسٰكِنُ تَرْضَوْنَهَآ اَحَبَّ اِلَيْكُمْ مِّنَ اللّٰهِ وَرَسُوْلِهٖ وَجِهَادٍ فِيْ سَبِيْلِهٖ فَتَرَبَّصُوْا حَتّٰى يَأْتِيَ اللّٰهُ بِاَمْرِهٖۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الْفٰسِقِيْنَ', 'qul ing kāna ābā`ukum wa abnā`ukum wa ikhwānukum wa azwājukum wa \'asyīratukum wa amwāluniqtaraftumụhā wa tijāratun takhsyauna kasādahā wa masākinu tarḍaunahā aḥabba ilaikum minallāhi wa rasụlihī wa jihādin fī sabīlihī fa tarabbaṣụ ḥattā ya`tiyallāhu bi`amrih, wallāhu lā yahdil-qaumal-fāsiqīn'),
(1259, 9, 25, 'لَقَدْ نَصَرَكُمُ اللّٰهُ فِيْ مَوَاطِنَ كَثِيْرَةٍۙ وَّيَوْمَ حُنَيْنٍۙ اِذْ اَعْجَبَتْكُمْ كَثْرَتُكُمْ فَلَمْ تُغْنِ عَنْكُمْ شَيْـًٔا وَّضَاقَتْ عَلَيْكُمُ الْاَرْضُ بِمَا رَحُبَتْ ثُمَّ وَلَّيْتُمْ مُّدْبِرِيْنَۚ', 'laqad naṣarakumullāhu fī mawāṭina kaṡīratiw wa yauma ḥunainin iż a\'jabatkum kaṡratukum fa lam tugni \'angkum syai`aw wa ḍāqat \'alaikumul-arḍu bimā raḥubat ṡumma wallaitum mudbirīn'),
(1260, 9, 26, 'ثُمَّ اَنْزَلَ اللّٰهُ سَكِيْنَتَهٗ عَلٰى رَسُوْلِهٖ وَعَلَى الْمُؤْمِنِيْنَ وَاَنْزَلَ جُنُوْدًا لَّمْ تَرَوْهَا وَعَذَّبَ الَّذِيْنَ كَفَرُوْاۗ وَذٰلِكَ جَزَاۤءُ الْكٰفِرِيْنَ', 'ṡumma anzalallāhu sakīnatahụ \'alā rasụlihī wa \'alal-mu`minīna wa anzala junụdal lam tarauhā wa \'ażżaballażīna kafarụ, wa żālika jazā`ul-kāfirīn'),
(1261, 9, 27, 'ثُمَّ يَتُوْبُ اللّٰهُ مِنْۢ بَعْدِ ذٰلِكَ عَلٰى مَنْ يَّشَاۤءُۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ', 'ṡumma yatụbullāhu mim ba\'di żālika \'alā may yasyā`, wallāhu gafụrur raḥīm'),
(1262, 9, 28, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنَّمَا الْمُشْرِكُوْنَ نَجَسٌ فَلَا يَقْرَبُوا الْمَسْجِدَ الْحَرَامَ بَعْدَ عَامِهِمْ هٰذَا ۚوَاِنْ خِفْتُمْ عَيْلَةً فَسَوْفَ يُغْنِيْكُمُ اللّٰهُ مِنْ فَضْلِهٖٓ اِنْ شَاۤءَۗ اِنَّ اللّٰهَ عَلِيْمٌ حَكِيْمٌ', 'yā ayyuhallażīna āmanū innamal-musyrikụna najasun fa lā yaqrabul-masjidal-ḥarāma ba\'da \'āmihim hāżā, wa in khiftum \'ailatan fa saufa yugnīkumullāhu min faḍlihī in syā`, innallāha \'alīmun ḥakīm'),
(1263, 9, 29, 'قَاتِلُوا الَّذِيْنَ لَا يُؤْمِنُوْنَ بِاللّٰهِ وَلَا بِالْيَوْمِ الْاٰخِرِ وَلَا يُحَرِّمُوْنَ مَا حَرَّمَ اللّٰهُ وَرَسُوْلُهٗ وَلَا يَدِيْنُوْنَ دِيْنَ الْحَقِّ مِنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ حَتّٰى يُعْطُوا الْجِزْيَةَ عَنْ يَّدٍ وَّهُمْ صَاغِرُوْنَ', 'qātilullażīna lā yu`minụna billāhi wa lā bil-yaumil-ākhiri wa lā yuḥarrimụna mā ḥarramallāhu wa rasụluhụ wa lā yadīnụna dīnal-ḥaqqi minallażīna ụtul-kitāba ḥattā yu\'ṭul-jizyata \'ay yadiw wa hum ṣāgirụn'),
(1264, 9, 30, 'وَقَالَتِ الْيَهُوْدُ عُزَيْرُ ِۨابْنُ اللّٰهِ وَقَالَتِ النَّصٰرَى الْمَسِيْحُ ابْنُ اللّٰهِ ۗذٰلِكَ قَوْلُهُمْ بِاَفْوَاهِهِمْۚ يُضَاهِـُٔوْنَ قَوْلَ الَّذِيْنَ كَفَرُوْا مِنْ قَبْلُ ۗقَاتَلَهُمُ اللّٰهُ ۚ اَنّٰى يُؤْفَكُوْنَ', 'wa qālatil-yahụdu \'uzairunibnullāhi wa qālatin-naṣāral-masīḥubnullāh, żālika qauluhum bi`afwāhihim, yuḍāhi`ụna qaulallażīna kafarụ ming qabl, qātalahumullāh, annā yu`fakụn'),
(1265, 9, 31, 'اِتَّخَذُوْٓا اَحْبَارَهُمْ وَرُهْبَانَهُمْ اَرْبَابًا مِّنْ دُوْنِ اللّٰهِ وَالْمَسِيْحَ ابْنَ مَرْيَمَۚ وَمَآ اُمِرُوْٓا اِلَّا لِيَعْبُدُوْٓا اِلٰهًا وَّاحِدًاۚ لَآ اِلٰهَ اِلَّا هُوَۗ سُبْحٰنَهٗ عَمَّا يُشْرِكُوْنَ', 'ittakhażū aḥbārahum wa ruhbānahum arbābam min dụnillāhi wal-masīḥabna maryam, wa mā umirū illā liya\'budū ilāhaw wāḥidā, lā ilāha illā huw, sub-ḥānahụ \'ammā yusyrikụn'),
(1266, 9, 32, 'يُرِيْدُوْنَ اَنْ يُّطْفِـُٔوْا نُوْرَ اللّٰهِ بِاَفْوَاهِهِمْ وَيَأْبَى اللّٰهُ اِلَّآ اَنْ يُّتِمَّ نُوْرَهٗ وَلَوْ كَرِهَ الْكٰفِرُوْنَ', 'yurīdụna ay yuṭfi`ụ nụrallāhi bi`afwāhihim wa ya`ballāhu illā ay yutimma nụrahụ walau karihal-kāfirụn'),
(1267, 9, 33, 'هُوَ الَّذِيْٓ اَرْسَلَ رَسُوْلَهٗ بِالْهُدٰى وَدِيْنِ الْحَقِّ لِيُظْهِرَهٗ عَلَى الدِّيْنِ كُلِّهٖۙ وَلَوْ كَرِهَ الْمُشْرِكُوْنَ', 'huwallażī arsala rasụlahụ bil-hudā wa dīnil-ḥaqqi liyuẓ-hirahụ \'alad-dīni kullihī walau karihal-musyrikụn'),
(1268, 9, 34, '۞ يٰٓاَيُّهَا الَّذِينَ اٰمَنُوْٓا اِنَّ كَثِيْرًا مِّنَ الْاَحْبَارِ وَالرُّهْبَانِ لَيَأْكُلُوْنَ اَمْوَالَ النَّاسِ بِالْبَاطِلِ وَيَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ ۗوَالَّذِيْنَ يَكْنِزُوْنَ الذَّهَبَ وَالْفِضَّةَ وَلَا يُنْفِقُوْنَهَا فِيْ سَبِيْلِ اللّٰهِ ۙفَبَشِّرْهُمْ بِعَذَابٍ اَلِيْمٍۙ', 'yā ayyuhallażīna āmanū inna kaṡīram minal-aḥbāri war-ruhbāni laya`kulụna amwālan-nāsi bil-bāṭili wa yaṣuddụna \'an sabīlillāh, wallażīna yaknizụnaż-żahaba wal-fiḍḍata wa lā yunfiqụnahā fī sabīlillāhi fa basysyir-hum bi\'ażābin alīm'),
(1269, 9, 35, 'يَّوْمَ يُحْمٰى عَلَيْهَا فِيْ نَارِ جَهَنَّمَ فَتُكْوٰى بِهَا جِبَاهُهُمْ وَجُنُوْبُهُمْ وَظُهُوْرُهُمْۗ هٰذَا مَا كَنَزْتُمْ لِاَنْفُسِكُمْ فَذُوْقُوْا مَا كُنْتُمْ تَكْنِزُوْنَ', 'yauma yuḥmā \'alaihā fī nāri jahannama fa tukwā bihā jibāhuhum wa junụbuhum wa ẓuhụruhum, hāżā mā kanaztum li`anfusikum fa żụqụ mā kuntum taknizụn'),
(1270, 9, 36, 'اِنَّ عِدَّةَ الشُّهُوْرِ عِنْدَ اللّٰهِ اثْنَا عَشَرَ شَهْرًا فِيْ كِتٰبِ اللّٰهِ يَوْمَ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ مِنْهَآ اَرْبَعَةٌ حُرُمٌ ۗذٰلِكَ الدِّيْنُ الْقَيِّمُ ەۙ فَلَا تَظْلِمُوْا فِيْهِنَّ اَنْفُسَكُمْ وَقَاتِلُوا الْمُشْرِكِيْنَ كَاۤفَّةً كَمَا يُقَاتِلُوْنَكُمْ كَاۤفَّةً ۗوَاعْلَمُوْٓا اَنَّ اللّٰهَ مَعَ الْمُتَّقِيْنَ', 'inna \'iddatasy-syuhụri \'indallāhiṡnā \'asyara syahran fī kitābillāhi yauma khalaqas-samāwāti wal-arḍa min-hā arba\'atun ḥurum, żālikad-dīnul-qayyimu fa lā taẓlimụ fīhinna anfusakum wa qātilul-musyrikīna kāffatang kamā yuqātilụnakum kāffah, wa\'lamū annallāha ma\'al-muttaqīn'),
(1271, 9, 37, 'اِنَّمَا النَّسِيْۤءُ زِيَادَةٌ فِى الْكُفْرِ يُضَلُّ بِهِ الَّذِيْنَ كَفَرُوْا يُحِلُّوْنَهٗ عَامًا وَّيُحَرِّمُوْنَهٗ عَامًا لِّيُوَاطِـُٔوْا عِدَّةَ مَا حَرَّمَ اللّٰهُ فَيُحِلُّوْا مَا حَرَّمَ اللّٰهُ ۗزُيِّنَ لَهُمْ سُوْۤءُ اَعْمَالِهِمْۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الْكٰفِرِيْنَ', 'innaman-nasī`u ziyādatun fil-kufri yuḍallu bihillażīna kafarụ yuḥillụnahụ \'āmaw wa yuḥarrimụnahụ \'āmal liyuwāṭi`ụ \'iddata mā ḥarramallāhu fa yuḥillụ mā ḥarramallāh, zuyyina lahum sū`u a\'mālihim, wallāhu lā yahdil-qaumal-kāfirīn'),
(1272, 9, 38, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا مَا لَكُمْ اِذَا قِيْلَ لَكُمُ انْفِرُوْا فِيْ سَبِيْلِ اللّٰهِ اثَّاقَلْتُمْ اِلَى الْاَرْضِۗ اَرَضِيْتُمْ بِالْحَيٰوةِ الدُّنْيَا مِنَ الْاٰخِرَةِۚ فَمَا مَتَاعُ الْحَيٰوةِ الدُّنْيَا فِى الْاٰخِرَةِ اِلَّا قَلِيْلٌ', 'yā ayyuhallażīna āmanụ mā lakum iżā qīla lakumunfirụ fī sabīlillāhiṡ ṡāqaltum ilal-arḍ, a raḍītum bil-ḥayātid-dun-yā minal-ākhirah, fa mā matā\'ul-ḥayātid-dun-yā fil-ākhirati illā qalīl'),
(1273, 9, 39, 'اِلَّا تَنْفِرُوْا يُعَذِّبْكُمْ عَذَابًا اَلِيمًاۙ وَّيَسْتَبْدِلْ قَوْمًا غَيْرَكُمْ وَلَا تَضُرُّوْهُ شَيْـًٔاۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'illā tanfirụ yu\'ażżibkum \'ażāban alīmaw wa yastabdil qauman gairakum wa lā taḍurrụhu syai`ā, wallāhu \'alā kulli syai`ing qadīr'),
(1274, 9, 40, 'اِلَّا تَنْصُرُوْهُ فَقَدْ نَصَرَهُ اللّٰهُ اِذْ اَخْرَجَهُ الَّذِيْنَ كَفَرُوْا ثَانِيَ اثْنَيْنِ اِذْ هُمَا فِى الْغَارِ اِذْ يَقُوْلُ لِصَاحِبِهٖ لَا تَحْزَنْ اِنَّ اللّٰهَ مَعَنَاۚ فَاَنْزَلَ اللّٰهُ سَكِيْنَتَهٗ عَلَيْهِ وَاَيَّدَهٗ بِجُنُوْدٍ لَّمْ تَرَوْهَا وَجَعَلَ كَلِمَةَ الَّذِيْنَ كَفَرُوا السُّفْلٰىۗ وَكَلِمَةُ اللّٰهِ هِيَ الْعُلْيَاۗ وَاللّٰهُ عَزِيْزٌ حَكِيْمٌ', 'illā tanṣurụhu fa qad naṣarahullāhu iż akhrajahullażīna kafarụ ṡāniyaṡnaini iż humā fil-gāri iż yaqụlu liṣāḥibihī lā taḥzan innallāha ma\'anā, fa anzalallāhu sakīnatahụ \'alaihi wa ayyadahụ bijunụdil lam tarauhā wa ja\'ala kalimatallażīna kafarus-suflā, wa kalimatullāhi hiyal-\'ulyā, wallāhu \'azīzun ḥakīm'),
(1275, 9, 41, 'اِنْفِرُوْا خِفَافًا وَّثِقَالًا وَّجَاهِدُوْا بِاَمْوَالِكُمْ وَاَنْفُسِكُمْ فِيْ سَبِيْلِ اللّٰهِ ۗذٰلِكُمْ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ', 'infirụ khifāfaw wa ṡiqālaw wa jāhidụ bi`amwālikum wa anfusikum fī sabīlillāh, żālikum khairul lakum ing kuntum ta\'lamụn'),
(1276, 9, 42, 'لَوْ كَانَ عَرَضًا قَرِيْبًا وَّسَفَرًا قَاصِدًا لَّاتَّبَعُوْكَ وَلٰكِنْۢ بَعُدَتْ عَلَيْهِمُ الشُّقَّةُۗ وَسَيَحْلِفُوْنَ بِاللّٰهِ لَوِ اسْتَطَعْنَا لَخَرَجْنَا مَعَكُمْۚ يُهْلِكُوْنَ اَنْفُسَهُمْۚ وَاللّٰهُ يَعْلَمُ اِنَّهُمْ لَكٰذِبُوْنَ', 'lau kāna \'araḍang qarībaw wa safarang qāṣidal lattaba\'ụka wa lākim ba\'udat \'alaihimusy-syuqqah, wa sayaḥlifụna billāhi lawistaṭa\'nā lakharajnā ma\'akum, yuhlikụna anfusahum, wallāhu ya\'lamu innahum lakāżibụn'),
(1277, 9, 43, 'عَفَا اللّٰهُ عَنْكَۚ لِمَ اَذِنْتَ لَهُمْ حَتّٰى يَتَبَيَّنَ لَكَ الَّذِيْنَ صَدَقُوْا وَتَعْلَمَ الْكٰذِبِيْنَ', '\'afallāhu \'angk, lima ażinta lahum ḥattā yatabayyana lakallażīna ṣadaqụ wa ta\'lamal-kāżibīn'),
(1278, 9, 44, 'لَا يَسْتَأْذِنُكَ الَّذِيْنَ يُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ اَنْ يُّجَاهِدُوْا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۗ وَاللّٰهُ عَلِيْمٌۢ بِالْمُتَّقِيْنَ', 'lā yasta`żinukallażīna yu`minụna billāhi wal-yaumil-ākhiri ay yujāhidụ bi`amwālihim wa anfusihim, wallāhu \'alīmum bil-muttaqīn'),
(1279, 9, 45, 'اِنَّمَا يَسْتَأْذِنُكَ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَارْتَابَتْ قُلُوْبُهُمْ فَهُمْ فِيْ رَيْبِهِمْ يَتَرَدَّدُوْنَ', 'innamā yasta`żinukallażīna lā yu`minụna billāhi wal-yaumil-ākhiri wartābat qulụbuhum fa hum fī raibihim yataraddadụn'),
(1280, 9, 46, '۞ وَلَوْ اَرَادُوا الْخُرُوْجَ لَاَعَدُّوْا لَهٗ عُدَّةً وَّلٰكِنْ كَرِهَ اللّٰهُ انْۢبِعَاثَهُمْ فَثَبَّطَهُمْ وَقِيْلَ اقْعُدُوْا مَعَ الْقٰعِدِيْنَ', 'walau arādul-khurụja la`a\'addụ lahụ \'uddataw wa lāking karihallāhumbi\'āṡahum fa ṡabbaṭahum wa qīlaq\'udụ ma\'al-qā\'idīn'),
(1281, 9, 47, 'لَوْ خَرَجُوْا فِيْكُمْ مَّا زَادُوْكُمْ اِلَّا خَبَالًا وَّلَاَوْضَعُوْا خِلٰلَكُمْ يَبْغُوْنَكُمُ الْفِتْنَةَۚ وَفِيْكُمْ سَمّٰعُوْنَ لَهُمْۗ وَاللّٰهُ عَلِيْمٌۢ بِالظّٰلِمِيْنَ', 'lau kharajụ fīkum mā zādụkum illā khabālaw wa la`auḍa\'ụ khilālakum yabgụnakumul-fitnah, wa fīkum sammā\'ụna lahum, wallāhu \'alīmum biẓ-ẓālimīn'),
(1282, 9, 48, 'لَقَدِ ابْتَغَوُا الْفِتْنَةَ مِنْ قَبْلُ وَقَلَّبُوْا لَكَ الْاُمُوْرَ حَتّٰى جَاۤءَ الْحَقُّ وَظَهَرَ اَمْرُ اللّٰهِ وَهُمْ كٰرِهُوْنَ', 'laqadibtagawul-fitnata ming qablu wa qallabụ lakal-umụra ḥattā jā`al-ḥaqqu wa ẓahara amrullāhi wa hum kārihụn'),
(1283, 9, 49, 'وَمِنْهُمْ مَّنْ يَّقُوْلُ ائْذَنْ لِّيْ وَلَا تَفْتِنِّيْۗ اَلَا فِى الْفِتْنَةِ سَقَطُوْاۗ وَاِنَّ جَهَنَّمَ لَمُحِيْطَةٌ ۢ بِالْكٰفِرِيْنَ', 'wa min-hum may yaqụlu`żal lī wa lā taftinnī, alā fil-fitnati saqaṭụ, wa inna jahannama lamuḥīṭatum bil-kāfirīn'),
(1284, 9, 50, 'اِنْ تُصِبْكَ حَسَنَةٌ تَسُؤْهُمْۚ وَاِنْ تُصِبْكَ مُصِيْبَةٌ يَّقُوْلُوْا قَدْ اَخَذْنَآ اَمْرَنَا مِنْ قَبْلُ وَيَتَوَلَّوْا وَّهُمْ فَرِحُوْنَ', 'in tuṣibka ḥasanatun tasu`hum, wa in tuṣibka muṣībatuy yaqụlụ qad akhażnā amranā ming qablu wa yatawallaw wa hum fariḥụn'),
(1285, 9, 51, 'قُلْ لَّنْ يُّصِيْبَنَآ اِلَّا مَا كَتَبَ اللّٰهُ لَنَاۚ هُوَ مَوْلٰىنَا وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ', 'qul lay yuṣībanā illā mā kataballāhu lanā, huwa maulānā wa \'alallāhi falyatawakkalil-mu`minụn'),
(1286, 9, 52, 'قُلْ هَلْ تَرَبَّصُوْنَ بِنَآ اِلَّآ اِحْدَى الْحُسْنَيَيْنِۗ وَنَحْنُ نَتَرَبَّصُ بِكُمْ اَنْ يُّصِيْبَكُمُ اللّٰهُ بِعَذَابٍ مِّنْ عِنْدِهٖٓ اَوْ بِاَيْدِيْنَاۖ فَتَرَبَّصُوْٓا اِنَّا مَعَكُمْ مُّتَرَبِّصُوْنَ', 'qul hal tarabbaṣụna binā illā iḥdal-ḥusnayaīn, wa naḥnu natarabbaṣu bikum ay yuṣībakumullāhu bi\'ażābim min \'indihī au bi`aidīnā fa tarabbaṣū innā ma\'akum mutarabbiṣụn'),
(1287, 9, 53, 'قُلْ اَنْفِقُوْا طَوْعًا اَوْ كَرْهًا لَّنْ يُّتَقَبَّلَ مِنْكُمْ ۗاِنَّكُمْ كُنْتُمْ قَوْمًا فٰسِقِيْنَ', 'qul anfiqụ ṭau\'an au kar-hal lay yutaqabbala mingkum, innakum kuntum qauman fāsiqīn'),
(1288, 9, 54, 'وَمَا مَنَعَهُمْ اَنْ تُقْبَلَ مِنْهُمْ نَفَقٰتُهُمْ اِلَّآ اَنَّهُمْ كَفَرُوْا بِاللّٰهِ وَبِرَسُوْلِهٖ وَلَا يَأْتُوْنَ الصَّلٰوةَ اِلَّا وَهُمْ كُسَالٰى وَلَا يُنْفِقُوْنَ اِلَّا وَهُمْ كٰرِهُوْنَ', 'wa mā mana\'ahum an tuqbala min-hum nafaqātuhum illā annahum kafarụ billāhi wa birasụlihī wa lā ya`tụnaṣ-ṣalāta illā wa hum kusālā wa lā yunfiqụna illā wa hum kārihụn'),
(1289, 9, 55, 'فَلَا تُعْجِبْكَ اَمْوَالُهُمْ وَلَآ اَوْلَادُهُمْ ۗاِنَّمَا يُرِيْدُ اللّٰهُ لِيُعَذِّبَهُمْ بِهَا فِى الْحَيٰوةِ الدُّنْيَا وَتَزْهَقَ اَنْفُسُهُمْ وَهُمْ كٰفِرُوْنَ', 'fa lā tu\'jibka amwāluhum wa lā aulāduhum, innamā yurīdullāhu liyu\'ażżibahum bihā fil-ḥayātid-dun-yā wa taz-haqa anfusuhum wa hum kāfirụn'),
(1290, 9, 56, 'وَيَحْلِفُوْنَ بِاللّٰهِ اِنَّهُمْ لَمِنْكُمْۗ وَمَا هُمْ مِّنْكُمْ وَلٰكِنَّهُمْ قَوْمٌ يَّفْرَقُوْنَ', 'wa yaḥlifụna billāhi innahum lamingkum, wa mā hum mingkum wa lākinnahum qaumuy yafraqụn'),
(1291, 9, 57, 'لَوْ يَجِدُوْنَ مَلْجَاً اَوْ مَغٰرٰتٍ اَوْ مُدَّخَلًا لَّوَلَّوْا اِلَيْهِ وَهُمْ يَجْمَحُوْنَ', 'lau yajidụna malja`an au magārātin au muddakhalal lawallau ilaihi wa hum yajmaḥụn'),
(1292, 9, 58, 'وَمِنْهُمْ مَّنْ يَّلْمِزُكَ فِى الصَّدَقٰتِۚ فَاِنْ اُعْطُوْا مِنْهَا رَضُوْا وَاِنْ لَّمْ يُعْطَوْا مِنْهَآ اِذَا هُمْ يَسْخَطُوْنَ', 'wa min-hum may yalmizuka fiṣ-ṣadaqāt, fa in u\'ṭụ min-hā raḍụ wa il lam yu\'ṭau min-hā iżā hum yaskhaṭụn'),
(1293, 9, 59, 'وَلَوْ اَنَّهُمْ رَضُوْا مَآ اٰتٰىهُمُ اللّٰهُ وَرَسُوْلُهٗۙ وَقَالُوْا حَسْبُنَا اللّٰهُ سَيُؤْتِيْنَا اللّٰهُ مِنْ فَضْلِهٖ وَرَسُوْلُهٗٓ اِنَّآ اِلَى اللّٰهِ رَاغِبُوْنَ', 'walau annahum raḍụ mā ātāhumullāhu wa rasụluhụ wa qālụ ḥasbunallāhu sayu`tīnallāhu min faḍlihī wa rasụluhụ innā ilallāhi rāgibụn'),
(1294, 9, 60, '۞ اِنَّمَا الصَّدَقٰتُ لِلْفُقَرَاۤءِ وَالْمَسٰكِيْنِ وَالْعَامِلِيْنَ عَلَيْهَا وَالْمُؤَلَّفَةِ قُلُوْبُهُمْ وَفِى الرِّقَابِ وَالْغَارِمِيْنَ وَفِيْ سَبِيْلِ اللّٰهِ وَابْنِ السَّبِيْلِۗ فَرِيْضَةً مِّنَ اللّٰهِ ۗوَاللّٰهُ عَلِيْمٌ حَكِيْمٌ', 'innamaṣ-ṣadaqātu lil-fuqarā`i wal-masākīni wal-\'āmilīna \'alaihā wal-mu`allafati qulụbuhum wa fir-riqābi wal-gārimīna wa fī sabīlillāhi wabnis-sabīl, farīḍatam minallāh, wallāhu \'alīmun ḥakīm');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(1295, 9, 61, 'وَمِنْهُمُ الَّذِيْنَ يُؤْذُوْنَ النَّبِيَّ وَيَقُوْلُوْنَ هُوَ اُذُنٌ ۗقُلْ اُذُنُ خَيْرٍ لَّكُمْ يُؤْمِنُ بِاللّٰهِ وَيُؤْمِنُ لِلْمُؤْمِنِيْنَ وَرَحْمَةٌ لِّلَّذِيْنَ اٰمَنُوْا مِنْكُمْۗ وَالَّذِيْنَ يُؤْذُوْنَ رَسُوْلَ اللّٰهِ لَهُمْ عَذَابٌ اَلِيْمٌ', 'wa min-humullażīna yu`żụnan-nabiyya wa yaqụlụna huwa użun, qul użunu khairil lakum yu`minu billāhi wa yu`minu lil-mu`minīna wa raḥmatul lillażīna āmanụ mingkum, wallażīna yu`żụna rasụlallāhi lahum \'ażābun alīm'),
(1296, 9, 62, 'يَحْلِفُوْنَ بِاللّٰهِ لَكُمْ لِيُرْضُوْكُمْ وَاللّٰهُ وَرَسُوْلُهٗٓ اَحَقُّ اَنْ يُّرْضُوْهُ اِنْ كَانُوْا مُؤْمِنِيْنَ', 'yaḥlifụna billāhi lakum liyurḍụkum wallāhu wa rasụluhū aḥaqqu ay yurḍụhu ing kānụ mu`minīn'),
(1297, 9, 63, 'اَلَمْ يَعْلَمُوْٓا اَنَّهٗ مَنْ يُّحَادِدِ اللّٰهَ وَرَسُوْلَهٗ فَاَنَّ لَهٗ نَارَ جَهَنَّمَ خَالِدًا فِيْهَاۗ ذٰلِكَ الْخِزْيُ الْعَظِيْمُ', 'a lam ya\'lamū annahụ may yuḥādidillāha wa rasụlahụ fa anna lahụ nāra jahannama khālidan fīhā, żālikal-khizyul-\'aẓīm'),
(1298, 9, 64, 'يَحْذَرُ الْمُنٰفِقُوْنَ اَنْ تُنَزَّلَ عَلَيْهِمْ سُوْرَةٌ تُنَبِّئُهُمْ بِمَا فِيْ قُلُوْبِهِمْۗ قُلِ اسْتَهْزِءُوْاۚ اِنَّ اللّٰهَ مُخْرِجٌ مَّا تَحْذَرُوْنَ', 'yaḥżarul-munāfiqụna an tunazzala \'alaihim sụratun tunabbi`uhum bimā fī qulụbihim, qulistahzi`ụ, innallāha mukhrijum mā taḥżarụn'),
(1299, 9, 65, 'وَلَىِٕنْ سَاَلْتَهُمْ لَيَقُوْلُنَّ اِنَّمَا كُنَّا نَخُوْضُ وَنَلْعَبُۗ قُلْ اَبِاللّٰهِ وَاٰيٰتِهٖ وَرَسُوْلِهٖ كُنْتُمْ تَسْتَهْزِءُوْنَ', 'wa la`in sa`altahum layaqụlunna innamā kunnā nakhụḍu wa nal\'ab, qul a billāhi wa āyātihī wa rasụlihī kuntum tastahzi`ụn'),
(1300, 9, 66, 'لَا تَعْتَذِرُوْا قَدْ كَفَرْتُمْ بَعْدَ اِيْمَانِكُمْ ۗ اِنْ نَّعْفُ عَنْ طَاۤىِٕفَةٍ مِّنْكُمْ نُعَذِّبْ طَاۤىِٕفَةً ۢ بِاَنَّهُمْ كَانُوْا مُجْرِمِيْنَ', 'lā ta\'tażirụ qad kafartum ba\'da īmānikum, in na\'fu \'an ṭā`ifatim mingkum nu\'ażżib ṭā`ifatam bi`annahum kānụ mujrimīn'),
(1301, 9, 67, 'اَلْمُنٰفِقُوْنَ وَالْمُنٰفِقٰتُ بَعْضُهُمْ مِّنْۢ بَعْضٍۘ يَأْمُرُوْنَ بِالْمُنْكَرِ وَيَنْهَوْنَ عَنِ الْمَعْرُوْفِ وَيَقْبِضُوْنَ اَيْدِيَهُمْۗ نَسُوا اللّٰهَ فَنَسِيَهُمْ ۗ اِنَّ الْمُنٰفِقِيْنَ هُمُ الْفٰسِقُوْنَ', 'al-munāfiqụna wal-munāfiqātu ba\'ḍuhum mim ba\'ḍ, ya`murụna bil-mungkari wa yan-hauna \'anil-ma\'rụfi wa yaqbiḍụna aidiyahum, nasullāha fa nasiyahum, innal-munāfiqīna humul-fāsiqụn'),
(1302, 9, 68, 'وَعَدَ اللّٰهُ الْمُنٰفِقِيْنَ وَالْمُنٰفِقٰتِ وَالْكُفَّارَ نَارَ جَهَنَّمَ خٰلِدِيْنَ فِيْهَاۗ هِيَ حَسْبُهُمْ ۚوَلَعَنَهُمُ اللّٰهُ ۚوَلَهُمْ عَذَابٌ مُّقِيْمٌۙ', 'wa\'adallāhul-munāfiqīna wal-munāfiqāti wal-kuffāra nāra jahannama khālidīna fīhā, hiya ḥasbuhum, wa la\'anahumullāh, wa lahum \'ażābum muqīm'),
(1303, 9, 69, 'كَالَّذِيْنَ مِنْ قَبْلِكُمْ كَانُوْٓا اَشَدَّ مِنْكُمْ قُوَّةً وَّاَكْثَرَ اَمْوَالًا وَّاَوْلَادًاۗ فَاسْتَمْتَعُوْا بِخَلَاقِهِمْ فَاسْتَمْتَعْتُمْ بِخَلَاقِكُمْ كَمَا اسْتَمْتَعَ الَّذِيْنَ مِنْ قَبْلِكُمْ بِخَلَاقِهِمْ وَخُضْتُمْ كَالَّذِيْ خَاضُوْاۗ اُولٰۤىِٕكَ حَبِطَتْ اَعْمَالُهُمْ فِى الدُّنْيَا وَالْاٰخِرَةِ ۚوَاُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ', 'kallażīna ming qablikum kānū asyadda mingkum quwwataw wa akṡara amwālaw wa aulādā, fastamta\'ụ bikhalāqihim fastamta\'tum bikhalāqikum kamastamta\'allażīna ming qablikum bikhalāqihim wa khuḍtum kallażī khāḍụ, ulā`ika ḥabiṭat a\'māluhum fid-dun-yā wal-ākhirah, wa ulā`ika humul-khāsirụn'),
(1304, 9, 70, 'اَلَمْ يَأْتِهِمْ نَبَاُ الَّذِيْنَ مِنْ قَبْلِهِمْ قَوْمِ نُوْحٍ وَّعَادٍ وَّثَمُوْدَ ەۙ وَقَوْمِ اِبْرٰهِيْمَ وَاَصْحٰبِ مَدْيَنَ وَالْمُؤْتَفِكٰتِۗ اَتَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِۚ فَمَا كَانَ اللّٰهُ لِيَظْلِمَهُمْ وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَ', 'a lam ya`tihim naba`ullażīna ming qablihim qaumi nụḥiw wa \'ādiw wa ṡamụda wa qaumi ibrāhīma wa aṣ-ḥābi madyana wal-mu`tafikāt, atat-hum rusuluhum bil-bayyināt, fa mā kānallāhu liyaẓlimahum wa lāking kānū anfusahum yaẓlimụn'),
(1305, 9, 71, 'وَالْمُؤْمِنُوْنَ وَالْمُؤْمِنٰتُ بَعْضُهُمْ اَوْلِيَاۤءُ بَعْضٍۘ يَأْمُرُوْنَ بِالْمَعْرُوْفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ وَيُقِيْمُوْنَ الصَّلٰوةَ وَيُؤْتُوْنَ الزَّكٰوةَ وَيُطِيْعُوْنَ اللّٰهَ وَرَسُوْلَهٗ ۗاُولٰۤىِٕكَ سَيَرْحَمُهُمُ اللّٰهُ ۗاِنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ', 'wal-mu`minụna wal-mu`minātu ba\'ḍuhum auliyā`u ba\'ḍ, ya`murụna bil-ma\'rụfi wa yan-hauna \'anil-mungkari wa yuqīmụnaṣ-ṣalāta wa yu`tụnaz-zakāta wa yuṭī\'ụnallāha wa rasụlah, ulā`ika sayar-ḥamuhumullāh, innallāha \'azīzun ḥakīm'),
(1306, 9, 72, 'وَعَدَ اللّٰهُ الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا وَمَسٰكِنَ طَيِّبَةً فِيْ جَنّٰتِ عَدْنٍ ۗوَرِضْوَانٌ مِّنَ اللّٰهِ اَكْبَرُ ۗذٰلِكَ هُوَ الْفَوْزُ الْعَظِيْمُ', 'wa\'adallāhul-mu`minīna wal-mu`mināti jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā wa masākina ṭayyibatan fī jannāti \'adn, wa riḍwānum minallāhi akbar, żālika huwal-fauzul-\'aẓīm'),
(1307, 9, 73, 'يٰٓاَيُّهَا النَّبِيُّ جَاهِدِ الْكُفَّارَ وَالْمُنٰفِقِيْنَ وَاغْلُظْ عَلَيْهِمْ ۗوَمَأْوٰىهُمْ جَهَنَّمُ وَبِئْسَ الْمَصِيْرُ', 'yā ayyuhan-nabiyyu jāhidil-kuffāra wal-munāfiqīna wagluẓ \'alaihim, wa ma`wāhum jahannamu wa bi`sal-maṣīr'),
(1308, 9, 74, 'يَحْلِفُوْنَ بِاللّٰهِ مَا قَالُوْا ۗوَلَقَدْ قَالُوْا كَلِمَةَ الْكُفْرِ وَكَفَرُوْا بَعْدَ اِسْلَامِهِمْ وَهَمُّوْا بِمَا لَمْ يَنَالُوْاۚ وَمَا نَقَمُوْٓا اِلَّآ اَنْ اَغْنٰىهُمُ اللّٰهُ وَرَسُوْلُهٗ مِنْ فَضْلِهٖ ۚفَاِنْ يَّتُوْبُوْا يَكُ خَيْرًا لَّهُمْ ۚوَاِنْ يَّتَوَلَّوْا يُعَذِّبْهُمُ اللّٰهُ عَذَابًا اَلِيْمًا فِى الدُّنْيَا وَالْاٰخِرَةِ ۚوَمَا لَهُمْ فِى الْاَرْضِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ', 'yaḥlifụna billāhi mā qālụ, wa laqad qālụ kalimatal-kufri wa kafarụ ba\'da islāmihim wa hammụ bimā lam yanalụ, wa mā naqamū illā an agnāhumullāhu wa rasụluhụ min faḍlih, fa iy yatụbụ yaku khairal lahum, wa iy yatawallau yu\'ażżib-humullāhu \'ażāban alīman fid-dun-yā wal-ākhirah, wa mā lahum fil-arḍi miw waliyyiw wa lā naṣīr'),
(1309, 9, 75, '۞ وَمِنْهُمْ مَّنْ عٰهَدَ اللّٰهَ لَىِٕنْ اٰتٰىنَا مِنْ فَضْلِهٖ لَنَصَّدَّقَنَّ وَلَنَكُوْنَنَّ مِنَ الصّٰلِحِيْنَ', 'wa min-hum man \'āhadallāha la`in ātānā min faḍlihī lanaṣṣaddaqanna wa lanakụnanna minaṣ-ṣāliḥīn'),
(1310, 9, 76, 'فَلَمَّآ اٰتٰىهُمْ مِّنْ فَضْلِهٖ بَخِلُوْا بِهٖ وَتَوَلَّوْا وَّهُمْ مُّعْرِضُوْنَ', 'fa lammā ātāhum min faḍlihī bakhilụ bihī wa tawallaw wa hum mu\'riḍụn'),
(1311, 9, 77, 'فَاَعْقَبَهُمْ نِفَاقًا فِيْ قُلُوْبِهِمْ اِلٰى يَوْمِ يَلْقَوْنَهٗ بِمَآ اَخْلَفُوا اللّٰهَ مَا وَعَدُوْهُ وَبِمَا كَانُوْا يَكْذِبُوْنَ', 'fa a\'qabahum nifāqan fī qulụbihim ilā yaumi yalqaunahụ bimā akhlafullāha mā wa\'adụhu wa bimā kānụ yakżibụn'),
(1312, 9, 78, 'اَلَمْ يَعْلَمُوْٓا اَنَّ اللّٰهَ يَعْلَمُ سِرَّهُمْ وَنَجْوٰىهُمْ وَاَنَّ اللّٰهَ عَلَّامُ الْغُيُوْبِ', 'a lam ya\'lamū annallāha ya\'lamu sirrahum wa najwāhum wa annallāha \'allāmul-guyụb'),
(1313, 9, 79, 'اَلَّذِيْنَ يَلْمِزُوْنَ الْمُطَّوِّعِيْنَ مِنَ الْمُؤْمِنِيْنَ فِى الصَّدَقٰتِ وَالَّذِيْنَ لَا يَجِدُوْنَ اِلَّا جُهْدَهُمْ فَيَسْخَرُوْنَ مِنْهُمْ ۗسَخِرَ اللّٰهُ مِنْهُمْ ۖ وَلَهُمْ عَذَابٌ اَلِيْمٌ', 'allażīna yalmizụnal-muṭṭawwi\'īna minal-mu`minīna fiṣ-ṣadaqāti wallażīna lā yajidụna illā juhdahum fa yaskharụna min-hum, sakhirallāhu min-hum wa lahum \'ażābun alīm'),
(1314, 9, 80, 'اِسْتَغْفِرْ لَهُمْ اَوْ لَا تَسْتَغْفِرْ لَهُمْۗ اِنْ تَسْتَغْفِرْ لَهُمْ سَبْعِيْنَ مَرَّةً فَلَنْ يَّغْفِرَ اللّٰهُ لَهُمْ ۗذٰلِكَ بِاَنَّهُمْ كَفَرُوْا بِاللّٰهِ وَرَسُوْلِهٖۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الْفٰسِقِيْنَ', 'istagfir lahum au lā tastagfir lahum, in tastagfir lahum sab\'īna marratan fa lay yagfirallāhu lahum, żālika bi`annahum kafarụ billāhi wa rasụlih, wallāhu lā yahdil-qaumal-fāsiqīn'),
(1315, 9, 81, 'فَرِحَ الْمُخَلَّفُوْنَ بِمَقْعَدِهِمْ خِلٰفَ رَسُوْلِ اللّٰهِ وَكَرِهُوْٓا اَنْ يُّجَاهِدُوْا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ فِيْ سَبِيْلِ اللّٰهِ وَقَالُوْا لَا تَنْفِرُوْا فِى الْحَرِّۗ قُلْ نَارُ جَهَنَّمَ اَشَدُّ حَرًّاۗ لَوْ كَانُوْا يَفْقَهُوْنَ', 'fariḥal-mukhallafụna bimaq\'adihim khilāfa rasụlillāhi wa karihū ay yujāhidụ bi`amwālihim wa anfusihim fī sabīlillāhi wa qālụ lā tanfirụ fil-ḥarr, qul nāru jahannama asyaddu ḥarrā, lau kānụ yafqahụn'),
(1316, 9, 82, 'فَلْيَضْحَكُوْا قَلِيْلًا وَّلْيَبْكُوْا كَثِيْرًاۚ جَزَاۤءًۢ بِمَا كَانُوْا يَكْسِبُوْنَ', 'falyaḍ-ḥakụ qalīlaw walyabkụ kaṡīrā, jazā`am bimā kānụ yaksibụn'),
(1317, 9, 83, 'فَاِنْ رَّجَعَكَ اللّٰهُ اِلٰى طَاۤىِٕفَةٍ مِّنْهُمْ فَاسْتَأْذَنُوْكَ لِلْخُرُوْجِ فَقُلْ لَّنْ تَخْرُجُوْا مَعِيَ اَبَدًا وَّلَنْ تُقَاتِلُوْا مَعِيَ عَدُوًّاۗ اِنَّكُمْ رَضِيْتُمْ بِالْقُعُوْدِ اَوَّلَ مَرَّةٍۗ فَاقْعُدُوْا مَعَ الْخَالِفِيْنَ', 'fa ir raja\'akallāhu ilā ṭā`ifatim min-hum fasta`żanụka lil-khurụji fa qul lan takhrujụ ma\'iya abadaw wa lan tuqātilụ ma\'iya \'aduwwā, innakum raḍītum bil-qu\'ụdi awwala marrah, faq\'udụ ma\'al-khālifīn'),
(1318, 9, 84, 'وَلَا تُصَلِّ عَلٰٓى اَحَدٍ مِّنْهُمْ مَّاتَ اَبَدًا وَّلَا تَقُمْ عَلٰى قَبْرِهٖۗ اِنَّهُمْ كَفَرُوْا بِاللّٰهِ وَرَسُوْلِهٖ وَمَاتُوْا وَهُمْ فٰسِقُوْنَ', 'wa lā tuṣalli \'alā aḥadim min-hum māta abadaw wa lā taqum \'alā qabrih, innahum kafarụ billāhi wa rasụlihī wa mātụ wa hum fāsiqụn'),
(1319, 9, 85, 'وَلَا تُعْجِبْكَ اَمْوَالُهُمْ وَاَوْلَادُهُمْۗ اِنَّمَا يُرِيْدُ اللّٰهُ اَنْ يُّعَذِّبَهُمْ بِهَا فِى الدُّنْيَا وَتَزْهَقَ اَنْفُسُهُمْ وَهُمْ كٰفِرُوْنَ', 'wa lā tu\'jibka amwāluhum wa aulāduhum, innamā yurīdullāhu ay yu\'ażżibahum bihā fid-dun-yā wa taz-haqa anfusuhum wa hum kāfirụn'),
(1320, 9, 86, 'وَاِذَآ اُنْزِلَتْ سُوْرَةٌ اَنْ اٰمِنُوْا بِاللّٰهِ وَجَاهِدُوْا مَعَ رَسُوْلِهِ اسْتَأْذَنَكَ اُولُوا الطَّوْلِ مِنْهُمْ وَقَالُوْا ذَرْنَا نَكُنْ مَّعَ الْقٰعِدِيْنَ', 'wa iżā unzilat sụratun an āminụ billāhi wa jāhidụ ma\'a rasụlihista`żanaka uluṭ-ṭauli min-hum wa qālụ żarnā nakum ma\'al-qā\'idīn'),
(1321, 9, 87, 'رَضُوْا بِاَنْ يَّكُوْنُوْا مَعَ الْخَوَالِفِ وَطُبِعَ عَلٰى قُلُوْبِهِمْ فَهُمْ لَا يَفْقَهُوْنَ', 'raḍụ bi`ay yakụnụ ma\'al-khawālifi wa ṭubi\'a \'alā qulụbihim fa hum lā yafqahụn'),
(1322, 9, 88, 'لٰكِنِ الرَّسُوْلُ وَالَّذِيْنَ اٰمَنُوْا مَعَهٗ جَاهَدُوْا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۗ وَاُولٰۤىِٕكَ لَهُمُ الْخَيْرٰتُ ۖوَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ', 'lākinir-rasụlu wallażīna āmanụ ma\'ahụ jāhadụ bi`amwālihim wa anfusihim, wa ulā`ika lahumul-khairātu wa ulā`ika humul-mufliḥụn'),
(1323, 9, 89, 'اَعَدَّ اللّٰهُ لَهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَاۗ ذٰلِكَ الْفَوْزُ الْعَظِيْمُ', 'a\'addallāhu lahum jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā, żālikal-fauzul-\'aẓīm'),
(1324, 9, 90, 'وَجَاۤءَ الْمُعَذِّرُوْنَ مِنَ الْاَعْرَابِ لِيُؤْذَنَ لَهُمْ وَقَعَدَ الَّذِيْنَ كَذَبُوا اللّٰهَ وَرَسُوْلَهٗ ۗسَيُصِيْبُ الَّذِيْنَ كَفَرُوْا مِنْهُمْ عَذَابٌ اَلِيْمٌ', 'wa jā`al-mu\'ażżirụna minal-a\'rābi liyu`żana lahum wa qa\'adallażīna każabullāha wa rasụlah, sayuṣībullażīna kafarụ min-hum \'ażābun alīm'),
(1325, 9, 91, 'لَيْسَ عَلَى الضُّعَفَاۤءِ وَلَا عَلَى الْمَرْضٰى وَلَا عَلَى الَّذِيْنَ لَا يَجِدُوْنَ مَا يُنْفِقُوْنَ حَرَجٌ اِذَا نَصَحُوْا لِلّٰهِ وَرَسُوْلِهٖۗ مَا عَلَى الْمُحْسِنِيْنَ مِنْ سَبِيْلٍ ۗوَاللّٰهُ غَفُوْرٌ رَّحِيْمٌۙ', 'laisa \'alaḍ-ḍu\'afā`i wa lā \'alal-marḍā wa lā \'alallażīna lā yajidụna mā yunfiqụna ḥarajun iżā naṣaḥụ lillāhi wa rasụlih, mā \'alal-muḥsinīna min sabīl, wallāhu gafụrur raḥīm'),
(1326, 9, 92, 'وَّلَا عَلَى الَّذِيْنَ اِذَا مَآ اَتَوْكَ لِتَحْمِلَهُمْ قُلْتَ لَآ اَجِدُ مَآ اَحْمِلُكُمْ عَلَيْهِ ۖتَوَلَّوْا وَّاَعْيُنُهُمْ تَفِيْضُ مِنَ الدَّمْعِ حَزَنًا اَلَّا يَجِدُوْا مَا يُنْفِقُوْنَۗ', 'wa lā \'alallażīna iżā mā atauka litaḥmilahum qulta lā ajidu mā aḥmilukum \'alaihi tawallaw wa a\'yunuhum tafīḍu minad-dam\'i ḥazanan allā yajidụ mā yunfiqụn'),
(1327, 9, 93, 'اِنَّمَا السَّبِيْلُ عَلَى الَّذِيْنَ يَسْتَأْذِنُوْنَكَ وَهُمْ اَغْنِيَاۤءُۚ رَضُوْا بِاَنْ يَّكُوْنُوْا مَعَ الْخَوَالِفِۙ وَطَبَعَ اللّٰهُ عَلٰى قُلُوْبِهِمْ فَهُمْ لَا يَعْلَمُوْنَ ۔', 'innamas-sabīlu \'alallażīna yasta`żinụnaka wa hum agniyā`, raḍụ bi`ay yakụnụ ma\'al-khawālifi wa ṭaba\'allāhu \'alā qulụbihim fa hum lā ya\'lamụn'),
(1328, 9, 94, 'يَعْتَذِرُوْنَ اِلَيْكُمْ اِذَا رَجَعْتُمْ اِلَيْهِمْ ۗ قُلْ لَّا تَعْتَذِرُوْا لَنْ نُّؤْمِنَ لَكُمْ قَدْ نَبَّاَنَا اللّٰهُ مِنْ اَخْبَارِكُمْ وَسَيَرَى اللّٰهُ عَمَلَكُمْ وَرَسُوْلُهٗ ثُمَّ تُرَدُّوْنَ اِلٰى عٰلِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ', 'ya\'tażirụna ilaikum iżā raja\'tum ilaihim, qul lā ta\'tażirụ lan nu`mina lakum qad nabba`anallāhu min akhbārikum wa sayarallāhu \'amalakum wa rasụluhụ ṡumma turaddụna ilā \'ālimil-gaibi wasy-syahādati fa yunabbi`ukum bimā kuntum ta\'malụn'),
(1329, 9, 95, 'سَيَحْلِفُوْنَ بِاللّٰهِ لَكُمْ اِذَا انْقَلَبْتُمْ اِلَيْهِمْ لِتُعْرِضُوْا عَنْهُمْ ۗ فَاَعْرِضُوْا عَنْهُمْ ۗ اِنَّهُمْ رِجْسٌۙ وَّمَأْوٰىهُمْ جَهَنَّمُ جَزَاۤءً ۢبِمَا كَانُوْا يَكْسِبُوْنَ', 'sayaḥlifụna billāhi lakum iżangqalabtum ilaihim litu\'riḍụ \'an-hum, fa a\'riḍụ \'an-hum, innahum rijsuw wa ma`wāhum jahannamu jazā`am bimā kānụ yaksibụn'),
(1330, 9, 96, 'يَحْلِفُوْنَ لَكُمْ لِتَرْضَوْا عَنْهُمْ ۚفَاِنْ تَرْضَوْا عَنْهُمْ فَاِنَّ اللّٰهَ لَا يَرْضٰى عَنِ الْقَوْمِ الْفٰسِقِيْنَ', 'yaḥlifụna lakum litarḍau \'an-hum, fa in tarḍau \'an-hum fa innallāha lā yarḍā \'anil-qaumil-fāsiqīn'),
(1331, 9, 97, 'اَلْاَعْرَابُ اَشَدُّ كُفْرًا وَّنِفَاقًا وَّاَجْدَرُ اَلَّا يَعْلَمُوْا حُدُوْدَ مَآ اَنْزَلَ اللّٰهُ عَلٰى رَسُوْلِهٖ ۗوَاللّٰهُ عَلِيْمٌ حَكِيْمٌ', 'al-a\'rābu asyaddu kufraw wa nifāqaw wa ajdaru allā ya\'lamụ ḥudụda mā anzalallāhu \'alā rasụlih, wallāhu \'alīmun ḥakīm'),
(1332, 9, 98, 'وَمِنَ الْاَعْرَابِ مَنْ يَّتَّخِذُ مَا يُنْفِقُ مَغْرَمًا وَّيَتَرَبَّصُ بِكُمُ الدَّوَاۤىِٕرَ ۗعَلَيْهِمْ دَاۤىِٕرَةُ السَّوْءِ ۗوَاللّٰهُ سَمِيْعٌ عَلِيْمٌ', 'wa minal-a\'rābi may yattakhiżu mā yunfiqu magramaw wa yatarabbaṣu bikumud-dawā`ir, \'alaihim dā`iratus-saụ`, wallāhu samī\'un \'alīm'),
(1333, 9, 99, 'وَمِنَ الْاَعْرَابِ مَنْ يُّؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَيَتَّخِذُ مَا يُنْفِقُ قُرُبٰتٍ عِنْدَ اللّٰهِ وَصَلَوٰتِ الرَّسُوْلِ ۗ اَلَآ اِنَّهَا قُرْبَةٌ لَّهُمْ ۗ سَيُدْخِلُهُمُ اللّٰهُ فِيْ رَحْمَتِهٖ ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'wa minal-a\'rābi may yu`minu billāhi wal-yaumil-ākhiri wa yattakhiżu mā yunfiqu qurubātin \'indallāhi wa ṣalawātir-rasụl, alā innahā qurbatul lahum, sayudkhiluhumullāhu fī raḥmatih, innallāha gafụrur raḥīm'),
(1334, 9, 100, 'وَالسّٰبِقُوْنَ الْاَوَّلُوْنَ مِنَ الْمُهٰجِرِيْنَ وَالْاَنْصَارِ وَالَّذِيْنَ اتَّبَعُوْهُمْ بِاِحْسَانٍۙ رَّضِيَ اللّٰهُ عَنْهُمْ وَرَضُوْا عَنْهُ وَاَعَدَّ لَهُمْ جَنّٰتٍ تَجْرِيْ تَحْتَهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًا ۗذٰلِكَ الْفَوْزُ الْعَظِيْمُ', 'was-sābiqụnal-awwalụna minal-muhājirīna wal-anṣāri wallażīnattaba\'ụhum bi`iḥsānir raḍiyallāhu \'an-hum wa raḍụ \'an-hu wa a\'adda lahum jannātin tajrī taḥtahal-an-hāru khālidīna fīhā abadā, żālikal-fauzul-\'aẓīm'),
(1335, 9, 101, 'وَمِمَّنْ حَوْلَكُمْ مِّنَ الْاَعْرَابِ مُنٰفِقُوْنَ ۗوَمِنْ اَهْلِ الْمَدِيْنَةِ مَرَدُوْا عَلَى النِّفَاقِۗ لَا تَعْلَمُهُمْۗ نَحْنُ نَعْلَمُهُمْۗ سَنُعَذِّبُهُمْ مَّرَّتَيْنِ ثُمَّ يُرَدُّوْنَ اِلٰى عَذَابٍ عَظِيْمٍ ۚ', 'wa mim man ḥaulakum minal-a\'rābi munāfiqụn, wa min ahlil-madīnati maradụ \'alan-nifāq, lā ta\'lamuhum, naḥnu na\'lamuhum, sanu\'ażżibuhum marrataini ṡumma yuraddụna ilā \'ażābin \'aẓīm'),
(1336, 9, 102, 'وَاٰخَرُوْنَ اعْتَرَفُوْا بِذُنُوْبِهِمْ خَلَطُوْا عَمَلًا صَالِحًا وَّاٰخَرَ سَيِّئًاۗ عَسَى اللّٰهُ اَنْ يَّتُوْبَ عَلَيْهِمْۗ اِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'wa ākharụna\'tarafụ biżunụbihim khalaṭụ \'amalan ṣāliḥaw wa ākhara sayyi`ā, \'asallāhu ay yatụba \'alaihim, innallāha gafụrur raḥīm'),
(1337, 9, 103, 'خُذْ مِنْ اَمْوَالِهِمْ صَدَقَةً تُطَهِّرُهُمْ وَتُزَكِّيْهِمْ بِهَا وَصَلِّ عَلَيْهِمْۗ اِنَّ صَلٰوتَكَ سَكَنٌ لَّهُمْۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌ', 'khuż min amwālihim ṣadaqatan tuṭahhiruhum wa tuzakkīhim bihā wa ṣalli \'alaihim, inna ṣalātaka sakanul lahum, wallāhu samī\'un \'alīm'),
(1338, 9, 104, 'اَلَمْ يَعْلَمُوْٓا اَنَّ اللّٰهَ هُوَ يَقْبَلُ التَّوْبَةَ عَنْ عِبَادِهٖ وَيَأْخُذُ الصَّدَقٰتِ وَاَنَّ اللّٰهَ هُوَ التَّوَّابُ الرَّحِيْمُ', 'a lam ya\'lamū annallāha huwa yaqbalut-taubata \'an \'ibādihī wa ya`khużuṣ-ṣadaqāti wa annallāha huwat-tawwābur-raḥīm'),
(1339, 9, 105, 'وَقُلِ اعْمَلُوْا فَسَيَرَى اللّٰهُ عَمَلَكُمْ وَرَسُوْلُهٗ وَالْمُؤْمِنُوْنَۗ وَسَتُرَدُّوْنَ اِلٰى عٰلِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَۚ', 'wa quli\'malụ fa sayarallāhu \'amalakum wa rasụluhụ wal-mu`minụn, wa saturaddụna ilā \'ālimil-gaibi wasy-syahādati fa yunabbi`ukum bimā kuntum ta\'malụn'),
(1340, 9, 106, 'وَاٰخَرُوْنَ مُرْجَوْنَ لِاَمْرِ اللّٰهِ اِمَّا يُعَذِّبُهُمْ وَاِمَّا يَتُوْبُ عَلَيْهِمْۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ', 'wa ākharụna murjauna li`amrillāhi immā yu\'ażżibuhum wa immā yatụbu \'alaihim, wallāhu \'alīmun ḥakīm'),
(1341, 9, 107, 'وَالَّذِيْنَ اتَّخَذُوْا مَسْجِدًا ضِرَارًا وَّكُفْرًا وَّتَفْرِيْقًاۢ بَيْنَ الْمُؤْمِنِيْنَ وَاِرْصَادًا لِّمَنْ حَارَبَ اللّٰهَ وَرَسُوْلَهٗ مِنْ قَبْلُ ۗوَلَيَحْلِفُنَّ اِنْ اَرَدْنَآ اِلَّا الْحُسْنٰىۗ وَاللّٰهُ يَشْهَدُ اِنَّهُمْ لَكٰذِبُوْنَ', 'wallażīnattakhażụ masjidan ḍirāraw wa kufraw wa tafrīqam bainal-mu`minīna wa irṣādal liman ḥāraballāha wa rasụlahụ ming qabl, wa layaḥlifunna in aradnā illal-ḥusnā, wallāhu yasy-hadu innahum lakāżibụn'),
(1342, 9, 108, 'لَا تَقُمْ فِيْهِ اَبَدًاۗ لَمَسْجِدٌ اُسِّسَ عَلَى التَّقْوٰى مِنْ اَوَّلِ يَوْمٍ اَحَقُّ اَنْ تَقُوْمَ فِيْهِۗ فِيْهِ رِجَالٌ يُّحِبُّوْنَ اَنْ يَّتَطَهَّرُوْاۗ وَاللّٰهُ يُحِبُّ الْمُطَّهِّرِيْنَ', 'lā taqum fīhi abadā, lamasjidun ussisa \'alat-taqwā min awwali yaumin aḥaqqu an taqụma fīh, fīhi rijāluy yuḥibbụna ay yataṭahharụ, wallāhu yuḥibbul-muṭṭahhirīn'),
(1343, 9, 109, 'اَفَمَنْ اَسَّسَ بُنْيَانَهٗ عَلٰى تَقْوٰى مِنَ اللّٰهِ وَرِضْوَانٍ خَيْرٌ اَمْ مَّنْ اَسَّسَ بُنْيَانَهٗ عَلٰى شَفَا جُرُفٍ هَارٍ فَانْهَارَ بِهٖ فِيْ نَارِ جَهَنَّمَۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ', 'a fa man assasa bun-yānahụ \'alā taqwā minallāhi wa riḍwānin khairun am man assasa bun-yānahụ \'alā syafā jurufin hārin fan-hāra bihī fī nāri jahannam, wallāhu lā yahdil qaumaẓ-ẓālimīn'),
(1344, 9, 110, 'لَا يَزَالُ بُنْيَانُهُمُ الَّذِيْ بَنَوْا رِيْبَةً فِيْ قُلُوْبِهِمْ اِلَّآ اَنْ تَقَطَّعَ قُلُوْبُهُمْۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ', 'lā yazālu bun-yānuhumullażī banau rībatan fī qulụbihim illā an taqaṭṭa\'a qulụbuhum, wallāhu \'alīmun ḥakīm'),
(1345, 9, 111, '۞ اِنَّ اللّٰهَ اشْتَرٰى مِنَ الْمُؤْمِنِيْنَ اَنْفُسَهُمْ وَاَمْوَالَهُمْ بِاَنَّ لَهُمُ الْجَنَّةَۗ يُقَاتِلُوْنَ فِيْ سَبِيْلِ اللّٰهِ فَيَقْتُلُوْنَ وَيُقْتَلُوْنَ وَعْدًا عَلَيْهِ حَقًّا فِى التَّوْرٰىةِ وَالْاِنْجِيْلِ وَالْقُرْاٰنِۗ وَمَنْ اَوْفٰى بِعَهْدِهٖ مِنَ اللّٰهِ فَاسْتَبْشِرُوْا بِبَيْعِكُمُ الَّذِيْ بَايَعْتُمْ بِهٖۗ وَذٰلِكَ هُوَ الْفَوْزُ الْعَظِيْمُ', 'innallāhasytarā minal-mu`minīna anfusahum wa amwālahum bi`anna lahumul-jannah, yuqātilụna fī sabīlillāhi fa yaqtulụna wa yuqtalụna wa\'dan \'alaihi ḥaqqan fit-taurāti wal-injīli wal-qur`ān, wa man aufā bi\'ahdihī minallāhi fastabsyirụ bibai\'ikumullażī bāya\'tum bih, wa żālika huwal-fauzul-\'aẓīm'),
(1346, 9, 112, 'اَلتَّاۤىِٕبُوْنَ الْعٰبِدُوْنَ الْحَامِدُوْنَ السَّاۤىِٕحُوْنَ الرَّاكِعُوْنَ السَّاجِدُوْنَ الْاٰمِرُوْنَ بِالْمَعْرُوْفِ وَالنَّاهُوْنَ عَنِ الْمُنْكَرِ وَالْحٰفِظُوْنَ لِحُدُوْدِ اللّٰهِ ۗوَبَشِّرِ الْمُؤْمِنِيْنَ', 'attā`ibụnal-\'ābidụnal-ḥāmidụnas-sā`iḥụnar-rāki\'ụnas-sājidụnal-āmirụna bil-ma\'rụfi wan-nāhụna \'anil-mungkari wal-ḥāfiẓụna liḥudụdillāh, wa basysyiril-mu`minīn'),
(1347, 9, 113, 'مَا كَانَ لِلنَّبِيِّ وَالَّذِيْنَ اٰمَنُوْٓا اَنْ يَّسْتَغْفِرُوْا لِلْمُشْرِكِيْنَ وَلَوْ كَانُوْٓا اُولِيْ قُرْبٰى مِنْۢ بَعْدِ مَا تَبَيَّنَ لَهُمْ اَنَّهُمْ اَصْحٰبُ الْجَحِيْمِ', 'mā kāna lin-nabiyyi wallażīna āmanū ay yastagfirụ lil-musyrikīna walau kānū ulī qurbā mim ba\'di mā tabayyana lahum annahum aṣ-ḥābul-jaḥīm'),
(1348, 9, 114, 'وَمَا كَانَ اسْتِغْفَارُ اِبْرٰهِيْمَ لِاَبِيْهِ اِلَّا عَنْ مَّوْعِدَةٍ وَّعَدَهَآ اِيَّاهُۚ فَلَمَّا تَبَيَّنَ لَهٗٓ اَنَّهٗ عَدُوٌّ لِّلّٰهِ تَبَرَّاَ مِنْهُۗ اِنَّ اِبْرٰهِيْمَ لَاَوَّاهٌ حَلِيْمٌ', 'wa mā kānastigfāru ibrāhīma li`abīhi illā \'am mau\'idatiw wa\'adahā iyyāh, fa lammā tabayyana lahū annahụ \'aduwwul lillāhi tabarra`a min-h, inna ibrāhīma la`awwāhun ḥalīm'),
(1349, 9, 115, 'وَمَا كَانَ اللّٰهُ لِيُضِلَّ قَوْمًاۢ بَعْدَ اِذْ هَدٰىهُمْ حَتّٰى يُبَيِّنَ لَهُمْ مَّا يَتَّقُوْنَۗ اِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ', 'wa mā kānallāhu liyuḍilla qaumam ba\'da iż hadāhum ḥattā yubayyina lahum mā yattaqụn, innallāha bikulli syai`in \'alīm'),
(1350, 9, 116, 'اِنَّ اللّٰهَ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ يُحْيٖ وَيُمِيْتُۗ وَمَا لَكُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ', 'innallāha lahụ mulkus-samāwāti wal-arḍ, yuḥyī wa yumīt, wa mā lakum min dụnillāhi miw waliyyiw wa lā naṣīr'),
(1351, 9, 117, 'لَقَدْ تَّابَ اللّٰهُ عَلَى النَّبِيِّ وَالْمُهٰجِرِيْنَ وَالْاَنْصَارِ الَّذِيْنَ اتَّبَعُوْهُ فِيْ سَاعَةِ الْعُسْرَةِ مِنْۢ بَعْدِ مَا كَادَ يَزِيْغُ قُلُوْبُ فَرِيْقٍ مِّنْهُمْ ثُمَّ تَابَ عَلَيْهِمْۗ اِنَّهٗ بِهِمْ رَءُوْفٌ رَّحِيْمٌ ۙ', 'laqat tāballāhu \'alan-nabiyyi wal-muhājirīna wal-anṣārillażīnattaba\'ụhu fī sā\'atil-\'usrati mim ba\'di mā kāda yazīgu qulụbu farīqim min-hum ṡumma tāba \'alaihim, innahụ bihim ra`ụfur raḥīm'),
(1352, 9, 118, 'وَّعَلَى الثَّلٰثَةِ الَّذِيْنَ خُلِّفُوْاۗ حَتّٰٓى اِذَا ضَاقَتْ عَلَيْهِمُ الْاَرْضُ بِمَا رَحُبَتْ وَضَاقَتْ عَلَيْهِمْ اَنْفُسُهُمْ وَظَنُّوْٓا اَنْ لَّا مَلْجَاَ مِنَ اللّٰهِ اِلَّآ اِلَيْهِۗ ثُمَّ تَابَ عَلَيْهِمْ لِيَتُوْبُوْاۗ اِنَّ اللّٰهَ هُوَ التَّوَّابُ الرَّحِيْمُ', 'wa \'alaṡ-ṡalāṡatillażīna khullifụ, ḥattā iżā ḍāqat \'alaihimul-arḍu bimā raḥubat wa ḍāqat \'alaihim anfusuhum wa ẓannū al lā malja`a minallāhi illā ilaīh, ṡumma tāba \'alaihim liyatụbụ, innallāha huwat-tawwābur-raḥīm'),
(1353, 9, 119, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَكُوْنُوْا مَعَ الصّٰدِقِيْنَ', 'yā ayyuhallażīna āmanuttaqullāha wa kụnụ ma\'aṣ-ṣādiqīn'),
(1354, 9, 120, 'مَا كَانَ لِاَهْلِ الْمَدِيْنَةِ وَمَنْ حَوْلَهُمْ مِّنَ الْاَعْرَابِ اَنْ يَّتَخَلَّفُوْا عَنْ رَّسُوْلِ اللّٰهِ وَلَا يَرْغَبُوْا بِاَنْفُسِهِمْ عَنْ نَّفْسِهٖۗ ذٰلِكَ بِاَنَّهُمْ لَا يُصِيْبُهُمْ ظَمَاٌ وَّلَا نَصَبٌ وَّلَا مَخْمَصَةٌ فِيْ سَبِيْلِ اللّٰهِ وَلَا يَطَـُٔوْنَ مَوْطِئًا يَّغِيْظُ الْكُفَّارَ وَلَا يَنَالُوْنَ مِنْ عَدُوٍّ نَّيْلًا اِلَّا كُتِبَ لَهُمْ بِهٖ عَمَلٌ صَالِحٌۗ اِنَّ اللّٰهَ لَا يُضِيْعُ اَجْرَ الْمُحْسِنِيْنَ', 'mā kāna li`ahlil-madīnati wa man ḥaulahum minal-a\'rābi ay yatakhallafụ \'ar rasụlillāhi wa lā yargabụ bi`anfusihim \'an nafsih, żālika bi`annahum lā yuṣībuhum ẓama`uw wa lā naṣabuw wa lā makhmaṣatun fī sabīlillāhi wa lā yaṭa`ụna mauṭi`ay yagīẓul-kuffāra wa lā yanalụna min \'aduwwin nailan illā kutiba lahum bihī \'amalun ṣāliḥ, innallāha lā yuḍī\'u ajral-muḥsinīn'),
(1355, 9, 121, 'وَلَا يُنْفِقُوْنَ نَفَقَةً صَغِيْرَةً وَّلَا كَبِيْرَةً وَّلَا يَقْطَعُوْنَ وَادِيًا اِلَّا كُتِبَ لَهُمْ لِيَجْزِيَهُمُ اللّٰهُ اَحْسَنَ مَا كَانُوْا يَعْمَلُوْنَ', 'wa lā yunfiqụna nafaqatan ṣagīrataw wa lā kabīrataw wa lā yaqṭa\'ụna wādiyan illā kutiba lahum liyajziyahumullāhu aḥsana mā kānụ ya\'malụn'),
(1356, 9, 122, '۞ وَمَا كَانَ الْمُؤْمِنُوْنَ لِيَنْفِرُوْا كَاۤفَّةًۗ فَلَوْلَا نَفَرَ مِنْ كُلِّ فِرْقَةٍ مِّنْهُمْ طَاۤىِٕفَةٌ لِّيَتَفَقَّهُوْا فِى الدِّيْنِ وَلِيُنْذِرُوْا قَوْمَهُمْ اِذَا رَجَعُوْٓا اِلَيْهِمْ لَعَلَّهُمْ يَحْذَرُوْنَ', 'wa mā kānal-mu`minụna liyanfirụ kāffah, falau lā nafara ming kulli firqatim min-hum ṭā`ifatul liyatafaqqahụ fid-dīni wa liyunżirụ qaumahum iżā raja\'ū ilaihim la\'allahum yaḥżarụn'),
(1357, 9, 123, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا قَاتِلُوا الَّذِيْنَ يَلُوْنَكُمْ مِّنَ الْكُفَّارِ وَلْيَجِدُوْا فِيْكُمْ غِلْظَةًۗ وَاعْلَمُوْٓا اَنَّ اللّٰهَ مَعَ الْمُتَّقِيْنَ', 'yā ayyuhallażīna āmanụ qātilullażīna yalụnakum minal-kuffāri walyajidụ fīkum gilẓah, wa\'lamū annallāha ma\'al-muttaqīn'),
(1358, 9, 124, 'وَاِذَا مَآ اُنْزِلَتْ سُوْرَةٌ فَمِنْهُمْ مَّنْ يَّقُوْلُ اَيُّكُمْ زَادَتْهُ هٰذِهٖٓ اِيْمَانًاۚ فَاَمَّا الَّذِيْنَ اٰمَنُوْا فَزَادَتْهُمْ اِيْمَانًا وَّهُمْ يَسْتَبْشِرُوْنَ', 'wa iżā mā unzilat sụratun fa min-hum may yaqụlu ayyukum zādat-hu hāżihī īmānā, fa ammallażīna āmanụ fa zādat-hum īmānaw wa hum yastabsyirụn'),
(1359, 9, 125, 'وَاَمَّا الَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ فَزَادَتْهُمْ رِجْسًا اِلٰى رِجْسِهِمْ وَمَاتُوْا وَهُمْ كٰفِرُوْنَ', 'wa ammallażīna fī qulụbihim maraḍun fa zādat-hum rijsan ilā rijsihim wa mātụ wa hum kāfirụn'),
(1360, 9, 126, 'اَوَلَا يَرَوْنَ اَنَّهُمْ يُفْتَنُوْنَ فِيْ كُلِّ عَامٍ مَّرَّةً اَوْ مَرَّتَيْنِ ثُمَّ لَا يَتُوْبُوْنَ وَلَا هُمْ يَذَّكَّرُوْنَ', 'a wa lā yarauna annahum yuftanụna fī kulli \'āmim marratan au marrataini ṡumma lā yatụbụna wa lā hum yażżakkarụn'),
(1361, 9, 127, 'وَاِذَا مَآ اُنْزِلَتْ سُوْرَةٌ نَّظَرَ بَعْضُهُمْ اِلٰى بَعْضٍۗ هَلْ يَرٰىكُمْ مِّنْ اَحَدٍ ثُمَّ انْصَرَفُوْاۗ صَرَفَ اللّٰهُ قُلُوْبَهُمْ بِاَنَّهُمْ قَوْمٌ لَّا يَفْقَهُوْنَ', 'wa iżā mā unzilat sụratun naẓara ba\'ḍuhum ilā ba\'ḍ, hal yarākum min aḥadin ṡummanṣarafụ, ṣarafallāhu qulụbahum bi`annahum qaumul lā yafqahụn'),
(1362, 9, 128, 'لَقَدْ جَاۤءَكُمْ رَسُوْلٌ مِّنْ اَنْفُسِكُمْ عَزِيْزٌ عَلَيْهِ مَا عَنِتُّمْ حَرِيْصٌ عَلَيْكُمْ بِالْمُؤْمِنِيْنَ رَءُوْفٌ رَّحِيْمٌ', 'laqad jā`akum rasụlum min anfusikum \'azīzun \'alaihi mā \'anittum ḥarīṣun \'alaikum bil-mu`minīna ra`ụfur raḥīm'),
(1363, 9, 129, 'فَاِنْ تَوَلَّوْا فَقُلْ حَسْبِيَ اللّٰهُ لَآ اِلٰهَ اِلَّا هُوَ ۗ عَلَيْهِ تَوَكَّلْتُ وَهُوَ رَبُّ الْعَرْشِ الْعَظِيْمِ', 'fa in tawallau fa qul ḥasbiyallāhu lā ilāha illā huw, \'alaihi tawakkaltu wa huwa rabbul-\'arsyil-\'aẓīm'),
(1364, 10, 1, 'الۤرٰ ۗتِلْكَ اٰيٰتُ الْكِتٰبِ الْحَكِيْمِ', 'alif lām rā, tilka āyātul-kitābil-ḥakīm'),
(1365, 10, 2, 'اَكَانَ لِلنَّاسِ عَجَبًا اَنْ اَوْحَيْنَآ اِلٰى رَجُلٍ مِّنْهُمْ اَنْ اَنْذِرِ النَّاسَ وَبَشِّرِ الَّذِيْنَ اٰمَنُوْٓا اَنَّ لَهُمْ قَدَمَ صِدْقٍ عِنْدَ رَبِّهِمْ ۗ قَالَ الْكٰفِرُوْنَ اِنَّ هٰذَا لَسٰحِرٌ مُّبِيْنٌ', 'a kāna lin-nāsi \'ajaban an auḥainā ilā rajulim min-hum an anżirin-nāsa wa basysyirillażīna āmanū anna lahum qadama ṣidqin \'inda rabbihim, qālal-kāfirụna inna hāżā lasāḥirum mubīn'),
(1366, 10, 3, 'اِنَّ رَبَّكُمُ اللّٰهُ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ فِيْ سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِ يُدَبِّرُ الْاَمْرَۗ مَا مِنْ شَفِيْعٍ اِلَّا مِنْۢ بَعْدِ اِذْنِهٖۗ ذٰلِكُمُ اللّٰهُ رَبُّكُمْ فَاعْبُدُوْهُۗ اَفَلَا تَذَكَّرُوْنَ', 'inna rabbakumullāhullażī khalaqas-samāwāti wal-arḍa fī sittati ayyāmin ṡummastawā \'alal-\'arsyi yudabbirul-amr, mā min syafī\'in illā mim ba\'di iżnih, żālikumullāhu rabbukum fa\'budụh, a fa lā tażakkarụn'),
(1367, 10, 4, 'اِلَيْهِ مَرْجِعُكُمْ جَمِيْعًاۗ وَعْدَ اللّٰهِ حَقًّاۗ اِنَّهٗ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗ لِيَجْزِيَ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ بِالْقِسْطِۗ وَالَّذِيْنَ كَفَرُوْا لَهُمْ شَرَابٌ مِّنْ حَمِيْمٍ وَّعَذَابٌ اَلِيْمٌ ۢبِمَا كَانُوْا يَكْفُرُوْنَ', 'ilaihi marji\'ukum jamī\'ā, wa\'dallāhi ḥaqqā, innahụ yabda`ul-khalqa ṡumma yu\'īduhụ liyajziyallażīna āmanụ wa \'amiluṣ-ṣāliḥāti bil-qisṭ, wallażīna kafarụ lahum syarābum min ḥamīmiw wa \'ażābun alīmum bimā kānụ yakfurụn'),
(1368, 10, 5, 'هُوَ الَّذِيْ جَعَلَ الشَّمْسَ ضِيَاۤءً وَّالْقَمَرَ نُوْرًا وَّقَدَّرَهٗ مَنَازِلَ لِتَعْلَمُوْا عَدَدَ السِّنِيْنَ وَالْحِسَابَۗ مَا خَلَقَ اللّٰهُ ذٰلِكَ اِلَّا بِالْحَقِّۗ يُفَصِّلُ الْاٰيٰتِ لِقَوْمٍ يَّعْلَمُوْنَ', 'huwallażī ja\'alasy-syamsa ḍiyā`aw wal-qamara nụraw wa qaddarahụ manāzila lita\'lamụ \'adadas-sinīna wal-ḥisāb, mā khalaqallāhu żālika illā bil-ḥaqq, yufaṣṣilul-āyāti liqaumiy ya\'lamụn'),
(1369, 10, 6, 'اِنَّ فِى اخْتِلَافِ الَّيْلِ وَالنَّهَارِ وَمَا خَلَقَ اللّٰهُ فِى السَّمٰوٰتِ وَالْاَرْضِ لَاٰيٰتٍ لِّقَوْمٍ يَّتَّقُوْنَ', 'inna fikhtilāfil-laili wan-nahāri wa mā khalaqallāhu fis-samāwāti wal-arḍi la`āyātil liqaumiy yattaqụn'),
(1370, 10, 7, 'اِنَّ الَّذِيْنَ لَا يَرْجُوْنَ لِقَاۤءَنَا وَرَضُوْا بِالْحَيٰوةِ الدُّنْيَا وَاطْمَـَٔنُّوْا بِهَا وَالَّذِيْنَ هُمْ عَنْ اٰيٰتِنَا غٰفِلُوْنَۙ', 'innallażīna lā yarjụna liqā`ana wa raḍụ bil-ḥayātid-dun-yā waṭma`annụ bihā wallażīna hum \'an āyātinā gāfilụn'),
(1371, 10, 8, 'اُولٰۤىِٕكَ مَأْوٰىهُمُ النَّارُ بِمَا كَانُوْا يَكْسِبُوْنَ', 'ulā`ika ma`wāhumun-nāru bimā kānụ yaksibụn'),
(1372, 10, 9, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ يَهْدِيْهِمْ رَبُّهُمْ بِاِيْمَانِهِمْۚ تَجْرِيْ مِنْ تَحْتِهِمُ الْاَنْهٰرُ فِيْ جَنّٰتِ النَّعِيْمِ', 'innallażīna āmanụ wa \'amiluṣ-ṣāliḥāti yahdīhim rabbuhum bi`īmānihim, tajrī min taḥtihimul-an-hāru fī jannātin-na\'īm'),
(1373, 10, 10, 'دَعْوٰىهُمْ فِيْهَا سُبْحٰنَكَ اللهم وَتَحِيَّتُهُمْ فِيْهَا سَلٰمٌۚ وَاٰخِرُ دَعْوٰىهُمْ اَنِ الْحَمْدُ لِلّٰهِ رَبِّ الْعٰلَمِيْنَ', 'da\'wāhum fīhā sub-ḥānakallāhumma wa taḥiyyatuhum fīhā salām, wa ākhiru da\'wāhum anil-ḥamdu lillāhi rabbil \'ālamīn'),
(1374, 10, 11, '۞ وَلَوْ يُعَجِّلُ اللّٰهُ لِلنَّاسِ الشَّرَّ اسْتِعْجَالَهُمْ بِالْخَيْرِ لَقُضِيَ اِلَيْهِمْ اَجَلُهُمْۗ فَنَذَرُ الَّذِيْنَ لَا يَرْجُوْنَ لِقَاۤءَنَا فِيْ طُغْيَانِهِمْ يَعْمَهُوْنَ', 'walau yu\'ajjilullāhu lin-nāsisy-syarrasti\'jālahum bil-khairi laquḍiya ilaihim ajaluhum, fa nażarullażīna lā yarjụna liqā`anā fī ṭugyānihim ya\'mahụn'),
(1375, 10, 12, 'وَاِذَا مَسَّ الْاِنْسَانَ الضُّرُّ دَعَانَا لِجَنْۢبِهٖٓ اَوْ قَاعِدًا اَوْ قَاۤىِٕمًا ۚفَلَمَّا كَشَفْنَا عَنْهُ ضُرَّهٗ مَرَّ كَاَنْ لَّمْ يَدْعُنَآ اِلٰى ضُرٍّ مَّسَّهٗۗ كَذٰلِكَ زُيِّنَ لِلْمُسْرِفِيْنَ مَا كَانُوْا يَعْمَلُوْنَ', 'wa iżā massal-insānaḍ-ḍurru da\'ānā lijambihī au qā\'idan au qā`imā, fa lammā kasyafnā \'an-hu ḍurrahụ marra ka`al lam yad\'unā ilā ḍurrim massah, każālika zuyyina lil-musrifīna mā kānụ ya\'malụn'),
(1376, 10, 13, 'وَلَقَدْ اَهْلَكْنَا الْقُرُوْنَ مِنْ قَبْلِكُمْ لَمَّا ظَلَمُوْاۙ وَجَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِ وَمَا كَانُوْا لِيُؤْمِنُوْا ۗ كَذٰلِكَ نَجْزِى الْقَوْمَ الْمُجْرِمِيْنَ', 'wa laqad ahlaknal-qurụna ming qablikum lammā ẓalamụ wa jā`at-hum rusuluhum bil-bayyināti wa mā kānụ liyu`minụ, każālika najzil-qaumal-mujrimīn'),
(1377, 10, 14, 'ثُمَّ جَعَلْنٰكُمْ خَلٰۤىِٕفَ فِى الْاَرْضِ مِنْۢ بَعْدِهِمْ لِنَنْظُرَ كَيْفَ تَعْمَلُوْنَ', 'ṡumma ja\'alnākum khalā`ifa fil-arḍi mim ba\'dihim linanẓura kaifa ta\'malụn'),
(1378, 10, 15, 'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيَاتُنَا بَيِّنٰتٍۙ قَالَ الَّذِيْنَ لَا يَرْجُوْنَ لِقَاۤءَنَا ائْتِ بِقُرْاٰنٍ غَيْرِ هٰذَآ اَوْ بَدِّلْهُ ۗ قُلْ مَا يَكُوْنُ لِيْٓ اَنْ اُبَدِّلَهٗ مِنْ تِلْقَاۤئِ نَفْسِيْ ۚاِنْ اَتَّبِعُ اِلَّا مَا يُوْحٰٓى اِلَيَّ ۚ اِنِّيْٓ اَخَافُ اِنْ عَصَيْتُ رَبِّيْ عَذَابَ يَوْمٍ عَظِيْمٍ', 'wa iżā tutlā \'alaihim āyātunā bayyināting qālallażīna lā yarjụna liqā`ana`ti biqur`ānin gairi hāżā au baddil-h, qul mā yakụnu lī an ubaddilahụ min tilqā`i nafsī, in attabi\'u illā mā yụḥā ilayy, innī akhāfu in \'aṣaitu rabbī \'ażāba yaumin \'aẓīm'),
(1379, 10, 16, 'قُلْ لَّوْ شَاۤءَ اللّٰهُ مَا تَلَوْتُهٗ عَلَيْكُمْ وَلَآ اَدْرٰىكُمْ بِهٖ ۖفَقَدْ لَبِثْتُ فِيْكُمْ عُمُرًا مِّنْ قَبْلِهٖۗ اَفَلَا تَعْقِلُوْنَ', 'qul lau syā`allāhu mā talautuhụ \'alaikum wa lā adrākum bihī fa qad labiṡtu fīkum \'umuram ming qablih, a fa lā ta\'qilụn'),
(1380, 10, 17, 'فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا اَوْ كَذَّبَ بِاٰيٰتِهٖۗ اِنَّهٗ لَا يُفْلِحُ الْمُجْرِمُوْنَ', 'fa man aẓlamu mim maniftarā \'alallāhi każiban au każżaba bi`āyātih, innahụ lā yufliḥul-mujrimụn'),
(1381, 10, 18, 'وَيَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَا يَضُرُّهُمْ وَلَا يَنْفَعُهُمْ وَيَقُوْلُوْنَ هٰٓؤُلَاۤءِ شُفَعَاۤؤُنَا عِنْدَ اللّٰهِ ۗقُلْ اَتُنَبِّـُٔوْنَ اللّٰهَ بِمَا لَا يَعْلَمُ فِى السَّمٰوٰتِ وَلَا فِى الْاَرْضِۗ سُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يُشْرِكُوْنَ', 'wa ya\'budụna min dụnillāhi mā lā yaḍurruhum wa lā yanfa\'uhum wa yaqụlụna hā`ulā`i syufa\'ā`unā \'indallāh, qul a tunabbi`ụnallāha bimā lā ya\'lamu fis-samāwāti wa lā fil-arḍ, sub-ḥānahụ wa ta\'ālā \'ammā yusyrikụn'),
(1382, 10, 19, 'وَمَا كَانَ النَّاسُ اِلَّآ اُمَّةً وَّاحِدَةً فَاخْتَلَفُوْاۗ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَّبِّكَ لَقُضِيَ بَيْنَهُمْ فِيْمَا فِيْهِ يَخْتَلِفُوْنَ', 'wa mā kānan-nāsu illā ummataw wāḥidatan fakhtalafụ, walau lā kalimatun sabaqat mir rabbika laquḍiya bainahum fīmā fīhi yakhtalifụn'),
(1383, 10, 20, 'وَيَقُوْلُوْنَ لَوْلَآ اُنْزِلَ عَلَيْهِ اٰيَةٌ مِّنْ رَّبِّهٖۚ فَقُلْ اِنَّمَا الْغَيْبُ لِلّٰهِ فَانْتَظِرُوْاۚ اِنِّيْ مَعَكُمْ مِّنَ الْمُنْتَظِرِيْنَ', 'wa yaqụlụna lau lā unzila \'alaihi āyatum mir rabbih, fa qul innamal-gaibu lillāhi fantaẓirụ, innī ma\'akum minal-muntaẓirīn'),
(1384, 10, 21, 'وَاِذَآ اَذَقْنَا النَّاسَ رَحْمَةً مِّنْۢ بَعْدِ ضَرَّاۤءَ مَسَّتْهُمْ اِذَا لَهُمْ مَّكْرٌ فِيْٓ اٰيٰتِنَاۗ قُلِ اللّٰهُ اَسْرَعُ مَكْرًاۗ اِنَّ رُسُلَنَا يَكْتُبُوْنَ مَا تَمْكُرُوْنَ', 'wa iżā ażaqnan-nāsa raḥmatam mim ba\'di ḍarrā`a massat-hum iżā lahum makrun fī āyātinā, qulillāhu asra\'u makrā, inna rusulana yaktubụna mā tamkurụn'),
(1385, 10, 22, 'هُوَ الَّذِيْ يُسَيِّرُكُمْ فِى الْبَرِّ وَالْبَحْرِۗ حَتّٰٓى اِذَا كُنْتُمْ فِىْ الْفُلْكِۚ وَجَرَيْنَ بِهِمْ بِرِيْحٍ طَيِّبَةٍ وَّفَرِحُوْا بِهَا جَاۤءَتْهَا رِيْحٌ عَاصِفٌ وَّجَاۤءَهُمُ الْمَوْجُ مِنْ كُلِّ مَكَانٍ وَّظَنُّوْٓا اَنَّهُمْ اُحِيْطَ بِهِمْۙ دَعَوُا اللّٰهَ مُخْلِصِيْنَ لَهُ الدِّيْنَ ەۚ لَىِٕنْ اَنْجَيْتَنَا مِنْ هٰذِهٖ لَنَكُوْنَنَّ مِنَ الشّٰكِرِيْنَ', 'huwallażī yusayyirukum fil-barri wal-baḥr, ḥattā iżā kuntum fil-fulk, wa jaraina bihim birīḥin ṭayyibatiw wa fariḥụ bihā jā`at-hā rīḥun \'āṣifuw wa jā`ahumul-mauju ming kulli makāniw wa ẓannū annahum uḥīṭa bihim da\'awullāha mukhliṣīna lahud-dīn, la`in anjaitanā min hāżihī lanakụnanna minasy-syākirīn'),
(1386, 10, 23, 'فَلَمَّآ اَنْجٰىهُمْ اِذَا هُمْ يَبْغُوْنَ فِى الْاَرْضِ بِغَيْرِ الْحَقِّ ۗيٰٓاَيُّهَا النَّاسُ اِنَّمَا بَغْيُكُمْ عَلٰٓى اَنْفُسِكُمْ مَّتَاعَ الْحَيٰوةِ الدُّنْيَاۖ ثُمَّ اِلَيْنَا مَرْجِعُكُمْ فَنُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ', 'fa lammā anjāhum iżā hum yabgụna fil-arḍi bigairil-ḥaqq, yā ayyuhan-nāsu innamā bagyukum \'alā anfusikum matā\'al-ḥayātid-dun-yā ṡumma ilainā marji\'ukum fa nunabbi`ukum bimā kuntum ta\'malụn'),
(1387, 10, 24, 'اِنَّمَا مَثَلُ الْحَيٰوةِ الدُّنْيَا كَمَاۤءٍ اَنْزَلْنٰهُ مِنَ السَّمَاۤءِ فَاخْتَلَطَ بِهٖ نَبَاتُ الْاَرْضِ مِمَّا يَأْكُلُ النَّاسُ وَالْاَنْعَامُ ۗحَتّٰٓى اِذَآ اَخَذَتِ الْاَرْضُ زُخْرُفَهَا وَازَّيَّنَتْ وَظَنَّ اَهْلُهَآ اَنَّهُمْ قٰدِرُوْنَ عَلَيْهَآ اَتٰىهَآ اَمْرُنَا لَيْلًا اَوْ نَهَارًا فَجَعَلْنٰهَا حَصِيْدًا كَاَنْ لَّمْ تَغْنَ بِالْاَمْسِۗ كَذٰلِكَ نُفَصِّلُ الْاٰيٰتِ لِقَوْمٍ يَّتَفَكَّرُوْنَ', 'innamā maṡalul-ḥayātid-dun-yā kamā`in anzalnāhu minas-samā`i fakhtalaṭa bihī nabātul-arḍi mimmā ya`kulun-nāsu wal-an\'ām, ḥattā iżā akhażatil-arḍu zukhrufahā wazzayyanat wa ẓanna ahluhā annahum qādirụna \'alaihā atāhā amrunā lailan au nahāran fa ja\'alnāhā ḥaṣīdang ka`al lam tagna bil-ams, każālika nufaṣṣilul-āyāti liqaumiy yatafakkarụn'),
(1388, 10, 25, 'وَاللّٰهُ يَدْعُوْٓ اِلٰى دَارِ السَّلٰمِ ۚوَيَهْدِيْ مَنْ يَّشَاۤءُ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ', 'wallāhu yad\'ū ilā dāris-salām, wa yahdī may yasyā`u ilā ṣirāṭim mustaqīm'),
(1389, 10, 26, '۞ لِلَّذِيْنَ اَحْسَنُوا الْحُسْنٰى وَزِيَادَةٌ ۗوَلَا يَرْهَقُ وُجُوْهَهُمْ قَتَرٌ وَّلَا ذِلَّةٌ ۗاُولٰۤىِٕكَ اَصْحٰبُ الْجَنَّةِ هُمْ فِيْهَا خٰلِدُوْنَ', 'lillażīna aḥsanul-ḥusnā wa ziyādah, wa lā yar-haqu wujụhahum qataruw wa lā żillah, ulā`ika aṣ-ḥābul-jannati hum fīhā khālidụn'),
(1390, 10, 27, 'وَالَّذِيْنَ كَسَبُوا السَّيِّاٰتِ جَزَاۤءُ سَيِّئَةٍ ۢبِمِثْلِهَاۙ وَتَرْهَقُهُمْ ذِلَّةٌ ۗمَا لَهُمْ مِّنَ اللّٰهِ مِنْ عَاصِمٍۚ كَاَنَّمَآ اُغْشِيَتْ وُجُوْهُهُمْ قِطَعًا مِّنَ الَّيْلِ مُظْلِمًاۗ اُولٰۤىِٕكَ اَصْحٰبُ النَّارِ ۚهُمْ فِيْهَا خٰلِدُوْنَ', 'wallażīna kasabus-sayyi`āti jazā`u sayyi`atim bimiṡlihā wa tar-haquhum żillah, mā lahum minallāhi min \'āṣim, ka`annamā ugsyiyat wujụhuhum qiṭa\'am minal-laili muẓlimā, ulā`ika aṣ-ḥābun-nār, hum fīhā khālidụn'),
(1391, 10, 28, 'وَيَوْمَ نَحْشُرُهُمْ جَمِيْعًا ثُمَّ نَقُوْلُ لِلَّذِيْنَ اَشْرَكُوْا مَكَانَكُمْ اَنْتُمْ وَشُرَكَاۤؤُكُمْۚ فَزَيَّلْنَا بَيْنَهُمْ وَقَالَ شُرَكَاۤؤُهُمْ مَّا كُنْتُمْ اِيَّانَا تَعْبُدُوْنَ', 'wa yauma naḥsyuruhum jamī\'an ṡumma naqụlu lillażīna asyrakụ makānakum antum wa syurakā`ukum, fa zayyalnā bainahum wa qāla syurakā`uhum mā kuntum iyyānā ta\'budụn'),
(1392, 10, 29, 'فَكَفٰى بِاللّٰهِ شَهِيْدًاۢ بَيْنَنَا وَبَيْنَكُمْ اِنْ كُنَّا عَنْ عِبَادَتِكُمْ لَغٰفِلِيْنَ', 'fa kafā billāhi syahīdam bainanā wa bainakum ing kunnā \'an \'ibādatikum lagāfilīn'),
(1393, 10, 30, 'هُنَالِكَ تَبْلُوْا كُلُّ نَفْسٍ مَّآ اَسْلَفَتْ وَرُدُّوْٓا اِلَى اللّٰهِ مَوْلٰىهُمُ الْحَقِّ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ', 'hunālika tablụ kullu nafsim mā aslafat wa ruddū ilallāhi maulāhumul-ḥaqqi wa ḍalla \'an-hum mā kānụ yaftarụn'),
(1394, 10, 31, 'قُلْ مَنْ يَّرْزُقُكُمْ مِّنَ السَّمَاۤءِ وَالْاَرْضِ اَمَّنْ يَّمْلِكُ السَّمْعَ وَالْاَبْصَارَ وَمَنْ يُّخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَيُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَمَنْ يُّدَبِّرُ الْاَمْرَۗ فَسَيَقُوْلُوْنَ اللّٰهُ ۚفَقُلْ اَفَلَا تَتَّقُوْنَ', 'qul may yarzuqukum minas-samā`i wal-arḍi am may yamlikus-sam\'a wal-abṣāra wa may yukhrijul-ḥayya minal-mayyiti wa yukhrijul-mayyita minal-ḥayyi wa may yudabbirul-amr, fa sayaqụlụnallāh, fa qul a fa lā tattaqụn'),
(1395, 10, 32, 'فَذٰلِكُمُ اللّٰهُ رَبُّكُمُ الْحَقُّۚ فَمَاذَا بَعْدَ الْحَقِّ اِلَّا الضَّلٰلُ ۖفَاَنّٰى تُصْرَفُوْنَ', 'fa żālikumullāhu rabbukumul-ḥaqq, fa māżā ba\'dal-ḥaqqi illaḍ-ḍalālu fa annā tuṣrafụn'),
(1396, 10, 33, 'كَذٰلِكَ حَقَّتْ كَلِمَتُ رَبِّكَ عَلَى الَّذِيْنَ فَسَقُوْٓا اَنَّهُمْ لَا يُؤْمِنُوْنَ', 'każālika ḥaqqat kalimatu rabbika \'alallażīna fasaqū annahum lā yu`minụn'),
(1397, 10, 34, 'قُلْ هَلْ مِنْ شُرَكَاۤىِٕكُمْ مَّنْ يَّبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗۗ قُلِ اللّٰهُ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗ فَاَنّٰى تُؤْفَكُوْنَ', 'qul hal min syurakā`ikum may yabda`ul-khalqa ṡumma yu\'īduh, qulillāhu yabda`ul-khalqa ṡumma yu\'īduhụ fa annā tu`fakụn'),
(1398, 10, 35, 'قُلْ هَلْ مِنْ شُرَكَاۤىِٕكُمْ مَّنْ يَّهْدِيْٓ اِلَى الْحَقِّۗ قُلِ اللّٰهُ يَهْدِيْ لِلْحَقِّۗ اَفَمَنْ يَّهْدِيْٓ اِلَى الْحَقِّ اَحَقُّ اَنْ يُّتَّبَعَ اَمَّنْ لَّا يَهِدِّيْٓ اِلَّآ اَنْ يُّهْدٰىۚ فَمَا لَكُمْۗ كَيْفَ تَحْكُمُوْنَ', 'qul hal min syurakā`ikum may yahdī ilal-ḥaqq, qulillāhu yahdī lil-ḥaqq, a fa may yahdī ilal-ḥaqqi aḥaqqu ay yuttaba\'a am mal lā yahiddī illā ay yuhdā, fa mā lakum, kaifa taḥkumụn'),
(1399, 10, 36, 'وَمَا يَتَّبِعُ اَكْثَرُهُمْ اِلَّا ظَنًّاۗ اِنَّ الظَّنَّ لَا يُغْنِيْ مِنَ الْحَقِّ شَيْـًٔاۗ اِنَّ اللّٰهَ عَلِيْمٌ ۢبِمَا يَفْعَلُوْنَ', 'wa mā yattabi\'u akṡaruhum illā ẓannā, innaẓ-ẓanna lā yugnī minal-ḥaqqi syai`ā, innallāha \'alīmum bimā yaf\'alụn'),
(1400, 10, 37, 'وَمَا كَانَ هٰذَا الْقُرْاٰنُ اَنْ يُّفْتَرٰى مِنْ دُوْنِ اللّٰهِ وَلٰكِنْ تَصْدِيْقَ الَّذِيْ بَيْنَ يَدَيْهِ وَتَفْصِيْلَ الْكِتٰبِ لَا رَيْبَ فِيْهِ مِنْ رَّبِّ الْعٰلَمِيْنَۗ', 'wa mā kāna hāżal-qur`ānu ay yuftarā min dụnillāhi wa lākin taṣdīqallażī baina yadaihi wa tafṣīlal-kitābi lā raiba fīhi mir rabbil-\'ālamīn'),
(1401, 10, 38, 'اَمْ يَقُوْلُوْنَ افْتَرٰىهُ ۗ قُلْ فَأْتُوْا بِسُوْرَةٍ مِّثْلِهٖ وَادْعُوْا مَنِ اسْتَطَعْتُمْ مِّنْ دُوْنِ اللّٰهِ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'am yaqụlụnaftarāh, qul fa`tụ bisụratim miṡlihī wad\'ụ manistaṭa\'tum min dụnillāhi ing kuntum ṣādiqīn'),
(1402, 10, 39, 'بَلْ كَذَّبُوْا بِمَا لَمْ يُحِيْطُوْا بِعِلْمِهٖ وَلَمَّا يَأْتِهِمْ تَأْوِيْلُهٗۗ كَذٰلِكَ كَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الظّٰلِمِيْنَ', 'bal każżabụ bimā lam yuḥīṭụ bi\'ilmihī wa lammā ya`tihim ta`wīluh, każālika każżaballażīna ming qablihim fanẓur kaifa kāna \'āqibatuẓ-ẓālimīn'),
(1403, 10, 40, 'وَمِنْهُمْ مَّنْ يُّؤْمِنُ بِهٖ وَمِنْهُمْ مَّنْ لَّا يُؤْمِنُ بِهٖۗ وَرَبُّكَ اَعْلَمُ بِالْمُفْسِدِيْنَ', 'wa min-hum may yu`minu bihī wa min-hum mal lā yu`minu bih, wa rabbuka a\'lamu bil-mufsidīn'),
(1404, 10, 41, 'وَاِنْ كَذَّبُوْكَ فَقُلْ لِّيْ عَمَلِيْ وَلَكُمْ عَمَلُكُمْۚ اَنْتُمْ بَرِيْۤـُٔوْنَ مِمَّآ اَعْمَلُ وَاَنَا۠ بَرِيْۤءٌ مِّمَّا تَعْمَلُوْنَ', 'wa ing każżabụka fa qul lī \'amalī wa lakum \'amalukum, antum barī`ụna mimmā a\'malu wa ana barī`um mimmā ta\'malụn'),
(1405, 10, 42, 'وَمِنْهُمْ مَّنْ يَّسْتَمِعُوْنَ اِلَيْكَۗ اَفَاَنْتَ تُسْمِعُ الصُّمَّ وَلَوْ كَانُوْا لَا يَعْقِلُوْنَ', 'wa min-hum may yastami\'ụna ilaīk, a fa anta tusmi\'uṣ-ṣumma walau kānụ lā ya\'qilụn'),
(1406, 10, 43, 'وَمِنْهُمْ مَّنْ يَّنْظُرُ اِلَيْكَۗ اَفَاَنْتَ تَهْدِى الْعُمْيَ وَلَوْ كَانُوْا لَا يُبْصِرُوْنَ', 'wa min-hum may yanẓuru ilaīk, a fa anta tahdil-\'umya walau kānụ lā yubṣirụn'),
(1407, 10, 44, 'اِنَّ اللّٰهَ لَا يَظْلِمُ النَّاسَ شَيْـًٔا وَّلٰكِنَّ النَّاسَ اَنْفُسَهُمْ يَظْلِمُوْنَ', 'innallāha lā yaẓlimun-nāsa syai`aw wa lākinnan-nāsa anfusahum yaẓlimụn'),
(1408, 10, 45, 'وَيَوْمَ يَحْشُرُهُمْ كَاَنْ لَّمْ يَلْبَثُوْٓا اِلَّا سَاعَةً مِّنَ النَّهَارِ يَتَعَارَفُوْنَ بَيْنَهُمْۗ قَدْ خَسِرَ الَّذِيْنَ كَذَّبُوْا بِلِقَاۤءِ اللّٰهِ وَمَا كَانُوْا مُهْتَدِيْنَ', 'wa yauma yaḥsyuruhum ka`al lam yalbaṡū illā sā\'atam minan-nahāri yata\'ārafụna bainahum, qad khasirallażīna każżabụ biliqā`illāhi wa mā kānụ muhtadīn'),
(1409, 10, 46, 'وَاِمَّا نُرِيَنَّكَ بَعْضَ الَّذِيْ نَعِدُهُمْ اَوْ نَتَوَفَّيَنَّكَ فَاِلَيْنَا مَرْجِعُهُمْ ثُمَّ اللّٰهُ شَهِيْدٌ عَلٰى مَا يَفْعَلُوْنَ', 'wa immā nuriyannaka ba\'ḍallażī na\'iduhum au natawaffayannaka fa ilainā marji\'uhum ṡummallāhu syahīdun \'alā mā yaf\'alụn'),
(1410, 10, 47, 'وَلِكُلِّ اُمَّةٍ رَّسُوْلٌ ۚفَاِذَا جَاۤءَ رَسُوْلُهُمْ قُضِيَ بَيْنَهُمْ بِالْقِسْطِ وَهُمْ لَا يُظْلَمُوْنَ', 'wa likulli ummatir rasụl, fa iżā jā`a rasụluhum quḍiya bainahum bil-qisṭi wa hum lā yuẓlamụn'),
(1411, 10, 48, 'وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'wa yaqụlụna matā hāżal-wa\'du ing kuntum ṣādiqīn'),
(1412, 10, 49, 'قُلْ لَّآ اَمْلِكُ لِنَفْسِيْ ضَرًّا وَّلَا نَفْعًا اِلَّا مَا شَاۤءَ اللّٰهُ ۗ لِكُلِّ اُمَّةٍ اَجَلٌ ۚاِذَا جَاۤءَ اَجَلُهُمْ فَلَا يَسْتَأْخِرُوْنَ سَاعَةً وَّلَا يَسْتَقْدِمُوْنَ', 'qul lā amliku linafsī ḍarraw wa lā naf\'an illā mā syā`allāh, likulli ummatin ajal, iżā jā`a ajaluhum fa lā yasta`khirụna sā\'ataw wa lā yastaqdimụn'),
(1413, 10, 50, 'قُلْ اَرَءَيْتُمْ اِنْ اَتٰىكُمْ عَذَابُهٗ بَيَاتًا اَوْ نَهَارًا مَّاذَا يَسْتَعْجِلُ مِنْهُ الْمُجْرِمُوْنَ', 'qul a ra`aitum in atākum \'ażābuhụ bayātan au nahāram māżā yasta\'jilu min-hul-mujrimụn'),
(1414, 10, 51, 'اَثُمَّ اِذَا مَا وَقَعَ اٰمَنْتُمْ بِهٖۗ اٰۤلْـٰٔنَ وَقَدْ كُنْتُمْ بِهٖ تَسْتَعْجِلُوْنَ', 'a ṡumma iżā mā waqa\'a āmantum bih, āl-āna waqad kuntum bihī tasta\'jilụn'),
(1415, 10, 52, 'ثُمَّ قِيْلَ لِلَّذِيْنَ ظَلَمُوْا ذُوْقُوْا عَذَابَ الْخُلْدِۚ هَلْ تُجْزَوْنَ اِلَّا بِمَا كُنْتُمْ تَكْسِبُوْنَ', 'ṡumma qīla lillażīna ẓalamụ żụqụ \'ażābal-khuld, hal tujzauna illā bimā kuntum taksibụn'),
(1416, 10, 53, 'وَيَسْتَنْۢبِـُٔوْنَكَ اَحَقٌّ هُوَ ۗ قُلْ اِيْ وَرَبِّيْٓ اِنَّهٗ لَحَقٌّ ۗوَمَآ اَنْتُمْ بِمُعْجِزِيْنَ', 'wa yastambi`ụnaka aḥaqqun huw, qul ī wa rabbī innahụ laḥaqq, wa mā antum bimu\'jizīn'),
(1417, 10, 54, 'وَلَوْ اَنَّ لِكُلِّ نَفْسٍ ظَلَمَتْ مَا فِى الْاَرْضِ لَافْتَدَتْ بِهٖۗ وَاَسَرُّوا النَّدَامَةَ لَمَّا رَاَوُا الْعَذَابَۚ وَقُضِيَ بَيْنَهُمْ بِالْقِسْطِ وَهُمْ لَا يُظْلَمُوْنَ', 'walau anna likulli nafsin ẓalamat mā fil-arḍi laftadat bih, wa asarrun-nadāmata lammā ra`awul-\'ażāb, wa quḍiya bainahum bil-qisṭi wa hum lā yuẓlamụn'),
(1418, 10, 55, 'اَلَآ اِنَّ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ اَلَآ اِنَّ وَعْدَ اللّٰهِ حَقٌّ وَّلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ', 'alā inna lillāhi mā fis-samāwāti wal-arḍ, alā inna wa\'dallāhi ḥaqquw wa lākinna akṡarahum lā ya\'lamụn'),
(1419, 10, 56, 'هُوَ يُحْيٖ وَيُمِيْتُ وَاِلَيْهِ تُرْجَعُوْنَ', 'huwa yuḥyī wa yumītu wa ilaihi turja\'ụn'),
(1420, 10, 57, 'يٰٓاَيُّهَا النَّاسُ قَدْ جَاۤءَتْكُمْ مَّوْعِظَةٌ مِّنْ رَّبِّكُمْ وَشِفَاۤءٌ لِّمَا فِى الصُّدُوْرِۙ وَهُدًى وَّرَحْمَةٌ لِّلْمُؤْمِنِيْنَ', 'yā ayyuhan-nāsu qad jā`atkum mau\'iẓatum mir rabbikum wa syifā`ul limā fiṣ-ṣudụri wa hudaw wa raḥmatul lil-mu`minīn'),
(1421, 10, 58, 'قُلْ بِفَضْلِ اللّٰهِ وَبِرَحْمَتِهٖ فَبِذٰلِكَ فَلْيَفْرَحُوْاۗ هُوَ خَيْرٌ مِّمَّا يَجْمَعُوْنَ', 'qul bifaḍlillāhi wa biraḥmatihī fa biżālika falyafraḥụ, huwa khairum mimmā yajma\'ụn'),
(1422, 10, 59, 'قُلْ اَرَءَيْتُمْ مَّآ اَنْزَلَ اللّٰهُ لَكُمْ مِّنْ رِّزْقٍ فَجَعَلْتُمْ مِّنْهُ حَرَامًا وَّحَلٰلًا ۗ قُلْ اٰۤللّٰهُ اَذِنَ لَكُمْ اَمْ عَلَى اللّٰهِ تَفْتَرُوْنَ', 'qul a ra`aitum mā anzalallāhu lakum mir rizqin fa ja\'altum min-hu ḥarāmaw wa halālā, qul āllāhu ażina lakum am \'alallāhi taftarụn'),
(1423, 10, 60, 'وَمَا ظَنُّ الَّذِيْنَ يَفْتَرُوْنَ عَلَى اللّٰهِ الْكَذِبَ يَوْمَ الْقِيٰمَةِ ۗاِنَّ اللّٰهَ لَذُوْ فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَشْكُرُوْنَ', 'wa mā ẓannullażīna yaftarụna \'alallāhil-każiba yaumal-qiyāmah, innallāha lażụ faḍlin \'alan-nāsi wa lākinna akṡarahum lā yasykurụn'),
(1424, 10, 61, 'وَمَا تَكُوْنُ فِيْ شَأْنٍ وَّمَا تَتْلُوْا مِنْهُ مِنْ قُرْاٰنٍ وَّلَا تَعْمَلُوْنَ مِنْ عَمَلٍ اِلَّا كُنَّا عَلَيْكُمْ شُهُوْدًا اِذْ تُفِيْضُوْنَ فِيْهِۗ وَمَا يَعْزُبُ عَنْ رَّبِّكَ مِنْ مِّثْقَالِ ذَرَّةٍ فِى الْاَرْضِ وَلَا فِى السَّمَاۤءِ وَلَآ اَصْغَرَ مِنْ ذٰلِكَ وَلَآ اَكْبَرَ اِلَّا فِيْ كِتٰبٍ مُّبِيْنٍ', 'wa mā takụnu fī sya`niw wa mā tatlụ min-hu ming qur`āniw wa lā ta\'malụna min \'amalin illā kunnā \'alaikum syuhụdan iż tufīḍụna fīh, wa mā ya\'zubu \'ar rabbika mim miṡqāli żarratin fil-arḍi wa lā fis-samā`i wa lā aṣgara min żālika wa lā akbara illā fī kitābim mubīn'),
(1425, 10, 62, 'اَلَآ اِنَّ اَوْلِيَاۤءَ اللّٰهِ لَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَۚ', 'alā inna auliyā`allāhi lā khaufun \'alaihim wa lā hum yaḥzanụn'),
(1426, 10, 63, 'اَلَّذِيْنَ اٰمَنُوْا وَكَانُوْا يَتَّقُوْنَۗ', 'allażīna āmanụ wa kānụ yattaqụn'),
(1427, 10, 64, 'لَهُمُ الْبُشْرٰى فِى الْحَيٰوةِ الدُّنْيَا وَفِى الْاٰخِرَةِۗ لَا تَبْدِيْلَ لِكَلِمٰتِ اللّٰهِ ۗذٰلِكَ هُوَ الْفَوْزُ الْعَظِيْمُۗ', 'lahumul-busyrā fil-ḥayātid-dun-yā wa fil-ākhirah, lā tabdīla likalimātillāh, żālika huwal-fauzul-\'aẓīm'),
(1428, 10, 65, 'وَلَا يَحْزُنْكَ قَوْلُهُمْۘ اِنَّ الْعِزَّةَ لِلّٰهِ جَمِيْعًاۗ هُوَ السَّمِيْعُ الْعَلِيْمُ', 'wa lā yaḥzungka qauluhum, innal-\'izzata lillāhi jamī\'ā, huwas-samī\'ul-\'alīm'),
(1429, 10, 66, 'اَلَآ اِنَّ لِلّٰهِ مَنْ فِى السَّمٰوٰتِ وَمَنْ فِى الْاَرْضِۗ وَمَا يَتَّبِعُ الَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ شُرَكَاۤءَ ۗاِنْ يَّتَّبِعُوْنَ اِلَّا الظَّنَّ وَاِنْ هُمْ اِلَّا يَخْرُصُوْنَ', 'alā inna lillāhi man fis-samāwāti wa man fil-arḍ, wa mā yattabi\'ullażīna yad\'ụna min dụnillāhi syurakā`, iy yattabi\'ụna illaẓ-ẓanna wa in hum illā yakhruṣụn'),
(1430, 10, 67, 'هُوَ الَّذِيْ جَعَلَ لَكُمُ الَّيْلَ لِتَسْكُنُوْا فِيْهِ وَالنَّهَارَ مُبْصِرًا ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّسْمَعُوْنَ', 'huwallażī ja\'ala lakumul-laila litaskunụ fīhi wan-nahāra mubṣirā, inna fī żālika la`āyātil liqaumiy yasma\'ụn'),
(1431, 10, 68, 'قَالُوا اتَّخَذَ اللّٰهُ وَلَدًا سُبْحٰنَهٗ ۗ هُوَ الْغَنِيُّ ۗ لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ اِنْ عِنْدَكُمْ مِّنْ سُلْطٰنٍۢ بِهٰذَاۗ اَتَقُوْلُوْنَ عَلَى اللّٰهِ مَا لَا تَعْلَمُوْنَ', 'qāluttakhażallāhu waladan sub-ḥānah, huwal-ganiyy, lahụ mā fis-samāwāti wa mā fil-arḍ, in \'indakum min sulṭānim bihāżā, a taqụlụna \'alallāhi mā lā ta\'lamụn'),
(1432, 10, 69, 'قُلْ اِنَّ الَّذِيْنَ يَفْتَرُوْنَ عَلَى اللّٰهِ الْكَذِبَ لَا يُفْلِحُوْنَۗ', 'qul innallażīna yaftarụna \'alallāhil-każiba lā yufliḥụn'),
(1433, 10, 70, 'مَتَاعٌ فِى الدُّنْيَا ثُمَّ اِلَيْنَا مَرْجِعُهُمْ ثُمَّ نُذِيْقُهُمُ الْعَذَابَ الشَّدِيْدَ بِمَا كَانُوْا يَكْفُرُوْنَ', 'matā\'un fid-dun-yā ṡumma ilainā marji\'uhum ṡumma nużīquhumul-\'ażābasy-syadīda bimā kānụ yakfurụn'),
(1434, 10, 71, '۞ وَاتْلُ عَلَيْهِمْ نَبَاَ نُوْحٍۘ اِذْ قَالَ لِقَوْمِهٖ يٰقَوْمِ اِنْ كَانَ كَبُرَ عَلَيْكُمْ مَّقَامِيْ وَتَذْكِيْرِيْ بِاٰيٰتِ اللّٰهِ فَعَلَى اللّٰهِ تَوَكَّلْتُ فَاَجْمِعُوْٓا اَمْرَكُمْ وَشُرَكَاۤءَكُمْ ثُمَّ لَا يَكُنْ اَمْرُكُمْ عَلَيْكُمْ غُمَّةً ثُمَّ اقْضُوْٓا اِلَيَّ وَلَا تُنْظِرُوْنِ', 'watlu \'alaihim naba`a nụḥ, iż qāla liqaumihī yā qaumi ing kāna kabura \'alaikum maqāmī wa tażkīrī bi`āyātillāhi fa \'alallāhi tawakkaltu fa ajmi\'ū amrakum wa syurakā`akum ṡumma lā yakun amrukum \'alaikum gummatan ṡummaqḍū ilayya wa lā tunẓirụn'),
(1435, 10, 72, 'فَاِنْ تَوَلَّيْتُمْ فَمَا سَاَلْتُكُمْ مِّنْ اَجْرٍۗ اِنْ اَجْرِيَ اِلَّا عَلَى اللّٰهِ ۙوَاُمِرْتُ اَنْ اَكُوْنَ مِنَ الْمُسْلِمِيْنَ', 'fa in tawallaitum fa mā sa`altukum min ajr, in ajriya illā \'alallāhi wa umirtu an akụna minal-muslimīn'),
(1436, 10, 73, 'فَكَذَّبُوْهُ فَنَجَّيْنٰهُ وَمَنْ مَّعَهٗ فِى الْفُلْكِ وَجَعَلْنٰهُمْ خَلٰۤىِٕفَ وَاَغْرَقْنَا الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَاۚ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُنْذَرِيْنَ', 'fa każżabụhu fa najjaināhu wa mam ma\'ahụ fil-fulki wa ja\'alnāhum khalā`ifa wa agraqnallażīna każżabụ bi`āyātinā, fanẓur kaifa kāna \'āqibatul-munżarīn'),
(1437, 10, 74, 'ثُمَّ بَعَثْنَا مِنْۢ بَعْدِهٖ رُسُلًا اِلٰى قَوْمِهِمْ فَجَاۤءُوْهُمْ بِالْبَيِّنٰتِ فَمَا كَانُوْا لِيُؤْمِنُوْا بِمَا كَذَّبُوْا بِهٖ مِنْ قَبْلُ ۗ كَذٰلِكَ نَطْبَعُ عَلٰى قُلُوْبِ الْمُعْتَدِيْنَ', 'ṡumma ba\'aṡnā mim ba\'dihī rusulan ilā qaumihim fa jā`ụhum bil-bayyināti fa mā kānụ liyu`minụ bimā każżabụ bihī ming qabl, każālika naṭba\'u \'alā qulụbil-mu\'tadīn'),
(1438, 10, 75, 'ثُمَّ بَعَثْنَا مِنْۢ بَعْدِهِمْ مُّوْسٰى وَهٰرُوْنَ اِلٰى فِرْعَوْنَ وَمَلَا۟ىِٕهٖ بِاٰيٰتِنَا فَاسْتَكْبَرُوْا وَكَانُوْا قَوْمًا مُّجْرِمِيْنَ', 'ṡumma ba\'aṡnā mim ba\'dihim mụsā wa hārụna ilā fir\'auna wa mala`ihī bi`āyātinā fastakbarụ wa kānụ qaumam mujrimīn'),
(1439, 10, 76, 'فَلَمَّا جَاۤءَهُمُ الْحَقُّ مِنْ عِنْدِنَا قَالُوْٓا اِنَّ هٰذَا لَسِحْرٌ مُّبِيْنٌ', 'fa lammā jā`ahumul-ḥaqqu min \'indinā qālū inna hāżā lasiḥrum mubīn'),
(1440, 10, 77, 'قَالَ مُوْسٰٓى اَتَقُوْلُوْنَ لِلْحَقِّ لَمَّا جَاۤءَكُمْ ۗ اَسِحْرٌ هٰذَاۗ وَلَا يُفْلِحُ السَّاحِرُوْنَ', 'qāla mụsā a taqụlụna lil-ḥaqqi lammā jā`akum, a siḥrun hāżā, wa lā yufliḥus-sāḥirụn'),
(1441, 10, 78, 'قَالُوْٓا اَجِئْتَنَا لِتَلْفِتَنَا عَمَّا وَجَدْنَا عَلَيْهِ اٰبَاۤءَنَا وَتَكُوْنَ لَكُمَا الْكِبْرِيَاۤءُ فِى الْاَرْضِۗ وَمَا نَحْنُ لَكُمَا بِمُؤْمِنِيْنَ', 'qālū a ji`tanā litalfitanā \'ammā wajadnā \'alaihi ābā`anā wa takụna lakumal-kibriyā`u fil-arḍ, wa mā naḥnu lakumā bimu`minīn'),
(1442, 10, 79, 'وَقَالَ فِرْعَوْنُ ائْتُوْنِيْ بِكُلِّ سٰحِرٍ عَلِيْمٍ', 'wa qāla fir\'aunu`tụnī bikulli sāḥirin \'alīm'),
(1443, 10, 80, 'فَلَمَّا جَاۤءَ السَّحَرَةُ قَالَ لَهُمْ مُّوْسٰٓى اَلْقُوْا مَآ اَنْتُمْ مُّلْقُوْنَ', 'fa lammā jā`as-saḥaratu qāla lahum mụsā alqụ mā antum mulqụn'),
(1444, 10, 81, 'فَلَمَّآ اَلْقَوْا قَالَ مُوْسٰى مَا جِئْتُمْ بِهِ ۙالسِّحْرُۗ اِنَّ اللّٰهَ سَيُبْطِلُهٗۗ اِنَّ اللّٰهَ لَا يُصْلِحُ عَمَلَ الْمُفْسِدِيْنَ', 'fa lammā alqau qāla mụsā mā ji`tum bihis-siḥr, innallāha sayubṭiluh, innallāha lā yuṣliḥu \'amalal-mufsidīn'),
(1445, 10, 82, 'وَيُحِقُّ اللّٰهُ الْحَقَّ بِكَلِمٰتِهٖ وَلَوْ كَرِهَ الْمُجْرِمُوْنَ', 'wa yuḥiqqullāhul-ḥaqqa bikalimātihī walau karihal-mujrimụn');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(1446, 10, 83, 'فَمَآ اٰمَنَ لِمُوْسٰىٓ اِلَّا ذُرِّيَّةٌ مِّنْ قَوْمِهٖ عَلٰى خَوْفٍ مِّنْ فِرْعَوْنَ وَمَلَا۟ىِٕهِمْ اَنْ يَّفْتِنَهُمْ ۗوَاِنَّ فِرْعَوْنَ لَعَالٍ فِى الْاَرْضِۚ وَاِنَّهٗ لَمِنَ الْمُسْرِفِيْنَ', 'fa mā āmana limụsā illā żurriyyatum ming qaumihī \'alā khaufim min fir\'auna wa mala`ihim ay yaftinahum, wa inna fir\'auna la\'ālin fil-arḍ, wa innahụ laminal-musrifīn'),
(1447, 10, 84, 'وَقَالَ مُوْسٰى يٰقَوْمِ اِنْ كُنْتُمْ اٰمَنْتُمْ بِاللّٰهِ فَعَلَيْهِ تَوَكَّلُوْٓا اِنْ كُنْتُمْ مُّسْلِمِيْنَ', 'wa qāla mụsā yā qaumi ing kuntum āmantum billāhi fa \'alaihi tawakkalū ing kuntum muslimīn'),
(1448, 10, 85, 'فَقَالُوْا عَلَى اللّٰهِ تَوَكَّلْنَا ۚرَبَّنَا لَا تَجْعَلْنَا فِتْنَةً لِّلْقَوْمِ الظّٰلِمِيْنَ', 'fa qālụ \'alallāhi tawakkalnā, rabbanā lā taj\'alnā fitnatal lil-qaumiẓ-ẓālimīn'),
(1449, 10, 86, 'وَنَجِّنَا بِرَحْمَتِكَ مِنَ الْقَوْمِ الْكٰفِرِيْنَ', 'wa najjinā biraḥmatika minal-qaumil-kāfirīn'),
(1450, 10, 87, 'وَاَوْحَيْنَآ اِلٰى مُوْسٰى وَاَخِيْهِ اَنْ تَبَوَّاٰ لِقَوْمِكُمَا بِمِصْرَ بُيُوْتًا وَّاجْعَلُوْا بُيُوْتَكُمْ قِبْلَةً وَّاَقِيْمُوا الصَّلٰوةَۗ وَبَشِّرِ الْمُؤْمِنِيْنَ', 'wa auḥainā ilā mụsā wa akhīhi an tabawwa`ā liqaumikumā bimiṣra buyụtaw waj\'alụ buyụtakum qiblataw wa aqīmuṣ-ṣalāh, wa basysyiril-mu`minīn'),
(1451, 10, 88, 'وَقَالَ مُوْسٰى رَبَّنَآ اِنَّكَ اٰتَيْتَ فِرْعَوْنَ وَمَلَاَهٗ زِيْنَةً وَّاَمْوَالًا فِى الْحَيٰوةِ الدُّنْيَاۗ رَبَّنَا لِيُضِلُّوْا عَنْ سَبِيْلِكَ ۚرَبَّنَا اطْمِسْ عَلٰٓى اَمْوَالِهِمْ وَاشْدُدْ عَلٰى قُلُوْبِهِمْ فَلَا يُؤْمِنُوْا حَتّٰى يَرَوُا الْعَذَابَ الْاَلِيْمَ', 'wa qāla mụsā rabbanā innaka ātaita fir\'auna wa mala`ahụ zīnataw wa amwālan fil-ḥayātid-dun-yā, rabbanā liyuḍillụ \'an sabīlik, rabbanaṭmis \'alā amwālihim wasydud \'alā qulụbihim fa lā yu`minụ ḥattā yarawul-\'ażābal-alīm'),
(1452, 10, 89, 'قَالَ قَدْ اُجِيْبَتْ دَّعْوَتُكُمَا فَاسْتَقِيْمَا وَلَا تَتَّبِعٰۤنِّ سَبِيْلَ الَّذِيْنَ لَا يَعْلَمُوْنَ', 'qāla qad ujībad da\'watukumā fastaqīmā wa lā tattabi\'ānni sabīlallażīna lā ya\'lamụn'),
(1453, 10, 90, '۞ وَجَاوَزْنَا بِبَنِيْٓ اِسْرَاۤءِيْلَ الْبَحْرَ فَاَتْبَعَهُمْ فِرْعَوْنُ وَجُنُوْدُهٗ بَغْيًا وَّعَدْوًا ۗحَتّٰىٓ اِذَآ اَدْرَكَهُ الْغَرَقُ قَالَ اٰمَنْتُ اَنَّهٗ لَآ اِلٰهَ اِلَّا الَّذِيْٓ اٰمَنَتْ بِهٖ بَنُوْٓا اِسْرَاۤءِيْلَ وَاَنَا۠ مِنَ الْمُسْلِمِيْنَ', 'wa jāwaznā bibanī isrā`īlal-baḥra fa atba\'ahum fir\'aunu wa junụduhụ bagyaw wa \'adwā, ḥattā iżā adrakahul-garaqu qāla āmantu annahụ lā ilāha illallażī āmanat bihī banū isrā`īla wa ana minal-muslimīn'),
(1454, 10, 91, 'اٰۤلْـٰٔنَ وَقَدْ عَصَيْتَ قَبْلُ وَكُنْتَ مِنَ الْمُفْسِدِيْنَ', 'āl-āna wa qad \'aṣaita qablu wa kunta minal-mufsidīn'),
(1455, 10, 92, 'فَالْيَوْمَ نُنَجِّيْكَ بِبَدَنِكَ لِتَكُوْنَ لِمَنْ خَلْفَكَ اٰيَةً ۗوَاِنَّ كَثِيْرًا مِّنَ النَّاسِ عَنْ اٰيٰتِنَا لَغٰفِلُوْنَ', 'fal-yauma nunajjīka bibadanika litakụna liman khalfaka āyah, wa inna kaṡīram minan-nāsi \'an āyātinā lagāfilụn'),
(1456, 10, 93, 'وَلَقَدْ بَوَّأْنَا بَنِيْٓ اِسْرَاۤءِيْلَ مُبَوَّاَ صِدْقٍ وَّرَزَقْنٰهُمْ مِّنَ الطَّيِّبٰتِ ۚفَمَا اخْتَلَفُوْا حَتّٰى جَاۤءَهُمُ الْعِلْمُ ۗاِنَّ رَبَّكَ يَقْضِيْ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كَانُوْا فِيْهِ يَخْتَلِفُوْنَ', 'wa laqad bawwa`nā banī isrā`īla mubawwa`a ṣidqiw wa razaqnāhum minaṭ-ṭayyibāt, famakhtalafụ ḥattā jā`ahumul-\'ilm, inna rabbaka yaqḍī bainahum yaumal-qiyāmati fīmā kānụ fīhi yakhtalifụn'),
(1457, 10, 94, 'فَاِنْ كُنْتَ فِيْ شَكٍّ مِّمَّآ اَنْزَلْنَآ اِلَيْكَ فَسْـَٔلِ الَّذِيْنَ يَقْرَءُوْنَ الْكِتٰبَ مِنْ قَبْلِكَ ۚ لَقَدْ جَاۤءَكَ الْحَقُّ مِنْ رَّبِّكَ فَلَا تَكُوْنَنَّ مِنَ الْمُمْتَرِيْنَۙ', 'fa ing kunta fī syakkim mimmā anzalnā ilaika fas`alillażīna yaqra`ụnal-kitāba ming qablik, laqad jā`akal-ḥaqqu mir rabbika fa lā takụnanna minal-mumtarīn'),
(1458, 10, 95, 'وَلَا تَكُوْنَنَّ مِنَ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِ اللّٰهِ فَتَكُوْنَ مِنَ الْخٰسِرِيْنَ', 'wa lā takụnanna minallażīna każżabụ bi`āyātillāhi fa takụna minal-khāsirīn'),
(1459, 10, 96, 'اِنَّ الَّذِيْنَ حَقَّتْ عَلَيْهِمْ كَلِمَةُ رَبِّكَ لَا يُؤْمِنُوْنَ', 'innallażīna ḥaqqat \'alaihim kalimatu rabbika lā yu`minụn'),
(1460, 10, 97, 'وَلَوْ جَاۤءَتْهُمْ كُلُّ اٰيَةٍ حَتّٰى يَرَوُا الْعَذَابَ الْاَلِيْمَ', 'walau jā`at-hum kullu āyatin ḥattā yarawul-\'ażābal-alīm'),
(1461, 10, 98, 'فَلَوْلَا كَانَتْ قَرْيَةٌ اٰمَنَتْ فَنَفَعَهَآ اِيْمَانُهَآ اِلَّا قَوْمَ يُوْنُسَۗ لَمَّآ اٰمَنُوْا كَشَفْنَا عَنْهُمْ عَذَابَ الْخِزْيِ فِى الْحَيٰوةِ الدُّنْيَا وَمَتَّعْنٰهُمْ اِلٰى حِيْنٍ', 'falau lā kānat qaryatun āmanat fa nafa\'ahā īmānuhā illā qauma yụnus, lammā āmanụ kasyafnā \'an-hum \'ażābal-khizyi fil-ḥayātid-dun-yā wa matta\'nāhum ilā ḥīn'),
(1462, 10, 99, 'وَلَوْ شَاۤءَ رَبُّكَ لَاٰمَنَ مَنْ فِى الْاَرْضِ كُلُّهُمْ جَمِيْعًاۗ اَفَاَنْتَ تُكْرِهُ النَّاسَ حَتّٰى يَكُوْنُوْا مُؤْمِنِيْنَ', 'walau syā`a rabbuka la`āmana man fil-arḍi kulluhum jamī\'ā, a fa anta tukrihun-nāsa ḥattā yakụnụ mu`minīn'),
(1463, 10, 100, 'وَمَا كَانَ لِنَفْسٍ اَنْ تُؤْمِنَ اِلَّا بِاِذْنِ اللّٰهِ ۗوَيَجْعَلُ الرِّجْسَ عَلَى الَّذِيْنَ لَا يَعْقِلُوْنَ', 'wa mā kāna linafsin an tu`mina illā bi`iżnillāh, wa yaj\'alur-rijsa \'alallażīna lā ya\'qilụn'),
(1464, 10, 101, 'قُلِ انْظُرُوْا مَاذَا فِى السَّمٰوٰتِ وَالْاَرْضِ ۗوَمَا تُغْنِى الْاٰيٰتُ وَالنُّذُرُ عَنْ قَوْمٍ لَّا يُؤْمِنُوْنَ', 'qulinẓurụ māżā fis-samāwāti wal-arḍ, wa mā tugnil-āyātu wan-nużuru \'ang qaumil lā yu`minụn'),
(1465, 10, 102, 'فَهَلْ يَنْتَظِرُوْنَ اِلَّا مِثْلَ اَيَّامِ الَّذِيْنَ خَلَوْا مِنْ قَبْلِهِمْۗ قُلْ فَانْتَظِرُوْٓا اِنِّيْ مَعَكُمْ مِّنَ الْمُنْتَظِرِيْنَ', 'fa hal yantaẓirụna illā miṡla ayyāmillażīna khalau ming qablihim, qul fantaẓirū innī ma\'akum minal-muntaẓirīn'),
(1466, 10, 103, 'ثُمَّ نُنَجِّيْ رُسُلَنَا وَالَّذِيْنَ اٰمَنُوْا كَذٰلِكَ ۚحَقًّا عَلَيْنَا نُنْجِ الْمُؤْمِنِيْنَ', 'ṡumma nunajjī rusulana wallażīna āmanụ każālik, ḥaqqan \'alainā nunjil-mu`minīn'),
(1467, 10, 104, 'قُلْ يٰٓاَيُّهَا النَّاسُ اِنْ كُنْتُمْ فِيْ شَكٍّ مِّنْ دِيْنِيْ فَلَآ اَعْبُدُ الَّذِيْنَ تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ وَلٰكِنْ اَعْبُدُ اللّٰهَ الَّذِيْ يَتَوَفّٰىكُمْ ۖ وَاُمِرْتُ اَنْ اَكُوْنَ مِنَ الْمُؤْمِنِيْنَ', 'qul yā ayyuhan-nāsu ing kuntum fī syakkim min dīnī fa lā a\'budullażīna ta\'budụna min dụnillāhi wa lākin a\'budullāhallażī yatawaffākum wa umirtu an akụna minal-mu`minīn'),
(1468, 10, 105, 'وَاَنْ اَقِمْ وَجْهَكَ لِلدِّيْنِ حَنِيْفًاۚ وَلَا تَكُوْنَنَّ مِنَ الْمُشْرِكِيْنَ', 'wa an aqim waj-haka lid-dīni ḥanīfā, wa lā takụnanna minal-musyrikīn'),
(1469, 10, 106, 'وَلَا تَدْعُ مِنْ دُوْنِ اللّٰهِ مَا لَا يَنْفَعُكَ وَلَا يَضُرُّكَ ۚفَاِنْ فَعَلْتَ فَاِنَّكَ اِذًا مِّنَ الظّٰلِمِيْنَ', 'wa lā tad\'u min dụnillāhi mā lā yanfa\'uka wa lā yaḍurruk, fa in fa\'alta fa innaka iżam minaẓ-ẓālimīn'),
(1470, 10, 107, 'وَاِنْ يَّمْسَسْكَ اللّٰهُ بِضُرٍّ فَلَا كَاشِفَ لَهٗ ٓاِلَّا هُوَ ۚوَاِنْ يُّرِدْكَ بِخَيْرٍ فَلَا رَاۤدَّ لِفَضْلِهٖۗ يُصِيْبُ بِهٖ مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ ۗوَهُوَ الْغَفُوْرُ الرَّحِيْمُ', 'wa iy yamsaskallāhu biḍurrin fa lā kāsyifa lahū illā huw, wa iy yuridka bikhairin fa lā rādda lifaḍlih, yuṣību bihī may yasyā`u min \'ibādih, wa huwal-gafụrur-raḥīm'),
(1471, 10, 108, 'قُلْ يٰٓاَيُّهَا النَّاسُ قَدْ جَاۤءَكُمُ الْحَقُّ مِنْ رَّبِّكُمْ ۚفَمَنِ اهْتَدٰى فَاِنَّمَا يَهْتَدِيْ لِنَفْسِهٖ ۚوَمَنْ ضَلَّ فَاِنَّمَا يَضِلُّ عَلَيْهَا ۚوَمَآ اَنَا۠ عَلَيْكُمْ بِوَكِيْلٍۗ', 'qul yā ayyuhan-nāsu qad jā`akumul-ḥaqqu mir rabbikum, fa manihtadā fa innamā yahtadī linafsih, wa man ḍalla fa innamā yaḍillu \'alaihā, wa mā ana \'alaikum biwakīl'),
(1472, 10, 109, 'وَاتَّبِعْ مَا يُوْحٰىٓ اِلَيْكَ وَاصْبِرْ حَتّٰى يَحْكُمَ اللّٰهُ ۚوَهُوَ خَيْرُ الْحٰكِمِيْنَ', 'wattabi\' mā yụḥā ilaika waṣbir ḥattā yaḥkumallāh, wa huwa khairul-ḥākimīn'),
(1473, 11, 1, 'الۤرٰ ۗ كِتٰبٌ اُحْكِمَتْ اٰيٰتُهٗ ثُمَّ فُصِّلَتْ مِنْ لَّدُنْ حَكِيْمٍ خَبِيْرٍۙ', 'alif lām rā, kitābun uḥkimat āyātuhụ ṡumma fuṣṣilat mil ladun ḥakīmin khabīr'),
(1474, 11, 2, 'اَلَّا تَعْبُدُوْٓا اِلَّا اللّٰهَ ۗاِنَّنِيْ لَكُمْ مِّنْهُ نَذِيْرٌ وَّبَشِيْرٌۙ', 'allā ta\'budū illallāh, innanī lakum min-hu nażīruw wa basyīr'),
(1475, 11, 3, 'وَّاَنِ اسْتَغْفِرُوْا رَبَّكُمْ ثُمَّ تُوْبُوْٓا اِلَيْهِ يُمَتِّعْكُمْ مَّتَاعًا حَسَنًا اِلٰٓى اَجَلٍ مُّسَمًّى وَّيُؤْتِ كُلَّ ذِيْ فَضْلٍ فَضْلَهٗ ۗوَاِنْ تَوَلَّوْا فَاِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ كَبِيْرٍ', 'wa anistagfirụ rabbakum ṡumma tụbū ilaihi yumatti\'kum matā\'an ḥasanan ilā ajalim musamman wa yu`ti kulla żī faḍlin faḍlah, wa in tawallau fa innī akhāfu \'alaikum \'ażāba yauming kabīr'),
(1476, 11, 4, 'اِلَى اللّٰهِ مَرْجِعُكُمْ ۚوَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'ilallāhi marji\'ukum, wa huwa \'alā kulli syai`ing qadīr'),
(1477, 11, 5, 'اَلَآ اِنَّهُمْ يَثْنُوْنَ صُدُوْرَهُمْ لِيَسْتَخْفُوْا مِنْهُۗ اَلَا حِيْنَ يَسْتَغْشُوْنَ ثِيَابَهُمْ ۙيَعْلَمُ مَا يُسِرُّوْنَ وَمَا يُعْلِنُوْنَۚ اِنَّهٗ عَلِيْمٌ ۢ بِذَاتِ الصُّدُوْرِ ۔', 'alā innahum yaṡnụna ṣudụrahum liyastakhfụ min-h, alā ḥīna yastagsyụna ṡiyābahum ya\'lamu mā yusirrụna wa mā yu\'linụn, innahụ \'alīmum biżātiṣ-ṣudụr'),
(1478, 11, 6, '۞ وَمَا مِنْ دَاۤبَّةٍ فِى الْاَرْضِ اِلَّا عَلَى اللّٰهِ رِزْقُهَا وَيَعْلَمُ مُسْتَقَرَّهَا وَمُسْتَوْدَعَهَا ۗ كُلٌّ فِيْ كِتٰبٍ مُّبِيْنٍ', 'wa mā min dābbatin fil-arḍi illā \'alallāhi rizquhā wa ya\'lamu mustaqarrahā wa mustauda\'ahā, kullun fī kitābim mubīn'),
(1479, 11, 7, 'وَهُوَ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ فِيْ سِتَّةِ اَيَّامٍ وَّكَانَ عَرْشُهٗ عَلَى الْمَاۤءِ لِيَبْلُوَكُمْ اَيُّكُمْ اَحْسَنُ عَمَلًا ۗوَلَىِٕنْ قُلْتَ اِنَّكُمْ مَّبْعُوْثُوْنَ مِنْۢ بَعْدِ الْمَوْتِ لَيَقُوْلَنَّ الَّذِيْنَ كَفَرُوْٓا اِنْ هٰذَٓا اِلَّا سِحْرٌ مُّبِيْنٌ', 'wa huwallażī khalaqas-samāwāti wal-arḍa fī sittati ayyāmiw wa kāna \'arsyuhụ \'alal-mā`i liyabluwakum ayyukum aḥsanu \'amalā, wa la`ing qulta innakum mab\'ụṡụna mim ba\'dil-mauti layaqụlannallażīna kafarū in hāżā illā siḥrum mubīn'),
(1480, 11, 8, 'وَلَىِٕنْ اَخَّرْنَا عَنْهُمُ الْعَذَابَ اِلٰٓى اُمَّةٍ مَّعْدُوْدَةٍ لَّيَقُوْلُنَّ مَا يَحْبِسُهٗ ۗ اَلَا يَوْمَ يَأْتِيْهِمْ لَيْسَ مَصْرُوْفًا عَنْهُمْ وَحَاقَ بِهِمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ', 'wa la`in akhkharnā \'an-humul-\'ażāba ilā ummatim ma\'dụdatil layaqụlunna mā yaḥbisuh, alā yauma ya`tīhim laisa maṣrụfan \'an-hum wa ḥāqa bihim mā kānụ bihī yastahzi`ụn'),
(1481, 11, 9, 'وَلَىِٕنْ اَذَقْنَا الْاِنْسَانَ مِنَّا رَحْمَةً ثُمَّ نَزَعْنٰهَا مِنْهُۚ اِنَّهٗ لَيَـُٔوْسٌ كَفُوْرٌ', 'wa la`in ażaqnal-insāna minnā raḥmatan ṡumma naza\'nāhā min-h, innahụ laya`ụsung kafụr'),
(1482, 11, 10, 'وَلَىِٕنْ اَذَقْنٰهُ نَعْمَاۤءَ بَعْدَ ضَرَّاۤءَ مَسَّتْهُ لَيَقُوْلَنَّ ذَهَبَ السَّيِّاٰتُ عَنِّيْ ۗاِنَّهٗ لَفَرِحٌ فَخُوْرٌۙ', 'wa la`in ażaqnāhu na\'mā`a ba\'da ḍarrā`a massat-hu layaqụlanna żahabas-sayyi`ātu \'annī, innahụ lafariḥun fakhụr'),
(1483, 11, 11, 'اِلَّا الَّذِيْنَ صَبَرُوْا وَعَمِلُوا الصّٰلِحٰتِۗ اُولٰۤىِٕكَ لَهُمْ مَّغْفِرَةٌ وَّاَجْرٌ كَبِيْرٌ', 'illallażīna ṣabarụ wa \'amiluṣ-ṣāliḥāt, ulā`ika lahum magfiratuw wa ajrung kabīr'),
(1484, 11, 12, 'فَلَعَلَّكَ تَارِكٌۢ بَعْضَ مَا يُوْحٰىٓ اِلَيْكَ وَضَاۤىِٕقٌۢ بِهٖ صَدْرُكَ اَنْ يَّقُوْلُوْا لَوْلَآ اُنْزِلَ عَلَيْهِ كَنْزٌ اَوْ جَاۤءَ مَعَهٗ مَلَكٌ ۗاِنَّمَآ اَنْتَ نَذِيْرٌ ۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ وَّكِيْلٌ ۗ', 'fa la\'allaka tārikum ba\'ḍa mā yụḥā ilaika wa ḍā`iqum bihī ṣadruka ay yaqụlụ lau lā unzila \'alaihi kanzun au jā`a ma\'ahụ malak, innamā anta nażīr, wallāhu \'alā kulli syai`iw wakīl'),
(1485, 11, 13, 'اَمْ يَقُوْلُوْنَ افْتَرٰىهُ ۗقُلْ فَأْتُوْا بِعَشْرِ سُوَرٍ مِّثْلِهٖ مُفْتَرَيٰتٍ وَّادْعُوْا مَنِ اسْتَطَعْتُمْ مِّنْ دُوْنِ اللّٰهِ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'am yaqụlụnaftarāh, qul fa`tụ bi\'asyri suwarim miṡlihī muftarayātiw wad\'ụ manistaṭa\'tum min dụnillāhi ing kuntum ṣādiqīn'),
(1486, 11, 14, 'فَاِلَّمْ يَسْتَجِيْبُوْا لَكُمْ فَاعْلَمُوْٓا اَنَّمَآ اُنْزِلَ بِعِلْمِ اللّٰهِ وَاَنْ لَّآ اِلٰهَ اِلَّا هُوَ ۚفَهَلْ اَنْتُمْ مُّسْلِمُوْنَ', 'fa il lam yastajībụ lakum fa\'lamū annamā unzila bi\'ilmillāhi wa al lā ilāha illā huw, fa hal antum muslimụn'),
(1487, 11, 15, 'مَنْ كَانَ يُرِيْدُ الْحَيٰوةَ الدُّنْيَا وَزِيْنَتَهَا نُوَفِّ اِلَيْهِمْ اَعْمَالَهُمْ فِيْهَا وَهُمْ فِيْهَا لَا يُبْخَسُوْنَ', 'mang kāna yurīdul-ḥayātad-dun-yā wa zīnatahā nuwaffi ilaihim a\'mālahum fīhā wa hum fīhā lā yubkhasụn'),
(1488, 11, 16, 'اُولٰۤىِٕكَ الَّذِيْنَ لَيْسَ لَهُمْ فِى الْاٰخِرَةِ اِلَّا النَّارُ ۖوَحَبِطَ مَا صَنَعُوْا فِيْهَا وَبٰطِلٌ مَّا كَانُوْا يَعْمَلُوْنَ', 'ulā`ikallażīna laisa lahum fil-ākhirati illan-nāru wa ḥabiṭa mā ṣana\'ụ fīhā wa bāṭilum mā kānụ ya\'malụn'),
(1489, 11, 17, 'اَفَمَنْ كَانَ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّهٖ وَيَتْلُوْهُ شَاهِدٌ مِّنْهُ وَمِنْ قَبْلِهٖ كِتٰبُ مُوْسٰىٓ اِمَامًا وَّرَحْمَةًۗ اُولٰۤىِٕكَ يُؤْمِنُوْنَ بِهٖ ۗوَمَنْ يَّكْفُرْ بِهٖ مِنَ الْاَحْزَابِ فَالنَّارُ مَوْعِدُهٗ فَلَا تَكُ فِيْ مِرْيَةٍ مِّنْهُ اِنَّهُ الْحَقُّ مِنْ رَّبِّكَ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يُؤْمِنُوْنَ', 'a fa mang kāna \'alā bayyinatim mir rabbihī wa yatlụhu syāhidum min-hu wa ming qablihī kitābu mụsā imāmaw wa raḥmah, ulā`ika yu`minụna bih, wa may yakfur bihī minal-aḥzābi fan-nāru mau\'iduhụ fa lā taku fī miryatim min-hu innahul-ḥaqqu mir rabbika wa lākinna akṡaran-nāsi lā yu`minụn'),
(1490, 11, 18, 'وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًاۗ اُولٰۤىِٕكَ يُعْرَضُوْنَ عَلٰى رَبِّهِمْ وَيَقُوْلُ الْاَشْهَادُ هٰٓؤُلَاۤءِ الَّذِيْنَ كَذَبُوْا عَلٰى رَبِّهِمْۚ اَلَا لَعْنَةُ اللّٰهِ عَلَى الظّٰلِمِيْنَ ۙ', 'wa man aẓlamu mim maniftarā \'alallāhi każibā, ulā`ika yu\'raḍụna \'alā rabbihim wa yaqụlul-asy-hādu hā`ulā`illażīna każabụ \'alā rabbihim, alā la\'natullāhi \'alaẓ-ẓālimīn'),
(1491, 11, 19, 'الَّذِيْنَ يَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ وَيَبْغُوْنَهَا عِوَجًاۗ وَهُمْ بِالْاٰخِرَةِ هُمْ كفِٰرُوْنَ', 'allażīna yaṣuddụna \'an sabīlillāhi wa yabgụnahā \'iwajā, wa hum bil-ākhirati hum kāfirụn'),
(1492, 11, 20, 'اُولٰۤىِٕكَ لَمْ يَكُوْنُوْا مُعْجِزِيْنَ فِى الْاَرْضِ وَمَا كَانَ لَهُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ اَوْلِيَاۤءَ ۘ يُضٰعَفُ لَهُمُ الْعَذَابُ ۗمَا كَانُوْا يَسْتَطِيْعُوْنَ السَّمْعَ وَمَا كَانُوْا يُبْصِرُوْنَ', 'ulā`ika lam yakụnụ mu\'jizīna fil-arḍi wa mā kāna lahum min dụnillāhi min auliyā`, yuḍā\'afu lahumul-\'ażāb, mā kānụ yastaṭī\'ụnas-sam\'a wa mā kānụ yubṣirụn'),
(1493, 11, 21, 'اُولٰۤىِٕكَ الَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ', 'ulā`ikallażīna khasirū anfusahum wa ḍalla \'an-hum mā kānụ yaftarụn'),
(1494, 11, 22, 'لَاجَرَمَ اَنَّهُمْ فِى الْاٰخِرَةِ هُمُ الْاَخْسَرُوْنَ', 'lā jarama annahum fil-ākhirati humul-akhsarụn'),
(1495, 11, 23, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَاَخْبَتُوْٓا اِلٰى رَبِّهِمْۙ اُولٰۤىِٕكَ اَصْحٰبُ الْجَنَّةِۚ هُمْ فِيْهَا خٰلِدُوْنَ', 'innallażīna āmanụ wa \'amiluṣ-ṣāliḥāti wa akhbatū ilā rabbihim ulā`ika aṣ-ḥābul-jannah, hum fīhā khālidụn'),
(1496, 11, 24, '۞ مَثَلُ الْفَرِيْقَيْنِ كَالْاَعْمٰى وَالْاَصَمِّ وَالْبَصِيْرِ وَالسَّمِيْعِۗ هَلْ يَسْتَوِيٰنِ مَثَلًا ۗ اَفَلَا تَذَكَّرُوْنَ', 'maṡalul-farīqaini kal-a\'mā wal-aṣammi wal-baṣīri was-samī\', hal yastawiyāni maṡalā, a fa lā tażakkarụn'),
(1497, 11, 25, 'وَلَقَدْ اَرْسَلْنَا نُوْحًا اِلٰى قَوْمِهٖٓ اِنِّيْ لَكُمْ نَذِيْرٌ مُّبِيْنٌ ۙ', 'wa laqad arsalnā nụḥan ilā qaumihī innī lakum nażīrum mubīn'),
(1498, 11, 26, 'اَنْ لَّا تَعْبُدُوْٓا اِلَّا اللّٰهَ ۖاِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ اَلِيْمٍ', 'al lā ta\'budū illallāh, innī akhāfu \'alaikum \'ażāba yaumin alīm'),
(1499, 11, 27, 'فَقَالَ الْمَلَاُ الَّذِيْنَ كَفَرُوْا مِنْ قَوْمِهٖ مَا نَرٰىكَ اِلَّا بَشَرًا مِّثْلَنَا وَمَا نَرٰىكَ اتَّبَعَكَ اِلَّا الَّذِيْنَ هُمْ اَرَاذِلُنَا بَادِيَ الرَّأْيِۚ وَمَا نَرٰى لَكُمْ عَلَيْنَا مِنْ فَضْلٍۢ بَلْ نَظُنُّكُمْ كٰذِبِيْنَ', 'fa qālal-mala`ullażīna kafarụ ming qaumihī mā narāka illā basyaram miṡlanā wa mā narākattaba\'aka illallażīna hum arāżilunā bādiyar-ra`y, wa mā narā lakum \'alainā min faḍlim bal naẓunnukum kāżibīn'),
(1500, 11, 28, 'قَالَ يٰقَوْمِ اَرَءَيْتُمْ اِنْ كُنْتُ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّيْ وَاٰتٰىنِيْ رَحْمَةً مِّنْ عِنْدِهٖ فَعُمِّيَتْ عَلَيْكُمْۗ اَنُلْزِمُكُمُوْهَا وَاَنْتُمْ لَهَا كٰرِهُوْنَ', 'qāla yā qaumi a ra`aitum ing kuntu \'alā bayyinatim mir rabbī wa ātānī raḥmatam min \'indihī fa \'ummiyat \'alaikum, a nulzimukumụhā wa antum lahā kārihụn'),
(1501, 11, 29, 'وَيٰقَوْمِ لَآ اَسْـَٔلُكُمْ عَلَيْهِ مَالًاۗ اِنْ اَجْرِيَ اِلَّا عَلَى اللّٰهِ وَمَآ اَنَا۠ بِطَارِدِ الَّذِيْنَ اٰمَنُوْاۗ اِنَّهُمْ مُّلٰقُوْا رَبِّهِمْ وَلٰكِنِّيْٓ اَرٰىكُمْ قَوْمًا تَجْهَلُوْنَ', 'wa yā qaumi lā as`alukum \'alaihi mālā, in ajriya illā \'alallāhi wa mā ana biṭāridillażīna āmanụ, innahum mulāqụ rabbihim wa lākinnī arākum qauman taj-halụn'),
(1502, 11, 30, 'وَيٰقَوْمِ مَنْ يَّنْصُرُنِيْ مِنَ اللّٰهِ اِنْ طَرَدْتُّهُمْ ۗ اَفَلَا تَذَكَّرُوْنَ', 'wa yā qaumi may yanṣurunī minallāhi in ṭarattuhum, a fa lā tażakkarụn'),
(1503, 11, 31, 'وَلَآ اَقُوْلُ لَكُمْ عِنْدِيْ خَزَاۤىِٕنُ اللّٰهِ وَلَآ اَعْلَمُ الْغَيْبَ وَلَآ اَقُوْلُ اِنِّيْ مَلَكٌ وَّلَآ اَقُوْلُ لِلَّذِيْنَ تَزْدَرِيْٓ اَعْيُنُكُمْ لَنْ يُّؤْتِيَهُمُ اللّٰهُ خَيْرًا ۗ اَللّٰهُ اَعْلَمُ بِمَا فِيْٓ اَنْفُسِهِمْ ۚاِنِّيْٓ اِذًا لَّمِنَ الظّٰلِمِيْنَ', 'wa lā aqụlu lakum \'indī khazā`inullāhi wa lā a\'lamul-gaiba wa lā aqụlu innī malakuw wa lā aqụlu lillażīna tazdarī a\'yunukum lay yu`tiyahumullāhu khairā, allāhu a\'lamu bimā fī anfusihim, innī iżal laminaẓ-ẓālimīn'),
(1504, 11, 32, 'قَالُوْا يٰنُوْحُ قَدْ جَادَلْتَنَا فَاَ كْثَرْتَ جِدَالَنَا فَأْتِنَا بِمَا تَعِدُنَآ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ', 'qālụ yā nụḥu qad jādaltana fa akṡarta jidālana fa`tinā bimā ta\'idunā ing kunta minaṣ-ṣādiqīn'),
(1505, 11, 33, 'قَالَ اِنَّمَا يَأْتِيْكُمْ بِهِ اللّٰهُ اِنْ شَاۤءَ وَمَآ اَنْتُمْ بِمُعْجِزِيْنَ', 'qāla innamā ya`tīkum bihillāhu in syā`a wa mā antum bimu\'jizīn'),
(1506, 11, 34, 'وَلَا يَنْفَعُكُمْ نُصْحِيْٓ اِنْ اَرَدْتُّ اَنْ اَنْصَحَ لَكُمْ اِنْ كَانَ اللّٰهُ يُرِيْدُ اَنْ يُّغْوِيَكُمْ ۗهُوَ رَبُّكُمْ ۗوَاِلَيْهِ تُرْجَعُوْنَۗ', 'wa lā yanfa\'ukum nuṣ-ḥī in arattu an anṣaḥa lakum ing kānallāhu yurīdu ay yugwiyakum, huwa rabbukum, wa ilaihi turja\'ụn'),
(1507, 11, 35, 'اَمْ يَقُوْلُوْنَ افْتَرٰىهُۗ قُلْ اِنِ افْتَرَيْتُهٗ فَعَلَيَّ اِجْرَامِيْ وَاَنَا۠ بَرِيْۤءٌ مِّمَّا تُجْرِمُوْنَ', 'am yaqụlụnaftarāh, qul iniftaraituhụ fa \'alayya ijrāmī wa ana barī`um mimmā tujrimụn'),
(1508, 11, 36, 'وَاُوْحِيَ اِلٰى نُوْحٍ اَنَّهٗ لَنْ يُّؤْمِنَ مِنْ قَوْمِكَ اِلَّا مَنْ قَدْ اٰمَنَ فَلَا تَبْتَىِٕسْ بِمَا كَانُوْا يَفْعَلُوْنَۖ', 'wa ụḥiya ilā nụḥin annahụ lay yu`mina ming qaumika illā mang qad āmana fa lā tabta`is bimā kānụ yaf\'alụn'),
(1509, 11, 37, 'وَاصْنَعِ الْفُلْكَ بِاَعْيُنِنَا وَوَحْيِنَا وَلَا تُخَاطِبْنِيْ فِى الَّذِيْنَ ظَلَمُوْا ۚاِنَّهُمْ مُّغْرَقُوْنَ', 'waṣna\'il-fulka bi`a\'yuninā wa waḥyinā wa lā tukhāṭibnī fillażīna ẓalamụ, innahum mugraqụn'),
(1510, 11, 38, 'وَيَصْنَعُ الْفُلْكَۗ وَكُلَّمَا مَرَّ عَلَيْهِ مَلَاٌ مِّنْ قَوْمِهٖ سَخِرُوْا مِنْهُ ۗقَالَ اِنْ تَسْخَرُوْا مِنَّا فَاِنَّا نَسْخَرُ مِنْكُمْ كَمَا تَسْخَرُوْنَۗ', 'wa yaṣna\'ul-fulk, wa kullamā marra \'alaihi mala`um ming qaumihī sakhirụ min-h, qāla in taskharụ minnā fa innā naskharu mingkum kamā taskharụn'),
(1511, 11, 39, 'فَسَوْفَ تَعْلَمُوْنَۙ مَنْ يَّأْتِيْهِ عَذَابٌ يُّخْزِيْهِ وَيَحِلُّ عَلَيْهِ عَذَابٌ مُّقِيْمٌ', 'fa saufa ta\'lamụna may ya`tīhi \'ażābuy yukhzīhi wa yaḥillu \'alaihi \'ażābum muqīm'),
(1512, 11, 40, 'حَتّٰىٓ اِذَا جَاۤءَ اَمْرُنَا وَفَارَ التَّنُّوْرُۙ قُلْنَا احْمِلْ فِيْهَا مِنْ كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَاَهْلَكَ اِلَّا مَنْ سَبَقَ عَلَيْهِ الْقَوْلُ وَمَنْ اٰمَنَ ۗوَمَآ اٰمَنَ مَعَهٗٓ اِلَّا قَلِيْلٌ', 'ḥattā iżā jā`a amrunā wa fārat-tannụru qulnaḥmil fīhā ming kullin zaujainiṡnaini wa ahlaka illā man sabaqa \'alaihil-qaulu wa man āman, wa mā āmana ma\'ahū illā qalīl'),
(1513, 11, 41, '۞ وَقَالَ ارْكَبُوْا فِيْهَا بِسْمِ اللّٰهِ مَجْرٰ۪ىهَا وَمُرْسٰىهَا ۗاِنَّ رَبِّيْ لَغَفُوْرٌ رَّحِيْمٌ', 'wa qālarkabụ fīhā bismillāhi majr)hā wa mursāhā, inna rabbī lagafụrur raḥīm'),
(1514, 11, 42, 'وَهِيَ تَجْرِيْ بِهِمْ فِيْ مَوْجٍ كَالْجِبَالِۗ وَنَادٰى نُوْحُ ِۨابْنَهٗ وَكَانَ فِيْ مَعْزِلٍ يّٰبُنَيَّ ارْكَبْ مَّعَنَا وَلَا تَكُنْ مَّعَ الْكٰفِرِيْنَ', 'wa hiya tajrī bihim fī maujing kal-jibāl, wa nādā nụḥunibnahụ wa kāna fī ma\'ziliy yā bunayyarkam ma\'anā wa lā takum ma\'al-kāfirīn'),
(1515, 11, 43, 'قَالَ سَاٰوِيْٓ اِلٰى جَبَلٍ يَّعْصِمُنِيْ مِنَ الْمَاۤءِ ۗقَالَ لَا عَاصِمَ الْيَوْمَ مِنْ اَمْرِ اللّٰهِ اِلَّا مَنْ رَّحِمَ ۚوَحَالَ بَيْنَهُمَا الْمَوْجُ فَكَانَ مِنَ الْمُغْرَقِيْنَ', 'qāla sa`āwī ilā jabaliy ya\'ṣimunī minal-mā`, qāla lā \'āṣimal-yauma min amrillāhi illā mar raḥim, wa ḥāla bainahumal-mauju fa kāna minal-mugraqīn'),
(1516, 11, 44, 'وَقِيْلَ يٰٓاَرْضُ ابْلَعِيْ مَاۤءَكِ وَيَا سَمَاۤءُ اَقْلِعِيْ وَغِيْضَ الْمَاۤءُ وَقُضِيَ الْاَمْرُ وَاسْتَوَتْ عَلَى الْجُوْدِيِّ وَقِيْلَ بُعْدًا لِّلْقَوْمِ الظّٰلِمِيْنَ', 'wa qīla yā arḍubla\'ī mā`aki wa yā samā`u aqli\'ī wa gīḍal-mā`u wa quḍiyal-amru wastawat \'alal-jụdiyyi wa qīla bu\'dal lil-qaumiẓ-ẓālimīn'),
(1517, 11, 45, 'وَنَادٰى نُوْحٌ رَّبَّهٗ فَقَالَ رَبِّ اِنَّ ابْنِيْ مِنْ اَهْلِيْۚ وَاِنَّ وَعْدَكَ الْحَقُّ وَاَنْتَ اَحْكَمُ الْحٰكِمِيْنَ', 'wa nādā nụḥur rabbahụ fa qāla rabbi innabnī min ahlī, wa inna wa\'dakal-ḥaqqu wa anta aḥkamul-ḥākimīn'),
(1518, 11, 46, 'قَالَ يٰنُوْحُ اِنَّهٗ لَيْسَ مِنْ اَهْلِكَ ۚاِنَّهٗ عَمَلٌ غَيْرُ صَالِحٍ فَلَا تَسْـَٔلْنِ مَا لَيْسَ لَكَ بِهٖ عِلْمٌ ۗاِنِّيْٓ اَعِظُكَ اَنْ تَكُوْنَ مِنَ الْجٰهِلِيْنَ', 'qāla yā nụḥu innahụ laisa min ahlik, innahụ \'amalun gairu ṣāliḥin fa lā tas`alni mā laisa laka bihī \'ilm, innī a\'iẓuka an takụna minal-jāhilīn'),
(1519, 11, 47, 'قَالَ رَبِّ اِنِّيْٓ اَعُوْذُ بِكَ اَنْ اَسْـَٔلَكَ مَا لَيْسَ لِيْ بِهٖ عِلْمٌ ۗوَاِلَّا تَغْفِرْ لِيْ وَتَرْحَمْنِيْٓ اَكُنْ مِّنَ الْخٰسِرِيْنَ', 'qāla rabbi innī a\'ụżu bika an as`alaka mā laisa lī bihī \'ilm, wa illā tagfir lī wa tar-ḥamnī akum minal-khāsirīn'),
(1520, 11, 48, 'قِيْلَ يٰنُوْحُ اهْبِطْ بِسَلٰمٍ مِّنَّا وَبَرَكٰتٍ عَلَيْكَ وَعَلٰٓى اُمَمٍ مِّمَّنْ مَّعَكَ ۗوَاُمَمٌ سَنُمَتِّعُهُمْ ثُمَّ يَمَسُّهُمْ مِّنَّا عَذَابٌ اَلِيْمٌ', 'qīla yā nụḥuhbiṭ bisalāmim minnā wa barakātin \'alaika wa \'alā umamim mim mam ma\'ak, wa umamun sanumatti\'uhum ṡumma yamassuhum minnā \'ażābun alīm'),
(1521, 11, 49, 'تِلْكَ مِنْ اَنْۢبَاۤءِ الْغَيْبِ نُوْحِيْهَآ اِلَيْكَ ۚمَا كُنْتَ تَعْلَمُهَآ اَنْتَ وَلَا قَوْمُكَ مِنْ قَبْلِ هٰذَاۚ فَاصْبِرْۚ اِنَّ الْعَاقِبَةَ لِلْمُتَّقِيْنَ', 'tilka min ambā`il-gaibi nụḥīhā ilaīk, mā kunta ta\'lamuhā anta wa lā qaumuka ming qabli hāżā, faṣbir, innal-\'āqibata lil-muttaqīn'),
(1522, 11, 50, 'وَاِلٰى عَادٍ اَخَاهُمْ هُوْدًا ۗقَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗ ۗاِنْ اَنْتُمْ اِلَّا مُفْتَرُوْنَ', 'wa ilā \'ādin akhāhum hụdā, qāla yā qaumi\'budullāha mā lakum min ilāhin gairuh, in antum illā muftarụn'),
(1523, 11, 51, 'يٰقَوْمِ لَآ اَسْـَٔلُكُمْ عَلَيْهِ اَجْرًا ۗاِنْ اَجْرِيَ اِلَّا عَلَى الَّذِيْ فَطَرَنِيْ ۗ اَفَلَا تَعْقِلُوْنَ', 'yā qaumi lā as`alukum \'alaihi ajrā, in ajriya illā \'alallażī faṭaranī, a fa lā ta\'qilụn'),
(1524, 11, 52, 'وَيٰقَوْمِ اسْتَغْفِرُوْا رَبَّكُمْ ثُمَّ تُوْبُوْٓا اِلَيْهِ يُرْسِلِ السَّمَاۤءَ عَلَيْكُمْ مِّدْرَارًا وَّيَزِدْكُمْ قُوَّةً اِلٰى قُوَّتِكُمْ وَلَا تَتَوَلَّوْا مُجْرِمِيْنَ', 'wa yā qaumistagfirụ rabbakum ṡumma tụbū ilaihi yursilis-samā`a \'alaikum midrāraw wa yazidkum quwwatan ilā quwwatikum wa lā tatawallau mujrimīn'),
(1525, 11, 53, 'قَالُوْا يٰهُوْدُ مَاجِئْتَنَا بِبَيِّنَةٍ وَّمَا نَحْنُ بِتَارِكِيْٓ اٰلِهَتِنَا عَنْ قَوْلِكَ وَمَا نَحْنُ لَكَ بِمُؤْمِنِيْنَ', 'qālụ yā hụdu mā ji`tanā bibayyinatiw wa mā naḥnu bitārikī ālihatinā \'ang qaulika wa mā naḥnu laka bimu`minīn'),
(1526, 11, 54, 'اِنْ نَّقُوْلُ اِلَّا اعْتَرٰىكَ بَعْضُ اٰلِهَتِنَا بِسُوْۤءٍ ۗقَالَ اِنِّيْٓ اُشْهِدُ اللّٰهَ وَاشْهَدُوْٓا اَنِّيْ بَرِيْۤءٌ مِّمَّا تُشْرِكُوْنَ', 'in naqụlu illa\'tarāka ba\'ḍu ālihatinā bisū`, qāla innī usy-hidullāha wasy-hadū annī barī`um mimmā tusyrikụn'),
(1527, 11, 55, 'مِنْ دُوْنِهٖ فَكِيْدُوْنِيْ جَمِيْعًا ثُمَّ لَا تُنْظِرُوْنِ', 'min dụnihī fa kīdụnī jamī\'an ṡumma lā tunẓirụn'),
(1528, 11, 56, 'اِنِّيْ تَوَكَّلْتُ عَلَى اللّٰهِ رَبِّيْ وَرَبِّكُمْ ۗمَا مِنْ دَاۤبَّةٍ اِلَّا هُوَ اٰخِذٌۢ بِنَاصِيَتِهَا ۗاِنَّ رَبِّيْ عَلٰى صِرَاطٍ مُّسْتَقِيْمٍ', 'innī tawakkaltu \'alallāhi rabbī wa rabbikum, mā min dābbatin illā huwa ākhiżum bināṣiyatihā, inna rabbī \'alā ṣirāṭim mustaqīm'),
(1529, 11, 57, 'فَاِنْ تَوَلَّوْا فَقَدْ اَبْلَغْتُكُمْ مَّآ اُرْسِلْتُ بِهٖٓ اِلَيْكُمْ ۗوَيَسْتَخْلِفُ رَبِّيْ قَوْمًا غَيْرَكُمْۗ وَلَا تَضُرُّوْنَهٗ شَيْـًٔا ۗاِنَّ رَبِّيْ عَلٰى كُلِّ شَيْءٍ حَفِيْظٌ', 'fa in tawallau fa qad ablagtukum mā ursiltu bihī ilaikum, wa yastakhlifu rabbī qauman gairakum, wa lā taḍurrụnahụ syai`ā, inna rabbī \'alā kulli syai`in ḥafīẓ'),
(1530, 11, 58, 'وَلَمَّا جَاۤءَ اَمْرُنَا نَجَّيْنَا هُوْدًا وَّالَّذِيْنَ اٰمَنُوْا مَعَهٗ بِرَحْمَةٍ مِّنَّاۚ وَنَجَّيْنٰهُمْ مِّنْ عَذَابٍ غَلِيْظٍ', 'wa lammā jā`a amrunā najjainā hụdaw wallażīna āmanụ ma\'ahụ biraḥmatim minnā, wa najjaināhum min \'ażābin galīẓ'),
(1531, 11, 59, 'وَتِلْكَ عَادٌ ۖجَحَدُوْا بِاٰيٰتِ رَبِّهِمْ وَعَصَوْا رُسُلَهٗ وَاتَّبَعُوْٓا اَمْرَ كُلِّ جَبَّارٍ عَنِيْدٍ', 'wa tilka \'ādun jaḥadụ bi`āyāti rabbihim wa \'aṣau rusulahụ wattaba\'ū amra kulli jabbārin \'anīd'),
(1532, 11, 60, 'وَاُتْبِعُوْا فِيْ هٰذِهِ الدُّنْيَا لَعْنَةً وَّيَوْمَ الْقِيٰمَةِ ۗ اَلَآ اِنَّ عَادًا كَفَرُوْا رَبَّهُمْ ۗ اَلَا بُعْدًا لِّعَادٍ قَوْمِ هُوْدٍ', 'wa utbi\'ụ fī hāżihid-dun-yā la\'nataw wa yaumal-qiyāmah, alā inna \'ādang kafarụ rabbahum, alā bu\'dal li\'āding qaumi hụd'),
(1533, 11, 61, '۞ وَاِلٰى ثَمُوْدَ اَخَاهُمْ صٰلِحًا ۘ قَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗ ۗهُوَ اَنْشَاَكُمْ مِّنَ الْاَرْضِ وَاسْتَعْمَرَكُمْ فِيْهَا فَاسْتَغْفِرُوْهُ ثُمَّ تُوْبُوْٓا اِلَيْهِ ۗاِنَّ رَبِّيْ قَرِيْبٌ مُّجِيْبٌ', 'wa ilā ṡamụda akhāhum ṣāliḥā, qāla yā qaumi\'budullāha mā lakum min ilāhin gairuh, huwa ansya`akum minal-arḍi wasta\'marakum fīhā fastagfirụhu ṡumma tụbū ilaīh, inna rabbī qarībum mujīb'),
(1534, 11, 62, 'قَالُوْا يٰصٰلِحُ قَدْ كُنْتَ فِيْنَا مَرْجُوًّا قَبْلَ هٰذَآ اَتَنْهٰىنَآ اَنْ نَّعْبُدَ مَا يَعْبُدُ اٰبَاۤؤُنَا وَاِنَّنَا لَفِيْ شَكٍّ مِّمَّا تَدْعُوْنَآ اِلَيْهِ مُرِيْبٍ', 'qālụ yā ṣāliḥu qad kunta fīnā marjuwwang qabla hāżā a tan-hānā an na\'buda mā ya\'budu ābā`unā wa innanā lafī syakkim mimmā tad\'ụnā ilaihi murīb'),
(1535, 11, 63, 'قَالَ يٰقَوْمِ اَرَءَيْتُمْ اِنْ كُنْتُ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّيْۗ وَاٰتٰىنِيْ مِنْهُ رَحْمَةً فَمَنْ يَّنْصُرُنِيْ مِنَ اللّٰهِ اِنْ عَصَيْتُهٗ ۗفَمَا تَزِيْدُوْنَنِيْ غَيْرَ تَخْسِيْرٍ', 'qāla yā qaumi a ra`aitum ing kuntu \'alā bayyinatim mir rabbī, wa ātānī min-hu raḥmatan fa may yanṣurunī minallāhi in \'aṣaituh, fa mā tazīdụnanī gaira takhsīr'),
(1536, 11, 64, 'وَيٰقَوْمِ هٰذِهٖ نَاقَةُ اللّٰهِ لَكُمْ اٰيَةً فَذَرُوْهَا تَأْكُلْ فِيْٓ اَرْضِ اللّٰهِ وَلَا تَمَسُّوْهَا بِسُوْۤءٍ فَيَأْخُذَكُمْ عَذَابٌ قَرِيْبٌ', 'wa yā qaumi hāżihī nāqatullāhi lakum āyatan fa żarụhā ta`kul fī arḍillāhi wa lā tamassụhā bisū`in fa ya`khużakum \'ażābung qarīb'),
(1537, 11, 65, 'فَعَقَرُوْهَا فَقَالَ تَمَتَّعُوْا فِيْ دَارِكُمْ ثَلٰثَةَ اَيَّامٍ ۗذٰلِكَ وَعْدٌ غَيْرُ مَكْذُوْبٍ', 'fa \'aqarụhā fa qāla tamatta\'ụ fī dārikum ṡalāṡata ayyām, żālika wa\'dun gairu makżụb'),
(1538, 11, 66, 'فَلَمَّا جَاۤءَ اَمْرُنَا نَجَّيْنَا صٰلِحًا وَّالَّذِيْنَ اٰمَنُوْا مَعَهٗ بِرَحْمَةٍ مِّنَّا وَمِنْ خِزْيِ يَوْمِىِٕذٍ ۗاِنَّ رَبَّكَ هُوَ الْقَوِيُّ الْعَزِيْزُ', 'fa lammā jā`a amrunā najjainā ṣāliḥaw wallażīna āmanụ ma\'ahụ biraḥmatim minnā wa min khizyi yaumi`iż, inna rabbaka huwal-qawiyyul-\'azīz'),
(1539, 11, 67, 'وَاَخَذَ الَّذِيْنَ ظَلَمُوا الصَّيْحَةُ فَاَصْبَحُوْا فِيْ دِيَارِهِمْ جٰثِمِيْنَۙ', 'wa akhażallażīna ẓalamuṣ-ṣaiḥatu fa aṣbaḥụ fī diyārihim jāṡimīn'),
(1540, 11, 68, 'كَاَنْ لَّمْ يَغْنَوْا فِيْهَا ۗ اَلَآ اِنَّ ثَمُوْدَا۠ كَفَرُوْا رَبَّهُمْ ۗ اَلَا بُعْدًا لِّثَمُوْدَ', 'ka`al lam yagnau fīhā, alā inna ṡamụda kafarụ rabbahum, alā bu\'dal liṡamụd'),
(1541, 11, 69, 'وَلَقَدْ جَاۤءَتْ رُسُلُنَآ اِبْرٰهِيْمَ بِالْبُشْرٰى قَالُوْا سَلٰمًا ۖقَالَ سَلٰمٌ فَمَا لَبِثَ اَنْ جَاۤءَ بِعِجْلٍ حَنِيْذٍ', 'wa laqad jā`at rusulunā ibrāhīma bil-busyrā qālụ salāmā, qāla salāmun fa mā labiṡa an jā`a bi\'ijlin ḥanīż'),
(1542, 11, 70, 'فَلَمَّا رَاٰىٓ اَيْدِيَهُمْ لَا تَصِلُ اِلَيْهِ نَكِرَهُمْ وَاَوْجَسَ مِنْهُمْ خِيْفَةً ۗقَالُوْا لَا تَخَفْ اِنَّآ اُرْسِلْنَآ اِلٰى قَوْمِ لُوْطٍۗ', 'fa lammā ra`ā aidiyahum lā taṣilu ilaihi nakirahum wa aujasa min-hum khīfah, qālụ lā takhaf innā ursilnā ilā qaumi lụṭ'),
(1543, 11, 71, 'وَامْرَاَتُهٗ قَاۤىِٕمَةٌ فَضَحِكَتْ فَبَشَّرْنٰهَا بِاِسْحٰقَۙ وَمِنْ وَّرَاۤءِ اِسْحٰقَ يَعْقُوْبَ', 'wamra`atuhụ qā`imatun fa ḍaḥikat fa basysyarnāhā bi`is-ḥāqa wa miw warā`i is-ḥāqa ya\'qụb'),
(1544, 11, 72, 'قَالَتْ يٰوَيْلَتٰىٓ ءَاَلِدُ وَاَنَا۠ عَجُوْزٌ وَّهٰذَا بَعْلِيْ شَيْخًا ۗاِنَّ هٰذَا لَشَيْءٌ عَجِيْبٌ', 'qālat yā wailatā a alidu wa ana \'ajụzuw wa hāżā ba\'lī syaikhā, inna hāżā lasyai`un \'ajīb'),
(1545, 11, 73, 'قَالُوْٓا اَتَعْجَبِيْنَ مِنْ اَمْرِ اللّٰهِ رَحْمَتُ اللّٰهِ وَبَرَكٰتُهٗ عَلَيْكُمْ اَهْلَ الْبَيْتِۗ اِنَّهُ حَمِيْدٌ مَّجِيْدٌ', 'qālū a ta\'jabīna min amrillāhi raḥmatullāhi wa barakātuhụ \'alaikum ahlal-baīt, innahụ ḥamīdum majīd'),
(1546, 11, 74, 'فَلَمَّا ذَهَبَ عَنْ اِبْرٰهِيْمَ الرَّوْعُ وَجَاۤءَتْهُ الْبُشْرٰى يُجَادِلُنَا فِيْ قَوْمِ لُوْطٍ', 'fa lammā żahaba \'an ibrāhīmar-rau\'u wa jā`at-hul-busyrā yujādilunā fī qaumi lụṭ'),
(1547, 11, 75, 'اِنَّ اِبْرٰهِيْمَ لَحَلِيْمٌ اَوَّاهٌ مُّنِيْبٌ', 'inna ibrāhīma laḥalīmun awwāhum munīb'),
(1548, 11, 76, 'يٰٓاِبْرٰهِيْمُ اَعْرِضْ عَنْ هٰذَا ۚاِنَّهٗ قَدْ جَاۤءَ اَمْرُ رَبِّكَۚ وَاِنَّهُمْ اٰتِيْهِمْ عَذَابٌ غَيْرُ مَرْدُوْدٍ', 'yā ibrāhīmu a\'riḍ \'an hāżā, innahụ qad jā`a amru rabbik, wa innahum ātīhim \'ażābun gairu mardụd'),
(1549, 11, 77, 'وَلَمَّا جَاۤءَتْ رُسُلُنَا لُوْطًا سِيْۤءَ بِهِمْ وَضَاقَ بِهِمْ ذَرْعًا وَّقَالَ هٰذَا يَوْمٌ عَصِيْبٌ', 'wa lammā jā`at rusulunā lụṭan sī`a bihim wa ḍāqa bihim żar\'aw wa qāla hāżā yaumun \'aṣīb'),
(1550, 11, 78, 'وَجَاۤءَهٗ قَوْمُهٗ يُهْرَعُوْنَ اِلَيْهِۗ وَمِنْ قَبْلُ كَانُوْا يَعْمَلُوْنَ السَّيِّاٰتِۗ قَالَ يٰقَوْمِ هٰٓؤُلَاۤءِ بَنَاتِيْ هُنَّ اَطْهَرُ لَكُمْ فَاتَّقُوا اللّٰهَ وَلَا تُخْزُوْنِ فِيْ ضَيْفِيْۗ اَلَيْسَ مِنْكُمْ رَجُلٌ رَّشِيْدٌ', 'wa jā`ahụ qaumuhụ yuhra\'ụna ilaīh, wa ming qablu kānụ ya\'malụnas-sayyi`āt, qāla yā qaumi hā`ulā`i banatī hunna aṭ-haru lakum fattaqullāha wa lā tukhzụni fī ḍaifī, a laisa mingkum rajulur rasyīd'),
(1551, 11, 79, 'قَالُوْا لَقَدْ عَلِمْتَ مَا لَنَا فِيْ بَنٰتِكَ مِنْ حَقٍّۚ وَاِنَّكَ لَتَعْلَمُ مَا نُرِيْدُ', 'qālụ laqad \'alimta mā lanā fī banātika min ḥaqq, wa innaka lata\'lamu mā nurīd'),
(1552, 11, 80, 'قَالَ لَوْ اَنَّ لِيْ بِكُمْ قُوَّةً اَوْ اٰوِيْٓ اِلٰى رُكْنٍ شَدِيْدٍ', 'qāla lau anna lī bikum quwwatan au āwī ilā ruknin syadīd'),
(1553, 11, 81, 'قَالُوْا يٰلُوْطُ اِنَّا رُسُلُ رَبِّكَ لَنْ يَّصِلُوْٓا اِلَيْكَ فَاَسْرِ بِاَهْلِكَ بِقِطْعٍ مِّنَ الَّيْلِ وَلَا يَلْتَفِتْ مِنْكُمْ اَحَدٌ اِلَّا امْرَاَتَكَۗ اِنَّهٗ مُصِيْبُهَا مَآ اَصَابَهُمْ ۗاِنَّ مَوْعِدَهُمُ الصُّبْحُ ۗ اَلَيْسَ الصُّبْحُ بِقَرِيْبٍ', 'qālụ yā lụṭu innā rusulu rabbika lay yaṣilū ilaika fa asri bi`ahlika biqiṭ\'im minal-laili wa lā yaltafit mingkum aḥadun illamra`atak, innahụ muṣībuhā mā aṣābahum, inna mau\'idahumuṣ-ṣub-h, a laisaṣ-ṣub-ḥu biqarīb'),
(1554, 11, 82, 'فَلَمَّا جَاۤءَ اَمْرُنَا جَعَلْنَا عَالِيَهَا سَافِلَهَا وَاَمْطَرْنَا عَلَيْهَا حِجَارَةً مِّنْ سِجِّيْلٍ مَّنْضُوْدٍ', 'fa lammā jā`a amrunā ja\'alnā \'āliyahā sāfilahā wa amṭarnā \'alaihā ḥijāratam min sijjīlim manḍụd'),
(1555, 11, 83, 'مُسَوَّمَةً عِنْدَ رَبِّكَۗ وَمَا هِيَ مِنَ الظّٰلِمِيْنَ بِبَعِيْدٍ', 'musawwamatan \'inda rabbik, wa mā hiya minaẓ-ẓālimīna biba\'īd'),
(1556, 11, 84, '۞ وَاِلٰى مَدْيَنَ اَخَاهُمْ شُعَيْبًا ۗقَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗ ۗوَلَا تَنْقُصُوا الْمِكْيَالَ وَالْمِيْزَانَ اِنِّيْٓ اَرٰىكُمْ بِخَيْرٍ وَّاِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ مُّحِيْطٍ', 'wa ilā madyana akhāhum syu\'aibā, qāla yā qaumi\'budullāha mā lakum min ilāhin gairuh, wa lā tangquṣul-mikyāla wal-mīzāna innī arākum bikhairiw wa innī akhāfu \'alaikum \'ażāba yaumim muḥīṭ'),
(1557, 11, 85, 'وَيٰقَوْمِ اَوْفُوا الْمِكْيَالَ وَالْمِيْزَانَ بِالْقِسْطِ وَلَا تَبْخَسُوا النَّاسَ اَشْيَاۤءَهُمْ وَلَا تَعْثَوْا فِى الْاَرْضِ مُفْسِدِيْنَ', 'wa yā qaumi auful-mikyāla wal-mīzāna bil-qisṭi wa lā tabkhasun-nāsa asy-yā`ahum wa lā ta\'ṡau fil-arḍi mufsidīn'),
(1558, 11, 86, 'بَقِيَّتُ اللّٰهِ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ەۚ وَمَآ اَنَا۠ عَلَيْكُمْ بِحَفِيْظٍ', 'baqiyyatullāhi khairul lakum ing kuntum mu`minīn, wa mā ana \'alaikum biḥafīẓ'),
(1559, 11, 87, 'قَالُوْا يٰشُعَيْبُ اَصَلٰوتُكَ تَأْمُرُكَ اَنْ نَّتْرُكَ مَا يَعْبُدُ اٰبَاۤؤُنَآ اَوْ اَنْ نَّفْعَلَ فِيْٓ اَمْوَالِنَا مَا نَشٰۤؤُا ۗاِنَّكَ لَاَنْتَ الْحَلِيْمُ الرَّشِيْدُ', 'qālụ yā syu\'aibu a ṣalātuka ta`muruka an natruka mā ya\'budu ābā`unā au an naf\'ala fī amwālinā mā nasyā`, innaka la`antal-ḥalīmur-rasyīd'),
(1560, 11, 88, 'قَالَ يٰقَوْمِ اَرَءَيْتُمْ اِنْ كُنْتُ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّيْ وَرَزَقَنِيْ مِنْهُ رِزْقًا حَسَنًا وَّمَآ اُرِيْدُ اَنْ اُخَالِفَكُمْ اِلٰى مَآ اَنْهٰىكُمْ عَنْهُ ۗاِنْ اُرِيْدُ اِلَّا الْاِصْلَاحَ مَا اسْتَطَعْتُۗ وَمَا تَوْفِيْقِيْٓ اِلَّا بِاللّٰهِ ۗعَلَيْهِ تَوَكَّلْتُ وَاِلَيْهِ اُنِيْبُ', 'qāla yā qaumi a ra`aitum ing kuntu \'alā bayyinatim mir rabbī wa razaqanī min-hu rizqan ḥasanaw wa mā urīdu an ukhālifakum ilā mā an-hākum \'an-h, in urīdu illal-iṣlāḥa mastaṭa\'t, wa mā taufīqī illā billāh, \'alaihi tawakkaltu wa ilaihi unīb'),
(1561, 11, 89, 'وَيٰقَوْمِ لَا يَجْرِمَنَّكُمْ شِقَاقِيْٓ اَنْ يُّصِيْبَكُمْ مِّثْلُ مَآ اَصَابَ قَوْمَ نُوْحٍ اَوْ قَوْمَ هُوْدٍ اَوْ قَوْمَ صٰلِحٍ ۗوَمَا قَوْمُ لُوْطٍ مِّنْكُمْ بِبَعِيْدٍ', 'wa yā qaumi lā yajrimannakum syiqāqī ay yuṣībakum miṡlu mā aṣāba qauma nụḥin au qauma hụdin au qauma ṣāliḥ, wa mā qaumu lụṭim mingkum biba\'īd'),
(1562, 11, 90, 'وَاسْتَغْفِرُوْا رَبَّكُمْ ثُمَّ تُوْبُوْٓا اِلَيْهِ ۗاِنَّ رَبِّيْ رَحِيْمٌ وَّدُوْدٌ', 'wastagfirụ rabbakum ṡumma tụbū ilaīh, inna rabbī raḥīmuw wadụd'),
(1563, 11, 91, 'قَالُوْا يٰشُعَيْبُ مَا نَفْقَهُ كَثِيْرًا مِّمَّا تَقُوْلُ وَاِنَّا لَنَرٰىكَ فِيْنَا ضَعِيْفًا ۗوَلَوْلَا رَهْطُكَ لَرَجَمْنٰكَ ۖوَمَآ اَنْتَ عَلَيْنَا بِعَزِيْزٍ', 'qālụ yā syu\'aibu mā nafqahu kaṡīram mimmā taqụlu wa innā lanarāka fīnā ḍa\'īfā, walau lā rahṭuka larajamnāka wa mā anta \'alainā bi\'azīz'),
(1564, 11, 92, 'قَالَ يٰقَوْمِ اَرَهْطِيْٓ اَعَزُّ عَلَيْكُمْ مِّنَ اللّٰهِ ۗوَاتَّخَذْتُمُوْهُ وَرَاۤءَكُمْ ظِهْرِيًّا ۗاِنَّ رَبِّيْ بِمَا تَعْمَلُوْنَ مُحِيْطٌ', 'qāla yā qaumi a rahṭī a\'azzu \'alaikum minallāh, wattakhażtumụhu warā`akum ẓihriyyā, inna rabbī bimā ta\'malụna muḥīṭ'),
(1565, 11, 93, 'وَيٰقَوْمِ اعْمَلُوْا عَلٰى مَكَانَتِكُمْ اِنِّيْ عَامِلٌ ۗسَوْفَ تَعْلَمُوْنَۙ مَنْ يَّأْتِيْهِ عَذَابٌ يُّخْزِيْهِ وَمَنْ هُوَ كَاذِبٌۗ وَارْتَقِبُوْٓا اِنِّيْ مَعَكُمْ رَقِيْبٌ', 'wa yā qaumi\'malụ \'alā makānatikum innī \'āmil, saufa ta\'lamụna may ya`tīhi \'ażābuy yukhzīhi wa man huwa kāżib, wartaqibū innī ma\'akum raqīb'),
(1566, 11, 94, 'وَلَمَّا جَاۤءَ اَمْرُنَا نَجَّيْنَا شُعَيْبًا وَّالَّذِيْنَ اٰمَنُوْا مَعَهٗ بِرَحْمَةٍ مِّنَّاۚ وَاَخَذَتِ الَّذِيْنَ ظَلَمُوا الصَّيْحَةُ فَاَصْبَحُوْا فِيْ دِيَارِهِمْ جٰثِمِيْنَۙ', 'wa lammā jā`a amrunā najjainā syu\'aibaw wallażīna āmanụ ma\'ahụ biraḥmatim minnā, wa akhażatillażīna ẓalamuṣ-ṣaiḥatu fa aṣbaḥụ fī diyārihim jāṡimīn'),
(1567, 11, 95, 'كَاَنْ لَّمْ يَغْنَوْا فِيْهَا ۗ اَلَا بُعْدًا لِّمَدْيَنَ كَمَا بَعِدَتْ ثَمُوْدُ', 'ka`al lam yagnau fīhā, alā bu\'dal limadyana kamā ba\'idat ṡamụd'),
(1568, 11, 96, 'وَلَقَدْ اَرْسَلْنَا مُوْسٰى بِاٰيٰتِنَا وَسُلْطٰنٍ مُّبِيْنٍۙ', 'wa laqad arsalnā mụsā bi`āyātinā wa sulṭānim mubīn'),
(1569, 11, 97, 'اِلٰى فِرْعَوْنَ وَملَا۟ىِٕهٖ فَاتَّبَعُوْٓا اَمْرَ فِرْعَوْنَ ۚوَمَآ اَمْرُ فِرْعَوْنَ بِرَشِيْدٍ', 'ilā fir\'auna wa mala`ihī fattaba\'ū amra fir\'aụn, wa mā amru fir\'auna birasyīd'),
(1570, 11, 98, 'يَقْدُمُ قَوْمَهٗ يَوْمَ الْقِيٰمَةِ فَاَوْرَدَهُمُ النَّارَ ۗوَبِئْسَ الْوِرْدُ الْمَوْرُوْدُ', 'yaqdumu qaumahụ yaumal-qiyāmati fa auradahumun-nār, wa bi`sal-wirdul-maurụd'),
(1571, 11, 99, 'وَاُتْبِعُوْا فِيْ هٰذِهٖ لَعْنَةً وَّيَوْمَ الْقِيٰمَةِۗ بِئْسَ الرِّفْدُ الْمَرْفُوْدُ', 'wa utbi\'ụ fī hāżihī la\'nataw wa yaumal-qiyāmah, bi`sar-rifdul-marfụd'),
(1572, 11, 100, 'ذٰلِكَ مِنْ اَنْۢبَاۤءِ الْقُرٰى نَقُصُّهٗ عَلَيْكَ مِنْهَا قَاۤىِٕمٌ وَّحَصِيْدٌ', 'żālika min ambā`il-qurā naquṣṣuhụ \'alaika min-hā qā`imuw wa ḥaṣīd'),
(1573, 11, 101, 'وَمَا ظَلَمْنٰهُمْ وَلٰكِنْ ظَلَمُوْٓا اَنْفُسَهُمْ فَمَآ اَغْنَتْ عَنْهُمْ اٰلِهَتُهُمُ الَّتِيْ يَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ مِنْ شَيْءٍ لَّمَّا جَاۤءَ اَمْرُ رَبِّكَۗ وَمَا زَادُوْهُمْ غَيْرَ تَتْبِيْبٍ', 'wa mā ẓalamnāhum wa lākin ẓalamū anfusahum fa mā agnat \'an-hum ālihatuhumullatī yad\'ụna min dụnillāhi min syai`il lammā jā`a amru rabbik, wa mā zādụhum gaira tatbīb'),
(1574, 11, 102, 'وَكَذٰلِكَ اَخْذُ رَبِّكَ اِذَآ اَخَذَ الْقُرٰى وَهِيَ ظَالِمَةٌ ۗاِنَّ اَخْذَهٗٓ اَلِيْمٌ شَدِيْدٌ', 'wa każālika akhżu rabbika iżā akhażal-qurā wa hiya ẓālimah, inna akhżahū alīmun syadīd'),
(1575, 11, 103, 'اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّمَنْ خَافَ عَذَابَ الْاٰخِرَةِ ۗذٰلِكَ يَوْمٌ مَّجْمُوْعٌۙ لَّهُ النَّاسُ وَذٰلِكَ يَوْمٌ مَّشْهُوْدٌ', 'inna fī żālika la`āyatal liman khāfa \'ażābal-ākhirah, żālika yaumum majmụ\'ul lahun-nāsu wa żālika yaumum masy-hụd'),
(1576, 11, 104, 'وَمَا نُؤَخِّرُهٗٓ اِلَّا لِاَجَلٍ مَّعْدُوْدٍۗ', 'wa mā nu`akhkhiruhū illā li`ajalim ma\'dụd'),
(1577, 11, 105, 'يَوْمَ يَأْتِ لَا تَكَلَّمُ نَفْسٌ اِلَّا بِاِذْنِهٖۚ فَمِنْهُمْ شَقِيٌّ وَّسَعِيْدٌ', 'yauma ya`ti lā takallamu nafsun illā bi`iżnih, fa min-hum syaqiyyuw wa sa\'īd'),
(1578, 11, 106, 'فَاَمَّا الَّذِيْنَ شَقُوْا فَفِى النَّارِ لَهُمْ فِيْهَا زَفِيْرٌ وَّشَهِيْقٌۙ', 'fa ammallażīna syaqụ fa fin-nāri lahum fīhā zafīruw wa syahīq'),
(1579, 11, 107, 'خٰلِدِيْنَ فِيْهَا مَا دَامَتِ السَّمٰوٰتُ وَالْاَرْضُ اِلَّا مَا شَاۤءَ رَبُّكَۗ اِنَّ رَبَّكَ فَعَّالٌ لِّمَا يُرِيْدُ', 'khālidīna fīhā mā dāmatis-samāwātu wal-arḍu illā mā syā`a rabbuk, inna rabbaka fa\'\'ālul limā yurīd'),
(1580, 11, 108, '۞ وَاَمَّا الَّذِيْنَ سُعِدُوْا فَفِى الْجَنَّةِ خٰلِدِيْنَ فِيْهَا مَا دَامَتِ السَّمٰوٰتُ وَالْاَرْضُ اِلَّا مَا شَاۤءَ رَبُّكَۗ عَطَاۤءً غَيْرَ مَجْذُوْذٍ', 'wa ammallażīna su\'idụ fa fil-jannati khālidīna fīhā mā dāmatis-samāwātu wal-arḍu illā mā syā`a rabbuk, \'aṭā`an gaira majżụż'),
(1581, 11, 109, 'فَلَا تَكُ فِيْ مِرْيَةٍ مِّمَّا يَعْبُدُ هٰٓؤُلَاۤءِ ۗمَا يَعْبُدُوْنَ اِلَّا كَمَا يَعْبُدُ اٰبَاۤؤُهُمْ مِّنْ قَبْلُ ۗوَاِنَّا لَمُوَفُّوْهُمْ نَصِيْبَهُمْ غَيْرَ مَنْقُوْصٍ', 'fa lā taku fī miryatim mimmā ya\'budu hā`ulā`, mā ya\'budụna illā kamā ya\'budu ābā`uhum ming qabl, wa innā lamuwaffụhum naṣībahum gaira mangqụṣ'),
(1582, 11, 110, 'وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ فَاخْتُلِفَ فِيْهِ ۗوَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَّبِّكَ لَقُضِيَ بَيْنَهُمْ ۚوَاِنَّهُمْ لَفِيْ شَكٍّ مِّنْهُ مُرِيْبٍ', 'wa laqad ātainā mụsal-kitāba fakhtulifa fīh, walau lā kalimatun sabaqat mir rabbika laquḍiya bainahum, wa innahum lafī syakkim min-hu murīb'),
(1583, 11, 111, 'وَاِنَّ كُلًّا لَّمَّا لَيُوَفِّيَنَّهُمْ رَبُّكَ اَعْمَالَهُمْ ۗاِنَّهٗ بِمَا يَعْمَلُوْنَ خَبِيْرٌ', 'wa inna kullal lammā layuwaffiyannahum rabbuka a\'mālahum, innahụ bimā ya\'malụna khabīr'),
(1584, 11, 112, 'فَاسْتَقِمْ كَمَآ اُمِرْتَ وَمَنْ تَابَ مَعَكَ وَلَا تَطْغَوْاۗ اِنَّهٗ بِمَا تَعْمَلُوْنَ بَصِيْرٌ', 'fastaqim kamā umirta wa man tāba ma\'aka wa lā taṭgau, innahụ bimā ta\'malụna baṣīr'),
(1585, 11, 113, 'وَلَا تَرْكَنُوْٓا اِلَى الَّذِيْنَ ظَلَمُوْا فَتَمَسَّكُمُ النَّارُۙ وَمَا لَكُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ اَوْلِيَاۤءَ ثُمَّ لَا تُنْصَرُوْنَ', 'wa lā tarkanū ilallażīna ẓalamụ fa tamassakumun-nāru wa mā lakum min dụnillāhi min auliyā`a ṡumma lā tunṣarụn'),
(1586, 11, 114, 'وَاَقِمِ الصَّلٰوةَ طَرَفَيِ النَّهَارِ وَزُلَفًا مِّنَ الَّيْلِ ۗاِنَّ الْحَسَنٰتِ يُذْهِبْنَ السَّيِّاٰتِۗ ذٰلِكَ ذِكْرٰى لِلذَّاكِرِيْنَ', 'wa aqimiṣ-ṣalāta ṭarafayin-nahāri wa zulafam minal-laīl, innal-ḥasanāti yuż-hibnas-sayyi`āt, żālika żikrā liż-żākirīn'),
(1587, 11, 115, 'وَاصْبِرْ فَاِنَّ اللّٰهَ لَا يُضِيْعُ اَجْرَ الْمُحْسِنِيْنَ', 'waṣbir fa innallāha lā yuḍī\'u ajral-muḥsinīn'),
(1588, 11, 116, 'فَلَوْلَا كَانَ مِنَ الْقُرُوْنِ مِنْ قَبْلِكُمْ اُولُوْا بَقِيَّةٍ يَّنْهَوْنَ عَنِ الْفَسَادِ فِى الْاَرْضِ اِلَّا قَلِيْلًا مِّمَّنْ اَنْجَيْنَا مِنْهُمْ ۚوَاتَّبَعَ الَّذِيْنَ ظَلَمُوْا مَآ اُتْرِفُوْا فِيْهِ وَكَانُوْا مُجْرِمِيْنَ', 'falau lā kāna minal-qurụni ming qablikum ulụ baqiyyatiy yan-hauna \'anil-fasādi fil-arḍi illā qalīlam mim man anjainā min-hum, wattaba\'allażīna ẓalamụ mā utrifụ fīhi wa kānụ mujrimīn'),
(1589, 11, 117, 'وَمَا كَانَ رَبُّكَ لِيُهْلِكَ الْقُرٰى بِظُلْمٍ وَّاَهْلُهَا مُصْلِحُوْنَ', 'wa mā kāna rabbuka liyuhlikal-qurā biẓulmiw wa ahluhā muṣliḥụn'),
(1590, 11, 118, 'وَلَوْ شَاۤءَ رَبُّكَ لَجَعَلَ النَّاسَ اُمَّةً وَّاحِدَةً وَّلَا يَزَالُوْنَ مُخْتَلِفِيْنَۙ', 'walau syā`a rabbuka laja\'alan-nāsa ummataw wāḥidataw wa lā yazālụna mukhtalifīn'),
(1591, 11, 119, 'اِلَّا مَنْ رَّحِمَ رَبُّكَ ۗوَلِذٰلِكَ خَلَقَهُمْ ۗوَتَمَّتْ كَلِمَةُ رَبِّكَ لَاَمْلَـَٔنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَالنَّاسِ اَجْمَعِيْنَ', 'illā mar raḥima rabbuk, wa liżālika khalaqahum, wa tammat kalimatu rabbika la`amla`anna jahannama minal-jinnati wan-nāsi ajma\'īn'),
(1592, 11, 120, 'وَكُلًّا نَّقُصُّ عَلَيْكَ مِنْ اَنْۢبَاۤءِ الرُّسُلِ مَا نُثَبِّتُ بِهٖ فُؤَادَكَ وَجَاۤءَكَ فِيْ هٰذِهِ الْحَقُّ وَمَوْعِظَةٌ وَّذِكْرٰى لِلْمُؤْمِنِيْنَ', 'wa kullan naquṣṣu \'alaika min ambā`ir-rusuli mā nuṡabbitu bihī fu`ādaka wa jā`aka fī hāżihil-ḥaqqu wa mau\'iẓatuw wa żikrā lil-mu`minīn'),
(1593, 11, 121, 'وَقُلْ لِّلَّذِيْنَ لَا يُؤْمِنُوْنَ اعْمَلُوْا عَلٰى مَكَانَتِكُمْۗ اِنَّا عٰمِلُوْنَۙ', 'wa qul lillażīna lā yu`minụna\'malụ \'alā makānatikum, innā \'āmilụn'),
(1594, 11, 122, 'وَانْتَظِرُوْاۚ اِنَّا مُنْتَظِرُوْنَ', 'wantaẓirụ, innā muntaẓirụn'),
(1595, 11, 123, 'وَلِلّٰهِ غَيْبُ السَّمٰوٰتِ وَالْاَرْضِ وَاِلَيْهِ يُرْجَعُ الْاَمْرُ كُلُّهٗ فَاعْبُدْهُ وَتَوَكَّلْ عَلَيْهِۗ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ', 'wa lillāhi gaibus-samāwāti wal-arḍi wa ilaihi yurja\'ul-amru kulluhụ fa\'bud-hu wa tawakkal \'alaīh, wa mā rabbuka bigāfilin \'ammā ta\'malụn'),
(1596, 12, 1, 'الۤرٰ ۗ تِلْكَ اٰيٰتُ الْكِتٰبِ الْمُبِيْنِۗ', 'alif lām rā, tilka āyātul-kitābil mubīn'),
(1597, 12, 2, 'اِنَّآ اَنْزَلْنٰهُ قُرْاٰنًا عَرَبِيًّا لَّعَلَّكُمْ تَعْقِلُوْنَ', 'innā anzalnāhu qur`ānan \'arabiyyal la\'allakum ta\'qilụn'),
(1598, 12, 3, 'نَحْنُ نَقُصُّ عَلَيْكَ اَحْسَنَ الْقَصَصِ بِمَآ اَوْحَيْنَآ اِلَيْكَ هٰذَا الْقُرْاٰنَۖ وَاِنْ كُنْتَ مِنْ قَبْلِهٖ لَمِنَ الْغٰفِلِيْنَ', 'naḥnu naquṣṣu \'alaika aḥsanal-qaṣaṣi bimā auḥainā ilaika hāżal-qur`āna wa ing kunta ming qablihī laminal-gāfilīn'),
(1599, 12, 4, 'اِذْ قَالَ يُوْسُفُ لِاَبِيْهِ يٰٓاَبَتِ اِنِّيْ رَاَيْتُ اَحَدَ عَشَرَ كَوْكَبًا وَّالشَّمْسَ وَالْقَمَرَ رَاَيْتُهُمْ لِيْ سٰجِدِيْنَ', 'iż qāla yụsufu li`abīhi yā abati innī ra`aitu aḥada \'asyara kaukabaw wasy-syamsa wal-qamara ra`aituhum lī sājidīn'),
(1600, 12, 5, 'قَالَ يٰبُنَيَّ لَا تَقْصُصْ رُءْيَاكَ عَلٰٓى اِخْوَتِكَ فَيَكِيْدُوْا لَكَ كَيْدًا ۗاِنَّ الشَّيْطٰنَ لِلْاِنْسَانِ عَدُوٌّ مُّبِيْنٌ', 'qāla yā bunayya lā taqṣuṣ ru`yāka \'alā ikhwatika fa yakīdụ laka kaidā, innasy-syaiṭāna lil-insāni \'aduwwum mubīn'),
(1601, 12, 6, 'وَكَذٰلِكَ يَجْتَبِيْكَ رَبُّكَ وَيُعَلِّمُكَ مِنْ تَأْوِيْلِ الْاَحَادِيْثِ وَيُتِمُّ نِعْمَتَهٗ عَلَيْكَ وَعَلٰٓى اٰلِ يَعْقُوْبَ كَمَآ اَتَمَّهَا عَلٰٓى اَبَوَيْكَ مِنْ قَبْلُ اِبْرٰهِيْمَ وَاِسْحٰقَۗ اِنَّ رَبَّكَ عَلِيْمٌ حَكِيْمٌ', 'wa każālika yajtabīka rabbuka wa yu\'allimuka min ta`wīlil-aḥādīṡi wa yutimmu ni\'matahụ \'alaika wa \'alā āli ya\'qụba kamā atammahā \'alā abawaika ming qablu ibrāhīma wa is-ḥāq, inna rabbaka \'alīmun ḥakīm'),
(1602, 12, 7, '۞ لَقَدْ كَانَ فِيْ يُوْسُفَ وَاِخْوَتِهٖٓ اٰيٰتٌ لِّلسَّاۤىِٕلِيْنَ', 'laqad kāna fī yụsufa wa ikhwatihī āyātul lis-sā`ilīn'),
(1603, 12, 8, 'اِذْ قَالُوْا لَيُوْسُفُ وَاَخُوْهُ اَحَبُّ اِلٰٓى اَبِيْنَا مِنَّا وَنَحْنُ عُصْبَةٌ ۗاِنَّ اَبَانَا لَفِيْ ضَلٰلٍ مُّبِيْنٍۙ', 'iż qālụ layụsufu wa akhụhu aḥabbu ilā abīnā minnā wa naḥnu \'uṣbah, inna abānā lafī ḍalālim mubīn'),
(1604, 12, 9, ' ۨاقْتُلُوْا يُوْسُفَ اَوِ اطْرَحُوْهُ اَرْضًا يَّخْلُ لَكُمْ وَجْهُ اَبِيْكُمْ وَتَكُوْنُوْا مِنْۢ بَعْدِهٖ قَوْمًا صٰلِحِيْنَ', 'uqtulụ yụsufa awiṭraḥụhu arḍay yakhlu lakum waj-hu abīkum wa takụnụ mim ba\'dihī qauman ṣāliḥīn'),
(1605, 12, 10, 'قَالَ قَاۤئِلٌ مِّنْهُمْ لَا تَقْتُلُوْا يُوْسُفَ وَاَلْقُوْهُ فِيْ غَيٰبَتِ الْجُبِّ يَلْتَقِطْهُ بَعْضُ السَّيَّارَةِ اِنْ كُنْتُمْ فٰعِلِيْنَ', 'qāla qā`ilum min-hum lā taqtulụ yụsufa wa alqụhu fī gayābatil-jubbi yaltaqiṭ-hu ba\'ḍus-sayyārati ing kuntum fā\'ilīn'),
(1606, 12, 11, 'قَالُوْا يٰٓاَبَانَا مَالَكَ لَا تَأْمَنَّ۫ا عَلٰى يُوْسُفَ وَاِنَّا لَهٗ لَنَاصِحُوْنَ', 'qālụ yā abānā mā laka lā ta`mannā \'alā yụsufa wa innā lahụ lanāṣiḥụn'),
(1607, 12, 12, 'اَرْسِلْهُ مَعَنَا غَدًا يَّرْتَعْ وَيَلْعَبْ وَاِنَّا لَهٗ لَحٰفِظُوْنَ', 'arsil-hu ma\'anā gaday yarta\' wa yal\'ab wa innā lahụ laḥāfiẓụn'),
(1608, 12, 13, 'قَالَ اِنِّيْ لَيَحْزُنُنِيْٓ اَنْ تَذْهَبُوْا بِهٖ وَاَخَافُ اَنْ يَّأْكُلَهُ الذِّئْبُ وَاَنْتُمْ عَنْهُ غٰفِلُوْنَ', 'qāla innī layaḥzununī an taż-habụ bihī wa akhāfu ay ya`kulahuż-żi`bu wa antum \'an-hu gāfilụn'),
(1609, 12, 14, 'قَالُوْا لَىِٕنْ اَكَلَهُ الذِّئْبُ وَنَحْنُ عُصْبَةٌ اِنَّآ اِذًا لَّخٰسِرُوْنَ', 'qālụ la`in akalahuż-żi`bu wa naḥnu \'uṣbatun innā iżal lakhāsirụn'),
(1610, 12, 15, 'فَلَمَّا ذَهَبُوْا بِهٖ وَاَجْمَعُوْٓا اَنْ يَّجْعَلُوْهُ فِيْ غَيٰبَتِ الْجُبِّۚ وَاَوْحَيْنَآ اِلَيْهِ لَتُنَبِّئَنَّهُمْ بِاَمْرِهِمْ هٰذَا وَهُمْ لَا يَشْعُرُوْنَ', 'fa lammā żahabụ bihī wa ajma\'ū ay yaj\'alụhu fī gayābatil-jubb, wa auḥainā ilaihi latunabbi`annahum bi`amrihim hāżā wa hum lā yasy\'urụn'),
(1611, 12, 16, 'وَجَاۤءُوْٓ اَبَاهُمْ عِشَاۤءً يَّبْكُوْنَۗ', 'wa jā`ū abāhum \'isyā`ay yabkụn'),
(1612, 12, 17, 'قَالُوْا يٰٓاَبَانَآ اِنَّا ذَهَبْنَا نَسْتَبِقُ وَتَرَكْنَا يُوْسُفَ عِنْدَ مَتَاعِنَا فَاَكَلَهُ الذِّئْبُۚ وَمَآ اَنْتَ بِمُؤْمِنٍ لَّنَا وَلَوْ كُنَّا صٰدِقِيْنَ', 'qālụ yā abānā innā żahabnā nastabiqu wa taraknā yụsufa \'inda matā\'inā fa akalahuż-żi`b, wa mā anta bimu`minil lanā walau kunnā ṣādiqīn'),
(1613, 12, 18, 'وَجَاۤءُوْ عَلٰى قَمِيْصِهٖ بِدَمٍ كَذِبٍۗ قَالَ بَلْ سَوَّلَتْ لَكُمْ اَنْفُسُكُمْ اَمْرًاۗ فَصَبْرٌ جَمِيْلٌ ۗوَاللّٰهُ الْمُسْتَعَانُ عَلٰى مَا تَصِفُوْنَ', 'wa jā`ụ \'alā qamīṣihī bidaming każib, qāla bal sawwalat lakum anfusukum amrā, fa ṣabrun jamīl, wallāhul-musta\'ānu \'alā mā taṣifụn'),
(1614, 12, 19, 'وَجَاۤءَتْ سَيَّارَةٌ فَاَرْسَلُوْا وَارِدَهُمْ فَاَدْلٰى دَلْوَهٗ ۗقَالَ يٰبُشْرٰى هٰذَا غُلٰمٌ ۗوَاَسَرُّوْهُ بِضَاعَةً ۗوَاللّٰهُ عَلِيْمٌ ۢبِمَا يَعْمَلُوْنَ', 'wa jā`at sayyāratun fa arsalụ wāridahum fa adlā dalwah, qāla yā busyrā hāżā gulām, wa asarrụhu biḍā\'ah, wallāhu \'alīmum bimā ya\'malụn'),
(1615, 12, 20, 'وَشَرَوْهُ بِثَمَنٍۢ بَخْسٍ دَرَاهِمَ مَعْدُوْدَةٍ ۚوَكَانُوْا فِيْهِ مِنَ الزَّاهِدِيْنَ', 'wa syarauhu biṡamanim bakhsin darāhima ma\'dụdah, wa kānụ fīhi minaz-zāhidīn'),
(1616, 12, 21, 'وَقَالَ الَّذِى اشْتَرٰىهُ مِنْ مِّصْرَ لِامْرَاَتِهٖٓ اَكْرِمِيْ مَثْوٰىهُ عَسٰىٓ اَنْ يَّنْفَعَنَآ اَوْ نَتَّخِذَهٗ وَلَدًا ۗوَكَذٰلِكَ مَكَّنَّا لِيُوْسُفَ فِى الْاَرْضِۖ وَلِنُعَلِّمَهٗ مِنْ تَأْوِيْلِ الْاَحَادِيْثِۗ وَاللّٰهُ غَالِبٌ عَلٰٓى اَمْرِهٖ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ', 'wa qālallażisytarāhu mim miṣra limra`atihī akrimī maṡwāhu \'asā ay yanfa\'anā au nattakhiżahụ waladā, wa każālika makkannā liyụsufa fil-arḍi wa linu\'allimahụ min ta`wīlil-aḥādīṡ, wallāhu gālibun \'alā amrihī wa lākinna akṡaran-nāsi lā ya\'lamụn'),
(1617, 12, 22, 'وَلَمَّا بَلَغَ اَشُدَّهٗٓ اٰتَيْنٰهُ حُكْمًا وَّعِلْمًا ۗوَكَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ', 'wa lammā balaga asyuddahū ātaināhu ḥukmaw wa \'ilmā, wa każālika najzil-muḥsinīn'),
(1618, 12, 23, 'وَرَاوَدَتْهُ الَّتِيْ هُوَ فِيْ بَيْتِهَا عَنْ نَّفْسِهٖ وَغَلَّقَتِ الْاَبْوَابَ وَقَالَتْ هَيْتَ لَكَ ۗقَالَ مَعَاذَ اللّٰهِ اِنَّهٗ رَبِّيْٓ اَحْسَنَ مَثْوَايَۗ اِنَّهٗ لَا يُفْلِحُ الظّٰلِمُوْنَ', 'wa rāwadat-hullatī huwa fī baitihā \'an nafsihī wa gallaqatil-abwāba wa qālat haita lak, qāla ma\'āżallāhi innahụ rabbī aḥsana maṡwāy, innahụ lā yufliḥuẓ-ẓālimụn'),
(1619, 12, 24, 'وَلَقَدْ هَمَّتْ بِهٖۙ وَهَمَّ بِهَا ۚ لَوْلَآ اَنْ رَّاٰى بُرْهَانَ رَبِّهٖۗ كَذٰلِكَ لِنَصْرِفَ عَنْهُ السُّوْۤءَ وَالْفَحْشَاۤءَۗ اِنَّهٗ مِنْ عِبَادِنَا الْمُخْلَصِيْنَ', 'wa laqad hammat bihī wa hamma bihā, lau lā ar ra`ā bur-hāna rabbih, każālika linaṣrifa \'an-hus-sū`a wal-faḥsyā`, innahụ min \'ibādinal-mukhlaṣīn'),
(1620, 12, 25, 'وَاسْتَبَقَا الْبَابَ وَقَدَّتْ قَمِيْصَهٗ مِنْ دُبُرٍ وَّاَلْفَيَا سَيِّدَهَا لَدَا الْبَابِۗ قَالَتْ مَا جَزَاۤءُ مَنْ اَرَادَ بِاَهْلِكَ سُوْۤءًا اِلَّآ اَنْ يُّسْجَنَ اَوْ عَذَابٌ اَلِيْمٌ', 'wastabaqal-bāba wa qaddat qamīṣahụ min duburiw wa alfayā sayyidahā ladal-bāb, qālat mā jazā`u man arāda bi`ahlika sū`an illā ay yusjana au \'ażābun alīm'),
(1621, 12, 26, 'قَالَ هِيَ رَاوَدَتْنِيْ عَنْ نَّفْسِيْ وَشَهِدَ شَاهِدٌ مِّنْ اَهْلِهَاۚ اِنْ كَانَ قَمِيْصُهٗ قُدَّ مِنْ قُبُلٍ فَصَدَقَتْ وَهُوَ مِنَ الْكٰذِبِيْنَ', 'qāla hiya rāwadatnī \'an nafsī wa syahida syāhidum min ahlihā, ing kāna qamīṣuhụ qudda ming qubulin fa ṣadaqat wa huwa minal-kāżibīn'),
(1622, 12, 27, 'وَاِنْ كَانَ قَمِيْصُهٗ قُدَّ مِنْ دُبُرٍ فَكَذَبَتْ وَهُوَ مِنَ الصّٰدِقِيْنَ', 'wa ing kāna qamīṣuhụ qudda min duburin fa każabat wa huwa minaṣ-ṣādiqīn'),
(1623, 12, 28, 'فَلَمَّا رَاٰى قَمِيْصَهٗ قُدَّ مِنْ دُبُرٍ قَالَ اِنَّهٗ مِنْ كَيْدِكُنَّ ۗاِنَّ كَيْدَكُنَّ عَظِيْمٌ', 'fa lammā ra`ā qamīṣahụ qudda min duburing qāla innahụ ming kaidikunn, inna kaidakunna \'aẓīm'),
(1624, 12, 29, 'يُوْسُفُ اَعْرِضْ عَنْ هٰذَا وَاسْتَغْفِرِيْ لِذَنْۢبِكِۖ اِنَّكِ كُنْتِ مِنَ الْخٰطِـِٕيْنَ', 'yụsufu a\'riḍ \'an hāżā wastagfirī liżambiki innaki kunti minal-khāṭi`īn'),
(1625, 12, 30, '۞ وَقَالَ نِسْوَةٌ فِى الْمَدِيْنَةِ امْرَاَتُ الْعَزِيْزِ تُرَاوِدُ فَتٰىهَا عَنْ نَّفْسِهٖۚ قَدْ شَغَفَهَا حُبًّاۗ اِنَّا لَنَرٰىهَا فِيْ ضَلٰلٍ مُّبِيْنٍ', 'wa qāla niswatun fil-madīnatimra`atul-\'azīzi turāwidu fatāhā \'an nafsih, qad syagafahā ḥubbā, innā lanarāhā fī ḍalālim mubīn'),
(1626, 12, 31, 'فَلَمَّا سَمِعَتْ بِمَكْرِهِنَّ اَرْسَلَتْ اِلَيْهِنَّ وَاَعْتَدَتْ لَهُنَّ مُتَّكَاً وَّاٰتَتْ كُلَّ وَاحِدَةٍ مِّنْهُنَّ سِكِّيْنًا وَّقَالَتِ اخْرُجْ عَلَيْهِنَّ ۚ فَلَمَّا رَاَيْنَهٗٓ اَكْبَرْنَهٗ وَقَطَّعْنَ اَيْدِيَهُنَّۖ وَقُلْنَ حَاشَ لِلّٰهِ مَا هٰذَا بَشَرًاۗ اِنْ هٰذَآ اِلَّا مَلَكٌ كَرِيْمٌ', 'fa lammā sami\'at bimakrihinna arsalat ilaihinna wa a\'tadat lahunna muttaka`aw wa ātat kulla wāḥidatim min-hunna sikkīnaw wa qālatikhruj \'alaihinn, fa lammā ra`ainahū akbarnahụ wa qaṭṭa\'na aidiyahunna wa qulna ḥāsya lillāhi mā hāżā basyarā, in hāżā illā malakung karīm');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(1627, 12, 32, 'قَالَتْ فَذٰلِكُنَّ الَّذِيْ لُمْتُنَّنِيْ فِيْهِ ۗوَلَقَدْ رَاوَدْتُّهٗ عَنْ نَّفْسِهٖ فَاسْتَعْصَمَ ۗوَلَىِٕنْ لَّمْ يَفْعَلْ مَآ اٰمُرُهٗ لَيُسْجَنَنَّ وَلَيَكُوْنًا مِّنَ الصّٰغِرِيْنَ', 'qālat fa żālikunnallażī lumtunnanī fīh, wa laqad rāwattuhụ \'an nafsihī fasta\'ṣam, wa la`il lam yaf\'al mā āmuruhụ layusjananna wa layakụnam minaṣ-ṣāgirīn'),
(1628, 12, 33, 'قَالَ رَبِّ السِّجْنُ اَحَبُّ اِلَيَّ مِمَّا يَدْعُوْنَنِيْٓ اِلَيْهِ ۚوَاِلَّا تَصْرِفْ عَنِّيْ كَيْدَهُنَّ اَصْبُ اِلَيْهِنَّ وَاَكُنْ مِّنَ الْجٰهِلِيْنَ', 'qāla rabbis-sijnu aḥabbu ilayya mimmā yad\'ụnanī ilaīh, wa illā taṣrif \'annī kaidahunna aṣbu ilaihinna wa akum minal-jāhilīn'),
(1629, 12, 34, 'فَاسْتَجَابَ لَهٗ رَبُّهٗ فَصَرَفَ عَنْهُ كَيْدَهُنَّ ۗاِنَّهٗ هُوَ السَّمِيْعُ الْعَلِيْمُ', 'fastajāba lahụ rabbuhụ fa ṣarafa \'an-hu kaidahunn, innahụ huwas-samī\'ul \'alīm'),
(1630, 12, 35, 'ثُمَّ بَدَا لَهُمْ مِّنْۢ بَعْدِ مَا رَاَوُا الْاٰيٰتِ لَيَسْجُنُنَّهٗ حَتّٰى حِيْنٍ', 'ṡumma badā lahum mim ba\'di mā ra`awul-āyāti layasjununnahụ ḥattā ḥīn'),
(1631, 12, 36, 'وَدَخَلَ مَعَهُ السِّجْنَ فَتَيٰنِ ۗقَالَ اَحَدُهُمَآ اِنِّيْٓ اَرٰىنِيْٓ اَعْصِرُ خَمْرًا ۚوَقَالَ الْاٰخَرُ اِنِّيْٓ اَرٰىنِيْٓ اَحْمِلُ فَوْقَ رَأْسِيْ خُبْزًا تَأْكُلُ الطَّيْرُ مِنْهُ ۗنَبِّئْنَا بِتَأْوِيْلِهٖ ۚاِنَّا نَرٰىكَ مِنَ الْمُحْسِنِيْنَ', 'wa dakhala ma\'ahus-sijna fatayān, qāla aḥaduhumā innī arānī a\'ṣiru khamrā, wa qālal-ākharu innī arānī aḥmilu fauqa ra`sī khubzan ta`kuluṭ-ṭairu min-h, nabbi`nā bita`wīlih, innā narāka minal-muḥsinīn'),
(1632, 12, 37, 'قَالَ لَا يَأْتِيْكُمَا طَعَامٌ تُرْزَقٰنِهٖٓ اِلَّا نَبَّأْتُكُمَا بِتَأْوِيْلِهٖ قَبْلَ اَنْ يَّأْتِيَكُمَا ۗذٰلِكُمَا مِمَّا عَلَّمَنِيْ رَبِّيْۗ اِنِّيْ تَرَكْتُ مِلَّةَ قَوْمٍ لَّا يُؤْمِنُوْنَ بِاللّٰهِ وَهُمْ بِالْاٰخِرَةِ هُمْ كٰفِرُوْنَۙ', 'qāla lā ya`tīkumā ṭa\'āmun turzaqānihī illā nabba`tukumā bita`wīlihī qabla ay ya`tiyakumā, żālikumā mimmā \'allamanī rabbī, innī taraktu millata qaumil lā yu`minụna billāhi wa hum bil-ākhirati hum kāfirụn'),
(1633, 12, 38, 'وَاتَّبَعْتُ مِلَّةَ اٰبَاۤءِيْٓ اِبْرٰهِيْمَ وَاِسْحٰقَ وَيَعْقُوْبَۗ مَا كَانَ لَنَآ اَنْ نُّشْرِكَ بِاللّٰهِ مِنْ شَيْءٍۗ ذٰلِكَ مِنْ فَضْلِ اللّٰهِ عَلَيْنَا وَعَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَشْكُرُوْنَ', 'wattaba\'tu millata ābā`ī ibrāhīma wa is-ḥāqa wa ya\'qụb, mā kāna lanā an nusyrika billāhi min syaī`, żālika min faḍlillāhi \'alainā wa \'alan-nāsi wa lākinna akṡaran-nāsi lā yasykurụn'),
(1634, 12, 39, 'يٰصَاحِبَيِ السِّجْنِ ءَاَرْبَابٌ مُتَفَرِّقُوْنَ خَيْرٌ اَمِ اللّٰهُ الْوَاحِدُ الْقَهَّارُۗ', 'yā ṣāḥibayis-sijni a arbābum mutafarriqụna khairun amillāhul-wāḥidul-qahhār'),
(1635, 12, 40, 'مَا تَعْبُدُوْنَ مِنْ دُوْنِهٖٓ اِلَّآ اَسْمَاۤءً سَمَّيْتُمُوْهَآ اَنْتُمْ وَاٰبَاۤؤُكُمْ مَّآ اَنْزَلَ اللّٰهُ بِهَا مِنْ سُلْطٰنٍۗ اِنِ الْحُكْمُ اِلَّا لِلّٰهِ ۗاَمَرَ اَلَّا تَعْبُدُوْٓا اِلَّآ اِيَّاهُ ۗذٰلِكَ الدِّيْنُ الْقَيِّمُ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ', 'mā ta\'budụna min dụnihī illā asmā`an sammaitumụhā antum wa ābā`ukum mā anzalallāhu bihā min sulṭān, inil-ḥukmu illā lillāh, amara allā ta\'budū illā iyyāh, żālikad-dīnul-qayyimu wa lākinna akṡaran-nāsi lā ya\'lamụn'),
(1636, 12, 41, 'يٰصَاحِبَيِ السِّجْنِ اَمَّآ اَحَدُكُمَا فَيَسْقِيْ رَبَّهٗ خَمْرًا ۗوَاَمَّا الْاٰخَرُ فَيُصْلَبُ فَتَأْكُلُ الطَّيْرُ مِنْ رَّأْسِهٖ ۗ قُضِيَ الْاَمْرُ الَّذِيْ فِيْهِ تَسْتَفْتِيٰنِۗ', 'yā ṣāḥibayis-sijni ammā aḥadukumā fa yasqī rabbahụ khamrā, wa ammal-ākharu fa yuṣlabu fa ta`kuluṭ-ṭairu mir ra`sih, quḍiyal-amrullażī fīhi tastaftiyān'),
(1637, 12, 42, 'وَقَالَ لِلَّذِيْ ظَنَّ اَنَّهٗ نَاجٍ مِّنْهُمَا اذْكُرْنِيْ عِنْدَ رَبِّكَۖ فَاَنْسٰىهُ الشَّيْطٰنُ ذِكْرَ رَبِّهٖ فَلَبِثَ فِى السِّجْنِ بِضْعَ سِنِيْنَ', 'wa qāla lillażī ẓanna annahụ nājim min-humażkurnī \'inda rabbika fa ansāhusy-syaiṭānu żikra rabbihī fa labiṡa fis-sijni biḍ\'a sinīn'),
(1638, 12, 43, 'وَقَالَ الْمَلِكُ اِنِّيْٓ اَرٰى سَبْعَ بَقَرٰتٍ سِمَانٍ يَّأْكُلُهُنَّ سَبْعٌ عِجَافٌ وَّسَبْعَ سُنْۢبُلٰتٍ خُضْرٍ وَّاُخَرَ يٰبِسٰتٍۗ يٰٓاَيُّهَا الْمَلَاُ اَفْتُوْنِيْ فِيْ رُؤْيَايَ اِنْ كُنْتُمْ لِلرُّءْيَا تَعْبُرُوْنَ', 'wa qālal-maliku innī arā sab\'a baqarātin simāniy ya`kuluhunna sab\'un \'ijāfuw wa sab\'a sumbulātin khuḍriw wa ukhara yābisāt, yā ayyuhal-mala`u aftụnī fī ru`yāya ing kuntum lir-ru`yā ta\'burụn'),
(1639, 12, 44, 'قَالُوْٓا اَضْغَاثُ اَحْلَامٍ ۚوَمَا نَحْنُ بِتَأْوِيْلِ الْاَحْلَامِ بِعٰلِمِيْنَ', 'qālū aḍgāṡu aḥlām, wa mā naḥnu bita`wīlil-aḥlāmi bi\'ālimīn'),
(1640, 12, 45, 'وَقَالَ الَّذِيْ نَجَا مِنْهُمَا وَادَّكَرَ بَعْدَ اُمَّةٍ اَنَا۠ اُنَبِّئُكُمْ بِتَأْوِيْلِهٖ فَاَرْسِلُوْنِ', 'wa qālallażī najā min-humā waddakara ba\'da ummatin ana unabbi`ukum bita`wīlihī fa arsilụn'),
(1641, 12, 46, 'يُوْسُفُ اَيُّهَا الصِّدِّيْقُ اَفْتِنَا فِيْ سَبْعِ بَقَرٰتٍ سِمَانٍ يَّأْكُلُهُنَّ سَبْعٌ عِجَافٌ وَّسَبْعِ سُنْۢبُلٰتٍ خُضْرٍ وَّاُخَرَ يٰبِسٰتٍۙ لَّعَلِّيْٓ اَرْجِعُ اِلَى النَّاسِ لَعَلَّهُمْ يَعْلَمُوْنَ', 'yụsufu ayyuhaṣ-ṣiddīqu aftinā fī sab\'i baqarātin simāniy ya`kuluhunna sab\'un \'ijāfuw wa sab\'i sumbulātin khuḍriw wa ukhara yābisātil la\'allī arji\'u ilan-nāsi la\'allahum ya\'lamụn'),
(1642, 12, 47, 'قَالَ تَزْرَعُوْنَ سَبْعَ سِنِيْنَ دَاَبًاۚ فَمَا حَصَدْتُّمْ فَذَرُوْهُ فِيْ سُنْۢبُلِهٖٓ اِلَّا قَلِيْلًا مِّمَّا تَأْكُلُوْنَ', 'qāla tazra\'ụna sab\'a sinīna da`abā, fa mā ḥaṣattum fa żarụhu fī sumbulihī illā qalīlam mimmā ta`kulụn'),
(1643, 12, 48, 'ثُمَّ يَأْتِيْ مِنْۢ بَعْدِ ذٰلِكَ سَبْعٌ شِدَادٌ يَّأْكُلْنَ مَا قَدَّمْتُمْ لَهُنَّ اِلَّا قَلِيْلًا مِّمَّا تُحْصِنُوْنَ', 'ṡumma ya`tī mim ba\'di żālika sab\'un syidāduy ya`kulna mā qaddamtum lahunna illā qalīlam mimmā tuḥṣinụn'),
(1644, 12, 49, 'ثُمَّ يَأْتِيْ مِنْۢ بَعْدِ ذٰلِكَ عَامٌ فِيْهِ يُغَاثُ النَّاسُ وَفِيْهِ يَعْصِرُوْنَ', 'ṡumma ya`tī mim ba\'di żālika \'āmun fīhi yugāṡun-nāsu wa fīhi ya\'ṣirụn'),
(1645, 12, 50, 'وَقَالَ الْمَلِكُ ائْتُوْنِيْ بِهٖ ۚفَلَمَّا جَاۤءَهُ الرَّسُوْلُ قَالَ ارْجِعْ اِلٰى رَبِّكَ فَسْـَٔلْهُ مَا بَالُ النِّسْوَةِ الّٰتِيْ قَطَّعْنَ اَيْدِيَهُنَّ ۗاِنَّ رَبِّيْ بِكَيْدِهِنَّ عَلِيْمٌ', 'wa qālal-maliku`tụnī bih, fa lammā jā`ahur-rasụlu qālarji\' ilā rabbika fas`al-hu mā bālun-niswatillātī qaṭṭa\'na aidiyahunn, inna rabbī bikaidihinna \'alīm'),
(1646, 12, 51, 'قَالَ مَا خَطْبُكُنَّ اِذْ رَاوَدْتُّنَّ يُوْسُفَ عَنْ نَّفْسِهٖۗ قُلْنَ حَاشَ لِلّٰهِ مَا عَلِمْنَا عَلَيْهِ مِنْ سُوْۤءٍ ۗقَالَتِ امْرَاَتُ الْعَزِيْزِ الْـٰٔنَ حَصْحَصَ الْحَقُّۖ اَنَا۠ رَاوَدْتُّهٗ عَنْ نَّفْسِهٖ وَاِنَّهٗ لَمِنَ الصّٰدِقِيْنَ', 'qāla mā khaṭbukunna iż rāwattunna yụsufa \'an nafsih, qulna ḥāsya lillāhi mā \'alimnā \'alaihi min sū`, qālatimra`atul-\'azīzil-āna ḥaṣ-ḥaṣal-ḥaqqu ana rāwattuhụ \'an nafsihī wa innahụ laminaṣ-ṣādiqīn'),
(1647, 12, 52, 'ذٰلِكَ لِيَعْلَمَ اَنِّيْ لَمْ اَخُنْهُ بِالْغَيْبِ وَاَنَّ اللّٰهَ لَا يَهْدِيْ كَيْدَ الْخَاۤىِٕنِيْنَ ۔', 'żālika liya\'lama annī lam akhun-hu bil-gaibi wa annallāha lā yahdī kaidal-khā`inīn'),
(1648, 12, 53, '۞ وَمَآ اُبَرِّئُ نَفْسِيْۚ اِنَّ النَّفْسَ لَاَمَّارَةٌ ۢ بِالسُّوْۤءِ اِلَّا مَا رَحِمَ رَبِّيْۗ اِنَّ رَبِّيْ غَفُوْرٌ رَّحِيْمٌ', 'wa mā ubarri`u nafsī, innan-nafsa la`ammāratum bis-sū`i illā mā raḥima rabbī, inna rabbī gafụrur raḥīm'),
(1649, 12, 54, 'وَقَالَ الْمَلِكُ ائْتُوْنِيْ بِهٖٓ اَسْتَخْلِصْهُ لِنَفْسِيْۚ فَلَمَّا كَلَّمَهٗ قَالَ اِنَّكَ الْيَوْمَ لَدَيْنَا مَكِيْنٌ اَمِيْنٌ', 'wa qālal-maliku`tụnī bihī astakhliṣ-hu linafsī, fa lammā kallamahụ qāla innakal-yauma ladainā makīnun amīn'),
(1650, 12, 55, 'قَالَ اجْعَلْنِيْ عَلٰى خَزَاۤىِٕنِ الْاَرْضِۚ اِنِّيْ حَفِيْظٌ عَلِيْمٌ', 'qālaj\'alnī \'alā khazā`inil-arḍ, innī ḥafīẓun \'alīm'),
(1651, 12, 56, 'وَكَذٰلِكَ مَكَّنَّا لِيُوْسُفَ فِى الْاَرْضِ يَتَبَوَّاُ مِنْهَا حَيْثُ يَشَاۤءُۗ نُصِيْبُ بِرَحْمَتِنَا مَنْ نَّشَاۤءُ وَلَا نُضِيْعُ اَجْرَ الْمُحْسِنِيْنَ', 'wa każālika makkannā liyụsufa fil-arḍi yatabawwa`u min-hā ḥaiṡu yasyā`, nuṣību biraḥmatinā man nasyā`u wa lā nuḍī\'u ajral-muḥsinīn'),
(1652, 12, 57, 'وَلَاَجْرُ الْاٰخِرَةِ خَيْرٌ لِّلَّذِيْنَ اٰمَنُوْا وَكَانُوْا يَتَّقُوْنَ', 'wa la`ajrul-ākhirati khairul lillażīna āmanụ wa kānụ yattaqụn'),
(1653, 12, 58, 'وَجَاۤءَ اِخْوَةُ يُوْسُفَ فَدَخَلُوْا عَلَيْهِ فَعَرَفَهُمْ وَهُمْ لَهٗ مُنْكِرُوْنَ', 'wa jā`a ikhwatu yụsufa fa dakhalụ \'alaihi fa \'arafahum wa hum lahụ mungkirụn'),
(1654, 12, 59, 'وَلَمَّا جَهَّزَهُمْ بِجَهَازِهِمْ قَالَ ائْتُوْنِيْ بِاَخٍ لَّكُمْ مِّنْ اَبِيْكُمْ ۚ اَلَا تَرَوْنَ اَنِّيْٓ اُوْفِى الْكَيْلَ وَاَنَا۠ خَيْرُ الْمُنْزِلِيْنَ', 'wa lammā jahhazahum bijahāzihim qāla`tụnī bi`akhil lakum min abīkum, alā tarauna annī ụfil-kaila wa ana khairul-munzilīn'),
(1655, 12, 60, 'فَاِنْ لَّمْ تَأْتُوْنِيْ بِهٖ فَلَا كَيْلَ لَكُمْ عِنْدِيْ وَلَا تَقْرَبُوْنِ', 'fa il lam ta`tụnī bihī fa lā kaila lakum \'indī wa lā taqrabụn'),
(1656, 12, 61, 'قَالُوْا سَنُرَاوِدُ عَنْهُ اَبَاهُ وَاِنَّا لَفَاعِلُوْنَ', 'qālụ sanurāwidu \'an-hu abāhu wa innā lafā\'ilụn'),
(1657, 12, 62, 'وَقَالَ لِفِتْيٰنِهِ اجْعَلُوْا بِضَاعَتَهُمْ فِيْ رِحَالِهِمْ لَعَلَّهُمْ يَعْرِفُوْنَهَآ اِذَا انْقَلَبُوْٓا اِلٰٓى اَهْلِهِمْ لَعَلَّهُمْ يَرْجِعُوْنَ', 'wa qāla lifityānihij\'alụ biḍā\'atahum fī riḥālihim la\'allahum ya\'rifụnahā iżangqalabū ilā ahlihim la\'allahum yarji\'ụn'),
(1658, 12, 63, 'فَلَمَّا رَجَعُوْٓا اِلٰٓى اَبِيْهِمْ قَالُوْا يٰٓاَبَانَا مُنِعَ مِنَّا الْكَيْلُ فَاَرْسِلْ مَعَنَآ اَخَانَا نَكْتَلْ وَاِنَّا لَهٗ لَحٰفِظُوْنَ', 'fa lammā raja\'ū ilā abīhim qālụ yā abānā muni\'a minnal-kailu fa arsil ma\'anā akhānā naktal wa innā lahụ laḥāfiẓụn'),
(1659, 12, 64, 'قَالَ هَلْ اٰمَنُكُمْ عَلَيْهِ اِلَّا كَمَآ اَمِنْتُكُمْ عَلٰٓى اَخِيْهِ مِنْ قَبْلُۗ فَاللّٰهُ خَيْرٌ حٰفِظًا وَّهُوَ اَرْحَمُ الرّٰحِمِيْنَ', 'qāla hal āmanukum \'alaihi illā kamā amintukum \'alā akhīhi ming qabl, fallāhu khairun ḥāfiẓaw wa huwa ar-ḥamur-rāḥimīn'),
(1660, 12, 65, 'وَلَمَّا فَتَحُوْا مَتَاعَهُمْ وَجَدُوْا بِضَاعَتَهُمْ رُدَّتْ اِلَيْهِمْۗ قَالُوْا يٰٓاَبَانَا مَا نَبْغِيْۗ هٰذِهٖ بِضَاعَتُنَا رُدَّتْ اِلَيْنَا وَنَمِيْرُ اَهْلَنَا وَنَحْفَظُ اَخَانَا وَنَزْدَادُ كَيْلَ بَعِيْرٍۗ ذٰلِكَ كَيْلٌ يَّسِيْرٌ', 'wa lammā fataḥụ matā\'ahum wajadụ biḍā\'atahum ruddat ilaihim, qālụ yā abānā mā nabgī, hāżihī biḍā\'atunā ruddat ilainā wa namīru ahlanā wa naḥfaẓu akhānā wa nazdādu kaila ba\'īr, żālika kailuy yasīr'),
(1661, 12, 66, 'قَالَ لَنْ اُرْسِلَهٗ مَعَكُمْ حَتّٰى تُؤْتُوْنِ مَوْثِقًا مِّنَ اللّٰهِ لَتَأْتُنَّنِيْ بِهٖٓ اِلَّآ اَنْ يُّحَاطَ بِكُمْۚ فَلَمَّآ اٰتَوْهُ مَوْثِقَهُمْ قَالَ اللّٰهُ عَلٰى مَا نَقُوْلُ وَكِيْلٌ', 'qāla lan ursilahụ ma\'akum ḥattā tu`tụni mauṡiqam minallāhi lata`tunnanī bihī illā ay yuḥāṭa bikum, fa lammā ātauhu mauṡiqahum qālallāhu \'alā mā naqụlu wakīl'),
(1662, 12, 67, 'وَقَالَ يٰبَنِيَّ لَا تَدْخُلُوْا مِنْۢ بَابٍ وَّاحِدٍ وَّادْخُلُوْا مِنْ اَبْوَابٍ مُّتَفَرِّقَةٍۗ وَمَآ اُغْنِيْ عَنْكُمْ مِّنَ اللّٰهِ مِنْ شَيْءٍۗ اِنِ الْحُكْمُ اِلَّا لِلّٰهِ ۗعَلَيْهِ تَوَكَّلْتُ وَعَلَيْهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُوْنَ', 'wa qāla yā baniyya lā tadkhulụ mim bābiw wāḥidiw wadkhulụ min abwābim mutafarriqah, wa mā ugnī \'angkum minallāhi min syaī`, inil-ḥukmu illā lillāh, \'alaihi tawakkaltu wa \'alaihi falyatawakkalil-mutawakkilụn'),
(1663, 12, 68, 'وَلَمَّا دَخَلُوْا مِنْ حَيْثُ اَمَرَهُمْ اَبُوْهُمْۗ مَا كَانَ يُغْنِيْ عَنْهُمْ مِّنَ اللّٰهِ مِنْ شَيْءٍ اِلَّا حَاجَةً فِيْ نَفْسِ يَعْقُوْبَ قَضٰىهَاۗ وَاِنَّهٗ لَذُوْ عِلْمٍ لِّمَا عَلَّمْنٰهُ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ', 'wa lammā dakhalụ min ḥaiṡu amarahum abụhum, mā kāna yugnī \'an-hum minallāhi min syai`in illā ḥājatan fī nafsi ya\'qụba qaḍāhā, wa innahụ lażụ \'ilmil limā \'allamnāhu wa lākinna akṡaran-nāsi lā ya\'lamụn'),
(1664, 12, 69, 'وَلَمَّا دَخَلُوْا عَلٰى يُوْسُفَ اٰوٰٓى اِلَيْهِ اَخَاهُ قَالَ اِنِّيْٓ اَنَا۠ اَخُوْكَ فَلَا تَبْتَىِٕسْ بِمَا كَانُوْا يَعْمَلُوْنَ', 'wa lammā dakhalụ \'alā yụsufa āwā ilaihi akhāhu qāla innī ana akhụka fa lā tabta`is bimā kānụ ya\'malụn'),
(1665, 12, 70, 'فَلَمَّا جَهَّزَهُمْ بِجَهَازِهِمْ جَعَلَ السِّقَايَةَ فِيْ رَحْلِ اَخِيْهِ ثُمَّ اَذَّنَ مُؤَذِّنٌ اَيَّتُهَا الْعِيْرُ اِنَّكُمْ لَسَارِقُوْنَ', 'fa lammā jahhazahum bijahāzihim ja\'alas-siqāyata fī raḥli akhīhi ṡumma ażżana mu`ażżinun ayyatuhal-\'īru innakum lasāriqụn'),
(1666, 12, 71, 'قَالُوْا وَاَقْبَلُوْا عَلَيْهِمْ مَّاذَا تَفْقِدُوْنَ', 'qālụ wa aqbalụ \'alaihim māżā tafqidụn'),
(1667, 12, 72, 'قَالُوْا نَفْقِدُ صُوَاعَ الْمَلِكِ وَلِمَنْ جَاۤءَ بِهٖ حِمْلُ بَعِيْرٍ وَّاَنَا۠ بِهٖ زَعِيْمٌ', 'qālụ nafqidu ṣuwā\'al-maliki wa liman jā`a bihī ḥimlu ba\'īriw wa ana bihī za\'īm'),
(1668, 12, 73, 'قَالُوْا تَاللّٰهِ لَقَدْ عَلِمْتُمْ مَّا جِئْنَا لِنُفْسِدَ فِى الْاَرْضِ وَمَا كُنَّا سَارِقِيْنَ', 'qālụ tallāhi laqad \'alimtum mā ji`nā linufsida fil-arḍi wa mā kunnā sāriqīn'),
(1669, 12, 74, 'قَالُوْا فَمَا جَزَاۤؤُهٗٓ اِنْ كُنْتُمْ كٰذِبِيْنَ', 'qālụ fa mā jazā`uhū ing kuntum kāżibīn'),
(1670, 12, 75, 'قَالُوْا جَزَاۤؤُهٗ مَنْ وُّجِدَ فِيْ رَحْلِهٖ فَهُوَ جَزَاۤؤُهٗ ۗ كَذٰلِكَ نَجْزِى الظّٰلِمِيْنَ', 'qālụ jazā`uhụ maw wujida fī raḥlihī fa huwa jazā`uh, każālika najziẓ-ẓālimīn'),
(1671, 12, 76, 'فَبَدَاَ بِاَوْعِيَتِهِمْ قَبْلَ وِعَاۤءِ اَخِيْهِ ثُمَّ اسْتَخْرَجَهَا مِنْ وِّعَاۤءِ اَخِيْهِۗ كَذٰلِكَ كِدْنَا لِيُوْسُفَۗ مَا كَانَ لِيَأْخُذَ اَخَاهُ فِيْ دِيْنِ الْمَلِكِ اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ ۗنَرْفَعُ دَرَجَاتٍ مَّنْ نَّشَاۤءُۗ وَفَوْقَ كُلِّ ذِيْ عِلْمٍ عَلِيْمٌ', 'fa bada`a bi`au\'iyatihim qabla wi\'ā`i akhīhi ṡummastakhrajahā miw wi\'ā`i akhīh, każālika kidnā liyụsuf, mā kāna liya`khuża akhāhu fī dīnil-maliki illā ay yasyā`allāh, narfa\'u darajātim man nasyā`, wa fauqa kulli żī \'ilmin \'alīm'),
(1672, 12, 77, '۞ قَالُوْٓا اِنْ يَّسْرِقْ فَقَدْ سَرَقَ اَخٌ لَّهٗ مِنْ قَبْلُۚ فَاَسَرَّهَا يُوْسُفُ فِيْ نَفْسِهٖ وَلَمْ يُبْدِهَا لَهُمْۚ قَالَ اَنْتُمْ شَرٌّ مَّكَانًا ۚوَاللّٰهُ اَعْلَمُ بِمَا تَصِفُوْنَ', 'qālū iy yasriq fa qad saraqa akhul lahụ ming qabl, fa asarrahā yụsufu fī nafsihī wa lam yubdihā lahum, qāla antum syarrum makānā, wallāhu a\'lamu bimā taṣifụn'),
(1673, 12, 78, 'قَالُوْا يٰٓاَيُّهَا الْعَزِيْزُ اِنَّ لَهٗٓ اَبًا شَيْخًا كَبِيْرًا فَخُذْ اَحَدَنَا مَكَانَهٗ ۚاِنَّا نَرٰىكَ مِنَ الْمُحْسِنِيْنَ', 'qālụ yā ayyuhal-\'azīzu inna lahū aban syaikhang kabīran fa khuż aḥadanā makānah, innā narāka minal-muḥsinīn'),
(1674, 12, 79, 'قَالَ مَعَاذَ اللّٰهِ اَنْ نَّأْخُذَ اِلَّا مَنْ وَّجَدْنَا مَتَاعَنَا عِنْدَهٗٓ ۙاِنَّآ اِذًا لَّظٰلِمُوْنَ', 'qāla ma\'āżallāhi an na`khuża illā maw wajadnā matā\'anā \'indahū innā iżal laẓālimụn'),
(1675, 12, 80, 'فَلَمَّا اسْتَا۟يْـَٔسُوْا مِنْهُ خَلَصُوْا نَجِيًّاۗ قَالَ كَبِيْرُهُمْ اَلَمْ تَعْلَمُوْٓا اَنَّ اَبَاكُمْ قَدْ اَخَذَ عَلَيْكُمْ مَّوْثِقًا مِّنَ اللّٰهِ وَمِنْ قَبْلُ مَا فَرَّطْتُّمْ فِيْ يُوْسُفَ فَلَنْ اَبْرَحَ الْاَرْضَ حَتّٰى يَأْذَنَ لِيْٓ اَبِيْٓ اَوْ يَحْكُمَ اللّٰهُ لِيْۚ وَهُوَ خَيْرُ الْحٰكِمِيْنَ', 'fa lammastai`asụ min-hu khalaṣụ najiyyā, qāla kabīruhum a lam ta\'lamū anna abākum qad akhaża \'alaikum mauṡiqam minallāhi wa ming qablu mā farrattum fī yụsufa fa lan abraḥal-arḍa ḥattā ya`żana lī abī au yaḥkumallāhu lī, wa huwa khairul-ḥākimīn'),
(1676, 12, 81, 'اِرْجِعُوْٓا اِلٰٓى اَبِيْكُمْ فَقُوْلُوْا يٰٓاَبَانَآ اِنَّ ابْنَكَ سَرَقَۚ وَمَا شَهِدْنَآ اِلَّا بِمَا عَلِمْنَا وَمَا كُنَّا لِلْغَيْبِ حٰفِظِيْنَ', 'irji\'ū ilā abīkum fa qụlụ yā abānā innabnaka saraq, wa mā syahidnā illā bimā \'alimnā wa mā kunnā lil-gaibi ḥāfiẓīn'),
(1677, 12, 82, 'وَسْـَٔلِ الْقَرْيَةَ الَّتِيْ كُنَّا فِيْهَا وَالْعِيْرَ الَّتِيْٓ اَقْبَلْنَا فِيْهَاۗ وَاِنَّا لَصٰدِقُوْنَ', 'was`alil-qaryatallatī kunnā fīhā wal-\'īrallatī aqbalnā fīhā, wa innā laṣādiqụn'),
(1678, 12, 83, 'قَالَ بَلْ سَوَّلَتْ لَكُمْ اَنْفُسُكُمْ اَمْرًاۗ فَصَبْرٌ جَمِيْلٌ ۗعَسَى اللّٰهُ اَنْ يَّأْتِيَنِيْ بِهِمْ جَمِيْعًاۗ اِنَّهٗ هُوَ الْعَلِيْمُ الْحَكِيْمُ', 'qāla bal sawwalat lakum anfusukum amrā, fa ṣabrun jamīl, \'asallāhu ay ya`tiyanī bihim jamī\'ā, innahụ huwal-\'alīmul-ḥakīm'),
(1679, 12, 84, 'وَتَوَلّٰى عَنْهُمْ وَقَالَ يٰٓاَسَفٰى عَلٰى يُوْسُفَ وَابْيَضَّتْ عَيْنٰهُ مِنَ الْحُزْنِ فَهُوَ كَظِيْمٌ', 'wa tawallā \'an-hum wa qāla yā asafā \'alā yụsufa wabyaḍḍat \'aināhu minal-ḥuzni fa huwa kaẓīm'),
(1680, 12, 85, 'قَالُوْا تَاللّٰهِ تَفْتَؤُا تَذْكُرُ يُوْسُفَ حَتّٰى تَكُوْنَ حَرَضًا اَوْ تَكُوْنَ مِنَ الْهَالِكِيْنَ', 'qālụ tallāhi tafta`u tażkuru yụsufa ḥattā takụna ḥaraḍan au takụna minal-hālikīn'),
(1681, 12, 86, 'قَالَ اِنَّمَآ اَشْكُوْا بَثِّيْ وَحُزْنِيْٓ اِلَى اللّٰهِ وَاَعْلَمُ مِنَ اللّٰهِ مَا لَا تَعْلَمُوْنَ', 'qāla innamā asykụ baṡṡī wa ḥuznī ilallāhi wa a\'lamu minallāhi mā lā ta\'lamụn'),
(1682, 12, 87, 'يٰبَنِيَّ اذْهَبُوْا فَتَحَسَّسُوْا مِنْ يُّوْسُفَ وَاَخِيْهِ وَلَا تَا۟يْـَٔسُوْا مِنْ رَّوْحِ اللّٰهِ ۗاِنَّهٗ لَا يَا۟يْـَٔسُ مِنْ رَّوْحِ اللّٰهِ اِلَّا الْقَوْمُ الْكٰفِرُوْنَ', 'yā baniyyaż-habụ fa taḥassasụ miy yụsufa wa akhīhi wa lā tai`asụ mir rauḥillāh, innahụ lā yai`asu mir rauḥillāhi illal-qaumul-kāfirụn'),
(1683, 12, 88, 'فَلَمَّا دَخَلُوْا عَلَيْهِ قَالُوْا يٰٓاَيُّهَا الْعَزِيْزُ مَسَّنَا وَاَهْلَنَا الضُّرُّ وَجِئْنَا بِبِضَاعَةٍ مُّزْجٰىةٍ فَاَوْفِ لَنَا الْكَيْلَ وَتَصَدَّقْ عَلَيْنَاۗ اِنَّ اللّٰهَ يَجْزِى الْمُتَصَدِّقِيْنَ', 'fa lammā dakhalụ \'alaihi qālụ yā ayyuhal-\'azīzu massanā wa ahlanaḍ-ḍurru wa ji`nā bibiḍā\'atim muzjātin fa aufi lanal-kaila wa taṣaddaq \'alainā, innallāha yajzil-mutaṣaddiqīn'),
(1684, 12, 89, 'قَالَ هَلْ عَلِمْتُمْ مَّا فَعَلْتُمْ بِيُوْسُفَ وَاَخِيْهِ اِذْ اَنْتُمْ جَاهِلُوْنَ', 'qāla hal \'alimtum mā fa\'altum biyụsufa wa akhīhi iż antum jāhilụn'),
(1685, 12, 90, 'قَالُوْٓا ءَاِنَّكَ لَاَنْتَ يُوْسُفُۗ قَالَ اَنَا۠ يُوْسُفُ وَهٰذَآ اَخِيْ قَدْ مَنَّ اللّٰهُ عَلَيْنَاۗ اِنَّهٗ مَنْ يَّتَّقِ وَيَصْبِرْ فَاِنَّ اللّٰهَ لَا يُضِيْعُ اَجْرَ الْمُحْسِنِيْنَ', 'qālū a innaka la`anta yụsuf, qāla ana yụsufu wa hāżā akhī qad mannallāhu \'alainā, innahụ may yattaqi wa yaṣbir fa innallāha lā yuḍī\'u ajral-muḥsinīn'),
(1686, 12, 91, 'قَالُوْا تَاللّٰهِ لَقَدْ اٰثَرَكَ اللّٰهُ عَلَيْنَا وَاِنْ كُنَّا لَخٰطِـِٕيْنَ', 'qālụ tallāhi laqad āṡarakallāhu \'alainā wa ing kunnā lakhāṭi`īn'),
(1687, 12, 92, 'قَالَ لَا تَثْرِيْبَ عَلَيْكُمُ الْيَوْمَۗ يَغْفِرُ اللّٰهُ لَكُمْ ۖوَهُوَ اَرْحَمُ الرّٰحِمِيْنَ', 'qāla lā taṡrība \'alaikumul-yaụm, yagfirullāhu lakum wa huwa ar-ḥamur-rāḥimīn'),
(1688, 12, 93, 'اِذْهَبُوْا بِقَمِيْصِيْ هٰذَا فَاَلْقُوْهُ عَلٰى وَجْهِ اَبِيْ يَأْتِ بَصِيْرًا ۚوَأْتُوْنِيْ بِاَهْلِكُمْ اَجْمَعِيْنَ', 'iż-habụ biqamīṣī hāżā fa alqụhu \'alā waj-hi abī ya`ti baṣīrā, wa`tụnī bi`ahlikum ajma\'īn'),
(1689, 12, 94, 'وَلَمَّا فَصَلَتِ الْعِيْرُ قَالَ اَبُوْهُمْ اِنِّيْ لَاَجِدُ رِيْحَ يُوْسُفَ لَوْلَآ اَنْ تُفَنِّدُوْنِ', 'wa lammā faṣalatil-\'īru qāla abụhum innī la`ajidu rīḥa yụsufa lau lā an tufannidụn'),
(1690, 12, 95, 'قَالُوْا تَاللّٰهِ اِنَّكَ لَفِيْ ضَلٰلِكَ الْقَدِيْمِ', 'qālụ tallāhi innaka lafī ḍalālikal-qadīm'),
(1691, 12, 96, 'فَلَمَّآ اَنْ جَاۤءَ الْبَشِيْرُ اَلْقٰىهُ عَلٰى وَجْهِهٖ فَارْتَدَّ بَصِيْرًاۗ قَالَ اَلَمْ اَقُلْ لَّكُمْۙ اِنِّيْٓ اَعْلَمُ مِنَ اللّٰهِ مَا لَا تَعْلَمُوْنَ', 'fa lammā an jā`al-basyīru alqāhu \'alā waj-hihī fartadda baṣīrā, qāla a lam aqul lakum innī a\'lamu minallāhi mā lā ta\'lamụn'),
(1692, 12, 97, 'قَالُوْا يٰٓاَبَانَا اسْتَغْفِرْ لَنَا ذُنُوْبَنَآ اِنَّا كُنَّا خٰطِـِٕيْنَ', 'qālụ yā abānastagfir lanā żunụbanā innā kunnā khāṭi`īn'),
(1693, 12, 98, 'قَالَ سَوْفَ اَسْتَغْفِرُ لَكُمْ رَبِّيْ ۗاِنَّهٗ هُوَ الْغَفُوْرُ الرَّحِيْمُ', 'qāla saufa astagfiru lakum rabbī, innahụ huwal-gafụrur-raḥīm'),
(1694, 12, 99, 'فَلَمَّا دَخَلُوْا عَلٰى يُوْسُفَ اٰوٰٓى اِلَيْهِ اَبَوَيْهِ وَقَالَ ادْخُلُوْا مِصْرَ اِنْ شَاۤءَ اللّٰهُ اٰمِنِيْنَ ۗ', 'fa lammā dakhalụ \'alā yụsufa āwā ilaihi abawaihi wa qāladkhulụ miṣra in syā`allāhu āminīn'),
(1695, 12, 100, 'وَرَفَعَ اَبَوَيْهِ عَلَى الْعَرْشِ وَخَرُّوْا لَهٗ سُجَّدًاۚ وَقَالَ يٰٓاَبَتِ هٰذَا تَأْوِيْلُ رُءْيَايَ مِنْ قَبْلُ ۖقَدْ جَعَلَهَا رَبِّيْ حَقًّاۗ وَقَدْ اَحْسَنَ بِيْٓ اِذْ اَخْرَجَنِيْ مِنَ السِّجْنِ وَجَاۤءَ بِكُمْ مِّنَ الْبَدْوِ مِنْۢ بَعْدِ اَنْ نَّزَغَ الشَّيْطٰنُ بَيْنِيْ وَبَيْنَ اِخْوَتِيْۗ اِنَّ رَبِّيْ لَطِيْفٌ لِّمَا يَشَاۤءُ ۗاِنَّهٗ هُوَ الْعَلِيْمُ الْحَكِيْمُ', 'wa rafa\'a abawaihi \'alal-\'arsyi wa kharrụ lahụ sujjadā, wa qāla yā abati hāżā ta`wīlu ru`yāya ming qablu qad ja\'alahā rabbī ḥaqqā, wa qad aḥsana bī iż akhrajanī minas-sijni wa jā`a bikum minal-badwi mim ba\'di an nazagasy-syaiṭānu bainī wa baina ikhwatī, inna rabbī laṭīful limā yasyā`, innahụ huwal-\'alīmul-ḥakīm'),
(1696, 12, 101, '۞ رَبِّ قَدْ اٰتَيْتَنِيْ مِنَ الْمُلْكِ وَعَلَّمْتَنِيْ مِنْ تَأْوِيْلِ الْاَحَادِيْثِۚ فَاطِرَ السَّمٰوٰتِ وَالْاَرْضِۗ اَنْتَ وَلِيّٖ فِى الدُّنْيَا وَالْاٰخِرَةِۚ تَوَفَّنِيْ مُسْلِمًا وَّاَلْحِقْنِيْ بِالصّٰلِحِيْنَ', 'rabbi qad ātaitanī minal-mulki wa \'allamtanī min ta`wīlil-aḥādīṡ, fāṭiras-samāwāti wal-arḍ, anta waliyyī fid-dun-yā wal-ākhirah, tawaffanī muslimaw wa al-ḥiqnī biṣ-ṣāliḥīn'),
(1697, 12, 102, 'ذٰلِكَ مِنْ اَنْۢبَاۤءِ الْغَيْبِ نُوْحِيْهِ اِلَيْكَۚ وَمَا كُنْتَ لَدَيْهِمْ اِذْ اَجْمَعُوْٓا اَمْرَهُمْ وَهُمْ يَمْكُرُوْنَ', 'żālika min ambā`il-gaibi nụḥīhi ilaīk, wa mā kunta ladaihim iż ajma\'ū amrahum wa hum yamkurụn'),
(1698, 12, 103, 'وَمَآ اَكْثَرُ النَّاسِ وَلَوْ حَرَصْتَ بِمُؤْمِنِيْنَ', 'wa mā akṡarun-nāsi walau ḥaraṣta bimu`minīn'),
(1699, 12, 104, 'وَمَا تَسْـَٔلُهُمْ عَلَيْهِ مِنْ اَجْرٍۗ اِنْ هُوَ اِلَّا ذِكْرٌ لِّلْعٰلَمِيْنَ', 'wa mā tas`aluhum \'alaihi min ajr, in huwa illā żikrul lil-\'ālamīn'),
(1700, 12, 105, 'وَكَاَيِّنْ مِّنْ اٰيَةٍ فِى السَّمٰوٰتِ وَالْاَرْضِ يَمُرُّوْنَ عَلَيْهَا وَهُمْ عَنْهَا مُعْرِضُوْنَ', 'wa ka`ayyim min āyatin fis-samāwāti wal-arḍi yamurrụna \'alaihā wa hum \'an-hā mu\'riḍụn'),
(1701, 12, 106, 'وَمَا يُؤْمِنُ اَكْثَرُهُمْ بِاللّٰهِ اِلَّا وَهُمْ مُّشْرِكُوْنَ', 'wa mā yu`minu akṡaruhum billāhi illā wa hum musyrikụn'),
(1702, 12, 107, 'اَفَاَمِنُوْٓا اَنْ تَأْتِيَهُمْ غَاشِيَةٌ مِّنْ عَذَابِ اللّٰهِ اَوْ تَأْتِيَهُمُ السَّاعَةُ بَغْتَةً وَّهُمْ لَا يَشْعُرُوْنَ', 'a fa aminū an ta`tiyahum gāsyiyatum min \'ażābillāhi au ta`tiyahumus-sā\'atu bagtataw wa hum lā yasy\'urụn'),
(1703, 12, 108, 'قُلْ هٰذِهٖ سَبِيْلِيْٓ اَدْعُوْٓا اِلَى اللّٰهِ ۗعَلٰى بَصِيْرَةٍ اَنَا۠ وَمَنِ اتَّبَعَنِيْ ۗوَسُبْحٰنَ اللّٰهِ وَمَآ اَنَا۠ مِنَ الْمُشْرِكِيْنَ', 'qul hāżihī sabīlī ad\'ū ilallāh, \'alā baṣīratin ana wa manittaba\'anī, wa sub-ḥānallāhi wa mā ana minal-musyrikīn'),
(1704, 12, 109, 'وَمَآ اَرْسَلْنَا مِنْ قَبْلِكَ اِلَّا رِجَالًا نُّوْحِيْٓ اِلَيْهِمْ مِّنْ اَهْلِ الْقُرٰىۗ اَفَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَيَنْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلِهِمْۗ وَلَدَارُ الْاٰخِرَةِ خَيْرٌ لِّلَّذِيْنَ اتَّقَوْاۗ اَفَلَا تَعْقِلُوْنَ', 'wa mā arsalnā ming qablika illā rijālan nụḥī ilaihim min ahlil-qurā, a fa lam yasīrụ fil-arḍi fa yanẓurụ kaifa kāna \'āqibatullażīna ming qablihim, wa ladārul-ākhirati khairul lillażīnattaqau, a fa lā ta\'qilụn'),
(1705, 12, 110, 'حَتّٰٓى اِذَا اسْتَا۟يْـَٔسَ الرُّسُلُ وَظَنُّوْٓا اَنَّهُمْ قَدْ كُذِبُوْا جَاۤءَهُمْ نَصْرُنَاۙ فَنُجِّيَ مَنْ نَّشَاۤءُ ۗوَلَا يُرَدُّ بَأْسُنَا عَنِ الْقَوْمِ الْمُجْرِمِيْنَ', 'ḥattā iżastai`asar-rusulu wa ẓannū annahum qad kużibụ jā`ahum naṣrunā fa nujjiya man nasyā`, wa lā yuraddu ba`sunā \'anil-qaumil-mujrimīn'),
(1706, 12, 111, 'لَقَدْ كَانَ فِيْ قَصَصِهِمْ عِبْرَةٌ لِّاُولِى الْاَلْبَابِۗ مَا كَانَ حَدِيْثًا يُّفْتَرٰى وَلٰكِنْ تَصْدِيْقَ الَّذِيْ بَيْنَ يَدَيْهِ وَتَفْصِيْلَ كُلِّ شَيْءٍ وَّهُدًى وَّرَحْمَةً لِّقَوْمٍ يُّؤْمِنُوْنَ', 'laqad kāna fī qaṣaṣihim \'ibratul li`ulil-albāb, mā kāna ḥadīṡay yuftarā wa lākin taṣdīqallażī baina yadaihi wa tafṣīla kulli syai`iw wa hudaw wa raḥmatal liqaumiy yu`minụn'),
(1707, 13, 1, 'الۤمّۤرٰۗ تِلْكَ اٰيٰتُ الْكِتٰبِۗ وَالَّذِيْٓ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ الْحَقُّ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يُؤْمِنُوْنَ', 'alif lām mīm rā, tilka āyātul-kitāb, wallażī unzila ilaika mir rabbikal-ḥaqqu wa lākinna akṡaran-nāsi lā yu`minụn'),
(1708, 13, 2, 'اَللّٰهُ الَّذِيْ رَفَعَ السَّمٰوٰتِ بِغَيْرِ عَمَدٍ تَرَوْنَهَا ثُمَّ اسْتَوٰى عَلَى الْعَرْشِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَۗ كُلٌّ يَّجْرِيْ لِاَجَلٍ مُّسَمًّىۗ يُدَبِّرُ الْاَمْرَ يُفَصِّلُ الْاٰيٰتِ لَعَلَّكُمْ بِلِقَاۤءِ رَبِّكُمْ تُوْقِنُوْنَ', 'allāhullażī rafa\'as-samāwāti bigairi \'amadin taraunahā ṡummastawā \'alal-\'arsyi wa sakhkharasy-syamsa wal-qamar, kulluy yajrī li`ajalim musammā, yudabbirul-amra yufaṣṣilul-āyāti la\'allakum biliqā`i rabbikum tụqinụn'),
(1709, 13, 3, 'وَهُوَ الَّذِيْ مَدَّ الْاَرْضَ وَجَعَلَ فِيْهَا رَوَاسِيَ وَاَنْهٰرًا ۗوَمِنْ كُلِّ الثَّمَرٰتِ جَعَلَ فِيْهَا زَوْجَيْنِ اثْنَيْنِ يُغْشِى الَّيْلَ النَّهَارَۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّتَفَكَّرُوْنَ', 'wa huwallażī maddal-arḍa wa ja\'ala fīhā rawāsiya wa an-hārā, wa ming kulliṡ-ṡamarāti ja\'ala fīhā zaujainiṡnaini yugsyil-lailan-nahār, inna fī żālika la`āyātil liqaumiy yatafakkarụn'),
(1710, 13, 4, 'وَفِى الْاَرْضِ قِطَعٌ مُّتَجٰوِرٰتٌ وَّجَنّٰتٌ مِّنْ اَعْنَابٍ وَّزَرْعٌ وَّنَخِيْلٌ صِنْوَانٌ وَّغَيْرُ صِنْوَانٍ يُّسْقٰى بِمَاۤءٍ وَّاحِدٍۙ وَّنُفَضِّلُ بَعْضَهَا عَلٰى بَعْضٍ فِى الْاُكُلِۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّعْقِلُوْنَ', 'wa fil-arḍi qiṭa\'um mutajāwirātuw wa jannātum min a\'nābiw wa zar\'uw wa nakhīlun ṣinwānuw wa gairu ṣinwāniy yusqā bimā`iw wāḥidiw wa nufaḍḍilu ba\'ḍahā \'alā ba\'ḍin fil-ukul, inna fī żālika la`āyātil liqaumiy ya\'qilụn'),
(1711, 13, 5, '۞ وَاِنْ تَعْجَبْ فَعَجَبٌ قَوْلُهُمْ ءَاِذَا كُنَّا تُرَابًا ءَاِنَّا لَفِيْ خَلْقٍ جَدِيْدٍ ەۗ اُولٰۤىِٕكَ الَّذِيْنَ كَفَرُوْا بِرَبِّهِمْۚ وَاُولٰۤىِٕكَ الْاَغْلٰلُ فِيْٓ اَعْنَاقِهِمْۚ وَاُولٰۤىِٕكَ اَصْحٰبُ النَّارِۚ هُمْ فِيْهَا خٰلِدُوْنَ', 'wa in ta\'jab fa \'ajabung qauluhum a iżā kunnā turāban a innā lafī khalqin jadīd, ulā`ikallażīna kafarụ birabbihim, wa ulā`ikal-aglālu fī a\'nāqihim, wa ulā`ika aṣ-ḥābun-nār, hum fīhā khālidụn'),
(1712, 13, 6, 'وَيَسْتَعْجِلُوْنَكَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِ وَقَدْ خَلَتْ مِنْ قَبْلِهِمُ الْمَثُلٰتُۗ وَاِنَّ رَبَّكَ لَذُوْ مَغْفِرَةٍ لِّلنَّاسِ عَلٰى ظُلْمِهِمْۚ وَاِنَّ رَبَّكَ لَشَدِيْدُ الْعِقَابِ', 'wa yasta\'jilụnaka bis-sayyi`ati qablal-ḥasanati wa qad khalat ming qablihimul-maṡulāt, wa inna rabbaka lażụ magfiratil lin-nāsi \'alā ẓulmihim, wa inna rabbaka lasyadīdul-\'iqāb'),
(1713, 13, 7, 'وَيَقُوْلُ الَّذِيْنَ كَفَرُوْا لَوْلَآ اُنْزِلَ عَلَيْهِ اٰيَةٌ مِّنْ رَّبِّهٖۗ اِنَّمَآ اَنْتَ مُنْذِرٌ وَّلِكُلِّ قَوْمٍ هَادٍ', 'wa yaqụlullażīna kafarụ lau lā unzila \'alaihi āyatum mir rabbih, innamā anta munżiruw wa likulli qaumin hād'),
(1714, 13, 8, 'اَللّٰهُ يَعْلَمُ مَا تَحْمِلُ كُلُّ اُنْثٰى وَمَا تَغِيْضُ الْاَرْحَامُ وَمَا تَزْدَادُ ۗوَكُلُّ شَيْءٍ عِنْدَهٗ بِمِقْدَارٍ', 'allāhu ya\'lamu mā taḥmilu kullu unṡā wa mā tagīḍul-ar-ḥāmu wa mā tazdād, wa kullu syai`in \'indahụ bimiqdār'),
(1715, 13, 9, 'عٰلِمُ الْغَيْبِ وَالشَّهَادَةِ الْكَبِيْرُ الْمُتَعَالِ', '\'ālimul-gaibi wasy-syahādatil-kabīrul-muta\'āl'),
(1716, 13, 10, 'سَوَاۤءٌ مِّنْكُمْ مَّنْ اَسَرَّ الْقَوْلَ وَمَنْ جَهَرَ بِهٖ وَمَنْ هُوَ مُسْتَخْفٍۢ بِالَّيْلِ وَسَارِبٌۢ بِالنَّهَارِ', 'sawā`um mingkum man asarral-qaula wa man jahara bihī wa man huwa mustakhfim bil-laili wa sāribum bin-nahār'),
(1717, 13, 11, 'لَهٗ مُعَقِّبٰتٌ مِّنْۢ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهٖ يَحْفَظُوْنَهٗ مِنْ اَمْرِ اللّٰهِ ۗاِنَّ اللّٰهَ لَا يُغَيِّرُ مَا بِقَوْمٍ حَتّٰى يُغَيِّرُوْا مَا بِاَنْفُسِهِمْۗ وَاِذَآ اَرَادَ اللّٰهُ بِقَوْمٍ سُوْۤءًا فَلَا مَرَدَّ لَهٗ ۚوَمَا لَهُمْ مِّنْ دُوْنِهٖ مِنْ وَّالٍ', 'lahụ mu\'aqqibātum mim baini yadaihi wa min khalfihī yaḥfaẓụnahụ min amrillāh, innallāha lā yugayyiru mā biqaumin ḥattā yugayyirụ mā bi`anfusihim, wa iżā arādallāhu biqaumin sū`an fa lā maradda lah, wa mā lahum min dụnihī miw wāl'),
(1718, 13, 12, 'هُوَ الَّذِيْ يُرِيْكُمُ الْبَرْقَ خَوْفًا وَّطَمَعًا وَّيُنْشِئُ السَّحَابَ الثِّقَالَۚ', 'huwallażī yurīkumul-barqa khaufaw wa ṭama\'aw wa yunsyi`us-saḥābaṡ-ṡiqāl'),
(1719, 13, 13, 'وَيُسَبِّحُ الرَّعْدُ بِحَمْدِهٖ وَالْمَلٰۤىِٕكَةُ مِنْ خِيْفَتِهٖۚ وَيُرْسِلُ الصَّوَاعِقَ فَيُصِيْبُ بِهَا مَنْ يَّشَاۤءُ وَهُمْ يُجَادِلُوْنَ فِى اللّٰهِ ۚوَهُوَ شَدِيْدُ الْمِحَالِۗ', 'wa yusabbiḥur-ra\'du biḥamdihī wal-malā`ikatu min khīfatih, wa yursiluṣ-ṣawā\'iqa fa yuṣību bihā may yasyā`u wa hum yujādilụna fillāh, wa huwa syadīdul miḥāl'),
(1720, 13, 14, 'لَهٗ دَعْوَةُ الْحَقِّۗ وَالَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِهٖ لَا يَسْتَجِيْبُوْنَ لَهُمْ بِشَيْءٍ اِلَّا كَبَاسِطِ كَفَّيْهِ اِلَى الْمَاۤءِ لِيَبْلُغَ فَاهُ وَمَا هُوَ بِبَالِغِهٖۗ وَمَا دُعَاۤءُ الْكٰفِرِيْنَ اِلَّا فِيْ ضَلٰلٍ', 'lahụ da\'watul-ḥaqq, wallażīna yad\'ụna min dụnihī lā yastajībụna lahum bisyai`in illā kabāsiṭi kaffaihi ilal-mā`i liyabluga fāhu wa mā huwa bibāligih, wa mā du\'ā`ul-kāfirīna illā fī ḍalāl'),
(1721, 13, 15, 'وَلِلّٰهِ يَسْجُدُ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِ طَوْعًا وَّكَرْهًا وَّظِلٰلُهُمْ بِالْغُدُوِّ وَالْاٰصَالِ ۩', 'wa lillāhi yasjudu man fis-samāwāti wal-arḍi ṭau\'aw wa kar-haw wa ẓilāluhum bil-guduwwi wal-āṣāl'),
(1722, 13, 16, 'قُلْ مَنْ رَّبُّ السَّمٰوٰتِ وَالْاَرْضِۗ قُلِ اللّٰهُ ۗقُلْ اَفَاتَّخَذْتُمْ مِّنْ دُوْنِهٖٓ اَوْلِيَاۤءَ لَا يَمْلِكُوْنَ لِاَنْفُسِهِمْ نَفْعًا وَّلَا ضَرًّاۗ قُلْ هَلْ يَسْتَوِى الْاَعْمٰى وَالْبَصِيْرُ ەۙ اَمْ هَلْ تَسْتَوِى الظُّلُمٰتُ وَالنُّوْرُ ەۚ اَمْ جَعَلُوْا لِلّٰهِ شُرَكَاۤءَ خَلَقُوْا كَخَلْقِهٖ فَتَشَابَهَ الْخَلْقُ عَلَيْهِمْۗ قُلِ اللّٰهُ خَالِقُ كُلِّ شَيْءٍ وَّهُوَ الْوَاحِدُ الْقَهَّارُ', 'qul mar rabbus-samāwāti wal-arḍ, qulillāh, qul a fattakhażtum min dụnihī auliyā`a lā yamlikụna li`anfusihim naf\'aw wa lā ḍarrā, qul hal yastawil-a\'mā wal-baṣīru am hal tastawiẓ-ẓulumātu wan-nụr, am ja\'alụ lillāhi syurakā`a khalaqụ kakhalqihī fa tasyābahal-khalqu \'alaihim, qulillāhu khāliqu kulli syai`iw wa huwal-wāḥidul-qahhār'),
(1723, 13, 17, 'اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً فَسَالَتْ اَوْدِيَةٌ ۢ بِقَدَرِهَا فَاحْتَمَلَ السَّيْلُ زَبَدًا رَّابِيًا ۗوَمِمَّا يُوْقِدُوْنَ عَلَيْهِ فِى النَّارِ ابْتِغَاۤءَ حِلْيَةٍ اَوْ مَتَاعٍ زَبَدٌ مِّثْلُهٗ ۗ كَذٰلِكَ يَضْرِبُ اللّٰهُ الْحَقَّ وَالْبَاطِلَ ەۗ فَاَمَّا الزَّبَدُ فَيَذْهَبُ جُفَاۤءً ۚوَاَمَّا مَا يَنْفَعُ النَّاسَ فَيَمْكُثُ فِى الْاَرْضِۗ كَذٰلِكَ يَضْرِبُ اللّٰهُ الْاَمْثَالَ ۗ', 'anzala minas-samā`i mā`an fa sālat audiyatum biqadarihā faḥtamalas-sailu zabadar rābiyā, wa mimmā yụqidụna \'alaihi fin-nāribtigā`a ḥilyatin au matā\'in zabadum miṡluh, każālika yaḍribullāhul-ḥaqqa wal-bāṭil, fa ammaz-zabadu fa yaż-habu jufā`ā, wa ammā mā yanfa\'un-nāsa fa yamkuṡu fil-arḍ, każālika yaḍribullāhul-amṡāl'),
(1724, 13, 18, 'لِلَّذِيْنَ اسْتَجَابُوْا لِرَبِّهِمُ الْحُسْنٰىۗ وَالَّذِيْنَ لَمْ يَسْتَجِيْبُوْا لَهٗ لَوْ اَنَّ لَهُمْ مَّا فِى الْاَرْضِ جَمِيْعًا وَّمِثْلَهٗ مَعَهٗ لَافْتَدَوْا بِهٖ ۗ اُولٰۤىِٕكَ لَهُمْ سُوْۤءُ الْحِسَابِ ەۙ وَمَأْوٰىهُمْ جَهَنَّمُ ۗوَبِئْسَ الْمِهَادُ', 'lillażīnastajābụ lirabbihimul-ḥusnā, wallażīna lam yastajībụ lahụ lau anna lahum mā fil-arḍi jamī\'aw wa miṡlahụ ma\'ahụ laftadau bih, ulā`ika lahum sū`ul-ḥisābi wa ma`wāhum jahannam, wa bi`sal-mihād'),
(1725, 13, 19, '۞ اَفَمَنْ يَّعْلَمُ اَنَّمَآ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ الْحَقُّ كَمَنْ هُوَ اَعْمٰىۗ اِنَّمَا يَتَذَكَّرُ اُولُوا الْاَلْبَابِۙ', 'a fa may ya\'lamu annamā unzila ilaika mir rabbikal-ḥaqqu kaman huwa a\'mā, innamā yatażakkaru ulul-albāb'),
(1726, 13, 20, 'الَّذِيْنَ يُوْفُوْنَ بِعَهْدِ اللّٰهِ وَلَا يَنْقُضُوْنَ الْمِيْثَاقَۙ', 'allażīna yụfụna bi\'ahdillāhi wa lā yangquḍụnal-mīṡāq'),
(1727, 13, 21, 'وَالَّذِيْنَ يَصِلُوْنَ مَآ اَمَرَ اللّٰهُ بِهٖٓ اَنْ يُّوْصَلَ وَيَخْشَوْنَ رَبَّهُمْ وَيَخَافُوْنَ سُوْۤءَ الْحِسَابِ ۗ', 'wallażīna yaṣilụna mā amarallāhu bihī ay yụṣala wa yakhsyauna rabbahum wa yakhāfụna sū`al-ḥisāb'),
(1728, 13, 22, 'وَالَّذِيْنَ صَبَرُوا ابْتِغَاۤءَ وَجْهِ رَبِّهِمْ وَاَقَامُوا الصَّلٰوةَ وَاَنْفَقُوْا مِمَّا رَزَقْنٰهُمْ سِرًّا وَّعَلَانِيَةً وَّيَدْرَءُوْنَ بِالْحَسَنَةِ السَّيِّئَةَ اُولٰۤىِٕكَ لَهُمْ عُقْبَى الدَّارِۙ', 'wallażīna ṣabarubtigā`a waj-hi rabbihim wa aqāmuṣ-ṣalāta wa anfaqụ mimmā razaqnāhum sirraw wa \'alāniyataw wa yadra`ụna bil-ḥasanatis-sayyi`ata ulā`ika lahum \'uqbad-dār'),
(1729, 13, 23, 'جَنّٰتُ عَدْنٍ يَّدْخُلُوْنَهَا وَمَنْ صَلَحَ مِنْ اٰبَاۤىِٕهِمْ وَاَزْوَاجِهِمْ وَذُرِّيّٰتِهِمْ وَالْمَلٰۤىِٕكَةُ يَدْخُلُوْنَ عَلَيْهِمْ مِّنْ كُلِّ بَابٍۚ', 'jannātu \'adniy yadkhulụnahā wa man ṣalaḥa min ābā`ihim wa azwājihim wa żurriyyātihim wal-malā`ikatu yadkhulụna \'alaihim ming kulli bāb'),
(1730, 13, 24, 'سَلٰمٌ عَلَيْكُمْ بِمَا صَبَرْتُمْ فَنِعْمَ عُقْبَى الدَّارِۗ', 'salāmun \'alaikum bimā ṣabartum fa ni\'ma \'uqbad-dār'),
(1731, 13, 25, 'وَالَّذِيْنَ يَنْقُضُوْنَ عَهْدَ اللّٰهِ مِنْ ۢ بَعْدِ مِيْثَاقِهٖ وَيَقْطَعُوْنَ مَآ اَمَرَ اللّٰهُ بِهٖٓ اَنْ يُّوْصَلَ وَيُفْسِدُوْنَ فِى الْاَرْضِۙ اُولٰۤىِٕكَ لَهُمُ اللَّعْنَةُ وَلَهُمْ سُوْۤءُ الدَّارِ', 'wallażīna yangquḍụna \'ahdallāhi mim ba\'di mīṡāqihī wa yaqṭa\'ụna mā amarallāhu bihī ay yụṣala wa yufsidụna fil-arḍi ulā`ika lahumul-la\'natu wa lahum sū`ud-dār'),
(1732, 13, 26, 'اَللّٰهُ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُ ۗوَفَرِحُوْا بِالْحَيٰوةِ الدُّنْيَاۗ وَمَا الْحَيٰوةُ الدُّنْيَا فِى الْاٰخِرَةِ اِلَّا مَتَاعٌ', 'allāhu yabsuṭur-rizqa limay yasyā`u wa yaqdir, wa fariḥụ bil-ḥayātid-dun-yā, wa mal-ḥayātud-dun-yā fil-ākhirati illā matā\''),
(1733, 13, 27, 'وَيَقُوْلُ الَّذِيْنَ كَفَرُوْا لَوْلَآ اُنْزِلَ عَلَيْهِ اٰيَةٌ مِّنْ رَّبِّهٖۗ قُلْ اِنَّ اللّٰهَ يُضِلُّ مَنْ يَّشَاۤءُ وَيَهْدِيْٓ اِلَيْهِ مَنْ اَنَابَۖ', 'wa yaqụlullażīna kafarụ lau lā unzila \'alaihi āyatum mir rabbih, qul innallāha yuḍillu may yasyā`u wa yahdī ilaihi man anāb'),
(1734, 13, 28, 'الَّذِيْنَ اٰمَنُوْا وَتَطْمَىِٕنُّ قُلُوْبُهُمْ بِذِكْرِ اللّٰهِ ۗ اَلَا بِذِكْرِ اللّٰهِ تَطْمَىِٕنُّ الْقُلُوْبُ ۗ', 'allażīna āmanụ wa taṭma`innu qulụbuhum biżikrillāh, alā biżikrillāhi taṭma`innul-qulụb'),
(1735, 13, 29, 'اَلَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ طُوْبٰى لَهُمْ وَحُسْنُ مَاٰبٍ', 'allażīna āmanụ wa \'amiluṣ-ṣāliḥāti ṭụbā lahum wa ḥusnu ma`āb'),
(1736, 13, 30, 'كَذٰلِكَ اَرْسَلْنٰكَ فِيْٓ اُمَّةٍ قَدْ خَلَتْ مِنْ قَبْلِهَآ اُمَمٌ لِّتَتْلُوَا۟ عَلَيْهِمُ الَّذِيْٓ اَوْحَيْنَآ اِلَيْكَ وَهُمْ يَكْفُرُوْنَ بِالرَّحْمٰنِۗ قُلْ هُوَ رَبِّيْ لَآ اِلٰهَ اِلَّا هُوَۚ عَلَيْهِ تَوَكَّلْتُ وَاِلَيْهِ مَتَابِ', 'każālika arsalnāka fī ummating qad khalat ming qablihā umamul litatluwā \'alaihimullażī auḥainā ilaika wa hum yakfurụna bir-raḥmān, qul huwa rabbī lā ilāha illā huw, \'alaihi tawakkaltu wa ilaihi matāb'),
(1737, 13, 31, 'وَلَوْ اَنَّ قُرْاٰنًا سُيِّرَتْ بِهِ الْجِبَالُ اَوْ قُطِّعَتْ بِهِ الْاَرْضُ اَوْ كُلِّمَ بِهِ الْمَوْتٰىۗ بَلْ لِّلّٰهِ الْاَمْرُ جَمِيْعًاۗ اَفَلَمْ يَا۟يْـَٔسِ الَّذِيْنَ اٰمَنُوْٓا اَنْ لَّوْ يَشَاۤءُ اللّٰهُ لَهَدَى النَّاسَ جَمِيْعًاۗ وَلَا يَزَالُ الَّذِيْنَ كَفَرُوْا تُصِيْبُهُمْ بِمَا صَنَعُوْا قَارِعَةٌ اَوْ تَحُلُّ قَرِيْبًا مِّنْ دَارِهِمْ حَتّٰى يَأْتِيَ وَعْدُ اللّٰهِ ۗاِنَّ اللّٰهَ لَا يُخْلِفُ الْمِيْعَادَ', 'walau anna qur`ānan suyyirat bihil-jibālu au quṭṭi\'at bihil-arḍu au kullima bihil-mautā, bal lillāhil-amru jamī\'ā, a fa lam yai`asillażīna āmanū al lau yasyā`ullāhu laḥadan-nāsa jamī\'ā, wa lā yazālullażīna kafarụ tuṣībuhum bimā ṣana\'ụ qāri\'atun au taḥullu qarībam min dārihim ḥattā ya`tiya wa\'dullāh, innallāha lā yukhliful-mī\'ād'),
(1738, 13, 32, 'وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِّنْ قَبْلِكَ فَاَمْلَيْتُ لِلَّذِيْنَ كَفَرُوْا ثُمَّ اَخَذْتُهُمْ فَكَيْفَ كَانَ عِقَابِ', 'wa laqadistuhzi`a birusulim ming qablika fa amlaitu lillażīna kafarụ ṡumma akhażtuhum fa kaifa kāna \'iqāb'),
(1739, 13, 33, 'اَفَمَنْ هُوَ قَاۤىِٕمٌ عَلٰى كُلِّ نَفْسٍۢ بِمَا كَسَبَتْۚ وَجَعَلُوْا لِلّٰهِ شُرَكَاۤءَ ۗ قُلْ سَمُّوْهُمْۗ اَمْ تُنَبِّـُٔوْنَهٗ بِمَا لَا يَعْلَمُ فِى الْاَرْضِ اَمْ بِظَاهِرٍ مِّنَ الْقَوْلِ ۗبَلْ زُيِّنَ لِلَّذِيْنَ كَفَرُوْا مَكْرُهُمْ وَصُدُّوْا عَنِ السَّبِيْلِ ۗوَمَنْ يُّضْلِلِ اللّٰهُ فَمَا لَهٗ مِنْ هَادٍ', 'a fa man huwa qā`imun \'alā kulli nafsim bimā kasabat, wa ja\'alụ lillāhi syurakā`, qul sammụhum, am tunabbi`ụnahụ bimā lā ya\'lamu fil-arḍi am biẓāhirim minal-qaụl, bal zuyyina lillażīna kafarụ makruhum wa ṣuddụ \'anis-sabīl, wa may yuḍlilillāhu fa mā lahụ min hād'),
(1740, 13, 34, 'لَهُمْ عَذَابٌ فِى الْحَيٰوةِ الدُّنْيَا وَلَعَذَابُ الْاٰخِرَةِ اَشَقُّۚ وَمَا لَهُمْ مِّنَ اللّٰهِ مِنْ وَّاقٍ', 'lahum \'ażābun fil-ḥayātid-dun-yā wa la\'ażābul-ākhirati asyaqq, wa mā lahum minallāhi miw wāq'),
(1741, 13, 35, '۞ مَثَلُ الْجَنَّةِ الَّتِيْ وُعِدَ الْمُتَّقُوْنَۗ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۗ اُكُلُهَا دَاۤىِٕمٌ وَّظِلُّهَاۗ تِلْكَ عُقْبَى الَّذِيْنَ اتَّقَوْا ۖوَّعُقْبَى الْكٰفِرِيْنَ النَّارُ', 'maṡalul-jannatillatī wu\'idal-muttaqụn, tajrī min taḥtihal-an-hār, ukuluhā dā`imuw wa ẓilluhā, tilka \'uqballażīnattaqaw wa \'uqbal-kāfirīnan-nār'),
(1742, 13, 36, 'وَالَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يَفْرَحُوْنَ بِمَآ اُنْزِلَ اِلَيْكَ وَمِنَ الْاَحْزَابِ مَنْ يُّنْكِرُ بَعْضَهٗ ۗ قُلْ اِنَّمَآ اُمِرْتُ اَنْ اَعْبُدَ اللّٰهَ وَلَآ اُشْرِكَ بِهٖ ۗاِلَيْهِ اَدْعُوْا وَاِلَيْهِ مَاٰبِ', 'wallażīna ātaināhumul-kitāba yafraḥụna bimā unzila ilaika wa minal-aḥzābi may yungkiru ba\'ḍah, qul innamā umirtu an a\'budallāha wa lā usyrika bih, ilaihi ad\'ụ wa ilaihi ma`āb'),
(1743, 13, 37, 'وَكَذٰلِكَ اَنْزَلْنٰهُ حُكْمًا عَرَبِيًّاۗ وَلَىِٕنِ اتَّبَعْتَ اَهْوَاۤءَهُمْ بَعْدَمَا جَاۤءَكَ مِنَ الْعِلْمِۙ مَا لَكَ مِنَ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا وَاقٍ', 'wa każālika anzalnāhu ḥukman \'arabiyyā, wa la`inittaba\'ta ahwā`ahum ba\'da mā jā`aka minal-\'ilmi mā laka minallāhi miw waliyyiw wa lā wāq'),
(1744, 13, 38, 'وَلَقَدْ اَرْسَلْنَا رُسُلًا مِّنْ قَبْلِكَ وَجَعَلْنَا لَهُمْ اَزْوَاجًا وَّذُرِّيَّةً ۗوَمَا كَانَ لِرَسُوْلٍ اَنْ يَّأْتِيَ بِاٰيَةٍ اِلَّا بِاِذْنِ اللّٰهِ ۗلِكُلِّ اَجَلٍ كِتَابٌ', 'wa laqad arsalnā rusulam ming qablika wa ja\'alnā lahum azwājaw wa żurriyyah, wa mā kāna lirasụlin ay ya`tiya bi`āyatin illā bi`iżnillāh, likulli ajaling kitāb'),
(1745, 13, 39, 'يَمْحُوا اللّٰهُ مَا يَشَاۤءُ وَيُثْبِتُ ۚوَعِنْدَهٗٓ اُمُّ الْكِتٰبِ', 'yam-ḥullāhu mā yasyā`u wa yuṡbit, wa \'indahū ummul-kitāb'),
(1746, 13, 40, 'وَاِنْ مَّا نُرِيَنَّكَ بَعْضَ الَّذِيْ نَعِدُهُمْ اَوْ نَتَوَفَّيَنَّكَ فَاِنَّمَا عَلَيْكَ الْبَلٰغُ وَعَلَيْنَا الْحِسَابُ', 'wa im mā nuriyannaka ba\'ḍallażī na\'iduhum au natawaffayannaka fa innamā \'alaikal-balāgu wa \'alainal-ḥisāb'),
(1747, 13, 41, 'اَوَلَمْ يَرَوْا اَنَّا نَأْتِى الْاَرْضَ نَنْقُصُهَا مِنْ اَطْرَافِهَاۗ وَاللّٰهُ يَحْكُمُ لَا مُعَقِّبَ لِحُكْمِهٖۗ وَهُوَ سَرِيْعُ الْحِسَابِ', 'a wa lam yarau annā na`til-arḍa nangquṣuhā min aṭrāfihā, wallāhu yaḥkumu lā mu\'aqqiba liḥukmih, wa huwa sarī\'ul-ḥisāb'),
(1748, 13, 42, 'وَقَدْ مَكَرَ الَّذِيْنَ مِنْ قَبْلِهِمْ فَلِلّٰهِ الْمَكْرُ جَمِيْعًا ۗيَعْلَمُ مَا تَكْسِبُ كُلُّ نَفْسٍۗ وَسَيَعْلَمُ الْكُفّٰرُ لِمَنْ عُقْبَى الدَّارِ', 'wa qad makarallażīna ming qablihim fa lillāhil-makru jamī\'ā, ya\'lamu mā taksibu kullu nafs, wa saya\'lamul-kuffāru liman \'uqbad-dār'),
(1749, 13, 43, 'وَيَقُوْلُ الَّذِيْنَ كَفَرُوْا لَسْتَ مُرْسَلًا ۗ قُلْ كَفٰى بِاللّٰهِ شَهِيْدًاۢ بَيْنِيْ وَبَيْنَكُمْۙ وَمَنْ عِنْدَهٗ عِلْمُ الْكِتٰبِ', 'wa yaqụlullażīna kafarụ lasta mursalā, qul kafā billāhi syahīdam bainī wa bainakum wa man \'indahụ \'ilmul-kitāb'),
(1750, 14, 1, 'الۤرٰ ۗ كِتٰبٌ اَنْزَلْنٰهُ اِلَيْكَ لِتُخْرِجَ النَّاسَ مِنَ الظُّلُمٰتِ اِلَى النُّوْرِ ەۙ بِاِذْنِ رَبِّهِمْ اِلٰى صِرَاطِ الْعَزِيْزِ الْحَمِيْدِۙ', 'alif lām rā, kitābun anzalnāhu ilaika litukhrijan-nāsa minaẓ-ẓulumāti ilan-nụri bi`iżni rabbihim ilā ṣirāṭil-\'azīzil-ḥamīd'),
(1751, 14, 2, 'اللّٰهِ الَّذِيْ لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَوَيْلٌ لِّلْكٰفِرِيْنَ مِنْ عَذَابٍ شَدِيْدٍۙ', 'allāhillażī lahụ mā fis-samāwāti wa mā fil-arḍ, wa wailul lil-kāfirīna min \'ażābin syadīd'),
(1752, 14, 3, ' ۨالَّذِيْنَ يَسْتَحِبُّوْنَ الْحَيٰوةَ الدُّنْيَا عَلَى الْاٰخِرَةِ وَيَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ وَيَبْغُوْنَهَا عِوَجًا ۗ اُولٰۤىِٕكَ فِيْ ضَلٰلٍۢ بَعِيْدٍ', 'allażīna yastaḥibbụnal-ḥayātad-dun-yā \'alal-ākhirati wa yaṣuddụna \'an sabīlillāhi wa yabgụnahā \'iwajā, ulā`ika fī ḍalālim ba\'īd'),
(1753, 14, 4, 'وَمَآ اَرْسَلْنَا مِنْ رَّسُوْلٍ اِلَّا بِلِسَانِ قَوْمِهٖ لِيُبَيِّنَ لَهُمْ ۗفَيُضِلُّ اللّٰهُ مَنْ يَّشَاۤءُ وَيَهْدِيْ مَنْ يَّشَاۤءُ ۗوَهُوَ الْعَزِيْزُ الْحَكِيْمُ', 'wa mā arsalnā mir rasụlin illā bilisāni qaumihī liyubayyina lahum, fa yuḍillullāhu may yasyā`u wa yahdī may yasyā`, wa huwal-\'azīzul-ḥakīm'),
(1754, 14, 5, 'وَلَقَدْ اَرْسَلْنَا مُوْسٰى بِاٰيٰتِنَآ اَنْ اَخْرِجْ قَوْمَكَ مِنَ الظُّلُمٰتِ اِلَى النُّوْرِ ەۙ وَذَكِّرْهُمْ بِاَيّٰىمِ اللّٰهِ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّكُلِّ صَبَّارٍ شَكُوْرٍ', 'wa laqad arsalnā mụsā bi`āyātinā an akhrij qaumaka minaẓ-ẓulumāti ilan-nụri wa żakkir-hum bi`ayyāmillāh, inna fī żālika la`āyātil likulli ṣabbārin syakụr'),
(1755, 14, 6, 'وَاِذْ قَالَ مُوْسٰى لِقَوْمِهِ اذْكُرُوْا نِعْمَةَ اللّٰهِ عَلَيْكُمْ اِذْ اَنْجٰىكُمْ مِّنْ اٰلِ فِرْعَوْنَ يَسُوْمُوْنَكُمْ سُوْۤءَ الْعَذَابِ وَيُذَبِّحُوْنَ اَبْنَاۤءَكُمْ وَيَسْتَحْيُوْنَ نِسَاۤءَكُمْ ۗوَفِيْ ذٰلِكُمْ بَلَاۤءٌ مِّنْ رَّبِّكُمْ عَظِيْمٌ', 'wa iż qāla mụsā liqaumihiżkurụ ni\'matallāhi \'alaikum iż anjākum min āli fir\'auna yasụmụnakum sū`al-\'ażābi wa yużabbiḥụna abnā`akum wa yastaḥyụna nisā`akum, wa fī żālikum balā`um mir rabbikum \'aẓīm'),
(1756, 14, 7, 'وَاِذْ تَاَذَّنَ رَبُّكُمْ لَىِٕنْ شَكَرْتُمْ لَاَزِيْدَنَّكُمْ وَلَىِٕنْ كَفَرْتُمْ اِنَّ عَذَابِيْ لَشَدِيْدٌ', 'wa iż ta`ażżana rabbukum la`in syakartum la`azīdannakum wa la`ing kafartum inna \'ażābī lasyadīd'),
(1757, 14, 8, 'وَقَالَ مُوْسٰٓى اِنْ تَكْفُرُوْٓا اَنْتُمْ وَمَنْ فِى الْاَرْضِ جَمِيْعًا ۙفَاِنَّ اللّٰهَ لَغَنِيٌّ حَمِيْدٌ', 'wa qāla mụsā in takfurū antum wa man fil-arḍi jamī\'an fa innallāha laganiyyun ḥamīd'),
(1758, 14, 9, 'اَلَمْ يَأْتِكُمْ نَبَؤُا الَّذِيْنَ مِنْ قَبْلِكُمْ قَوْمِ نُوْحٍ وَّعَادٍ وَّثَمُوْدَ ەۗ وَالَّذِيْنَ مِنْۢ بَعْدِهِمْ ۗ لَا يَعْلَمُهُمْ اِلَّا اللّٰهُ ۗجَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِ فَرَدُّوْٓا اَيْدِيَهُمْ فِيْٓ اَفْوَاهِهِمْ وَقَالُوْٓا اِنَّا كَفَرْنَا بِمَآ اُرْسِلْتُمْ بِهٖ وَاِنَّا لَفِيْ شَكٍّ مِّمَّا تَدْعُوْنَنَآ اِلَيْهِ مُرِيْبٍ', 'a lam ya`tikum naba`ullażīna ming qablikum qaumi nụḥiw wa \'ādiw wa ṡamụd, wallażīna mim ba\'dihim, lā ya\'lamuhum illallāh, jā`at-hum rusuluhum bil-bayyināti fa raddū aidiyahum fī afwāhihim wa qālū innā kafarnā bimā ursiltum bihī wa innā lafī syakkim mimmā tad\'ụnanā ilaihi murīb'),
(1759, 14, 10, '۞ قَالَتْ رُسُلُهُمْ اَفِى اللّٰهِ شَكٌّ فَاطِرِ السَّمٰوٰتِ وَالْاَرْضِۗ يَدْعُوْكُمْ لِيَغْفِرَ لَكُمْ مِّنْ ذُنُوْبِكُمْ وَيُؤَخِّرَكُمْ اِلٰٓى اَجَلٍ مُّسَمًّىۗ قَالُوْٓا اِنْ اَنْتُمْ اِلَّا بَشَرٌ مِّثْلُنَا ۗ تُرِيْدُوْنَ اَنْ تَصُدُّوْنَا عَمَّا كَانَ يَعْبُدُ اٰبَاۤؤُنَا فَأْتُوْنَا بِسُلْطٰنٍ مُّبِيْنٍ', 'qālat rusuluhum a fillāhi syakkun fāṭiris-samāwāti wal-arḍ, yad\'ụkum liyagfira lakum min żunụbikum wa yu`akhkhirakum ilā ajalim musammā, qālū in antum illā basyarum miṡlunā, turīdụna an taṣuddụnā \'ammā kāna ya\'budu ābā`unā fa`tụnā bisulṭānim mubīn'),
(1760, 14, 11, 'قَالَتْ لَهُمْ رُسُلُهُمْ اِنْ نَّحْنُ اِلَّا بَشَرٌ مِّثْلُكُمْ وَلٰكِنَّ اللّٰهَ يَمُنُّ عَلٰى مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖۗ وَمَا كَانَ لَنَآ اَنْ نَّأْتِيَكُمْ بِسُلْطٰنٍ اِلَّا بِاِذْنِ اللّٰهِ ۗوَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ', 'qālat lahum rusuluhum in naḥnu illā basyarum miṡlukum wa lākinnallāha yamunnu \'alā may yasyā`u min \'ibādih, wa mā kāna lanā an na`tiyakum bisulṭānin illā bi`iżnillāh, wa \'alallāhi falyatawakkalil-mu`minụn'),
(1761, 14, 12, 'وَمَا لَنَآ اَلَّا نَتَوَكَّلَ عَلَى اللّٰهِ وَقَدْ هَدٰىنَا سُبُلَنَاۗ وَلَنَصْبِرَنَّ عَلٰى مَآ اٰذَيْتُمُوْنَاۗ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُوْنَ', 'wa mā lanā allā natawakkala \'alallāhi wa qad hadānā subulanā, wa lanaṣbiranna \'alā mā āżaitumụnā, wa \'alallāhi falyatawakkalil-mutawakkilụn'),
(1762, 14, 13, 'وَقَالَ الَّذِيْنَ كَفَرُوْا لِرُسُلِهِمْ لَنُخْرِجَنَّكُمْ مِّنْ اَرْضِنَآ اَوْ لَتَعُوْدُنَّ فِيْ مِلَّتِنَاۗ فَاَوْحٰٓى اِلَيْهِمْ رَبُّهُمْ لَنُهْلِكَنَّ الظّٰلِمِيْنَ ۗ', 'wa qālallażīna kafarụ lirusulihim lanukhrijannakum min arḍinā au lata\'ụdunna fī millatinā, fa auḥā ilaihim rabbuhum lanuhlikannaẓ-ẓālimīn'),
(1763, 14, 14, 'وَلَنُسْكِنَنَّكُمُ الْاَرْضَ مِنْۢ بَعْدِهِمْ ۗذٰلِكَ لِمَنْ خَافَ مَقَامِيْ وَخَافَ وَعِيْدِ', 'wa lanuskinannakumul-arḍa mim ba\'dihim, żālika liman khāfa maqāmī wa khāfa wa\'īd'),
(1764, 14, 15, 'وَاسْتَفْتَحُوْا وَخَابَ كُلُّ جَبَّارٍ عَنِيْدٍۙ', 'wastaftaḥụ wa khāba kullu jabbārin \'anīd'),
(1765, 14, 16, 'مِّنْ وَّرَاۤىِٕهٖ جَهَنَّمُ وَيُسْقٰى مِنْ مَّاۤءٍ صَدِيْدٍۙ', 'miw warā`ihī jahannamu wa yusqā mim mā`in ṣadīd'),
(1766, 14, 17, 'يَّتَجَرَّعُهٗ وَلَا يَكَادُ يُسِيْغُهٗ وَيَأْتِيْهِ الْمَوْتُ مِنْ كُلِّ مَكَانٍ وَّمَا هُوَ بِمَيِّتٍۗ وَمِنْ وَّرَاۤىِٕهٖ عَذَابٌ غَلِيْظٌ', 'yatajarra\'uhụ wa lā yakādu yusīguhụ wa ya`tīhil-mautu ming kulli makāniw wa mā huwa bimayyit, wa miw warā`ihī \'ażābun galīẓ'),
(1767, 14, 18, 'مَثَلُ الَّذِيْنَ كَفَرُوْا بِرَبِّهِمْ اَعْمَالُهُمْ كَرَمَادِ ِۨاشْتَدَّتْ بِهِ الرِّيْحُ فِيْ يَوْمٍ عَاصِفٍۗ لَا يَقْدِرُوْنَ مِمَّا كَسَبُوْا عَلٰى شَيْءٍ ۗذٰلِكَ هُوَ الضَّلٰلُ الْبَعِيْدُ', 'maṡalullażīna kafarụ birabbihim a\'māluhum karamādinisytaddat bihir-rīḥu fī yaumin \'āṣif, lā yaqdirụna mimmā kasabụ \'alā syaī`, żālika huwaḍ-ḍalālul-ba\'īd'),
(1768, 14, 19, 'اَلَمْ تَرَ اَنَّ اللّٰهَ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّۗ اِنْ يَّشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍ جَدِيْدٍۙ', 'a lam tara annallāha khalaqas-samāwāti wal-arḍa bil-ḥaqq, iy yasya` yuż-hibkum wa ya`ti bikhalqin jadīd'),
(1769, 14, 20, 'وَّمَا ذٰلِكَ عَلَى اللّٰهِ بِعَزِيْزٍ', 'wa mā żālika \'alallāhi bi\'azīz'),
(1770, 14, 21, 'وَبَرَزُوْا لِلّٰهِ جَمِيْعًا فَقَالَ الضُّعَفٰۤؤُا لِلَّذِيْنَ اسْتَكْبَرُوْٓا اِنَّا كُنَّا لَكُمْ تَبَعًا فَهَلْ اَنْتُمْ مُّغْنُوْنَ عَنَّا مِنْ عَذَابِ اللّٰهِ مِنْ شَيْءٍ ۗقَالُوْا لَوْ هَدٰىنَا اللّٰهُ لَهَدَيْنٰكُمْۗ سَوَاۤءٌ عَلَيْنَآ اَجَزِعْنَآ اَمْ صَبَرْنَا مَا لَنَا مِنْ مَّحِيْصٍ', 'wa barazụ lillāhi jamī\'an fa qālaḍ-ḍu\'afā`u lillażīnastakbarū innā kunnā lakum taba\'an fa hal antum mugnụna \'annā min \'ażābillāhi min syaī`, qālụ lau hadānallāhu lahadainākum, sawā`un \'alainā ajazi\'nā am ṣabarnā mā lanā mim maḥīṣ'),
(1771, 14, 22, 'وَقَالَ الشَّيْطٰنُ لَمَّا قُضِيَ الْاَمْرُ اِنَّ اللّٰهَ وَعَدَكُمْ وَعْدَ الْحَقِّ وَوَعَدْتُّكُمْ فَاَخْلَفْتُكُمْۗ وَمَا كَانَ لِيَ عَلَيْكُمْ مِّنْ سُلْطٰنٍ اِلَّآ اَنْ دَعَوْتُكُمْ فَاسْتَجَبْتُمْ لِيْ ۚفَلَا تَلُوْمُوْنِيْ وَلُوْمُوْٓا اَنْفُسَكُمْۗ مَآ اَنَا۠ بِمُصْرِخِكُمْ وَمَآ اَنْتُمْ بِمُصْرِخِيَّۗ اِنِّيْ كَفَرْتُ بِمَآ اَشْرَكْتُمُوْنِ مِنْ قَبْلُ ۗاِنَّ الظّٰلِمِيْنَ لَهُمْ عَذَابٌ اَلِيْمٌ', 'wa qālasy-syaiṭānu lammā quḍiyal-amru innallāha wa\'adakum wa\'dal-ḥaqqi wa wa\'attukum fa akhlaftukum, wa mā kāna liya \'alaikum min sulṭānin illā an da\'autukum fastajabtum lī, fa lā talụmụnī wa lụmū anfusakum, mā ana bimuṣrikhikum wa mā antum bimuṣrikhiyy, innī kafartu bimā asyraktumụni ming qabl, innaẓ-ẓālimīna lahum \'ażābun alīm'),
(1772, 14, 23, 'وَاُدْخِلَ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا بِاِذْنِ رَبِّهِمْۗ تَحِيَّتُهُمْ فِيْهَا سَلٰمٌ', 'wa udkhilallażīna āmanụ wa \'amiluṣ-ṣāliḥāti jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā bi`iżni rabbihim, taḥiyyatuhum fīhā salām'),
(1773, 14, 24, 'اَلَمْ تَرَ كَيْفَ ضَرَبَ اللّٰهُ مَثَلًا كَلِمَةً طَيِّبَةً كَشَجَرَةٍ طَيِّبَةٍ اَصْلُهَا ثَابِتٌ وَّفَرْعُهَا فِى السَّمَاۤءِۙ', 'a lam tara kaifa ḍaraballāhu maṡalang kalimatan ṭayyibatang kasyajaratin ṭayyibatin aṣluhā ṡābituw wa far\'uhā fis-samā`'),
(1774, 14, 25, 'تُؤْتِيْٓ اُكُلَهَا كُلَّ حِيْنٍ ۢبِاِذْنِ رَبِّهَاۗ وَيَضْرِبُ اللّٰهُ الْاَمْثَالَ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُوْنَ', 'tu`tī ukulahā kulla ḥīnim bi`iżni rabbihā, wa yaḍribullāhul-amṡāla lin-nāsi la\'allahum yatażakkarụn'),
(1775, 14, 26, 'وَمَثَلُ كَلِمَةٍ خَبِيْثَةٍ كَشَجَرَةٍ خَبِيْثَةِ ِۨاجْتُثَّتْ مِنْ فَوْقِ الْاَرْضِ مَا لَهَا مِنْ قَرَارٍ', 'wa maṡalu kalimatin khabīṡating kasyajaratin khabīṡatinijtuṡṡat min fauqil-arḍi mā lahā ming qarār'),
(1776, 14, 27, 'يُثَبِّتُ اللّٰهُ الَّذِيْنَ اٰمَنُوْا بِالْقَوْلِ الثَّابِتِ فِى الْحَيٰوةِ الدُّنْيَا وَفِى الْاٰخِرَةِۚ وَيُضِلُّ اللّٰهُ الظّٰلِمِيْنَۗ وَيَفْعَلُ اللّٰهُ مَا يَشَاۤءُ', 'yuṡabbitullāhullażīna āmanụ bil-qauliṡ-ṡābiti fil-ḥayātid-dun-yā wa fil-ākhirah, wa yuḍillullāhuẓ-ẓālimīn, wa yaf\'alullāhu mā yasyā`'),
(1777, 14, 28, '۞ اَلَمْ تَرَ اِلَى الَّذِيْنَ بَدَّلُوْا نِعْمَتَ اللّٰهِ كُفْرًا وَّاَحَلُّوْا قَوْمَهُمْ دَارَ الْبَوَارِۙ', 'a lam tara ilallażīna baddalụ ni\'matallāhi kufraw wa aḥallụ qaumahum dāral-bawār'),
(1778, 14, 29, 'جَهَنَّمَ ۚيَصْلَوْنَهَاۗ وَبِئْسَ الْقَرَارُ', 'jahannam, yaṣlaunahā, wa bi`sal-qarār'),
(1779, 14, 30, 'وَجَعَلُوْا لِلّٰهِ اَنْدَادًا لِّيُضِلُّوْا عَنْ سَبِيْلِهٖۗ قُلْ تَمَتَّعُوْا فَاِنَّ مَصِيْرَكُمْ اِلَى النَّارِ', 'wa ja\'alụ lillāhi andādal liyuḍillụ \'an sabīlih, qul tamatta\'ụ fa inna maṣīrakum ilan-nār'),
(1780, 14, 31, 'قُلْ لِّعِبَادِيَ الَّذِيْنَ اٰمَنُوْا يُقِيْمُوا الصَّلٰوةَ وَيُنْفِقُوْا مِمَّا رَزَقْنٰهُمْ سِرًّا وَّعَلَانِيَةً مِّنْ قَبْلِ اَنْ يَّأْتِيَ يَوْمٌ لَّا بَيْعٌ فِيْهِ وَلَا خِلٰلٌ', 'qul li\'ibādiyallażīna āmanụ yuqīmuṣ-ṣalāta wa yunfiqụ mimmā razaqnāhum sirraw wa \'alāniyatam ming qabli ay ya`tiya yaumul lā bai\'un fīhi wa lā khilāl'),
(1781, 14, 32, 'اَللّٰهُ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَاَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً فَاَخْرَجَ بِهٖ مِنَ الثَّمَرٰتِ رِزْقًا لَّكُمْ ۚوَسَخَّرَ لَكُمُ الْفُلْكَ لِتَجْرِيَ فِى الْبَحْرِ بِاَمْرِهٖ ۚوَسَخَّرَ لَكُمُ الْاَنْهٰرَ', 'allāhullażī khalaqas-samāwāti wal-arḍa wa anzala minas-samā`i mā`an fa akhraja bihī minaṡ-ṡamarāti rizqal lakum, wa sakhkhara lakumul-fulka litajriya fil-baḥri bi`amrih, wasakhkhara lakumul-an-hār'),
(1782, 14, 33, 'وَسَخَّرَ لَكُمُ الشَّمْسَ وَالْقَمَرَ دَاۤىِٕبَيْنِۚ وَسَخَّرَ لَكُمُ الَّيْلَ وَالنَّهَارَ ۚ', 'wa sakhkhara lakumusy-syamsa wal-qamara dā`ibaīn, wa sakhkhara lakumul-laila wan-nahār'),
(1783, 14, 34, 'وَاٰتٰىكُمْ مِّنْ كُلِّ مَا سَاَلْتُمُوْهُۗ وَاِنْ تَعُدُّوْا نِعْمَتَ اللّٰهِ لَا تُحْصُوْهَاۗ اِنَّ الْاِنْسَانَ لَظَلُوْمٌ كَفَّارٌ', 'wa ātākum ming kulli mā sa`altumụh, wa in ta\'uddụ ni\'matallāhi lā tuḥṣụhā, innal-insāna laẓalụmung kaffār'),
(1784, 14, 35, 'وَاِذْ قَالَ اِبْرٰهِيْمُ رَبِّ اجْعَلْ هٰذَا الْبَلَدَ اٰمِنًا وَّاجْنُبْنِيْ وَبَنِيَّ اَنْ نَّعْبُدَ الْاَصْنَامَ ۗ', 'wa iż qāla ibrāhīmu rabbij\'al hāżal-balada āminaw wajnubnī wa baniyya an na\'budal-aṣnām'),
(1785, 14, 36, 'رَبِّ اِنَّهُنَّ اَضْلَلْنَ كَثِيْرًا مِّنَ النَّاسِۚ فَمَنْ تَبِعَنِيْ فَاِنَّهٗ مِنِّيْۚ وَمَنْ عَصَانِيْ فَاِنَّكَ غَفُوْرٌ رَّحِيْمٌ', 'rabbi innahunna aḍlalna kaṡīram minan-nās, fa man tabi\'anī fa innahụ minnī, wa man \'aṣānī fa innaka gafụrur raḥīm'),
(1786, 14, 37, 'رَبَّنَآ اِنِّيْٓ اَسْكَنْتُ مِنْ ذُرِّيَّتِيْ بِوَادٍ غَيْرِ ذِيْ زَرْعٍ عِنْدَ بَيْتِكَ الْمُحَرَّمِۙ رَبَّنَا لِيُقِيْمُوا الصَّلٰوةَ فَاجْعَلْ اَفْـِٕدَةً مِّنَ النَّاسِ تَهْوِيْٓ اِلَيْهِمْ وَارْزُقْهُمْ مِّنَ الثَّمَرٰتِ لَعَلَّهُمْ يَشْكُرُوْنَ', 'rabbanā innī askantu min żurriyyatī biwādin gairi żī zar\'in \'inda baitikal-muḥarrami rabbanā liyuqīmuṣ-ṣalāta faj\'al af`idatam minan-nāsi tahwī ilaihim warzuq-hum minaṡ-ṡamarāti la\'allahum yasykurụn');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(1787, 14, 38, 'رَبَّنَآ اِنَّكَ تَعْلَمُ مَا نُخْفِيْ وَمَا نُعْلِنُۗ وَمَا يَخْفٰى عَلَى اللّٰهِ مِنْ شَيْءٍ فِى الْاَرْضِ وَلَا فِى السَّمَاۤءِ', 'rabbanā innaka ta\'lamu mā nukhfī wa mā nu\'lin, wa mā yakhfā \'alallāhi min syai`in fil-arḍi wa lā fis-samā`'),
(1788, 14, 39, 'اَلْحَمْدُ لِلّٰهِ الَّذِيْ وَهَبَ لِيْ عَلَى الْكِبَرِ اِسْمٰعِيْلَ وَاِسْحٰقَۗ اِنَّ رَبِّيْ لَسَمِيْعُ الدُّعَاۤءِ', 'al-ḥamdu lillāhillażī wahaba lī \'alal-kibari ismā\'īla wa is-ḥāq, inna rabbī lasamī\'ud-du\'ā`'),
(1789, 14, 40, 'رَبِّ اجْعَلْنِيْ مُقِيْمَ الصَّلٰوةِ وَمِنْ ذُرِّيَّتِيْۖ رَبَّنَا وَتَقَبَّلْ دُعَاۤءِ', 'rabbij\'alnī muqīmaṣ-ṣalāti wa min żurriyyatī rabbanā wa taqabbal du\'ā`'),
(1790, 14, 41, 'رَبَّنَا اغْفِرْ لِيْ وَلِوَالِدَيَّ وَلِلْمُؤْمِنِيْنَ يَوْمَ يَقُوْمُ الْحِسَابُ', 'rabbanagfir lī wa liwālidayya wa lil-mu`minīna yauma yaqụmul-ḥisāb'),
(1791, 14, 42, 'وَلَا تَحْسَبَنَّ اللّٰهَ غَافِلًا عَمَّا يَعْمَلُ الظّٰلِمُوْنَ ەۗ اِنَّمَا يُؤَخِّرُهُمْ لِيَوْمٍ تَشْخَصُ فِيْهِ الْاَبْصَارُۙ', 'wa lā taḥsabannallāha gāfilan \'ammā ya\'maluẓ-ẓālimụn, innamā yu`akhkhiruhum liyaumin tasykhaṣu fīhil-abṣār'),
(1792, 14, 43, 'مُهْطِعِيْنَ مُقْنِعِيْ رُءُوْسِهِمْ لَا يَرْتَدُّ اِلَيْهِمْ طَرْفُهُمْ ۚوَاَفْـِٕدَتُهُمْ هَوَاۤءٌ ۗ', 'muhṭi\'īna muqni\'ī ru`ụsihim lā yartaddu ilaihim ṭarfuhum, wa af`idatuhum hawā`'),
(1793, 14, 44, 'وَاَنْذِرِ النَّاسَ يَوْمَ يَأْتِيْهِمُ الْعَذَابُۙ فَيَقُوْلُ الَّذِيْنَ ظَلَمُوْا رَبَّنَآ اَخِّرْنَآ اِلٰٓى اَجَلٍ قَرِيْبٍۙ نُّجِبْ دَعْوَتَكَ وَنَتَّبِعِ الرُّسُلَۗ اَوَلَمْ تَكُوْنُوْٓا اَقْسَمْتُمْ مِّنْ قَبْلُ مَا لَكُمْ مِّنْ زَوَالٍۙ', 'wa anżirin-nāsa yauma ya`tīhimul-\'ażābu fa yaqụlullażīna ẓalamụ rabbanā akhkhirnā ilā ajaling qarībin nujib da\'wataka wa nattabi\'ir-rusul, a wa lam takụnū aqsamtum ming qablu mā lakum min zawāl'),
(1794, 14, 45, 'وَّسَكَنْتُمْ فِيْ مَسٰكِنِ الَّذِيْنَ ظَلَمُوْٓا اَنْفُسَهُمْ وَتَبَيَّنَ لَكُمْ كَيْفَ فَعَلْنَا بِهِمْ وَضَرَبْنَا لَكُمُ الْاَمْثَالَ', 'wa sakantum fī masākinillażīna ẓalamū anfusahum wa tabayyana lakum kaifa fa\'alnā bihim wa ḍarabnā lakumul-amṡāl'),
(1795, 14, 46, 'وَقَدْ مَكَرُوْا مَكْرَهُمْ وَعِنْدَ اللّٰهِ مَكْرُهُمْۗ وَاِنْ كَانَ مَكْرُهُمْ لِتَزُوْلَ مِنْهُ الْجِبَالُ', 'wa qad makarụ makrahum wa \'indallāhi makruhum, wa ing kāna makruhum litazụla min-hul-jibāl'),
(1796, 14, 47, 'فَلَا تَحْسَبَنَّ اللّٰهَ مُخْلِفَ وَعْدِهٖ رُسُلَهٗ ۗاِنَّ اللّٰهَ عَزِيْزٌ ذُو انْتِقَامٍۗ', 'fa lā taḥsabannallāha mukhlifa wa\'dihī rusulah, innallāha \'azīzun żuntiqām'),
(1797, 14, 48, 'يَوْمَ تُبَدَّلُ الْاَرْضُ غَيْرَ الْاَرْضِ وَالسَّمٰوٰتُ وَبَرَزُوْا لِلّٰهِ الْوَاحِدِ الْقَهَّارِ', 'yauma tubaddalul-arḍu gairal-arḍi was-samāwātu wa barazụ lillāhil-wāḥidil-qahhār'),
(1798, 14, 49, 'وَتَرَى الْمُجْرِمِيْنَ يَوْمَىِٕذٍ مُّقَرَّنِيْنَ فِى الْاَصْفَادِۚ', 'wa taral-mujrimīna yauma`iżim muqarranīna fil-aṣfād'),
(1799, 14, 50, 'سَرَابِيْلُهُمْ مِّنْ قَطِرَانٍ وَّتَغْشٰى وُجُوْهَهُمُ النَّارُۙ', 'sarābīluhum ming qaṭirāniw wa tagsyā wujụhahumun-nār'),
(1800, 14, 51, 'لِيَجْزِيَ اللّٰهُ كُلَّ نَفْسٍ مَّا كَسَبَتْۗ اِنَّ اللّٰهَ سَرِيْعُ الْحِسَابِ', 'liyajziyallāhu kulla nafsim mā kasabat, innallāha sarī\'ul-ḥisāb'),
(1801, 14, 52, 'هٰذَا بَلٰغٌ لِّلنَّاسِ وَلِيُنْذَرُوْا بِهٖ وَلِيَعْلَمُوْٓا اَنَّمَا هُوَ اِلٰهٌ وَّاحِدٌ وَّلِيَذَّكَّرَ اُولُوا الْاَلْبَابِ', 'hāżā balāgul lin-nāsi wa liyunżarụ bihī wa liya\'lamū annamā huwa ilāhuw wāḥiduw wa liyażżakkara ulul-albāb'),
(1802, 15, 1, 'الۤرٰ ۗتِلْكَ اٰيٰتُ الْكِتٰبِ وَقُرْاٰنٍ مُّبِيْنٍ ۔', 'alif lām rā, tilka āyātul-kitābi wa qur`ānim mubīn'),
(1803, 15, 2, 'رُبَمَا يَوَدُّ الَّذِيْنَ كَفَرُوْا لَوْ كَانُوْا مُسْلِمِيْنَ', 'rubamā yawaddullażīna kafarụ lau kānụ muslimīn'),
(1804, 15, 3, 'ذَرْهُمْ يَأْكُلُوْا وَيَتَمَتَّعُوْا وَيُلْهِهِمُ الْاَمَلُ فَسَوْفَ يَعْلَمُوْنَ', 'żar-hum ya`kulụ wa yatamatta\'ụ wa yul-hihimul-amalu fa saufa ya\'lamụn'),
(1805, 15, 4, 'وَمَآ اَهْلَكْنَا مِنْ قَرْيَةٍ اِلَّا وَلَهَا كِتَابٌ مَّعْلُوْمٌ', 'wa mā ahlaknā ming qaryatin illā wa lahā kitābum ma\'lụm'),
(1806, 15, 5, 'مَا تَسْبِقُ مِنْ اُمَّةٍ اَجَلَهَا وَمَا يَسْتَأْخِرُوْنَ', 'mā tasbiqu min ummatin ajalahā wa mā yasta`khirụn'),
(1807, 15, 6, 'وَقَالُوْا يٰٓاَيُّهَا الَّذِيْ نُزِّلَ عَلَيْهِ الذِّكْرُ اِنَّكَ لَمَجْنُوْنٌ ۗ', 'wa qālụ yā ayyuhallażī nuzzila \'alaihiż-żikru innaka lamajnụn'),
(1808, 15, 7, 'لَوْمَا تَأْتِيْنَا بِالْمَلٰۤىِٕكَةِ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ', 'lau mā ta`tīnā bil-malā`ikati ing kunta minaṣ-ṣādiqīn'),
(1809, 15, 8, 'مَا نُنَزِّلُ الْمَلٰۤىِٕكَةَ اِلَّا بِالْحَقِّ وَمَا كَانُوْٓا اِذًا مُّنْظَرِيْنَ', 'mā nunazzilul-malā`ikata illā bil-ḥaqqi wa mā kānū iżam munẓarīn'),
(1810, 15, 9, 'اِنَّا نَحْنُ نَزَّلْنَا الذِّكْرَ وَاِنَّا لَهٗ لَحٰفِظُوْنَ', 'innā naḥnu nazzalnaż-żikra wa innā lahụ laḥāfiẓụn'),
(1811, 15, 10, 'وَلَقَدْ اَرْسَلْنَا مِنْ قَبْلِكَ فِيْ شِيَعِ الْاَوَّلِيْنَ', 'wa laqad arsalnā ming qablika fī syiya\'il-awwalīn'),
(1812, 15, 11, 'وَمَا يَأْتِيْهِمْ مِّنْ رَّسُوْلٍ اِلَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ', 'wa mā ya`tīhim mir rasụlin illā kānụ bihī yastahzi`ụn'),
(1813, 15, 12, 'كَذٰلِكَ نَسْلُكُهٗ فِيْ قُلُوْبِ الْمُجْرِمِيْنَۙ', 'każālika naslukuhụ fī qulụbil-mujrimīn'),
(1814, 15, 13, 'لَا يُؤْمِنُوْنَ بِهٖ وَقَدْ خَلَتْ سُنَّةُ الْاَوَّلِيْنَ', 'lā yu`minụna bihī wa qad khalat sunnatul-awwalīn'),
(1815, 15, 14, 'وَلَوْ فَتَحْنَا عَلَيْهِمْ بَابًا مِّنَ السَّمَاۤءِ فَظَلُّوْا فِيْهِ يَعْرُجُوْنَۙ', 'walau fataḥnā \'alaihim bābam minas-samā`i fa ẓallụ fīhi ya\'rujụn'),
(1816, 15, 15, 'لَقَالُوْٓا اِنَّمَا سُكِّرَتْ اَبْصَارُنَا بَلْ نَحْنُ قَوْمٌ مَّسْحُوْرُوْنَ', 'laqālū innamā sukkirat abṣārunā bal naḥnu qaumum mas-ḥụrụn'),
(1817, 15, 16, 'وَلَقَدْ جَعَلْنَا فِى السَّمَاۤءِ بُرُوْجًا وَّزَيَّنّٰهَا لِلنّٰظِرِيْنَۙ', 'wa laqad ja\'alnā fis-samā`i burụjaw wa zayyannāhā lin-nāẓirīn'),
(1818, 15, 17, 'وَحَفِظْنٰهَا مِنْ كُلِّ شَيْطٰنٍ رَّجِيْمٍۙ', 'wa ḥafiẓnāhā ming kulli syaiṭānir rajīm'),
(1819, 15, 18, 'اِلَّا مَنِ اسْتَرَقَ السَّمْعَ فَاَتْبَعَهٗ شِهَابٌ مُّبِيْنٌ', 'illā manistaraqas-sam\'a fa atba\'ahụ syihābum mubīn'),
(1820, 15, 19, 'وَالْاَرْضَ مَدَدْنٰهَا وَاَلْقَيْنَا فِيْهَا رَوَاسِيَ وَاَنْۢبَتْنَا فِيْهَا مِنْ كُلِّ شَيْءٍ مَّوْزُوْنٍ', 'wal-arḍa madadnāhā wa alqainā fīhā rawāsiya wa ambatnā fīhā ming kulli syai`im mauzụn'),
(1821, 15, 20, 'وَجَعَلْنَا لَكُمْ فِيْهَا مَعَايِشَ وَمَنْ لَّسْتُمْ لَهٗ بِرَازِقِيْنَ', 'wa ja\'alnā lakum fīhā ma\'āyisya wa mal lastum lahụ birāziqīn'),
(1822, 15, 21, 'وَاِنْ مِّنْ شَيْءٍ اِلَّا عِنْدَنَا خَزَاۤىِٕنُهٗ وَمَا نُنَزِّلُهٗٓ اِلَّا بِقَدَرٍ مَّعْلُوْمٍ', 'wa im min syai`in illā \'indanā khazā`inuhụ wa mā nunazziluhū illā biqadarim ma\'lụm'),
(1823, 15, 22, 'وَاَرْسَلْنَا الرِّيٰحَ لَوَاقِحَ فَاَنْزَلْنَا مِنَ السَّمَاۤءِ مَاۤءً فَاَسْقَيْنٰكُمُوْهُۚ وَمَآ اَنْتُمْ لَهٗ بِخَازِنِيْنَ', 'wa arsalnar-riyāḥa lawāqiḥa fa anzalnā minas-samā`i mā`an fa asqainākumụh, wa mā antum lahụ bikhāzinīn'),
(1824, 15, 23, 'وَاِنَّا لَنَحْنُ نُحْيٖ وَنُمِيْتُ وَنَحْنُ الْوَارِثُوْنَ', 'wa innā lanaḥnu nuḥyī wa numītu wa naḥnul-wāriṡụn'),
(1825, 15, 24, 'وَلَقَدْ عَلِمْنَا الْمُسْتَقْدِمِيْنَ مِنْكُمْ وَلَقَدْ عَلِمْنَا الْمُسْتَأْخِرِيْنَ', 'wa laqad \'alimnal-mustaqdimīna mingkum wa laqad \'alimnal-musta`khirīn'),
(1826, 15, 25, 'وَاِنَّ رَبَّكَ هُوَ يَحْشُرُهُمْۗ اِنَّهٗ حَكِيْمٌ عَلِيْمٌ', 'wa inna rabbaka huwa yaḥsyuruhum, innahụ ḥakīmun \'alīm'),
(1827, 15, 26, 'وَلَقَدْ خَلَقْنَا الْاِنْسَانَ مِنْ صَلْصَالٍ مِّنْ حَمَاٍ مَّسْنُوْنٍۚ', 'wa laqad khalaqnal-insāna min ṣalṣālim min ḥama`im masnụn'),
(1828, 15, 27, 'وَالْجَاۤنَّ خَلَقْنٰهُ مِنْ قَبْلُ مِنْ نَّارِ السَّمُوْمِ', 'wal-jānna khalaqnāhu ming qablu min nāris-samụm'),
(1829, 15, 28, 'وَاِذْ قَالَ رَبُّكَ لِلْمَلٰۤىِٕكَةِ اِنِّيْ خَالِقٌۢ بَشَرًا مِّنْ صَلْصَالٍ مِّنْ حَمَاٍ مَّسْنُوْنٍۚ', 'wa iż qāla rabbuka lil-malā`ikati innī khāliqum basyaram min ṣalṣālim min ḥama`im masnụn'),
(1830, 15, 29, 'فَاِذَا سَوَّيْتُهٗ وَنَفَخْتُ فِيْهِ مِنْ رُّوْحِيْ فَقَعُوْا لَهٗ سٰجِدِيْنَ', 'fa iżā sawwaituhụ wa nafakhtu fīhi mir rụḥī faqa\'ụ lahụ sājidīn'),
(1831, 15, 30, 'فَسَجَدَ الْمَلٰۤىِٕكَةُ كُلُّهُمْ اَجْمَعُوْنَۙ', 'fa sajadal-malā`ikatu kulluhum ajma\'ụn'),
(1832, 15, 31, 'اِلَّآ اِبْلِيْسَۗ اَبٰىٓ اَنْ يَّكُوْنَ مَعَ السّٰجِدِيْنَ', 'illā iblīs, abā ay yakụna ma\'as-sājidīn'),
(1833, 15, 32, 'قَالَ يٰٓاِبْلِيْسُ مَا لَكَ اَلَّا تَكُوْنَ مَعَ السّٰجِدِيْنَ', 'qāla yā iblīsu mā laka allā takụna ma\'as-sājidīn'),
(1834, 15, 33, 'قَالَ لَمْ اَكُنْ لِّاَسْجُدَ لِبَشَرٍ خَلَقْتَهٗ مِنْ صَلْصَالٍ مِّنْ حَمَاٍ مَّسْنُوْنٍ', 'qāla lam akul li`asjuda libasyarin khalaqtahụ min ṣalṣālim min ḥama`im masnụn'),
(1835, 15, 34, 'قَالَ فَاخْرُجْ مِنْهَا فَاِنَّكَ رَجِيْمٌۙ', 'qāla fakhruj min-hā fa innaka rajīm'),
(1836, 15, 35, 'وَّاِنَّ عَلَيْكَ اللَّعْنَةَ اِلٰى يَوْمِ الدِّيْنِ', 'wa inna \'alaikal-la\'nata ilā yaumid-dīn'),
(1837, 15, 36, 'قَالَ رَبِّ فَاَنْظِرْنِيْٓ اِلٰى يَوْمِ يُبْعَثُوْنَ', 'qāla rabbi fa anẓirnī ilā yaumi yub\'aṡụn'),
(1838, 15, 37, 'قَالَ فَاِنَّكَ مِنَ الْمُنْظَرِيْنَۙ', 'qāla fa innaka minal-munẓarīn'),
(1839, 15, 38, 'اِلٰى يَوْمِ الْوَقْتِ الْمَعْلُوْمِ', 'ilā yaumil-waqtil-ma\'lụm'),
(1840, 15, 39, 'قَالَ رَبِّ بِمَآ اَغْوَيْتَنِيْ لَاُزَيِّنَنَّ لَهُمْ فِى الْاَرْضِ وَلَاُغْوِيَنَّهُمْ اَجْمَعِيْنَۙ', 'qāla rabbi bimā agwaitanī la`uzayyinanna lahum fil-arḍi wa la`ugwiyannahum ajma\'īn'),
(1841, 15, 40, 'اِلَّا عِبَادَكَ مِنْهُمُ الْمُخْلَصِيْنَ', 'illā \'ibādaka min-humul-mukhlaṣīn'),
(1842, 15, 41, 'قَالَ هٰذَا صِرَاطٌ عَلَيَّ مُسْتَقِيْمٌ', 'qāla hāżā ṣirāṭun \'alayya mustaqīm'),
(1843, 15, 42, 'اِنَّ عِبَادِيْ لَيْسَ لَكَ عَلَيْهِمْ سُلْطٰنٌ اِلَّا مَنِ اتَّبَعَكَ مِنَ الْغٰوِيْنَ', 'inna \'ibādī laisa laka \'alaihim sulṭānun illā manittaba\'aka minal-gāwīn'),
(1844, 15, 43, 'وَاِنَّ جَهَنَّمَ لَمَوْعِدُهُمْ اَجْمَعِيْنَۙ', 'wa inna jahannama lamau\'iduhum ajma\'īn'),
(1845, 15, 44, 'لَهَا سَبْعَةُ اَبْوَابٍۗ لِكُلِّ بَابٍ مِّنْهُمْ جُزْءٌ مَّقْسُوْمٌ', 'lahā sab\'atu abwāb, likulli bābim min-hum juz`um maqsụm'),
(1846, 15, 45, 'اِنَّ الْمُتَّقِيْنَ فِيْ جَنّٰتٍ وَّعُيُوْنٍۗ', 'innal-muttaqīna fī jannātiw wa \'uyụn'),
(1847, 15, 46, 'اُدْخُلُوْهَا بِسَلٰمٍ اٰمِنِيْنَ', 'udkhulụhā bisalāmin āminīn'),
(1848, 15, 47, 'وَنَزَعْنَا مَا فِيْ صُدُوْرِهِمْ مِّنْ غِلٍّ اِخْوَانًا عَلٰى سُرُرٍ مُّتَقٰبِلِيْنَ', 'wa naza\'nā mā fī ṣudụrihim min gillin ikhwānan \'alā sururim mutaqābilīn'),
(1849, 15, 48, 'لَا يَمَسُّهُمْ فِيْهَا نَصَبٌ وَّمَا هُمْ مِّنْهَا بِمُخْرَجِيْنَ', 'lā yamassuhum fīhā naṣabuw wa mā hum min-hā bimukhrajīn'),
(1850, 15, 49, '۞ نَبِّئْ عِبَادِيْٓ اَنِّيْٓ اَنَا الْغَفُوْرُ الرَّحِيْمُۙ', 'nabbi` \'ibādī annī anal-gafụrur-raḥīm'),
(1851, 15, 50, 'وَاَنَّ عَذَابِيْ هُوَ الْعَذَابُ الْاَلِيْمُ', 'wa anna \'ażābī huwal-\'ażābul-alīm'),
(1852, 15, 51, 'وَنَبِّئْهُمْ عَنْ ضَيْفِ اِبْرٰهِيْمَۘ', 'wa nabbi`hum \'an ḍaifi ibrāhīm'),
(1853, 15, 52, 'اِذْ دَخَلُوْا عَلَيْهِ فَقَالُوْا سَلٰمًاۗ قَالَ اِنَّا مِنْكُمْ وَجِلُوْنَ', 'iż dakhalụ \'alaihi fa qālụ salāmā, qāla innā mingkum wajilụn'),
(1854, 15, 53, 'قَالُوْا لَا تَوْجَلْ اِنَّا نُبَشِّرُكَ بِغُلٰمٍ عَلِيْمٍ', 'qālụ lā taujal innā nubasysyiruka bigulāmin \'alīm'),
(1855, 15, 54, 'قَالَ اَبَشَّرْتُمُوْنِيْ عَلٰٓى اَنْ مَّسَّنِيَ الْكِبَرُ فَبِمَ تُبَشِّرُوْنَ', 'qāla a basysyartumụnī \'alā am massaniyal-kibaru fa bima tubasysyirụn'),
(1856, 15, 55, 'قَالُوْا بَشَّرْنٰكَ بِالْحَقِّ فَلَا تَكُنْ مِّنَ الْقٰنِطِيْنَ', 'qālụ basysyarnāka bil-ḥaqqi fa lā takum minal-qāniṭīn'),
(1857, 15, 56, 'قَالَ وَمَنْ يَّقْنَطُ مِنْ رَّحْمَةِ رَبِّهٖٓ اِلَّا الضَّاۤلُّوْنَ', 'qāla wa may yaqnaṭu mir raḥmati rabbihī illaḍ-ḍāllụn'),
(1858, 15, 57, 'قَالَ فَمَا خَطْبُكُمْ اَيُّهَا الْمُرْسَلُوْنَ', 'qāla fa mā khaṭbukum ayyuhal-mursalụn'),
(1859, 15, 58, 'قَالُوْٓا اِنَّآ اُرْسِلْنَآ اِلٰى قَوْمٍ مُّجْرِمِيْنَۙ', 'qālū innā ursilnā ilā qaumim mujrimīn'),
(1860, 15, 59, 'اِلَّآ اٰلَ لُوْطٍۗ اِنَّا لَمُنَجُّوْهُمْ اَجْمَعِيْنَۙ', 'illā āla lụṭ, innā lamunajjụhum ajma\'īn'),
(1861, 15, 60, 'اِلَّا امْرَاَتَهٗ قَدَّرْنَآ اِنَّهَا لَمِنَ الْغٰبِرِيْنَ', 'illamra`atahụ qaddarnā innahā laminal-gābirīn'),
(1862, 15, 61, 'فَلَمَّا جَاۤءَ اٰلَ لُوْطِ ِۨالْمُرْسَلُوْنَۙ', 'fa lammā jā`a āla lụṭinil-mursalụn'),
(1863, 15, 62, 'قَالَ اِنَّكُمْ قَوْمٌ مُّنْكَرُوْنَ', 'qāla innakum qaumum mungkarụn'),
(1864, 15, 63, 'قَالُوْا بَلْ جِئْنٰكَ بِمَا كَانُوْا فِيْهِ يَمْتَرُوْنَ', 'qālụ bal ji`nāka bimā kānụ fīhi yamtarụn'),
(1865, 15, 64, 'وَاَتَيْنٰكَ بِالْحَقِّ وَاِنَّا لَصٰدِقُوْنَ', 'wa ataināka bil-ḥaqqi wa innā laṣādiqụn'),
(1866, 15, 65, 'فَاَسْرِ بِاَهْلِكَ بِقِطْعٍ مِّنَ الَّيْلِ وَاتَّبِعْ اَدْبَارَهُمْ وَلَا يَلْتَفِتْ مِنْكُمْ اَحَدٌ وَّامْضُوْا حَيْثُ تُؤْمَرُوْنَ', 'fa asri bi`ahlika biqiṭ\'im minal-laili wattabi\' adbārahum wa lā yaltafit mingkum aḥaduw wamḍụ ḥaiṡu tu`marụn'),
(1867, 15, 66, 'وَقَضَيْنَآ اِلَيْهِ ذٰلِكَ الْاَمْرَ اَنَّ دَابِرَ هٰٓؤُلَاۤءِ مَقْطُوْعٌ مُّصْبِحِيْنَ', 'wa qaḍainā ilaihi żālikal-amra anna dābira hā`ulā`i maqṭụ\'um muṣbiḥīn'),
(1868, 15, 67, 'وَجَاۤءَ اَهْلُ الْمَدِيْنَةِ يَسْتَبْشِرُوْنَ', 'wa jā`a ahlul-madīnati yastabsyirụn'),
(1869, 15, 68, 'قَالَ اِنَّ هٰٓؤُلَاۤءِ ضَيْفِيْ فَلَا تَفْضَحُوْنِۙ', 'qāla inna hā`ulā`i ḍaifī fa lā tafḍaḥụn'),
(1870, 15, 69, 'وَاتَّقُوا اللّٰهَ وَلَا تُخْزُوْنِ', 'wattaqullāha wa lā tukhzụn'),
(1871, 15, 70, 'قَالُوْٓا اَوَلَمْ نَنْهَكَ عَنِ الْعٰلَمِيْنَ', 'qālū a wa lam nan-haka \'anil-\'ālamīn'),
(1872, 15, 71, 'قَالَ هٰٓؤُلَاۤءِ بَنٰتِيْٓ اِنْ كُنْتُمْ فٰعِلِيْنَۗ', 'qāla hā`ulā`i banātī ing kuntum fā\'ilīn'),
(1873, 15, 72, 'لَعَمْرُكَ اِنَّهُمْ لَفِيْ سَكْرَتِهِمْ يَعْمَهُوْنَ', 'la\'amruka innahum lafī sakratihim ya\'mahụn'),
(1874, 15, 73, 'فَاَخَذَتْهُمُ الصَّيْحَةُ مُشْرِقِيْنَۙ', 'fa akhażat-humuṣ-ṣaiḥatu musyriqīn'),
(1875, 15, 74, 'فَجَعَلْنَا عَالِيَهَا سَافِلَهَا وَاَمْطَرْنَا عَلَيْهِمْ حِجَارَةً مِّنْ سِجِّيْلٍ', 'fa ja\'alnā \'āliyahā sāfilahā wa amṭarnā \'alaihim ḥijāratam min sijjīl'),
(1876, 15, 75, 'اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّلْمُتَوَسِّمِيْنَۙ', 'inna fī żālika la`āyātil lil-mutawassimīn'),
(1877, 15, 76, 'وَاِنَّهَا لَبِسَبِيْلٍ مُّقِيْمٍ', 'wa innahā labisabīlim muqīm'),
(1878, 15, 77, 'اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّلْمُؤْمِنِيْنَۗ', 'inna fī żālika la`āyatal lil-mu`minīn'),
(1879, 15, 78, 'وَاِنْ كَانَ اَصْحٰبُ الْاَيْكَةِ لَظٰلِمِيْنَۙ', 'wa ing kāna aṣ-ḥābul-aikati laẓālimīn'),
(1880, 15, 79, 'فَانْتَقَمْنَا مِنْهُمْۘ وَاِنَّهُمَا لَبِاِمَامٍ مُّبِيْنٍۗ', 'fantaqamnā min-hum, wa innahumā labi`imāmim mubīn'),
(1881, 15, 80, 'وَلَقَدْ كَذَّبَ اَصْحٰبُ الْحِجْرِ الْمُرْسَلِيْنَۙ', 'wa laqad każżaba aṣ-ḥābul-ḥijril-mursalīn'),
(1882, 15, 81, 'وَاٰتَيْنٰهُمْ اٰيٰتِنَا فَكَانُوْا عَنْهَا مُعْرِضِيْنَۙ', 'wa ātaināhum āyātinā fa kānụ \'an-hā mu\'riḍīn'),
(1883, 15, 82, 'وَكَانُوْا يَنْحِتُوْنَ مِنَ الْجِبَالِ بُيُوْتًا اٰمِنِيْنَ', 'wa kānụ yan-ḥitụna minal-jibāli buyụtan āminīn'),
(1884, 15, 83, 'فَاَخَذَتْهُمُ الصَّيْحَةُ مُصْبِحِيْنَۙ', 'fa akhażat-humuṣ-ṣaiḥatu muṣbiḥīn'),
(1885, 15, 84, 'فَمَآ اَغْنٰى عَنْهُمْ مَّا كَانُوْا يَكْسِبُوْنَۗ', 'fa mā agnā \'an-hum mā kānụ yaksibụn'),
(1886, 15, 85, 'وَمَا خَلَقْنَا السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَآ اِلَّا بِالْحَقِّۗ وَاِنَّ السَّاعَةَ لَاٰتِيَةٌ فَاصْفَحِ الصَّفْحَ الْجَمِيْلَ', 'wa mā khalaqnas-samāwāti wal-arḍa wa mā bainahumā illā bil-ḥaqq, wa innas-sā\'ata la`ātiyatun faṣfaḥiṣ-ṣaf-ḥal jamīl'),
(1887, 15, 86, 'اِنَّ رَبَّكَ هُوَ الْخَلّٰقُ الْعَلِيْمُ', 'inna rabbaka huwal-khallāqul-\'alīm'),
(1888, 15, 87, 'وَلَقَدْ اٰتَيْنٰكَ سَبْعًا مِّنَ الْمَثَانِيْ وَالْقُرْاٰنَ الْعَظِيْمَ', 'wa laqad ātaināka sab\'am minal-maṡānī wal-qur`ānal-\'aẓīm'),
(1889, 15, 88, 'لَا تَمُدَّنَّ عَيْنَيْكَ اِلٰى مَا مَتَّعْنَا بِهٖٓ اَزْوَاجًا مِّنْهُمْ وَلَا تَحْزَنْ عَلَيْهِمْ وَاخْفِضْ جَنَاحَكَ لِلْمُؤْمِنِيْنَ', 'lā tamuddanna \'ainaika ilā mā matta\'nā bihī azwājam min-hum wa lā taḥzan \'alaihim wakhfiḍ janāḥaka lil-mu`minīn'),
(1890, 15, 89, 'وَقُلْ اِنِّيْٓ اَنَا النَّذِيْرُ الْمُبِيْنُۚ', 'wa qul innī anan-nażīrul-mubīn'),
(1891, 15, 90, 'كَمَآ اَنْزَلْنَا عَلَى الْمُقْتَسِمِيْنَۙ', 'kamā anzalnā \'alal-muqtasimīn'),
(1892, 15, 91, 'الَّذِيْنَ جَعَلُوا الْقُرْاٰنَ عِضِيْنَ', 'allażīna ja\'alul-qur`āna \'iḍīn'),
(1893, 15, 92, 'فَوَرَبِّكَ لَنَسْـَٔلَنَّهُمْ اَجْمَعِيْنَۙ', 'fa wa rabbika lanas`alannahum ajma\'īn'),
(1894, 15, 93, 'عَمَّا كَانُوْا يَعْمَلُوْنَ', '\'ammā kānụ ya\'malụn'),
(1895, 15, 94, 'فَاصْدَعْ بِمَا تُؤْمَرُ وَاَعْرِضْ عَنِ الْمُشْرِكِيْنَ', 'faṣda\' bimā tu`maru wa a\'riḍ \'anil-musyrikīn'),
(1896, 15, 95, 'اِنَّا كَفَيْنٰكَ الْمُسْتَهْزِءِيْنَۙ', 'innā kafainākal-mustahzi`īn'),
(1897, 15, 96, 'الَّذِيْنَ يَجْعَلُوْنَ مَعَ اللّٰهِ اِلٰهًا اٰخَرَۚ فَسَوْفَ يَعْلَمُوْنَ', 'allażīna yaj\'alụna ma\'allāhi ilāhan ākhar, fa saufa ya\'lamụn'),
(1898, 15, 97, 'وَلَقَدْ نَعْلَمُ اَنَّكَ يَضِيْقُ صَدْرُكَ بِمَا يَقُوْلُوْنَۙ', 'wa laqad na\'lamu annaka yaḍīqu ṣadruka bimā yaqụlụn'),
(1899, 15, 98, 'فَسَبِّحْ بِحَمْدِ رَبِّكَ وَكُنْ مِّنَ السَّاجِدِيْنَۙ', 'fa sabbiḥ biḥamdi rabbika wa kum minas-sājidīn'),
(1900, 15, 99, 'وَاعْبُدْ رَبَّكَ حَتّٰى يَأْتِيَكَ الْيَقِيْنُ', 'wa\'bud rabbaka ḥattā ya`tiyakal-yaqīn'),
(1901, 16, 1, 'اَتٰىٓ اَمْرُ اللّٰهِ فَلَا تَسْتَعْجِلُوْهُ ۗسُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يُشْرِكُوْنَ', 'atā amrullāhi fa lā tasta\'jilụh, sub-ḥānahụ wa ta\'ālā \'ammā yusyrikụn'),
(1902, 16, 2, 'يُنَزِّلُ الْمَلٰۤىِٕكَةَ بِالرُّوْحِ مِنْ اَمْرِهٖ عَلٰى مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖٓ اَنْ اَنْذِرُوْٓا اَنَّهٗ لَآ اِلٰهَ اِلَّآ اَنَا۠ فَاتَّقُوْنِ', 'yunazzilul-malā`ikata bir-rụḥi min amrihī \'alā may yasyā`u min \'ibādihī an anżirū annahụ lā ilāha illā ana fattaqụn'),
(1903, 16, 3, 'خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّۗ تَعٰلٰى عَمَّا يُشْرِكُوْنَ', 'khalaqas-samāwāti wal-arḍa bil-ḥaqq, ta\'ālā \'ammā yusyrikụn'),
(1904, 16, 4, 'خَلَقَ الْاِنْسَانَ مِنْ نُّطْفَةٍ فَاِذَا هُوَ خَصِيْمٌ مُّبِيْنٌ', 'khalaqal-insāna min nuṭfatin fa iżā huwa khaṣīmum mubīn'),
(1905, 16, 5, 'وَالْاَنْعَامَ خَلَقَهَا لَكُمْ فِيْهَا دِفْءٌ وَّمَنَافِعُ وَمِنْهَا تَأْكُلُوْنَ', 'wal-an\'āma khalaqahā lakum fīhā dif`uw wa manāfi\'u wa min-hā ta`kulụn'),
(1906, 16, 6, 'وَلَكُمْ فِيْهَا جَمَالٌ حِيْنَ تُرِيْحُوْنَ وَحِيْنَ تَسْرَحُوْنَۖ', 'wa lakum fīhā jamālun ḥīna turīḥụna wa ḥīna tasraḥụn'),
(1907, 16, 7, 'وَتَحْمِلُ اَثْقَالَكُمْ اِلٰى بَلَدٍ لَّمْ تَكُوْنُوْا بٰلِغِيْهِ اِلَّا بِشِقِّ الْاَنْفُسِۗ اِنَّ رَبَّكُمْ لَرَءُوْفٌ رَّحِيْمٌۙ', 'wa taḥmilu aṡqālakum ilā baladil lam takụnụ bāligīhi illā bisyiqqil-anfus, inna rabbakum lara`ụfur raḥīm'),
(1908, 16, 8, 'وَّالْخَيْلَ وَالْبِغَالَ وَالْحَمِيْرَ لِتَرْكَبُوْهَا وَزِيْنَةًۗ وَيَخْلُقُ مَا لَا تَعْلَمُوْنَ', 'wal-khaila wal-bigāla wal-ḥamīra litarkabụhā wa zīnah, wa yakhluqu mā lā ta\'lamụn'),
(1909, 16, 9, 'وَعَلَى اللّٰهِ قَصْدُ السَّبِيْلِ وَمِنْهَا جَاۤىِٕرٌ ۗوَلَوْ شَاۤءَ لَهَدٰىكُمْ اَجْمَعِيْنَ', 'wa \'alallāhi qaṣdus-sabīli wa min-hā jā`ir, walau syā`a lahadākum ajma\'īn'),
(1910, 16, 10, 'هُوَ الَّذِيْٓ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً لَّكُمْ مِّنْهُ شَرَابٌ وَّمِنْهُ شَجَرٌ فِيْهِ تُسِيْمُوْنَ', 'huwallażī anzala minas-samā`i mā`al lakum min-hu syarābuw wa min-hu syajarun fīhi tusīmụn'),
(1911, 16, 11, 'يُنْۢبِتُ لَكُمْ بِهِ الزَّرْعَ وَالزَّيْتُوْنَ وَالنَّخِيْلَ وَالْاَعْنَابَ وَمِنْ كُلِّ الثَّمَرٰتِۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّتَفَكَّرُوْنَ', 'yumbitu lakum bihiz-zar\'a waz-zaitụna wan-nakhīla wal-a\'nāba wa ming kulliṡ-ṡamarāt, inna fī żālika la`āyatal liqaumiy yatafakkarụn'),
(1912, 16, 12, 'وَسَخَّرَ لَكُمُ الَّيْلَ وَالنَّهَارَۙ وَالشَّمْسَ وَالْقَمَرَ ۗوَالنُّجُوْمُ مُسَخَّرٰتٌۢ بِاَمْرِهٖ ۗاِنَّ فِي ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّعْقِلُوْنَۙ', 'wa sakhkhara lakumul-laila wan-nahāra wasy-syamsa wal-qamar, wan-nujụmu musakhkharātum bi`amrih, inna fī żālika la`āyātil liqaumiy ya\'qilụn'),
(1913, 16, 13, 'وَمَا ذَرَاَ لَكُمْ فِى الْاَرْضِ مُخْتَلِفًا اَلْوَانُهٗ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّذَّكَّرُوْنَ', 'wa mā żara`a lakum fil-arḍi mukhtalifan alwānuh, inna fī żālika la`āyatal liqaumiy yażżakkarụn'),
(1914, 16, 14, 'وَهُوَ الَّذِيْ سَخَّرَ الْبَحْرَ لِتَأْكُلُوْا مِنْهُ لَحْمًا طَرِيًّا وَّتَسْتَخْرِجُوْا مِنْهُ حِلْيَةً تَلْبَسُوْنَهَاۚ وَتَرَى الْفُلْكَ مَوَاخِرَ فِيْهِ وَلِتَبْتَغُوْا مِنْ فَضْلِهٖ وَلَعَلَّكُمْ تَشْكُرُوْنَ', 'wa huwallażī sakhkharal-baḥra lita`kulụ min-hu laḥman ṭariyyaw wa tastakhrijụ min-hu ḥilyatan talbasụnahā, wa taral-fulka mawākhira fīhi wa litabtagụ min faḍlihī wa la\'allakum tasykurụn'),
(1915, 16, 15, 'وَاَلْقٰى فِى الْاَرْضِ رَوَاسِيَ اَنْ تَمِيْدَ بِكُمْ وَاَنْهٰرًا وَّسُبُلًا لَّعَلَّكُمْ تَهْتَدُوْنَۙ', 'wa alqā fil-arḍi rawāsiya an tamīda bikum wa an-hāraw wa subulal la\'allakum tahtadụn'),
(1916, 16, 16, 'وَعَلٰمٰتٍۗ وَبِالنَّجْمِ هُمْ يَهْتَدُوْنَ', 'wa \'alāmāt, wa bin-najmi hum yahtadụn'),
(1917, 16, 17, 'اَفَمَنْ يَّخْلُقُ كَمَنْ لَّا يَخْلُقُۗ اَفَلَا تَذَكَّرُوْنَ', 'a fa may yakhluqu kamal lā yakhluq, a fa lā tażakkarụn'),
(1918, 16, 18, 'وَاِنْ تَعُدُّوْا نِعْمَةَ اللّٰهِ لَا تُحْصُوْهَا ۗاِنَّ اللّٰهَ لَغَفُوْرٌ رَّحِيْمٌ', 'wa in ta\'uddụ ni\'matallāhi lā tuḥṣụhā, innallāha lagafụrur raḥīm'),
(1919, 16, 19, 'وَاللّٰهُ يَعْلَمُ مَا تُسِرُّوْنَ وَمَا تُعْلِنُوْنَ', 'wallāhu ya\'lamu mā tusirrụna wa mā tu\'linụn'),
(1920, 16, 20, 'وَالَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ لَا يَخْلُقُوْنَ شَيْـًٔا وَّهُمْ يُخْلَقُوْنَۗ', 'wallażīna yad\'ụna min dụnillāhi lā yakhluqụna syai`aw wa hum yukhlaqụn'),
(1921, 16, 21, 'اَمْوَاتٌ غَيْرُ اَحْيَاۤءٍ ۗوَمَا يَشْعُرُوْنَۙ اَيَّانَ يُبْعَثُوْنَ', 'amwātun gairu aḥyā`, wa mā yasy\'urụna ayyāna yub\'aṡụn'),
(1922, 16, 22, 'اِلٰهُكُمْ اِلٰهٌ وَّاحِدٌ ۚفَالَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ قُلُوْبُهُمْ مُّنْكِرَةٌ وَّهُمْ مُّسْتَكْبِرُوْنَ', 'ilāhukum ilāhuw wāḥid, fallażīna lā yu`minụna bil-ākhirati qulụbuhum mungkiratuw wa hum mustakbirụn'),
(1923, 16, 23, 'لَا جَرَمَ اَنَّ اللّٰهَ يَعْلَمُ مَا يُسِرُّوْنَ وَمَا يُعْلِنُوْنَ ۗاِنَّهٗ لَا يُحِبُّ الْمُسْتَكْبِرِيْنَ', 'lā jarama annallāha ya\'lamu mā yusirrụna wa mā yu\'linụn, innahụ lā yuḥibbul-mustakbirīn'),
(1924, 16, 24, 'وَاِذَا قِيْلَ لَهُمْ مَّاذَآ اَنْزَلَ رَبُّكُمْ ۙقَالُوْٓا اَسَاطِيْرُ الْاَوَّلِيْنَ', 'wa iżā qīla lahum māżā anzala rabbukum qālū asāṭīrul-awwalīn'),
(1925, 16, 25, 'لِيَحْمِلُوْٓا اَوْزَارَهُمْ كَامِلَةً يَّوْمَ الْقِيٰمَةِ ۙوَمِنْ اَوْزَارِ الَّذِيْنَ يُضِلُّوْنَهُمْ بِغَيْرِ عِلْمٍ ۗ اَلَا سَاۤءَ مَا يَزِرُوْنَ', 'liyaḥmilū auzārahum kāmilatay yaumal-qiyāmati wa min auzārillażīna yuḍillụnahum bigairi \'ilm, alā sā`a mā yazirụn'),
(1926, 16, 26, 'قَدْ مَكَرَ الَّذِيْنَ مِنْ قَبْلِهِمْ فَاَتَى اللّٰهُ بُنْيَانَهُمْ مِّنَ الْقَوَاعِدِ فَخَرَّ عَلَيْهِمُ السَّقْفُ مِنْ فَوْقِهِمْ وَاَتٰىهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُوْنَ', 'qad makarallażīna ming qablihim fa atallāhu bun-yānahum minal-qawā\'idi fa kharra \'alaihimus-saqfu min fauqihim wa atāhumul-\'ażābu min ḥaiṡu lā yasy\'urụn'),
(1927, 16, 27, 'ثُمَّ يَوْمَ الْقِيٰمَةِ يُخْزِيْهِمْ وَيَقُوْلُ اَيْنَ شُرَكَاۤءِيَ الَّذِيْنَ كُنْتُمْ تُشَاۤقُّوْنَ فِيْهِمْ ۗقَالَ الَّذِيْنَ اُوْتُوا الْعِلْمَ اِنَّ الْخِزْيَ الْيَوْمَ وَالسُّوْۤءَ عَلَى الْكٰفِرِيْنَۙ', 'ṡumma yaumal-qiyāmati yukhzīhim wa yaqụlu aina syurakā`iyallażīna kuntum tusyāqqụna fīhim, qālallażīna ụtul-\'ilma innal-khizyal-yauma was-sū`a \'alal-kāfirīn'),
(1928, 16, 28, 'الَّذِيْنَ تَتَوَفّٰىهُمُ الْمَلٰۤىِٕكَةُ ظَالِمِيْٓ اَنْفُسِهِمْ ۖفَاَلْقَوُا السَّلَمَ مَا كُنَّا نَعْمَلُ مِنْ سُوْۤءٍ ۗبَلٰىٓ اِنَّ اللّٰهَ عَلِيْمٌۢ بِمَا كُنْتُمْ تَعْمَلُوْنَ', 'allażīna tatawaffāhumul-malā`ikatu ẓālimī anfusihim fa alqawus-salama mā kunnā na\'malu min sū`, balā innallāha \'alīmum bimā kuntum ta\'malụn'),
(1929, 16, 29, 'فَادْخُلُوْٓا اَبْوَابَ جَهَنَّمَ خٰلِدِيْنَ فِيْهَا ۗفَلَبِئْسَ مَثْوَى الْمُتَكَبِّرِيْنَ', 'fadkhulū abwāba jahannama khālidīna fīhā, fa labi`sa maṡwal-mutakabbirīn'),
(1930, 16, 30, '۞ وَقِيْلَ لِلَّذِيْنَ اتَّقَوْا مَاذَآ اَنْزَلَ رَبُّكُمْ ۗقَالُوْا خَيْرًا ۚلِلَّذِيْنَ اَحْسَنُوْا فِيْ هٰذِهِ الدُّنْيَا حَسَنَةٌ ۗوَلَدَارُ الْاٰخِرَةِ خَيْرٌ ۗوَلَنِعْمَ دَارُ الْمُتَّقِيْنَۙ', 'wa qīla lillażīnattaqau māżā anzala rabbukum, qālụ khairā, lillażīna aḥsanụ fī hāżihid-dun-yā ḥasanah, wa ladārul-ākhirati khaīr, wa lani\'ma dārul-muttaqīn'),
(1931, 16, 31, 'جَنّٰتُ عَدْنٍ يَّدْخُلُوْنَهَا تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ لَهُمْ فِيْهَا مَا يَشَاۤءُوْنَ ۗ كَذٰلِكَ يَجْزِى اللّٰهُ الْمُتَّقِيْنَۙ', 'jannātu \'adniy yadkhulụnahā tajrī min taḥtihal-an-hāru lahum fīhā mā yasyā`ụn, każālika yajzillāhul-muttaqīn'),
(1932, 16, 32, 'الَّذِيْنَ تَتَوَفّٰىهُمُ الْمَلٰۤىِٕكَةُ طَيِّبِيْنَ ۙيَقُوْلُوْنَ سَلٰمٌ عَلَيْكُمُ ادْخُلُوا الْجَنَّةَ بِمَا كُنْتُمْ تَعْمَلُوْنَ', 'allażīna tatawaffāhumul-malā`ikatu ṭayyibīna yaqụlụna salāmun \'alaikumudkhulul-jannata bimā kuntum ta\'malụn'),
(1933, 16, 33, 'هَلْ يَنْظُرُوْنَ اِلَّآ اَنْ تَأْتِيَهُمُ الْمَلٰۤىِٕكَةُ اَوْ يَأْتِيَ اَمْرُ رَبِّكَ ۗ كَذٰلِكَ فَعَلَ الَّذِيْنَ مِنْ قَبْلِهِمْ ۗوَمَا ظَلَمَهُمُ اللّٰهُ وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَ', 'hal yanẓurụna illā an ta`tiyahumul-malā`ikatu au ya`tiya amru rabbik, każālika fa\'alallażīna ming qablihim, wa mā ẓalamahumullāhu wa lāking kānū anfusahum yaẓlimụn'),
(1934, 16, 34, 'فَاَصَابَهُمْ سَيِّاٰتُ مَا عَمِلُوْا وَحَاقَ بِهِمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ', 'fa aṣābahum sayyi`ātu mā \'amilụ wa ḥāqa bihim mā kānụ bihī yastahzi`ụn'),
(1935, 16, 35, 'وَقَالَ الَّذِيْنَ اَشْرَكُوْا لَوْ شَاۤءَ اللّٰهُ مَا عَبَدْنَا مِنْ دُوْنِهٖ مِنْ شَيْءٍ نَّحْنُ وَلَآ اٰبَاۤؤُنَا وَلَا حَرَّمْنَا مِنْ دُوْنِهٖ مِنْ شَيْءٍ ۗ كَذٰلِكَ فَعَلَ الَّذِيْنَ مِنْ قَبْلِهِمْ ۚفَهَلْ عَلَى الرُّسُلِ اِلَّا الْبَلٰغُ الْمُبِيْنُ', 'wa qālallażīna asyrakụ lau syā`allāhu mā \'abadnā min dụnihī min syai`in naḥnu wa lā ābā`unā wa lā ḥarramnā min dụnihī min syaī`, każālika fa\'alallażīna ming qablihim, fa hal \'alar-rusuli illal-balāgul-mubīn'),
(1936, 16, 36, 'وَلَقَدْ بَعَثْنَا فِيْ كُلِّ اُمَّةٍ رَّسُوْلًا اَنِ اعْبُدُوا اللّٰهَ وَاجْتَنِبُوا الطَّاغُوْتَۚ فَمِنْهُمْ مَّنْ هَدَى اللّٰهُ وَمِنْهُمْ مَّنْ حَقَّتْ عَلَيْهِ الضَّلٰلَةُ ۗ فَسِيْرُوْا فِى الْاَرْضِ فَانْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِيْنَ', 'wa laqad ba\'aṡnā fī kulli ummatir rasụlan ani\'budullāha wajtanibuṭ-ṭāgụt, fa min-hum man hadallāhu wa min-hum man ḥaqqat \'alaihiḍ-ḍalālah, fa sīrụ fil-arḍi fanẓurụ kaifa kāna \'āqibatul-mukażżibīn'),
(1937, 16, 37, 'اِنْ تَحْرِصْ عَلٰى هُدٰىهُمْ فَاِنَّ اللّٰهَ لَا يَهْدِيْ مَنْ يُّضِلُّ وَمَا لَهُمْ مِّنْ نّٰصِرِيْنَ', 'in taḥriṣ \'alā hudāhum fa innallāha lā yahdī may yuḍillu wa mā lahum min nāṣirīn'),
(1938, 16, 38, 'وَاَقْسَمُوْا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْۙ لَا يَبْعَثُ اللّٰهُ مَنْ يَّمُوْتُۗ بَلٰى وَعْدًا عَلَيْهِ حَقًّا وَّلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَۙ', 'wa aqsamụ billāhi jahda aimānihim lā yab\'aṡullāhu may yamụt, balā wa\'dan \'alaihi ḥaqqaw wa lākinna akṡaran-nāsi lā ya\'lamụn'),
(1939, 16, 39, 'لِيُبَيِّنَ لَهُمُ الَّذِيْ يَخْتَلِفُوْنَ فِيْهِ وَلِيَعْلَمَ الَّذِيْنَ كَفَرُوْٓا اَنَّهُمْ كَانُوْا كٰذِبِيْنَ', 'liyubayyina lahumullażī yakhtalifụna fīhi wa liya\'lamallażīna kafarū annahum kānụ kāżibīn'),
(1940, 16, 40, 'اِنَّمَا قَوْلُنَا لِشَيْءٍ اِذَآ اَرَدْنٰهُ اَنْ نَّقُوْلَ لَهٗ كُنْ فَيَكُوْنُ', 'innamā qaulunā lisyai`in iżā aradnāhu an naqụla lahụ kun fa yakụn'),
(1941, 16, 41, 'وَالَّذِيْنَ هَاجَرُوْا فِى اللّٰهِ مِنْۢ بَعْدِ مَا ظُلِمُوْا لَنُبَوِّئَنَّهُمْ فِى الدُّنْيَا حَسَنَةً ۗوَلَاَجْرُ الْاٰخِرَةِ اَكْبَرُۘ لَوْ كَانُوْا يَعْلَمُوْنَۙ', 'wallażīna hājarụ fillāhi mim ba\'di mā ẓulimụ lanubawwi`annahum fid-dun-yā ḥasanah, wa la`ajrul-ākhirati akbar, lau kānụ ya\'lamụn'),
(1942, 16, 42, 'الَّذِيْنَ صَبَرُوْا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُوْنَ', 'allażīna ṣabarụ wa \'alā rabbihim yatawakkalụn'),
(1943, 16, 43, 'وَمَآ اَرْسَلْنَا مِنْ قَبْلِكَ اِلَّا رِجَالًا نُّوْحِيْٓ اِلَيْهِمْ فَاسْـَٔلُوْٓا اَهْلَ الذِّكْرِ اِنْ كُنْتُمْ لَا تَعْلَمُوْنَۙ', 'wa mā arsalnā ming qablika illā rijālan nụḥī ilaihim fas`alū ahlaż-żikri ing kuntum lā ta\'lamụn'),
(1944, 16, 44, 'بِالْبَيِّنٰتِ وَالزُّبُرِۗ وَاَنْزَلْنَآ اِلَيْكَ الذِّكْرَ لِتُبَيِّنَ لِلنَّاسِ مَا نُزِّلَ اِلَيْهِمْ وَلَعَلَّهُمْ يَتَفَكَّرُوْنَ', 'bil-bayyināti waz-zubur, wa anzalnā ilaikaż-żikra litubayyina lin-nāsi mā nuzzila ilaihim wa la\'allahum yatafakkarụn'),
(1945, 16, 45, 'اَفَاَمِنَ الَّذِيْنَ مَكَرُوا السَّيِّاٰتِ اَنْ يَّخْسِفَ اللّٰهُ بِهِمُ الْاَرْضَ اَوْ يَأْتِيَهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُوْنَۙ', 'a fa aminallażīna makarus-sayyi`āti ay yakhsifallāhu bihimul-arḍa au ya`tiyahumul-\'ażābu min ḥaiṡu lā yasy\'urụn'),
(1946, 16, 46, 'اَوْ يَأْخُذَهُمْ فِيْ تَقَلُّبِهِمْ فَمَا هُمْ بِمُعْجِزِيْنَۙ', 'au ya`khużahum fī taqallubihim fa mā hum bimu\'jizīn'),
(1947, 16, 47, 'اَوْ يَأْخُذَهُمْ عَلٰى تَخَوُّفٍۗ فَاِنَّ رَبَّكُمْ لَرَءُوْفٌ رَّحِيْمٌ', 'au ya`khużahum \'alā takhawwuf, fa inna rabbakum lara`ụfur raḥīm'),
(1948, 16, 48, 'اَوَلَمْ يَرَوْا اِلٰى مَا خَلَقَ اللّٰهُ مِنْ شَيْءٍ يَّتَفَيَّؤُا ظِلٰلُهٗ عَنِ الْيَمِيْنِ وَالشَّمَاۤىِٕلِ سُجَّدًا لِّلّٰهِ وَهُمْ دَاخِرُوْنَ', 'a wa lam yarau ilā mā khalaqallāhu min syai`iy yatafayya`u ẓilāluhụ \'anil-yamīni wasy-syamā`ili sujjadal lillāhi wa hum dākhirụn'),
(1949, 16, 49, 'وَلِلّٰهِ يَسْجُدُ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ مِنْ دَاۤبَّةٍ وَّالْمَلٰۤىِٕكَةُ وَهُمْ لَا يَسْتَكْبِرُوْنَ', 'wa lillāhi yasjudu mā fis-samāwāti wa mā fil-arḍi min dābbatiw wal-malā`ikatu wa hum lā yastakbirụn'),
(1950, 16, 50, 'يَخَافُوْنَ رَبَّهُمْ مِّنْ فَوْقِهِمْ وَيَفْعَلُوْنَ مَا يُؤْمَرُوْنَ ۩', 'yakhāfụna rabbahum min fauqihim wa yaf\'alụna mā yu`marụn'),
(1951, 16, 51, '۞ وَقَالَ اللّٰهُ لَا تَتَّخِذُوْٓا اِلٰهَيْنِ اثْنَيْنِۚ اِنَّمَا هُوَ اِلٰهٌ وَّاحِدٌ فَاِيَّايَ فَارْهَبُوْنِ', 'wa qālallāhu lā tattakhiżū ilāhainiṡnaīn, innamā huwa ilāhuw wāḥidun fa iyyāya far-habụn'),
(1952, 16, 52, 'وَلَهٗ مَا فِى السَّمٰوٰتِ وَالْاَرْضِ وَلَهُ الدِّيْنُ وَاصِبًاۗ اَفَغَيْرَ اللّٰهِ تَتَّقُوْنَ', 'wa lahụ mā fis-samāwāti wal-arḍi wa lahud-dīnu wāṣibā, a fa gairallāhi tattaqụn'),
(1953, 16, 53, 'وَمَا بِكُمْ مِّنْ نِّعْمَةٍ فَمِنَ اللّٰهِ ثُمَّ اِذَا مَسَّكُمُ الضُّرُّ فَاِلَيْهِ تَجْـَٔرُوْنَۚ', 'wa mā bikum min ni\'matin fa minallāhi ṡumma iżā massakumuḍ-ḍurru fa ilaihi taj`arụn'),
(1954, 16, 54, 'ثُمَّ اِذَا كَشَفَ الضُّرَّ عَنْكُمْ اِذَا فَرِيْقٌ مِّنْكُمْ بِرَبِّهِمْ يُشْرِكُوْنَۙ', 'ṡumma iżā kasyafaḍ-ḍurra \'angkum iżā farīqum mingkum birabbihim yusyrikụn'),
(1955, 16, 55, 'لِيَكْفُرُوْا بِمَآ اٰتَيْنٰهُمْۗ فَتَمَتَّعُوْاۗ فَسَوْفَ تَعْلَمُوْنَ', 'liyakfurụ bimā ātaināhum, fa tamatta\'ụ, fa saufa ta\'lamụn'),
(1956, 16, 56, 'وَيَجْعَلُوْنَ لِمَا لَا يَعْلَمُوْنَ نَصِيْبًا مِّمَّا رَزَقْنٰهُمْۗ تَاللّٰهِ لَتُسْـَٔلُنَّ عَمَّا كُنْتُمْ تَفْتَرُوْنَ', 'wa yaj\'alụna limā lā ya\'lamụna naṣībam mimmā razaqnāhum, tallāhi latus`alunna \'ammā kuntum taftarụn'),
(1957, 16, 57, 'وَيَجْعَلُوْنَ لِلّٰهِ الْبَنٰتِ سُبْحٰنَهٗۙ وَلَهُمْ مَّا يَشْتَهُوْنَ', 'wa yaj\'alụna lillāhil-banāti sub-ḥānahụ wa lahum mā yasytahụn'),
(1958, 16, 58, 'وَاِذَا بُشِّرَ اَحَدُهُمْ بِالْاُنْثٰى ظَلَّ وَجْهُهٗ مُسْوَدًّا وَّهُوَ كَظِيْمٌۚ', 'wa iżā busysyira aḥaduhum bil-unṡā ẓalla waj-huhụ muswaddaw wa huwa kaẓīm'),
(1959, 16, 59, 'يَتَوٰرٰى مِنَ الْقَوْمِ مِنْ سُوْۤءِ مَا بُشِّرَ بِهٖۗ اَيُمْسِكُهٗ عَلٰى هُوْنٍ اَمْ يَدُسُّهٗ فِى التُّرَابِۗ اَلَا سَاۤءَ مَا يَحْكُمُوْنَ', 'yatawarā minal-qaumi min sū`i mā busysyira bih, a yumsikuhụ \'alā hụnin am yadussuhụ fit-turāb, alā sā`a mā yaḥkumụn'),
(1960, 16, 60, 'لِلَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ مَثَلُ السَّوْءِۚ وَلِلّٰهِ الْمَثَلُ الْاَعْلٰىۗ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ', 'lillażīna lā yu`minụna bil-ākhirati maṡalus-saụ`, walillāhil-maṡalul-a\'lā, wa huwal-\'azīzul-ḥakīm'),
(1961, 16, 61, 'وَلَوْ يُؤَاخِذُ اللّٰهُ النَّاسَ بِظُلْمِهِمْ مَّا تَرَكَ عَلَيْهَا مِنْ دَاۤبَّةٍ وَّلٰكِنْ يُّؤَخِّرُهُمْ اِلٰٓى اَجَلٍ مُّسَمًّىۚ فَاِذَا جَاۤءَ اَجَلُهُمْ لَا يَسْتَأْخِرُوْنَ سَاعَةً وَّلَا يَسْتَقْدِمُوْنَ', 'walau yu`ākhiżullāhun-nāsa biẓulmihim mā taraka \'alaihā min dābbatiw wa lākiy yu`akhkhiruhum ilā ajalim musammā, fa iżā jā`a ajaluhum lā yasta`khirụna sā\'ataw wa lā yastaqdimụn'),
(1962, 16, 62, 'وَيَجْعَلُوْنَ لِلّٰهِ مَا يَكْرَهُوْنَ وَتَصِفُ اَلْسِنَتُهُمُ الْكَذِبَ اَنَّ لَهُمُ الْحُسْنٰى لَا جَرَمَ اَنَّ لَهُمُ النَّارَ وَاَنَّهُمْ مُّفْرَطُوْنَ', 'wa yaj\'alụna lillāhi mā yakrahụna wa taṣifu alsinatuhumul-każiba anna lahumul-ḥusnā lā jarama anna lahumun-nāra wa annahum mufraṭụn'),
(1963, 16, 63, 'تَاللّٰهِ لَقَدْ اَرْسَلْنَآ اِلٰٓى اُمَمٍ مِّنْ قَبْلِكَ فَزَيَّنَ لَهُمُ الشَّيْطٰنُ اَعْمَالَهُمْ فَهُوَ وَلِيُّهُمُ الْيَوْمَ وَلَهُمْ عَذَابٌ اَلِيْمٌ', 'tallāhi laqad arsalnā ilā umamim ming qablika fa zayyana lahumusy-syaiṭānu a\'mālahum fa huwa waliyyuhumul-yauma wa lahum \'ażābun alīm'),
(1964, 16, 64, 'وَمَآ اَنْزَلْنَا عَلَيْكَ الْكِتٰبَ اِلَّا لِتُبَيِّنَ لَهُمُ الَّذِى اخْتَلَفُوْا فِيْهِۙ وَهُدًى وَّرَحْمَةً لِّقَوْمٍ يُّؤْمِنُوْنَ', 'wa mā anzalnā \'alaikal-kitāba illā litubayyina lahumullażikhtalafụ fīhi wa hudaw wa raḥmatal liqaumiy yu`minụn'),
(1965, 16, 65, 'وَاللّٰهُ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً فَاَحْيَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَاۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّسْمَعُوْنَ', 'wallāhu anzala minas-samā`i mā`an fa aḥyā bihil-arḍa ba\'da mautihā, inna fī żālika la`āyatal liqaumiy yasma\'ụn'),
(1966, 16, 66, 'وَاِنَّ لَكُمْ فِى الْاَنْعَامِ لَعِبْرَةً ۚ نُسْقِيْكُمْ مِّمَّا فِيْ بُطُوْنِهٖ مِنْۢ بَيْنِ فَرْثٍ وَّدَمٍ لَّبَنًا خَالِصًا سَاۤىِٕغًا لِّلشّٰرِبِيْنَ', 'wa inna lakum fil-an\'āmi la\'ibrah, nusqīkum mimmā fī buṭụnihī mim baini farṡiw wa damil labanan khāliṣan sā`igal lisy-syāribīn'),
(1967, 16, 67, 'وَمِنْ ثَمَرٰتِ النَّخِيْلِ وَالْاَعْنَابِ تَتَّخِذُوْنَ مِنْهُ سَكَرًا وَّرِزْقًا حَسَنًاۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّعْقِلُوْنَ', 'wa min ṡamarātin-nakhīli wal-a\'nābi tattakhiżụna min-hu sakaraw wa rizqan ḥasanā, inna fī żālika la`āyatal liqaumiy ya\'qilụn'),
(1968, 16, 68, 'وَاَوْحٰى رَبُّكَ اِلَى النَّحْلِ اَنِ اتَّخِذِيْ مِنَ الْجِبَالِ بُيُوْتًا وَّمِنَ الشَّجَرِ وَمِمَّا يَعْرِشُوْنَۙ', 'wa auḥā rabbuka ilan-naḥli anittakhiżī minal-jibāli buyụtaw wa minasy-syajari wa mimmā ya\'risyụn'),
(1969, 16, 69, 'ثُمَّ كُلِيْ مِنْ كُلِّ الثَّمَرٰتِ فَاسْلُكِيْ سُبُلَ رَبِّكِ ذُلُلًاۗ يَخْرُجُ مِنْ بُطُوْنِهَا شَرَابٌ مُّخْتَلِفٌ اَلْوَانُهٗ ۖفِيْهِ شِفَاۤءٌ لِّلنَّاسِۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّتَفَكَّرُوْنَ', 'ṡumma kulī ming kulliṡ-ṡamarāti faslukī subula rabbiki żululā, yakhruju mim buṭụnihā syarābum mukhtalifun alwānuhụ fīhi syifā`ul lin-nās, inna fī żālika la`āyatal liqaumiy yatafakkarụn'),
(1970, 16, 70, 'وَاللّٰهُ خَلَقَكُمْ ثُمَّ يَتَوَفّٰىكُمْ وَمِنْكُمْ مَّنْ يُّرَدُّ اِلٰٓى اَرْذَلِ الْعُمُرِ لِكَيْ لَا يَعْلَمَ بَعْدَ عِلْمٍ شَيْـًٔاۗ اِنَّ اللّٰهَ عَلِيْمٌ قَدِيْرٌ', 'wallāhu khalaqakum ṡumma yatawaffākum wa mingkum may yuraddu ilā arżalil-\'umuri likai lā ya\'lama ba\'da \'ilmin syai`ā, innallāha \'alīmung qadīr'),
(1971, 16, 71, 'وَاللّٰهُ فَضَّلَ بَعْضَكُمْ عَلٰى بَعْضٍ فِى الرِّزْقِۚ فَمَا الَّذِيْنَ فُضِّلُوْا بِرَاۤدِّيْ رِزْقِهِمْ عَلٰى مَا مَلَكَتْ اَيْمَانُهُمْ فَهُمْ فِيْهِ سَوَاۤءٌۗ اَفَبِنِعْمَةِ اللّٰهِ يَجْحَدُوْنَ', 'wallāhu faḍḍala ba\'ḍakum \'alā ba\'ḍin fir-rizq, fa mallażīna fuḍḍilụ birāddī rizqihim \'alā mā malakat aimānuhum fa hum fīhi sawā`, a fa bini\'matillāhi yaj-ḥadụn'),
(1972, 16, 72, 'وَاللّٰهُ جَعَلَ لَكُمْ مِّنْ اَنْفُسِكُمْ اَزْوَاجًا وَّجَعَلَ لَكُمْ مِّنْ اَزْوَاجِكُمْ بَنِيْنَ وَحَفَدَةً وَّرَزَقَكُمْ مِّنَ الطَّيِّبٰتِۗ اَفَبِالْبَاطِلِ يُؤْمِنُوْنَ وَبِنِعْمَتِ اللّٰهِ هُمْ يَكْفُرُوْنَۙ', 'wallāhu ja\'ala lakum min anfusikum azwājaw wa ja\'ala lakum min azwājikum banīna wa ḥafadataw wa razaqakum minaṭ-ṭayyibāt, a fa bil-bāṭili yu`minụna wa bini\'matillāhi hum yakfurụn'),
(1973, 16, 73, 'وَيَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَا يَمْلِكُ لَهُمْ رِزْقًا مِّنَ السَّمٰوٰتِ وَالْاَرْضِ شَيْـًٔا وَّلَا يَسْتَطِيْعُوْنَ ۚ', 'wa ya\'budụna min dụnillāhi mā lā yamliku lahum rizqam minas-samāwāti wal-arḍi syai`aw wa lā yastaṭī\'ụn'),
(1974, 16, 74, 'فَلَا تَضْرِبُوْا لِلّٰهِ الْاَمْثَالَ ۗاِنَّ اللّٰهَ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُوْنَ', 'fa lā taḍribụ lillāhil-amṡāl, innallāha ya\'lamu wa antum lā ta\'lamụn'),
(1975, 16, 75, '۞ ضَرَبَ اللّٰهُ مَثَلًا عَبْدًا مَّمْلُوْكًا لَّا يَقْدِرُ عَلٰى شَيْءٍ وَّمَنْ رَّزَقْنٰهُ مِنَّا رِزْقًا حَسَنًا فَهُوَ يُنْفِقُ مِنْهُ سِرًّا وَّجَهْرًاۗ هَلْ يَسْتَوٗنَ ۚ اَلْحَمْدُ لِلّٰهِ ۗبَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَ', 'ḍaraballāhu maṡalan \'abdam mamlụkal lā yaqdiru \'alā syai`iw wa mar razaqnāhu minnā rizqan ḥasanan fa huwa yunfiqu min-hu sirraw wa jahrā, hal yastawụn, al-ḥamdu lillāh, bal akṡaruhum lā ya\'lamụn'),
(1976, 16, 76, 'وَضَرَبَ اللّٰهُ مَثَلًا رَّجُلَيْنِ اَحَدُهُمَآ اَبْكَمُ لَا يَقْدِرُ عَلٰى شَيْءٍ وَّهُوَ كَلٌّ عَلٰى مَوْلٰىهُ ۗ اَيْنَمَا يُوَجِّهْهُّ لَا يَأْتِ بِخَيْرٍ ۖهَلْ يَسْتَوِيْ هُوَۙ وَمَنْ يَّأْمُرُ بِالْعَدْلِ وَهُوَ عَلٰى صِرَاطٍ مُّسْتَقِيْمٍ', 'wa ḍaraballāhu maṡalar rajulaini aḥaduhumā abkamu lā yaqdiru \'alā syai`iw wa huwa kallun \'alā maulāh, ainamā yuwajjihhu lā ya`ti bikhairin hal yastawī huwa wa may ya`muru bil-\'adli wa huwa \'alā ṣirāṭim mustaqīm'),
(1977, 16, 77, 'وَلِلّٰهِ غَيْبُ السَّمٰوٰتِ وَالْاَرْضِۗ وَمَآ اَمْرُ السَّاعَةِ اِلَّا كَلَمْحِ الْبَصَرِ اَوْ هُوَ اَقْرَبُۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'wa lillāhi gaibus-samāwāti wal-arḍ, wa mā amrus-sā\'ati illā kalam-ḥil-baṣari au huwa aqrab, innallāha \'alā kulli syai`ing qadīr'),
(1978, 16, 78, 'وَاللّٰهُ اَخْرَجَكُمْ مِّنْۢ بُطُوْنِ اُمَّهٰتِكُمْ لَا تَعْلَمُوْنَ شَيْـًٔاۙ وَّجَعَلَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٕدَةَ ۙ لَعَلَّكُمْ تَشْكُرُوْنَ', 'wallāhu akhrajakum mim buṭụni ummahātikum lā ta\'lamụna syai`aw wa ja\'ala lakumus-sam\'a wal-abṣāra wal-af`idata la\'allakum tasykurụn'),
(1979, 16, 79, 'اَلَمْ يَرَوْا اِلَى الطَّيْرِ مُسَخَّرٰتٍ فِيْ جَوِّ السَّمَاۤءِ ۗمَا يُمْسِكُهُنَّ اِلَّا اللّٰهُ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ', 'a lam yarau ilaṭ-ṭairi musakhkharātin fī jawwis-samā`, mā yumsikuhunna illallāh, inna fī żālika la`āyātil liqaumiy yu`minụn'),
(1980, 16, 80, 'وَاللّٰهُ جَعَلَ لَكُمْ مِّنْۢ بُيُوْتِكُمْ سَكَنًا وَّجَعَلَ لَكُمْ مِّنْ جُلُوْدِ الْاَنْعَامِ بُيُوْتًا تَسْتَخِفُّوْنَهَا يَوْمَ ظَعْنِكُمْ وَيَوْمَ اِقَامَتِكُمْ ۙ وَمِنْ اَصْوَافِهَا وَاَوْبَارِهَا وَاَشْعَارِهَآ اَثَاثًا وَّمَتَاعًا اِلٰى حِيْنٍ', 'wallāhu ja\'ala lakum mim buyụtikum sakanaw wa ja\'ala lakum min julụdil-an\'āmi buyụtan tastakhiffụnahā yauma ẓa\'nikum wa yauma iqāmatikum wa min aṣwāfihā wa aubārihā wa asy\'ārihā aṡāṡaw wa matā\'an ilā ḥīn'),
(1981, 16, 81, 'وَاللّٰهُ جَعَلَ لَكُمْ مِّمَّا خَلَقَ ظِلٰلًا وَّجَعَلَ لَكُمْ مِّنَ الْجِبَالِ اَكْنَانًا وَّجَعَلَ لَكُمْ سَرَابِيْلَ تَقِيْكُمُ الْحَرَّ وَسَرَابِيْلَ تَقِيْكُمْ بَأْسَكُمْ ۚ كَذٰلِكَ يُتِمُّ نِعْمَتَهٗ عَلَيْكُمْ لَعَلَّكُمْ تُسْلِمُوْنَ', 'wallāhu ja\'ala lakum mimmā khalaqa ẓilālaw wa ja\'ala lakum minal-jibāli aknānaw wa ja\'ala lakum sarābīla taqīkumul-ḥarra wa sarābīla taqīkum ba`sakum, każālika yutimmu ni\'matahụ \'alaikum la\'allakum tuslimụn'),
(1982, 16, 82, 'فَاِنْ تَوَلَّوْا فَاِنَّمَا عَلَيْكَ الْبَلٰغُ الْمُبِيْنُ', 'fa in tawallau fa innamā \'alaikal-balāgul-mubīn'),
(1983, 16, 83, 'يَعْرِفُوْنَ نِعْمَتَ اللّٰهِ ثُمَّ يُنْكِرُوْنَهَا وَاَكْثَرُهُمُ الْكٰفِرُوْنَ', 'ya\'rifụna ni\'matallāhi ṡumma yungkirụnahā wa akṡaruhumul-kāfirụn'),
(1984, 16, 84, 'وَيَوْمَ نَبْعَثُ مِنْ كُلِّ اُمَّةٍ شَهِيْدًا ثُمَّ لَا يُؤْذَنُ لِلَّذِيْنَ كَفَرُوْا وَلَا هُمْ يُسْتَعْتَبُوْنَ', 'wa yauma nab\'aṡu ming kulli ummatin syahīdan ṡumma lā yu`żanu lillażīna kafarụ wa lā hum yusta\'tabụn'),
(1985, 16, 85, 'وَاِذَا رَاَ الَّذِيْنَ ظَلَمُوا الْعَذَابَ فَلَا يُخَفَّفُ عَنْهُمْ وَلَا هُمْ يُنْظَرُوْنَ', 'wa iżā ra`allażīna ẓalamul-\'ażāba fa lā yukhaffafu \'an-hum wa lā hum yunẓarụn'),
(1986, 16, 86, 'وَاِذَا رَاَ الَّذِيْنَ اَشْرَكُوْا شُرَكَاۤءَهُمْ قَالُوْا رَبَّنَا هٰٓؤُلَاۤءِ شُرَكَاۤؤُنَا الَّذِيْنَ كُنَّا نَدْعُوْا مِنْ دُوْنِكَۚ فَاَلْقَوْا اِلَيْهِمُ الْقَوْلَ اِنَّكُمْ لَكٰذِبُوْنَۚ', 'wa iżā ra`allażīna asyrakụ syurakā`ahum qālụ rabbanā hā`ulā`i syurakā`unallażīna kunnā nad\'ụ min dụnik, fa alqau ilaihimul-qaula innakum lakāżibụn'),
(1987, 16, 87, 'وَاَلْقَوْا اِلَى اللّٰهِ يَوْمَىِٕذِ ِۨالسَّلَمَ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ', 'wa alqau ilallāhi yauma`iżinis-salama wa ḍalla \'an-hum mā kānụ yaftarụn'),
(1988, 16, 88, 'اَلَّذِيْنَ كَفَرُوْا وَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ زِدْنٰهُمْ عَذَابًا فَوْقَ الْعَذَابِ بِمَا كَانُوْا يُفْسِدُوْنَ', 'allażīna kafarụ wa ṣaddụ \'an sabīlillāhi zidnāhum \'ażāban fauqal-\'ażābi bimā kānụ yufsidụn'),
(1989, 16, 89, 'وَيَوْمَ نَبْعَثُ فِيْ كُلِّ اُمَّةٍ شَهِيْدًا عَلَيْهِمْ مِّنْ اَنْفُسِهِمْ وَجِئْنَا بِكَ شَهِيْدًا عَلٰى هٰٓؤُلَاۤءِۗ وَنَزَّلْنَا عَلَيْكَ الْكِتٰبَ تِبْيَانًا لِّكُلِّ شَيْءٍ وَّهُدًى وَّرَحْمَةً وَّبُشْرٰى لِلْمُسْلِمِيْنَ', 'wa yauma nab\'aṡu fī kulli ummatin syahīdan \'alaihim min anfusihim wa ji`nā bika syahīdan \'alā hā`ulā`, wa nazzalnā \'alaikal-kitāba tibyānal likulli syai`iw wa hudaw wa raḥmataw wa busyrā lil-muslimīn'),
(1990, 16, 90, '۞ اِنَّ اللّٰهَ يَأْمُرُ بِالْعَدْلِ وَالْاِحْسَانِ وَاِيْتَاۤئِ ذِى الْقُرْبٰى وَيَنْهٰى عَنِ الْفَحْشَاۤءِ وَالْمُنْكَرِ وَالْبَغْيِ يَعِظُكُمْ لَعَلَّكُمْ تَذَكَّرُوْنَ', 'innallāha ya`muru bil-\'adli wal-iḥsāni wa ītā`i żil-qurbā wa yan-hā \'anil-faḥsyā`i wal-mungkari wal-bagyi ya\'iẓukum la\'allakum tażakkarụn'),
(1991, 16, 91, 'وَاَوْفُوْا بِعَهْدِ اللّٰهِ اِذَا عَاهَدْتُّمْ وَلَا تَنْقُضُوا الْاَيْمَانَ بَعْدَ تَوْكِيْدِهَا وَقَدْ جَعَلْتُمُ اللّٰهَ عَلَيْكُمْ كَفِيْلًا ۗاِنَّ اللّٰهَ يَعْلَمُ مَا تَفْعَلُوْنَ', 'wa aufụ bi\'ahdillāhi iżā \'āhattum wa lā tangquḍul-aimāna ba\'da taukīdihā wa qad ja\'altumullāha \'alaikum kafīlā, innallāha ya\'lamu mā taf\'alụn'),
(1992, 16, 92, 'وَلَا تَكُوْنُوْا كَالَّتِيْ نَقَضَتْ غَزْلَهَا مِنْۢ بَعْدِ قُوَّةٍ اَنْكَاثًاۗ تَتَّخِذُوْنَ اَيْمَانَكُمْ دَخَلًا ۢ بَيْنَكُمْ اَنْ تَكُوْنَ اُمَّةٌ هِيَ اَرْبٰى مِنْ اُمَّةٍ ۗاِنَّمَا يَبْلُوْكُمُ اللّٰهُ بِهٖۗ وَلَيُبَيِّنَنَّ لَكُمْ يَوْمَ الْقِيٰمَةِ مَا كُنْتُمْ فِيْهِ تَخْتَلِفُوْنَ', 'wa lā takụnụ kallatī naqaḍat gazlahā mim ba\'di quwwatin angkāṡā, tattakhiżụna aimānakum dakhalam bainakum an takụna ummatun hiya arbā min ummah, innamā yablụkumullāhu bih, wa layubayyinanna lakum yaumal-qiyāmati mā kuntum fīhi takhtalifụn'),
(1993, 16, 93, 'وَلَوْ شَاۤءَ اللّٰهُ لَجَعَلَكُمْ اُمَّةً وَّاحِدَةً وَّلٰكِنْ يُّضِلُّ مَنْ يَّشَاۤءُ وَيَهْدِيْ مَنْ يَّشَاۤءُۗ وَلَتُسْـَٔلُنَّ عَمَّا كُنْتُمْ تَعْمَلُوْنَ', 'walau syā`allāhu laja\'alakum ummataw wāḥidataw wa lākiy yuḍillu may yasyā`u wa yahdī may yasyā`, wa latus`alunna \'ammā kuntum ta\'malụn'),
(1994, 16, 94, 'وَلَا تَتَّخِذُوْٓا اَيْمَانَكُمْ دَخَلًا ۢ بَيْنَكُمْ فَتَزِلَّ قَدَمٌۢ بَعْدَ ثُبُوْتِهَا وَتَذُوْقُوا السُّوْۤءَ بِمَا صَدَدْتُّمْ عَنْ سَبِيْلِ اللّٰهِ ۚوَلَكُمْ عَذَابٌ عَظِيْمٌ', 'wa lā tattakhiżū aimānakum dakhalam bainakum fa tazilla qadamum ba\'da ṡubụtihā wa tażụqus-sū`a bimā ṣadattum \'an sabīlillāh, wa lakum \'ażābun \'aẓīm'),
(1995, 16, 95, 'وَلَا تَشْتَرُوْا بِعَهْدِ اللّٰهِ ثَمَنًا قَلِيْلًاۗ اِنَّمَا عِنْدَ اللّٰهِ هُوَ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ', 'wa lā tasytarụ bi\'ahdillāhi ṡamanang qalīlā, innamā \'indallāhi huwa khairul lakum ing kuntum ta\'lamụn'),
(1996, 16, 96, 'مَا عِنْدَكُمْ يَنْفَدُ وَمَا عِنْدَ اللّٰهِ بَاقٍۗ وَلَنَجْزِيَنَّ الَّذِيْنَ صَبَرُوْٓا اَجْرَهُمْ بِاَحْسَنِ مَا كَانُوْا يَعْمَلُوْنَ', 'mā \'indakum yanfadu wa mā \'indallāhi bāq, wa lanajziyannallażīna ṣabarū ajrahum bi`aḥsani mā kānụ ya\'malụn'),
(1997, 16, 97, 'مَنْ عَمِلَ صَالِحًا مِّنْ ذَكَرٍ اَوْ اُنْثٰى وَهُوَ مُؤْمِنٌ فَلَنُحْيِيَنَّهٗ حَيٰوةً طَيِّبَةًۚ وَلَنَجْزِيَنَّهُمْ اَجْرَهُمْ بِاَحْسَنِ مَا كَانُوْا يَعْمَلُوْنَ', 'man \'amila ṣāliḥam min żakarin au unṡā wa huwa mu`minun fa lanuḥyiyannahụ ḥayātan ṭayyibah, wa lanajziyannahum ajrahum bi`aḥsani mā kānụ ya\'malụn'),
(1998, 16, 98, 'فَاِذَا قَرَأْتَ الْقُرْاٰنَ فَاسْتَعِذْ بِاللّٰهِ مِنَ الشَّيْطٰنِ الرَّجِيْمِ', 'fa iżā qara`tal-qur`āna fasta\'iż billāhi minasy-syaiṭānir-rajīm'),
(1999, 16, 99, 'اِنَّهٗ لَيْسَ لَهٗ سُلْطٰنٌ عَلَى الَّذِيْنَ اٰمَنُوْا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُوْنَ', 'innahụ laisa lahụ sulṭānun \'alallażīna āmanụ wa \'alā rabbihim yatawakkalụn'),
(2000, 16, 100, 'اِنَّمَا سُلْطٰنُهٗ عَلَى الَّذِيْنَ يَتَوَلَّوْنَهٗ وَالَّذِيْنَ هُمْ بِهٖ مُشْرِكُوْنَ', 'innamā sulṭānuhụ \'alallażīna yatawallaunahụ wallażīna hum bihī musyrikụn'),
(2001, 16, 101, 'وَاِذَا بَدَّلْنَآ اٰيَةً مَّكَانَ اٰيَةٍ ۙوَّاللّٰهُ اَعْلَمُ بِمَا يُنَزِّلُ قَالُوْٓا اِنَّمَآ اَنْتَ مُفْتَرٍۗ بَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَ', 'wa iżā baddalnā āyatam makāna āyatiw wallāhu a\'lamu bimā yunazzilu qālū innamā anta muftar, bal akṡaruhum lā ya\'lamụn'),
(2002, 16, 102, 'قُلْ نَزَّلَهٗ رُوْحُ الْقُدُسِ مِنْ رَّبِّكَ بِالْحَقِّ لِيُثَبِّتَ الَّذِيْنَ اٰمَنُوْا وَهُدًى وَّبُشْرٰى لِلْمُسْلِمِيْنَ', 'qul nazzalahụ rụḥul-qudusi mir rabbika bil-ḥaqqi liyuṡabbitallażīna āmanụ wa hudaw wa busyrā lil-muslimīn'),
(2003, 16, 103, 'وَلَقَدْ نَعْلَمُ اَنَّهُمْ يَقُوْلُوْنَ اِنَّمَا يُعَلِّمُهٗ بَشَرٌۗ لِسَانُ الَّذِيْ يُلْحِدُوْنَ اِلَيْهِ اَعْجَمِيٌّ وَّهٰذَا لِسَانٌ عَرَبِيٌّ مُّبِيْنٌ', 'wa laqad na\'lamu annahum yaqụlụna innamā yu\'allimuhụ basyar, lisānullażī yul-ḥidụna ilaihi a\'jamiyyuw wa hāżā lisānun \'arabiyyum mubīn'),
(2004, 16, 104, 'اِنَّ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِاٰيٰتِ اللّٰهِۙ لَا يَهْدِيْهِمُ اللّٰهُ وَلَهُمْ عَذَابٌ اَلِيْمٌ', 'innallażīna lā yu`minụna bi`āyātillāhi lā yahdīhimullāhu wa lahum \'ażābun alīm'),
(2005, 16, 105, 'اِنَّمَا يَفْتَرِى الْكَذِبَ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِاٰيٰتِ اللّٰهِۚ وَاُولٰۤىِٕكَ هُمُ الْكٰذِبُوْنَ', 'innamā yaftaril-każiballażīna lā yu`minụna bi`āyātillāh, wa ulā`ika humul-kāżibụn'),
(2006, 16, 106, 'مَنْ كَفَرَ بِاللّٰهِ مِنْۢ بَعْدِ اِيْمَانِهٖٓ اِلَّا مَنْ اُكْرِهَ وَقَلْبُهٗ مُطْمَىِٕنٌّۢ بِالْاِيْمَانِ وَلٰكِنْ مَّنْ شَرَحَ بِالْكُفْرِ صَدْرًا فَعَلَيْهِمْ غَضَبٌ مِّنَ اللّٰهِ ۗوَلَهُمْ عَذَابٌ عَظِيْمٌ', 'mang kafara billāhi mim ba\'di īmānihī illā man ukriha wa qalbuhụ muṭma`innum bil-īmāni wa lākim man syaraḥa bil-kufri ṣadran fa \'alaihim gaḍabum minallāh, wa lahum \'ażābun \'aẓīm'),
(2007, 16, 107, 'ذٰلِكَ بِاَنَّهُمُ اسْتَحَبُّوا الْحَيٰوةَ الدُّنْيَا عَلَى الْاٰخِرَةِۙ وَاَنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الْكٰفِرِيْنَ', 'żālika bi`annahumustaḥabbul-ḥayātad-dun-yā \'alal-ākhirati wa annallāha lā yahdil-qaumal-kāfirīn'),
(2008, 16, 108, 'اُولٰۤىِٕكَ الَّذِيْنَ طَبَعَ اللّٰهُ عَلٰى قُلُوْبِهِمْ وَسَمْعِهِمْ وَاَبْصَارِهِمْۗ وَاُولٰۤىِٕكَ هُمُ الْغٰفِلُوْنَ', 'ulā`ikallażīna ṭaba\'allāhu \'alā qulụbihim wa sam\'ihim wa abṣārihim, wa ulā`ika humul-gāfilụn'),
(2009, 16, 109, 'لَا جَرَمَ اَنَّهُمْ فِى الْاٰخِرَةِ هُمُ الْخٰسِرُوْنَ', 'lā jarama annahum fil-ākhirati humul-khāsirụn'),
(2010, 16, 110, 'ثُمَّ اِنَّ رَبَّكَ لِلَّذِيْنَ هَاجَرُوْا مِنْۢ بَعْدِ مَا فُتِنُوْا ثُمَّ جَاهَدُوْا وَصَبَرُوْاۚ اِنَّ رَبَّكَ مِنْۢ بَعْدِهَا لَغَفُوْرٌ رَّحِيْمٌ', 'ṡumma inna rabbaka lillażīna hājarụ mim ba\'di mā futinụ ṡumma jāhadụ wa ṣabarụ, inna rabbaka mim ba\'dihā lagafụrur raḥīm'),
(2011, 16, 111, '۞ يَوْمَ تَأْتِيْ كُلُّ نَفْسٍ تُجَادِلُ عَنْ نَّفْسِهَا وَتُوَفّٰى كُلُّ نَفْسٍ مَّا عَمِلَتْ وَهُمْ لَا يُظْلَمُوْنَ', 'yauma ta`tī kullu nafsin tujādilu \'an nafsihā wa tuwaffā kullu nafsim mā \'amilat wa hum lā yuẓlamụn'),
(2012, 16, 112, 'وَضَرَبَ اللّٰهُ مَثَلًا قَرْيَةً كَانَتْ اٰمِنَةً مُّطْمَىِٕنَّةً يَّأْتِيْهَا رِزْقُهَا رَغَدًا مِّنْ كُلِّ مَكَانٍ فَكَفَرَتْ بِاَنْعُمِ اللّٰهِ فَاَذَاقَهَا اللّٰهُ لِبَاسَ الْجُوْعِ وَالْخَوْفِ بِمَا كَانُوْا يَصْنَعُوْنَ', 'wa ḍaraballāhu maṡalang qaryatang kānat āminatam muṭma`innatay ya`tīhā rizquhā ragadam ming kulli makānin fa kafarat bi`an\'umillāhi fa ażāqahallāhu libāsal-jụ\'i wal-khaufi bimā kānụ yaṣna\'ụn'),
(2013, 16, 113, 'وَلَقَدْ جَاۤءَهُمْ رَسُوْلٌ مِّنْهُمْ فَكَذَّبُوْهُ فَاَخَذَهُمُ الْعَذَابُ وَهُمْ ظٰلِمُوْنَ', 'wa laqad jā`ahum rasụlum min-hum fa każżabụhu fa akhażahumul-\'ażābu wa hum ẓālimụn'),
(2014, 16, 114, 'فَكُلُوْا مِمَّا رَزَقَكُمُ اللّٰهُ حَلٰلًا طَيِّبًاۖ وَّاشْكُرُوْا نِعْمَتَ اللّٰهِ اِنْ كُنْتُمْ اِيَّاهُ تَعْبُدُوْنَ', 'fa kulụ mimmā razaqakumullāhu ḥalālan ṭayyibaw wasykurụ ni\'matallāhi ing kuntum iyyāhu ta\'budụn'),
(2015, 16, 115, 'اِنَّمَا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَالدَّمَ وَلَحْمَ الْخِنْزِيْرِ وَمَآ اُهِلَّ لِغَيْرِ اللّٰهِ بِهٖۚ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَّلَا عَادٍ فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'innamā ḥarrama \'alaikumul-maitata wad-dama wa laḥmal-khinzīri wa mā uḥilla ligairillāhi bih, fa maniḍṭurra gaira bāgiw wa lā \'ādin fa innallāha gafụrur raḥīm'),
(2016, 16, 116, 'وَلَا تَقُوْلُوْا لِمَا تَصِفُ اَلْسِنَتُكُمُ الْكَذِبَ هٰذَا حَلٰلٌ وَّهٰذَا حَرَامٌ لِّتَفْتَرُوْا عَلَى اللّٰهِ الْكَذِبَۗ اِنَّ الَّذِيْنَ يَفْتَرُوْنَ عَلَى اللّٰهِ الْكَذِبَ لَا يُفْلِحُوْنَۗ', 'wa lā taqụlụ limā taṣifu alsinatukumul-każiba hāżā ḥalāluw wa hāżā ḥarāmul litaftarụ \'alallāhil-każib, innallażīna yaftarụna \'alallāhil-każiba lā yufliḥụn'),
(2017, 16, 117, 'مَتَاعٌ قَلِيْلٌ ۖوَّلَهُمْ عَذَابٌ اَلِيْمٌ', 'matā\'ung qalīluw wa lahum \'ażābun alīm'),
(2018, 16, 118, 'وَعَلَى الَّذِيْنَ هَادُوْا حَرَّمْنَا مَا قَصَصْنَا عَلَيْكَ مِنْ قَبْلُ ۗوَمَا ظَلَمْنٰهُمْ وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَ', 'wa \'alallażīna hādụ ḥarramnā mā qaṣaṣnā \'alaika ming qabl, wa mā ẓalamnāhum wa lāking kānū anfusahum yaẓlimụn'),
(2019, 16, 119, 'ثُمَّ اِنَّ رَبَّكَ لِلَّذِيْنَ عَمِلُوا السُّوْۤءَ بِجَهَالَةٍ ثُمَّ تَابُوْا مِنْۢ بَعْدِ ذٰلِكَ وَاَصْلَحُوْٓا اِنَّ رَبَّكَ مِنْۢ بَعْدِهَا لَغَفُوْرٌ رَّحِيْمٌ', 'ṡumma inna rabbaka lillażīna \'amilus-sū`a bijahālatin ṡumma tābụ mim ba\'di żālika wa aṣlaḥū inna rabbaka mim ba\'dihā lagafụrur raḥīm'),
(2020, 16, 120, 'اِنَّ اِبْرٰهِيْمَ كَانَ اُمَّةً قَانِتًا لِّلّٰهِ حَنِيْفًاۗ وَلَمْ يَكُ مِنَ الْمُشْرِكِيْنَۙ', 'inna ibrāhīma kāna ummatang qānital lillāhi ḥanīfā, wa lam yaku minal-musyrikīn'),
(2021, 16, 121, 'شَاكِرًا لِّاَنْعُمِهِ ۖاجْتَبٰىهُ وَهَدٰىهُ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ', 'syākiral li`an\'umihijtabāhu wa hadāhu ilā ṣirāṭim mustaqīm'),
(2022, 16, 122, 'وَاٰتَيْنٰهُ فِى الدُّنْيَا حَسَنَةً ۗوَاِنَّهٗ فِى الْاٰخِرَةِ لَمِنَ الصّٰلِحِيْنَ ۗ', 'wa ātaināhu fid-dun-yā ḥasanah, wa innahụ fil-ākhirati laminaṣ-ṣāliḥīn'),
(2023, 16, 123, 'ثُمَّ اَوْحَيْنَآ اِلَيْكَ اَنِ اتَّبِعْ مِلَّةَ اِبْرٰهِيْمَ حَنِيْفًا ۗوَمَا كَانَ مِنَ الْمُشْرِكِيْنَ', 'ṡumma auḥainā ilaika anittabi\' millata ibrāhīma ḥanīfā, wa mā kāna minal-musyrikīn'),
(2024, 16, 124, 'اِنَّمَا جُعِلَ السَّبْتُ عَلَى الَّذِيْنَ اخْتَلَفُوْا فِيْهِۗ وَاِنَّ رَبَّكَ لَيَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كَانُوْا فِيْهِ يَخْتَلِفُوْنَ', 'innamā ju\'ilas-sabtu \'alallażīnakhtalafụ fīh, wa inna rabbaka layaḥkumu bainahum yaumal-qiyāmati fīmā kānụ fīhi yakhtalifụn'),
(2025, 16, 125, 'اُدْعُ اِلٰى سَبِيْلِ رَبِّكَ بِالْحِكْمَةِ وَالْمَوْعِظَةِ الْحَسَنَةِ وَجَادِلْهُمْ بِالَّتِيْ هِيَ اَحْسَنُۗ اِنَّ رَبَّكَ هُوَ اَعْلَمُ بِمَنْ ضَلَّ عَنْ سَبِيْلِهٖ وَهُوَ اَعْلَمُ بِالْمُهْتَدِيْنَ', 'ud\'u ilā sabīli rabbika bil-ḥikmati wal-mau\'iẓatil-ḥasanati wa jādil-hum billatī hiya aḥsan, inna rabbaka huwa a\'lamu biman ḍalla \'an sabīlihī wa huwa a\'lamu bil-muhtadīn');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(2026, 16, 126, 'وَاِنْ عَاقَبْتُمْ فَعَاقِبُوْا بِمِثْلِ مَا عُوْقِبْتُمْ بِهٖۗ وَلَىِٕنْ صَبَرْتُمْ لَهُوَ خَيْرٌ لِّلصّٰبِرِيْنَ', 'wa in \'āqabtum fa \'āqibụ bimiṡli mā \'ụqibtum bih, wa la`in ṣabartum lahuwa khairul liṣ-ṣābirīn'),
(2027, 16, 127, 'وَاصْبِرْ وَمَا صَبْرُكَ اِلَّا بِاللّٰهِ وَلَا تَحْزَنْ عَلَيْهِمْ وَلَا تَكُ فِيْ ضَيْقٍ مِّمَّا يَمْكُرُوْنَ', 'waṣbir wa mā ṣabruka illā billāhi wa lā taḥzan \'alaihim wa lā taku fī ḍaiqim mimmā yamkurụn'),
(2028, 16, 128, 'اِنَّ اللّٰهَ مَعَ الَّذِيْنَ اتَّقَوْا وَّالَّذِيْنَ هُمْ مُّحْسِنُوْنَ ۔', 'innallāha ma\'allażīnattaqaw wallażīna hum muḥsinụn'),
(2029, 17, 1, 'سُبْحٰنَ الَّذِيْٓ اَسْرٰى بِعَبْدِهٖ لَيْلًا مِّنَ الْمَسْجِدِ الْحَرَامِ اِلَى الْمَسْجِدِ الْاَقْصَا الَّذِيْ بٰرَكْنَا حَوْلَهٗ لِنُرِيَهٗ مِنْ اٰيٰتِنَاۗ اِنَّهٗ هُوَ السَّمِيْعُ الْبَصِيْرُ', 'sub-ḥānallażī asrā bi\'abdihī lailam minal-masjidil-ḥarāmi ilal-masjidil-aqṣallażī bāraknā ḥaulahụ linuriyahụ min āyātinā, innahụ huwas-samī\'ul-baṣīr'),
(2030, 17, 2, 'وَاٰتَيْنَا مُوْسَى الْكِتٰبَ وَجَعَلْنٰهُ هُدًى لِّبَنِيْٓ اِسْرَاۤءِيْلَ اَلَّا تَتَّخِذُوْا مِنْ دُوْنِيْ وَكِيْلًاۗ', 'wa ātainā mụsal-kitāba wa ja\'alnāhu hudal libanī isrā`īla allā tattakhiżụ min dụnī wakīlā'),
(2031, 17, 3, 'ذُرِّيَّةَ مَنْ حَمَلْنَا مَعَ نُوْحٍۗ اِنَّهٗ كَانَ عَبْدًا شَكُوْرًا', 'żurriyyata man ḥamalnā ma\'a nụḥ, innahụ kāna \'abdan syakụrā'),
(2032, 17, 4, 'وَقَضَيْنَآ اِلٰى بَنِيْٓ اِسْرَاۤءِيْلَ فِى الْكِتٰبِ لَتُفْسِدُنَّ فِى الْاَرْضِ مَرَّتَيْنِ وَلَتَعْلُنَّ عُلُوًّا كَبِيْرًا', 'wa qaḍainā ilā banī isrā`īla fil-kitābi latufsidunna fil-arḍi marrataini wa lata\'lunna \'uluwwang kabīrā'),
(2033, 17, 5, 'فَاِذَا جَاۤءَ وَعْدُ اُوْلٰىهُمَا بَعَثْنَا عَلَيْكُمْ عِبَادًا لَّنَآ اُولِيْ بَأْسٍ شَدِيْدٍ فَجَاسُوْا خِلٰلَ الدِّيَارِۗ وَكَانَ وَعْدًا مَّفْعُوْلًا', 'fa iżā jā`a wa\'du ụlāhumā ba\'aṡnā \'alaikum \'ibādal lanā ulī ba`sin syadīdin fa jāsụ khilālad-diyār, wa kāna wa\'dam maf\'ụlā'),
(2034, 17, 6, 'ثُمَّ رَدَدْنَا لَكُمُ الْكَرَّةَ عَلَيْهِمْ وَاَمْدَدْنٰكُمْ بِاَمْوَالٍ وَّبَنِيْنَ وَجَعَلْنٰكُمْ اَكْثَرَ نَفِيْرًا', 'ṡumma radadnā lakumul-karrata \'alaihim wa amdadnākum bi`amwāliw wa banīna wa ja\'alnākum akṡara nafīrā'),
(2035, 17, 7, 'اِنْ اَحْسَنْتُمْ اَحْسَنْتُمْ لِاَنْفُسِكُمْ ۗوَاِنْ اَسَأْتُمْ فَلَهَاۗ فَاِذَا جَاۤءَ وَعْدُ الْاٰخِرَةِ لِيَسٗۤـُٔوْا وُجُوْهَكُمْ وَلِيَدْخُلُوا الْمَسْجِدَ كَمَا دَخَلُوْهُ اَوَّلَ مَرَّةٍ وَّلِيُتَبِّرُوْا مَا عَلَوْا تَتْبِيْرًا', 'in aḥsantum aḥsantum li`anfusikum, wa in asa`tum fa lahā, fa iżā jā`a wa\'dul-ākhirati liyasū`ụ wujụhakum wa liyadkhulul-masjida kamā dakhalụhu awwala marratiw wa liyutabbirụ mā \'alau tatbīrā'),
(2036, 17, 8, 'عَسٰى رَبُّكُمْ اَنْ يَّرْحَمَكُمْۚ وَاِنْ عُدْتُّمْ عُدْنَاۘ وَجَعَلْنَا جَهَنَّمَ لِلْكٰفِرِيْنَ حَصِيْرًا', '\'asā rabbukum ay yar-ḥamakum, wa in \'uttum \'udnā, wa ja\'alnā jahannama lil-kāfirīna ḥaṣīrā'),
(2037, 17, 9, 'اِنَّ هٰذَا الْقُرْاٰنَ يَهْدِيْ لِلَّتِيْ هِيَ اَقْوَمُ وَيُبَشِّرُ الْمُؤْمِنِيْنَ الَّذِيْنَ يَعْمَلُوْنَ الصّٰلِحٰتِ اَنَّ لَهُمْ اَجْرًا كَبِيْرًاۙ', 'inna hāżal-qur`āna yahdī lillatī hiya aqwamu wa yubasysyirul-mu`minīnallażīna ya\'malụnaṣ-ṣāliḥāti anna lahum ajrang kabīrā'),
(2038, 17, 10, 'وَّاَنَّ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ اَعْتَدْنَا لَهُمْ عَذَابًا اَلِيْمًا', 'wa annallażīna lā yu`minụna bil-ākhirati a\'tadnā lahum \'ażāban alīmā'),
(2039, 17, 11, 'وَيَدْعُ الْاِنْسَانُ بِالشَّرِّ دُعَاۤءَهٗ بِالْخَيْرِۗ وَكَانَ الْاِنْسَانُ عَجُوْلًا', 'wa yad\'ul-insānu bisy-syarri du\'ā`ahụ bil-khaīr, wa kānal-insānu \'ajụlā'),
(2040, 17, 12, 'وَجَعَلْنَا الَّيْلَ وَالنَّهَارَ اٰيَتَيْنِ فَمَحَوْنَآ اٰيَةَ الَّيْلِ وَجَعَلْنَآ اٰيَةَ النَّهَارِ مُبْصِرَةً لِّتَبْتَغُوْا فَضْلًا مِّنْ رَّبِّكُمْ وَلِتَعْلَمُوْا عَدَدَ السِّنِيْنَ وَالْحِسَابَۗ وَكُلَّ شَيْءٍ فَصَّلْنٰهُ تَفْصِيْلًا', 'wa ja\'alnal-laila wan-nahāra āyataini fa maḥaunā āyatal-laili wa ja\'alnā āyatan-nahāri mubṣiratal litabtagụ faḍlam mir rabbikum wa lita\'lamụ \'adadas-sinīna wal-ḥisāb, wa kulla syai`in faṣṣalnāhu tafṣīlā'),
(2041, 17, 13, 'وَكُلَّ اِنْسَانٍ اَلْزَمْنٰهُ طٰۤىِٕرَهٗ فِيْ عُنُقِهٖۗ وَنُخْرِجُ لَهٗ يَوْمَ الْقِيٰمَةِ كِتٰبًا يَّلْقٰىهُ مَنْشُوْرًا', 'wa kulla insānin alzamnāhu ṭā`irahụ fī \'unuqih, wa nukhriju lahụ yaumal-qiyāmati kitābay yalqāhu mansyụrā'),
(2042, 17, 14, 'اِقْرَأْ كِتَابَكَۗ كَفٰى بِنَفْسِكَ الْيَوْمَ عَلَيْكَ حَسِيْبًاۗ', 'iqra` kitābak, kafā binafsikal-yauma \'alaika ḥasībā'),
(2043, 17, 15, 'مَنِ اهْتَدٰى فَاِنَّمَا يَهْتَدِيْ لِنَفْسِهٖۚ وَمَنْ ضَلَّ فَاِنَّمَا يَضِلُّ عَلَيْهَاۗ وَلَا تَزِرُ وَازِرَةٌ وِّزْرَ اُخْرٰىۗ وَمَا كُنَّا مُعَذِّبِيْنَ حَتّٰى نَبْعَثَ رَسُوْلًا', 'manihtadā fa innamā yahtadī linafsih, wa man ḍalla fa innamā yaḍillu \'alaihā, wa lā taziru wāziratuw wizra ukhrā, wa mā kunnā mu\'ażżibīna ḥattā nab\'aṡa rasụlā'),
(2044, 17, 16, 'وَاِذَآ اَرَدْنَآ اَنْ نُّهْلِكَ قَرْيَةً اَمَرْنَا مُتْرَفِيْهَا فَفَسَقُوْا فِيْهَا فَحَقَّ عَلَيْهَا الْقَوْلُ فَدَمَّرْنٰهَا تَدْمِيْرًا', 'wa iżā aradnā an nuhlika qaryatan amarnā mutrafīhā fa fasaqụ fīhā fa ḥaqqa \'alaihal-qaulu fa dammarnāhā tadmīrā'),
(2045, 17, 17, 'وَكَمْ اَهْلَكْنَا مِنَ الْقُرُوْنِ مِنْۢ بَعْدِ نُوْحٍۗ وَكَفٰى بِرَبِّكَ بِذُنُوْبِ عِبَادِهٖ خَبِيْرًاۢ بَصِيْرًا', 'wa kam ahlaknā minal-qurụni mim ba\'di nụḥ, wa kafā birabbika biżunụbi \'ibādihī khabīram baṣīrā'),
(2046, 17, 18, 'مَنْ كَانَ يُرِيْدُ الْعَاجِلَةَ عَجَّلْنَا لَهٗ فِيْهَا مَا نَشَاۤءُ لِمَنْ نُّرِيْدُ ثُمَّ جَعَلْنَا لَهٗ جَهَنَّمَۚ يَصْلٰىهَا مَذْمُوْمًا مَّدْحُوْرًا', 'mang kāna yurīdul-\'ājilata \'ajjalnā lahụ fīhā mā nasyā`u liman nurīdu ṡumma ja\'alnā lahụ jahannam, yaṣlāhā mażmụmam mad-ḥụrā'),
(2047, 17, 19, 'وَمَنْ اَرَادَ الْاٰخِرَةَ وَسَعٰى لَهَا سَعْيَهَا وَهُوَ مُؤْمِنٌ فَاُولٰۤىِٕكَ كَانَ سَعْيُهُمْ مَّشْكُوْرًا', 'wa man arādal-ākhirata wa sa\'ā lahā sa\'yahā wa huwa mu`minun fa ulā`ika kāna sa\'yuhum masykụrā'),
(2048, 17, 20, 'كُلًّا نُّمِدُّ هٰٓؤُلَاۤءِ وَهٰٓؤُلَاۤءِ مِنْ عَطَاۤءِ رَبِّكَ ۗوَمَا كَانَ عَطَاۤءُ رَبِّكَ مَحْظُوْرًا', 'kullan numiddu hā`ulā`i wa hā`ulā`i min \'aṭā`i rabbik, wa mā kāna \'aṭā`u rabbika maḥẓụrā'),
(2049, 17, 21, 'اُنْظُرْ كَيْفَ فَضَّلْنَا بَعْضَهُمْ عَلٰى بَعْضٍۗ وَلَلْاٰخِرَةُ اَكْبَرُ دَرَجٰتٍ وَّاَكْبَرُ تَفْضِيْلًا', 'unẓur kaifa faḍḍalnā ba\'ḍahum \'alā ba\'ḍ, wa lal-ākhiratu akbaru darajātiw wa akbaru tafḍīlā'),
(2050, 17, 22, 'لَا تَجْعَلْ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ فَتَقْعُدَ مَذْمُوْمًا مَّخْذُوْلًا', 'lā taj\'al ma\'allāhi ilāhan ākhara fa taq\'uda mażmụmam makhżụlā'),
(2051, 17, 23, '۞ وَقَضٰى رَبُّكَ اَلَّا تَعْبُدُوْٓا اِلَّآ اِيَّاهُ وَبِالْوَالِدَيْنِ اِحْسٰنًاۗ اِمَّا يَبْلُغَنَّ عِنْدَكَ الْكِبَرَ اَحَدُهُمَآ اَوْ كِلٰهُمَا فَلَا تَقُلْ لَّهُمَآ اُفٍّ وَّلَا تَنْهَرْهُمَا وَقُلْ لَّهُمَا قَوْلًا كَرِيْمًا', 'wa qaḍā rabbuka allā ta\'budū illā iyyāhu wa bil-wālidaini iḥsānā, immā yabluganna \'indakal-kibara aḥaduhumā au kilāhumā fa lā taqul lahumā uffiw wa lā tan-har-humā wa qul lahumā qaulang karīmā'),
(2052, 17, 24, 'وَاخْفِضْ لَهُمَا جَنَاحَ الذُّلِّ مِنَ الرَّحْمَةِ وَقُلْ رَّبِّ ارْحَمْهُمَا كَمَا رَبَّيٰنِيْ صَغِيْرًاۗ', 'wakhfiḍ lahumā janāḥaż-żulli minar-raḥmati wa qur rabbir-ḥam-humā kamā rabbayānī ṣagīrā'),
(2053, 17, 25, 'رَبُّكُمْ اَعْلَمُ بِمَا فِيْ نُفُوْسِكُمْ ۗاِنْ تَكُوْنُوْا صٰلِحِيْنَ فَاِنَّهٗ كَانَ لِلْاَوَّابِيْنَ غَفُوْرًا', 'rabbukum a\'lamu bimā fī nufụsikum, in takụnụ ṣāliḥīna fa innahụ kāna lil-awwābīna gafụrā'),
(2054, 17, 26, 'وَاٰتِ ذَا الْقُرْبٰى حَقَّهٗ وَالْمِسْكِيْنَ وَابْنَ السَّبِيْلِ وَلَا تُبَذِّرْ تَبْذِيْرًا', 'wa āti żal-qurbā ḥaqqahụ wal-miskīna wabnas-sabīli wa lā tubażżir tabżīrā'),
(2055, 17, 27, 'اِنَّ الْمُبَذِّرِيْنَ كَانُوْٓا اِخْوَانَ الشَّيٰطِيْنِ ۗوَكَانَ الشَّيْطٰنُ لِرَبِّهٖ كَفُوْرًا', 'innal-mubażżirīna kānū ikhwānasy-syayāṭīn, wa kānasy-syaiṭānu lirabbihī kafụrā'),
(2056, 17, 28, 'وَاِمَّا تُعْرِضَنَّ عَنْهُمُ ابْتِغَاۤءَ رَحْمَةٍ مِّنْ رَّبِّكَ تَرْجُوْهَا فَقُلْ لَّهُمْ قَوْلًا مَّيْسُوْرًا', 'wa immā tu\'riḍanna \'an-humubtigā`a raḥmatim mir rabbika tarjụhā fa qul lahum qaulam maisụrā'),
(2057, 17, 29, 'وَلَا تَجْعَلْ يَدَكَ مَغْلُوْلَةً اِلٰى عُنُقِكَ وَلَا تَبْسُطْهَا كُلَّ الْبَسْطِ فَتَقْعُدَ مَلُوْمًا مَّحْسُوْرًا', 'wa lā taj\'al yadaka maglụlatan ilā \'unuqika wa lā tabsuṭ-hā kullal-basṭi fa taq\'uda malụmam maḥsụrā'),
(2058, 17, 30, 'اِنَّ رَبَّكَ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُ ۗاِنَّهٗ كَانَ بِعِبَادِهٖ خَبِيْرًاۢ بَصِيْرًا', 'inna rabbaka yabsuṭur-rizqa limay yasyā`u wa yaqdir, innahụ kāna bi\'ibādihī khabīram baṣīrā'),
(2059, 17, 31, 'وَلَا تَقْتُلُوْٓا اَوْلَادَكُمْ خَشْيَةَ اِمْلَاقٍۗ نَحْنُ نَرْزُقُهُمْ وَاِيَّاكُمْۗ اِنَّ قَتْلَهُمْ كَانَ خِطْـًٔا كَبِيْرًا', 'wa lā taqtulū aulādakum khasy-yata imlāq, naḥnu narzuquhum wa iyyākum, inna qatlahum kāna khiṭ`ang kabīrā'),
(2060, 17, 32, 'وَلَا تَقْرَبُوا الزِّنٰىٓ اِنَّهٗ كَانَ فَاحِشَةً ۗوَسَاۤءَ سَبِيْلًا', 'wa lā taqrabuz-zinā innahụ kāna fāḥisyah, wa sā`a sabīlā'),
(2061, 17, 33, 'وَلَا تَقْتُلُوا النَّفْسَ الَّتِيْ حَرَّمَ اللّٰهُ اِلَّا بِالْحَقِّۗ وَمَنْ قُتِلَ مَظْلُوْمًا فَقَدْ جَعَلْنَا لِوَلِيِّهٖ سُلْطٰنًا فَلَا يُسْرِفْ فِّى الْقَتْلِۗ اِنَّهٗ كَانَ مَنْصُوْرًا', 'wa lā taqtulun-nafsallatī ḥarramallāhu illā bil-ḥaqq, wa mang qutila maẓlụman fa qad ja\'alnā liwaliyyihī sulṭānan fa lā yusrif fil-qatl, innahụ kāna manṣụrā'),
(2062, 17, 34, 'وَلَا تَقْرَبُوْا مَالَ الْيَتِيْمِ اِلَّا بِالَّتِيْ هِيَ اَحْسَنُ حَتّٰى يَبْلُغَ اَشُدَّهٗۖ وَاَوْفُوْا بِالْعَهْدِۖ اِنَّ الْعَهْدَ كَانَ مَسْـُٔوْلًا', 'wa lā taqrabụ mālal-yatīmi illā billatī hiya aḥsanu ḥattā yabluga asyuddahụ wa aufụ bil-\'ahdi innal-\'ahda kāna mas`ụlā'),
(2063, 17, 35, 'وَاَوْفُوا الْكَيْلَ اِذَا كِلْتُمْ وَزِنُوْا بِالْقِسْطَاسِ الْمُسْتَقِيْمِۗ ذٰلِكَ خَيْرٌ وَّاَحْسَنُ تَأْوِيْلًا', 'wa auful-kaila iżā kiltum wazinụ bil-qisṭāsil-mustaqīm, żālika khairuw wa aḥsanu ta`wīlā'),
(2064, 17, 36, 'وَلَا تَقْفُ مَا لَيْسَ لَكَ بِهٖ عِلْمٌ ۗاِنَّ السَّمْعَ وَالْبَصَرَ وَالْفُؤَادَ كُلُّ اُولٰۤىِٕكَ كَانَ عَنْهُ مَسْـُٔوْلًا', 'wa lā taqfu mā laisa laka bihī \'ilm, innas-sam\'a wal-baṣara wal-fu`āda kullu ulā`ika kāna \'an-hu mas`ụlā'),
(2065, 17, 37, 'وَلَا تَمْشِ فِى الْاَرْضِ مَرَحًاۚ اِنَّكَ لَنْ تَخْرِقَ الْاَرْضَ وَلَنْ تَبْلُغَ الْجِبَالَ طُوْلًا', 'wa lā tamsyi fil-arḍi maraḥā, innaka lan takhriqal-arḍa wa lan tablugal-jibāla ṭụlā'),
(2066, 17, 38, 'كُلُّ ذٰلِكَ كَانَ سَيِّئُهٗ عِنْدَ رَبِّكَ مَكْرُوْهًا', 'kullu żālika kāna sayyi`uhụ \'inda rabbika makrụhā'),
(2067, 17, 39, 'ذٰلِكَ مِمَّآ اَوْحٰٓى اِلَيْكَ رَبُّكَ مِنَ الْحِكْمَةِۗ وَلَا تَجْعَلْ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ فَتُلْقٰى فِيْ جَهَنَّمَ مَلُوْمًا مَّدْحُوْرًا', 'żālika mimmā auḥā ilaika rabbuka minal-ḥikmah, wa lā taj\'al ma\'allāhi ilāhan ākhara fa tulqā fī jahannama malụmam mad-ḥụrā'),
(2068, 17, 40, 'اَفَاَصْفٰىكُمْ رَبُّكُمْ بِالْبَنِيْنَ وَاتَّخَذَ مِنَ الْمَلٰۤىِٕكَةِ اِنَاثًاۗ اِنَّكُمْ لَتَقُوْلُوْنَ قَوْلًا عَظِيْمًا', 'a fa aṣfākum rabbukum bil-banīna wattakhaża minal-malā`ikati ināṡā, innakum lataqụlụna qaulan \'aẓīmā'),
(2069, 17, 41, 'وَلَقَدْ صَرَّفْنَا فِيْ هٰذَا الْقُرْاٰنِ لِيَذَّكَّرُوْاۗ وَمَا يَزِيْدُهُمْ اِلَّا نُفُوْرًا', 'wa laqad ṣarrafnā fī hāżal-qur`āni liyażżakkarụ, wa mā yazīduhum illā nufụrā'),
(2070, 17, 42, 'قُلْ لَّوْ كَانَ مَعَهٗ ٓ اٰلِهَةٌ كَمَا يَقُوْلُوْنَ اِذًا لَّابْتَغَوْا اِلٰى ذِى الْعَرْشِ سَبِيْلًا', 'qul lau kāna ma\'ahū ālihatung kamā yaqụlụna iżal labtagau ilā żil-\'arsyi sabīlā'),
(2071, 17, 43, ' سُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يَقُوْلُوْنَ عُلُوًّا كَبِيْرًا', 'sub-ḥānahụ wa ta\'ālā \'ammā yaqụlụna \'uluwwang kabīrā'),
(2072, 17, 44, 'تُسَبِّحُ لَهُ السَّمٰوٰتُ السَّبْعُ وَالْاَرْضُ وَمَنْ فِيْهِنَّۗ وَاِنْ مِّنْ شَيْءٍ اِلَّا يُسَبِّحُ بِحَمْدِهٖ وَلٰكِنْ لَّا تَفْقَهُوْنَ تَسْبِيْحَهُمْۗ اِنَّهٗ كَانَ حَلِيْمًا غَفُوْرًا', 'tusabbiḥu lahus-samāwātus-sab\'u wal-arḍu wa man fīhinn, wa im min syai`in illā yusabbiḥu biḥamdihī wa lākil lā tafqahụna tasbīḥahum, innahụ kāna ḥalīman gafụrā'),
(2073, 17, 45, 'وَاِذَا قَرَأْتَ الْقُرْاٰنَ جَعَلْنَا بَيْنَكَ وَبَيْنَ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ حِجَابًا مَّسْتُوْرًاۙ', 'wa iżā qara`tal-qur`āna ja\'alnā bainaka wa bainallażīna lā yu`minụna bil-ākhirati ḥijābam mastụrā'),
(2074, 17, 46, 'وَّجَعَلْنَا عَلٰى قُلُوْبِهِمْ اَكِنَّةً اَنْ يَّفْقَهُوْهُ وَفِيْٓ اٰذَانِهِمْ وَقْرًاۗ وَاِذَا ذَكَرْتَ رَبَّكَ فِى الْقُرْاٰنِ وَحْدَهٗ وَلَّوْا عَلٰٓى اَدْبَارِهِمْ نُفُوْرًا', 'wa ja\'alnā \'alā qulụbihim akinnatan ay yafqahụhu wa fī āżānihim waqrā, wa iżā żakarta rabbaka fil-qur`āni waḥdahụ wallau \'alā adbārihim nufụrā'),
(2075, 17, 47, 'نَحْنُ اَعْلَمُ بِمَا يَسْتَمِعُوْنَ بِهٖٓ اِذْ يَسْتَمِعُوْنَ اِلَيْكَ وَاِذْ هُمْ نَجْوٰٓى اِذْ يَقُوْلُ الظّٰلِمُوْنَ اِنْ تَتَّبِعُوْنَ اِلَّا رَجُلًا مَّسْحُوْرًا', 'naḥnu a\'lamu bimā yastami\'ụna bihī iż yastami\'ụna ilaika wa iż hum najwā iż yaqụluẓ-ẓālimụna in tattabi\'ụna illā rajulam mas-ḥụrā'),
(2076, 17, 48, 'اُنْظُرْ كَيْفَ ضَرَبُوْا لَكَ الْاَمْثَالَ فَضَلُّوْا فَلَا يَسْتَطِيْعُوْنَ سَبِيْلًا', 'unẓur kaifa ḍarabụ lakal-amṡāla fa ḍallụ fa lā yastaṭī\'ụna sabīlā'),
(2077, 17, 49, 'وَقَالُوْٓا ءَاِذَا كُنَّا عِظَامًا وَّرُفَاتًا ءَاِنَّا لَمَبْعُوْثُوْنَ خَلْقًا جَدِيْدًا', 'wa qālū a iżā kunnā \'iẓāmaw wa rufātan a innā lamab\'ụṡụna khalqan jadīdā'),
(2078, 17, 50, '۞ قُلْ كُوْنُوْا حِجَارَةً اَوْ حَدِيْدًاۙ', 'qul kụnụ ḥijāratan au ḥadīdā'),
(2079, 17, 51, 'اَوْ خَلْقًا مِّمَّا يَكْبُرُ فِيْ صُدُوْرِكُمْ ۚفَسَيَقُوْلُوْنَ مَنْ يُّعِيْدُنَاۗ قُلِ الَّذِيْ فَطَرَكُمْ اَوَّلَ مَرَّةٍۗ فَسَيُنْغِضُوْنَ اِلَيْكَ رُءُوْسَهُمْ وَيَقُوْلُوْنَ مَتٰى هُوَۗ قُلْ عَسٰٓى اَنْ يَّكُوْنَ قَرِيْبًا', 'au khalqam mimmā yakburu fī ṣudụrikum, fa sayaqụlụna may yu\'īdunā, qulillażī faṭarakum awwala marrah, fa sayun-giḍụna ilaika ru`ụsahum wa yaqụlụna matā huw, qul \'asā ay yakụna qarībā'),
(2080, 17, 52, 'يَوْمَ يَدْعُوْكُمْ فَتَسْتَجِيْبُوْنَ بِحَمْدِهٖ وَتَظُنُّوْنَ اِنْ لَّبِثْتُمْ اِلَّا قَلِيْلًا', 'yauma yad\'ụkum fa tastajībụna biḥamdihī wa taẓunnụna il labiṡtum illā qalīlā'),
(2081, 17, 53, 'وَقُلْ لِّعِبَادِيْ يَقُوْلُوا الَّتِيْ هِيَ اَحْسَنُۗ اِنَّ الشَّيْطٰنَ يَنْزَغُ بَيْنَهُمْۗ اِنَّ الشَّيْطٰنَ كَانَ لِلْاِنْسَانِ عَدُوًّا مُّبِيْنًا', 'wa qul li\'ibādī yaqụlullatī hiya aḥsan, innasy-syaiṭāna yanzagu bainahum, innasy-syaiṭāna kāna lil-insāni \'aduwwam mubīnā'),
(2082, 17, 54, 'رَبُّكُمْ اَعْلَمُ بِكُمْ اِنْ يَّشَأْ يَرْحَمْكُمْ اَوْ اِنْ يَّشَأْ يُعَذِّبْكُمْۗ وَمَآ اَرْسَلْنٰكَ عَلَيْهِمْ وَكِيْلًا', 'rabbukum a\'lamu bikum iy yasya` yar-ḥamkum au iy yasya` yu\'ażżibkum, wa mā arsalnāka \'alaihim wakīlā'),
(2083, 17, 55, 'وَرَبُّكَ اَعْلَمُ بِمَنْ فِى السَّمٰوٰتِ وَالْاَرْضِۗ وَلَقَدْ فَضَّلْنَا بَعْضَ النَّبِيّٖنَ عَلٰى بَعْضٍ وَّاٰتَيْنَا دَاوٗدَ زَبُوْرًا', 'wa rabbuka a\'lamu biman fis-samāwāti wal-arḍ, wa laqad faḍḍalnā ba\'ḍan-nabiyyīna \'alā ba\'ḍiw wa ātainā dāwụda zabụrā'),
(2084, 17, 56, 'قُلِ ادْعُوا الَّذِيْنَ زَعَمْتُمْ مِّنْ دُوْنِهٖ فَلَا يَمْلِكُوْنَ كَشْفَ الضُّرِّ عَنْكُمْ وَلَا تَحْوِيْلًا', 'qulid\'ullażīna za\'amtum min dụnihī fa lā yamlikụna kasyfaḍ-ḍurri \'angkum wa lā taḥwīlā'),
(2085, 17, 57, 'اُولٰۤىِٕكَ الَّذِيْنَ يَدْعُوْنَ يَبْتَغُوْنَ اِلٰى رَبِّهِمُ الْوَسِيْلَةَ اَيُّهُمْ اَقْرَبُ وَيَرْجُوْنَ رَحْمَتَهٗ وَيَخَافُوْنَ عَذَابَهٗۗ اِنَّ عَذَابَ رَبِّكَ كَانَ مَحْذُوْرًا', 'ulā`ikallażīna yad\'ụna yabtagụna ilā rabbihimul-wasīlata ayyuhum aqrabu wa yarjụna raḥmatahụ wa yakhāfụna \'ażābah, inna \'ażāba rabbika kāna maḥżụrā'),
(2086, 17, 58, 'وَاِنْ مِّنْ قَرْيَةٍ اِلَّا نَحْنُ مُهْلِكُوْهَا قَبْلَ يَوْمِ الْقِيٰمَةِ اَوْ مُعَذِّبُوْهَا عَذَابًا شَدِيْدًاۗ كَانَ ذٰلِكَ فىِ الْكِتٰبِ مَسْطُوْرًا', 'wa im ming qaryatin illā naḥnu muhlikụhā qabla yaumil-qiyāmati au mu\'ażżibụhā \'ażāban syadīdā, kāna żālika fil-kitābi masṭụrā'),
(2087, 17, 59, 'وَمَا مَنَعَنَآ اَنْ نُّرْسِلَ بِالْاٰيٰتِ اِلَّآ اَنْ كَذَّبَ بِهَا الْاَوَّلُوْنَۗ وَاٰتَيْنَا ثَمُوْدَ النَّاقَةَ مُبْصِرَةً فَظَلَمُوْا بِهَاۗ وَمَا نُرْسِلُ بِالْاٰيٰتِ اِلَّا تَخْوِيْفًا', 'wa mā mana\'anā an nursila bil-āyāti illā ang każżaba bihal-awwalụn, wa ātainā ṡamụdan-nāqata mubṣiratan fa ẓalamụ bihā, wa mā nursilu bil-āyāti illā takhwīfā'),
(2088, 17, 60, 'وَاِذْ قُلْنَا لَكَ اِنَّ رَبَّكَ اَحَاطَ بِالنَّاسِۗ وَمَا جَعَلْنَا الرُّءْيَا الَّتِيْٓ اَرَيْنٰكَ اِلَّا فِتْنَةً لِّلنَّاسِ وَالشَّجَرَةَ الْمَلْعُوْنَةَ فِى الْقُرْاٰنِ ۗ وَنُخَوِّفُهُمْۙ فَمَا يَزِيْدُهُمْ اِلَّا طُغْيَانًا كَبِيْرًا', 'wa iż qulnā laka inna rabbaka aḥāṭa bin-nās, wa mā ja\'alnar-ru`yallatī araināka illā fitnatal lin-nāsi wasy-syajaratal-mal\'ụnata fil-qur`ān, wa nukhawwifuhum fa mā yazīduhum illā ṭugyānang kabīrā'),
(2089, 17, 61, 'وَاِذْ قُلْنَا لِلْمَلٰۤىِٕكَةِ اسْجُدُوْا لِاٰدَمَ فَسَجَدُوْٓا اِلَّآ اِبْلِيْسَۗ قَالَ ءَاَسْجُدُ لِمَنْ خَلَقْتَ طِيْنًاۚ', 'wa iż qulnā lil-malā`ikatisjudụ li`ādama fa sajadū illā iblīs, qāla a asjudu liman khalaqta ṭīnā'),
(2090, 17, 62, 'قَالَ اَرَاَيْتَكَ هٰذَا الَّذِيْ كَرَّمْتَ عَلَيَّ لَىِٕنْ اَخَّرْتَنِ اِلٰى يَوْمِ الْقِيٰمَةِ لَاَحْتَنِكَنَّ ذُرِّيَّتَهٗٓ اِلَّا قَلِيْلًا', 'qāla a ra`aitaka hāżallażī karramta \'alayya la`in akhkhartani ilā yaumil-qiyāmati la`aḥtanikanna żurriyyatahū illā qalīlā'),
(2091, 17, 63, 'قَالَ اذْهَبْ فَمَنْ تَبِعَكَ مِنْهُمْ فَاِنَّ جَهَنَّمَ جَزَاۤؤُكُمْ جَزَاۤءً مَّوْفُوْرًا', 'qālaż-hab fa man tabi\'aka min-hum fa inna jahannama jazā`ukum jazā`am maufụrā'),
(2092, 17, 64, 'وَاسْتَفْزِزْ مَنِ اسْتَطَعْتَ مِنْهُمْ بِصَوْتِكَ وَاَجْلِبْ عَلَيْهِمْ بِخَيْلِكَ وَرَجِلِكَ وَشَارِكْهُمْ فِى الْاَمْوَالِ وَالْاَوْلَادِ وَعِدْهُمْۗ وَمَا يَعِدُهُمُ الشَّيْطٰنُ اِلَّا غُرُوْرًا', 'wastafziz manistaṭa\'ta min-hum biṣautika wa ajlib \'alaihim bikhailika wa rajilika wa syārik-hum fil-amwāli wal-aulādi wa \'id-hum, wa mā ya\'iduhumusy-syaiṭānu illā gurụrā'),
(2093, 17, 65, 'اِنَّ عِبَادِيْ لَيْسَ لَكَ عَلَيْهِمْ سُلْطٰنٌۗ وَكَفٰى بِرَبِّكَ وَكِيْلًا', 'inna \'ibādī laisa laka \'alaihim sulṭān, wa kafā birabbika wakīlā'),
(2094, 17, 66, 'رَبُّكُمُ الَّذِيْ يُزْجِيْ لَكُمُ الْفُلْكَ فِى الْبَحْرِ لِتَبْتَغُوْا مِنْ فَضْلِهٖۗ اِنَّهٗ كَانَ بِكُمْ رَحِيْمًا', 'rabbukumullażī yuzjī lakumul-fulka fil-baḥri litabtagụ min faḍlih, innahụ kāna bikum raḥīmā'),
(2095, 17, 67, 'وَاِذَا مَسَّكُمُ الضُّرُّ فِى الْبَحْرِ ضَلَّ مَنْ تَدْعُوْنَ اِلَّآ اِيَّاهُۚ فَلَمَّا نَجّٰىكُمْ اِلَى الْبَرِّ اَعْرَضْتُمْۗ وَكَانَ الْاِنْسَانُ كَفُوْرًا', 'wa iżā massakumuḍ-ḍurru fil-baḥri ḍalla man tad\'ụna illā iyyāh, fa lammā najjākum ilal-barri a\'raḍtum, wa kānal-insānu kafụrā'),
(2096, 17, 68, 'اَفَاَمِنْتُمْ اَنْ يَّخْسِفَ بِكُمْ جَانِبَ الْبَرِّ اَوْ يُرْسِلَ عَلَيْكُمْ حَاصِبًا ثُمَّ لَا تَجِدُوْا لَكُمْ وَكِيْلًا ۙ', 'a fa amintum ay yakhsifa bikum jānibal-barri au yursila \'alaikum ḥāṣiban ṡumma lā tajidụ lakum wakīlā'),
(2097, 17, 69, 'اَمْ اَمِنْتُمْ اَنْ يُّعِيْدَكُمْ فِيْهِ تَارَةً اُخْرٰى فَيُرْسِلَ عَلَيْكُمْ قَاصِفًا مِّنَ الرِّيْحِ فَيُغْرِقَكُمْ بِمَا كَفَرْتُمْۙ ثُمَّ لَا تَجِدُوْا لَكُمْ عَلَيْنَا بِهٖ تَبِيْعًا', 'am amintum ay yu\'īdakum fīhi tāratan ukhrā fa yursila \'alaikum qāṣifam minar-rīḥi fa yugriqakum bimā kafartum ṡumma lā tajidụ lakum \'alainā bihī tabī\'ā'),
(2098, 17, 70, '۞ وَلَقَدْ كَرَّمْنَا بَنِيْٓ اٰدَمَ وَحَمَلْنٰهُمْ فِى الْبَرِّ وَالْبَحْرِ وَرَزَقْنٰهُمْ مِّنَ الطَّيِّبٰتِ وَفَضَّلْنٰهُمْ عَلٰى كَثِيْرٍ مِّمَّنْ خَلَقْنَا تَفْضِيْلًا', 'wa laqad karramnā banī ādama wa ḥamalnāhum fil-barri wal-baḥri wa razaqnāhum minaṭ-ṭayyibāti wa faḍḍalnāhum \'alā kaṡīrim mim man khalaqnā tafḍīlā'),
(2099, 17, 71, 'يَوْمَ نَدْعُوْا كُلَّ اُنَاسٍۢ بِاِمَامِهِمْۚ فَمَنْ اُوْتِيَ كِتٰبَهٗ بِيَمِيْنِهٖ فَاُولٰۤىِٕكَ يَقْرَءُوْنَ كِتٰبَهُمْ وَلَا يُظْلَمُوْنَ فَتِيْلًا', 'yauma nad\'ụ kulla unāsim bi`imāmihim, fa man ụtiya kitābahụ biyamīnihī fa ulā`ika yaqra`ụna kitābahum wa lā yuẓlamụna fatīlā'),
(2100, 17, 72, 'وَمَنْ كَانَ فِيْ هٰذِهٖٓ اَعْمٰى فَهُوَ فِى الْاٰخِرَةِ اَعْمٰى وَاَضَلُّ سَبِيْلًا', 'wa mang kāna fī hāżihī a\'mā fa huwa fil-ākhirati a\'mā wa aḍallu sabīlā'),
(2101, 17, 73, 'وَاِنْ كَادُوْا لَيَفْتِنُوْنَكَ عَنِ الَّذِيْٓ اَوْحَيْنَآ اِلَيْكَ لِتَفْتَرِيَ عَلَيْنَا غَيْرَهٗۖ وَاِذًا لَّاتَّخَذُوْكَ خَلِيْلًا', 'wa ing kādụ layaftinụnaka \'anillażī auḥainā ilaika litaftariya \'alainā gairahụ wa iżal lattakhażụka khalīlā'),
(2102, 17, 74, 'وَلَوْلَآ اَنْ ثَبَّتْنٰكَ لَقَدْ كِدْتَّ تَرْكَنُ اِلَيْهِمْ شَيْـًٔا قَلِيْلًا ۙ', 'walau lā an ṡabbatnāka laqad kitta tarkanu ilaihim syai`ang qalīlā'),
(2103, 17, 75, 'اِذًا لَّاَذَقْنٰكَ ضِعْفَ الْحَيٰوةِ وَضِعْفَ الْمَمَاتِ ثُمَّ لَا تَجِدُ لَكَ عَلَيْنَا نَصِيْرًا', 'iżal la`ażaqnāka ḍi\'fal-ḥayāti wa ḍi\'fal-mamāti ṡumma lā tajidu laka \'alainā naṣīrā'),
(2104, 17, 76, 'وَاِنْ كَادُوْا لَيَسْتَفِزُّوْنَكَ مِنَ الْاَرْضِ لِيُخْرِجُوْكَ مِنْهَا وَاِذًا لَّا يَلْبَثُوْنَ خِلٰفَكَ اِلَّا قَلِيْلًا', 'wa ing kādụ layastafizzụnaka minal-arḍi liyukhrijụka min-hā wa iżal lā yalbaṡụna khilāfaka illā qalīlā'),
(2105, 17, 77, 'سُنَّةَ مَنْ قَدْ اَرْسَلْنَا قَبْلَكَ مِنْ رُّسُلِنَا وَلَا تَجِدُ لِسُنَّتِنَا تَحْوِيْلًا', 'sunnata mang qad arsalnā qablaka mir rusulinā wa lā tajidu lisunnatinā taḥwīlā'),
(2106, 17, 78, 'اَقِمِ الصَّلٰوةَ لِدُلُوْكِ الشَّمْسِ اِلٰى غَسَقِ الَّيْلِ وَقُرْاٰنَ الْفَجْرِۗ اِنَّ قُرْاٰنَ الْفَجْرِ كَانَ مَشْهُوْدًا', 'aqimiṣ-ṣalāta lidulụkisy-syamsi ilā gasaqil-laili wa qur`ānal-fajr, inna qur`ānal-fajri kāna masy-hụdā'),
(2107, 17, 79, 'وَمِنَ الَّيْلِ فَتَهَجَّدْ بِهٖ نَافِلَةً لَّكَۖ عَسٰٓى اَنْ يَّبْعَثَكَ رَبُّكَ مَقَامًا مَّحْمُوْدًا', 'wa minal-laili fa taḥajjad bihī nāfilatal laka \'asā ay yab\'aṡaka rabbuka maqāmam maḥmụdā'),
(2108, 17, 80, 'وَقُلْ رَّبِّ اَدْخِلْنِيْ مُدْخَلَ صِدْقٍ وَّاَخْرِجْنِيْ مُخْرَجَ صِدْقٍ وَّاجْعَلْ لِّيْ مِنْ لَّدُنْكَ سُلْطٰنًا نَّصِيْرًا', 'wa qur rabbi adkhilnī mudkhala ṣidqiw wa akhrijnī mukhraja ṣidqiw waj\'al lī mil ladungka sulṭānan naṣīrā'),
(2109, 17, 81, 'وَقُلْ جَاۤءَ الْحَقُّ وَزَهَقَ الْبَاطِلُ ۖاِنَّ الْبَاطِلَ كَانَ زَهُوْقًا', 'wa qul jā`al-ḥaqqu wa zahaqal-bāṭilu innal-bāṭila kāna zahụqā'),
(2110, 17, 82, 'وَنُنَزِّلُ مِنَ الْقُرْاٰنِ مَا هُوَ شِفَاۤءٌ وَّرَحْمَةٌ لِّلْمُؤْمِنِيْنَۙ وَلَا يَزِيْدُ الظّٰلِمِيْنَ اِلَّا خَسَارًا', 'wa nunazzilu minal-qur`āni mā huwa syifā`uw wa raḥmatul lil-mu`minīna wa lā yazīduẓ-ẓālimīna illā khasārā'),
(2111, 17, 83, 'وَاِذَآ اَنْعَمْنَا عَلَى الْاِنْسَانِ اَعْرَضَ وَنَاٰ بِجَانِبِهٖۚ وَاِذَا مَسَّهُ الشَّرُّ كَانَ يَـُٔوْسًا', 'wa iżā an\'amnā \'alal-insāni a\'raḍa wa na`ā bijānibih, wa iżā massahusy-syarru kāna ya`ụsā'),
(2112, 17, 84, 'قُلْ كُلٌّ يَّعْمَلُ عَلٰى شَاكِلَتِهٖۗ فَرَبُّكُمْ اَعْلَمُ بِمَنْ هُوَ اَهْدٰى سَبِيْلًا', 'qul kulluy ya\'malu \'alā syākilatih, fa rabbukum a\'lamu biman huwa ahdā sabīlā'),
(2113, 17, 85, 'وَيَسْـَٔلُوْنَكَ عَنِ الرُّوْحِۗ قُلِ الرُّوْحُ مِنْ اَمْرِ رَبِّيْ وَمَآ اُوْتِيْتُمْ مِّنَ الْعِلْمِ اِلَّا قَلِيْلًا', 'wa yas`alụnaka \'anir-rụḥ, qulir-rụḥu min amri rabbī wa mā ụtītum minal-\'ilmi illā qalīlā'),
(2114, 17, 86, 'وَلَىِٕنْ شِئْنَا لَنَذْهَبَنَّ بِالَّذِيْٓ اَوْحَيْنَآ اِلَيْكَ ثُمَّ لَا تَجِدُ لَكَ بِهٖ عَلَيْنَا وَكِيْلًاۙ؉؉', 'wa la`in syi`nā lanaż-habanna billażī auḥainā ilaika ṡumma lā tajidu laka bihī \'alainā wakīlā'),
(2115, 17, 87, 'اِلَّا رَحْمَةً مِّنْ رَّبِّكَۗ اِنَّ فَضْلَهٗ كَانَ عَلَيْكَ كَبِيْرًا', 'illā raḥmatam mir rabbik, inna faḍlahụ kāna \'alaika kabīrā'),
(2116, 17, 88, 'قُلْ لَّىِٕنِ اجْتَمَعَتِ الْاِنْسُ وَالْجِنُّ عَلٰٓى اَنْ يَّأْتُوْا بِمِثْلِ هٰذَا الْقُرْاٰنِ لَا يَأْتُوْنَ بِمِثْلِهٖ وَلَوْ كَانَ بَعْضُهُمْ لِبَعْضٍ ظَهِيْرًا', 'qul la`inijtama\'atil-insu wal-jinnu \'alā ay ya`tụ bimiṡli hāżal-qur`āni lā ya`tụna bimiṡlihī walau kāna ba\'ḍuhum liba\'ḍin ẓahīrā'),
(2117, 17, 89, 'وَلَقَدْ صَرَّفْنَا لِلنَّاسِ فِيْ هٰذَا الْقُرْاٰنِ مِنْ كُلِّ مَثَلٍۖ فَاَبٰىٓ اَكْثَرُ النَّاسِ اِلَّا كُفُوْرًا', 'wa laqad ṣarrafnā lin-nāsi fī hāżal-qur`āni ming kulli maṡalin fa abā akṡarun-nāsi illā kufụrā'),
(2118, 17, 90, 'وَقَالُوْا لَنْ نُّؤْمِنَ لَكَ حَتّٰى تَفْجُرَ لَنَا مِنَ الْاَرْضِ يَنْۢبُوْعًاۙ', 'wa qālụ lan nu`mina laka ḥattā tafjura lanā minal-arḍi yambụ\'ā'),
(2119, 17, 91, 'اَوْ تَكُوْنَ لَكَ جَنَّةٌ مِّنْ نَّخِيْلٍ وَّعِنَبٍ فَتُفَجِّرَ الْاَنْهٰرَ خِلٰلَهَا تَفْجِيْرًاۙ', 'au takụna laka jannatum min nakhīliw wa \'inabin fa tufajjiral-an-hāra khilālahā tafjīrā'),
(2120, 17, 92, 'اَوْ تُسْقِطَ السَّمَاۤءَ كَمَا زَعَمْتَ عَلَيْنَا كِسَفًا اَوْ تَأْتِيَ بِاللّٰهِ وَالْمَلٰۤىِٕكَةِ قَبِيْلًاۙ', 'au tusqiṭas-samā`a kamā za\'amta \'alainā kisafan au ta`tiya billāhi wal-malā`ikati qabīlā'),
(2121, 17, 93, 'اَوْ يَكُوْنَ لَكَ بَيْتٌ مِّنْ زُخْرُفٍ اَوْ تَرْقٰى فِى السَّمَاۤءِ ۗوَلَنْ نُّؤْمِنَ لِرُقِيِّكَ حَتّٰى تُنَزِّلَ عَلَيْنَا كِتٰبًا نَّقْرَؤُهٗۗ قُلْ سُبْحَانَ رَبِّيْ هَلْ كُنْتُ اِلَّا بَشَرًا رَّسُوْلًا', 'au yakụna laka baitum min zukhrufin au tarqā fis-samā`, wa lan nu`mina liruqiyyika ḥattā tunazzila \'alainā kitāban naqra`uh, qul sub-ḥāna rabbī hal kuntu illā basyarar rasụlā'),
(2122, 17, 94, 'وَمَا مَنَعَ النَّاسَ اَنْ يُّؤْمِنُوْٓا اِذْ جَاۤءَهُمُ الْهُدٰٓى اِلَّآ اَنْ قَالُوْٓا اَبَعَثَ اللّٰهُ بَشَرًا رَّسُوْلًا', 'wa mā mana\'an-nāsa ay yu`minū iż jā`ahumul-hudā illā ang qālū a ba\'aṡallāhu basyarar rasụlā'),
(2123, 17, 95, 'قُلْ لَّوْ كَانَ فِى الْاَرْضِ مَلٰۤىِٕكَةٌ يَّمْشُوْنَ مُطْمَىِٕنِّيْنَ لَنَزَّلْنَا عَلَيْهِمْ مِّنَ السَّمَاۤءِ مَلَكًا رَّسُوْلًا', 'qul lau kāna fil-arḍi malā`ikatuy yamsyụna muṭma`innīna lanazzalnā \'alaihim minas-samā`i malakar rasụlā'),
(2124, 17, 96, 'قُلْ كَفٰى بِاللّٰهِ شَهِيْدًاۢ بَيْنِيْ وَبَيْنَكُمْۗ اِنَّهٗ كَانَ بِعِبَادِهٖ خَبِيْرًاۢ بَصِيْرًا', 'qul kafā billāhi syahīdam bainī wa bainakum, innahụ kāna bi\'ibādihī khabīram baṣīrā'),
(2125, 17, 97, 'وَمَنْ يَّهْدِ اللّٰهُ فَهُوَ الْمُهْتَدِۚ وَمَنْ يُّضْلِلْ فَلَنْ تَجِدَ لَهُمْ اَوْلِيَاۤءَ مِنْ دُوْنِهٖۗ وَنَحْشُرُهُمْ يَوْمَ الْقِيٰمَةِ عَلٰى وُجُوْهِهِمْ عُمْيًا وَّبُكْمًا وَّصُمًّاۗ مَأْوٰىهُمْ جَهَنَّمُۗ كُلَّمَا خَبَتْ زِدْنٰهُمْ سَعِيْرًا', 'wa may yahdillāhu fa huwal-muhtad, wa may yuḍlil fa lan tajida lahum auliyā`a min dụnih, wa naḥsyuruhum yaumal-qiyāmati \'alā wujụhihim \'umyaw wa bukmaw wa ṣummā, ma`wāhum jahannam, kullamā khabat zidnāhum sa\'īrā'),
(2126, 17, 98, 'ذٰلِكَ جَزَاۤؤُهُمْ بِاَنَّهُمْ كَفَرُوْا بِاٰيٰتِنَا وَقَالُوْٓا ءَاِذَا كُنَّا عِظَامًا وَّرُفَاتًا ءَاِنَّا لَمَبْعُوْثُوْنَ خَلْقًا جَدِيْدًا', 'żālika jazā`uhum bi`annahum kafarụ bi`āyātinā wa qālū a iżā kunnā \'iẓāmaw wa rufātan a innā lamab\'ụṡụna khalqan jadīdā'),
(2127, 17, 99, '۞ اَوَلَمْ يَرَوْا اَنَّ اللّٰهَ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ قَادِرٌ عَلٰٓى اَنْ يَّخْلُقَ مِثْلَهُمْ وَجَعَلَ لَهُمْ اَجَلًا لَّا رَيْبَ فِيْهِۗ فَاَبَى الظّٰلِمُوْنَ اِلَّا كُفُوْرًا', 'a wa lam yarau annallāhallażī khalaqas-samāwāti wal-arḍa qādirun \'alā ay yakhluqa miṡlahum wa ja\'ala lahum ajalal lā raiba fīh, fa abaẓ-ẓālimụna illā kufụrā'),
(2128, 17, 100, 'قُلْ لَّوْ اَنْتُمْ تَمْلِكُوْنَ خَزَاۤىِٕنَ رَحْمَةِ رَبِّيْٓ اِذًا لَّاَمْسَكْتُمْ خَشْيَةَ الْاِنْفَاقِۗ وَكَانَ الْاِنْسَانُ قَتُوْرًا', 'qul lau antum tamlikụna khazā`ina raḥmati rabbī iżal la`amsaktum khasy-yatal-infāq, wa kānal-insānu qatụrā'),
(2129, 17, 101, 'وَلَقَدْ اٰتَيْنَا مُوْسٰى تِسْعَ اٰيٰتٍۢ بَيِّنٰتٍ فَسْـَٔلْ بَنِيْٓ اِسْرَاۤءِيْلَ اِذْ جَاۤءَهُمْ فَقَالَ لَهٗ فِرْعَوْنُ اِنِّيْ لَاَظُنُّكَ يٰمُوْسٰى مَسْحُوْرًا', 'wa laqad ātainā mụsā tis\'a āyātim bayyinātin fas`al banī isrā`īla iż jā`ahum fa qāla lahụ fir\'aunu innī la`aẓunnuka yā mụsā mas-ḥụrā'),
(2130, 17, 102, 'قَالَ لَقَدْ عَلِمْتَ مَآ اَنْزَلَ هٰٓؤُلَاۤءِ اِلَّا رَبُّ السَّمٰوٰتِ وَالْاَرْضِ بَصَاۤىِٕرَۚ وَاِنِّيْ لَاَظُنُّكَ يٰفِرْعَوْنُ مَثْبُوْرًا', 'qāla laqad \'alimta mā anzala hā`ulā`i illā rabbus-samāwāti wal-arḍi baṣā`ir, wa innī la`aẓunnuka yā fir\'aunu maṡbụrā'),
(2131, 17, 103, 'فَاَرَادَ اَنْ يَّسْتَفِزَّهُمْ مِّنَ الْاَرْضِ فَاَغْرَقْنٰهُ وَمَنْ مَّعَهٗ جَمِيْعًاۙ', 'fa arāda ay yastafizzahum minal-arḍi fa agraqnāhu wa mam ma\'ahụ jamī\'ā'),
(2132, 17, 104, 'وَّقُلْنَا مِنْۢ بَعْدِهٖ لِبَنِيْٓ اِسْرَاۤءِيْلَ اسْكُنُوا الْاَرْضَ فَاِذَا جَاۤءَ وَعْدُ الْاٰخِرَةِ جِئْنَا بِكُمْ لَفِيْفًاۗ', 'wa qulnā mim ba\'dihī libanī isrā`īlaskunul-arḍa fa iżā jā`a wa\'dul-ākhirati ji`nā bikum lafīfā'),
(2133, 17, 105, 'وَبِالْحَقِّ اَنْزَلْنٰهُ وَبِالْحَقِّ نَزَلَۗ وَمَآ اَرْسَلْنٰكَ اِلَّا مُبَشِّرًا وَّنَذِيْرًاۘ', 'wa bil-ḥaqqi anzalnāhu wa bil-ḥaqqi nazal, wa mā arsalnāka illā mubasysyiraw wa nażīrā'),
(2134, 17, 106, 'وَقُرْاٰنًا فَرَقْنٰهُ لِتَقْرَاَهٗ عَلَى النَّاسِ عَلٰى مُكْثٍ وَّنَزَّلْنٰهُ تَنْزِيْلًا', 'wa qur`ānan faraqnāhu litaqra`ahụ \'alan-nāsi \'alā mukṡiw wa nazzalnāhu tanzīlā'),
(2135, 17, 107, 'قُلْ اٰمِنُوْا بِهٖٓ اَوْ لَا تُؤْمِنُوْاۗ اِنَّ الَّذِيْنَ اُوْتُوا الْعِلْمَ مِنْ قَبْلِهٖٓ اِذَا يُتْلٰى عَلَيْهِمْ يَخِرُّوْنَ لِلْاَذْقَانِ سُجَّدًاۙ', 'qul āminụ bihī au lā tu`minụ, innallażīna ụtul-\'ilma ming qablihī iżā yutlā \'alaihim yakhirrụna lil-ażqāni sujjadā'),
(2136, 17, 108, 'وَّيَقُوْلُوْنَ سُبْحٰنَ رَبِّنَآ اِنْ كَانَ وَعْدُ رَبِّنَا لَمَفْعُوْلًا', 'wa yaqụlụna sub-ḥāna rabbinā ing kāna wa\'du rabbinā lamaf\'ụlā'),
(2137, 17, 109, 'وَيَخِرُّوْنَ لِلْاَذْقَانِ يَبْكُوْنَ وَيَزِيْدُهُمْ خُشُوْعًا ۩', 'wa yakhirrụna lil-ażqāni yabkụna wa yazīduhum khusyụ\'ā'),
(2138, 17, 110, 'قُلِ ادْعُوا اللّٰهَ اَوِ ادْعُوا الرَّحْمٰنَۗ اَيًّا مَّا تَدْعُوْا فَلَهُ الْاَسْمَاۤءُ الْحُسْنٰىۚ وَلَا تَجْهَرْ بِصَلَاتِكَ وَلَا تُخَافِتْ بِهَا وَابْتَغِ بَيْنَ ذٰلِكَ سَبِيْلًا', 'qulid\'ullāha awid\'ur-raḥmān, ayyam mā tad\'ụ fa lahul-asmā`ul-ḥusnā, wa lā taj-har biṣalātika wa lā tukhāfit bihā wabtagi baina żālika sabīlā'),
(2139, 17, 111, 'وَقُلِ الْحَمْدُ لِلّٰهِ الَّذِيْ لَمْ يَتَّخِذْ وَلَدًا وَّلَمْ يَكُنْ لَّهٗ شَرِيْكٌ فِى الْمُلْكِ وَلَمْ يَكُنْ لَّهٗ وَلِيٌّ مِّنَ الذُّلِّ وَكَبِّرْهُ تَكْبِيْرًا', 'wa qulil-ḥamdu lillāhillażī lam yattakhiż waladaw wa lam yakul lahụ syarīkun fil-mulki wa lam yakul lahụ waliyyum minaż-żulli wa kabbir-hu takbīrā'),
(2140, 18, 1, 'اَلْحَمْدُ لِلّٰهِ الَّذِيْٓ اَنْزَلَ عَلٰى عَبْدِهِ الْكِتٰبَ وَلَمْ يَجْعَلْ لَّهٗ عِوَجًا ۜ', 'al-ḥamdu lillāhillażī anzala \'alā \'abdihil-kitāba wa lam yaj\'al lahụ \'iwajā'),
(2141, 18, 2, 'قَيِّمًا لِّيُنْذِرَ بَأْسًا شَدِيْدًا مِّنْ لَّدُنْهُ وَيُبَشِّرَ الْمُؤْمِنِيْنَ الَّذِيْنَ يَعْمَلُوْنَ الصّٰلِحٰتِ اَنَّ لَهُمْ اَجْرًا حَسَنًاۙ', 'qayyimal liyunżira ba`san syadīdam mil ladun-hu wa yubasysyiral-mu`minīnallażīna ya\'malụnaṣ-ṣāliḥāti anna lahum ajran ḥasanā'),
(2142, 18, 3, 'مَّاكِثِيْنَ فِيْهِ اَبَدًاۙ', 'mākiṡīna fīhi abadā'),
(2143, 18, 4, 'وَّيُنْذِرَ الَّذِيْنَ قَالُوا اتَّخَذَ اللّٰهُ وَلَدًاۖ', 'wa yunżirallażīna qāluttakhażallāhu waladā'),
(2144, 18, 5, 'مَّا لَهُمْ بِهٖ مِنْ عِلْمٍ وَّلَا لِاٰبَاۤىِٕهِمْۗ كَبُرَتْ كَلِمَةً تَخْرُجُ مِنْ اَفْوَاهِهِمْۗ اِنْ يَّقُوْلُوْنَ اِلَّا كَذِبًا', 'mā lahum bihī min \'ilmiw wa lā li`ābā`ihim, kaburat kalimatan takhruju min afwāhihim, iy yaqụlụna illā każibā'),
(2145, 18, 6, 'فَلَعَلَّكَ بَاخِعٌ نَّفْسَكَ عَلٰٓى اٰثَارِهِمْ اِنْ لَّمْ يُؤْمِنُوْا بِهٰذَا الْحَدِيْثِ اَسَفًا', 'fa la\'allaka bākhi\'un nafsaka \'alā āṡārihim il lam yu`minụ bihāżal-ḥadīṡi asafā'),
(2146, 18, 7, 'اِنَّا جَعَلْنَا مَا عَلَى الْاَرْضِ زِيْنَةً لَّهَا لِنَبْلُوَهُمْ اَيُّهُمْ اَحْسَنُ عَمَلًا', 'innā ja\'alnā mā \'alal-arḍi zīnatal lahā linabluwahum ayyuhum aḥsanu \'amalā'),
(2147, 18, 8, 'وَاِنَّا لَجَاعِلُوْنَ مَا عَلَيْهَا صَعِيْدًا جُرُزًاۗ', 'wa innā lajā\'ilụna mā \'alaihā ṣa\'īdan juruzā'),
(2148, 18, 9, 'اَمْ حَسِبْتَ اَنَّ اَصْحٰبَ الْكَهْفِ وَالرَّقِيْمِ كَانُوْا مِنْ اٰيٰتِنَا عَجَبًا', 'am ḥasibta anna aṣ-ḥābal-kahfi war-raqīmi kānụ min āyātinā \'ajabā'),
(2149, 18, 10, 'اِذْ اَوَى الْفِتْيَةُ اِلَى الْكَهْفِ فَقَالُوْا رَبَّنَآ اٰتِنَا مِنْ لَّدُنْكَ رَحْمَةً وَّهَيِّئْ لَنَا مِنْ اَمْرِنَا رَشَدًا', 'iż awal-fityatu ilal-kahfi fa qālụ rabbanā ātinā mil ladungka raḥmataw wa hayyi` lanā min amrinā rasyadā'),
(2150, 18, 11, 'فَضَرَبْنَا عَلٰٓى اٰذَانِهِمْ فِى الْكَهْفِ سِنِيْنَ عَدَدًاۙ', 'fa ḍarabnā \'alā āżānihim fil-kahfi sinīna \'adadā'),
(2151, 18, 12, 'ثُمَّ بَعَثْنٰهُمْ لِنَعْلَمَ اَيُّ الْحِزْبَيْنِ اَحْصٰى لِمَا لَبِثُوْٓا اَمَدًا', 'ṡumma ba\'aṡnāhum lina\'lama ayyul-ḥizbaini aḥṣā limā labiṡū amadā'),
(2152, 18, 13, 'نَحْنُ نَقُصُّ عَلَيْكَ نَبَاَهُمْ بِالْحَقِّۗ اِنَّهُمْ فِتْيَةٌ اٰمَنُوْا بِرَبِّهِمْ وَزِدْنٰهُمْ هُدًىۖ', 'naḥnu naquṣṣu \'alaika naba`ahum bil-ḥaqq, innahum fityatun āmanụ birabbihim wa zidnāhum hudā'),
(2153, 18, 14, 'وَّرَبَطْنَا عَلٰى قُلُوْبِهِمْ اِذْ قَامُوْا فَقَالُوْا رَبُّنَا رَبُّ السَّمٰوٰتِ وَالْاَرْضِ لَنْ نَّدْعُوَا۟ مِنْ دُوْنِهٖٓ اِلٰهًا لَّقَدْ قُلْنَآ اِذًا شَطَطًا', 'wa rabaṭnā \'alā qulụbihim iż qāmụ fa qālụ rabbunā rabbus-samāwāti wal-arḍi lan nad\'uwa min dụnihī ilāhal laqad qulnā iżan syaṭaṭā'),
(2154, 18, 15, 'هٰٓؤُلَاۤءِ قَوْمُنَا اتَّخَذُوْا مِنْ دُوْنِهٖٓ اٰلِهَةًۗ لَوْلَا يَأْتُوْنَ عَلَيْهِمْ بِسُلْطٰنٍۢ بَيِّنٍۗ فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًاۗ', 'hā`ulā`i qaumunattakhażụ min dụnihī ālihah, lau lā ya`tụna \'alaihim bisulṭānim bayyin, fa man aẓlamu mim maniftarā \'alallāhi każibā'),
(2155, 18, 16, 'وَاِذِ اعْتَزَلْتُمُوْهُمْ وَمَا يَعْبُدُوْنَ اِلَّا اللّٰهَ فَأْوٗٓا اِلَى الْكَهْفِ يَنْشُرْ لَكُمْ رَبُّكُمْ مِّنْ رَّحْمَتِهٖ وَيُهَيِّئْ لَكُمْ مِّنْ اَمْرِكُمْ مِّرْفَقًا', 'wa iżi\'tazaltumụhum wa mā ya\'budụna illallāha fa`wū ilal-kahfi yansyur lakum rabbukum mir raḥmatihī wa yuhayyi` lakum min amrikum mirfaqā'),
(2156, 18, 17, '۞ وَتَرَى الشَّمْسَ اِذَا طَلَعَتْ تَّزَاوَرُ عَنْ كَهْفِهِمْ ذَاتَ الْيَمِيْنِ وَاِذَا غَرَبَتْ تَّقْرِضُهُمْ ذَاتَ الشِّمَالِ وَهُمْ فِيْ فَجْوَةٍ مِّنْهُۗ ذٰلِكَ مِنْ اٰيٰتِ اللّٰهِ ۗمَنْ يَّهْدِ اللّٰهُ فَهُوَ الْمُهْتَدِ وَمَنْ يُّضْلِلْ فَلَنْ تَجِدَ لَهٗ وَلِيًّا مُّرْشِدًا', 'wa tarasy-syamsa iżā ṭala\'at tazāwaru \'ang kahfihim żātal-yamīni wa iżā garabat taqriḍuhum żātasy-syimāli wa hum fī fajwatim min-h, żālika min āyātillāh, may yahdillāhu fa huwal-muhtadi wa may yuḍlil fa lan tajida lahụ waliyyam mursyidā'),
(2157, 18, 18, 'وَتَحْسَبُهُمْ اَيْقَاظًا وَّهُمْ رُقُوْدٌ ۖوَّنُقَلِّبُهُمْ ذَاتَ الْيَمِيْنِ وَذَاتَ الشِّمَالِ ۖوَكَلْبُهُمْ بَاسِطٌ ذِرَاعَيْهِ بِالْوَصِيْدِۗ لَوِ اطَّلَعْتَ عَلَيْهِمْ لَوَلَّيْتَ مِنْهُمْ فِرَارًا وَّلَمُلِئْتَ مِنْهُمْ رُعْبًا', 'wa taḥsabuhum aiqāẓaw wa hum ruqụduw wa nuqallibuhum żātal-yamīni wa żātasy-syimāli wa kalbuhum bāsiṭun żirā\'aihi bil-waṣīd, lawiṭṭala\'ta \'alaihim lawallaita min-hum firāraw wa lamuli`ta min-hum ru\'bā'),
(2158, 18, 19, 'وَكَذٰلِكَ بَعَثْنٰهُمْ لِيَتَسَاۤءَلُوْا بَيْنَهُمْۗ قَالَ قَاۤىِٕلٌ مِّنْهُمْ كَمْ لَبِثْتُمْۗ قَالُوْا لَبِثْنَا يَوْمًا اَوْ بَعْضَ يَوْمٍۗ قَالُوْا رَبُّكُمْ اَعْلَمُ بِمَا لَبِثْتُمْۗ فَابْعَثُوْٓا اَحَدَكُمْ بِوَرِقِكُمْ هٰذِهٖٓ اِلَى الْمَدِيْنَةِ فَلْيَنْظُرْ اَيُّهَآ اَزْكٰى طَعَامًا فَلْيَأْتِكُمْ بِرِزْقٍ مِّنْهُ وَلْيَتَلَطَّفْ وَلَا يُشْعِرَنَّ بِكُمْ اَحَدًا', 'wa każālika ba\'aṡnāhum liyatasā`alụ bainahum, qāla qā`ilum min-hum kam labiṡtum, qālụ labiṡnā yauman au ba\'ḍa yaụm, qālụ rabbukum a\'lamu bimā labiṡtum, fab\'aṡū aḥadakum biwariqikum hāżihī ilal-madīnati falyanẓur ayyuhā azkā ṭa\'āman falya`tikum birizqim min-hu walyatalaṭṭaf wa lā yusy\'iranna bikum aḥadā'),
(2159, 18, 20, 'اِنَّهُمْ اِنْ يَّظْهَرُوْا عَلَيْكُمْ يَرْجُمُوْكُمْ اَوْ يُعِيْدُوْكُمْ فِيْ مِلَّتِهِمْ وَلَنْ تُفْلِحُوْٓا اِذًا اَبَدًا', 'innahum iy yaẓ-harụ \'alaikum yarjumụkum au yu\'īdụkum fī millatihim wa lan tufliḥū iżan abadā'),
(2160, 18, 21, 'وَكَذٰلِكَ اَعْثَرْنَا عَلَيْهِمْ لِيَعْلَمُوْٓا اَنَّ وَعْدَ اللّٰهِ حَقٌّ وَّاَنَّ السَّاعَةَ لَا رَيْبَ فِيْهَاۚ اِذْ يَتَنَازَعُوْنَ بَيْنَهُمْ اَمْرَهُمْ فَقَالُوا ابْنُوْا عَلَيْهِمْ بُنْيَانًاۗ رَبُّهُمْ اَعْلَمُ بِهِمْۗ قَالَ الَّذِيْنَ غَلَبُوْا عَلٰٓى اَمْرِهِمْ لَنَتَّخِذَنَّ عَلَيْهِمْ مَّسْجِدًا', 'wa każālika a\'ṡarnā \'alaihim liya\'lamū anna wa\'dallāhi ḥaqquw wa annas-sā\'ata lā raiba fīhā, iż yatanāza\'ụna bainahum amrahum fa qālubnụ \'alaihim bun-yānā, rabbuhum a\'lamu bihim, qālallażīna galabụ \'alā amrihim lanattakhiżanna \'alaihim masjidā'),
(2161, 18, 22, 'سَيَقُوْلُوْنَ ثَلٰثَةٌ رَّابِعُهُمْ كَلْبُهُمْۚ وَيَقُوْلُوْنَ خَمْسَةٌ سَادِسُهُمْ كَلْبُهُمْ رَجْمًاۢ بِالْغَيْبِۚ وَيَقُوْلُوْنَ سَبْعَةٌ وَّثَامِنُهُمْ كَلْبُهُمْ ۗقُلْ رَّبِّيْٓ اَعْلَمُ بِعِدَّتِهِمْ مَّا يَعْلَمُهُمْ اِلَّا قَلِيْلٌ ەۗ فَلَا تُمَارِ فِيْهِمْ اِلَّا مِرَاۤءً ظَاهِرًا ۖوَّلَا تَسْتَفْتِ فِيْهِمْ مِّنْهُمْ اَحَدًا', 'sayaqụlụna ṡalāṡatur rābi\'uhum kalbuhum, wa yaqụlụna khamsatun sādisuhum kalbuhum rajmam bil-gaīb, wa yaqụlụna sab\'atuw wa ṡāminuhum kalbuhum, qur rabbī a\'lamu bi\'iddatihim mā ya\'lamuhum illā qalīl, fa lā tumāri fīhim illā mirā`an ẓāhiraw wa lā tastafti fīhim min-hum aḥadā'),
(2162, 18, 23, 'وَلَا تَقُوْلَنَّ لِشَا۟يْءٍ اِنِّيْ فَاعِلٌ ذٰلِكَ غَدًاۙ', 'wa lā taqụlanna lisyai`in innī fā\'ilun żālika gadā'),
(2163, 18, 24, 'اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ ۖوَاذْكُرْ رَّبَّكَ اِذَا نَسِيْتَ وَقُلْ عَسٰٓى اَنْ يَّهْدِيَنِ رَبِّيْ لِاَقْرَبَ مِنْ هٰذَا رَشَدًا', 'illā ay yasyā`allāhu ważkur rabbaka iżā nasīta wa qul \'asā ay yahdiyani rabbī li`aqraba min hāżā rasyadā'),
(2164, 18, 25, 'وَلَبِثُوْا فِيْ كَهْفِهِمْ ثَلٰثَ مِائَةٍ سِنِيْنَ وَازْدَادُوْا تِسْعًا', 'wa labiṡụ fī kahfihim ṡalāṡa mi`atin sinīna wazdādụ tis\'ā'),
(2165, 18, 26, 'قُلِ اللّٰهُ اَعْلَمُ بِمَا لَبِثُوْا ۚ لَهٗ غَيْبُ السَّمٰوٰتِ وَالْاَرْضِۗ اَبْصِرْ بِهٖ وَاَسْمِعْۗ مَا لَهُمْ مِّنْ دُوْنِهٖ مِنْ وَّلِيٍّۗ وَلَا يُشْرِكُ فِيْ حُكْمِهٖٓ اَحَدًا', 'qulillāhu a\'lamu bimā labiṡụ, lahụ gaibus-samāwāti wal-arḍ, abṣir bihī wa asmi\', mā lahum min dụnihī miw waliyy, wa lā yusyriku fī ḥukmihī aḥadā'),
(2166, 18, 27, 'وَاتْلُ مَآ اُوْحِيَ اِلَيْكَ مِنْ كِتَابِ رَبِّكَۗ لَا مُبَدِّلَ لِكَلِمٰتِهٖۗ وَلَنْ تَجِدَ مِنْ دُوْنِهٖ مُلْتَحَدًا', 'watlu mā ụḥiya ilaika ming kitābi rabbik, lā mubaddila likalimātih, wa lan tajida min dụnihī multaḥadā'),
(2167, 18, 28, 'وَاصْبِرْ نَفْسَكَ مَعَ الَّذِيْنَ يَدْعُوْنَ رَبَّهُمْ بِالْغَدٰوةِ وَالْعَشِيِّ يُرِيْدُوْنَ وَجْهَهٗ وَلَا تَعْدُ عَيْنٰكَ عَنْهُمْۚ تُرِيْدُ زِيْنَةَ الْحَيٰوةِ الدُّنْيَاۚ وَلَا تُطِعْ مَنْ اَغْفَلْنَا قَلْبَهٗ عَنْ ذِكْرِنَا وَاتَّبَعَ هَوٰىهُ وَكَانَ اَمْرُهٗ فُرُطًا', 'waṣbir nafsaka ma\'allażīna yad\'ụna rabbahum bil-gadāti wal-\'asyiyyi yurīdụna waj-hahụ wa lā ta\'du \'aināka \'an-hum, turīdu zīnatal-ḥayātid-dun-yā, wa lā tuṭi\' man agfalnā qalbahụ \'an żikrinā wattaba\'a hawāhu wa kāna amruhụ furuṭā'),
(2168, 18, 29, 'وَقُلِ الْحَقُّ مِنْ رَّبِّكُمْۗ فَمَنْ شَاۤءَ فَلْيُؤْمِنْ وَّمَنْ شَاۤءَ فَلْيَكْفُرْۚ اِنَّآ اَعْتَدْنَا لِلظّٰلِمِيْنَ نَارًاۙ اَحَاطَ بِهِمْ سُرَادِقُهَاۗ وَاِنْ يَّسْتَغِيْثُوْا يُغَاثُوْا بِمَاۤءٍ كَالْمُهْلِ يَشْوِى الْوُجُوْهَۗ بِئْسَ الشَّرَابُۗ وَسَاۤءَتْ مُرْتَفَقًا', 'wa qulil-ḥaqqu mir rabbikum, fa man syā`a falyu`miw wa man syā`a falyakfur, innā a\'tadnā liẓ-ẓālimīna nāran aḥāṭa bihim surādiquhā, wa iy yastagīṡụ yugāṡụ bimā`ing kal-muhli yasywil-wujụh, bi`sasy-syarāb, wa sā`at murtafaqā'),
(2169, 18, 30, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ اِنَّا لَا نُضِيْعُ اَجْرَ مَنْ اَحْسَنَ عَمَلًاۚ', 'innallażīna āmanụ wa \'amiluṣ-ṣāliḥāti innā lā nuḍī\'u ajra man aḥsana \'amalā'),
(2170, 18, 31, 'اُولٰۤىِٕكَ لَهُمْ جَنّٰتُ عَدْنٍ تَجْرِيْ مِنْ تَحْتِهِمُ الْاَنْهٰرُ يُحَلَّوْنَ فِيْهَا مِنْ اَسَاوِرَ مِنْ ذَهَبٍ وَّيَلْبَسُوْنَ ثِيَابًا خُضْرًا مِّنْ سُنْدُسٍ وَّاِسْتَبْرَقٍ مُّتَّكِىِٕيْنَ فِيْهَا عَلَى الْاَرَاۤىِٕكِۗ نِعْمَ الثَّوَابُۗ وَحَسُنَتْ مُرْتَفَقًا', 'ulā`ika lahum jannātu \'adnin tajrī min taḥtihimul-an-hāru yuḥallauna fīhā min asāwira min żahabiw wa yalbasụna ṡiyāban khuḍram min sundusiw wa istabraqim muttaki`īna fīhā \'alal-arā`ik, ni\'maṡ-ṡawāb, wa ḥasunat murtafaqā'),
(2171, 18, 32, '۞ وَاضْرِبْ لَهُمْ مَّثَلًا رَّجُلَيْنِ جَعَلْنَا لِاَحَدِهِمَا جَنَّتَيْنِ مِنْ اَعْنَابٍ وَّحَفَفْنٰهُمَا بِنَخْلٍ وَّجَعَلْنَا بَيْنَهُمَا زَرْعًاۗ', 'waḍrib lahum maṡalar rajulaini ja\'alnā li`aḥadihimā jannataini min a\'nābiw wa ḥafafnāhumā binakhliw wa ja\'alnā bainahumā zar\'ā'),
(2172, 18, 33, 'كِلْتَا الْجَنَّتَيْنِ اٰتَتْ اُكُلَهَا وَلَمْ تَظْلِمْ مِّنْهُ شَيْـًٔاۙ وَّفَجَّرْنَا خِلٰلَهُمَا نَهَرًاۙ', 'kiltal-jannataini ātat ukulahā wa lam taẓlim min-hu syai`aw wa fajjarnā khilālahumā naharā'),
(2173, 18, 34, 'وَّكَانَ لَهٗ ثَمَرٌۚ فَقَالَ لِصَاحِبِهٖ وَهُوَ يُحَاوِرُهٗٓ اَنَا۠ اَكْثَرُ مِنْكَ مَالًا وَّاَعَزُّ نَفَرًا', 'wa kāna lahụ ṡamar, fa qāla liṣāḥibihī wa huwa yuḥāwiruhū ana akṡaru mingka mālaw wa a\'azzu nafarā'),
(2174, 18, 35, 'وَدَخَلَ جَنَّتَهٗ وَهُوَ ظَالِمٌ لِّنَفْسِهٖۚ قَالَ مَآ اَظُنُّ اَنْ تَبِيْدَ هٰذِهٖٓ اَبَدًاۙ', 'wa dakhala jannatahụ wa huwa ẓālimul linafsih, qāla mā aẓunnu an tabīda hāżihī abadā'),
(2175, 18, 36, 'وَّمَآ اَظُنُّ السَّاعَةَ قَاۤىِٕمَةً وَّلَىِٕنْ رُّدِدْتُّ اِلٰى رَبِّيْ لَاَجِدَنَّ خَيْرًا مِّنْهَا مُنْقَلَبًا', 'wa mā aẓunnus-sā\'ata qā`imataw wa la`ir rudittu ilā rabbī la`ajidanna khairam min-hā mungqalabā'),
(2176, 18, 37, 'قَالَ لَهٗ صَاحِبُهٗ وَهُوَ يُحَاوِرُهٗٓ اَكَفَرْتَ بِالَّذِيْ خَلَقَكَ مِنْ تُرَابٍ ثُمَّ مِنْ نُّطْفَةٍ ثُمَّ سَوّٰىكَ رَجُلًاۗ', 'qāla lahụ ṣāḥibuhụ wa huwa yuḥāwiruhū a kafarta billażī khalaqaka min turābin ṡumma min nuṭfatin ṡumma sawwāka rajulā'),
(2177, 18, 38, 'لٰكِنَّا۠ هُوَ اللّٰهُ رَبِّيْ وَلَآ اُشْرِكُ بِرَبِّيْٓ اَحَدًا', 'lākinna huwallāhu rabbī wa lā usyriku birabbī aḥadā'),
(2178, 18, 39, 'وَلَوْلَآ اِذْ دَخَلْتَ جَنَّتَكَ قُلْتَ مَا شَاۤءَ اللّٰهُ ۙ لَا قُوَّةَ اِلَّا بِاللّٰهِ ۚاِنْ تَرَنِ اَنَا۠ اَقَلَّ مِنْكَ مَالًا وَّوَلَدًاۚ', 'walau lā iż dakhalta jannataka qulta mā syā`allāhu lā quwwata illā billāh, in tarani ana aqalla mingka mālaw wa waladā'),
(2179, 18, 40, 'فَعَسٰى رَبِّيْٓ اَنْ يُّؤْتِيَنِ خَيْرًا مِّنْ جَنَّتِكَ وَيُرْسِلَ عَلَيْهَا حُسْبَانًا مِّنَ السَّمَاۤءِ فَتُصْبِحَ صَعِيْدًا زَلَقًاۙ', 'fa asā rabbī ay yu`tiyani khairam min jannatika wa yursila \'alaihā ḥusbānam minas-samā`i fa tuṣbiḥa ṣa\'īdan zalaqā'),
(2180, 18, 41, 'اَوْ يُصْبِحَ مَاۤؤُهَا غَوْرًا فَلَنْ تَسْتَطِيْعَ لَهٗ طَلَبًا', 'au yuṣbiḥa mā`uhā gauran fa lan tastaṭī\'a lahụ ṭalabā'),
(2181, 18, 42, 'وَاُحِيْطَ بِثَمَرِهٖ فَاَصْبَحَ يُقَلِّبُ كَفَّيْهِ عَلٰى مَآ اَنْفَقَ فِيْهَا وَهِيَ خَاوِيَةٌ عَلٰى عُرُوْشِهَا وَيَقُوْلُ يٰلَيْتَنِيْ لَمْ اُشْرِكْ بِرَبِّيْٓ اَحَدًا', 'wa uḥīṭa biṡamarihī fa aṣbaḥa yuqallibu kaffaihi \'alā mā anfaqa fīhā wa hiya khāwiyatun \'alā \'urụsyihā wa yaqụlu yā laitanī lam usyrik birabbī aḥadā'),
(2182, 18, 43, 'وَلَمْ تَكُنْ لَّهٗ فِئَةٌ يَّنْصُرُوْنَهٗ مِنْ دُوْنِ اللّٰهِ وَمَا كَانَ مُنْتَصِرًاۗ', 'wa lam takul lahụ fi`atuy yanṣurụnahụ min dụnillāhi wa mā kāna muntaṣirā'),
(2183, 18, 44, 'هُنَالِكَ الْوَلَايَةُ لِلّٰهِ الْحَقِّۗ هُوَ خَيْرٌ ثَوَابًا وَّخَيْرٌ عُقْبًا', 'hunālikal-walāyatu lillāhil-ḥaqq, huwa khairun ṡawābaw wa khairun \'uqbā'),
(2184, 18, 45, 'وَاضْرِبْ لَهُمْ مَّثَلَ الْحَيٰوةِ الدُّنْيَا كَمَاۤءٍ اَنْزَلْنٰهُ مِنَ السَّمَاۤءِ فَاخْتَلَطَ بِهٖ نَبَاتُ الْاَرْضِ فَاَصْبَحَ هَشِيْمًا تَذْرُوْهُ الرِّيٰحُ ۗوَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ مُّقْتَدِرًا', 'waḍrib lahum maṡalal-ḥayātid-dun-yā kamā`in anzalnāhu minas-samā`i fakhtalaṭa bihī nabātul-arḍi fa aṣbaḥa hasyīman tażrụhur-riyāḥ, wa kānallāhu \'alā kulli syai`im muqtadirā'),
(2185, 18, 46, 'اَلْمَالُ وَالْبَنُوْنَ زِيْنَةُ الْحَيٰوةِ الدُّنْيَاۚ وَالْبٰقِيٰتُ الصّٰلِحٰتُ خَيْرٌ عِنْدَ رَبِّكَ ثَوَابًا وَّخَيْرٌ اَمَلًا', 'al-mālu wal-banụna zīnatul-ḥayātid-dun-yā, wal-bāqiyātuṣ-ṣāliḥātu khairun \'inda rabbika ṡawābaw wa khairun amalā'),
(2186, 18, 47, 'وَيَوْمَ نُسَيِّرُ الْجِبَالَ وَتَرَى الْاَرْضَ بَارِزَةًۙ وَّحَشَرْنٰهُمْ فَلَمْ نُغَادِرْ مِنْهُمْ اَحَدًاۚ', 'wa yauma nusayyirul-jibāla wa taral-arḍa bārizataw wa ḥasyarnāhum fa lam nugādir min-hum aḥadā'),
(2187, 18, 48, 'وَعُرِضُوْا عَلٰى رَبِّكَ صَفًّاۗ لَقَدْ جِئْتُمُوْنَا كَمَا خَلَقْنٰكُمْ اَوَّلَ مَرَّةٍۢ ۖبَلْ زَعَمْتُمْ اَلَّنْ نَّجْعَلَ لَكُمْ مَّوْعِدًا', 'wa \'uriḍụ \'alā rabbika ṣaffā, laqad ji`tumụnā kamā khalaqnākum awwala marratim bal za\'amtum allan naj\'ala lakum mau\'idā'),
(2188, 18, 49, 'وَوُضِعَ الْكِتٰبُ فَتَرَى الْمُجْرِمِيْنَ مُشْفِقِيْنَ مِمَّا فِيْهِ وَيَقُوْلُوْنَ يٰوَيْلَتَنَا مَالِ هٰذَا الْكِتٰبِ لَا يُغَادِرُ صَغِيْرَةً وَّلَا كَبِيْرَةً اِلَّآ اَحْصٰىهَاۚ وَوَجَدُوْا مَا عَمِلُوْا حَاضِرًاۗ وَلَا يَظْلِمُ رَبُّكَ اَحَدًا', 'wa wuḍi\'al kitābu fa taral-mujrimīna musyfiqīna mimmā fīhi wa yaqụlụna yā wailatanā māli hāżal-kitābi lā yugādiru ṣagīrataw wa lā kabīratan illā aḥṣāhā, wa wajadụ mā \'amilụ ḥāḍirā, wa lā yaẓlimu rabbuka aḥadā'),
(2189, 18, 50, 'وَاِذْ قُلْنَا لِلْمَلٰۤىِٕكَةِ اسْجُدُوْا لِاٰدَمَ فَسَجَدُوْٓا اِلَّآ اِبْلِيْسَۗ كَانَ مِنَ الْجِنِّ فَفَسَقَ عَنْ اَمْرِ رَبِّهٖۗ اَفَتَتَّخِذُوْنَهٗ وَذُرِّيَّتَهٗٓ اَوْلِيَاۤءَ مِنْ دُوْنِيْ وَهُمْ لَكُمْ عَدُوٌّۗ بِئْسَ لِلظّٰلِمِيْنَ بَدَلًا', 'wa iż qulnā lil-malā`ikatisjudụ li`ādama fa sajadū illā iblīs, kāna minal-jinni fa fasaqa \'an amri rabbih, a fa tattakhiżụnahụ wa żurriyyatahū auliyā`a min dụnī wa hum lakum \'aduww, bi`sa liẓ-ẓālimīna badalā'),
(2190, 18, 51, '۞ مَآ اَشْهَدْتُّهُمْ خَلْقَ السَّمٰوٰتِ وَالْاَرْضِ وَلَا خَلْقَ اَنْفُسِهِمْۖ وَمَا كُنْتُ مُتَّخِذَ الْمُضِلِّيْنَ عَضُدًا', 'mā asy-hattuhum khalqas-samāwāti wal-arḍi wa lā khalqa anfusihim wa mā kuntu muttakhiżal-muḍillīna \'aḍudā'),
(2191, 18, 52, 'وَيَوْمَ يَقُوْلُ نَادُوْا شُرَكَاۤءِيَ الَّذِيْنَ زَعَمْتُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَجِيْبُوْا لَهُمْ وَجَعَلْنَا بَيْنَهُمْ مَّوْبِقًا', 'wa yauma yaqụlu nādụ syurakā`iyallażīna za\'amtum fa da\'auhum fa lam yastajībụ lahum wa ja\'alnā bainahum maubiqā'),
(2192, 18, 53, 'وَرَاَ الْمُجْرِمُوْنَ النَّارَ فَظَنُّوْٓا اَنَّهُمْ مُّوَاقِعُوْهَا وَلَمْ يَجِدُوْا عَنْهَا مَصْرِفًا', 'wa ra`al-mujrimụnan-nāra fa ẓannū annahum muwāqi\'ụhā wa lam yajidụ \'an-hā maṣrifā'),
(2193, 18, 54, 'وَلَقَدْ صَرَّفْنَا فِيْ هٰذَا الْقُرْاٰنِ لِلنَّاسِ مِنْ كُلِّ مَثَلٍۗ وَكَانَ الْاِنْسَانُ اَكْثَرَ شَيْءٍ جَدَلًا', 'wa laqad ṣarrafnā fī hāżal-qur`āni lin-nāsi ming kulli maṡal, wa kānal-insānu akṡara syai`in jadalā'),
(2194, 18, 55, 'وَمَا مَنَعَ النَّاسَ اَنْ يُّؤْمِنُوْٓا اِذْ جَاۤءَهُمُ الْهُدٰى وَيَسْتَغْفِرُوْا رَبَّهُمْ اِلَّآ اَنْ تَأْتِيَهُمْ سُنَّةُ الْاَوَّلِيْنَ اَوْ يَأْتِيَهُمُ الْعَذَابُ قُبُلًا', 'wa mā mana\'an-nāsa ay yu`minū iż jā`ahumul-hudā wa yastagfirụ rabbahum illā an ta`tiyahum sunnatul-awwalīna au ya`tiyahumul-\'ażābu qubulā'),
(2195, 18, 56, 'وَمَا نُرْسِلُ الْمُرْسَلِيْنَ اِلَّا مُبَشِّرِيْنَ وَمُنْذِرِيْنَۚ وَيُجَادِلُ الَّذِيْنَ كَفَرُوْا بِالْبَاطِلِ لِيُدْحِضُوْا بِهِ الْحَقَّ وَاتَّخَذُوْٓا اٰيٰتِيْ وَمَآ اُنْذِرُوْا هُزُوًا', 'wa mā nursilul-mursalīna illā mubasysyirīna wa munżirīn, wa yujādilullażīna kafarụ bil-bāṭili liyud-ḥiḍụ bihil-ḥaqqa wattakhażū āyātī wa mā unżirụ huzuwā'),
(2196, 18, 57, 'وَمَنْ اَظْلَمُ مِمَّنْ ذُكِّرَ بِاٰيٰتِ رَبِّهٖ فَاَعْرَضَ عَنْهَا وَنَسِيَ مَا قَدَّمَتْ يَدَاهُۗ اِنَّا جَعَلْنَا عَلٰى قُلُوْبِهِمْ اَكِنَّةً اَنْ يَّفْقَهُوْهُ وَفِيْٓ اٰذَانِهِمْ وَقْرًاۗ وَاِنْ تَدْعُهُمْ اِلَى الْهُدٰى فَلَنْ يَّهْتَدُوْٓا اِذًا اَبَدًا', 'wa man aẓlamu mim man żukkira bi`āyāti rabbihī fa a\'raḍa \'an-hā wa nasiya mā qaddamat yadāh, innā ja\'alnā \'alā qulụbihim akinnatan ay yafqahụhu wa fī āżānihim waqrā, wa in tad\'uhum ilal-hudā fa lay yahtadū iżan abadā'),
(2197, 18, 58, 'وَرَبُّكَ الْغَفُوْرُ ذُو الرَّحْمَةِۗ لَوْ يُؤَاخِذُهُمْ بِمَا كَسَبُوْا لَعَجَّلَ لَهُمُ الْعَذَابَۗ بَلْ لَّهُمْ مَّوْعِدٌ لَّنْ يَّجِدُوْا مِنْ دُوْنِهٖ مَوْىِٕلًا', 'wa rabbukal-gafụru żur-raḥmah, lau yu`ākhiżuhum bimā kasabụ la\'ajjala lahumul-\'ażāb, bal lahum mau\'idul lay yajidụ min dụnihī mau`ilā'),
(2198, 18, 59, 'وَتِلْكَ الْقُرٰٓى اَهْلَكْنٰهُمْ لَمَّا ظَلَمُوْا وَجَعَلْنَا لِمَهْلِكِهِمْ مَّوْعِدًا', 'wa tilkal-qurā ahlaknāhum lammā ẓalamụ wa ja\'alnā limahlikihim mau\'idā'),
(2199, 18, 60, 'وَاِذْ قَالَ مُوْسٰى لِفَتٰىهُ لَآ اَبْرَحُ حَتّٰٓى اَبْلُغَ مَجْمَعَ الْبَحْرَيْنِ اَوْ اَمْضِيَ حُقُبًا', 'wa iż qāla mụsā lifatāhu lā abraḥu ḥattā abluga majma\'al-baḥraini au amḍiya ḥuqubā'),
(2200, 18, 61, 'فَلَمَّا بَلَغَا مَجْمَعَ بَيْنِهِمَا نَسِيَا حُوْتَهُمَا فَاتَّخَذَ سَبِيْلَهٗ فِى الْبَحْرِ سَرَبًا', 'fa lammā balagā majma\'a bainihimā nasiyā ḥụtahumā fattakhaża sabīlahụ fil-baḥri sarabā'),
(2201, 18, 62, 'فَلَمَّا جَاوَزَا قَالَ لِفَتٰىهُ اٰتِنَا غَدَاۤءَنَاۖ لَقَدْ لَقِيْنَا مِنْ سَفَرِنَا هٰذَا نَصَبًا', 'fa lammā jāwazā qāla lifatāhu ātinā gadā`anā laqad laqīnā min safarinā hāżā naṣabā'),
(2202, 18, 63, 'قَالَ اَرَاَيْتَ اِذْ اَوَيْنَآ اِلَى الصَّخْرَةِ فَاِنِّيْ نَسِيْتُ الْحُوْتَۖ وَمَآ اَنْسٰىنِيْهُ اِلَّا الشَّيْطٰنُ اَنْ اَذْكُرَهٗۚ وَاتَّخَذَ سَبِيْلَهٗ فِى الْبَحْرِ عَجَبًا', 'qāla a ra`aita iż awainā ilaṣ-ṣakhrati fa innī nasītul-ḥụta wa mā ansānīhu illasy-syaiṭānu an ażkurah, wattakhaża sabīlahụ fil-baḥri \'ajabā'),
(2203, 18, 64, 'قَالَ ذٰلِكَ مَا كُنَّا نَبْغِۖ فَارْتَدَّا عَلٰٓى اٰثَارِهِمَا قَصَصًاۙ', 'qāla żālika mā kunnā nabgi fartaddā \'alā āṡārihimā qaṣaṣā'),
(2204, 18, 65, 'فَوَجَدَا عَبْدًا مِّنْ عِبَادِنَآ اٰتَيْنٰهُ رَحْمَةً مِّنْ عِنْدِنَا وَعَلَّمْنٰهُ مِنْ لَّدُنَّا عِلْمًا', 'fa wajadā \'abdam min \'ibādinā ātaināhu raḥmatam min \'indinā wa \'allamnāhu mil ladunnā \'ilmā'),
(2205, 18, 66, 'قَالَ لَهٗ مُوسٰى هَلْ اَتَّبِعُكَ عَلٰٓى اَنْ تُعَلِّمَنِ مِمَّا عُلِّمْتَ رُشْدًا', 'qāla lahụ mụsā hal attabi\'uka \'alā an tu\'allimani mimmā \'ullimta rusydā'),
(2206, 18, 67, 'قَالَ اِنَّكَ لَنْ تَسْتَطِيْعَ مَعِيَ صَبْرًا', 'qāla innaka lan tastaṭī\'a ma\'iya ṣabrā'),
(2207, 18, 68, 'وَكَيْفَ تَصْبِرُ عَلٰى مَا لَمْ تُحِطْ بِهٖ خُبْرًا', 'wa kaifa taṣbiru \'alā mā lam tuḥiṭ bihī khubrā'),
(2208, 18, 69, 'قَالَ سَتَجِدُنِيْٓ اِنْ شَاۤءَ اللّٰهُ صَابِرًا وَّلَآ اَعْصِيْ لَكَ اَمْرًا', 'qāla satajidunī in syā`allāhu ṣābiraw wa lā a\'ṣī laka amrā'),
(2209, 18, 70, 'قَالَ فَاِنِ اتَّبَعْتَنِيْ فَلَا تَسْـَٔلْنِيْ عَنْ شَيْءٍ حَتّٰٓى اُحْدِثَ لَكَ مِنْهُ ذِكْرًا', 'qāla fa inittaba\'tanī fa lā tas`alnī \'an syai`in ḥattā uḥdiṡa laka min-hu żikrā'),
(2210, 18, 71, 'فَانْطَلَقَاۗ حَتّٰٓى اِذَا رَكِبَا فِى السَّفِيْنَةِ خَرَقَهَاۗ قَالَ اَخَرَقْتَهَا لِتُغْرِقَ اَهْلَهَاۚ لَقَدْ جِئْتَ شَيْـًٔا اِمْرًا', 'fanṭalaqā, ḥattā iżā rakibā fis-safīnati kharaqahā, qāla a kharaqtahā litugriqa ahlahā, laqad ji`ta syai`an imrā'),
(2211, 18, 72, 'قَالَ اَلَمْ اَقُلْ اِنَّكَ لَنْ تَسْتَطِيْعَ مَعِيَ صَبْرًا', 'qāla a lam aqul innaka lan tastaṭī\'a ma\'iya ṣabrā'),
(2212, 18, 73, 'قَالَ لَا تُؤَاخِذْنِيْ بِمَا نَسِيْتُ وَلَا تُرْهِقْنِيْ مِنْ اَمْرِيْ عُسْرًا', 'qāla lā tu`ākhiżnī bimā nasītu wa lā tur-hiqnī min amrī \'usrā'),
(2213, 18, 74, 'فَانْطَلَقَا ۗحَتّٰٓى اِذَا لَقِيَا غُلٰمًا فَقَتَلَهٗ ۙقَالَ اَقَتَلْتَ نَفْسًا زَكِيَّةً؈ۢبِغَيْرِ نَفْسٍۗ لَقَدْ جِئْتَ شَيْـًٔا نُكْرًا ۔', 'fanṭalaqā, ḥattā iżā laqiyā gulāman fa qatalahụ qāla a qatalta nafsan zakiyyatam bigairi nafs, laqad ji`ta syai`an nukrā'),
(2214, 18, 75, '۞ قَالَ اَلَمْ اَقُلْ لَّكَ اِنَّكَ لَنْ تَسْتَطِيْعَ مَعِيَ صَبْرًا', 'qāla a lam aqul laka innaka lan tastaṭī\'a ma\'iya ṣabrā'),
(2215, 18, 76, 'قَالَ اِنْ سَاَلْتُكَ عَنْ شَيْءٍۢ بَعْدَهَا فَلَا تُصٰحِبْنِيْۚ قَدْ بَلَغْتَ مِنْ لَّدُنِّيْ عُذْرًا', 'qāla in sa`altuka \'an syai`im ba\'dahā fa lā tuṣāḥibnī, qad balagta mil ladunnī \'użrā');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(2216, 18, 77, 'فَانْطَلَقَا ۗحَتّٰىٓ اِذَآ اَتَيَآ اَهْلَ قَرْيَةِ ِۨاسْتَطْعَمَآ اَهْلَهَا فَاَبَوْا اَنْ يُّضَيِّفُوْهُمَا فَوَجَدَا فِيْهَا جِدَارًا يُّرِيْدُ اَنْ يَّنْقَضَّ فَاَقَامَهٗ ۗقَالَ لَوْ شِئْتَ لَتَّخَذْتَ عَلَيْهِ اَجْرًا', 'fanṭalaqā, ḥattā iżā atayā ahla qaryatinistaṭ\'amā ahlahā fa abau ay yuḍayyifụhumā fa wajadā fīhā jidāray yurīdu ay yangqaḍḍa fa aqāmah, qāla lau syi`ta lattakhażta \'alaihi ajrā'),
(2217, 18, 78, 'قَالَ هٰذَا فِرَاقُ بَيْنِيْ وَبَيْنِكَۚ سَاُنَبِّئُكَ بِتَأْوِيْلِ مَا لَمْ تَسْتَطِعْ عَّلَيْهِ صَبْرًا', 'qāla hāżā firāqu bainī wa bainik, sa`unabbi`uka bita`wīli mā lam tastaṭi\' \'alaihi ṣabrā'),
(2218, 18, 79, 'اَمَّا السَّفِيْنَةُ فَكَانَتْ لِمَسٰكِيْنَ يَعْمَلُوْنَ فِى الْبَحْرِ فَاَرَدْتُّ اَنْ اَعِيْبَهَاۗ وَكَانَ وَرَاۤءَهُمْ مَّلِكٌ يَّأْخُذُ كُلَّ سَفِيْنَةٍ غَصْبًا', 'ammas-safīnatu fa kānat limasākīna ya\'malụna fil-baḥri fa arattu an a\'ībahā, wa kāna warā`ahum malikuy ya`khużu kulla safīnatin gaṣbā'),
(2219, 18, 80, 'وَاَمَّا الْغُلٰمُ فَكَانَ اَبَوَاهُ مُؤْمِنَيْنِ فَخَشِيْنَآ اَنْ يُّرْهِقَهُمَا طُغْيَانًا وَّكُفْرًا ۚ', 'wa ammal-gulāmu fa kāna abawāhu mu`minaini fa khasyīnā ay yur-hiqahumā ṭugyānaw wa kufrā'),
(2220, 18, 81, 'فَاَرَدْنَآ اَنْ يُّبْدِلَهُمَا رَبُّهُمَا خَيْرًا مِّنْهُ زَكٰوةً وَّاَقْرَبَ رُحْمًا', 'fa aradnā ay yubdilahumā rabbuhumā khairam min-hu zakātaw wa aqraba ruḥmā'),
(2221, 18, 82, 'وَاَمَّا الْجِدَارُ فَكَانَ لِغُلٰمَيْنِ يَتِيْمَيْنِ فِى الْمَدِيْنَةِ وَكَانَ تَحْتَهٗ كَنْزٌ لَّهُمَا وَكَانَ اَبُوْهُمَا صَالِحًا ۚفَاَرَادَ رَبُّكَ اَنْ يَّبْلُغَآ اَشُدَّهُمَا وَيَسْتَخْرِجَا كَنْزَهُمَا رَحْمَةً مِّنْ رَّبِّكَۚ وَمَا فَعَلْتُهٗ عَنْ اَمْرِيْۗ ذٰلِكَ تَأْوِيْلُ مَا لَمْ تَسْطِعْ عَّلَيْهِ صَبْرًاۗ', 'wa ammal-jidāru fa kāna ligulāmaini yatīmaini fil-madīnati wa kāna taḥtahụ kanzul lahumā wa kāna abụhumā ṣāliḥā, fa arāda rabbuka ay yablugā asyuddahumā wa yastakhrijā kanzahumā raḥmatam mir rabbik, wa mā fa\'altuhụ \'an amrī, żālika ta`wīlu mā lam tasṭi\' \'alaihi ṣabrā'),
(2222, 18, 83, 'وَيَسْـَٔلُوْنَكَ عَنْ ذِى الْقَرْنَيْنِۗ قُلْ سَاَتْلُوْا عَلَيْكُمْ مِّنْهُ ذِكْرًا ۗ', 'wa yas`alụnaka \'an żil-qarnaīn, qul sa`atlụ \'alaikum min-hu żikrā'),
(2223, 18, 84, 'اِنَّا مَكَّنَّا لَهٗ فِى الْاَرْضِ وَاٰتَيْنٰهُ مِنْ كُلِّ شَيْءٍ سَبَبًا ۙ', 'innā makkannā lahụ fil-arḍi wa ātaināhu ming kulli syai`in sababā'),
(2224, 18, 85, 'فَاَتْبَعَ سَبَبًا', 'fa atba\'a sababā'),
(2225, 18, 86, 'حَتّٰىٓ اِذَا بَلَغَ مَغْرِبَ الشَّمْسِ وَجَدَهَا تَغْرُبُ فِيْ عَيْنٍ حَمِئَةٍ وَّوَجَدَ عِنْدَهَا قَوْمًا ەۗ قُلْنَا يٰذَا الْقَرْنَيْنِ اِمَّآ اَنْ تُعَذِّبَ وَاِمَّآ اَنْ تَتَّخِذَ فِيْهِمْ حُسْنًا', 'ḥattā iżā balaga magribasy-syamsi wajadahā tagrubu fī \'ainin ḥami`atiw wa wajada \'indahā qaumā, qulnā yā żal-qarnaini immā an tu\'ażżiba wa immā an tattakhiża fīhim ḥusnā'),
(2226, 18, 87, 'قَالَ اَمَّا مَنْ ظَلَمَ فَسَوْفَ نُعَذِّبُهٗ ثُمَّ يُرَدُّ اِلٰى رَبِّهٖ فَيُعَذِّبُهٗ عَذَابًا نُّكْرًا', 'qāla ammā man ẓalama fa saufa nu\'ażżibuhụ ṡumma yuraddu ilā rabbihī fa yu\'ażżibuhụ \'ażāban nukrā'),
(2227, 18, 88, 'وَاَمَّا مَنْ اٰمَنَ وَعَمِلَ صَالِحًا فَلَهٗ جَزَاۤءً ۨالْحُسْنٰىۚ وَسَنَقُوْلُ لَهٗ مِنْ اَمْرِنَا يُسْرًا ۗ', 'wa ammā man āmana wa \'amila ṣāliḥan fa lahụ jazā`anil-ḥusnā, wa sanaqụlu lahụ min amrinā yusrā'),
(2228, 18, 89, 'ثُمَّ اَتْبَعَ سَبَبًا', 'ṡumma atba\'a sababā'),
(2229, 18, 90, 'حَتّٰىٓ اِذَا بَلَغَ مَطْلِعَ الشَّمْسِ وَجَدَهَا تَطْلُعُ عَلٰى قَوْمٍ لَّمْ نَجْعَلْ لَّهُمْ مِّنْ دُوْنِهَا سِتْرًا ۙ', 'ḥattā iżā balaga maṭli\'asy-syamsi wajadahā taṭlu\'u \'alā qaumil lam naj\'al lahum min dụnihā sitrā'),
(2230, 18, 91, 'كَذٰلِكَۗ وَقَدْ اَحَطْنَا بِمَا لَدَيْهِ خُبْرًا', 'każālik, wa qad aḥaṭnā bimā ladaihi khubrā'),
(2231, 18, 92, 'ثُمَّ اَتْبَعَ سَبَبًا', 'ṡumma atba\'a sababā'),
(2232, 18, 93, 'حَتّٰىٓ اِذَا بَلَغَ بَيْنَ السَّدَّيْنِ وَجَدَ مِنْ دُوْنِهِمَا قَوْمًاۙ لَّا يَكَادُوْنَ يَفْقَهُوْنَ قَوْلًا', 'ḥattā iżā balaga bainas-saddaini wajada min dụnihimā qaumal lā yakādụna yafqahụna qaulā'),
(2233, 18, 94, 'قَالُوْا يٰذَا الْقَرْنَيْنِ اِنَّ يَأْجُوْجَ وَمَأْجُوْجَ مُفْسِدُوْنَ فِى الْاَرْضِ فَهَلْ نَجْعَلُ لَكَ خَرْجًا عَلٰٓى اَنْ تَجْعَلَ بَيْنَنَا وَبَيْنَهُمْ سَدًّا', 'qālụ yā żal-qarnaini inna ya`jụja wa ma`jụja mufsidụna fil-arḍi fa hal naj\'alu laka kharjan \'alā an taj\'ala bainanā wa bainahum saddā'),
(2234, 18, 95, 'قَالَ مَا مَكَّنِّيْ فِيْهِ رَبِّيْ خَيْرٌ فَاَعِيْنُوْنِيْ بِقُوَّةٍ اَجْعَلْ بَيْنَكُمْ وَبَيْنَهُمْ رَدْمًا ۙ', 'qāla mā makkannī fīhi rabbī khairun fa a\'īnụnī biquwwatin aj\'al bainakum wa bainahum radmā'),
(2235, 18, 96, 'اٰتُوْنِيْ زُبَرَ الْحَدِيْدِۗ حَتّٰىٓ اِذَا سَاوٰى بَيْنَ الصَّدَفَيْنِ قَالَ انْفُخُوْا ۗحَتّٰىٓ اِذَا جَعَلَهٗ نَارًاۙ قَالَ اٰتُوْنِيْٓ اُفْرِغْ عَلَيْهِ قِطْرًا ۗ', 'ātụnī zubaral-ḥadīd, ḥattā iżā sāwā bainaṣ-ṣadafaini qālanfukhụ, ḥattā iżā ja\'alahụ nārang qāla ātụnī ufrig \'alaihi qiṭrā'),
(2236, 18, 97, 'فَمَا اسْطَاعُوْٓا اَنْ يَّظْهَرُوْهُ وَمَا اسْتَطَاعُوْا لَهٗ نَقْبًا', 'fa masṭā\'ū ay yaẓ-harụhu wa mastaṭā\'ụ lahụ naqbā'),
(2237, 18, 98, 'قَالَ هٰذَا رَحْمَةٌ مِّنْ رَّبِّيْۚ فَاِذَا جَاۤءَ وَعْدُ رَبِّيْ جَعَلَهٗ دَكَّاۤءَۚ وَكَانَ وَعْدُ رَبِّيْ حَقًّا ۗ', 'qāla hāżā raḥmatum mir rabbī, fa iżā jā`a wa\'du rabbī ja\'alahụ dakkā`, wa kāna wa\'du rabbī ḥaqqā'),
(2238, 18, 99, '۞ وَتَرَكْنَا بَعْضَهُمْ يَوْمَىِٕذٍ يَّمُوْجُ فِيْ بَعْضٍ وَّنُفِخَ فِى الصُّوْرِ فَجَمَعْنٰهُمْ جَمْعًا ۙ', 'wa taraknā ba\'ḍahum yauma`iżiy yamụju fī ba\'ḍiw wa nufikha fiṣ-ṣụri fa jama\'nāhum jam\'ā'),
(2239, 18, 100, 'وَّعَرَضْنَا جَهَنَّمَ يَوْمَىِٕذٍ لِّلْكٰفِرِيْنَ عَرْضًا ۙ', 'wa \'araḍnā jahannama yauma`iżil lil-kāfirīna \'arḍā'),
(2240, 18, 101, ' ۨالَّذِيْنَ كَانَتْ اَعْيُنُهُمْ فِيْ غِطَاۤءٍ عَنْ ذِكْرِيْ وَكَانُوْا لَا يَسْتَطِيْعُوْنَ سَمْعًا', 'allażīna kānat a\'yunuhum fī giṭā`in \'an żikrī wa kānụ lā yastaṭī\'ụna sam\'ā'),
(2241, 18, 102, 'اَفَحَسِبَ الَّذِيْنَ كَفَرُوْٓا اَنْ يَّتَّخِذُوْا عِبَادِيْ مِنْ دُوْنِيْٓ اَوْلِيَاۤءَ ۗاِنَّآ اَعْتَدْنَا جَهَنَّمَ لِلْكٰفِرِيْنَ نُزُلًا', 'a fa ḥasiballażīna kafarū ay yattakhiżụ \'ibādī min dụnī auliyā`, innā a\'tadnā jahannama lil-kāfirīna nuzulā'),
(2242, 18, 103, 'قُلْ هَلْ نُنَبِّئُكُمْ بِالْاَخْسَرِيْنَ اَعْمَالًا ۗ', 'qul hal nunabbi`ukum bil-akhsarīna a\'mālā'),
(2243, 18, 104, 'اَلَّذِيْنَ ضَلَّ سَعْيُهُمْ فِى الْحَيٰوةِ الدُّنْيَا وَهُمْ يَحْسَبُوْنَ اَنَّهُمْ يُحْسِنُوْنَ صُنْعًا', 'allażīna ḍalla sa\'yuhum fil-ḥayātid-dun-yā wa hum yaḥsabụna annahum yuḥsinụna ṣun\'ā'),
(2244, 18, 105, 'اُولٰۤىِٕكَ الَّذِيْنَ كَفَرُوْا بِاٰيٰتِ رَبِّهِمْ وَلِقَاۤىِٕهٖ فَحَبِطَتْ اَعْمَالُهُمْ فَلَا نُقِيْمُ لَهُمْ يَوْمَ الْقِيٰمَةِ وَزْنًا', 'ulā`ikallażīna kafarụ bi`āyāti rabbihim wa liqā`ihī fa ḥabiṭat a\'māluhum fa lā nuqīmu lahum yaumal-qiyāmati waznā'),
(2245, 18, 106, 'ذٰلِكَ جَزَاۤؤُهُمْ جَهَنَّمُ بِمَا كَفَرُوْا وَاتَّخَذُوْٓا اٰيٰتِيْ وَرُسُلِيْ هُزُوًا', 'żālika jazā`uhum jahannamu bimā kafarụ wattakhażū āyātī wa rusulī huzuwā'),
(2246, 18, 107, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ كَانَتْ لَهُمْ جَنّٰتُ الْفِرْدَوْسِ نُزُلًا ۙ', 'innallażīna āmanụ wa \'amiluṣ-ṣāliḥāti kānat lahum jannātul-firdausi nuzulā'),
(2247, 18, 108, 'خٰلِدِيْنَ فِيْهَا لَا يَبْغُوْنَ عَنْهَا حِوَلًا', 'khālidīna fīhā lā yabgụna \'an-hā ḥiwalā'),
(2248, 18, 109, 'قُلْ لَّوْ كَانَ الْبَحْرُ مِدَادًا لِّكَلِمٰتِ رَبِّيْ لَنَفِدَ الْبَحْرُ قَبْلَ اَنْ تَنْفَدَ كَلِمٰتُ رَبِّيْ وَلَوْ جِئْنَا بِمِثْلِهٖ مَدَدًا', 'qul lau kānal-baḥru midādal likalimāti rabbī lanafidal-baḥru qabla an tanfada kalimātu rabbī walau ji`nā bimiṡlihī madadā'),
(2249, 18, 110, 'قُلْ اِنَّمَآ اَنَا۠ بَشَرٌ مِّثْلُكُمْ يُوْحٰٓى اِلَيَّ اَنَّمَآ اِلٰهُكُمْ اِلٰهٌ وَّاحِدٌۚ فَمَنْ كَانَ يَرْجُوْا لِقَاۤءَ رَبِّهٖ فَلْيَعْمَلْ عَمَلًا صَالِحًا وَّلَا يُشْرِكْ بِعِبَادَةِ رَبِّهٖٓ اَحَدًا', 'qul innamā ana basyarum miṡlukum yụḥā ilayya annamā ilāhukum ilāhuw wāḥid, fa mang kāna yarjụ liqā`a rabbihī falya\'mal \'amalan ṣāliḥaw wa lā yusyrik bi\'ibādati rabbihī aḥadā'),
(2250, 19, 1, 'كۤهٰيٰعۤصۤ ۚ', 'kaf hā yā \'aīn ṣad'),
(2251, 19, 2, 'ذِكْرُ رَحْمَتِ رَبِّكَ عَبْدَهٗ زَكَرِيَّا ۚ', 'żikru raḥmati rabbika \'abdahu zakariyyā'),
(2252, 19, 3, 'اِذْ نَادٰى رَبَّهٗ نِدَاۤءً خَفِيًّا', 'iż nādā rabbahu nida`an khafiyyā'),
(2253, 19, 4, 'قَالَ رَبِّ اِنِّيْ وَهَنَ الْعَظْمُ مِنِّيْ وَاشْتَعَلَ الرَّأْسُ شَيْبًا وَّلَمْ اَكُنْۢ بِدُعَاۤىِٕكَ رَبِّ شَقِيًّا', 'qāla rabbi innī wahanal-\'aẓmu minnī wasyta\'alar-ra`su syaibaw wa lam akum bidu\'a`ika rabbi syaqiyyā'),
(2254, 19, 5, 'وَاِنِّيْ خِفْتُ الْمَوَالِيَ مِنْ وَّرَاۤءِيْ وَكَانَتِ امْرَاَتِيْ عَاقِرًا فَهَبْ لِيْ مِنْ لَّدُنْكَ وَلِيًّا ۙ', 'wa innī khiftul-mawāliya miw wara`ī wa kānatimra`atī \'āqiran fa hab lī mil ladungka waliyyā'),
(2255, 19, 6, 'يَّرِثُنِيْ وَيَرِثُ مِنْ اٰلِ يَعْقُوْبَ وَاجْعَلْهُ رَبِّ رَضِيًّا', 'yariṡunī wa yariṡu min āli ya\'quba waj\'al-hu rabbi raḍiyyā'),
(2256, 19, 7, 'يٰزَكَرِيَّآ اِنَّا نُبَشِّرُكَ بِغُلٰمِ ِۨاسْمُهٗ يَحْيٰىۙ لَمْ نَجْعَلْ لَّهٗ مِنْ قَبْلُ سَمِيًّا', 'yā zakariyya innā nubasysyiruka bigulāminismuhu yaḥyā lam naj\'al lahu ming qablu samiyyā'),
(2257, 19, 8, 'قَالَ رَبِّ اَنّٰى يَكُوْنُ لِيْ غُلٰمٌ وَّكَانَتِ امْرَاَتِيْ عَاقِرًا وَّقَدْ بَلَغْتُ مِنَ الْكِبَرِ عِتِيًّا', 'qāla rabbi annā yakunu lī gulāmuw wa kānatimra`atī \'āqiraw wa qad balagtu minal-kibari \'itiyyā'),
(2258, 19, 9, 'قَالَ كَذٰلِكَۗ قَالَ رَبُّكَ هُوَ عَلَيَّ هَيِّنٌ وَّقَدْ خَلَقْتُكَ مِنْ قَبْلُ وَلَمْ تَكُ شَيْـًٔا', 'qāla każālik, qāla rabbuka huwa \'alayya hayyinuw wa qad khalaqtuka ming qablu wa lam taku syai`ā'),
(2259, 19, 10, 'قَالَ رَبِّ اجْعَلْ لِّيْٓ اٰيَةً ۗقَالَ اٰيَتُكَ اَلَّا تُكَلِّمَ النَّاسَ ثَلٰثَ لَيَالٍ سَوِيًّا', 'qāla rabbij\'al lī āyah, qāla āyatuka allā tukalliman-nāsa ṡalāṡa layālin sawiyyā'),
(2260, 19, 11, 'فَخَرَجَ عَلٰى قَوْمِهٖ مِنَ الْمِحْرَابِ فَاَوْحٰٓى اِلَيْهِمْ اَنْ سَبِّحُوْا بُكْرَةً وَّعَشِيًّا', 'fa kharaja \'alā qaumihī minal-miḥrābi fa auḥa ilaihim an sabbiḥu bukrataw wa \'asyiyyā'),
(2261, 19, 12, 'يٰيَحْيٰى خُذِ الْكِتٰبَ بِقُوَّةٍ ۗوَاٰتَيْنٰهُ الْحُكْمَ صَبِيًّاۙ', 'yā yaḥyā khużil-kitāba biquwwah, wa ātaināhul-ḥukma ṣabiyyā'),
(2262, 19, 13, 'وَّحَنَانًا مِّنْ لَّدُنَّا وَزَكٰوةً ۗوَكَانَ تَقِيًّا ۙ', 'wa ḥanānam mil ladunnā wa zakāh, wa kāna taqiyyā'),
(2263, 19, 14, 'وَّبَرًّاۢ بِوَالِدَيْهِ وَلَمْ يَكُنْ جَبَّارًا عَصِيًّا', 'wa barram biwālidaihi wa lam yakun jabbāran \'aṣiyyā'),
(2264, 19, 15, 'وَسَلٰمٌ عَلَيْهِ يَوْمَ وُلِدَ وَيَوْمَ يَمُوْتُ وَيَوْمَ يُبْعَثُ حَيًّا', 'wa salāmun \'alaihi yauma wulida wa yauma yamutu wa yauma yub\'aṡu ḥayyā'),
(2265, 19, 16, 'وَاذْكُرْ فِى الْكِتٰبِ مَرْيَمَۘ اِذِ انْتَبَذَتْ مِنْ اَهْلِهَا مَكَانًا شَرْقِيًّا ۙ', 'ważkur fil-kitābi maryam, iżintabażat min ahlihā makānan syarqiyyā'),
(2266, 19, 17, 'فَاتَّخَذَتْ مِنْ دُوْنِهِمْ حِجَابًاۗ فَاَرْسَلْنَآ اِلَيْهَا رُوْحَنَا فَتَمَثَّلَ لَهَا بَشَرًا سَوِيًّا', 'fattakhażat min dunihim ḥijābā, fa arsalna ilaihā ruḥanā fa tamaṡṡala lahā basyaran sawiyyā'),
(2267, 19, 18, 'قَالَتْ اِنِّيْٓ اَعُوْذُ بِالرَّحْمٰنِ مِنْكَ اِنْ كُنْتَ تَقِيًّا', 'qālat innī a\'użu bir-raḥmāni mingka ing kunta taqiyyā'),
(2268, 19, 19, 'قَالَ اِنَّمَآ اَنَا۠ رَسُوْلُ رَبِّكِۖ لِاَهَبَ لَكِ غُلٰمًا زَكِيًّا', 'qāla innama ana rasulu rabbiki li`ahaba laki gulāman zakiyyā'),
(2269, 19, 20, 'قَالَتْ اَنّٰى يَكُوْنُ لِيْ غُلٰمٌ وَّلَمْ يَمْسَسْنِيْ بَشَرٌ وَّلَمْ اَكُ بَغِيًّا', 'qālat annā yakunu lī gulāmuw wa lam yamsasnī basyaruw wa lam aku bagiyyā'),
(2270, 19, 21, 'قَالَ كَذٰلِكِۚ قَالَ رَبُّكِ هُوَ عَلَيَّ هَيِّنٌۚ وَلِنَجْعَلَهٗٓ اٰيَةً لِّلنَّاسِ وَرَحْمَةً مِّنَّاۚ وَكَانَ اَمْرًا مَّقْضِيًّا', 'qāla każālik, qāla rabbuki huwa \'alayya hayyin, wa linaj\'alahū āyatal lin-nāsi wa raḥmatam minnā, wa kāna amram maqḍiyyā'),
(2271, 19, 22, '۞ فَحَمَلَتْهُ فَانْتَبَذَتْ بِهٖ مَكَانًا قَصِيًّا', 'fa ḥamalat-hu fantabażat bihī makānang qaṣiyyā'),
(2272, 19, 23, 'فَاَجَاۤءَهَا الْمَخَاضُ اِلٰى جِذْعِ النَّخْلَةِۚ قَالَتْ يٰلَيْتَنِيْ مِتُّ قَبْلَ هٰذَا وَكُنْتُ نَسْيًا مَّنْسِيًّا', 'fa aja`ahal-makhāḍu ilā jiż\'in-nakhlah, qālat yā laitanī mittu qabla hāżā wa kuntu nas-yam mansiyyā'),
(2273, 19, 24, 'فَنَادٰىهَا مِنْ تَحْتِهَآ اَلَّا تَحْزَنِيْ قَدْ جَعَلَ رَبُّكِ تَحْتَكِ سَرِيًّا', 'fa nādāhā min taḥtiha allā taḥzanī qad ja\'ala rabbuki taḥtaki sariyyā'),
(2274, 19, 25, 'وَهُزِّيْٓ اِلَيْكِ بِجِذْعِ النَّخْلَةِ تُسٰقِطْ عَلَيْكِ رُطَبًا جَنِيًّا ۖ', 'wa huzzī ilaiki bijiż\'in-nakhlati tusāqiṭ \'alaiki ruṭaban janiyyā'),
(2275, 19, 26, 'فَكُلِيْ وَاشْرَبِيْ وَقَرِّيْ عَيْنًا ۚفَاِمَّا تَرَيِنَّ مِنَ الْبَشَرِ اَحَدًاۙ فَقُوْلِيْٓ اِنِّيْ نَذَرْتُ لِلرَّحْمٰنِ صَوْمًا فَلَنْ اُكَلِّمَ الْيَوْمَ اِنْسِيًّا ۚ', 'fa kulī wasyrabī wa qarrī \'ainā, fa immā tarayinna minal-basyari aḥadan fa qulī innī nażartu lir-raḥmāni ṣauman fa lan ukallimal-yauma insiyyā'),
(2276, 19, 27, 'فَاَتَتْ بِهٖ قَوْمَهَا تَحْمِلُهٗ ۗقَالُوْا يٰمَرْيَمُ لَقَدْ جِئْتِ شَيْـًٔا فَرِيًّا', 'fa atat bihī qaumahā taḥmiluh, qālu yā maryamu laqad ji`ti syai`an fariyyā'),
(2277, 19, 28, 'يٰٓاُخْتَ هٰرُوْنَ مَا كَانَ اَبُوْكِ امْرَاَ سَوْءٍ وَّمَا كَانَتْ اُمُّكِ بَغِيًّا ۖ', 'ya ukhta hāruna mā kāna abukimra`a sau`iw wa mā kānat ummuki bagiyyā'),
(2278, 19, 29, 'فَاَشَارَتْ اِلَيْهِۗ قَالُوْا كَيْفَ نُكَلِّمُ مَنْ كَانَ فِى الْمَهْدِ صَبِيًّا', 'fa asyārat ilaīh, qālu kaifa nukallimu mang kāna fil-mahdi ṣabiyyā'),
(2279, 19, 30, 'قَالَ اِنِّيْ عَبْدُ اللّٰهِ ۗاٰتٰنِيَ الْكِتٰبَ وَجَعَلَنِيْ نَبِيًّا ۙ', 'qāla innī \'abdullāh, ātāniyal-kitāba wa ja\'alanī nabiyyā'),
(2280, 19, 31, 'وَّجَعَلَنِيْ مُبٰرَكًا اَيْنَ مَا كُنْتُۖ وَاَوْصٰنِيْ بِالصَّلٰوةِ وَالزَّكٰوةِ مَا دُمْتُ حَيًّا ۖ', 'wa ja\'alanī mubārakan aina mā kuntu wa auṣānī biṣ-ṣalāti waz-zakāti mā dumtu ḥayyā'),
(2281, 19, 32, 'وَّبَرًّاۢ بِوَالِدَتِيْ وَلَمْ يَجْعَلْنِيْ جَبَّارًا شَقِيًّا', 'wa barram biwālidatī wa lam yaj\'alnī jabbāran syaqiyyā'),
(2282, 19, 33, 'وَالسَّلٰمُ عَلَيَّ يَوْمَ وُلِدْتُّ وَيَوْمَ اَمُوْتُ وَيَوْمَ اُبْعَثُ حَيًّا', 'was-salāmu \'alayya yauma wulittu wa yauma amutu wa yauma ub\'aṡu ḥayyā'),
(2283, 19, 34, 'ذٰلِكَ عِيْسَى ابْنُ مَرْيَمَ ۚقَوْلَ الْحَقِّ الَّذِيْ فِيْهِ يَمْتَرُوْنَ', 'żālika \'īsabnu maryam, qaulal-ḥaqqillażī fīhi yamtarun'),
(2284, 19, 35, 'مَا كَانَ لِلّٰهِ اَنْ يَّتَّخِذَ مِنْ وَّلَدٍ سُبْحٰنَهٗ ۗاِذَا قَضٰٓى اَمْرًا فَاِنَّمَا يَقُوْلُ لَهٗ كُنْ فَيَكُوْنُ ۗ', 'mā kāna lillāhi ay yattakhiża miw waladin sub-ḥānah, iżā qaḍa amran fa innamā yaqulu lahu kun fa yakun'),
(2285, 19, 36, 'وَاِنَّ اللّٰهَ رَبِّيْ وَرَبُّكُمْ فَاعْبُدُوْهُ ۗهٰذَا صِرَاطٌ مُّسْتَقِيْمٌ', 'wa innallāha rabbī wa rabbukum fa\'buduh, hāżā ṣirāṭum mustaqīm'),
(2286, 19, 37, 'فَاخْتَلَفَ الْاَحْزَابُ مِنْۢ بَيْنِهِمْۚ فَوَيْلٌ لِّلَّذِيْنَ كَفَرُوْا مِنْ مَّشْهَدِ يَوْمٍ عَظِيْمٍ', 'fakhtalafal-aḥzābu mim bainihim, fa wailul lillażīna kafaru mim masy-hadi yaumin \'aẓīm'),
(2287, 19, 38, 'اَسْمِعْ بِهِمْ وَاَبْصِرْۙ يَوْمَ يَأْتُوْنَنَا لٰكِنِ الظّٰلِمُوْنَ الْيَوْمَ فِيْ ضَلٰلٍ مُّبِيْنٍ', 'asmi\' bihim wa abṣir yauma ya`tunanā lākiniẓ-ẓālimunal-yauma fī ḍalālim mubīn'),
(2288, 19, 39, 'وَاَنْذِرْهُمْ يَوْمَ الْحَسْرَةِ اِذْ قُضِيَ الْاَمْرُۘ وَهُمْ فِيْ غَفْلَةٍ وَّهُمْ لَا يُؤْمِنُوْنَ', 'wa anżir-hum yaumal-ḥasrati iż quḍiyal-amr, wa hum fī gaflatiw wa hum lā yu`minun'),
(2289, 19, 40, 'اِنَّا نَحْنُ نَرِثُ الْاَرْضَ وَمَنْ عَلَيْهَا وَاِلَيْنَا يُرْجَعُوْنَ', 'innā naḥnu nariṡul-arḍa wa man \'alaihā wa ilainā yurja\'un'),
(2290, 19, 41, 'وَاذْكُرْ فِى الْكِتٰبِ اِبْرٰهِيْمَ ەۗ اِنَّهٗ كَانَ صِدِّيْقًا نَّبِيًّا', 'ważkur fil-kitābi ibrāhīm, innahu kāna ṣiddīqan nabiyyā'),
(2291, 19, 42, 'اِذْ قَالَ لِاَبِيْهِ يٰٓاَبَتِ لِمَ تَعْبُدُ مَا لَا يَسْمَعُ وَلَا يُبْصِرُ وَلَا يُغْنِيْ عَنْكَ شَيْـًٔا', 'iż qāla li`abīhi ya abati lima ta\'budu mā lā yasma\'u wa lā yubṣiru wa lā yugnī \'angka syai`ā'),
(2292, 19, 43, 'يٰٓاَبَتِ اِنِّي قَدْ جَاۤءَنِيْ مِنَ الْعِلْمِ مَا لَمْ يَأْتِكَ فَاتَّبِعْنِيْٓ اَهْدِكَ صِرَاطًا سَوِيًّا', 'ya abati innī qad ja`anī minal-\'ilmi mā lam ya`tika fattabi\'nī ahdika ṣirāṭan sawiyyā'),
(2293, 19, 44, 'يٰٓاَبَتِ لَا تَعْبُدِ الشَّيْطٰنَۗ اِنَّ الشَّيْطٰنَ كَانَ لِلرَّحْمٰنِ عَصِيًّا', 'ya abati lā ta\'budisy-syaiṭān, innasy-syaiṭāna kāna lir-raḥmāni \'aṣiyyā'),
(2294, 19, 45, 'يٰٓاَبَتِ اِنِّيْٓ اَخَافُ اَنْ يَّمَسَّكَ عَذَابٌ مِّنَ الرَّحْمٰنِ فَتَكُوْنَ لِلشَّيْطٰنِ وَلِيًّا', 'ya abati innī akhāfu ay yamassaka \'ażābum minar-raḥmāni fa takuna lisy-syaiṭāni waliyyā'),
(2295, 19, 46, 'قَالَ اَرَاغِبٌ اَنْتَ عَنْ اٰلِهَتِيْ يٰٓاِبْرٰهِيْمُ ۚ لَىِٕنْ لَّمْ تَنْتَهِ لَاَرْجُمَنَّكَ وَاهْجُرْنِيْ مَلِيًّا', 'qāla a rāgibun anta \'an ālihatī ya ibrāhīm, la`il lam tantahi la`arjumannaka wahjurnī maliyyā'),
(2296, 19, 47, 'قَالَ سَلٰمٌ عَلَيْكَۚ سَاَسْتَغْفِرُ لَكَ رَبِّيْۗ اِنَّهٗ كَانَ بِيْ حَفِيًّا', 'qāla salāmun \'alaīk, sa`astagfiru laka rabbī, innahu kāna bī ḥafiyyā'),
(2297, 19, 48, 'وَاَعْتَزِلُكُمْ وَمَا تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ وَاَدْعُوْ رَبِّيْۖ عَسٰٓى اَلَّآ اَكُوْنَ بِدُعَاۤءِ رَبِّيْ شَقِيًّا', 'wa a\'tazilukum wa mā tad\'una min dunillāhi wa ad\'u rabbī \'asa alla akuna bidu\'a`i rabbī syaqiyyā'),
(2298, 19, 49, 'فَلَمَّا اعْتَزَلَهُمْ وَمَا يَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ ۙوَهَبْنَا لَهٗٓ اِسْحٰقَ وَيَعْقُوْبَۗ وَكُلًّا جَعَلْنَا نَبِيًّا', 'fa lamma\'tazalahum wa mā ya\'buduna min dunillāhi wahabnā lahū is-ḥāqa wa ya\'qub, wa kullan ja\'alnā nabiyyā'),
(2299, 19, 50, 'وَوَهَبْنَا لَهُمْ مِّنْ رَّحْمَتِنَا وَجَعَلْنَا لَهُمْ لِسَانَ صِدْقٍ عَلِيًّا', 'wa wahabnā lahum mir raḥmatinā wa ja\'alnā lahum lisāna ṣidqin \'aliyyā'),
(2300, 19, 51, 'وَاذْكُرْ فِى الْكِتٰبِ مُوْسٰٓىۖ اِنَّهٗ كَانَ مُخْلَصًا وَّكَانَ رَسُوْلًا نَّبِيًّا', 'ważkur fil-kitābi musa innahu kāna mukhlaṣaw wa kāna rasulan nabiyyā'),
(2301, 19, 52, 'وَنَادَيْنٰهُ مِنْ جَانِبِ الطُّوْرِ الْاَيْمَنِ وَقَرَّبْنٰهُ نَجِيًّا', 'wa nādaināhu min jānibiṭ-ṭuril-aimani wa qarrabnāhu najiyyā'),
(2302, 19, 53, 'وَوَهَبْنَا لَهٗ مِنْ رَّحْمَتِنَآ اَخَاهُ هٰرُوْنَ نَبِيًّا', 'wa wahabnā lahu mir raḥmatina akhāhu hāruna nabiyyā'),
(2303, 19, 54, 'وَاذْكُرْ فِى الْكِتٰبِ اِسْمٰعِيْلَ ۖاِنَّهٗ كَانَ صَادِقَ الْوَعْدِ وَكَانَ رَسُوْلًا نَّبِيًّا ۚ', 'ważkur fil-kitābi ismā\'īla innahu kāna ṣādiqal-wa\'di wa kāna rasulan nabiyyā'),
(2304, 19, 55, 'وَكَانَ يَأْمُرُ اَهْلَهٗ بِالصَّلٰوةِ وَالزَّكٰوةِۖ وَكَانَ عِنْدَ رَبِّهٖ مَرْضِيًّا', 'wa kāna ya`muru ahlahu biṣ-ṣalāti waz-zakāti wa kāna \'inda rabbihī marḍiyyā'),
(2305, 19, 56, 'وَاذْكُرْ فِى الْكِتٰبِ اِدْرِيْسَۖ اِنَّهٗ كَانَ صِدِّيْقًا نَّبِيًّا ۙ', 'ważkur fil-kitābi idrīsa innahu kāna ṣiddīqan nabiyyā'),
(2306, 19, 57, 'وَّرَفَعْنٰهُ مَكَانًا عَلِيًّا', 'wa rafa\'nāhu makānan \'aliyyā'),
(2307, 19, 58, 'اُولٰۤىِٕكَ الَّذِيْنَ اَنْعَمَ اللّٰهُ عَلَيْهِمْ مِّنَ النَّبِيّٖنَ مِنْ ذُرِّيَّةِ اٰدَمَ وَمِمَّنْ حَمَلْنَا مَعَ نُوْحٍۖ وَّمِنْ ذُرِّيَّةِ اِبْرٰهِيْمَ وَاِسْرَاۤءِيْلَ ۖوَمِمَّنْ هَدَيْنَا وَاجْتَبَيْنَاۗ اِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُ الرَّحْمٰنِ خَرُّوْا سُجَّدًا وَّبُكِيًّا ۩', 'ula`ikallażīna an\'amallāhu \'alaihim minan-nabiyyīna min żurriyyati ādama wa mim man ḥamalnā ma\'a nuḥiw wa min żurriyyati ibrāhīma wa isra`īla wa mim man hadainā wajtabainā, iżā tutlā \'alaihim āyātur-raḥmāni kharru sujjadaw wa bukiyyā'),
(2308, 19, 59, '۞ فَخَلَفَ مِنْۢ بَعْدِهِمْ خَلْفٌ اَضَاعُوا الصَّلٰوةَ وَاتَّبَعُوا الشَّهَوٰتِ فَسَوْفَ يَلْقَوْنَ غَيًّا ۙ', 'fa khalafa mim ba\'dihim khalfun aḍā\'uṣ-ṣalāta wattaba\'usy-syahawāti fa saufa yalqauna gayyā'),
(2309, 19, 60, 'اِلَّا مَنْ تَابَ وَاٰمَنَ وَعَمِلَ صَالِحًا فَاُولٰۤىِٕكَ يَدْخُلُوْنَ الْجَنَّةَ وَلَا يُظْلَمُوْنَ شَيْـًٔا ۙ', 'illā man tāba wa āmana wa \'amila ṣāliḥan fa ula`ika yadkhulunal-jannata wa lā yuẓlamuna syai`ā'),
(2310, 19, 61, 'جَنّٰتِ عَدْنِ ِۨالَّتِيْ وَعَدَ الرَّحْمٰنُ عِبَادَهٗ بِالْغَيْبِۗ اِنَّهٗ كَانَ وَعْدُهٗ مَأْتِيًّا', 'jannāti \'adninillatī wa\'adar-raḥmānu \'ibādahu bil-gaīb, innahu kāna wa\'duhu ma`tiyyā'),
(2311, 19, 62, 'لَا يَسْمَعُوْنَ فِيْهَا لَغْوًا اِلَّا سَلٰمًاۗ وَلَهُمْ رِزْقُهُمْ فِيْهَا بُكْرَةً وَّعَشِيًّا', 'lā yasma\'una fīhā lagwan illā salāmā, wa lahum rizquhum fīhā bukrataw wa \'asyiyyā'),
(2312, 19, 63, 'تِلْكَ الْجَنَّةُ الَّتِيْ نُوْرِثُ مِنْ عِبَادِنَا مَنْ كَانَ تَقِيًّا', 'tilkal-jannatullatī nuriṡu min \'ibādinā mang kāna taqiyyā'),
(2313, 19, 64, 'وَمَا نَتَنَزَّلُ اِلَّا بِاَمْرِ رَبِّكَۚ لَهٗ مَا بَيْنَ اَيْدِيْنَا وَمَا خَلْفَنَا وَمَا بَيْنَ ذٰلِكَ وَمَا كَانَ رَبُّكَ نَسِيًّا ۚ', 'wa mā natanazzalu illā bi`amri rabbik, lahu mā baina aidīnā wa mā khalfanā wa mā baina żālika wa mā kāna rabbuka nasiyyā'),
(2314, 19, 65, 'رَبُّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا فَاعْبُدْهُ وَاصْطَبِرْ لِعِبَادَتِهٖۗ هَلْ تَعْلَمُ لَهٗ سَمِيًّا', 'rabbus-samāwāti wal-arḍi wa mā bainahumā fa\'bud-hu waṣṭabir li\'ibādatih, hal ta\'lamu lahu samiyyā'),
(2315, 19, 66, 'وَيَقُوْلُ الْاِنْسَانُ ءَاِذَا مَا مِتُّ لَسَوْفَ اُخْرَجُ حَيًّا', 'wa yaqulul-insānu a iżā mā mittu lasaufa ukhraju ḥayyā'),
(2316, 19, 67, 'اَوَلَا يَذْكُرُ الْاِنْسَانُ اَنَّا خَلَقْنٰهُ مِنْ قَبْلُ وَلَمْ يَكُ شَيْـًٔا', 'a wa lā yażkurul-insānu annā khalaqnāhu ming qablu wa lam yaku syai`ā'),
(2317, 19, 68, 'فَوَرَبِّكَ لَنَحْشُرَنَّهُمْ وَالشَّيٰطِيْنَ ثُمَّ لَنُحْضِرَنَّهُمْ حَوْلَ جَهَنَّمَ جِثِيًّا', 'fa wa rabbika lanaḥsyurannahum wasy-syayāṭīna ṡumma lanuḥḍirannahum ḥaula jahannama jiṡiyyā'),
(2318, 19, 69, 'ثُمَّ لَنَنْزِعَنَّ مِنْ كُلِّ شِيْعَةٍ اَيُّهُمْ اَشَدُّ عَلَى الرَّحْمٰنِ عِتِيًّا ۚ', 'ṡumma lananzi\'anna ming kulli syī\'atin ayyuhum asyaddu \'alar-raḥmāni \'itiyyā'),
(2319, 19, 70, 'ثُمَّ لَنَحْنُ اَعْلَمُ بِالَّذِيْنَ هُمْ اَوْ لٰى بِهَا صِلِيًّا', 'ṡumma lanaḥnu a\'lamu billażīna hum aulā bihā ṣiliyyā'),
(2320, 19, 71, 'وَاِنْ مِّنْكُمْ اِلَّا وَارِدُهَا ۚ كَانَ عَلٰى رَبِّكَ حَتْمًا مَّقْضِيًّا ۚ', 'wa im mingkum illā wāriduhā, kāna \'alā rabbika ḥatmam maqḍiyyā'),
(2321, 19, 72, 'ثُمَّ نُنَجِّى الَّذِيْنَ اتَّقَوْا وَّنَذَرُ الظّٰلِمِيْنَ فِيْهَا جِثِيًّا', 'ṡumma nunajjillażīnattaqaw wa nażaruẓ-ẓālimīna fīhā jiṡiyyā'),
(2322, 19, 73, 'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا بَيِّنٰتٍ قَالَ الَّذِيْنَ كَفَرُوْا لِلَّذِيْنَ اٰمَنُوْٓاۙ اَيُّ الْفَرِيْقَيْنِ خَيْرٌ مَّقَامًا وَّاَحْسَنُ نَدِيًّا', 'wa iżā tutlā \'alaihim āyātunā bayyināting qālallażīna kafaru lillażīna āmanū ayyul-farīqaini khairum maqāmaw wa aḥsanu nadiyyā'),
(2323, 19, 74, 'وَكَمْ اَهْلَكْنَا قَبْلَهُمْ مِّنْ قَرْنٍ هُمْ اَحْسَنُ اَثَاثًا وَّرِءْيًا', 'wa kam ahlaknā qablahum ming qarnin hum aḥsanu aṡāṡaw wa ri`yā'),
(2324, 19, 75, 'قُلْ مَنْ كَانَ فِى الضَّلٰلَةِ فَلْيَمْدُدْ لَهُ الرَّحْمٰنُ مَدًّا ەۚ حَتّٰىٓ اِذَا رَاَوْا مَا يُوْعَدُوْنَ اِمَّا الْعَذَابَ وَاِمَّا السَّاعَةَ ۗفَسَيَعْلَمُوْنَ مَنْ هُوَ شَرٌّ مَّكَانًا وَّاَضْعَفُ جُنْدًا', 'qul mang kāna fiḍ-ḍalālati falyamdud lahur-raḥmānu maddā, ḥatta iżā ra`au mā yu\'aduna immal-\'ażāba wa immas-sā\'ah, fa saya\'lamuna man huwa syarrum makānaw wa aḍ\'afu jundā'),
(2325, 19, 76, 'وَيَزِيْدُ اللّٰهُ الَّذِيْنَ اهْتَدَوْا هُدًىۗ وَالْبٰقِيٰتُ الصّٰلِحٰتُ خَيْرٌ عِنْدَ رَبِّكَ ثَوَابًا وَّخَيْرٌ مَّرَدًّا', 'wa yazīdullāhullażīnahtadau hudā, wal-bāqiyātuṣ-ṣāliḥātu khairun \'inda rabbika ṡawābaw wa khairum maraddā'),
(2326, 19, 77, 'اَفَرَاَيْتَ الَّذِيْ كَفَرَ بِاٰيٰتِنَا وَقَالَ لَاُوْتَيَنَّ مَالًا وَّوَلَدًا ۗ', 'a fa ra`aitallażī kafara bi`āyātinā wa qāla la`utayanna mālaw wa waladā'),
(2327, 19, 78, 'اَطَّلَعَ الْغَيْبَ اَمِ اتَّخَذَ عِنْدَ الرَّحْمٰنِ عَهْدًا ۙ', 'aṭṭala\'al-gaiba amittakhaża \'indar-raḥmāni \'ahdā'),
(2328, 19, 79, 'كَلَّاۗ سَنَكْتُبُ مَا يَقُوْلُ وَنَمُدُّ لَهٗ مِنَ الْعَذَابِ مَدًّا ۙ', 'kallā, sanaktubu mā yaqulu wa namuddu lahu minal-\'ażābi maddā'),
(2329, 19, 80, 'وَّنَرِثُهٗ مَا يَقُوْلُ وَيَأْتِيْنَا فَرْدًا', 'wa nariṡuhu mā yaqulu wa ya`tīnā fardā'),
(2330, 19, 81, 'وَاتَّخَذُوْا مِنْ دُوْنِ اللّٰهِ اٰلِهَةً لِّيَكُوْنُوْا لَهُمْ عِزًّا ۙ', 'wattakhażu min dunillāhi ālihatal liyakunu lahum \'izzā'),
(2331, 19, 82, 'كَلَّا ۗسَيَكْفُرُوْنَ بِعِبَادَتِهِمْ وَيَكُوْنُوْنَ عَلَيْهِمْ ضِدًّا', 'kallā, sayakfuruna bi\'ibādatihim wa yakununa \'alaihim ḍiddā'),
(2332, 19, 83, 'اَلَمْ تَرَ اَنَّآ اَرْسَلْنَا الشَّيٰطِيْنَ عَلَى الْكٰفِرِيْنَ تَؤُزُّهُمْ اَزًّا ۙ', 'a lam tara anna arsalnasy-syayāṭīna \'alal-kāfirīna ta`uzzuhum azzā'),
(2333, 19, 84, 'فَلَا تَعْجَلْ عَلَيْهِمْۗ اِنَّمَا نَعُدُّ لَهُمْ عَدًّا ۗ', 'fa lā ta\'jal \'alaihim, innamā na\'uddu lahum \'addā'),
(2334, 19, 85, 'يَوْمَ نَحْشُرُ الْمُتَّقِيْنَ اِلَى الرَّحْمٰنِ وَفْدًا', 'yauma naḥsyurul-muttaqīna ilar-raḥmāni wafdā'),
(2335, 19, 86, 'وَنَسُوْقُ الْمُجْرِمِيْنَ اِلٰى جَهَنَّمَ وِرْدًا ۘ', 'wa nasuqul-mujrimīna ilā jahannama wirdā'),
(2336, 19, 87, 'لَا يَمْلِكُوْنَ الشَّفَاعَةَ اِلَّا مَنِ اتَّخَذَ عِنْدَ الرَّحْمٰنِ عَهْدًا ۘ', 'lā yamlikunasy-syafā\'ata illā manittakhaża \'indar-raḥmāni \'ahdā'),
(2337, 19, 88, 'وَقَالُوا اتَّخَذَ الرَّحْمٰنُ وَلَدًا ۗ', 'wa qāluttakhażar-raḥmānu waladā'),
(2338, 19, 89, 'لَقَدْ جِئْتُمْ شَيْـًٔا اِدًّا ۙ', 'laqad ji`tum syai`an iddā'),
(2339, 19, 90, 'تَكَادُ السَّمٰوٰتُ يَتَفَطَّرْنَ مِنْهُ وَتَنْشَقُّ الْاَرْضُ وَتَخِرُّ الْجِبَالُ هَدًّا ۙ', 'takādus-samāwātu yatafaṭṭarna min-hu wa tansyaqqul-arḍu wa takhirrul-jibālu haddā'),
(2340, 19, 91, 'اَنْ دَعَوْا لِلرَّحْمٰنِ وَلَدًا ۚ', 'an da\'au lir-raḥmāni waladā'),
(2341, 19, 92, 'وَمَا يَنْۢبَغِيْ لِلرَّحْمٰنِ اَنْ يَّتَّخِذَ وَلَدًا ۗ', 'wa mā yambagī lir-raḥmāni ay yattakhiża waladā'),
(2342, 19, 93, 'اِنْ كُلُّ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِ اِلَّآ اٰتِى الرَّحْمٰنِ عَبْدًا ۗ', 'ing kullu man fis-samāwāti wal-arḍi illa ātir-raḥmāni \'abdā'),
(2343, 19, 94, 'لَقَدْ اَحْصٰىهُمْ وَعَدَّهُمْ عَدًّا ۗ', 'laqad aḥṣāhum wa \'addahum \'addā'),
(2344, 19, 95, 'وَكُلُّهُمْ اٰتِيْهِ يَوْمَ الْقِيٰمَةِ فَرْدًا', 'wa kulluhum ātīhi yaumal-qiyāmati fardā'),
(2345, 19, 96, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ سَيَجْعَلُ لَهُمُ الرَّحْمٰنُ وُدًّا', 'innallażīna āmanu wa \'amiluṣ-ṣāliḥāti sayaj\'alu lahumur-raḥmānu wuddā'),
(2346, 19, 97, 'فَاِنَّمَا يَسَّرْنٰهُ بِلِسَانِكَ لِتُبَشِّرَ بِهِ الْمُتَّقِيْنَ وَتُنْذِرَ بِهٖ قَوْمًا لُّدًّا', 'fa innamā yassarnāhu bilisānika litubasysyira bihil-muttaqīna wa tunżira bihī qaumal luddā'),
(2347, 19, 98, 'وَكَمْ اَهْلَكْنَا قَبْلَهُمْ مِّنْ قَرْنٍۗ هَلْ تُحِسُّ مِنْهُمْ مِّنْ اَحَدٍ اَوْ تَسْمَعُ لَهُمْ رِكْزًا', 'wa kam ahlaknā qablahum ming qarn, hal tuḥissu min-hum min aḥadin au tasma\'u lahum rikzā'),
(2348, 20, 1, 'طٰهٰ ۚ', 'ṭā hā'),
(2349, 20, 2, 'مَآ اَنْزَلْنَا عَلَيْكَ الْقُرْاٰنَ لِتَشْقٰٓى ۙ', 'mā anzalnā \'alaikal-qur`āna litasyqā'),
(2350, 20, 3, 'اِلَّا تَذْكِرَةً لِّمَنْ يَّخْشٰى ۙ', 'illā tażkiratal limay yakhsyā'),
(2351, 20, 4, 'تَنْزِيْلًا مِّمَّنْ خَلَقَ الْاَرْضَ وَالسَّمٰوٰتِ الْعُلٰى ۗ', 'tanzīlam mim man khalaqal-arḍa was-samāwātil-\'ulā'),
(2352, 20, 5, 'اَلرَّحْمٰنُ عَلَى الْعَرْشِ اسْتَوٰى', 'ar-raḥmānu \'alal-\'arsyistawā'),
(2353, 20, 6, 'لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ وَمَا بَيْنَهُمَا وَمَا تَحْتَ الثَّرٰى', 'lahụ mā fis-samāwāti wa mā fil-arḍi wa mā bainahumā wa mā taḥtaṡ-ṡarā'),
(2354, 20, 7, 'وَاِنْ تَجْهَرْ بِالْقَوْلِ فَاِنَّهٗ يَعْلَمُ السِّرَّ وَاَخْفٰى', 'wa in taj-har bil-qauli fa innahụ ya\'lamus-sirra wa akhfā'),
(2355, 20, 8, 'اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَۗ لَهُ الْاَسْمَاۤءُ الْحُسْنٰى', 'allāhu lā ilāha illā huw, lahul-asmā`ul-ḥusnā'),
(2356, 20, 9, 'وَهَلْ اَتٰىكَ حَدِيْثُ مُوْسٰى ۘ', 'wa hal atāka ḥadīṡu mụsā'),
(2357, 20, 10, 'اِذْ رَاٰ نَارًا فَقَالَ لِاَهْلِهِ امْكُثُوْٓا اِنِّيْ اٰنَسْتُ نَارًا لَّعَلِّيْٓ اٰتِيْكُمْ مِّنْهَا بِقَبَسٍ اَوْ اَجِدُ عَلَى النَّارِ هُدًى', 'iż ra`ā nāran fa qāla li`ahlihimkuṡū innī ānastu nāral la\'allī ātīkum min-hā biqabasin au ajidu \'alan-nāri hudā'),
(2358, 20, 11, 'فَلَمَّآ اَتٰىهَا نُوْدِيَ يٰمُوْسٰٓى ۙ', 'fa lammā atāhā nụdiya yā mụsā'),
(2359, 20, 12, 'اِنِّيْٓ اَنَا۠ رَبُّكَ فَاخْلَعْ نَعْلَيْكَۚ اِنَّكَ بِالْوَادِ الْمُقَدَّسِ طُوًى ۗ', 'innī anā rabbuka fakhla\' na\'laīk, innaka bil-wādil-muqaddasi ṭuwā'),
(2360, 20, 13, 'وَاَنَا اخْتَرْتُكَ فَاسْتَمِعْ لِمَا يُوْحٰى', 'wa anakhtartuka fastami\' limā yụḥā'),
(2361, 20, 14, 'اِنَّنِيْٓ اَنَا اللّٰهُ لَآ اِلٰهَ اِلَّآ اَنَا۠ فَاعْبُدْنِيْۙ وَاَقِمِ الصَّلٰوةَ لِذِكْرِيْ', 'innanī anallāhu lā ilāha illā ana fa\'budnī wa aqimiṣ-ṣalāta liżikrī'),
(2362, 20, 15, 'اِنَّ السَّاعَةَ اٰتِيَةٌ اَكَادُ اُخْفِيْهَا لِتُجْزٰى كُلُّ نَفْسٍۢ بِمَا تَسْعٰى', 'innas-sā\'ata ātiyatun akādu ukhfīhā litujzā kullu nafsim bimā tas\'ā'),
(2363, 20, 16, 'فَلَا يَصُدَّنَّكَ عَنْهَا مَنْ لَّا يُؤْمِنُ بِهَا وَاتَّبَعَ هَوٰىهُ فَتَرْدٰى', 'fa lā yaṣuddannaka \'an-hā mal lā yu`minu bihā wattaba\'a hawāhu fa tardā'),
(2364, 20, 17, 'وَمَا تِلْكَ بِيَمِيْنِكَ يٰمُوْسٰى', 'wa mā tilka biyamīnika yā mụsā'),
(2365, 20, 18, 'قَالَ هِيَ عَصَايَۚ اَتَوَكَّؤُا عَلَيْهَا وَاَهُشُّ بِهَا عَلٰى غَنَمِيْ وَلِيَ فِيْهَا مَاٰرِبُ اُخْرٰى', 'qāla hiya \'aṣāy, atawakka`u \'alaihā wa ahusysyu bihā \'alā ganamī wa liya fīhā ma`āribu ukhrā'),
(2366, 20, 19, 'قَالَ اَلْقِهَا يٰمُوْسٰى', 'qāla alqihā yā mụsā'),
(2367, 20, 20, 'فَاَلْقٰىهَا فَاِذَا هِيَ حَيَّةٌ تَسْعٰى', 'fa alqāhā fa iżā hiya ḥayyatun tas\'ā'),
(2368, 20, 21, 'قَالَ خُذْهَا وَلَا تَخَفْۗ سَنُعِيْدُهَا سِيْرَتَهَا الْاُوْلٰى', 'qāla khuż-hā wa lā takhaf, sanu\'īduhā sīratahal-ụlā'),
(2369, 20, 22, 'وَاضْمُمْ يَدَكَ اِلٰى جَنَاحِكَ تَخْرُجْ بَيْضَاۤءَ مِنْ غَيْرِ سُوْۤءٍ اٰيَةً اُخْرٰىۙ', 'waḍmum yadaka ilā janāḥika takhruj baiḍā`a min gairi sū`in āyatan ukhrā'),
(2370, 20, 23, 'لِنُرِيَكَ مِنْ اٰيٰتِنَا الْكُبْرٰى ۚ', 'linuriyaka min āyātinal-kubrā'),
(2371, 20, 24, 'اِذْهَبْ اِلٰى فِرْعَوْنَ اِنَّهٗ طَغٰى', 'iż-hab ilā fir\'auna innahụ ṭagā'),
(2372, 20, 25, 'قَالَ رَبِّ اشْرَحْ لِيْ صَدْرِيْ ۙ', 'qāla rabbisyraḥ lī ṣadrī'),
(2373, 20, 26, 'وَيَسِّرْ لِيْٓ اَمْرِيْ ۙ', 'wa yassir lī amrī'),
(2374, 20, 27, 'وَاحْلُلْ عُقْدَةً مِّنْ لِّسَانِيْ ۙ', 'waḥlul \'uqdatam mil lisānī'),
(2375, 20, 28, 'يَفْقَهُوْا قَوْلِيْ ۖ', 'yafqahụ qaulī'),
(2376, 20, 29, 'وَاجْعَلْ لِّيْ وَزِيْرًا مِّنْ اَهْلِيْ ۙ', 'waj\'al lī wazīram min ahlī'),
(2377, 20, 30, 'هٰرُوْنَ اَخِى ۙ', 'hārụna akhī'),
(2378, 20, 31, 'اشْدُدْ بِهٖٓ اَزْرِيْ ۙ', 'usydud bihī azrī'),
(2379, 20, 32, 'وَاَشْرِكْهُ فِيْٓ اَمْرِيْ ۙ', 'wa asyrik-hu fī amrī'),
(2380, 20, 33, 'كَيْ نُسَبِّحَكَ كَثِيْرًا ۙ', 'kai nusabbiḥaka kaṡīrā'),
(2381, 20, 34, 'وَّنَذْكُرَكَ كَثِيْرًا ۗ', 'wa nażkuraka kaṡīrā'),
(2382, 20, 35, 'اِنَّكَ كُنْتَ بِنَا بَصِيْرًا', 'innaka kunta binā baṣīrā'),
(2383, 20, 36, 'قَالَ قَدْ اُوْتِيْتَ سُؤْلَكَ يٰمُوْسٰى', 'qāla qad ụtīta su`laka yā mụsā'),
(2384, 20, 37, 'وَلَقَدْ مَنَنَّا عَلَيْكَ مَرَّةً اُخْرٰىٓ ۙ', 'wa laqad manannā \'alaika marratan ukhrā'),
(2385, 20, 38, 'اِذْ اَوْحَيْنَآ اِلٰٓى اُمِّكَ مَا يُوْحٰىٓ ۙ', 'iż auḥainā ilā ummika mā yụḥā'),
(2386, 20, 39, 'اَنِ اقْذِفِيْهِ فِى التَّابُوْتِ فَاقْذِفِيْهِ فِى الْيَمِّ فَلْيُلْقِهِ الْيَمُّ بِالسَّاحِلِ يَأْخُذْهُ عَدُوٌّ لِّيْ وَعَدُوٌّ لَّهٗ ۗوَاَلْقَيْتُ عَلَيْكَ مَحَبَّةً مِّنِّيْ ەۚ وَلِتُصْنَعَ عَلٰى عَيْنِيْ ۘ', 'aniqżi fīhi fit-tābụti faqżi fīhi fil-yammi falyulqihil-yammu bis-sāḥili ya`khuż-hu \'aduwwul lī wa \'aduwwul lah, wa alqaitu \'alaika maḥabbatam minnī, wa lituṣna\'a \'alā \'ainī'),
(2387, 20, 40, 'اِذْ تَمْشِيْٓ اُخْتُكَ فَتَقُوْلُ هَلْ اَدُلُّكُمْ عَلٰى مَنْ يَّكْفُلُهٗ ۗفَرَجَعْنٰكَ اِلٰٓى اُمِّكَ كَيْ تَقَرَّ عَيْنُهَا وَلَا تَحْزَنَ ەۗ وَقَتَلْتَ نَفْسًا فَنَجَّيْنٰكَ مِنَ الْغَمِّ وَفَتَنّٰكَ فُتُوْنًا ەۗ فَلَبِثْتَ سِنِيْنَ فِيْٓ اَهْلِ مَدْيَنَ ەۙ ثُمَّ جِئْتَ عَلٰى قَدَرٍ يّٰمُوْسٰى', 'iż tamsyī ukhtuka fa taqụlu hal adullukum \'alā may yakfuluh, fa raja\'nāka ilā ummika kai taqarra \'ainuhā wa lā taḥzan, wa qatalta nafsan fa najjaināka minal-gammi wa fatannāka futụnā, fa labiṡta sinīna fī ahli madyana ṡumma ji`ta \'alā qadariy yā mụsā'),
(2388, 20, 41, 'وَاصْطَنَعْتُكَ لِنَفْسِيْۚ', 'waṣṭana\'tuka linafsī'),
(2389, 20, 42, 'اِذْهَبْ اَنْتَ وَاَخُوْكَ بِاٰيٰتِيْ وَلَا تَنِيَا فِيْ ذِكْرِيْۚ', 'iż-hab anta wa akhụka bi`āyātī wa lā taniyā fī żikrī'),
(2390, 20, 43, 'اِذْهَبَآ اِلٰى فِرْعَوْنَ اِنَّهٗ طَغٰىۚ', 'iż-habā ilā fir\'auna innahụ ṭagā'),
(2391, 20, 44, 'فَقُوْلَا لَهٗ قَوْلًا لَّيِّنًا لَّعَلَّهٗ يَتَذَكَّرُ اَوْ يَخْشٰى', 'fa qụlā lahụ qaulal layyinal la\'allahụ yatażakkaru au yakhsyā'),
(2392, 20, 45, 'قَالَا رَبَّنَآ اِنَّنَا نَخَافُ اَنْ يَّفْرُطَ عَلَيْنَآ اَوْ اَنْ يَّطْغٰى', 'qālā rabbanā innanā nakhāfu ay yafruṭa \'alainā au ay yaṭgā'),
(2393, 20, 46, 'قَالَ لَا تَخَافَآ اِنَّنِيْ مَعَكُمَآ اَسْمَعُ وَاَرٰى', 'qāla lā takhāfā innanī ma\'akumā asma\'u wa arā'),
(2394, 20, 47, 'فَأْتِيٰهُ فَقُوْلَآ اِنَّا رَسُوْلَا رَبِّكَ فَاَرْسِلْ مَعَنَا بَنِيْٓ اِسْرَاۤءِيْلَ ەۙ وَلَا تُعَذِّبْهُمْۗ قَدْ جِئْنٰكَ بِاٰيَةٍ مِّنْ رَّبِّكَ ۗوَالسَّلٰمُ عَلٰى مَنِ اتَّبَعَ الْهُدٰى', 'fa`tiyāhu fa qụlā innā rasụlā rabbika fa arsil ma\'anā banī isrā`īla wa lā tu\'ażżib-hum, qad ji`nāka bi`āyatim mir rabbik, was-salāmu \'alā manittaba\'al-hudā'),
(2395, 20, 48, 'اِنَّا قَدْ اُوْحِيَ اِلَيْنَآ اَنَّ الْعَذَابَ عَلٰى مَنْ كَذَّبَ وَتَوَلّٰى', 'innā qad ụḥiya ilainā annal-\'ażāba \'alā mang każżaba wa tawallā'),
(2396, 20, 49, 'قَالَ فَمَنْ رَّبُّكُمَا يٰمُوْسٰى', 'qāla fa mar rabbukumā yā mụsā'),
(2397, 20, 50, 'قَالَ رَبُّنَا الَّذِيْٓ اَعْطٰى كُلَّ شَيْءٍ خَلْقَهٗ ثُمَّ هَدٰى', 'qāla rabbunallażī a\'ṭā kulla syai`in khalqahụ ṡumma hadā'),
(2398, 20, 51, 'قَالَ فَمَا بَالُ الْقُرُوْنِ الْاُوْلٰى', 'qāla fa mā bālul-qurụnil-ụlā'),
(2399, 20, 52, 'قَالَ عِلْمُهَا عِنْدَ رَبِّيْ فِيْ كِتٰبٍۚ لَا يَضِلُّ رَبِّيْ وَلَا يَنْسَىۖ', 'qāla \'ilmuhā \'inda rabbī fī kitāb, lā yaḍillu rabbī wa lā yansā'),
(2400, 20, 53, 'الَّذِيْ جَعَلَ لَكُمُ الْاَرْضَ مَهْدًا وَّسَلَكَ لَكُمْ فِيْهَا سُبُلًا وَّاَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءًۗ فَاَخْرَجْنَا بِهٖٓ اَزْوَاجًا مِّنْ نَّبَاتٍ شَتّٰى', 'allażī ja\'ala lakumul-arḍa mahdaw wa salaka lakum fīhā subulaw wa anzala minas-samā`i mā`ā, fa akhrajnā bihī azwājam min nabātin syattā'),
(2401, 20, 54, 'كُلُوْا وَارْعَوْا اَنْعَامَكُمْ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّاُولِى النُّهٰى', 'kulụ war\'au an\'āmakum, inna fī żālika la`āyātil li`ulin-nuhā'),
(2402, 20, 55, '۞ مِنْهَا خَلَقْنٰكُمْ وَفِيْهَا نُعِيْدُكُمْ وَمِنْهَا نُخْرِجُكُمْ تَارَةً اُخْرٰى', 'min-hā khalaqnākum wa fīhā nu\'īdukum wa min-hā nukhrijukum tāratan ukhrā'),
(2403, 20, 56, 'وَلَقَدْ اَرَيْنٰهُ اٰيٰتِنَا كُلَّهَا فَكَذَّبَ وَاَبٰى', 'wa laqad araināhu āyātinā kullahā fa każżaba wa abā'),
(2404, 20, 57, 'قَالَ اَجِئْتَنَا لِتُخْرِجَنَا مِنْ اَرْضِنَا بِسِحْرِكَ يٰمُوْسٰى', 'qāla a ji`tanā litukhrijanā min arḍinā bisiḥrika yā mụsā'),
(2405, 20, 58, 'فَلَنَأْتِيَنَّكَ بِسِحْرٍ مِّثْلِهٖ فَاجْعَلْ بَيْنَنَا وَبَيْنَكَ مَوْعِدًا لَّا نُخْلِفُهٗ نَحْنُ وَلَآ اَنْتَ مَكَانًا سُوًى', 'fa lana`tiyannaka bisiḥrim miṡlihī faj\'al bainanā wa bainaka mau\'idal lā nukhlifuhụ naḥnu wa lā anta makānan suwā'),
(2406, 20, 59, 'قَالَ مَوْعِدُكُمْ يَوْمُ الزِّيْنَةِ وَاَنْ يُّحْشَرَ النَّاسُ ضُحًى', 'qāla mau\'idukum yaumuz-zīnati wa ay yuḥsyaran-nāsu ḍuḥā'),
(2407, 20, 60, 'فَتَوَلّٰى فِرْعَوْنُ فَجَمَعَ كَيْدَهٗ ثُمَّ اَتٰى', 'fa tawallā fir\'aunu fa jama\'a kaidahụ ṡumma atā'),
(2408, 20, 61, 'قَالَ لَهُمْ مُّوْسٰى وَيْلَكُمْ لَا تَفْتَرُوْا عَلَى اللّٰهِ كَذِبًا فَيُسْحِتَكُمْ بِعَذَابٍۚ وَقَدْ خَابَ مَنِ افْتَرٰى', 'qāla lahum mụsā wailakum lā taftarụ \'alallāhi każiban fa yus-ḥitakum bi\'ażāb, wa qad khāba maniftarā'),
(2409, 20, 62, 'فَتَنَازَعُوْٓا اَمْرَهُمْ بَيْنَهُمْ وَاَسَرُّوا النَّجْوٰى', 'fa tanāza\'ū amrahum bainahum wa asarrun-najwā'),
(2410, 20, 63, 'قَالُوْٓا اِنْ هٰذٰنِ لَسٰحِرَانِ يُرِيْدَانِ اَنْ يُّخْرِجٰكُمْ مِّنْ اَرْضِكُمْ بِسِحْرِهِمَا وَيَذْهَبَا بِطَرِيْقَتِكُمُ الْمُثْلٰى', 'qālū in hāżāni lasāḥirāni yurīdāni ay yukhrijākum min arḍikum bisiḥrihimā wa yaż-habā biṭarīqatikumul-muṡlā'),
(2411, 20, 64, 'فَاَجْمِعُوْا كَيْدَكُمْ ثُمَّ ائْتُوْا صَفًّاۚ وَقَدْ اَفْلَحَ الْيَوْمَ مَنِ اسْتَعْلٰى', 'fa ajmi\'ụ kaidakum ṡumma`tụ ṣaffā, wa qad aflaḥal-yauma manista\'lā'),
(2412, 20, 65, 'قَالُوْا يٰمُوْسٰٓى اِمَّآ اَنْ تُلْقِيَ وَاِمَّآ اَنْ نَّكُوْنَ اَوَّلَ مَنْ اَلْقٰى', 'qālụ yā mụsā immā an tulqiya wa immā an nakụna awwala man alqā'),
(2413, 20, 66, 'قَالَ بَلْ اَلْقُوْاۚ فَاِذَا حِبَالُهُمْ وَعِصِيُّهُمْ يُخَيَّلُ اِلَيْهِ مِنْ سِحْرِهِمْ اَنَّهَا تَسْعٰى', 'qāla bal alqụ, fa iżā ḥibāluhum wa \'iṣiyyuhum yukhayyalu ilaihi min siḥrihim annahā tas\'ā'),
(2414, 20, 67, 'فَاَوْجَسَ فِيْ نَفْسِهٖ خِيْفَةً مُّوْسٰى', 'fa aujasa fī nafsihī khīfatam mụsā'),
(2415, 20, 68, 'قُلْنَا لَا تَخَفْ اِنَّكَ اَنْتَ الْاَعْلٰى', 'qulnā lā takhaf innaka antal-a\'lā'),
(2416, 20, 69, 'وَاَلْقِ مَا فِيْ يَمِيْنِكَ تَلْقَفْ مَا صَنَعُوْاۗ اِنَّمَا صَنَعُوْا كَيْدُ سٰحِرٍۗ وَلَا يُفْلِحُ السّٰحِرُ حَيْثُ اَتٰى', 'wa alqi mā fī yamīnika talqaf mā ṣana\'ụ, innamā ṣana\'ụ kaidu sāḥir, wa lā yufliḥus-sāḥiru ḥaiṡu atā'),
(2417, 20, 70, 'فَاُلْقِيَ السَّحَرَةُ سُجَّدًا قَالُوْٓا اٰمَنَّا بِرَبِّ هٰرُوْنَ وَمُوْسٰى', 'fa ulqiyas-saḥaratu sujjadang qālū āmannā birabbi hārụna wa mụsā'),
(2418, 20, 71, 'قَالَ اٰمَنْتُمْ لَهٗ قَبْلَ اَنْ اٰذَنَ لَكُمْۗ اِنَّهٗ لَكَبِيْرُكُمُ الَّذِيْ عَلَّمَكُمُ السِّحْرَۚ فَلَاُقَطِّعَنَّ اَيْدِيَكُمْ وَاَرْجُلَكُمْ مِّنْ خِلَافٍ وَّلَاُصَلِّبَنَّكُمْ فِيْ جُذُوْعِ النَّخْلِۖ وَلَتَعْلَمُنَّ اَيُّنَآ اَشَدُّ عَذَابًا وَّاَبْقٰى', 'qāla āmantum lahụ qabla an āżana lakum, innahụ lakabīrukumullażī \'allamakumus-siḥr, fa la`uqaṭṭi\'anna aidiyakum wa arjulakum min khilāfiw wa la`uṣallibannakum fī jużụ\'in-nakhli wa lata\'lamunna ayyunā asyaddu \'ażābaw wa abqā'),
(2419, 20, 72, 'قَالُوْا لَنْ نُّؤْثِرَكَ عَلٰى مَا جَاۤءَنَا مِنَ الْبَيِّنٰتِ وَالَّذِيْ فَطَرَنَا فَاقْضِ مَآ اَنْتَ قَاضٍۗ اِنَّمَا تَقْضِيْ هٰذِهِ الْحَيٰوةَ الدُّنْيَا ۗ', 'qālụ lan nu`ṡiraka \'alā mā jā`anā minal-bayyināti wallażī faṭaranā faqḍi mā anta qāḍ, innamā taqḍī hāżihil-ḥayātad-dun-yā'),
(2420, 20, 73, 'اِنَّآ اٰمَنَّا بِرَبِّنَا لِيَغْفِرَ لَنَا خَطٰيٰنَا وَمَآ اَكْرَهْتَنَا عَلَيْهِ مِنَ السِّحْرِۗ وَاللّٰهُ خَيْرٌ وَّاَبْقٰى', 'innā āmannā birabbinā liyagfira lanā khaṭāyānā wa mā akrahtanā \'alaihi minas-siḥr, wallāhu khairuw wa abqā'),
(2421, 20, 74, 'اِنَّهٗ مَنْ يَّأْتِ رَبَّهٗ مُجْرِمًا فَاِنَّ لَهٗ جَهَنَّمَ ۗ لَا يَمُوْتُ فِيْهَا وَلَا يَحْيٰى', 'innahụ may ya`ti rabbahụ mujriman fa inna lahụ jahannam, lā yamụtu fīhā wa lā yaḥyā'),
(2422, 20, 75, 'وَمَنْ يَّأْتِهٖ مُؤْمِنًا قَدْ عَمِلَ الصّٰلِحٰتِ فَاُولٰۤىِٕكَ لَهُمُ الدَّرَجٰتُ الْعُلٰى ۙ', 'wa may ya`tihī mu`minang qad \'amilaṣ-ṣāliḥāti fa ulā`ika lahumud-darajātul-\'ulā'),
(2423, 20, 76, 'جَنّٰتُ عَدْنٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا ۗوَذٰلِكَ جَزٰۤؤُا مَنْ تَزَكّٰى', 'jannātu \'adnin tajrī min taḥtihal-an-hāru khālidīna fīhā, wa żālika jazā`u man tazakkā'),
(2424, 20, 77, 'وَلَقَدْ اَوْحَيْنَآ اِلٰى مُوْسٰٓى اَنْ اَسْرِ بِعِبَادِيْ فَاضْرِبْ لَهُمْ طَرِيْقًا فِى الْبَحْرِ يَبَسًاۙ لَّا تَخٰفُ دَرَكًا وَّلَا تَخْشٰى', 'wa laqad auḥainā ilā mụsā an asri bi\'ibādī faḍrib lahum ṭarīqan fil-baḥri yabasal lā takhāfu darakaw wa lā takhsyā'),
(2425, 20, 78, 'فَاَتْبَعَهُمْ فِرْعَوْنُ بِجُنُوْدِهٖ فَغَشِيَهُمْ مِّنَ الْيَمِّ مَا غَشِيَهُمْ ۗ', 'fa atba\'ahum fir\'aunu bijunụdihī fa gasyiyahum minal-yammi mā gasyiyahum'),
(2426, 20, 79, 'وَاَضَلَّ فِرْعَوْنُ قَوْمَهٗ وَمَا هَدٰى', 'wa aḍalla fir\'aunu qaumahụ wa mā hadā'),
(2427, 20, 80, 'يٰبَنِيْٓ اِسْرَاۤءِيْلَ قَدْ اَنْجَيْنٰكُمْ مِّنْ عَدُوِّكُمْ وَوٰعَدْنٰكُمْ جَانِبَ الطُّوْرِ الْاَيْمَنَ وَنَزَّلْنَا عَلَيْكُمُ الْمَنَّ وَالسَّلْوٰى', 'yā banī isrā`īla qad anjainākum min \'aduwwikum wa wā\'adnākum jānibaṭ-ṭụril-aimana wa nazzalnā \'alaikumul-manna was-salwā'),
(2428, 20, 81, 'كُلُوْا مِنْ طَيِّبٰتِ مَا رَزَقْنٰكُمْۙ وَلَا تَطْغَوْا فِيْهِ فَيَحِلَّ عَلَيْكُمْ غَضَبِيْۚ وَمَنْ يَّحْلِلْ عَلَيْهِ غَضَبِيْ فَقَدْ هَوٰى', 'kulụ min ṭayyibāti mā razaqnākum wa lā taṭgau fīhi fa yaḥilla \'alaikum gaḍabī, wa may yaḥlil \'alaihi gaḍabī fa qad hawā'),
(2429, 20, 82, 'وَاِنِّي لَغَفَّارٌ لِّمَنْ تَابَ وَاٰمَنَ وَعَمِلَ صَالِحًا ثُمَّ اهْتَدٰى', 'wa innī lagaffārul liman tāba wa āmana wa \'amila ṣāliḥan ṡummahtadā'),
(2430, 20, 83, '۞ وَمَآ اَعْجَلَكَ عَنْ قَوْمِكَ يٰمُوْسٰى', 'wa mā a\'jalaka \'ang qaumika yā mụsā'),
(2431, 20, 84, 'قَالَ هُمْ اُولَاۤءِ عَلٰٓى اَثَرِيْ وَعَجِلْتُ اِلَيْكَ رَبِّ لِتَرْضٰى', 'qāla hum ulā`i \'alā aṡarī wa \'ajiltu ilaika rabbi litarḍā'),
(2432, 20, 85, 'قَالَ فَاِنَّا قَدْ فَتَنَّا قَوْمَكَ مِنْۢ بَعْدِكَ وَاَضَلَّهُمُ السَّامِرِيُّ', 'qāla fa innā qad fatannā qaumaka mim ba\'dika wa aḍallahumus-sāmiriyy'),
(2433, 20, 86, 'فَرَجَعَ مُوْسٰٓى اِلٰى قَوْمِهٖ غَضْبَانَ اَسِفًا ەۚ قَالَ يٰقَوْمِ اَلَمْ يَعِدْكُمْ رَبُّكُمْ وَعْدًا حَسَنًا ەۗ اَفَطَالَ عَلَيْكُمُ الْعَهْدُ اَمْ اَرَدْتُّمْ اَنْ يَّحِلَّ عَلَيْكُمْ غَضَبٌ مِّنْ رَّبِّكُمْ فَاَخْلَفْتُمْ مَّوْعِدِيْ', 'fa raja\'a mụsā ilā qaumihī gaḍbāna asifā, qāla yā qaumi a lam ya\'idkum rabbukum wa\'dan ḥasanā, a fa ṭāla \'alaikumul-\'ahdu am arattum ay yaḥilla \'alaikum gaḍabum mir rabbikum fa akhlaftum mau\'idī'),
(2434, 20, 87, 'قَالُوْا مَآ اَخْلَفْنَا مَوْعِدَكَ بِمَلْكِنَا وَلٰكِنَّا حُمِّلْنَآ اَوْزَارًا مِّنْ زِيْنَةِ الْقَوْمِ فَقَذَفْنٰهَا فَكَذٰلِكَ اَلْقَى السَّامِرِيُّ ۙ', 'qālụ mā akhlafnā mau\'idaka bimalkinā wa lākinnā ḥummilnā auzāram min zīnatil-qaumi fa qażafnāhā fa każālika alqas-sāmiriyy'),
(2435, 20, 88, 'فَاَخْرَجَ لَهُمْ عِجْلًا جَسَدًا لَّهٗ خُوَارٌ فَقَالُوْا هٰذَآ اِلٰهُكُمْ وَاِلٰهُ مُوْسٰى ەۙ فَنَسِيَ ۗ', 'fa akhraja lahum \'ijlan jasadal lahụ khuwārun fa qālụ hāżā ilāhukum wa ilāhu mụsā fa nasiy'),
(2436, 20, 89, 'اَفَلَا يَرَوْنَ اَلَّا يَرْجِعُ اِلَيْهِمْ قَوْلًا ەۙ وَّلَا يَمْلِكُ لَهُمْ ضَرًّا وَّلَا نَفْعًا', 'a fa lā yarauna allā yarji\'u ilaihim qaulaw wa lā yamliku lahum ḍarraw wa lā naf\'ā'),
(2437, 20, 90, 'وَلَقَدْ قَالَ لَهُمْ هٰرُوْنُ مِنْ قَبْلُ يٰقَوْمِ اِنَّمَا فُتِنْتُمْ بِهٖۚ وَاِنَّ رَبَّكُمُ الرَّحْمٰنُ فَاتَّبِعُوْنِيْ وَاَطِيْعُوْٓا اَمْرِيْ', 'wa laqad qāla lahum hārụnu ming qablu yā qaumi innamā futintum bih, wa inna rabbakumur-raḥmānu fattabi\'ụnī wa aṭī\'ū amrī'),
(2438, 20, 91, 'قَالُوْا لَنْ نَّبْرَحَ عَلَيْهِ عٰكِفِيْنَ حَتّٰى يَرْجِعَ اِلَيْنَا مُوْسٰى', 'qālụ lan nabraḥa \'alaihi \'ākifīna ḥattā yarji\'a ilainā mụsā'),
(2439, 20, 92, 'قَالَ يٰهٰرُوْنُ مَا مَنَعَكَ اِذْ رَاَيْتَهُمْ ضَلُّوْٓا ۙ', 'qāla yā hārụnu mā mana\'aka iż ra`aitahum ḍallū'),
(2440, 20, 93, 'اَلَّا تَتَّبِعَنِۗ اَفَعَصَيْتَ اَمْرِيْ', 'allā tattabi\'an, a fa \'aṣaita amrī'),
(2441, 20, 94, 'قَالَ يَبْنَؤُمَّ لَا تَأْخُذْ بِلِحْيَتِيْ وَلَا بِرَأْسِيْۚ اِنِّيْ خَشِيْتُ اَنْ تَقُوْلَ فَرَّقْتَ بَيْنَ بَنِيْٓ اِسْرَاۤءِيْلَ وَلَمْ تَرْقُبْ قَوْلِيْ', 'qāla yabna`umma lā ta`khuż biliḥyatī wa lā bira`sī, innī khasyītu an taqụla farraqta baina banī isrā`īla wa lam tarqub qaulī'),
(2442, 20, 95, 'قَالَ فَمَا خَطْبُكَ يٰسَامِرِيُّ', 'qāla fa mā khaṭbuka yā sāmiriyy'),
(2443, 20, 96, 'قَالَ بَصُرْتُ بِمَا لَمْ يَبْصُرُوْا بِهٖ فَقَبَضْتُ قَبْضَةً مِّنْ اَثَرِ الرَّسُوْلِ فَنَبَذْتُهَا وَكَذٰلِكَ سَوَّلَتْ لِيْ نَفْسِيْ', 'qāla baṣurtu bimā lam yabṣurụ bihī fa qabaḍtu qabḍatam min aṡarir-rasụli fa nabażtuhā wa każālika sawwalat lī nafsī'),
(2444, 20, 97, 'قَالَ فَاذْهَبْ فَاِنَّ لَكَ فِى الْحَيٰوةِ اَنْ تَقُوْلَ لَا مِسَاسَۖ وَاِنَّ لَكَ مَوْعِدًا لَّنْ تُخْلَفَهٗۚ وَانْظُرْ اِلٰٓى اِلٰهِكَ الَّذِيْ ظَلْتَ عَلَيْهِ عَاكِفًا ۗ لَنُحَرِّقَنَّهٗ ثُمَّ لَنَنْسِفَنَّهٗ فِى الْيَمِّ نَسْفًا', 'qāla faż-hab fa inna laka fil-ḥayāti an taqụla lā misāsa wa inna laka mau\'idal lan tukhlafah, wanẓur ilā ilāhikallażī ẓalta \'alaihi \'ākifā, lanuḥarriqannahụ ṡumma lanansifannahụ fil-yammi nasfā'),
(2445, 20, 98, 'اِنَّمَآ اِلٰهُكُمُ اللّٰهُ الَّذِيْ لَآ اِلٰهَ اِلَّا هُوَۗ وَسِعَ كُلَّ شَيْءٍ عِلْمًا', 'innamā ilāhukumullāhullażī lā ilāha illā huw, wasi\'a kulla syai`in \'ilmā'),
(2446, 20, 99, 'كَذٰلِكَ نَقُصُّ عَلَيْكَ مِنْ اَنْۢبَاۤءِ مَا قَدْ سَبَقَۚ وَقَدْ اٰتَيْنٰكَ مِنْ لَّدُنَّا ذِكْرًا ۚ', 'każālika naquṣṣu \'alaika min ambā`i mā qad sabaq, wa qad ātaināka mil ladunnā żikrā'),
(2447, 20, 100, 'مَنْ اَعْرَضَ عَنْهُ فَاِنَّهٗ يَحْمِلُ يَوْمَ الْقِيٰمَةِ وِزْرًا', 'man a\'raḍa \'an-hu fa innahụ yaḥmilu yaumal-qiyāmati wizrā'),
(2448, 20, 101, 'خٰلِدِيْنَ فِيْهِ ۗوَسَاۤءَ لَهُمْ يَوْمَ الْقِيٰمَةِ حِمْلًاۙ', 'khālidīna fīh, wa sā`a lahum yaumal-qiyāmati ḥimlā'),
(2449, 20, 102, 'يَّوْمَ يُنْفَخُ فِى الصُّوْرِ وَنَحْشُرُ الْمُجْرِمِيْنَ يَوْمَىِٕذٍ زُرْقًا ۖ', 'yauma yunfakhu fiṣ-ṣụri wa naḥsyurul-mujrimīna yauma`iżin zurqā'),
(2450, 20, 103, 'يَّتَخَافَتُوْنَ بَيْنَهُمْ اِنْ لَّبِثْتُمْ اِلَّا عَشْرًا', 'yatakhāfatụna bainahum il labiṡtum illā \'asyrā'),
(2451, 20, 104, 'نَحْنُ اَعْلَمُ بِمَا يَقُوْلُوْنَ اِذْ يَقُوْلُ اَمْثَلُهُمْ طَرِيْقَةً اِنْ لَّبِثْتُمْ اِلَّا يَوْمًا', 'naḥnu a\'lamu bimā yaqụlụna iż yaqụlu amṡaluhum ṭarīqatan il labiṡtum illā yaumā'),
(2452, 20, 105, 'وَيَسْـَٔلُوْنَكَ عَنِ الْجِبَالِ فَقُلْ يَنْسِفُهَا رَبِّيْ نَسْفًا ۙ', 'wa yas`alụnaka \'anil-jibāli fa qul yansifuhā rabbī nasfā'),
(2453, 20, 106, 'فَيَذَرُهَا قَاعًا صَفْصَفًا ۙ', 'fa yażaruhā qā\'an ṣafṣafā'),
(2454, 20, 107, 'لَّا تَرٰى فِيْهَا عِوَجًا وَّلَآ اَمْتًا ۗ', 'lā tarā fīhā \'iwajaw wa lā amtā'),
(2455, 20, 108, 'يَوْمَىِٕذٍ يَّتَّبِعُوْنَ الدَّاعِيَ لَا عِوَجَ لَهٗ ۚوَخَشَعَتِ الْاَصْوَاتُ لِلرَّحْمٰنِ فَلَا تَسْمَعُ اِلَّا هَمْسًا', 'yauma`iżiy yattabi\'ụnad-dā\'iya lā \'iwaja lah, wa khasya\'atil-aṣwātu lir-raḥmāni fa lā tasma\'u illā hamsā'),
(2456, 20, 109, 'يَوْمَىِٕذٍ لَّا تَنْفَعُ الشَّفَاعَةُ اِلَّا مَنْ اَذِنَ لَهُ الرَّحْمٰنُ وَرَضِيَ لَهٗ قَوْلًا', 'yauma`iżil lā tanfa\'usy-syafā\'atu illā man ażina lahur-raḥmānu wa raḍiya lahụ qaulā'),
(2457, 20, 110, 'يَعْلَمُ مَا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْ وَلَا يُحِيْطُوْنَ بِهٖ عِلْمًا', 'ya\'lamu mā baina aidīhim wa mā khalfahum wa lā yuḥīṭụna bihī \'ilmā'),
(2458, 20, 111, '۞ وَعَنَتِ الْوُجُوْهُ لِلْحَيِّ الْقَيُّوْمِۗ وَقَدْ خَابَ مَنْ حَمَلَ ظُلْمًا', 'wa \'anatil-wujụhu lil-ḥayyil-qayyụm, wa qad khāba man ḥamala ẓulmā'),
(2459, 20, 112, 'وَمَنْ يَّعْمَلْ مِنَ الصّٰلِحٰتِ وَهُوَ مُؤْمِنٌ فَلَا يَخٰفُ ظُلْمًا وَّلَا هَضْمًا', 'wa may ya\'mal minaṣ-ṣāliḥāti wa huwa mu`minun fa lā yakhāfu ẓulmaw wa lā haḍmā'),
(2460, 20, 113, 'وَكَذٰلِكَ اَنْزَلْنٰهُ قُرْاٰنًا عَرَبِيًّا وَّصَرَّفْنَا فِيْهِ مِنَ الْوَعِيْدِ لَعَلَّهُمْ يَتَّقُوْنَ اَوْ يُحْدِثُ لَهُمْ ذِكْرًا', 'wa każālika anzalnāhu qur`ānan \'arabiyyaw wa ṣarrafnā fīhi minal-wa\'īdi la\'allahum yattaqụna au yuḥdiṡu lahum żikrā'),
(2461, 20, 114, 'فَتَعٰلَى اللّٰهُ الْمَلِكُ الْحَقُّۚ وَلَا تَعْجَلْ بِالْقُرْاٰنِ مِنْ قَبْلِ اَنْ يُّقْضٰٓى اِلَيْكَ وَحْيُهٗ ۖوَقُلْ رَّبِّ زِدْنِيْ عِلْمًا', 'fa ta\'ālallāhul-malikul-ḥaqq, wa lā ta\'jal bil-qur`āni ming qabli ay yuqḍā ilaika waḥyuhụ wa qur rabbi zidnī \'ilmā'),
(2462, 20, 115, 'وَلَقَدْ عَهِدْنَآ اِلٰٓى اٰدَمَ مِنْ قَبْلُ فَنَسِيَ وَلَمْ نَجِدْ لَهٗ عَزْمًا', 'wa laqad \'ahidnā ilā ādama ming qablu fa nasiya wa lam najid lahụ \'azmā'),
(2463, 20, 116, 'وَاِذْ قُلْنَا لِلْمَلٰۤىِٕكَةِ اسْجُدُوْا لِاٰدَمَ فَسَجَدُوْٓا اِلَّآ اِبْلِيْسَ اَبٰى ۗ', 'wa iż qulnā lil-malā`ikatisjudụ li`ādama fa sajadū illā iblīsa abā'),
(2464, 20, 117, 'فَقُلْنَا يٰٓاٰدَمُ اِنَّ هٰذَا عَدُوٌّ لَّكَ وَلِزَوْجِكَ فَلَا يُخْرِجَنَّكُمَا مِنَ الْجَنَّةِ فَتَشْقٰى', 'fa qulnā yā ādamu inna hāżā \'aduwwul laka wa lizaujika fa lā yukhrijannakumā minal-jannati fa tasyqā'),
(2465, 20, 118, 'اِنَّ لَكَ اَلَّا تَجُوْعَ فِيْهَا وَلَا تَعْرٰى ۙ', 'inna laka allā tajụ\'a fīhā wa lā ta\'rā'),
(2466, 20, 119, 'وَاَنَّكَ لَا تَظْمَؤُا فِيْهَا وَلَا تَضْحٰى', 'wa annaka lā taẓma`u fīhā wa lā taḍ-ḥā'),
(2467, 20, 120, 'فَوَسْوَسَ اِلَيْهِ الشَّيْطٰنُ قَالَ يٰٓاٰدَمُ هَلْ اَدُلُّكَ عَلٰى شَجَرَةِ الْخُلْدِ وَمُلْكٍ لَّا يَبْلٰى', 'fa waswasa ilaihisy-syaiṭānu qāla yā ādamu hal adulluka \'alā syajaratil-khuldi wa mulkil lā yablā'),
(2468, 20, 121, 'فَاَكَلَا مِنْهَا فَبَدَتْ لَهُمَا سَوْاٰتُهُمَا وَطَفِقَا يَخْصِفٰنِ عَلَيْهِمَا مِنْ وَّرَقِ الْجَنَّةِۚ وَعَصٰٓى اٰدَمُ رَبَّهٗ فَغَوٰى ۖ', 'fa akalā min-hā fa badat lahumā sau`ātuhumā wa ṭafiqā yakhṣifāni \'alaihimā miw waraqil jannah, wa \'aṣā ādamu rabbahụ fa gawā'),
(2469, 20, 122, 'ثُمَّ اجْتَبٰىهُ رَبُّهٗ فَتَابَ عَلَيْهِ وَهَدٰى', 'ṡummajtabāhu rabbuhụ fa tāba \'alaihi wa hadā'),
(2470, 20, 123, 'قَالَ اهْبِطَا مِنْهَا جَمِيعًاۢ بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ ۚفَاِمَّا يَأْتِيَنَّكُمْ مِّنِّيْ هُدًى ەۙ فَمَنِ اتَّبَعَ هُدٰيَ فَلَا يَضِلُّ وَلَا يَشْقٰى', 'qālahbiṭā min-hā jamī\'am ba\'ḍukum liba\'ḍin \'aduww, fa immā ya`tiyannakum minnī hudan fa manittaba\'a hudāya fa lā yaḍillu wa lā yasyqā'),
(2471, 20, 124, 'وَمَنْ اَعْرَضَ عَنْ ذِكْرِيْ فَاِنَّ لَهٗ مَعِيْشَةً ضَنْكًا وَّنَحْشُرُهٗ يَوْمَ الْقِيٰمَةِ اَعْمٰى', 'wa man a\'raḍa \'an żikrī fa inna lahụ ma\'īsyatan ḍangkaw wa naḥsyuruhụ yaumal-qiyāmati a\'mā'),
(2472, 20, 125, 'قَالَ رَبِّ لِمَ حَشَرْتَنِيْٓ اَعْمٰى وَقَدْ كُنْتُ بَصِيْرًا', 'qāla rabbi lima ḥasyartanī a\'mā wa qad kuntu baṣīrā'),
(2473, 20, 126, 'قَالَ كَذٰلِكَ اَتَتْكَ اٰيٰتُنَا فَنَسِيْتَهَاۚ وَكَذٰلِكَ الْيَوْمَ تُنْسٰى', 'qāla każālika atatka āyātunā fa nasītahā, wa każālikal-yauma tunsā'),
(2474, 20, 127, 'وَكَذٰلِكَ نَجْزِيْ مَنْ اَسْرَفَ وَلَمْ يُؤْمِنْۢ بِاٰيٰتِ رَبِّهٖۗ وَلَعَذَابُ الْاٰخِرَةِ اَشَدُّ وَاَبْقٰى', 'wa każālika najzī man asrafa wa lam yu`mim bi`āyāti rabbih, wa la\'ażābul-ākhirati asyaddu wa abqā'),
(2475, 20, 128, 'اَفَلَمْ يَهْدِ لَهُمْ كَمْ اَهْلَكْنَا قَبْلَهُمْ مِّنَ الْقُرُوْنِ يَمْشُوْنَ فِيْ مَسٰكِنِهِمْۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّاُولِى النُّهٰى', 'a fa lam yahdi lahum kam ahlaknā qablahum minal-qurụni yamsyụna fī masākinihim, inna fī żālika la`āyātil li`ulin-nuhā'),
(2476, 20, 129, 'وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَّبِّكَ لَكَانَ لِزَامًا وَّاَجَلٌ مُّسَمًّى ۗ', 'walau lā kalimatun sabaqat mir rabbika lakāna lizāmaw wa ajalum musammā'),
(2477, 20, 130, 'فَاصْبِرْ عَلٰى مَا يَقُوْلُوْنَ وَسَبِّحْ بِحَمْدِ رَبِّكَ قَبْلَ طُلُوْعِ الشَّمْسِ وَقَبْلَ غُرُوْبِهَا ۚوَمِنْ اٰنَاۤئِ الَّيْلِ فَسَبِّحْ وَاَطْرَافَ النَّهَارِ لَعَلَّكَ تَرْضٰى', 'faṣbir \'alā mā yaqụlụna wa sabbiḥ biḥamdi rabbika qabla ṭulụ\'isy-syamsi wa qabla gurụbihā, wa min ānā`il-laili fa sabbiḥ wa aṭrāfan-nahāri la\'allaka tarḍā'),
(2478, 20, 131, 'وَلَا تَمُدَّنَّ عَيْنَيْكَ اِلٰى مَا مَتَّعْنَا بِهٖٓ اَزْوَاجًا مِّنْهُمْ زَهْرَةَ الْحَيٰوةِ الدُّنْيَا ەۙ لِنَفْتِنَهُمْ فِيْهِ ۗوَرِزْقُ رَبِّكَ خَيْرٌ وَّاَبْقٰى', 'wa lā tamuddanna \'ainaika ilā mā matta\'nā bihī azwājam min-hum zahratal-ḥayātid-dun-yā linaftinahum fīh, wa rizqu rabbika khairuw wa abqā'),
(2479, 20, 132, 'وَأْمُرْ اَهْلَكَ بِالصَّلٰوةِ وَاصْطَبِرْ عَلَيْهَاۗ لَا نَسْـَٔلُكَ رِزْقًاۗ نَحْنُ نَرْزُقُكَۗ وَالْعَاقِبَةُ لِلتَّقْوٰى', 'wa`mur ahlaka biṣ-ṣalāti waṣṭabir \'alaihā, lā nas`aluka rizqā, naḥnu narzuquk, wal-\'āqibatu lit-taqwā'),
(2480, 20, 133, 'وَقَالُوْا لَوْلَا يَأْتِيْنَا بِاٰيَةٍ مِّنْ رَّبِّهٖۗ اَوَلَمْ تَأْتِهِمْ بَيِّنَةُ مَا فِى الصُّحُفِ الْاُولٰى', 'wa qālụ lau lā ya`tīnā bi`āyatim mir rabbih, a wa lam ta`tihim bayyinatu mā fiṣ-ṣuḥufil-ụlā'),
(2481, 20, 134, 'وَلَوْ اَنَّآ اَهْلَكْنٰهُمْ بِعَذَابٍ مِّنْ قَبْلِهٖ لَقَالُوْا رَبَّنَا لَوْلَآ اَرْسَلْتَ اِلَيْنَا رَسُوْلًا فَنَتَّبِعَ اٰيٰتِكَ مِنْ قَبْلِ اَنْ نَّذِلَّ وَنَخْزٰى', 'walau annā ahlaknāhum bi\'ażābim ming qablihī laqālụ rabbanā lau lā arsalta ilainā rasụlan fa nattabi\'a āyātika ming qabli an nażilla wa nakhzā');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(2482, 20, 135, 'قُلْ كُلٌّ مُّتَرَبِّصٌ فَتَرَبَّصُوْاۚ فَسَتَعْلَمُوْنَ مَنْ اَصْحٰبُ الصِّرَاطِ السَّوِيِّ وَمَنِ اهْتَدٰى ۔', 'qul kullum mutarabbiṣun fa tarabbaṣụ, fa sata\'lamụna man aṣ-ḥābuṣ-ṣirāṭis-sawiyyi wa manihtadā'),
(2483, 21, 1, 'اِقْتَرَبَ لِلنَّاسِ حِسَابُهُمْ وَهُمْ فِيْ غَفْلَةٍ مُّعْرِضُوْنَ ۚ', 'iqtaraba lin-nāsi ḥisābuhum wa hum fī gaflatim mu\'riḍụn'),
(2484, 21, 2, 'مَا يَأْتِيْهِمْ مِّنْ ذِكْرٍ مِّنْ رَّبِّهِمْ مُّحْدَثٍ اِلَّا اسْتَمَعُوْهُ وَهُمْ يَلْعَبُوْنَ ۙ', 'mā ya`tīhim min żikrim mir rabbihim muḥdaṡin illastama\'ụhu wa hum yal\'abụn'),
(2485, 21, 3, 'لَاهِيَةً قُلُوْبُهُمْۗ وَاَسَرُّوا النَّجْوَىۖ الَّذِيْنَ ظَلَمُوْاۖ هَلْ هٰذَآ اِلَّا بَشَرٌ مِّثْلُكُمْۚ اَفَتَأْتُوْنَ السِّحْرَ وَاَنْتُمْ تُبْصِرُوْنَ', 'lāhiyatang qulụbuhum, wa asarrun-najwallażīna ẓalamụ hal hāżā illā basyarum miṡlukum, a fa ta`tụnas-siḥra wa antum tubṣirụn'),
(2486, 21, 4, 'قٰلَ رَبِّيْ يَعْلَمُ الْقَوْلَ فِى السَّمَاۤءِ وَالْاَرْضِۖ وَهُوَ السَّمِيْعُ الْعَلِيْمُ', 'qāla rabbī ya\'lamul-qaula fis-samā`i wal-arḍi wa huwas-samī\'ul-\'alīm'),
(2487, 21, 5, 'بَلْ قَالُوْٓا اَضْغَاثُ اَحْلَامٍۢ بَلِ افْتَرٰىهُ بَلْ هُوَ شَاعِرٌۚ فَلْيَأْتِنَا بِاٰيَةٍ كَمَآ اُرْسِلَ الْاَوَّلُوْنَ', 'bal qālū aḍgāṡu aḥlām, baliftarāhu bal huwa syā\'ir, falya`tinā bi`āyating kamā ursilal-awwalụn'),
(2488, 21, 6, 'مَآ اٰمَنَتْ قَبْلَهُمْ مِّنْ قَرْيَةٍ اَهْلَكْنٰهَاۚ اَفَهُمْ يُؤْمِنُوْنَ', 'mā āmanat qablahum ming qaryatin ahlaknāhā, a fa hum yu`minụn'),
(2489, 21, 7, 'وَمَآ اَرْسَلْنَا قَبْلَكَ اِلَّا رِجَالًا نُّوْحِيْٓ اِلَيْهِمْ فَسْـَٔلُوْٓا اَهْلَ الذِّكْرِ اِنْ كُنْتُمْ لَا تَعْلَمُوْنَ', 'wa mā arsalnā qablaka illā rijālan nụḥī ilaihim fas`alū ahlaż-żikri ing kuntum lā ta\'lamụn'),
(2490, 21, 8, 'وَمَا جَعَلْنٰهُمْ جَسَدًا لَّا يَأْكُلُوْنَ الطَّعَامَ وَمَا كَانُوْا خٰلِدِيْنَ', 'wa mā ja\'alnāhum jasadal lā ya`kulụnaṭ-ṭa\'āma wa mā kānụ khālidīn'),
(2491, 21, 9, 'ثُمَّ صَدَقْنٰهُمُ الْوَعْدَ فَاَنْجَيْنٰهُمْ وَمَنْ نَّشَاۤءُ وَاَهْلَكْنَا الْمُسْرِفِيْنَ', 'ṡumma ṣadaqnāhumul-wa\'da fa anjaināhum wa man nasyā`u wa ahlaknal-musrifīn'),
(2492, 21, 10, 'لَقَدْ اَنْزَلْنَآ اِلَيْكُمْ كِتٰبًا فِيْهِ ذِكْرُكُمْۗ اَفَلَا تَعْقِلُوْنَ', 'laqad anzalnā ilaikum kitāban fīhi żikrukum, a fa lā ta\'qilụn'),
(2493, 21, 11, 'وَكَمْ قَصَمْنَا مِنْ قَرْيَةٍ كَانَتْ ظَالِمَةً وَّاَنْشَأْنَا بَعْدَهَا قَوْمًا اٰخَرِيْنَ', 'wa kam qaṣamnā ming qaryating kānat ẓālimataw wa ansya`nā ba\'dahā qauman ākharīn'),
(2494, 21, 12, 'فَلَمَّآ اَحَسُّوْا بَأْسَنَآ اِذَا هُمْ مِّنْهَا يَرْكُضُوْنَ ۗ', 'fa lammā aḥassụ ba`sanā iżā hum min-hā yarkuḍụn'),
(2495, 21, 13, 'لَا تَرْكُضُوْا وَارْجِعُوْٓا اِلٰى مَآ اُتْرِفْتُمْ فِيْهِ وَمَسٰكِنِكُمْ لَعَلَّكُمْ تُسْـَٔلُوْنَ', 'lā tarkuḍụ warji\'ū ilā mā utriftum fīhi wa masākinikum la\'allakum tus`alụn'),
(2496, 21, 14, 'قَالُوْا يٰوَيْلَنَآ اِنَّا كُنَّا ظٰلِمِيْنَ', 'qālụ yā wailanā innā kunnā ẓālimīn'),
(2497, 21, 15, 'فَمَا زَالَتْ تِّلْكَ دَعْوٰىهُمْ حَتّٰى جَعَلْنٰهُمْ حَصِيْدًا خَامِدِيْنَ', 'fa mā zālat tilka da\'wāhum ḥattā ja\'alnāhum ḥaṣīdan khāmidīn'),
(2498, 21, 16, 'وَمَا خَلَقْنَا السَّمَاۤءَ وَالْاَرْضَ وَمَا بَيْنَهُمَا لٰعِبِيْنَ', 'wa mā khalaqnas-samā`a wal-arḍa wa mā bainahumā lā\'ibīn'),
(2499, 21, 17, 'لَوْ اَرَدْنَآ اَنْ نَّتَّخِذَ لَهْوًا لَّاتَّخَذْنٰهُ مِنْ لَّدُنَّآ ۖاِنْ كُنَّا فٰعِلِيْنَ', 'lau aradnā an nattakhiża lahwal lattakhażnāhu mil ladunnā ing kunnā fā\'ilīn'),
(2500, 21, 18, 'بَلْ نَقْذِفُ بِالْحَقِّ عَلَى الْبَاطِلِ فَيَدْمَغُهٗ فَاِذَا هُوَ زَاهِقٌۗ وَلَكُمُ الْوَيْلُ مِمَّا تَصِفُوْنَ', 'bal naqżifu bil-ḥaqqi \'alal-bāṭili fa yadmaguhụ fa iżā huwa zāhiq, wa lakumul-wailu mimmā taṣifụn'),
(2501, 21, 19, 'وَلَهٗ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِۗ وَمَنْ عِنْدَهٗ لَا يَسْتَكْبِرُوْنَ عَنْ عِبَادَتِهٖ وَلَا يَسْتَحْسِرُوْنَ ۚ', 'wa lahụ man fis-samāwāti wal-arḍ, wa man \'indahụ lā yastakbirụna \'an \'ibādatihī wa lā yastaḥsirụn'),
(2502, 21, 20, 'يُسَبِّحُوْنَ الَّيْلَ وَالنَّهَارَ لَا يَفْتُرُوْنَ', 'yusabbiḥụnal-laila wan-nahāra lā yafturụn'),
(2503, 21, 21, 'اَمِ اتَّخَذُوْٓا اٰلِهَةً مِّنَ الْاَرْضِ هُمْ يُنْشِرُوْنَ', 'amittakhażū ālihatam minal-arḍi hum yunsyirụn'),
(2504, 21, 22, 'لَوْ كَانَ فِيْهِمَآ اٰلِهَةٌ اِلَّا اللّٰهُ لَفَسَدَتَاۚ فَسُبْحٰنَ اللّٰهِ رَبِّ الْعَرْشِ عَمَّا يَصِفُوْنَ', 'lau kāna fīhimā ālihatun illallāhu lafasadatā, fa sub-ḥānallāhi rabbil-\'arsyi \'ammā yaṣifụn'),
(2505, 21, 23, 'لَا يُسْـَٔلُ عَمَّا يَفْعَلُ وَهُمْ يُسْـَٔلُوْنَ', 'lā yus`alu \'ammā yaf\'alu wa hum yus`alụn'),
(2506, 21, 24, 'اَمِ اتَّخَذُوْا مِنْ دُوْنِهٖٓ اٰلِهَةً ۗقُلْ هَاتُوْا بُرْهَانَكُمْۚ هٰذَا ذِكْرُ مَنْ مَّعِيَ وَذِكْرُ مَنْ قَبْلِيْۗ بَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَۙ الْحَقَّ فَهُمْ مُّعْرِضُوْنَ', 'amittakhażụ min dụnihī ālihah, qul hātụ bur-hānakum, hāżā żikru mam ma\'iya wa żikru mang qablī, bal akṡaruhum lā ya\'lamụnal-ḥaqqa fa hum mu\'riḍụn'),
(2507, 21, 25, 'وَمَآ اَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رَّسُوْلٍ اِلَّا نُوْحِيْٓ اِلَيْهِ اَنَّهٗ لَآ اِلٰهَ اِلَّآ اَنَا۠ فَاعْبُدُوْنِ', 'wa mā arsalnā ming qablika mir rasụlin illā nụḥī ilaihi annahụ lā ilāha illā ana fa\'budụn'),
(2508, 21, 26, 'وَقَالُوا اتَّخَذَ الرَّحْمٰنُ وَلَدًا سُبْحٰنَهٗ ۗبَلْ عِبَادٌ مُّكْرَمُوْنَ ۙ', 'wa qāluttakhażar-raḥmānu waladan sub-ḥānah, bal \'ibādum mukramụn'),
(2509, 21, 27, 'لَا يَسْبِقُوْنَهٗ بِالْقَوْلِ وَهُمْ بِاَمْرِهٖ يَعْمَلُوْنَ', 'lā yasbiqụnahụ bil-qauli wa hum bi`amrihī ya\'malụn'),
(2510, 21, 28, 'يَعْلَمُ مَا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْ وَلَا يَشْفَعُوْنَۙ اِلَّا لِمَنِ ارْتَضٰى وَهُمْ مِّنْ خَشْيَتِهٖ مُشْفِقُوْنَ', 'ya\'lamu mā baina aidīhim wa mā khalfahum wa lā yasyfa\'ụna illā limanirtaḍā wa hum min khasy-yatihī musyfiqụn'),
(2511, 21, 29, '۞ وَمَنْ يَّقُلْ مِنْهُمْ اِنِّيْٓ اِلٰهٌ مِّنْ دُوْنِهٖ فَذٰلِكَ نَجْزِيْهِ جَهَنَّمَۗ كَذٰلِكَ نَجْزِى الظّٰلِمِيْنَ', 'wa may yaqul min-hum innī ilāhum min dụnihī fa żālika najzīhi jahannam, każālika najziẓ-ẓālimīn'),
(2512, 21, 30, 'اَوَلَمْ يَرَ الَّذِيْنَ كَفَرُوْٓا اَنَّ السَّمٰوٰتِ وَالْاَرْضَ كَانَتَا رَتْقًا فَفَتَقْنٰهُمَاۗ وَجَعَلْنَا مِنَ الْمَاۤءِ كُلَّ شَيْءٍ حَيٍّۗ اَفَلَا يُؤْمِنُوْنَ', 'a wa lam yarallażīna kafarū annas-samāwāti wal-arḍa kānatā ratqan fa fataqnāhumā, wa ja\'alnā minal-mā`i kulla syai`in ḥayy, a fa lā yu`minụn'),
(2513, 21, 31, 'وَجَعَلْنَا فِى الْاَرْضِ رَوَاسِيَ اَنْ تَمِيْدَ بِهِمْۖ وَجَعَلْنَا فِيْهَا فِجَاجًا سُبُلًا لَّعَلَّهُمْ يَهْتَدُوْنَ', 'wa ja\'alnā fil-arḍi rawāsiya an tamīda bihim wa ja\'alnā fīhā fijājan subulal la\'allahum yahtadụn'),
(2514, 21, 32, 'وَجَعَلْنَا السَّمَاۤءَ سَقْفًا مَّحْفُوْظًاۚ وَهُمْ عَنْ اٰيٰتِهَا مُعْرِضُوْنَ', 'wa ja\'alnas-samā`a saqfam maḥfụẓā, wa hum \'an āyātihā mu\'riḍụn'),
(2515, 21, 33, 'وَهُوَ الَّذِيْ خَلَقَ الَّيْلَ وَالنَّهَارَ وَالشَّمْسَ وَالْقَمَرَۗ كُلٌّ فِيْ فَلَكٍ يَّسْبَحُوْنَ', 'wa huwallażī khalaqal-laila wan-nahāra wasy-syamsa wal-qamar, kullun fī falakiy yasbaḥụn'),
(2516, 21, 34, 'وَمَا جَعَلْنَا لِبَشَرٍ مِّنْ قَبْلِكَ الْخُلْدَۗ اَفَا۟ىِٕنْ مِّتَّ فَهُمُ الْخٰلِدُوْنَ', 'wa mā ja\'alnā libasyarim ming qablikal-khuld, a fā im mitta fa humul-khālidụn'),
(2517, 21, 35, 'كُلُّ نَفْسٍ ذَاۤىِٕقَةُ الْمَوْتِۗ وَنَبْلُوْكُمْ بِالشَّرِّ وَالْخَيْرِ فِتْنَةً ۗوَاِلَيْنَا تُرْجَعُوْنَ', 'kullu nafsin żā`iqatul-maụt, wa nablụkum bisy-syarri wal-khairi fitnah, wa ilainā turja\'ụn'),
(2518, 21, 36, 'وَاِذَا رَاٰكَ الَّذِيْنَ كَفَرُوْٓا اِنْ يَّتَّخِذُوْنَكَ اِلَّا هُزُوًاۗ اَهٰذَا الَّذِيْ يَذْكُرُ اٰلِهَتَكُمْۚ وَهُمْ بِذِكْرِ الرَّحْمٰنِ هُمْ كٰفِرُوْنَ', 'wa iżā ra`ākallażīna kafarū iy yattakhiżụnaka illā huzuwā, a hāżallażī yażkuru ālihatakum, wa hum biżikrir-raḥmāni hum kāfirụn'),
(2519, 21, 37, 'خُلِقَ الْاِنْسَانُ مِنْ عَجَلٍۗ سَاُورِيْكُمْ اٰيٰتِيْ فَلَا تَسْتَعْجِلُوْنِ', 'khuliqal-insānu min \'ajal, sa`urīkum āyātī fa lā tasta\'jilụn'),
(2520, 21, 38, 'وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'wa yaqụlụna matā hāżal-wa\'du ing kuntum ṣādiqīn'),
(2521, 21, 39, 'لَوْ يَعْلَمُ الَّذِيْنَ كَفَرُوْا حِيْنَ لَا يَكُفُّوْنَ عَنْ وُّجُوْهِهِمُ النَّارَ وَلَا عَنْ ظُهُوْرِهِمْ وَلَا هُمْ يُنْصَرُوْنَ', 'lau ya\'lamullażīna kafarụ ḥīna lā yakuffụna \'aw wujụhihimun-nāra wa lā \'an ẓuhụrihim wa lā hum yunṣarụn'),
(2522, 21, 40, 'بَلْ تَأْتِيْهِمْ بَغْتَةً فَتَبْهَتُهُمْ فَلَا يَسْتَطِيْعُوْنَ رَدَّهَا وَلَا هُمْ يُنْظَرُوْنَ', 'bal ta`tīhim bagtatan fa tab-hatuhum fa lā yastaṭī\'ụna raddahā wa lā hum yunẓarụn'),
(2523, 21, 41, 'وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِّنْ قَبْلِكَ فَحَاقَ بِالَّذِيْنَ سَخِرُوْا مِنْهُمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ', 'wa laqadistuhzi`a birusulim ming qablika fa ḥāqa billażīna sakhirụ min-hum mā kānụ bihī yastahzi`ụn'),
(2524, 21, 42, 'قُلْ مَنْ يَّكْلَؤُكُمْ بِالَّيْلِ وَالنَّهَارِ مِنَ الرَّحْمٰنِۗ بَلْ هُمْ عَنْ ذِكْرِ رَبِّهِمْ مُّعْرِضُوْنَ', 'qul may yakla`ukum bil-laili wan-nahāri minar-raḥmān, bal hum \'an żikri rabbihim mu\'riḍụn'),
(2525, 21, 43, 'اَمْ لَهُمْ اٰلِهَةٌ تَمْنَعُهُمْ مِّنْ دُوْنِنَاۗ لَا يَسْتَطِيْعُوْنَ نَصْرَ اَنْفُسِهِمْ وَلَا هُمْ مِّنَّا يُصْحَبُوْنَ', 'am lahum ālihatun tamna\'uhum min dụninā, lā yastaṭī\'ụna naṣra anfusihim wa lā hum minnā yuṣ-ḥabụn'),
(2526, 21, 44, 'بَلْ مَتَّعْنَا هٰٓؤُلَاۤءِ وَاٰبَاۤءَهُمْ حَتّٰى طَالَ عَلَيْهِمُ الْعُمُرُۗ اَفَلَا يَرَوْنَ اَنَّا نَأْتِى الْاَرْضَ نَنْقُصُهَا مِنْ اَطْرَافِهَاۗ اَفَهُمُ الْغٰلِبُوْنَ', 'bal matta\'nā hā`ulā`i wa ābā`ahum ḥattā ṭāla \'alaihimul-\'umur, a fa lā yarauna annā na`til-arḍa nangquṣuhā min aṭrāfihā, a fa humul-gālibụn'),
(2527, 21, 45, 'قُلْ اِنَّمَآ اُنْذِرُكُمْ بِالْوَحْيِۖ وَلَا يَسْمَعُ الصُّمُّ الدُّعَاۤءَ اِذَا مَا يُنْذَرُوْنَ', 'qul innamā unżirukum bil-waḥyi wa lā yasma\'uṣ-ṣummud-du\'ā`a iżā mā yunżarụn'),
(2528, 21, 46, 'وَلَىِٕنْ مَّسَّتْهُمْ نَفْحَةٌ مِّنْ عَذَابِ رَبِّكَ لَيَقُوْلُنَّ يٰوَيْلَنَآ اِنَّا كُنَّا ظٰلِمِيْنَ', 'wa la`im massat-hum naf-ḥatum min \'ażābi rabbika layaqụlunna yā wailanā innā kunnā ẓālimīn'),
(2529, 21, 47, 'وَنَضَعُ الْمَوَازِيْنَ الْقِسْطَ لِيَوْمِ الْقِيٰمَةِ فَلَا تُظْلَمُ نَفْسٌ شَيْـًٔاۗ وَاِنْ كَانَ مِثْقَالَ حَبَّةٍ مِّنْ خَرْدَلٍ اَتَيْنَا بِهَاۗ وَكَفٰى بِنَا حَاسِبِيْنَ', 'wa naḍa\'ul-mawāzīnal-qisṭa liyaumil-qiyāmati fa lā tuẓlamu nafsun syai`ā, wa ing kāna miṡqāla ḥabbatim min khardalin atainā bihā, wa kafā binā ḥāsibīn'),
(2530, 21, 48, 'وَلَقَدْ اٰتَيْنَا مُوْسٰى وَهٰرُوْنَ الْفُرْقَانَ وَضِيَاۤءً وَّذِكْرًا لِّلْمُتَّقِيْنَ ۙ', 'wa laqad ātainā mụsā wa hārụnal-furqāna wa ḍiyā`aw wa żikral lil-muttaqīn'),
(2531, 21, 49, 'الَّذِيْنَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ وَهُمْ مِّنَ السَّاعَةِ مُشْفِقُوْنَ', 'allażīna yakhsyauna rabbahum bil-gaibi wa hum minas-sā\'ati musyfiqụn'),
(2532, 21, 50, 'وَهٰذَا ذِكْرٌ مُّبٰرَكٌ اَنْزَلْنٰهُۗ اَفَاَنْتُمْ لَهٗ مُنْكِرُوْنَ', 'wa hāżā żikrum mubārakun anzalnāh, a fa antum lahụ mungkirụn'),
(2533, 21, 51, '۞ وَلَقَدْ اٰتَيْنَآ اِبْرٰهِيْمَ رُشْدَهٗ مِنْ قَبْلُ وَكُنَّا بِهٖ عٰلِمِيْنَ', 'wa laqad ātainā ibrāhīma rusydahụ ming qablu wa kunnā bihī \'ālimīn'),
(2534, 21, 52, 'اِذْ قَالَ لِاَبِيْهِ وَقَوْمِهٖ مَا هٰذِهِ التَّمَاثِيْلُ الَّتِيْٓ اَنْتُمْ لَهَا عَاكِفُوْنَ', 'iż qāla li`abīhi wa qaumihī mā hāżihit-tamāṡīlullatī antum lahā \'ākifụn'),
(2535, 21, 53, 'قَالُوْا وَجَدْنَآ اٰبَاۤءَنَا لَهَا عٰبِدِيْنَ', 'qālụ wajadnā ābā`anā lahā \'ābidīn'),
(2536, 21, 54, 'قَالَ لَقَدْ كُنْتُمْ اَنْتُمْ وَاٰبَاۤؤُكُمْ فِيْ ضَلٰلٍ مُّبِيْنٍ', 'qāla laqad kuntum antum wa ābā`ukum fī ḍalālim mubīn'),
(2537, 21, 55, 'قَالُوْٓا اَجِئْتَنَا بِالْحَقِّ اَمْ اَنْتَ مِنَ اللّٰعِبِيْنَ', 'qālū a ji`tanā bil-ḥaqqi am anta minal-lā\'ibīn'),
(2538, 21, 56, 'قَالَ بَلْ رَّبُّكُمْ رَبُّ السَّمٰوٰتِ وَالْاَرْضِ الَّذِيْ فَطَرَهُنَّۖ وَاَنَا۠ عَلٰى ذٰلِكُمْ مِّنَ الشّٰهِدِيْنَ', 'qāla bar rabbukum rabbus-samāwāti wal-arḍillażī faṭarahunna wa ana \'alā żālikum minasy-syāhidīn'),
(2539, 21, 57, 'وَتَاللّٰهِ لَاَكِيْدَنَّ اَصْنَامَكُمْ بَعْدَ اَنْ تُوَلُّوْا مُدْبِرِيْنَ', 'wa tallāhi la`akīdanna aṣnāmakum ba\'da an tuwallụ mudbirīn'),
(2540, 21, 58, 'فَجَعَلَهُمْ جُذَاذًا اِلَّا كَبِيْرًا لَّهُمْ لَعَلَّهُمْ اِلَيْهِ يَرْجِعُوْنَ', 'fa ja\'alahum jużāżan illā kabīral lahum la\'allahum ilaihi yarji\'ụn'),
(2541, 21, 59, 'قَالُوْا مَنْ فَعَلَ هٰذَا بِاٰلِهَتِنَآ اِنَّهٗ لَمِنَ الظّٰلِمِيْنَ', 'qālụ man fa\'ala hāżā bi`ālihatinā innahụ laminaẓ-ẓālimīn'),
(2542, 21, 60, 'قَالُوْا سَمِعْنَا فَتًى يَّذْكُرُهُمْ يُقَالُ لَهٗٓ اِبْرٰهِيْمُ ۗ', 'qālụ sami\'nā fatay yażkuruhum yuqālu lahū ibrāhīm'),
(2543, 21, 61, 'قَالُوْا فَأْتُوْا بِهٖ عَلٰٓى اَعْيُنِ النَّاسِ لَعَلَّهُمْ يَشْهَدُوْنَ', 'qālụ fa`tụ bihī \'alā a\'yunin-nāsi la\'allahum yasy-hadụn'),
(2544, 21, 62, 'قَالُوْٓا ءَاَنْتَ فَعَلْتَ هٰذَا بِاٰلِهَتِنَا يٰٓاِبْرٰهِيْمُ ۗ', 'qālū a anta fa\'alta hāżā bi`ālihatinā yā ibrāhīm'),
(2545, 21, 63, 'قَالَ بَلْ فَعَلَهٗ كَبِيْرُهُمْ هٰذَا فَسْـَٔلُوْهُمْ اِنْ كَانُوْا يَنْطِقُوْنَ', 'qāla bal fa\'alahụ kabīruhum hāżā fas`alụhum ing kānụ yanṭiqụn'),
(2546, 21, 64, 'فَرَجَعُوْٓا اِلٰٓى اَنْفُسِهِمْ فَقَالُوْٓا اِنَّكُمْ اَنْتُمُ الظّٰلِمُوْنَ ۙ', 'fa raja\'ū ilā anfusihim fa qālū innakum antumuẓ-ẓālimụn'),
(2547, 21, 65, 'ثُمَّ نُكِسُوْا عَلٰى رُءُوْسِهِمْۚ لَقَدْ عَلِمْتَ مَا هٰٓؤُلَاۤءِ يَنْطِقُوْنَ', 'ṡumma nukisụ \'alā ru`ụsihim, laqad \'alimta mā hā`ulā`i yanṭiqụn'),
(2548, 21, 66, 'قَالَ اَفَتَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَا يَنْفَعُكُمْ شَيْـًٔا وَّلَا يَضُرُّكُمْ ۗ', 'qāla a fa ta\'budụna min dụnillāhi mā lā yanfa\'ukum syai`aw wa lā yaḍurrukum'),
(2549, 21, 67, 'اُفٍّ لَّكُمْ وَلِمَا تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ ۗاَفَلَا تَعْقِلُوْنَ', 'uffil lakum wa limā ta\'budụna min dụnillāh, a fa lā ta\'qilụn'),
(2550, 21, 68, 'قَالُوْا حَرِّقُوْهُ وَانْصُرُوْٓا اٰلِهَتَكُمْ اِنْ كُنْتُمْ فٰعِلِيْنَ', 'qālụ ḥarriqụhu wanṣurū ālihatakum ing kuntum fā\'ilīn'),
(2551, 21, 69, 'قُلْنَا يَا نَارُ كُوْنِيْ بَرْدًا وَّسَلٰمًا عَلٰٓى اِبْرٰهِيْمَ ۙ', 'qulnā yā nāru kụnī bardaw wa salāman \'alā ibrāhīm'),
(2552, 21, 70, 'وَاَرَادُوْا بِهٖ كَيْدًا فَجَعَلْنٰهُمُ الْاَخْسَرِيْنَ ۚ', 'wa arādụ bihī kaidan fa ja\'alnāhumul-akhsarīn'),
(2553, 21, 71, 'وَنَجَّيْنٰهُ وَلُوْطًا اِلَى الْاَرْضِ الَّتِيْ بٰرَكْناَ فِيْهَا لِلْعٰلَمِيْنَ', 'wa najjaināhu wa lụṭan ilal-arḍillatī bāraknā fīhā lil-\'ālamīn'),
(2554, 21, 72, 'وَوَهَبْنَا لَهٗٓ اِسْحٰقَ وَيَعْقُوْبَ نَافِلَةً ۗوَكُلًّا جَعَلْنَا صٰلِحِيْنَ', 'wa wahabnā lahū is-ḥāqa wa ya\'qụba nāfilah, wa kullan ja\'alnā ṣāliḥīn'),
(2555, 21, 73, 'وَجَعَلْنٰهُمْ اَىِٕمَّةً يَّهْدُوْنَ بِاَمْرِنَا وَاَوْحَيْنَآ اِلَيْهِمْ فِعْلَ الْخَيْرٰتِ وَاِقَامَ الصَّلٰوةِ وَاِيْتَاۤءَ الزَّكٰوةِۚ وَكَانُوْا لَنَا عٰبِدِيْنَ ۙ', 'wa ja\'alnāhum a`immatay yahdụna bi`amrinā wa auḥainā ilaihim fi\'lal-khairāti wa iqāmaṣ-ṣalāti wa ītā`az-zakāh, wa kānụ lanā \'ābidīn'),
(2556, 21, 74, 'وَلُوْطًا اٰتَيْنٰهُ حُكْمًا وَّعِلْمًا وَّنَجَّيْنٰهُ مِنَ الْقَرْيَةِ الَّتِيْ كَانَتْ تَّعْمَلُ الْخَبٰۤىِٕثَ ۗاِنَّهُمْ كَانُوْا قَوْمَ سَوْءٍ فٰسِقِيْنَۙ', 'wa lụṭan ātaināhu ḥukmaw wa \'ilmaw wa najjaināhu minal-qaryatillatī kānat ta\'malul-khabā`iṡ, innahum kānụ qauma sau`in fāsiqīn'),
(2557, 21, 75, 'وَاَدْخَلْنٰهُ فِيْ رَحْمَتِنَاۗ اِنَّهٗ مِنَ الصّٰلِحِيْنَ', 'wa adkhalnāhu fī raḥmatinā, innahụ minaṣ-ṣāliḥīn'),
(2558, 21, 76, 'وَنُوْحًا اِذْ نَادٰى مِنْ قَبْلُ فَاسْتَجَبْنَا لَهٗ فَنَجَّيْنٰهُ وَاَهْلَهٗ مِنَ الْكَرْبِ الْعَظِيْمِ ۚ', 'wa nụḥan iż nādā ming qablu fastajabnā lahụ fa najjaināhu wa ahlahụ minal-karbil-\'aẓīm'),
(2559, 21, 77, 'وَنَصَرْنٰهُ مِنَ الْقَوْمِ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَاۗ اِنَّهُمْ كَانُوْا قَوْمَ سَوْءٍ فَاَغْرَقْنٰهُمْ اَجْمَعِيْنَ', 'wa naṣarnāhu minal-qaumillażīna każżabụ bi`āyātinā, innahum kānụ qauma sau`in fa agraqnāhum ajma\'īn'),
(2560, 21, 78, 'وَدَاوٗدَ وَسُلَيْمٰنَ اِذْ يَحْكُمٰنِ فِى الْحَرْثِ اِذْ نَفَشَتْ فِيْهِ غَنَمُ الْقَوْمِۚ وَكُنَّا لِحُكْمِهِمْ شٰهِدِيْنَ ۖ', 'wa dāwụda wa sulaimāna iż yaḥkumāni fil-ḥarṡi iż nafasyat fīhi ganamul-qaụm, wa kunnā liḥukmihim syāhidīn'),
(2561, 21, 79, 'فَفَهَّمْنٰهَا سُلَيْمٰنَۚ وَكُلًّا اٰتَيْنَا حُكْمًا وَّعِلْمًاۖ وَّسَخَّرْنَا مَعَ دَاوٗدَ الْجِبَالَ يُسَبِّحْنَ وَالطَّيْرَۗ وَكُنَّا فٰعِلِيْنَ', 'fa fahhamnāhā sulaimān, wa kullan ātainā ḥukmaw wa \'ilmaw wa sakhkharnā ma\'a dāwụdal-jibāla yusabbiḥna waṭ-ṭaīr, wa kunnā fā\'ilīn'),
(2562, 21, 80, 'وَعَلَّمْنٰهُ صَنْعَةَ لَبُوْسٍ لَّكُمْ لِتُحْصِنَكُمْ مِّنْۢ بَأْسِكُمْۚ فَهَلْ اَنْتُمْ شَاكِرُوْنَ', 'wa \'allamnāhu ṣan\'ata labụsil lakum lituhṣinakum mim ba`sikum, fa hal antum syākirụn'),
(2563, 21, 81, 'وَلِسُلَيْمٰنَ الرِّيْحَ عَاصِفَةً تَجْرِيْ بِاَمْرِهٖٓ اِلَى الْاَرْضِ الَّتِيْ بٰرَكْنَا فِيْهَاۗ وَكُنَّا بِكُلِّ شَيْءٍ عٰلِمِيْنَ', 'wa lisulaimānar-rīḥa \'āṣifatan tajrī bi`amrihī ilal-arḍillatī bāraknā fīhā, wa kunnā bikulli syai`in \'ālimīn'),
(2564, 21, 82, 'وَمِنَ الشَّيٰطِيْنِ مَنْ يَّغُوْصُوْنَ لَهٗ وَيَعْمَلُوْنَ عَمَلًا دُوْنَ ذٰلِكَۚ وَكُنَّا لَهُمْ حٰفِظِيْنَ ۙ', 'wa minasy-syayāṭīni may yagụṣụna lahụ wa ya\'malụna \'amalan dụna żālik, wa kunnā lahum ḥāfiẓīn'),
(2565, 21, 83, '۞ وَاَيُّوْبَ اِذْ نَادٰى رَبَّهٗٓ اَنِّيْ مَسَّنِيَ الضُّرُّ وَاَنْتَ اَرْحَمُ الرَّاحِمِيْنَ ۚ', 'wa ayyụba iż nādā rabbahū annī massaniyaḍ-ḍurru wa anta ar-ḥamur-rāḥimīn'),
(2566, 21, 84, 'فَاسْتَجَبْنَا لَهٗ فَكَشَفْنَا مَا بِهٖ مِنْ ضُرٍّ وَّاٰتَيْنٰهُ اَهْلَهٗ وَمِثْلَهُمْ مَّعَهُمْ رَحْمَةً مِّنْ عِنْدِنَا وَذِكْرٰى لِلْعٰبِدِيْنَ ۚ', 'fastajabnā lahụ fa kasyafnā mā bihī min ḍurriw wa ātaināhu ahlahụ wa miṡlahum ma\'ahum raḥmatam min \'indinā wa żikrā lil-\'ābidīn'),
(2567, 21, 85, 'وَاِسْمٰعِيْلَ وَاِدْرِيْسَ وَذَا الْكِفْلِۗ كُلٌّ مِّنَ الصّٰبِرِيْنَ ۙ', 'wa ismā\'īla wa idrīsa wa żal-kifl, kullum minaṣ-ṣābirīn'),
(2568, 21, 86, 'وَاَدْخَلْنٰهُمْ فِيْ رَحْمَتِنَاۗ اِنَّهُمْ مِّنَ الصّٰلِحِيْنَ', 'wa adkhalnāhum fī raḥmatinā, innahum minaṣ-ṣāliḥīn'),
(2569, 21, 87, 'وَذَا النُّوْنِ اِذْ ذَّهَبَ مُغَاضِبًا فَظَنَّ اَنْ لَّنْ نَّقْدِرَ عَلَيْهِ فَنَادٰى فِى الظُّلُمٰتِ اَنْ لَّآ اِلٰهَ اِلَّآ اَنْتَ سُبْحٰنَكَ اِنِّيْ كُنْتُ مِنَ الظّٰلِمِيْنَ ۚ', 'wa żan-nụni iż żahaba mugāḍiban fa ẓanna al lan naqdira \'alaihi fa nādā fiẓ-ẓulumāti al lā ilāha illā anta sub-ḥānaka innī kuntu minaẓ-ẓālimīn'),
(2570, 21, 88, 'فَاسْتَجَبْنَا لَهٗۙ وَنَجَّيْنٰهُ مِنَ الْغَمِّۗ وَكَذٰلِكَ نُـْۨجِى الْمُؤْمِنِيْنَ', 'fastajabnā lahụ wa najjaināhu minal-gamm, wa każālika nunjil-mu`minīn'),
(2571, 21, 89, 'وَزَكَرِيَّآ اِذْ نَادٰى رَبَّهٗ رَبِّ لَا تَذَرْنِيْ فَرْدًا وَّاَنْتَ خَيْرُ الْوٰرِثِيْنَ ۚ', 'wa zakariyyā iż nādā rabbahụ rabbi lā tażarnī fardaw wa anta khairul-wāriṡīn'),
(2572, 21, 90, 'فَاسْتَجَبْنَا لَهٗ ۖوَوَهَبْنَا لَهٗ يَحْيٰى وَاَصْلَحْنَا لَهٗ زَوْجَهٗۗ اِنَّهُمْ كَانُوْا يُسٰرِعُوْنَ فِى الْخَيْرٰتِ وَيَدْعُوْنَنَا رَغَبًا وَّرَهَبًاۗ وَكَانُوْا لَنَا خٰشِعِيْنَ', 'fastajabnā lahụ wa wahabnā lahụ yaḥyā wa aṣlaḥnā lahụ zaujah, innahum kānụ yusāri\'ụna fil-khairāti wa yad\'ụnanā ragabaw wa rahabā, wa kānụ lanā khāsyi\'īn'),
(2573, 21, 91, 'وَالَّتِيْٓ اَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا فِيْهَا مِنْ رُّوْحِنَا وَجَعَلْنٰهَا وَابْنَهَآ اٰيَةً لِّلْعٰلَمِيْنَ', 'wallatī aḥṣanat farjahā fa nafakhnā fīhā mir rụḥinā wa ja\'alnāhā wabnahā āyatal lil-\'ālamīn'),
(2574, 21, 92, 'اِنَّ هٰذِهٖٓ اُمَّتُكُمْ اُمَّةً وَّاحِدَةًۖ وَّاَنَا۠ رَبُّكُمْ فَاعْبُدُوْنِ', 'inna hāżihī ummatukum ummataw wāḥidataw wa ana rabbukum fa\'budụn'),
(2575, 21, 93, 'وَتَقَطَّعُوْٓا اَمْرَهُمْ بَيْنَهُمْۗ كُلٌّ اِلَيْنَا رَاجِعُوْنَ', 'wa taqaṭṭa\'ū amrahum bainahum, kullun ilainā rāji\'ụn'),
(2576, 21, 94, 'فَمَنْ يَّعْمَلْ مِنَ الصّٰلِحٰتِ وَهُوَ مُؤْمِنٌ فَلَا كُفْرَانَ لِسَعْيِهٖۚ وَاِنَّا لَهٗ كَاتِبُوْنَ', 'fa may ya\'mal minaṣ-ṣāliḥāti wa huwa mu`minun fa lā kufrāna lisa\'yih, wa innā lahụ kātibụn'),
(2577, 21, 95, 'وَحَرَامٌ عَلٰى قَرْيَةٍ اَهْلَكْنٰهَآ اَنَّهُمْ لَا يَرْجِعُوْنَ', 'wa ḥarāmun \'alā qaryatin ahlaknāhā annahum lā yarji\'ụn'),
(2578, 21, 96, 'حَتّٰىٓ اِذَا فُتِحَتْ يَأْجُوْجُ وَمَأْجُوْجُ وَهُمْ مِّنْ كُلِّ حَدَبٍ يَّنْسِلُوْنَ', 'ḥattā iżā futiḥat ya`jụju wa ma`jụju wa hum ming kulli ḥadabiy yansilụn'),
(2579, 21, 97, 'وَاقْتَرَبَ الْوَعْدُ الْحَقُّ فَاِذَا هِيَ شَاخِصَةٌ اَبْصَارُ الَّذِيْنَ كَفَرُوْاۗ يٰوَيْلَنَا قَدْ كُنَّا فِيْ غَفْلَةٍ مِّنْ هٰذَا بَلْ كُنَّا ظٰلِمِيْنَ', 'waqtarabal-wa\'dul-ḥaqqu fa iżā hiya syākhiṣatun abṣārullażīna kafarụ, yā wailanā qad kunnā fī gaflatim min hāżā bal kunnā ẓālimīn'),
(2580, 21, 98, 'اِنَّكُمْ وَمَا تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ حَصَبُ جَهَنَّمَۗ اَنْتُمْ لَهَا وَارِدُوْنَ', 'innakum wa mā ta\'budụna min dụnillāhi ḥaṣabu jahannam, antum lahā wāridụn'),
(2581, 21, 99, 'لَوْ كَانَ هٰٓؤُلَاۤءِ اٰلِهَةً مَّا وَرَدُوْهَاۗ وَكُلٌّ فِيْهَا خٰلِدُوْنَ', 'lau kāna hā`ulā`i ālihatam mā waradụhā, wa kullun fīhā khālidụn'),
(2582, 21, 100, 'لَهُمْ فِيْهَا زَفِيْرٌ وَّهُمْ فِيْهَا لَا يَسْمَعُوْنَ', 'lahum fīhā zafīruw wa hum fīhā lā yasma\'ụn'),
(2583, 21, 101, 'اِنَّ الَّذِيْنَ سَبَقَتْ لَهُمْ مِّنَّا الْحُسْنٰىٓۙ اُولٰۤىِٕكَ عَنْهَا مُبْعَدُوْنَ ۙ', 'innallażīna sabaqat lahum minnal-ḥusnā ulā`ika \'an-hā mub\'adụn'),
(2584, 21, 102, 'لَا يَسْمَعُوْنَ حَسِيْسَهَاۚ وَهُمْ فِيْ مَا اشْتَهَتْ اَنْفُسُهُمْ خٰلِدُوْنَ ۚ', 'lā yasma\'ụna ḥasīsahā, wa hum fī masytahat anfusuhum khālidụn'),
(2585, 21, 103, 'لَا يَحْزُنُهُمُ الْفَزَعُ الْاَكْبَرُ وَتَتَلَقّٰىهُمُ الْمَلٰۤىِٕكَةُۗ هٰذَا يَوْمُكُمُ الَّذِيْ كُنْتُمْ تُوْعَدُوْنَ', 'lā yaḥzunuhumul-faza\'ul-akbaru wa tatalaqqāhumul-malā`ikah, hāżā yaumukumullażī kuntum tụ\'adụn'),
(2586, 21, 104, 'يَوْمَ نَطْوِى السَّمَاۤءَ كَطَيِّ السِّجِلِّ لِلْكُتُبِۗ كَمَا بَدَأْنَآ اَوَّلَ خَلْقٍ نُّعِيْدُهٗۗ وَعْدًا عَلَيْنَاۗ اِنَّا كُنَّا فٰعِلِيْنَ', 'yauma naṭwis-samā`a kaṭayyis-sijilli lil-kutub, kamā bada`nā awwala khalqin nu\'īduh, wa\'dan \'alainā, innā kunnā fā\'ilīn'),
(2587, 21, 105, 'وَلَقَدْ كَتَبْنَا فِى الزَّبُوْرِ مِنْۢ بَعْدِ الذِّكْرِ اَنَّ الْاَرْضَ يَرِثُهَا عِبَادِيَ الصّٰلِحُوْنَ', 'wa laqad katabnā fiz-zabụri mim ba\'diż-żikri annal-arḍa yariṡuhā \'ibādiyaṣ-ṣāliḥụn'),
(2588, 21, 106, 'اِنَّ فِيْ هٰذَا لَبَلٰغًا لِّقَوْمٍ عٰبِدِيْنَ ۗ', 'inna fī hāżā labalāgal liqaumin \'ābidīn'),
(2589, 21, 107, 'وَمَآ اَرْسَلْنٰكَ اِلَّا رَحْمَةً لِّلْعٰلَمِيْنَ', 'wa mā arsalnāka illā raḥmatal lil-\'ālamīn'),
(2590, 21, 108, 'قُلْ اِنَّمَا يُوْحٰٓى اِلَيَّ اَنَّمَآ اِلٰهُكُمْ اِلٰهٌ وَّاحِدٌۚ فَهَلْ اَنْتُمْ مُّسْلِمُوْنَ', 'qul innamā yụḥā ilayya annamā ilāhukum ilāhuw wāḥid, fa hal antum muslimụn'),
(2591, 21, 109, 'فَاِنْ تَوَلَّوْا فَقُلْ اٰذَنْتُكُمْ عَلٰى سَوَاۤءٍۗ وَاِنْ اَدْرِيْٓ اَقَرِيْبٌ اَمْ بَعِيْدٌ مَّا تُوْعَدُوْنَ', 'fa in tawallau fa qul āżantukum \'alā sawā`, wa in adrī a qarībun am ba\'īdum mā tụ\'adụn'),
(2592, 21, 110, 'اِنَّهٗ يَعْلَمُ الْجَهْرَ مِنَ الْقَوْلِ وَيَعْلَمُ مَا تَكْتُمُوْنَ', 'innahụ ya\'lamul-jahra minal-qauli wa ya\'lamu mā taktumụn'),
(2593, 21, 111, 'وَاِنْ اَدْرِيْ لَعَلَّهٗ فِتْنَةٌ لَّكُمْ وَمَتَاعٌ اِلٰى حِيْنٍ', 'wa in adrī la\'allahụ fitnatul lakum wa matā\'un ilā ḥīn'),
(2594, 21, 112, 'قَالَ رَبِّ احْكُمْ بِالْحَقِّۗ وَرَبُّنَا الرَّحْمٰنُ الْمُسْتَعَانُ عَلٰى مَا تَصِفُوْنَ', 'qāla rabbiḥkum bil-ḥaqq, wa rabbunar-raḥmānul-musta\'ānu \'alā mā taṣifụn'),
(2595, 22, 1, 'يٰٓاَيُّهَا النَّاسُ اتَّقُوْا رَبَّكُمْۚ اِنَّ زَلْزَلَةَ السَّاعَةِ شَيْءٌ عَظِيْمٌ', 'yā ayyuhan-nāsuttaqụ rabbakum, inna zalzalatas-sā\'ati syai`un \'aẓīm'),
(2596, 22, 2, 'يَوْمَ تَرَوْنَهَا تَذْهَلُ كُلُّ مُرْضِعَةٍ عَمَّآ اَرْضَعَتْ وَتَضَعُ كُلُّ ذَاتِ حَمْلٍ حَمْلَهَا وَتَرَى النَّاسَ سُكٰرٰى وَمَا هُمْ بِسُكٰرٰى وَلٰكِنَّ عَذَابَ اللّٰهِ شَدِيْدٌ', 'yauma taraunahā taż-halu kullu murḍi\'atin \'ammā arḍa\'at wa taḍa\'u kullu żāti ḥamlin ḥamlahā wa taran-nāsa sukārā wa mā hum bisukārā wa lākinna \'ażāballāhi syadīd'),
(2597, 22, 3, 'وَمِنَ النَّاسِ مَنْ يُّجَادِلُ فِى اللّٰهِ بِغَيْرِ عِلْمٍ وَّيَتَّبِعُ كُلَّ شَيْطٰنٍ مَّرِيْدٍۙ', 'wa minan-nāsi may yujādilu fillāhi bigairi \'ilmiw wa yattabi\'u kulla syaiṭānim marīd'),
(2598, 22, 4, 'كُتِبَ عَلَيْهِ اَنَّهٗ مَنْ تَوَلَّاهُ فَاَنَّهٗ يُضِلُّهٗ وَيَهْدِيْهِ اِلٰى عَذَابِ السَّعِيْرِ', 'kutiba \'alaihi annahụ man tawallāhu fa annahụ yuḍilluhụ wa yahdīhi ilā \'ażābis-sa\'īr'),
(2599, 22, 5, 'يٰٓاَيُّهَا النَّاسُ اِنْ كُنْتُمْ فِيْ رَيْبٍ مِّنَ الْبَعْثِ فَاِنَّا خَلَقْنٰكُمْ مِّنْ تُرَابٍ ثُمَّ مِنْ نُّطْفَةٍ ثُمَّ مِنْ عَلَقَةٍ ثُمَّ مِنْ مُّضْغَةٍ مُّخَلَّقَةٍ وَّغَيْرِ مُخَلَّقَةٍ لِّنُبَيِّنَ لَكُمْۗ وَنُقِرُّ فِى الْاَرْحَامِ مَا نَشَاۤءُ اِلٰٓى اَجَلٍ مُّسَمًّى ثُمَّ نُخْرِجُكُمْ طِفْلًا ثُمَّ لِتَبْلُغُوْٓا اَشُدَّكُمْۚ وَمِنْكُمْ مَّنْ يُّتَوَفّٰى وَمِنْكُمْ مَّنْ يُّرَدُّ اِلٰٓى اَرْذَلِ الْعُمُرِ لِكَيْلَا يَعْلَمَ مِنْۢ بَعْدِ عِلْمٍ شَيْـًٔاۗ وَتَرَى الْاَرْضَ هَامِدَةً فَاِذَآ اَنْزَلْنَا عَلَيْهَا الْمَاۤءَ اهْتَزَّتْ وَرَبَتْ وَاَنْۢبَتَتْ مِنْ كُلِّ زَوْجٍۢ بَهِيْجٍ', 'yā ayyuhan-nāsu ing kuntum fī raibim minal-ba\'ṡi fa innā khalaqnākum min turābin ṡumma min nuṭfatin ṡumma min \'alaqatin ṡumma mim muḍgatim mukhallaqatiw wa gairi mukhallaqatil linubayyina lakum, wa nuqirru fil-ar-ḥāmi mā nasyā`u ilā ajalim musamman ṡumma nukhrijukum ṭiflan ṡumma litablugū asyuddakum, wa mingkum may yutawaffā wa mingkum may yuraddu ilā arżalil-\'umuri likai lā ya\'lama mim ba\'di \'ilmin syai`ā, wa taral-arḍa hāmidatan fa iżā anzalnā \'alaihal-mā`ahtazzat wa rabat wa ambatat ming kulli zaujim bahīj'),
(2600, 22, 6, 'ذٰلِكَ بِاَنَّ اللّٰهَ هُوَ الْحَقُّ وَاَنَّهٗ يُحْيِ الْمَوْتٰى وَاَنَّهٗ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ۙ', 'żālika bi`annallāha huwal-ḥaqqu wa annahụ yuḥyil-mautā wa annahụ \'alā kulli syai`ing qadīr'),
(2601, 22, 7, 'وَّاَنَّ السَّاعَةَ اٰتِيَةٌ لَّا رَيْبَ فِيْهَاۙ وَاَنَّ اللّٰهَ يَبْعَثُ مَنْ فِى الْقُبُوْرِ', 'wa annas-sā\'ata ātiyatul lā raiba fīhā wa annallāha yab\'aṡu man fil-qubụr'),
(2602, 22, 8, 'وَمِنَ النَّاسِ مَنْ يُّجَادِلُ فِى اللّٰهِ بِغَيْرِ عِلْمٍ وَّلَا هُدًى وَّلَا كِتٰبٍ مُّنِيْرٍ ۙ', 'wa minan-nāsi may yujādilu fillāhi bigairi \'ilmiw wa lā hudaw wa lā kitābim munīr'),
(2603, 22, 9, 'ثَانِيَ عِطْفِهٖ لِيُضِلَّ عَنْ سَبِيْلِ اللّٰهِ ۗ لَهٗ فِى الدُّنْيَا خِزْيٌ وَّنُذِيْقُهٗ يَوْمَ الْقِيٰمَةِ عَذَابَ الْحَرِيْقِ', 'ṡāniya \'iṭfihī liyuḍilla \'an sabīlillāh, lahụ fid-dun-yā khizyuw wa nużīquhụ yaumal-qiyāmati \'ażābal-ḥarīq'),
(2604, 22, 10, 'ذٰلِكَ بِمَا قَدَّمَتْ يَدٰكَ وَاَنَّ اللّٰهَ لَيْسَ بِظَلَّامٍ لِّلْعَبِيْدِ', 'żālika bimā qaddamat yadāka wa annallāha laisa biẓallāmil lil-\'abīd'),
(2605, 22, 11, 'وَمِنَ النَّاسِ مَنْ يَّعْبُدُ اللّٰهَ عَلٰى حَرْفٍۚ فَاِنْ اَصَابَهٗ خَيْرُ ِۨاطْمَـَٔنَّ بِهٖۚ وَاِنْ اَصَابَتْهُ فِتْنَةُ ِۨانْقَلَبَ عَلٰى وَجْهِهٖۗ خَسِرَ الدُّنْيَا وَالْاٰخِرَةَۗ ذٰلِكَ هُوَ الْخُسْرَانُ الْمُبِيْنُ', 'wa minan-nāsi may ya\'budullāha \'alā ḥarf, fa in aṣābahụ khairuniṭma`anna bih, wa in aṣābat-hu fitnatuningqalaba \'alā waj-hih, khasirad-dun-yā wal-ākhirah, żālika huwal-khusrānul-mubīn'),
(2606, 22, 12, 'يَدْعُوْا مِنْ دُوْنِ اللّٰهِ مَا لَا يَضُرُّهٗ وَمَا لَا يَنْفَعُهٗۗ ذٰلِكَ هُوَ الضَّلٰلُ الْبَعِيْدُ ۚ', 'yad\'ụ min dụnillāhi mā lā yaḍurruhụ wa mā lā yanfa\'uh, żālika huwaḍ-ḍalālul-ba\'īd'),
(2607, 22, 13, 'يَدْعُوْا لَمَنْ ضَرُّهٗٓ اَقْرَبُ مِنْ نَّفْعِهٖۗ لَبِئْسَ الْمَوْلٰى وَلَبِئْسَ الْعَشِيْرُ', 'yad\'ụ laman ḍarruhū aqrabu min naf\'ih, labi`sal-maulā wa labi`sal-\'asyīr'),
(2608, 22, 14, 'اِنَّ اللّٰهَ يُدْخِلُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۗ اِنَّ اللّٰهَ يَفْعَلُ مَا يُرِيْدُ', 'innallāha yudkhilullażīna āmanụ wa \'amiluṣ-ṣāliḥāti jannātin tajrī min taḥtihal-an-hār, innallāha yaf\'alu mā yurīd'),
(2609, 22, 15, 'مَنْ كَانَ يَظُنُّ اَنْ لَّنْ يَّنْصُرَهُ اللّٰهُ فِى الدُّنْيَا وَالْاٰخِرَةِ فَلْيَمْدُدْ بِسَبَبٍ اِلَى السَّمَاۤءِ ثُمَّ لْيَقْطَعْ فَلْيَنْظُرْ هَلْ يُذْهِبَنَّ كَيْدُهٗ مَا يَغِيْظُ', 'mang kāna yaẓunnu al lay yanṣurahullāhu fid-dun-yā wal-ākhirati falyamdud bisababin ilas-samā`i ṡummalyaqṭa\' falyanẓur hal yuż-hibanna kaiduhụ mā yagīẓ'),
(2610, 22, 16, 'وَكَذٰلِكَ اَنْزَلْنٰهُ اٰيٰتٍۢ بَيِّنٰتٍۙ وَّاَنَّ اللّٰهَ يَهْدِيْ مَنْ يُّرِيْدُ', 'wa każālika anzalnāhu āyātim bayyinātiw wa annallāha yahdī may yurīd'),
(2611, 22, 17, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَالَّذِيْنَ هَادُوْا وَالصَّابِـِٕيْنَ وَالنَّصٰرٰى وَالْمَجُوْسَ وَالَّذِيْنَ اَشْرَكُوْٓا ۖاِنَّ اللّٰهَ يَفْصِلُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ', 'innallażīna āmanụ wallażīna hādụ waṣ-ṣābi`īna wan-naṣārā wal-majụsa wallażīna asyrakū innallāha yafṣilu bainahum yaumal-qiyāmah, innallāha \'alā kulli syai`in syahīd'),
(2612, 22, 18, 'اَلَمْ تَرَ اَنَّ اللّٰهَ يَسْجُدُ لَهٗ مَنْ فِى السَّمٰوٰتِ وَمَنْ فِى الْاَرْضِ وَالشَّمْسُ وَالْقَمَرُ وَالنُّجُوْمُ وَالْجِبَالُ وَالشَّجَرُ وَالدَّوَاۤبُّ وَكَثِيْرٌ مِّنَ النَّاسِۗ وَكَثِيْرٌ حَقَّ عَلَيْهِ الْعَذَابُۗ وَمَنْ يُّهِنِ اللّٰهُ فَمَا لَهٗ مِنْ مُّكْرِمٍۗ اِنَّ اللّٰهَ يَفْعَلُ مَا يَشَاۤءُ ۩ۗ', 'a lam tara annallāha yasjudu lahụ man fis-samāwāti wa man fil-arḍi wasy-syamsu wal-qamaru wan-nujụmu wal-jibālu wasy-syajaru wad-dawābbu wa kaṡīrum minan-nās, wa kaṡīrun ḥaqqa \'alaihil-\'ażāb, wa may yuhinillāhu fa mā lahụ mim mukrim, innallāha yaf\'alu mā yasyā`,'),
(2613, 22, 19, '۞ هٰذَانِ خَصْمٰنِ اخْتَصَمُوْا فِيْ رَبِّهِمْ فَالَّذِيْنَ كَفَرُوْا قُطِّعَتْ لَهُمْ ثِيَابٌ مِّنْ نَّارٍۗ يُصَبُّ مِنْ فَوْقِ رُءُوْسِهِمُ الْحَمِيْمُ ۚ', 'hāżāni khaṣmānikhtaṣamụ fī rabbihim fallażīna kafarụ quṭṭi\'at lahum ṡiyābum min nār, yuṣabbu min fauqi ru`ụsihimul-ḥamīm'),
(2614, 22, 20, 'يُصْهَرُ بِهٖ مَا فِيْ بُطُوْنِهِمْ وَالْجُلُوْدُ ۗ', 'yuṣ-haru bihī mā fī buṭụnihim wal-julụd'),
(2615, 22, 21, 'وَلَهُمْ مَّقَامِعُ مِنْ حَدِيْدٍ', 'wa lahum maqāmi\'u min ḥadīd'),
(2616, 22, 22, 'كُلَّمَآ اَرَادُوْٓا اَنْ يَّخْرُجُوْا مِنْهَا مِنْ غَمٍّ اُعِيْدُوْا فِيْهَا وَذُوْقُوْا عَذَابَ الْحَرِيْقِ', 'kullamā arādū ay yakhrujụ min-hā min gammin u\'īdụ fīhā wa żụqụ \'ażābal-ḥarīq'),
(2617, 22, 23, 'اِنَّ اللّٰهَ يُدْخِلُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ يُحَلَّوْنَ فِيْهَا مِنْ اَسَاوِرَ مِنْ ذَهَبٍ وَّلُؤْلُؤًاۗ وَلِبَاسُهُمْ فِيْهَا حَرِيْرٌ', 'innallāha yudkhilullażīna āmanụ wa \'amiluṣ-ṣāliḥāti jannātin tajrī min taḥtihal-an-hāru yuḥallauna fīhā min asāwira min żahabiw wa lu`lu`ā, wa libāsuhum fīhā ḥarīr'),
(2618, 22, 24, 'وَهُدُوْٓا اِلَى الطَّيِّبِ مِنَ الْقَوْلِۚ وَهُدُوْٓا اِلٰى صِرَاطِ الْحَمِيْدِ', 'wa hudū ilaṭ-ṭayyibi minal-qaụl, wa hudū ilā ṣirāṭil-ḥamīd'),
(2619, 22, 25, 'اِنَّ الَّذِيْنَ كَفَرُوْا وَيَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ وَالْمَسْجِدِ الْحَرَامِ الَّذِيْ جَعَلْنٰهُ لِلنَّاسِ سَوَاۤءً ۨالْعَاكِفُ فِيْهِ وَالْبَادِۗ وَمَنْ يُّرِدْ فِيْهِ بِاِلْحَادٍۢ بِظُلْمٍ نُّذِقْهُ مِنْ عَذَابٍ اَلِيْمٍ', 'innallażīna kafarụ wa yaṣuddụna \'an sabīlillāhi wal-masjidil-ḥarāmillażī ja\'alnāhu lin-nāsi sawā`anil-\'ākifu fīhi wal-bād, wa may yurid fīhi bi`il-ḥādim biẓulmin nużiq-hu min \'ażābin alīm'),
(2620, 22, 26, 'وَاِذْ بَوَّأْنَا لِاِبْرٰهِيْمَ مَكَانَ الْبَيْتِ اَنْ لَّا تُشْرِكْ بِيْ شَيْـًٔا وَّطَهِّرْ بَيْتِيَ لِلطَّاۤىِٕفِيْنَ وَالْقَاۤىِٕمِيْنَ وَالرُّكَّعِ السُّجُوْدِ', 'wa iż bawwa`nā li`ibrāhīma makānal-baiti al lā tusyrik bī syai`aw wa ṭahhir baitiya liṭ-ṭā`ifīna wal-qā`imīna war-rukka\'is-sujụd'),
(2621, 22, 27, 'وَاَذِّنْ فِى النَّاسِ بِالْحَجِّ يَأْتُوْكَ رِجَالًا وَّعَلٰى كُلِّ ضَامِرٍ يَّأْتِيْنَ مِنْ كُلِّ فَجٍّ عَمِيْقٍ ۙ', 'wa ażżin fin-nāsi bil-ḥajji ya`tụka rijālaw wa \'alā kulli ḍāmiriy ya`tīna ming kulli fajjin \'amīq'),
(2622, 22, 28, 'لِّيَشْهَدُوْا مَنَافِعَ لَهُمْ وَيَذْكُرُوا اسْمَ اللّٰهِ فِيْٓ اَيَّامٍ مَّعْلُوْمٰتٍ عَلٰى مَا رَزَقَهُمْ مِّنْۢ بَهِيْمَةِ الْاَنْعَامِۚ فَكُلُوْا مِنْهَا وَاَطْعِمُوا الْبَاۤىِٕسَ الْفَقِيْرَ ۖ', 'liyasy-hadụ manāfi\'a lahum wa yażkurusmallāhi fī ayyāmim ma\'lụmātin \'alā mā razaqahum mim bahīmatil-an\'ām, fa kulụ min-hā wa aṭ\'imul-bā`isal-faqīr'),
(2623, 22, 29, 'ثُمَّ لْيَقْضُوْا تَفَثَهُمْ وَلْيُوْفُوْا نُذُوْرَهُمْ وَلْيَطَّوَّفُوْا بِالْبَيْتِ الْعَتِيْقِ', 'ṡummalyaqḍụ tafaṡahum walyụfụ nużụrahum walyaṭṭawwafụ bil-baitil-\'atīq'),
(2624, 22, 30, 'ذٰلِكَ وَمَنْ يُّعَظِّمْ حُرُمٰتِ اللّٰهِ فَهُوَ خَيْرٌ لَّهٗ عِنْدَ رَبِّهٖۗ وَاُحِلَّتْ لَكُمُ الْاَنْعَامُ اِلَّا مَا يُتْلٰى عَلَيْكُمْ فَاجْتَنِبُوا الرِّجْسَ مِنَ الْاَوْثَانِ وَاجْتَنِبُوْا قَوْلَ الزُّوْرِ ۙ', 'żālika wa may yu\'aẓẓim ḥurumātillāhi fa huwa khairul lahụ \'inda rabbih, wa uḥillat lakumul-an\'āmu illā mā yutlā \'alaikum fajtanibur-rijsa minal-auṡāni wajtanibụ qaulaz-zụr'),
(2625, 22, 31, 'حُنَفَاۤءَ لِلّٰهِ غَيْرَ مُشْرِكِيْنَ بِهٖۗ وَمَنْ يُّشْرِكْ بِاللّٰهِ فَكَاَنَّمَا خَرَّ مِنَ السَّمَاۤءِ فَتَخْطَفُهُ الطَّيْرُ اَوْ تَهْوِيْ بِهِ الرِّيْحُ فِيْ مَكَانٍ سَحِيْقٍ', 'hunafā`a lillāhi gaira musyrikīna bih, wa may yusyrik billāhi fa ka`annamā kharra minas-samā`i fa takhṭafuhuṭ-ṭairu au tahwī bihir-rīḥu fī makānin saḥīq'),
(2626, 22, 32, 'ذٰلِكَ وَمَنْ يُّعَظِّمْ شَعَاۤىِٕرَ اللّٰهِ فَاِنَّهَا مِنْ تَقْوَى الْقُلُوْبِ', 'żālika wa may yu\'aẓẓim sya\'ā`irallāhi fa innahā min taqwal-qulụb'),
(2627, 22, 33, 'لَكُمْ فِيْهَا مَنَافِعُ اِلٰٓى اَجَلٍ مُّسَمًّى ثُمَّ مَحِلُّهَآ اِلَى الْبَيْتِ الْعَتِيْقِ', 'lakum fīhā manāfi\'u ilā ajalim musamman ṡumma mahilluhā ilal-baitil-\'atīq'),
(2628, 22, 34, 'وَلِكُلِّ اُمَّةٍ جَعَلْنَا مَنْسَكًا لِّيَذْكُرُوا اسْمَ اللّٰهِ عَلٰى مَا رَزَقَهُمْ مِّنْۢ بَهِيْمَةِ الْاَنْعَامِۗ فَاِلٰهُكُمْ اِلٰهٌ وَّاحِدٌ فَلَهٗٓ اَسْلِمُوْاۗ وَبَشِّرِ الْمُخْبِتِيْنَ ۙ', 'wa likulli ummatin ja\'alnā mansakal liyażkurusmallāhi \'alā mā razaqahum mim bahīmatil-an\'ām, fa ilāhukum ilāhuw wāḥidun fa lahū aslimụ, wa basysyiril-mukhbitīn'),
(2629, 22, 35, 'الَّذِيْنَ اِذَا ذُكِرَ اللّٰهُ وَجِلَتْ قُلُوْبُهُمْ وَالصَّابِرِيْنَ عَلٰى مَآ اَصَابَهُمْ وَالْمُقِيْمِى الصَّلٰوةِۙ وَمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَ', 'allażīna iżā żukirallāhu wajilat qulụbuhum waṣ-ṣābirīna \'alā mā aṣābahum wal-muqīmiṣ-ṣalāti wa mimmā razaqnāhum yunfiqụn'),
(2630, 22, 36, 'وَالْبُدْنَ جَعَلْنٰهَا لَكُمْ مِّنْ شَعَاۤىِٕرِ اللّٰهِ لَكُمْ فِيْهَا خَيْرٌۖ فَاذْكُرُوا اسْمَ اللّٰهِ عَلَيْهَا صَوَاۤفَّۚ فَاِذَا وَجَبَتْ جُنُوْبُهَا فَكُلُوْا مِنْهَا وَاَطْعِمُوا الْقَانِعَ وَالْمُعْتَرَّۗ كَذٰلِكَ سَخَّرْنٰهَا لَكُمْ لَعَلَّكُمْ تَشْكُرُوْنَ', 'wal-budna ja\'alnāhā lakum min sya\'ā`irillāhi lakum fīhā khairun fażkurusmallāhi \'alaihā ṣawāff, fa iżā wajabat junụbuhā fa kulụ min-hā wa aṭ\'imul-qāni\'a wal-mu\'tarr, każālika sakhkharnāhā lakum la\'allakum tasykurụn'),
(2631, 22, 37, 'لَنْ يَّنَالَ اللّٰهَ لُحُوْمُهَا وَلَا دِمَاۤؤُهَا وَلٰكِنْ يَّنَالُهُ التَّقْوٰى مِنْكُمْۗ كَذٰلِكَ سَخَّرَهَا لَكُمْ لِتُكَبِّرُوا اللّٰهَ عَلٰى مَا هَدٰىكُمْ ۗ وَبَشِّرِ الْمُحْسِنِيْنَ', 'lay yanālallāha luḥụmuhā wa lā dimā`uhā wa lākiy yanāluhut-taqwā mingkum, każālika sakhkharahā lakum litukabbirullāha \'alā mā hadākum, wa basysyiril-muḥsinīn'),
(2632, 22, 38, '۞ اِنَّ اللّٰهَ يُدَافِعُ عَنِ الَّذِيْنَ اٰمَنُوْاۗ اِنَّ اللّٰهَ لَا يُحِبُّ كُلَّ خَوَّانٍ كَفُوْرٍ', 'innallāha yudāfi\'u \'anillażīna āmanū, innallāha lā yuḥibbu kulla khawwāning kafụr'),
(2633, 22, 39, 'اُذِنَ لِلَّذِيْنَ يُقَاتَلُوْنَ بِاَنَّهُمْ ظُلِمُوْاۗ وَاِنَّ اللّٰهَ عَلٰى نَصْرِهِمْ لَقَدِيْرٌ ۙ', 'użina lillażīna yuqātalụna bi`annahum ẓulimụ, wa innallāha \'alā naṣrihim laqadīr'),
(2634, 22, 40, ' ۨالَّذِيْنَ اُخْرِجُوْا مِنْ دِيَارِهِمْ بِغَيْرِ حَقٍّ اِلَّآ اَنْ يَّقُوْلُوْا رَبُّنَا اللّٰهُ ۗوَلَوْلَا دَفْعُ اللّٰهِ النَّاسَ بَعْضَهُمْ بِبَعْضٍ لَّهُدِّمَتْ صَوَامِعُ وَبِيَعٌ وَّصَلَوٰتٌ وَّمَسٰجِدُ يُذْكَرُ فِيْهَا اسْمُ اللّٰهِ كَثِيْرًاۗ وَلَيَنْصُرَنَّ اللّٰهُ مَنْ يَّنْصُرُهٗۗ اِنَّ اللّٰهَ لَقَوِيٌّ عَزِيْزٌ', 'allażīna ukhrijụ min diyārihim bigairi ḥaqqin illā ay yaqụlụ rabbunallāh, walau lā daf\'ullāhin-nāsa ba\'ḍahum biba\'ḍil lahuddimat ṣawāmi\'u wa biya\'uw wa ṣalawātuw wa masājidu yużkaru fīhasmullāhi kaṡīrā, wa layanṣurannallāhu may yanṣuruh, innallāha laqawiyyun \'azīz'),
(2635, 22, 41, 'اَلَّذِيْنَ اِنْ مَّكَّنّٰهُمْ فِى الْاَرْضِ اَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ وَاَمَرُوْا بِالْمَعْرُوْفِ وَنَهَوْا عَنِ الْمُنْكَرِۗ وَلِلّٰهِ عَاقِبَةُ الْاُمُوْرِ', 'allażīna im makkannāhum fil-arḍi aqāmuṣ-ṣalāta wa ātawuz-zakāta wa amarụ bil-ma\'rụfi wa nahau \'anil-mungkar, wa lillāhi \'āqibatul-umụr'),
(2636, 22, 42, 'وَاِنْ يُّكَذِّبُوْكَ فَقَدْ كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوْحٍ وَّعَادٌ وَّثَمُوْدُ ۙ', 'wa iy yukażżibụka fa qad każżabat qablahum qaumu nụḥiw wa \'āduw wa ṡamụd'),
(2637, 22, 43, 'وَقَوْمُ اِبْرٰهِيْمَ وَقَوْمُ لُوْطٍ ۙ', 'wa qaumu ibrāhīma wa qaumu lụṭ'),
(2638, 22, 44, 'وَّاَصْحٰبُ مَدْيَنَۚ وَكُذِّبَ مُوْسٰى فَاَمْلَيْتُ لِلْكٰفِرِيْنَ ثُمَّ اَخَذْتُهُمْۚ فَكَيْفَ كَانَ نَكِيْرِ', 'wa aṣ-ḥābu madyan, wa kużżiba mụsā fa amlaitu lil-kāfirīna ṡumma akhażtuhum, fa kaifa kāna nakīr'),
(2639, 22, 45, 'فَكَاَيِّنْ مِّنْ قَرْيَةٍ اَهْلَكْنٰهَا وَهِيَ ظَالِمَةٌ فَهِيَ خَاوِيَةٌ عَلٰى عُرُوْشِهَاۖ وَبِئْرٍ مُّعَطَّلَةٍ وَّقَصْرٍ مَّشِيْدٍ', 'fa ka`ayyim ming qaryatin ahlaknāhā wa hiya ẓālimatun fa hiya khāwiyatun \'alā \'urụsyihā wa bi`rim mu\'aṭṭalatiw wa qaṣrim masyīd'),
(2640, 22, 46, 'اَفَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَتَكُوْنَ لَهُمْ قُلُوْبٌ يَّعْقِلُوْنَ بِهَآ اَوْ اٰذَانٌ يَّسْمَعُوْنَ بِهَاۚ فَاِنَّهَا لَا تَعْمَى الْاَبْصَارُ وَلٰكِنْ تَعْمَى الْقُلُوْبُ الَّتِيْ فِى الصُّدُوْرِ', 'a fa lam yasīrụ fil-arḍi fa takụna lahum qulụbuy ya\'qilụna bihā au āżānuy yasma\'ụna bihā, fa innahā lā ta\'mal-abṣāru wa lākin ta\'mal-qulụbullatī fiṣ-ṣudụr'),
(2641, 22, 47, 'وَيَسْتَعْجِلُوْنَكَ بِالْعَذَابِ وَلَنْ يُّخْلِفَ اللّٰهُ وَعْدَهٗۗ وَاِنَّ يَوْمًا عِنْدَ رَبِّكَ كَاَلْفِ سَنَةٍ مِّمَّا تَعُدُّوْنَ', 'wa yasta\'jilụnaka bil-\'ażābi wa lay yukhlifallāhu wa\'dah, wa inna yauman \'inda rabbika ka`alfi sanatim mimmā ta\'uddụn'),
(2642, 22, 48, 'وَكَاَيِّنْ مِّنْ قَرْيَةٍ اَمْلَيْتُ لَهَا وَهِيَ ظَالِمَةٌ ثُمَّ اَخَذْتُهَاۚ وَاِلَيَّ الْمَصِيْرُ', 'wa ka`ayyim ming qaryatin amlaitu lahā wa hiya ẓālimatun ṡumma akhażtuhā, wa ilayyal-maṣīr'),
(2643, 22, 49, 'قُلْ يٰٓاَيُّهَا النَّاسُ اِنَّمَآ اَنَا۠ لَكُمْ نَذِيْرٌ مُّبِيْنٌ ۚ', 'qul yā ayyuhan-nāsu innamā ana lakum nażīrum mubīn'),
(2644, 22, 50, 'فَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ مَّغْفِرَةٌ وَّرِزْقٌ كَرِيْمٌ', 'fallażīna āmanụ wa \'amiluṣ-ṣāliḥāti lahum magfiratuw wa rizqung karīm'),
(2645, 22, 51, 'وَالَّذِيْنَ سَعَوْا فِيْٓ اٰيٰتِنَا مُعٰجِزِيْنَ اُولٰۤىِٕكَ اَصْحٰبُ الْجَحِيْمِ', 'wallażīna sa\'au fī āyātinā mu\'ājizīna ulā`ika aṣ-ḥābul-jaḥīm'),
(2646, 22, 52, 'وَمَآ اَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رَّسُوْلٍ وَّلَا نَبِيٍّ اِلَّآ اِذَا تَمَنّٰىٓ اَلْقَى الشَّيْطٰنُ فِيْٓ اُمْنِيَّتِهٖۚ فَيَنْسَخُ اللّٰهُ مَا يُلْقِى الشَّيْطٰنُ ثُمَّ يُحْكِمُ اللّٰهُ اٰيٰتِهٖۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ۙ', 'wa mā arsalnā ming qablika mir rasụliw wa lā nabiyyin illā iżā tamannā alqasy-syaiṭānu fī umniyyatih, fa yansakhullāhu mā yulqisy-syaiṭānu ṡumma yuḥkimullāhu āyātih, wallāhu \'alīmun ḥakīm'),
(2647, 22, 53, 'لِّيَجْعَلَ مَا يُلْقِى الشَّيْطٰنُ فِتْنَةً لِّلَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ وَّالْقَاسِيَةِ قُلُوْبُهُمْۗ وَاِنَّ الظّٰلِمِيْنَ لَفِيْ شِقَاقٍۢ بَعِيْدٍ ۙ', 'liyaj\'ala mā yulqisy-syaiṭānu fitnatal lillażīna fī qulụbihim maraḍuw wal-qāsiyati qulụbuhum, wa innaẓ-ẓālimīna lafī syiqāqim ba\'īd'),
(2648, 22, 54, 'وَّلِيَعْلَمَ الَّذِيْنَ اُوْتُوا الْعِلْمَ اَنَّهُ الْحَقُّ مِنْ رَّبِّكَ فَيُؤْمِنُوْا بِهٖ فَتُخْبِتَ لَهٗ قُلُوْبُهُمْۗ وَاِنَّ اللّٰهَ لَهَادِ الَّذِيْنَ اٰمَنُوْٓا اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ', 'wa liya\'lamallażīna ụtul-\'ilma annahul-ḥaqqu mir rabbika fa yu`minụ bihī fa tukhbita lahụ qulụbuhum, wa innallāha lahādillażīna āmanū ilā ṣirāṭim mustaqīm'),
(2649, 22, 55, 'وَلَا يَزَالُ الَّذِيْنَ كَفَرُوْا فِيْ مِرْيَةٍ مِّنْهُ حَتّٰى تَأْتِيَهُمُ السَّاعَةُ بَغْتَةً اَوْ يَأْتِيَهُمْ عَذَابُ يَوْمٍ عَقِيْمٍ', 'wa lā yazālullażīna kafarụ fī miryatim min-hu ḥattā ta`tiyahumus-sā\'atu bagtatan au ya`tiyahum \'ażābu yaumin \'aqīm'),
(2650, 22, 56, 'اَلْمُلْكُ يَوْمَىِٕذٍ لِّلّٰهِ ۗيَحْكُمُ بَيْنَهُمْۗ فَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فِيْ جَنّٰتِ النَّعِيْمِ', 'al-mulku yauma`iżil lillāh, yaḥkumu bainahum, fallażīna āmanụ wa \'amiluṣ-ṣāliḥāti fī jannātin-na\'īm'),
(2651, 22, 57, 'وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَا فَاُولٰۤىِٕكَ لَهُمْ عَذَابٌ مُّهِيْنٌ', 'wallażīna kafarụ wa każżabụ bi`āyātinā fa ulā`ika lahum \'ażābum muhīn'),
(2652, 22, 58, 'وَالَّذِيْنَ هَاجَرُوْا فِيْ سَبِيْلِ اللّٰهِ ثُمَّ قُتِلُوْٓا اَوْ مَاتُوْا لَيَرْزُقَنَّهُمُ اللّٰهُ رِزْقًا حَسَنًاۗ وَاِنَّ اللّٰهَ لَهُوَ خَيْرُ الرّٰزِقِيْنَ', 'wallażīna hājarụ fī sabīlillāhi ṡumma qutilū au mātụ layarzuqannahumullāhu rizqan ḥasanā, wa innallāha lahuwa khairur-rāziqīn'),
(2653, 22, 59, 'لَيُدْخِلَنَّهُمْ مُّدْخَلًا يَّرْضَوْنَهٗۗ وَاِنَّ اللّٰهَ لَعَلِيْمٌ حَلِيْمٌ', 'layudkhilannahum mudkhalay yarḍaunah, wa innallāha la\'alīmun ḥalīm'),
(2654, 22, 60, '۞ ذٰلِكَ وَمَنْ عَاقَبَ بِمِثْلِ مَا عُوْقِبَ بِهٖ ثُمَّ بُغِيَ عَلَيْهِ لَيَنْصُرَنَّهُ اللّٰهُ ۗاِنَّ اللّٰهَ لَعَفُوٌّ غَفُوْرٌ', 'żālika wa man \'āqaba bimiṡli mā \'ụqiba bihī ṡumma bugiya \'alaihi layanṣurannahullāh, innallāha la\'afuwwun gafụr'),
(2655, 22, 61, 'ذٰلِكَ بِاَنَّ اللّٰهَ يُوْلِجُ الَّيْلَ فِى النَّهَارِ وَيُوْلِجُ النَّهَارَ فِى الَّيْلِ وَاَنَّ اللّٰهَ سَمِيْعٌۢ بَصِيْرٌ', 'żālika bi`annallāha yụlijul-laila fin-nahāri wa yụlijun-nahāra fil-laili wa annallāha samī\'um baṣīr'),
(2656, 22, 62, 'ذٰلِكَ بِاَنَّ اللّٰهَ هُوَ الْحَقُّ وَاَنَّ مَا يَدْعُوْنَ مِنْ دُوْنِهٖ هُوَ الْبَاطِلُ وَاَنَّ اللّٰهَ هُوَ الْعَلِيُّ الْكَبِيْرُ', 'żālika bi`annallāha huwal-ḥaqqu wa anna mā yad\'ụna min dụnihī huwal-bāṭilu wa annallāha huwal-\'aliyyul-kabīr'),
(2657, 22, 63, 'اَلَمْ تَرَ اَنَّ اللّٰهَ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءًۖ فَتُصْبِحُ الْاَرْضُ مُخْضَرَّةًۗ اِنَّ اللّٰهَ لَطِيْفٌ خَبِيْرٌ ۚ', 'a lam tara annallāha anzala minas-samā`i mā`an fa tuṣbiḥul-arḍu mukhḍarrah, innallāha laṭīfun khabīr'),
(2658, 22, 64, 'لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَاِنَّ اللّٰهَ لَهُوَ الْغَنِيُّ الْحَمِيْدُ', 'lahụ mā fis-samāwāti wa mā fil-arḍ, wa innallāha lahuwal-ganiyyul-ḥamīd'),
(2659, 22, 65, 'اَلَمْ تَرَ اَنَّ اللّٰهَ سَخَّرَ لَكُمْ مَّا فِى الْاَرْضِ وَالْفُلْكَ تَجْرِيْ فِى الْبَحْرِ بِاَمْرِهٖۗ وَيُمْسِكُ السَّمَاۤءَ اَنْ تَقَعَ عَلَى الْاَرْضِ اِلَّا بِاِذْنِهٖۗ اِنَّ اللّٰهَ بِالنَّاسِ لَرَءُوْفٌ رَّحِيْمٌ', 'a lam tara annallāha sakhkhara lakum mā fil-arḍi wal-fulka tajrī fil-baḥri bi`amrih, wa yumsikus-samā`a an taqa\'a \'alal-arḍi illā bi`iżnih, innallāha bin-nāsi lara`ụfur raḥīm'),
(2660, 22, 66, 'وَهُوَ الَّذِيْٓ اَحْيَاكُمْ ۖ ثُمَّ يُمِيْتُكُمْ ثُمَّ يُحْيِيْكُمْۗ اِنَّ الْاِنْسَانَ لَكَفُوْرٌ', 'wa huwallażī aḥyākum ṡumma yumītukum ṡumma yuḥyīkum, innal-insāna lakafụr'),
(2661, 22, 67, 'لِكُلِّ اُمَّةٍ جَعَلْنَا مَنْسَكًا هُمْ نَاسِكُوْهُ فَلَا يُنَازِعُنَّكَ فِى الْاَمْرِ وَادْعُ اِلٰى رَبِّكَۗ اِنَّكَ لَعَلٰى هُدًى مُّسْتَقِيْمٍ', 'likulli ummatin ja\'alnā mansakan hum nāsikụhu fa lā yunāzi\'unnaka fil-amri wad\'u ilā rabbik, innaka la\'alā hudam mustaqīm'),
(2662, 22, 68, 'وَاِنْ جَادَلُوْكَ فَقُلِ اللّٰهُ اَعْلَمُ بِمَا تَعْمَلُوْنَ', 'wa in jādalụka fa qulillāhu a\'lamu bimā ta\'malụn'),
(2663, 22, 69, 'اَللّٰهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كُنْتُمْ فِيْهِ تَخْتَلِفُوْنَ', 'allāhu yaḥkumu bainakum yaumal-qiyāmati fīmā kuntum fīhi takhtalifụn'),
(2664, 22, 70, 'اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ يَعْلَمُ مَا فِى السَّمَاۤءِ وَالْاَرْضِۗ اِنَّ ذٰلِكَ فِيْ كِتٰبٍۗ اِنَّ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرٌ', 'a lam ta\'lam annallāha ya\'lamu mā fis-samā`i wal-arḍ, inna żālika fī kitāb, inna żālika \'alallāhi yasīr'),
(2665, 22, 71, 'وَيَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَمْ يُنَزِّلْ بِهٖ سُلْطٰنًا وَّمَا لَيْسَ لَهُمْ بِهٖ عِلْمٌ ۗوَمَا لِلظّٰلِمِيْنَ مِنْ نَّصِيْرٍ', 'wa ya\'budụna min dụnillāhi mā lam yunazzil bihī sulṭānaw wa mā laisa lahum bihī \'ilm, wa mā liẓ-ẓālimīna min naṣīr'),
(2666, 22, 72, 'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا بَيِّنٰتٍ تَعْرِفُ فِيْ وُجُوْهِ الَّذِيْنَ كَفَرُوا الْمُنْكَرَۗ يَكَادُوْنَ يَسْطُوْنَ بِالَّذِيْنَ يَتْلُوْنَ عَلَيْهِمْ اٰيٰتِنَاۗ قُلْ اَفَاُنَبِّئُكُمْ بِشَرٍّ مِّنْ ذٰلِكُمْۗ اَلنَّارُۗ وَعَدَهَا اللّٰهُ الَّذِيْنَ كَفَرُوْاۗ وَبِئْسَ الْمَصِيْرُ', 'wa iżā tutlā \'alaihim āyātunā bayyinātin ta\'rifu fī wujụhillażīna kafarul-mungkar, yakādụna yasṭụna billażīna yatlụna \'alaihim āyātinā, qul a fa unabbi`ukum bisyarrim min żālikum, an-nār, wa\'adahallāhullażīna kafarụ, wa bi`sal-maṣīr'),
(2667, 22, 73, 'يٰٓاَيُّهَا النَّاسُ ضُرِبَ مَثَلٌ فَاسْتَمِعُوْا لَهٗ ۗاِنَّ الَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ لَنْ يَّخْلُقُوْا ذُبَابًا وَّلَوِ اجْتَمَعُوْا لَهٗ ۗوَاِنْ يَّسْلُبْهُمُ الذُّبَابُ شَيْـًٔا لَّا يَسْتَنْقِذُوْهُ مِنْهُۗ ضَعُفَ الطَّالِبُ وَالْمَطْلُوْبُ', 'yā ayyuhan-nāsu ḍuriba maṡalun fastami\'ụ lah, innallażīna tad\'ụna min dụnillāhi lay yakhluqụ żubābaw wa lawijtama\'ụ lah, wa iy yaslub-humuż-żubābu syai`al lā yastangqiżụhu min-h, ḍa\'ufaṭ-ṭālibu wal-maṭlụb'),
(2668, 22, 74, 'مَا قَدَرُوا اللّٰهَ حَقَّ قَدْرِهٖۗ اِنَّ اللّٰهَ لَقَوِيٌّ عَزِيْزٌ', 'mā qadarullāha ḥaqqa qadrih, innallāha laqawiyyun \'azīz'),
(2669, 22, 75, 'اَللّٰهُ يَصْطَفِيْ مِنَ الْمَلٰۤىِٕكَةِ رُسُلًا وَّمِنَ النَّاسِۗ اِنَّ اللّٰهَ سَمِيْعٌۢ بَصِيْرٌ ۚ', 'allāhu yaṣṭafī minal-malā`ikati rusulaw wa minan-nās, innallāha samī\'um baṣīr'),
(2670, 22, 76, 'يَعْلَمُ مَا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْۗ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ', 'ya\'lamu mā baina aidīhim wa mā khalfahum, wa ilallāhi turja\'ul-umụr'),
(2671, 22, 77, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا ارْكَعُوْا وَاسْجُدُوْا وَاعْبُدُوْا رَبَّكُمْ وَافْعَلُوا الْخَيْرَ لَعَلَّكُمْ تُفْلِحُوْنَ ۚ۩', 'yā ayyuhallażīna āmanurka\'ụ wasjudụ wa\'budụ rabbakum waf\'alul-khaira la\'allakum tufliḥụn'),
(2672, 22, 78, 'وَجَاهِدُوْا فِى اللّٰهِ حَقَّ جِهَادِهٖۗ هُوَ اجْتَبٰىكُمْ وَمَا جَعَلَ عَلَيْكُمْ فِى الدِّيْنِ مِنْ حَرَجٍۗ مِلَّةَ اَبِيْكُمْ اِبْرٰهِيْمَۗ هُوَ سَمّٰىكُمُ الْمُسْلِمِيْنَ ەۙ مِنْ قَبْلُ وَفِيْ هٰذَا لِيَكُوْنَ الرَّسُوْلُ شَهِيْدًا عَلَيْكُمْ وَتَكُوْنُوْا شُهَدَاۤءَ عَلَى النَّاسِۖ فَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَاعْتَصِمُوْا بِاللّٰهِ ۗهُوَ مَوْلٰىكُمْۚ فَنِعْمَ الْمَوْلٰى وَنِعْمَ النَّصِيْرُ ۔', 'wa jāhidụ fillāhi ḥaqqa jihādih, huwajtabākum wa mā ja\'ala \'alaikum fid-dīni min ḥaraj, millata abīkum ibrāhīm, huwa sammākumul-muslimīna ming qablu wa fī hāżā liyakụnar-rasụlu syahīdan \'alaikum wa takụnụ syuhadā`a \'alan-nāsi fa aqīmuṣ-ṣalāta wa ātuz-zakāta wa\'taṣimụ billāh, huwa maulākum, fa ni\'mal-maulā wa ni\'man-naṣīr'),
(2673, 23, 1, ' قَدْ اَفْلَحَ الْمُؤْمِنُوْنَ ۙ', 'qad aflaḥal-mu`minụn'),
(2674, 23, 2, ' الَّذِيْنَ هُمْ فِيْ صَلٰو تِهِمْ خَاشِعُوْنَ', 'allażīna hum fī ṣalātihim khāsyi\'ụn'),
(2675, 23, 3, 'وَالَّذِيْنَ هُمْ عَنِ اللَّغْوِ مُعْرِضُوْنَ ۙ', 'wallażīna hum \'anil-lagwi mu\'riḍụn'),
(2676, 23, 4, 'وَالَّذِيْنَ هُمْ لِلزَّكٰوةِ فَاعِلُوْنَ ۙ', 'wallażīna hum liz-zakāti fā\'ilụn'),
(2677, 23, 5, ' وَالَّذِيْنَ هُمْ لِفُرُوْجِهِمْ حٰفِظُوْنَ ۙ', 'wallażīna hum lifurụjihim ḥāfiẓụn'),
(2678, 23, 6, 'اِلَّا عَلٰٓى اَزْوَاجِهِمْ اَوْ مَا مَلَكَتْ اَيْمَانُهُمْ فَاِنَّهُمْ غَيْرُ مَلُوْمِيْنَۚ', 'illā \'alā azwājihim au mā malakat aimānuhum fa innahum gairu malụmīn'),
(2679, 23, 7, 'فَمَنِ ابْتَغٰى وَرَاۤءَ ذٰلِكَ فَاُولٰۤىِٕكَ هُمُ الْعٰدُوْنَ ۚ', 'fa manibtagā warā`a żālika fa ulā`ika humul-\'ādụn'),
(2680, 23, 8, 'وَالَّذِيْنَ هُمْ لِاَمٰنٰتِهِمْ وَعَهْدِهِمْ رَاعُوْنَ ۙ', 'wallażīna hum li`amānātihim wa \'ahdihim rā\'ụn'),
(2681, 23, 9, 'وَالَّذِيْنَ هُمْ عَلٰى صَلَوٰتِهِمْ يُحَافِظُوْنَ ۘ', 'wallażīna hum \'alā ṣalawātihim yuḥāfiẓụn'),
(2682, 23, 10, 'اُولٰۤىِٕكَ هُمُ الْوَارِثُوْنَ ۙ', 'ulā`ika humul-wāriṡụn'),
(2683, 23, 11, 'الَّذِيْنَ يَرِثُوْنَ الْفِرْدَوْسَۗ هُمْ فِيْهَا خٰلِدُوْنَ', 'allażīna yariṡụnal-firdaụs, hum fīhā khālidụn'),
(2684, 23, 12, 'وَلَقَدْ خَلَقْنَا الْاِنْسَانَ مِنْ سُلٰلَةٍ مِّنْ طِيْنٍ ۚ', 'wa laqad khalaqnal-insāna min sulālatim min ṭīn'),
(2685, 23, 13, 'ثُمَّ جَعَلْنٰهُ نُطْفَةً فِيْ قَرَارٍ مَّكِيْنٍ ۖ', 'ṡumma ja\'alnāhu nuṭfatan fī qarārim makīn'),
(2686, 23, 14, 'ثُمَّ خَلَقْنَا النُّطْفَةَ عَلَقَةً فَخَلَقْنَا الْعَلَقَةَ مُضْغَةً فَخَلَقْنَا الْمُضْغَةَ عِظٰمًا فَكَسَوْنَا الْعِظٰمَ لَحْمًا ثُمَّ اَنْشَأْنٰهُ خَلْقًا اٰخَرَۗ فَتَبَارَكَ اللّٰهُ اَحْسَنُ الْخَالِقِيْنَۗ', 'ṡumma khalaqnan-nuṭfata \'alaqatan fa khalaqnal-\'alaqata muḍgatan fa khalaqnal-muḍgata \'iẓāman fa kasaunal-\'iẓāma laḥman ṡumma ansya`nāhu khalqan ākhar, fa tabārakallāhu aḥsanul-khāliqīn'),
(2687, 23, 15, ' ثُمَّ اِنَّكُمْ بَعْدَ ذٰلِكَ لَمَيِّتُوْنَ ۗ', 'ṡumma innakum ba\'da żālika lamayyitụn'),
(2688, 23, 16, 'ثُمَّ اِنَّكُمْ يَوْمَ الْقِيٰمَةِ تُبْعَثُوْنَ', 'ṡumma innakum yaumal-qiyāmati tub\'aṡụn'),
(2689, 23, 17, 'وَلَقَدْ خَلَقْنَا فَوْقَكُمْ سَبْعَ طَرَاۤىِٕقَۖ وَمَا كُنَّا عَنِ الْخَلْقِ غٰفِلِيْنَ', 'wa laqad khalaqnā fauqakum sab\'a ṭarā`iqa wa mā kunnā \'anil-khalqi gāfilīn'),
(2690, 23, 18, 'وَاَنْزَلْنَا مِنَ السَّمَاۤءِ مَاۤءًۢ بِقَدَرٍ فَاَسْكَنّٰهُ فِى الْاَرْضِۖ وَاِنَّا عَلٰى ذَهَابٍۢ بِهٖ لَقٰدِرُوْنَ ۚ', 'wa anzalnā minas-samā`i mā`am biqadarin fa askannāhu fil-arḍi wa innā \'alā żahābim bihī laqādirụn'),
(2691, 23, 19, 'فَاَنْشَأْنَا لَكُمْ بِهٖ جَنّٰتٍ مِّنْ نَّخِيْلٍ وَّاَعْنَابٍۘ لَكُمْ فِيْهَا فَوَاكِهُ كَثِيْرَةٌ وَّمِنْهَا تَأْكُلُوْنَ ۙ', 'fa ansya`nā lakum bihī jannātim min nakhīliw wa a\'nāb, lakum fīhā fawākihu kaṡīratuw wa min-hā ta`kulụn'),
(2692, 23, 20, 'وَشَجَرَةً تَخْرُجُ مِنْ طُوْرِ سَيْنَاۤءَ تَنْۢبُتُ بِالدُّهْنِ وَصِبْغٍ لِّلْاٰكِلِيْنَ', 'wa syajaratan takhruju min ṭụri sainā`a tambutu bid-duhni wa ṣibgil lil-ākilīn'),
(2693, 23, 21, 'وَاِنَّ لَكُمْ فِى الْاَنْعَامِ لَعِبْرَةًۗ نُسْقِيْكُمْ مِّمَّا فِيْ بُطُوْنِهَا وَلَكُمْ فِيْهَا مَنَافِعُ كَثِيْرَةٌ وَّمِنْهَا تَأْكُلُوْنَ ۙ', 'wa inna lakum fil-an\'āmi la\'ibrah, nusqīkum mimmā fī buṭụnihā wa lakum fīhā manāfi\'u kaṡīratuw wa min-hā ta`kulụn'),
(2694, 23, 22, 'وَعَلَيْهَا وَعَلَى الْفُلْكِ تُحْمَلُوْنَ', 'wa \'alaihā wa \'alal-fulki tuḥmalụn'),
(2695, 23, 23, 'وَلَقَدْ اَرْسَلْنَا نُوْحًا اِلٰى قَوْمِهٖ فَقَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ اَفَلَا تَتَّقُوْنَ', 'wa laqad arsalnā nụḥan ilā qaumihī fa qāla yā qaumi\'budullāha mā lakum min ilāhin gairuh, a fa lā tattaqụn'),
(2696, 23, 24, 'فَقَالَ الْمَلَؤُا الَّذِيْنَ كَفَرُوْا مِنْ قَوْمِهٖ مَا هٰذَآ اِلَّا بَشَرٌ مِّثْلُكُمْۙ يُرِيْدُ اَنْ يَّتَفَضَّلَ عَلَيْكُمْۗ وَلَوْ شَاۤءَ اللّٰهُ لَاَنْزَلَ مَلٰۤىِٕكَةً ۖمَّا سَمِعْنَا بِهٰذَا فِيْٓ اٰبَاۤىِٕنَا الْاَوَّلِيْنَ ۚ', 'fa qālal-mala`ullażīna kafarụ ming qaumihī mā hāżā illā basyarum miṡlukum yurīdu ay yatafaḍḍala \'alaikum, walau syā`allāhu la`anzala malā`ikatam mā sami\'nā bihāżā fī ābā`inal-awwalīn'),
(2697, 23, 25, 'اِنْ هُوَ اِلَّا رَجُلٌۢ بِهٖ جِنَّةٌ فَتَرَبَّصُوْا بِهٖ حَتّٰى حِيْنٍ', 'in huwa illā rajulum bihī jinnatun fa tarabbaṣụ bihī ḥattā ḥīn'),
(2698, 23, 26, 'قَالَ رَبِّ انْصُرْنِيْ بِمَا كَذَّبُوْنِ', 'qāla rabbinṣurnī bimā każżabụn');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(2699, 23, 27, 'فَاَوْحَيْنَآ اِلَيْهِ اَنِ اصْنَعِ الْفُلْكَ بِاَعْيُنِنَا وَوَحْيِنَا فَاِذَا جَاۤءَ اَمْرُنَا وَفَارَ التَّنُّوْرُۙ فَاسْلُكْ فِيْهَا مِنْ كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَاَهْلَكَ اِلَّا مَنْ سَبَقَ عَلَيْهِ الْقَوْلُ مِنْهُمْۚ وَلَا تُخَاطِبْنِيْ فِى الَّذِيْنَ ظَلَمُوْاۚ اِنَّهُمْ مُّغْرَقُوْنَ', 'fa auḥainā ilaihi aniṣna\'il-fulka bi`a\'yuninā wa waḥyinā fa iżā jā`a amrunā wa fārat-tannụru fasluk fīhā ming kullin zaujainiṡnaini wa ahlaka illā man sabaqa \'alaihil-qaulu min-hum, wa lā tukhāṭibnī fillażīna ẓalamụ, innahum mugraqụn'),
(2700, 23, 28, 'فَاِذَا اسْتَوَيْتَ اَنْتَ وَمَنْ مَّعَكَ عَلَى الْفُلْكِ فَقُلِ الْحَمْدُ لِلّٰهِ الَّذِيْ نَجّٰىنَا مِنَ الْقَوْمِ الظّٰلِمِيْنَ', 'fa iżastawaita anta wa mam ma\'aka \'alal-fulki fa qulil-ḥamdu lillāhillażī najjānā minal-qaumiẓ-ẓālimīn'),
(2701, 23, 29, 'وَقُلْ رَّبِّ اَنْزِلْنِيْ مُنْزَلًا مُّبٰرَكًا وَّاَنْتَ خَيْرُ الْمُنْزِلِيْنَ', 'wa qur rabbi anzilnī munzalam mubārakaw wa anta khairul-munzilīn'),
(2702, 23, 30, 'اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ وَّاِنْ كُنَّا لَمُبْتَلِيْنَ', 'inna fī żālika la`āyātiw wa ing kunnā lamubtalīn'),
(2703, 23, 31, 'ثُمَّ اَنْشَأْنَا مِنْۢ بَعْدِهِمْ قَرْنًا اٰخَرِيْنَ ۚ', 'ṡumma ansya`nā mim ba\'dihim qarnan ākharīn'),
(2704, 23, 32, 'فَاَرْسَلْنَا فِيْهِمْ رَسُوْلًا مِّنْهُمْ اَنِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ اَفَلَا تَتَّقُوْنَ', 'fa arsalnā fīhim rasụlam min-hum ani\'budullāha mā lakum min ilāhin gairuh, a fa lā tattaqụn'),
(2705, 23, 33, 'وَقَالَ الْمَلَاُ مِنْ قَوْمِهِ الَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِلِقَاۤءِ الْاٰخِرَةِ وَاَتْرَفْنٰهُمْ فِى الْحَيٰوةِ الدُّنْيَاۙ مَا هٰذَآ اِلَّا بَشَرٌ مِّثْلُكُمْۙ يَأْكُلُ مِمَّا تَأْكُلُوْنَ مِنْهُ وَيَشْرَبُ مِمَّا تَشْرَبُوْنَ', 'wa qālal-mala`u ming qaumihillażīna kafarụ wa każżabụ biliqā`il-ākhirati wa atrafnāhum fil-ḥayātid-dun-yā mā hāżā illā basyarum miṡlukum ya`kulu mimmā ta`kulụna min-hu wa yasyrabu mimmā tasyrabụn'),
(2706, 23, 34, 'وَلَىِٕنْ اَطَعْتُمْ بَشَرًا مِّثْلَكُمْ اِنَّكُمْ اِذًا لَّخٰسِرُوْنَ ۙ', 'wa la`in aṭa\'tum basyaram miṡlakum innakum iżal lakhāsirụn'),
(2707, 23, 35, 'اَيَعِدُكُمْ اَنَّكُمْ اِذَا مِتُّمْ وَكُنْتُمْ تُرَابًا وَّعِظَامًا اَنَّكُمْ مُّخْرَجُوْنَ ۖ', 'a ya\'idukum annakum iżā mittum wa kuntum turābaw wa \'iẓāman annakum mukhrajụn'),
(2708, 23, 36, '۞ هَيْهَاتَ هَيْهَاتَ لِمَا تُوْعَدُوْنَ ۖ', 'haihāta haihāta limā tụ\'adụn'),
(2709, 23, 37, 'اِنْ هِيَ اِلَّا حَيَاتُنَا الدُّنْيَا نَمُوْتُ وَنَحْيَا وَمَا نَحْنُ بِمَبْعُوْثِيْنَ ۖ', 'in hiya illā ḥayātunad-dun-yā namụtu wa naḥyā wa mā naḥnu bimab\'ụṡīn'),
(2710, 23, 38, 'اِنْ هُوَ اِلَّا رَجُلُ ِۨافْتَرٰى عَلَى اللّٰهِ كَذِبًا وَّمَا نَحْنُ لَهٗ بِمُؤْمِنِيْنَ', 'in huwa illā rajuluniftarā \'alallāhi każibaw wa mā naḥnu lahụ bimu`minīn'),
(2711, 23, 39, 'قَالَ رَبِّ انْصُرْنِيْ بِمَا كَذَّبُوْنِ', 'qāla rabbinṣurnī bimā każżabụn'),
(2712, 23, 40, 'قَالَ عَمَّا قَلِيْلٍ لَّيُصْبِحُنَّ نٰدِمِيْنَ ۚ', 'qāla \'ammā qalīlil layuṣbiḥunna nādimīn'),
(2713, 23, 41, 'فَاَخَذَتْهُمُ الصَّيْحَةُ بِالْحَقِّ فَجَعَلْنٰهُمْ غُثَاۤءًۚ فَبُعْدًا لِّلْقَوْمِ الظّٰلِمِيْنَ', 'fa akhażat-humuṣ-ṣaiḥatu bil-ḥaqqi fa ja\'alnāhum guṡā`ā, fa bu\'dal lil-qaumiẓ-ẓālimīn'),
(2714, 23, 42, 'ثُمَّ اَنْشَأْنَا مِنْۢ بَعْدِهِمْ قُرُوْنًا اٰخَرِيْنَ ۗ', 'ṡumma ansya`nā mim ba\'dihim qurụnan ākharīn'),
(2715, 23, 43, 'مَا تَسْبِقُ مِنْ اُمَّةٍ اَجَلَهَا وَمَا يَسْتَأْخِرُوْنَ ۗ', 'mā tasbiqu min ummatin ajalahā wa mā yasta`khirụn'),
(2716, 23, 44, 'ثُمَّ اَرْسَلْنَا رُسُلَنَا تَتْرَاۗ كُلَّمَا جَاۤءَ اُمَّةً رَّسُوْلُهَا كَذَّبُوْهُ فَاَتْبَعْنَا بَعْضَهُمْ بَعْضًا وَّجَعَلْنٰهُمْ اَحَادِيْثَۚ فَبُعْدًا لِّقَوْمٍ لَّا يُؤْمِنُوْنَ', 'ṡumma arsalnā rusulanā tatrā, kullamā jā`a ummatar rasụluhā każżabụhu fa atba\'nā ba\'ḍahum ba\'ḍaw wa ja\'alnāhum aḥādīṡ, fa bu\'dal liqaumil lā yu`minụn'),
(2717, 23, 45, 'ثُمَّ اَرْسَلْنَا مُوْسٰى وَاَخَاهُ هٰرُوْنَ ەۙ بِاٰيٰتِنَا وَسُلْطٰنٍ مُّبِيْنٍۙ', 'ṡumma arsalnā mụsā wa akhāhu hārụna bi`āyātinā wa sulṭānim mubīn'),
(2718, 23, 46, 'اِلٰى فِرْعَوْنَ وَمَلَا۟ىِٕهٖ فَاسْتَكْبَرُوْا وَكَانُوْا قَوْمًا عَالِيْنَ ۚ', 'ilā fir\'auna wa malā`ihī fastakbarụ wa kānụ qauman \'ālīn'),
(2719, 23, 47, 'فَقَالُوْٓا اَنُؤْمِنُ لِبَشَرَيْنِ مِثْلِنَا وَقَوْمُهُمَا لَنَا عٰبِدُوْنَ ۚ', 'fa qālū a nu`minu libasyaraini miṡlinā wa qaumuhumā lanā \'ābidụn'),
(2720, 23, 48, 'فَكَذَّبُوْهُمَا فَكَانُوْا مِنَ الْمُهْلَكِيْنَ', 'fa każżabụhumā fa kānụ minal-muhlakīn'),
(2721, 23, 49, 'وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ لَعَلَّهُمْ يَهْتَدُوْنَ', 'wa laqad ātainā mụsal-kitāba la\'allahum yahtadụn'),
(2722, 23, 50, 'وَجَعَلْنَا ابْنَ مَرْيَمَ وَاُمَّهٗٓ اٰيَةً وَّاٰوَيْنٰهُمَآ اِلٰى رَبْوَةٍ ذَاتِ قَرَارٍ وَّمَعِيْنٍ', 'wa ja\'alnabna maryama wa ummahū āyataw wa āwaināhumā ilā rabwatin żāti qarāriw wa ma\'īn'),
(2723, 23, 51, 'يٰٓاَيُّهَا الرُّسُلُ كُلُوْا مِنَ الطَّيِّبٰتِ وَاعْمَلُوْا صَالِحًاۗ اِنِّيْ بِمَا تَعْمَلُوْنَ عَلِيْمٌ ۗ', 'yā ayyuhar-rusulu kulụ minaṭ-ṭayyibāti wa\'malụ ṣāliḥā, innī bimā ta\'malụna \'alīm'),
(2724, 23, 52, 'وَاِنَّ هٰذِهٖٓ اُمَّتُكُمْ اُمَّةً وَّاحِدَةً وَّاَنَا۠ رَبُّكُمْ فَاتَّقُوْنِ', 'wa inna hāżihī ummatukum ummataw wāḥidataw wa ana rabbukum fattaqụn'),
(2725, 23, 53, 'فَتَقَطَّعُوْٓا اَمْرَهُمْ بَيْنَهُمْ زُبُرًاۗ كُلُّ حِزْبٍۢ بِمَا لَدَيْهِمْ فَرِحُوْنَ', 'fa taqaṭṭa\'ū amrahum bainahum zuburā, kullu ḥizbim bimā ladaihim fariḥụn'),
(2726, 23, 54, 'فَذَرْهُمْ فِيْ غَمْرَتِهِمْ حَتّٰى حِيْنٍ', 'fa żar-hum fī gamratihim ḥattā ḥīn'),
(2727, 23, 55, 'اَيَحْسَبُوْنَ اَنَّمَا نُمِدُّهُمْ بِهٖ مِنْ مَّالٍ وَّبَنِيْنَ ۙ', 'a yaḥsabụna annamā numidduhum bihī mim māliw wa banīn'),
(2728, 23, 56, 'نُسَارِعُ لَهُمْ فِى الْخَيْرٰتِۗ بَلْ لَّا يَشْعُرُوْنَ', 'nusāri\'u lahum fil-khairāt, bal lā yasy\'urụn'),
(2729, 23, 57, 'اِنَّ الَّذِيْنَ هُمْ مِّنْ خَشْيَةِ رَبِّهِمْ مُّشْفِقُوْنَ ۙ', 'innallażīna hum min khasy-yati rabbihim musyfiqụn'),
(2730, 23, 58, 'وَالَّذِيْنَ هُمْ بِاٰيٰتِ رَبِّهِمْ يُؤْمِنُوْنَ ۙ', 'wallażīna hum bi`āyāti rabbihim yu`minụn'),
(2731, 23, 59, 'وَالَّذِيْنَ هُمْ بِرَبِّهِمْ لَا يُشْرِكُوْنَ ۙ', 'wallażīna hum birabbihim lā yusyrikụn'),
(2732, 23, 60, 'وَالَّذِيْنَ يُؤْتُوْنَ مَآ اٰتَوْا وَّقُلُوْبُهُمْ وَجِلَةٌ اَنَّهُمْ اِلٰى رَبِّهِمْ رٰجِعُوْنَ ۙ', 'wallażīna yu`tụna mā ātaw wa qulụbuhum wajilatun annahum ilā rabbihim rāji\'ụn'),
(2733, 23, 61, 'اُولٰۤىِٕكَ يُسَارِعُوْنَ فِى الْخَيْرٰتِ وَهُمْ لَهَا سٰبِقُوْنَ', 'ulā`ika yusāri\'ụna fil-khairāti wa hum lahā sābiqụn'),
(2734, 23, 62, 'وَلَا نُكَلِّفُ نَفْسًا اِلَّا وُسْعَهَاۖ وَلَدَيْنَا كِتٰبٌ يَّنْطِقُ بِالْحَقِّ وَهُمْ لَا يُظْلَمُوْنَ', 'wa lā nukallifu nafsan illā wus\'ahā waladainā kitābuy yanṭiqu bil-ḥaqqi wa hum lā yuẓlamụn'),
(2735, 23, 63, 'بَلْ قُلُوْبُهُمْ فِيْ غَمْرَةٍ مِّنْ هٰذَا وَلَهُمْ اَعْمَالٌ مِّنْ دُوْنِ ذٰلِكَ هُمْ لَهَا عَامِلُوْنَ', 'bal qulụbuhum fī gamratim min hāżā wa lahum a\'mālum min dụni żālika hum lahā \'āmilụn'),
(2736, 23, 64, 'حَتّٰٓى اِذَآ اَخَذْنَا مُتْرَفِيْهِمْ بِالْعَذَابِ اِذَا هُمْ يَجْـَٔرُوْنَ ۗ', 'ḥattā iżā akhażnā mutrafīhim bil-\'ażābi iżā hum yaj`arụn'),
(2737, 23, 65, 'لَا تَجْـَٔرُوا الْيَوْمَۖ اِنَّكُمْ مِّنَّا لَا تُنْصَرُوْنَ', 'lā taj`arul-yauma innakum minnā lā tunṣarụn'),
(2738, 23, 66, 'قَدْ كَانَتْ اٰيٰتِيْ تُتْلٰى عَلَيْكُمْ فَكُنْتُمْ عَلٰٓى اَعْقَابِكُمْ تَنْكِصُوْنَ ۙ', 'qad kānat āyātī tutlā \'alaikum fakuntum \'alā a\'qābikum tangkiṣụn'),
(2739, 23, 67, 'مُسْتَكْبِرِيْنَۙ بِهٖ سٰمِرًا تَهْجُرُوْنَ', 'mustakbirīna bihī sāmiran tahjurụn'),
(2740, 23, 68, 'اَفَلَمْ يَدَّبَّرُوا الْقَوْلَ اَمْ جَاۤءَهُمْ مَّا لَمْ يَأْتِ اٰبَاۤءَهُمُ الْاَوَّلِيْنَ ۖ', 'a fa lam yaddabbarul-qaula am jā`ahum mā lam ya`ti ābā`ahumul-awwalīn'),
(2741, 23, 69, 'اَمْ لَمْ يَعْرِفُوْا رَسُوْلَهُمْ فَهُمْ لَهٗ مُنْكِرُوْنَ ۖ', 'am lam ya\'rifụ rasụlahum fa hum lahụ mungkirụn'),
(2742, 23, 70, 'اَمْ يَقُوْلُوْنَ بِهٖ جِنَّةٌ ۗ بَلْ جَاۤءَهُمْ بِالْحَقِّ وَاَكْثَرُهُمْ لِلْحَقِّ كٰرِهُوْنَ', 'am yaqụlụna bihī jinnah, bal jā`ahum bil-ḥaqqi wa akṡaruhum lil-ḥaqqi kārihụn'),
(2743, 23, 71, 'وَلَوِ اتَّبَعَ الْحَقُّ اَهْوَاۤءَهُمْ لَفَسَدَتِ السَّمٰوٰتُ وَالْاَرْضُ وَمَنْ فِيْهِنَّۗ بَلْ اَتَيْنٰهُمْ بِذِكْرِهِمْ فَهُمْ عَنْ ذِكْرِهِمْ مُّعْرِضُوْنَ ۗ', 'wa lawittaba\'al-ḥaqqu ahwā`ahum lafasadatis-samāwātu wal-arḍu wa man fīhinn, bal ataināhum biżikrihim fa hum \'an żikrihim mu\'riḍụn'),
(2744, 23, 72, 'اَمْ تَسْـَٔلُهُمْ خَرْجًا فَخَرَاجُ رَبِّكَ خَيْرٌ ۖوَّهُوَ خَيْرُ الرّٰزِقِيْنَ', 'am tas`aluhum kharjan fa kharāju rabbika khairuw wa huwa khairur-rāziqīn'),
(2745, 23, 73, 'وَاِنَّكَ لَتَدْعُوْهُمْ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ', 'wa innaka latad\'ụhum ilā ṣirāṭim mustaqīm'),
(2746, 23, 74, 'وَاِنَّ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ عَنِ الصِّرَاطِ لَنَاكِبُوْنَ', 'wa innallażīna lā yu`minụna bil-ākhirati \'aniṣ-ṣirāṭi lanākibụn'),
(2747, 23, 75, '۞ وَلَوْ رَحِمْنٰهُمْ وَكَشَفْنَا مَا بِهِمْ مِّنْ ضُرٍّ لَّلَجُّوْا فِيْ طُغْيَانِهِمْ يَعْمَهُوْنَ', 'walau raḥimnāhum wa kasyafnā mā bihim min ḍurril lalajjụ fī ṭugyānihim ya\'mahụn'),
(2748, 23, 76, 'وَلَقَدْ اَخَذْنٰهُمْ بِالْعَذَابِ فَمَا اسْتَكَانُوْا لِرَبِّهِمْ وَمَا يَتَضَرَّعُوْنَ', 'wa laqad akhażnāhum bil-\'ażābi fa mastakānụ lirabbihim wa mā yataḍarra\'ụn'),
(2749, 23, 77, 'حَتّٰٓى اِذَا فَتَحْنَا عَلَيْهِمْ بَابًا ذَا عَذَابٍ شَدِيْدٍ اِذَا هُمْ فِيْهِ مُبْلِسُوْنَ', 'ḥattā iżā fataḥnā \'alaihim bāban żā \'ażābin syadīdin iżā hum fīhi mublisụn'),
(2750, 23, 78, 'وَهُوَ الَّذِيْٓ اَنْشَاَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٕدَةَۗ قَلِيْلًا مَّا تَشْكُرُوْنَ', 'wa huwallażī ansya`a lakumus-sam\'a wal-abṣāra wal-af`idah, qalīlam mā tasykurụn'),
(2751, 23, 79, 'وَهُوَ الَّذِيْ ذَرَاَكُمْ فِى الْاَرْضِ وَاِلَيْهِ تُحْشَرُوْنَ', 'wa huwallażī żara`akum fil-arḍi wa ilaihi tuḥsyarụn'),
(2752, 23, 80, 'وَهُوَ الَّذِيْ يُحْيٖ وَيُمِيْتُ وَلَهُ اخْتِلَافُ الَّيْلِ وَالنَّهَارِۗ اَفَلَا تَعْقِلُوْنَ', 'wa huwallażī yuḥyī wa yumītu wa lahukhtilāful-laili wan-nahār, a fa lā ta\'qilụn'),
(2753, 23, 81, 'بَلْ قَالُوْا مِثْلَ مَا قَالَ الْاَوَّلُوْنَ', 'bal qālụ miṡla mā qālal-awwalụn'),
(2754, 23, 82, 'قَالُوْٓا ءَاِذَا مِتْنَا وَكُنَّا تُرَابًا وَّعِظَامًا ءَاِنَّا لَمَبْعُوْثُوْنَ', 'qālū a iżā mitnā wa kunnā turābaw wa \'iẓāman a innā lamab\'ụṡụn'),
(2755, 23, 83, 'لَقَدْ وُعِدْنَا نَحْنُ وَاٰبَاۤؤُنَا هٰذَا مِنْ قَبْلُ اِنْ هٰذَآ اِلَّآ اَسَاطِيْرُ الْاَوَّلِيْنَ', 'laqad wu\'idnā naḥnu wa ābā`unā hāżā ming qablu in hāżā illā asāṭīrul-awwalīn'),
(2756, 23, 84, 'قُلْ لِّمَنِ الْاَرْضُ وَمَنْ فِيْهَآ اِنْ كُنْتُمْ تَعْلَمُوْنَ', 'qul limanil-arḍu wa man fīhā ing kuntum ta\'lamụn'),
(2757, 23, 85, 'سَيَقُوْلُوْنَ لِلّٰهِ ۗقُلْ اَفَلَا تَذَكَّرُوْنَ', 'sayaqụlụna lillāh, qul a fa lā tażakkarụn'),
(2758, 23, 86, 'قُلْ مَنْ رَّبُّ السَّمٰوٰتِ السَّبْعِ وَرَبُّ الْعَرْشِ الْعَظِيْمِ', 'qul mar rabbus-samāwātis-sab\'i wa rabbul-\'arsyil-\'aẓīm'),
(2759, 23, 87, 'سَيَقُوْلُوْنَ لِلّٰهِ ۗقُلْ اَفَلَا تَتَّقُوْنَ', 'sayaqụlụna lillāh, qul a fa lā tattaqụn'),
(2760, 23, 88, 'قُلْ مَنْۢ بِيَدِهٖ مَلَكُوْتُ كُلِّ شَيْءٍ وَّهُوَ يُجِيْرُ وَلَا يُجَارُ عَلَيْهِ اِنْ كُنْتُمْ تَعْلَمُوْنَ', 'qul mam biyadihī malakụtu kulli syai`iw wa huwa yujīru wa lā yujāru \'alaihi ing kuntum ta\'lamụn'),
(2761, 23, 89, 'سَيَقُوْلُوْنَ لِلّٰهِ ۗقُلْ فَاَنّٰى تُسْحَرُوْنَ', 'sayaqụlụna lillāh, qul fa annā tus-ḥarụn'),
(2762, 23, 90, 'بَلْ اَتَيْنٰهُمْ بِالْحَقِّ وَاِنَّهُمْ لَكٰذِبُوْنَ', 'bal ataināhum bil-ḥaqqi wa innahum lakāżibụn'),
(2763, 23, 91, 'مَا اتَّخَذَ اللّٰهُ مِنْ وَّلَدٍ وَّمَا كَانَ مَعَهٗ مِنْ اِلٰهٍ اِذًا لَّذَهَبَ كُلُّ اِلٰهٍۢ بِمَا خَلَقَ وَلَعَلَا بَعْضُهُمْ عَلٰى بَعْضٍۗ سُبْحٰنَ اللّٰهِ عَمَّا يَصِفُوْنَ ۙ', 'mattakhażallāhu miw waladiw wa mā kāna ma\'ahụ min ilāhin iżal lażahaba kullu ilāhim bimā khalaqa wa la\'alā ba\'ḍuhum \'alā ba\'ḍ, sub-ḥānallāhi \'ammā yaṣifụn'),
(2764, 23, 92, 'عٰلِمِ الْغَيْبِ وَالشَّهَادَةِ فَتَعٰلٰى عَمَّا يُشْرِكُوْنَ', '\'ālimil-gaibi wasy-syahādati fa ta\'ālā \'ammā yusyrikụn'),
(2765, 23, 93, 'قُلْ رَّبِّ اِمَّا تُرِيَنِّيْ مَا يُوْعَدُوْنَ ۙ', 'qur rabbi immā turiyannī mā yụ\'adụn'),
(2766, 23, 94, 'رَبِّ فَلَا تَجْعَلْنِيْ فِى الْقَوْمِ الظّٰلِمِيْنَ', 'rabbi fa lā taj\'alnī fil-qaumiẓ-ẓālimīn'),
(2767, 23, 95, 'وَاِنَّا عَلٰٓى اَنْ نُّرِيَكَ مَا نَعِدُهُمْ لَقٰدِرُوْنَ', 'wa innā \'alā an nuriyaka mā na\'iduhum laqādirụn'),
(2768, 23, 96, 'اِدْفَعْ بِالَّتِيْ هِيَ اَحْسَنُ السَّيِّئَةَۗ نَحْنُ اَعْلَمُ بِمَا يَصِفُوْنَ', 'idfa\' billatī hiya aḥsanus-sayyi`ah, naḥnu a\'lamu bimā yaṣifụn'),
(2769, 23, 97, 'وَقُلْ رَّبِّ اَعُوْذُ بِكَ مِنْ هَمَزٰتِ الشَّيٰطِيْنِ ۙ', 'wa qur rabbi a\'ụżu bika min hamazātisy-syayāṭīn'),
(2770, 23, 98, 'وَاَعُوْذُ بِكَ رَبِّ اَنْ يَّحْضُرُوْنِ', 'wa a\'ụżu bika rabbi ay yaḥḍurụn'),
(2771, 23, 99, 'حَتّٰٓى اِذَا جَاۤءَ اَحَدَهُمُ الْمَوْتُ قَالَ رَبِّ ارْجِعُوْنِ ۙ', 'ḥattā iżā jā`a aḥadahumul-mautu qāla rabbirji\'ụn'),
(2772, 23, 100, 'لَعَلِّيْٓ اَعْمَلُ صَالِحًا فِيْمَا تَرَكْتُ كَلَّاۗ اِنَّهَا كَلِمَةٌ هُوَ قَاۤىِٕلُهَاۗ وَمِنْ وَّرَاۤىِٕهِمْ بَرْزَخٌ اِلٰى يَوْمِ يُبْعَثُوْنَ', 'la\'allī a\'malu ṣāliḥan fīmā taraktu kallā, innahā kalimatun huwa qā`iluhā, wa miw warā`ihim barzakhun ilā yaumi yub\'aṡụn'),
(2773, 23, 101, 'فَاِذَا نُفِخَ فِى الصُّوْرِ فَلَآ اَنْسَابَ بَيْنَهُمْ يَوْمَىِٕذٍ وَّلَا يَتَسَاۤءَلُوْنَ', 'fa iżā nufikha fiṣ-ṣụri fa lā ansāba bainahum yauma`iżiw wa lā yatasā`alụn'),
(2774, 23, 102, 'فَمَنْ ثَقُلَتْ مَوَازِيْنُهٗ فَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ', 'fa man ṡaqulat mawāzīnuhụ fa ulā`ika humul-mufliḥụn'),
(2775, 23, 103, 'وَمَنْ خَفَّتْ مَوَازِيْنُهٗ فَاُولٰۤىِٕكَ الَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ فِيْ جَهَنَّمَ خٰلِدُوْنَ ۚ', 'wa man khaffat mawāzīnuhụ fa ulā`ikallażīna khasirū anfusahum fī jahannama khālidụn'),
(2776, 23, 104, 'تَلْفَحُ وُجُوْهَهُمُ النَّارُ وَهُمْ فِيْهَا كَالِحُوْنَ', 'talfaḥu wujụhahumun-nāru wa hum fīhā kāliḥụn'),
(2777, 23, 105, 'اَلَمْ تَكُنْ اٰيٰتِيْ تُتْلٰى عَلَيْكُمْ فَكُنْتُمْ بِهَا تُكَذِّبُوْنَ', 'a lam takun āyātī tutlā \'alaikum fa kuntum bihā tukażżibụn'),
(2778, 23, 106, 'قَالُوْا رَبَّنَا غَلَبَتْ عَلَيْنَا شِقْوَتُنَا وَكُنَّا قَوْمًا ضَاۤلِّيْنَ', 'qālụ rabbanā galabat \'alainā syiqwatunā wa kunnā qauman ḍāllīn'),
(2779, 23, 107, 'رَبَّنَآ اَخْرِجْنَا مِنْهَا فَاِنْ عُدْنَا فَاِنَّا ظٰلِمُوْنَ', 'rabbanā akhrijnā min-hā fa in \'udnā fa innā ẓālimụn'),
(2780, 23, 108, 'قَالَ اخْسَـُٔوْا فِيْهَا وَلَا تُكَلِّمُوْنِ', 'qālakhsa`ụ fīhā wa lā tukallimụn'),
(2781, 23, 109, 'اِنَّهٗ كَانَ فَرِيْقٌ مِّنْ عِبَادِيْ يَقُوْلُوْنَ رَبَّنَآ اٰمَنَّا فَاغْفِرْ لَنَا وَارْحَمْنَا وَاَنْتَ خَيْرُ الرّٰحِمِيْنَ ۚ', 'innahụ kāna farīqum min \'ibādī yaqụlụna rabbanā āmannā fagfir lanā war-ḥamnā wa anta khairur-rāḥimīn'),
(2782, 23, 110, 'فَاتَّخَذْتُمُوْهُمْ سِخْرِيًّا حَتّٰٓى اَنْسَوْكُمْ ذِكْرِيْ وَكُنْتُمْ مِّنْهُمْ تَضْحَكُوْنَ', 'fattakhażtumụhum sikhriyyan ḥattā ansaukum żikrī wa kuntum min-hum taḍ-ḥakụn'),
(2783, 23, 111, 'اِنِّيْ جَزَيْتُهُمُ الْيَوْمَ بِمَا صَبَرُوْٓاۙ اَنَّهُمْ هُمُ الْفَاۤىِٕزُوْنَ', 'innī jazaituhumul-yauma bimā ṣabarū annahum humul-fā`izụn'),
(2784, 23, 112, 'قٰلَ كَمْ لَبِثْتُمْ فِى الْاَرْضِ عَدَدَ سِنِيْنَ', 'qāla kam labiṡtum fil-arḍi \'adada sinīn'),
(2785, 23, 113, 'قَالُوْا لَبِثْنَا يَوْمًا اَوْ بَعْضَ يَوْمٍ فَسْـَٔلِ الْعَاۤدِّيْنَ', 'qālụ labiṡnā yauman au ba\'ḍa yaumin fas`alil-\'āddīn'),
(2786, 23, 114, 'قٰلَ اِنْ لَّبِثْتُمْ اِلَّا قَلِيْلًا لَّوْ اَنَّكُمْ كُنْتُمْ تَعْلَمُوْنَ', 'qāla il labiṡtum illā qalīlal lau annakum kuntum ta\'lamụn'),
(2787, 23, 115, 'اَفَحَسِبْتُمْ اَنَّمَا خَلَقْنٰكُمْ عَبَثًا وَّاَنَّكُمْ اِلَيْنَا لَا تُرْجَعُوْنَ', 'a fa ḥasibtum annamā khalaqnākum \'abaṡaw wa annakum ilainā lā turja\'ụn'),
(2788, 23, 116, 'فَتَعٰلَى اللّٰهُ الْمَلِكُ الْحَقُّۚ لَآ اِلٰهَ اِلَّا هُوَۚ رَبُّ الْعَرْشِ الْكَرِيْمِ', 'fa ta\'ālallāhul-malikul-ḥaqq, lā ilāha illā huw, rabbul-\'arsyil-karīm'),
(2789, 23, 117, 'وَمَنْ يَّدْعُ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ لَا بُرْهَانَ لَهٗ بِهٖۙ فَاِنَّمَا حِسَابُهٗ عِنْدَ رَبِّهٖۗ اِنَّهٗ لَا يُفْلِحُ الْكٰفِرُوْنَ', 'wa may yad\'u ma\'allāhi ilāhan ākhara lā bur-hāna lahụ bihī fa innamā ḥisābuhụ \'inda rabbih, innahụ lā yufliḥul-kāfirụn'),
(2790, 23, 118, 'وَقُلْ رَّبِّ اغْفِرْ وَارْحَمْ وَاَنْتَ خَيْرُ الرّٰحِمِيْنَ', 'wa qur rabbigfir war-ḥam wa anta khairur-rāḥimīn'),
(2791, 24, 1, 'سُوْرَةٌ اَنْزَلْنٰهَا وَفَرَضْنٰهَا وَاَنْزَلْنَا فِيْهَآ اٰيٰتٍۢ بَيِّنٰتٍ لَّعَلَّكُمْ تَذَكَّرُوْنَ', 'sụratun anzalnāhā wa faraḍnāhā wa anzalnā fīhā āyātim bayyinātil la\'allakum tażakkarụn'),
(2792, 24, 2, 'اَلزَّانِيَةُ وَالزَّانِيْ فَاجْلِدُوْا كُلَّ وَاحِدٍ مِّنْهُمَا مِائَةَ جَلْدَةٍ ۖوَّلَا تَأْخُذْكُمْ بِهِمَا رَأْفَةٌ فِيْ دِيْنِ اللّٰهِ اِنْ كُنْتُمْ تُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۚ وَلْيَشْهَدْ عَذَابَهُمَا طَاۤىِٕفَةٌ مِّنَ الْمُؤْمِنِيْنَ', 'az-zāniyatu waz-zānī fajlidụ kulla wāḥidim min-humā mi`ata jaldatiw wa lā ta`khużkum bihimā ra`fatun fī dīnillāhi ing kuntum tu`minụna billāhi wal-yaumil-ākhir, walyasy-had \'ażābahumā ṭā`ifatum minal-mu`minīn'),
(2793, 24, 3, 'اَلزَّانِيْ لَا يَنْكِحُ اِلَّا زَانِيَةً اَوْ مُشْرِكَةً ۖوَّالزَّانِيَةُ لَا يَنْكِحُهَآ اِلَّا زَانٍ اَوْ مُشْرِكٌۚ وَحُرِّمَ ذٰلِكَ عَلَى الْمُؤْمِنِيْنَ', 'az-zānī lā yangkiḥu illā zāniyatan au musyrikataw waz-zāniyatu lā yangkiḥuhā illā zānin au musyrik, wa ḥurrima żālika \'alal-mu`minīn'),
(2794, 24, 4, 'وَالَّذِيْنَ يَرْمُوْنَ الْمُحْصَنٰتِ ثُمَّ لَمْ يَأْتُوْا بِاَرْبَعَةِ شُهَدَاۤءَ فَاجْلِدُوْهُمْ ثَمٰنِيْنَ جَلْدَةً وَّلَا تَقْبَلُوْا لَهُمْ شَهَادَةً اَبَدًاۚ وَاُولٰۤىِٕكَ هُمُ الْفٰسِقُوْنَ ۙ', 'wallażīna yarmụnal-muḥṣanāti ṡumma lam ya`tụ bi`arba\'ati syuhadā`a fajlidụhum ṡamānīna jaldataw wa lā taqbalụ lahum syahādatan abadā, wa ulā`ika humul-fāsiqụn'),
(2795, 24, 5, 'اِلَّا الَّذِيْنَ تَابُوْا مِنْۢ بَعْدِ ذٰلِكَ وَاَصْلَحُوْاۚ فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'illallażīna tābụ mim ba\'di żālika wa aṣlaḥụ, fa innallāha gafụrur raḥīm'),
(2796, 24, 6, 'وَالَّذِيْنَ يَرْمُوْنَ اَزْوَاجَهُمْ وَلَمْ يَكُنْ لَّهُمْ شُهَدَاۤءُ اِلَّآ اَنْفُسُهُمْ فَشَهَادَةُ اَحَدِهِمْ اَرْبَعُ شَهٰدٰتٍۢ بِاللّٰهِ ۙاِنَّهٗ لَمِنَ الصّٰدِقِيْنَ', 'wallażīna yarmụna azwājahum wa lam yakul lahum syuhadā`u illā anfusuhum fa syahādatu aḥadihim arba\'u syahādātim billāhi innahụ laminaṣ-ṣādiqīn'),
(2797, 24, 7, 'وَالْخَامِسَةُ اَنَّ لَعْنَتَ اللّٰهِ عَلَيْهِ اِنْ كَانَ مِنَ الْكٰذِبِيْنَ', 'wal-khāmisatu anna la\'natallāhi \'alaihi ing kāna minal-kāżibīn'),
(2798, 24, 8, 'وَيَدْرَؤُا عَنْهَا الْعَذَابَ اَنْ تَشْهَدَ اَرْبَعَ شَهٰدٰتٍۢ بِاللّٰهِ اِنَّهٗ لَمِنَ الْكٰذِبِيْنَ ۙ', 'wa yadra`u \'an-hal-\'ażāba an tasy-hada arba\'a syahādātim billāhi innahụ laminal-kāżibīn'),
(2799, 24, 9, 'وَالْخَامِسَةَ اَنَّ غَضَبَ اللّٰهِ عَلَيْهَآ اِنْ كَانَ مِنَ الصّٰدِقِيْنَ', 'wal-khāmisata anna gaḍaballāhi \'alaihā ing kāna minaṣ-ṣādiqīn'),
(2800, 24, 10, 'وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ وَاَنَّ اللّٰهَ تَوَّابٌ حَكِيْمٌ', 'walau lā faḍlullāhi \'alaikum wa raḥmatuhụ wa annallāha tawwābun ḥakīm'),
(2801, 24, 11, 'اِنَّ الَّذِيْنَ جَاۤءُوْ بِالْاِفْكِ عُصْبَةٌ مِّنْكُمْۗ لَا تَحْسَبُوْهُ شَرًّا لَّكُمْۗ بَلْ هُوَ خَيْرٌ لَّكُمْۗ لِكُلِّ امْرِئٍ مِّنْهُمْ مَّا اكْتَسَبَ مِنَ الْاِثْمِۚ وَالَّذِيْ تَوَلّٰى كِبْرَهٗ مِنْهُمْ لَهٗ عَذَابٌ عَظِيْمٌ', 'innallażīna jā`ụ bil-ifki \'uṣbatum mingkum, lā taḥsabụhu syarral lakum, bal huwa khairul lakum, likullimri`im min-hum maktasaba minal-iṡm, wallażī tawallā kibrahụ min-hum lahụ \'ażābun \'aẓīm'),
(2802, 24, 12, 'لَوْلَآ اِذْ سَمِعْتُمُوْهُ ظَنَّ الْمُؤْمِنُوْنَ وَالْمُؤْمِنٰتُ بِاَنْفُسِهِمْ خَيْرًاۙ وَّقَالُوْا هٰذَآ اِفْكٌ مُّبِيْنٌ', 'lau lā iż sami\'tumụhu ẓannal-mu`minụna wal-mu`minātu bi`anfusihim khairaw wa qālụ hāżā ifkum mubīn'),
(2803, 24, 13, 'لَوْلَا جَاۤءُوْ عَلَيْهِ بِاَرْبَعَةِ شُهَدَاۤءَۚ فَاِذْ لَمْ يَأْتُوْا بِالشُّهَدَاۤءِ فَاُولٰۤىِٕكَ عِنْدَ اللّٰهِ هُمُ الْكٰذِبُوْنَ', 'lau lā jā`ụ \'alaihi bi`arba\'ati syuhadā`, fa iż lam ya`tụ bisy-syuhadā`i fa ulā`ika \'indallāhi humul-kāżibụn'),
(2804, 24, 14, 'وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ فِى الدُّنْيَا وَالْاٰخِرَةِ لَمَسَّكُمْ فِيْ مَآ اَفَضْتُمْ فِيْهِ عَذَابٌ عَظِيْمٌ', 'walau lā faḍlullāhi \'alaikum wa raḥmatuhụ fid-dun-yā wal-ākhirati lamassakum fī mā afaḍtum fīhi \'ażābun \'aẓīm'),
(2805, 24, 15, 'اِذْ تَلَقَّوْنَهٗ بِاَلْسِنَتِكُمْ وَتَقُوْلُوْنَ بِاَفْوَاهِكُمْ مَّا لَيْسَ لَكُمْ بِهٖ عِلْمٌ وَّتَحْسَبُوْنَهٗ هَيِّنًاۙ وَّهُوَ عِنْدَ اللّٰهِ عَظِيْمٌ ۚ', 'iż talaqqaunahụ bi`alsinatikum wa taqụlụna bi`afwāhikum mā laisa lakum bihī \'ilmuw wa taḥsabụnahụ hayyinaw wa huwa \'indallāhi \'aẓīm'),
(2806, 24, 16, 'وَلَوْلَآ اِذْ سَمِعْتُمُوْهُ قُلْتُمْ مَّا يَكُوْنُ لَنَآ اَنْ نَّتَكَلَّمَ بِهٰذَاۖ سُبْحٰنَكَ هٰذَا بُهْتَانٌ عَظِيْمٌ', 'walau lā iż sami\'tumụhu qultum mā yakụnu lanā an natakallama bihāżā sub-ḥānaka hāżā buhtānun \'aẓīm'),
(2807, 24, 17, 'يَعِظُكُمُ اللّٰهُ اَنْ تَعُوْدُوْا لِمِثْلِهٖٓ اَبَدًا اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ۚ', 'ya\'iẓukumullāhu an ta\'ụdụ limiṡlihī abadan ing kuntum mu`minīn'),
(2808, 24, 18, 'وَيُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيٰتِۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ', 'wa yubayyinullāhu lakumul-āyāt, wallāhu \'alīmun ḥakīm'),
(2809, 24, 19, 'اِنَّ الَّذِيْنَ يُحِبُّوْنَ اَنْ تَشِيْعَ الْفَاحِشَةُ فِى الَّذِيْنَ اٰمَنُوْا لَهُمْ عَذَابٌ اَلِيْمٌۙ فِى الدُّنْيَا وَالْاٰخِرَةِۗ وَاللّٰهُ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُوْنَ', 'innallażīna yuḥibbụna an tasyī\'al-fāḥisyatu fillażīna āmanụ lahum \'ażābun alīmun fid-dun-yā wal-ākhirah, wallāhu ya\'lamu wa antum lā ta\'lamụn'),
(2810, 24, 20, 'وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ وَاَنَّ اللّٰهَ رَءُوْفٌ رَّحِيْمٌ', 'walau lā faḍlullāhi \'alaikum wa raḥmatuhụ wa annallāha ra`ụfur raḥīm'),
(2811, 24, 21, '۞ يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّبِعُوْا خُطُوٰتِ الشَّيْطٰنِۗ وَمَنْ يَّتَّبِعْ خُطُوٰتِ الشَّيْطٰنِ فَاِنَّهٗ يَأْمُرُ بِالْفَحْشَاۤءِ وَالْمُنْكَرِۗ وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ مَا زَكٰى مِنْكُمْ مِّنْ اَحَدٍ اَبَدًاۙ وَّلٰكِنَّ اللّٰهَ يُزَكِّيْ مَنْ يَّشَاۤءُۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌ', 'yā ayyuhallażīna āmanụ lā tattabi\'ụ khuṭuwātisy-syaiṭān, wa may yattabi\' khuṭuwātisy-syaiṭāni fa innahụ ya`muru bil-faḥsyā`i wal-mungkar, walau lā faḍlullāhi \'alaikum wa raḥmatuhụ mā zakā mingkum min aḥadin abadaw wa lākinnallāha yuzakkī may yasyā`, wallāhu samī\'un \'alīm'),
(2812, 24, 22, 'وَلَا يَأْتَلِ اُولُو الْفَضْلِ مِنْكُمْ وَالسَّعَةِ اَنْ يُّؤْتُوْٓا اُولِى الْقُرْبٰى وَالْمَسٰكِيْنَ وَالْمُهٰجِرِيْنَ فِيْ سَبِيْلِ اللّٰهِ ۖوَلْيَعْفُوْا وَلْيَصْفَحُوْاۗ اَلَا تُحِبُّوْنَ اَنْ يَّغْفِرَ اللّٰهُ لَكُمْ ۗوَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ', 'wa lā ya`tali ulul-faḍli mingkum was-sa\'ati ay yu`tū ulil-qurbā wal-masākīna wal-muhājirīna fī sabīlillāhi walya\'fụ walyaṣfaḥụ, alā tuḥibbụna ay yagfirallāhu lakum, wallāhu gafụrur raḥīm'),
(2813, 24, 23, 'اِنَّ الَّذِيْنَ يَرْمُوْنَ الْمُحْصَنٰتِ الْغٰفِلٰتِ الْمُؤْمِنٰتِ لُعِنُوْا فِى الدُّنْيَا وَالْاٰخِرَةِۖ وَلَهُمْ عَذَابٌ عَظِيْمٌ ۙ', 'innallażīna yarmụnal-muḥṣanātil-gāfilātil-mu`mināti lu\'inụ fid-dun-yā wal-ākhirati wa lahum \'ażābun \'aẓīm'),
(2814, 24, 24, 'يَّوْمَ تَشْهَدُ عَلَيْهِمْ اَلْسِنَتُهُمْ وَاَيْدِيْهِمْ وَاَرْجُلُهُمْ بِمَا كَانُوْا يَعْمَلُوْنَ', 'yauma tasy-hadu \'alaihim alsinatuhum wa aidīhim wa arjuluhum bimā kānụ ya\'malụn'),
(2815, 24, 25, 'يَوْمَىِٕذٍ يُّوَفِّيْهِمُ اللّٰهُ دِيْنَهُمُ الْحَقَّ وَيَعْلَمُوْنَ اَنَّ اللّٰهَ هُوَ الْحَقُّ الْمُبِيْنُ', 'yauma`iżiy yuwaffīhimullāhu dīnahumul-ḥaqqa wa ya\'lamụna annallāha huwal-ḥaqqul-mubīn'),
(2816, 24, 26, 'اَلْخَبِيْثٰتُ لِلْخَبِيْثِيْنَ وَالْخَبِيْثُوْنَ لِلْخَبِيْثٰتِۚ وَالطَّيِّبٰتُ لِلطَّيِّبِيْنَ وَالطَّيِّبُوْنَ لِلطَّيِّبٰتِۚ اُولٰۤىِٕكَ مُبَرَّءُوْنَ مِمَّا يَقُوْلُوْنَۗ لَهُمْ مَّغْفِرَةٌ وَّرِزْقٌ كَرِيْمٌ', 'al-khabīṡātu lil-khabīṡīna wal-khabīṡụna lil-khabīṡāt, waṭ-ṭayyibātu liṭ-ṭayyibīna waṭ-ṭayyibụna liṭ-ṭayyibāt, ulā`ika mubarra`ụna mimmā yaqụlụn, lahum magfiratuw wa rizqung karīm'),
(2817, 24, 27, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَدْخُلُوْا بُيُوْتًا غَيْرَ بُيُوْتِكُمْ حَتّٰى تَسْتَأْنِسُوْا وَتُسَلِّمُوْا عَلٰٓى اَهْلِهَاۗ ذٰلِكُمْ خَيْرٌ لَّكُمْ لَعَلَّكُمْ تَذَكَّرُوْنَ', 'yā ayyuhallażīna āmanụ lā tadkhulụ buyụtan gaira buyụtikum ḥattā tasta`nisụ wa tusallimụ \'alā ahlihā, żālikum khairul lakum la\'allakum tażakkarụn'),
(2818, 24, 28, 'فَاِنْ لَّمْ تَجِدُوْا فِيْهَآ اَحَدًا فَلَا تَدْخُلُوْهَا حَتّٰى يُؤْذَنَ لَكُمْ وَاِنْ قِيْلَ لَكُمُ ارْجِعُوْا فَارْجِعُوْا هُوَ اَزْكٰى لَكُمْ ۗوَاللّٰهُ بِمَا تَعْمَلُوْنَ عَلِيْمٌ', 'fa il lam tajidụ fīhā aḥadan fa lā tadkhulụhā ḥattā yu`żana lakum wa ing qīla lakumurji\'ụ farji\'ụ huwa azkā lakum, wallāhu bimā ta\'malụna \'alīm'),
(2819, 24, 29, 'لَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَدْخُلُوْا بُيُوْتًا غَيْرَ مَسْكُوْنَةٍ فِيْهَا مَتَاعٌ لَّكُمْۗ وَاللّٰهُ يَعْلَمُ مَا تُبْدُوْنَ وَمَا تَكْتُمُوْنَ', 'laisa \'alaikum junāḥun an tadkhulụ buyụtan gaira maskụnatin fīhā matā\'ul lakum, wallāhu ya\'lamu mā tubdụna wa mā taktumụn'),
(2820, 24, 30, 'قُلْ لِّلْمُؤْمِنِيْنَ يَغُضُّوْا مِنْ اَبْصَارِهِمْ وَيَحْفَظُوْا فُرُوْجَهُمْۗ ذٰلِكَ اَزْكٰى لَهُمْۗ اِنَّ اللّٰهَ خَبِيْرٌۢ بِمَا يَصْنَعُوْنَ', 'qul lil-mu`minīna yaguḍḍụ min abṣārihim wa yaḥfaẓụ furụjahum, żālika azkā lahum, innallāha khabīrum bimā yaṣna\'ụn'),
(2821, 24, 31, 'وَقُلْ لِّلْمُؤْمِنٰتِ يَغْضُضْنَ مِنْ اَبْصَارِهِنَّ وَيَحْفَظْنَ فُرُوْجَهُنَّ وَلَا يُبْدِيْنَ زِيْنَتَهُنَّ اِلَّا مَا ظَهَرَ مِنْهَا وَلْيَضْرِبْنَ بِخُمُرِهِنَّ عَلٰى جُيُوْبِهِنَّۖ وَلَا يُبْدِيْنَ زِيْنَتَهُنَّ اِلَّا لِبُعُوْلَتِهِنَّ اَوْ اٰبَاۤىِٕهِنَّ اَوْ اٰبَاۤءِ بُعُوْلَتِهِنَّ اَوْ اَبْنَاۤىِٕهِنَّ اَوْ اَبْنَاۤءِ بُعُوْلَتِهِنَّ اَوْ اِخْوَانِهِنَّ اَوْ بَنِيْٓ اِخْوَانِهِنَّ اَوْ بَنِيْٓ اَخَوٰتِهِنَّ اَوْ نِسَاۤىِٕهِنَّ اَوْ مَا مَلَكَتْ اَيْمَانُهُنَّ اَوِ التَّابِعِيْنَ غَيْرِ اُولِى الْاِرْبَةِ مِنَ الرِّجَالِ اَوِ الطِّفْلِ الَّذِيْنَ لَمْ يَظْهَرُوْا عَلٰى عَوْرٰتِ النِّسَاۤءِ ۖوَلَا يَضْرِبْنَ بِاَرْجُلِهِنَّ لِيُعْلَمَ مَا يُخْفِيْنَ مِنْ زِيْنَتِهِنَّۗ وَتُوْبُوْٓا اِلَى اللّٰهِ جَمِيْعًا اَيُّهَ الْمُؤْمِنُوْنَ لَعَلَّكُمْ تُفْلِحُوْنَ', 'wa qul lil-mu`mināti yagḍuḍna min abṣārihinna wa yaḥfaẓna furụjahunna wa lā yubdīna zīnatahunna illā mā ẓahara min-hā walyaḍribna bikhumurihinna \'alā juyụbihinna wa lā yubdīna zīnatahunna illā libu\'ụlatihinna au ābā`ihinna au ābā`i bu\'ụlatihinna au abnā`ihinna au abnā`i bu\'ụlatihinna au ikhwānihinna au banī ikhwānihinna au banī akhawātihinna au nisā`ihinna au mā malakat aimānuhunna awittābi\'īna gairi ulil-irbati minar-rijāli awiṭ-ṭiflillażīna lam yaẓ-harụ \'alā \'aurātin-nisā`i wa lā yaḍribna bi`arjulihinna liyu\'lama mā yukhfīna min zīnatihinn, wa tụbū ilallāhi jamī\'an ayyuhal-mu`minụna la\'allakum tufliḥụn'),
(2822, 24, 32, 'وَاَنْكِحُوا الْاَيَامٰى مِنْكُمْ وَالصّٰلِحِيْنَ مِنْ عِبَادِكُمْ وَاِمَاۤىِٕكُمْۗ اِنْ يَّكُوْنُوْا فُقَرَاۤءَ يُغْنِهِمُ اللّٰهُ مِنْ فَضْلِهٖۗ وَاللّٰهُ وَاسِعٌ عَلِيْمٌ', 'wa angkiḥul-ayāmā mingkum waṣ-ṣāliḥīna min \'ibādikum wa imā`ikum, iy yakụnụ fuqarā`a yugnihimullāhu min faḍlih, wallāhu wāsi\'un \'alīm'),
(2823, 24, 33, 'وَلْيَسْتَعْفِفِ الَّذِيْنَ لَا يَجِدُوْنَ نِكَاحًا حَتّٰى يُغْنِيَهُمُ اللّٰهُ مِنْ فَضْلِهٖ ۗوَالَّذِيْنَ يَبْتَغُوْنَ الْكِتٰبَ مِمَّا مَلَكَتْ اَيْمَانُكُمْ فَكَاتِبُوْهُمْ اِنْ عَلِمْتُمْ فِيْهِمْ خَيْرًا وَّاٰتُوْهُمْ مِّنْ مَّالِ اللّٰهِ الَّذِيْٓ اٰتٰىكُمْ ۗوَلَا تُكْرِهُوْا فَتَيٰتِكُمْ عَلَى الْبِغَاۤءِ اِنْ اَرَدْنَ تَحَصُّنًا لِّتَبْتَغُوْا عَرَضَ الْحَيٰوةِ الدُّنْيَا ۗوَمَنْ يُّكْرِهْهُّنَّ فَاِنَّ اللّٰهَ مِنْۢ بَعْدِ اِكْرَاهِهِنَّ غَفُوْرٌ رَّحِيْمٌ', 'walyasta\'fifillażīna lā yajidụna nikāḥan ḥattā yugniyahumullāhu min faḍlih, wallażīna yabtagụnal-kitāba mimmā malakat aimānukum fa kātibụhum in \'alimtum fīhim khairaw wa ātụhum mim mālillāhillażī ātākum, wa lā tukrihụ fatayātikum \'alal-bigā`i in aradna taḥaṣṣunal litabtagụ \'araḍal ḥayātid-dun-yā, wa may yukrihhunna fa innallāha mim ba\'di ikrāhihinna gafụrur raḥīm'),
(2824, 24, 34, 'وَلَقَدْ اَنْزَلْنَآ اِلَيْكُمْ اٰيٰتٍ مُّبَيِّنٰتٍ وَّمَثَلًا مِّنَ الَّذِيْنَ خَلَوْا مِنْ قَبْلِكُمْ وَمَوْعِظَةً لِّلْمُتَّقِيْنَ', 'wa laqad anzalnā ilaikum āyātim mubayyinātiw wa maṡalam minallażīna khalau ming qablikum wa mau\'iẓatal lil-muttaqīn'),
(2825, 24, 35, '۞ اَللّٰهُ نُوْرُ السَّمٰوٰتِ وَالْاَرْضِۗ مَثَلُ نُوْرِهٖ كَمِشْكٰوةٍ فِيْهَا مِصْبَاحٌۗ اَلْمِصْبَاحُ فِيْ زُجَاجَةٍۗ اَلزُّجَاجَةُ كَاَنَّهَا كَوْكَبٌ دُرِّيٌّ يُّوْقَدُ مِنْ شَجَرَةٍ مُّبٰرَكَةٍ زَيْتُوْنَةٍ لَّا شَرْقِيَّةٍ وَّلَا غَرْبِيَّةٍۙ يَّكَادُ زَيْتُهَا يُضِيْۤءُ وَلَوْ لَمْ تَمْسَسْهُ نَارٌۗ نُوْرٌ عَلٰى نُوْرٍۗ يَهْدِى اللّٰهُ لِنُوْرِهٖ مَنْ يَّشَاۤءُۗ وَيَضْرِبُ اللّٰهُ الْاَمْثَالَ لِلنَّاسِۗ وَاللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمٌ ۙ', 'allāhu nụrus-samāwāti wal-arḍ, maṡalu nụrihī kamisykātin fīhā miṣbāḥ, al-miṣbāḥu fī zujājah, az-zujājatu ka`annahā kaukabun durriyyuy yụqadu min syajaratim mubārakatin zaitụnatil lā syarqiyyatiw wa lā garbiyyatiy yakādu zaituhā yuḍī`u walau lam tamsas-hu nār, nụrun \'alā nụr, yahdillāhu linụrihī may yasyā`, wa yaḍribullāhul-amṡāla lin-nās, wallāhu bikulli syai`in \'alīm'),
(2826, 24, 36, 'فِيْ بُيُوْتٍ اَذِنَ اللّٰهُ اَنْ تُرْفَعَ وَيُذْكَرَ فِيْهَا اسْمُهٗۙ يُسَبِّحُ لَهٗ فِيْهَا بِالْغُدُوِّ وَالْاٰصَالِ ۙ', 'fī buyụtin ażinallāhu an turfa\'a wa yużkara fīhasmuhụ yusabbiḥu lahụ fīhā bil-guduwwi wal-āṣāl'),
(2827, 24, 37, 'رِجَالٌ لَّا تُلْهِيْهِمْ تِجَارَةٌ وَّلَا بَيْعٌ عَنْ ذِكْرِ اللّٰهِ وَاِقَامِ الصَّلٰوةِ وَاِيْتَاۤءِ الزَّكٰوةِ ۙيَخَافُوْنَ يَوْمًا تَتَقَلَّبُ فِيْهِ الْقُلُوْبُ وَالْاَبْصَارُ ۙ', 'rijālul lā tul-hīhim tijāratuw wa lā bai\'un \'an żikrillāhi wa iqāmiṣ-ṣalāti wa ītā`iz-zakāti yakhāfụna yauman tataqallabu fīhil qulụbu wal-abṣār'),
(2828, 24, 38, 'لِيَجْزِيَهُمُ اللّٰهُ اَحْسَنَ مَا عَمِلُوْا وَيَزِيْدَهُمْ مِّنْ فَضْلِهٖۗ وَاللّٰهُ يَرْزُقُ مَنْ يَّشَاۤءُ بِغَيْرِ حِسَابٍ', 'liyajziyahumullāhu aḥsana mā \'amilụ wa yazīdahum min faḍlih, wallāhu yarzuqu may yasyā`u bigairi ḥisāb'),
(2829, 24, 39, 'وَالَّذِيْنَ كَفَرُوْٓا اَعْمَالُهُمْ كَسَرَابٍۢ بِقِيْعَةٍ يَّحْسَبُهُ الظَّمْاٰنُ مَاۤءًۗ حَتّٰٓى اِذَا جَاۤءَهٗ لَمْ يَجِدْهُ شَيْـًٔا وَّوَجَدَ اللّٰهَ عِنْدَهٗ فَوَفّٰىهُ حِسَابَهٗ ۗ وَاللّٰهُ سَرِيْعُ الْحِسَابِ ۙ', 'wallażīna kafarū a\'māluhum kasarābim biqī\'atiy yaḥsabuhuẓ-ẓam`ānu mā`ā, ḥattā iżā jā`ahụ lam yajid-hu syai`aw wa wajadallāha \'indahụ fa waffāhu ḥisābah, wallāhu sarī\'ul ḥisāb'),
(2830, 24, 40, 'اَوْ كَظُلُمٰتٍ فِيْ بَحْرٍ لُّجِّيٍّ يَّغْشٰىهُ مَوْجٌ مِّنْ فَوْقِهٖ مَوْجٌ مِّنْ فَوْقِهٖ سَحَابٌۗ ظُلُمٰتٌۢ بَعْضُهَا فَوْقَ بَعْضٍۗ اِذَآ اَخْرَجَ يَدَهٗ لَمْ يَكَدْ يَرٰىهَاۗ وَمَنْ لَّمْ يَجْعَلِ اللّٰهُ لَهٗ نُوْرًا فَمَا لَهٗ مِنْ نُّوْرٍ', 'au kaẓulumātin fī baḥril lujjiyyiy yagsyāhu maujum min fauqihī maujum min fauqihī saḥāb, ẓulumātum ba\'ḍuhā fauqa ba\'ḍ, iżā akhraja yadahụ lam yakad yarāhā, wa mal lam yaj\'alillāhu lahụ nụran fa mā lahụ min nụr'),
(2831, 24, 41, 'اَلَمْ تَرَ اَنَّ اللّٰهَ يُسَبِّحُ لَهٗ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِ وَالطَّيْرُ صٰۤفّٰتٍۗ كُلٌّ قَدْ عَلِمَ صَلَاتَهٗ وَتَسْبِيْحَهٗۗ وَاللّٰهُ عَلِيْمٌۢ بِمَا يَفْعَلُوْنَ', 'a lam tara annallāha yusabbiḥu lahụ man fis-samāwāti wal-arḍi waṭ-ṭairu ṣāffāt, kullung qad \'alima ṣalātahụ wa tasbīḥah, wallāhu \'alīmum bimā yaf\'alụn'),
(2832, 24, 42, 'وَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۚ وَاِلَى اللّٰهِ الْمَصِيْرُ', 'wa lillāhi mulkus-samāwāti wal-arḍ, wa ilallāhil-maṣīr'),
(2833, 24, 43, 'اَلَمْ تَرَ اَنَّ اللّٰهَ يُزْجِيْ سَحَابًا ثُمَّ يُؤَلِّفُ بَيْنَهٗ ثُمَّ يَجْعَلُهٗ رُكَامًا فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلٰلِهٖۚ وَيُنَزِّلُ مِنَ السَّمَاۤءِ مِنْ جِبَالٍ فِيْهَا مِنْۢ بَرَدٍ فَيُصِيْبُ بِهٖ مَنْ يَّشَاۤءُ وَيَصْرِفُهٗ عَنْ مَّنْ يَّشَاۤءُۗ يَكَادُ سَنَا بَرْقِهٖ يَذْهَبُ بِالْاَبْصَارِ ۗ', 'a lam tara annallāha yuzjī saḥāban ṡumma yu`allifu bainahụ ṡumma yaj\'aluhụ rukāman fa taral-wadqa yakhruju min khilālih, wa yunazzilu minas-samā`i min jibālin fīhā mim baradin fa yuṣību bihī may yasyā`u wa yaṣrifuhụ \'am may yasyā`, yakādu sanā barqihī yaż-habu bil-abṣār'),
(2834, 24, 44, 'يُقَلِّبُ اللّٰهُ الَّيْلَ وَالنَّهَارَۗ اِنَّ فِيْ ذٰلِكَ لَعِبْرَةً لِّاُولِى الْاَبْصَارِ', 'yuqallibullāhul-laila wan-nahār, inna fī żālika la\'ibratal li`ulil-abṣār'),
(2835, 24, 45, 'وَاللّٰهُ خَلَقَ كُلَّ دَاۤبَّةٍ مِّنْ مَّاۤءٍۚ فَمِنْهُمْ مَّنْ يَّمْشِيْ عَلٰى بَطْنِهٖۚ وَمِنْهُمْ مَّنْ يَّمْشِيْ عَلٰى رِجْلَيْنِۚ وَمِنْهُمْ مَّنْ يَّمْشِيْ عَلٰٓى اَرْبَعٍۗ يَخْلُقُ اللّٰهُ مَا يَشَاۤءُۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'wallāhu khalaqa kulla dābbatim mim mā`, fa min-hum may yamsyī \'alā baṭnih, wa min-hum may yamsyī \'alā rijlaīn, wa min-hum may yamsyī \'alā arba\', yakhluqullāhu mā yasyā`, innallāha \'alā kulli syai`ing qadīr'),
(2836, 24, 46, 'لَقَدْ اَنْزَلْنَآ اٰيٰتٍ مُّبَيِّنٰتٍۗ وَاللّٰهُ يَهْدِيْ مَنْ يَّشَاۤءُ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ', 'laqad anzalnā āyātim mubayyināt, wallāhu yahdī may yasyā`u ilā ṣirāṭim mustaqīm'),
(2837, 24, 47, 'وَيَقُوْلُوْنَ اٰمَنَّا بِاللّٰهِ وَبِالرَّسُوْلِ وَاَطَعْنَا ثُمَّ يَتَوَلّٰى فَرِيْقٌ مِّنْهُمْ مِّنْۢ بَعْدِ ذٰلِكَۗ وَمَآ اُولٰۤىِٕكَ بِالْمُؤْمِنِيْنَ', 'wa yaqụlụna āmannā billāhi wa bir-rasụli wa aṭa\'nā ṡumma yatawallā farīqum min-hum mim ba\'di żālik, wa mā ulā`ika bil-mu`minīn'),
(2838, 24, 48, 'وَاِذَا دُعُوْٓا اِلَى اللّٰهِ وَرَسُوْلِهٖ لِيَحْكُمَ بَيْنَهُمْ اِذَا فَرِيْقٌ مِّنْهُمْ مُّعْرِضُوْنَ', 'wa iżā du\'ū ilallāhi wa rasụlihī liyaḥkuma bainahum iżā farīqum min-hum mu\'riḍụn'),
(2839, 24, 49, 'وَاِنْ يَّكُنْ لَّهُمُ الْحَقُّ يَأْتُوْٓا اِلَيْهِ مُذْعِنِيْنَ', 'wa iy yakul lahumul-ḥaqqu ya`tū ilaihi muż\'inīn'),
(2840, 24, 50, 'اَفِيْ قُلُوْبِهِمْ مَّرَضٌ اَمِ ارْتَابُوْٓا اَمْ يَخَافُوْنَ اَنْ يَّحِيْفَ اللّٰهُ عَلَيْهِمْ وَرَسُوْلُهٗ ۗبَلْ اُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ', 'a fī qulụbihim maraḍun amirtābū am yakhāfụna ay yaḥīfallāhu \'alaihim wa rasụluh, bal ulā`ika humuẓ-ẓālimụn'),
(2841, 24, 51, 'اِنَّمَا كَانَ قَوْلَ الْمُؤْمِنِيْنَ اِذَا دُعُوْٓا اِلَى اللّٰهِ وَرَسُوْلِهٖ لِيَحْكُمَ بَيْنَهُمْ اَنْ يَّقُوْلُوْا سَمِعْنَا وَاَطَعْنَاۗ وَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ', 'innamā kāna qaulal-mu`minīna iżā du\'ū ilallāhi wa rasụlihī liyaḥkuma bainahum ay yaqụlụ sami\'nā wa aṭa\'nā, wa ulā`ika humul-mufliḥụn'),
(2842, 24, 52, 'وَمَنْ يُّطِعِ اللّٰهَ وَرَسُوْلَهٗ وَيَخْشَ اللّٰهَ وَيَتَّقْهِ فَاُولٰۤىِٕكَ هُمُ الْفَاۤىِٕزُوْنَ', 'wa may yuṭi\'illāha wa rasụlahụ wa yakhsyallāha wa yattaq-hi fa ulā`ika humul-fā`izụn'),
(2843, 24, 53, '۞ وَاَقْسَمُوْا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْ لَىِٕنْ اَمَرْتَهُمْ لَيَخْرُجُنَّۗ قُلْ لَّا تُقْسِمُوْاۚ طَاعَةٌ مَّعْرُوْفَةٌ ۗاِنَّ اللّٰهَ خَبِيْرٌۢ بِمَا تَعْمَلُوْنَ', 'wa aqsamụ billāhi jahda aimānihim la`in amartahum layakhrujunn, qul lā tuqsimụ, ṭā\'atum ma\'rụfah, innallāha khabīrum bimā ta\'malụn'),
(2844, 24, 54, 'قُلْ اَطِيْعُوا اللّٰهَ وَاَطِيْعُوا الرَّسُوْلَۚ فَاِنْ تَوَلَّوْا فَاِنَّمَا عَلَيْهِ مَا حُمِّلَ وَعَلَيْكُمْ مَّا حُمِّلْتُمْۗ وَاِنْ تُطِيْعُوْهُ تَهْتَدُوْاۗ وَمَا عَلَى الرَّسُوْلِ اِلَّا الْبَلٰغُ الْمُبِيْنُ', 'qul aṭī\'ullāha wa aṭī\'ur-rasụl, fa in tawallau fa innamā \'alaihi mā ḥummila wa \'alaikum mā ḥummiltum, wa in tuṭī\'ụhu tahtadụ, wa mā \'alar-rasụli illal-balāgul-mubīn'),
(2845, 24, 55, 'وَعَدَ اللّٰهُ الَّذِيْنَ اٰمَنُوْا مِنْكُمْ وَعَمِلُوا الصّٰلِحٰتِ لَيَسْتَخْلِفَنَّهُمْ فِى الْاَرْضِ كَمَا اسْتَخْلَفَ الَّذِيْنَ مِنْ قَبْلِهِمْۖ وَلَيُمَكِّنَنَّ لَهُمْ دِيْنَهُمُ الَّذِى ارْتَضٰى لَهُمْ وَلَيُبَدِّلَنَّهُمْ مِّنْۢ بَعْدِ خَوْفِهِمْ اَمْنًاۗ يَعْبُدُوْنَنِيْ لَا يُشْرِكُوْنَ بِيْ شَيْـًٔاۗ وَمَنْ كَفَرَ بَعْدَ ذٰلِكَ فَاُولٰۤىِٕكَ هُمُ الْفٰسِقُوْنَ', 'wa\'adallāhullażīna āmanụ mingkum wa \'amiluṣ-ṣāliḥāti layastakhlifannahum fil-arḍi kamastakhlafallażīna ming qablihim wa layumakkinanna lahum dīnahumullażirtaḍā lahum wa layubaddilannahum mim ba\'di khaufihim amnā, ya\'budụnanī lā yusyrikụna bī syai`ā, wa mang kafara ba\'da żālika fa ulā`ika humul-fāsiqụn'),
(2846, 24, 56, 'وَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَاَطِيْعُوا الرَّسُوْلَ لَعَلَّكُمْ تُرْحَمُوْنَ', 'wa aqīmuṣ-ṣalāta wa ātuz-zakāta wa aṭī\'ur-rasụla la\'allakum tur-ḥamụn'),
(2847, 24, 57, 'لَا تَحْسَبَنَّ الَّذِيْنَ كَفَرُوْا مُعْجِزِيْنَ فِى الْاَرْضِۚ وَمَأْوٰىهُمُ النَّارُۗ وَلَبِئْسَ الْمَصِيْرُ', 'lā taḥsabannallażīna kafarụ mu\'jizīna fil-arḍ, wa ma`wāhumun-nār, wa labi`sal-maṣīr'),
(2848, 24, 58, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لِيَسْتَأْذِنْكُمُ الَّذِيْنَ مَلَكَتْ اَيْمَانُكُمْ وَالَّذِيْنَ لَمْ يَبْلُغُوا الْحُلُمَ مِنْكُمْ ثَلٰثَ مَرّٰتٍۗ مِنْ قَبْلِ صَلٰوةِ الْفَجْرِ وَحِيْنَ تَضَعُوْنَ ثِيَابَكُمْ مِّنَ الظَّهِيْرَةِ وَمِنْۢ بَعْدِ صَلٰوةِ الْعِشَاۤءِۗ ثَلٰثُ عَوْرٰتٍ لَّكُمْۗ لَيْسَ عَلَيْكُمْ وَلَا عَلَيْهِمْ جُنَاحٌۢ بَعْدَهُنَّۗ طَوَّافُوْنَ عَلَيْكُمْ بَعْضُكُمْ عَلٰى بَعْضٍۗ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيٰتِۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ', 'yā ayyuhallażīna āmanụ liyasta`żingkumullażīna malakat aimānukum wallażīna lam yablugul-ḥuluma mingkum ṡalāṡa marrāt, ming qabli ṣalātil-fajri wa ḥīna taḍa\'ụna ṡiyābakum minaẓ-ẓahīrati wa mim ba\'di ṣalātil-\'isyā`, ṡalāṡu \'aurātil lakum, laisa \'alaikum wa lā \'alaihim junāḥum ba\'dahunn, ṭawwāfụna \'alaikum ba\'ḍukum \'alā ba\'ḍ, każālika yubayyinullāhu lakumul-āyāt, wallāhu \'alīmun ḥakīm'),
(2849, 24, 59, 'وَاِذَا بَلَغَ الْاَطْفَالُ مِنْكُمُ الْحُلُمَ فَلْيَسْتَأْذِنُوْا كَمَا اسْتَأْذَنَ الَّذِيْنَ مِنْ قَبْلِهِمْۗ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيٰتِهٖۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ', 'wa iżā balagal-aṭfālu mingkumul-ḥuluma falyasta`żinụ kamasta`żanallażīna ming qablihim, każālika yubayyinullāhu lakum āyātih, wallāhu \'alīmun ḥakīm'),
(2850, 24, 60, 'وَالْقَوَاعِدُ مِنَ النِّسَاۤءِ الّٰتِيْ لَا يَرْجُوْنَ نِكَاحًا فَلَيْسَ عَلَيْهِنَّ جُنَاحٌ اَنْ يَّضَعْنَ ثِيَابَهُنَّ غَيْرَ مُتَبَرِّجٰتٍۢ بِزِيْنَةٍۗ وَاَنْ يَّسْتَعْفِفْنَ خَيْرٌ لَّهُنَّۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌ', 'wal-qawā\'idu minan-nisā`illātī lā yarjụna nikāḥan fa laisa \'alaihinna junāḥun ay yaḍa\'na ṡiyābahunna gaira mutabarrijātim bizīnah, wa ay yasta\'fifna khairul lahunn, wallāhu samī\'un \'alīm'),
(2851, 24, 61, 'لَيْسَ عَلَى الْاَعْمٰى حَرَجٌ وَّلَا عَلَى الْاَعْرَجِ حَرَجٌ وَّلَا عَلَى الْمَرِيْضِ حَرَجٌ وَّلَا عَلٰٓى اَنْفُسِكُمْ اَنْ تَأْكُلُوْا مِنْۢ بُيُوْتِكُمْ اَوْ بُيُوْتِ اٰبَاۤىِٕكُمْ اَوْ بُيُوْتِ اُمَّهٰتِكُمْ اَوْ بُيُوْتِ اِخْوَانِكُمْ اَوْ بُيُوْتِ اَخَوٰتِكُمْ اَوْ بُيُوْتِ اَعْمَامِكُمْ اَوْ بُيُوْتِ عَمّٰتِكُمْ اَوْ بُيُوْتِ اَخْوَالِكُمْ اَوْ بُيُوْتِ خٰلٰتِكُمْ اَوْ مَا مَلَكْتُمْ مَّفَاتِحَهٗٓ اَوْ صَدِيْقِكُمْۗ لَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَأْكُلُوْا جَمِيْعًا اَوْ اَشْتَاتًاۗ فَاِذَا دَخَلْتُمْ بُيُوْتًا فَسَلِّمُوْا عَلٰٓى اَنْفُسِكُمْ تَحِيَّةً مِّنْ عِنْدِ اللّٰهِ مُبٰرَكَةً طَيِّبَةً ۗ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيٰتِ لَعَلَّكُمْ تَعْقِلُوْنَ', 'laisa \'alal-a\'mā ḥarajuw wa lā \'alal-a\'raji ḥarajuw wa lā \'alal-marīḍi ḥarajuw wa lā \'alā anfusikum an ta`kulụ mim buyụtikum au buyụti ābā`ikum au buyụti ummahātikum au buyụti ikhwānikum au buyụti akhawātikum au buyụti a\'māmikum au buyụti \'ammātikum au buyụti akhwālikum au buyụti khālātikum au mā malaktum mafātiḥahū au ṣadīqikum, laisa \'alaikum junāḥun an ta`kulụ jamī\'an au asytātā, fa iżā dakhaltum buyụtan fa sallimụ \'alā anfusikum taḥiyyatam min \'indillāhi mubārakatan ṭayyibah, każālika yubayyinullāhu lakumul-āyāti la\'allakum ta\'qilụn'),
(2852, 24, 62, 'اِنَّمَا الْمُؤْمِنُوْنَ الَّذِيْنَ اٰمَنُوْا بِاللّٰهِ وَرَسُوْلِهٖ وَاِذَا كَانُوْا مَعَهٗ عَلٰٓى اَمْرٍ جَامِعٍ لَّمْ يَذْهَبُوْا حَتّٰى يَسْتَأْذِنُوْهُۗ اِنَّ الَّذِيْنَ يَسْتَأْذِنُوْنَكَ اُولٰۤىِٕكَ الَّذِيْنَ يُؤْمِنُوْنَ بِاللّٰهِ وَرَسُوْلِهٖۚ فَاِذَا اسْتَأْذَنُوْكَ لِبَعْضِ شَأْنِهِمْ فَأْذَنْ لِّمَنْ شِئْتَ مِنْهُمْ وَاسْتَغْفِرْ لَهُمُ اللّٰهَ ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'innamal-mu`minụnallażīna āmanụ billāhi wa rasụlihī wa iżā kānụ ma\'ahụ \'alā amrin jāmi\'il lam yaż-habụ ḥattā yasta`żinụh, innallażīna yasta`żinụnaka ulā`ikallażīna yu`minụna billāhi wa rasụlih, fa iżasta`żanụka liba\'ḍi sya`nihim fa`żal liman syi`ta min-hum wastagfir lahumullāh, innallāha gafụrur raḥīm'),
(2853, 24, 63, 'لَا تَجْعَلُوْا دُعَاۤءَ الرَّسُوْلِ بَيْنَكُمْ كَدُعَاۤءِ بَعْضِكُمْ بَعْضًاۗ قَدْ يَعْلَمُ اللّٰهُ الَّذِيْنَ يَتَسَلَّلُوْنَ مِنْكُمْ لِوَاذًاۚ فَلْيَحْذَرِ الَّذِيْنَ يُخَالِفُوْنَ عَنْ اَمْرِهٖٓ اَنْ تُصِيْبَهُمْ فِتْنَةٌ اَوْ يُصِيْبَهُمْ عَذَابٌ اَلِيْمٌ', 'lā taj\'alụ du\'ā`ar-rasụli bainakum kadu\'ā`i ba\'ḍikum ba\'ḍā, qad ya\'lamullāhullażīna yatasallalụna mingkum liwāżā, falyaḥżarillażīna yukhālifụna \'an amrihī an tuṣībahum fitnatun au yuṣībahum \'ażābun alīm'),
(2854, 24, 64, 'اَلَآ اِنَّ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ قَدْ يَعْلَمُ مَآ اَنْتُمْ عَلَيْهِۗ وَيَوْمَ يُرْجَعُوْنَ اِلَيْهِ فَيُنَبِّئُهُمْ بِمَا عَمِلُوْاۗ وَاللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمٌ', 'alā inna lillāhi mā fis-samāwāti wal-arḍ, qad ya\'lamu mā antum \'alaīh, wa yauma yurja\'ụna ilaihi fa yunabbi`uhum bimā \'amilụ, wallāhu bikulli syai`in \'alīm'),
(2855, 25, 1, 'تَبٰرَكَ الَّذِيْ نَزَّلَ الْفُرْقَانَ عَلٰى عَبْدِهٖ لِيَكُوْنَ لِلْعٰلَمِيْنَ نَذِيْرًا ۙ', 'tabārakallażī nazzalal-furqāna \'alā \'abdihī liyakụna lil-\'ālamīna nażīrā'),
(2856, 25, 2, ' ۨالَّذِيْ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ وَلَمْ يَتَّخِذْ وَلَدًا وَّلَمْ يَكُنْ لَّهٗ شَرِيْكٌ فِى الْمُلْكِ وَخَلَقَ كُلَّ شَيْءٍ فَقَدَّرَهٗ تَقْدِيْرًا', 'allażī lahụ mulkus-samāwāti wal-arḍi wa lam yattakhiż waladaw wa lam yakul lahụ syarīkun fil-mulki wa khalaqa kulla syai`in fa qaddarahụ taqdīrā'),
(2857, 25, 3, 'وَاتَّخَذُوْا مِنْ دُوْنِهٖٓ اٰلِهَةً لَّا يَخْلُقُوْنَ شَيْـًٔا وَّهُمْ يُخْلَقُوْنَ وَلَا يَمْلِكُوْنَ لِاَنْفُسِهِمْ ضَرًّا وَّلَا نَفْعًا وَّلَا يَمْلِكُوْنَ مَوْتًا وَّلَا حَيٰوةً وَّلَا نُشُوْرًا', 'wattakhażụ min dụnihī ālihatal lā yakhluqụna syai`aw wa hum yukhlaqụna wa lā yamlikụna li`anfusihim ḍarraw wa lā naf\'aw wa lā yamlikụna mautaw wa lā ḥayātaw wa lā nusyụrā'),
(2858, 25, 4, 'وَقَالَ الَّذِيْنَ كَفَرُوْٓا اِنْ هٰذَآ اِلَّآ اِفْكُ ِۨافْتَرٰىهُ وَاَعَانَهٗ عَلَيْهِ قَوْمٌ اٰخَرُوْنَۚ فَقَدْ جَاۤءُوْ ظُلْمًا وَّزُوْرًا ۚ', 'wa qālallażīna kafarū in hāżā illā ifkuniftarāhu wa a\'ānahụ \'alaihi qaumun ākharụn, fa qad jā`ụ ẓulmaw wa zụrā'),
(2859, 25, 5, 'وَقَالُوْٓا اَسَاطِيْرُ الْاَوَّلِيْنَ اكْتَتَبَهَا فَهِيَ تُمْلٰى عَلَيْهِ بُكْرَةً وَّاَصِيْلًا', 'wa qālū asāṭīrul-awwalīnaktatabahā fa hiya tumlā \'alaihi bukrataw wa aṣīlā'),
(2860, 25, 6, 'قُلْ اَنْزَلَهُ الَّذِيْ يَعْلَمُ السِّرَّ فِى السَّمٰوٰتِ وَالْاَرْضِۗ اِنَّهٗ كَانَ غَفُوْرًا رَّحِيْمًا', 'qul anzalahullażī ya\'lamus-sirra fis-samāwāti wal-arḍ, innahụ kāna gafụrar-raḥīmā'),
(2861, 25, 7, 'وَقَالُوْا مَالِ هٰذَا الرَّسُوْلِ يَأْكُلُ الطَّعَامَ وَيَمْشِيْ فِى الْاَسْوَاقِۗ لَوْلَآ اُنْزِلَ اِلَيْهِ مَلَكٌ فَيَكُوْنَ مَعَهٗ نَذِيْرًا ۙ', 'wa qālụ mā lihāżar-rasụli ya`kuluṭ-ṭa\'āma wa yamsyī fil-aswāq, lau lā unzila ilaihi malakun fa yakụna ma\'ahụ nażīrā'),
(2862, 25, 8, 'اَوْ يُلْقٰىٓ اِلَيْهِ كَنْزٌ اَوْ تَكُوْنُ لَهٗ جَنَّةٌ يَّأْكُلُ مِنْهَاۗ وَقَالَ الظّٰلِمُوْنَ اِنْ تَتَّبِعُوْنَ اِلَّا رَجُلًا مَّسْحُوْرًا', 'au yulqā ilaihi kanzun au takụnu lahụ jannatuy ya`kulu min-hā, wa qālaẓ-ẓālimụna in tattabi\'ụna illā rajulam mas-ḥụrā'),
(2863, 25, 9, 'اُنْظُرْ كَيْفَ ضَرَبُوْا لَكَ الْاَمْثَالَ فَضَلُّوْا فَلَا يَسْتَطِيْعُوْنَ سَبِيْلًا', 'unẓur kaifa ḍarabụ lakal-amṡāla fa ḍallụ fa lā yastaṭī\'ụna sabīlā'),
(2864, 25, 10, 'تَبٰرَكَ الَّذِيْٓ اِنْ شَاۤءَ جَعَلَ لَكَ خَيْرًا مِّنْ ذٰلِكَ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۙ وَيَجْعَلْ لَّكَ قُصُوْرًا', 'tabārakallażī in syā`a ja\'ala laka khairam min żālika jannātin tajrī min taḥtihal-an-hāru wa yaj\'al laka quṣụrā'),
(2865, 25, 11, 'بَلْ كَذَّبُوْا بِالسَّاعَةِۙ وَاَعْتَدْنَا لِمَنْ كَذَّبَ بِالسَّاعَةِ سَعِيْرًا', 'bal każżabụ bis-sā\'ati wa a\'tadnā limang każżaba bis-sā\'ati sa\'īrā'),
(2866, 25, 12, 'اِذَا رَاَتْهُمْ مِّنْ مَّكَانٍۢ بَعِيْدٍ سَمِعُوْا لَهَا تَغَيُّظًا وَّزَفِيْرًا', 'iżā ra`at-hum mim makānim ba\'īdin sami\'ụ lahā tagayyuẓaw wa zafīrā'),
(2867, 25, 13, 'وَاِذَآ اُلْقُوْا مِنْهَا مَكَانًا ضَيِّقًا مُّقَرَّنِيْنَ دَعَوْا هُنَالِكَ ثُبُوْرًا ۗ', 'wa iżā ulqụ min-hā makānan ḍayyiqam muqarranīna da\'au hunālika ṡubụrā'),
(2868, 25, 14, 'لَا تَدْعُوا الْيَوْمَ ثُبُوْرًا وَّاحِدًا وَّادْعُوْا ثُبُوْرًا كَثِيْرًا', 'lā tad\'ul-yauma ṡubụraw wāḥidaw wad\'ụ ṡubụrang kaṡīrā'),
(2869, 25, 15, 'قُلْ اَذٰلِكَ خَيْرٌ اَمْ جَنَّةُ الْخُلْدِ الَّتِيْ وُعِدَ الْمُتَّقُوْنَۗ كَانَتْ لَهُمْ جَزَاۤءً وَّمَصِيْرًا', 'qul a żālika khairun am jannatul-khuldillatī wu\'idal-muttaqụn, kānat lahum jazā`aw wa maṣīrā'),
(2870, 25, 16, 'لَهُمْ فِيْهَا مَا يَشَاۤءُوْنَ خٰلِدِيْنَۗ كَانَ عَلٰى رَبِّكَ وَعْدًا مَّسْـُٔوْلًا', 'lahum fīhā mā yasyā`ụna khālidīn, kāna \'alā rabbika wa\'dam mas`ụlā'),
(2871, 25, 17, 'وَيَوْمَ يَحْشُرُهُمْ وَمَا يَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ فَيَقُوْلُ ءَاَنْتُمْ اَضْلَلْتُمْ عِبَادِيْ هٰٓؤُلَاۤءِ اَمْ هُمْ ضَلُّوا السَّبِيْلَ ۗ', 'wa yauma yaḥsyuruhum wa mā ya\'budụna min dụnillāhi fa yaqụlu a antum aḍlaltum \'ibādī hā`ulā`i am hum ḍallus-sabīl'),
(2872, 25, 18, 'قَالُوْا سُبْحٰنَكَ مَا كَانَ يَنْۢبَغِيْ لَنَآ اَنْ نَّتَّخِذَ مِنْ دُوْنِكَ مِنْ اَوْلِيَاۤءَ وَلٰكِنْ مَّتَّعْتَهُمْ وَاٰبَاۤءَهُمْ حَتّٰى نَسُوا الذِّكْرَۚ وَكَانُوْا قَوْمًاۢ بُوْرًا', 'qālụ sub-ḥānaka mā kāna yambagī lanā an nattakhiża min dụnika min auliyā`a wa lākim matta\'tahum wa ābā`ahum ḥattā nasuż-żikr, wa kānụ qaumam bụrā'),
(2873, 25, 19, 'فَقَدْ كَذَّبُوْكُمْ بِمَا تَقُوْلُوْنَۙ فَمَا تَسْتَطِيْعُوْنَ صَرْفًا وَّلَا نَصْرًاۚ وَمَنْ يَّظْلِمْ مِّنْكُمْ نُذِقْهُ عَذَابًا كَبِيْرًا', 'fa qad każżabụkum bimā taqụlụna fa mā tastaṭī\'ụna ṣarfaw wa lā naṣrā, wa may yaẓlim mingkum nużiq-hu \'ażābang kabīrā'),
(2874, 25, 20, 'وَمَآ اَرْسَلْنَا قَبْلَكَ مِنَ الْمُرْسَلِيْنَ اِلَّآ اِنَّهُمْ لَيَأْكُلُوْنَ الطَّعَامَ وَيَمْشُوْنَ فِى الْاَسْوَاقِۗ وَجَعَلْنَا بَعْضَكُمْ لِبَعْضٍ فِتْنَةً ۗ اَتَصْبِرُوْنَۚ وَكَانَ رَبُّكَ بَصِيْرًا ۔', 'wa mā arsalnā qablaka minal-mursalīna illā innahum laya`kulụnaṭ-ṭa\'āma wa yamsyụna fil-aswāq, wa ja\'alnā ba\'ḍakum liba\'ḍin fitnah, a taṣbirụn, wa kāna rabbuka baṣīrā'),
(2875, 25, 21, '۞ وَقَالَ الَّذِيْنَ لَا يَرْجُوْنَ لِقَاۤءَنَا لَوْلَآ اُنْزِلَ عَلَيْنَا الْمَلٰۤىِٕكَةُ اَوْ نَرٰى رَبَّنَا ۗ لَقَدِ اسْتَكْبَرُوْا فِيْٓ اَنْفُسِهِمْ وَعَتَوْ عُتُوًّا كَبِيْرًا', 'wa qālallażīna lā yarjụna liqā`anā lau lā unzila \'alainal-malā`ikatu au narā rabbanā, laqadistakbarụ fī anfusihim wa \'atau \'utuwwang kabīrā'),
(2876, 25, 22, 'يَوْمَ يَرَوْنَ الْمَلٰۤىِٕكَةَ لَا بُشْرٰى يَوْمَىِٕذٍ لِّلْمُجْرِمِيْنَ وَيَقُوْلُوْنَ حِجْرًا مَّحْجُوْرًا', 'yauma yaraunal-malā`ikata lā busyrā yauma`iżil lil-mujrimīna wa yaqụlụna ḥijram maḥjụrā'),
(2877, 25, 23, 'وَقَدِمْنَآ اِلٰى مَا عَمِلُوْا مِنْ عَمَلٍ فَجَعَلْنٰهُ هَبَاۤءً مَّنْثُوْرًا', 'wa qadimnā ilā mā \'amilụ min \'amalin fa ja\'alnāhu habā`am manṡụrā'),
(2878, 25, 24, 'اَصْحٰبُ الْجَنَّةِ يَوْمَىِٕذٍ خَيْرٌ مُّسْتَقَرًّا وَّاَحْسَنُ مَقِيْلًا', 'aṣ-ḥābul-jannati yauma`iżin khairum mustaqarraw wa aḥsanu maqīlā'),
(2879, 25, 25, 'وَيَوْمَ تَشَقَّقُ السَّمَاۤءُ بِالْغَمَامِ وَنُزِّلَ الْمَلٰۤىِٕكَةُ تَنْزِيْلًا', 'wa yauma tasyaqqaqus-samā`u bil-gamāmi wa nuzzilal-malā`ikatu tanzīlā'),
(2880, 25, 26, 'اَلْمُلْكُ يَوْمَىِٕذِ ِۨالْحَقُّ لِلرَّحْمٰنِۗ وَكَانَ يَوْمًا عَلَى الْكٰفِرِيْنَ عَسِيْرًا', 'al-mulku yauma`iżinil-ḥaqqu lir-raḥmān, wa kāna yauman \'alal-kāfirīna \'asīrā'),
(2881, 25, 27, 'وَيَوْمَ يَعَضُّ الظَّالِمُ عَلٰى يَدَيْهِ يَقُوْلُ يٰلَيْتَنِى اتَّخَذْتُ مَعَ الرَّسُوْلِ سَبِيْلًا', 'wa yauma ya\'aḍḍuẓ-ẓālimu \'alā yadaihi yaqụlu yā laitanittakhażtu ma\'ar-rasụli sabīlā'),
(2882, 25, 28, 'يٰوَيْلَتٰى لَيْتَنِيْ لَمْ اَتَّخِذْ فُلَانًا خَلِيْلًا', 'yā wailatā laitanī lam attakhiż fulānan khalīlā'),
(2883, 25, 29, 'لَقَدْ اَضَلَّنِيْ عَنِ الذِّكْرِ بَعْدَ اِذْ جَاۤءَنِيْۗ وَكَانَ الشَّيْطٰنُ لِلْاِنْسَانِ خَذُوْلًا', 'laqad aḍallanī \'aniż-żikri ba\'da iż jā`anī, wa kānasy-syaiṭānu lil-insāni khażụlā'),
(2884, 25, 30, 'وَقَالَ الرَّسُوْلُ يٰرَبِّ اِنَّ قَوْمِى اتَّخَذُوْا هٰذَا الْقُرْاٰنَ مَهْجُوْرًا', 'wa qālar-rasụlu yā rabbi inna qaumittakhażụ hāżal-qur`āna mahjụrā'),
(2885, 25, 31, 'وَكَذٰلِكَ جَعَلْنَا لِكُلِّ نَبِيٍّ عَدُوًّا مِّنَ الْمُجْرِمِيْنَۗ وَكَفٰى بِرَبِّكَ هَادِيًا وَّنَصِيْرًا', 'wa każālika ja\'alnā likulli nabiyyin \'aduwwam minal-mujrimīn, wa kafā birabbika hādiyaw wa naṣīrā'),
(2886, 25, 32, 'وَقَالَ الَّذِيْنَ كَفَرُوْا لَوْلَا نُزِّلَ عَلَيْهِ الْقُرْاٰنُ جُمْلَةً وَّاحِدَةً ۛ كَذٰلِكَ ۛ لِنُثَبِّتَ بِهٖ فُؤَادَكَ وَرَتَّلْنٰهُ تَرْتِيْلًا', 'wa qālallażīna kafarụ lau lā nuzzila \'alaihil-qur`ānu jumlataw wāḥidah, każālika linuṡabbita bihī fu`ādaka wa rattalnāhu tartīlā'),
(2887, 25, 33, 'وَلَا يَأْتُوْنَكَ بِمَثَلٍ اِلَّا جِئْنٰكَ بِالْحَقِّ وَاَحْسَنَ تَفْسِيْرًا ۗ', 'wa lā ya`tụnaka bimaṡalin illā ji`nāka bil-ḥaqqi wa aḥsana tafsīrā'),
(2888, 25, 34, 'اَلَّذِيْنَ يُحْشَرُوْنَ عَلٰى وُجُوْهِهِمْ اِلٰى جَهَنَّمَۙ اُولٰۤىِٕكَ شَرٌّ مَّكَانًا وَّاَضَلُّ سَبِيْلًا', 'allażīna yuḥsyarụna \'alā wujụhihim ilā jahannama ulā`ika syarrum makānaw wa aḍallu sabīlā'),
(2889, 25, 35, 'وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ وَجَعَلْنَا مَعَهٗٓ اَخَاهُ هٰرُوْنَ وَزِيْرًا ۚ', 'wa laqad ātainā mụsal-kitāba wa ja\'alnā ma\'ahū akhāhu hārụna wazīrā'),
(2890, 25, 36, 'فَقُلْنَا اذْهَبَآ اِلَى الْقَوْمِ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَاۗ فَدَمَّرْنٰهُمْ تَدْمِيْرًا ۗ', 'fa qulnaż-habā ilal-qaumillażīna każżabụ bi`āyātinā, fa dammarnāhum tadmīrā'),
(2891, 25, 37, 'وَقَوْمَ نُوْحٍ لَّمَّا كَذَّبُوا الرُّسُلَ اَغْرَقْنٰهُمْ وَجَعَلْنٰهُمْ لِلنَّاسِ اٰيَةًۗ وَاَعْتَدْنَا لِلظّٰلِمِيْنَ عَذَابًا اَلِيْمًا ۚ', 'wa qauma nụḥil lammā każżabur-rusula agraqnāhum wa ja\'alnāhum lin-nāsi āyah, wa a\'tadnā liẓ-ẓālimīna \'ażāban alīmā'),
(2892, 25, 38, 'وَعَادًا وَّثَمُوْدَا۟ وَاَصْحٰبَ الرَّسِّ وَقُرُوْنًاۢ بَيْنَ ذٰلِكَ كَثِيْرًا', 'wa \'ādaw wa ṡamụda wa aṣ-ḥābar-rassi wa qurụnam baina żālika kaṡīrā'),
(2893, 25, 39, 'وَكُلًّا ضَرَبْنَا لَهُ الْاَمْثَالَۖ وَكُلًّا تَبَّرْنَا تَتْبِيْرًا', 'wa kullan ḍarabnā lahul-amṡāla wa kullan tabbarnā tatbīrā'),
(2894, 25, 40, 'وَلَقَدْ اَتَوْا عَلَى الْقَرْيَةِ الَّتِيْٓ اُمْطِرَتْ مَطَرَ السَّوْءِۗ اَفَلَمْ يَكُوْنُوْا يَرَوْنَهَاۚ بَلْ كَانُوْا لَا يَرْجُوْنَ نُشُوْرًا', 'wa laqad atau \'alal-qaryatillatī umṭirat maṭaras-saụ`, a fa lam yakụnụ yaraunahā, bal kānụ lā yarjụna nusyụrā'),
(2895, 25, 41, 'وَاِذَا رَاَوْكَ اِنْ يَّتَّخِذُوْنَكَ اِلَّا هُزُوًاۗ اَهٰذَا الَّذِيْ بَعَثَ اللّٰهُ رَسُوْلًا', 'wa iżā ra`auka iy yattakhiżụnaka illā huzuwā, a hāżallażī ba\'aṡallāhu rasụlā'),
(2896, 25, 42, 'اِنْ كَادَ لَيُضِلُّنَا عَنْ اٰلِهَتِنَا لَوْلَآ اَنْ صَبَرْنَا عَلَيْهَاۗ وَسَوْفَ يَعْلَمُوْنَ حِيْنَ يَرَوْنَ الْعَذَابَ مَنْ اَضَلُّ سَبِيْلًا', 'ing kāda layuḍillunā \'an ālihatinā lau lā an ṣabarnā \'alaihā, wa saufa ya\'lamụna ḥīna yaraunal-\'ażāba man aḍallu sabīlā'),
(2897, 25, 43, 'اَرَءَيْتَ مَنِ اتَّخَذَ اِلٰهَهٗ هَوٰىهُۗ اَفَاَنْتَ تَكُوْنُ عَلَيْهِ وَكِيْلًا ۙ', 'a ra`aita manittakhaża ilāhahụ hawāh, a fa anta takụnu \'alaihi wakīlā'),
(2898, 25, 44, 'اَمْ تَحْسَبُ اَنَّ اَكْثَرَهُمْ يَسْمَعُوْنَ اَوْ يَعْقِلُوْنَۗ اِنْ هُمْ اِلَّا كَالْاَنْعَامِ بَلْ هُمْ اَضَلُّ سَبِيْلًا', 'am taḥsabu anna akṡarahum yasma\'ụna au ya\'qilụn, in hum illā kal-an\'āmi bal hum aḍallu sabīlā');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(2899, 25, 45, 'اَلَمْ تَرَ اِلٰى رَبِّكَ كَيْفَ مَدَّ الظِّلَّۚ وَلَوْ شَاۤءَ لَجَعَلَهٗ سَاكِنًاۚ ثُمَّ جَعَلْنَا الشَّمْسَ عَلَيْهِ دَلِيْلًا ۙ', 'a lam tara ilā rabbika kaifa maddaẓ-ẓill, walau syā`a laja\'alahụ sākinā, ṡumma ja\'alnasy-syamsa \'alaihi dalīlā'),
(2900, 25, 46, 'ثُمَّ قَبَضْنٰهُ اِلَيْنَا قَبْضًا يَّسِيْرًا', 'ṡumma qabaḍnāhu ilainā qabḍay yasīrā'),
(2901, 25, 47, 'وَهُوَ الَّذِيْ جَعَلَ لَكُمُ الَّيْلَ لِبَاسًا وَّالنَّوْمَ سُبَاتًا وَّجَعَلَ النَّهَارَ نُشُوْرًا', 'wa huwallażī ja\'ala lakumul-laila libāsaw wan-nauma subātaw wa ja\'alan-nahāra nusyụrā'),
(2902, 25, 48, 'وَهُوَ الَّذِيْٓ اَرْسَلَ الرِّيٰحَ بُشْرًاۢ بَيْنَ يَدَيْ رَحْمَتِهٖۚ وَاَنْزَلْنَا مِنَ السَّمَاۤءِ مَاۤءً طَهُوْرًا ۙ', 'wa huwallażī arsalar-riyāḥa busyram baina yadai raḥmatih, wa anzalnā minas-samā`i mā`an ṭahụrā'),
(2903, 25, 49, 'لِّنُحْيِ َۧ بِهٖ بَلْدَةً مَّيْتًا وَّنُسْقِيَهٗ مِمَّا خَلَقْنَآ اَنْعَامًا وَّاَنَاسِيَّ كَثِيْرًا', 'linuḥyiya bihī baldatam maitaw wa nusqiyahụ mimmā khalaqnā an\'āmaw wa anāsiyya kaṡīrā'),
(2904, 25, 50, 'وَلَقَدْ صَرَّفْنٰهُ بَيْنَهُمْ لِيَذَّكَّرُوْاۖ فَاَبٰىٓ اَكْثَرُ النَّاسِ اِلَّا كُفُوْرًا', 'wa laqad ṣarrafnāhu bainahum liyażżakkarụ fa abā akṡarun-nāsi illā kufụrā'),
(2905, 25, 51, 'وَلَوْ شِئْنَا لَبَعَثْنَا فِيْ كُلِّ قَرْيَةٍ نَّذِيْرًا ۖ', 'walau syi`nā laba\'aṡnā fī kulli qaryatin nażīrā'),
(2906, 25, 52, 'فَلَا تُطِعِ الْكٰفِرِيْنَ وَجَاهِدْهُمْ بِهٖ جِهَادًا كَبِيْرًا', 'fa lā tuṭi\'il kāfirīna wa jāhid-hum bihī jihādang kabīrā'),
(2907, 25, 53, '۞ وَهُوَ الَّذِيْ مَرَجَ الْبَحْرَيْنِ هٰذَا عَذْبٌ فُرَاتٌ وَّهٰذَا مِلْحٌ اُجَاجٌۚ وَجَعَلَ بَيْنَهُمَا بَرْزَخًا وَّحِجْرًا مَّحْجُوْرًا', 'wa huwallażī marajal-baḥraini hāżā \'ażbun furātuw wa hāżā mil-ḥun ujāj, wa ja\'ala bainahumā barzakhaw wa ḥijram maḥjụrā'),
(2908, 25, 54, 'وَهُوَ الَّذِيْ خَلَقَ مِنَ الْمَاۤءِ بَشَرًا فَجَعَلَهٗ نَسَبًا وَّصِهْرًاۗ وَكَانَ رَبُّكَ قَدِيْرًا', 'wa huwallażī khalaqa minal-mā`i basyaran fa ja\'alahụ nasabaw wa ṣihrā, wa kāna rabbuka qadīrā'),
(2909, 25, 55, 'وَيَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَا يَنْفَعُهُمْ وَلَا يَضُرُّهُمْۗ وَكَانَ الْكَافِرُ عَلٰى رَبِّهٖ ظَهِيْرًا', 'wa ya\'budụna min dụnillāhi mā lā yanfa\'uhum wa lā yaḍurruhum, wa kānal-kāfiru \'alā rabbihī ẓahīrā'),
(2910, 25, 56, 'وَمَآ اَرْسَلْنٰكَ اِلَّا مُبَشِّرًا وَّنَذِيْرًا', 'wa mā arsalnāka illā mubasysyiraw wa nażīrā'),
(2911, 25, 57, 'قُلْ مَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍ اِلَّا مَنْ شَاۤءَ اَنْ يَّتَّخِذَ اِلٰى رَبِّهٖ سَبِيْلًا', 'qul mā as`alukum \'alaihi min ajrin illā man syā`a ay yattakhiża ilā rabbihī sabīlā'),
(2912, 25, 58, 'وَتَوَكَّلْ عَلَى الْحَيِّ الَّذِيْ لَا يَمُوْتُ وَسَبِّحْ بِحَمْدِهٖۗ وَكَفٰى بِهٖ بِذُنُوْبِ عِبَادِهٖ خَبِيْرًا ۚ', 'wa tawakkal \'alal-ḥayyillażī lā yamụtu wa sabbiḥ biḥamdih, wa kafā bihī biżunụbi \'ibādihī khabīrā'),
(2913, 25, 59, 'اَلَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَا فِيْ سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِۚ اَلرَّحْمٰنُ فَسْـَٔلْ بِهٖ خَبِيْرًا', 'allażī khalaqas-samāwāti wal-arḍa wa mā bainahumā fī sittati ayyāmin ṡummastawā \'alal-\'arsy, ar-raḥmānu fas`al bihī khabīrā'),
(2914, 25, 60, 'وَاِذَا قِيْلَ لَهُمُ اسْجُدُوْا لِلرَّحْمٰنِ قَالُوْا وَمَا الرَّحْمٰنُ اَنَسْجُدُ لِمَا تَأْمُرُنَا وَزَادَهُمْ نُفُوْرًا ۩', 'wa iżā qīla lahumusjudụ lir-raḥmāni qālụ wa mar-raḥmānu a nasjudu limā ta`murunā wa zādahum nufụrā'),
(2915, 25, 61, 'تَبٰرَكَ الَّذِيْ جَعَلَ فِى السَّمَاۤءِ بُرُوْجًا وَّجَعَلَ فِيْهَا سِرَاجًا وَّقَمَرًا مُّنِيْرًا', 'tabārakallażī ja\'ala fis-samā`i burụjaw wa ja\'ala fīhā sirājaw wa qamaram munīrā'),
(2916, 25, 62, 'وَهُوَ الَّذِيْ جَعَلَ الَّيْلَ وَالنَّهَارَ خِلْفَةً لِّمَنْ اَرَادَ اَنْ يَّذَّكَّرَ اَوْ اَرَادَ شُكُوْرًا', 'wa huwallażī ja\'alal-laila wan-nahāra khilfatal liman arāda ay yażżakkara au arāda syukụrā'),
(2917, 25, 63, 'وَعِبَادُ الرَّحْمٰنِ الَّذِيْنَ يَمْشُوْنَ عَلَى الْاَرْضِ هَوْنًا وَّاِذَا خَاطَبَهُمُ الْجٰهِلُوْنَ قَالُوْا سَلٰمًا', 'wa \'ibādur-raḥmānillażīna yamsyụna \'alal-arḍi haunaw wa iżā khāṭabahumul-jāhilụna qālụ salāmā'),
(2918, 25, 64, 'وَالَّذِيْنَ يَبِيْتُوْنَ لِرَبِّهِمْ سُجَّدًا وَّقِيَامًا', 'wallażīna yabītụna lirabbihim sujjadaw wa qiyāmā'),
(2919, 25, 65, 'وَالَّذِيْنَ يَقُوْلُوْنَ رَبَّنَا اصْرِفْ عَنَّا عَذَابَ جَهَنَّمَۖ اِنَّ عَذَابَهَا كَانَ غَرَامًا ۖ', 'wallażīna yaqụlụna rabbanaṣrif \'annā \'ażāba jahannama inna \'ażābahā kāna garāmā'),
(2920, 25, 66, 'اِنَّهَا سَاۤءَتْ مُسْتَقَرًّا وَّمُقَامًا', 'innahā sā`at mustaqarraw wa muqāmā'),
(2921, 25, 67, 'وَالَّذِيْنَ اِذَآ اَنْفَقُوْا لَمْ يُسْرِفُوْا وَلَمْ يَقْتُرُوْا وَكَانَ بَيْنَ ذٰلِكَ قَوَامًا', 'wallażīna iżā anfaqụ lam yusrifụ wa lam yaqturụ wa kāna baina żālika qawāmā'),
(2922, 25, 68, 'وَالَّذِيْنَ لَا يَدْعُوْنَ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ وَلَا يَقْتُلُوْنَ النَّفْسَ الَّتِيْ حَرَّمَ اللّٰهُ اِلَّا بِالْحَقِّ وَلَا يَزْنُوْنَۚ وَمَنْ يَّفْعَلْ ذٰلِكَ يَلْقَ اَثَامًا ۙ', 'wallażīna lā yad\'ụna ma\'allāhi ilāhan ākhara wa lā yaqtulụnan-nafsallatī ḥarramallāhu illā bil-ḥaqqi wa lā yaznụn, wa may yaf\'al żālika yalqa aṡāmā'),
(2923, 25, 69, 'يُّضٰعَفْ لَهُ الْعَذَابُ يَوْمَ الْقِيٰمَةِ وَيَخْلُدْ فِيْهٖ مُهَانًا ۙ', 'yuḍā\'af lahul-\'ażābu yaumal-qiyāmati wa yakhlud fīhī muhānā'),
(2924, 25, 70, 'اِلَّا مَنْ تَابَ وَاٰمَنَ وَعَمِلَ عَمَلًا صَالِحًا فَاُولٰۤىِٕكَ يُبَدِّلُ اللّٰهُ سَيِّاٰتِهِمْ حَسَنٰتٍۗ وَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا', 'illā man tāba wa āmana wa \'amila \'amalan ṣāliḥan fa ulā`ika yubaddilullāhu sayyi`ātihim ḥasanāt, wa kānallāhu gafụrar raḥīmā'),
(2925, 25, 71, 'وَمَنْ تَابَ وَعَمِلَ صَالِحًا فَاِنَّهٗ يَتُوْبُ اِلَى اللّٰهِ مَتَابًا', 'wa man tāba wa \'amila ṣāliḥan fa innahụ yatụbu ilallāhi matābā'),
(2926, 25, 72, 'وَالَّذِيْنَ لَا يَشْهَدُوْنَ الزُّوْرَۙ وَاِذَا مَرُّوْا بِاللَّغْوِ مَرُّوْا كِرَامًا', 'wallażīna lā yasy-hadụnaz-zụra wa iżā marrụ bil-lagwi marrụ kirāmā'),
(2927, 25, 73, 'وَالَّذِيْنَ اِذَا ذُكِّرُوْا بِاٰيٰتِ رَبِّهِمْ لَمْ يَخِرُّوْا عَلَيْهَا صُمًّا وَّعُمْيَانًا', 'wallażīna iżā żukkirụ bi`āyāti rabbihim lam yakhirrụ \'alaihā ṣummaw wa \'umyānā'),
(2928, 25, 74, 'وَالَّذِيْنَ يَقُوْلُوْنَ رَبَّنَا هَبْ لَنَا مِنْ اَزْوَاجِنَا وَذُرِّيّٰتِنَا قُرَّةَ اَعْيُنٍ وَّاجْعَلْنَا لِلْمُتَّقِيْنَ اِمَامًا', 'wallażīna yaqụlụna rabbanā hab lanā min azwājinā wa żurriyyātinā qurrata a\'yuniw waj\'alnā lil-muttaqīna imāmā'),
(2929, 25, 75, 'اُولٰۤىِٕكَ يُجْزَوْنَ الْغُرْفَةَ بِمَا صَبَرُوْا وَيُلَقَّوْنَ فِيْهَا تَحِيَّةً وَّسَلٰمًا ۙ', 'ulā`ika yujzaunal-gurfata bimā ṣabarụ wa yulaqqauna fīhā taḥiyyataw wa salāmā'),
(2930, 25, 76, 'خٰلِدِيْنَ فِيْهَاۗ حَسُنَتْ مُسْتَقَرًّا وَّمُقَامًا', 'khālidīna fīhā, ḥasunat mustaqarraw wa muqāmā'),
(2931, 25, 77, 'قُلْ مَا يَعْبَؤُا بِكُمْ رَبِّيْ لَوْلَا دُعَاۤؤُكُمْۚ فَقَدْ كَذَّبْتُمْ فَسَوْفَ يَكُوْنُ لِزَامًا', 'qul mā ya\'ba`u bikum rabbī lau lā du\'ā`ukum, fa qad każżabtum fa saufa yakụnu lizāmā'),
(2932, 26, 1, 'طٰسۤمّۤ', 'ṭā sīm mīm'),
(2933, 26, 2, 'تِلْكَ اٰيٰتُ الْكِتٰبِ الْمُبِيْنِ', 'tilka āyātul-kitābil-mubīn'),
(2934, 26, 3, 'لَعَلَّكَ بَاخِعٌ نَّفْسَكَ اَلَّا يَكُوْنُوْا مُؤْمِنِيْنَ', 'la\'allaka bākhi\'un nafsaka allā yakụnụ mu`minīn'),
(2935, 26, 4, 'اِنْ نَّشَأْ نُنَزِّلْ عَلَيْهِمْ مِّنَ السَّمَاۤءِ اٰيَةً فَظَلَّتْ اَعْنَاقُهُمْ لَهَا خَاضِعِيْنَ', 'in nasya` nunazzil \'alaihim minas-samā`i āyatan fa ẓallat a\'nāquhum lahā khāḍi\'īn'),
(2936, 26, 5, 'وَمَا يَأْتِيْهِمْ مِّنْ ذِكْرٍ مِّنَ الرَّحْمٰنِ مُحْدَثٍ اِلَّا كَانُوْا عَنْهُ مُعْرِضِيْنَ', 'wa mā ya`tīhim min żikrim minar-raḥmāni muḥdaṡin illā kānụ \'an-hu mu\'riḍīn'),
(2937, 26, 6, 'فَقَدْ كَذَّبُوْا فَسَيَأْتِيْهِمْ اَنْۢبـٰۤؤُا مَا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ', 'fa qad każżabụ fa saya`tīhim ambā`u mā kānụ bihī yastahzi`ụn'),
(2938, 26, 7, 'اَوَلَمْ يَرَوْا اِلَى الْاَرْضِ كَمْ اَنْۢبَتْنَا فِيْهَا مِنْ كُلِّ زَوْجٍ كَرِيْمٍ', 'a wa lam yarau ilal-arḍi kam ambatnā fīhā ming kulli zaujing karīm'),
(2939, 26, 8, 'اِنَّ فِيْ ذٰلِكَ لَاٰيَةًۗ وَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ', 'inna fī żālika la`āyah, wa mā kāna akṡaruhum mu`minīn'),
(2940, 26, 9, 'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ', 'wa inna rabbaka lahuwal-\'azīzur-raḥīm'),
(2941, 26, 10, 'وَاِذْ نَادٰى رَبُّكَ مُوْسٰٓى اَنِ ائْتِ الْقَوْمَ الظّٰلِمِيْنَ ۙ', 'wa iż nādā rabbuka mụsā ani`til-qaumaẓ-ẓālimīn'),
(2942, 26, 11, 'قَوْمَ فِرْعَوْنَ ۗ اَلَا يَتَّقُوْنَ', 'qauma fir\'aụn, alā yattaqụn'),
(2943, 26, 12, 'قَالَ رَبِّ اِنِّيْٓ اَخَافُ اَنْ يُّكَذِّبُوْنِ ۗ', 'qāla rabbi innī akhāfu ay yukażżibụn'),
(2944, 26, 13, 'وَيَضِيْقُ صَدْرِيْ وَلَا يَنْطَلِقُ لِسَانِيْ فَاَرْسِلْ اِلٰى هٰرُوْنَ', 'wa yaḍīqu ṣadrī wa lā yanṭaliqu lisānī fa arsil ilā hārụn'),
(2945, 26, 14, 'وَلَهُمْ عَلَيَّ ذَنْۢبٌ فَاَخَافُ اَنْ يَّقْتُلُوْنِ ۚ', 'wa lahum \'alayya żambun fa akhāfu ay yaqtulụn'),
(2946, 26, 15, 'قَالَ كَلَّاۚ فَاذْهَبَا بِاٰيٰتِنَآ اِنَّا مَعَكُمْ مُّسْتَمِعُوْنَ ۙ', 'qāla kallā, faż-habā bi`āyātinā innā ma\'akum mustami\'ụn'),
(2947, 26, 16, 'فَأْتِيَا فِرْعَوْنَ فَقُوْلَآ اِنَّا رَسُوْلُ رَبِّ الْعٰلَمِيْنَ ۙ', 'fa`tiyā fir\'auna fa qụlā innā rasụlu rabbil-\'ālamīn'),
(2948, 26, 17, 'اَنْ اَرْسِلْ مَعَنَا بَنِيْٓ اِسْرَاۤءِيْلَ ۗ', 'an arsil ma\'anā banī isrā`īl'),
(2949, 26, 18, 'قَالَ اَلَمْ نُرَبِّكَ فِيْنَا وَلِيْدًا وَّلَبِثْتَ فِيْنَا مِنْ عُمُرِكَ سِنِيْنَ ۗ', 'qāla a lam nurabbika fīnā walīdaw wa labiṡta fīnā min \'umurika sinīn'),
(2950, 26, 19, 'وَفَعَلْتَ فَعْلَتَكَ الَّتِيْ فَعَلْتَ وَاَنْتَ مِنَ الْكٰفِرِيْنَ', 'wa fa\'alta fa\'latakallatī fa\'alta wa anta minal-kāfirīn'),
(2951, 26, 20, 'قَالَ فَعَلْتُهَآ اِذًا وَّاَنَا۠ مِنَ الضَّاۤلِّيْنَ', 'qāla fa\'altuhā iżaw wa ana minaḍ-ḍāllīn'),
(2952, 26, 21, 'فَفَرَرْتُ مِنْكُمْ لَمَّا خِفْتُكُمْ فَوَهَبَ لِيْ رَبِّيْ حُكْمًا وَّجَعَلَنِيْ مِنَ الْمُرْسَلِيْنَ', 'fa farartu mingkum lammā khiftukum fa wahaba lī rabbī ḥukmaw wa ja\'alanī minal-mursalīn'),
(2953, 26, 22, 'وَتِلْكَ نِعْمَةٌ تَمُنُّهَا عَلَيَّ اَنْ عَبَّدْتَّ بَنِيْٓ اِسْرَاۤءِيْلَ ۗ', 'wa tilka ni\'matun tamunnuhā \'alayya an \'abbatta banī isrā`īl'),
(2954, 26, 23, 'قَالَ فِرْعَوْنُ وَمَا رَبُّ الْعٰلَمِيْنَ ۗ', 'qāla fir\'aunu wa mā rabbul-\'ālamīn'),
(2955, 26, 24, 'قَالَ رَبُّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَاۗ اِنْ كُنْتُمْ مُّوْقِنِيْنَ', 'qāla rabbus-samāwāti wal-arḍi wa mā bainahumā, ing kuntum mụqinīn'),
(2956, 26, 25, 'قَالَ لِمَنْ حَوْلَهٗٓ اَلَا تَسْتَمِعُوْنَ', 'qāla liman ḥaulahū alā tastami\'ụn'),
(2957, 26, 26, 'قَالَ رَبُّكُمْ وَرَبُّ اٰبَاۤىِٕكُمُ الْاَوَّلِيْنَ', 'qāla rabbukum wa rabbu ābā`ikumul-awwalīn'),
(2958, 26, 27, 'قَالَ اِنَّ رَسُوْلَكُمُ الَّذِيْٓ اُرْسِلَ اِلَيْكُمْ لَمَجْنُوْنٌ', 'qāla inna rasụlakumullażī ursila ilaikum lamajnụn'),
(2959, 26, 28, 'قَالَ رَبُّ الْمَشْرِقِ وَالْمَغْرِبِ وَمَا بَيْنَهُمَاۗ اِنْ كُنْتُمْ تَعْقِلُوْنَ', 'qāla rabbul-masyriqi wal-magribi wa mā bainahumā, ing kuntum ta\'qilụn'),
(2960, 26, 29, 'قَالَ لَىِٕنِ اتَّخَذْتَ اِلٰهًا غَيْرِيْ لَاَجْعَلَنَّكَ مِنَ الْمَسْجُوْنِيْنَ', 'qāla la`inittakhażta ilāhan gairī la`aj\'alannaka minal-masjụnīn'),
(2961, 26, 30, 'قَالَ اَوَلَوْ جِئْتُكَ بِشَيْءٍ مُّبِيْنٍ', 'qāla a walau ji`tuka bisyai`im mubīn'),
(2962, 26, 31, 'قَالَ فَأْتِ بِهٖٓ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ', 'qāla fa`ti bihī ing kunta minaṣ-ṣādiqīn'),
(2963, 26, 32, 'فَاَلْقٰى عَصَاهُ فَاِذَا هِيَ ثُعْبَانٌ مُّبِيْنٌ ۚ', 'fa alqā \'aṣāhu fa iżā hiya ṡu\'bānum mubīn'),
(2964, 26, 33, 'وَنَزَعَ يَدَهٗ فَاِذَا هِيَ بَيْضَاۤءُ لِلنّٰظِرِيْنَ', 'wa naza\'a yadahụ fa iżā hiya baiḍā`u lin-nāẓirīn'),
(2965, 26, 34, 'قَالَ لِلْمَلَاِ حَوْلَهٗٓ اِنَّ هٰذَا لَسٰحِرٌ عَلِيْمٌ ۙ', 'qāla lil-mala`i ḥaulahū inna hāżā lasāḥirun \'alīm'),
(2966, 26, 35, 'يُّرِيْدُ اَنْ يُّخْرِجَكُمْ مِّنْ اَرْضِكُمْ بِسِحْرِهٖۖ فَمَاذَا تَأْمُرُوْنَ', 'yurīdu ay yukhrijakum min arḍikum bisiḥrihī fa māżā ta`murụn'),
(2967, 26, 36, 'قَالُوْٓا اَرْجِهْ وَاَخَاهُ وَابْعَثْ فِى الْمَدَاۤىِٕنِ حٰشِرِيْنَ ۙ', 'qālū arjih wa akhāhu wab\'aṡ fil-madā`ini ḥāsyirīn'),
(2968, 26, 37, 'يَأْتُوْكَ بِكُلِّ سَحَّارٍ عَلِيْمٍ', 'ya`tụka bikulli saḥḥārin \'alīm'),
(2969, 26, 38, 'فَجُمِعَ السَّحَرَةُ لِمِيْقَاتِ يَوْمٍ مَّعْلُوْمٍ ۙ', 'fa jumi\'as-saḥaratu limīqāti yaumim ma\'lụm'),
(2970, 26, 39, 'وَّقِيْلَ لِلنَّاسِ هَلْ اَنْتُمْ مُّجْتَمِعُوْنَ ۙ', 'wa qīla lin-nāsi hal antum mujtami\'ụn'),
(2971, 26, 40, 'لَعَلَّنَا نَتَّبِعُ السَّحَرَةَ اِنْ كَانُوْا هُمُ الْغٰلِبِيْنَ', 'la\'allanā nattabi\'us-saḥarata ing kānụ humul-gālibīn'),
(2972, 26, 41, 'فَلَمَّا جَاۤءَ السَّحَرَةُ قَالُوْا لِفِرْعَوْنَ اَىِٕنَّ لَنَا لَاَجْرًا اِنْ كُنَّا نَحْنُ الْغٰلِبِيْنَ', 'fa lammā jā`as-saḥaratu qālụ lifir\'auna a inna lanā la`ajran ing kunnā naḥnul-gālibīn'),
(2973, 26, 42, 'قَالَ نَعَمْ وَاِنَّكُمْ اِذًا لَّمِنَ الْمُقَرَّبِيْنَ', 'qāla na\'am wa innakum iżal laminal-muqarrabīn'),
(2974, 26, 43, 'قَالَ لَهُمْ مُّوْسٰٓى اَلْقُوْا مَآ اَنْتُمْ مُّلْقُوْنَ', 'qāla lahum mụsā alqụ mā antum mulqụn'),
(2975, 26, 44, 'فَاَلْقَوْا حِبَالَهُمْ وَعِصِيَّهُمْ وَقَالُوْا بِعِزَّةِ فِرْعَوْنَ اِنَّا لَنَحْنُ الْغٰلِبُوْنَ', 'fa alqau ḥibālahum wa \'iṣiyyahum wa qālụ bi\'izzati fir\'auna innā lanaḥnul-gālibụn'),
(2976, 26, 45, 'فَاَلْقٰى مُوْسٰى عَصَاهُ فَاِذَا هِيَ تَلْقَفُ مَا يَأْفِكُوْنَ ۚ', 'fa alqā mụsā \'aṣāhu fa iżā hiya talqafu mā ya`fikụn'),
(2977, 26, 46, 'فَاُلْقِيَ السَّحَرَةُ سٰجِدِيْنَ ۙ', 'fa ulqiyas-saḥaratu sājidīn'),
(2978, 26, 47, 'قَالُوْٓا اٰمَنَّا بِرَبِّ الْعٰلَمِيْنَ ۙ', 'qālū āmannā birabbil-\'ālamīn'),
(2979, 26, 48, 'رَبِّ مُوْسٰى وَهٰرُوْنَ', 'rabbi mụsā wa hārụn'),
(2980, 26, 49, 'قَالَ اٰمَنْتُمْ لَهٗ قَبْلَ اَنْ اٰذَنَ لَكُمْۚ اِنَّهٗ لَكَبِيْرُكُمُ الَّذِيْ عَلَّمَكُمُ السِّحْرَۚ فَلَسَوْفَ تَعْلَمُوْنَ ەۗ لَاُقَطِّعَنَّ اَيْدِيَكُمْ وَاَرْجُلَكُمْ مِّنْ خِلَافٍ وَّلَاُصَلِّبَنَّكُمْ اَجْمَعِيْنَۚ', 'qāla āmantum lahụ qabla an āżana lakum, innahụ lakabīrukumullażī \'allamakumus-siḥr, fa lasaufa ta\'lamụn, la`uqaṭṭi\'anna aidiyakum wa arjulakum min khilāfiw wa la`uṣallibannakum ajma\'īn'),
(2981, 26, 50, 'قَالُوْا لَا ضَيْرَ ۖاِنَّآ اِلٰى رَبِّنَا مُنْقَلِبُوْنَ ۚ', 'qālụ lā ḍaira innā ilā rabbinā mungqalibụn'),
(2982, 26, 51, ' اِنَّا نَطْمَعُ اَنْ يَّغْفِرَ لَنَا رَبُّنَا خَطٰيٰنَآ اَنْ كُنَّآ اَوَّلَ الْمُؤْمِنِيْنَ ۗ', 'innā naṭma\'u ay yagfira lanā rabbunā khaṭāyānā ang kunnā awwalal-mu`minīn'),
(2983, 26, 52, '۞ وَاَوْحَيْنَآ اِلٰى مُوْسٰٓى اَنْ اَسْرِ بِعِبَادِيْٓ اِنَّكُمْ مُّتَّبَعُوْنَ', 'wa auḥainā ilā mụsā an asri bi\'ibādī innakum muttaba\'ụn'),
(2984, 26, 53, 'فَاَرْسَلَ فِرْعَوْنُ فِى الْمَدَاۤىِٕنِ حٰشِرِيْنَ ۚ', 'fa arsala fir\'aunu fil-madā`ini ḥāsyirīn'),
(2985, 26, 54, 'اِنَّ هٰٓؤُلَاۤءِ لَشِرْذِمَةٌ قَلِيْلُوْنَۙ', 'inna hā`ulā`i lasyirżimatung qalīlụn'),
(2986, 26, 55, 'وَاِنَّهُمْ لَنَا لَغَاۤىِٕظُوْنَ ۙ', 'wa innahum lanā lagā`iẓụn'),
(2987, 26, 56, 'وَاِنَّا لَجَمِيْعٌ حٰذِرُوْنَ ۗ', 'wa innā lajamī\'un ḥāżirụn'),
(2988, 26, 57, 'فَاَخْرَجْنٰهُمْ مِّنْ جَنّٰتٍ وَّعُيُوْنٍ ۙ', 'fa akhrajnāhum min jannātiw wa \'uyụn'),
(2989, 26, 58, 'وَّكُنُوْزٍ وَّمَقَامٍ كَرِيْمٍ ۙ', 'wa kunụziw wa maqāming karīm'),
(2990, 26, 59, 'كَذٰلِكَۚ وَاَوْرَثْنٰهَا بَنِيْٓ اِسْرَاۤءِيْلَ ۗ', 'każālik, wa auraṡnāhā banī isrā`īl'),
(2991, 26, 60, 'فَاَتْبَعُوْهُمْ مُّشْرِقِيْنَ', 'fa atba\'ụhum musyriqīn'),
(2992, 26, 61, 'فَلَمَّا تَرَاۤءَ الْجَمْعٰنِ قَالَ اَصْحٰبُ مُوْسٰٓى اِنَّا لَمُدْرَكُوْنَ ۚ', 'fa lammā tarā`al-jam\'āni qāla aṣ-ḥābu mụsā innā lamudrakụn'),
(2993, 26, 62, 'قَالَ كَلَّاۗ اِنَّ مَعِيَ رَبِّيْ سَيَهْدِيْنِ', 'qāla kallā, inna ma\'iya rabbī sayahdīn'),
(2994, 26, 63, 'فَاَوْحَيْنَآ اِلٰى مُوْسٰٓى اَنِ اضْرِبْ بِّعَصَاكَ الْبَحْرَۗ فَانْفَلَقَ فَكَانَ كُلُّ فِرْقٍ كَالطَّوْدِ الْعَظِيْمِ ۚ', 'fa auḥainā ilā mụsā aniḍrib bi\'aṣākal-baḥr, fanfalaqa fa kāna kullu firqing kaṭ-ṭaudil-\'aẓīm'),
(2995, 26, 64, 'وَاَزْلَفْنَا ثَمَّ الْاٰخَرِيْنَ ۚ', 'wa azlafnā ṡammal-ākharīn'),
(2996, 26, 65, 'وَاَنْجَيْنَا مُوْسٰى وَمَنْ مَّعَهٗٓ اَجْمَعِيْنَ ۚ', 'wa anjainā mụsā wa mam ma\'ahū ajma\'īn'),
(2997, 26, 66, 'ثُمَّ اَغْرَقْنَا الْاٰخَرِيْنَ ۗ', 'ṡumma agraqnal-ākharīn'),
(2998, 26, 67, 'اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ', 'inna fī żālika la`āyah, wa mā kāna akṡaruhum mu`minīn'),
(2999, 26, 68, 'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ', 'wa inna rabbaka lahuwal-\'azīzur-raḥīm'),
(3000, 26, 69, 'وَاتْلُ عَلَيْهِمْ نَبَاَ اِبْرٰهِيْمَ ۘ', 'watlu \'alaihim naba`a ibrāhīm'),
(3001, 26, 70, 'اِذْ قَالَ لِاَبِيْهِ وَقَوْمِهٖ مَا تَعْبُدُوْنَ', 'iż qāla li`abīhi wa qaumihī mā ta\'budụn'),
(3002, 26, 71, 'قَالُوْا نَعْبُدُ اَصْنَامًا فَنَظَلُّ لَهَا عٰكِفِيْنَ', 'qālụ na\'budu aṣnāman fa naẓallu lahā \'ākifīn'),
(3003, 26, 72, 'قَالَ هَلْ يَسْمَعُوْنَكُمْ اِذْ تَدْعُوْنَ ۙ', 'qāla hal yasma\'ụnakum iż tad\'ụn'),
(3004, 26, 73, 'اَوْ يَنْفَعُوْنَكُمْ اَوْ يَضُرُّوْنَ', 'au yanfa\'ụnakum au yaḍurrụn'),
(3005, 26, 74, 'قَالُوْا بَلْ وَجَدْنَآ اٰبَاۤءَنَا كَذٰلِكَ يَفْعَلُوْنَ', 'qālụ bal wajadnā ābā`anā każālika yaf\'alụn'),
(3006, 26, 75, 'قَالَ اَفَرَءَيْتُمْ مَّا كُنْتُمْ تَعْبُدُوْنَ ۙ', 'qāla a fa ra`aitum mā kuntum ta\'budụn'),
(3007, 26, 76, 'اَنْتُمْ وَاٰبَاۤؤُكُمُ الْاَقْدَمُوْنَ ۙ', 'antum wa ābā`ukumul-aqdamụn'),
(3008, 26, 77, 'فَاِنَّهُمْ عَدُوٌّ لِّيْٓ اِلَّا رَبَّ الْعٰلَمِيْنَ ۙ', 'fa innahum \'aduwwul lī illā rabbal-\'ālamīn'),
(3009, 26, 78, 'الَّذِيْ خَلَقَنِيْ فَهُوَ يَهْدِيْنِ ۙ', 'allażī khalaqanī fa huwa yahdīn'),
(3010, 26, 79, 'وَالَّذِيْ هُوَ يُطْعِمُنِيْ وَيَسْقِيْنِ ۙ', 'wallażī huwa yuṭ\'imunī wa yasqīn'),
(3011, 26, 80, 'وَاِذَا مَرِضْتُ فَهُوَ يَشْفِيْنِ ۙ', 'wa iżā mariḍtu fa huwa yasyfīn'),
(3012, 26, 81, 'وَالَّذِيْ يُمِيْتُنِيْ ثُمَّ يُحْيِيْنِ ۙ', 'wallażī yumītunī ṡumma yuḥyīn'),
(3013, 26, 82, 'وَالَّذِيْٓ اَطْمَعُ اَنْ يَّغْفِرَ لِيْ خَطِيْۤـَٔتِيْ يَوْمَ الدِّيْنِ ۗ', 'wallażī aṭma\'u ay yagfira lī khaṭī`atī yaumad-dīn'),
(3014, 26, 83, 'رَبِّ هَبْ لِيْ حُكْمًا وَّاَلْحِقْنِيْ بِالصّٰلِحِيْنَ ۙ', 'rabbi hab lī ḥukmaw wa al-ḥiqnī biṣ-ṣāliḥīn'),
(3015, 26, 84, 'وَاجْعَلْ لِّيْ لِسَانَ صِدْقٍ فِى الْاٰخِرِيْنَ ۙ', 'waj\'al lī lisāna ṣidqin fil-ākhirīn'),
(3016, 26, 85, 'وَاجْعَلْنِيْ مِنْ وَّرَثَةِ جَنَّةِ النَّعِيْمِ ۙ', 'waj\'alnī miw waraṡati jannatin-na\'īm'),
(3017, 26, 86, 'وَاغْفِرْ لِاَبِيْٓ اِنَّهٗ كَانَ مِنَ الضَّاۤلِّيْنَ ۙ', 'wagfir li`abī innahụ kāna minaḍ-ḍāllīn'),
(3018, 26, 87, 'وَلَا تُخْزِنِيْ يَوْمَ يُبْعَثُوْنَۙ', 'wa lā tukhzinī yauma yub\'aṡụn'),
(3019, 26, 88, 'يَوْمَ لَا يَنْفَعُ مَالٌ وَّلَا بَنُوْنَ ۙ', 'yauma lā yanfa\'u māluw wa lā banụn'),
(3020, 26, 89, 'اِلَّا مَنْ اَتَى اللّٰهَ بِقَلْبٍ سَلِيْمٍ ۗ', 'illā man atallāha biqalbin salīm'),
(3021, 26, 90, 'وَاُزْلِفَتِ الْجَنَّةُ لِلْمُتَّقِيْنَ ۙ', 'wa uzlifatil-jannatu lil-muttaqīn'),
(3022, 26, 91, 'وَبُرِّزَتِ الْجَحِيْمُ لِلْغٰوِيْنَ ۙ', 'wa burrizatil-jaḥīmu lil-gāwīn'),
(3023, 26, 92, 'وَقِيْلَ لَهُمْ اَيْنَ مَا كُنْتُمْ تَعْبُدُوْنَ ۙ', 'wa qīla lahum aina mā kuntum ta\'budụn'),
(3024, 26, 93, 'مِنْ دُوْنِ اللّٰهِ ۗهَلْ يَنْصُرُوْنَكُمْ اَوْ يَنْتَصِرُوْنَ ۗ', 'min dụnillāh, hal yanṣurụnakum au yantaṣirụn'),
(3025, 26, 94, 'فَكُبْكِبُوْا فِيْهَا هُمْ وَالْغَاوٗنَ ۙ', 'fakubkibụ fīhā hum wal-gāwụn'),
(3026, 26, 95, 'وَجُنُوْدُ اِبْلِيْسَ اَجْمَعُوْنَ ۗ', 'wa junụdu iblīsa ajma\'ụn'),
(3027, 26, 96, 'قَالُوْا وَهُمْ فِيْهَا يَخْتَصِمُوْنَ', 'qālụ wa hum fīhā yakhtaṣimụn'),
(3028, 26, 97, 'تَاللّٰهِ اِنْ كُنَّا لَفِيْ ضَلٰلٍ مُّبِيْنٍ ۙ', 'tallāhi ing kunnā lafī ḍalālim mubīn'),
(3029, 26, 98, 'اِذْ نُسَوِّيْكُمْ بِرَبِّ الْعٰلَمِيْنَ', 'iż nusawwīkum birabbil-\'ālamīn'),
(3030, 26, 99, 'وَمَآ اَضَلَّنَآ اِلَّا الْمُجْرِمُوْنَ', 'wa mā aḍallanā illal-mujrimụn'),
(3031, 26, 100, 'فَمَا لَنَا مِنْ شَافِعِيْنَ ۙ', 'fa mā lanā min syāfi\'īn'),
(3032, 26, 101, 'وَلَا صَدِيْقٍ حَمِيْمٍ', 'wa lā ṣadīqin ḥamīm'),
(3033, 26, 102, 'فَلَوْ اَنَّ لَنَا كَرَّةً فَنَكُوْنَ مِنَ الْمُؤْمِنِيْنَ', 'falau anna lanā karratan fa nakụna minal-mu`minīn'),
(3034, 26, 103, 'اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ', 'inna fī żālika la`āyah, wa mā kāna akṡaruhum mu`minīn'),
(3035, 26, 104, 'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ', 'wa inna rabbaka lahuwal-\'azīzur-raḥīm'),
(3036, 26, 105, 'كَذَّبَتْ قَوْمُ نُوْحِ ِۨالْمُرْسَلِيْنَ ۚ', 'każżabat qaumu nụḥinil-mursalīn'),
(3037, 26, 106, 'اِذْ قَالَ لَهُمْ اَخُوْهُمْ نُوْحٌ اَلَا تَتَّقُوْنَ ۚ', 'iż qāla lahum akhụhum nụḥun alā tattaqụn'),
(3038, 26, 107, 'اِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌ ۙ', 'innī lakum rasụlun amīn'),
(3039, 26, 108, 'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِۚ', 'fattaqullāha wa aṭī\'ụn'),
(3040, 26, 109, 'وَمَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍۚ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعٰلَمِيْنَ ۚ', 'wa mā as`alukum \'alaihi min ajr, in ajriya illā \'alā rabbil-\'ālamīn'),
(3041, 26, 110, 'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ', 'fattaqullāha wa aṭī\'ụn'),
(3042, 26, 111, '۞ قَالُوْٓا اَنُؤْمِنُ لَكَ وَاتَّبَعَكَ الْاَرْذَلُوْنَ ۗ', 'qālū a nu`minu laka wattaba\'akal-arżalụn'),
(3043, 26, 112, 'قَالَ وَمَا عِلْمِيْ بِمَا كَانُوْا يَعْمَلُوْنَ ۚ', 'qāla wa mā \'ilmī bimā kānụ ya\'malụn'),
(3044, 26, 113, 'اِنْ حِسَابُهُمْ اِلَّا عَلٰى رَبِّيْ لَوْ تَشْعُرُوْنَ ۚ', 'in ḥisābuhum illā \'alā rabbī lau tasy\'urụn'),
(3045, 26, 114, 'وَمَآ اَنَا۠ بِطَارِدِ الْمُؤْمِنِيْنَ ۚ', 'wa mā ana biṭāridil-mu`minīn'),
(3046, 26, 115, 'اِنْ اَنَا۠ اِلَّا نَذِيْرٌ مُّبِيْنٌ ۗ', 'in ana illā nażīrum mubīn'),
(3047, 26, 116, 'قَالُوْا لَىِٕنْ لَّمْ تَنْتَهِ يٰنُوْحُ لَتَكُوْنَنَّ مِنَ الْمَرْجُوْمِيْنَۗ', 'qālụ la`il lam tantahi yā nụḥu latakụnanna minal-marjụmīn'),
(3048, 26, 117, 'قَالَ رَبِّ اِنَّ قَوْمِيْ كَذَّبُوْنِۖ', 'qāla rabbi inna qaumī każżabụn'),
(3049, 26, 118, 'فَافْتَحْ بَيْنِيْ وَبَيْنَهُمْ فَتْحًا وَّنَجِّنِيْ وَمَنْ مَّعِيَ مِنَ الْمُؤْمِنِيْنَ', 'faftaḥ bainī wa bainahum fat-ḥaw wa najjinī wa mam ma\'iya minal-mu`minīn'),
(3050, 26, 119, 'فَاَنْجَيْنٰهُ وَمَنْ مَّعَهٗ فِى الْفُلْكِ الْمَشْحُوْنِ', 'fa anjaināhu wa mam ma\'ahụ fil-fulkil-masy-ḥụn'),
(3051, 26, 120, 'ثُمَّ اَغْرَقْنَا بَعْدُ الْبٰقِيْنَ', 'ṡumma agraqnā ba\'dul-bāqīn'),
(3052, 26, 121, 'اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ', 'inna fī żālika la`āyah, wa mā kāna akṡaruhum mu`minīn'),
(3053, 26, 122, 'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ', 'wa inna rabbaka lahuwal-\'azīzur-raḥīm'),
(3054, 26, 123, 'كَذَّبَتْ عَادُ ِۨالْمُرْسَلِيْنَ ۖ', 'każżabat \'ādunil-mursalīn'),
(3055, 26, 124, 'اِذْ قَالَ لَهُمْ اَخُوْهُمْ هُوْدٌ اَلَا تَتَّقُوْنَ ۚ', 'iż qāla lahum akhụhum hụdun alā tattaqụn'),
(3056, 26, 125, 'اِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌ ۙ', 'innī lakum rasụlun amīn'),
(3057, 26, 126, 'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ۚ', 'fattaqullāha wa aṭī\'ụn'),
(3058, 26, 127, 'وَمَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍۚ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعٰلَمِيْنَ ۗ', 'wa mā as`alukum \'alaihi min ajr, in ajriya illā \'alā rabbil-\'ālamīn'),
(3059, 26, 128, 'اَتَبْنُوْنَ بِكُلِّ رِيْعٍ اٰيَةً تَعْبَثُوْنَ ۙ', 'a tabnụna bikulli rī\'in āyatan ta\'baṡụn'),
(3060, 26, 129, 'وَتَتَّخِذُوْنَ مَصَانِعَ لَعَلَّكُمْ تَخْلُدُوْنَۚ', 'wa tattakhiżụna maṣāni\'a la\'allakum takhludụn'),
(3061, 26, 130, 'وَاِذَا بَطَشْتُمْ بَطَشْتُمْ جَبَّارِيْنَۚ', 'wa iżā baṭasytum baṭasytum jabbārīn'),
(3062, 26, 131, 'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِۚ', 'fattaqullāha wa aṭī\'ụn'),
(3063, 26, 132, 'وَاتَّقُوا الَّذِيْٓ اَمَدَّكُمْ بِمَا تَعْلَمُوْنَ ۚ', 'wattaqullażī amaddakum bimā ta\'lamụn'),
(3064, 26, 133, 'اَمَدَّكُمْ بِاَنْعَامٍ وَّبَنِيْنَۙ', 'amaddakum bi`an\'āmiw wa banīn'),
(3065, 26, 134, 'وَجَنّٰتٍ وَّعُيُوْنٍۚ', 'wa jannātiw wa \'uyụn'),
(3066, 26, 135, 'اِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظِيْمٍ ۗ', 'innī akhāfu \'alaikum \'ażāba yaumin \'aẓīm'),
(3067, 26, 136, 'قَالُوْا سَوَاۤءٌ عَلَيْنَآ اَوَعَظْتَ اَمْ لَمْ تَكُنْ مِّنَ الْوَاعِظِيْنَ ۙ', 'qālụ sawā`un \'alainā a wa\'aẓta am lam takum minal-wā\'iẓīn'),
(3068, 26, 137, 'اِنْ هٰذَآ اِلَّا خُلُقُ الْاَوَّلِيْنَ ۙ', 'in hāżā illā khuluqul-awwalīn'),
(3069, 26, 138, 'وَمَا نَحْنُ بِمُعَذَّبِيْنَ ۚ', 'wa mā naḥnu bimu\'ażżabīn'),
(3070, 26, 139, 'فَكَذَّبُوْهُ فَاَهْلَكْنٰهُمْۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ', 'fa każżabụhu fa ahlaknāhum, inna fī żālika la`āyah, wa mā kāna akṡaruhum mu`minīn'),
(3071, 26, 140, 'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ', 'wa inna rabbaka lahuwal-\'azīzur-raḥīm'),
(3072, 26, 141, 'كَذَّبَتْ ثَمُوْدُ الْمُرْسَلِيْنَ ۖ', 'każżabat ṡamụdul-mursalīn'),
(3073, 26, 142, 'اِذْ قَالَ لَهُمْ اَخُوْهُمْ صٰلِحٌ اَلَا تَتَّقُوْنَ ۚ', 'iż qāla lahum akhụhum ṣāliḥun alā tattaqụn'),
(3074, 26, 143, 'اِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌ ۙ', 'innī lakum rasụlun amīn'),
(3075, 26, 144, 'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ۚ', 'fattaqullāha wa aṭī\'ụn'),
(3076, 26, 145, 'وَمَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍۚ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعٰلَمِيْنَ ۗ', 'wa mā as`alukum \'alaihi min ajr, in ajriya illā \'alā rabbil-\'ālamīn'),
(3077, 26, 146, 'اَتُتْرَكُوْنَ فِيْ مَا هٰهُنَآ اٰمِنِيْنَ ۙ', 'a tutrakụna fī mā hāhunā āminīn'),
(3078, 26, 147, 'فِيْ جَنّٰتٍ وَّعُيُوْنٍ ۙ', 'fī jannātiw wa \'uyụn'),
(3079, 26, 148, 'وَّزُرُوْعٍ وَّنَخْلٍ طَلْعُهَا هَضِيْمٌ ۚ', 'wa zurụ\'iw wa nakhlin ṭal\'uhā haḍīm'),
(3080, 26, 149, 'وَتَنْحِتُوْنَ مِنَ الْجِبَالِ بُيُوْتًا فٰرِهِيْنَ', 'wa tan-ḥitụna minal-jibāli buyụtan fārihīn'),
(3081, 26, 150, 'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ۚ', 'fattaqullāha wa aṭī\'ụn'),
(3082, 26, 151, 'وَلَا تُطِيْعُوْٓا اَمْرَ الْمُسْرِفِيْنَ ۙ', 'wa lā tuṭī\'ū amral-musrifīn'),
(3083, 26, 152, 'الَّذِيْنَ يُفْسِدُوْنَ فِى الْاَرْضِ وَلَا يُصْلِحُوْنَ', 'allażīna yufsidụna fil-arḍi wa lā yuṣliḥụn'),
(3084, 26, 153, 'قَالُوْٓا اِنَّمَآ اَنْتَ مِنَ الْمُسَحَّرِيْنَ ۙ', 'qālū innamā anta minal-musaḥḥarīn'),
(3085, 26, 154, 'مَآ اَنْتَ اِلَّا بَشَرٌ مِّثْلُنَاۙ فَأْتِ بِاٰيَةٍ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ', 'mā anta illā basyarum miṡlunā fa`ti bi`āyatin ing kunta minaṣ-ṣādiqīn'),
(3086, 26, 155, 'قَالَ هٰذِهٖ نَاقَةٌ لَّهَا شِرْبٌ وَّلَكُمْ شِرْبُ يَوْمٍ مَّعْلُوْمٍ ۚ', 'qāla hāżihī nāqatul lahā syirbuw wa lakum syirbu yaumim ma\'lụm'),
(3087, 26, 156, 'وَلَا تَمَسُّوْهَا بِسُوْۤءٍ فَيَأْخُذَكُمْ عَذَابُ يَوْمٍ عَظِيْمٍ', 'wa lā tamassụhā bisū`in fa ya`khużakum \'ażābu yaumin \'aẓīm'),
(3088, 26, 157, 'فَعَقَرُوْهَا فَاَصْبَحُوْا نٰدِمِيْنَ ۙ', 'fa \'aqarụhā fa aṣbaḥụ nādimīn'),
(3089, 26, 158, 'فَاَخَذَهُمُ الْعَذَابُۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ', 'fa akhażahumul-\'ażāb, inna fī żālika la`āyah, wa mā kāna akṡaruhum mu`minīn'),
(3090, 26, 159, 'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ', 'wa inna rabbaka lahuwal-\'azīzur-raḥīm'),
(3091, 26, 160, 'كَذَّبَتْ قَوْمُ لُوْطِ ِۨالْمُرْسَلِيْنَ ۖ', 'każżabat qaumu lụṭinil-mursalīn'),
(3092, 26, 161, 'اِذْ قَالَ لَهُمْ اَخُوْهُمْ لُوْطٌ اَلَا تَتَّقُوْنَ ۚ', 'iż qāla lahum akhụhum lụṭun alā tattaqụn'),
(3093, 26, 162, 'اِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌ ۙ', 'innī lakum rasụlun amīn'),
(3094, 26, 163, 'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ۚ', 'fattaqullāha wa aṭī\'ụn'),
(3095, 26, 164, 'وَمَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعٰلَمِيْنَ ۗ', 'wa mā as`alukum \'alaihi min ajrin in ajriya illā \'alā rabbil-\'ālamīn'),
(3096, 26, 165, 'اَتَأْتُوْنَ الذُّكْرَانَ مِنَ الْعٰلَمِيْنَ ۙ', 'a ta`tụnaż-żukrāna minal-\'ālamīn'),
(3097, 26, 166, 'وَتَذَرُوْنَ مَا خَلَقَ لَكُمْ رَبُّكُمْ مِّنْ اَزْوَاجِكُمْۗ بَلْ اَنْتُمْ قَوْمٌ عٰدُوْنَ', 'wa tażarụna mā khalaqa lakum rabbukum min azwājikum, bal antum qaumun \'ādụn'),
(3098, 26, 167, 'قَالُوْا لَىِٕنْ لَّمْ تَنْتَهِ يٰلُوْطُ لَتَكُوْنَنَّ مِنَ الْمُخْرَجِيْنَ', 'qālụ la`il lam tantahi yā lụṭu latakụnanna minal-mukhrajīn'),
(3099, 26, 168, 'قَالَ ِانِّيْ لِعَمَلِكُمْ مِّنَ الْقَالِيْنَ ۗ', 'qāla innī li\'amalikum minal-qālīn'),
(3100, 26, 169, 'رَبِّ نَجِّنِيْ وَاَهْلِيْ مِمَّا يَعْمَلُوْنَ', 'rabbi najjinī wa ahlī mimmā ya\'malụn'),
(3101, 26, 170, 'فَنَجَّيْنٰهُ وَاَهْلَهٗٓ اَجْمَعِيْنَ ۙ', 'fa najjaināhu wa ahlahū ajma\'īn'),
(3102, 26, 171, 'اِلَّا عَجُوْزًا فِى الْغٰبِرِيْنَ ۚ', 'illā \'ajụzan fil-gābirīn'),
(3103, 26, 172, 'ثُمَّ دَمَّرْنَا الْاٰخَرِيْنَ ۚ', 'ṡumma dammarnal-ākharīn'),
(3104, 26, 173, 'وَاَمْطَرْنَا عَلَيْهِمْ مَّطَرًاۚ فَسَاۤءَ مَطَرُ الْمُنْذَرِيْنَ', 'wa amṭarnā \'alaihim maṭarā, fa sā`a maṭarul-munżarīn'),
(3105, 26, 174, 'اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ', 'inna fī żālika la`āyah, wa mā kāna akṡaruhum mu`minīn'),
(3106, 26, 175, 'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ', 'wa inna rabbaka lahuwal-\'azīzur-raḥīm'),
(3107, 26, 176, 'كَذَّبَ اَصْحٰبُ لْـَٔيْكَةِ الْمُرْسَلِيْنَ ۖ', 'każżaba aṣ-ḥābul-aikatil-mursalīn'),
(3108, 26, 177, 'اِذْ قَالَ لَهُمْ شُعَيْبٌ اَلَا تَتَّقُوْنَ ۚ', 'iż qāla lahum syu\'aibun alā tattaqụn'),
(3109, 26, 178, 'اِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌ ۙ', 'innī lakum rasụlun amīn'),
(3110, 26, 179, 'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ۚ', 'fattaqullāha wa aṭī\'ụn'),
(3111, 26, 180, 'وَمَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعٰلَمِيْنَ ۗ', 'wa mā as`alukum \'alaihi min ajrin in ajriya illā \'alā rabbil-\'ālamīn'),
(3112, 26, 181, '۞ اَوْفُوا الْكَيْلَ وَلَا تَكُوْنُوْا مِنَ الْمُخْسِرِيْنَ ۚ', 'auful-kaila wa lā takụnụ minal-mukhsirīn'),
(3113, 26, 182, 'وَزِنُوْا بِالْقِسْطَاسِ الْمُسْتَقِيْمِ ۚ', 'wa zinụ bil-qisṭāsil-mustaqīm'),
(3114, 26, 183, 'وَلَا تَبْخَسُوا النَّاسَ اَشْيَاۤءَهُمْ وَلَا تَعْثَوْا فِى الْاَرْضِ مُفْسِدِيْنَ ۚ', 'wa lā tabkhasun-nāsa asy-yā`ahum wa lā ta\'ṡau fil-arḍi mufsidīn'),
(3115, 26, 184, 'وَاتَّقُوا الَّذِيْ خَلَقَكُمْ وَالْجِبِلَّةَ الْاَوَّلِيْنَ ۗ', 'wattaqullażī khalaqakum wal-jibillatal-awwalīn'),
(3116, 26, 185, 'قَالُوْٓا اِنَّمَآ اَنْتَ مِنَ الْمُسَحَّرِيْنَ ۙ', 'qālū innamā anta minal-musaḥḥarīn'),
(3117, 26, 186, 'وَمَآ اَنْتَ اِلَّا بَشَرٌ مِّثْلُنَا وَاِنْ نَّظُنُّكَ لَمِنَ الْكٰذِبِيْنَ ۚ', 'wa mā anta illā basyarum miṡlunā wa in naẓunnuka laminal-kāżibīn'),
(3118, 26, 187, 'فَاَسْقِطْ عَلَيْنَا كِسَفًا مِّنَ السَّمَاۤءِ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ ۗ', 'fa asqiṭ \'alainā kisafam minas-samā`i ing kunta minaṣ-ṣādiqīn'),
(3119, 26, 188, 'قَالَ رَبِّيْٓ اَعْلَمُ بِمَا تَعْمَلُوْنَ', 'qāla rabbī a\'lamu bimā ta\'malụn'),
(3120, 26, 189, 'فَكَذَّبُوْهُ فَاَخَذَهُمْ عَذَابُ يَوْمِ الظُّلَّةِ ۗاِنَّهٗ كَانَ عَذَابَ يَوْمٍ عَظِيْمٍ', 'fa każżabụhu fa akhażahum \'ażābu yaumiẓ-ẓullah, innahụ kāna \'ażāba yaumin \'aẓīm'),
(3121, 26, 190, 'اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ', 'inna fī żālika la`āyah, wa mā kāna akṡaruhum mu`minīn'),
(3122, 26, 191, 'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ', 'wa inna rabbaka lahuwal-\'azīzur-raḥīm'),
(3123, 26, 192, 'وَاِنَّهٗ لَتَنْزِيْلُ رَبِّ الْعٰلَمِيْنَ ۗ', 'wa innahụ latanzīlu rabbil-\'ālamīn'),
(3124, 26, 193, 'نَزَلَ بِهِ الرُّوْحُ الْاَمِيْنُ ۙ', 'nazala bihir-rụḥul-amīn'),
(3125, 26, 194, 'عَلٰى قَلْبِكَ لِتَكُوْنَ مِنَ الْمُنْذِرِيْنَ ۙ', '\'alā qalbika litakụna minal-munżirīn'),
(3126, 26, 195, 'بِلِسَانٍ عَرَبِيٍّ مُّبِيْنٍ ۗ', 'bilisānin \'arabiyyim mubīn'),
(3127, 26, 196, 'وَاِنَّهٗ لَفِيْ زُبُرِ الْاَوَّلِيْنَ', 'wa innahụ lafī zuburil-awwalīn'),
(3128, 26, 197, 'اَوَلَمْ يَكُنْ لَّهُمْ اٰيَةً اَنْ يَّعْلَمَهٗ عُلَمٰۤؤُا بَنِيْٓ اِسْرَاۤءِيْلَ', 'a wa lam yakul lahum āyatan ay ya\'lamahụ \'ulamā`u banī isrā`īl'),
(3129, 26, 198, 'وَلَوْ نَزَّلْنٰهُ عَلٰى بَعْضِ الْاَعْجَمِيْنَ ۙ', 'walau nazzalnāhu \'alā ba\'ḍil-a\'jamīn'),
(3130, 26, 199, 'فَقَرَاَهٗ عَلَيْهِمْ مَّا كَانُوْا بِهٖ مُؤْمِنِيْنَ ۗ', 'faqara`ahụ \'alaihim mā kānụ bihī mu`minīn'),
(3131, 26, 200, 'كَذٰلِكَ سَلَكْنٰهُ فِيْ قُلُوْبِ الْمُجْرِمِيْنَ ۗ', 'każālika salaknāhu fī qulụbil-mujrimīn'),
(3132, 26, 201, 'لَا يُؤْمِنُوْنَ بِهٖ حَتّٰى يَرَوُا الْعَذَابَ الْاَلِيْمَ', 'lā yu`minụna bihī ḥattā yarawul-\'ażābal-alīm'),
(3133, 26, 202, 'فَيَأْتِيَهُمْ بَغْتَةً وَّهُمْ لَا يَشْعُرُوْنَ ۙ', 'fa ya`tiyahum bagtataw wa hum lā yasy\'urụn'),
(3134, 26, 203, 'فَيَقُوْلُوْا هَلْ نَحْنُ مُنْظَرُوْنَ ۗ', 'fa yaqụlụ hal naḥnu munẓarụn'),
(3135, 26, 204, 'اَفَبِعَذَابِنَا يَسْتَعْجِلُوْنَ', 'a fa bi\'ażābinā yasta\'jilụn'),
(3136, 26, 205, 'اَفَرَءَيْتَ اِنْ مَّتَّعْنٰهُمْ سِنِيْنَ ۙ', 'a fa ra`aita im matta\'nāhum sinīn'),
(3137, 26, 206, 'ثُمَّ جَاۤءَهُمْ مَّا كَانُوْا يُوْعَدُوْنَ ۙ', 'ṡumma jā`ahum mā kānụ yụ\'adụn'),
(3138, 26, 207, 'مَآ اَغْنٰى عَنْهُمْ مَّا كَانُوْا يُمَتَّعُوْنَ ۗ', 'mā agnā \'an-hum mā kānụ yumatta\'ụn'),
(3139, 26, 208, 'وَمَآ اَهْلَكْنَا مِنْ قَرْيَةٍ اِلَّا لَهَا مُنْذِرُوْنَ ۖ', 'wa mā ahlaknā ming qaryatin illā lahā munżirụn'),
(3140, 26, 209, 'ذِكْرٰىۚ وَمَا كُنَّا ظٰلِمِيْنَ', 'żikrā, wa mā kunnā ẓālimīn'),
(3141, 26, 210, 'وَمَا تَنَزَّلَتْ بِهِ الشَّيٰطِيْنُ', 'wa mā tanazzalat bihisy-syayāṭīn'),
(3142, 26, 211, 'وَمَا يَنْۢبَغِيْ لَهُمْ وَمَا يَسْتَطِيْعُوْنَ ۗ', 'wa mā yambagī lahum wa mā yastaṭī\'ụn'),
(3143, 26, 212, 'اِنَّهُمْ عَنِ السَّمْعِ لَمَعْزُوْلُوْنَ ۗ', 'innahum \'anis-sam\'i lama\'zụlụn'),
(3144, 26, 213, 'فَلَا تَدْعُ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ فَتَكُوْنَ مِنَ الْمُعَذَّبِيْنَ', 'fa lā tad\'u ma\'allāhi ilāhan ākhara fa takụna minal-mu\'ażżabīn'),
(3145, 26, 214, 'وَاَنْذِرْ عَشِيْرَتَكَ الْاَقْرَبِيْنَ ۙ', 'wa anżir \'asyīratakal-aqrabīn'),
(3146, 26, 215, 'وَاخْفِضْ جَنَاحَكَ لِمَنِ اتَّبَعَكَ مِنَ الْمُؤْمِنِيْنَ ۚ', 'wakhfiḍ janāḥaka limanittaba\'aka minal-mu`minīn'),
(3147, 26, 216, 'فَاِنْ عَصَوْكَ فَقُلْ اِنِّيْ بَرِيْۤءٌ مِّمَّا تَعْمَلُوْنَ ۚ', 'fa in \'aṣauka fa qul innī barī`um mimmā ta\'malụn'),
(3148, 26, 217, 'وَتَوَكَّلْ عَلَى الْعَزِيْزِ الرَّحِيْمِ ۙ', 'wa tawakkal \'alal-\'azīzir-raḥīm'),
(3149, 26, 218, 'الَّذِيْ يَرٰىكَ حِيْنَ تَقُوْمُ', 'allażī yarāka ḥīna taqụm'),
(3150, 26, 219, 'وَتَقَلُّبَكَ فِى السّٰجِدِيْنَ', 'wa taqallubaka fis-sājidīn'),
(3151, 26, 220, 'اِنَّهٗ هُوَ السَّمِيْعُ الْعَلِيْمُ', 'innahụ huwas-samī\'ul-\'alīm'),
(3152, 26, 221, 'هَلْ اُنَبِّئُكُمْ عَلٰى مَنْ تَنَزَّلُ الشَّيٰطِيْنُ ۗ', 'hal unabbi`ukum \'alā man tanazzalusy-syayāṭīn'),
(3153, 26, 222, 'تَنَزَّلُ عَلٰى كُلِّ اَفَّاكٍ اَثِيْمٍ ۙ', 'tanazzalu \'alā kulli affākin aṡīm'),
(3154, 26, 223, 'يُّلْقُوْنَ السَّمْعَ وَاَكْثَرُهُمْ كٰذِبُوْنَ ۗ', 'yulqụnas-sam\'a wa akṡaruhum kāżibụn'),
(3155, 26, 224, 'وَالشُّعَرَاۤءُ يَتَّبِعُهُمُ الْغَاوٗنَ ۗ', 'wasy-syu\'arā`u yattabi\'uhumul-gāwụn'),
(3156, 26, 225, 'اَلَمْ تَرَ اَنَّهُمْ فِيْ كُلِّ وَادٍ يَّهِيْمُوْنَ ۙ', 'a lam tara annahum fī kulli wādiy yahīmụn'),
(3157, 26, 226, 'وَاَنَّهُمْ يَقُوْلُوْنَ مَا لَا يَفْعَلُوْنَ ۙ', 'wa annahum yaqụlụna mā lā yaf\'alụn'),
(3158, 26, 227, 'اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَذَكَرُوا اللّٰهَ كَثِيْرًا وَّانْتَصَرُوْا مِنْۢ بَعْدِ مَا ظُلِمُوْا ۗوَسَيَعْلَمُ الَّذِيْنَ ظَلَمُوْٓا اَيَّ مُنْقَلَبٍ يَّنْقَلِبُوْنَ', 'illallażīna āmanụ wa \'amiluṣ-ṣāliḥāti wa żakarullāha kaṡīraw wantaṣarụ mim ba\'di mā ẓulimụ, wa saya\'lamullażīna ẓalamū ayya mungqalabiy yangqalibụn'),
(3159, 27, 1, 'طٰسۤ ۚ تِلْكَ اٰيٰتُ الْقُرْاٰنِ وَكِتَابٍ مُّبِيْنٍ ۙ', 'ṭā sīn, tilka āyātul-qur`āni wa kitābim mubīn'),
(3160, 27, 2, 'هُدًى وَّبُشْرٰى لِلْمُؤْمِنِيْنَ ۙ', 'hudaw wa busyrā lil-mu`minīn'),
(3161, 27, 3, 'الَّذِيْنَ يُقِيْمُوْنَ الصَّلٰوةَ وَيُؤْتُوْنَ الزَّكٰوةَ وَهُمْ بِالْاٰخِرَةِ هُمْ يُوْقِنُوْنَ', 'allażīna yuqīmụnaṣ-ṣalāta wa yu`tụnaz-zakāta wa hum bil-ākhirati hum yụqinụn'),
(3162, 27, 4, 'اِنَّ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ زَيَّنَّا لَهُمْ اَعْمَالَهُمْ فَهُمْ يَعْمَهُوْنَ ۗ', 'innallażīna lā yu`minụna bil-ākhirati zayyannā lahum a\'mālahum fa hum ya\'mahụn'),
(3163, 27, 5, 'اُولٰۤىِٕكَ الَّذِيْنَ لَهُمْ سُوْۤءُ الْعَذَابِ وَهُمْ فِى الْاٰخِرَةِ هُمُ الْاَخْسَرُوْنَ', 'ulā`ikallażīna lahum sū`ul-\'ażābi wa hum fil-ākhirati humul-akhsarụn'),
(3164, 27, 6, 'وَاِنَّكَ لَتُلَقَّى الْقُرْاٰنَ مِنْ لَّدُنْ حَكِيْمٍ عَلِيْمٍ', 'wa innaka latulaqqal-qur`āna mil ladun ḥakīmin \'alīm'),
(3165, 27, 7, 'اِذْ قَالَ مُوْسٰى لِاَهْلِهٖٓ اِنِّيْٓ اٰنَسْتُ نَارًاۗ سَاٰتِيْكُمْ مِّنْهَا بِخَبَرٍ اَوْ اٰتِيْكُمْ بِشِهَابٍ قَبَسٍ لَّعَلَّكُمْ تَصْطَلُوْنَ', 'iż qāla mụsā li`ahlihī innī ānastu nārā, sa`ātīkum min-hā bikhabarin au ātīkum bisyihābing qabasil la\'allakum taṣṭalụn'),
(3166, 27, 8, 'فَلَمَّا جَاۤءَهَا نُوْدِيَ اَنْۢ بُوْرِكَ مَنْ فِى النَّارِ وَمَنْ حَوْلَهَاۗ وَسُبْحٰنَ اللّٰهِ رَبِّ الْعٰلَمِيْنَ', 'fa lammā jā`ahā nụdiya am bụrika man fin-nāri wa man ḥaulahā, wa sub-ḥānallāhi rabbil-\'ālamīn'),
(3167, 27, 9, 'يٰمُوْسٰٓى اِنَّهٗٓ اَنَا اللّٰهُ الْعَزِيْزُ الْحَكِيْمُ ۙ', 'yā mụsā innahū anallāhul-\'azīzul-ḥakīm'),
(3168, 27, 10, 'وَاَلْقِ عَصَاكَ ۗفَلَمَّا رَاٰهَا تَهْتَزُّ كَاَنَّهَا جَاۤنٌّ وَّلّٰى مُدْبِرًا وَّلَمْ يُعَقِّبْۗ يٰمُوْسٰى لَا تَخَفْۗ اِنِّيْ لَا يَخَافُ لَدَيَّ الْمُرْسَلُوْنَ ۖ', 'wa alqi \'aṣāk, fa lammā ra`āhā tahtazzu ka`annahā jānnuw wallā mudbiraw wa lam yu\'aqqib, yā mụsā lā takhaf, innī lā yakhāfu ladayyal-mursalụn'),
(3169, 27, 11, 'اِلَّا مَنْ ظَلَمَ ثُمَّ بَدَّلَ حُسْنًاۢ بَعْدَ سُوْۤءٍ فَاِنِّيْ غَفُوْرٌ رَّحِيْمٌ', 'illā man ẓalama ṡumma baddala ḥusnam ba\'da sū`in fa innī gafụrur raḥīm'),
(3170, 27, 12, 'وَاَدْخِلْ يَدَكَ فِيْ جَيْبِكَ تَخْرُجْ بَيْضَاۤءَ مِنْ غَيْرِ سُوْۤءٍۙ فِيْ تِسْعِ اٰيٰتٍ اِلٰى فِرْعَوْنَ وَقَوْمِهٖۚ اِنَّهُمْ كَانُوْا قَوْمًا فٰسِقِيْنَ', 'wa adkhil yadaka fī jaibika takhruj baiḍā`a min gairi sū`in fī tis\'i āyātin ilā fir\'auna wa qaumih, innahum kānụ qauman fāsiqīn'),
(3171, 27, 13, 'فَلَمَّا جَاۤءَتْهُمْ اٰيٰتُنَا مُبْصِرَةً قَالُوْا هٰذَا سِحْرٌ مُّبِيْنٌ ۚ', 'fa lammā jā`at-hum āyātunā mubṣiratang qālụ hāżā siḥrum mubīn'),
(3172, 27, 14, 'وَجَحَدُوْا بِهَا وَاسْتَيْقَنَتْهَآ اَنْفُسُهُمْ ظُلْمًا وَّعُلُوًّاۗ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِيْنَ', 'wa jaḥadụ bihā wastaiqanat-hā anfusuhum ẓulmaw wa \'uluwwā, fanẓur kaifa kāna \'āqibatul-mufsidīn'),
(3173, 27, 15, 'وَلَقَدْ اٰتَيْنَا دَاوٗدَ وَسُلَيْمٰنَ عِلْمًاۗ وَقَالَا الْحَمْدُ لِلّٰهِ الَّذِيْ فَضَّلَنَا عَلٰى كَثِيْرٍ مِّنْ عِبَادِهِ الْمُؤْمِنِيْنَ', 'wa laqad ātainā dāwụda wa sulaimāna \'ilmā, wa qālal-ḥamdu lillāhillażī faḍḍalanā \'alā kaṡīrim min \'ibādihil-mu`minīn'),
(3174, 27, 16, 'وَوَرِثَ سُلَيْمٰنُ دَاوٗدَ وَقَالَ يٰٓاَيُّهَا النَّاسُ عُلِّمْنَا مَنْطِقَ الطَّيْرِ وَاُوْتِيْنَا مِنْ كُلِّ شَيْءٍۗ اِنَّ هٰذَا لَهُوَ الْفَضْلُ الْمُبِيْنُ', 'wa wariṡa sulaimānu dāwụda wa qāla yā ayyuhan-nāsu \'ullimnā manṭiqaṭ-ṭairi wa ụtīnā ming kulli syaī`, inna hāżā lahuwal-faḍlul-mubīn'),
(3175, 27, 17, 'وَحُشِرَ لِسُلَيْمٰنَ جُنُوْدُهٗ مِنَ الْجِنِّ وَالْاِنْسِ وَالطَّيْرِ فَهُمْ يُوْزَعُوْنَ', 'wa ḥusyira lisulaimāna junụduhụ minal-jinni wal-insi waṭ-ṭairi fa hum yụza\'ụn'),
(3176, 27, 18, 'حَتّٰىٓ اِذَآ اَتَوْا عَلٰى وَادِ النَّمْلِۙ قَالَتْ نَمْلَةٌ يّٰٓاَيُّهَا النَّمْلُ ادْخُلُوْا مَسٰكِنَكُمْۚ لَا يَحْطِمَنَّكُمْ سُلَيْمٰنُ وَجُنُوْدُهٗۙ وَهُمْ لَا يَشْعُرُوْنَ', 'hattā iżā atau \'alā wādin-namli qālat namlatuy yā ayyuhan-namludkhulụ masākinakum, lā yahṭimannakum sulaimānu wa junụduhụ wa hum lā yasy\'urụn'),
(3177, 27, 19, 'فَتَبَسَّمَ ضَاحِكًا مِّنْ قَوْلِهَا وَقَالَ رَبِّ اَوْزِعْنِيْٓ اَنْ اَشْكُرَ نِعْمَتَكَ الَّتِيْٓ اَنْعَمْتَ عَلَيَّ وَعَلٰى وَالِدَيَّ وَاَنْ اَعْمَلَ صَالِحًا تَرْضٰىهُ وَاَدْخِلْنِيْ بِرَحْمَتِكَ فِيْ عِبَادِكَ الصّٰلِحِيْنَ', 'fa tabassama ḍāḥikam ming qaulihā wa qāla rabbi auzi\'nī an asykura ni\'matakallatī an\'amta \'alayya wa \'alā wālidayya wa an a\'mala ṣāliḥan tarḍāhu wa adkhilnī biraḥmatika fī \'ibādikaṣ-ṣāliḥīn'),
(3178, 27, 20, 'وَتَفَقَّدَ الطَّيْرَ فَقَالَ مَا لِيَ لَآ اَرَى الْهُدْهُدَۖ اَمْ كَانَ مِنَ الْغَاۤىِٕبِيْنَ', 'wa tafaqqadaṭ-ṭaira fa qāla mā liya lā aral-hudhuda am kāna minal-gā`ibīn'),
(3179, 27, 21, 'لَاُعَذِّبَنَّهٗ عَذَابًا شَدِيْدًا اَوْ لَاَا۟ذْبَحَنَّهٗٓ اَوْ لَيَأْتِيَنِّيْ بِسُلْطٰنٍ مُّبِيْنٍ', 'la`u\'ażżibannahụ \'ażāban syadīdan au la`ażbaḥannahū au laya`tiyannī bisulṭānim mubīn'),
(3180, 27, 22, 'فَمَكَثَ غَيْرَ بَعِيْدٍ فَقَالَ اَحَطْتُّ بِمَا لَمْ تُحِطْ بِهٖ وَجِئْتُكَ مِنْ سَبَاٍ ۢبِنَبَاٍ يَّقِيْنٍ', 'fa makaṡa gaira ba\'īdin fa qāla aḥaṭtu bimā lam tuḥiṭ bihī wa ji`tuka min saba`im binaba`iy yaqīn'),
(3181, 27, 23, 'اِنِّيْ وَجَدْتُّ امْرَاَةً تَمْلِكُهُمْ وَاُوْتِيَتْ مِنْ كُلِّ شَيْءٍ وَّلَهَا عَرْشٌ عَظِيْمٌ', 'innī wajattumra`atan tamlikuhum wa ụtiyat ming kulli syai`iu wa lahā \'arsyun \'aẓīm'),
(3182, 27, 24, 'وَجَدْتُّهَا وَقَوْمَهَا يَسْجُدُوْنَ لِلشَّمْسِ مِنْ دُوْنِ اللّٰهِ وَزَيَّنَ لَهُمُ الشَّيْطٰنُ اَعْمَالَهُمْ فَصَدَّهُمْ عَنِ السَّبِيْلِ فَهُمْ لَا يَهْتَدُوْنَۙ', 'wajattuhā wa qaumahā yasjudụna lisy-syamsi min dụnillāhi wa zayyana lahumusy-syaiṭānu a\'mālahum fa ṣaddahum \'anis-sabīli fa hum lā yahtadụn'),
(3183, 27, 25, 'اَلَّا يَسْجُدُوْا لِلّٰهِ الَّذِيْ يُخْرِجُ الْخَبْءَ فِى السَّمٰوٰتِ وَالْاَرْضِ وَيَعْلَمُ مَا تُخْفُوْنَ وَمَا تُعْلِنُوْنَ', 'allā yasjudụ lillāhillażī yukhrijul-khab`a fis-samāwāti wal-arḍi wa ya\'lamu mā tukhfụna wa mā tu\'linụn'),
(3184, 27, 26, 'اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَۙ رَبُّ الْعَرْشِ الْعَظِيْمِ ۩', 'allāhu lā ilāha illā huwa rabbul-\'arsyil-\'aẓīm'),
(3185, 27, 27, '۞ قَالَ سَنَنْظُرُ اَصَدَقْتَ اَمْ كُنْتَ مِنَ الْكٰذِبِيْنَ', 'qāla sananẓuru a ṣadaqta am kunta minal-kāżibīn'),
(3186, 27, 28, 'اِذْهَبْ بِّكِتٰبِيْ هٰذَا فَاَلْقِهْ اِلَيْهِمْ ثُمَّ تَوَلَّ عَنْهُمْ فَانْظُرْ مَاذَا يَرْجِعُوْنَ', 'iż-hab bikitābī hāżā fa alqih ilaihim ṡumma tawalla \'an-hum fanẓur māżā yarji\'ụn'),
(3187, 27, 29, 'قَالَتْ يٰٓاَيُّهَا الْمَلَؤُا اِنِّيْٓ اُلْقِيَ اِلَيَّ كِتٰبٌ كَرِيْمٌ', 'qālat yā ayyuhal-mala`u innī ulqiya ilayya kitābung karīm'),
(3188, 27, 30, 'اِنَّهٗ مِنْ سُلَيْمٰنَ وَاِنَّهٗ بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيْمِ ۙ', 'innahụ min sulaimāna wa innahụ bismillāhir-raḥmānir-raḥīm'),
(3189, 27, 31, 'اَلَّا تَعْلُوْا عَلَيَّ وَأْتُوْنِيْ مُسْلِمِيْنَ', 'allā ta\'lụ \'alayya wa`tụnī muslimīn'),
(3190, 27, 32, 'قَالَتْ يٰٓاَيُّهَا الْمَلَؤُا اَفْتُوْنِيْ فِيْٓ اَمْرِيْۚ مَا كُنْتُ قَاطِعَةً اَمْرًا حَتّٰى تَشْهَدُوْنِ', 'qālat yā ayyuhal-mala`u aftụnī fī amrī, mā kuntu qāṭi\'atan amran ḥattā tasy-hadụn'),
(3191, 27, 33, 'قَالُوْا نَحْنُ اُولُوْا قُوَّةٍ وَّاُولُوْا بَأْسٍ شَدِيْدٍ ەۙ وَّالْاَمْرُ اِلَيْكِ فَانْظُرِيْ مَاذَا تَأْمُرِيْنَ', 'qālụ naḥnu ulụ quwwatiw wa ulụ ba`sin syadīdiw wal-amru ilaiki fanẓurī māżā ta`murīn'),
(3192, 27, 34, 'قَالَتْ اِنَّ الْمُلُوْكَ اِذَا دَخَلُوْا قَرْيَةً اَفْسَدُوْهَا وَجَعَلُوْٓا اَعِزَّةَ اَهْلِهَآ اَذِلَّةً ۚوَكَذٰلِكَ يَفْعَلُوْنَ', 'qālat innal-mulụka iżā dakhalụ qaryatan afsadụhā wa ja\'alū a\'izzata ahlihā ażillah, wa każālika yaf\'alụn'),
(3193, 27, 35, 'وَاِنِّيْ مُرْسِلَةٌ اِلَيْهِمْ بِهَدِيَّةٍ فَنٰظِرَةٌ ۢبِمَ يَرْجِعُ الْمُرْسَلُوْنَ', 'wa innī mursilatun ilaihim bihadiyyatin fa nāẓiratum bima yarji\'ul-mursalụn'),
(3194, 27, 36, 'فَلَمَّا جَاۤءَ سُلَيْمٰنَ قَالَ اَتُمِدُّوْنَنِ بِمَالٍ فَمَآ اٰتٰىنِ َۧ اللّٰهُ خَيْرٌ مِّمَّآ اٰتٰىكُمْۚ بَلْ اَنْتُمْ بِهَدِيَّتِكُمْ تَفْرَحُوْنَ', 'fa lammā jā`a sulaimāna qāla a tumiddụnani bimālin fa mā ātāniyallāhu khairum mimmā ātākum, bal antum bihadiyyatikum tafraḥụn'),
(3195, 27, 37, 'اِرْجِعْ اِلَيْهِمْ فَلَنَأْتِيَنَّهُمْ بِجُنُوْدٍ لَّا قِبَلَ لَهُمْ بِهَا وَلَنُخْرِجَنَّهُمْ مِّنْهَآ اَذِلَّةً وَّهُمْ صَاغِرُوْنَ', 'irji\' ilaihim falana`tiyannahum bijunụdil lā qibala lahum bihā wa lanukhrijannahum min-hā ażillataw wa hum ṣāgirụn'),
(3196, 27, 38, 'قَالَ يٰٓاَيُّهَا الْمَلَؤُا اَيُّكُمْ يَأْتِيْنِيْ بِعَرْشِهَا قَبْلَ اَنْ يَّأْتُوْنِيْ مُسْلِمِيْنَ', 'qāla yā ayyuhal-mala`u ayyukum ya`tīnī bi\'arsyihā qabla ay ya`tụnī muslimīn'),
(3197, 27, 39, 'قَالَ عِفْرِيْتٌ مِّنَ الْجِنِّ اَنَا۠ اٰتِيْكَ بِهٖ قَبْلَ اَنْ تَقُوْمَ مِنْ مَّقَامِكَۚ وَاِنِّيْ عَلَيْهِ لَقَوِيٌّ اَمِيْنٌ', 'qāla \'ifrītum minal-jinni ana ātīka bihī qabla an taqụma mim maqāmik, wa innī \'alaihi laqawiyyun amīn'),
(3198, 27, 40, 'قَالَ الَّذِيْ عِنْدَهٗ عِلْمٌ مِّنَ الْكِتٰبِ اَنَا۠ اٰتِيْكَ بِهٖ قَبْلَ اَنْ يَّرْتَدَّ اِلَيْكَ طَرْفُكَۗ فَلَمَّا رَاٰهُ مُسْتَقِرًّا عِنْدَهٗ قَالَ هٰذَا مِنْ فَضْلِ رَبِّيْۗ لِيَبْلُوَنِيْٓ ءَاَشْكُرُ اَمْ اَكْفُرُۗ وَمَنْ شَكَرَ فَاِنَّمَا يَشْكُرُ لِنَفْسِهٖۚ وَمَنْ كَفَرَ فَاِنَّ رَبِّيْ غَنِيٌّ كَرِيْمٌ', 'qālallażī \'indahụ \'ilmum minal-kitābi ana ātīka bihī qabla ay yartadda ilaika ṭarfuk, fa lammā ra`āhu mustaqirran \'indahụ qāla hāżā min faḍli rabbī, liyabluwanī a asykuru am akfur, wa man syakara fa innamā yasykuru linafsih, wa mang kafara fa inna rabbī ganiyyung karīm'),
(3199, 27, 41, 'قَالَ نَكِّرُوْا لَهَا عَرْشَهَا نَنْظُرْ اَتَهْتَدِيْٓ اَمْ تَكُوْنُ مِنَ الَّذِيْنَ لَا يَهْتَدُوْنَ', 'qāla nakkirụ lahā \'arsyahā nanẓur a tahtadī am takụnu minallażīna lā yahtadụn'),
(3200, 27, 42, 'فَلَمَّا جَاۤءَتْ قِيْلَ اَهٰكَذَا عَرْشُكِۗ قَالَتْ كَاَنَّهٗ هُوَۚ وَاُوْتِيْنَا الْعِلْمَ مِنْ قَبْلِهَا وَكُنَّا مُسْلِمِيْنَ', 'fa lammā jā`at qīla a hākażā \'arsyuk, qālat ka`annahụ huw, wa ụtīnal-\'ilma ming qablihā wa kunnā muslimīn'),
(3201, 27, 43, 'وَصَدَّهَا مَا كَانَتْ تَّعْبُدُ مِنْ دُوْنِ اللّٰهِ ۗاِنَّهَا كَانَتْ مِنْ قَوْمٍ كٰفِرِيْنَ', 'wa ṣaddahā mā kānat ta\'budu min dụnillāh, innahā kānat ming qauming kāfirīn'),
(3202, 27, 44, 'قِيْلَ لَهَا ادْخُلِى الصَّرْحَۚ فَلَمَّا رَاَتْهُ حَسِبَتْهُ لُجَّةً وَّكَشَفَتْ عَنْ سَاقَيْهَاۗ قَالَ اِنَّهٗ صَرْحٌ مُّمَرَّدٌ مِّنْ قَوَارِيْرَ ەۗ قَالَتْ رَبِّ اِنِّيْ ظَلَمْتُ نَفْسِيْ وَاَسْلَمْتُ مَعَ سُلَيْمٰنَ لِلّٰهِ رَبِّ الْعٰلَمِيْنَ', 'qīla lahadkhuliṣ-ṣar-ḥ, fa lammā ra`at-hu ḥasibat-hu lujjataw wa kasyafat \'an sāqaihā, qāla innahụ ṣar-ḥum mumarradum ming qawārīr, qālat rabbi innī ẓalamtu nafsī wa aslamtu ma\'a sulaimāna lillāhi rabbil-\'ālamīn'),
(3203, 27, 45, 'وَلَقَدْ اَرْسَلْنَآ اِلٰى ثَمُوْدَ اَخَاهُمْ صٰلِحًا اَنِ اعْبُدُوا اللّٰهَ فَاِذَا هُمْ فَرِيْقٰنِ يَخْتَصِمُوْنَ', 'wa laqad arsalnā ilā ṡamụda akhāhum ṣāliḥan ani\'budullāha fa iżā hum farīqāni yakhtaṣimụn'),
(3204, 27, 46, 'قَالَ يٰقَوْمِ لِمَ تَسْتَعْجِلُوْنَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِۚ لَوْلَا تَسْتَغْفِرُوْنَ اللّٰهَ لَعَلَّكُمْ تُرْحَمُوْنَ', 'qāla yā qaumi lima tasta\'jilụna bis-sayyi`ati qablal-ḥasanah, lau lā tastagfirụnallāha la\'allakum tur-ḥamụn'),
(3205, 27, 47, 'قَالُوا اطَّيَّرْنَا بِكَ وَبِمَنْ مَّعَكَۗ قَالَ طٰۤىِٕرُكُمْ عِنْدَ اللّٰهِ بَلْ اَنْتُمْ قَوْمٌ تُفْتَنُوْنَ', 'qāluṭ ṭayyarnā bika wa bimam ma\'ak, qāla ṭā`irukum \'indallāhi bal antum qaumun tuftanụn'),
(3206, 27, 48, 'وَكَانَ فِى الْمَدِيْنَةِ تِسْعَةُ رَهْطٍ يُّفْسِدُوْنَ فِى الْاَرْضِ وَلَا يُصْلِحُوْنَ', 'wa kāna fil-madīnati tis\'atu rahṭiy yufsidụna fil-arḍi wa lā yuṣliḥụn'),
(3207, 27, 49, 'قَالُوْا تَقَاسَمُوْا بِاللّٰهِ لَنُبَيِّتَنَّهٗ وَاَهْلَهٗ ثُمَّ لَنَقُوْلَنَّ لِوَلِيِّهٖ مَا شَهِدْنَا مَهْلِكَ اَهْلِهٖ وَاِنَّا لَصٰدِقُوْنَ', 'qālụ taqāsamụ billāhi lanubayyitannahụ wa ahlahụ ṡumma lanaqụlanna liwaliyyihī mā syahidnā mahlika ahlihī wa innā laṣādiqụn'),
(3208, 27, 50, 'وَمَكَرُوْا مَكْرًا وَّمَكَرْنَا مَكْرًا وَّهُمْ لَا يَشْعُرُوْنَ', 'wa makarụ makraw wa makarnā makraw wa hum lā yasy\'urụn'),
(3209, 27, 51, 'فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ مَكْرِهِمْ اَنَّا دَمَّرْنٰهُمْ وَقَوْمَهُمْ اَجْمَعِيْنَ', 'fanẓur kaifa kāna \'āqibatu makrihim annā dammarnāhum wa qaumahum ajma\'īn'),
(3210, 27, 52, 'فَتِلْكَ بُيُوْتُهُمْ خَاوِيَةً ۢبِمَا ظَلَمُوْاۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّعْلَمُوْنَ', 'fa tilka buyụtuhum khāwiyatam bimā ẓalamụ, inna fī żālika la`āyatal liqaumiy ya\'lamụn'),
(3211, 27, 53, 'وَاَنْجَيْنَا الَّذِيْنَ اٰمَنُوْا وَكَانُوْا يَتَّقُوْنَ', 'wa anjainallażīna āmanụ wa kānụ yattaqụn'),
(3212, 27, 54, 'وَلُوْطًا اِذْ قَالَ لِقَوْمِهٖٓ اَتَأْتُوْنَ الْفَاحِشَةَ وَاَنْتُمْ تُبْصِرُوْنَ', 'wa lụṭan iż qāla liqaumihī a ta`tụnal-fāḥisyata wa antum tubṣirụn'),
(3213, 27, 55, 'اَىِٕنَّكُمْ لَتَأْتُوْنَ الرِّجَالَ شَهْوَةً مِّنْ دُوْنِ النِّسَاۤءِ ۗبَلْ اَنْتُمْ قَوْمٌ تَجْهَلُوْنَ', 'a innakum lata`tụnar-rijāla syahwatam min dụnin-nisā`, bal antum qaumun taj-halụn'),
(3214, 27, 56, '۞ فَمَا كَانَ جَوَابَ قَوْمِهٖٓ اِلَّآ اَنْ قَالُوْٓا اَخْرِجُوْٓا اٰلَ لُوْطٍ مِّنْ قَرْيَتِكُمْۙ اِنَّهُمْ اُنَاسٌ يَّتَطَهَّرُوْنَ', 'fa mā kāna jawāba qaumihī illā ang qālū akhrijū āla lụṭim ming qaryatikum innahum unāsuy yataṭahharụn'),
(3215, 27, 57, 'فَاَنْجَيْنٰهُ وَاَهْلَهٗٓ اِلَّا امْرَاَتَهٗ قَدَّرْنٰهَا مِنَ الْغٰبِرِيْنَ', 'fa anjaināhu wa ahlahū illamra`atahụ qaddarnāhā minal-gābirīn'),
(3216, 27, 58, 'وَاَمْطَرْنَا عَلَيْهِمْ مَّطَرًاۚ فَسَاۤءَ مَطَرُ الْمُنْذَرِيْنَ', 'wa amṭarnā \'alaihim maṭarā, fa sā`a maṭarul-munżarīn'),
(3217, 27, 59, 'قُلِ الْحَمْدُ لِلّٰهِ وَسَلٰمٌ عَلٰى عِبَادِهِ الَّذِيْنَ اصْطَفٰىۗ ءٰۤاللّٰهُ خَيْرٌ اَمَّا يُشْرِكُوْنَ ۔', 'qulil-ḥamdu lillāhi wa salāmun \'alā \'ibādihillażīnaṣṭafā, āllāhu khairun ammā yusyrikụn'),
(3218, 27, 60, 'اَمَّنْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَاَنْزَلَ لَكُمْ مِّنَ السَّمَاۤءِ مَاۤءً فَاَنْۢبَتْنَا بِهٖ حَدَاۤىِٕقَ ذَاتَ بَهْجَةٍۚ مَا كَانَ لَكُمْ اَنْ تُنْۢبِتُوْا شَجَرَهَاۗ ءَاِلٰهٌ مَّعَ اللّٰهِ ۗبَلْ هُمْ قَوْمٌ يَّعْدِلُوْنَ ۗ', 'am man khalaqas-samāwāti wal-arḍa wa anzala lakum minas-samā`i mā`an fa ambatnā bihī ḥadā`iqa żāta bahjah, mā kāna lakum an tumbitụ syajarahā, a ilāhum ma\'allāh, bal hum qaumuy ya\'dilụn'),
(3219, 27, 61, 'اَمَّنْ جَعَلَ الْاَرْضَ قَرَارًا وَّجَعَلَ خِلٰلَهَآ اَنْهٰرًا وَّجَعَلَ لَهَا رَوَاسِيَ وَجَعَلَ بَيْنَ الْبَحْرَيْنِ حَاجِزًاۗ ءَاِلٰهٌ مَّعَ اللّٰهِ ۗبَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَ ۗ', 'am man ja\'alal-arḍa qarāraw wa ja\'ala khilālahā an-hāraw wa ja\'ala lahā rawāsiya wa ja\'ala bainal-baḥraini ḥājizā, a ilāhum ma\'allāh, bal akṡaruhum lā ya\'lamụn'),
(3220, 27, 62, 'اَمَّنْ يُّجِيْبُ الْمُضْطَرَّ اِذَا دَعَاهُ وَيَكْشِفُ السُّوْۤءَ وَيَجْعَلُكُمْ خُلَفَاۤءَ الْاَرْضِۗ ءَاِلٰهٌ مَّعَ اللّٰهِ ۗقَلِيْلًا مَّا تَذَكَّرُوْنَۗ', 'am may yujībul-muḍṭarra iżā da\'āhu wa yaksyifus-sū`a wa yaj\'alukum khulafā`al-arḍ, a ilāhum ma\'allāh, qalīlam mā tażakkarụn'),
(3221, 27, 63, 'اَمَّنْ يَّهْدِيْكُمْ فِيْ ظُلُمٰتِ الْبَرِّ وَالْبَحْرِ وَمَنْ يُّرْسِلُ الرِّيٰحَ بُشْرًا ۢ بَيْنَ يَدَيْ رَحْمَتِهٖۗ ءَاِلٰهٌ مَّعَ اللّٰهِ ۗتَعٰلَى اللّٰهُ عَمَّا يُشْرِكُوْنَ', 'am may yahdīkum fī ẓulumātil-barri wal-baḥri wa may yursilur-riyāḥa busyram baina yadai raḥmatih, a ilāhum ma\'allāh, ta\'ālallāhu \'ammā yusyrikụn'),
(3222, 27, 64, 'اَمَّنْ يَّبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗ وَمَنْ يَّرْزُقُكُمْ مِّنَ السَّمَاۤءِ وَالْاَرْضِۗ ءَاِلٰهٌ مَّعَ اللّٰهِ ۗقُلْ هَاتُوْا بُرْهَانَكُمْ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'am may yabda`ul-khalqa ṡumma yu\'īduhụ wa may yarzuqukum minas-samā`i wal-arḍ, a ilāhum ma\'allāh, qul hātụ bur-hānakum ing kuntum ṣādiqīn'),
(3223, 27, 65, 'قُلْ لَّا يَعْلَمُ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِ الْغَيْبَ اِلَّا اللّٰهُ ۗوَمَا يَشْعُرُوْنَ اَيَّانَ يُبْعَثُوْنَ', 'qul lā ya\'lamu man fis-samāwāti wal-arḍil-gaiba illallāh, wa mā yasy\'urụna ayyāna yub\'aṡụn'),
(3224, 27, 66, 'بَلِ ادّٰرَكَ عِلْمُهُمْ فِى الْاٰخِرَةِۗ بَلْ هُمْ فِيْ شَكٍّ مِّنْهَاۗ بَلْ هُمْ مِّنْهَا عَمُوْنَ', 'baliddāraka \'ilmuhum fil-ākhirah, bal hum fī syakkim min-hā, bal hum min-hā \'amụn'),
(3225, 27, 67, 'وَقَالَ الَّذِيْنَ كَفَرُوْٓا ءَاِذَا كُنَّا تُرَابًا وَّاٰبَاۤؤُنَآ اَىِٕنَّا لَمُخْرَجُوْنَ', 'wa qālallażīna kafarū a iżā kunnā turābaw wa ābā`unā a innā lamukhrajụn'),
(3226, 27, 68, 'لَقَدْ وُعِدْنَا هٰذَا نَحْنُ وَاٰبَاۤؤُنَا مِنْ قَبْلُۙ اِنْ هٰذَآ اِلَّآ اَسَاطِيْرُ الْاَوَّلِيْنَ', 'laqad wu\'idnā hāżā naḥnu wa ābā`unā ming qablu in hāżā illā asāṭīrul-awwalīn'),
(3227, 27, 69, 'قُلْ سِيْرُوْا فِى الْاَرْضِ فَانْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الْمُجْرِمِيْنَ', 'qul sīrụ fil-arḍi fanẓurụ kaifa kāna \'āqibatul-mujrimīn');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(3228, 27, 70, 'وَلَا تَحْزَنْ عَلَيْهِمْ وَلَا تَكُنْ فِيْ ضَيْقٍ مِّمَّا يَمْكُرُوْنَ', 'wa lā taḥzan \'alaihim wa lā takun fī ḍaiqim mimmā yamkurụn'),
(3229, 27, 71, 'وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'wa yaqụlụna matā hāżal-wa\'du ing kuntum ṣādiqīn'),
(3230, 27, 72, 'قُلْ عَسٰٓى اَنْ يَّكُوْنَ رَدِفَ لَكُمْ بَعْضُ الَّذِيْ تَسْتَعْجِلُوْنَ', 'qul \'asā ay yakụna radifa lakum ba\'ḍullażī tasta\'jilụn'),
(3231, 27, 73, 'وَاِنَّ رَبَّكَ لَذُوْ فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَشْكُرُوْنَ', 'wa inna rabbaka lażụ faḍlin \'alan-nāsi wa lākinna akṡarahum lā yasykurụn'),
(3232, 27, 74, 'وَاِنَّ رَبَّكَ لَيَعْلَمُ مَا تُكِنُّ صُدُوْرُهُمْ وَمَا يُعْلِنُوْنَ', 'wa inna rabbaka laya\'lamu mā tukinnu ṣudụruhum wa mā yu\'linụn'),
(3233, 27, 75, 'وَمَا مِنْ غَاۤىِٕبَةٍ فِى السَّمَاۤءِ وَالْاَرْضِ اِلَّا فِيْ كِتٰبٍ مُّبِيْنٍ', 'wa mā min gā`ibatin fis-samā`i wal-arḍi illā fī kitābim mubīn'),
(3234, 27, 76, 'اِنَّ هٰذَا الْقُرْاٰنَ يَقُصُّ عَلٰى بَنِيْٓ اِسْرَاۤءِيْلَ اَكْثَرَ الَّذِيْ هُمْ فِيْهِ يَخْتَلِفُوْنَ', 'inna hāżal-qur`āna yaquṣṣu \'alā banī isrā`īla akṡarallażī hum fīhi yakhtalifụn'),
(3235, 27, 77, 'وَاِنَّهٗ لَهُدًى وَّرَحْمَةٌ لِّلْمُؤْمِنِيْنَ', 'wa innahụ lahudaw wa raḥmatul lil-mu`minīn'),
(3236, 27, 78, 'اِنَّ رَبَّكَ يَقْضِيْ بَيْنَهُمْ بِحُكْمِهٖۚ وَهُوَ الْعَزِيْزُ الْعَلِيْمُۚ', 'inna rabbaka yaqḍī bainahum biḥukmih, wa huwal-\'azīzul-\'alīm'),
(3237, 27, 79, 'فَتَوَكَّلْ عَلَى اللّٰهِ ۗاِنَّكَ عَلَى الْحَقِّ الْمُبِيْنِ', 'fa tawakkal \'alallāh, innaka \'alal-ḥaqqil-mubīn'),
(3238, 27, 80, 'اِنَّكَ لَا تُسْمِعُ الْمَوْتٰى وَلَا تُسْمِعُ الصُّمَّ الدُّعَاۤءَ اِذَا وَلَّوْا مُدْبِرِيْنَ', 'innaka lā tusmi\'ul-mautā wa lā tusmi\'uṣ-ṣummad-du\'ā`a iżā wallau mudbirīn'),
(3239, 27, 81, 'وَمَآ اَنْتَ بِهٰدِى الْعُمْيِ عَنْ ضَلٰلَتِهِمْۗ اِنْ تُسْمِعُ اِلَّا مَنْ يُّؤْمِنُ بِاٰيٰتِنَا فَهُمْ مُّسْلِمُوْنَ', 'wa mā anta bihādil-\'umyi \'an ḍalālatihim, in tusmi\'u illā may yu`minu bi`āyātinā fa hum muslimụn'),
(3240, 27, 82, '۞ وَاِذَا وَقَعَ الْقَوْلُ عَلَيْهِمْ اَخْرَجْنَا لَهُمْ دَاۤبَّةً مِّنَ الْاَرْضِ تُكَلِّمُهُمْ اَنَّ النَّاسَ كَانُوْا بِاٰيٰتِنَا لَا يُوْقِنُوْنَ', 'wa iżā waqa\'al-qaulu \'alaihim akhrajnā lahum dābbatam minal-arḍi tukallimuhum annan-nāsa kānụ bi`āyātinā lā yụqinụn'),
(3241, 27, 83, 'وَيَوْمَ نَحْشُرُ مِنْ كُلِّ اُمَّةٍ فَوْجًا مِّمَّنْ يُّكَذِّبُ بِاٰيٰتِنَا فَهُمْ يُوْزَعُوْنَ', 'wa yauma naḥsyuru ming kulli ummatin faujam mim may yukażżibu bi`āyātinā fa hum yụza\'ụn'),
(3242, 27, 84, 'حَتّٰٓى اِذَا جَاۤءُوْ قَالَ اَكَذَّبْتُمْ بِاٰيٰتِيْ وَلَمْ تُحِيْطُوْا بِهَا عِلْمًا اَمَّاذَا كُنْتُمْ تَعْمَلُوْنَ', 'ḥattā iżā jā`ụ qāla a każżabtum bi`āyātī wa lam tuḥīṭụ bihā \'ilman ammāżā kuntum ta\'malụn'),
(3243, 27, 85, 'وَوَقَعَ الْقَوْلُ عَلَيْهِمْ بِمَا ظَلَمُوْا فَهُمْ لَا يَنْطِقُوْنَ', 'wa waqa\'al-qaulu \'alaihim bimā ẓalamụ fa hum lā yanṭiqụn'),
(3244, 27, 86, 'اَلَمْ يَرَوْا اَنَّا جَعَلْنَا الَّيْلَ لِيَسْكُنُوْا فِيْهِ وَالنَّهَارَ مُبْصِرًاۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ', 'a lam yarau annā ja\'alnal-laila liyaskunụ fīhi wan-nahāra mubṣirā, inna fī żālika la`āyātil liqaumiy yu`minụn'),
(3245, 27, 87, 'وَيَوْمَ يُنْفَخُ فِى الصُّوْرِ فَفَزِعَ مَنْ فِى السَّمٰوٰتِ وَمَنْ فِى الْاَرْضِ اِلَّا مَنْ شَاۤءَ اللّٰهُ ۗوَكُلٌّ اَتَوْهُ دَاخِرِيْنَ', 'wa yauma yunfakhu fiṣ-ṣụri fa fazi\'a man fis-samāwāti wa man fil-arḍi illā man syā`allāh, wa kullun atauhu dākhirīn'),
(3246, 27, 88, 'وَتَرَى الْجِبَالَ تَحْسَبُهَا جَامِدَةً وَّهِيَ تَمُرُّ مَرَّ السَّحَابِۗ صُنْعَ اللّٰهِ الَّذِيْٓ اَتْقَنَ كُلَّ شَيْءٍۗ اِنَّهٗ خَبِيْرٌ ۢبِمَا تَفْعَلُوْنَ', 'wa taral-jibāla taḥsabuhā jāmidataw wa hiya tamurru marras-saḥāb, ṣun\'allāhillażī atqana kulla syaī`, innahụ khabīrum bimā taf\'alụn'),
(3247, 27, 89, 'مَنْ جَاۤءَ بِالْحَسَنَةِ فَلَهٗ خَيْرٌ مِّنْهَاۚ وَهُمْ مِّنْ فَزَعٍ يَّوْمَىِٕذٍ اٰمِنُوْنَ', 'man jā`a bil-ḥasanati fa lahụ khairum min-hā, wa hum min faza\'iy yauma`iżin āminụn'),
(3248, 27, 90, 'وَمَنْ جَاۤءَ بِالسَّيِّئَةِ فَكُبَّتْ وُجُوْهُهُمْ فِى النَّارِۗ هَلْ تُجْزَوْنَ اِلَّا مَا كُنْتُمْ تَعْمَلُوْنَ', 'wa man jā`a bis-sayyi`ati fa kubbat wujụhuhum fin-nār, hal tujzauna illā mā kuntum ta\'malụn'),
(3249, 27, 91, 'اِنَّمَآ اُمِرْتُ اَنْ اَعْبُدَ رَبَّ هٰذِهِ الْبَلْدَةِ الَّذِيْ حَرَّمَهَا وَلَهٗ كُلُّ شَيْءٍ وَّاُمِرْتُ اَنْ اَكُوْنَ مِنَ الْمُسْلِمِيْنَ ۙ', 'innamā umirtu an a\'buda rabba hāżihil-baldatillażī ḥarramahā wa lahụ kullu syai`iw wa umirtu an akụna minal-muslimīn'),
(3250, 27, 92, 'وَاَنْ اَتْلُوَا الْقُرْاٰنَ ۚفَمَنِ اهْتَدٰى فَاِنَّمَا يَهْتَدِيْ لِنَفْسِهٖۚ وَمَنْ ضَلَّ فَقُلْ اِنَّمَآ اَنَا۠ مِنَ الْمُنْذِرِيْنَ', 'wa an atluwal-qur`ān, fa manihtadā fa innamā yahtadī linafsih, wa man ḍalla fa qul innamā ana minal-munżirīn'),
(3251, 27, 93, 'وَقُلِ الْحَمْدُ لِلّٰهِ سَيُرِيْكُمْ اٰيٰتِهٖ فَتَعْرِفُوْنَهَاۗ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ', 'wa qulil-ḥamdu lillāhi sayurīkum āyātihī fa ta\'rifụnahā, wa mā rabbuka bigāfilin \'ammā ta\'malụn'),
(3252, 28, 1, ' طٰسۤمّۤ', 'ṭā sīm mīm'),
(3253, 28, 2, 'تِلْكَ اٰيٰتُ الْكِتٰبِ الْمُبِيْنِ', 'tilka āyātul-kitābil-mubīn'),
(3254, 28, 3, 'نَتْلُوْا عَلَيْكَ مِنْ نَّبَاِ مُوْسٰى وَفِرْعَوْنَ بِالْحَقِّ لِقَوْمٍ يُّؤْمِنُوْنَ', 'natlụ \'alaika min naba`i mụsā wa fir\'auna bil-ḥaqqi liqaumiy yu`minụn'),
(3255, 28, 4, 'اِنَّ فِرْعَوْنَ عَلَا فِى الْاَرْضِ وَجَعَلَ اَهْلَهَا شِيَعًا يَّسْتَضْعِفُ طَاۤىِٕفَةً مِّنْهُمْ يُذَبِّحُ اَبْنَاۤءَهُمْ وَيَسْتَحْيٖ نِسَاۤءَهُمْ ۗاِنَّهٗ كَانَ مِنَ الْمُفْسِدِيْنَ', 'inna fir\'auna \'alā fil-arḍi wa ja\'ala ahlahā syiya\'ay yastaḍ\'ifu ṭā`ifatam min-hum yużabbiḥu abnā`ahum wa yastaḥyī nisā`ahum, innahụ kāna minal-mufsidīn'),
(3256, 28, 5, 'وَنُرِيْدُ اَنْ نَّمُنَّ عَلَى الَّذِيْنَ اسْتُضْعِفُوْا فِى الْاَرْضِ وَنَجْعَلَهُمْ اَىِٕمَّةً وَّنَجْعَلَهُمُ الْوٰرِثِيْنَ ۙ', 'wa nurīdu an namunna \'alallażīnastuḍ\'ifụ fil-arḍi wa naj\'alahum a immataw wa naj\'alahumul-wāriṡīn'),
(3257, 28, 6, 'وَنُمَكِّنَ لَهُمْ فِى الْاَرْضِ وَنُرِيَ فِرْعَوْنَ وَهَامٰنَ وَجُنُوْدَهُمَا مِنْهُمْ مَّا كَانُوْا يَحْذَرُوْنَ', 'wa numakkina lahum fil-arḍi wa nuriya fir\'auna wa hāmāna wa junụdahumā min-hum mā kānụ yaḥżarụn'),
(3258, 28, 7, 'وَاَوْحَيْنَآ اِلٰٓى اُمِّ مُوْسٰٓى اَنْ اَرْضِعِيْهِۚ فَاِذَا خِفْتِ عَلَيْهِ فَاَلْقِيْهِ فِى الْيَمِّ وَلَا تَخَافِيْ وَلَا تَحْزَنِيْ ۚاِنَّا رَاۤدُّوْهُ اِلَيْكِ وَجَاعِلُوْهُ مِنَ الْمُرْسَلِيْنَ', 'wa auḥainā ilā ummi mụsā an arḍi\'īh, fa iżā khifti \'alaihi fa alqīhi fil-yammi wa lā takhāfī wa lā taḥzanī, innā rāddụhu ilaiki wa jā\'ilụhu minal-mursalīn'),
(3259, 28, 8, 'فَالْتَقَطَهٗٓ اٰلُ فِرْعَوْنَ لِيَكُوْنَ لَهُمْ عَدُوًّا وَّحَزَنًاۗ اِنَّ فِرْعَوْنَ وَهَامٰنَ وَجُنُوْدَهُمَا كَانُوْا خٰطِـِٕيْنَ', 'faltaqaṭahū ālu fir\'auna liyakụna lahum \'aduwwaw wa ḥazanā, inna fir\'auna wa hāmāna wa junụdahumā kānụ khāṭi`īn'),
(3260, 28, 9, 'وَقَالَتِ امْرَاَتُ فِرْعَوْنَ قُرَّتُ عَيْنٍ لِّيْ وَلَكَۗ لَا تَقْتُلُوْهُ ۖعَسٰٓى اَنْ يَّنْفَعَنَآ اَوْ نَتَّخِذَهٗ وَلَدًا وَّهُمْ لَا يَشْعُرُوْنَ', 'wa qālatimra`atu fir\'auna qurratu \'ainil lī wa lak, lā taqtulụhu \'asā ay yanfa\'anā au nattakhiżahụ waladaw wa hum lā yasy\'urụn'),
(3261, 28, 10, 'وَاَصْبَحَ فُؤَادُ اُمِّ مُوْسٰى فٰرِغًاۗ اِنْ كَادَتْ لَتُبْدِيْ بِهٖ لَوْلَآ اَنْ رَّبَطْنَا عَلٰى قَلْبِهَا لِتَكُوْنَ مِنَ الْمُؤْمِنِيْنَ', 'wa aṣbaḥa fu`ādu ummi mụsā fārigā, ing kādat latubdī bihī lau lā ar rabaṭnā \'alā qalbihā litakụna minal-mu`minīn'),
(3262, 28, 11, 'وَقَالَتْ لِاُخْتِهٖ قُصِّيْهِۗ فَبَصُرَتْ بِهٖ عَنْ جُنُبٍ وَّهُمْ لَا يَشْعُرُوْنَ ۙ', 'wa qālat li`ukhtihī quṣṣīh, fa baṣurat bihī \'an junubiw wa hum lā yasy\'urụn'),
(3263, 28, 12, '۞ وَحَرَّمْنَا عَلَيْهِ الْمَرَاضِعَ مِنْ قَبْلُ فَقَالَتْ هَلْ اَدُلُّكُمْ عَلٰٓى اَهْلِ بَيْتٍ يَّكْفُلُوْنَهٗ لَكُمْ وَهُمْ لَهٗ نَاصِحُوْنَ', 'wa ḥarramnā \'alaihil-marāḍi\'a ming qablu fa qālat hal adullukum \'alā ahli baitiy yakfulụnahụ lakum wa hum lahụ nāṣiḥụn'),
(3264, 28, 13, 'فَرَدَدْنٰهُ اِلٰٓى اُمِّهٖ كَيْ تَقَرَّ عَيْنُهَا وَلَا تَحْزَنَ وَلِتَعْلَمَ اَنَّ وَعْدَ اللّٰهِ حَقٌّ وَّلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ', 'fa radadnāhu ilā ummihī kai taqarra \'ainuhā wa lā taḥzana wa lita\'lama anna wa\'dallāhi ḥaqquw wa lākinna akṡarahum lā ya\'lamụn'),
(3265, 28, 14, 'وَلَمَّا بَلَغَ اَشُدَّهٗ وَاسْتَوٰىٓ اٰتَيْنٰهُ حُكْمًا وَّعِلْمًاۗ وَكَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ', 'wa lammā balaga asyuddahụ wastawā ātaināhu ḥukmaw wa \'ilmā, wa każālika najzil-muḥsinīn'),
(3266, 28, 15, 'وَدَخَلَ الْمَدِيْنَةَ عَلٰى حِيْنِ غَفْلَةٍ مِّنْ اَهْلِهَا فَوَجَدَ فِيْهَا رَجُلَيْنِ يَقْتَتِلٰنِۖ هٰذَا مِنْ شِيْعَتِهٖ وَهٰذَا مِنْ عَدُوِّهٖۚ فَاسْتَغَاثَهُ الَّذِيْ مِنْ شِيْعَتِهٖ عَلَى الَّذِيْ مِنْ عَدُوِّهٖ ۙفَوَكَزَهٗ مُوْسٰى فَقَضٰى عَلَيْهِۖ قَالَ هٰذَا مِنْ عَمَلِ الشَّيْطٰنِۗ اِنَّهٗ عَدُوٌّ مُّضِلٌّ مُّبِيْنٌ', 'wa dakhalal-madīnata \'alā ḥīni gaflatim min ahlihā fa wajada fīhā rajulaini yaqtatilāni hāżā min syī\'atihī wa hāżā min \'aduwwih, fastagāṡahullażī min syī\'atihī \'alallażī min \'aduwwihī fa wakazahụ mụsā fa qaḍā \'alaihi qāla hāżā min \'amalisy-syaiṭān, innahụ \'aduwwum muḍillum mubīn'),
(3267, 28, 16, 'قَالَ رَبِّ اِنِّيْ ظَلَمْتُ نَفْسِيْ فَاغْفِرْ لِيْ فَغَفَرَ لَهٗ ۗاِنَّهٗ هُوَ الْغَفُوْرُ الرَّحِيْمُ', 'qāla rabbi innī ẓalamtu nafsī fagfir lī fa gafara lah, innahụ huwal-gafụrur-raḥīm'),
(3268, 28, 17, 'قَالَ رَبِّ بِمَآ اَنْعَمْتَ عَلَيَّ فَلَنْ اَكُوْنَ ظَهِيْرًا لِّلْمُجْرِمِيْنَ', 'qāla rabbi bimā an\'amta \'alayya fa lan akụna ẓahīral lil-mujrimīn'),
(3269, 28, 18, 'فَاَصْبَحَ فِى الْمَدِيْنَةِ خَاۤىِٕفًا يَّتَرَقَّبُ فَاِذَا الَّذِى اسْتَنْصَرَهٗ بِالْاَمْسِ يَسْتَصْرِخُهٗ ۗقَالَ لَهٗ مُوْسٰٓى اِنَّكَ لَغَوِيٌّ مُّبِيْنٌ', 'fa aṣbaḥa fil-madīnati khā`ifay yataraqqabu fa iżallażistanṣarahụ bil-amsi yastaṣrikhuh, qāla lahụ mụsā innaka lagawiyyum mubīn'),
(3270, 28, 19, 'فَلَمَّآ اَنْ اَرَادَ اَنْ يَّبْطِشَ بِالَّذِيْ هُوَ عَدُوٌّ لَّهُمَاۙ قَالَ يٰمُوْسٰٓى اَتُرِيْدُ اَنْ تَقْتُلَنِيْ كَمَا قَتَلْتَ نَفْسًاۢ بِالْاَمْسِۖ اِنْ تُرِيْدُ اِلَّآ اَنْ تَكُوْنَ جَبَّارًا فِى الْاَرْضِ وَمَا تُرِيْدُ اَنْ تَكُوْنَ مِنَ الْمُصْلِحِيْنَ', 'fa lammā an arāda ay yabṭisya billażī huwa \'aduwwul lahumā qāla yā mụsā a turīdu an taqtulanī kamā qatalta nafsam bil-amsi in turīdu illā an takụna jabbāran fil-arḍi wa mā turīdu an takụna minal-muṣliḥīn'),
(3271, 28, 20, 'وَجَاۤءَ رَجُلٌ مِّنْ اَقْصَى الْمَدِيْنَةِ يَسْعٰىۖ قَالَ يٰمُوْسٰٓى اِنَّ الْمَلَاَ يَأْتَمِرُوْنَ بِكَ لِيَقْتُلُوْكَ فَاخْرُجْ اِنِّيْ لَكَ مِنَ النّٰصِحِيْنَ', 'wa jā`a rajulum min aqṣal-madīnati yas\'ā qāla yā mụsā innal-mala`a ya`tamirụna bika liyaqtulụka fakhruj innī laka minan-nāṣiḥīn'),
(3272, 28, 21, 'فَخَرَجَ مِنْهَا خَاۤىِٕفًا يَّتَرَقَّبُ ۖقَالَ رَبِّ نَجِّنِيْ مِنَ الْقَوْمِ الظّٰلِمِيْنَ', 'fa kharaja min-hā khā`ifay yataraqqabu qāla rabbi najjinī minal-qaumiẓ-ẓālimīn'),
(3273, 28, 22, 'وَلَمَّا تَوَجَّهَ تِلْقَاۤءَ مَدْيَنَ قَالَ عَسٰى رَبِّيْٓ اَنْ يَّهْدِيَنِيْ سَوَاۤءَ السَّبِيْلِ', 'wa lammā tawajjaha tilqā`a madyana qāla \'asā rabbī ay yahdiyanī sawā`as-sabīl'),
(3274, 28, 23, 'وَلَمَّا وَرَدَ مَاۤءَ مَدْيَنَ وَجَدَ عَلَيْهِ اُمَّةً مِّنَ النَّاسِ يَسْقُوْنَ ەۖ وَوَجَدَ مِنْ دُوْنِهِمُ امْرَاَتَيْنِ تَذُوْدٰنِۚ قَالَ مَا خَطْبُكُمَا ۗقَالَتَا لَا نَسْقِيْ حَتّٰى يُصْدِرَ الرِّعَاۤءُ وَاَبُوْنَا شَيْخٌ كَبِيْرٌ', 'wa lammā warada mā`a madyana wajada \'alaihi ummatam minan-nāsi yasqụna wa wajada min dụnihimumra`ataini tażụdān, qāla mā khaṭbukumā, qālatā lā nasqī ḥattā yuṣdirar-ri\'ā`u wa abụnā syaikhung kabīr'),
(3275, 28, 24, 'فَسَقٰى لَهُمَا ثُمَّ تَوَلّٰىٓ اِلَى الظِّلِّ فَقَالَ رَبِّ اِنِّيْ لِمَآ اَنْزَلْتَ اِلَيَّ مِنْ خَيْرٍ فَقِيْرٌ', 'fa saqā lahumā ṡumma tawallā ilaẓ-ẓilli fa qāla rabbi innī limā anzalta ilayya min khairin faqīr'),
(3276, 28, 25, 'فَجَاۤءَتْهُ اِحْدٰىهُمَا تَمْشِيْ عَلَى اسْتِحْيَاۤءٍ ۖقَالَتْ اِنَّ اَبِيْ يَدْعُوْكَ لِيَجْزِيَكَ اَجْرَ مَا سَقَيْتَ لَنَاۗ فَلَمَّا جَاۤءَهٗ وَقَصَّ عَلَيْهِ الْقَصَصَۙ قَالَ لَا تَخَفْۗ نَجَوْتَ مِنَ الْقَوْمِ الظّٰلِمِيْنَ', 'fa jā`at-hu iḥdāhumā tamsyī \'alastiḥyā`ing qālat inna abī yad\'ụka liyajziyaka ajra mā saqaita lanā, fa lammā jā`ahụ wa qaṣṣa \'alaihil-qaṣaṣa qāla lā takhaf, najauta minal-qaumiẓ-ẓālimīn'),
(3277, 28, 26, 'قَالَتْ اِحْدٰىهُمَا يٰٓاَبَتِ اسْتَأْجِرْهُ ۖاِنَّ خَيْرَ مَنِ اسْتَأْجَرْتَ الْقَوِيُّ الْاَمِيْنُ', 'qālat iḥdāhumā yā abatista`jir-hu inna khaira manista`jartal-qawiyyul-amīn'),
(3278, 28, 27, 'قَالَ اِنِّيْٓ اُرِيْدُ اَنْ اُنْكِحَكَ اِحْدَى ابْنَتَيَّ هٰتَيْنِ عَلٰٓى اَنْ تَأْجُرَنِيْ ثَمٰنِيَ حِجَجٍۚ فَاِنْ اَتْمَمْتَ عَشْرًا فَمِنْ عِنْدِكَۚ وَمَآ اُرِيْدُ اَنْ اَشُقَّ عَلَيْكَۗ سَتَجِدُنِيْٓ اِنْ شَاۤءَ اللّٰهُ مِنَ الصّٰلِحِيْنَ', 'qāla innī urīdu an ungkiḥaka iḥdabnatayya hātaini \'alā an ta`juranī ṡamāniya ḥijaj, fa in atmamta \'asyran fa min \'indik, wa mā urīdu an asyuqqa \'alaīk, satajidunī in syā`allāhu minaṣ-ṣāliḥīn'),
(3279, 28, 28, 'قَالَ ذٰلِكَ بَيْنِيْ وَبَيْنَكَۗ اَيَّمَا الْاَجَلَيْنِ قَضَيْتُ فَلَا عُدْوَانَ عَلَيَّۗ وَاللّٰهُ عَلٰى مَا نَقُوْلُ وَكِيْلٌ', 'qāla żālika bainī wa bainak, ayyamal-ajalaini qaḍaitu fa lā \'udwāna \'alayy, wallāhu \'alā mā naqụlu wakīl'),
(3280, 28, 29, '۞ فَلَمَّا قَضٰى مُوْسَى الْاَجَلَ وَسَارَ بِاَهْلِهٖٓ اٰنَسَ مِنْ جَانِبِ الطُّوْرِ نَارًاۗ قَالَ لِاَهْلِهِ امْكُثُوْٓا اِنِّيْٓ اٰنَسْتُ نَارًا لَّعَلِّيْٓ اٰتِيْكُمْ مِّنْهَا بِخَبَرٍ اَوْ جَذْوَةٍ مِّنَ النَّارِ لَعَلَّكُمْ تَصْطَلُوْنَ', 'fa lammā qaḍā mụsal-ajala wa sāra bi`ahlihī ānasa min jānibiṭ-ṭụri nārā, qāla li`ahlihimkuṡū innī ānastu nāral-la\'allī ātīkum min-hā bikhabarin au jażwatim minan-nāri la\'allakum taṣṭalụn'),
(3281, 28, 30, 'فَلَمَّآ اَتٰىهَا نُوْدِيَ مِنْ شَاطِئِ الْوَادِ الْاَيْمَنِ فِى الْبُقْعَةِ الْمُبٰرَكَةِ مِنَ الشَّجَرَةِ اَنْ يّٰمُوْسٰٓى اِنِّيْٓ اَنَا اللّٰهُ رَبُّ الْعٰلَمِيْنَ ۙ', 'fa lammā atāhā nụdiya min syāṭi`il-wādil-aimani fil-buq\'atil-mubārakati minasy-syajarati ay yā mụsā innī anallāhu rabbul-\'ālamīn'),
(3282, 28, 31, 'وَاَنْ اَلْقِ عَصَاكَ ۗفَلَمَّا رَاٰهَا تَهْتَزُّ كَاَنَّهَا جَاۤنٌّ وَّلّٰى مُدْبِرًا وَّلَمْ يُعَقِّبْۗ يٰمُوْسٰٓى اَقْبِلْ وَلَا تَخَفْۗ اِنَّكَ مِنَ الْاٰمِنِيْنَ', 'wa an alqi \'aṣāk, fa lammā ra`āhā tahtazzu ka`annahā jānnuw wallā mudbiraw wa lam yu\'aqqib, yā mụsā aqbil wa lā takhaf, innaka minal-āminīn'),
(3283, 28, 32, 'اُسْلُكْ يَدَكَ فِيْ جَيْبِكَ تَخْرُجْ بَيْضَاۤءَ مِنْ غَيْرِ سُوْۤءٍ ۖوَّاضْمُمْ اِلَيْكَ جَنَاحَكَ مِنَ الرَّهْبِ فَذٰنِكَ بُرْهَانٰنِ مِنْ رَّبِّكَ اِلٰى فِرْعَوْنَ وَمَلَا۟ىِٕهٖۗ اِنَّهُمْ كَانُوْا قَوْمًا فٰسِقِيْنَ', 'usluk yadaka fī jaibika takhruj baiḍā`a min gairi sū`iw waḍmum ilaika janāḥaka minar-rahbi fażānika bur-hānāni mir rabbika ilā fir\'auna wa mala`ih, innahum kānụ qauman fāsiqīn'),
(3284, 28, 33, 'قَالَ رَبِّ اِنِّيْ قَتَلْتُ مِنْهُمْ نَفْسًا فَاَخَافُ اَنْ يَّقْتُلُوْنِ', 'qāla rabbi innī qataltu min-hum nafsan fa akhāfu ay yaqtulụn'),
(3285, 28, 34, 'وَاَخِيْ هٰرُوْنُ هُوَ اَفْصَحُ مِنِّيْ لِسَانًا فَاَرْسِلْهُ مَعِيَ رِدْءًا يُّصَدِّقُنِيْٓ ۖاِنِّيْٓ اَخَافُ اَنْ يُّكَذِّبُوْنِ', 'wa akhī hārụnu huwa afṣaḥu minnī lisānan fa arsil-hu ma\'iya rid`ay yuṣaddiqunī innī akhāfu ay yukażżibụn'),
(3286, 28, 35, 'قَالَ سَنَشُدُّ عَضُدَكَ بِاَخِيْكَ وَنَجْعَلُ لَكُمَا سُلْطٰنًا فَلَا يَصِلُوْنَ اِلَيْكُمَا ۛبِاٰيٰتِنَا ۛ اَنْتُمَا وَمَنِ اتَّبَعَكُمَا الْغٰلِبُوْنَ', 'qāla sanasyuddu \'aḍudaka bi`akhīka wa naj\'alu lakumā sulṭānan fa lā yaṣilụna ilaikumā bi`āyātinā, antumā wa manittaba\'akumal-gālibụn'),
(3287, 28, 36, 'فَلَمَّا جَاۤءَهُمْ مُّوْسٰى بِاٰيٰتِنَا بَيِّنٰتٍ قَالُوْا مَا هٰذَآ اِلَّا سِحْرٌ مُّفْتَرًىۙ وَّمَا سَمِعْنَا بِهٰذَا فِيْٓ اٰبَاۤىِٕنَا الْاَوَّلِيْنَ', 'fa lammā jā`ahum mụsā bi`āyātinā bayyināting qālụ mā hāżā illā siḥrum muftaraw wa mā sami\'nā bihāżā fī ābā`inal-awwalīn'),
(3288, 28, 37, 'وَقَالَ مُوْسٰى رَبِّيْٓ اَعْلَمُ بِمَنْ جَاۤءَ بِالْهُدٰى مِنْ عِنْدِهٖ وَمَنْ تَكُوْنُ لَهٗ عَاقِبَةُ الدَّارِۗ اِنَّهٗ لَا يُفْلِحُ الظّٰلِمُوْنَ', 'wa qāla mụsā rabbī a\'lamu biman jā`a bil-hudā min \'indihī wa man takụnu lahụ \'āqibatud-dār, innahụ lā yufliḥuẓ-ẓālimụn'),
(3289, 28, 38, 'وَقَالَ فِرْعَوْنُ يٰٓاَيُّهَا الْمَلَاُ مَا عَلِمْتُ لَكُمْ مِّنْ اِلٰهٍ غَيْرِيْۚ فَاَوْقِدْ لِيْ يٰهَامٰنُ عَلَى الطِّيْنِ فَاجْعَلْ لِّيْ صَرْحًا لَّعَلِّيْٓ اَطَّلِعُ اِلٰٓى اِلٰهِ مُوْسٰىۙ وَاِنِّيْ لَاَظُنُّهٗ مِنَ الْكٰذِبِيْنَ', 'wa qāla fir\'aunu yā ayyuhal-mala`u mā \'alimtu lakum min ilāhin gairī, fa auqid lī yā hāmānu \'alaṭ-ṭīni faj\'al lī ṣarḥal la\'allī aṭṭali\'u ilā ilāhi mụsā wa innī la`aẓunnuhụ minal-kāżibīn'),
(3290, 28, 39, 'وَاسْتَكْبَرَ هُوَ وَجُنُوْدُهٗ فِى الْاَرْضِ بِغَيْرِ الْحَقِّ وَظَنُّوْٓا اَنَّهُمْ اِلَيْنَا لَا يُرْجَعُوْنَ', 'wastakbara huwa wa junụduhụ fil-arḍi bigairil-ḥaqqi wa ẓannū annahum ilainā lā yurja\'ụn'),
(3291, 28, 40, 'فَاَخَذْنٰهُ وَجُنُوْدَهٗ فَنَبَذْنٰهُمْ فِى الْيَمِّ ۚفَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الظّٰلِمِيْنَ', 'fa akhażnāhu wa junụdahụ fa nabażnāhum fil-yamm, fanẓur kaifa kāna \'āqibatuẓ-ẓālimīn'),
(3292, 28, 41, 'وَجَعَلْنٰهُمْ اَىِٕمَّةً يَّدْعُوْنَ اِلَى النَّارِۚ وَيَوْمَ الْقِيٰمَةِ لَا يُنْصَرُوْنَ', 'wa ja\'alnāhum a immatay yad\'ụna ilan-nār, wa yaumal-qiyāmati lā yunṣarụn'),
(3293, 28, 42, 'وَاَتْبَعْنٰهُمْ فِيْ هٰذِهِ الدُّنْيَا لَعْنَةً ۚوَيَوْمَ الْقِيٰمَةِ هُمْ مِّنَ الْمَقْبُوْحِيْنَ', 'wa atba\'nāhum fī hāżihid-dun-yā la\'nah, wa yaumal-qiyāmati hum minal-maqbụḥīn'),
(3294, 28, 43, 'وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ مِنْۢ بَعْدِ مَآ اَهْلَكْنَا الْقُرُوْنَ الْاُوْلٰى بَصَاۤىِٕرَ لِلنَّاسِ وَهُدًى وَّرَحْمَةً لَّعَلَّهُمْ يَتَذَكَّرُوْنَ', 'wa laqad ātainā mụsal-kitāba mim ba\'di mā ahlaknal-qurụnal-ụlā baṣā`ira lin-nāsi wa hudaw wa raḥmatal la\'allahum yatażakkarụn'),
(3295, 28, 44, 'وَمَا كُنْتَ بِجَانِبِ الْغَرْبِيِّ اِذْ قَضَيْنَآ اِلٰى مُوْسَى الْاَمْرَ وَمَا كُنْتَ مِنَ الشّٰهِدِيْنَ ۙ', 'wa mā kunta bijānibil-garbiyyi iż qaḍainā ilā mụsal-amra wa mā kunta minasy-syāhidīn'),
(3296, 28, 45, 'وَلٰكِنَّآ اَنْشَأْنَا قُرُوْنًا فَتَطَاوَلَ عَلَيْهِمُ الْعُمُرُۚ وَمَا كُنْتَ ثَاوِيًا فِيْٓ اَهْلِ مَدْيَنَ تَتْلُوْا عَلَيْهِمْ اٰيٰتِنَاۙ وَلٰكِنَّا كُنَّا مُرْسِلِيْنَ', 'wa lākinnā ansya`nā qurụnan fa taṭāwala \'alaihimul-\'umur, wa mā kunta ṡāwiyan fī ahli madyana tatlụ \'alaihim āyātinā wa lākinnā kunnā mursilīn'),
(3297, 28, 46, 'وَمَا كُنْتَ بِجَانِبِ الطُّوْرِ اِذْ نَادَيْنَا وَلٰكِنْ رَّحْمَةً مِّنْ رَّبِّكَ لِتُنْذِرَ قَوْمًا مَّآ اَتٰىهُمْ مِّنْ نَّذِيْرٍ مِّنْ قَبْلِكَ لَعَلَّهُمْ يَتَذَكَّرُوْنَ', 'wa mā kunta bijānibiṭ-ṭụri iż nādainā wa lākir raḥmatam mir rabbika litunżira qaumam mā atāhum min nażīrim ming qablika la\'allahum yatażakkarụn'),
(3298, 28, 47, 'وَلَوْلَآ اَنْ تُصِيْبَهُمْ مُّصِيْبَةٌ ۢبِمَا قَدَّمَتْ اَيْدِيْهِمْ فَيَقُوْلُوْا رَبَّنَا لَوْلَآ اَرْسَلْتَ اِلَيْنَا رَسُوْلًا فَنَتَّبِعَ اٰيٰتِكَ وَنَكُوْنَ مِنَ الْمُؤْمِنِيْنَ', 'walau lā an tuṣībahum muṣībatum bimā qaddamat aidīhim fa yaqụlụ rabbanā lau lā arsalta ilainā rasụlan fa nattabi\'a āyātika wa nakụna minal-mu`minīn'),
(3299, 28, 48, 'فَلَمَّا جَاۤءَهُمُ الْحَقُّ مِنْ عِنْدِنَا قَالُوْا لَوْلَآ اُوْتِيَ مِثْلَ مَآ اُوْتِيَ مُوْسٰىۗ اَوَلَمْ يَكْفُرُوْا بِمَآ اُوْتِيَ مُوْسٰى مِنْ قَبْلُۚ قَالُوْا سِحْرٰنِ تَظَاهَرَاۗ وَقَالُوْٓا اِنَّا بِكُلٍّ كٰفِرُوْنَ', 'fa lammā jā`ahumul-ḥaqqu min \'indinā qālụ lau lā ụtiya miṡla mā ụtiya mụsā, a wa lam yakfurụ bimā ụtiya mụsā ming qabl, qālụ siḥrāni taẓāharā, wa qālū innā bikulling kāfirụn'),
(3300, 28, 49, 'قُلْ فَأْتُوْا بِكِتٰبٍ مِّنْ عِنْدِ اللّٰهِ هُوَ اَهْدٰى مِنْهُمَآ اَتَّبِعْهُ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'qul fa`tụ bikitābim min \'indillāhi huwa ahdā min-humā attabi\'hu ing kuntum ṣādiqīn'),
(3301, 28, 50, 'فَاِنْ لَّمْ يَسْتَجِيْبُوْا لَكَ فَاعْلَمْ اَنَّمَا يَتَّبِعُوْنَ اَهْوَاۤءَهُمْۗ وَمَنْ اَضَلُّ مِمَّنِ اتَّبَعَ هَوٰىهُ بِغَيْرِ هُدًى مِّنَ اللّٰهِ ۗاِنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ', 'fa il lam yastajībụ laka fa\'lam annamā yattabi\'ụna ahwā`ahum, wa man aḍallu mim manittaba\'a hawāhu bigairi hudam minallāh, innallāha lā yahdil-qaumaẓ-ẓālimīn'),
(3302, 28, 51, '۞ وَلَقَدْ وَصَّلْنَا لَهُمُ الْقَوْلَ لَعَلَّهُمْ يَتَذَكَّرُوْنَ ۗ', 'wa laqad waṣṣalnā lahumul-qaula la\'allahum yatażakkarụn'),
(3303, 28, 52, 'اَلَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ مِنْ قَبْلِهٖ هُمْ بِهٖ يُؤْمِنُوْنَ', 'allażīna ātaināhumul-kitāba ming qablihī hum bihī yu`minụn'),
(3304, 28, 53, 'وَاِذَا يُتْلٰى عَلَيْهِمْ قَالُوْٓا اٰمَنَّا بِهٖٓ اِنَّهُ الْحَقُّ مِنْ رَّبِّنَآ اِنَّا كُنَّا مِنْ قَبْلِهٖ مُسْلِمِيْنَ', 'wa iżā yutlā \'alaihim qālū āmannā bihī innahul-ḥaqqu mir rabbinā innā kunnā ming qablihī muslimīn'),
(3305, 28, 54, 'اُولٰۤىِٕكَ يُؤْتَوْنَ اَجْرَهُمْ مَّرَّتَيْنِ بِمَا صَبَرُوْا وَيَدْرَءُوْنَ بِالْحَسَنَةِ السَّيِّئَةَ وَمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَ', 'ulā`ika yu`tauna ajrahum marrataini bimā ṣabarụ wa yadra`ụna bil-ḥasanatis-sayyi`ata wa mimmā razaqnāhum yunfiqụn'),
(3306, 28, 55, 'وَاِذَا سَمِعُوا اللَّغْوَ اَعْرَضُوْا عَنْهُ وَقَالُوْا لَنَآ اَعْمَالُنَا وَلَكُمْ اَعْمَالُكُمْ ۖسَلٰمٌ عَلَيْكُمْ ۖ لَا نَبْتَغِى الْجٰهِلِيْنَ', 'wa iżā sami\'ul-lagwa a\'raḍụ \'an-hu wa qālụ lanā a\'mālunā wa lakum a\'mālukum salāmun \'alaikum lā nabtagil-jāhilīn'),
(3307, 28, 56, 'اِنَّكَ لَا تَهْدِيْ مَنْ اَحْبَبْتَ وَلٰكِنَّ اللّٰهَ يَهْدِيْ مَنْ يَّشَاۤءُ ۚوَهُوَ اَعْلَمُ بِالْمُهْتَدِيْنَ', 'innaka lā tahdī man aḥbabta wa lākinnallāha yahdī may yasyā`, wa huwa a\'lamu bil-muhtadīn'),
(3308, 28, 57, 'وَقَالُوْٓا اِنْ نَّتَّبِعِ الْهُدٰى مَعَكَ نُتَخَطَّفْ مِنْ اَرْضِنَاۗ اَوَلَمْ نُمَكِّنْ لَّهُمْ حَرَمًا اٰمِنًا يُّجْبٰٓى اِلَيْهِ ثَمَرٰتُ كُلِّ شَيْءٍ رِّزْقًا مِّنْ لَّدُنَّا وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ', 'wa qālū in nattabi\'il-hudā ma\'aka nutakhaṭṭaf min arḍinā, a wa lam numakkil lahum ḥaraman āminay yujbā ilaihi ṡamarātu kulli syai`ir rizqam mil ladunnā wa lākinna akṡarahum lā ya\'lamụn'),
(3309, 28, 58, 'وَكَمْ اَهْلَكْنَا مِنْ قَرْيَةٍ ۢ بَطِرَتْ مَعِيْشَتَهَا ۚفَتِلْكَ مَسٰكِنُهُمْ لَمْ تُسْكَنْ مِّنْۢ بَعْدِهِمْ اِلَّا قَلِيْلًاۗ وَكُنَّا نَحْنُ الْوَارِثِيْنَ', 'wa kam ahlaknā ming qaryatim baṭirat ma\'īsyatahā, fa tilka masākinuhum lam tuskam mim ba\'dihim illā qalīlā, wa kunnā naḥnul-wāriṡīn'),
(3310, 28, 59, 'وَمَا كَانَ رَبُّكَ مُهْلِكَ الْقُرٰى حَتّٰى يَبْعَثَ فِيْٓ اُمِّهَا رَسُوْلًا يَّتْلُوْا عَلَيْهِمْ اٰيٰتِنَاۚ وَمَا كُنَّا مُهْلِكِى الْقُرٰىٓ اِلَّا وَاَهْلُهَا ظٰلِمُوْنَ', 'wa mā kāna rabbuka muhlikal-qurā ḥattā yab\'aṡa fī ummihā rasụlay yatlụ \'alaihim āyātinā, wa mā kunnā muhlikil-qurā illā wa ahluhā ẓālimụn'),
(3311, 28, 60, 'وَمَآ اُوْتِيْتُمْ مِّنْ شَيْءٍ فَمَتَاعُ الْحَيٰوةِ الدُّنْيَا وَزِيْنَتُهَا ۚوَمَا عِنْدَ اللّٰهِ خَيْرٌ وَّاَبْقٰىۗ اَفَلَا تَعْقِلُوْنَ', 'wa mā ụtītum min syai`in fa matā\'ul-ḥayātid-dun-yā wa zīnatuhā, wa mā \'indallāhi khairuw wa abqā, a fa lā ta\'qilụn'),
(3312, 28, 61, 'اَفَمَنْ وَّعَدْنٰهُ وَعْدًا حَسَنًا فَهُوَ لَاقِيْهِ كَمَنْ مَّتَّعْنٰهُ مَتَاعَ الْحَيٰوةِ الدُّنْيَا ثُمَّ هُوَ يَوْمَ الْقِيٰمَةِ مِنَ الْمُحْضَرِيْنَ', 'a fa maw wa\'adnāhu wa\'dan ḥasanan fa huwa lāqīhi kamam matta\'nāhu matā\'al-ḥayātid-dun-yā ṡumma huwa yaumal-qiyāmati minal-muḥḍarīn'),
(3313, 28, 62, 'وَيَوْمَ يُنَادِيْهِمْ فَيَقُوْلُ اَيْنَ شُرَكَاۤءِيَ الَّذِيْنَ كُنْتُمْ تَزْعُمُوْنَ', 'wa yauma yunādīhim fa yaqụlu aina syurakā`iyallażīna kuntum taz\'umụn'),
(3314, 28, 63, 'قَالَ الَّذِيْنَ حَقَّ عَلَيْهِمُ الْقَوْلُ رَبَّنَا هٰٓؤُلَاۤءِ الَّذِيْنَ اَغْوَيْنَاۚ اَغْوَيْنٰهُمْ كَمَا غَوَيْنَاۚ تَبَرَّأْنَآ اِلَيْكَ مَا كَانُوْٓا اِيَّانَا يَعْبُدُوْنَ', 'qālallażīna ḥaqqa \'alaihimul-qaulu rabbanā hā`ulā`illażīna agwainā, agwaināhum kamā gawainā, tabarra`nā ilaika mā kānū iyyānā ya\'budụn'),
(3315, 28, 64, 'وَقِيْلَ ادْعُوْا شُرَكَاۤءَكُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَجِيْبُوْا لَهُمْ ۗوَرَاَوُا الْعَذَابَۚ لَوْ اَنَّهُمْ كَانُوْا يَهْتَدُوْنَ', 'wa qīlad\'ụ syurakā`akum fa da\'auhum fa lam yastajībụ lahum, wa ra`awul-\'ażāb, lau annahum kānụ yahtadụn'),
(3316, 28, 65, 'وَيَوْمَ يُنَادِيْهِمْ فَيَقُوْلُ مَاذَآ اَجَبْتُمُ الْمُرْسَلِيْنَ', 'wa yauma yunādīhim fa yaqụlu māżā ajabtumul-mursalīn'),
(3317, 28, 66, 'فَعَمِيَتْ عَلَيْهِمُ الْاَنْۢبَاۤءُ يَوْمَىِٕذٍ فَهُمْ لَا يَتَسَاۤءَلُوْنَ', 'fa \'amiyat \'alaihimul-ambā`u yauma`iżin fa hum lā yatasā`alụn'),
(3318, 28, 67, 'فَاَمَّا مَنْ تَابَ وَاٰمَنَ وَعَمِلَ صَالِحًا فَعَسٰٓى اَنْ يَّكُوْنَ مِنَ الْمُفْلِحِيْنَ', 'fa ammā man tāba wa āmana wa \'amila ṣāliḥan fa \'asā ay yakụna minal-mufliḥīn'),
(3319, 28, 68, 'وَرَبُّكَ يَخْلُقُ مَا يَشَاۤءُ وَيَخْتَارُ ۗمَا كَانَ لَهُمُ الْخِيَرَةُ ۗسُبْحٰنَ اللّٰهِ وَتَعٰلٰى عَمَّا يُشْرِكُوْنَ', 'wa rabbuka yakhluqu mā yasyā`u wa yakhtār, mā kāna lahumul-khiyarah, sub-ḥānallāhi wa ta\'ālā \'ammā yusyrikụn'),
(3320, 28, 69, 'وَرَبُّكَ يَعْلَمُ مَا تُكِنُّ صُدُوْرُهُمْ وَمَا يُعْلِنُوْنَ', 'wa rabbuka ya\'lamu mā tukinnu ṣudụruhum wa mā yu\'linụn'),
(3321, 28, 70, 'وَهُوَ اللّٰهُ لَآ اِلٰهَ اِلَّا هُوَ ۗ لَهُ الْحَمْدُ فِى الْاُوْلٰى وَالْاٰخِرَةِ ۖوَلَهُ الْحُكْمُ وَاِلَيْهِ تُرْجَعُوْنَ', 'wa huwallāhu lā ilāha illā huw, lahul-ḥamdu fil-ụlā wal-ākhirati wa lahul-ḥukmu wa ilaihi turja\'ụn'),
(3322, 28, 71, 'قُلْ اَرَءَيْتُمْ اِنْ جَعَلَ اللّٰهُ عَلَيْكُمُ الَّيْلَ سَرْمَدًا اِلٰى يَوْمِ الْقِيٰمَةِ مَنْ اِلٰهٌ غَيْرُ اللّٰهِ يَأْتِيْكُمْ بِضِيَاۤءٍ ۗ اَفَلَا تَسْمَعُوْنَ', 'qul a ra`aitum in ja\'alallāhu \'alaikumul-laila sarmadan ilā yaumil-qiyāmati man ilāhun gairullāhi ya`tīkum biḍiyā`, a fa lā tasma\'ụn'),
(3323, 28, 72, 'قُلْ اَرَءَيْتُمْ اِنْ جَعَلَ اللّٰهُ عَلَيْكُمُ النَّهَارَ سَرْمَدًا اِلٰى يَوْمِ الْقِيٰمَةِ مَنْ اِلٰهٌ غَيْرُ اللّٰهِ يَأْتِيْكُمْ بِلَيْلٍ تَسْكُنُوْنَ فِيْهِ ۗ اَفَلَا تُبْصِرُوْنَ', 'qul a ra`aitum in ja\'alallāhu \'alaikumun-nahāra sarmadan ilā yaumil-qiyāmati man ilāhun gairullāhi ya`tīkum bilailin taskunụna fīh, a fa lā tubṣirụn'),
(3324, 28, 73, 'وَمِنْ رَّحْمَتِهٖ جَعَلَ لَكُمُ الَّيْلَ وَالنَّهَارَ لِتَسْكُنُوْا فِيْهِ وَلِتَبْتَغُوْا مِنْ فَضْلِهٖ وَلَعَلَّكُمْ تَشْكُرُوْنَ', 'wa mir raḥmatihī ja\'ala lakumul-laila wan-nahāra litaskunụ fīhi wa litabtagụ min faḍlihī wa la\'allakum tasykurụn'),
(3325, 28, 74, 'وَيَوْمَ يُنَادِيْهِمْ فَيَقُوْلُ اَيْنَ شُرَكَاۤءِيَ الَّذِيْنَ كُنْتُمْ تَزْعُمُوْنَ', 'wa yauma yunādīhim fa yaqụlu aina syurakā`iyallażīna kuntum taz\'umụn'),
(3326, 28, 75, 'وَنَزَعْنَا مِنْ كُلِّ اُمَّةٍ شَهِيْدًا فَقُلْنَا هَاتُوْا بُرْهَانَكُمْ فَعَلِمُوْٓا اَنَّ الْحَقَّ لِلّٰهِ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ', 'wa naza\'nā ming kulli ummatin syahīdan fa qulnā hātụ burhānakum fa \'alimū annal-ḥaqqa lillāhi wa ḍalla \'an-hum mā kānụ yaftarụn'),
(3327, 28, 76, '۞ اِنَّ قَارُوْنَ كَانَ مِنْ قَوْمِ مُوْسٰى فَبَغٰى عَلَيْهِمْ ۖوَاٰتَيْنٰهُ مِنَ الْكُنُوْزِ مَآ اِنَّ مَفَاتِحَهٗ لَتَنُوْۤاُ بِالْعُصْبَةِ اُولِى الْقُوَّةِ اِذْ قَالَ لَهٗ قَوْمُهٗ لَا تَفْرَحْ اِنَّ اللّٰهَ لَا يُحِبُّ الْفَرِحِيْنَ', 'inna qārụna kāna ming qaumi mụsā fa bagā \'alaihim wa ātaināhu minal-kunụzi mā inna mafātiḥahụ latanū`u bil-\'uṣbati ulil-quwwati iż qāla lahụ qaumuhụ lā tafraḥ innallāha lā yuḥibbul-fariḥīn'),
(3328, 28, 77, 'وَابْتَغِ فِيْمَآ اٰتٰىكَ اللّٰهُ الدَّارَ الْاٰخِرَةَ وَلَا تَنْسَ نَصِيْبَكَ مِنَ الدُّنْيَا وَاَحْسِنْ كَمَآ اَحْسَنَ اللّٰهُ اِلَيْكَ وَلَا تَبْغِ الْفَسَادَ فِى الْاَرْضِ ۗاِنَّ اللّٰهَ لَا يُحِبُّ الْمُفْسِدِيْنَ', 'wabtagi fīmā ātākallāhud-dāral-ākhirata wa lā tansa naṣībaka minad-dun-yā wa aḥsing kamā aḥsanallāhu ilaika wa lā tabgil-fasāda fil-arḍ, innallāha lā yuḥibbul-mufsidīn'),
(3329, 28, 78, 'قَالَ اِنَّمَآ اُوْتِيْتُهٗ عَلٰى عِلْمٍ عِنْدِيْۗ اَوَلَمْ يَعْلَمْ اَنَّ اللّٰهَ قَدْ اَهْلَكَ مِنْ قَبْلِهٖ مِنَ الْقُرُوْنِ مَنْ هُوَ اَشَدُّ مِنْهُ قُوَّةً وَّاَكْثَرُ جَمْعًا ۗوَلَا يُسْـَٔلُ عَنْ ذُنُوْبِهِمُ الْمُجْرِمُوْنَ', 'qāla innamā ụtītuhụ \'alā \'ilmin \'indī, a wa lam ya\'lam annallāha qad ahlaka ming qablihī minal-qurụni man huwa asyaddu min-hu quwwataw wa akṡaru jam\'ā, wa lā yus`alu \'an żunụbihimul-mujrimụn'),
(3330, 28, 79, 'فَخَرَجَ عَلٰى قَوْمِهٖ فِيْ زِيْنَتِهٖ ۗقَالَ الَّذِيْنَ يُرِيْدُوْنَ الْحَيٰوةَ الدُّنْيَا يٰلَيْتَ لَنَا مِثْلَ مَآ اُوْتِيَ قَارُوْنُۙ اِنَّهٗ لَذُوْ حَظٍّ عَظِيْمٍ', 'fa kharaja \'alā qaumihī fī zīnatih, qālallażīna yurīdụnal-ḥayātad-dun-yā yā laita lanā miṡla mā ụtiya qārụnu innahụ lażụ haẓẓin \'aẓīm'),
(3331, 28, 80, 'وَقَالَ الَّذِيْنَ اُوْتُوا الْعِلْمَ وَيْلَكُمْ ثَوَابُ اللّٰهِ خَيْرٌ لِّمَنْ اٰمَنَ وَعَمِلَ صَالِحًا ۚوَلَا يُلَقّٰىهَآ اِلَّا الصّٰبِرُوْنَ', 'wa qālallażīna ụtul-\'ilma wailakum ṡawābullāhi khairul liman āmana wa \'amila ṣāliḥā, wa lā yulaqqāhā illaṣ-ṣābirụn'),
(3332, 28, 81, 'فَخَسَفْنَا بِهٖ وَبِدَارِهِ الْاَرْضَ ۗفَمَا كَانَ لَهٗ مِنْ فِئَةٍ يَّنْصُرُوْنَهٗ مِنْ دُوْنِ اللّٰهِ ۖوَمَا كَانَ مِنَ الْمُنْتَصِرِيْنَ', 'fa khasafnā bihī wa bidārihil-arḍ, fa mā kāna lahụ min fi`atiy yanṣurụnahụ min dụnillāhi wa mā kāna minal-muntaṣirīn'),
(3333, 28, 82, 'وَاَصْبَحَ الَّذِيْنَ تَمَنَّوْا مَكَانَهٗ بِالْاَمْسِ يَقُوْلُوْنَ وَيْكَاَنَّ اللّٰهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ وَيَقْدِرُۚ لَوْلَآ اَنْ مَّنَّ اللّٰهُ عَلَيْنَا لَخَسَفَ بِنَا ۗوَيْكَاَنَّهٗ لَا يُفْلِحُ الْكٰفِرُوْنَ', 'wa aṣbaḥallażīna tamannau makānahụ bil-amsi yaqụlụna waika`annallāha yabsuṭur-rizqa limay yasyā`u min \'ibādihī wa yaqdir, lau lā am mannallāhu \'alainā lakhasafa binā, waika`annahụ lā yufliḥul-kāfirụn'),
(3334, 28, 83, 'تِلْكَ الدَّارُ الْاٰخِرَةُ نَجْعَلُهَا لِلَّذِيْنَ لَا يُرِيْدُوْنَ عُلُوًّا فِى الْاَرْضِ وَلَا فَسَادًا ۗوَالْعَاقِبَةُ لِلْمُتَّقِيْنَ', 'tilkad-dārul-ākhiratu naj\'aluhā lillażīna lā yurīdụna \'uluwwan fil-arḍi wa lā fasādā, wal-\'āqibatu lil-muttaqīn'),
(3335, 28, 84, 'مَنْ جَاۤءَ بِالْحَسَنَةِ فَلَهٗ خَيْرٌ مِّنْهَاۚ وَمَنْ جَاۤءَ بِالسَّيِّئَةِ فَلَا يُجْزَى الَّذِيْنَ عَمِلُوا السَّيِّاٰتِ اِلَّا مَا كَانُوْا يَعْمَلُوْنَ', 'man jā`a bil-ḥasanati fa lahụ khairum min-hā, wa man jā`a bis-sayyi`ati fa lā yujzallażīna \'amilus-sayyi`āti illā mā kānụ ya\'malụn'),
(3336, 28, 85, 'اِنَّ الَّذِيْ فَرَضَ عَلَيْكَ الْقُرْاٰنَ لَرَاۤدُّكَ اِلٰى مَعَادٍ ۗقُلْ رَّبِّيْٓ اَعْلَمُ مَنْ جَاۤءَ بِالْهُدٰى وَمَنْ هُوَ فِيْ ضَلٰلٍ مُّبِيْنٍ', 'innallażī faraḍa \'alaikal-qur`āna larādduka ilā ma\'ād, qul rabbī a\'lamu man jā`a bil-hudā wa man huwa fī ḍalālim mubīn'),
(3337, 28, 86, 'وَمَا كُنْتَ تَرْجُوْٓا اَنْ يُّلْقٰٓى اِلَيْكَ الْكِتٰبُ اِلَّا رَحْمَةً مِّنْ رَّبِّكَ فَلَا تَكُوْنَنَّ ظَهِيْرًا لِّلْكٰفِرِيْنَ ۖ', 'wa mā kunta tarjū ay yulqā ilaikal-kitābu illā raḥmatam mir rabbika fa lā takụnanna ẓahīral lil-kāfirīn'),
(3338, 28, 87, 'وَلَا يَصُدُّنَّكَ عَنْ اٰيٰتِ اللّٰهِ بَعْدَ اِذْ اُنْزِلَتْ اِلَيْكَ وَادْعُ اِلٰى رَبِّكَ وَلَا تَكُوْنَنَّ مِنَ الْمُشْرِكِيْنَ ۚ', 'wa lā yaṣuddunnaka \'an āyātillāhi ba\'da iż unzilat ilaika wad\'u ilā rabbika wa lā takụnanna minal-musyrikīn'),
(3339, 28, 88, 'وَلَا تَدْعُ مَعَ اللّٰهِ اِلٰهًا اٰخَرَۘ لَآ اِلٰهَ اِلَّا هُوَۗ كُلُّ شَيْءٍ هَالِكٌ اِلَّا وَجْهَهٗ ۗ لَهُ الْحُكْمُ وَاِلَيْهِ تُرْجَعُوْنَ', 'wa lā tad\'u ma\'allāhi ilāhan ākhar, lā ilāha illā huw, kullu syai`in hālikun illā waj-hah, lahul-ḥukmu wa ilaihi turja\'ụn'),
(3340, 29, 1, 'الۤمّۤ ۗ', 'alif lām mīm'),
(3341, 29, 2, 'اَحَسِبَ النَّاسُ اَنْ يُّتْرَكُوْٓا اَنْ يَّقُوْلُوْٓا اٰمَنَّا وَهُمْ لَا يُفْتَنُوْنَ', 'a ḥasiban-nāsu ay yutrakū ay yaqụlū āmannā wa hum lā yuftanụn'),
(3342, 29, 3, 'وَلَقَدْ فَتَنَّا الَّذِيْنَ مِنْ قَبْلِهِمْ فَلَيَعْلَمَنَّ اللّٰهُ الَّذِيْنَ صَدَقُوْا وَلَيَعْلَمَنَّ الْكٰذِبِيْنَ', 'wa laqad fatannallażīna ming qablihim fa laya\'lamannallāhullażīna ṣadaqụ walaya\'lamannal-kāżibīn'),
(3343, 29, 4, 'اَمْ حَسِبَ الَّذِيْنَ يَعْمَلُوْنَ السَّيِّاٰتِ اَنْ يَّسْبِقُوْنَا ۗسَاۤءَ مَا يَحْكُمُوْنَ', 'am ḥasiballażīna ya\'malụnas-sayyi`āti ay yasbiqụnā, sā`a mā yaḥkumụn'),
(3344, 29, 5, 'مَنْ كَانَ يَرْجُوْا لِقَاۤءَ اللّٰهِ فَاِنَّ اَجَلَ اللّٰهِ لَاٰتٍ ۗوَهُوَ السَّمِيْعُ الْعَلِيْمُ', 'mang kāna yarjụ liqā`allāhi fa inna ajalallāhi la`āt, wa huwas-samī\'ul-\'alīm'),
(3345, 29, 6, 'وَمَنْ جَاهَدَ فَاِنَّمَا يُجَاهِدُ لِنَفْسِهٖ ۗاِنَّ اللّٰهَ لَغَنِيٌّ عَنِ الْعٰلَمِيْنَ', 'wa man jāhada fa innamā yujāhidu linafsih, innallāha laganiyyun \'anil-\'ālamīn'),
(3346, 29, 7, 'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَنُكَفِّرَنَّ عَنْهُمْ سَيِّاٰتِهِمْ وَلَنَجْزِيَنَّهُمْ اَحْسَنَ الَّذِيْ كَانُوْا يَعْمَلُوْنَ', 'wallażīna āmanụ wa \'amiluṣ-ṣāliḥāti lanukaffiranna \'an-hum sayyi`ātihim wa lanajziyannahum aḥsanallażī kānụ ya\'malụn'),
(3347, 29, 8, 'وَوَصَّيْنَا الْاِنْسَانَ بِوَالِدَيْهِ حُسْنًا ۗوَاِنْ جَاهَدٰكَ لِتُشْرِكَ بِيْ مَا لَيْسَ لَكَ بِهٖ عِلْمٌ فَلَا تُطِعْهُمَا ۗاِلَيَّ مَرْجِعُكُمْ فَاُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ', 'wa waṣṣainal-insāna biwālidaihi ḥusnā, wa in jāhadāka litusyrika bī mā laisa laka bihī \'ilmun fa lā tuṭi\'humā, ilayya marji\'ukum fa unabbi`ukum bimā kuntum ta\'malụn'),
(3348, 29, 9, 'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَنُدْخِلَنَّهُمْ فِى الصّٰلِحِيْنَ', 'wallażīna āmanụ wa \'amiluṣ-ṣāliḥāti lanudkhilannahum fiṣ-ṣāliḥīn'),
(3349, 29, 10, 'وَمِنَ النَّاسِ مَنْ يَّقُوْلُ اٰمَنَّا بِاللّٰهِ فَاِذَآ اُوْذِيَ فِى اللّٰهِ جَعَلَ فِتْنَةَ النَّاسِ كَعَذَابِ اللّٰهِ ۗوَلَىِٕنْ جَاۤءَ نَصْرٌ مِّنْ رَّبِّكَ لَيَقُوْلُنَّ اِنَّا كُنَّا مَعَكُمْۗ اَوَلَيْسَ اللّٰهُ بِاَعْلَمَ بِمَا فِيْ صُدُوْرِ الْعٰلَمِيْنَ', 'wa minan-nāsi may yaqụlu āmannā billāhi fa iżā ụżiya fillāhi ja\'ala fitnatan-nāsi ka\'ażābillāh, wa la`in jā`a naṣrum mir rabbika layaqụlunna innā kunnā ma\'akum, a wa laisallāhu bi`a\'lama bimā fī ṣudụril-\'ālamīn'),
(3350, 29, 11, 'وَلَيَعْلَمَنَّ اللّٰهُ الَّذِيْنَ اٰمَنُوْا وَلَيَعْلَمَنَّ الْمُنٰفِقِيْنَ', 'wa laya\'lamannallāhullażīna āmanụ wa laya\'lamannal-munāfiqīn'),
(3351, 29, 12, 'وَقَالَ الَّذِيْنَ كَفَرُوْا لِلَّذِيْنَ اٰمَنُوا اتَّبِعُوْا سَبِيْلَنَا وَلْنَحْمِلْ خَطٰيٰكُمْۗ وَمَا هُمْ بِحَامِلِيْنَ مِنْ خَطٰيٰهُمْ مِّنْ شَيْءٍۗ اِنَّهُمْ لَكٰذِبُوْنَ', 'wa qālallażīna kafarụ lillażīna āmanuttabi\'ụ sabīlanā walnaḥmil khaṭāyākum, wa mā hum biḥāmilīna min khaṭāyāhum min syaī`, innahum lakāżibụn'),
(3352, 29, 13, 'وَلَيَحْمِلُنَّ اَثْقَالَهُمْ وَاَثْقَالًا مَّعَ اَثْقَالِهِمْ وَلَيُسْـَٔلُنَّ يَوْمَ الْقِيٰمَةِ عَمَّا كَانُوْا يَفْتَرُوْنَ', 'wa layaḥmilunna aṡqālahum wa aṡqālam ma\'a aṡqālihim wa layus`alunna yaumal-qiyāmati \'ammā kānụ yaftarụn'),
(3353, 29, 14, 'وَلَقَدْ اَرْسَلْنَا نُوْحًا اِلٰى قَوْمِهٖ فَلَبِثَ فِيْهِمْ اَلْفَ سَنَةٍ اِلَّا خَمْسِيْنَ عَامًا ۗفَاَخَذَهُمُ الطُّوْفَانُ وَهُمْ ظٰلِمُوْنَ', 'wa laqad arsalnā nụḥan ilā qaumihī fa labiṡa fīhim alfa sanatin illā khamsīna \'āmā, fa akhażahumuṭ-ṭụfānu wa hum ẓālimụn'),
(3354, 29, 15, 'فَاَنْجَيْنٰهُ وَاَصْحٰبَ السَّفِيْنَةِ وَجَعَلْنٰهَآ اٰيَةً لِّلْعٰلَمِيْنَ', 'fa anjaināhu wa aṣ-hābas-safīnati wa ja\'alnāhā āyatal lil-\'ālamīn'),
(3355, 29, 16, 'وَاِبْرٰهِيْمَ اِذْ قَالَ لِقَوْمِهِ اعْبُدُوا اللّٰهَ وَاتَّقُوْهُ ۗذٰلِكُمْ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ', 'wa ibrāhīma iż qāla liqaumihi\'budullāha wattaqụh, żālikum khairul lakum ing kuntum ta\'lamụn'),
(3356, 29, 17, 'اِنَّمَا تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ اَوْثَانًا وَّتَخْلُقُوْنَ اِفْكًا ۗاِنَّ الَّذِيْنَ تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ لَا يَمْلِكُوْنَ لَكُمْ رِزْقًا فَابْتَغُوْا عِنْدَ اللّٰهِ الرِّزْقَ وَاعْبُدُوْهُ وَاشْكُرُوْا لَهٗ ۗاِلَيْهِ تُرْجَعُوْنَ', 'innamā ta\'budụna min dụnillāhi auṡānaw wa takhluqụna ifkā, innallażīna ta\'budụna min dụnillāhi lā yamlikụna lakum rizqan fabtagụ \'indallāhir-rizqa wa\'budụhu wasykurụ lah, ilaihi turja\'ụn'),
(3357, 29, 18, 'وَاِنْ تُكَذِّبُوْا فَقَدْ كَذَّبَ اُمَمٌ مِّنْ قَبْلِكُمْ ۗوَمَا عَلَى الرَّسُوْلِ اِلَّا الْبَلٰغُ الْمُبِيْنُ', 'wa in tukażżibụ fa qad każżaba umamum ming qablikum, wa mā \'alar-rasụli illal-balāgul-mubīn'),
(3358, 29, 19, 'اَوَلَمْ يَرَوْا كَيْفَ يُبْدِئُ اللّٰهُ الْخَلْقَ ثُمَّ يُعِيْدُهٗ ۗاِنَّ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرٌ', 'a wa lam yarau kaifa yubdi`ullāhul-khalqa ṡumma yu\'īduh, inna żālika \'alallāhi yasīr'),
(3359, 29, 20, 'قُلْ سِيْرُوْا فِى الْاَرْضِ فَانْظُرُوْا كَيْفَ بَدَاَ الْخَلْقَ ثُمَّ اللّٰهُ يُنْشِئُ النَّشْاَةَ الْاٰخِرَةَ ۗاِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ۚ', 'qul sīrụ fil-arḍi fanẓurụ kaifa bada`al-khalqa ṡummallāhu yunsyi`un-nasy`atal-ākhirah, innallāha \'alā kulli syai`ing qadīr'),
(3360, 29, 21, 'يُعَذِّبُ مَنْ يَّشَاۤءُ وَيَرْحَمُ مَنْ يَّشَاۤءُ ۚوَاِلَيْهِ تُقْلَبُوْنَ', 'yu\'ażżibu may yasyā`u wa yar-ḥamu may yasyā`, wa ilaihi tuqlabụn'),
(3361, 29, 22, 'وَمَآ اَنْتُمْ بِمُعْجِزِيْنَ فِى الْاَرْضِ وَلَا فِى السَّمَاۤءِ ۖوَمَا لَكُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ', 'wa mā antum bimu\'jizīna fil-arḍi wa lā fis-samā`i wa mā lakum min dụnillāhi miw waliyyiw wa lā naṣīr'),
(3362, 29, 23, 'وَالَّذِيْنَ كَفَرُوْا بِاٰيٰتِ اللّٰهِ وَلِقَاۤىِٕهٖٓ اُولٰۤىِٕكَ يَىِٕسُوْا مِنْ رَّحْمَتِيْ وَاُولٰۤىِٕكَ لَهُمْ عَذَابٌ اَلِيْمٌ', 'wallażīna kafarụ bi`āyātillāhi wa liqā`ihī ulā`ika ya`isụ mir raḥmatī wa ulā`ika lahum \'ażābun alīm'),
(3363, 29, 24, 'فَمَا كَانَ جَوَابَ قَوْمِهٖٓ اِلَّآ اَنْ قَالُوا اقْتُلُوْهُ اَوْ حَرِّقُوْهُ فَاَنْجٰىهُ اللّٰهُ مِنَ النَّارِۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ', 'fa mā kāna jawāba qaumihī illā ang qāluqtulụhu au ḥarriqụhu fa anjāhullāhu minan-nār, inna fī żālika la`āyātil liqaumiy yu`minụn'),
(3364, 29, 25, 'وَقَالَ اِنَّمَا اتَّخَذْتُمْ مِّنْ دُوْنِ اللّٰهِ اَوْثَانًاۙ مَّوَدَّةَ بَيْنِكُمْ فِى الْحَيٰوةِ الدُّنْيَا ۚ ثُمَّ يَوْمَ الْقِيٰمَةِ يَكْفُرُ بَعْضُكُمْ بِبَعْضٍ وَّيَلْعَنُ بَعْضُكُمْ بَعْضًا ۖوَّمَأْوٰىكُمُ النَّارُ وَمَا لَكُمْ مِّنْ نّٰصِرِيْنَۖ', 'wa qāla innamattakhażtum min dụnillāhi auṡānam mawaddata bainikum fil-ḥayātid-dun-yā, ṡumma yaumal-qiyāmati yakfuru ba\'ḍukum biba\'ḍiw wa yal\'anu ba\'ḍukum ba\'ḍaw wa ma`wākumun-nāru wa mā lakum min nāṣirīn'),
(3365, 29, 26, '۞ فَاٰمَنَ لَهٗ لُوْطٌۘ وَقَالَ اِنِّيْ مُهَاجِرٌ اِلٰى رَبِّيْ ۗاِنَّهٗ هُوَ الْعَزِيْزُ الْحَكِيْمُ', 'fa āmana lahụ lụṭ, wa qāla innī muhājirun ilā rabbī, innahụ huwal-\'azīzul-ḥakīm'),
(3366, 29, 27, 'وَوَهَبْنَا لَهٗٓ اِسْحٰقَ وَيَعْقُوْبَ وَجَعَلْنَا فِيْ ذُرِّيَّتِهِ النُّبُوَّةَ وَالْكِتٰبَ وَاٰتَيْنٰهُ اَجْرَهٗ فِى الدُّنْيَا ۚوَاِنَّهٗ فِى الْاٰخِرَةِ لَمِنَ الصّٰلِحِيْنَ', 'wa wahabnā lahū is-ḥāqa wa ya\'qụba wa ja\'alnā fī żurriyyatihin-nubuwwata wal-kitāba wa ātaināhu ajrahụ fid-dun-yā, wa innahụ fil-ākhirati laminaṣ-ṣāliḥīn'),
(3367, 29, 28, 'وَلُوْطًا اِذْ قَالَ لِقَوْمِهٖٓ اِنَّكُمْ لَتَأْتُوْنَ الْفَاحِشَةَ ۖمَا سَبَقَكُمْ بِهَا مِنْ اَحَدٍ مِّنَ الْعٰلَمِيْنَ', 'wa lụṭan iż qāla liqaumihī innakum lata`tụnal-fāḥisyata mā sabaqakum bihā min aḥadim minal-\'ālamīn'),
(3368, 29, 29, 'اَىِٕنَّكُمْ لَتَأْتُوْنَ الرِّجَالَ وَتَقْطَعُوْنَ السَّبِيْلَ ەۙ وَتَأْتُوْنَ فِيْ نَادِيْكُمُ الْمُنْكَرَ ۗفَمَا كَانَ جَوَابَ قَوْمِهٖٓ اِلَّآ اَنْ قَالُوا ائْتِنَا بِعَذَابِ اللّٰهِ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ', 'a innakum lata`tụnar-rijāla wa taqṭa\'ụnas-sabīla wa ta`tụna fī nādīkumul-mungkar, fa mā kāna jawāba qaumihī illā ang qālu`tinā bi\'ażābillāhi ing kunta minaṣ-ṣādiqīn'),
(3369, 29, 30, 'قَالَ رَبِّ انْصُرْنِيْ عَلَى الْقَوْمِ الْمُفْسِدِيْنَ', 'qāla rabbinṣurnī \'alal-qaumil-mufsidīn'),
(3370, 29, 31, 'وَلَمَّا جَاۤءَتْ رُسُلُنَآ اِبْرٰهِيْمَ بِالْبُشْرٰىۙ قَالُوْٓا اِنَّا مُهْلِكُوْٓا اَهْلِ هٰذِهِ الْقَرْيَةِ ۚاِنَّ اَهْلَهَا كَانُوْا ظٰلِمِيْنَ ۚ', 'wa lammā jā`at rusulunā ibrāhīma bil-busyrā qālū innā muhlikū ahli hāżihil-qaryah, inna ahlahā kānụ ẓālimīn'),
(3371, 29, 32, 'قَالَ اِنَّ فِيْهَا لُوْطًا ۗقَالُوْا نَحْنُ اَعْلَمُ بِمَنْ فِيْهَا ۖ لَنُنَجِّيَنَّهٗ وَاَهْلَهٗٓ اِلَّا امْرَاَتَهٗ كَانَتْ مِنَ الْغٰبِرِيْنَ', 'qāla inna fīhā lụṭā, qālụ naḥnu a\'lamu biman fīhā lanunajjiyannahụ wa ahlahū illamra`atahụ kānat minal-gābirīn'),
(3372, 29, 33, 'وَلَمَّآ اَنْ جَاۤءَتْ رُسُلُنَا لُوْطًا سِيْۤءَ بِهِمْ وَضَاقَ بِهِمْ ذَرْعًا وَّقَالُوْا لَا تَخَفْ وَلَا تَحْزَنْ ۗاِنَّا مُنَجُّوْكَ وَاَهْلَكَ اِلَّا امْرَاَتَكَ كَانَتْ مِنَ الْغٰبِرِيْنَ', 'wa lammā an jā`at rusulunā lụṭan sī`a bihim wa ḍāqa bihim żar\'aw wa qālụ lā takhaf wa lā taḥzan, innā munajjụka wa ahlaka illamra`ataka kānat minal-gābirīn'),
(3373, 29, 34, 'اِنَّا مُنْزِلُوْنَ عَلٰٓى اَهْلِ هٰذِهِ الْقَرْيَةِ رِجْزًا مِّنَ السَّمَاۤءِ بِمَا كَانُوْا يَفْسُقُوْنَ', 'innā munzilụna \'alā ahli hāżihil-qaryati rijzam minas-samā`i bimā kānụ yafsuqụn'),
(3374, 29, 35, 'وَلَقَدْ تَّرَكْنَا مِنْهَآ اٰيَةً ۢ بَيِّنَةً لِّقَوْمٍ يَّعْقِلُوْنَ', 'wa laqat taraknā min-hā āyatam bayyinatal liqaumiy ya\'qilụn'),
(3375, 29, 36, 'وَاِلٰى مَدْيَنَ اَخَاهُمْ شُعَيْبًاۙ فَقَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ وَارْجُوا الْيَوْمَ الْاٰخِرَ وَلَا تَعْثَوْا فِى الْاَرْضِ مُفْسِدِيْنَ ۖ', 'wa ilā madyana akhāhum syu\'aiban fa qāla yā qaumi\'budullāha warjul-yaumal-ākhira wa lā ta\'ṡau fil-arḍi mufsidīn'),
(3376, 29, 37, 'فَكَذَّبُوْهُ فَاَخَذَتْهُمُ الرَّجْفَةُ فَاَصْبَحُوْا فِيْ دَارِهِمْ جٰثِمِيْنَ ۙ', 'fa każżabụhu fa akhażat-humur-rajfatu fa aṣbaḥụ fī dārihim jāṡimīn'),
(3377, 29, 38, 'وَعَادًا وَّثَمُوْدَا۟ وَقَدْ تَّبَيَّنَ لَكُمْ مِّنْ مَّسٰكِنِهِمْۗ وَزَيَّنَ لَهُمُ الشَّيْطٰنُ اَعْمَالَهُمْ فَصَدَّهُمْ عَنِ السَّبِيْلِ وَكَانُوْا مُسْتَبْصِرِيْنَ ۙ', 'wa \'ādaw wa ṡamụda wa qat tabayyana lakum mim masākinihim, wa zayyana lahumusy-syaiṭānu a\'mālahum fa ṣaddahum \'anis-sabīli wa kānụ mustabṣirīn'),
(3378, 29, 39, 'وَقَارُوْنَ وَفِرْعَوْنَ وَهَامٰنَۗ وَلَقَدْ جَاۤءَهُمْ مُّوْسٰى بِالْبَيِّنٰتِ فَاسْتَكْبَرُوْا فِى الْاَرْضِ وَمَا كَانُوْا سَابِقِيْنَ ۚ', 'wa qārụna wa fir\'auna wa hāmān, wa laqad jā`ahum mụsā bil-bayyināti fastakbarụ fil-arḍi wa mā kānụ sābiqīn'),
(3379, 29, 40, 'فَكُلًّا اَخَذْنَا بِذَنْۢبِهٖۙ فَمِنْهُمْ مَّنْ اَرْسَلْنَا عَلَيْهِ حَاصِبًا ۚوَمِنْهُمْ مَّنْ اَخَذَتْهُ الصَّيْحَةُ ۚوَمِنْهُمْ مَّنْ خَسَفْنَا بِهِ الْاَرْضَۚ وَمِنْهُمْ مَّنْ اَغْرَقْنَاۚ وَمَا كَانَ اللّٰهُ لِيَظْلِمَهُمْ وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَ', 'fa kullan akhażnā biżambihī fa min-hum man arsalnā \'alaihi ḥāṣibā, wa min-hum man akhażat-huṣ-ṣaiḥah, wa min-hum man khasafnā bihil-arḍ, wa min-hum man agraqnā, wa mā kānallāhu liyaẓlimahum wa lāking kānū anfusahum yaẓlimụn'),
(3380, 29, 41, 'مَثَلُ الَّذِيْنَ اتَّخَذُوْا مِنْ دُوْنِ اللّٰهِ اَوْلِيَاۤءَ كَمَثَلِ الْعَنْكَبُوْتِۚ اِتَّخَذَتْ بَيْتًاۗ وَاِنَّ اَوْهَنَ الْبُيُوْتِ لَبَيْتُ الْعَنْكَبُوْتِۘ لَوْ كَانُوْا يَعْلَمُوْنَ', 'maṡalullażīnattakhażụ min dụnillāhi auliyā`a kamaṡalil-\'angkabụt, ittakhażat baitā, wa inna auhanal-buyụti labaitul-\'angkabụt, lau kānụ ya\'lamụn'),
(3381, 29, 42, 'اِنَّ اللّٰهَ يَعْلَمُ مَا يَدْعُوْنَ مِنْ دُوْنِهٖ مِنْ شَيْءٍۗ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ', 'innallāha ya\'lamu mā yad\'ụna min dụnihī min syaī`, wa huwal-\'azīzul-ḥakīm'),
(3382, 29, 43, 'وَتِلْكَ الْاَمْثَالُ نَضْرِبُهَا لِلنَّاسِۚ وَمَا يَعْقِلُهَآ اِلَّا الْعَالِمُوْنَ', 'wa tilkal-amṡālu naḍribuhā lin-nās, wa mā ya\'qiluhā illal-\'ālimụn'),
(3383, 29, 44, 'خَلَقَ اللّٰهُ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّلْمُؤْمِنِيْنَ ۔', 'khalaqallāhus-samāwāti wal-arḍa bil-ḥaqq, inna fī żālika la`āyatal lil-mu`minīn'),
(3384, 29, 45, 'اُتْلُ مَآ اُوْحِيَ اِلَيْكَ مِنَ الْكِتٰبِ وَاَقِمِ الصَّلٰوةَۗ اِنَّ الصَّلٰوةَ تَنْهٰى عَنِ الْفَحْشَاۤءِ وَالْمُنْكَرِ ۗوَلَذِكْرُ اللّٰهِ اَكْبَرُ ۗوَاللّٰهُ يَعْلَمُ مَا تَصْنَعُوْنَ', 'utlu mā ụḥiya ilaika minal-kitābi wa aqimiṣ-ṣalāh, innaṣ-ṣalāta tan-hā \'anil-faḥsyā`i wal-mungkar, walażikrullāhi akbar, wallāhu ya\'lamu mā taṣna\'ụn'),
(3385, 29, 46, '۞ وَلَا تُجَادِلُوْٓا اَهْلَ الْكِتٰبِ اِلَّا بِالَّتِيْ هِيَ اَحْسَنُۖ اِلَّا الَّذِيْنَ ظَلَمُوْا مِنْهُمْ وَقُوْلُوْٓا اٰمَنَّا بِالَّذِيْٓ اُنْزِلَ اِلَيْنَا وَاُنْزِلَ اِلَيْكُمْ وَاِلٰهُنَا وَاِلٰهُكُمْ وَاحِدٌ وَّنَحْنُ لَهٗ مُسْلِمُوْنَ', 'wa lā tujādilū ahlal-kitābi illā billatī hiya aḥsanu illallażīna ẓalamụ min-hum wa qụlū āmannā billażī unzila ilainā wa unzila ilaikum wa ilāhunā wa ilāhukum wāḥiduw wa naḥnu lahụ muslimụn'),
(3386, 29, 47, 'وَكَذٰلِكَ اَنْزَلْنَآ اِلَيْكَ الْكِتٰبَۗ فَالَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يُؤْمِنُوْنَ بِهٖۚ وَمِنْ هٰٓؤُلَاۤءِ مَنْ يُّؤْمِنُ بِهٖۗ وَمَا يَجْحَدُ بِاٰيٰتِنَآ اِلَّا الْكٰفِرُوْنَ', 'wa każālika anzalnā ilaikal-kitāb, fallażīna ātaināhumul-kitāba yu`minụna bih, wa min hā`ulā`i may yu`minu bih, wa mā yaj-ḥadu bi`āyātinā illal-kāfirụn'),
(3387, 29, 48, 'وَمَا كُنْتَ تَتْلُوْا مِنْ قَبْلِهٖ مِنْ كِتٰبٍ وَّلَا تَخُطُّهٗ بِيَمِيْنِكَ اِذًا لَّارْتَابَ الْمُبْطِلُوْنَ', 'wa mā kunta tatlụ ming qablihī ming kitābiw wa lā takhuṭṭuhụ biyamīnika iżal lartābal-mubṭilụn'),
(3388, 29, 49, 'بَلْ هُوَ اٰيٰتٌۢ بَيِّنٰتٌ فِيْ صُدُوْرِ الَّذِيْنَ اُوْتُوا الْعِلْمَۗ وَمَا يَجْحَدُ بِاٰيٰتِنَآ اِلَّا الظّٰلِمُوْنَ', 'bal huwa āyātum bayyinātun fī ṣudụrillażīna ụtul-\'ilm, wa mā yaj-ḥadu bi`āyātinā illaẓ-ẓālimụn'),
(3389, 29, 50, 'وَقَالُوْا لَوْلَآ اُنْزِلَ عَلَيْهِ اٰيٰتٌ مِّنْ رَّبِّهٖ ۗ قُلْ اِنَّمَا الْاٰيٰتُ عِنْدَ اللّٰهِ ۗوَاِنَّمَآ اَنَا۠ نَذِيْرٌ مُّبِيْنٌ', 'wa qālụ lau lā unzila \'alaihi āyātum mir rabbih, qul innamal-āyātu \'indallāh, wa innamā ana nażīrum mubīn'),
(3390, 29, 51, 'اَوَلَمْ يَكْفِهِمْ اَنَّآ اَنْزَلْنَا عَلَيْكَ الْكِتٰبَ يُتْلٰى عَلَيْهِمْ ۗاِنَّ فِيْ ذٰلِكَ لَرَحْمَةً وَّذِكْرٰى لِقَوْمٍ يُّؤْمِنُوْنَ', 'a wa lam yakfihim annā anzalnā \'alaikal-kitāba yutlā \'alaihim, inna fī żālika laraḥmataw wa żikrā liqaumiy yu`minụn'),
(3391, 29, 52, 'قُلْ كَفٰى بِاللّٰهِ بَيْنِيْ وَبَيْنَكُمْ شَهِيْدًاۚ يَعْلَمُ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ وَالَّذِيْنَ اٰمَنُوْا بِالْبَاطِلِ وَكَفَرُوْا بِاللّٰهِ اُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ', 'qul kafā billāhi bainī wa bainakum syahīdā, ya\'lamu mā fis-samāwāti wal-arḍ, wallażīna āmanụ bil-bāṭili wa kafarụ billāhi ulā`ika humul-khāsirụn'),
(3392, 29, 53, 'وَيَسْتَعْجِلُوْنَكَ بِالْعَذَابِۗ وَلَوْلَآ اَجَلٌ مُّسَمًّى لَّجَاۤءَهُمُ الْعَذَابُۗ وَلَيَأْتِيَنَّهُمْ بَغْتَةً وَّهُمْ لَا يَشْعُرُوْنَ', 'wa yasta\'jilụnaka bil-\'ażāb, walau lā ajalum musammal lajā`ahumul-\'ażāb, wa laya`tiyannahum bagtataw wa hum lā yasy\'urụn'),
(3393, 29, 54, 'يَسْتَعْجِلُوْنَكَ بِالْعَذَابِۗ وَاِنَّ جَهَنَّمَ لَمُحِيْطَةٌ ۢ بِالْكٰفِرِيْنَۙ', 'yasta\'jilụnaka bil-\'ażāb, wa inna jahannama lamuḥīṭatum bil-kāfirīn'),
(3394, 29, 55, 'يَوْمَ يَغْشٰىهُمُ الْعَذَابُ مِنْ فَوْقِهِمْ وَمِنْ تَحْتِ اَرْجُلِهِمْ وَيَقُوْلُ ذُوْقُوْا مَا كُنْتُمْ تَعْمَلُوْنَ', 'yauma yagsyāhumul-\'ażābu min fauqihim wa min taḥti arjulihim wa yaqụlu żụqụ mā kuntum ta\'malụn'),
(3395, 29, 56, 'يٰعِبَادِيَ الَّذِيْنَ اٰمَنُوْٓا اِنَّ اَرْضِيْ وَاسِعَةٌ فَاِيَّايَ فَاعْبُدُوْنِ', 'yā \'ibādiyallażīna āmanū inna arḍī wāsi\'atun fa iyyāya fa\'budụn'),
(3396, 29, 57, 'كُلُّ نَفْسٍ ذَاۤىِٕقَةُ الْمَوْتِۗ ثُمَّ اِلَيْنَا تُرْجَعُوْنَ', 'kullu nafsin żā`iqatul-maụt, ṡumma ilainā turja\'ụn'),
(3397, 29, 58, 'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَنُبَوِّئَنَّهُمْ مِّنَ الْجَنَّةِ غُرَفًا تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَاۗ نِعْمَ اَجْرُ الْعٰمِلِيْنَۖ', 'wallażīna āmanụ wa \'amiluṣ-ṣāliḥāti lanubawwi`annahum minal-jannati gurafan tajrī min taḥtihal-an-hāru khālidīna fīhā, ni\'ma ajrul-\'āmilīn'),
(3398, 29, 59, 'الَّذِيْنَ صَبَرُوْا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُوْنَ', 'allażīna ṣabarụ wa \'alā rabbihim yatawakkalụn'),
(3399, 29, 60, 'وَكَاَيِّنْ مِّنْ دَاۤبَّةٍ لَّا تَحْمِلُ رِزْقَهَاۖ اللّٰهُ يَرْزُقُهَا وَاِيَّاكُمْ وَهُوَ السَّمِيْعُ الْعَلِيْمُ', 'wa ka`ayyim min dābbatil lā taḥmilu rizqahallāhu yarzuquhā wa iyyākum wa huwas-samī\'ul-\'alīm'),
(3400, 29, 61, 'وَلَىِٕنْ سَاَلْتَهُمْ مَّنْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ لَيَقُوْلُنَّ اللّٰهُ ۗفَاَنّٰى يُؤْفَكُوْنَ', 'wa la`in sa`altahum man khalaqas-samāwāti wal-arḍa wa sakhkharasy-syamsa wal-qamara layaqụlunnallāh, fa annā yu`fakụn'),
(3401, 29, 62, 'اَللّٰهُ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ وَيَقْدِرُ لَهٗ ۗاِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ', 'allāhu yabsuṭur-rizqa limay yasyā`u min \'ibādihī wa yaqdiru lah, innallāha bikulli syai`in \'alīm'),
(3402, 29, 63, 'وَلَىِٕنْ سَاَلْتَهُمْ مَّنْ نَّزَّلَ مِنَ السَّمَاۤءِ مَاۤءً فَاَحْيَا بِهِ الْاَرْضَ مِنْۢ بَعْدِ مَوْتِهَا لَيَقُوْلُنَّ اللّٰهُ ۙقُلِ الْحَمْدُ لِلّٰهِ ۗبَلْ اَكْثَرُهُمْ لَا يَعْقِلُوْنَ', 'wa la`in sa`altahum man nazzala minas-samā`i mā`an fa aḥyā bihil-arḍa mim ba\'di mautihā layaqụlunnallāhu qulil-ḥamdu lillāh, bal akṡaruhum lā ya\'qilụn'),
(3403, 29, 64, 'وَمَا هٰذِهِ الْحَيٰوةُ الدُّنْيَآ اِلَّا لَهْوٌ وَّلَعِبٌۗ وَاِنَّ الدَّارَ الْاٰخِرَةَ لَهِيَ الْحَيَوَانُۘ لَوْ كَانُوْا يَعْلَمُوْنَ', 'wa mā hāżihil-ḥayātud-dun-yā illā lahwuw wa la\'ib, wa innad-dāral-ākhirata lahiyal-ḥayawān, lau kānụ ya\'lamụn'),
(3404, 29, 65, 'فَاِذَا رَكِبُوْا فِى الْفُلْكِ دَعَوُا اللّٰهَ مُخْلِصِيْنَ لَهُ الدِّيْنَ ەۚ فَلَمَّا نَجّٰىهُمْ اِلَى الْبَرِّ اِذَا هُمْ يُشْرِكُوْنَۙ', 'fa iżā rakibụ fil-fulki da\'awullāha mukhliṣīna lahud-dīn, fa lammā najjāhum ilal-barri iżā hum yusyrikụn'),
(3405, 29, 66, 'لِيَكْفُرُوْا بِمَآ اٰتَيْنٰهُمْۙ وَلِيَتَمَتَّعُوْاۗ فَسَوْفَ يَعْلَمُوْنَ', 'liyakfurụ bimā ātaināhum wa liyatamatta\'ụ, fa saufa ya\'lamụn'),
(3406, 29, 67, 'اَوَلَمْ يَرَوْا اَنَّا جَعَلْنَا حَرَمًا اٰمِنًا وَّيُتَخَطَّفُ النَّاسُ مِنْ حَوْلِهِمْۗ اَفَبِالْبَاطِلِ يُؤْمِنُوْنَ وَبِنِعْمَةِ اللّٰهِ يَكْفُرُوْنَ', 'a wa lam yarau annā ja\'alnā ḥaraman āminaw wa yutakhaṭṭafun-nāsu min ḥaulihim, a fa bil-bāṭili yu`minụna wa bini\'matillāhi yakfurụn'),
(3407, 29, 68, 'وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا اَوْ كَذَّبَ بِالْحَقِّ لَمَّا جَاۤءَهٗ ۗ اَلَيْسَ فِيْ جَهَنَّمَ مَثْوًى لِّلْكٰفِرِيْنَ', 'wa man aẓlamu mim maniftarā \'alallāhi każiban au każżaba bil-ḥaqqi lammā jā`ah, a laisa fī jahannama maṡwal lil-kāfirīn'),
(3408, 29, 69, 'وَالَّذِيْنَ جَاهَدُوْا فِيْنَا لَنَهْدِيَنَّهُمْ سُبُلَنَاۗ وَاِنَّ اللّٰهَ لَمَعَ الْمُحْسِنِيْنَ', 'wallażīna jāhadụ fīnā lanahdiyannahum subulanā, wa innallāha lama\'al-muḥsinīn'),
(3409, 30, 1, ' الۤمّۤ ۚ', 'alif lām mīm'),
(3410, 30, 2, 'غُلِبَتِ الرُّوْمُۙ', 'gulibatir-rụm'),
(3411, 30, 3, 'فِيْٓ اَدْنَى الْاَرْضِ وَهُمْ مِّنْۢ بَعْدِ غَلَبِهِمْ سَيَغْلِبُوْنَۙ', 'fī adnal-arḍi wa hum mim ba\'di galabihim sayaglibụn'),
(3412, 30, 4, 'فِيْ بِضْعِ سِنِيْنَ ەۗ لِلّٰهِ الْاَمْرُ مِنْ قَبْلُ وَمِنْۢ بَعْدُ ۗوَيَوْمَىِٕذٍ يَّفْرَحُ الْمُؤْمِنُوْنَۙ', 'fī biḍ\'i sinīn, lillāhil-amru ming qablu wa mim ba\'d, wa yauma`iżiy yafraḥul-mu`minụn'),
(3413, 30, 5, 'بِنَصْرِ اللّٰهِ ۗيَنْصُرُ مَنْ يَّشَاۤءُۗ وَهُوَ الْعَزِيْزُ الرَّحِيْمُ', 'binaṣrillāh, yanṣuru may yasyā`, wa huwal-\'azīzur-raḥīm'),
(3414, 30, 6, 'وَعْدَ اللّٰهِ ۗ لَا يُخْلِفُ اللّٰهُ وَعْدَهٗ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ', 'wa\'dallāh, lā yukhlifullāhu wa\'dahụ wa lākinna akṡaran-nāsi lā ya\'lamụn'),
(3415, 30, 7, 'يَعْلَمُوْنَ ظَاهِرًا مِّنَ الْحَيٰوةِ الدُّنْيَاۖ وَهُمْ عَنِ الْاٰخِرَةِ هُمْ غٰفِلُوْنَ', 'ya\'lamụna ẓāhiram minal-ḥayātid-dun-yā wa hum \'anil-ākhirati hum gāfilụn'),
(3416, 30, 8, 'اَوَلَمْ يَتَفَكَّرُوْا فِيْٓ اَنْفُسِهِمْ ۗ مَا خَلَقَ اللّٰهُ السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَآ اِلَّا بِالْحَقِّ وَاَجَلٍ مُّسَمًّىۗ وَاِنَّ كَثِيْرًا مِّنَ النَّاسِ بِلِقَاۤئِ رَبِّهِمْ لَكٰفِرُوْنَ', 'a wa lam yatafakkarụ fī anfusihim, mā khalaqallāhus-samāwāti wal-arḍa wa mā bainahumā illā bil-ḥaqqi wa ajalim musammā, wa inna kaṡīram minan-nāsi biliqā`i rabbihim lakāfirụn');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(3417, 30, 9, 'اَوَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَيَنْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلِهِمْۗ كَانُوْٓا اَشَدَّ مِنْهُمْ قُوَّةً وَّاَثَارُوا الْاَرْضَ وَعَمَرُوْهَآ اَكْثَرَ مِمَّا عَمَرُوْهَا وَجَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِۗ فَمَا كَانَ اللّٰهُ لِيَظْلِمَهُمْ وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَۗ', 'a wa lam yasīrụ fil-arḍi fa yanẓurụ kaifa kāna \'āqibatullażīna ming qablihim, kānū asyadda min-hum quwwataw wa aṡārul-arḍa wa \'amarụhā akṡara mimmā \'amarụhā wa jā`at-hum rusuluhum bil-bayyināt, fa mā kānallāhu liyaẓlimahum wa lāking kānū anfusahum yaẓlimụn'),
(3418, 30, 10, 'ثُمَّ كَانَ عَاقِبَةَ الَّذِيْنَ اَسَاۤءُوا السُّوْۤاٰىٓ اَنْ كَذَّبُوْا بِاٰيٰتِ اللّٰهِ وَكَانُوْا بِهَا يَسْتَهْزِءُوْنَ', 'ṡumma kāna \'āqibatallażīna asā`us-sū`ā ang każżabụ bi`āyātillāhi wa kānụ bihā yastahzi`ụn'),
(3419, 30, 11, 'اَللّٰهُ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗ ثُمَّ اِلَيْهِ تُرْجَعُوْنَ', 'allāhu yabda`ul-khalqa ṡumma yu\'īduhụ ṡumma ilaihi turja\'ụn'),
(3420, 30, 12, 'وَيَوْمَ تَقُوْمُ السَّاعَةُ يُبْلِسُ الْمُجْرِمُوْنَ', 'wa yauma taqụmus-sā\'atu yublisul-mujrimụn'),
(3421, 30, 13, 'وَلَمْ يَكُنْ لَّهُمْ مِّنْ شُرَكَاۤىِٕهِمْ شُفَعٰۤؤُا وَكَانُوْا بِشُرَكَاۤىِٕهِمْ كٰفِرِيْنَ', 'wa lam yakul lahum min syurakā`ihim syufa\'ā`u wa kānụ bisyurakā`ihim kāfirīn'),
(3422, 30, 14, 'وَيَوْمَ تَقُوْمُ السَّاعَةُ يَوْمَىِٕذٍ يَّتَفَرَّقُوْنَ', 'wa yauma taqụmus-sā\'atu yauma`iżiy yatafarraqụn'),
(3423, 30, 15, 'فَاَمَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَهُمْ فِيْ رَوْضَةٍ يُّحْبَرُوْنَ', 'fa ammallażīna āmanụ wa \'amiluṣ-ṣāliḥāti fa hum fī rauḍatiy yuḥbarụn'),
(3424, 30, 16, 'وَاَمَّا الَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَا وَلِقَاۤئِ الْاٰخِرَةِ فَاُولٰۤىِٕكَ فِى الْعَذَابِ مُحْضَرُوْنَ', 'wa ammallażīna kafarụ wa każżabụ bi`āyātinā wa liqā`il-ākhirati fa ulā`ika fil-\'ażābi muḥḍarụn'),
(3425, 30, 17, 'فَسُبْحٰنَ اللّٰهِ حِيْنَ تُمْسُوْنَ وَحِيْنَ تُصْبِحُوْنَ', 'fa sub-ḥānallāhi ḥīna tumsụna wa ḥīna tuṣbiḥụn'),
(3426, 30, 18, 'وَلَهُ الْحَمْدُ فِى السَّمٰوٰتِ وَالْاَرْضِ وَعَشِيًّا وَّحِيْنَ تُظْهِرُوْنَ', 'wa lahul-ḥamdu fis-samāwāti wal-arḍi wa \'asyiyyaw wa ḥīna tuẓ-hirụn'),
(3427, 30, 19, 'يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَيُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَيُحْيِ الْاَرْضَ بَعْدَ مَوْتِهَا ۗوَكَذٰلِكَ تُخْرَجُوْنَ', 'yukhrijul-ḥayya minal-mayyiti wa yukhrijul-mayyita minal-ḥayyi wa yuḥyil-arḍa ba\'da mautihā, wa każālika tukhrajụn'),
(3428, 30, 20, 'وَمِنْ اٰيٰتِهٖٓ اَنْ خَلَقَكُمْ مِّنْ تُرَابٍ ثُمَّ اِذَآ اَنْتُمْ بَشَرٌ تَنْتَشِرُوْنَ', 'wa min āyātihī an khalaqakum min turābin ṡumma iżā antum basyarun tantasyirụn'),
(3429, 30, 21, 'وَمِنْ اٰيٰتِهٖٓ اَنْ خَلَقَ لَكُمْ مِّنْ اَنْفُسِكُمْ اَزْوَاجًا لِّتَسْكُنُوْٓا اِلَيْهَا وَجَعَلَ بَيْنَكُمْ مَّوَدَّةً وَّرَحْمَةً ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّتَفَكَّرُوْنَ', 'wa min āyātihī an khalaqa lakum min anfusikum azwājal litaskunū ilaihā wa ja\'ala bainakum mawaddataw wa raḥmah, inna fī żālika la`āyātil liqaumiy yatafakkarụn'),
(3430, 30, 22, 'وَمِنْ اٰيٰتِهٖ خَلْقُ السَّمٰوٰتِ وَالْاَرْضِ وَاخْتِلَافُ اَلْسِنَتِكُمْ وَاَلْوَانِكُمْۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّلْعٰلِمِيْنَ', 'wa min āyātihī khalqus-samāwāti wal-arḍi wakhtilāfu alsinatikum wa alwānikum, inna fī żālika la`āyātil lil-\'ālimīn'),
(3431, 30, 23, 'وَمِنْ اٰيٰتِهٖ مَنَامُكُمْ بِالَّيْلِ وَالنَّهَارِ وَابْتِغَاۤؤُكُمْ مِّنْ فَضْلِهٖۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّسْمَعُوْنَ', 'wa min āyātihī manāmukum bil-laili wan-nahāri wabtigā`ukum min faḍlih, inna fī żālika la`āyātil liqaumiy yasma\'ụn'),
(3432, 30, 24, 'وَمِنْ اٰيٰتِهٖ يُرِيْكُمُ الْبَرْقَ خَوْفًا وَّطَمَعًا وَّيُنَزِّلُ مِنَ السَّمَاۤءِ مَاۤءً فَيُحْيٖ بِهِ الْاَرْضَ بَعْدَ مَوْتِهَاۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّعْقِلُوْنَ', 'wa min āyātihī yurīkumul-barqa khaufaw wa ṭama\'aw wa yunazzilu minas-samā`i mā`an fa yuḥyī bihil-arḍa ba\'da mautihā, inna fī żālika la`āyātil liqaumiy ya\'qilụn'),
(3433, 30, 25, 'وَمِنْ اٰيٰتِهٖٓ اَنْ تَقُوْمَ السَّمَاۤءُ وَالْاَرْضُ بِاَمْرِهٖۗ ثُمَّ اِذَا دَعَاكُمْ دَعْوَةًۖ مِّنَ الْاَرْضِ اِذَآ اَنْتُمْ تَخْرُجُوْنَ', 'wa min āyātihī an taqụmas-samā`u wal-arḍu bi`amrih, ṡumma iżā da\'ākum da\'watam minal-arḍi iżā antum takhrujụn'),
(3434, 30, 26, 'وَلَهٗ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِۗ كُلٌّ لَّهٗ قَانِتُوْنَ', 'wa lahụ man fis-samāwāti wal-arḍ, kullul lahụ qānitụn'),
(3435, 30, 27, 'وَهُوَ الَّذِيْ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗ وَهُوَ اَهْوَنُ عَلَيْهِۗ وَلَهُ الْمَثَلُ الْاَعْلٰى فِى السَّمٰوٰتِ وَالْاَرْضِۚ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ', 'wa huwallażī yabda`ul-khalqa ṡumma yu\'īduhụ wa huwa ahwanu \'alaīh, wa lahul-maṡalul-a\'lā fis-samāwāti wal-arḍ, wa huwal-\'azīzul-ḥakīm'),
(3436, 30, 28, 'ضَرَبَ لَكُمْ مَّثَلًا مِّنْ اَنْفُسِكُمْۗ هَلْ لَّكُمْ مِّنْ مَّا مَلَكَتْ اَيْمَانُكُمْ مِّنْ شُرَكَاۤءَ فِيْ مَا رَزَقْنٰكُمْ فَاَنْتُمْ فِيْهِ سَوَاۤءٌ تَخَافُوْنَهُمْ كَخِيْفَتِكُمْ اَنْفُسَكُمْۗ كَذٰلِكَ نُفَصِّلُ الْاٰيٰتِ لِقَوْمٍ يَّعْقِلُوْنَ', 'ḍaraba lakum maṡalam min anfusikum, hal lakum mimmā malakat aimānukum min syurakā`a fī mā razaqnākum fa antum fīhi sawā`un takhāfụnahum kakhīfatikum anfusakum, każālika nufaṣṣilul-āyāti liqaumiy ya\'qilụn'),
(3437, 30, 29, 'بَلِ اتَّبَعَ الَّذِيْنَ ظَلَمُوْٓا اَهْوَاۤءَهُمْ بِغَيْرِ عِلْمٍۗ فَمَنْ يَّهْدِيْ مَنْ اَضَلَّ اللّٰهُ ۗوَمَا لَهُمْ مِّنْ نّٰصِرِيْنَ', 'balittaba\'allażīna ẓalamū ahwā`ahum bigairi \'ilm, fa may yahdī man aḍallallāh, wa mā lahum min nāṣirīn'),
(3438, 30, 30, 'فَاَقِمْ وَجْهَكَ لِلدِّيْنِ حَنِيْفًاۗ فِطْرَتَ اللّٰهِ الَّتِيْ فَطَرَ النَّاسَ عَلَيْهَاۗ لَا تَبْدِيْلَ لِخَلْقِ اللّٰهِ ۗذٰلِكَ الدِّيْنُ الْقَيِّمُۙ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَۙ', 'fa aqim waj-haka lid-dīni ḥanīfā, fiṭratallāhillatī faṭaran-nāsa \'alaihā, lā tabdīla likhalqillāh, żālikad-dīnul qayyimu wa lākinna akṡaran-nāsi lā ya\'lamụn'),
(3439, 30, 31, '۞ مُنِيْبِيْنَ اِلَيْهِ وَاتَّقُوْهُ وَاَقِيْمُوا الصَّلٰوةَ وَلَا تَكُوْنُوْا مِنَ الْمُشْرِكِيْنَۙ', 'munībīna ilaihi wattaqụhu wa aqīmuṣ-ṣalāta wa lā takụnụ minal-musyrikīn'),
(3440, 30, 32, 'مِنَ الَّذِيْنَ فَرَّقُوْا دِيْنَهُمْ وَكَانُوْا شِيَعًا ۗ كُلُّ حِزْبٍۢ بِمَا لَدَيْهِمْ فَرِحُوْنَ', 'minallażīna farraqụ dīnahum wa kānụ syiya\'ā, kullu ḥizbim bimā ladaihim fariḥụn'),
(3441, 30, 33, 'وَاِذَا مَسَّ النَّاسَ ضُرٌّ دَعَوْا رَبَّهُمْ مُّنِيْبِيْنَ اِلَيْهِ ثُمَّ اِذَآ اَذَاقَهُمْ مِّنْهُ رَحْمَةً اِذَا فَرِيْقٌ مِّنْهُمْ بِرَبِّهِمْ يُشْرِكُوْنَۙ', 'wa iżā massan-nāsa ḍurrun da\'au rabbahum munībīna ilaihi ṡumma iżā ażāqahum min-hu raḥmatan iżā farīqum min-hum birabbihim yusyrikụn'),
(3442, 30, 34, 'لِيَكْفُرُوْا بِمَآ اٰتَيْنٰهُمْۗ فَتَمَتَّعُوْاۗ فَسَوْفَ تَعْلَمُوْنَ', 'liyakfurụ bimā ātaināhum, fa tamatta\'ụ, fa saufa ta\'lamụn'),
(3443, 30, 35, 'اَمْ اَنْزَلْنَا عَلَيْهِمْ سُلْطٰنًا فَهُوَ يَتَكَلَّمُ بِمَا كَانُوْا بِهٖ يُشْرِكُوْنَ', 'am anzalnā \'alaihim sulṭānan fa huwa yatakallamu bimā kānụ bihī yusyrikụn'),
(3444, 30, 36, 'وَاِذَآ اَذَقْنَا النَّاسَ رَحْمَةً فَرِحُوْا بِهَاۗ وَاِنْ تُصِبْهُمْ سَيِّئَةٌ ۢبِمَا قَدَّمَتْ اَيْدِيْهِمْ اِذَا هُمْ يَقْنَطُوْنَ', 'wa iżā ażaqnan-nāsa raḥmatan fariḥụ bihā, wa in tuṣib-hum sayyi`atum bimā qaddamat aidīhim iżā hum yaqnaṭụn'),
(3445, 30, 37, 'اَوَلَمْ يَرَوْا اَنَّ اللّٰهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ', 'a wa lam yarau annallāha yabsuṭur-rizqa limay yasyā`u wa yaqdir, inna fī żālika la`āyātil liqaumiy yu`minụn'),
(3446, 30, 38, 'فَاٰتِ ذَا الْقُرْبٰى حَقَّهٗ وَالْمِسْكِيْنَ وَابْنَ السَّبِيْلِۗ ذٰلِكَ خَيْرٌ لِّلَّذِيْنَ يُرِيْدُوْنَ وَجْهَ اللّٰهِ ۖوَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ', 'fa āti żal-qurbā ḥaqqahụ wal-miskīna wabnas-sabīl, żālika khairul lillażīna yurīdụna waj-hallāhi wa ulā`ika humul-mufliḥụn'),
(3447, 30, 39, 'وَمَآ اٰتَيْتُمْ مِّنْ رِّبًا لِّيَرْبُوَا۠ فِيْٓ اَمْوَالِ النَّاسِ فَلَا يَرْبُوْا عِنْدَ اللّٰهِ ۚوَمَآ اٰتَيْتُمْ مِّنْ زَكٰوةٍ تُرِيْدُوْنَ وَجْهَ اللّٰهِ فَاُولٰۤىِٕكَ هُمُ الْمُضْعِفُوْنَ', 'wa mā ātaitum mir ribal liyarbuwa fī amwālin-nāsi fa lā yarbụ \'indallāh, wa mā ātaitum min zakātin turīdụna waj-hallāhi fa ulā`ika humul-muḍ\'ifụn'),
(3448, 30, 40, 'اَللّٰهُ الَّذِيْ خَلَقَكُمْ ثُمَّ رَزَقَكُمْ ثُمَّ يُمِيْتُكُمْ ثُمَّ يُحْيِيْكُمْۗ هَلْ مِنْ شُرَكَاۤىِٕكُمْ مَّنْ يَّفْعَلُ مِنْ ذٰلِكُمْ مِّنْ شَيْءٍۗ سُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يُشْرِكُوْنَ', 'allāhullażī khalaqakum ṡumma razaqakum ṡumma yumītukum ṡumma yuḥyīkum, hal min syurakā`ikum may yaf\'alu min żālikum min syaī`, sub-ḥānahụ wa ta\'ālā \'ammā yusyrikụn'),
(3449, 30, 41, 'ظَهَرَ الْفَسَادُ فِى الْبَرِّ وَالْبَحْرِ بِمَا كَسَبَتْ اَيْدِى النَّاسِ لِيُذِيْقَهُمْ بَعْضَ الَّذِيْ عَمِلُوْا لَعَلَّهُمْ يَرْجِعُوْنَ', 'ẓaharal-fasādu fil-barri wal-baḥri bimā kasabat aidin-nāsi liyużīqahum ba\'ḍallażī \'amilụ la\'allahum yarji\'ụn'),
(3450, 30, 42, 'قُلْ سِيْرُوْا فِى الْاَرْضِ فَانْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلُۗ كَانَ اَكْثَرُهُمْ مُّشْرِكِيْنَ', 'qul sīrụ fil-arḍi fanẓurụ kaifa kāna \'āqibatullażīna ming qabl, kāna akṡaruhum musyrikīn'),
(3451, 30, 43, 'فَاَقِمْ وَجْهَكَ لِلدِّيْنِ الْقَيِّمِ مِنْ قَبْلِ اَنْ يَّأْتِيَ يَوْمٌ لَّا مَرَدَّ لَهٗ مِنَ اللّٰهِ يَوْمَىِٕذٍ يَّصَّدَّعُوْنَ', 'fa aqim waj-haka liddīnil-qayyimi ming qabli ay ya`tiya yaumul lā maradda lahụ minallāhi yauma`iżiy yaṣṣadda\'ụn'),
(3452, 30, 44, 'مَنْ كَفَرَ فَعَلَيْهِ كُفْرُهٗۚ وَمَنْ عَمِلَ صَالِحًا فَلِاَنْفُسِهِمْ يَمْهَدُوْنَۙ', 'mang kafara fa \'alaihi kufruh, wa man \'amila ṣāliḥan fa li`anfusihim yam-hadụn'),
(3453, 30, 45, 'لِيَجْزِيَ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ مِنْ فَضْلِهٖۗ اِنَّهٗ لَا يُحِبُّ الْكٰفِرِيْنَ', 'liyajziyallażīna āmanụ wa \'amiluṣ-ṣāliḥāti min faḍlih, innahụ lā yuḥibbul-kāfirīn'),
(3454, 30, 46, 'وَمِنْ اٰيٰتِهٖٓ اَنْ يُّرْسِلَ الرِّيٰحَ مُبَشِّرٰتٍ وَّلِيُذِيْقَكُمْ مِّنْ رَّحْمَتِهٖ وَلِتَجْرِيَ الْفُلْكُ بِاَمْرِهٖ وَلِتَبْتَغُوْا مِنْ فَضْلِهٖ وَلَعَلَّكُمْ تَشْكُرُوْنَ', 'wa min āyātihī ay yursilar-riyāḥa mubasysyirātiw wa liyużīqakum mir raḥmatihī wa litajriyal-fulku bi`amrihī wa litabtagụ min faḍlihī wa la\'allakum tasykurụn'),
(3455, 30, 47, 'وَلَقَدْ اَرْسَلْنَا مِنْ قَبْلِكَ رُسُلًا اِلٰى قَوْمِهِمْ فَجَاۤءُوْهُمْ بِالْبَيِّنٰتِ فَانْتَقَمْنَا مِنَ الَّذِيْنَ اَجْرَمُوْاۗ وَكَانَ حَقًّاۖ عَلَيْنَا نَصْرُ الْمُؤْمِنِيْنَ', 'wa laqad arsalnā ming qablika rusulan ilā qaumihim fa jā`ụhum bil-bayyināti fantaqamnā minallażīna ajramụ, wa kāna ḥaqqan \'alainā naṣrul-mu`minīn'),
(3456, 30, 48, 'اَللّٰهُ الَّذِيْ يُرْسِلُ الرِّيٰحَ فَتُثِيْرُ سَحَابًا فَيَبْسُطُهٗ فِى السَّمَاۤءِ كَيْفَ يَشَاۤءُ وَيَجْعَلُهٗ كِسَفًا فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلٰلِهٖۚ فَاِذَآ اَصَابَ بِهٖ مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖٓ اِذَا هُمْ يَسْتَبْشِرُوْنَۚ', 'allāhullażī yursilur-riyāḥa fa tuṡīru saḥāban fa yabsuṭuhụ fis-samā`i kaifa yasyā`u wa yaj\'aluhụ kisafan fa taral-wadqa yakhruju min khilālih, fa iżā aṣāba bihī may yasyā`u min \'ibādihī iżā hum yastabsyirụn'),
(3457, 30, 49, 'وَاِنْ كَانُوْا مِنْ قَبْلِ اَنْ يُّنَزَّلَ عَلَيْهِمْ مِّنْ قَبْلِهٖ لَمُبْلِسِيْنَۚ', 'wa ing kānụ ming qabli ay yunazzala \'alaihim ming qablihī lamublisīn'),
(3458, 30, 50, 'فَانْظُرْ اِلٰٓى اٰثٰرِ رَحْمَتِ اللّٰهِ كَيْفَ يُحْيِ الْاَرْضَ بَعْدَ مَوْتِهَاۗ اِنَّ ذٰلِكَ لَمُحْيِ الْمَوْتٰىۚ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'fanẓur ilā āṡāri raḥmatillāhi kaifa yuḥyil-arḍa ba\'da mautihā, inna żālika lamuḥyil mautā, wa huwa \'alā kulli syai`ing qadīr'),
(3459, 30, 51, 'وَلَىِٕنْ اَرْسَلْنَا رِيْحًا فَرَاَوْهُ مُصْفَرًّا لَّظَلُّوْا مِنْۢ بَعْدِهٖ يَكْفُرُوْنَ', 'wa la`in arsalnā rīḥan fa ra`auhu muṣfarral laẓallụ mim ba\'dihī yakfurụn'),
(3460, 30, 52, 'فَاِنَّكَ لَا تُسْمِعُ الْمَوْتٰى وَلَا تُسْمِعُ الصُّمَّ الدُّعَاۤءَ اِذَا وَلَّوْا مُدْبِرِيْنَ', 'fa innaka lā tusmi\'ul-mautā wa lā tusmi\'uṣ-ṣummad-du\'ā`a iżā wallau mudbirīn'),
(3461, 30, 53, 'وَمَآ اَنْتَ بِهٰدِ الْعُمْيِ عَنْ ضَلٰلَتِهِمْۗ اِنْ تُسْمِعُ اِلَّا مَنْ يُّؤْمِنُ بِاٰيٰتِنَا فَهُمْ مُّسْلِمُوْنَ', 'wa mā anta bihādil-\'umyi \'an ḍalālatihim, in tusmi\'u illā may yu`minu bi`āyātinā fa hum muslimụn'),
(3462, 30, 54, '۞ اَللّٰهُ الَّذِيْ خَلَقَكُمْ مِّنْ ضَعْفٍ ثُمَّ جَعَلَ مِنْۢ بَعْدِ ضَعْفٍ قُوَّةً ثُمَّ جَعَلَ مِنْۢ بَعْدِ قُوَّةٍ ضَعْفًا وَّشَيْبَةً ۗيَخْلُقُ مَا يَشَاۤءُۚ وَهُوَ الْعَلِيْمُ الْقَدِيْرُ', 'allāhullażī khalaqakum min ḍa\'fin ṡumma ja\'ala mim ba\'di ḍa\'fing quwwatan ṡumma ja\'ala mim ba\'di quwwatin ḍa\'faw wa syaibah, yakhluqu mā yasyā`, wa huwal-\'alīmul-qadīr'),
(3463, 30, 55, 'وَيَوْمَ تَقُوْمُ السَّاعَةُ يُقْسِمُ الْمُجْرِمُوْنَ ەۙ مَا لَبِثُوْا غَيْرَ سَاعَةٍ ۗ كَذٰلِكَ كَانُوْا يُؤْفَكُوْنَ', 'wa yauma taqụmus-sā\'atu yuqsimul-mujrimụna mā labiṡụ gaira sā\'ah, każālika kānụ yu`fakụn'),
(3464, 30, 56, 'وَقَالَ الَّذِيْنَ اُوْتُوا الْعِلْمَ وَالْاِيْمَانَ لَقَدْ لَبِثْتُمْ فِيْ كِتٰبِ اللّٰهِ اِلٰى يَوْمِ الْبَعْثِۖ فَهٰذَا يَوْمُ الْبَعْثِ وَلٰكِنَّكُمْ كُنْتُمْ لَا تَعْلَمُوْنَ', 'wa qālallażīna ụtul-\'ilma wal-īmāna laqad labiṡtum fī kitābillāhi ilā yaumil-ba\'ṡi fa hāżā yaumul-ba\'ṡi wa lākinnakum kuntum lā ta\'lamụn'),
(3465, 30, 57, 'فَيَوْمَىِٕذٍ لَّا يَنْفَعُ الَّذِيْنَ ظَلَمُوْا مَعْذِرَتُهُمْ وَلَا هُمْ يُسْتَعْتَبُوْنَ', 'fa yauma`iżil lā yanfa\'ullażīna ẓalamụ ma\'żiratuhum wa lā hum yusta\'tabụn'),
(3466, 30, 58, 'وَلَقَدْ ضَرَبْنَا لِلنَّاسِ فِيْ هٰذَا الْقُرْاٰنِ مِنْ كُلِّ مَثَلٍۗ وَلَىِٕنْ جِئْتَهُمْ بِاٰيَةٍ لَّيَقُوْلَنَّ الَّذِيْنَ كَفَرُوْٓا اِنْ اَنْتُمْ اِلَّا مُبْطِلُوْنَ', 'wa laqad ḍarabnā lin-nāsi fī hāżal-qur`āni ming kulli maṡal, wa la`in ji`tahum bi`āyatil layaqụlannallażīna kafarū in antum illā mubṭilụn'),
(3467, 30, 59, 'كَذٰلِكَ يَطْبَعُ اللّٰهُ عَلٰى قُلُوْبِ الَّذِيْنَ لَا يَعْلَمُوْنَ', 'każālika yaṭba\'ullāhu \'alā qulụbillażīna lā ya\'lamụn'),
(3468, 30, 60, 'فَاصْبِرْ اِنَّ وَعْدَ اللّٰهِ حَقٌّ وَّلَا يَسْتَخِفَّنَّكَ الَّذِيْنَ لَا يُوْقِنُوْنَ', 'faṣbir inna wa\'dallāhi ḥaqquw wa lā yastakhiffannakallażīna lā yụqinụn'),
(3469, 31, 1, ' الۤمّۤ ۗ', 'alif lām mīm'),
(3470, 31, 2, 'تِلْكَ اٰيٰتُ الْكِتٰبِ الْحَكِيْمِۙ', 'tilka āyātul-kitābil-ḥakīm'),
(3471, 31, 3, 'هُدًى وَّرَحْمَةً لِّلْمُحْسِنِيْنَۙ', 'hudaw wa raḥmatal lil-muḥsinīn'),
(3472, 31, 4, 'الَّذِيْنَ يُقِيْمُوْنَ الصَّلٰوةَ وَيُؤْتُوْنَ الزَّكٰوةَ وَهُمْ بِالْاٰخِرَةِ هُمْ يُوْقِنُوْنَۗ', 'allażīna yuqīmụnaṣ-ṣalāta wa yu`tụnaz-zakāta wa hum bil-ākhirati hum yụqinụn'),
(3473, 31, 5, 'اُولٰۤىِٕكَ عَلٰى هُدًى مِّنْ رَّبِّهِمْ وَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ', 'ulā`ika \'alā hudam mir rabbihim wa ulā`ika humul-mufliḥụn'),
(3474, 31, 6, 'وَمِنَ النَّاسِ مَنْ يَّشْتَرِيْ لَهْوَ الْحَدِيْثِ لِيُضِلَّ عَنْ سَبِيْلِ اللّٰهِ بِغَيْرِ عِلْمٍۖ وَّيَتَّخِذَهَا هُزُوًاۗ اُولٰۤىِٕكَ لَهُمْ عَذَابٌ مُّهِيْنٌ', 'wa minan-nāsi may yasytarī lahwal-ḥadīṡi liyuḍilla \'an sabīlillāhi bigairi \'ilmiw wa yattakhiżahā huzuwā, ulā`ika lahum \'ażābum muhīn'),
(3475, 31, 7, 'وَاِذَا تُتْلٰى عَلَيْهِ اٰيٰتُنَا وَلّٰى مُسْتَكْبِرًا كَاَنْ لَّمْ يَسْمَعْهَا كَاَنَّ فِيْٓ اُذُنَيْهِ وَقْرًاۚ فَبَشِّرْهُ بِعَذَابٍ اَلِيْمٍ', 'wa iżā tutlā \'alaihi āyātunā wallā mustakbirang ka`al lam yasma\'hā ka`anna fī użunaihi waqrā, fa basysyir-hu bi\'ażābin alīm'),
(3476, 31, 8, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ جَنّٰتُ النَّعِيْمِۙ', 'innallażīna āmanu wa \'amiluṣ-ṣāliḥāti lahum jannātun na\'īm'),
(3477, 31, 9, 'خٰلِدِيْنَ فِيْهَاۗ وَعْدَ اللّٰهِ حَقًّاۗ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ', 'khālidīna fīhā, wa\'dallāhi ḥaqqā, wa huwal-\'azīzul-ḥakīm'),
(3478, 31, 10, 'خَلَقَ السَّمٰوٰتِ بِغَيْرِ عَمَدٍ تَرَوْنَهَا وَاَلْقٰى فِى الْاَرْضِ رَوَاسِيَ اَنْ تَمِيْدَ بِكُمْ وَبَثَّ فِيْهَا مِنْ كُلِّ دَاۤبَّةٍۗ وَاَنْزَلْنَا مِنَ السَّمَاۤءِ مَاۤءً فَاَنْۢبَتْنَا فِيْهَا مِنْ كُلِّ زَوْجٍ كَرِيْمٍ', 'khalaqas-samāwāti bigairi \'amadin taraunahā wa alqā fil-arḍi rawāsiya an tamīda bikum wa baṡṡa fīhā ming kulli dābbah, wa anzalnā minas-samā`i mā`an fa ambatnā fīhā ming kulli zaujing karīm'),
(3479, 31, 11, 'هٰذَا خَلْقُ اللّٰهِ فَاَرُوْنِيْ مَاذَا خَلَقَ الَّذِيْنَ مِنْ دُوْنِهٖۗ بَلِ الظّٰلِمُوْنَ فِيْ ضَلٰلٍ مُّبِيْنٍ', 'hāżā khalqullāhi fa arụnī māżā khalaqallażīna min dụnih, baliẓ-ẓālimụna fī ḍalālim mubīn'),
(3480, 31, 12, 'وَلَقَدْ اٰتَيْنَا لُقْمٰنَ الْحِكْمَةَ اَنِ اشْكُرْ لِلّٰهِ ۗوَمَنْ يَّشْكُرْ فَاِنَّمَا يَشْكُرُ لِنَفْسِهٖۚ وَمَنْ كَفَرَ فَاِنَّ اللّٰهَ غَنِيٌّ حَمِيْدٌ', 'wa laqad ātainā luqmānal-ḥikmata anisykur lillāh, wa may yasykur fa innamā yasykuru linafsih, wa mang kafara fa innallāha ganiyyun ḥamīd'),
(3481, 31, 13, 'وَاِذْ قَالَ لُقْمٰنُ لِابْنِهٖ وَهُوَ يَعِظُهٗ يٰبُنَيَّ لَا تُشْرِكْ بِاللّٰهِ ۗاِنَّ الشِّرْكَ لَظُلْمٌ عَظِيْمٌ', 'wa iż qāla luqmānu libnihī wa huwa ya\'iẓuhụ yā bunayya lā tusyrik billāh, innasy-syirka laẓulmun \'aẓīm'),
(3482, 31, 14, 'وَوَصَّيْنَا الْاِنْسَانَ بِوَالِدَيْهِۚ حَمَلَتْهُ اُمُّهٗ وَهْنًا عَلٰى وَهْنٍ وَّفِصَالُهٗ فِيْ عَامَيْنِ اَنِ اشْكُرْ لِيْ وَلِوَالِدَيْكَۗ اِلَيَّ الْمَصِيْرُ', 'wa waṣṣainal-insāna biwālidaīh, ḥamalat-hu ummuhụ wahnan \'alā wahniw wa fiṣāluhụ fī \'āmaini anisykur lī wa liwālidaīk, ilayyal-maṣīr'),
(3483, 31, 15, 'وَاِنْ جَاهَدٰكَ عَلٰٓى اَنْ تُشْرِكَ بِيْ مَا لَيْسَ لَكَ بِهٖ عِلْمٌ فَلَا تُطِعْهُمَا وَصَاحِبْهُمَا فِى الدُّنْيَا مَعْرُوْفًا ۖوَّاتَّبِعْ سَبِيْلَ مَنْ اَنَابَ اِلَيَّۚ ثُمَّ اِلَيَّ مَرْجِعُكُمْ فَاُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ', 'wa in jāhadāka \'alā an tusyrika bī mā laisa laka bihī \'ilmun fa lā tuṭi\'humā wa ṣāḥib-humā fid-dun-yā ma\'rụfaw wattabi\' sabīla man anāba ilayy, ṡumma ilayya marji\'ukum fa unabbi`ukum bimā kuntum ta\'malụn'),
(3484, 31, 16, 'يٰبُنَيَّ اِنَّهَآ اِنْ تَكُ مِثْقَالَ حَبَّةٍ مِّنْ خَرْدَلٍ فَتَكُنْ فِيْ صَخْرَةٍ اَوْ فِى السَّمٰوٰتِ اَوْ فِى الْاَرْضِ يَأْتِ بِهَا اللّٰهُ ۗاِنَّ اللّٰهَ لَطِيْفٌ خَبِيْرٌ', 'yā bunayya innahā in taku miṡqāla ḥabbatim min khardalin fa takun fī ṣakhratin au fis-samāwāti au fil-arḍi ya`ti bihallāh, innallāha laṭīfun khabīr'),
(3485, 31, 17, 'يٰبُنَيَّ اَقِمِ الصَّلٰوةَ وَأْمُرْ بِالْمَعْرُوْفِ وَانْهَ عَنِ الْمُنْكَرِ وَاصْبِرْ عَلٰى مَآ اَصَابَكَۗ اِنَّ ذٰلِكَ مِنْ عَزْمِ الْاُمُوْرِ', 'yā bunayya aqimiṣ-ṣalāta wa`mur bil-ma\'rụfi wan-ha \'anil-mungkari waṣbir \'alā mā aṣābak, inna żālika min \'azmil-umụr'),
(3486, 31, 18, 'وَلَا تُصَعِّرْ خَدَّكَ لِلنَّاسِ وَلَا تَمْشِ فِى الْاَرْضِ مَرَحًاۗ اِنَّ اللّٰهَ لَا يُحِبُّ كُلَّ مُخْتَالٍ فَخُوْرٍۚ', 'wa lā tuṣa\'\'ir khaddaka lin-nāsi wa lā tamsyi fil-arḍi maraḥā, innallāha lā yuḥibbu kulla mukhtālin fakhụr'),
(3487, 31, 19, 'وَاقْصِدْ فِيْ مَشْيِكَ وَاغْضُضْ مِنْ صَوْتِكَۗ اِنَّ اَنْكَرَ الْاَصْوَاتِ لَصَوْتُ الْحَمِيْرِ', 'waqṣid fī masy-yika wagḍuḍ min ṣautik, inna angkaral-aṣwāti laṣautul-ḥamīr'),
(3488, 31, 20, 'اَلَمْ تَرَوْا اَنَّ اللّٰهَ سَخَّرَ لَكُمْ مَّا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ وَاَسْبَغَ عَلَيْكُمْ نِعَمَهٗ ظَاهِرَةً وَّبَاطِنَةً ۗوَمِنَ النَّاسِ مَنْ يُّجَادِلُ فِى اللّٰهِ بِغَيْرِ عِلْمٍ وَّلَا هُدًى وَّلَا كِتٰبٍ مُّنِيْرٍ', 'a lam tarau annallāha sakhkhara lakum mā fis-samāwāti wa mā fil-arḍi wa asbaga \'alaikum ni\'amahụ ẓāhirataw wa bāṭinah, wa minan-nāsi may yujādilu fillāhi bigairi \'ilmiw wa lā hudaw wa lā kitābim munīr'),
(3489, 31, 21, 'وَاِذَا قِيْلَ لَهُمُ اتَّبِعُوْا مَآ اَنْزَلَ اللّٰهُ قَالُوْا بَلْ نَتَّبِعُ مَا وَجَدْنَا عَلَيْهِ اٰبَاۤءَنَاۗ اَوَلَوْ كَانَ الشَّيْطٰنُ يَدْعُوْهُمْ اِلٰى عَذَابِ السَّعِيْرِ', 'wa iżā qīla lahumuttabi\'ụ ma anzalallāhu qālụ bal nattabi\'u mā wajadnā \'alaihi ābā`anā, a walau kānasy-syaiṭānu yad\'ụhum ilā \'ażābis-sa\'īr'),
(3490, 31, 22, '۞ وَمَنْ يُّسْلِمْ وَجْهَهٗٓ اِلَى اللّٰهِ وَهُوَ مُحْسِنٌ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقٰىۗ وَاِلَى اللّٰهِ عَاقِبَةُ الْاُمُوْرِ', 'wa may yuslim waj-hahū ilallāhi wa huwa muḥsinun fa qadistamsaka bil-\'urwatil-wuṡqā, wa ilallāhi \'āqibatul-umụr'),
(3491, 31, 23, 'وَمَنْ كَفَرَ فَلَا يَحْزُنْكَ كُفْرُهٗۗ اِلَيْنَا مَرْجِعُهُمْ فَنُنَبِّئُهُمْ بِمَا عَمِلُوْاۗ اِنَّ اللّٰهَ عَلِيْمٌۢ بِذَاتِ الصُّدُوْرِ', 'wa mang kafara fa lā yaḥzungka kufruh, ilainā marji\'uhum fa nunabbi`uhum bimā \'amilụ, innallāha \'alīmum biżātiṣ-ṣudụr'),
(3492, 31, 24, 'نُمَتِّعُهُمْ قَلِيْلًا ثُمَّ نَضْطَرُّهُمْ اِلٰى عَذَابٍ غَلِيْظٍ', 'numatti\'uhum qalīlan ṡumma naḍṭarruhum ilā \'ażābin galīẓ'),
(3493, 31, 25, 'وَلَىِٕنْ سَاَلْتَهُمْ مَّنْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ لَيَقُوْلُنَّ اللّٰهُ ۗقُلِ الْحَمْدُ لِلّٰهِ ۗبَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَ', 'wa la`in sa`altahum man khalaqas-samāwāti wal-arḍa layaqụlunnallāh, qulil-ḥamdu lillāh, bal akṡaruhum lā ya\'lamụn'),
(3494, 31, 26, 'لِلّٰهِ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ اِنَّ اللّٰهَ هُوَ الْغَنِيُّ الْحَمِيْدُ', 'lillāhi mā fis-samāwāti wal-arḍ, innallāha huwal-ganiyyul-ḥamīd'),
(3495, 31, 27, 'وَلَوْ اَنَّ مَا فِى الْاَرْضِ مِنْ شَجَرَةٍ اَقْلَامٌ وَّالْبَحْرُ يَمُدُّهٗ مِنْۢ بَعْدِهٖ سَبْعَةُ اَبْحُرٍ مَّا نَفِدَتْ كَلِمٰتُ اللّٰهِ ۗاِنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ', 'walau anna mā fil-arḍi min syajaratin aqlāmuw wal-baḥru yamudduhụ mim ba\'dihī sab\'atu ab-ḥurim mā nafidat kalimātullāh, innallāha \'azīzun ḥakīm'),
(3496, 31, 28, 'مَا خَلْقُكُمْ وَلَا بَعْثُكُمْ اِلَّا كَنَفْسٍ وَّاحِدَةٍ ۗاِنَّ اللّٰهَ سَمِيْعٌۢ بَصِيْرٌ', 'mā khalqukum wa lā ba\'ṡukum illā kanafsiw wāḥidah, innallāha samī\'um baṣīr'),
(3497, 31, 29, 'اَلَمْ تَرَ اَنَّ اللّٰهَ يُوْلِجُ الَّيْلَ فِى النَّهَارِ وَيُوْلِجُ النَّهَارَ فِى الَّيْلِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَۖ كُلٌّ يَّجْرِيْٓ اِلٰٓى اَجَلٍ مُّسَمًّى وَّاَنَّ اللّٰهَ بِمَا تَعْمَلُوْنَ خَبِيْرٌ', 'a lam tara annallāha yụlijul-laila fin-nahāri wa yụlijun-nahāra fil-laili wa sakhkharasy-syamsa wal-qamara kullui yajrī ilā ajalim musammaw wa annallāha bimā ta\'malụna khabīr'),
(3498, 31, 30, 'ذٰلِكَ بِاَنَّ اللّٰهَ هُوَ الْحَقُّ وَاَنَّ مَا يَدْعُوْنَ مِنْ دُوْنِهِ الْبَاطِلُۙ وَاَنَّ اللّٰهَ هُوَ الْعَلِيُّ الْكَبِيْرُ', 'żālika bi`annallāha huwal-ḥaqqu wa anna mā yad\'ụna min dụnihil-bāṭilu wa annallāha huwal-\'aliyyul-kabīr'),
(3499, 31, 31, 'اَلَمْ تَرَ اَنَّ الْفُلْكَ تَجْرِيْ فِى الْبَحْرِ بِنِعْمَتِ اللّٰهِ لِيُرِيَكُمْ مِّنْ اٰيٰتِهٖۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّكُلِّ صَبَّارٍ شَكُوْرٍ', 'a lam tara annal-fulka tajrī fil-baḥri bini\'matillāhi liyuriyakum min āyātih, inna fī żālika la`āyātil likulli ṣabbārin syakụr'),
(3500, 31, 32, 'وَاِذَا غَشِيَهُمْ مَّوْجٌ كَالظُّلَلِ دَعَوُا اللّٰهَ مُخْلِصِيْنَ لَهُ الدِّيْنَ ەۚ فَلَمَّا نَجّٰىهُمْ اِلَى الْبَرِّ فَمِنْهُمْ مُّقْتَصِدٌۗ وَمَا يَجْحَدُ بِاٰيٰتِنَآ اِلَّا كُلُّ خَتَّارٍ كَفُوْرٍ', 'wa iżā gasyiyahum maujung kaẓ-ẓulali da\'awullāha mukhliṣīna lahud-dīn, fa lammā najjāhum ilal-barri fa min-hum muqtaṣid, wa mā yaj-ḥadu bi`āyātinā illā kullu khattāring kafụr'),
(3501, 31, 33, 'يٰٓاَيُّهَا النَّاسُ اتَّقُوْا رَبَّكُمْ وَاخْشَوْا يَوْمًا لَّا يَجْزِيْ وَالِدٌ عَنْ وَّلَدِهٖۖ وَلَا مَوْلُوْدٌ هُوَ جَازٍ عَنْ وَّالِدِهٖ شَيْـًٔاۗ اِنَّ وَعْدَ اللّٰهِ حَقٌّ فَلَا تَغُرَّنَّكُمُ الْحَيٰوةُ الدُّنْيَاۗ وَلَا يَغُرَّنَّكُمْ بِاللّٰهِ الْغَرُوْرُ', 'yā ayyuhan-nāsuttaqụ rabbakum wakhsyau yaumal lā yajzī wālidun \'aw waladihī wa lā maulụdun huwa jāzin \'aw wālidihī syai`ā, inna wa\'dallāhi ḥaqqun fa lā tagurrannakumul-ḥayātud-dun-yā, wa lā yagurrannakum billāhil-garụr'),
(3502, 31, 34, 'اِنَّ اللّٰهَ عِنْدَهٗ عِلْمُ السَّاعَةِۚ وَيُنَزِّلُ الْغَيْثَۚ وَيَعْلَمُ مَا فِى الْاَرْحَامِۗ وَمَا تَدْرِيْ نَفْسٌ مَّاذَا تَكْسِبُ غَدًاۗ وَمَا تَدْرِيْ نَفْسٌۢ بِاَيِّ اَرْضٍ تَمُوْتُۗ اِنَّ اللّٰهَ عَلِيْمٌ خَبِيْرٌ', 'innallāha \'indahụ \'ilmus-sā\'ah, wa yunazzilul-gaīṡ, wa ya\'lamu mā fil-ar-ḥām, wa mā tadrī nafsum māżā taksibu gadā, wa mā tadrī nafsum bi`ayyi arḍin tamụt, innallāha \'alīmun khabīr'),
(3503, 32, 1, ' الۤمّۤ ۗ', 'alif lām mīm'),
(3504, 32, 2, 'تَنْزِيْلُ الْكِتٰبِ لَا رَيْبَ فِيْهِ مِنْ رَّبِّ الْعٰلَمِيْنَۗ', 'tanzīlul-kitābi lā raiba fīhi mir rabbil-\'ālamīn'),
(3505, 32, 3, 'اَمْ يَقُوْلُوْنَ افْتَرٰىهُ ۚ بَلْ هُوَ الْحَقُّ مِنْ رَّبِّكَ لِتُنْذِرَ قَوْمًا مَّآ اَتٰىهُمْ مِّنْ نَّذِيْرٍ مِّنْ قَبْلِكَ لَعَلَّهُمْ يَهْتَدُوْنَ', 'am yaqụlụnaftarāh, bal huwal-ḥaqqu mir rabbika litunżira qaumam mā atāhum min nażīrim ming qablika la\'allahum yahtadụn'),
(3506, 32, 4, 'اَللّٰهُ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَا فِيْ سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِۗ مَا لَكُمْ مِّنْ دُوْنِهٖ مِنْ وَّلِيٍّ وَّلَا شَفِيْعٍۗ اَفَلَا تَتَذَكَّرُوْنَ', 'allāhullażī khalaqas-samāwāti wal-arḍa wa mā bainahumā fī sittati ayyāmin ṡummastawā \'alal-\'arsy, mā lakum min dụnihī miw waliyyiw wa lā syafī\', a fa lā tatażakkarụn'),
(3507, 32, 5, 'يُدَبِّرُ الْاَمْرَ مِنَ السَّمَاۤءِ اِلَى الْاَرْضِ ثُمَّ يَعْرُجُ اِلَيْهِ فِيْ يَوْمٍ كَانَ مِقْدَارُهٗٓ اَلْفَ سَنَةٍ مِّمَّا تَعُدُّوْنَ', 'yudabbirul-amra minas-samā`i ilal-arḍi ṡumma ya\'ruju ilaihi fī yauming kāna miqdāruhū alfa sanatim mimmā ta\'uddụn'),
(3508, 32, 6, 'ذٰلِكَ عٰلِمُ الْغَيْبِ وَالشَّهَادَةِ الْعَزِيْزُ الرَّحِيْمُۙ', 'żālika \'ālimul-gaibi wasy-syahādatil-\'azīzur-raḥīm'),
(3509, 32, 7, 'الَّذِيْٓ اَحْسَنَ كُلَّ شَيْءٍ خَلَقَهٗ وَبَدَاَ خَلْقَ الْاِنْسَانِ مِنْ طِيْنٍ', 'allażī aḥsana kulla syai`in khalaqahụ wa bada`a khalqal-insāni min ṭīn'),
(3510, 32, 8, 'ثُمَّ جَعَلَ نَسْلَهٗ مِنْ سُلٰلَةٍ مِّنْ مَّاۤءٍ مَّهِيْنٍ ۚ', 'ṡumma ja\'ala naslahụ min sulālatim mim mā`im mahīn'),
(3511, 32, 9, 'ثُمَّ سَوّٰىهُ وَنَفَخَ فِيْهِ مِنْ رُّوْحِهٖ وَجَعَلَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٕدَةَۗ قَلِيْلًا مَّا تَشْكُرُوْنَ', 'ṡumma sawwāhu wa nafakha fīhi mir rụḥihī wa ja\'ala lakumus-sam\'a wal-abṣāra wal-af`idah, qalīlam mā tasykurụn'),
(3512, 32, 10, 'وَقَالُوْٓا ءَاِذَا ضَلَلْنَا فِى الْاَرْضِ ءَاِنَّا لَفِيْ خَلْقٍ جَدِيْدٍ ەۗ بَلْ هُمْ بِلِقَاۤءِ رَبِّهِمْ كٰفِرُوْنَ', 'wa qālū a iżā ḍalalnā fil-arḍi a innā lafī khalqin jadīd, bal hum biliqā`i rabbihim kāfirụn'),
(3513, 32, 11, '۞ قُلْ يَتَوَفّٰىكُمْ مَّلَكُ الْمَوْتِ الَّذِيْ وُكِّلَ بِكُمْ ثُمَّ اِلٰى رَبِّكُمْ تُرْجَعُوْنَ', 'qul yatawaffākum malakul-mautillażī wukkila bikum ṡumma ilā rabbikum turja\'ụn'),
(3514, 32, 12, 'وَلَوْ تَرٰىٓ اِذِ الْمُجْرِمُوْنَ نَاكِسُوْا رُءُوْسِهِمْ عِنْدَ رَبِّهِمْۗ رَبَّنَآ اَبْصَرْنَا وَسَمِعْنَا فَارْجِعْنَا نَعْمَلْ صَالِحًا اِنَّا مُوْقِنُوْنَ', 'walau tarā iżil-mujrimụna nākisụ ru`ụsihim \'inda rabbihim, rabbanā abṣarnā wa sami\'nā farji\'nā na\'mal ṣāliḥan innā mụqinụn'),
(3515, 32, 13, 'وَلَوْ شِئْنَا لَاٰتَيْنَا كُلَّ نَفْسٍ هُدٰىهَا وَلٰكِنْ حَقَّ الْقَوْلُ مِنِّيْ لَاَمْلَـَٔنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَالنَّاسِ اَجْمَعِيْنَ', 'walau syi`nā la`ātainā kulla nafsin hudāhā wa lākin ḥaqqal-qaulu minnī la`amla`anna jahannama minal-jinnati wan-nāsi ajma\'īn'),
(3516, 32, 14, 'فَذُوْقُوْا بِمَا نَسِيْتُمْ لِقَاۤءَ يَوْمِكُمْ هٰذَاۚ اِنَّا نَسِيْنٰكُمْ وَذُوْقُوْا عَذَابَ الْخُلْدِ بِمَا كُنْتُمْ تَعْمَلُوْنَ', 'fa żụqụ bimā nasītum liqā`a yaumikum hāżā, innā nasīnākum wa żụqụ \'ażābal-khuldi bimā kuntum ta\'malụn'),
(3517, 32, 15, 'اِنَّمَا يُؤْمِنُ بِاٰيٰتِنَا الَّذِيْنَ اِذَا ذُكِّرُوْا بِهَا خَرُّوْا سُجَّدًا وَّسَبَّحُوْا بِحَمْدِ رَبِّهِمْ وَهُمْ لَا يَسْتَكْبِرُوْنَ ۩', 'innamā yu`minu bi`āyātinallażīna iżā żukkirụ bihā kharrụ sujjadaw wa sabbaḥụ biḥamdi rabbihim wa hum lā yastakbirụn'),
(3518, 32, 16, 'تَتَجَافٰى جُنُوْبُهُمْ عَنِ الْمَضَاجِعِ يَدْعُوْنَ رَبَّهُمْ خَوْفًا وَّطَمَعًاۖ وَّمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَ', 'tatajāfā junụbuhum \'anil-maḍāji\'i yad\'ụna rabbahum khaufaw wa ṭama\'aw wa mimmā razaqnāhum yunfiqụn'),
(3519, 32, 17, 'فَلَا تَعْلَمُ نَفْسٌ مَّآ اُخْفِيَ لَهُمْ مِّنْ قُرَّةِ اَعْيُنٍۚ جَزَاۤءًۢ بِمَا كَانُوْا يَعْمَلُوْنَ', 'fa lā ta\'lamu nafsum mā ukhfiya lahum ming qurrati a\'yun, jazā`am bimā kānụ ya\'malụn'),
(3520, 32, 18, 'اَفَمَنْ كَانَ مُؤْمِنًا كَمَنْ كَانَ فَاسِقًاۗ لَا يَسْتَوٗنَ', 'a fa mang kāna mu`minang kamang kāna fāsiqā, lā yastawụn'),
(3521, 32, 19, 'اَمَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَلَهُمْ جَنّٰتُ الْمَأْوٰىۖ نُزُلًا ۢبِمَا كَانُوْا يَعْمَلُوْنَ', 'ammallażīna āmanụ wa \'amiluṣ-ṣāliḥāti fa lahum jannātul-ma`wā nuzulam bimā kānụ ya\'malụn'),
(3522, 32, 20, 'وَاَمَّا الَّذِيْنَ فَسَقُوْا فَمَأْوٰىهُمُ النَّارُ كُلَّمَآ اَرَادُوْٓا اَنْ يَّخْرُجُوْا مِنْهَآ اُعِيْدُوْا فِيْهَا وَقِيْلَ لَهُمْ ذُوْقُوْا عَذَابَ النَّارِ الَّذِيْ كُنْتُمْ بِهٖ تُكَذِّبُوْنَ', 'wa ammallażīna fasaqụ fa ma`wāhumun-nāru kullamā arādū ay yakhrujụ min-hā u\'īdụ fīhā wa qīla lahum żụqụ \'ażāban-nārillażī kuntum bihī tukażżibụn'),
(3523, 32, 21, 'وَلَنُذِيْقَنَّهُمْ مِّنَ الْعَذَابِ الْاَدْنٰى دُوْنَ الْعَذَابِ الْاَكْبَرِ لَعَلَّهُمْ يَرْجِعُوْنَ', 'wa lanużīqannahum minal-\'ażābil-adnā dụnal-\'ażābil-akbari la\'allahum yarji\'ụn'),
(3524, 32, 22, 'وَمَنْ اَظْلَمُ مِمَّنْ ذُكِّرَ بِاٰيٰتِ رَبِّهٖ ثُمَّ اَعْرَضَ عَنْهَا ۗاِنَّا مِنَ الْمُجْرِمِيْنَ مُنْتَقِمُوْنَ', 'wa man aẓlamu mim man żukkira bi`āyāti rabbihī ṡumma a\'raḍa \'an-hā, innā minal-mujrimīna muntaqimụn'),
(3525, 32, 23, 'وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ فَلَا تَكُنْ فِيْ مِرْيَةٍ مِّنْ لِّقَاۤىِٕهٖ وَجَعَلْنٰهُ هُدًى لِّبَنِيْٓ اِسْرَاۤءِيْلَۚ', 'wa laqad ātainā mụsal-kitāba fa lā takun fī miryatim mil liqā`ihī wa ja\'alnāhu hudal libanī isrā`īl'),
(3526, 32, 24, 'وَجَعَلْنَا مِنْهُمْ اَىِٕمَّةً يَّهْدُوْنَ بِاَمْرِنَا لَمَّا صَبَرُوْاۗ وَكَانُوْا بِاٰيٰتِنَا يُوْقِنُوْنَ', 'wa ja\'alnā min-hum a`immatay yahdụna bi`amrinā lammā ṣabarụ, wa kānụ bi`āyātinā yụqinụn'),
(3527, 32, 25, 'اِنَّ رَبَّكَ هُوَ يَفْصِلُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كَانُوْا فِيْهِ يَخْتَلِفُوْنَ', 'inna rabbaka huwa yafṣilu bainahum yaumal-qiyāmati fīmā kānụ fīhi yakhtalifụn'),
(3528, 32, 26, 'اَوَلَمْ يَهْدِ لَهُمْ كَمْ اَهْلَكْنَا مِنْ قَبْلِهِمْ مِّنَ الْقُرُوْنِ يَمْشُوْنَ فِيْ مَسٰكِنِهِمْ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍۗ اَفَلَا يَسْمَعُوْنَ', 'a wa lam yahdi lahum kam ahlaknā ming qablihim minal-qurụni yamsyụna fī masākinihim, inna fī żālika la`āyāt, a fa lā yasma\'ụn'),
(3529, 32, 27, 'اَوَلَمْ يَرَوْا اَنَّا نَسُوْقُ الْمَاۤءَ اِلَى الْاَرْضِ الْجُرُزِ فَنُخْرِجُ بِهٖ زَرْعًا تَأْكُلُ مِنْهُ اَنْعَامُهُمْ وَاَنْفُسُهُمْۗ اَفَلَا يُبْصِرُوْنَ', 'a wa lam yarau annā nasụqul-mā`a ilal-arḍil-juruzi fa nukhriju bihī zar\'an ta`kulu min-hu an\'āmuhum wa anfusuhum, a fa lā yubṣirụn'),
(3530, 32, 28, 'وَيَقُوْلُوْنَ مَتٰى هٰذَا الْفَتْحُ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'wa yaqụlụna matā hāżal-fat-ḥu ing kuntum ṣādiqīn'),
(3531, 32, 29, 'قُلْ يَوْمَ الْفَتْحِ لَا يَنْفَعُ الَّذِيْنَ كَفَرُوْٓا اِيْمَانُهُمْ وَلَا هُمْ يُنْظَرُوْنَ', 'qul yaumal-fat-ḥi lā yanfa\'ullażīna kafarū īmānuhum wa lā hum yunẓarụn'),
(3532, 32, 30, 'فَاَعْرِضْ عَنْهُمْ وَانْتَظِرْ اِنَّهُمْ مُّنْتَظِرُوْنَ', 'fa a\'riḍ \'an-hum wantaẓir innahum muntaẓirụn'),
(3533, 33, 1, 'يٰٓاَيُّهَا النَّبِيُّ اتَّقِ اللّٰهَ وَلَا تُطِعِ الْكٰفِرِيْنَ وَالْمُنٰفِقِيْنَ ۗاِنَّ اللّٰهَ كَانَ عَلِيْمًا حَكِيْمًاۙ', 'yā ayyuhan-nabiyyuttaqillāha wa lā tuṭi\'il-kāfirīna wal-munāfiqīn, innallāha kāna \'alīman ḥakīmā'),
(3534, 33, 2, 'وَّاتَّبِعْ مَا يُوْحٰىٓ اِلَيْكَ مِنْ رَّبِّكَ ۗاِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُوْنَ خَبِيْرًاۙ', 'wattabi\' mā yụḥā ilaika mir rabbik, innallāha kāna bimā ta\'malụna khabīrā'),
(3535, 33, 3, 'وَّتَوَكَّلْ عَلَى اللّٰهِ ۗوَكَفٰى بِاللّٰهِ وَكِيْلًا', 'wa tawakkal \'alallāh, wa kafā billāhi wakīlā'),
(3536, 33, 4, 'مَا جَعَلَ اللّٰهُ لِرَجُلٍ مِّنْ قَلْبَيْنِ فِيْ جَوْفِهٖ ۚوَمَا جَعَلَ اَزْوَاجَكُمُ الّٰـِٕۤيْ تُظٰهِرُوْنَ مِنْهُنَّ اُمَّهٰتِكُمْ ۚوَمَا جَعَلَ اَدْعِيَاۤءَكُمْ اَبْنَاۤءَكُمْۗ ذٰلِكُمْ قَوْلُكُمْ بِاَفْوَاهِكُمْ ۗوَاللّٰهُ يَقُوْلُ الْحَقَّ وَهُوَ يَهْدِى السَّبِيْلَ', 'mā ja\'alallāhu lirajulim ming qalbaini fī jaufih, wa mā ja\'ala azwājakumul-lā`ī tuẓāhirụna min-hunna ummahātikum, wa mā ja\'ala ad\'iyā`akum abnā`akum, żālikum qaulukum bi`afwāhikum, wallāhu yaqụlul-ḥaqqa wa huwa yahdis-sabīl'),
(3537, 33, 5, 'اُدْعُوْهُمْ لِاٰبَاۤىِٕهِمْ هُوَ اَقْسَطُ عِنْدَ اللّٰهِ ۚ فَاِنْ لَّمْ تَعْلَمُوْٓا اٰبَاۤءَهُمْ فَاِخْوَانُكُمْ فِى الدِّيْنِ وَمَوَالِيْكُمْ ۗوَلَيْسَ عَلَيْكُمْ جُنَاحٌ فِيْمَآ اَخْطَأْتُمْ بِهٖ وَلٰكِنْ مَّا تَعَمَّدَتْ قُلُوْبُكُمْ ۗوَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا', 'ud\'ụhum li`ābā`ihim huwa aqsaṭu \'indallāh, fa il lam ta\'lamū ābā`ahum fa ikhwānukum fid-dīni wa mawālīkum, wa laisa \'alaikum junāḥun fīmā akhṭa`tum bihī wa lākim mā ta\'ammadat qulụbukum, wa kānallāhu gafụrar raḥīmā'),
(3538, 33, 6, 'اَلنَّبِيُّ اَوْلٰى بِالْمُؤْمِنِيْنَ مِنْ اَنْفُسِهِمْ وَاَزْوَاجُهٗٓ اُمَّهٰتُهُمْ ۗوَاُولُوا الْاَرْحَامِ بَعْضُهُمْ اَوْلٰى بِبَعْضٍ فِيْ كِتٰبِ اللّٰهِ مِنَ الْمُؤْمِنِيْنَ وَالْمُهٰجِرِيْنَ اِلَّآ اَنْ تَفْعَلُوْٓا اِلٰٓى اَوْلِيَاۤىِٕكُمْ مَّعْرُوْفًا ۗ كَانَ ذٰلِكَ فِى الْكِتٰبِ مَسْطُوْرًا', 'an-nabiyyu aulā bil-mu`minīna min anfusihim wa azwājuhū ummahātuhum, wa ulul-ar-ḥāmi ba\'ḍuhum aulā biba\'ḍin fī kitābillāhi minal-mu`minīna wal-muhājirīna illā an taf\'alū ilā auliyā`ikum ma\'rụfā, kāna żālika fil-kitābi masṭụrā'),
(3539, 33, 7, 'وَاِذْ اَخَذْنَا مِنَ النَّبِيّٖنَ مِيْثَاقَهُمْ وَمِنْكَ وَمِنْ نُّوْحٍ وَّاِبْرٰهِيْمَ وَمُوْسٰى وَعِيْسَى ابْنِ مَرْيَمَ ۖوَاَخَذْنَا مِنْهُمْ مِّيْثَاقًا غَلِيْظًاۙ', 'wa iż akhażnā minan-nabiyyīna mīṡāqahum wa mingka wa min nụḥiw wa ibrāhīma wa mụsā wa \'īsabni maryama wa akhażnā min-hum mīṡāqan galīẓā'),
(3540, 33, 8, 'لِّيَسْـَٔلَ الصّٰدِقِيْنَ عَنْ صِدْقِهِمْ ۚوَاَعَدَّ لِلْكٰفِرِيْنَ عَذَابًا اَلِيْمًا', 'liyas`alaṣ-ṣādiqīna \'an ṣidqihim, wa a\'adda lil-kāfirīna \'ażāban alīmā'),
(3541, 33, 9, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اذْكُرُوْا نِعْمَةَ اللّٰهِ عَلَيْكُمْ اِذْ جَاۤءَتْكُمْ جُنُوْدٌ فَاَرْسَلْنَا عَلَيْهِمْ رِيْحًا وَّجُنُوْدًا لَّمْ تَرَوْهَا ۗوَكَانَ اللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرًاۚ', 'yā ayyuhallażīna āmanużkurụ ni\'matallāhi \'alaikum iż jā`atkum junụdun fa arsalnā \'alaihim rīḥaw wa junụdal lam tarauhā, wa kānallāhu bimā ta\'malụna baṣīrā'),
(3542, 33, 10, 'اِذْ جَاۤءُوْكُمْ مِّنْ فَوْقِكُمْ وَمِنْ اَسْفَلَ مِنْكُمْ وَاِذْ زَاغَتِ الْاَبْصَارُ وَبَلَغَتِ الْقُلُوْبُ الْحَنَاجِرَ وَتَظُنُّوْنَ بِاللّٰهِ الظُّنُوْنَا۠ ۗ', 'iż jā`ụkum min fauqikum wa min asfala mingkum wa iż zāgatil-abṣāru wa balagatil-qulụbul-ḥanājira wa taẓunnụna billāhiẓ-ẓunụnā'),
(3543, 33, 11, 'هُنَالِكَ ابْتُلِيَ الْمُؤْمِنُوْنَ وَزُلْزِلُوْا زِلْزَالًا شَدِيْدًا', 'hunālikabtuliyal-mu`minụna wa zulzilụ zilzālan syadīdā'),
(3544, 33, 12, 'وَاِذْ يَقُوْلُ الْمُنٰفِقُوْنَ وَالَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ مَّا وَعَدَنَا اللّٰهُ وَرَسُوْلُهٗٓ اِلَّا غُرُوْرًا', 'wa iż yaqụlul-munāfiqụna wallażīna fī qulụbihim maraḍum mā wa\'adanallāhu wa rasụluhū illā gurụrā'),
(3545, 33, 13, 'وَاِذْ قَالَتْ طَّاۤىِٕفَةٌ مِّنْهُمْ يٰٓاَهْلَ يَثْرِبَ لَا مُقَامَ لَكُمْ فَارْجِعُوْا ۚوَيَسْتَأْذِنُ فَرِيْقٌ مِّنْهُمُ النَّبِيَّ يَقُوْلُوْنَ اِنَّ بُيُوْتَنَا عَوْرَةٌ ۗوَمَا هِيَ بِعَوْرَةٍ ۗاِنْ يُّرِيْدُوْنَ اِلَّا فِرَارًا', 'wa iż qālat ṭā`ifatum min-hum yā ahla yaṡriba lā muqāma lakum farji\'ụ, wa yasta`żinu farīqum min-humun-nabiyya yaqụlụna inna buyụtanā \'aurah, wa mā hiya bi\'aurah, iy yurīdụna illā firārā'),
(3546, 33, 14, 'وَلَوْ دُخِلَتْ عَلَيْهِمْ مِّنْ اَقْطَارِهَا ثُمَّ سُـِٕلُوا الْفِتْنَةَ لَاٰتَوْهَا وَمَا تَلَبَّثُوْا بِهَآ اِلَّا يَسِيْرًا', 'walau dukhilat \'alaihim min aqṭārihā ṡumma su`ilul-fitnata la`ātauhā wa mā talabbaṡụ bihā illā yasīrā'),
(3547, 33, 15, 'وَلَقَدْ كَانُوْا عَاهَدُوا اللّٰهَ مِنْ قَبْلُ لَا يُوَلُّوْنَ الْاَدْبَارَ ۗوَكَانَ عَهْدُ اللّٰهِ مَسْـُٔوْلًا', 'wa laqad kānụ \'āhadullāha ming qablu lā yuwallụnal-adbār, wa kāna \'ahdullāhi mas`ụlā'),
(3548, 33, 16, 'قُلْ لَّنْ يَّنْفَعَكُمُ الْفِرَارُ اِنْ فَرَرْتُمْ مِّنَ الْمَوْتِ اَوِ الْقَتْلِ وَاِذًا لَّا تُمَتَّعُوْنَ اِلَّا قَلِيْلًا', 'qul lay yanfa\'akumul-firāru in farartum minal-mauti awil-qatli wa iżal lā tumatta\'ụna illā qalīlā'),
(3549, 33, 17, 'قُلْ مَنْ ذَا الَّذِيْ يَعْصِمُكُمْ مِّنَ اللّٰهِ اِنْ اَرَادَ بِكُمْ سُوْۤءًا اَوْ اَرَادَ بِكُمْ رَحْمَةً ۗوَلَا يَجِدُوْنَ لَهُمْ مِّنْ دُوْنِ اللّٰهِ وَلِيًّا وَّلَا نَصِيْرًا', 'qul man żallażī ya\'ṣimukum minallāhi in arāda bikum sū`an au arāda bikum raḥmah, wa lā yajidụna lahum min dụnillāhi waliyyaw wa lā naṣīrā'),
(3550, 33, 18, '۞ قَدْ يَعْلَمُ اللّٰهُ الْمُعَوِّقِيْنَ مِنْكُمْ وَالْقَاۤىِٕلِيْنَ لِاِخْوَانِهِمْ هَلُمَّ اِلَيْنَا ۚوَلَا يَأْتُوْنَ الْبَأْسَ اِلَّا قَلِيْلًاۙ', 'qad ya\'lamullāhul-mu\'awwiqīna mingkum wal-qā`ilīna li`ikhwānihim halumma ilainā, wa lā ya`tụnal-ba`sa illā qalīlā'),
(3551, 33, 19, 'اَشِحَّةً عَلَيْكُمْ ۖ فَاِذَا جَاۤءَ الْخَوْفُ رَاَيْتَهُمْ يَنْظُرُوْنَ اِلَيْكَ تَدُوْرُ اَعْيُنُهُمْ كَالَّذِيْ يُغْشٰى عَلَيْهِ مِنَ الْمَوْتِۚ فَاِذَا ذَهَبَ الْخَوْفُ سَلَقُوْكُمْ بِاَلْسِنَةٍ حِدَادٍ اَشِحَّةً عَلَى الْخَيْرِۗ اُولٰۤىِٕكَ لَمْ يُؤْمِنُوْا فَاَحْبَطَ اللّٰهُ اَعْمَالَهُمْۗ وَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرًا', 'asyiḥḥatan \'alaikum fa iżā jā`al-khaufu ra`aitahum yanẓurụna ilaika tadụru a\'yunuhum kallażī yugsyā \'alaihi minal-maụt, fa iżā żahabal-khaufu salaqụkum bi`alsinatin ḥidādin asyiḥḥatan \'alal-khaīr, ulā`ika lam yu`minụ fa aḥbaṭallāhu a\'mālahum, wa kāna żālika \'alallāhi yasīrā'),
(3552, 33, 20, 'يَحْسَبُوْنَ الْاَحْزَابَ لَمْ يَذْهَبُوْا ۚوَاِنْ يَّأْتِ الْاَحْزَابُ يَوَدُّوْا لَوْ اَنَّهُمْ بَادُوْنَ فِى الْاَعْرَابِ يَسْاَلُوْنَ عَنْ اَنْۢبَاۤىِٕكُمْ ۖوَلَوْ كَانُوْا فِيْكُمْ مَّا قٰتَلُوْٓا اِلَّا قَلِيْلًا', 'yaḥsabụnal-aḥzāba lam yaż-habụ, wa iy ya`til-aḥzābu yawaddụ lau annahum bādụna fil-a\'rābi yas`alụna \'an ambā`ikum, walau kānụ fīkum mā qātalū illā qalīlā'),
(3553, 33, 21, 'لَقَدْ كَانَ لَكُمْ فِيْ رَسُوْلِ اللّٰهِ اُسْوَةٌ حَسَنَةٌ لِّمَنْ كَانَ يَرْجُوا اللّٰهَ وَالْيَوْمَ الْاٰخِرَ وَذَكَرَ اللّٰهَ كَثِيْرًاۗ', 'laqad kāna lakum fī rasụlillāhi uswatun ḥasanatul limang kāna yarjullāha wal-yaumal-ākhira wa żakarallāha kaṡīrā'),
(3554, 33, 22, 'وَلَمَّا رَاَ الْمُؤْمِنُوْنَ الْاَحْزَابَۙ قَالُوْا هٰذَا مَا وَعَدَنَا اللّٰهُ وَرَسُوْلُهٗ وَصَدَقَ اللّٰهُ وَرَسُوْلُهٗ ۖوَمَا زَادَهُمْ اِلَّآ اِيْمَانًا وَّتَسْلِيْمًاۗ', 'wa lammā ra`al-mu`minụnal-aḥzāba qālụ hāżā mā wa\'adanallāhu wa rasụluhụ wa ṣadaqallāhu wa rasụluhụ wa mā zādahum illā īmānaw wa taslīmā'),
(3555, 33, 23, 'مِنَ الْمُؤْمِنِيْنَ رِجَالٌ صَدَقُوْا مَا عَاهَدُوا اللّٰهَ عَلَيْهِ ۚ فَمِنْهُمْ مَّنْ قَضٰى نَحْبَهٗۙ وَمِنْهُمْ مَّنْ يَّنْتَظِرُ ۖوَمَا بَدَّلُوْا تَبْدِيْلًاۙ', 'minal-mu`minīna rijālun ṣadaqụ mā \'āhadullāha \'alaīh, fa min-hum mang qaḍā naḥbahụ wa min-hum may yantaẓiru wa mā baddalụ tabdīlā'),
(3556, 33, 24, 'لِيَجْزِيَ اللّٰهُ الصّٰدِقِيْنَ بِصِدْقِهِمْ وَيُعَذِّبَ الْمُنٰفِقِيْنَ اِنْ شَاۤءَ اَوْ يَتُوْبَ عَلَيْهِمْ ۗاِنَّ اللّٰهَ كَانَ غَفُوْرًا رَّحِيْمًاۚ', 'liyajziyallāhuṣ-ṣādiqīna biṣidqihim wa yu\'ażżibal-munāfiqīna in syā`a au yatụba \'alaihim, innallāha kāna gafụrar raḥīmā'),
(3557, 33, 25, 'وَرَدَّ اللّٰهُ الَّذِيْنَ كَفَرُوْا بِغَيْظِهِمْ لَمْ يَنَالُوْا خَيْرًا ۗوَكَفَى اللّٰهُ الْمُؤْمِنِيْنَ الْقِتَالَ ۗوَكَانَ اللّٰهُ قَوِيًّا عَزِيْزًاۚ', 'wa raddallāhullażīna kafarụ bigaiẓihim lam yanālụ khairā, wa kafallāhul-mu`minīnal-qitāl, wa kānallāhu qawiyyan \'azīzā'),
(3558, 33, 26, 'وَاَنْزَلَ الَّذِيْنَ ظَاهَرُوْهُمْ مِّنْ اَهْلِ الْكِتٰبِ مِنْ صَيَاصِيْهِمْ وَقَذَفَ فِيْ قُلُوْبِهِمُ الرُّعْبَ فَرِيْقًا تَقْتُلُوْنَ وَتَأْسِرُوْنَ فَرِيْقًاۚ', 'wa anzalallażīna ẓāharụhum min ahlil-kitābi min ṣayāṣīhim wa qażafa fī qulụbihimur-ru\'ba farīqan taqtulụna wa ta`sirụna farīqā'),
(3559, 33, 27, 'وَاَوْرَثَكُمْ اَرْضَهُمْ وَدِيَارَهُمْ وَاَمْوَالَهُمْ وَاَرْضًا لَّمْ تَطَـُٔوْهَا ۗوَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرًا', 'wa auraṡakum arḍahum wa diyārahum wa amwālahum wa arḍal lam taṭa\'ụhā, wa kānallāhu \'alā kulli syai`ing qadīrā'),
(3560, 33, 28, 'يٰٓاَيُّهَا النَّبِيُّ قُلْ لِّاَزْوَاجِكَ اِنْ كُنْتُنَّ تُرِدْنَ الْحَيٰوةَ الدُّنْيَا وَزِيْنَتَهَا فَتَعَالَيْنَ اُمَتِّعْكُنَّ وَاُسَرِّحْكُنَّ سَرَاحًا جَمِيْلًا', 'yā ayyuhan-nabiyyu qul li`azwājika ing kuntunna turidnal-ḥayātad-dun-yā wa zīnatahā fa ta\'ālaina umatti\'kunna wa usarriḥkunna sarāḥan jamīlā'),
(3561, 33, 29, 'وَاِنْ كُنْتُنَّ تُرِدْنَ اللّٰهَ وَرَسُوْلَهٗ وَالدَّارَ الْاٰخِرَةَ فَاِنَّ اللّٰهَ اَعَدَّ لِلْمُحْسِنٰتِ مِنْكُنَّ اَجْرًا عَظِيْمًا', 'wa ing kuntunna turidnallāha wa rasụlahụ wad-dāral-ākhirata fa innallāha a\'adda lil-muḥsināti mingkunna ajran \'aẓīmā'),
(3562, 33, 30, 'يٰنِسَاۤءَ النَّبِيِّ مَنْ يَّأْتِ مِنْكُنَّ بِفَاحِشَةٍ مُّبَيِّنَةٍ يُّضٰعَفْ لَهَا الْعَذَابُ ضِعْفَيْنِۗ وَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرًا ۔', 'yā nisā`an-nabiyyi may ya`ti mingkunna bifāḥisyatim mubayyinatiy yuḍā\'af lahal-\'ażābu ḍi\'faīn, wa kāna żālika \'alallāhi yasīrā'),
(3563, 33, 31, '۞ وَمَنْ يَّقْنُتْ مِنْكُنَّ لِلّٰهِ وَرَسُوْلِهٖ وَتَعْمَلْ صَالِحًا نُّؤْتِهَآ اَجْرَهَا مَرَّتَيْنِۙ وَاَعْتَدْنَا لَهَا رِزْقًا كَرِيْمًا', 'wa may yaqnut mingkunna lillāhi wa rasụlihī wa ta\'mal ṣāliḥan nu`tihā ajrahā marrataini wa a\'tadnā lahā rizqang karīmā'),
(3564, 33, 32, 'يٰنِسَاۤءَ النَّبِيِّ لَسْتُنَّ كَاَحَدٍ مِّنَ النِّسَاۤءِ اِنِ اتَّقَيْتُنَّ فَلَا تَخْضَعْنَ بِالْقَوْلِ فَيَطْمَعَ الَّذِيْ فِيْ قَلْبِهٖ مَرَضٌ وَّقُلْنَ قَوْلًا مَّعْرُوْفًاۚ', 'yā nisā`an-nabiyyi lastunna ka`aḥadim minan-nisā`i inittaqaitunna fa lā takhḍa\'na bil-qauli fa yaṭma\'allażī fī qalbihī maraḍuw wa qulna qaulam ma\'rụfā'),
(3565, 33, 33, 'وَقَرْنَ فِيْ بُيُوْتِكُنَّ وَلَا تَبَرَّجْنَ تَبَرُّجَ الْجَاهِلِيَّةِ الْاُوْلٰى وَاَقِمْنَ الصَّلٰوةَ وَاٰتِيْنَ الزَّكٰوةَ وَاَطِعْنَ اللّٰهَ وَرَسُوْلَهٗ ۗاِنَّمَا يُرِيْدُ اللّٰهُ لِيُذْهِبَ عَنْكُمُ الرِّجْسَ اَهْلَ الْبَيْتِ وَيُطَهِّرَكُمْ تَطْهِيْرًاۚ', 'wa qarna fī buyụtikunna wa lā tabarrajna tabarrujal-jāhiliyyatil-ụlā wa aqimnaṣ-ṣalāta wa ātīnaz-zakāta wa aṭi\'nallāha wa rasụlah, innamā yurīdullāhu liyuż-hiba \'angkumur-rijsa ahlal-baiti wa yuṭahhirakum taṭ-hīrā'),
(3566, 33, 34, 'وَاذْكُرْنَ مَا يُتْلٰى فِيْ بُيُوْتِكُنَّ مِنْ اٰيٰتِ اللّٰهِ وَالْحِكْمَةِۗ اِنَّ اللّٰهَ كَانَ لَطِيْفًا خَبِيْرًا', 'ważkurna mā yutlā fī buyụtikunna min āyātillāhi wal-ḥikmah, innallāha kāna laṭīfan khabīrā'),
(3567, 33, 35, 'اِنَّ الْمُسْلِمِيْنَ وَالْمُسْلِمٰتِ وَالْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ وَالْقٰنِتِيْنَ وَالْقٰنِتٰتِ وَالصّٰدِقِيْنَ وَالصّٰدِقٰتِ وَالصّٰبِرِيْنَ وَالصّٰبِرٰتِ وَالْخٰشِعِيْنَ وَالْخٰشِعٰتِ وَالْمُتَصَدِّقِيْنَ وَالْمُتَصَدِّقٰتِ وَالصَّاۤىِٕمِيْنَ وَالصّٰۤىِٕمٰتِ وَالْحٰفِظِيْنَ فُرُوْجَهُمْ وَالْحٰفِظٰتِ وَالذَّاكِرِيْنَ اللّٰهَ كَثِيْرًا وَّالذَّاكِرٰتِ اَعَدَّ اللّٰهُ لَهُمْ مَّغْفِرَةً وَّاَجْرًا عَظِيْمًا', 'innal-muslimīna wal-muslimāti wal-mu`minīna wal-mu`mināti wal-qānitīna wal-qānitāti waṣ-ṣādiqīna waṣ-ṣādiqāti waṣ-ṣābirīna waṣ-ṣābirāti wal-khāsyi\'īna wal-khāsyi\'āti wal-mutaṣaddiqīna wal-mutaṣaddiqāti waṣ-ṣā`imīna waṣ-ṣā`imāti wal-ḥāfiẓīna furụjahum wal-ḥāfiẓāti waż-żākirīnallāha kaṡīraw waż-żākirāti a\'addallāhu lahum magfirataw wa ajran \'aẓīmā'),
(3568, 33, 36, 'وَمَا كَانَ لِمُؤْمِنٍ وَّلَا مُؤْمِنَةٍ اِذَا قَضَى اللّٰهُ وَرَسُوْلُهٗٓ اَمْرًا اَنْ يَّكُوْنَ لَهُمُ الْخِيَرَةُ مِنْ اَمْرِهِمْ ۗوَمَنْ يَّعْصِ اللّٰهَ وَرَسُوْلَهٗ فَقَدْ ضَلَّ ضَلٰلًا مُّبِيْنًاۗ', 'wa mā kāna limu`miniw wa lā mu`minatin iżā qaḍallāhu wa rasụluhū amran ay yakụna lahumul-khiyaratu min amrihim, wa may ya\'ṣillāha wa rasụlahụ fa qad ḍalla ḍalālam mubīnā'),
(3569, 33, 37, 'وَاِذْ تَقُوْلُ لِلَّذِيْٓ اَنْعَمَ اللّٰهُ عَلَيْهِ وَاَنْعَمْتَ عَلَيْهِ اَمْسِكْ عَلَيْكَ زَوْجَكَ وَاتَّقِ اللّٰهَ وَتُخْفِيْ فِيْ نَفْسِكَ مَا اللّٰهُ مُبْدِيْهِ وَتَخْشَى النَّاسَۚ وَاللّٰهُ اَحَقُّ اَنْ تَخْشٰىهُ ۗ فَلَمَّا قَضٰى زَيْدٌ مِّنْهَا وَطَرًاۗ زَوَّجْنٰكَهَا لِكَيْ لَا يَكُوْنَ عَلَى الْمُؤْمِنِيْنَ حَرَجٌ فِيْٓ اَزْوَاجِ اَدْعِيَاۤىِٕهِمْ اِذَا قَضَوْا مِنْهُنَّ وَطَرًاۗ وَكَانَ اَمْرُ اللّٰهِ مَفْعُوْلًا', 'wa iż taqụlu lillażī an\'amallāhu \'alaihi wa an\'amta \'alaihi amsik \'alaika zaujaka wattaqillāha wa tukhfī fī nafsika mallāhu mubdīhi wa takhsyan-nās, wallāhu aḥaqqu an takhsyāh, fa lammā qaḍā zaidum min-hā waṭarā, zawwajnākahā likai lā yakụna \'alal-mu`minīna ḥarajun fī azwāji ad\'iyā`ihim iżā qaḍau min-hunna waṭarā, wa kāna amrullāhi maf\'ụlā'),
(3570, 33, 38, 'مَا كَانَ عَلَى النَّبِيِّ مِنْ حَرَجٍ فِيْمَا فَرَضَ اللّٰهُ لَهٗ ۗسُنَّةَ اللّٰهِ فِى الَّذِيْنَ خَلَوْا مِنْ قَبْلُ ۗوَكَانَ اَمْرُ اللّٰهِ قَدَرًا مَّقْدُوْرًاۙ', 'mā kāna \'alan-nabiyyi min ḥarajin fīmā faraḍallāhu lah, sunnatallāhi fillażīna khalau ming qabl, wa kāna amrullāhi qadaram maqdụrā'),
(3571, 33, 39, ' ۨالَّذِيْنَ يُبَلِّغُوْنَ رِسٰلٰتِ اللّٰهِ وَيَخْشَوْنَهٗ وَلَا يَخْشَوْنَ اَحَدًا اِلَّا اللّٰهَ ۗوَكَفٰى بِاللّٰهِ حَسِيْبًا', 'allażīna yuballigụna risālātillāhi wa yakhsyaunahụ wa lā yakhsyauna aḥadan illallāh, wa kafā billāhi ḥasībā'),
(3572, 33, 40, 'مَا كَانَ مُحَمَّدٌ اَبَآ اَحَدٍ مِّنْ رِّجَالِكُمْ وَلٰكِنْ رَّسُوْلَ اللّٰهِ وَخَاتَمَ النَّبِيّٖنَۗ وَكَانَ اللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمًا', 'mā kāna muḥammadun aba aḥadim mir rijālikum wa lākir rasụlallāhi wa khātaman-nabiyyīn, wa kānallāhu bikulli syai`in \'alīmā'),
(3573, 33, 41, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اذْكُرُوا اللّٰهَ ذِكْرًا كَثِيْرًاۙ', 'yā ayyuhallażīna āmanużkurullāha żikrang kaṡīrā'),
(3574, 33, 42, 'وَّسَبِّحُوْهُ بُكْرَةً وَّاَصِيْلًا', 'wa sabbiḥụhu bukrataw wa aṣīlā'),
(3575, 33, 43, 'هُوَ الَّذِيْ يُصَلِّيْ عَلَيْكُمْ وَمَلٰۤىِٕكَتُهٗ لِيُخْرِجَكُمْ مِّنَ الظُّلُمٰتِ اِلَى النُّوْرِۗ وَكَانَ بِالْمُؤْمِنِيْنَ رَحِيْمًا', 'huwallażī yuṣallī \'alaikum wa malā`ikatuhụ liyukhrijakum minaẓ-ẓulumāti ilan-nụr, wa kāna bil-mu`minīna raḥīmā'),
(3576, 33, 44, 'تَحِيَّتُهُمْ يَوْمَ يَلْقَوْنَهٗ سَلٰمٌ ۚوَاَعَدَّ لَهُمْ اَجْرًا كَرِيْمًا', 'taḥiyyatuhum yauma yalqaunahụ salām, wa a\'adda lahum ajrang karīmā'),
(3577, 33, 45, 'يٰٓاَيُّهَا النَّبِيُّ اِنَّآ اَرْسَلْنٰكَ شَاهِدًا وَّمُبَشِّرًا وَّنَذِيْرًاۙ', 'yā ayyuhan-nabiyyu innā arsalnāka syāhidaw wa mubasysyiraw wa nażīrā'),
(3578, 33, 46, 'وَّدَاعِيًا اِلَى اللّٰهِ بِاِذْنِهٖ وَسِرَاجًا مُّنِيْرًا', 'wa dā\'iyan ilallāhi bi`iżnihī wa sirājam munīrā'),
(3579, 33, 47, 'وَبَشِّرِ الْمُؤْمِنِيْنَ بِاَنَّ لَهُمْ مِّنَ اللّٰهِ فَضْلًا كَبِيْرًا', 'wa basysyiril-mu`minīna bi`anna lahum minallāhi faḍlang kabīrā'),
(3580, 33, 48, 'وَلَا تُطِعِ الْكٰفِرِيْنَ وَالْمُنٰفِقِيْنَ وَدَعْ اَذٰىهُمْ وَتَوَكَّلْ عَلَى اللّٰهِ ۗوَكَفٰى بِاللّٰهِ وَكِيْلًا', 'wa lā tuṭi\'il-kāfirīna wal-munāfiqīna wa da\' ażāhum wa tawakkal \'alallāh, wa kafā billāhi wakīlā'),
(3581, 33, 49, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا نَكَحْتُمُ الْمُؤْمِنٰتِ ثُمَّ طَلَّقْتُمُوْهُنَّ مِنْ قَبْلِ اَنْ تَمَسُّوْهُنَّ فَمَا لَكُمْ عَلَيْهِنَّ مِنْ عِدَّةٍ تَعْتَدُّوْنَهَاۚ فَمَتِّعُوْهُنَّ وَسَرِّحُوْهُنَّ سَرَاحًا جَمِيْلًا', 'yā ayyuhallażīna āmanū iżā nakaḥtumul-mu`mināti ṡumma ṭallaqtumụhunna ming qabli an tamassụhunna fa mā lakum \'alaihinna min \'iddatin ta\'taddụnahā, fa matti\'ụhunna wa sarriḥụhunna sarāḥan jamīlā'),
(3582, 33, 50, 'يٰٓاَيُّهَا النَّبِيُّ اِنَّآ اَحْلَلْنَا لَكَ اَزْوَاجَكَ الّٰتِيْٓ اٰتَيْتَ اُجُوْرَهُنَّ وَمَا مَلَكَتْ يَمِيْنُكَ مِمَّآ اَفَاۤءَ اللّٰهُ عَلَيْكَ وَبَنٰتِ عَمِّكَ وَبَنٰتِ عَمّٰتِكَ وَبَنٰتِ خَالِكَ وَبَنٰتِ خٰلٰتِكَ الّٰتِيْ هَاجَرْنَ مَعَكَۗ وَامْرَاَةً مُّؤْمِنَةً اِنْ وَّهَبَتْ نَفْسَهَا لِلنَّبِيِّ اِنْ اَرَادَ النَّبِيُّ اَنْ يَّسْتَنْكِحَهَا خَالِصَةً لَّكَ مِنْ دُوْنِ الْمُؤْمِنِيْنَۗ قَدْ عَلِمْنَا مَا فَرَضْنَا عَلَيْهِمْ فِيْٓ اَزْوَاجِهِمْ وَمَا مَلَكَتْ اَيْمَانُهُمْ لِكَيْلَا يَكُوْنَ عَلَيْكَ حَرَجٌۗ وَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا', 'yā ayyuhan-nabiyyu innā aḥlalnā laka azwājakallātī ātaita ujụrahunna wa mā malakat yamīnuka mimmā afā`allāhu \'alaika wa banāti \'ammika wa banāti \'ammātika wa banāti khālika wa banāti khālātikallātī hājarna ma\'ak, wamra`atam mu`minatan iw wahabat nafsahā lin-nabiyyi in arādan-nabiyyu ay yastangkiḥahā khāliṣatal laka min dụnil-mu`minīn, qad \'alimnā mā faraḍnā \'alaihim fī azwājihim wa mā malakat aimānuhum likai lā yakụna \'alaika ḥaraj, wa kānallāhu gafụrar raḥīmā'),
(3583, 33, 51, '۞ تُرْجِيْ مَنْ تَشَاۤءُ مِنْهُنَّ وَتُـْٔوِيْٓ اِلَيْكَ مَنْ تَشَاۤءُۗ وَمَنِ ابْتَغَيْتَ مِمَّنْ عَزَلْتَ فَلَا جُنَاحَ عَلَيْكَۗ ذٰلِكَ اَدْنٰٓى اَنْ تَقَرَّ اَعْيُنُهُنَّ وَلَا يَحْزَنَّ وَيَرْضَيْنَ بِمَآ اٰتَيْتَهُنَّ كُلُّهُنَّۗ وَاللّٰهُ يَعْلَمُ مَا فِيْ قُلُوْبِكُمْ ۗوَكَانَ اللّٰهُ عَلِيْمًا حَلِيْمًا', 'turjī man tasyā`u min-hunna wa tu`wī ilaika man tasyā`, wa manibtagaita mim man \'azalta fa lā junāḥa \'alaīk, żālika adnā an taqarra a\'yunuhunna wa lā yaḥzanna wa yarḍaina bimā ātaitahunna kulluhunn, wallāhu ya\'lamu mā fī qulụbikum, wa kānallāhu \'alīman ḥalīmā'),
(3584, 33, 52, 'لَا يَحِلُّ لَكَ النِّسَاۤءُ مِنْۢ بَعْدُ وَلَآ اَنْ تَبَدَّلَ بِهِنَّ مِنْ اَزْوَاجٍ وَّلَوْ اَعْجَبَكَ حُسْنُهُنَّ اِلَّا مَا مَلَكَتْ يَمِيْنُكَۗ وَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ رَّقِيْبًا', 'lā yaḥillu lakan-nisā`u mim ba\'du wa lā an tabaddala bihinna min azwājiw walau a\'jabaka ḥusnuhunna illā mā malakat yamīnuk, wa kānallāhu \'alā kulli syai`ir raqībā'),
(3585, 33, 53, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَدْخُلُوْا بُيُوْتَ النَّبِيِّ اِلَّآ اَنْ يُّؤْذَنَ لَكُمْ اِلٰى طَعَامٍ غَيْرَ نٰظِرِيْنَ اِنٰىهُ وَلٰكِنْ اِذَا دُعِيْتُمْ فَادْخُلُوْا فَاِذَا طَعِمْتُمْ فَانْتَشِرُوْا وَلَا مُسْتَأْنِسِيْنَ لِحَدِيْثٍۗ اِنَّ ذٰلِكُمْ كَانَ يُؤْذِى النَّبِيَّ فَيَسْتَحْيٖ مِنْكُمْ ۖوَاللّٰهُ لَا يَسْتَحْيٖ مِنَ الْحَقِّۗ وَاِذَا سَاَلْتُمُوْهُنَّ مَتَاعًا فَاسْـَٔلُوْهُنَّ مِنْ وَّرَاۤءِ حِجَابٍۗ ذٰلِكُمْ اَطْهَرُ لِقُلُوْبِكُمْ وَقُلُوْبِهِنَّۗ وَمَا كَانَ لَكُمْ اَنْ تُؤْذُوْا رَسُوْلَ اللّٰهِ وَلَآ اَنْ تَنْكِحُوْٓا اَزْوَاجَهٗ مِنْۢ بَعْدِهٖٓ اَبَدًاۗ اِنَّ ذٰلِكُمْ كَانَ عِنْدَ اللّٰهِ عَظِيْمًا', 'yā ayyuhallażīna āmanụ lā tadkhulụ buyụtan-nabiyyi illā ay yu`żana lakum ilā ṭa\'āmin gaira nāẓirīna ināhu wa lākin iżā du\'ītum fadkhulụ fa iżā ṭa\'imtum fantasyirụ wa lā musta`nisīna liḥadīṡ, inna żālikum kāna yu`żin-nabiyya fa yastaḥyī mingkum wallāhu lā yastaḥyī minal-ḥaqq, wa iżā sa`altumụhunna matā\'an fas`alụhunna miw warā`i ḥijāb, żālikum aṭ-haru liqulụbikum wa qulụbihinn, wa mā kāna lakum an tu`żụ rasụlallāhi wa lā an tangkiḥū azwājahụ mim ba\'dihī abadā, inna żālikum kāna \'indallāhi \'aẓīmā'),
(3586, 33, 54, 'اِنْ تُبْدُوْا شَيْـًٔا اَوْ تُخْفُوْهُ فَاِنَّ اللّٰهَ كَانَ بِكُلِّ شَيْءٍ عَلِيْمًا', 'in tubdụ syai`an au tukhfụhu fa innallāha kāna bikulli syai`in \'alīmā'),
(3587, 33, 55, 'لَا جُنَاحَ عَلَيْهِنَّ فِيْٓ اٰبَاۤىِٕهِنَّ وَلَآ اَبْنَاۤىِٕهِنَّ وَلَآ اِخْوَانِهِنَّ وَلَآ اَبْنَاۤءِ اِخْوَانِهِنَّ وَلَآ اَبْنَاۤءِ اَخَوٰتِهِنَّ وَلَا نِسَاۤىِٕهِنَّ وَلَا مَا مَلَكَتْ اَيْمَانُهُنَّۚ وَاتَّقِيْنَ اللّٰهَ ۗاِنَّ اللّٰهَ كَانَ عَلٰى كُلِّ شَيْءٍ شَهِيْدًا', 'lā junāḥa \'alaihinna fī ābā`ihinna wa lā abnā`ihinna wa lā ikhwānihinna wa lā abnā`i ikhwānihinna wa lā abnā`i akhawātihinna wa lā nisā`ihinna wa lā mā malakat aimānuhunn, wattaqīnallāh, innallāha kāna \'alā kulli syai`in syahīdā'),
(3588, 33, 56, 'اِنَّ اللّٰهَ وَمَلٰۤىِٕكَتَهٗ يُصَلُّوْنَ عَلَى النَّبِيِّۗ يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا صَلُّوْا عَلَيْهِ وَسَلِّمُوْا تَسْلِيْمًا', 'innallāha wa malā`ikatahụ yuṣallụna \'alan-nabiyy, yā ayyuhallażīna āmanụ ṣallụ \'alaihi wa sallimụ taslīmā'),
(3589, 33, 57, 'اِنَّ الَّذِيْنَ يُؤْذُوْنَ اللّٰهَ وَرَسُوْلَهٗ لَعَنَهُمُ اللّٰهُ فِى الدُّنْيَا وَالْاٰخِرَةِ وَاَعَدَّ لَهُمْ عَذَابًا مُّهِيْنًا', 'innallażīna yu`żụnallāha wa rasụlahụ la\'anahumullāhu fid-dun-yā wal-ākhirati wa a\'adda lahum \'ażābam muhīnā');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(3590, 33, 58, 'وَالَّذِيْنَ يُؤْذُوْنَ الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ بِغَيْرِ مَا اكْتَسَبُوْا فَقَدِ احْتَمَلُوْا بُهْتَانًا وَّاِثْمًا مُّبِيْنًا', 'wallażīna yu`żụnal-mu`minīna wal-mu`mināti bigairi maktasabụ faqadiḥtamalụ buhtānaw wa iṡmam mubīnā'),
(3591, 33, 59, 'يٰٓاَيُّهَا النَّبِيُّ قُلْ لِّاَزْوَاجِكَ وَبَنٰتِكَ وَنِسَاۤءِ الْمُؤْمِنِيْنَ يُدْنِيْنَ عَلَيْهِنَّ مِنْ جَلَابِيْبِهِنَّۗ ذٰلِكَ اَدْنٰىٓ اَنْ يُّعْرَفْنَ فَلَا يُؤْذَيْنَۗ وَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا', 'yā ayyuhan-nabiyyu qul li`azwājika wa banātika wa nisā`il-mu`minīna yudnīna \'alaihinna min jalābībihinn, żālika adnā ay yu\'rafna fa lā yu`żaīn, wa kānallāhu gafụrar raḥīmā'),
(3592, 33, 60, '۞ لَىِٕنْ لَّمْ يَنْتَهِ الْمُنٰفِقُوْنَ وَالَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ وَّالْمُرْجِفُوْنَ فِى الْمَدِيْنَةِ لَنُغْرِيَنَّكَ بِهِمْ ثُمَّ لَا يُجَاوِرُوْنَكَ فِيْهَآ اِلَّا قَلِيْلًا', 'la`il lam yantahil-munāfiqụna wallażīna fī qulụbihim maraḍuw wal-murjifụna fil-madīnati lanugriyannaka bihim ṡumma lā yujāwirụnaka fīhā illā qalīlā'),
(3593, 33, 61, 'مَلْعُوْنِيْنَۖ اَيْنَمَا ثُقِفُوْٓا اُخِذُوْا وَقُتِّلُوْا تَقْتِيْلًا', 'mal\'ụnīna ainamā ṡuqifū ukhiżụ wa quttilụ taqtīlā'),
(3594, 33, 62, 'سُنَّةَ اللّٰهِ فِى الَّذِيْنَ خَلَوْا مِنْ قَبْلُ ۚوَلَنْ تَجِدَ لِسُنَّةِ اللّٰهِ تَبْدِيْلًا', 'sunnatallāhi fillażīna khalau ming qabl, wa lan tajida lisunnatillāhi tabdīlā'),
(3595, 33, 63, 'يَسْـَٔلُكَ النَّاسُ عَنِ السَّاعَةِۗ قُلْ اِنَّمَا عِلْمُهَا عِنْدَ اللّٰهِ ۗوَمَا يُدْرِيْكَ لَعَلَّ السَّاعَةَ تَكُوْنُ قَرِيْبًا', 'yas`alukan-nāsu \'anis-sā\'ah, qul innamā \'ilmuhā \'indallāh, wa mā yudrīka la\'allas-sā\'ata takụnu qarībā'),
(3596, 33, 64, 'اِنَّ اللّٰهَ لَعَنَ الْكٰفِرِيْنَ وَاَعَدَّ لَهُمْ سَعِيْرًاۙ', 'innallāha la\'anal-kāfirīna wa a\'adda lahum sa\'īrā'),
(3597, 33, 65, 'خٰلِدِيْنَ فِيْهَآ اَبَدًاۚ لَا يَجِدُوْنَ وَلِيًّا وَّلَا نَصِيْرًا ۚ', 'khālidīna fīhā abadā, lā yajidụna waliyyaw wa lā naṣīrā'),
(3598, 33, 66, 'يَوْمَ تُقَلَّبُ وُجُوْهُهُمْ فِى النَّارِ يَقُوْلُوْنَ يٰلَيْتَنَآ اَطَعْنَا اللّٰهَ وَاَطَعْنَا الرَّسُوْلَا۠', 'yauma tuqallabu wujụhuhum fin-nāri yaqụlụna yā laitanā aṭa\'nallāha wa aṭa\'nar-rasụlā'),
(3599, 33, 67, 'وَقَالُوْا رَبَّنَآ اِنَّآ اَطَعْنَا سَادَتَنَا وَكُبَرَاۤءَنَا فَاَضَلُّوْنَا السَّبِيْلَا۠', 'wa qālụ rabbanā innā aṭa\'nā sādatanā wa kubarā`anā fa aḍallụnas-sabīlā'),
(3600, 33, 68, 'رَبَّنَآ اٰتِهِمْ ضِعْفَيْنِ مِنَ الْعَذَابِ وَالْعَنْهُمْ لَعْنًا كَبِيْرًا', 'rabbanā ātihim ḍi\'faini minal-\'ażābi wal\'an-hum la\'nang kabīrā'),
(3601, 33, 69, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَكُوْنُوْا كَالَّذِيْنَ اٰذَوْا مُوْسٰى فَبَرَّاَهُ اللّٰهُ مِمَّا قَالُوْا ۗوَكَانَ عِنْدَ اللّٰهِ وَجِيْهًا ۗ', 'yā ayyuhallażīna āmanụ lā takụnụ kallażīna āżau mụsā fa barra`ahullāhu mimmā qālụ, wa kāna \'indallāhi wajīhā'),
(3602, 33, 70, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَقُوْلُوْا قَوْلًا سَدِيْدًاۙ', 'yā ayyuhallażīna āmanuttaqullāha wa qụlụ qaulan sadīdā'),
(3603, 33, 71, 'يُّصْلِحْ لَكُمْ اَعْمَالَكُمْ وَيَغْفِرْ لَكُمْ ذُنُوْبَكُمْۗ وَمَنْ يُّطِعِ اللّٰهَ وَرَسُوْلَهٗ فَقَدْ فَازَ فَوْزًا عَظِيْمًا', 'yuṣliḥ lakum a\'mālakum wa yagfir lakum żunụbakum, wa may yuṭi\'illāha wa rasụlahụ fa qad fāza fauzan \'aẓīmā'),
(3604, 33, 72, 'اِنَّا عَرَضْنَا الْاَمَانَةَ عَلَى السَّمٰوٰتِ وَالْاَرْضِ وَالْجِبَالِ فَاَبَيْنَ اَنْ يَّحْمِلْنَهَا وَاَشْفَقْنَ مِنْهَا وَحَمَلَهَا الْاِنْسَانُۗ اِنَّهٗ كَانَ ظَلُوْمًا جَهُوْلًاۙ', 'innā \'araḍnal-amānata \'alas-samāwāti wal-arḍi wal-jibāli fa abaina ay yaḥmilnahā wa asyfaqna min-hā wa ḥamalahal-insān, innahụ kāna ẓalụman jahụlā'),
(3605, 33, 73, 'لِّيُعَذِّبَ اللّٰهُ الْمُنٰفِقِيْنَ وَالْمُنٰفِقَتِ وَالْمُشْرِكِيْنَ وَالْمُشْرِكٰتِ وَيَتُوْبَ اللّٰهُ عَلَى الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِۗ وَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا', 'liyu\'ażżiballāhul-munāfiqīna wal-munāfiqāti wal-musyrikīna wal-musyrikāti wa yatụballāhu \'alal-mu`minīna wal-mu`mināt, wa kānallāhu gafụrar raḥīmā'),
(3606, 34, 1, 'اَلْحَمْدُ لِلّٰهِ الَّذِيْ لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ وَلَهُ الْحَمْدُ فِى الْاٰخِرَةِۗ وَهُوَ الْحَكِيْمُ الْخَبِيْرُ', 'al-ḥamdu lillāhillażī lahụ mā fis-samāwāti wa mā fil-arḍi wa lahul-ḥamdu fil`ākhirah, wa huwal-ḥakīmul-khabīr'),
(3607, 34, 2, 'يَعْلَمُ مَا يَلِجُ فِى الْاَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنْزِلُ مِنَ السَّمَاۤءِ وَمَا يَعْرُجُ فِيْهَاۗ وَهُوَ الرَّحِيْمُ الْغَفُوْرُ', 'ya\'lamu mā yaliju fil-arḍi wa mā yakhruju min-hā wa mā yanzilu minas-samā`i wa mā ya\'ruju fīhā, wa huwar-raḥīmul-gafụr'),
(3608, 34, 3, 'وَقَالَ الَّذِيْنَ كَفَرُوْا لَا تَأْتِيْنَا السَّاعَةُ ۗقُلْ بَلٰى وَرَبِّيْ لَتَأْتِيَنَّكُمْۙ عٰلِمِ الْغَيْبِۙ لَا يَعْزُبُ عَنْهُ مِثْقَالُ ذَرَّةٍ فِى السَّمٰوٰتِ وَلَا فِى الْاَرْضِ وَلَآ اَصْغَرُ مِنْ ذٰلِكَ وَلَآ اَكْبَرُ اِلَّا فِيْ كِتٰبٍ مُّبِيْنٍۙ', 'wa qālallażīna kafarụ lā ta`tīnas-sā\'ah, qul balā wa rabbī lata`tiyannakum \'ālimil-gaibi lā ya\'zubu \'an-hu miṡqālu żarratin fis-samāwāti wa lā fil-arḍi wa lā aṣgaru min żālika wa lā akbaru illā fī kitābim mubīn'),
(3609, 34, 4, 'لِّيَجْزِيَ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِۗ اُولٰۤىِٕكَ لَهُمْ مَّغْفِرَةٌ وَّرِزْقٌ كَرِيْمٌ', 'liyajziyallażīna āmanụ wa \'amiluṣ-ṣāliḥāt, ulā`ika lahum magfiratuw wa rizqung karīm'),
(3610, 34, 5, 'وَالَّذِيْنَ سَعَوْ فِيْٓ اٰيٰتِنَا مُعٰجِزِيْنَ اُولٰۤىِٕكَ لَهُمْ عَذَابٌ مِّنْ رِّجْزٍ اَلِيْمٌ', 'wallażīna sa\'au fī āyātinā mu\'ājizīna ulā`ika lahum \'ażābum mir rijzin alīm'),
(3611, 34, 6, 'وَيَرَى الَّذِيْنَ اُوْتُوا الْعِلْمَ الَّذِيْٓ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ هُوَ الْحَقَّۙ وَيَهْدِيْٓ اِلٰى صِرَاطِ الْعَزِيْزِ الْحَمِيْدِ', 'wa yarallażīna ụtul-\'ilmallażī unzila ilaika mir rabbika huwal-ḥaqqa wa yahdī ilā ṣirāṭil-\'azīzil-ḥamīd'),
(3612, 34, 7, 'وَقَالَ الَّذِيْنَ كَفَرُوْا هَلْ نَدُلُّكُمْ عَلٰى رَجُلٍ يُّنَبِّئُكُمْ اِذَا مُزِّقْتُمْ كُلَّ مُمَزَّقٍۙ اِنَّكُمْ لَفِيْ خَلْقٍ جَدِيْدٍۚ', 'wa qālallażīna kafarụ hal nadullukum \'alā rajuliy yunabbi`ukum iżā muzziqtum kulla mumazzaqin innakum lafī khalqin jadīd'),
(3613, 34, 8, 'اَفْتَرٰى عَلَى اللّٰهِ كَذِبًا اَمْ بِهٖ جِنَّةٌ ۗبَلِ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ فِى الْعَذَابِ وَالضَّلٰلِ الْبَعِيْدِ', 'aftarā \'alallāhi każiban am bihī jinnah, balillażīna lā yu`minụna bil-ākhirati fil-\'ażābi waḍ-ḍalālil-ba\'īd'),
(3614, 34, 9, 'اَفَلَمْ يَرَوْا اِلٰى مَا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْ مِّنَ السَّمَاۤءِ وَالْاَرْضِۗ اِنْ نَّشَأْ نَخْسِفْ بِهِمُ الْاَرْضَ اَوْ نُسْقِطْ عَلَيْهِمْ كِسَفًا مِّنَ السَّمَاۤءِۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّكُلِّ عَبْدٍ مُّنِيْبٍ', 'a fa lam yarau ilā mā baina aidīhim wa mā khalfahum minas-samā`i wal-arḍ, in nasya` nakhsif bihimul-arḍa au nusqiṭ \'alaihim kisafam minas-samā`, inna fī żālika la`āyatal likulli \'abdim munīb'),
(3615, 34, 10, '۞ وَلَقَدْ اٰتَيْنَا دَاوٗدَ مِنَّا فَضْلًاۗ يٰجِبَالُ اَوِّبِيْ مَعَهٗ وَالطَّيْرَ ۚوَاَلَنَّا لَهُ الْحَدِيْدَۙ', 'wa laqad ātainā dāwụda minnā faḍlā, yā jibālu awwibī ma\'ahụ waṭ-ṭaīr, wa alannā lahul-ḥadīd'),
(3616, 34, 11, 'اَنِ اعْمَلْ سٰبِغٰتٍ وَّقَدِّرْ فِى السَّرْدِ وَاعْمَلُوْا صَالِحًاۗ اِنِّيْ بِمَا تَعْمَلُوْنَ بَصِيْرٌ', 'ani\'mal sābigātiw wa qaddir fis-sardi wa\'malụ ṣāliḥā, innī bimā ta\'malụna baṣīr'),
(3617, 34, 12, 'وَلِسُلَيْمٰنَ الرِّيْحَ غُدُوُّهَا شَهْرٌ وَّرَوَاحُهَا شَهْرٌۚ وَاَسَلْنَا لَهٗ عَيْنَ الْقِطْرِۗ وَمِنَ الْجِنِّ مَنْ يَّعْمَلُ بَيْنَ يَدَيْهِ بِاِذْنِ رَبِّهٖۗ وَمَنْ يَّزِغْ مِنْهُمْ عَنْ اَمْرِنَا نُذِقْهُ مِنْ عَذَابِ السَّعِيْرِ', 'wa lisulaimānar-rīḥa guduwwuhā syahruw wa rawāḥuhā syahr, wa asalnā lahụ \'ainal-qiṭr, wa minal-jinni may ya\'malu baina yadaihi bi`iżni rabbih, wa may yazig min-hum \'an amrinā nużiq-hu min \'ażābis-sa\'īr'),
(3618, 34, 13, 'يَعْمَلُوْنَ لَهٗ مَا يَشَاۤءُ مِنْ مَّحَارِيْبَ وَتَمَاثِيْلَ وَجِفَانٍ كَالْجَوَابِ وَقُدُوْرٍ رّٰسِيٰتٍۗ اِعْمَلُوْٓا اٰلَ دَاوٗدَ شُكْرًا ۗوَقَلِيْلٌ مِّنْ عِبَادِيَ الشَّكُوْرُ', 'ya\'malụna lahụ mā yasyā`u mim maḥārība wa tamāṡīla wa jifāning kal-jawābi wa qudụrir rāsiyāt, i\'malū āla dāwụda syukrā, wa qalīlum min \'ibādiyasy-syakụr'),
(3619, 34, 14, 'فَلَمَّا قَضَيْنَا عَلَيْهِ الْمَوْتَ مَا دَلَّهُمْ عَلٰى مَوْتِهٖٓ اِلَّا دَاۤبَّةُ الْاَرْضِ تَأْكُلُ مِنْسَاَتَهٗ ۚفَلَمَّا خَرَّ تَبَيَّنَتِ الْجِنُّ اَنْ لَّوْ كَانُوْا يَعْلَمُوْنَ الْغَيْبَ مَا لَبِثُوْا فِى الْعَذَابِ الْمُهِيْنِۗ', 'fa lammā qaḍainā \'alaihil-mauta mā dallahum \'alā mautihī illā dābbatul-arḍi ta`kulu minsa`atah, fa lammā kharra tabayyanatil-jinnu al lau kānụ ya\'lamụnal-gaiba mā labiṡụ fil-\'ażābil-muhīn'),
(3620, 34, 15, 'لَقَدْ كَانَ لِسَبَاٍ فِيْ مَسْكَنِهِمْ اٰيَةٌ ۚجَنَّتٰنِ عَنْ يَّمِيْنٍ وَّشِمَالٍ ەۗ كُلُوْا مِنْ رِّزْقِ رَبِّكُمْ وَاشْكُرُوْا لَهٗ ۗبَلْدَةٌ طَيِّبَةٌ وَّرَبٌّ غَفُوْرٌ', 'laqad kāna lisaba`in fī maskanihim āyah, jannatāni \'ay yamīniw wa syimāl, kulụ mir rizqi rabbikum wasykurụ lah, baldatun ṭayyibatuw wa rabbun gafụr'),
(3621, 34, 16, 'فَاَعْرَضُوْا فَاَرْسَلْنَا عَلَيْهِمْ سَيْلَ الْعَرِمِ وَبَدَّلْنٰهُمْ بِجَنَّتَيْهِمْ جَنَّتَيْنِ ذَوَاتَيْ اُكُلٍ خَمْطٍ وَّاَثْلٍ وَّشَيْءٍ مِّنْ سِدْرٍ قَلِيْلٍ', 'fa a\'raḍụ fa arsalnā \'alaihim sailal-\'arimi wa baddalnāhum bijannataihim jannataini żawātai ukulin khamṭiw wa aṡliw wa syai`im min sidring qalīl'),
(3622, 34, 17, 'ذٰلِكَ جَزَيْنٰهُمْ بِمَا كَفَرُوْاۗ وَهَلْ نُجٰزِيْٓ اِلَّا الْكَفُوْرَ', 'żālika jazaināhum bimā kafarụ, wa hal nujāzī illal-kafụr'),
(3623, 34, 18, 'وَجَعَلْنَا بَيْنَهُمْ وَبَيْنَ الْقُرَى الَّتِيْ بٰرَكْنَا فِيْهَا قُرًى ظَاهِرَةً وَّقَدَّرْنَا فِيْهَا السَّيْرَۗ سِيْرُوْا فِيْهَا لَيَالِيَ وَاَيَّامًا اٰمِنِيْنَ', 'wa ja\'alnā bainahum wa bainal-qurallatī bāraknā fīhā quran ẓāhirataw wa qaddarnā fīhas-saīr, sīrụ fīhā layāliya wa ayyāman āminīn'),
(3624, 34, 19, 'فَقَالُوْا رَبَّنَا بٰعِدْ بَيْنَ اَسْفَارِنَا وَظَلَمُوْٓا اَنْفُسَهُمْ فَجَعَلْنٰهُمْ اَحَادِيْثَ وَمَزَّقْنٰهُمْ كُلَّ مُمَزَّقٍۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّكُلِّ صَبَّارٍ شَكُوْرٍ', 'fa qālụ rabbanā bā\'id baina asfārinā wa ẓalamū anfusahum fa ja\'alnāhum aḥādīṡa wa mazzaqnāhum kulla mumazzaq, inna fī żālika la`āyātil likulli ṣabbārin syakụr'),
(3625, 34, 20, 'وَلَقَدْ صَدَّقَ عَلَيْهِمْ اِبْلِيْسُ ظَنَّهٗ فَاتَّبَعُوْهُ اِلَّا فَرِيْقًا مِّنَ الْمُؤْمِنِيْنَ', 'wa laqad ṣaddaqa \'alaihim iblīsu ẓannahụ fattaba\'ụhu illā farīqam minal-mu`minīn'),
(3626, 34, 21, 'وَمَا كَانَ لَهٗ عَلَيْهِمْ مِّنْ سُلْطَانٍ اِلَّا لِنَعْلَمَ مَنْ يُّؤْمِنُ بِالْاٰخِرَةِ مِمَّنْ هُوَ مِنْهَا فِيْ شَكٍّ ۗوَرَبُّكَ عَلٰى كُلِّ شَيْءٍ حَفِيْظٌ', 'wa mā kāna lahụ \'alaihim min sulṭānin illā lina\'lama may yu`minu bil-ākhirati mim man huwa min-hā fī syakk, wa rabbuka \'alā kulli syai`in ḥafīẓ'),
(3627, 34, 22, 'قُلِ ادْعُوا الَّذِيْنَ زَعَمْتُمْ مِّنْ دُوْنِ اللّٰهِۚ لَا يَمْلِكُوْنَ مِثْقَالَ ذَرَّةٍ فِى السَّمٰوٰتِ وَلَا فِى الْاَرْضِ وَمَا لَهُمْ فِيْهِمَا مِنْ شِرْكٍ وَّمَا لَهٗ مِنْهُمْ مِّنْ ظَهِيْرٍ', 'qulid\'ullażīna za\'amtum min dụnillāh, lā yamlikụna miṡqāla żarratin fis-samāwāti wa lā fil-arḍi wa mā lahum fīhimā min syirkiw wa mā lahụ min-hum min ẓahīr'),
(3628, 34, 23, 'وَلَا تَنْفَعُ الشَّفَاعَةُ عِنْدَهٗٓ اِلَّا لِمَنْ اَذِنَ لَهٗ ۗحَتّٰىٓ اِذَا فُزِّعَ عَنْ قُلُوْبِهِمْ قَالُوْا مَاذَاۙ قَالَ رَبُّكُمْۗ قَالُوا الْحَقَّۚ وَهُوَ الْعَلِيُّ الْكَبِيْرُ', 'wa lā tanfa\'usy-syafā\'atu \'indahū illā liman ażina lah, ḥattā iżā fuzzi\'a \'ang qulụbihim qālụ māżā qāla rabbukum, qālul-ḥaqq, wa huwal-\'aliyyul-kabīr'),
(3629, 34, 24, '۞ قُلْ مَنْ يَّرْزُقُكُمْ مِّنَ السَّمٰوٰتِ وَالْاَرْضِۗ قُلِ اللّٰهُ ۙوَاِنَّآ اَوْ اِيَّاكُمْ لَعَلٰى هُدًى اَوْ فِيْ ضَلٰلٍ مُّبِيْنٍ', 'qul may yarzuqukum minas-samāwāti wal-arḍ, qulillāhu wa innā au iyyākum la\'alā hudan au fī ḍalālim mubīn'),
(3630, 34, 25, 'قُلْ لَّا تُسْـَٔلُوْنَ عَمَّآ اَجْرَمْنَا وَلَا نُسْـَٔلُ عَمَّا تَعْمَلُوْنَ', 'qul lā tus`alụna \'ammā ajramnā wa lā nus`alu \'ammā ta\'malụn'),
(3631, 34, 26, 'قُلْ يَجْمَعُ بَيْنَنَا رَبُّنَا ثُمَّ يَفْتَحُ بَيْنَنَا بِالْحَقِّۗ وَهُوَ الْفَتَّاحُ الْعَلِيْمُ', 'qul yajma\'u bainanā rabbunā ṡumma yaftaḥu bainanā bil-ḥaqq, wa huwal-fattāḥul-\'alīm'),
(3632, 34, 27, 'قُلْ اَرُوْنِيَ الَّذِيْنَ اَلْحَقْتُمْ بِهٖ شُرَكَاۤءَ كَلَّا ۗبَلْ هُوَ اللّٰهُ الْعَزِيْزُ الْحَكِيْمُ', 'qul arụniyallażīna alḥaqtum bihī syurakā`a kallā, bal huwallāhul-\'azīzul-ḥakīm'),
(3633, 34, 28, 'وَمَآ اَرْسَلْنٰكَ اِلَّا كَاۤفَّةً لِّلنَّاسِ بَشِيْرًا وَّنَذِيْرًا وَّلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ', 'wa mā arsalnāka illā kāffatal lin-nāsi basyīraw wa nażīraw wa lākinna akṡaran-nāsi lā ya\'lamụn'),
(3634, 34, 29, 'وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'wa yaqụlụna matā hāżal-wa\'du ing kuntum ṣādiqīn'),
(3635, 34, 30, 'قُلْ لَّكُمْ مِّيْعَادُ يَوْمٍ لَّا تَسْتَأْخِرُوْنَ عَنْهُ سَاعَةً وَّلَا تَسْتَقْدِمُوْنَ', 'qul lakum mī\'ādu yaumil lā tasta`khirụna \'an-hu sā\'ataw wa lā tastaqdimụn'),
(3636, 34, 31, 'وَقَالَ الَّذِيْنَ كَفَرُوْا لَنْ نُّؤْمِنَ بِهٰذَا الْقُرْاٰنِ وَلَا بِالَّذِيْ بَيْنَ يَدَيْهِۗ وَلَوْ تَرٰىٓ اِذِ الظّٰلِمُوْنَ مَوْقُوْفُوْنَ عِنْدَ رَبِّهِمْۖ يَرْجِعُ بَعْضُهُمْ اِلٰى بَعْضِ ِۨالْقَوْلَۚ يَقُوْلُ الَّذِيْنَ اسْتُضْعِفُوْا لِلَّذِيْنَ اسْتَكْبَرُوْا لَوْلَآ اَنْتُمْ لَكُنَّا مُؤْمِنِيْنَ', 'wa qālallażīna kafarụ lan nu`mina bihāżal-qur`āni wa lā billażī baina yadaīh, walau tarā iżiẓ-ẓālimụna mauqụfụna \'inda rabbihim yarji\'u ba\'ḍuhum ilā ba\'ḍinil-qaụl, yaqụlullażīnastuḍ\'ifụ lillażīnastakbarụ lau lā antum lakunnā mu`minīn'),
(3637, 34, 32, 'قَالَ الَّذِيْنَ اسْتَكْبَرُوْا لِلَّذِيْنَ اسْتُضْعِفُوْٓا اَنَحْنُ صَدَدْنٰكُمْ عَنِ الْهُدٰى بَعْدَ اِذْ جَاۤءَكُمْ بَلْ كُنْتُمْ مُّجْرِمِيْنَ', 'qālallażīnastakbarụ lillażīnastuḍ\'ifū a naḥnu ṣadadnākum \'anil-hudā ba\'da iż jā`akum bal kuntum mujrimīn'),
(3638, 34, 33, 'وَقَالَ الَّذِيْنَ اسْتُضْعِفُوْا لِلَّذِيْنَ اسْتَكْبَرُوْا بَلْ مَكْرُ الَّيْلِ وَالنَّهَارِ اِذْ تَأْمُرُوْنَنَآ اَنْ نَّكْفُرَ بِاللّٰهِ وَنَجْعَلَ لَهٗٓ اَنْدَادًا ۗوَاَسَرُّوا النَّدَامَةَ لَمَّا رَاَوُا الْعَذَابَۗ وَجَعَلْنَا الْاَغْلٰلَ فِيْٓ اَعْنَاقِ الَّذِيْنَ كَفَرُوْاۗ هَلْ يُجْزَوْنَ اِلَّا مَا كَانُوْا يَعْمَلُوْنَ', 'wa qālallażīnastuḍ\'ifụ lillażīnastakbarụ bal makrul-laili wan-nahāri iż ta`murụnanā an nakfura billāhi wa naj\'ala lahū andādā, wa asarrun-nadāmata lammā ra`awul-\'ażāb, wa ja\'alnal-aglāla fī a\'nāqillażīna kafarụ, hal yujzauna illā mā kānụ ya\'malụn'),
(3639, 34, 34, 'وَمَآ اَرْسَلْنَا فِيْ قَرْيَةٍ مِّنْ نَّذِيْرٍ ِالَّا قَالَ مُتْرَفُوْهَآ ۙاِنَّا بِمَآ اُرْسِلْتُمْ بِهٖ كٰفِرُوْنَ', 'wa mā arsalnā fī qaryatim min nażīrin illā qāla mutrafụhā innā bimā ursiltum bihī kāfirụn'),
(3640, 34, 35, 'وَقَالُوْا نَحْنُ اَكْثَرُ اَمْوَالًا وَّاَوْلَادًاۙ وَّمَا نَحْنُ بِمُعَذَّبِيْنَ', 'wa qālụ naḥnu akṡaru amwālaw wa aulādaw wa mā naḥnu bimu\'ażżabīn'),
(3641, 34, 36, 'قُلْ اِنَّ رَبِّيْ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ', 'qul inna rabbī yabsuṭur-rizqa limay yasyā`u wa yaqdiru wa lākinna akṡaran-nāsi lā ya\'lamụn'),
(3642, 34, 37, 'وَمَآ اَمْوَالُكُمْ وَلَآ اَوْلَادُكُمْ بِالَّتِيْ تُقَرِّبُكُمْ عِنْدَنَا زُلْفٰىٓ اِلَّا مَنْ اٰمَنَ وَعَمِلَ صَالِحًاۙ فَاُولٰۤىِٕكَ لَهُمْ جَزَاۤءُ الضِّعْفِ بِمَا عَمِلُوْا وَهُمْ فِى الْغُرُفٰتِ اٰمِنُوْنَ', 'wa mā amwālukum wa lā aulādukum billatī tuqarribukum \'indanā zulfā illā man āmana wa \'amila ṣāliḥan fa ulā`ika lahum jazā`uḍ-ḍi\'fi bimā \'amilụ wa hum fil-gurufāti āminụn'),
(3643, 34, 38, 'وَالَّذِيْنَ يَسْعَوْنَ فِيْٓ اٰيٰتِنَا مُعٰجِزِيْنَ اُولٰۤىِٕكَ فِى الْعَذَابِ مُحْضَرُوْنَ', 'wallażīna yas\'auna fī āyātinā mu\'ājizīna ulā`ika fil-\'ażābi muḥḍarụn'),
(3644, 34, 39, 'قُلْ اِنَّ رَبِّيْ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ وَيَقْدِرُ لَهٗ ۗوَمَآ اَنْفَقْتُمْ مِّنْ شَيْءٍ فَهُوَ يُخْلِفُهٗ ۚوَهُوَ خَيْرُ الرّٰزِقِيْنَ', 'qul inna rabbī yabsuṭur-rizqa limay yasyā`u min \'ibādihī wa yaqdiru lah, wa mā anfaqtum min syai`in fa huwa yukhlifuh, wa huwa khairur-rāziqīn'),
(3645, 34, 40, 'وَيَوْمَ يَحْشُرُهُمْ جَمِيْعًا ثُمَّ يَقُوْلُ لِلْمَلٰۤىِٕكَةِ اَهٰٓؤُلَاۤءِ اِيَّاكُمْ كَانُوْا يَعْبُدُوْنَ', 'wa yauma yaḥsyuruhum jamī\'an ṡumma yaqụlu lil-malā`ikati a hā`ulā`i iyyākum kānụ ya\'budụn'),
(3646, 34, 41, 'قَالُوْا سُبْحٰنَكَ اَنْتَ وَلِيُّنَا مِنْ دُوْنِهِمْ ۚبَلْ كَانُوْا يَعْبُدُوْنَ الْجِنَّ اَكْثَرُهُمْ بِهِمْ مُّؤْمِنُوْنَ', 'qālụ sub-ḥānaka anta waliyyunā min dụnihim, bal kānụ ya\'budụnal-jinna akṡaruhum bihim mu`minụn'),
(3647, 34, 42, 'فَالْيَوْمَ لَا يَمْلِكُ بَعْضُكُمْ لِبَعْضٍ نَّفْعًا وَّلَا ضَرًّا ۗوَنَقُوْلُ لِلَّذِيْنَ ظَلَمُوْا ذُوْقُوْا عَذَابَ النَّارِ الَّتِيْ كُنْتُمْ بِهَا تُكَذِّبُوْنَ', 'fal-yauma lā yamliku ba\'ḍukum liba\'ḍin naf\'aw wa lā ḍarrā, wa naqụlu lillażīna ẓalamụ żụqụ \'ażāban-nārillatī kuntum bihā tukażżibụn'),
(3648, 34, 43, 'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا بَيِّنٰتٍ قَالُوْا مَا هٰذَآ اِلَّا رَجُلٌ يُّرِيْدُ اَنْ يَّصُدَّكُمْ عَمَّا كَانَ يَعْبُدُ اٰبَاۤؤُكُمْ ۚوَقَالُوْا مَا هٰذَآ اِلَّآ اِفْكٌ مُّفْتَرًىۗ وَقَالَ الَّذِيْنَ كَفَرُوْا لِلْحَقِّ لَمَّا جَاۤءَهُمْۙ اِنْ هٰذَآ اِلَّا سِحْرٌ مُّبِيْنٌ', 'wa iżā tutlā \'alaihim āyātunā bayyināting qālụ mā hāżā illā rajuluy yurīdu ay yaṣuddakum \'ammā kāna ya\'budu ābā`ukum, wa qālụ mā hāżā illā ifkum muftarā, wa qālallażīna kafarụ lil-ḥaqqi lammā jā`ahum in hāżā illā siḥrum mubīn'),
(3649, 34, 44, 'وَمَآ اٰتَيْنٰهُمْ مِّنْ كُتُبٍ يَّدْرُسُوْنَهَا وَمَآ اَرْسَلْنَآ اِلَيْهِمْ قَبْلَكَ مِنْ نَّذِيْرٍۗ', 'wa mā ātaināhum ming kutubiy yadrusụnahā wa mā arsalnā ilaihim qablaka min nażīr'),
(3650, 34, 45, 'وَكَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْۙ وَمَا بَلَغُوْا مِعْشَارَ مَآ اٰتَيْنٰهُمْ فَكَذَّبُوْا رُسُلِيْۗ فَكَيْفَ كَانَ نَكِيْرِ', 'wa każżaballażīna ming qablihim wa mā balagụ mi\'syāra mā ātaināhum fa każżabụ rusulī, fa kaifa kāna nakīr'),
(3651, 34, 46, '۞ قُلْ اِنَّمَآ اَعِظُكُمْ بِوَاحِدَةٍۚ اَنْ تَقُوْمُوْا لِلّٰهِ مَثْنٰى وَفُرَادٰى ثُمَّ تَتَفَكَّرُوْاۗ مَا بِصَاحِبِكُمْ مِّنْ جِنَّةٍۗ اِنْ هُوَ اِلَّا نَذِيْرٌ لَّكُمْ بَيْنَ يَدَيْ عَذَابٍ شَدِيْدٍ', 'qul innamā a\'iẓukum biwāḥidah, an taqụmụ lillāhi maṡnā wa furādā ṡumma tatafakkarụ, mā biṣāḥibikum min jinnah, in huwa illā nażīrul lakum baina yadai \'ażābin syadīd'),
(3652, 34, 47, 'قُلْ مَا سَاَلْتُكُمْ مِّنْ اَجْرٍ فَهُوَ لَكُمْۗ اِنْ اَجْرِيَ اِلَّا عَلَى اللّٰهِ ۚوَهُوَ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ', 'qul mā sa`altukum min ajrin fa huwa lakum, in ajriya illā \'alallāh, wa huwa \'alā kulli syai`in syahīd'),
(3653, 34, 48, 'قُلْ اِنَّ رَبِّيْ يَقْذِفُ بِالْحَقِّۚ عَلَّامُ الْغُيُوْبِ', 'qul inna rabbī yaqżifu bil-ḥaqq, \'allāmul-guyụb'),
(3654, 34, 49, 'قُلْ جَاۤءَ الْحَقُّ وَمَا يُبْدِئُ الْبَاطِلُ وَمَا يُعِيْدُ', 'qul jā`al-ḥaqqu wa mā yubdi`ul-bāṭilu wa mā yu\'īd'),
(3655, 34, 50, 'قُلْ اِنْ ضَلَلْتُ فَاِنَّمَآ اَضِلُّ عَلٰى نَفْسِيْۚ وَاِنِ اهْتَدَيْتُ فَبِمَا يُوْحِيْٓ اِلَيَّ رَبِّيْۗ اِنَّهٗ سَمِيْعٌ قَرِيْبٌ', 'qul in ḍalaltu fa innamā aḍillu \'alā nafsī, wa inihtadaitu fa bimā yụḥī ilayya rabbī, innahụ samī\'ung qarīb'),
(3656, 34, 51, 'وَلَوْ تَرٰىٓ اِذْ فَزِعُوْا فَلَا فَوْتَ وَاُخِذُوْا مِنْ مَّكَانٍ قَرِيْبٍۙ', 'walau tarā iż fazi\'ụ fa lā fauta wa ukhiżụ mim makāning qarīb'),
(3657, 34, 52, 'وَّقَالُوْٓا اٰمَنَّا بِهٖۚ وَاَنّٰى لَهُمُ التَّنَاوُشُ مِنْ مَّكَانٍۢ بَعِيْدٍۚ', 'wa qālū āmannā bih, wa annā lahumut-tanāwusyu mim makānim ba\'īd'),
(3658, 34, 53, 'وَقَدْ كَفَرُوْا بِهٖ مِنْ قَبْلُۚ وَيَقْذِفُوْنَ بِالْغَيْبِ مِنْ مَّكَانٍۢ بَعِيْدٍۚ', 'wa qad kafarụ bihī ming qabl, wa yaqżifụna bil-gaibi mim makānim ba\'īd'),
(3659, 34, 54, 'وَحِيْلَ بَيْنَهُمْ وَبَيْنَ مَا يَشْتَهُوْنَۙ كَمَا فُعِلَ بِاَشْيَاعِهِمْ مِّنْ قَبْلُۗ اِنَّهُمْ كَانُوْا فِيْ شَكٍّ مُّرِيْبٍ', 'wa hīla bainahum wa baina mā yasytahụna kamā fu\'ila bi`asy-yā\'ihim ming qabl, innahum kānụ fī syakkim murīb'),
(3660, 35, 1, 'اَلْحَمْدُ لِلّٰهِ فَاطِرِ السَّمٰوٰتِ وَالْاَرْضِ جَاعِلِ الْمَلٰۤىِٕكَةِ رُسُلًاۙ اُولِيْٓ اَجْنِحَةٍ مَّثْنٰى وَثُلٰثَ وَرُبٰعَۗ يَزِيْدُ فِى الْخَلْقِ مَا يَشَاۤءُۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'al-ḥamdu lillāhi fāṭiris-samāwāti wal-arḍi jā\'ilil-malā`ikati rusulan ulī ajniḥatim maṡnā wa ṡulāṡa wa rubā\', yazīdu fil-khalqi mā yasyā`, innallāha \'alā kulli syai`ing qadīr'),
(3661, 35, 2, 'مَا يَفْتَحِ اللّٰهُ لِلنَّاسِ مِنْ رَّحْمَةٍ فَلَا مُمْسِكَ لَهَا ۚوَمَا يُمْسِكْۙ فَلَا مُرْسِلَ لَهٗ مِنْۢ بَعْدِهٖۗ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ', 'mā yaftaḥillāhu lin-nāsi mir raḥmatin fa lā mumsika lahā, wa mā yumsik fa lā mursila lahụ mim ba\'dih, wa huwal-\'azīzul-ḥakīm'),
(3662, 35, 3, 'يٰٓاَيُّهَا النَّاسُ اذْكُرُوْا نِعْمَتَ اللّٰهِ عَلَيْكُمْۗ هَلْ مِنْ خَالِقٍ غَيْرُ اللّٰهِ يَرْزُقُكُمْ مِّنَ السَّمَاۤءِ وَالْاَرْضِۗ لَآ اِلٰهَ اِلَّا هُوَۖ فَاَنّٰى تُؤْفَكُوْنَ', 'yā ayyuhan-nāsużkurụ ni\'matallāhi \'alaikum, hal min khāliqin gairullāhi yarzuqukum minas-samā`i wal-arḍ, lā ilāha illā huwa fa annā tu`fakụn'),
(3663, 35, 4, 'وَاِنْ يُّكَذِّبُوْكَ فَقَدْ كُذِّبَتْ رُسُلٌ مِّنْ قَبْلِكَۗ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ', 'wa iy yukażżibụka fa qad kużżibat rusulum ming qablik, wa ilallāhi turja\'ul-umụr'),
(3664, 35, 5, 'يٰٓاَيُّهَا النَّاسُ اِنَّ وَعْدَ اللّٰهِ حَقٌّ فَلَا تَغُرَّنَّكُمُ الْحَيٰوةُ الدُّنْيَاۗ وَلَا يَغُرَّنَّكُمْ بِاللّٰهِ الْغَرُوْرُ', 'yā ayyuhan-nāsu inna wa\'dallāhi ḥaqqun fa lā tagurrannakumul-ḥayātud-dun-yā, wa lā yagurrannakum billāhil-garụr'),
(3665, 35, 6, 'اِنَّ الشَّيْطٰنَ لَكُمْ عَدُوٌّ فَاتَّخِذُوْهُ عَدُوًّاۗ اِنَّمَا يَدْعُوْا حِزْبَهٗ لِيَكُوْنُوْا مِنْ اَصْحٰبِ السَّعِيْرِۗ', 'innasy-syaiṭāna lakum \'aduwwun fattakhiżụhu \'aduwwā, innamā yad\'ụ ḥizbahụ liyakụnụ min aṣ-ḥābis-sa\'īr'),
(3666, 35, 7, 'اَلَّذِيْنَ كَفَرُوْا لَهُمْ عَذَابٌ شَدِيْدٌ ەۗ وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ مَّغْفِرَةٌ وَّاَجْرٌ كَبِيْرٌ', 'allażīna kafarụ lahum \'ażābun syadīd, wallażīna āmanụ wa \'amiluṣ-ṣāliḥāti lahum magfiratuw wa ajrung kabīr'),
(3667, 35, 8, 'اَفَمَنْ زُيِّنَ لَهٗ سُوْۤءُ عَمَلِهٖ فَرَاٰهُ حَسَنًاۗ فَاِنَّ اللّٰهَ يُضِلُّ مَنْ يَّشَاۤءُ وَيَهْدِيْ مَنْ يَّشَاۤءُۖ فَلَا تَذْهَبْ نَفْسُكَ عَلَيْهِمْ حَسَرٰتٍۗ اِنَّ اللّٰهَ عَلِيْمٌ ۢبِمَا يَصْنَعُوْنَ', 'a fa man zuyyina lahụ sū`u \'amalihī fa ra`āhu ḥasanā, fa innallāha yuḍillu may yasyā`u wa yahdī may yasyā`u fa lā taż-hab nafsuka \'alaihim ḥasarāt, innallāha \'alīmum bimā yaṣna\'ụn'),
(3668, 35, 9, 'وَاللّٰهُ الَّذِيْٓ اَرْسَلَ الرِّيٰحَ فَتُثِيْرُ سَحَابًا فَسُقْنٰهُ اِلٰى بَلَدٍ مَّيِّتٍ فَاَحْيَيْنَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَاۗ كَذٰلِكَ النُّشُوْرُ', 'wallāhullażī arsalar-riyāḥa fa tuṡīru saḥāban fa suqnāhu ilā baladim mayyitin fa aḥyainā bihil-arḍa ba\'da mautihā, każālikan-nusyụr'),
(3669, 35, 10, 'مَنْ كَانَ يُرِيْدُ الْعِزَّةَ فَلِلّٰهِ الْعِزَّةُ جَمِيْعًاۗ اِلَيْهِ يَصْعَدُ الْكَلِمُ الطَّيِّبُ وَالْعَمَلُ الصَّالِحُ يَرْفَعُهٗ ۗوَالَّذِيْنَ يَمْكُرُوْنَ السَّيِّاٰتِ لَهُمْ عَذَابٌ شَدِيْدٌ ۗوَمَكْرُ اُولٰۤىِٕكَ هُوَ يَبُوْرُ', 'mang kāna yurīdul-\'izzata fa lillāhil-\'izzatu jamī\'ā, ilaihi yaṣ\'adul-kalimuṭ-ṭayyibu wal-\'amaluṣ-ṣāliḥu yarfa\'uh, wallażīna yamkurụnas-sayyi`āti lahum \'ażābun syadīd, wa makru ulā`ika huwa yabụr'),
(3670, 35, 11, 'وَاللّٰهُ خَلَقَكُمْ مِّنْ تُرَابٍ ثُمَّ مِنْ نُّطْفَةٍ ثُمَّ جَعَلَكُمْ اَزْوَاجًاۗ وَمَا تَحْمِلُ مِنْ اُنْثٰى وَلَا تَضَعُ اِلَّا بِعِلْمِهٖۗ وَمَا يُعَمَّرُ مِنْ مُّعَمَّرٍ وَّلَا يُنْقَصُ مِنْ عُمُرِهٖٓ اِلَّا فِيْ كِتٰبٍۗ اِنَّ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرٌ', 'wallāhu khalaqakum min turābin ṡumma min nuṭfatin ṡumma ja\'alakum azwājā, wa mā taḥmilu min unṡā wa lā taḍa\'u illā bi\'ilmih, wa mā yu\'ammaru mim mu\'ammariw wa lā yungqaṣu min \'umurihī illā fī kitāb, inna żālika \'alallāhi yasīr'),
(3671, 35, 12, 'وَمَا يَسْتَوِى الْبَحْرٰنِۖ هٰذَا عَذْبٌ فُرَاتٌ سَاۤىِٕغٌ شَرَابُهٗ وَهٰذَا مِلْحٌ اُجَاجٌۗ وَمِنْ كُلٍّ تَأْكُلُوْنَ لَحْمًا طَرِيًّا وَّتَسْتَخْرِجُوْنَ حِلْيَةً تَلْبَسُوْنَهَا ۚوَتَرَى الْفُلْكَ فِيْهِ مَوَاخِرَ لِتَبْتَغُوْا مِنْ فَضْلِهٖ وَلَعَلَّكُمْ تَشْكُرُوْنَ', 'wa mā yastawil-baḥrāni hāżā \'ażbun furātun sā`igun syarābuhụ wa hāżā mil-ḥun ujāj, wa ming kullin ta`kulụna laḥman ṭariyyaw wa tastakhrijụna ḥilyatan talbasụnahā, wa taral-fulka fīhi mawākhira litabtagụ min faḍlihī wa la\'allakum tasykurụn'),
(3672, 35, 13, 'يُوْلِجُ الَّيْلَ فِى النَّهَارِ وَيُوْلِجُ النَّهَارَ فِى الَّيْلِۚ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ كُلٌّ يَّجْرِيْ لِاَجَلٍ مُّسَمًّىۗ ذٰلِكُمُ اللّٰهُ رَبُّكُمْ لَهُ الْمُلْكُۗ وَالَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِهٖ مَا يَمْلِكُوْنَ مِنْ قِطْمِيْرٍۗ', 'yụlijul-laila fin-nahāri wa yụlijun-nahāra fil-laīl, wa sakhkharasy-syamsa wal-qamara kulluy yajrī li`ajalim musammā, żālikumullāhu rabbukum lahul-mulk, wallażīna tad\'ụna min dụnihī mā yamlikụna ming qiṭmīr'),
(3673, 35, 14, 'اِنْ تَدْعُوْهُمْ لَا يَسْمَعُوْا دُعَاۤءَكُمْۚ وَلَوْ سَمِعُوْا مَا اسْتَجَابُوْا لَكُمْۗ وَيَوْمَ الْقِيٰمَةِ يَكْفُرُوْنَ بِشِرْكِكُمْۗ وَلَا يُنَبِّئُكَ مِثْلُ خَبِيْرٍ', 'in tad\'ụhum lā yasma\'ụ du\'ā`akum, walau sami\'ụ mastajābụ lakum, wa yaumal-qiyāmati yakfurụna bisyirkikum, wa lā yunabbi`uka miṡlu khabīr'),
(3674, 35, 15, '۞ يٰٓاَيُّهَا النَّاسُ اَنْتُمُ الْفُقَرَاۤءُ اِلَى اللّٰهِ ۚوَاللّٰهُ هُوَ الْغَنِيُّ الْحَمِيْدُ', 'yā ayyuhan-nāsu antumul-fuqarā`u ilallāh, wallāhu huwal-ganiyyul-ḥamīd'),
(3675, 35, 16, 'اِنْ يَّشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍ جَدِيْدٍۚ', 'iy yasya` yuż-hibkum wa ya`ti bikhalqin jadīd'),
(3676, 35, 17, 'وَمَا ذٰلِكَ عَلَى اللّٰهِ بِعَزِيْزٍ', 'wa mā żālika \'alallāhi bi\'azīz'),
(3677, 35, 18, 'وَلَا تَزِرُ وَازِرَةٌ وِّزْرَ اُخْرٰى ۗوَاِنْ تَدْعُ مُثْقَلَةٌ اِلٰى حِمْلِهَا لَا يُحْمَلْ مِنْهُ شَيْءٌ وَّلَوْ كَانَ ذَا قُرْبٰىۗ اِنَّمَا تُنْذِرُ الَّذِيْنَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ وَاَقَامُوا الصَّلٰوةَ ۗوَمَنْ تَزَكّٰى فَاِنَّمَا يَتَزَكّٰى لِنَفْسِهٖ ۗوَاِلَى اللّٰهِ الْمَصِيْرُ', 'wa lā taziru wāziratuw wizra ukhrā, wa in tad\'u muṡqalatun ilā ḥimlihā lā yuḥmal min-hu syai`uw walau kāna żā qurbā, innamā tunżirullażīna yakhsyauna rabbahum bil-gaibi wa aqāmuṣ-ṣalāh, wa man tazakkā fa innamā yatazakkā linafsih, wa ilallāhil-maṣīr'),
(3678, 35, 19, 'وَمَا يَسْتَوِى الْاَعْمٰى وَالْبَصِيْرُ ۙ', 'wa mā yastawil-a\'mā wal-baṣīr'),
(3679, 35, 20, 'وَلَا الظُّلُمٰتُ وَلَا النُّوْرُۙ', 'wa laẓ-ẓulumātu wa lan-nụr'),
(3680, 35, 21, 'وَلَا الظِّلُّ وَلَا الْحَرُوْرُۚ', 'wa laẓ-ẓillu wa lal-ḥarụr'),
(3681, 35, 22, 'وَمَا يَسْتَوِى الْاَحْيَاۤءُ وَلَا الْاَمْوَاتُۗ اِنَّ اللّٰهَ يُسْمِعُ مَنْ يَّشَاۤءُ ۚوَمَآ اَنْتَ بِمُسْمِعٍ مَّنْ فِى الْقُبُوْرِ', 'wa mā yastawil-aḥyā`u wa lal-amwāt, innallāha yusmi\'u may yasyā`, wa mā anta bimusmi\'im man fil-qubụr'),
(3682, 35, 23, 'اِنْ اَنْتَ اِلَّا نَذِيْرٌ', 'in anta illā nażīr'),
(3683, 35, 24, 'اِنَّآ اَرْسَلْنٰكَ بِالْحَقِّ بَشِيْرًا وَّنَذِيْرًا ۗوَاِنْ مِّنْ اُمَّةٍ اِلَّا خَلَا فِيْهَا نَذِيْرٌ', 'innā arsalnāka bil-ḥaqqi basyīraw wa nażīrā, wa im min ummatin illā khalā fīhā nażīr'),
(3684, 35, 25, 'وَاِنْ يُّكَذِّبُوْكَ فَقَدْ كَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْ ۚجَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِ وَبِالزُّبُرِ وَبِالْكِتٰبِ الْمُنِيْرِ', 'wa iy yukażżibụka fa qad każżaballażīna ming qablihim, jā`at-hum rusuluhum bil-bayyināti wa biz-zuburi wa bil-kitābil-munīr'),
(3685, 35, 26, 'ثُمَّ اَخَذْتُ الَّذِيْنَ كَفَرُوْا فَكَيْفَ كَانَ نَكِيْرِ', 'ṡumma akhażtullażīna kafarụ fa kaifa kāna nakīr'),
(3686, 35, 27, 'اَلَمْ تَرَ اَنَّ اللّٰهَ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءًۚ فَاَخْرَجْنَا بِهٖ ثَمَرٰتٍ مُّخْتَلِفًا اَلْوَانُهَا ۗوَمِنَ الْجِبَالِ جُدَدٌ ۢبِيْضٌ وَّحُمْرٌ مُّخْتَلِفٌ اَلْوَانُهَا وَغَرَابِيْبُ سُوْدٌ', 'a lam tara annallāha anzala minas-samā`i mā`ā, fa akhrajnā bihī ṡamarātim mukhtalifan alwānuhā, wa minal-jibāli judadum bīḍuw wa ḥumrum mukhtalifun alwānuhā wa garābību sụd'),
(3687, 35, 28, 'وَمِنَ النَّاسِ وَالدَّوَاۤبِّ وَالْاَنْعَامِ مُخْتَلِفٌ اَلْوَانُهٗ كَذٰلِكَۗ اِنَّمَا يَخْشَى اللّٰهَ مِنْ عِبَادِهِ الْعُلَمٰۤؤُاۗ اِنَّ اللّٰهَ عَزِيْزٌ غَفُوْرٌ', 'wa minan-nāsi wad-dawābbi wal-an\'āmi mukhtalifun alwānuhụ każālik, innamā yakhsyallāha min \'ibādihil-\'ulamā`, innallāha \'azīzun gafụr'),
(3688, 35, 29, 'اِنَّ الَّذِيْنَ يَتْلُوْنَ كِتٰبَ اللّٰهِ وَاَقَامُوا الصَّلٰوةَ وَاَنْفَقُوْا مِمَّا رَزَقْنٰهُمْ سِرًّا وَّعَلَانِيَةً يَّرْجُوْنَ تِجَارَةً لَّنْ تَبُوْرَۙ', 'innallażīna yatlụna kitāballāhi wa aqāmuṣ-ṣalāta wa anfaqụ mimmā razaqnāhum sirraw wa \'alāniyatay yarjụna tijāratal lan tabụr'),
(3689, 35, 30, 'لِيُوَفِّيَهُمْ اُجُوْرَهُمْ وَيَزِيْدَهُمْ مِّنْ فَضْلِهٖۗ اِنَّهٗ غَفُوْرٌ شَكُوْرٌ', 'liyuwaffiyahum ujụrahum wa yazīdahum min faḍlih, innahụ gafụrun syakụr'),
(3690, 35, 31, 'وَالَّذِيْٓ اَوْحَيْنَآ اِلَيْكَ مِنَ الْكِتٰبِ هُوَ الْحَقُّ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِۗ اِنَّ اللّٰهَ بِعِبَادِهٖ لَخَبِيْرٌۢ بَصِيْرٌ', 'wallażī auḥainā ilaika minal-kitābi huwal-ḥaqqu muṣaddiqal limā baina yadaīh, innallāha bi\'ibādihī lakhabīrum baṣīr'),
(3691, 35, 32, 'ثُمَّ اَوْرَثْنَا الْكِتٰبَ الَّذِيْنَ اصْطَفَيْنَا مِنْ عِبَادِنَاۚ فَمِنْهُمْ ظَالِمٌ لِّنَفْسِهٖ ۚوَمِنْهُمْ مُّقْتَصِدٌ ۚوَمِنْهُمْ سَابِقٌۢ بِالْخَيْرٰتِ بِاِذْنِ اللّٰهِ ۗذٰلِكَ هُوَ الْفَضْلُ الْكَبِيْرُۗ', 'ṡumma auraṡnal-kitāballażīnaṣṭafainā min \'ibādinā, fa min-hum ẓālimul linafsih, wa min-hum muqtaṣid, wa min-hum sābiqum bil-khairāti bi`iżnillāh, żālika huwal-faḍlul-kabīr'),
(3692, 35, 33, 'جَنّٰتُ عَدْنٍ يَّدْخُلُوْنَهَا يُحَلَّوْنَ فِيْهَا مِنْ اَسَاوِرَ مِنْ ذَهَبٍ وَّلُؤْلُؤًا ۚوَلِبَاسُهُمْ فِيْهَا حَرِيْرٌ', 'jannātu \'adniy yadkhulụnahā yuḥallauna fīhā min asāwira min żahabiw wa lu`lu`ā, wa libāsuhum fīhā ḥarīr'),
(3693, 35, 34, 'وَقَالُوا الْحَمْدُ لِلّٰهِ الَّذِيْٓ اَذْهَبَ عَنَّا الْحَزَنَۗ اِنَّ رَبَّنَا لَغَفُوْرٌ شَكُوْرٌۙ', 'wa qālul-ḥamdu lillāhillażī aż-haba \'annal-ḥazan, inna rabbanā lagafụrun syakụr'),
(3694, 35, 35, ' ۨالَّذِيْٓ اَحَلَّنَا دَارَ الْمُقَامَةِ مِنْ فَضْلِهٖۚ لَا يَمَسُّنَا فِيْهَا نَصَبٌ وَّلَا يَمَسُّنَا فِيْهَا لُغُوْبٌ', 'allażī aḥallanā dāral-muqāmati min faḍlih, lā yamassunā fīhā naṣabuw wa lā yamassunā fīhā lugụb'),
(3695, 35, 36, 'وَالَّذِيْنَ كَفَرُوْا لَهُمْ نَارُ جَهَنَّمَۚ لَا يُقْضٰى عَلَيْهِمْ فَيَمُوْتُوْا وَلَا يُخَفَّفُ عَنْهُمْ مِّنْ عَذَابِهَاۗ كَذٰلِكَ نَجْزِيْ كُلَّ كَفُوْرٍ ۚ', 'wallażīna kafarụ lahum nāru jahannam, lā yuqḍā \'alaihim fa yamụtụ wa lā yukhaffafu \'an-hum min \'ażābihā, każālika najzī kulla kafụr'),
(3696, 35, 37, 'وَهُمْ يَصْطَرِخُوْنَ فِيْهَاۚ رَبَّنَآ اَخْرِجْنَا نَعْمَلْ صَالِحًا غَيْرَ الَّذِيْ كُنَّا نَعْمَلُۗ اَوَلَمْ نُعَمِّرْكُمْ مَّا يَتَذَكَّرُ فِيْهِ مَنْ تَذَكَّرَ وَجَاۤءَكُمُ النَّذِيْرُۗ فَذُوْقُوْا فَمَا لِلظّٰلِمِيْنَ مِنْ نَّصِيْرٍ', 'wa hum yaṣṭarikhụna fīhā, rabbanā akhrijnā na\'mal ṣāliḥan gairallażī kunnā na\'mal, a wa lam nu\'ammirkum mā yatażakkaru fīhi man tażakkara wa jā`akumun-nażīr, fa żụqụ fa mā liẓ-ẓālimīna min naṣīr'),
(3697, 35, 38, 'اِنَّ اللّٰهَ عَالِمُ غَيْبِ السَّمٰوٰتِ وَالْاَرْضِۗ اِنَّهٗ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ', 'innallāha \'ālimu gaibis-samāwāti wal-arḍ, innahụ \'alīmum biżātiṣ-ṣudụr'),
(3698, 35, 39, 'هُوَ الَّذِيْ جَعَلَكُمْ خَلٰۤىِٕفَ فِى الْاَرْضِۗ فَمَنْ كَفَرَ فَعَلَيْهِ كُفْرُهٗۗ وَلَا يَزِيْدُ الْكٰفِرِيْنَ كُفْرُهُمْ عِنْدَ رَبِّهِمْ اِلَّا مَقْتًا ۚوَلَا يَزِيْدُ الْكٰفِرِيْنَ كُفْرُهُمْ اِلَّا خَسَارًا', 'huwallażī ja\'alakum khalā`ifa fil-arḍ, fa mang kafara fa \'alaihi kufruh, wa lā yazīdul-kāfirīna kufruhum \'inda rabbihim illā maqtā, wa lā yazīdul-kāfirīna kufruhum illā khasārā'),
(3699, 35, 40, 'قُلْ اَرَاَيْتُمْ شُرَكَاۤءَكُمُ الَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ ۗاَرُوْنِيْ مَاذَا خَلَقُوْا مِنَ الْاَرْضِ اَمْ لَهُمْ شِرْكٌ فِى السَّمٰوٰتِۚ اَمْ اٰتَيْنٰهُمْ كِتٰبًا فَهُمْ عَلٰى بَيِّنَتٍ مِّنْهُۚ بَلْ اِنْ يَّعِدُ الظّٰلِمُوْنَ بَعْضُهُمْ بَعْضًا اِلَّا غُرُوْرًا', 'qul a ra`aitum syurakā`akumullażīna tad\'ụna min dụnillāh, arụnī māżā khalaqụ minal-arḍi am lahum syirkun fis-samāwāt, am ātaināhum kitāban fa hum \'alā bayyinatim min-h, bal iy ya\'iduẓ-ẓālimụna ba\'ḍuhum ba\'ḍan illā gurụrā'),
(3700, 35, 41, '۞ اِنَّ اللّٰهَ يُمْسِكُ السَّمٰوٰتِ وَالْاَرْضَ اَنْ تَزُوْلَا ەۚ وَلَىِٕنْ زَالَتَآ اِنْ اَمْسَكَهُمَا مِنْ اَحَدٍ مِّنْۢ بَعْدِهٖ ۗاِنَّهٗ كَانَ حَلِيْمًا غَفُوْرًا', 'innallāha yumsikus-samāwāti wal-arḍa an tazụlā, wa la`in zālatā in amsakahumā min aḥadim mim ba\'dih, innahụ kāna ḥalīman gafụrā'),
(3701, 35, 42, 'وَاَقْسَمُوْا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْ لَىِٕنْ جَاۤءَهُمْ نَذِيْرٌ لَّيَكُوْنُنَّ اَهْدٰى مِنْ اِحْدَى الْاُمَمِۚ فَلَمَّا جَاۤءَهُمْ نَذِيْرٌ مَّا زَادَهُمْ اِلَّا نُفُوْرًاۙ', 'wa aqsamụ billāhi jahda aimānihim la`in jā`ahum nażīrul layakụnunna ahdā min iḥdal-umam, fa lammā jā`ahum nażīrum mā zādahum illā nufụrā'),
(3702, 35, 43, ' ۨاسْتِكْبَارًا فِى الْاَرْضِ وَمَكْرَ السَّيِّئِۗ وَلَا يَحِيْقُ الْمَكْرُ السَّيِّئُ اِلَّا بِاَهْلِهٖ ۗفَهَلْ يَنْظُرُوْنَ اِلَّا سُنَّتَ الْاَوَّلِيْنَۚ فَلَنْ تَجِدَ لِسُنَّتِ اللّٰهِ تَبْدِيْلًا ەۚ وَلَنْ تَجِدَ لِسُنَّتِ اللّٰهِ تَحْوِيْلًا', 'istikbāran fil-arḍi wa makras-sayyi`, wa lā yaḥīqul-makrus-sayyi`u illā bi`ahlih, fa hal yanẓurụna illā sunnatal-awwalīn, fa lan tajida lisunnatillāhi tabdīlā, wa lan tajida lisunnatillāhi taḥwīlā'),
(3703, 35, 44, 'اَوَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَيَنْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلِهِمْ وَكَانُوْٓا اَشَدَّ مِنْهُمْ قُوَّةً ۗوَمَا كَانَ اللّٰهُ لِيُعْجِزَهٗ مِنْ شَيْءٍ فِى السَّمٰوٰتِ وَلَا فِى الْاَرْضِۗ اِنَّهٗ كَانَ عَلِيْمًا قَدِيْرًا', 'a wa lam yasīrụ fil-arḍi fa yanẓurụ kaifa kāna \'āqibatullażīna ming qablihim wa kānū asyadda min-hum quwwah, wa mā kānallāhu liyu\'jizahụ min syai`in fis-samāwāti wa lā fil-arḍ, innahụ kāna \'alīmang qadīrā'),
(3704, 35, 45, 'وَلَوْ يُؤَاخِذُ اللّٰهُ النَّاسَ بِمَا كَسَبُوْا مَا تَرَكَ عَلٰى ظَهْرِهَا مِنْ دَاۤبَّةٍ وَّلٰكِنْ يُّؤَخِّرُهُمْ اِلٰٓى اَجَلٍ مُّسَمًّىۚ فَاِذَا جَاۤءَ اَجَلُهُمْ فَاِنَّ اللّٰهَ كَانَ بِعِبَادِهٖ بَصِيْرًا', 'walau yu`ākhiżullāhun-nāsa bimā kasabụ mā taraka \'alā ẓahrihā min dābbatiw wa lākiy yu`akhkhiruhum ilā ajalim musammā, fa iżā jā`a ajaluhum fa innallāha kāna bi\'ibādihī baṣīrā'),
(3705, 36, 1, 'يٰسۤ ۚ', 'yā sīn'),
(3706, 36, 2, 'وَالْقُرْاٰنِ الْحَكِيْمِۙ', 'wal-qur`ānil-ḥakīm'),
(3707, 36, 3, 'اِنَّكَ لَمِنَ الْمُرْسَلِيْنَۙ', 'innaka laminal-mursalīn'),
(3708, 36, 4, 'عَلٰى صِرَاطٍ مُّسْتَقِيْمٍۗ', '\'alā ṣirāṭim mustaqīm'),
(3709, 36, 5, 'تَنْزِيْلَ الْعَزِيْزِ الرَّحِيْمِۙ', 'tanzīlal-\'azīzir-raḥīm'),
(3710, 36, 6, 'لِتُنْذِرَ قَوْمًا مَّآ اُنْذِرَ اٰبَاۤؤُهُمْ فَهُمْ غٰفِلُوْنَ', 'litunżira qaumam mā unżira ābā`uhum fa hum gāfilụn'),
(3711, 36, 7, 'لَقَدْ حَقَّ الْقَوْلُ عَلٰٓى اَكْثَرِهِمْ فَهُمْ لَا يُؤْمِنُوْنَ', 'laqad ḥaqqal-qaulu \'alā akṡarihim fa hum lā yu`minụn'),
(3712, 36, 8, 'اِنَّا جَعَلْنَا فِيْٓ اَعْنَاقِهِمْ اَغْلٰلًا فَهِيَ اِلَى الْاَذْقَانِ فَهُمْ مُّقْمَحُوْنَ', 'innā ja\'alnā fī a\'nāqihim aglālan fa hiya ilal-ażqāni fa hum muqmaḥụn'),
(3713, 36, 9, 'وَجَعَلْنَا مِنْۢ بَيْنِ اَيْدِيْهِمْ سَدًّا وَّمِنْ خَلْفِهِمْ سَدًّا فَاَغْشَيْنٰهُمْ فَهُمْ لَا يُبْصِرُوْنَ', 'wa ja\'alnā mim baini aidīhim saddaw wa min khalfihim saddan fa agsyaināhum fa hum lā yubṣirụn'),
(3714, 36, 10, 'وَسَوَاۤءٌ عَلَيْهِمْ ءَاَنْذَرْتَهُمْ اَمْ لَمْ تُنْذِرْهُمْ لَا يُؤْمِنُوْنَ', 'wa sawā`un \'alaihim a anżartahum am lam tunżir-hum lā yu`minụn'),
(3715, 36, 11, 'اِنَّمَا تُنْذِرُ مَنِ اتَّبَعَ الذِّكْرَ وَخَشِيَ الرَّحْمٰنَ بِالْغَيْبِۚ فَبَشِّرْهُ بِمَغْفِرَةٍ وَّاَجْرٍ كَرِيْمٍ', 'innamā tunżiru manittaba\'aż-żikra wa khasyiyar-raḥmāna bil-gaīb, fa basysyir-hu bimagfiratiw wa ajring karīm'),
(3716, 36, 12, 'اِنَّا نَحْنُ نُحْيِ الْمَوْتٰى وَنَكْتُبُ مَا قَدَّمُوْا وَاٰثَارَهُمْۗ وَكُلَّ شَيْءٍ اَحْصَيْنٰهُ فِيْٓ اِمَامٍ مُّبِيْنٍ', 'innā naḥnu nuḥyil-mautā wa naktubu mā qaddamụ wa āṡārahum, wa kulla syai`in aḥṣaināhu fī imāmim mubīn'),
(3717, 36, 13, 'وَاضْرِبْ لَهُمْ مَّثَلًا اَصْحٰبَ الْقَرْيَةِۘ اِذْ جَاۤءَهَا الْمُرْسَلُوْنَۚ', 'waḍrib lahum maṡalan aṣ-ḥābal-qaryah, iż jā`ahal-mursalụn'),
(3718, 36, 14, 'اِذْ اَرْسَلْنَآ اِلَيْهِمُ اثْنَيْنِ فَكَذَّبُوْهُمَا فَعَزَّزْنَا بِثَالِثٍ فَقَالُوْٓا اِنَّآ اِلَيْكُمْ مُّرْسَلُوْنَ', 'iż arsalnā ilaihimuṡnaini fa każżabụhumā fa \'azzaznā biṡāliṡin fa qālū innā ilaikum mursalụn'),
(3719, 36, 15, 'قَالُوْا مَآ اَنْتُمْ اِلَّا بَشَرٌ مِّثْلُنَاۙ وَمَآ اَنْزَلَ الرَّحْمٰنُ مِنْ شَيْءٍۙ اِنْ اَنْتُمْ اِلَّا تَكْذِبُوْنَ', 'qālụ mā antum illā basyarum miṡlunā wa mā anzalar-raḥmānu min syai`in in antum illā takżibụn'),
(3720, 36, 16, 'قَالُوْا رَبُّنَا يَعْلَمُ اِنَّآ اِلَيْكُمْ لَمُرْسَلُوْنَ', 'qālụ rabbunā ya\'lamu innā ilaikum lamursalụn'),
(3721, 36, 17, 'وَمَا عَلَيْنَآ اِلَّا الْبَلٰغُ الْمُبِيْنُ', 'wa mā \'alainā illal-balāgul-mubīn'),
(3722, 36, 18, 'قَالُوْٓا اِنَّا تَطَيَّرْنَا بِكُمْۚ لَىِٕنْ لَّمْ تَنْتَهُوْا لَنَرْجُمَنَّكُمْ وَلَيَمَسَّنَّكُمْ مِّنَّا عَذَابٌ اَلِيْمٌ', 'qālū innā taṭayyarnā bikum, la`il lam tantahụ lanarjumannakum wa layamassannakum minnā \'ażābun alīm'),
(3723, 36, 19, 'قَالُوْا طَاۤىِٕرُكُمْ مَّعَكُمْۗ اَىِٕنْ ذُكِّرْتُمْۗ بَلْ اَنْتُمْ قَوْمٌ مُّسْرِفُوْنَ', 'qālụ ṭā`irukum ma\'akum, a in żukkirtum, bal antum qaumum musrifụn'),
(3724, 36, 20, 'وَجَاۤءَ مِنْ اَقْصَا الْمَدِيْنَةِ رَجُلٌ يَّسْعٰى قَالَ يٰقَوْمِ اتَّبِعُوا الْمُرْسَلِيْنَۙ', 'wa jā`a min aqṣal-madīnati rajuluy yas\'ā qāla yā qaumittabi\'ul-mursalīn'),
(3725, 36, 21, 'اتَّبِعُوْا مَنْ لَّا يَسْـَٔلُكُمْ اَجْرًا وَّهُمْ مُّهْتَدُوْنَ ۔', 'ittabi\'ụ mal lā yas`alukum ajraw wa hum muhtadụn'),
(3726, 36, 22, 'وَمَا لِيَ لَآ اَعْبُدُ الَّذِيْ فَطَرَنِيْ وَاِلَيْهِ تُرْجَعُوْنَ', 'wa mā liya lā a\'budullażī faṭaranī wa ilaihi turja\'ụn'),
(3727, 36, 23, 'ءَاَتَّخِذُ مِنْ دُوْنِهٖٓ اٰلِهَةً اِنْ يُّرِدْنِ الرَّحْمٰنُ بِضُرٍّ لَّا تُغْنِ عَنِّيْ شَفَاعَتُهُمْ شَيْـًٔا وَّلَا يُنْقِذُوْنِۚ', 'a attakhiżu min dụnihī ālihatan iy yuridnir-raḥmānu biḍurril lā tugni \'annī syafā\'atuhum syai`aw wa lā yungqiżụn'),
(3728, 36, 24, 'اِنِّيْٓ اِذًا لَّفِيْ ضَلٰلٍ مُّبِيْنٍ', 'innī iżal lafī ḍalālim mubīn'),
(3729, 36, 25, 'اِنِّيْٓ اٰمَنْتُ بِرَبِّكُمْ فَاسْمَعُوْنِۗ', 'innī āmantu birabbikum fasma\'ụn'),
(3730, 36, 26, 'قِيْلَ ادْخُلِ الْجَنَّةَ ۗقَالَ يٰلَيْتَ قَوْمِيْ يَعْلَمُوْنَۙ', 'qīladkhulil-jannah, qāla yā laita qaumī ya\'lamụn'),
(3731, 36, 27, 'بِمَا غَفَرَ لِيْ رَبِّيْ وَجَعَلَنِيْ مِنَ الْمُكْرَمِيْنَ', 'bimā gafara lī rabbī wa ja\'alanī minal-mukramīn'),
(3732, 36, 28, '۞ وَمَآ اَنْزَلْنَا عَلٰى قَوْمِهٖ مِنْۢ بَعْدِهٖ مِنْ جُنْدٍ مِّنَ السَّمَاۤءِ وَمَا كُنَّا مُنْزِلِيْنَ', 'wa mā anzalnā \'alā qaumihī mim ba\'dihī min jundim minas-samā`i wa mā kunnā munzilīn'),
(3733, 36, 29, 'اِنْ كَانَتْ اِلَّا صَيْحَةً وَّاحِدَةً فَاِذَا هُمْ خَامِدُوْنَ', 'ing kānat illā ṣaiḥataw wāḥidatan fa iżā hum khāmidụn'),
(3734, 36, 30, 'يٰحَسْرَةً عَلَى الْعِبَادِۚ مَا يَأْتِيْهِمْ مِّنْ رَّسُوْلٍ اِلَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ', 'yā ḥasratan \'alal-\'ibād, mā ya`tīhim mir rasụlin illā kānụ bihī yastahzi`ụn'),
(3735, 36, 31, 'اَلَمْ يَرَوْا كَمْ اَهْلَكْنَا قَبْلَهُمْ مِّنَ الْقُرُوْنِ اَنَّهُمْ اِلَيْهِمْ لَا يَرْجِعُوْنَ', 'a lam yarau kam ahlaknā qablahum minal-qurụni annahum ilaihim lā yarji\'ụn'),
(3736, 36, 32, 'وَاِنْ كُلٌّ لَّمَّا جَمِيْعٌ لَّدَيْنَا مُحْضَرُوْنَ', 'wa ing kullul lammā jamī\'ul ladainā muḥḍarụn'),
(3737, 36, 33, 'وَاٰيَةٌ لَّهُمُ الْاَرْضُ الْمَيْتَةُ ۖاَحْيَيْنٰهَا وَاَخْرَجْنَا مِنْهَا حَبًّا فَمِنْهُ يَأْكُلُوْنَ', 'wa āyatul lahumul-arḍul-maitatu aḥyaināhā wa akhrajnā min-hā ḥabban fa min-hu ya`kulụn'),
(3738, 36, 34, 'وَجَعَلْنَا فِيْهَا جَنّٰتٍ مِّنْ نَّخِيْلٍ وَّاَعْنَابٍ وَّفَجَّرْنَا فِيْهَا مِنَ الْعُيُوْنِۙ', 'wa ja\'alnā fīhā jannātim min nakhīliw wa a\'nābiw wa fajjarnā fīhā minal-\'uyụn'),
(3739, 36, 35, 'لِيَأْكُلُوْا مِنْ ثَمَرِهٖۙ وَمَا عَمِلَتْهُ اَيْدِيْهِمْ ۗ اَفَلَا يَشْكُرُوْنَ', 'liya`kulụ min ṡamarihī wa mā \'amilat-hu aidīhim, a fa lā yasykurụn'),
(3740, 36, 36, 'سُبْحٰنَ الَّذِيْ خَلَقَ الْاَزْوَاجَ كُلَّهَا مِمَّا تُنْۢبِتُ الْاَرْضُ وَمِنْ اَنْفُسِهِمْ وَمِمَّا لَا يَعْلَمُوْنَ', 'sub-ḥānallażī khalaqal-azwāja kullahā mimmā tumbitul-arḍu wa min anfusihim wa mimmā lā ya\'lamụn'),
(3741, 36, 37, 'وَاٰيَةٌ لَّهُمُ الَّيْلُ ۖنَسْلَخُ مِنْهُ النَّهَارَ فَاِذَا هُمْ مُّظْلِمُوْنَۙ', 'wa āyatul lahumul-lailu naslakhu min-hun-nahāra fa iżā hum muẓlimụn'),
(3742, 36, 38, 'وَالشَّمْسُ تَجْرِيْ لِمُسْتَقَرٍّ لَّهَا ۗذٰلِكَ تَقْدِيْرُ الْعَزِيْزِ الْعَلِيْمِۗ', 'wasy-syamsu tajrī limustaqarril lahā, żālika taqdīrul-\'azīzil-\'alīm'),
(3743, 36, 39, 'وَالْقَمَرَ قَدَّرْنٰهُ مَنَازِلَ حَتّٰى عَادَ كَالْعُرْجُوْنِ الْقَدِيْمِ', 'wal-qamara qaddarnāhu manāzila ḥattā \'āda kal-\'urjụnil-qadīm'),
(3744, 36, 40, 'لَا الشَّمْسُ يَنْۢبَغِيْ لَهَآ اَنْ تُدْرِكَ الْقَمَرَ وَلَا الَّيْلُ سَابِقُ النَّهَارِ ۗوَكُلٌّ فِيْ فَلَكٍ يَّسْبَحُوْنَ', 'lasy-syamsu yambagī lahā an tudrikal-qamara wa lal-lailu sābiqun-nahār, wa kullun fī falakiy yasbaḥụn'),
(3745, 36, 41, 'وَاٰيَةٌ لَّهُمْ اَنَّا حَمَلْنَا ذُرِّيَّتَهُمْ فِى الْفُلْكِ الْمَشْحُوْنِۙ', 'wa āyatul lahum annā ḥamalnā żurriyyatahum fil-fulkil-masy-ḥụn'),
(3746, 36, 42, 'وَخَلَقْنَا لَهُمْ مِّنْ مِّثْلِهٖ مَا يَرْكَبُوْنَ', 'wa khalaqnā lahum mim miṡlihī mā yarkabụn'),
(3747, 36, 43, 'وَاِنْ نَّشَأْ نُغْرِقْهُمْ فَلَا صَرِيْخَ لَهُمْ وَلَاهُمْ يُنْقَذُوْنَۙ', 'wa in nasya` nugriq-hum fa lā ṣarīkha lahum wa lā hum yungqażụn'),
(3748, 36, 44, 'اِلَّا رَحْمَةً مِّنَّا وَمَتَاعًا اِلٰى حِيْنٍ', 'illā raḥmatam minnā wa matā\'an ilā ḥīn'),
(3749, 36, 45, 'وَاِذَا قِيْلَ لَهُمُ اتَّقُوْا مَا بَيْنَ اَيْدِيْكُمْ وَمَا خَلْفَكُمْ لَعَلَّكُمْ تُرْحَمُوْنَ', 'wa iżā qīla lahumuttaqụ mā baina aidīkum wa mā khalfakum la\'allakum tur-ḥamụn'),
(3750, 36, 46, 'وَمَا تَأْتِيْهِمْ مِّنْ اٰيَةٍ مِّنْ اٰيٰتِ رَبِّهِمْ اِلَّا كَانُوْا عَنْهَا مُعْرِضِيْنَ', 'wa mā ta`tīhim min āyatim min āyāti rabbihim illā kānụ \'an-hā mu\'riḍīn'),
(3751, 36, 47, 'وَاِذَا قِيْلَ لَهُمْ اَنْفِقُوْا مِمَّا رَزَقَكُمُ اللّٰهُ ۙقَالَ الَّذِيْنَ كَفَرُوْا لِلَّذِيْنَ اٰمَنُوْٓا اَنُطْعِمُ مَنْ لَّوْ يَشَاۤءُ اللّٰهُ اَطْعَمَهٗٓ ۖاِنْ اَنْتُمْ اِلَّا فِيْ ضَلٰلٍ مُّبِيْنٍ', 'wa iżā qīla lahum anfiqụ mimmā razaqakumullāhu qālallażīna kafarụ lillażīna āmanū a nuṭ\'imu mal lau yasyā`ullāhu aṭ\'amahū in antum illā fī ḍalālim mubīn'),
(3752, 36, 48, 'وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'wa yaqụlụna matā hāżal-wa\'du ing kuntum ṣādiqīn'),
(3753, 36, 49, 'مَا يَنْظُرُوْنَ اِلَّا صَيْحَةً وَّاحِدَةً تَأْخُذُهُمْ وَهُمْ يَخِصِّمُوْنَ', 'mā yanẓurụna illā ṣaiḥataw wāḥidatan ta`khużuhum wa hum yakhiṣṣimụn'),
(3754, 36, 50, 'فَلَا يَسْتَطِيْعُوْنَ تَوْصِيَةً وَّلَآ اِلٰٓى اَهْلِهِمْ يَرْجِعُوْنَ', 'fa lā yastaṭī\'ụna tauṣiyataw wa lā ilā ahlihim yarji\'ụn'),
(3755, 36, 51, 'وَنُفِخَ فِى الصُّوْرِ فَاِذَا هُمْ مِّنَ الْاَجْدَاثِ اِلٰى رَبِّهِمْ يَنْسِلُوْنَ', 'wa nufikha fiṣ-ṣụri fa iżā hum minal-ajdāṡi ilā rabbihim yansilụn'),
(3756, 36, 52, 'قَالُوْا يٰوَيْلَنَا مَنْۢ بَعَثَنَا مِنْ مَّرْقَدِنَا ۜهٰذَا مَا وَعَدَ الرَّحْمٰنُ وَصَدَقَ الْمُرْسَلُوْنَ', 'qālụ yā wailanā mam ba\'aṡanā mim marqadinā hāżā mā wa\'adar-raḥmānu wa ṣadaqal-mursalụn'),
(3757, 36, 53, 'اِنْ كَانَتْ اِلَّا صَيْحَةً وَّاحِدَةً فَاِذَا هُمْ جَمِيْعٌ لَّدَيْنَا مُحْضَرُوْنَ', 'ing kānat illā ṣaiḥataw wāḥidatan fa iżā hum jamī\'ul ladainā muḥḍarụn'),
(3758, 36, 54, 'فَالْيَوْمَ لَا تُظْلَمُ نَفْسٌ شَيْـًٔا وَّلَا تُجْزَوْنَ اِلَّا مَا كُنْتُمْ تَعْمَلُوْنَ', 'fal-yauma lā tuẓlamu nafsun syai`aw wa lā tujzauna illā mā kuntum ta\'malụn'),
(3759, 36, 55, 'اِنَّ اَصْحٰبَ الْجَنَّةِ الْيَوْمَ فِيْ شُغُلٍ فٰكِهُوْنَ ۚ', 'inna aṣ-ḥābal-jannatil-yauma fī syugulin fākihụn'),
(3760, 36, 56, 'هُمْ وَاَزْوَاجُهُمْ فِيْ ظِلٰلٍ عَلَى الْاَرَاۤىِٕكِ مُتَّكِـُٔوْنَ ۚ', 'hum wa azwājuhum fī ẓilālin \'alal-arā`iki muttaki`ụn'),
(3761, 36, 57, 'لَهُمْ فِيْهَا فَاكِهَةٌ وَّلَهُمْ مَّا يَدَّعُوْنَ ۚ', 'lahum fīhā fākihatuw wa lahum mā yadda\'ụn'),
(3762, 36, 58, 'سَلٰمٌۗ قَوْلًا مِّنْ رَّبٍّ رَّحِيْمٍ', 'salām, qaulam mir rabbir raḥīm'),
(3763, 36, 59, 'وَامْتَازُوا الْيَوْمَ اَيُّهَا الْمُجْرِمُوْنَ', 'wamtāzul-yauma ayyuhal-mujrimụn'),
(3764, 36, 60, 'اَلَمْ اَعْهَدْ اِلَيْكُمْ يٰبَنِيْٓ اٰدَمَ اَنْ لَّا تَعْبُدُوا الشَّيْطٰنَۚ اِنَّهٗ لَكُمْ عَدُوٌّ مُّبِيْنٌ', 'a lam a\'had ilaikum yā banī ādama al lā ta\'budusy-syaiṭān, innahụ lakum \'aduwwum mubīn'),
(3765, 36, 61, 'وَاَنِ اعْبُدُوْنِيْ ۗهٰذَا صِرَاطٌ مُّسْتَقِيْمٌ', 'wa ani\'budụnī, hāżā ṣirāṭum mustaqīm'),
(3766, 36, 62, 'وَلَقَدْ اَضَلَّ مِنْكُمْ جِبِلًّا كَثِيْرًا ۗاَفَلَمْ تَكُوْنُوْا تَعْقِلُوْنَ', 'wa laqad aḍalla mingkum jibillang kaṡīrā, a fa lam takụnụ ta\'qilụn'),
(3767, 36, 63, 'هٰذِهٖ جَهَنَّمُ الَّتِيْ كُنْتُمْ تُوْعَدُوْنَ', 'hāżihī jahannamullatī kuntum tụ\'adụn'),
(3768, 36, 64, 'اِصْلَوْهَا الْيَوْمَ بِمَا كُنْتُمْ تَكْفُرُوْنَ', 'iṣlauhal-yauma bimā kuntum takfurụn'),
(3769, 36, 65, 'اَلْيَوْمَ نَخْتِمُ عَلٰٓى اَفْوَاهِهِمْ وَتُكَلِّمُنَآ اَيْدِيْهِمْ وَتَشْهَدُ اَرْجُلُهُمْ بِمَا كَانُوْا يَكْسِبُوْنَ', 'al-yauma nakhtimu \'alā afwāhihim wa tukallimunā aidīhim wa tasy-hadu arjuluhum bimā kānụ yaksibụn'),
(3770, 36, 66, 'وَلَوْ نَشَاۤءُ لَطَمَسْنَا عَلٰٓى اَعْيُنِهِمْ فَاسْتَبَقُوا الصِّرَاطَ فَاَنّٰى يُبْصِرُوْنَ', 'walau nasyā`u laṭamasnā \'alā a\'yunihim fastabaquṣ-ṣirāṭa fa annā yubṣirụn'),
(3771, 36, 67, 'وَلَوْ نَشَاۤءُ لَمَسَخْنٰهُمْ عَلٰى مَكَانَتِهِمْ فَمَا اسْتَطَاعُوْا مُضِيًّا وَّلَا يَرْجِعُوْنَ', 'walau nasyā`u lamasakhnāhum \'alā makānatihim famastaṭā\'ụ muḍiyyaw wa lā yarji\'ụn'),
(3772, 36, 68, 'وَمَنْ نُّعَمِّرْهُ نُنَكِّسْهُ فِى الْخَلْقِۗ اَفَلَا يَعْقِلُوْنَ', 'wa man nu\'ammir-hu nunakkis-hu fil-khalq, a fa lā ya\'qilụn'),
(3773, 36, 69, 'وَمَا عَلَّمْنٰهُ الشِّعْرَ وَمَا يَنْۢبَغِيْ لَهٗ ۗاِنْ هُوَ اِلَّا ذِكْرٌ وَّقُرْاٰنٌ مُّبِيْنٌ ۙ', 'wa mā \'allamnāhusy-syi\'ra wa mā yambagī lah, in huwa illā żikruw wa qur`ānum mubīn'),
(3774, 36, 70, 'لِّيُنْذِرَ مَنْ كَانَ حَيًّا وَّيَحِقَّ الْقَوْلُ عَلَى الْكٰفِرِيْنَ', 'liyunżira mang kāna ḥayyaw wa yaḥiqqal-qaulu \'alal-kāfirīn'),
(3775, 36, 71, 'اَوَلَمْ يَرَوْا اَنَّا خَلَقْنَا لَهُمْ مِّمَّا عَمِلَتْ اَيْدِيْنَآ اَنْعَامًا فَهُمْ لَهَا مَالِكُوْنَ', 'a wa lam yarau annā khalaqnā lahum mimmā \'amilat aidīnā an\'āman fa hum lahā mālikụn'),
(3776, 36, 72, 'وَذَلَّلْنٰهَا لَهُمْ فَمِنْهَا رَكُوْبُهُمْ وَمِنْهَا يَأْكُلُوْنَ', 'wa żallalnāhā lahum fa min-hā rakụbuhum wa min-hā ya`kulụn'),
(3777, 36, 73, 'وَلَهُمْ فِيْهَا مَنَافِعُ وَمَشَارِبُۗ اَفَلَا يَشْكُرُوْنَ', 'wa lahum fīhā manāfi\'u wa masyārib, a fa lā yasykurụn'),
(3778, 36, 74, 'وَاتَّخَذُوْا مِنْ دُوْنِ اللّٰهِ اٰلِهَةً لَّعَلَّهُمْ يُنْصَرُوْنَ ۗ', 'wattakhażụ min dụnillāhi ālihatal la\'allahum yunṣarụn'),
(3779, 36, 75, 'لَا يَسْتَطِيْعُوْنَ نَصْرَهُمْۙ وَهُمْ لَهُمْ جُنْدٌ مُّحْضَرُوْنَ', 'lā yastaṭī\'ụna naṣrahum wa hum lahum jundum muḥḍarụn'),
(3780, 36, 76, 'فَلَا يَحْزُنْكَ قَوْلُهُمْ ۘاِنَّا نَعْلَمُ مَا يُسِرُّوْنَ وَمَا يُعْلِنُوْنَ', 'fa lā yaḥzungka qauluhum, innā na\'lamu mā yusirrụna wa mā yu\'linụn'),
(3781, 36, 77, 'اَوَلَمْ يَرَ الْاِنْسَانُ اَنَّا خَلَقْنٰهُ مِنْ نُّطْفَةٍ فَاِذَا هُوَ خَصِيْمٌ مُّبِيْنٌ', 'a wa lam yaral-insānu annā khalaqnāhu min nuṭfatin fa iżā huwa khaṣīmum mubīn'),
(3782, 36, 78, 'وَضَرَبَ لَنَا مَثَلًا وَّنَسِيَ خَلْقَهٗۗ قَالَ مَنْ يُّحْيِ الْعِظَامَ وَهِيَ رَمِيْمٌ', 'wa ḍaraba lanā maṡalaw wa nasiya khalqah, qāla may yuḥyil-\'iẓāma wa hiya ramīm'),
(3783, 36, 79, 'قُلْ يُحْيِيْهَا الَّذِيْٓ اَنْشَاَهَآ اَوَّلَ مَرَّةٍ ۗوَهُوَ بِكُلِّ خَلْقٍ عَلِيْمٌ ۙ', 'qul yuḥyīhallażī ansya`ahā awwala marrah, wa huwa bikulli khalqin \'alīm'),
(3784, 36, 80, ' ِۨالَّذِيْ جَعَلَ لَكُمْ مِّنَ الشَّجَرِ الْاَخْضَرِ نَارًاۙ فَاِذَآ اَنْتُمْ مِّنْهُ تُوْقِدُوْنَ', 'allażī ja\'ala lakum minasy-syajaril-akhḍari nāran fa iżā antum min-hu tụqidụn'),
(3785, 36, 81, 'اَوَلَيْسَ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ بِقٰدِرٍ عَلٰٓى اَنْ يَّخْلُقَ مِثْلَهُمْ ۗبَلٰى وَهُوَ الْخَلّٰقُ الْعَلِيْمُ', 'a wa laisallażī khalaqas-samāwāti wal-arḍa biqādirin \'alā ay yakhluqa miṡlahum, balā wa huwal-khallāqul-\'alīm'),
(3786, 36, 82, 'اِنَّمَآ اَمْرُهٗٓ اِذَآ اَرَادَ شَيْـًٔاۖ اَنْ يَّقُوْلَ لَهٗ كُنْ فَيَكُوْنُ', 'innamā amruhū iżā arāda syai`an ay yaqụla lahụ kun fa yakụn'),
(3787, 36, 83, 'فَسُبْحٰنَ الَّذِيْ بِيَدِهٖ مَلَكُوْتُ كُلِّ شَيْءٍ وَّاِلَيْهِ تُرْجَعُوْنَ', 'fa sub-ḥānallażī biyadihī malakụtu kulli syai`iw wa ilaihi turja\'ụn'),
(3788, 37, 1, 'وَالصّٰۤفّٰتِ صَفًّاۙ', 'waṣ-ṣāffāti ṣaffā'),
(3789, 37, 2, 'فَالزّٰجِرٰتِ زَجْرًاۙ', 'faz-zājirāti zajrā'),
(3790, 37, 3, 'فَالتّٰلِيٰتِ ذِكْرًاۙ', 'fat-tāliyāti żikrā'),
(3791, 37, 4, 'اِنَّ اِلٰهَكُمْ لَوَاحِدٌۗ', 'inna ilāhakum lawāḥid'),
(3792, 37, 5, 'رَبُّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا وَرَبُّ الْمَشَارِقِۗ', 'rabbus-samāwāti wal-arḍi wa mā bainahumā wa rabbul-masyāriq'),
(3793, 37, 6, 'اِنَّا زَيَّنَّا السَّمَاۤءَ الدُّنْيَا بِزِيْنَةِ ِۨالْكَوَاكِبِۙ', 'innā zayyannas-samā`ad-dun-yā bizīnatinil-kawākib'),
(3794, 37, 7, 'وَحِفْظًا مِّنْ كُلِّ شَيْطٰنٍ مَّارِدٍۚ', 'wa ḥifẓam ming kulli syaiṭānim mārid'),
(3795, 37, 8, 'لَا يَسَّمَّعُوْنَ اِلَى الْمَلَاِ الْاَعْلٰى وَيُقْذَفُوْنَ مِنْ كُلِّ جَانِبٍۖ', 'lā yassamma\'ụna ilal-mala`il-a\'lā wa yuqżafụna ming kulli jānib'),
(3796, 37, 9, 'دُحُوْرًا وَّلَهُمْ عَذَابٌ وَّاصِبٌ', 'duḥụraw wa lahum \'ażābuw wāṣib'),
(3797, 37, 10, 'اِلَّا مَنْ خَطِفَ الْخَطْفَةَ فَاَتْبَعَهٗ شِهَابٌ ثَاقِبٌ', 'illā man khaṭifal-khaṭfata fa atba\'ahụ syihābun ṡāqib'),
(3798, 37, 11, 'فَاسْتَفْتِهِمْ اَهُمْ اَشَدُّ خَلْقًا اَمْ مَّنْ خَلَقْنَا ۗاِنَّا خَلَقْنٰهُمْ مِّنْ طِيْنٍ لَّازِبٍ', 'fastaftihim a hum asyaddu khalqan am man khalaqnā, innā khalaqnāhum min ṭīnil lāzib'),
(3799, 37, 12, 'بَلْ عَجِبْتَ وَيَسْخَرُوْنَ ۖ', 'bal \'ajibta wa yaskharụn'),
(3800, 37, 13, 'وَاِذَا ذُكِّرُوْا لَا يَذْكُرُوْنَ ۖ', 'wa iżā żukkirụ lā yażkurụn'),
(3801, 37, 14, 'وَاِذَا رَاَوْا اٰيَةً يَّسْتَسْخِرُوْنَۖ', 'wa iżā ra`au āyatay yastaskhirụn'),
(3802, 37, 15, 'وَقَالُوْٓا اِنْ هٰذَآ اِلَّا سِحْرٌ مُّبِيْنٌ ۚ', 'wa qālū in hāżā illā siḥrum mubīn'),
(3803, 37, 16, 'ءَاِذَا مِتْنَا وَكُنَّا تُرَابًا وَّعِظَامًا ءَاِنَّا لَمَبْعُوْثُوْنَۙ', 'a iżā mitnā wa kunnā turābaw wa \'iẓāman a innā lamab\'ụṡụn'),
(3804, 37, 17, 'اَوَاٰبَاۤؤُنَا الْاَوَّلُوْنَۗ', 'a wa ābā`unal-awwalụn'),
(3805, 37, 18, 'قُلْ نَعَمْ وَاَنْتُمْ دَاخِرُوْنَۚ', 'qul na\'am wa antum dākhirụn'),
(3806, 37, 19, 'فَاِنَّمَا هِيَ زَجْرَةٌ وَّاحِدَةٌ فَاِذَا هُمْ يَنْظُرُوْنَ', 'fa innamā hiya zajratuw wāḥidatun fa iżā hum yanẓurụn'),
(3807, 37, 20, 'وَقَالُوْا يٰوَيْلَنَا هٰذَا يَوْمُ الدِّيْنِ', 'wa qālụ yā wailanā hāżā yaumud-dīn'),
(3808, 37, 21, 'هٰذَا يَوْمُ الْفَصْلِ الَّذِيْ كُنْتُمْ بِهٖ تُكَذِّبُوْنَ', 'hāżā yaumul-faṣlillażī kuntum bihī tukażżibụn'),
(3809, 37, 22, 'اُحْشُرُوا الَّذِيْنَ ظَلَمُوْا وَاَزْوَاجَهُمْ وَمَا كَانُوْا يَعْبُدُوْنَ ۙ', 'uḥsyurullażīna ẓalamụ wa azwājahum wa mā kānụ ya\'budụn'),
(3810, 37, 23, 'مِنْ دُوْنِ اللّٰهِ فَاهْدُوْهُمْ اِلٰى صِرَاطِ الْجَحِيْمِ', 'min dụnillāhi fahdụhum ilā ṣirāṭil-jaḥīm'),
(3811, 37, 24, 'وَقِفُوْهُمْ اِنَّهُمْ مَّسْـُٔوْلُوْنَ ۙ', 'waqifụhum innahum mas`ụlụn'),
(3812, 37, 25, 'مَا لَكُمْ لَا تَنَاصَرُوْنَ', 'mā lakum lā tanāṣarụn'),
(3813, 37, 26, 'بَلْ هُمُ الْيَوْمَ مُسْتَسْلِمُوْنَ', 'bal humul-yauma mustaslimụn'),
(3814, 37, 27, 'وَاَقْبَلَ بَعْضُهُمْ عَلٰى بَعْضٍ يَّتَسَاۤءَلُوْنَ', 'wa aqbala ba\'ḍuhum \'alā ba\'ḍiy yatasā`alụn'),
(3815, 37, 28, 'قَالُوْٓا اِنَّكُمْ كُنْتُمْ تَأْتُوْنَنَا عَنِ الْيَمِيْنِ', 'qālū innakum kuntum ta`tụnanā \'anil-yamīn');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(3816, 37, 29, 'قَالُوْا بَلْ لَّمْ تَكُوْنُوْا مُؤْمِنِيْنَۚ', 'qālụ bal lam takụnụ mu`minīn'),
(3817, 37, 30, 'وَمَا كَانَ لَنَا عَلَيْكُمْ مِّنْ سُلْطٰنٍۚ بَلْ كُنْتُمْ قَوْمًا طٰغِيْنَ', 'wa mā kāna lanā \'alaikum min sulṭān, bal kuntum qauman ṭāgīn'),
(3818, 37, 31, 'فَحَقَّ عَلَيْنَا قَوْلُ رَبِّنَآ ۖاِنَّا لَذَاۤىِٕقُوْنَ', 'fa ḥaqqa \'alainā qaulu rabbinā innā lażā`iqụn'),
(3819, 37, 32, 'فَاَغْوَيْنٰكُمْ اِنَّا كُنَّا غٰوِيْنَ', 'fa agwainākum innā kunnā gāwīn'),
(3820, 37, 33, 'فَاِنَّهُمْ يَوْمَىِٕذٍ فِى الْعَذَابِ مُشْتَرِكُوْنَ', 'fa innahum yauma`iżin fil-\'ażābi musytarikụn'),
(3821, 37, 34, 'اِنَّا كَذٰلِكَ نَفْعَلُ بِالْمُجْرِمِيْنَ', 'innā każālika naf\'alu bil-mujrimīn'),
(3822, 37, 35, 'اِنَّهُمْ كَانُوْٓا اِذَا قِيْلَ لَهُمْ لَآ اِلٰهَ اِلَّا اللّٰهُ يَسْتَكْبِرُوْنَ ۙ', 'innahum kānū iżā qīla lahum lā ilāha illallāhu yastakbirụn'),
(3823, 37, 36, 'وَيَقُوْلُوْنَ اَىِٕنَّا لَتَارِكُوْٓا اٰلِهَتِنَا لِشَاعِرٍ مَّجْنُوْنٍ ۗ', 'wa yaqụlụna a innā latārikū ālihatinā lisyā\'irim majnụn'),
(3824, 37, 37, 'بَلْ جَاۤءَ بِالْحَقِّ وَصَدَّقَ الْمُرْسَلِيْنَ', 'bal jā`a bil-ḥaqqi wa ṣaddaqal-mursalīn'),
(3825, 37, 38, 'اِنَّكُمْ لَذَاۤىِٕقُوا الْعَذَابِ الْاَلِيْمِ ۚ', 'innakum lażā`iqul-\'ażābil-alīm'),
(3826, 37, 39, 'وَمَا تُجْزَوْنَ اِلَّا مَا كُنْتُمْ تَعْمَلُوْنَۙ', 'wa mā tujzauna illā mā kuntum ta\'malụn'),
(3827, 37, 40, 'اِلَّا عِبَادَ اللّٰهِ الْمُخْلَصِيْنَ', 'illā \'ibādallāhil-mukhlaṣīn'),
(3828, 37, 41, 'اُولٰۤىِٕكَ لَهُمْ رِزْقٌ مَّعْلُوْمٌۙ', 'ulā`ika lahum rizqum ma\'lụm'),
(3829, 37, 42, 'فَوَاكِهُ ۚوَهُمْ مُّكْرَمُوْنَۙ', 'fawākih, wa hum mukramụn'),
(3830, 37, 43, 'فِيْ جَنّٰتِ النَّعِيْمِۙ', 'fī jannātin na\'īm'),
(3831, 37, 44, 'عَلٰى سُرُرٍ مُّتَقٰبِلِيْنَ', '\'alā sururim mutaqābilīn'),
(3832, 37, 45, 'يُطَافُ عَلَيْهِمْ بِكَأْسٍ مِّنْ مَّعِيْنٍۢ ۙ', 'yuṭāfu \'alaihim bika`sim mim ma\'īn'),
(3833, 37, 46, 'بَيْضَاۤءَ لَذَّةٍ لِّلشّٰرِبِيْنَۚ', 'baiḍā`a lażżatil lisy-syāribīn'),
(3834, 37, 47, 'لَا فِيْهَا غَوْلٌ وَّلَا هُمْ عَنْهَا يُنْزَفُوْنَ', 'lā fīhā gauluw wa lā hum \'an-hā yunzafụn'),
(3835, 37, 48, 'وَعِنْدَهُمْ قٰصِرٰتُ الطَّرْفِ عِيْنٌ ۙ', 'wa \'indahum qāṣirātuṭ-ṭarfi \'īn'),
(3836, 37, 49, 'كَاَنَّهُنَّ بَيْضٌ مَّكْنُوْنٌ', 'ka`annahunna baiḍum maknụn'),
(3837, 37, 50, 'فَاَقْبَلَ بَعْضُهُمْ عَلٰى بَعْضٍ يَّتَسَاۤءَلُوْنَ', 'fa aqbala ba\'ḍuhum \'alā ba\'ḍiy yatasā`alụn'),
(3838, 37, 51, 'قَالَ قَاۤىِٕلٌ مِّنْهُمْ اِنِّيْ كَانَ لِيْ قَرِيْنٌۙ', 'qāla qā`ilum min-hum innī kāna lī qarīn'),
(3839, 37, 52, 'يَّقُوْلُ اَىِٕنَّكَ لَمِنَ الْمُصَدِّقِيْنَ', 'yaqụlu a innaka laminal-muṣaddiqīn'),
(3840, 37, 53, 'ءَاِذَا مِتْنَا وَكُنَّا تُرَابًا وَّعِظَامًا ءَاِنَّا لَمَدِيْنُوْنَ', 'a iżā mitnā wa kunnā turābaw wa \'iẓāman a innā lamadīnụn'),
(3841, 37, 54, 'قَالَ هَلْ اَنْتُمْ مُّطَّلِعُوْنَ', 'qāla hal antum muṭṭali\'ụn'),
(3842, 37, 55, 'فَاطَّلَعَ فَرَاٰهُ فِيْ سَوَاۤءِ الْجَحِيْمِ', 'faṭṭala\'a fa ra`āhu fī sawā`il-jaḥīm'),
(3843, 37, 56, 'قَالَ تَاللّٰهِ اِنْ كِدْتَّ لَتُرْدِيْنِ ۙ', 'qāla tallāhi ing kitta laturdīn'),
(3844, 37, 57, 'وَلَوْلَا نِعْمَةُ رَبِّيْ لَكُنْتُ مِنَ الْمُحْضَرِيْنَ', 'walau lā ni\'matu rabbī lakuntu minal-muḥḍarīn'),
(3845, 37, 58, 'اَفَمَا نَحْنُ بِمَيِّتِيْنَۙ', 'a fa mā naḥnu bimayyitīn'),
(3846, 37, 59, 'اِلَّا مَوْتَتَنَا الْاُوْلٰى وَمَا نَحْنُ بِمُعَذَّبِيْنَ', 'illā mautatanal-ụlā wa mā naḥnu bimu\'ażżabīn'),
(3847, 37, 60, 'اِنَّ هٰذَا لَهُوَ الْفَوْزُ الْعَظِيْمُ', 'inna hāżā lahuwal-fauzul-\'aẓīm'),
(3848, 37, 61, 'لِمِثْلِ هٰذَا فَلْيَعْمَلِ الْعٰمِلُوْنَ', 'limiṡli hāżā falya\'malil-\'āmilụn'),
(3849, 37, 62, 'اَذٰلِكَ خَيْرٌ نُّزُلًا اَمْ شَجَرَةُ الزَّقُّوْمِ', 'a żālika khairun nuzulan am syajaratuz-zaqqụm'),
(3850, 37, 63, 'اِنَّا جَعَلْنٰهَا فِتْنَةً لِّلظّٰلِمِيْنَ', 'innā ja\'alnāhā fitnatal liẓ-ẓālimīn'),
(3851, 37, 64, 'اِنَّهَا شَجَرَةٌ تَخْرُجُ فِيْٓ اَصْلِ الْجَحِيْمِۙ', 'innahā syajaratun takhruju fī aṣlil-jaḥīm'),
(3852, 37, 65, 'طَلْعُهَا كَاَنَّهٗ رُءُوْسُ الشَّيٰطِيْنِ', 'ṭal\'uhā ka`annahụ ru`ụsusy-syayāṭīn'),
(3853, 37, 66, 'فَاِنَّهُمْ لَاٰكِلُوْنَ مِنْهَا فَمَالِـُٔوْنَ مِنْهَا الْبُطُوْنَۗ', 'fa innahum la`ākilụna min-hā famāli`ụna min-hal buṭụn'),
(3854, 37, 67, 'ثُمَّ اِنَّ لَهُمْ عَلَيْهَا لَشَوْبًا مِّنْ حَمِيْمٍۚ', 'ṡumma inna lahum \'alaihā lasyaubam min ḥamīm'),
(3855, 37, 68, 'ثُمَّ اِنَّ مَرْجِعَهُمْ لَاِلَى الْجَحِيْمِ', 'ṡumma inna marji\'ahum la`ilal-jaḥīm'),
(3856, 37, 69, 'اِنَّهُمْ اَلْفَوْا اٰبَاۤءَهُمْ ضَاۤلِّيْنَۙ', 'innahum alfau ābā`ahum ḍāllīn'),
(3857, 37, 70, 'فَهُمْ عَلٰٓى اٰثٰرِهِمْ يُهْرَعُوْنَ', 'fa hum \'alā āṡārihim yuhra\'ụn'),
(3858, 37, 71, 'وَلَقَدْ ضَلَّ قَبْلَهُمْ اَكْثَرُ الْاَوَّلِيْنَۙ', 'wa laqad ḍalla qablahum akṡarul-awwalīn'),
(3859, 37, 72, 'وَلَقَدْ اَرْسَلْنَا فِيْهِمْ مُّنْذِرِيْنَ', 'wa laqad arsalnā fīhim munżirīn'),
(3860, 37, 73, 'فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُنْذَرِيْنَۙ', 'fanẓur kaifa kāna \'āqibatul-munżarīn'),
(3861, 37, 74, 'اِلَّا عِبَادَ اللّٰهِ الْمُخْلَصِيْنَ', 'illā \'ibādallāhil-mukhlaṣīn'),
(3862, 37, 75, 'وَلَقَدْ نَادٰىنَا نُوْحٌ فَلَنِعْمَ الْمُجِيْبُوْنَۖ', 'wa laqad nādānā nụḥun fa lani\'mal-mujībụn'),
(3863, 37, 76, 'وَنَجَّيْنٰهُ وَاَهْلَهٗ مِنَ الْكَرْبِ الْعَظِيْمِۖ', 'wa najjaināhu wa ahlahụ minal-karbil-\'aẓīm'),
(3864, 37, 77, 'وَجَعَلْنَا ذُرِّيَّتَهٗ هُمُ الْبٰقِيْنَ', 'wa ja\'alnā żurriyyatahụ humul-bāqīn'),
(3865, 37, 78, 'وَتَرَكْنَا عَلَيْهِ فِى الْاٰخِرِيْنَ ۖ', 'wa taraknā \'alaihi fil-ākhirīn'),
(3866, 37, 79, 'سَلٰمٌ عَلٰى نُوْحٍ فِى الْعٰلَمِيْنَ', 'salāmun \'alā nụḥin fil-\'ālamīn'),
(3867, 37, 80, 'اِنَّا كَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ', 'innā każālika najzil-muḥsinīn'),
(3868, 37, 81, 'اِنَّهٗ مِنْ عِبَادِنَا الْمُؤْمِنِيْنَ', 'innahụ min \'ibādinal-mu`minīn'),
(3869, 37, 82, 'ثُمَّ اَغْرَقْنَا الْاٰخَرِيْنَ', 'ṡumma agraqnal-ākharīn'),
(3870, 37, 83, 'وَاِنَّ مِنْ شِيْعَتِهٖ لَاِبْرٰهِيْمَ ۘ', 'wa inna min syī\'atihī la`ibrāhīm'),
(3871, 37, 84, 'اِذْ جَاۤءَ رَبَّهٗ بِقَلْبٍ سَلِيْمٍۙ', 'iż jā`a rabbahụ biqalbin salīm'),
(3872, 37, 85, 'اِذْ قَالَ لِاَبِيْهِ وَقَوْمِهٖ مَاذَا تَعْبُدُوْنَ ۚ', 'iż qāla li`abīhi wa qaumihī māżā ta\'budụn'),
(3873, 37, 86, 'اَىِٕفْكًا اٰلِهَةً دُوْنَ اللّٰهِ تُرِيْدُوْنَۗ', 'a ifkan āliḥatan dụnallāhi turīdụn'),
(3874, 37, 87, 'فَمَا ظَنُّكُمْ بِرَبِّ الْعٰلَمِيْنَ', 'fa mā ẓannukum birabbil-\'ālamīn'),
(3875, 37, 88, 'فَنَظَرَ نَظْرَةً فِى النُّجُوْمِۙ', 'fa naẓara naẓratan fin-nujụm'),
(3876, 37, 89, 'فَقَالَ اِنِّيْ سَقِيْمٌ', 'fa qāla innī saqīm'),
(3877, 37, 90, 'فَتَوَلَّوْا عَنْهُ مُدْبِرِيْنَ', 'fa tawallau \'an-hu mudbirīn'),
(3878, 37, 91, 'فَرَاغَ اِلٰٓى اٰلِهَتِهِمْ فَقَالَ اَلَا تَأْكُلُوْنَۚ', 'fa rāga ilā ālihatihim fa qāla alā ta`kulụn'),
(3879, 37, 92, 'مَا لَكُمْ لَا تَنْطِقُوْنَ', 'mā lakum lā tanṭiqụn'),
(3880, 37, 93, 'فَرَاغَ عَلَيْهِمْ ضَرْبًا ۢبِالْيَمِيْنِ', 'fa rāga \'alaihim ḍarbam bil-yamīn'),
(3881, 37, 94, 'فَاَقْبَلُوْٓا اِلَيْهِ يَزِفُّوْنَ', 'fa aqbalū ilaihi yaziffụn'),
(3882, 37, 95, 'قَالَ اَتَعْبُدُوْنَ مَا تَنْحِتُوْنَۙ', 'qāla a ta\'budụna mā tan-ḥitụn'),
(3883, 37, 96, 'وَاللّٰهُ خَلَقَكُمْ وَمَا تَعْمَلُوْنَ', 'wallāhu khalaqakum wa mā ta\'malụn'),
(3884, 37, 97, 'قَالُوا ابْنُوْا لَهٗ بُنْيَانًا فَاَلْقُوْهُ فِى الْجَحِيْمِ', 'qālubnụ lahụ bun-yānan fa alqụhu fil-jaḥīm'),
(3885, 37, 98, 'فَاَرَادُوْا بِهٖ كَيْدًا فَجَعَلْنٰهُمُ الْاَسْفَلِيْنَ', 'fa arādụ bihī kaidan fa ja\'alnāhumul-asfalīn'),
(3886, 37, 99, 'وَقَالَ اِنِّيْ ذَاهِبٌ اِلٰى رَبِّيْ سَيَهْدِيْنِ', 'wa qāla innī żāhibun ilā rabbī sayahdīn'),
(3887, 37, 100, 'رَبِّ هَبْ لِيْ مِنَ الصّٰلِحِيْنَ', 'rabbi hab lī minaṣ-ṣāliḥīn'),
(3888, 37, 101, 'فَبَشَّرْنٰهُ بِغُلٰمٍ حَلِيْمٍ', 'fa basysyarnāhu bigulāmin ḥalīm'),
(3889, 37, 102, 'فَلَمَّا بَلَغَ مَعَهُ السَّعْيَ قَالَ يٰبُنَيَّ اِنِّيْٓ اَرٰى فِى الْمَنَامِ اَنِّيْٓ اَذْبَحُكَ فَانْظُرْ مَاذَا تَرٰىۗ قَالَ يٰٓاَبَتِ افْعَلْ مَا تُؤْمَرُۖ سَتَجِدُنِيْٓ اِنْ شَاۤءَ اللّٰهُ مِنَ الصّٰبِرِيْنَ', 'fa lammā balaga ma\'ahus-sa\'ya qāla yā bunayya innī arā fil-manāmi annī ażbaḥuka fanẓur māżā tarā, qāla yā abatif\'al mā tu`maru satajidunī in syā`allāhu minaṣ-ṣābirīn'),
(3890, 37, 103, 'فَلَمَّآ اَسْلَمَا وَتَلَّهٗ لِلْجَبِيْنِۚ', 'fa lammā aslamā wa tallahụ lil-jabīn'),
(3891, 37, 104, 'وَنَادَيْنٰهُ اَنْ يّٰٓاِبْرٰهِيْمُ ۙ', 'wa nādaināhu ay yā ibrāhīm'),
(3892, 37, 105, 'قَدْ صَدَّقْتَ الرُّؤْيَا ۚاِنَّا كَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ', 'qad ṣaddaqtar-ru`yā, innā każālika najzil-muḥsinīn'),
(3893, 37, 106, 'اِنَّ هٰذَا لَهُوَ الْبَلٰۤؤُا الْمُبِيْنُ', 'inna hāżā lahuwal-balā`ul mubīn'),
(3894, 37, 107, 'وَفَدَيْنٰهُ بِذِبْحٍ عَظِيْمٍ', 'wa fadaināhu biżib-ḥin \'aẓīm'),
(3895, 37, 108, 'وَتَرَكْنَا عَلَيْهِ فِى الْاٰخِرِيْنَ ۖ', 'wa taraknā \'alaihi fil-ākhirīn'),
(3896, 37, 109, 'سَلٰمٌ عَلٰٓى اِبْرٰهِيْمَ', 'salāmun \'alā ibrāhīm'),
(3897, 37, 110, 'كَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ', 'każālika najzil-muḥsinīn'),
(3898, 37, 111, 'اِنَّهٗ مِنْ عِبَادِنَا الْمُؤْمِنِيْنَ', 'innahụ min \'ibādinal-mu`minīn'),
(3899, 37, 112, 'وَبَشَّرْنٰهُ بِاِسْحٰقَ نَبِيًّا مِّنَ الصّٰلِحِيْنَ', 'wa basysyarnāhu bi`is-ḥāqa nabiyyam minaṣ-ṣāliḥīn'),
(3900, 37, 113, 'وَبٰرَكْنَا عَلَيْهِ وَعَلٰٓى اِسْحٰقَۗ وَمِنْ ذُرِّيَّتِهِمَا مُحْسِنٌ وَّظَالِمٌ لِّنَفْسِهٖ مُبِيْنٌ', 'wa bāraknā \'alaihi wa \'alā is-ḥāq, wa min żurriyyatihimā muḥsinuw wa ẓālimul linafsihī mubīn'),
(3901, 37, 114, 'وَلَقَدْ مَنَنَّا عَلٰى مُوْسٰى وَهٰرُوْنَ ۚ', 'wa laqad manannā \'alā mụsā wa hārụn'),
(3902, 37, 115, 'وَنَجَّيْنٰهُمَا وَقَوْمَهُمَا مِنَ الْكَرْبِ الْعَظِيْمِۚ', 'wa najjaināhumā wa qaumahumā minal-karbil-\'aẓīm'),
(3903, 37, 116, 'وَنَصَرْنٰهُمْ فَكَانُوْا هُمُ الْغٰلِبِيْنَۚ', 'wa naṣarnāhum fa kānụ humul-gālibīn'),
(3904, 37, 117, 'وَاٰتَيْنٰهُمَا الْكِتٰبَ الْمُسْتَبِيْنَ ۚ', 'wa ātaināhumal-kitābal-mustabīn'),
(3905, 37, 118, 'وَهَدَيْنٰهُمَا الصِّرَاطَ الْمُسْتَقِيْمَۚ', 'wa hadaināhumaṣ-ṣirāṭal-mustaqīm'),
(3906, 37, 119, 'وَتَرَكْنَا عَلَيْهِمَا فِى الْاٰخِرِيْنَ ۖ', 'wa taraknā \'alaihimā fil-ākhirīn'),
(3907, 37, 120, 'سَلٰمٌ عَلٰى مُوْسٰى وَهٰرُوْنَ', 'salāmun \'alā mụsā wa hārụn'),
(3908, 37, 121, 'اِنَّا كَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ', 'innā każālika najzil-muḥsinīn'),
(3909, 37, 122, 'اِنَّهُمَا مِنْ عِبَادِنَا الْمُؤْمِنِيْنَ', 'innahumā min \'ibādinal-mu`minīn'),
(3910, 37, 123, 'وَاِنَّ اِلْيَاسَ لَمِنَ الْمُرْسَلِيْنَۗ', 'wa inna ilyāsa laminal-mursalīn'),
(3911, 37, 124, 'اِذْ قَالَ لِقَوْمِهٖٓ اَلَا تَتَّقُوْنَ', 'iż qāla liqaumihī alā tattaqụn'),
(3912, 37, 125, 'اَتَدْعُوْنَ بَعْلًا وَّتَذَرُوْنَ اَحْسَنَ الْخَالِقِيْنَۙ', 'a tad\'ụna ba\'law wa tażarụna aḥsanal-khāliqīn'),
(3913, 37, 126, 'اللّٰهَ رَبَّكُمْ وَرَبَّ اٰبَاۤىِٕكُمُ الْاَوَّلِيْنَ', 'allāha rabbakum wa rabba ābā`ikumul-awwalīn'),
(3914, 37, 127, 'فَكَذَّبُوْهُ فَاِنَّهُمْ لَمُحْضَرُوْنَۙ', 'fa każżabụhu fa innahum lamuḥḍarụn'),
(3915, 37, 128, 'اِلَّا عِبَادَ اللّٰهِ الْمُخْلَصِيْنَ', 'illā \'ibādallāhil-mukhlaṣīn'),
(3916, 37, 129, 'وَتَرَكْنَا عَلَيْهِ فِى الْاٰخِرِيْنَ ۙ', 'wa taraknā \'alaihi fil-ākhirīn'),
(3917, 37, 130, 'سَلٰمٌ عَلٰٓى اِلْ يَاسِيْنَ', 'salāmun \'alā ilyāsīn'),
(3918, 37, 131, 'اِنَّا كَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ', 'innā każālika najzil-muḥsinīn'),
(3919, 37, 132, 'اِنَّهٗ مِنْ عِبَادِنَا الْمُؤْمِنِيْنَ', 'innahụ min \'ibādinal-mu`minīn'),
(3920, 37, 133, 'وَاِنَّ لُوْطًا لَّمِنَ الْمُرْسَلِيْنَۗ', 'wa inna lụṭal laminal-mursalīn'),
(3921, 37, 134, 'اِذْ نَجَّيْنٰهُ وَاَهْلَهٗٓ اَجْمَعِيْۙنَ', 'iż najjaināhu wa ahlahū ajma\'īn'),
(3922, 37, 135, 'اِلَّا عَجُوْزًا فِى الْغٰبِرِيْنَ', 'illā \'ajụzan fil-gābirīn'),
(3923, 37, 136, 'ثُمَّ دَمَّرْنَا الْاٰخَرِيْنَ', 'ṡumma dammarnal-ākharīn'),
(3924, 37, 137, 'وَاِنَّكُمْ لَتَمُرُّوْنَ عَلَيْهِمْ مُّصْبِحِيْنَۙ', 'wa innakum latamurrụna \'alaihim muṣbiḥīn'),
(3925, 37, 138, 'وَبِالَّيْلِۗ اَفَلَا تَعْقِلُوْنَ', 'wa bil-laīl, a fa lā ta\'qilụn'),
(3926, 37, 139, 'وَاِنَّ يُوْنُسَ لَمِنَ الْمُرْسَلِيْنَۗ', 'wa inna yụnusa laminal-mursalīn'),
(3927, 37, 140, 'اِذْ اَبَقَ اِلَى الْفُلْكِ الْمَشْحُوْنِۙ', 'iż abaqa ilal-fulkil-masy-ḥụn'),
(3928, 37, 141, 'فَسَاهَمَ فَكَانَ مِنَ الْمُدْحَضِيْنَۚ', 'fa sāhama fa kāna minal-mud-ḥaḍīn'),
(3929, 37, 142, 'فَالْتَقَمَهُ الْحُوْتُ وَهُوَ مُلِيْمٌ', 'faltaqamahul-ḥụtu wa huwa mulīm'),
(3930, 37, 143, 'فَلَوْلَآ اَنَّهٗ كَانَ مِنَ الْمُسَبِّحِيْنَ ۙ', 'falau lā annahụ kāna minal-musabbiḥīn'),
(3931, 37, 144, 'لَلَبِثَ فِيْ بَطْنِهٖٓ اِلٰى يَوْمِ يُبْعَثُوْنَۚ', 'lalabiṡa fī baṭnihī ilā yaumi yub\'aṡụn'),
(3932, 37, 145, 'فَنَبَذْنٰهُ بِالْعَرَاۤءِ وَهُوَ سَقِيْمٌ ۚ', 'fa nabażnāhu bil-\'arā`i wa huwa saqīm'),
(3933, 37, 146, 'وَاَنْۢبَتْنَا عَلَيْهِ شَجَرَةً مِّنْ يَّقْطِيْنٍۚ', 'wa ambatnā \'alaihi syajaratam miy yaqṭīn'),
(3934, 37, 147, 'وَاَرْسَلْنٰهُ اِلٰى مِائَةِ اَلْفٍ اَوْ يَزِيْدُوْنَۚ', 'wa arsalnāhu ilā mi`ati alfin au yazīdụn'),
(3935, 37, 148, 'فَاٰمَنُوْا فَمَتَّعْنٰهُمْ اِلٰى حِيْنٍ', 'fa āmanụ fa matta\'nāhum ilā ḥīn'),
(3936, 37, 149, 'فَاسْتَفْتِهِمْ اَلِرَبِّكَ الْبَنَاتُ وَلَهُمُ الْبَنُوْنَۚ', 'fastaftihim a lirabbikal-banātu wa lahumul-banụn'),
(3937, 37, 150, 'اَمْ خَلَقْنَا الْمَلٰۤىِٕكَةَ اِنَاثًا وَّهُمْ شَاهِدُوْنَ', 'am khalaqnal-malā`ikata ināṡaw wa hum syāhidụn'),
(3938, 37, 151, 'اَلَآ اِنَّهُمْ مِّنْ اِفْكِهِمْ لَيَقُوْلُوْنَۙ', 'alā innahum min ifkihim layaqụlụn'),
(3939, 37, 152, 'وَلَدَ اللّٰهُ ۙوَاِنَّهُمْ لَكٰذِبُوْنَۙ', 'waladallāhu wa innahum lakāżibụn'),
(3940, 37, 153, 'اَصْطَفَى الْبَنَاتِ عَلَى الْبَنِيْنَۗ', 'aṣṭafal-banāti \'alal-banīn'),
(3941, 37, 154, 'مَا لَكُمْۗ كَيْفَ تَحْكُمُوْنَ', 'mā lakum, kaifa taḥkumụn'),
(3942, 37, 155, 'اَفَلَا تَذَكَّرُوْنَۚ', 'a fa lā tażakkarụn'),
(3943, 37, 156, 'اَمْ لَكُمْ سُلْطٰنٌ مُّبِيْنٌۙ', 'am lakum sulṭānum mubīn'),
(3944, 37, 157, 'فَأْتُوْا بِكِتٰبِكُمْ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'fa`tụ bikitābikum ing kuntum ṣādiqīn'),
(3945, 37, 158, 'وَجَعَلُوْا بَيْنَهٗ وَبَيْنَ الْجِنَّةِ نَسَبًا ۗوَلَقَدْ عَلِمَتِ الْجِنَّةُ اِنَّهُمْ لَمُحْضَرُوْنَۙ', 'wa ja\'alụ bainahụ wa bainal-jinnati nasabā, wa laqad \'alimatil-jinnatu innahum lamuḥḍarụn'),
(3946, 37, 159, 'سُبْحٰنَ اللّٰهِ عَمَّا يَصِفُوْنَۙ', 'sub-ḥanallāhi \'ammā yaṣifụn'),
(3947, 37, 160, 'اِلَّا عِبَادَ اللّٰهِ الْمُخْلَصِيْنَ', 'illā \'ibādallāhil-mukhlaṣīn'),
(3948, 37, 161, 'فَاِنَّكُمْ وَمَا تَعْبُدُوْنَۙ', 'fa innakum wa mā ta\'budụn'),
(3949, 37, 162, 'مَآ اَنْتُمْ عَلَيْهِ بِفَاتِنِيْنَۙ', 'mā antum \'alaihi bifātinīn'),
(3950, 37, 163, 'اِلَّا مَنْ هُوَ صَالِ الْجَحِيْمِ', 'illā man huwa ṣālil-jaḥīm'),
(3951, 37, 164, 'وَمَا مِنَّآ اِلَّا لَهٗ مَقَامٌ مَّعْلُوْمٌۙ', 'wa mā minnā illā lahụ maqāmum ma\'lụm'),
(3952, 37, 165, 'وَاِنَّا لَنَحْنُ الصَّۤافُّوْنَۖ', 'wa innā lanaḥnuṣ-ṣāffụn'),
(3953, 37, 166, 'وَاِنَّا لَنَحْنُ الْمُسَبِّحُوْنَ', 'wa innā lanaḥnul-musabbiḥụn'),
(3954, 37, 167, 'وَاِنْ كَانُوْا لَيَقُوْلُوْنَۙ', 'wa ing kānụ layaqụlụn'),
(3955, 37, 168, 'لَوْ اَنَّ عِنْدَنَا ذِكْرًا مِّنَ الْاَوَّلِيْنَۙ', 'lau anna \'indanā żikram minal-awwalīn'),
(3956, 37, 169, 'لَكُنَّا عِبَادَ اللّٰهِ الْمُخْلَصِيْنَ', 'lakunnā \'ibādallāhil-mukhlaṣīn'),
(3957, 37, 170, 'فَكَفَرُوْا بِهٖۚ فَسَوْفَ يَعْلَمُوْنَ', 'fa kafarụ bih, fa saufa ya\'lamụn'),
(3958, 37, 171, 'وَلَقَدْ سَبَقَتْ كَلِمَتُنَا لِعِبَادِنَا الْمُرْسَلِيْنَ ۖ', 'wa laqad sabaqat kalimatunā li\'ibādinal-mursalīn'),
(3959, 37, 172, 'اِنَّهُمْ لَهُمُ الْمَنْصُوْرُوْنَۖ', 'innahum lahumul-manṣụrụn'),
(3960, 37, 173, 'وَاِنَّ جُنْدَنَا لَهُمُ الْغٰلِبُوْنَ', 'wa inna jundanā lahumul-gālibụn'),
(3961, 37, 174, 'فَتَوَلَّ عَنْهُمْ حَتّٰى حِيْنٍۙ', 'fa tawalla \'an-hum ḥattā ḥīn'),
(3962, 37, 175, 'وَّاَبْصِرْهُمْۗ فَسَوْفَ يُبْصِرُوْنَ', 'wa abṣir-hum, fa saufa yubṣirụn'),
(3963, 37, 176, 'اَفَبِعَذَابِنَا يَسْتَعْجِلُوْنَ', 'a fa bi\'ażābinā yasta\'jilụn'),
(3964, 37, 177, 'فَاِذَا نَزَلَ بِسَاحَتِهِمْ فَسَاۤءَ صَبَاحُ الْمُنْذَرِيْنَ', 'fa iżā nazala bisāḥatihim fa sā`a ṣabāḥul-munżarīn'),
(3965, 37, 178, 'وَتَوَلَّ عَنْهُمْ حَتّٰى حِيْنٍۙ', 'wa tawalla \'an-hum ḥattā ḥīn'),
(3966, 37, 179, 'وَّاَبْصِرْۗ فَسَوْفَ يُبْصِرُوْنَ', 'wa abṣir, fa saufa yubṣirụn'),
(3967, 37, 180, 'سُبْحٰنَ رَبِّكَ رَبِّ الْعِزَّةِ عَمَّا يَصِفُوْنَۚ', 'sub-ḥāna rabbika rabbil-\'izzati \'ammā yaṣifụn'),
(3968, 37, 181, 'وَسَلٰمٌ عَلَى الْمُرْسَلِيْنَۚ', 'wa salāmun \'alal-mursalīn'),
(3969, 37, 182, 'وَالْحَمْدُ لِلّٰهِ رَبِّ الْعٰلَمِيْنَ', 'wal-ḥamdu lillāhi rabbil-\'ālamīn'),
(3970, 38, 1, 'صۤ ۗوَالْقُرْاٰنِ ذِى الذِّكْرِۗ', 'shād, wal-qur`āni żiż-żikr'),
(3971, 38, 2, 'بَلِ الَّذِيْنَ كَفَرُوْا فِيْ عِزَّةٍ وَّشِقَاقٍ', 'balillażīna kafarụ fī \'izzatiw wa syiqāq'),
(3972, 38, 3, 'كَمْ اَهْلَكْنَا مِنْ قَبْلِهِمْ مِّنْ قَرْنٍ فَنَادَوْا وَّلَاتَ حِيْنَ مَنَاصٍ', 'kam ahlaknā ming qablihim ming qarnin fa nādaw wa lāta ḥīna manāṣ'),
(3973, 38, 4, 'وَعَجِبُوْٓا اَنْ جَاۤءَهُمْ مُّنْذِرٌ مِّنْهُمْ ۖوَقَالَ الْكٰفِرُوْنَ هٰذَا سٰحِرٌ كَذَّابٌۚ', 'wa \'ajibū an jā`ahum munżirum min-hum wa qālal-kāfirụna hāżā sāḥirung każżāb'),
(3974, 38, 5, 'اَجَعَلَ الْاٰلِهَةَ اِلٰهًا وَّاحِدًا ۖاِنَّ هٰذَا لَشَيْءٌ عُجَابٌ', 'a ja\'alal-ālihata ilāhaw wāḥidan inna hāżā lasyai`un \'ujāb'),
(3975, 38, 6, 'وَانْطَلَقَ الْمَلَاُ مِنْهُمْ اَنِ امْشُوْا وَاصْبِرُوْا عَلٰٓى اٰلِهَتِكُمْ ۖاِنَّ هٰذَا لَشَيْءٌ يُّرَادُ ۖ', 'wanṭalaqal-mala`u min-hum animsyụ waṣbirụ \'alā ālihatikum inna hāżā lasyai`uy yurād'),
(3976, 38, 7, 'مَا سَمِعْنَا بِهٰذَا فِى الْمِلَّةِ الْاٰخِرَةِ ۖاِنْ هٰذَآ اِلَّا اخْتِلَاقٌۚ', 'mā sami\'nā bihāżā fil-millatil-ākhirati in hāżā illakhtilāq'),
(3977, 38, 8, 'اَؤُنْزِلَ عَلَيْهِ الذِّكْرُ مِنْۢ بَيْنِنَا ۗبَلْ هُمْ فِيْ شَكٍّ مِّنْ ذِكْرِيْۚ بَلْ لَّمَّا يَذُوْقُوْا عَذَابِ ۗ', 'a unzila \'alaihiż-żikru mim baininā, bal hum fī syakkim min żikrī, bal lammā yażụqụ \'ażāb'),
(3978, 38, 9, 'اَمْ عِنْدَهُمْ خَزَاۤىِٕنُ رَحْمَةِ رَبِّكَ الْعَزِيْزِ الْوَهَّابِۚ', 'am \'indahum khazā`inu raḥmati rabbikal-\'azīzil wahhāb'),
(3979, 38, 10, 'اَمْ لَهُمْ مُّلْكُ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا ۗفَلْيَرْتَقُوْا فِى الْاَسْبَابِ', 'am lahum mulkus-samāwāti wal-arḍi wa mā bainahumā, falyartaqụ fil-asbāb'),
(3980, 38, 11, 'جُنْدٌ مَّا هُنَالِكَ مَهْزُوْمٌ مِّنَ الْاَحْزَابِ', 'jundum mā hunālika mahzụmum minal-aḥzāb'),
(3981, 38, 12, 'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوْحٍ وَّعَادٌ وَّفِرْعَوْنُ ذُو الْاَوْتَادِۙ', 'każżabat qablahum qaumu nụḥiw wa \'āduw wa fir\'aunu żul-autād'),
(3982, 38, 13, 'وَثَمُوْدُ وَقَوْمُ لُوْطٍ وَّاَصْحٰبُ لْـَٔيْكَةِ ۗ اُولٰۤىِٕكَ الْاَحْزَابُ', 'wa ṡamụdu wa qaumu lụṭiw wa aṣ-ḥābul-aikah, ulā`ikal-aḥzāb'),
(3983, 38, 14, 'اِنْ كُلٌّ اِلَّا كَذَّبَ الرُّسُلَ فَحَقَّ عِقَابِ', 'ing kullun illā każżabar-rusula fa ḥaqqa \'iqāb'),
(3984, 38, 15, 'وَمَا يَنْظُرُ هٰٓؤُلَاۤءِ اِلَّا صَيْحَةً وَّاحِدَةً مَّا لَهَا مِنْ فَوَاقٍ', 'wa mā yanẓuru hā`ulā`i illā ṣaiḥataw wāḥidatam mā lahā min fawāq'),
(3985, 38, 16, 'وَقَالُوْا رَبَّنَا عَجِّلْ لَّنَا قِطَّنَا قَبْلَ يَوْمِ الْحِسَابِ', 'wa qālụ rabbanā \'ajjil lanā qiṭṭanā qabla yaumil-ḥisāb'),
(3986, 38, 17, 'اِصْبِرْ عَلٰى مَا يَقُوْلُوْنَ وَاذْكُرْ عَبْدَنَا دَاوٗدَ ذَا الْاَيْدِۚ اِنَّهٗٓ اَوَّابٌ', 'iṣbir \'alā mā yaqụlụna ważkur \'abdanā dāwụda żal-aīd, innahū awwāb'),
(3987, 38, 18, 'اِنَّا سَخَّرْنَا الْجِبَالَ مَعَهٗ يُسَبِّحْنَ بِالْعَشِيِّ وَالْاِشْرَاقِۙ', 'innā sakhkharnal-jibāla ma\'ahụ yusabbiḥna bil-\'asyiyyi wal-isyrāq'),
(3988, 38, 19, 'وَالطَّيْرَمَحْشُوْرَةً ۗ كُلٌّ لَهٗٓ اَوَّابٌ', 'waṭ-ṭaira maḥsyụrah, kullul lahū awwāb'),
(3989, 38, 20, 'وَشَدَدْنَا مُلْكَهٗ وَاٰتَيْنٰهُ الْحِكْمَةَ وَفَصْلَ الْخِطَابِ', 'wa syadadnā mulkahụ wa ātaināhul-ḥikmata wa faṣlal-khiṭāb'),
(3990, 38, 21, 'وَهَلْ اَتٰىكَ نَبَؤُ الْخَصْمِۘ اِذْ تَسَوَّرُوا الْمِحْرَابَۙ', 'wa hal atāka naba`ul khaṣm, iż tasawwarul-miḥrāb'),
(3991, 38, 22, 'اِذْ دَخَلُوْا عَلٰى دَاوٗدَ فَفَزِعَ مِنْهُمْ قَالُوْا لَا تَخَفْۚ خَصْمٰنِ بَغٰى بَعْضُنَا عَلٰى بَعْضٍ فَاحْكُمْ بَيْنَنَا بِالْحَقِّ وَلَا تُشْطِطْ وَاهْدِنَآ اِلٰى سَوَاۤءِ الصِّرَاطِ', 'iż dakhalụ \'alā dāwụda fa fazi\'a min-hum qālụ lā takhaf, khaṣmāni bagā ba\'ḍunā \'alā ba\'ḍin faḥkum bainanā bil-ḥaqqi wa lā tusyṭiṭ wahdinā ilā sawā`iṣ-ṣirāṭ'),
(3992, 38, 23, 'اِنَّ هٰذَآ اَخِيْ ۗ لَهٗ تِسْعٌ وَّتِسْعُوْنَ نَعْجَةً وَّلِيَ نَعْجَةٌ وَّاحِدَةٌ ۗفَقَالَ اَكْفِلْنِيْهَا وَعَزَّنِيْ فِى الْخِطَابِ', 'inna hāżā akhī, lahụ tis\'uw wa tis\'ụna na\'jataw wa liya na\'jatuw wāḥidah, fa qāla akfilnīhā wa \'azzanī fil-khiṭāb'),
(3993, 38, 24, 'قَالَ لَقَدْ ظَلَمَكَ بِسُؤَالِ نَعْجَتِكَ اِلٰى نِعَاجِهٖۗ وَاِنَّ كَثِيْرًا مِّنَ الْخُلَطَاۤءِ لَيَبْغِيْ بَعْضُهُمْ عَلٰى بَعْضٍ اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَقَلِيْلٌ مَّا هُمْۗ وَظَنَّ دَاوٗدُ اَنَّمَا فَتَنّٰهُ فَاسْتَغْفَرَ رَبَّهٗ وَخَرَّ رَاكِعًا وَّاَنَابَ ۩', 'qāla laqad ẓalamaka bisu`āli na\'jatika ilā ni\'ājih, wa inna kaṡīram minal-khulaṭā`i layabgī ba\'ḍuhum \'alā ba\'ḍin illallażīna āmanụ wa \'amiluṣ-ṣāliḥāti wa qalīlum mā hum, wa ẓanna dāwụdu annamā fatannāhu fastagfara rabbahụ wa kharra rāki\'aw wa anāb'),
(3994, 38, 25, 'فَغَفَرْنَا لَهٗ ذٰلِكَۗ وَاِنَّ لَهٗ عِنْدَنَا لَزُلْفٰى وَحُسْنَ مَاٰبٍ', 'fa gafarnā lahụ żālik, wa inna lahụ \'indanā lazulfā wa ḥusna ma`āb'),
(3995, 38, 26, 'يٰدَاوٗدُ اِنَّا جَعَلْنٰكَ خَلِيْفَةً فِى الْاَرْضِ فَاحْكُمْ بَيْنَ النَّاسِ بِالْحَقِّ وَلَا تَتَّبِعِ الْهَوٰى فَيُضِلَّكَ عَنْ سَبِيْلِ اللّٰهِ ۗاِنَّ الَّذِيْنَ يَضِلُّوْنَ عَنْ سَبِيْلِ اللّٰهِ لَهُمْ عَذَابٌ شَدِيْدٌ ۢبِمَا نَسُوْا يَوْمَ الْحِسَابِ', 'yā dāwụdu innā ja\'alnāka khalīfatan fil-arḍi faḥkum bainan-nāsi bil-ḥaqqi wa lā tattabi\'il-hawā fa yuḍillaka \'an sabīlillāh, innallażīna yaḍillụna \'an sabīlillāhi lahum \'ażābun syadīdum bimā nasụ yaumal-ḥisāb'),
(3996, 38, 27, 'وَمَا خَلَقْنَا السَّمَاۤءَ وَالْاَرْضَ وَمَا بَيْنَهُمَا بَاطِلًا ۗذٰلِكَ ظَنُّ الَّذِيْنَ كَفَرُوْا فَوَيْلٌ لِّلَّذِيْنَ كَفَرُوْا مِنَ النَّارِۗ', 'wa mā khalaqnas-samā`a wal-arḍa wa mā bainahumā bāṭilā, żālika ẓannullażīna kafarụ fa wailul lillażīna kafarụ minan-nār'),
(3997, 38, 28, 'اَمْ نَجْعَلُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ كَالْمُفْسِدِيْنَ فِى الْاَرْضِۖ اَمْ نَجْعَلُ الْمُتَّقِيْنَ كَالْفُجَّارِ', 'am naj\'alullażīna āmanụ wa \'amiluṣ-ṣāliḥāti kal-mufsidīna fil-arḍi am naj\'alul-muttaqīna kal-fujjār'),
(3998, 38, 29, 'كِتٰبٌ اَنْزَلْنٰهُ اِلَيْكَ مُبٰرَكٌ لِّيَدَّبَّرُوْٓا اٰيٰتِهٖ وَلِيَتَذَكَّرَ اُولُوا الْاَلْبَابِ', 'kitābun anzalnāhu ilaika mubārakul liyaddabbarū āyātihī wa liyatażakkara ulul-albāb'),
(3999, 38, 30, 'وَوَهَبْنَا لِدَاوٗدَ سُلَيْمٰنَۗ نِعْمَ الْعَبْدُ ۗاِنَّهٗٓ اَوَّابٌۗ', 'wa wahabnā lidāwụda sulaimān, ni\'mal-\'abd, innahū awwāb'),
(4000, 38, 31, 'اِذْ عُرِضَ عَلَيْهِ بِالْعَشِيِّ الصّٰفِنٰتُ الْجِيَادُۙ', 'iż \'uriḍa \'alaihi bil-\'asyiyyiṣ-ṣāfinātul-jiyād'),
(4001, 38, 32, 'فَقَالَ اِنِّيْٓ اَحْبَبْتُ حُبَّ الْخَيْرِ عَنْ ذِكْرِ رَبِّيْۚ حَتّٰى تَوَارَتْ بِالْحِجَابِۗ', 'fa qāla innī aḥbabtu ḥubbal-khairi \'an żikri rabbī, ḥattā tawārat bil-ḥijāb'),
(4002, 38, 33, 'رُدُّوْهَا عَلَيَّ ۚفَطَفِقَ مَسْحًا ۢبِالسُّوْقِ وَالْاَعْنَاقِ', 'ruddụhā \'alayy, fa ṭafiqa mas-ḥam bis-sụqi wal-a\'nāq'),
(4003, 38, 34, 'وَلَقَدْ فَتَنَّا سُلَيْمٰنَ وَاَلْقَيْنَا عَلٰى كُرْسِيِّهٖ جَسَدًا ثُمَّ اَنَابَ', 'wa laqad fatannā sulaimāna wa alqainā \'alā kursiyyihī jasadan ṡumma anāb'),
(4004, 38, 35, 'قَالَ رَبِّ اغْفِرْ لِيْ وَهَبْ لِيْ مُلْكًا لَّا يَنْۢبَغِيْ لِاَحَدٍ مِّنْۢ بَعْدِيْۚ اِنَّكَ اَنْتَ الْوَهَّابُ', 'qāla rabbigfir lī wa hab lī mulkal lā yambagī li`aḥadim mim ba\'dī, innaka antal-wahhāb'),
(4005, 38, 36, 'فَسَخَّرْنَا لَهُ الرِّيْحَ تَجْرِيْ بِاَمْرِهٖ رُخَاۤءً حَيْثُ اَصَابَۙ', 'fa sakhkharnā lahur-rīḥa tajrī bi`amrihī rukhā`an ḥaiṡu aṣāb'),
(4006, 38, 37, 'وَالشَّيٰطِيْنَ كُلَّ بَنَّاۤءٍ وَّغَوَّاصٍۙ', 'wasy-syayāṭīna kulla bannā`iw wa gawwāṣ'),
(4007, 38, 38, 'وَّاٰخَرِيْنَ مُقَرَّنِيْنَ فِى الْاَصْفَادِ', 'wa ākharīna muqarranīna fil-aṣfād'),
(4008, 38, 39, 'هٰذَا عَطَاۤؤُنَا فَامْنُنْ اَوْ اَمْسِكْ بِغَيْرِ حِسَابٍ', 'hāżā \'aṭā`unā famnun au amsik bigairi ḥisāb'),
(4009, 38, 40, 'وَاِنَّ لَهٗ عِنْدَنَا لَزُلْفٰى وَحُسْنَ مَاٰبٍ', 'wa inna lahụ \'indanā lazulfā wa ḥusna ma`āb'),
(4010, 38, 41, 'وَاذْكُرْ عَبْدَنَآ اَيُّوْبَۘ اِذْ نَادٰى رَبَّهٗٓ اَنِّيْ مَسَّنِيَ الشَّيْطٰنُ بِنُصْبٍ وَّعَذَابٍۗ', 'ważkur \'abdanā ayyụb, iż nādā rabbahū annī massaniyasy-syaiṭānu binuṣbiw wa \'ażāb'),
(4011, 38, 42, 'اُرْكُضْ بِرِجْلِكَۚ هٰذَا مُغْتَسَلٌۢ بَارِدٌ وَّشَرَابٌ', 'urkuḍ birijlik, hāżā mugtasalum bāriduw wa syarāb'),
(4012, 38, 43, 'وَوَهَبْنَا لَهٗٓ اَهْلَهٗ وَمِثْلَهُمْ مَّعَهُمْ رَحْمَةً مِّنَّا وَذِكْرٰى لِاُولِى الْاَلْبَابِ', 'wa wahabnā lahū ahlahụ wa miṡlahum ma\'ahum raḥmatam minnā wa żikrā li`ulil-albāb'),
(4013, 38, 44, 'وَخُذْ بِيَدِكَ ضِغْثًا فَاضْرِبْ بِّهٖ وَلَا تَحْنَثْ ۗاِنَّا وَجَدْنٰهُ صَابِرًا ۗنِعْمَ الْعَبْدُ ۗاِنَّهٗٓ اَوَّابٌ', 'wa khuż biyadika ḍigṡan faḍrib bihī wa lā taḥnaṡ, innā wajadnāhu ṣābirā, ni\'mal-\'abd, innahū awwāb'),
(4014, 38, 45, 'وَاذْكُرْ عِبٰدَنَآ اِبْرٰهِيْمَ وَاِسْحٰقَ وَيَعْقُوْبَ اُولِى الْاَيْدِيْ وَالْاَبْصَارِ', 'ważkur \'ibādanā ibrāhīma wa is-ḥāqa wa ya\'qụba ulil-aidī wal-abṣār'),
(4015, 38, 46, 'اِنَّآ اَخْلَصْنٰهُمْ بِخَالِصَةٍ ذِكْرَى الدَّارِۚ', 'innā akhlaṣnāhum bikhāliṣatin żikrad-dār'),
(4016, 38, 47, 'وَاِنَّهُمْ عِنْدَنَا لَمِنَ الْمُصْطَفَيْنَ الْاَخْيَارِۗ', 'wa innahum \'indanā laminal-muṣṭafainal-akhyār'),
(4017, 38, 48, 'وَاذْكُرْ اِسْمٰعِيْلَ وَالْيَسَعَ وَذَا الْكِفْلِ ۗوَكُلٌّ مِّنَ الْاَخْيَارِۗ', 'ważkur ismā\'īla walyasa\'a wa żal-kifl, wa kullum minal-akhyār'),
(4018, 38, 49, 'هٰذَا ذِكْرٌ ۗوَاِنَّ لِلْمُتَّقِيْنَ لَحُسْنَ مَاٰبٍۙ', 'hāżā żikr, wa inna lil-muttaqīna laḥusna ma`āb'),
(4019, 38, 50, 'جَنّٰتِ عَدْنٍ مُّفَتَّحَةً لَّهُمُ الْاَبْوَابُۚ', 'jannāti \'adnim mufattaḥatal lahumul-abwāb'),
(4020, 38, 51, 'مُتَّكِـِٕيْنَ فِيْهَا يَدْعُوْنَ فِيْهَا بِفَاكِهَةٍ كَثِيْرَةٍ وَّشَرَابٍ', 'muttaki`īna fīhā yad\'ụna fīhā bifākihating kaṡīratiw wa syarāb'),
(4021, 38, 52, 'وَعِنْدَهُمْ قٰصِرٰتُ الطَّرْفِ اَتْرَابٌ', 'wa \'indahum qāṣirātuṭ-ṭarfi atrāb'),
(4022, 38, 53, 'هٰذَا مَا تُوْعَدُوْنَ لِيَوْمِ الْحِسَابِ', 'hāżā mā tụ\'adụna liyaumil-ḥisāb'),
(4023, 38, 54, 'اِنَّ هٰذَا لَرِزْقُنَا مَا لَهٗ مِنْ نَّفَادٍۚ', 'inna hāżā larizqunā mā lahụ min nafād'),
(4024, 38, 55, 'هٰذَا ۗوَاِنَّ لِلطّٰغِيْنَ لَشَرَّ مَاٰبٍۙ', 'hāżā, wa inna liṭ-ṭāgīna lasyarra ma`āb'),
(4025, 38, 56, 'جَهَنَّمَۚ يَصْلَوْنَهَاۚ فَبِئْسَ الْمِهَادُ', 'jahannam, yaṣlaunahā, fa bi`sal-mihād'),
(4026, 38, 57, 'هٰذَاۙ فَلْيَذُوْقُوْهُ حَمِيْمٌ وَّغَسَّاقٌۙ', 'hāżā falyażụqụhu ḥamīmuw wa gassāq'),
(4027, 38, 58, 'وَّاٰخَرُ مِنْ شَكْلِهٖٓ اَزْوَاجٌۗ', 'wa ākharu min syaklihī azwāj'),
(4028, 38, 59, 'هٰذَا فَوْجٌ مُّقْتَحِمٌ مَّعَكُمْۚ لَا مَرْحَبًا ۢبِهِمْ ۗ اِنَّهُمْ صَالُوا النَّارِ', 'hāżā faujum muqtaḥimum ma\'akum, lā mar-ḥabam bihim, innahum ṣālun-nār'),
(4029, 38, 60, 'قَالُوْا بَلْ اَنْتُمْ لَا مَرْحَبًاۢ بِكُمْ ۗ اَنْتُمْ قَدَّمْتُمُوْهُ لَنَاۚ فَبِئْسَ الْقَرَارُ', 'qālụ bal antum lā mar-ḥabam bikum, antum qaddamtumụhu lanā, fa bi`sal-qarār'),
(4030, 38, 61, 'قَالُوْا رَبَّنَا مَنْ قَدَّمَ لَنَا هٰذَا فَزِدْهُ عَذَابًا ضِعْفًا فِى النَّارِ', 'qālụ rabbanā mang qaddama lanā hāżā fa zid-hu \'ażāban ḍi\'fan fin-nār'),
(4031, 38, 62, 'وَقَالُوْا مَا لَنَا لَا نَرٰى رِجَالًا كُنَّا نَعُدُّهُمْ مِّنَ الْاَشْرَارِ', 'wa qālụ mā lanā lā narā rijālang kunnā na\'udduhum minal-asyrār'),
(4032, 38, 63, 'اَتَّخَذْنٰهُمْ سِخْرِيًّا اَمْ زَاغَتْ عَنْهُمُ الْاَبْصَارُ', 'attakhażnāhum sikhriyyan am zāgat \'an-humul-abṣār'),
(4033, 38, 64, 'اِنَّ ذٰلِكَ لَحَقٌّ تَخَاصُمُ اَهْلِ النَّارِ', 'inna żālika laḥaqqun takhāṣumu ahlin-nār'),
(4034, 38, 65, 'قُلْ اِنَّمَآ اَنَا۠ مُنْذِرٌ ۖوَّمَا مِنْ اِلٰهٍ اِلَّا اللّٰهُ الْوَاحِدُ الْقَهَّارُ', 'qul innamā ana munżiruw wa mā min ilāhin illallāhul-wāḥidul-qahhār'),
(4035, 38, 66, 'رَبُّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا الْعَزِيْزُ الْغَفَّارُ', 'rabbus-samāwāti wal-arḍi wa mā bainahumal-\'azīzul-gaffār'),
(4036, 38, 67, 'قُلْ هُوَ نَبَؤٌا عَظِيْمٌۙ', 'qul huwa naba`un \'aẓīm'),
(4037, 38, 68, 'اَنْتُمْ عَنْهُ مُعْرِضُوْنَ', 'antum \'an-hu mu\'riḍụn'),
(4038, 38, 69, 'مَا كَانَ لِيَ مِنْ عِلْمٍۢ بِالْمَلَاِ الْاَعْلٰٓى اِذْ يَخْتَصِمُوْنَ', 'mā kāna liya min \'ilmim bil-mala`il-a\'lā iż yakhtaṣimụn'),
(4039, 38, 70, 'اِنْ يُّوْحٰىٓ اِلَيَّ اِلَّآ اَنَّمَآ اَنَا۠ نَذِيْرٌ مُّبِيْنٌ', 'iy yụḥā ilayya illā annamā ana nażīrum mubīn'),
(4040, 38, 71, 'اِذْ قَالَ رَبُّكَ لِلْمَلٰۤىِٕكَةِ اِنِّيْ خَالِقٌۢ بَشَرًا مِّنْ طِيْنٍ', 'iż qāla rabbuka lil-malā`ikati innī khāliqum basyaram min ṭīn'),
(4041, 38, 72, 'فَاِذَا سَوَّيْتُهٗ وَنَفَخْتُ فِيْهِ مِنْ رُّوْحِيْ فَقَعُوْا لَهٗ سٰجِدِيْنَ', 'fa iżā sawwaituhụ wa nafakhtu fīhi mir rụḥī faqa\'ụ lahụ sājidīn'),
(4042, 38, 73, 'فَسَجَدَ الْمَلٰۤىِٕكَةُ كُلُّهُمْ اَجْمَعُوْنَۙ', 'fa sajadal-malā`ikatu kulluhum ajma\'ụn'),
(4043, 38, 74, 'اِلَّآ اِبْلِيْسَۗ اِسْتَكْبَرَ وَكَانَ مِنَ الْكٰفِرِيْنَ', 'illā iblīs, istakbara wa kāna minal-kāfirīn'),
(4044, 38, 75, 'قَالَ يٰٓاِبْلِيْسُ مَا مَنَعَكَ اَنْ تَسْجُدَ لِمَا خَلَقْتُ بِيَدَيَّ ۗ اَسْتَكْبَرْتَ اَمْ كُنْتَ مِنَ الْعَالِيْنَ', 'qāla yā iblīsu mā mana\'aka an tasjuda limā khalaqtu biyadayy, astakbarta am kunta minal-\'ālīn'),
(4045, 38, 76, 'قَالَ اَنَا۠ خَيْرٌ مِّنْهُ خَلَقْتَنِيْ مِنْ نَّارٍ وَّخَلَقْتَهٗ مِنْ طِيْنٍ', 'qāla ana khairum min-hu khalaqtanī min nāriw wa khalaqtahụ min ṭīn'),
(4046, 38, 77, 'قَالَ فَاخْرُجْ مِنْهَا فَاِنَّكَ رَجِيْمٌۖ', 'qāla fakhruj min-hā fa innaka rajīm'),
(4047, 38, 78, 'وَّاِنَّ عَلَيْكَ لَعْنَتِيْٓ اِلٰى يَوْمِ الدِّيْنِ', 'wa inna \'alaika la\'natī ilā yaumid-dīn'),
(4048, 38, 79, 'قَالَ رَبِّ فَاَنْظِرْنِيْٓ اِلٰى يَوْمِ يُبْعَثُوْنَ', 'qāla rabbi fa anẓirnī ilā yaumi yub\'aṡụn'),
(4049, 38, 80, 'قَالَ فَاِنَّكَ مِنَ الْمُنْظَرِيْنَۙ', 'qāla fa innaka minal-munẓarīn'),
(4050, 38, 81, 'اِلٰى يَوْمِ الْوَقْتِ الْمَعْلُوْمِ', 'ilā yaumil-waqtil-ma\'lụm'),
(4051, 38, 82, 'قَالَ فَبِعِزَّتِكَ لَاُغْوِيَنَّهُمْ اَجْمَعِيْنَۙ', 'qāla fa bi\'izzatika la`ugwiyannahum ajma\'īn'),
(4052, 38, 83, 'اِلَّا عِبَادَكَ مِنْهُمُ الْمُخْلَصِيْنَ', 'illā \'ibādaka min-humul-mukhlaṣīn'),
(4053, 38, 84, 'قَالَ فَالْحَقُّۖ وَالْحَقَّ اَقُوْلُۚ', 'qāla fal-ḥaqqu wal-ḥaqqa aqụl'),
(4054, 38, 85, 'لَاَمْلَئَنَّ جَهَنَّمَ مِنْكَ وَمِمَّنْ تَبِعَكَ مِنْهُمْ اَجْمَعِيْنَ', 'la`amla`anna jahannama mingka wa mim man tabi\'aka min-hum ajma\'īn'),
(4055, 38, 86, 'قُلْ مَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍ وَّمَآ اَنَا۠ مِنَ الْمُتَكَلِّفِيْنَ', 'qul mā as`alukum \'alaihi min ajriw wa mā ana minal-mutakallifīn'),
(4056, 38, 87, 'اِنْ هُوَ اِلَّا ذِكْرٌ لِّلْعٰلَمِيْنَ', 'in huwa illā żikrul lil-\'ālamīn'),
(4057, 38, 88, 'وَلَتَعْلَمُنَّ نَبَاَهٗ بَعْدَ حِيْنٍ', 'wa lata\'lamunna naba`ahụ ba\'da ḥīn'),
(4058, 39, 1, ' تَنْزِيْلُ الْكِتٰبِ مِنَ اللّٰهِ الْعَزِيْزِ الْحَكِيْمِ', 'tanzīlul-kitābi minallāhil-\'azīzil-ḥakīm'),
(4059, 39, 2, 'اِنَّآ اَنْزَلْنَآ اِلَيْكَ الْكِتٰبَ بِالْحَقِّ فَاعْبُدِ اللّٰهَ مُخْلِصًا لَّهُ الدِّيْنَۗ', 'innā anzalnā ilaikal-kitāba bil-ḥaqqi fa\'budillāha mukhliṣal lahud-dīn'),
(4060, 39, 3, 'اَلَا لِلّٰهِ الدِّيْنُ الْخَالِصُ ۗوَالَّذِيْنَ اتَّخَذُوْا مِنْ دُوْنِهٖٓ اَوْلِيَاۤءَۘ مَا نَعْبُدُهُمْ اِلَّا لِيُقَرِّبُوْنَآ اِلَى اللّٰهِ زُلْفٰىۗ اِنَّ اللّٰهَ يَحْكُمُ بَيْنَهُمْ فِيْ مَا هُمْ فِيْهِ يَخْتَلِفُوْنَ ەۗ اِنَّ اللّٰهَ لَا يَهْدِيْ مَنْ هُوَ كٰذِبٌ كَفَّارٌ', 'alā lillāhid-dīnul-khāliṣ, wallażīnattakhażụ min dụnihī auliyā`, mā na\'buduhum illā liyuqarribụnā ilallāhi zulfā, innallāha yaḥkumu bainahum fī mā hum fīhi yakhtalifụn, innallāha lā yahdī man huwa kāżibung kaffār'),
(4061, 39, 4, 'لَوْ اَرَادَ اللّٰهُ اَنْ يَّتَّخِذَ وَلَدًا لَّاصْطَفٰى مِمَّا يَخْلُقُ مَا يَشَاۤءُ ۙ سُبْحٰنَهٗ ۗهُوَ اللّٰهُ الْوَاحِدُ الْقَهَّارُ', 'lau arādallāhu ay yattakhiża waladal laṣṭafā mimmā yakhluqu mā yasyā`u sub-ḥānah, huwallāhul-wāḥidul-qahhār'),
(4062, 39, 5, 'خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّۚ يُكَوِّرُ الَّيْلَ عَلَى النَّهَارِ وَيُكَوِّرُ النَّهَارَ عَلَى الَّيْلِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَۗ كُلٌّ يَّجْرِيْ لِاَجَلٍ مُّسَمًّىۗ اَلَا هُوَ الْعَزِيْزُ الْغَفَّارُ', 'khalaqas-samāwāti wal-arḍa bil-ḥaqq, yukawwirul-laila \'alan-nahāri wa yukawwirun-nahāra \'alal-laili wa sakhkharasy-syamsa wal-qamar, kulluy yajrī li`ajalim musammā, alā huwal-\'azīzul-gaffār'),
(4063, 39, 6, 'خَلَقَكُمْ مِّنْ نَّفْسٍ وَّاحِدَةٍ ثُمَّ جَعَلَ مِنْهَا زَوْجَهَا وَاَنْزَلَ لَكُمْ مِّنَ الْاَنْعَامِ ثَمٰنِيَةَ اَزْوَاجٍ ۗ يَخْلُقُكُمْ فِيْ بُطُوْنِ اُمَّهٰتِكُمْ خَلْقًا مِّنْۢ بَعْدِ خَلْقٍ فِيْ ظُلُمٰتٍ ثَلٰثٍۗ ذٰلِكُمُ اللّٰهُ رَبُّكُمْ لَهُ الْمُلْكُۗ لَآ اِلٰهَ اِلَّا هُوَۚ فَاَنّٰى تُصْرَفُوْنَ', 'khalaqakum min nafsiw wāḥidatin ṡumma ja\'ala min-hā zaujahā wa anzala lakum minal-an\'āmi ṡamāniyata azwāj, yakhluqukum fī buṭụni ummahātikum khalqam mim ba\'di khalqin fī ẓulumātin ṡalāṡ, żālikumullāhu rabbukum lahul-mulk, lā ilāha illā huw, fa annā tuṣrafụn'),
(4064, 39, 7, 'اِنْ تَكْفُرُوْا فَاِنَّ اللّٰهَ غَنِيٌّ عَنْكُمْ ۗوَلَا يَرْضٰى لِعِبَادِهِ الْكُفْرَۚ وَاِنْ تَشْكُرُوْا يَرْضَهُ لَكُمْۗ وَلَا تَزِرُ وَازِرَةٌ وِّزْرَ اُخْرٰىۗ ثُمَّ اِلٰى رَبِّكُمْ مَّرْجِعُكُمْ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَۗ اِنَّهٗ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ', 'in takfurụ fa innallāha ganiyyun \'angkum, wa lā yarḍā li\'ibādihil-kufr, wa in tasykurụ yarḍahu lakum, wa lā taziru wāziratuw wizra ukhrā, ṡumma ilā rabbikum marji\'ukum fa yunabbi`ukum bimā kuntum ta\'malụn, innahụ \'alīmum biżātiṣ-ṣudụr'),
(4065, 39, 8, 'وَاِذَا مَسَّ الْاِنْسَانَ ضُرٌّ دَعَا رَبَّهٗ مُنِيْبًا اِلَيْهِ ثُمَّ اِذَا خَوَّلَهٗ نِعْمَةً مِّنْهُ نَسِيَ مَا كَانَ يَدْعُوْٓا اِلَيْهِ مِنْ قَبْلُ وَجَعَلَ لِلّٰهِ اَنْدَادًا لِّيُضِلَّ عَنْ سَبِيْلِهٖ ۗ قُلْ تَمَتَّعْ بِكُفْرِكَ قَلِيْلًا ۖاِنَّكَ مِنْ اَصْحٰبِ النَّارِ', 'wa iżā massal-insāna ḍurrun da\'ā rabbahụ munīban ilaihi ṡumma iżā khawwalahụ ni\'matam min-hu nasiya mā kāna yad\'ū ilaihi ming qablu wa ja\'ala lillāhi andādal liyuḍilla \'an sabīlih, qul tamatta\' bikufrika qalīlan innaka min aṣ-ḥābin-nār'),
(4066, 39, 9, 'اَمَّنْ هُوَ قَانِتٌ اٰنَاۤءَ الَّيْلِ سَاجِدًا وَّقَاۤىِٕمًا يَّحْذَرُ الْاٰخِرَةَ وَيَرْجُوْا رَحْمَةَ رَبِّهٖۗ قُلْ هَلْ يَسْتَوِى الَّذِيْنَ يَعْلَمُوْنَ وَالَّذِيْنَ لَا يَعْلَمُوْنَ ۗ اِنَّمَا يَتَذَكَّرُ اُولُوا الْاَلْبَابِ', 'am man huwa qānitun ānā`al-laili sājidaw wa qā`imay yaḥżarul-ākhirata wa yarjụ raḥmata rabbih, qul hal yastawillażīna ya\'lamụna wallażīna lā ya\'lamụn, innamā yatażakkaru ulul-albāb'),
(4067, 39, 10, 'قُلْ يٰعِبَادِ الَّذِيْنَ اٰمَنُوا اتَّقُوْا رَبَّكُمْ ۗلِلَّذِيْنَ اَحْسَنُوْا فِيْ هٰذِهِ الدُّنْيَا حَسَنَةٌ ۗوَاَرْضُ اللّٰهِ وَاسِعَةٌ ۗاِنَّمَا يُوَفَّى الصّٰبِرُوْنَ اَجْرَهُمْ بِغَيْرِ حِسَابٍ', 'qul yā \'ibādillażīna āmanuttaqụ rabbakum, lillażīna aḥsanụ fī hāżihid-dun-yā ḥasanah, wa arḍullāhi wāsi\'ah, innamā yuwaffaṣ-ṣābirụna ajrahum bigairi ḥisāb'),
(4068, 39, 11, 'قُلْ اِنِّيْٓ اُمِرْتُ اَنْ اَعْبُدَ اللّٰهَ مُخْلِصًا لَّهُ الدِّيْنَ', 'qul innī umirtu an a\'budallāha mukhliṣal lahud-dīn'),
(4069, 39, 12, 'وَاُمِرْتُ لِاَنْ اَكُوْنَ اَوَّلَ الْمُسْلِمِيْنَ', 'wa umirtu li`an akụna awwalal-muslimīn'),
(4070, 39, 13, 'قُلْ اِنِّيْٓ اَخَافُ اِنْ عَصَيْتُ رَبِّيْ عَذَابَ يَوْمٍ عَظِيْمٍ', 'qul innī akhāfu in \'aṣaitu rabbī \'ażāba yaumin \'aẓīm'),
(4071, 39, 14, 'قُلِ اللّٰهَ اَعْبُدُ مُخْلِصًا لَّهٗ دِيْنِيْۚ', 'qulillāha a\'budu mukhliṣal lahụ dīnī'),
(4072, 39, 15, 'فَاعْبُدُوْا مَا شِئْتُمْ مِّنْ دُوْنِهٖۗ قُلْ اِنَّ الْخٰسِرِيْنَ الَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ وَاَهْلِيْهِمْ يَوْمَ الْقِيٰمَةِۗ اَلَا ذٰلِكَ هُوَ الْخُسْرَانُ الْمُبِيْنُ', 'fa\'budụ mā syi`tum min dụnih, qul innal-khāsirīnallażīna khasirū anfusahum wa ahlīhim yaumal-qiyāmah, alā żālika huwal-khusrānul-mubīn'),
(4073, 39, 16, 'لَهُمْ مِّنْ فَوْقِهِمْ ظُلَلٌ مِّنَ النَّارِ وَمِنْ تَحْتِهِمْ ظُلَلٌ ۗذٰلِكَ يُخَوِّفُ اللّٰهُ بِهٖ عِبَادَهٗ ۗيٰعِبَادِ فَاتَّقُوْنِ', 'lahum min fauqihim ẓulalum minan-nāri wa min taḥtihim ẓulal, żālika yukhawwifullāhu bihī \'ibādah, yā \'ibādi fattaqụn'),
(4074, 39, 17, 'وَالَّذِيْنَ اجْتَنَبُوا الطَّاغُوْتَ اَنْ يَّعْبُدُوْهَا وَاَنَابُوْٓا اِلَى اللّٰهِ لَهُمُ الْبُشْرٰىۚ فَبَشِّرْ عِبَادِۙ', 'wallażīnajtanabuṭ-ṭāgụta ay ya\'budụhā wa anābū ilallāhi lahumul-busyrā, fa basysyir \'ibād'),
(4075, 39, 18, 'الَّذِيْنَ يَسْتَمِعُوْنَ الْقَوْلَ فَيَتَّبِعُوْنَ اَحْسَنَهٗ ۗ اُولٰۤىِٕكَ الَّذِيْنَ هَدٰىهُمُ اللّٰهُ وَاُولٰۤىِٕكَ هُمْ اُولُوا الْاَلْبَابِ', 'allażīna yastami\'ụnal-qaula fa yattabi\'ụna aḥsanah, ulā`ikallażīna hadāhumullāhu wa ulā`ika hum ulul-albāb'),
(4076, 39, 19, 'اَفَمَنْ حَقَّ عَلَيْهِ كَلِمَةُ الْعَذَابِۗ اَفَاَنْتَ تُنْقِذُ مَنْ فِى النَّارِ ۚ', 'a fa man ḥaqqa \'alaihi kalimatul-\'ażāb, a fa anta tungqiżu man fin-nār'),
(4077, 39, 20, 'لٰكِنِ الَّذِيْنَ اتَّقَوْا رَبَّهُمْ لَهُمْ غُرَفٌ مِّنْ فَوْقِهَا غُرَفٌ مَّبْنِيَّةٌ ۙتَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ ەۗ وَعْدَ اللّٰهِ ۗ لَا يُخْلِفُ اللّٰهُ الْمِيْعَادَ', 'lākinillażīnattaqau rabbahum lahum gurafum min fauqihā gurafum mabniyyatun tajrī min taḥtihal-an-hār, wa\'dallāh, lā yukhlifullāhul-mī\'ād'),
(4078, 39, 21, 'اَلَمْ تَرَ اَنَّ اللّٰهَ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً فَسَلَكَهٗ يَنَابِيْعَ فِى الْاَرْضِ ثُمَّ يُخْرِجُ بِهٖ زَرْعًا مُّخْتَلِفًا اَلْوَانُهٗ ثُمَّ يَهِيْجُ فَتَرٰىهُ مُصْفَرًّا ثُمَّ يَجْعَلُهٗ حُطَامًا ۗاِنَّ فِيْ ذٰلِكَ لَذِكْرٰى لِاُولِى الْاَلْبَابِ', 'a lam tara annallāha anzala minas-samā`i mā`an fa salakahụ yanābī\'a fil-arḍi ṡumma yukhriju bihī zar\'am mukhtalifan alwānuhụ ṡumma yahīju fa tarāhu muṣfarran ṡumma yaj\'aluhụ huṭāmā, inna fī żālika lażikrā li`ulil-albāb'),
(4079, 39, 22, 'اَفَمَنْ شَرَحَ اللّٰهُ صَدْرَهٗ لِلْاِسْلَامِ فَهُوَ عَلٰى نُوْرٍ مِّنْ رَّبِّهٖ ۗفَوَيْلٌ لِّلْقٰسِيَةِ قُلُوْبُهُمْ مِّنْ ذِكْرِ اللّٰهِ ۗ اُولٰۤىِٕكَ فِيْ ضَلٰلٍ مُّبِيْنٍ', 'a fa man syaraḥallāhu ṣadrahụ lil-islāmi fa huwa \'alā nụrim mir rabbih, fa wailul lil-qāsiyati qulụbuhum min żikrillāh, ulā`ika fī ḍalālim mubīn'),
(4080, 39, 23, 'اَللّٰهُ نَزَّلَ اَحْسَنَ الْحَدِيْثِ كِتٰبًا مُّتَشَابِهًا مَّثَانِيَۙ تَقْشَعِرُّ مِنْهُ جُلُوْدُ الَّذِيْنَ يَخْشَوْنَ رَبَّهُمْ ۚ ثُمَّ تَلِيْنُ جُلُوْدُهُمْ وَقُلُوْبُهُمْ اِلٰى ذِكْرِ اللّٰهِ ۗذٰلِكَ هُدَى اللّٰهِ يَهْدِيْ بِهٖ مَنْ يَّشَاۤءُ ۗوَمَنْ يُّضْلِلِ اللّٰهُ فَمَا لَهٗ مِنْ هَادٍ', 'allāhu nazzala aḥsanal-ḥadīṡi kitābam mutasyābiham maṡāniya taqsya\'irru min-hu julụdullażīna yakhsyauna rabbahum, ṡumma talīnu julụduhum wa qulụbuhum ilā żikrillāh, żālika hudallāhi yahdī bihī may yasyā`, wa may yuḍlilillāhu fa mā lahụ min hād'),
(4081, 39, 24, 'اَفَمَنْ يَّتَّقِيْ بِوَجْهِهٖ سُوْۤءَ الْعَذَابِ يَوْمَ الْقِيٰمَةِ ۗوَقِيْلَ لِلظّٰلِمِيْنَ ذُوْقُوْا مَا كُنْتُمْ تَكْسِبُوْنَ', 'a fa may yattaqī biwaj-hihī sū`al-\'ażābi yaumal-qiyāmah, wa qīla liẓ-ẓālimīna żụqụ mā kuntum taksibụn'),
(4082, 39, 25, 'كَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْ فَاَتٰىهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُوْنَ', 'każżaballażīna ming qablihim fa atāhumul-\'ażābu min ḥaiṡu lā yasy\'urụn'),
(4083, 39, 26, 'فَاَذَاقَهُمُ اللّٰهُ الْخِزْيَ فِى الْحَيٰوةِ الدُّنْيَا ۚوَلَعَذَابُ الْاٰخِرَةِ اَكْبَرُ ۘ لَوْ كَانُوْا يَعْلَمُوْنَ', 'fa ażāqahumullāhul-khizya fil-ḥayātid-dun-yā, wa la\'ażābul-ākhirati akbar, lau kānụ ya\'lamụn'),
(4084, 39, 27, 'وَلَقَدْ ضَرَبْنَا لِلنَّاسِ فِيْ هٰذَا الْقُرْاٰنِ مِنْ كُلِّ مَثَلٍ لَّعَلَّهُمْ يَتَذَكَّرُوْنَۚ', 'wa laqad darabnā lin-nāsi fī hāżal-qur`āni ming kulli maṡalil la\'allahum yatażakkarụn'),
(4085, 39, 28, 'قُرْاٰنًا عَرَبِيًّا غَيْرَ ذِيْ عِوَجٍ لَّعَلَّهُمْ يَتَّقُوْنَ', 'qur`ānan \'arabiyyan gaira żī \'iwajil la\'allahum yattaqụn'),
(4086, 39, 29, 'ضَرَبَ اللّٰهُ مَثَلًا رَّجُلًا فِيْهِ شُرَكَاۤءُ مُتَشَاكِسُوْنَ وَرَجُلًا سَلَمًا لِّرَجُلٍ هَلْ يَسْتَوِيٰنِ مَثَلًا ۗ اَلْحَمْدُ لِلّٰهِ ۗبَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَ', 'ḍaraballāhu maṡalar rajulan fīhi syurakā`u mutasyākisụna wa rajulan salamal lirajulin hal yastawiyāni maṡalā, al-ḥamdu lillāh, bal akṡaruhum lā ya\'lamụn'),
(4087, 39, 30, 'اِنَّكَ مَيِّتٌ وَّاِنَّهُمْ مَّيِّتُوْنَ ۖ', 'innaka mayyituw wa innahum mayyitụn'),
(4088, 39, 31, 'ثُمَّ اِنَّكُمْ يَوْمَ الْقِيٰمَةِ عِنْدَ رَبِّكُمْ تَخْتَصِمُوْنَ ۔', 'ṡumma innakum yaumal-qiyāmati \'inda rabbikum takhtaṣimụn'),
(4089, 39, 32, '۞ فَمَنْ اَظْلَمُ مِمَّنْ كَذَبَ عَلَى اللّٰهِ وَكَذَّبَ بِالصِّدْقِ اِذْ جَاۤءَهٗۗ اَلَيْسَ فِيْ جَهَنَّمَ مَثْوًى لِّلْكٰفِرِيْنَ', 'fa man aẓlamu mim mang każaba \'alallāhi wa każżaba biṣ-ṣidqi iż jā`ah, a laisa fī jahannama maṡwal lil-kāfirīn'),
(4090, 39, 33, 'وَالَّذِيْ جَاۤءَ بِالصِّدْقِ وَصَدَّقَ بِهٖٓ اُولٰۤىِٕكَ هُمُ الْمُتَّقُوْنَ', 'wallażī jā`a biṣ-ṣidqi wa ṣaddaqa bihī ulā`ika humul-muttaqụn'),
(4091, 39, 34, 'لَهُمْ مَّا يَشَاۤءُوْنَ عِنْدَ رَبِّهِمْ ۗ ذٰلِكَ جَزٰۤؤُا الْمُحْسِنِيْنَۚ', 'lahum mā yasyā`ụna \'inda rabbihim, żālika jazā`ul-muḥsinīn'),
(4092, 39, 35, 'لِيُكَفِّرَ اللّٰهُ عَنْهُمْ اَسْوَاَ الَّذِيْ عَمِلُوْا وَيَجْزِيَهُمْ اَجْرَهُمْ بِاَحْسَنِ الَّذِيْ كَانُوْا يَعْمَلُوْنَ', 'liyukaffirallāhu \'an-hum aswa`allażī \'amilụ wa yajziyahum ajrahum bi-aḥsanillażī kānụ ya\'malụn'),
(4093, 39, 36, 'اَلَيْسَ اللّٰهُ بِكَافٍ عَبْدَهٗۗ وَيُخَوِّفُوْنَكَ بِالَّذِيْنَ مِنْ دُوْنِهٖۗ وَمَنْ يُّضْلِلِ اللّٰهُ فَمَا لَهٗ مِنْ هَادٍۚ', 'a laisallāhu bikāfin \'abdah, wa yukhawwifụnaka billażīna min dụnih, wa may yuḍlilillāhu fa mā lahụ min hād'),
(4094, 39, 37, 'وَمَنْ يَّهْدِ اللّٰهُ فَمَا لَهٗ مِنْ مُّضِلٍّ ۗ اَلَيْسَ اللّٰهُ بِعَزِيْزٍ ذِى انْتِقَامٍ', 'wa may yahdillāhu fa mā lahụ mim muḍill, a laisallāhu bi\'azīzin żintiqām'),
(4095, 39, 38, 'وَلَىِٕنْ سَاَلْتَهُمْ مَّنْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ لَيَقُوْلُنَّ اللّٰهُ ۗ قُلْ اَفَرَءَيْتُمْ مَّا تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ اِنْ اَرَادَنِيَ اللّٰهُ بِضُرٍّ هَلْ هُنَّ كٰشِفٰتُ ضُرِّهٖٓ اَوْ اَرَادَنِيْ بِرَحْمَةٍ هَلْ هُنَّ مُمْسِكٰتُ رَحْمَتِهٖۗ قُلْ حَسْبِيَ اللّٰهُ ۗعَلَيْهِ يَتَوَكَّلُ الْمُتَوَكِّلُوْنَ', 'wa la`in sa`altahum man khalaqas-samāwāti wal-arḍa layaqụlunnallāh, qul a fa ra`aitum mā tad\'ụna min dụnillāhi in arādaniyallāhu biḍurrin hal hunna kāsyifātu ḍurrihī au arādanī biraḥmatin hal hunna mumsikātu raḥmatih, qul ḥasbiyallāh, \'alaihi yatawakkalul-mutawakkilụn'),
(4096, 39, 39, 'قُلْ يٰقَوْمِ اعْمَلُوْا عَلٰى مَكَانَتِكُمْ اِنِّيْ عَامِلٌ ۚفَسَوْفَ تَعْلَمُوْنَۙ', 'qul yā qaumi\'malụ \'alā makānatikum innī \'āmil, fa saufa ta\'lamụn'),
(4097, 39, 40, 'مَنْ يَّأْتِيْهِ عَذَابٌ يُّخْزِيْهِ وَيَحِلُّ عَلَيْهِ عَذَابٌ مُّقِيْمٌ', 'may ya`tīhi \'ażābuy yukhzīhi wa yaḥillu \'alaihi \'ażābum muqīm'),
(4098, 39, 41, 'اِنَّآ اَنْزَلْنَا عَلَيْكَ الْكِتٰبَ لِلنَّاسِ بِالْحَقِّۚ فَمَنِ اهْتَدٰى فَلِنَفْسِهٖۚ وَمَنْ ضَلَّ فَاِنَّمَا يَضِلُّ عَلَيْهَا ۚوَمَآ اَنْتَ عَلَيْهِمْ بِوَكِيْلٍ', 'innā anzalnā \'alaikal-kitāba lin-nāsi bil-ḥaqq, fa manihtadā fa linafsih, wa man ḍalla fa innamā yaḍillu \'alaihā, wa mā anta \'alaihim biwakīl'),
(4099, 39, 42, 'اَللّٰهُ يَتَوَفَّى الْاَنْفُسَ حِيْنَ مَوْتِهَا وَالَّتِيْ لَمْ تَمُتْ فِيْ مَنَامِهَا ۚ فَيُمْسِكُ الَّتِي قَضٰى عَلَيْهَا الْمَوْتَ وَيُرْسِلُ الْاُخْرٰىٓ اِلٰٓى اَجَلٍ مُّسَمًّىۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّتَفَكَّرُوْنَ', 'allāhu yatawaffal-anfusa ḥīna mautihā wallatī lam tamut fī manāmihā, fa yumsikullatī qaḍā \'alaihal-mauta wa yursilul-ukhrā ilā ajalim musammā, inna fī żālika la`āyātil liqaumiy yatafakkarụn'),
(4100, 39, 43, 'اَمِ اتَّخَذُوْا مِنْ دُوْنِ اللّٰهِ شُفَعَاۤءَۗ قُلْ اَوَلَوْ كَانُوْا لَا يَمْلِكُوْنَ شَيْـًٔا وَّلَا يَعْقِلُوْنَ', 'amittakhażụ min dụnillāhi syufa\'ā`, qul a walau kānụ lā yamlikụna syai`aw wa lā ya\'qilụn'),
(4101, 39, 44, 'قُلْ لِّلّٰهِ الشَّفَاعَةُ جَمِيْعًا ۗ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ ثُمَّ اِلَيْهِ تُرْجَعُوْنَ', 'qul lillahisy-syafā\'atu jamī\'ā, lahụ mulkus-samāwāti wal-arḍ, ṡumma ilaihi turja\'ụn'),
(4102, 39, 45, 'وَاِذَا ذُكِرَ اللّٰهُ وَحْدَهُ اشْمَـَٔزَّتْ قُلُوْبُ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِۚ وَاِذَا ذُكِرَ الَّذِيْنَ مِنْ دُوْنِهٖٓ اِذَا هُمْ يَسْتَبْشِرُوْنَ', 'wa iżā żukirallāhu waḥdahusyma`azzat qulụbullażīna lā yu`minụna bil-ākhirah, wa iżā żukirallażīna min dụnihī iżā hum yastabsyirụn'),
(4103, 39, 46, 'قُلِ اللهم فَاطِرَ السَّمٰوٰتِ وَالْاَرْضِ عٰلِمَ الْغَيْبِ وَالشَّهَادَةِ اَنْتَ تَحْكُمُ بَيْنَ عِبَادِكَ فِيْ مَا كَانُوْا فِيْهِ يَخْتَلِفُوْنَ', 'qulillāhumma fāṭiras-samāwāti wal-arḍi \'ālimal-gaibi wasy-syahādati anta taḥkumu baina \'ibādika fī mā kānụ fīhi yakhtalifụn'),
(4104, 39, 47, 'وَلَوْ اَنَّ لِلَّذِيْنَ ظَلَمُوْا مَا فِى الْاَرْضِ جَمِيْعًا وَّمِثْلَهٗ مَعَهٗ لَافْتَدَوْا بِهٖ مِنْ سُوْۤءِ الْعَذَابِ يَوْمَ الْقِيٰمَةِۗ وَبَدَا لَهُمْ مِّنَ اللّٰهِ مَا لَمْ يَكُوْنُوْا يَحْتَسِبُوْنَ', 'walau anna lillażīna ẓalamụ mā fil-arḍi jamī\'aw wa miṡlahụ ma\'ahụ laftadau bihī min sū`il-\'ażābi yaumal-qiyāmah, wa badā lahum minallāhi mā lam yakụnụ yaḥtasibụn'),
(4105, 39, 48, 'وَبَدَا لَهُمْ سَيِّاٰتُ مَا كَسَبُوْا وَحَاقَ بِهِمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ', 'wa badā lahum sayyi`ātu mā kasabụ wa ḥāqa bihim mā kānụ bihī yastahzi`ụn'),
(4106, 39, 49, 'فَاِذَا مَسَّ الْاِنْسَانَ ضُرٌّ دَعَانَاۖ ثُمَّ اِذَا خَوَّلْنٰهُ نِعْمَةً مِّنَّاۙ قَالَ اِنَّمَآ اُوْتِيْتُهٗ عَلٰى عِلْمٍ ۗبَلْ هِيَ فِتْنَةٌ وَّلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ', 'fa iżā massal-insāna ḍurrun da\'ānā ṡumma iżā khawwalnāhu ni\'matam minnā qāla innamā ụtītuhụ \'alā \'ilm, bal hiya fitnatuw wa lākinna akṡarahum lā ya\'lamụn'),
(4107, 39, 50, 'قَدْ قَالَهَا الَّذِيْنَ مِنْ قَبْلِهِمْ فَمَآ اَغْنٰى عَنْهُمْ مَّا كَانُوْا يَكْسِبُوْنَ', 'qad qālahallażīna ming qablihim fa mā agnā \'an-hum mā kānụ yaksibụn'),
(4108, 39, 51, 'فَاَصَابَهُمْ سَيِّاٰتُ مَا كَسَبُوْا ۗوَالَّذِيْنَ ظَلَمُوْا مِنْ هٰٓؤُلَاۤءِ سَيُصِيْبُهُمْ سَيِّاٰتُ مَا كَسَبُوْا ۙوَمَا هُمْ بِمُعْجِزِيْنَ', 'fa aṣābahum sayyi`ātu mā kasabụ, wallażīna ẓalamụ min hā`ulā`i sayuṣībuhum sayyi`ātu mā kasabụ wa mā hum bimu\'jizīn'),
(4109, 39, 52, 'اَوَلَمْ يَعْلَمُوْٓا اَنَّ اللّٰهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ', 'a wa lam ya\'lamū annallāha yabsuṭur-rizqa limay yasyā`u wa yaqdir, inna fī żālika la`āyātil liqaumiy yu`minụn'),
(4110, 39, 53, '۞ قُلْ يٰعِبَادِيَ الَّذِيْنَ اَسْرَفُوْا عَلٰٓى اَنْفُسِهِمْ لَا تَقْنَطُوْا مِنْ رَّحْمَةِ اللّٰهِ ۗاِنَّ اللّٰهَ يَغْفِرُ الذُّنُوْبَ جَمِيْعًا ۗاِنَّهٗ هُوَ الْغَفُوْرُ الرَّحِيْمُ', 'qul yā \'ibādiyallażīna asrafụ \'alā anfusihim lā taqnaṭụ mir raḥmatillāh, innallāha yagfiruż-żunụba jamī\'ā, innahụ huwal-gafụrur-raḥīm'),
(4111, 39, 54, 'وَاَنِيْبُوْٓا اِلٰى رَبِّكُمْ وَاَسْلِمُوْا لَهٗ مِنْ قَبْلِ اَنْ يَّأْتِيَكُمُ الْعَذَابُ ثُمَّ لَا تُنْصَرُوْنَ', 'wa anībū ilā rabbikum wa aslimụ lahụ ming qabli ay ya`tiyakumul-\'ażābu ṡumma lā tunṣarụn'),
(4112, 39, 55, 'وَاتَّبِعُوْٓا اَحْسَنَ مَآ اُنْزِلَ اِلَيْكُمْ مِّنْ رَّبِّكُمْ مِّنْ قَبْلِ اَنْ يَّأْتِيَكُمُ الْعَذَابُ بَغْتَةً وَّاَنْتُمْ لَا تَشْعُرُوْنَ ۙ', 'wattabi\'ū aḥsana mā unzila ilaikum mir rabbikum ming qabli ay ya`tiyakumul-\'ażābu bagtataw wa antum lā tasy\'urụn'),
(4113, 39, 56, 'اَنْ تَقُوْلَ نَفْسٌ يّٰحَسْرَتٰى عَلٰى مَا فَرَّطْتُّ فِيْ جَنْۢبِ اللّٰهِ وَاِنْ كُنْتُ لَمِنَ السَّاخِرِيْنَۙ', 'an taqụla nafsuy yā ḥasratā \'alā mā farrattu fī jambillāhi wa ing kuntu laminas-sākhirīn'),
(4114, 39, 57, 'اَوْ تَقُوْلَ لَوْ اَنَّ اللّٰهَ هَدٰىنِيْ لَكُنْتُ مِنَ الْمُتَّقِيْنَ ۙ', 'au taqụla lau annallāha hadānī lakuntu minal-muttaqīn'),
(4115, 39, 58, 'اَوْ تَقُوْلَ حِيْنَ تَرَى الْعَذَابَ لَوْ اَنَّ لِيْ كَرَّةً فَاَكُوْنَ مِنَ الْمُحْسِنِيْنَ', 'au taqụla ḥīna taral-\'ażāba lau anna lī karratan fa akụna minal-muḥsinīn'),
(4116, 39, 59, 'بَلٰى قَدْ جَاۤءَتْكَ اٰيٰتِيْ فَكَذَّبْتَ بِهَا وَاسْتَكْبَرْتَ وَكُنْتَ مِنَ الْكٰفِرِيْنَ', 'balā qad jā`atka āyātī fa każżabta bihā wastakbarta wa kunta minal-kāfirīn'),
(4117, 39, 60, 'وَيَوْمَ الْقِيٰمَةِ تَرَى الَّذِيْنَ كَذَبُوْا عَلَى اللّٰهِ وُجُوْهُهُمْ مُّسْوَدَّةٌ ۗ اَلَيْسَ فِيْ جَهَنَّمَ مَثْوًى لِّلْمُتَكَبِّرِيْنَ', 'wa yaumal-qiyāmati tarallażīna każabụ \'alallāhi wujụhuhum muswaddah, a laisa fī jahannama maṡwal lil-mutakabbirīn'),
(4118, 39, 61, 'وَيُنَجِّى اللّٰهُ الَّذِيْنَ اتَّقَوْا بِمَفَازَتِهِمْۖ لَا يَمَسُّهُمُ السُّوْۤءُ وَلَا هُمْ يَحْزَنُوْنَ', 'wa yunajjillāhullażīnattaqau bimafāzatihim lā yamassuhumus-sū`u wa lā hum yaḥzanụn'),
(4119, 39, 62, 'اَللّٰهُ خَالِقُ كُلِّ شَيْءٍ ۙوَّهُوَ عَلٰى كُلِّ شَيْءٍ وَّكِيْلٌ', 'allāhu khāliqu kulli syai`iw wa huwa \'alā kulli syai`iw wakīl'),
(4120, 39, 63, 'لَهٗ مَقَالِيْدُ السَّمٰوٰتِ وَالْاَرْضِۗ وَالَّذِيْنَ كَفَرُوْا بِاٰيٰتِ اللّٰهِ اُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ', 'lahụ maqālīdus-samāwāti wal-arḍ, wallażīna kafarụ bi`āyātillāhi ulā`ika humul-khāsirụn'),
(4121, 39, 64, 'قُلْ اَفَغَيْرَ اللّٰهِ تَأْمُرُوْۤنِّيْٓ اَعْبُدُ اَيُّهَا الْجٰهِلُوْنَ', 'qul a fa gairallāhi ta`murūnnī a\'budu ayyuhal-jāhilụn'),
(4122, 39, 65, 'وَلَقَدْ اُوْحِيَ اِلَيْكَ وَاِلَى الَّذِيْنَ مِنْ قَبْلِكَۚ لَىِٕنْ اَشْرَكْتَ لَيَحْبَطَنَّ عَمَلُكَ وَلَتَكُوْنَنَّ مِنَ الْخٰسِرِيْنَ', 'wa laqad ụḥiya ilaika wa ilallażīna ming qablik, la`in asyrakta layaḥbaṭanna \'amaluka wa latakụnanna minal-khāsirīn'),
(4123, 39, 66, 'بَلِ اللّٰهَ فَاعْبُدْ وَكُنْ مِّنَ الشّٰكِرِيْنَ', 'balillāha fa\'bud wa kum minasy-syākirīn'),
(4124, 39, 67, 'وَمَا قَدَرُوا اللّٰهَ حَقَّ قَدْرِهٖۖ وَالْاَرْضُ جَمِيْعًا قَبْضَتُهٗ يَوْمَ الْقِيٰمَةِ وَالسَّمٰوٰتُ مَطْوِيّٰتٌۢ بِيَمِيْنِهٖ ۗسُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يُشْرِكُوْنَ', 'wa mā qadarullāha ḥaqqa qadrihī wal-arḍu jamī\'ang qabḍatuhụ yaumal-qiyāmati was-samāwātu maṭwiyyātum biyamīnih, sub-ḥānahụ wa ta\'ālā \'ammā yusyrikụn'),
(4125, 39, 68, 'وَنُفِخَ فِى الصُّوْرِ فَصَعِقَ مَنْ فِى السَّمٰوٰتِ وَمَنْ فِى الْاَرْضِ اِلَّا مَنْ شَاۤءَ اللّٰهُ ۗ ثُمَّ نُفِخَ فِيْهِ اُخْرٰى فَاِذَا هُمْ قِيَامٌ يَّنْظُرُوْنَ', 'wa nufikha fiṣ-ṣụri fa ṣa\'iqa man fis-samāwāti wa man fil-arḍi illā man syā`allāh, ṡumma nufikha fīhi ukhrā fa iżā hum qiyāmuy yanẓurụn'),
(4126, 39, 69, 'وَاَشْرَقَتِ الْاَرْضُ بِنُوْرِ رَبِّهَا وَوُضِعَ الْكِتٰبُ وَجِايْۤءَ بِالنَّبِيّٖنَ وَالشُّهَدَاۤءِ وَقُضِيَ بَيْنَهُمْ بِالْحَقِّ وَهُمْ لَا يُظْلَمُوْنَ', 'wa asyraqatil-arḍu binụri rabbihā wa wuḍi\'al-kitābu wajī`a bin-nabiyyīna wasy-syuhadā`i wa quḍiya bainahum bil-ḥaqqi wa hum lā yuẓlamụn'),
(4127, 39, 70, 'وَوُفِّيَتْ كُلُّ نَفْسٍ مَّا عَمِلَتْ وَهُوَ اَعْلَمُ بِمَا يَفْعَلُوْنَ', 'wa wuffiyat kullu nafsim mā \'amilat wa huwa a\'lamu bimā yaf\'alụn'),
(4128, 39, 71, 'وَسِيْقَ الَّذِيْنَ كَفَرُوْٓا اِلٰى جَهَنَّمَ زُمَرًا ۗحَتّٰىٓ اِذَا جَاۤءُوْهَا فُتِحَتْ اَبْوَابُهَا وَقَالَ لَهُمْ خَزَنَتُهَآ اَلَمْ يَأْتِكُمْ رُسُلٌ مِّنْكُمْ يَتْلُوْنَ عَلَيْكُمْ اٰيٰتِ رَبِّكُمْ وَيُنْذِرُوْنَكُمْ لِقَاۤءَ يَوْمِكُمْ هٰذَا ۗقَالُوْا بَلٰى وَلٰكِنْ حَقَّتْ كَلِمَةُ الْعَذَابِ عَلَى الْكٰفِرِيْنَ', 'wa sīqallażīna kafarū ilā jahannama zumarā, ḥattā iżā jā`ụhā futiḥat abwābuhā wa qāla lahum khazanatuhā a lam ya`tikum rusulum mingkum yatlụna \'alaikum āyāti rabbikum wa yunżirụnakum liqā`a yaumikum hāżā, qālụ balā wa lākin ḥaqqat kalimatul-\'ażābi \'alal-kāfirīn'),
(4129, 39, 72, 'قِيْلَ ادْخُلُوْٓا اَبْوَابَ جَهَنَّمَ خٰلِدِيْنَ فِيْهَا ۚفَبِئْسَ مَثْوَى الْمُتَكَبِّرِيْنَ', 'qīladkhulū abwāba jahannama khālidīna fīhā, fa bi`sa maṡwal-mutakabbirīn'),
(4130, 39, 73, 'وَسِيْقَ الَّذِيْنَ اتَّقَوْا رَبَّهُمْ اِلَى الْجَنَّةِ زُمَرًا ۗحَتّٰىٓ اِذَا جَاۤءُوْهَا وَفُتِحَتْ اَبْوَابُهَا وَقَالَ لَهُمْ خَزَنَتُهَا سَلٰمٌ عَلَيْكُمْ طِبْتُمْ فَادْخُلُوْهَا خٰلِدِيْنَ', 'wa sīqallażīnattaqau rabbahum ilal-jannati zumarā, ḥattā iżā jā`ụhā wa futiḥat abwābuhā wa qāla lahum khazanatuhā salāmun \'alaikum ṭibtum fadkhulụhā khālidīn');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(4131, 39, 74, 'وَقَالُوا الْحَمْدُ لِلّٰهِ الَّذِيْ صَدَقَنَا وَعْدَهٗ وَاَوْرَثَنَا الْاَرْضَ نَتَبَوَّاُ مِنَ الْجَنَّةِ حَيْثُ نَشَاۤءُ ۚفَنِعْمَ اَجْرُ الْعٰمِلِيْنَ', 'wa qālul-ḥamdu lillāhillażī ṣadaqanā wa\'dahụ wa auraṡanal-arḍa natabawwa`u minal-jannati ḥaiṡu nasyā`, fa ni\'ma ajrul-\'āmilīn'),
(4132, 39, 75, 'وَتَرَى الْمَلٰۤىِٕكَةَ حَاۤفِّيْنَ مِنْ حَوْلِ الْعَرْشِ يُسَبِّحُوْنَ بِحَمْدِ رَبِّهِمْۚ وَقُضِيَ بَيْنَهُمْ بِالْحَقِّ وَقِيْلَ الْحَمْدُ لِلّٰهِ رَبِّ الْعٰلَمِيْنَ', 'wa taral-malā`ikata ḥāffīna min ḥaulil-\'arsyi yusabbiḥụna biḥamdi rabbihim, wa quḍiya bainahum bil-ḥaqqi wa qīlal-ḥamdu lillāhi rabbil-\'ālamīn'),
(4133, 40, 1, 'حٰمۤ ۚ', 'ḥā mīm'),
(4134, 40, 2, 'تَنْزِيْلُ الْكِتٰبِ مِنَ اللّٰهِ الْعَزِيْزِ الْعَلِيْمِۙ', 'tanzīlul-kitābi minallāhil-\'azīzil-\'alīm'),
(4135, 40, 3, 'غَافِرِ الذَّنْۢبِ وَقَابِلِ التَّوْبِ شَدِيْدِ الْعِقَابِ ذِى الطَّوْلِۗ لَآ اِلٰهَ اِلَّا هُوَ ۗاِلَيْهِ الْمَصِيْرُ', 'gāfiriż-żambi wa qābilit-taubi syadīdil-\'iqābi żiṭ-ṭaụl, lā ilāha illā huw, ilaihil-maṣīr'),
(4136, 40, 4, 'مَا يُجَادِلُ فِيْٓ اٰيٰتِ اللّٰهِ اِلَّا الَّذِيْنَ كَفَرُوْا فَلَا يَغْرُرْكَ تَقَلُّبُهُمْ فِى الْبِلَادِ', 'mā yujādilu fī āyātillāhi illallażīna kafarụ fa lā yagrurka taqallubuhum fil-bilād'),
(4137, 40, 5, 'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَّالْاَحْزَابُ مِنْۢ بَعْدِهِمْ ۖوَهَمَّتْ كُلُّ اُمَّةٍۢ بِرَسُوْلِهِمْ لِيَأْخُذُوْهُ وَجَادَلُوْا بِالْبَاطِلِ لِيُدْحِضُوا بِهِ الْحَقَّ فَاَخَذْتُهُمْ ۗفَكَيْفَ كَانَ عِقَابِ', 'każżabat qablahum qaumu nụḥiw wal-aḥzābu mim ba\'dihim wa hammat kullu ummatim birasụlihim liya`khużụhu wa jādalụ bil-bāṭili liyud-ḥiḍụ bihil-ḥaqqa fa akhażtuhum, fa kaifa kāna \'iqāb'),
(4138, 40, 6, 'وَكَذٰلِكَ حَقَّتْ كَلِمَتُ رَبِّكَ عَلَى الَّذِيْنَ كَفَرُوْٓا اَنَّهُمْ اَصْحٰبُ النَّارِۘ', 'wa każālika ḥaqqat kalimatu rabbika \'alallażīna kafarū annahum aṣ-ḥābun-nār'),
(4139, 40, 7, 'اَلَّذِيْنَ يَحْمِلُوْنَ الْعَرْشَ وَمَنْ حَوْلَهٗ يُسَبِّحُوْنَ بِحَمْدِ رَبِّهِمْ وَيُؤْمِنُوْنَ بِهٖ وَيَسْتَغْفِرُوْنَ لِلَّذِيْنَ اٰمَنُوْاۚ رَبَّنَا وَسِعْتَ كُلَّ شَيْءٍ رَّحْمَةً وَّعِلْمًا فَاغْفِرْ لِلَّذِيْنَ تَابُوْا وَاتَّبَعُوْا سَبِيْلَكَ وَقِهِمْ عَذَابَ الْجَحِيْمِ', 'allażīna yaḥmilụnal-\'arsya wa man ḥaulahụ yusabbiḥụna biḥamdi rabbihim wa yu`minụna bihī wa yastagfirụna lillażīna āmanụ, rabbanā wasi\'ta kulla syai`ir raḥmataw wa \'ilman fagfir lillażīna tābụ wattaba\'ụ sabīlaka wa qihim \'ażābal-jaḥīm'),
(4140, 40, 8, 'رَبَّنَا وَاَدْخِلْهُمْ جَنّٰتِ عَدْنِ ِۨالَّتِيْ وَعَدْتَّهُمْ وَمَنْ صَلَحَ مِنْ اٰبَاۤىِٕهِمْ وَاَزْوَاجِهِمْ وَذُرِّيّٰتِهِمْ ۗاِنَّكَ اَنْتَ الْعَزِيْزُ الْحَكِيْمُۙ', 'rabbanā wa adkhil-hum jannāti \'adninillatī wa\'attahum wa man ṣalaḥa min ābā`ihim wa azwājihim wa żurriyyātihim, innaka antal-\'azīzul-ḥakīm'),
(4141, 40, 9, 'وَقِهِمُ السَّيِّاٰتِۗ وَمَنْ تَقِ السَّيِّاٰتِ يَوْمَىِٕذٍ فَقَدْ رَحِمْتَهٗ ۗوَذٰلِكَ هُوَ الْفَوْزُ الْعَظِيْمُ', 'wa qihimus-sayyi`āt, wa man taqis-sayyi`āti yauma`iżin fa qad raḥimtah, wa żālika huwal-fauzul-\'aẓīm'),
(4142, 40, 10, 'اِنَّ الَّذِيْنَ كَفَرُوْا يُنَادَوْنَ لَمَقْتُ اللّٰهِ اَكْبَرُ مِنْ مَّقْتِكُمْ اَنْفُسَكُمْ اِذْ تُدْعَوْنَ اِلَى الْاِيْمَانِ فَتَكْفُرُوْنَ', 'innallażīna kafarụ yunādauna lamaqtullāhi akbaru mim maqtikum anfusakum iż tud\'auna ilal-īmāni fa takfurụn'),
(4143, 40, 11, 'قَالُوْا رَبَّنَآ اَمَتَّنَا اثْنَتَيْنِ وَاَحْيَيْتَنَا اثْنَتَيْنِ فَاعْتَرَفْنَا بِذُنُوْبِنَا فَهَلْ اِلٰى خُرُوْجٍ مِّنْ سَبِيْلٍ', 'qālụ rabbanā amattanaṡnataini wa aḥyaitanaṡnataini fa\'tarafnā biżunụbinā fa hal ilā khurụjim min sabīl'),
(4144, 40, 12, 'ذٰلِكُمْ بِاَنَّهٗٓ اِذَا دُعِيَ اللّٰهُ وَحْدَهٗ كَفَرْتُمْۚ وَاِنْ يُّشْرَكْ بِهٖ تُؤْمِنُوْا ۗفَالْحُكْمُ لِلّٰهِ الْعَلِيِّ الْكَبِيْرِ', 'żālikum bi`annahū iżā du\'iyallāhu waḥdahụ kafartum, wa iy yusyrak bihī tu`minụ, fal-ḥukmu lillāhil-\'aliyyil-kabīr'),
(4145, 40, 13, 'هُوَ الَّذِيْ يُرِيْكُمْ اٰيٰتِهٖ وَيُنَزِّلُ لَكُمْ مِّنَ السَّمَاۤءِ رِزْقًا ۗوَمَا يَتَذَكَّرُ اِلَّا مَنْ يُّنِيْبُ', 'huwallażī yurīkum āyātihī wa yunazzilu lakum minas-samā`i rizqā, wa mā yatażakkaru illā may yunīb'),
(4146, 40, 14, 'فَادْعُوا اللّٰهَ مُخْلِصِيْنَ لَهُ الدِّيْنَ وَلَوْ كَرِهَ الْكٰفِرُوْنَ', 'fad\'ullāha mukhliṣīna lahud-dīna walau karihal-kāfirụn'),
(4147, 40, 15, 'رَفِيْعُ الدَّرَجٰتِ ذُو الْعَرْشِۚ يُلْقِى الرُّوْحَ مِنْ اَمْرِهٖ عَلٰى مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ لِيُنْذِرَ يَوْمَ التَّلَاقِۙ', 'rafī\'ud-darajāti żul-\'arsy, yulqir-rụḥa min amrihī \'alā may yasyā`u min \'ibādihī liyunżira yaumat-talāq'),
(4148, 40, 16, 'يَوْمَ هُمْ بَارِزُوْنَ ۚ لَا يَخْفٰى عَلَى اللّٰهِ مِنْهُمْ شَيْءٌ ۗلِمَنِ الْمُلْكُ الْيَوْمَ ۗ لِلّٰهِ الْوَاحِدِ الْقَهَّارِ', 'yauma hum bārizụn, lā yakhfā \'alallāhi min-hum syaī`, limanil-mulkul-yaụm, lillāhil-wāḥidil-qahhār'),
(4149, 40, 17, 'اَلْيَوْمَ تُجْزٰى كُلُّ نَفْسٍۢ بِمَا كَسَبَتْ ۗ لَا ظُلْمَ الْيَوْمَ ۗاِنَّ اللّٰهَ سَرِيْعُ الْحِسَابِ', 'al-yauma tujzā kullu nafsim bimā kasabat, lā ẓulmal-yaụm, innallāha sarī\'ul-ḥisāb'),
(4150, 40, 18, 'وَاَنْذِرْهُمْ يَوْمَ الْاٰزِفَةِ اِذِ الْقُلُوْبُ لَدَى الْحَنَاجِرِ كَاظِمِيْنَ ەۗ مَا لِلظّٰلِمِيْنَ مِنْ حَمِيْمٍ وَّلَا شَفِيْعٍ يُّطَاعُۗ', 'wa anżir-hum yaumal-āzifati iżil-qulụbu ladal-ḥanājiri kāẓimīn, mā liẓ-ẓālimīna min ḥamīmiw wa lā syafī\'iy yuṭā\''),
(4151, 40, 19, 'يَعْلَمُ خَاۤىِٕنَةَ الْاَعْيُنِ وَمَا تُخْفِى الصُّدُوْرُ', 'ya\'lamu khā`inatal-a\'yuni wa mā tukhfiṣ-ṣudụr'),
(4152, 40, 20, 'وَاللّٰهُ يَقْضِيْ بِالْحَقِّ ۗوَالَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِهٖ لَا يَقْضُوْنَ بِشَيْءٍ ۗاِنَّ اللّٰهَ هُوَ السَّمِيْعُ الْبَصِيْرُ', 'wallāhu yaqḍī bil-ḥaqq, wallażīna yad\'ụna min dụnihī lā yaqḍụna bisyaī`, innallāha huwas-samī\'ul-baṣīr'),
(4153, 40, 21, '۞ اَوَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَيَنْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ كَانُوْا مِنْ قَبْلِهِمْ ۗ كَانُوْا هُمْ اَشَدَّ مِنْهُمْ قُوَّةً وَّاٰثَارًا فِى الْاَرْضِ فَاَخَذَهُمُ اللّٰهُ بِذُنُوْبِهِمْ ۗوَمَا كَانَ لَهُمْ مِّنَ اللّٰهِ مِنْ وَّاقٍ', 'a wa lam yasīrụ fil-arḍi fa yanẓurụ kaifa kāna \'āqibatullażīna kānụ ming qablihim, kānụ hum asyadda min-hum quwwataw wa āṡāran fil-arḍi fa akhażahumullāhu biżunụbihim, wa mā kāna lahum minallāhi miw wāq'),
(4154, 40, 22, 'ذٰلِكَ بِاَنَّهُمْ كَانَتْ تَّأْتِيْهِمْ رُسُلُهُمْ بِالْبَيِّنٰتِ فَكَفَرُوْا فَاَخَذَهُمُ اللّٰهُ ۗاِنَّهٗ قَوِيٌّ شَدِيْدُ الْعِقَابِ', 'żālika bi`annahum kānat ta`tīhim rusuluhum bil-bayyināti fa kafarụ fa akhażahumullāh, innahụ qawiyyun syadīdul-\'iqāb'),
(4155, 40, 23, 'وَلَقَدْ اَرْسَلْنَا مُوسٰى بِاٰيٰتِنَا وَسُلْطٰنٍ مُّبِيْنٍۙ', 'wa laqad arsalnā mụsā bi`āyātinā wa sulṭānim mubīn'),
(4156, 40, 24, 'اِلٰى فِرْعَوْنَ وَهَامٰنَ وَقَارُوْنَ فَقَالُوْا سٰحِرٌ كَذَّابٌ', 'ilā fir\'auna wa hāmāna wa qārụna fa qālụ sāḥirung każżāb'),
(4157, 40, 25, 'فَلَمَّا جَاۤءَهُمْ بِالْحَقِّ مِنْ عِنْدِنَا قَالُوا اقْتُلُوْٓا اَبْنَاۤءَ الَّذِيْنَ اٰمَنُوْا مَعَهٗ وَاسْتَحْيُوْا نِسَاۤءَهُمْ ۗوَمَا كَيْدُ الْكٰفِرِيْنَ اِلَّا فِيْ ضَلٰلٍ', 'fa lammā jā`ahum bil-ḥaqqi min \'indinā qāluqtulū abnā`allażīna āmanụ ma\'ahụ wastaḥyụ nisā`ahum, wa mā kaidul-kāfirīna illā fī ḍalāl'),
(4158, 40, 26, 'وَقَالَ فِرْعَوْنُ ذَرُوْنِيْٓ اَقْتُلْ مُوْسٰى وَلْيَدْعُ رَبَّهٗ ۚاِنِّيْٓ اَخَافُ اَنْ يُّبَدِّلَ دِيْنَكُمْ اَوْ اَنْ يُّظْهِرَ فِى الْاَرْضِ الْفَسَادَ', 'wa qāla fir\'aunu żarụnī aqtul mụsā walyad\'u rabbah, innī akhāfu ay yubaddila dīnakum au ay yuẓ-hira fil-arḍil-fasād'),
(4159, 40, 27, 'وَقَالَ مُوْسٰىٓ اِنِّيْ عُذْتُ بِرَبِّيْ وَرَبِّكُمْ مِّنْ كُلِّ مُتَكَبِّرٍ لَّا يُؤْمِنُ بِيَوْمِ الْحِسَابِ', 'wa qāla mụsā innī \'użtu birabbī wa rabbikum ming kulli mutakabbiril lā yu`minu biyaumil-ḥisāb'),
(4160, 40, 28, 'وَقَالَ رَجُلٌ مُّؤْمِنٌۖ مِّنْ اٰلِ فِرْعَوْنَ يَكْتُمُ اِيْمَانَهٗٓ اَتَقْتُلُوْنَ رَجُلًا اَنْ يَّقُوْلَ رَبِّيَ اللّٰهُ وَقَدْ جَاۤءَكُمْ بِالْبَيِّنٰتِ مِنْ رَّبِّكُمْ ۗوَاِنْ يَّكُ كَاذِبًا فَعَلَيْهِ كَذِبُهٗ ۚوَاِنْ يَّكُ صَادِقًا يُّصِبْكُمْ بَعْضُ الَّذِيْ يَعِدُكُمْ ۗاِنَّ اللّٰهَ لَا يَهْدِيْ مَنْ هُوَ مُسْرِفٌ كَذَّابٌ', 'wa qāla rajulum mu`minum min āli fir\'auna yaktumu īmānahū a taqtulụna rajulan ay yaqụla rabbiyallāhu wa qad jā`akum bil-bayyināti mir rabbikum, wa iy yaku kāżiban fa \'alaihi każibuh, wa iy yaku ṣādiqay yuṣibkum ba\'ḍullażī ya\'idukum, innallāha lā yahdī man huwa musrifung każżāb'),
(4161, 40, 29, 'يٰقَوْمِ لَكُمُ الْمُلْكُ الْيَوْمَ ظَاهِرِيْنَ فِى الْاَرْضِۖ فَمَنْ يَّنْصُرُنَا مِنْۢ بَأْسِ اللّٰهِ اِنْ جَاۤءَنَا ۗقَالَ فِرْعَوْنُ مَآ اُرِيْكُمْ اِلَّا مَآ اَرٰى وَمَآ اَهْدِيْكُمْ اِلَّا سَبِيْلَ الرَّشَادِ', 'yā qaumi lakumul-mulkul-yauma ẓāhirīna fil-arḍi fa may yanṣurunā mim ba`sillāhi in jā`anā, qāla fir\'aunu mā urīkum illā mā arā wa mā ahdīkum illā sabīlar-rasyād'),
(4162, 40, 30, 'وَقَالَ الَّذِيْٓ اٰمَنَ يٰقَوْمِ اِنِّيْٓ اَخَافُ عَلَيْكُمْ مِّثْلَ يَوْمِ الْاَحْزَابِۙ', 'wa qālallażī āmana yā qaumi innī akhāfu \'alaikum miṡla yaumil-aḥzāb'),
(4163, 40, 31, 'مِثْلَ دَأْبِ قَوْمِ نُوْحٍ وَّعَادٍ وَّثَمُوْدَ وَالَّذِيْنَ مِنْۢ بَعْدِهِمْ ۗوَمَا اللّٰهُ يُرِيْدُ ظُلْمًا لِّلْعِبَادِ', 'miṡla da`bi qaumi nụḥiw wa \'ādiw wa ṡamụda wallażīna mim ba\'dihim, wa mallāhu yurīdu ẓulmal lil-\'ibād'),
(4164, 40, 32, 'وَيٰقَوْمِ اِنِّيْٓ اَخَافُ عَلَيْكُمْ يَوْمَ التَّنَادِۙ', 'wa yā qaumi innī akhāfu \'alaikum yaumat-tanād'),
(4165, 40, 33, 'يَوْمَ تُوَلُّوْنَ مُدْبِرِيْنَۚ مَا لَكُمْ مِّنَ اللّٰهِ مِنْ عَاصِمٍۚ وَمَنْ يُّضْلِلِ اللّٰهُ فَمَا لَهٗ مِنْ هَادٍ', 'yauma tuwallụna mudbirīn, mā lakum minallāhi min \'āṣim, wa may yuḍlilillāhu fa mā lahụ min hād'),
(4166, 40, 34, 'وَلَقَدْ جَاۤءَكُمْ يُوْسُفُ مِنْ قَبْلُ بِالْبَيِّنٰتِ فَمَا زِلْتُمْ فِيْ شَكٍّ مِّمَّا جَاۤءَكُمْ بِهٖ ۗحَتّٰىٓ اِذَا هَلَكَ قُلْتُمْ لَنْ يَّبْعَثَ اللّٰهُ مِنْۢ بَعْدِهٖ رَسُوْلًا ۗ كَذٰلِكَ يُضِلُّ اللّٰهُ مَنْ هُوَ مُسْرِفٌ مُّرْتَابٌۙ', 'wa laqad jā`akum yụsufu ming qablu bil-bayyināti fa mā ziltum fī syakkim mimmā jā`akum bih, hattā iżā halaka qultum lay yab\'aṡallāhu mim ba\'dihī rasụlā, każālika yuḍillullāhu man huwa musrifum murtāb,'),
(4167, 40, 35, ' ۨالَّذِيْنَ يُجَادِلُوْنَ فِيْٓ اٰيٰتِ اللّٰهِ بِغَيْرِ سُلْطٰنٍ اَتٰىهُمْۗ كَبُرَ مَقْتًا عِنْدَ اللّٰهِ وَعِنْدَ الَّذِيْنَ اٰمَنُوْا ۗ كَذٰلِكَ يَطْبَعُ اللّٰهُ عَلٰى كُلِّ قَلْبِ مُتَكَبِّرٍ جَبَّارٍ', 'allażīna yujādilụna fī āyātillāhi bigairi sulṭānin atāhum, kabura maqtan \'indallāhi wa \'indallażīna āmanụ, każālika yaṭba\'ullāhu \'alā kulli qalbi mutakabbirin jabbār'),
(4168, 40, 36, 'وَقَالَ فِرْعَوْنُ يٰهَامٰنُ ابْنِ لِيْ صَرْحًا لَّعَلِّيْٓ اَبْلُغُ الْاَسْبَابَۙ', 'wa qāla fir\'aunu yā hāmānubni lī ṣar-ḥal la\'allī ablugul-asbāb'),
(4169, 40, 37, 'اَسْبَابَ السَّمٰوٰتِ فَاَطَّلِعَ اِلٰٓى اِلٰهِ مُوْسٰى وَاِنِّيْ لَاَظُنُّهٗ كَاذِبًا ۗوَكَذٰلِكَ زُيِّنَ لِفِرْعَوْنَ سُوْۤءُ عَمَلِهٖ وَصُدَّ عَنِ السَّبِيْلِ ۗوَمَا كَيْدُ فِرْعَوْنَ اِلَّا فِيْ تَبَابٍ', 'asbābas-samāwāti fa aṭṭali\'a ilā ilāhi mụsā wa innī la`aẓunnuhụ kāżibā, wa każālika zuyyina lifir\'auna sū`u \'amalihī wa ṣudda \'anis-sabīl, wa mā kaidu fir\'auna illā fī tabāb'),
(4170, 40, 38, 'وَقَالَ الَّذِيْٓ اٰمَنَ يٰقَوْمِ اتَّبِعُوْنِ اَهْدِكُمْ سَبِيْلَ الرَّشَادِۚ', 'wa qālallażī āmana yā qaumittabi\'ụni ahdikum sabīlar-rasyād'),
(4171, 40, 39, 'يٰقَوْمِ اِنَّمَا هٰذِهِ الْحَيٰوةُ الدُّنْيَا مَتَاعٌ ۖوَّاِنَّ الْاٰخِرَةَ هِيَ دَارُ الْقَرَارِ', 'yā qaumi innamā hāżihil-ḥayātud-dun-yā matā\'uw wa innal-ākhirata hiya dārul-qarār'),
(4172, 40, 40, 'مَنْ عَمِلَ سَيِّئَةً فَلَا يُجْزٰىٓ اِلَّا مِثْلَهَاۚ وَمَنْ عَمِلَ صَالِحًا مِّنْ ذَكَرٍ اَوْ اُنْثٰى وَهُوَ مُؤْمِنٌ فَاُولٰۤىِٕكَ يَدْخُلُوْنَ الْجَنَّةَ يُرْزَقُوْنَ فِيْهَا بِغَيْرِ حِسَابٍ', 'man \'amila sayyi`atan fa lā yujzā illā miṡlahā, wa man \'amila ṣāliḥam min żakarin au unṡā wa huwa mu`minun fa ulā`ika yadkhulụnal-jannata yurzaqụna fīhā bigairi ḥisāb'),
(4173, 40, 41, '۞ وَيٰقَوْمِ مَا لِيْٓ اَدْعُوْكُمْ اِلَى النَّجٰوةِ وَتَدْعُوْنَنِيْٓ اِلَى النَّارِۗ', 'wa yā qaumi mā lī ad\'ụkum ilan-najāti wa tad\'ụnanī ilan-nār'),
(4174, 40, 42, 'تَدْعُوْنَنِيْ لِاَكْفُرَ بِاللّٰهِ وَاُشْرِكَ بِهٖ مَا لَيْسَ لِيْ بِهٖ عِلْمٌ وَّاَنَا۠ اَدْعُوْكُمْ اِلَى الْعَزِيْزِ الْغَفَّارِ', 'tad\'ụnanī li`akfura billāhi wa usyrika bihī mā laisa lī bihī \'ilmuw wa ana ad\'ụkum ilal-\'azīzil-gaffār'),
(4175, 40, 43, 'لَا جَرَمَ اَنَّمَا تَدْعُوْنَنِيْٓ اِلَيْهِ لَيْسَ لَهٗ دَعْوَةٌ فِى الدُّنْيَا وَلَا فِى الْاٰخِرَةِ وَاَنَّ مَرَدَّنَآ اِلَى اللّٰهِ وَاَنَّ الْمُسْرِفِيْنَ هُمْ اَصْحٰبُ النَّارِ', 'lā jarama annamā tad\'ụnanī ilaihi laisa lahụ da\'watun fid-dun-yā wa lā fil-ākhirati wa anna maraddanā ilallāhi wa annal-musrifīna hum aṣ-ḥābun-nār'),
(4176, 40, 44, 'فَسَتَذْكُرُوْنَ مَآ اَقُوْلُ لَكُمْۗ وَاُفَوِّضُ اَمْرِيْٓ اِلَى اللّٰهِ ۗاِنَّ اللّٰهَ بَصِيْرٌ ۢبِالْعِبَادِ', 'fasatażkurụna mā aqụlu lakum, wa ufawwiḍu amrī ilallāh, innallāha baṣīrum bil-\'ibād'),
(4177, 40, 45, 'فَوَقٰىهُ اللّٰهُ سَيِّاٰتِ مَا مَكَرُوْا وَحَاقَ بِاٰلِ فِرْعَوْنَ سُوْۤءُ الْعَذَابِۚ', 'fa waqāhullāhu sayyi`āti mā makarụ wa ḥāqa bi`āli fir\'auna sū`ul-\'ażāb'),
(4178, 40, 46, 'اَلنَّارُ يُعْرَضُوْنَ عَلَيْهَا غُدُوًّا وَّعَشِيًّا ۚوَيَوْمَ تَقُوْمُ السَّاعَةُ ۗ اَدْخِلُوْٓا اٰلَ فِرْعَوْنَ اَشَدَّ الْعَذَابِ', 'an-nāru yu\'raḍụna \'alaihā guduwwaw wa \'asyiyyā, wa yauma taqụmus-sā\'ah, adkhilū āla fir\'auna asyaddal-\'ażāb'),
(4179, 40, 47, 'وَاِذْ يَتَحَاۤجُّوْنَ فِى النَّارِ فَيَقُوْلُ الضُّعَفٰۤؤُ لِلَّذِيْنَ اسْتَكْبَرُوْٓا اِنَّا كُنَّا لَكُمْ تَبَعًا فَهَلْ اَنْتُمْ مُّغْنُوْنَ عَنَّا نَصِيْبًا مِّنَ النَّارِ', 'wa iż yataḥājjụna fin-nāri fa yaqụlud-du\'afā`u lillażīnastakbarū innā kunnā lakum taba\'an fa hal antum mugnụna \'annā naṣībam minan-nār'),
(4180, 40, 48, 'قَالَ الَّذِيْنَ اسْتَكْبَرُوْٓا اِنَّا كُلٌّ فِيْهَآ اِنَّ اللّٰهَ قَدْ حَكَمَ بَيْنَ الْعِبَادِ', 'qālallażīnastakbarū innā kullun fīhā innallāha qad ḥakama bainal-\'ibād'),
(4181, 40, 49, 'وَقَالَ الَّذِيْنَ فِى النَّارِ لِخَزَنَةِ جَهَنَّمَ ادْعُوْا رَبَّكُمْ يُخَفِّفْ عَنَّا يَوْمًا مِّنَ الْعَذَابِ', 'wa qālallażīna fin-nāri likhazanati jahannamad\'ụ rabbakum yukhaffif \'annā yaumam minal-\'ażāb'),
(4182, 40, 50, 'قَالُوْٓا اَوَلَمْ تَكُ تَأْتِيْكُمْ رُسُلُكُمْ بِالْبَيِّنٰتِ ۗقَالُوْا بَلٰىۗ قَالُوْا فَادْعُوْا ۚوَمَا دُعٰۤؤُا الْكٰفِرِيْنَ اِلَّا فِيْ ضَلٰلٍ', 'qālū a wa lam taku ta`tīkum rusulukum bil-bayyināt, qālụ balā, qālụ fad\'ụ, wa mā du\'ā`ul-kāfirīna illā fī ḍalāl'),
(4183, 40, 51, 'اِنَّا لَنَنْصُرُ رُسُلَنَا وَالَّذِيْنَ اٰمَنُوْا فِى الْحَيٰوةِ الدُّنْيَا وَيَوْمَ يَقُوْمُ الْاَشْهَادُۙ', 'innā lananṣuru rusulanā wallażīna āmanụ fil-ḥayātid-dun-yā wa yauma yaqụmul-asy-hād'),
(4184, 40, 52, 'يَوْمَ لَا يَنْفَعُ الظّٰلِمِيْنَ مَعْذِرَتُهُمْ وَلَهُمُ اللَّعْنَةُ وَلَهُمْ سُوْۤءُ الدَّارِ', 'yauma lā yanfa\'uẓ-ẓālimīna ma\'żiratuhum wa lahumul-la\'natu wa lahum sū`ud-dār'),
(4185, 40, 53, 'وَلَقَدْاٰتَيْنَا مُوْسٰى الْهُدٰى وَاَوْرَثْنَا بَنِيْٓ اِسْرَاۤءِيْلَ الْكِتٰبَۙ', 'wa laqad ātainā mụsal-hudā wa auraṡnā banī isrā`īlal-kitāb'),
(4186, 40, 54, 'هُدًى وَّذِكْرٰى لِاُولِى الْاَلْبَابِ', 'hudaw wa żikrā li`ulil-albāb'),
(4187, 40, 55, 'فَاصْبِرْ اِنَّ وَعْدَ اللّٰهِ حَقٌّ وَّاسْتَغْفِرْ لِذَنْۢبِكَ وَسَبِّحْ بِحَمْدِ رَبِّكَ بِالْعَشِيِّ وَالْاِبْكَارِ', 'faṣbir inna wa\'dallāhi ḥaqquw wastagfir liżambika wa sabbiḥ biḥamdi rabbika bil-\'asyiyyi wal-ibkār'),
(4188, 40, 56, 'اِنَّ الَّذِيْنَ يُجَادِلُوْنَ فِيْٓ اٰيٰتِ اللّٰهِ بِغَيْرِ سُلْطٰنٍ اَتٰىهُمْ ۙاِنْ فِيْ صُدُوْرِهِمْ اِلَّا كِبْرٌ مَّا هُمْ بِبَالِغِيْهِۚ فَاسْتَعِذْ بِاللّٰهِ ۗاِنَّهٗ هُوَ السَّمِيْعُ الْبَصِيْرُ', 'innallażīna yujādilụna fī āyātillāhi bigairi sulṭānin atāhum in fī ṣudụrihim illā kibrum mā hum bibāligīh, fasta\'iż billāh, innahụ huwas-samī\'ul-baṣīr'),
(4189, 40, 57, 'لَخَلْقُ السَّمٰوٰتِ وَالْاَرْضِ اَكْبَرُ مِنْ خَلْقِ النَّاسِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ', 'lakhalqus-samāwāti wal-arḍi akbaru min khalqin-nāsi wa lākinna akṡaran-nāsi lā ya\'lamụn'),
(4190, 40, 58, 'وَمَا يَسْتَوِى الْاَعْمٰى وَالْبَصِيْرُ ەۙ وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَلَا الْمُسِيْۤئُ ۗقَلِيْلًا مَّا تَتَذَكَّرُوْنَ', 'wa mā yastawil-a\'mā wal-baṣīru wallażīna āmanụ wa \'amiluṣ-ṣāliḥāti wa lal-musī`, qalīlam mā tatażakkarụn'),
(4191, 40, 59, 'اِنَّ السَّاعَةَ لَاٰتِيَةٌ لَّا رَيْبَ فِيْهَا ۖوَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يُؤْمِنُوْنَ', 'innas-sā\'ata la`ātiyatul lā raiba fīhā wa lākinna akṡaran-nāsi lā yu`minụn'),
(4192, 40, 60, 'وَقَالَ رَبُّكُمُ ادْعُوْنِيْٓ اَسْتَجِبْ لَكُمْ ۗاِنَّ الَّذِيْنَ يَسْتَكْبِرُوْنَ عَنْ عِبَادَتِيْ سَيَدْخُلُوْنَ جَهَنَّمَ دَاخِرِيْنَࣖ', 'wa qāla rabbukumud\'ụnī astajib lakum, innallażīna yastakbirụna \'an \'ibādatī sayadkhulụna jahannama dākhirīn'),
(4193, 40, 61, 'اَللّٰهُ الَّذِيْ جَعَلَ لَكُمُ الَّيْلَ لِتَسْكُنُوْا فِيْهِ وَالنَّهَارَ مُبْصِرًا ۗاِنَّ اللّٰهَ لَذُوْ فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَشْكُرُوْنَ', 'allāhullażī ja\'ala lakumul-laila litaskunụ fīhi wan-nahāra mubṣirā, innallāha lażụ faḍlin \'alan-nāsi wa lākinna akṡaran-nāsi lā yasykurụn'),
(4194, 40, 62, 'ذٰلِكُمُ اللّٰهُ رَبُّكُمْ خَالِقُ كُلِّ شَيْءٍۘ لَآ اِلٰهَ اِلَّا هُوَ ۖفَاَنّٰى تُؤْفَكُوْنَ', 'żālikumullāhu rabbukum khāliqu kulli syaī`, lā ilāha illā huwa fa annā tu`fakụn'),
(4195, 40, 63, 'كَذٰلِكَ يُؤْفَكُ الَّذِيْنَ كَانُوْا بِاٰيٰتِ اللّٰهِ يَجْحَدُوْنَ', 'każālika yu`fakullażīna kānụ bi`āyātillāhi yaj-ḥadụn'),
(4196, 40, 64, 'اَللّٰهُ الَّذِيْ جَعَلَ لَكُمُ الْاَرْضَ قَرَارًا وَّالسَّمَاۤءَ بِنَاۤءً وَّصَوَّرَكُمْ فَاَحْسَنَ صُوَرَكُمْ وَرَزَقَكُمْ مِّنَ الطَّيِّبٰتِ ۗذٰلِكُمُ اللّٰهُ رَبُّكُمْ ۚ فَتَبٰرَكَ اللّٰهُ رَبُّ الْعٰلَمِيْنَ', 'allāhullażī ja\'ala lakumul-arḍa qarāraw was-samā`a binā`aw wa ṣawwarakum fa aḥsana ṣuwarakum wa razaqakum minaṭ-ṭayyibāt, żālikumullāhu rabbukum, fa tabārakallāhu rabbul-\'ālamīn'),
(4197, 40, 65, 'هُوَ الْحَيُّ لَآ اِلٰهَ اِلَّا هُوَ فَادْعُوْهُ مُخْلِصِيْنَ لَهُ الدِّيْنَ ۗ اَلْحَمْدُ لِلّٰهِ رَبِّ الْعٰلَمِيْنَ', 'huwal-ḥayyu lā ilāha illā huwa fad\'ụhu mukhliṣīna lahud-dīn, al-ḥamdu lillāhi rabbil-\'ālamīn'),
(4198, 40, 66, '۞ قُلْ اِنِّيْ نُهِيْتُ اَنْ اَعْبُدَ الَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ لَمَّا جَاۤءَنِيَ الْبَيِّنٰتُ مِنْ رَّبِّيْ وَاُمِرْتُ اَنْ اُسْلِمَ لِرَبِّ الْعٰلَمِيْنَ', 'qul innī nuhītu an a\'budallażīna tad\'ụna min dụnillāhi lammā jā`aniyal-bayyinātu mir rabbī wa umirtu an uslima lirabbil-\'ālamīn'),
(4199, 40, 67, 'هُوَ الَّذِيْ خَلَقَكُمْ مِّنْ تُرَابٍ ثُمَّ مِنْ نُّطْفَةٍ ثُمَّ مِنْ عَلَقَةٍ ثُمَّ يُخْرِجُكُمْ طِفْلًا ثُمَّ لِتَبْلُغُوْٓا اَشُدَّكُمْ ثُمَّ لِتَكُوْنُوْا شُيُوْخًا ۚوَمِنْكُمْ مَّنْ يُّتَوَفّٰى مِنْ قَبْلُ وَلِتَبْلُغُوْٓا اَجَلًا مُّسَمًّى وَّلَعَلَّكُمْ تَعْقِلُوْنَ', 'huwallażī khalaqakum min turābin ṡumma min nuṭfatin ṡumma min \'alaqatin ṡumma yukhrijukum ṭiflan ṡumma litablugū asyuddakum ṡumma litakụnụ syuyụkhā, wa mingkum may yutawaffā ming qablu wa litablugū ajalam musammaw wa la\'allakum ta\'qilụn'),
(4200, 40, 68, 'هُوَ الَّذِيْ يُحْيٖ وَيُمِيْتُۚ فَاِذَا قَضٰىٓ اَمْرًا فَاِنَّمَا يَقُوْلُ لَهٗ كُنْ فَيَكُوْنُ', 'huwallażī yuḥyī wa yumīt, fa iżā qaḍā amran fa innamā yaqụlu lahụ kun fa yakụn'),
(4201, 40, 69, 'اَلَمْ تَرَ اِلَى الَّذِيْنَ يُجَادِلُوْنَ فِيْٓ اٰيٰتِ اللّٰهِ ۗاَنّٰى يُصْرَفُوْنَۚ', 'a lam tara ilallażīna yujādilụna fī āyātillāh, annā yuṣrafụn'),
(4202, 40, 70, 'اَلَّذِيْنَ كَذَّبُوْا بِالْكِتٰبِ وَبِمَآ اَرْسَلْنَا بِهٖ رُسُلَنَا ۗفَسَوْفَ يَعْلَمُوْنَۙ', 'allażīna każżabụ bil-kitābi wa bimā arsalnā bihī rusulanā, fa saufa ya\'lamụn'),
(4203, 40, 71, 'اِذِ الْاَغْلٰلُ فِيْٓ اَعْنَاقِهِمْ وَالسَّلٰسِلُۗ يُسْحَبُوْنَۙ', 'iżil-aglālu fī a\'nāqihim was-salāsil, yus-ḥabụn'),
(4204, 40, 72, 'فِى الْحَمِيْمِ ەۙ ثُمَّ فِى النَّارِ يُسْجَرُوْنَۚ', 'fil-ḥamīmi ṡumma fin-nāri yusjarụn'),
(4205, 40, 73, 'ثُمَّ قِيْلَ لَهُمْ اَيْنَ مَا كُنْتُمْ تُشْرِكُوْنَۙ', 'ṡumma qīla lahum aina mā kuntum tusyrikụn'),
(4206, 40, 74, 'مِنْ دُوْنِ اللّٰهِ ۗقَالُوْا ضَلُّوْا عَنَّا بَلْ لَّمْ نَكُنْ نَّدْعُوْا مِنْ قَبْلُ شَيْـًٔاۚ كَذٰلِكَ يُضِلُّ اللّٰهُ الْكٰفِرِيْنَ', 'min dụnillāh, qālụ ḍallụ \'annā bal lam nakun nad\'ụ ming qablu syai`ā, każālika yuḍillullāhul-kāfirīn'),
(4207, 40, 75, 'ذٰلِكُمْ بِمَا كُنْتُمْ تَفْرَحُوْنَ فِى الْاَرْضِ بِغَيْرِ الْحَقِّ وَبِمَا كُنْتُمْ تَمْرَحُوْنَ', 'żālikum bimā kuntum tafraḥụna fil-arḍi bigairil-ḥaqqi wa bimā kuntum tamraḥụn'),
(4208, 40, 76, 'اُدْخُلُوْٓا اَبْوَابَ جَهَنَّمَ خٰلِدِيْنَ فِيْهَا ۚفَبِئْسَ مَثْوَى الْمُتَكَبِّرِيْنَ', 'udkhulū abwāba jahannama khālidīna fīhā, fa bi`sa maṡwal-mutakabbirīn'),
(4209, 40, 77, 'فَاصْبِرْ اِنَّ وَعْدَ اللّٰهِ حَقٌّ ۚفَاِمَّا نُرِيَنَّكَ بَعْضَ الَّذِيْ نَعِدُهُمْ اَوْ نَتَوَفَّيَنَّكَ فَاِلَيْنَا يُرْجَعُوْنَ', 'faṣbir inna wa\'dallāhi ḥaqq, fa immā nuriyannaka ba\'ḍallażī na\'iduhum au natawaffayannaka fa ilainā yurja\'ụn'),
(4210, 40, 78, 'وَلَقَدْ اَرْسَلْنَا رُسُلًا مِّنْ قَبْلِكَ مِنْهُمْ مَّنْ قَصَصْنَا عَلَيْكَ وَمِنْهُمْ مَّنْ لَّمْ نَقْصُصْ عَلَيْكَ ۗوَمَا كَانَ لِرَسُوْلٍ اَنْ يَّأْتِيَ بِاٰيَةٍ اِلَّا بِاِذْنِ اللّٰهِ ۚفَاِذَا جَاۤءَ اَمْرُ اللّٰهِ قُضِيَ بِالْحَقِّ وَخَسِرَ هُنَالِكَ الْمُبْطِلُوْنَ', 'wa laqad arsalnā rusulam ming qablika min-hum mang qaṣaṣnā \'alaika wa min-hum mal lam naqṣuṣ \'alaīk, wa mā kāna lirasụlin ay ya`tiya bi`āyatin illā bi`iżnillāh, fa iżā jā`a amrullāhi quḍiya bil-ḥaqqi wa khasira hunālikal-mubṭilụn'),
(4211, 40, 79, 'اَللّٰهُ الَّذِيْ جَعَلَ لَكُمُ الْاَنْعَامَ لِتَرْكَبُوْا مِنْهَا وَمِنْهَا تَأْكُلُوْنَۖ', 'allāhullażī ja\'ala lakumul-an\'āma litarkabụ min-hā wa min-hā ta`kulụn'),
(4212, 40, 80, 'وَلَكُمْ فِيْهَا مَنَافِعُ وَلِتَبْلُغُوْا عَلَيْهَا حَاجَةً فِيْ صُدُوْرِكُمْ وَعَلَيْهَا وَعَلَى الْفُلْكِ تُحْمَلُوْنَۗ', 'wa lakum fīhā manāfi\'u wa litablugụ \'alaihā ḥājatan fī ṣudụrikum wa \'alaihā wa \'alal-fulki tuḥmalụn'),
(4213, 40, 81, 'وَيُرِيْكُمْ اٰيٰتِهٖۖ فَاَيَّ اٰيٰتِ اللّٰهِ تُنْكِرُوْنَ', 'wa yurīkum āyātihī fa ayya āyātillāhi tungkirụn'),
(4214, 40, 82, 'اَفَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَيَنْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلِهِمْ ۗ كَانُوْٓا اَكْثَرَ مِنْهُمْ وَاَشَدَّ قُوَّةً وَّاٰثَارًا فِى الْاَرْضِ فَمَآ اَغْنٰى عَنْهُمْ مَّا كَانُوْا يَكْسِبُوْنَ', 'a fa lam yasīrụ fil-arḍi fa yanẓurụ kaifa kāna \'āqibatullażīna ming qablihim, kānū akṡara min-hum wa asyadda quwwataw wa āṡāran fil-arḍi fa mā agnā \'an-hum mā kānụ yaksibụn'),
(4215, 40, 83, 'فَلَمَّا جَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِ فَرِحُوْا بِمَا عِنْدَهُمْ مِّنَ الْعِلْمِ وَحَاقَ بِهِمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ', 'fa lammā jā`at-hum rusuluhum bil-bayyināti fariḥụ bimā \'indahum minal-\'ilmi wa ḥāqa bihim mā kānụ bihī yastahzi`ụn'),
(4216, 40, 84, 'فَلَمَّا رَاَوْا بَأْسَنَاۗ قَالُوْٓا اٰمَنَّا بِاللّٰهِ وَحْدَهٗ وَكَفَرْنَا بِمَا كُنَّا بِهٖ مُشْرِكِيْنَ', 'fa lammā ra`au ba`sanā, qālū āmannā billāhi waḥdahụ wa kafarnā bimā kunnā bihī musyrikīn'),
(4217, 40, 85, 'فَلَمْ يَكُ يَنْفَعُهُمْ اِيْمَانُهُمْ لَمَّا رَاَوْا بَأْسَنَا ۗسُنَّتَ اللّٰهِ الَّتِيْ قَدْ خَلَتْ فِيْ عِبَادِهِۚ وَخَسِرَ هُنَالِكَ الْكٰفِرُوْنَ', 'fa lam yaku yanfa\'uhum īmānuhum lammā ra`au ba`sanā, sunnatallāhillatī qad khalat fī \'ibādih, wa khasira hunālikal-kāfirụn'),
(4218, 41, 1, ' حٰمۤ ۚ', 'ḥā mīm'),
(4219, 41, 2, 'تَنْزِيْلٌ مِّنَ الرَّحْمٰنِ الرَّحِيْمِ ۚ', 'tanzīlum minar-raḥmānir-raḥīm'),
(4220, 41, 3, 'كِتٰبٌ فُصِّلَتْ اٰيٰتُهٗ قُرْاٰنًا عَرَبِيًّا لِّقَوْمٍ يَّعْلَمُوْنَۙ', 'kitābun fuṣṣilat āyātuhụ qur`ānan \'arabiyyal liqaumiy ya\'lamụn'),
(4221, 41, 4, 'بَشِيْرًا وَّنَذِيْرًاۚ فَاَعْرَضَ اَكْثَرُهُمْ فَهُمْ لَا يَسْمَعُوْنَ', 'basyīraw wa nażīrā, fa a\'raḍa akṡaruhum fa hum lā yasma\'ụn'),
(4222, 41, 5, 'وَقَالُوْا قُلُوْبُنَا فِيْٓ اَكِنَّةٍ مِّمَّا تَدْعُوْنَآ اِلَيْهِ وَفِيْٓ اٰذَانِنَا وَقْرٌ وَّمِنْۢ بَيْنِنَا وَبَيْنِكَ حِجَابٌ فَاعْمَلْ اِنَّنَا عٰمِلُوْنَ', 'wa qālụ qulụbunā fī akinnatim mimmā tad\'ụnā ilaihi wa fī āżāninā waqruw wa mim baininā wa bainika ḥijābun fa\'mal innanā \'āmilụn'),
(4223, 41, 6, 'قُلْ اِنَّمَآ اَنَا۟ بَشَرٌ مِّثْلُكُمْ يُوْحٰىٓ اِلَيَّ اَنَّمَآ اِلٰهُكُمْ اِلٰهٌ وَّاحِدٌ فَاسْتَقِيْمُوْٓا اِلَيْهِ وَاسْتَغْفِرُوْهُ ۗوَوَيْلٌ لِّلْمُشْرِكِيْنَۙ', 'qul innamā ana basyarum miṡlukum yụḥā ilayya annamā ilāhukum ilāhuw wāḥidun fastaqīmū ilaihi wastagfirụh, wa wailul lil-musyrikīn'),
(4224, 41, 7, 'الَّذِيْنَ لَا يُؤْتُوْنَ الزَّكٰوةَ وَهُمْ بِالْاٰخِرَةِ هُمْ كٰفِرُوْنَ', 'allażīna lā yu`tụnaz-zakāta wa hum bil-ākhirati hum kāfirụn'),
(4225, 41, 8, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ اَجْرٌ غَيْرُ مَمْنُوْنٍ', 'innallażīna āmanụ wa \'amiluṣ-ṣāliḥāti lahum ajrun gairu mamnụn'),
(4226, 41, 9, '۞ قُلْ اَىِٕنَّكُمْ لَتَكْفُرُوْنَ بِالَّذِيْ خَلَقَ الْاَرْضَ فِيْ يَوْمَيْنِ وَتَجْعَلُوْنَ لَهٗٓ اَنْدَادًا ۗذٰلِكَ رَبُّ الْعٰلَمِيْنَ ۚ', 'qul a innakum latakfurụna billażī khalaqal-arḍa fī yaumaini wa taj\'alụna lahū andādā, żālika rabbul-\'ālamīn'),
(4227, 41, 10, 'وَجَعَلَ فِيْهَا رَوَاسِيَ مِنْ فَوْقِهَا وَبٰرَكَ فِيْهَا وَقَدَّرَ فِيْهَآ اَقْوَاتَهَا فِيْٓ اَرْبَعَةِ اَيَّامٍۗ سَوَاۤءً لِّلسَّاۤىِٕلِيْنَ', 'wa ja\'ala fīhā rawāsiya min fauqihā wa bāraka fīhā wa qaddara fīhā aqwātahā fī arba\'ati ayyām, sawā`al lis-sā`ilīn'),
(4228, 41, 11, 'ثُمَّ اسْتَوٰىٓ اِلَى السَّمَاۤءِ وَهِيَ دُخَانٌ فَقَالَ لَهَا وَلِلْاَرْضِ ائْتِيَا طَوْعًا اَوْ كَرْهًاۗ قَالَتَآ اَتَيْنَا طَاۤىِٕعِيْنَ', 'ṡummastawā ilas-samā`i wa hiya dukhānun fa qāla lahā wa lil-arḍi`tiyā ṭau\'an au karhā, qālatā atainā ṭā`i\'īn'),
(4229, 41, 12, 'فَقَضٰىهُنَّ سَبْعَ سَمٰوٰتٍ فِيْ يَوْمَيْنِ وَاَوْحٰى فِيْ كُلِّ سَمَاۤءٍ اَمْرَهَا ۗوَزَيَّنَّا السَّمَاۤءَ الدُّنْيَا بِمَصَابِيْحَۖ وَحِفْظًا ۗذٰلِكَ تَقْدِيْرُ الْعَزِيْزِ الْعَلِيْمِ', 'fa qaḍāhunna sab\'a samāwātin fī yaumaini wa auḥā fī kulli samā`in amrahā, wa zayyannas-samā`ad-dun-yā bimaṣābīḥa wa ḥifẓā, żālika taqdīrul-\'azīzil-\'alīm'),
(4230, 41, 13, 'فَاِنْ اَعْرَضُوْا فَقُلْ اَنْذَرْتُكُمْ صٰعِقَةً مِّثْلَ صٰعِقَةِ عَادٍ وَّثَمُوْدَ ۗ', 'fa in a\'raḍụ fa qul anżartukum ṣā\'iqatam miṡla ṣā\'iqati \'ādiw wa ṡamụd'),
(4231, 41, 14, 'اِذْ جَاۤءَتْهُمُ الرُّسُلُ مِنْۢ بَيْنِ اَيْدِيْهِمْ وَمِنْ خَلْفِهِمْ اَلَّا تَعْبُدُوْٓا اِلَّا اللّٰهَ ۗقَالُوْا لَوْ شَاۤءَ رَبُّنَا لَاَنْزَلَ مَلٰۤىِٕكَةً فَاِنَّا بِمَآ اُرْسِلْتُمْ بِهٖ كٰفِرُوْنَ', 'iż jā`at-humur-rusulu mim baini aidīhim wa min khalfihim allā ta\'budū illallāh, qālụ lau syā`a rabbunā la`anzala malā`ikatan fa innā bimā ursiltum bihī kāfirụn'),
(4232, 41, 15, 'فَاَمَّا عَادٌ فَاسْتَكْبَرُوْا فِى الْاَرْضِ بِغَيْرِ الْحَقِّ وَقَالُوْا مَنْ اَشَدُّ مِنَّا قُوَّةً ۗ اَوَلَمْ يَرَوْا اَنَّ اللّٰهَ الَّذِيْ خَلَقَهُمْ هُوَ اَشَدُّ مِنْهُمْ قُوَّةً ۗ وَكَانُوْا بِاٰيٰتِنَا يَجْحَدُوْنَ', 'fa ammā \'ādun fastakbarụ fil-arḍi bigairil-ḥaqqi wa qālụ man asyaddu minnā quwwah, a wa lam yarau annallāhallażī khalaqahum huwa asyaddu min-hum quwwah, wa kānụ bi`āyātinā yaj-ḥadụn'),
(4233, 41, 16, 'فَاَرْسَلْنَا عَلَيْهِمْ رِيْحًا صَرْصَرًا فِيْٓ اَيَّامٍ نَّحِسَاتٍ لِّنُذِيْقَهُمْ عَذَابَ الْخِزْيِ فِى الْحَيٰوةِ الدُّنْيَا ۗوَلَعَذَابُ الْاٰخِرَةِ اَخْزٰى وَهُمْ لَا يُنْصَرُوْنَ', 'fa arsalnā \'alaihim rīḥan ṣarṣaran fī ayyāmin naḥisātil linużīqahum \'ażābal-khizyi fil-ḥayātid-dun-yā, wa la\'ażābul-ākhirati akhzā wa hum lā yunṣarụn'),
(4234, 41, 17, 'وَاَمَّا ثَمُوْدُ فَهَدَيْنٰهُمْ فَاسْتَحَبُّوا الْعَمٰى عَلَى الْهُدٰى فَاَخَذَتْهُمْ صٰعِقَةُ الْعَذَابِ الْهُوْنِ بِمَا كَانُوْا يَكْسِبُوْنَ ۚ', 'wa ammā ṡamụdu fa hadaināhum fastaḥabbul-\'amā \'alal-hudā fa akhażat-hum ṣā\'iqatul-\'ażābil-hụni bimā kānụ yaksibụn'),
(4235, 41, 18, 'وَنَجَّيْنَا الَّذِيْنَ اٰمَنُوْا وَكَانُوْا يَتَّقُوْنَ', 'wa najjainallażīna āmanụ wa kānụ yattaqụn'),
(4236, 41, 19, 'وَيَوْمَ يُحْشَرُ اَعْدَاۤءُ اللّٰهِ اِلَى النَّارِ فَهُمْ يُوْزَعُوْنَ', 'wa yauma yuḥsyaru a\'dā`ullāhi ilan-nāri fa hum yụza\'ụn'),
(4237, 41, 20, 'حَتّٰىٓ اِذَا مَا جَاۤءُوْهَا شَهِدَ عَلَيْهِمْ سَمْعُهُمْ وَاَبْصَارُهُمْ وَجُلُوْدُهُمْ بِمَا كَانُوْا يَعْمَلُوْنَ', 'ḥattā iżā mā jā`ụhā syahida \'alaihim sam\'uhum wa abṣāruhum wa julụduhum bimā kānụ ya\'malụn'),
(4238, 41, 21, 'وَقَالُوْا لِجُلُوْدِهِمْ لِمَ شَهِدْتُّمْ عَلَيْنَا ۗقَالُوْٓا اَنْطَقَنَا اللّٰهُ الَّذِيْٓ اَنْطَقَ كُلَّ شَيْءٍ وَّهُوَ خَلَقَكُمْ اَوَّلَ مَرَّةٍۙ وَّاِلَيْهِ تُرْجَعُوْنَ', 'wa qālụ lijulụdihim lima syahittum \'alainā, qālū anṭaqanallāhullażī anṭaqa kulla syai`iw wa huwa khalaqakum awwala marratiw wa ilaihi turja\'ụn'),
(4239, 41, 22, 'وَمَا كُنْتُمْ تَسْتَتِرُوْنَ اَنْ يَّشْهَدَ عَلَيْكُمْ سَمْعُكُمْ وَلَآ اَبْصَارُكُمْ وَلَا جُلُوْدُكُمْ وَلٰكِنْ ظَنَنْتُمْ اَنَّ اللّٰهَ لَا يَعْلَمُ كَثِيْرًا مِّمَّا تَعْمَلُوْنَ', 'wa mā kuntum tastatirụna ay yasy-hada \'alaikum sam\'ukum wa lā abṣārukum wa lā julụdukum wa lākin ẓanantum annallāha lā ya\'lamu kaṡīram mimmā ta\'malụn'),
(4240, 41, 23, 'وَذٰلِكُمْ ظَنُّكُمُ الَّذِيْ ظَنَنْتُمْ بِرَبِّكُمْ اَرْدٰىكُمْ فَاَصْبَحْتُمْ مِّنَ الْخٰسِرِيْنَ', 'wa żālikum ẓannukumullażī ẓanantum birabbikum ardākum fa aṣbaḥtum minal-khāsirīn'),
(4241, 41, 24, 'فَاِنْ يَّصْبِرُوْا فَالنَّارُ مَثْوًى لَّهُمْ ۚوَاِنْ يَّسْتَعْتِبُوْا فَمَا هُمْ مِّنَ الْمُعْتَبِيْنَ', 'fa iy yaṣbirụ fan-nāru maṡwal lahum, wa iy yasta\'tibụ fa mā hum minal-mu\'tabīn'),
(4242, 41, 25, '۞ وَقَيَّضْنَا لَهُمْ قُرَنَاۤءَ فَزَيَّنُوْا لَهُمْ مَّا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْ وَحَقَّ عَلَيْهِمُ الْقَوْلُ فِيْٓ اُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِهِمْ مِّنَ الْجِنِّ وَالْاِنْسِۚ اِنَّهُمْ كَانُوْا خٰسِرِيْنَ', 'wa qayyaḍnā lahum quranā`a fa zayyanụ lahum mā baina aidīhim wa mā khalfahum wa ḥaqqa \'alaihimul-qaulu fī umaming qad khalat ming qablihim minal-jinni wal-ins, innahum kānụ khāsirīn'),
(4243, 41, 26, 'وَقَالَ الَّذِيْنَ كَفَرُوْا لَا تَسْمَعُوْا لِهٰذَا الْقُرْاٰنِ وَالْغَوْا فِيْهِ لَعَلَّكُمْ تَغْلِبُوْنَ', 'wa qālallażīna kafarụ lā tasma\'ụ lihāżal-qur`āni walgau fīhi la\'allakum taglibụn'),
(4244, 41, 27, 'فَلَنُذِيْقَنَّ الَّذِيْنَ كَفَرُوْا عَذَابًا شَدِيْدًاۙ وَّلَنَجْزِيَنَّهُمْ اَسْوَاَ الَّذِيْ كَانُوْا يَعْمَلُوْنَ', 'fa lanużīqannallażīna kafarụ \'ażāban syadīdaw wa lanajziyannahum aswa`allażī kānụ ya\'malụn'),
(4245, 41, 28, 'ذٰلِكَ جَزَاۤءُ اَعْدَاۤءِ اللّٰهِ النَّارُ لَهُمْ فِيْهَا دَارُ الْخُلْدِ ۗجَزَاۤءً ۢبِمَا كَانُوْا بِاٰيٰتِنَا يَجْحَدُوْنَ', 'żālika jazā`u a\'dā`illāhin-nāru lahum fīhā dārul-khuld, jazā`am bimā kānụ bi`āyātinā yaj-ḥadụn'),
(4246, 41, 29, 'وَقَالَ الَّذِيْنَ كَفَرُوْا رَبَّنَآ اَرِنَا الَّذَيْنِ اَضَلّٰنَا مِنَ الْجِنِّ وَالْاِنْسِ نَجْعَلْهُمَا تَحْتَ اَقْدَامِنَا لِيَكُوْنَا مِنَ الْاَسْفَلِيْنَ', 'wa qālallażīna kafarụ rabbanā arinallażaini aḍallānā minal-jinni wal-insi naj\'al-humā taḥta aqdāminā liyakụnā minal-asfalīn'),
(4247, 41, 30, 'اِنَّ الَّذِيْنَ قَالُوْا رَبُّنَا اللّٰهُ ثُمَّ اسْتَقَامُوْا تَتَنَزَّلُ عَلَيْهِمُ الْمَلٰۤىِٕكَةُ اَلَّا تَخَافُوْا وَلَا تَحْزَنُوْا وَاَبْشِرُوْا بِالْجَنَّةِ الَّتِيْ كُنْتُمْ تُوْعَدُوْنَ', 'innallażīna qālụ rabbunallāhu ṡummastaqāmụ tatanazzalu \'alaihimul-malā`ikatu allā takhāfụ wa lā taḥzanụ wa absyirụ bil-jannatillatī kuntum tụ\'adụn'),
(4248, 41, 31, 'نَحْنُ اَوْلِيَاۤؤُكُمْ فِى الْحَيٰوةِ الدُّنْيَا وَفِى الْاٰخِرَةِ ۚوَلَكُمْ فِيْهَا مَا تَشْتَهِيْٓ اَنْفُسُكُمْ وَلَكُمْ فِيْهَا مَا تَدَّعُوْنَ ۗ', 'naḥnu auliyā`ukum fil-ḥayātid-dun-yā wa fil-ākhirah, wa lakum fīhā mā tasytahī anfusukum wa lakum fīhā mā tadda\'ụn'),
(4249, 41, 32, 'نُزُلًا مِّنْ غَفُوْرٍ رَّحِيْمٍ', 'nuzulam min gafụrir raḥīm'),
(4250, 41, 33, 'وَمَنْ اَحْسَنُ قَوْلًا مِّمَّنْ دَعَآ اِلَى اللّٰهِ وَعَمِلَ صَالِحًا وَّقَالَ اِنَّنِيْ مِنَ الْمُسْلِمِيْنَ', 'wa man aḥsanu qaulam mim man da\'ā ilallāhi wa \'amila ṣāliḥaw wa qāla innanī minal-muslimīn'),
(4251, 41, 34, 'وَلَا تَسْتَوِى الْحَسَنَةُ وَلَا السَّيِّئَةُ ۗاِدْفَعْ بِالَّتِيْ هِيَ اَحْسَنُ فَاِذَا الَّذِيْ بَيْنَكَ وَبَيْنَهٗ عَدَاوَةٌ كَاَنَّهٗ وَلِيٌّ حَمِيْمٌ', 'wa lā tastawil-ḥasanatu wa las-sayyi`ah, idfa\' billatī hiya aḥsanu fa iżallażī bainaka wa bainahụ \'adāwatung ka`annahụ waliyyun ḥamīm'),
(4252, 41, 35, 'وَمَا يُلَقّٰىهَآ اِلَّا الَّذِيْنَ صَبَرُوْاۚ وَمَا يُلَقّٰىهَآ اِلَّا ذُوْ حَظٍّ عَظِيْمٍ', 'wa mā yulaqqāhā illallażīna ṣabarụ, wa mā yulaqqāhā illā żụ ḥaẓẓin \'aẓīm'),
(4253, 41, 36, 'وَاِمَّا يَنْزَغَنَّكَ مِنَ الشَّيْطٰنِ نَزْغٌ فَاسْتَعِذْ بِاللّٰهِ ۗاِنَّهٗ هُوَ السَّمِيْعُ الْعَلِيْمُ', 'wa immā yanzagannaka minasy-syaiṭāni nazgun fasta\'iż billāh, innahụ huwas-samī\'ul-\'alīm'),
(4254, 41, 37, 'وَمِنْ اٰيٰتِهِ الَّيْلُ وَالنَّهَارُ وَالشَّمْسُ وَالْقَمَرُۗ لَا تَسْجُدُوْا لِلشَّمْسِ وَلَا لِلْقَمَرِ وَاسْجُدُوْا لِلّٰهِ الَّذِيْ خَلَقَهُنَّ اِنْ كُنْتُمْ اِيَّاهُ تَعْبُدُوْنَ', 'wa min āyātihil-lailu wan-nahāru wasy-syamsu wal-qamar, lā tasjudụ lisy-syamsi wa lā lil-qamari wasjudụ lillāhillażī khalaqahunna ing kuntum iyyāhu ta\'budụn'),
(4255, 41, 38, 'فَاِنِ اسْتَكْبَرُوْا فَالَّذِيْنَ عِنْدَ رَبِّكَ يُسَبِّحُوْنَ لَهٗ بِالَّيْلِ وَالنَّهَارِ وَهُمْ لَا يَسْـَٔمُوْنَ ۩', 'fa inistakbarụ fallażīna \'inda rabbika yusabbiḥụna lahụ bil-laili wan-nahāri wa hum lā yas`amụn'),
(4256, 41, 39, 'وَمِنْ اٰيٰتِهٖٓ اَنَّكَ تَرَى الْاَرْضَ خَاشِعَةً فَاِذَآ اَنْزَلْنَا عَلَيْهَا الْمَاۤءَ اهْتَزَّتْ وَرَبَتْۗ اِنَّ الَّذِيْٓ اَحْيَاهَا لَمُحْيِ الْمَوْتٰى ۗاِنَّهٗ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'wa min āyātihī annaka taral-arḍa khāsyi\'atan fa iżā anzalnā \'alaihal-mā`ahtazzat wa rabat, innallażī aḥyāhā lamuḥyil-mautā, innahụ \'alā kulli syai`ing qadīr'),
(4257, 41, 40, 'اِنَّ الَّذِيْنَ يُلْحِدُوْنَ فِيْٓ اٰيٰتِنَا لَا يَخْفَوْنَ عَلَيْنَاۗ اَفَمَنْ يُّلْقٰى فِى النَّارِ خَيْرٌ اَمَّنْ يَّأْتِيْٓ اٰمِنًا يَّوْمَ الْقِيٰمَةِ ۗاِعْمَلُوْا مَا شِئْتُمْ ۙاِنَّهٗ بِمَا تَعْمَلُوْنَ بَصِيْرٌ', 'innallażīna yul-ḥidụna fī āyātinā lā yakhfauna \'alainā, a fa may yulqā fin-nāri khairun am may ya`tī āminay yaumal-qiyāmah, i\'malụ mā syi`tum innahụ bimā ta\'malụna baṣīr'),
(4258, 41, 41, 'اِنَّ الَّذِيْنَ كَفَرُوْا بِالذِّكْرِ لَمَّا جَاۤءَهُمْ ۗوَاِنَّهٗ لَكِتٰبٌ عَزِيْزٌ ۙ', 'innallażīna kafarụ biż-żikri lammā jā`ahum, wa innahụ lakitābun \'azīz'),
(4259, 41, 42, 'لَّا يَأْتِيْهِ الْبَاطِلُ مِنْۢ بَيْنِ يَدَيْهِ وَلَا مِنْ خَلْفِهٖ ۗتَنْزِيْلٌ مِّنْ حَكِيْمٍ حَمِيْدٍ', 'lā ya`tīhil-bāṭilu mim baini yadaihi wa lā min khalfih, tanzīlum min ḥakīmin ḥamīd'),
(4260, 41, 43, 'مَا يُقَالُ لَكَ اِلَّا مَا قَدْ قِيْلَ لِلرُّسُلِ مِنْ قَبْلِكَ ۗاِنَّ رَبَّكَ لَذُوْ مَغْفِرَةٍ وَّذُوْ عِقَابٍ اَلِيْمٍ', 'mā yuqālu laka illā mā qad qīla lir-rusuli ming qablik, inna rabbaka lażụ magfiratiw wa żụ \'iqābin alīm'),
(4261, 41, 44, 'وَلَوْ جَعَلْنٰهُ قُرْاٰنًا اَعْجَمِيًّا لَّقَالُوْا لَوْلَا فُصِّلَتْ اٰيٰتُهٗ ۗ ءَاَ۬عْجَمِيٌّ وَّعَرَبِيٌّ ۗ قُلْ هُوَ لِلَّذِيْنَ اٰمَنُوْا هُدًى وَّشِفَاۤءٌ ۗوَالَّذِيْنَ لَا يُؤْمِنُوْنَ فِيْٓ اٰذَانِهِمْ وَقْرٌ وَّهُوَ عَلَيْهِمْ عَمًىۗ اُولٰۤىِٕكَ يُنَادَوْنَ مِنْ مَّكَانٍۢ بَعِيْدٍ', 'walau ja\'alnāhu qur`ānan a\'jamiyyal laqālụ lau lā fuṣṣilat āyātuh, a a\'jamiyyuw wa \'arabiyy, qul huwa lillażīna āmanụ hudaw wa syifā`, wallażīna lā yu`minụna fī āżānihim waqruw wa huwa \'alaihim \'amā, ulā`ika yunādauna mim makānim ba\'īd'),
(4262, 41, 45, 'وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ فَاخْتُلِفَ فِيْهِ ۗوَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَّبِّكَ لَقُضِيَ بَيْنَهُمْ ۗوَاِنَّهُمْ لَفِيْ شَكٍّ مِّنْهُ مُرِيْبٍ', 'wa laqad ātainā mụsal-kitāba fakhtulifa fīh, walau lā kalimatun sabaqat mir rabbika laquḍiya bainahum, wa innahum lafī syakkim min-hu murīb'),
(4263, 41, 46, 'مَنْ عَمِلَ صَالِحًا فَلِنَفْسِهٖ ۙوَمَنْ اَسَاۤءَ فَعَلَيْهَا ۗوَمَا رَبُّكَ بِظَلَّامٍ لِّلْعَبِيْدِ ۔', 'man \'amila ṣāliḥan falinafsihī wa man asā`a fa \'alaihā, wa mā rabbuka biẓallāmil lil-\'abīd'),
(4264, 41, 47, '۞ اِلَيْهِ يُرَدُّ عِلْمُ السَّاعَةِ ۗوَمَا تَخْرُجُ مِنْ ثَمَرٰتٍ ِمّنْ اَكْمَامِهَا وَمَا تَحْمِلُ مِنْ اُنْثٰى وَلَا تَضَعُ اِلَّا بِعِلْمِهٖ ۗوَيَوْمَ يُنَادِيْهِمْ اَيْنَ شُرَكَاۤءِيْۙ قَالُوْٓا اٰذَنّٰكَ مَا مِنَّا مِنْ شَهِيْدٍ ۚ', 'ilaihi yuraddu \'ilmus-sā\'ah, wa mā takhruju min ṡamarātim min akmāmihā wa mā taḥmilu min unṡā wa lā taḍa\'u illā bi\'ilmih, wa yauma yunādīhim aina syurakā`ī qālū āżannāka mā minnā min syahīd'),
(4265, 41, 48, 'وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَدْعُوْنَ مِنْ قَبْلُ وَظَنُّوْا مَا لَهُمْ مِّنْ مَّحِيْصٍ', 'wa ḍalla \'an-hum mā kānụ yad\'ụna ming qablu wa ẓannụ mā lahum mim maḥīṣ'),
(4266, 41, 49, 'لَا يَسْـَٔمُ الْاِنْسَانُ مِنْ دُعَاۤءِ الْخَيْرِۖ وَاِنْ مَّسَّهُ الشَّرُّ فَيَـُٔوْسٌ قَنُوْطٌ', 'lā yas`amul-insānu min du\'ā`il-khairi wa im massahusy-syarru fa ya`ụsung qanụṭ'),
(4267, 41, 50, 'وَلَىِٕنْ اَذَقْنٰهُ رَحْمَةً مِّنَّا مِنْۢ بَعْدِ ضَرَّاۤءَ مَسَّتْهُ لَيَقُوْلَنَّ هٰذَا لِيْۙ وَمَآ اَظُنُّ السَّاعَةَ قَاۤىِٕمَةًۙ وَّلَىِٕنْ رُّجِعْتُ اِلٰى رَبِّيْٓ اِنَّ لِيْ عِنْدَهٗ لَلْحُسْنٰىۚ فَلَنُنَبِّئَنَّ الَّذِيْنَ كَفَرُوْا بِمَا عَمِلُوْاۖ وَلَنُذِيْقَنَّهُمْ مِّنْ عَذَابٍ غَلِيْظٍ', 'wa la`in ażaqnāhu raḥmatam minnā mim ba\'di ḍarrā`a massat-hu layaqụlanna hāżā lī wa mā aẓunnus-sā\'ata qā`imataw wa la`ir ruji\'tu ilā rabbī inna lī \'indahụ lal-ḥusnā, fa lanunabbi`annallażīna kafarụ bimā \'amilụ wa lanużīqannahum min \'ażābin galīẓ'),
(4268, 41, 51, 'وَاِذَآ اَنْعَمْنَا عَلَى الْاِنْسَانِ اَعْرَضَ وَنَاٰ بِجَانِبِهٖۚ وَاِذَا مَسَّهُ الشَّرُّ فَذُوْ دُعَاۤءٍ عَرِيْضٍ', 'wa iżā an\'amnā \'alal-insāni a\'raḍa wa na`ā bijānibih, wa iżā massahusy-syarru fa żụ du\'ā`in \'arīḍ'),
(4269, 41, 52, 'قُلْ اَرَءَيْتُمْ اِنْ كَانَ مِنْ عِنْدِ اللّٰهِ ثُمَّ كَفَرْتُمْ بِهٖ مَنْ اَضَلُّ مِمَّنْ هُوَ فِيْ شِقَاقٍۢ بَعِيْدٍ', 'qul a ra`aitum ing kāna min \'indillāhi ṡumma kafartum bihī man aḍallu mim man huwa fī syiqāqim ba\'īd'),
(4270, 41, 53, 'سَنُرِيْهِمْ اٰيٰتِنَا فِى الْاٰفَاقِ وَفِيْٓ اَنْفُسِهِمْ حَتّٰى يَتَبَيَّنَ لَهُمْ اَنَّهُ الْحَقُّۗ اَوَلَمْ يَكْفِ بِرَبِّكَ اَنَّهٗ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ', 'sanurīhim āyātinā fil-āfāqi wa fī anfusihim ḥattā yatabayyana lahum annahul-ḥaqq, a wa lam yakfi birabbika annahụ \'alā kulli syai`in syahīd'),
(4271, 41, 54, 'اَلَآ اِنَّهُمْ فِيْ مِرْيَةٍ مِّنْ لِّقَاۤءِ رَبِّهِمْ ۗ اَلَآ اِنَّهٗ بِكُلِّ شَيْءٍ مُّحِيْطٌ', 'alā innahum fī miryatim mil liqā`i rabbihim, alā innahụ bikulli syai`im muḥīṭ'),
(4272, 42, 1, ' حٰمۤ ۚ', 'ḥā mīm'),
(4273, 42, 2, 'عۤسۤقۤ ۗ', '\'Aīn sīn qāf'),
(4274, 42, 3, 'كَذٰلِكَ يُوْحِيْٓ اِلَيْكَ وَاِلَى الَّذِيْنَ مِنْ قَبْلِكَۙ اللّٰهُ الْعَزِيْزُ الْحَكِيْمُ', 'każālika yụḥī ilaika wa ilallażīna ming qablikallāhul-\'azīzul-ḥakīm'),
(4275, 42, 4, 'لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَهُوَ الْعَلِيُّ الْعَظِيْمُ', 'lahụ mā fis-samāwāti wa mā fil-arḍ, wa huwal-\'aliyyul-\'aẓīm'),
(4276, 42, 5, 'تَكَادُ السَّمٰوٰتُ يَتَفَطَّرْنَ مِنْ فَوْقِهِنَّ وَالْمَلٰۤىِٕكَةُ يُسَبِّحُوْنَ بِحَمْدِ رَبِّهِمْ وَيَسْتَغْفِرُوْنَ لِمَنْ فِى الْاَرْضِۗ اَلَآ اِنَّ اللّٰهَ هُوَ الْغَفُوْرُ الرَّحِيْمُ', 'takādus-samāwātu yatafaṭṭarna min fauqihinna wal-malā`ikatu yusabbiḥụna biḥamdi rabbihim wa yastagfirụna liman fil-arḍ, alā innallāha huwal-gafụrur-raḥīm'),
(4277, 42, 6, 'وَالَّذِيْنَ اتَّخَذُوْا مِنْ دُوْنِهٖٓ اَوْلِيَاۤءَ اللّٰهُ حَفِيْظٌ عَلَيْهِمْۖ وَمَآ اَنْتَ عَلَيْهِمْ بِوَكِيْلٍ', 'wallażīnattakhażụ min dụnihī auliyā`allāhu ḥafīẓun \'alaihim wa mā anta \'alaihim biwakīl'),
(4278, 42, 7, 'وَكَذٰلِكَ اَوْحَيْنَآ اِلَيْكَ قُرْاٰنًا عَرَبِيًّا لِّتُنْذِرَ اُمَّ الْقُرٰى وَمَنْ حَوْلَهَا وَتُنْذِرَ يَوْمَ الْجَمْعِ لَا رَيْبَ فِيْهِ ۗفَرِيْقٌ فِى الْجَنَّةِ وَفَرِيْقٌ فِى السَّعِيْرِ', 'wa każālika auḥainā ilaika qur`ānan \'arabiyyal litunżira ummal-qurā wa man ḥaulahā wa tunżira yaumal-jam\'i lā raiba fīh, farīqun fil-jannati wa farīqun fis-sa\'īr'),
(4279, 42, 8, 'وَلَوْ شَاۤءَ اللّٰهُ لَجَعَلَهُمْ اُمَّةً وَّاحِدَةً وَّلٰكِنْ يُّدْخِلُ مَنْ يَّشَاۤءُ فِيْ رَحْمَتِهٖۗ وَالظّٰلِمُوْنَ مَا لَهُمْ مِّنْ وَّلِيٍّ وَّلَا نَصِيْرٍ', 'walau syā`allāhu laja\'alahum ummataw wāḥidataw wa lākiy yudkhilu may yasyā`u fī raḥmatih, waẓ-ẓālimụna mā lahum miw waliyyiw wa lā naṣīr'),
(4280, 42, 9, 'اَمِ اتَّخَذُوْا مِنْ دُوْنِهٖٓ اَوْلِيَاۤءَۚ فَاللّٰهُ هُوَ الْوَلِيُّ وَهُوَ يُحْيِ الْمَوْتٰى ۖوَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'amittakhażụ min dụnihī auliyā`, fallāhu huwal-waliyyu wa huwa yuḥyil-mautā wa huwa \'alā kulli syai`ing qadīr'),
(4281, 42, 10, 'وَمَا اخْتَلَفْتُمْ فِيْهِ مِنْ شَيْءٍ فَحُكْمُهٗٓ اِلَى اللّٰهِ ۗذٰلِكُمُ اللّٰهُ رَبِّيْ عَلَيْهِ تَوَكَّلْتُۖ وَاِلَيْهِ اُنِيْبُ', 'wa makhtalaftum fīhi min syai`in fa ḥukmuhū ilallāh, żālikumullāhu rabbī \'alaihi tawakkaltu wa ilaihi unīb'),
(4282, 42, 11, 'فَاطِرُ السَّمٰوٰتِ وَالْاَرْضِۗ جَعَلَ لَكُمْ مِّنْ اَنْفُسِكُمْ اَزْوَاجًا وَّمِنَ الْاَنْعَامِ اَزْوَاجًاۚ يَذْرَؤُكُمْ فِيْهِۗ لَيْسَ كَمِثْلِهٖ شَيْءٌ ۚوَهُوَ السَّمِيْعُ الْبَصِيْرُ', 'fāṭirus-samāwāti wal-arḍ, ja\'ala lakum min anfusikum azwājaw wa minal-an\'āmi azwājā, yażra`ukum fīh, laisa kamiṡlihī syaī`, wa huwas-samī\'ul-baṣīr'),
(4283, 42, 12, 'لَهٗ مَقَالِيْدُ السَّمٰوٰتِ وَالْاَرْضِۚ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُ ۚاِنَّهٗ بِكُلِّ شَيْءٍ عَلِيْمٌ', 'lahụ maqālīdus-samāwāti wal-arḍ, yabsuṭur-rizqa limay yasyā`u wa yaqdir, innahụ bikulli syai`in \'alīm'),
(4284, 42, 13, '۞ شَرَعَ لَكُمْ مِّنَ الدِّيْنِ مَا وَصّٰى بِهٖ نُوْحًا وَّالَّذِيْٓ اَوْحَيْنَآ اِلَيْكَ وَمَا وَصَّيْنَا بِهٖٓ اِبْرٰهِيْمَ وَمُوْسٰى وَعِيْسٰٓى اَنْ اَقِيْمُوا الدِّيْنَ وَلَا تَتَفَرَّقُوْا فِيْهِۗ كَبُرَ عَلَى الْمُشْرِكِيْنَ مَا تَدْعُوْهُمْ اِلَيْهِۗ اَللّٰهُ يَجْتَبِيْٓ اِلَيْهِ مَنْ يَّشَاۤءُ وَيَهْدِيْٓ اِلَيْهِ مَنْ يُّنِيْبُۗ', 'syara\'a lakum minad-dīni mā waṣṣā bihī nụḥaw wallażī auḥainā ilaika wa mā waṣṣainā bihī ibrāhīma wa mụsā wa \'īsā an aqīmud-dīna wa lā tatafarraqụ fīh, kabura \'alal-musyrikīna mā tad\'ụhum ilaīh, allāhu yajtabī ilaihi may yasyā`u wa yahdī ilaihi may yunīb'),
(4285, 42, 14, 'وَمَا تَفَرَّقُوْٓا اِلَّا مِنْۢ بَعْدِ مَا جَاۤءَهُمُ الْعِلْمُ بَغْيًاۢ بَيْنَهُمْۗ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَّبِّكَ اِلٰٓى اَجَلٍ مُّسَمًّى لَّقُضِيَ بَيْنَهُمْۗ وَاِنَّ الَّذِيْنَ اُوْرِثُوا الْكِتٰبَ مِنْۢ بَعْدِهِمْ لَفِيْ شَكٍّ مِّنْهُ مُرِيْبٍ', 'wa mā tafarraqū illā mim ba\'di mā jā`ahumul-\'ilmu bagyam bainahum, walau lā kalimatun sabaqat mir rabbika ilā ajalim musammal laquḍiya bainahum, wa innallażīna ụriṡul-kitāba mim ba\'dihim lafī syakkim min-hu murīb'),
(4286, 42, 15, 'فَلِذٰلِكَ فَادْعُ ۚوَاسْتَقِمْ كَمَآ اُمِرْتَۚ وَلَا تَتَّبِعْ اَهْوَاۤءَهُمْۚ وَقُلْ اٰمَنْتُ بِمَآ اَنْزَلَ اللّٰهُ مِنْ كِتٰبٍۚ وَاُمِرْتُ لِاَعْدِلَ بَيْنَكُمْ ۗ اَللّٰهُ رَبُّنَا وَرَبُّكُمْ ۗ لَنَآ اَعْمَالُنَا وَلَكُمْ اَعْمَالُكُمْ ۗ لَاحُجَّةَ بَيْنَنَا وَبَيْنَكُمْ ۗ اَللّٰهُ يَجْمَعُ بَيْنَنَا ۚوَاِلَيْهِ الْمَصِيْرُ ۗ', 'fa liżālika fad\', wastaqim kamā umirt, wa lā tattabi\' ahwā`ahum, wa qul āmantu bimā anzalallāhu ming kitāb, wa umirtu li`a\'dila bainakum, allāhu rabbunā wa rabbukum, lanā a\'mālunā wa lakum a\'mālukum, lā ḥujjata bainanā wa bainakum, allāhu yajma\'u bainanā, wa ilaihil-maṣīr'),
(4287, 42, 16, 'وَالَّذِيْنَ يُحَاۤجُّوْنَ فِى اللّٰهِ مِنْۢ بَعْدِ مَا اسْتُجِيْبَ لَهٗ حُجَّتُهُمْ دَاحِضَةٌ عِنْدَ رَبِّهِمْ وَعَلَيْهِمْ غَضَبٌ وَّلَهُمْ عَذَابٌ شَدِيْدٌ', 'wallażīna yuḥājjụna fillāhi mim ba\'di mastujība lahụ ḥujjatuhum dāḥiḍatun \'inda rabbihim wa \'alaihim gaḍabuw wa lahum \'ażābun syadīd'),
(4288, 42, 17, 'اَللّٰهُ الَّذِيْٓ اَنْزَلَ الْكِتٰبَ بِالْحَقِّ وَالْمِيْزَانَ ۗوَمَا يُدْرِيْكَ لَعَلَّ السَّاعَةَ قَرِيْبٌ', 'allāhullażī anzalal-kitāba bil-ḥaqqi wal mīzān, wa mā yudrīka la\'allas-sā\'ata qarīb'),
(4289, 42, 18, 'يَسْتَعْجِلُ بِهَا الَّذِيْنَ لَا يُؤْمِنُوْنَ بِهَاۚ وَالَّذِيْنَ اٰمَنُوْا مُشْفِقُوْنَ مِنْهَاۙ وَيَعْلَمُوْنَ اَنَّهَا الْحَقُّ ۗ اَلَآ اِنَّ الَّذِيْنَ يُمَارُوْنَ فِى السَّاعَةِ لَفِيْ ضَلٰلٍۢ بَعِيْدٍ', 'yasta\'jilu bihallażīna lā yu`minụna bihā, wallażīna āmanụ musyfiqụna min-hā wa ya\'lamụna annahal-ḥaqq, alā innallażīna yumārụna fis-sā\'ati lafī ḍalālim ba\'īd'),
(4290, 42, 19, 'اَللّٰهُ لَطِيْفٌۢ بِعِبَادِهٖ يَرْزُقُ مَنْ يَّشَاۤءُ ۚوَهُوَ الْقَوِيُّ الْعَزِيْزُ', 'allāhu laṭīfum bi\'ibādihī yarzuqu may yasyā`, wa huwal-qawiyyul-\'azīz'),
(4291, 42, 20, 'مَنْ كَانَ يُرِيْدُ حَرْثَ الْاٰخِرَةِ نَزِدْ لَهٗ فِيْ حَرْثِهٖۚ وَمَنْ كَانَ يُرِيْدُ حَرْثَ الدُّنْيَا نُؤْتِهٖ مِنْهَاۙ وَمَا لَهٗ فِى الْاٰخِرَةِ مِنْ نَّصِيْبٍ', 'mang kāna yurīdu ḥarṡal-ākhirati nazid lahụ fī ḥarṡih, wa mang kāna yurīdu ḥarṡad-dun-yā nu`tihī min-hā wa mā lahụ fil-ākhirati min naṣīb'),
(4292, 42, 21, 'اَمْ لَهُمْ شُرَكٰۤؤُا شَرَعُوْا لَهُمْ مِّنَ الدِّيْنِ مَا لَمْ يَأْذَنْۢ بِهِ اللّٰهُ ۗوَلَوْلَا كَلِمَةُ الْفَصْلِ لَقُضِيَ بَيْنَهُمْ ۗوَاِنَّ الظّٰلِمِيْنَ لَهُمْ عَذَابٌ اَلِيْمٌ', 'am lahum syurakā`u syara\'ụ lahum minad-dīni mā lam ya`żam bihillāh, walau lā kalimatul-faṣli laquḍiya bainahum, wa innaẓ-ẓālimīna lahum \'ażābun alīm'),
(4293, 42, 22, 'تَرَى الظّٰلِمِيْنَ مُشْفِقِيْنَ مِمَّا كَسَبُوْا وَهُوَ وَاقِعٌۢ بِهِمْ ۗوَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فِيْ رَوْضٰتِ الْجَنّٰتِۚ لَهُمْ مَّا يَشَاۤءُوْنَ عِنْدَ رَبِّهِمْ ۗذٰلِكَ هُوَ الْفَضْلُ الْكَبِيْرُ', 'taraẓ-ẓālimīna musyfiqīna mimmā kasabụ wa huwa wāqi\'um bihim, wallażīna āmanụ wa \'amiluṣ-ṣāliḥāti fī rauḍātil-jannāt, lahum mā yasyā`ụna \'inda rabbihim, żālika huwal-faḍlul-kabīr'),
(4294, 42, 23, 'ذٰلِكَ الَّذِيْ يُبَشِّرُ اللّٰهُ عِبَادَهُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِۗ قُلْ لَّآ اَسْـَٔلُكُمْ عَلَيْهِ اَجْرًا اِلَّا الْمَوَدَّةَ فِى الْقُرْبٰىۗ وَمَنْ يَّقْتَرِفْ حَسَنَةً نَّزِدْ لَهٗ فِيْهَا حُسْنًا ۗاِنَّ اللّٰهَ غَفُوْرٌ شَكُوْرٌ', 'żālikallażī yubasysyirullāhu \'ibādahullażīna āmanụ wa \'amiluṣ-ṣāliḥāt, qul lā as`alukum \'alaihi ajran illal-mawaddata fil-qurbā, wa may yaqtarif ḥasanatan nazid lahụ fīhā ḥusnā, innallāha gafụrun syakụr'),
(4295, 42, 24, 'اَمْ يَقُوْلُوْنَ افْتَرٰى عَلَى اللّٰهِ كَذِبًاۚ فَاِنْ يَّشَاِ اللّٰهُ يَخْتِمْ عَلٰى قَلْبِكَ ۗوَيَمْحُ اللّٰهُ الْبَاطِلَ وَيُحِقُّ الْحَقَّ بِكَلِمٰتِهٖ ۗاِنَّهٗ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ', 'am yaqụlụnaftarā \'alallāhi każibā, fa iy yasya`illāhu yakhtim \'alā qalbik, wa yam-ḥullāhul-bāṭila wa yuḥiqqul-ḥaqqa bikalimātih, innahụ \'alīmum biżātiṣ-ṣudụr'),
(4296, 42, 25, 'وَهُوَ الَّذِيْ يَقْبَلُ التَّوْبَةَ عَنْ عِبَادِهٖ وَيَعْفُوْا عَنِ السَّيِّاٰتِ وَيَعْلَمُ مَا تَفْعَلُوْنَۙ', 'wa huwallażī yaqbalut-taubata \'an \'ibādihī wa ya\'fụ \'anis-sayyi`āti wa ya\'lamu mā taf\'alụn'),
(4297, 42, 26, 'وَيَسْتَجِيْبُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَيَزِيْدُهُمْ مِّنْ فَضْلِهٖ ۗوَالْكٰفِرُوْنَ لَهُمْ عَذَابٌ شَدِيْدٌ', 'wa yastajībullażīna āmanụ wa \'amiluṣ-ṣāliḥāti wa yazīduhum min faḍlih, wal-kāfirụna lahum \'ażābun syadīd'),
(4298, 42, 27, '۞ وَلَوْ بَسَطَ اللّٰهُ الرِّزْقَ لِعِبَادِهٖ لَبَغَوْا فِى الْاَرْضِ وَلٰكِنْ يُنَزِّلُ بِقَدَرٍ مَّا يَشَاۤءُ ۗاِنَّهٗ بِعِبَادِهٖ خَبِيْرٌۢ بَصِيْرٌ', 'walau basaṭallāhur-rizqa li\'ibādihī labagau fil-arḍi wa lākiy yunazzilu biqadarim mā yasyā`, innahụ bi\'ibādihī khabīrum baṣīr'),
(4299, 42, 28, 'وَهُوَ الَّذِيْ يُنَزِّلُ الْغَيْثَ مِنْۢ بَعْدِ مَا قَنَطُوْا وَيَنْشُرُ رَحْمَتَهٗ ۗوَهُوَ الْوَلِيُّ الْحَمِيْدُ', 'wa huwallażī yunazzilul-gaiṡa mim ba\'di mā qanaṭụ wa yansyuru raḥmatah, wa huwal-waliyyul-ḥamīd'),
(4300, 42, 29, 'وَمِنْ اٰيٰتِهٖ خَلْقُ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَثَّ فِيْهِمَا مِنْ دَاۤبَّةٍ ۗوَهُوَ عَلٰى جَمْعِهِمْ اِذَا يَشَاۤءُ قَدِيْرٌ', 'wa min āyātihī khalqus-samāwāti wal-arḍi wa mā baṡṡa fīhimā min dābbah, wa huwa \'alā jam\'ihim iżā yasyā`u qadīr'),
(4301, 42, 30, 'وَمَآ اَصَابَكُمْ مِّنْ مُّصِيْبَةٍ فَبِمَا كَسَبَتْ اَيْدِيْكُمْ وَيَعْفُوْا عَنْ كَثِيْرٍۗ', 'wa mā aṣābakum mim muṣībatin fa bimā kasabat aidīkum wa ya\'fụ \'ang kaṡīr'),
(4302, 42, 31, 'وَمَآ اَنْتُمْ بِمُعْجِزِيْنَ فِى الْاَرْضِۚ وَمَا لَكُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ', 'wa mā antum bimu\'jizīna fil-arḍ, wa mā lakum min dụnillāhi miw waliyyiw wa lā naṣīr'),
(4303, 42, 32, 'وَمِنْ اٰيٰتِهِ الْجَوَارِ فِى الْبَحْرِ كَالْاَعْلَامِ ۗ', 'wa min āyātihil-jawāri fil-baḥri kal-a\'lām'),
(4304, 42, 33, 'اِنْ يَّشَأْ يُسْكِنِ الرِّيْحَ فَيَظْلَلْنَ رَوَاكِدَ عَلٰى ظَهْرِهٖۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّكُلِّ صَبَّارٍ شَكُوْرٍۙ', 'iy yasya` yuskinir-rīḥa fa yaẓlalna rawākida \'alā ẓahrih, inna fī żālika la`āyātil likulli ṣabbārin syakụr'),
(4305, 42, 34, 'اَوْ يُوْبِقْهُنَّ بِمَا كَسَبُوْا وَيَعْفُ عَنْ كَثِيْرٍۙ', 'au yụbiq-hunna bimā kasabụ wa ya\'fu \'ang kaṡīr'),
(4306, 42, 35, 'وَّيَعْلَمَ الَّذِيْنَ يُجَادِلُوْنَ فِيْٓ اٰيٰتِنَاۗ مَا لَهُمْ مِّنْ مَّحِيْصٍ', 'wa ya\'lamallażīna yujādilụna fī āyātinā, mā lahum mim maḥīṣ'),
(4307, 42, 36, 'فَمَآ اُوْتِيْتُمْ مِّنْ شَيْءٍ فَمَتَاعُ الْحَيٰوةِ الدُّنْيَا ۚوَمَا عِنْدَ اللّٰهِ خَيْرٌ وَّاَبْقٰى لِلَّذِيْنَ اٰمَنُوْا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُوْنَۚ', 'fa mā ụtītum min syai`in fa matā\'ul-ḥayātid-dun-yā, wa mā \'indallāhi khairuw wa abqā lillażīna āmanụ wa \'alā rabbihim yatawakkalụn'),
(4308, 42, 37, 'وَالَّذِيْنَ يَجْتَنِبُوْنَ كَبٰۤىِٕرَ الْاِثْمِ وَالْفَوَاحِشَ وَاِذَا مَا غَضِبُوْا هُمْ يَغْفِرُوْنَ ۚ', 'wallażīna yajtanibụna kabā`iral-iṡmi wal-fawāḥisya wa iżā mā gaḍibụ hum yagfirụn'),
(4309, 42, 38, 'وَالَّذِيْنَ اسْتَجَابُوْا لِرَبِّهِمْ وَاَقَامُوا الصَّلٰوةَۖ وَاَمْرُهُمْ شُوْرٰى بَيْنَهُمْۖ وَمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَ ۚ', 'wallażīnastajābụ lirabbihim wa aqāmuṣ-ṣalāta wa amruhum syụrā bainahum wa mimmā razaqnāhum yunfiqụn'),
(4310, 42, 39, 'وَالَّذِيْنَ اِذَآ اَصَابَهُمُ الْبَغْيُ هُمْ يَنْتَصِرُوْنَ', 'wallażīna iżā aṣābahumul-bagyu hum yantaṣirụn'),
(4311, 42, 40, 'وَجَزٰۤؤُا سَيِّئَةٍ سَيِّئَةٌ مِّثْلُهَا ۚفَمَنْ عَفَا وَاَصْلَحَ فَاَجْرُهٗ عَلَى اللّٰهِ ۗاِنَّهٗ لَا يُحِبُّ الظّٰلِمِيْنَ', 'wa jazā`u sayyi`atin sayyi`atum miṡluhā, fa man \'afā wa aṣlaḥa fa ajruhụ \'alallāh, innahụ lā yuḥibbuẓ-ẓālimīn'),
(4312, 42, 41, 'وَلَمَنِ انْتَصَرَ بَعْدَ ظُلْمِهٖ فَاُولٰۤىِٕكَ مَا عَلَيْهِمْ مِّنْ سَبِيْلٍۗ', 'wa lamanintaṣara ba\'da ẓulmihī fa ulā`ika mā \'alaihim min sabīl'),
(4313, 42, 42, 'اِنَّمَا السَّبِيْلُ عَلَى الَّذِيْنَ يَظْلِمُوْنَ النَّاسَ وَيَبْغُوْنَ فِى الْاَرْضِ بِغَيْرِ الْحَقِّۗ اُولٰۤىِٕكَ لَهُمْ عَذَابٌ اَلِيْمٌ', 'innamas-sabīlu \'alallażīna yaẓlimụnan-nāsa wa yabgụna fil-arḍi bigairil-ḥaqq, ulā`ika lahum \'ażābun alīm'),
(4314, 42, 43, 'وَلَمَنْ صَبَرَ وَغَفَرَ اِنَّ ذٰلِكَ لَمِنْ عَزْمِ الْاُمُوْرِ', 'wa laman ṣabara wa gafara inna żālika lamin \'azmil-umụr'),
(4315, 42, 44, 'وَمَنْ يُّضْلِلِ اللّٰهُ فَمَا لَهٗ مِنْ وَّلِيٍّ مِّنْۢ بَعْدِهٖ ۗوَتَرَى الظّٰلِمِيْنَ لَمَّا رَاَوُا الْعَذَابَ يَقُوْلُوْنَ هَلْ اِلٰى مَرَدٍّ مِّنْ سَبِيْلٍۚ', 'wa may yuḍlilillāhu fa mā lahụ miw waliyyim mim ba\'dih, wa taraẓ-ẓālimīna lammā ra`awul-\'ażāba yaqụlụna hal ilā maraddim min sabīl'),
(4316, 42, 45, 'وَتَرٰىهُمْ يُعْرَضُوْنَ عَلَيْهَا خٰشِعِيْنَ مِنَ الذُّلِّ يَنْظُرُوْنَ مِنْ طَرْفٍ خَفِيٍّۗ وَقَالَ الَّذِيْنَ اٰمَنُوْٓا اِنَّ الْخٰسِرِيْنَ الَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ وَاَهْلِيْهِمْ يَوْمَ الْقِيٰمَةِ ۗ اَلَآ اِنَّ الظّٰلِمِيْنَ فِيْ عَذَابٍ مُّقِيْمٍ', 'wa tarāhum yu\'raḍụna \'alaihā khāsyi\'īna minaż-żulli yanẓurụna min ṭarfin khafiyy, wa qālallażīna āmanū innal-khāsirīnallażīna khasirū anfusahum wa ahlīhim yaumal-qiyāmah, alā innaẓ-ẓālimīna fī \'ażābim muqīm'),
(4317, 42, 46, 'وَمَا كَانَ لَهُمْ مِّنْ اَوْلِيَاۤءَ يَنْصُرُوْنَهُمْ مِّنْ دُوْنِ اللّٰهِ ۗوَمَنْ يُّضْلِلِ اللّٰهُ فَمَا لَهٗ مِنْ سَبِيْلٍ ۗ', 'wa mā kāna lahum min auliyā`a yanṣurụnahum min dụnillāh, wa may yuḍlilillāhu fa mā lahụ min sabīl');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(4318, 42, 47, 'اِسْتَجِيْبُوْا لِرَبِّكُمْ مِّنْ قَبْلِ اَنْ يَّأْتِيَ يَوْمٌ لَّا مَرَدَّ لَهٗ مِنَ اللّٰهِ ۗمَا لَكُمْ مِّنْ مَّلْجَاٍ يَّوْمَىِٕذٍ وَّمَا لَكُمْ مِّنْ نَّكِيْرٍ', 'istajībụ lirabbikum ming qabli ay ya`tiya yaumul lā maradda lahụ minallāh, mā lakum mim malja`iy yauma`iżiw wa mā lakum min nakīr'),
(4319, 42, 48, 'فَاِنْ اَعْرَضُوْا فَمَآ اَرْسَلْنٰكَ عَلَيْهِمْ حَفِيْظًا ۗاِنْ عَلَيْكَ اِلَّا الْبَلٰغُ ۗوَاِنَّآ اِذَآ اَذَقْنَا الْاِنْسَانَ مِنَّا رَحْمَةً فَرِحَ بِهَا ۚوَاِنْ تُصِبْهُمْ سَيِّئَةٌ ۢبِمَا قَدَّمَتْ اَيْدِيْهِمْ فَاِنَّ الْاِنْسَانَ كَفُوْرٌ', 'fa in a\'raḍụ fa mā arsalnāka \'alaihim ḥafīẓā, in \'alaika illal-balāg, wa innā iżā ażaqnal-insāna minnā raḥmatan fariḥa bihā, wa in tuṣib-hum sayyi`atum bimā qaddamat aidīhim fa innal-insāna kafụr'),
(4320, 42, 49, 'لِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ يَخْلُقُ مَا يَشَاۤءُ ۗيَهَبُ لِمَنْ يَّشَاۤءُ اِنَاثًا وَّيَهَبُ لِمَنْ يَّشَاۤءُ الذُّكُوْرَ ۙ', 'lillahi mulkus-samāwāti wal-arḍ, yakhluqu mā yasyā`, yahabu limay yasyā`u ināṡaw wa yahabu limay yasyā`uż-żukụr'),
(4321, 42, 50, 'اَوْ يُزَوِّجُهُمْ ذُكْرَانًا وَّاِنَاثًا ۚوَيَجْعَلُ مَنْ يَّشَاۤءُ عَقِيْمًا ۗاِنَّهٗ عَلِيْمٌ قَدِيْرٌ', 'au yuzawwijuhum żukrānaw wa ināṡā, wa yaj\'alu may yasyā`u \'aqīmā, innahụ \'alīmung qadīr'),
(4322, 42, 51, '۞ وَمَا كَانَ لِبَشَرٍ اَنْ يُّكَلِّمَهُ اللّٰهُ اِلَّا وَحْيًا اَوْ مِنْ وَّرَاۤئِ حِجَابٍ اَوْ يُرْسِلَ رَسُوْلًا فَيُوْحِيَ بِاِذْنِهٖ مَا يَشَاۤءُ ۗاِنَّهٗ عَلِيٌّ حَكِيْمٌ', 'wa mā kāna libasyarin ay yukallimahullāhu illā waḥyan au miw warā`i ḥijābin au yursila rasụlan fa yụḥiya bi`iżnihī mā yasyā`, innahụ \'aliyyun ḥakīm'),
(4323, 42, 52, 'وَكَذٰلِكَ اَوْحَيْنَآ اِلَيْكَ رُوْحًا مِّنْ اَمْرِنَا ۗمَا كُنْتَ تَدْرِيْ مَا الْكِتٰبُ وَلَا الْاِيْمَانُ وَلٰكِنْ جَعَلْنٰهُ نُوْرًا نَّهْدِيْ بِهٖ مَنْ نَّشَاۤءُ مِنْ عِبَادِنَا ۗوَاِنَّكَ لَتَهْدِيْٓ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍۙ', 'wa każālika auḥainā ilaika rụḥam min amrinā, mā kunta tadrī mal-kitābu wa lal-īmānu wa lākin ja\'alnāhu nụran nahdī bihī man nasyā`u min \'ibādinā, wa innaka latahdī ilā ṣirāṭim mustaqīm'),
(4324, 42, 53, 'صِرَاطِ اللّٰهِ الَّذِيْ لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ اَلَآ اِلَى اللّٰهِ تَصِيْرُ الْاُمُوْرُ', 'ṣirāṭillāhillażī lahụ mā fis-samāwāti wa mā fil-arḍ, alā ilallāhi taṣīrul-umụr'),
(4325, 43, 1, ' حٰمۤ ۚ', 'ḥā mīm'),
(4326, 43, 2, 'وَالْكِتٰبِ الْمُبِيْنِ ۙ', 'wal-kitābil-mubīn'),
(4327, 43, 3, 'اِنَّا جَعَلْنٰهُ قُرْاٰنًا عَرَبِيًّا لَّعَلَّكُمْ تَعْقِلُوْنَۚ', 'innā ja\'alnāhu qur`ānan \'arabiyyal la\'allakum ta\'qilụn'),
(4328, 43, 4, 'وَاِنَّهٗ فِيْٓ اُمِّ الْكِتٰبِ لَدَيْنَا لَعَلِيٌّ حَكِيْمٌ ۗ', 'wa innahụ fī ummil-kitābi ladainā la\'aliyyun ḥakīm'),
(4329, 43, 5, 'اَفَنَضْرِبُ عَنْكُمُ الذِّكْرَ صَفْحًا اَنْ كُنْتُمْ قَوْمًا مُّسْرِفِيْنَ', 'a fa naḍribu \'angkumuż-żikra ṣaf-ḥan ang kuntum qaumam musrifīn'),
(4330, 43, 6, 'وَكَمْ اَرْسَلْنَا مِنْ نَّبِيٍّ فِى الْاَوَّلِيْنَ', 'wa kam arsalnā min nabiyyin fil-awwalīn'),
(4331, 43, 7, 'وَمَا يَأْتِيْهِمْ مِّنْ نَّبِيٍّ اِلَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ', 'wa mā ya`tīhim min nabiyyin illā kānụ bihī yastahzi`ụn'),
(4332, 43, 8, 'فَاَهْلَكْنَٓا اَشَدَّ مِنْهُمْ بَطْشًا وَّمَضٰى مَثَلُ الْاَوَّلِيْنَ', 'fa ahlaknā asyadda min-hum baṭsyaw wa maḍā maṡalul-awwalīn'),
(4333, 43, 9, 'وَلَىِٕنْ سَاَلْتَهُمْ مَّنْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ لَيَقُوْلُنَّ خَلَقَهُنَّ الْعَزِيْزُ الْعَلِيْمُۙ', 'wa la`in sa`altahum man khalaqas-samāwāti wal-arḍa layaqụlunna khalaqahunnal-\'azīzul-\'alīm'),
(4334, 43, 10, 'الَّذِيْ جَعَلَ لَكُمُ الْاَرْضَ مَهْدًا وَّجَعَلَ لَكُمْ فِيْهَا سُبُلًا لَّعَلَّكُمْ تَهْتَدُوْنَ ۚ', 'allażī ja\'ala lakumul-arḍa mahdaw wa ja\'ala lakum fīhā subulal la\'allakum tahtadụn'),
(4335, 43, 11, 'وَالَّذِيْ نَزَّلَ مِنَ السَّمَاۤءِ مَاۤءًۢ بِقَدَرٍۚ فَاَنْشَرْنَا بِهٖ بَلْدَةً مَّيْتًا ۚ كَذٰلِكَ تُخْرَجُوْنَ', 'wallażī nazzala minas-samā`i mā`am biqadar, fa ansyarnā bihī baldatam maitā, każālika tukhrajụn'),
(4336, 43, 12, 'وَالَّذِيْ خَلَقَ الْاَزْوَاجَ كُلَّهَا وَجَعَلَ لَكُمْ مِّنَ الْفُلْكِ وَالْاَنْعَامِ مَا تَرْكَبُوْنَۙ', 'wallażī khalaqal-azwāja kullahā wa ja\'ala lakum minal-fulki wal-an\'āmi mā tarkabụn'),
(4337, 43, 13, 'لِتَسْتَوٗا عَلٰى ظُهُوْرِهٖ ثُمَّ تَذْكُرُوْا نِعْمَةَ رَبِّكُمْ اِذَا اسْتَوَيْتُمْ عَلَيْهِ وَتَقُوْلُوْا سُبْحٰنَ الَّذِيْ سَخَّرَ لَنَا هٰذَا وَمَا كُنَّا لَهٗ مُقْرِنِيْنَۙ', 'litastawụ \'alā ẓuhụrihī ṡumma tażkurụ ni\'mata rabbikum iżastawaitum \'alaihi wa taqụlụ sub-ḥānallażī sakhkhara lanā hāżā wa mā kunnā lahụ muqrinīn'),
(4338, 43, 14, 'وَاِنَّآ اِلٰى رَبِّنَا لَمُنْقَلِبُوْنَ', 'wa innā ilā rabbinā lamungqalibụn'),
(4339, 43, 15, 'وَجَعَلُوْا لَهٗ مِنْ عِبَادِهٖ جُزْءًا ۗاِنَّ الْاِنْسَانَ لَكَفُوْرٌ مُّبِيْنٌ ۗ', 'wa ja\'alụ lahụ min \'ibādihī juz`ā, innal-insāna lakafụrum mubīn'),
(4340, 43, 16, 'اَمِ اتَّخَذَ مِمَّا يَخْلُقُ بَنٰتٍ وَّاَصْفٰىكُمْ بِالْبَنِيْنَ ۗ', 'amittakhaża mimmā yakhluqu banātiw wa aṣfākum bil-banīn'),
(4341, 43, 17, 'وَاِذَا بُشِّرَ اَحَدُهُمْ بِمَا ضَرَبَ لِلرَّحْمٰنِ مَثَلًا ظَلَّ وَجْهُهٗ مُسْوَدًّا وَّهُوَ كَظِيْمٌ', 'wa iżā busysyira aḥaduhum bimā ḍaraba lir-raḥmāni maṡalan ẓalla waj-huhụ muswaddaw wa huwa kaẓīm'),
(4342, 43, 18, 'اَوَمَنْ يُّنَشَّؤُا فِى الْحِلْيَةِ وَهُوَ فِى الْخِصَامِ غَيْرُ مُبِيْنٍ', 'a wa may yunasysya`u fil-ḥilyati wa huwa fil-khiṣāmi gairu mubīn'),
(4343, 43, 19, 'وَجَعَلُوا الْمَلٰۤىِٕكَةَ الَّذِيْنَ هُمْ عِبٰدُ الرَّحْمٰنِ اِنَاثًا ۗ اَشَهِدُوْا خَلْقَهُمْ ۗسَتُكْتَبُ شَهَادَتُهُمْ وَيُسْٔـَلُوْنَ', 'wa ja\'alul-malā`ikatallażīna hum \'ibādur-raḥmāni ināṡā, a syahidụ khalqahum, satuktabu syahādatuhum wa yus`alụn'),
(4344, 43, 20, 'وَقَالُوْا لَوْ شَاۤءَ الرَّحْمٰنُ مَا عَبَدْنٰهُمْ ۗمَا لَهُمْ بِذٰلِكَ مِنْ عِلْمٍ اِنْ هُمْ اِلَّا يَخْرُصُوْنَۗ', 'wa qālụ lau syā`ar-raḥmānu mā \'abadnāhum, mā lahum biżālika min \'ilmin in hum illā yakhruṣụn'),
(4345, 43, 21, 'اَمْ اٰتَيْنٰهُمْ كِتٰبًا مِّنْ قَبْلِهٖ فَهُمْ بِهٖ مُسْتَمْسِكُوْنَ', 'am ātaināhum kitābam ming qablihī fa hum bihī mustamsikụn'),
(4346, 43, 22, 'بَلْ قَالُوْٓا اِنَّا وَجَدْنَآ اٰبَاۤءَنَا عَلٰٓى اُمَّةٍ وَّاِنَّا عَلٰٓى اٰثٰرِهِمْ مُّهْتَدُوْنَ', 'bal qālū innā wajadnā ābā`anā \'alā ummatiw wa innā \'alā āṡārihim muhtadụn'),
(4347, 43, 23, 'وَكَذٰلِكَ مَآ اَرْسَلْنَا مِنْ قَبْلِكَ فِيْ قَرْيَةٍ مِّنْ نَّذِيْرٍۙ اِلَّا قَالَ مُتْرَفُوْهَآ ۙاِنَّا وَجَدْنَآ اٰبَاۤءَنَا عَلٰٓى اُمَّةٍ وَّاِنَّا عَلٰٓى اٰثٰرِهِمْ مُّقْتَدُوْنَ', 'wa każālika mā arsalnā ming qablika fī qaryatim min nażīrin illā qāla mutrafụhā innā wajadnā ābā`anā \'alā ummatiw wa innā \'alā āṡārihim muqtadụn'),
(4348, 43, 24, '۞ قٰلَ اَوَلَوْ جِئْتُكُمْ بِاَهْدٰى مِمَّا وَجَدْتُّمْ عَلَيْهِ اٰبَاۤءَكُمْۗ قَالُوْٓا اِنَّا بِمَآ اُرْسِلْتُمْ بِهٖ كٰفِرُوْنَ', 'qāla a walau ji`tukum bi`ahdā mimmā wajattum \'alaihi ābā`akum, qālū innā bimā ursiltum bihī kāfirụn'),
(4349, 43, 25, 'فَانْتَقَمْنَا مِنْهُمْ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِيْنَ', 'fantaqamnā min-hum fanẓur kaifa kāna \'āqibatul-mukażżibīn'),
(4350, 43, 26, 'وَاِذْ قَالَ اِبْرٰهِيْمُ لِاَبِيْهِ وَقَوْمِهٖٓ اِنَّنِيْ بَرَاۤءٌ مِّمَّا تَعْبُدُوْنَۙ', 'wa iż qāla ibrāhīmu li`abīhi wa qaumihī innanī barā`um mimmā ta\'budụn'),
(4351, 43, 27, 'اِلَّا الَّذِيْ فَطَرَنِيْ فَاِنَّهٗ سَيَهْدِيْنِ', 'illallażī faṭaranī fa innahụ sayahdīn'),
(4352, 43, 28, 'وَجَعَلَهَا كَلِمَةً ۢ بَاقِيَةً فِيْ عَقِبِهٖ لَعَلَّهُمْ يَرْجِعُوْنَۗ', 'wa ja\'alahā kalimatam bāqiyatan fī \'aqibihī la\'allahum yarji\'ụn'),
(4353, 43, 29, 'بَلْ مَتَّعْتُ هٰٓؤُلَاۤءِ وَاٰبَاۤءَهُمْ حَتّٰى جَاۤءَهُمُ الْحَقُّ وَرَسُوْلٌ مُّبِيْنٌ', 'bal matta\'tu hā`ulā`i wa ābā`ahum ḥattā jā`ahumul-ḥaqqu wa rasụlum mubīn'),
(4354, 43, 30, 'وَلَمَّا جَاۤءَهُمُ الْحَقُّ قَالُوْا هٰذَا سِحْرٌ وَّاِنَّا بِهٖ كٰفِرُوْنَ', 'wa lammā jā`ahumul-ḥaqqu qālụ hāżā siḥruw wa innā bihī kāfirụn'),
(4355, 43, 31, 'وَقَالُوْا لَوْلَا نُزِّلَ هٰذَا الْقُرْاٰنُ عَلٰى رَجُلٍ مِّنَ الْقَرْيَتَيْنِ عَظِيْمٍ', 'wa qālụ lau lā nuzzila hāżal-qur`ānu \'alā rajulim minal-qaryataini \'aẓīm'),
(4356, 43, 32, 'اَهُمْ يَقْسِمُوْنَ رَحْمَتَ رَبِّكَۗ نَحْنُ قَسَمْنَا بَيْنَهُمْ مَّعِيْشَتَهُمْ فِى الْحَيٰوةِ الدُّنْيَاۙ وَرَفَعْنَا بَعْضَهُمْ فَوْقَ بَعْضٍ دَرَجٰتٍ لِّيَتَّخِذَ بَعْضُهُمْ بَعْضًا سُخْرِيًّا ۗوَرَحْمَتُ رَبِّكَ خَيْرٌ مِّمَّا يَجْمَعُوْنَ', 'a hum yaqsimụna raḥmata rabbik, naḥnu qasamnā bainahum ma\'īsyatahum fil-ḥayātid-dun-yā wa rafa\'nā ba\'ḍahum fauqa ba\'ḍin darajātil liyattakhiża ba\'ḍuhum ba\'ḍan sukhriyyā, wa raḥmatu rabbika khairum mimmā yajma\'ụn'),
(4357, 43, 33, 'وَلَوْلَآ اَنْ يَّكُوْنَ النَّاسُ اُمَّةً وَّاحِدَةً لَّجَعَلْنَا لِمَنْ يَّكْفُرُ بِالرَّحْمٰنِ لِبُيُوْتِهِمْ سُقُفًا مِّنْ فِضَّةٍ وَّمَعَارِجَ عَلَيْهَا يَظْهَرُوْنَۙ', 'walau lā ay yakụnan-nāsu ummataw wāḥidatal laja\'alnā limay yakfuru bir-raḥmāni libuyụtihim suqufam min fiḍḍatiw wa ma\'ārija \'alaihā yaẓ-harụn'),
(4358, 43, 34, 'وَلِبُيُوْتِهِمْ اَبْوَابًا وَّسُرُرًا عَلَيْهَا يَتَّكِـُٔوْنَۙ', 'wa libuyụtihim abwābaw wa sururan \'alaihā yattaki`ụn'),
(4359, 43, 35, 'وَزُخْرُفًاۗ وَاِنْ كُلُّ ذٰلِكَ لَمَّا مَتَاعُ الْحَيٰوةِ الدُّنْيَا ۗوَالْاٰخِرَةُ عِنْدَ رَبِّكَ لِلْمُتَّقِيْنَ', 'wa zukhrufā, wa ing kullu żālika lammā matā\'ul-ḥayātid-dun-yā, wal-ākhiratu \'inda rabbika lil-muttaqīn'),
(4360, 43, 36, 'وَمَنْ يَّعْشُ عَنْ ذِكْرِ الرَّحْمٰنِ نُقَيِّضْ لَهٗ شَيْطٰنًا فَهُوَ لَهٗ قَرِيْنٌ', 'wa may ya\'syu \'an żikrir-raḥmāni nuqayyiḍ lahụ syaiṭānan fa huwa lahụ qarīn'),
(4361, 43, 37, 'وَاِنَّهُمْ لَيَصُدُّوْنَهُمْ عَنِ السَّبِيْلِ وَيَحْسَبُوْنَ اَنَّهُمْ مُّهْتَدُوْنَ', 'wa innahum layaṣuddụnahum \'anis-sabīli wa yaḥsabụna annahum muhtadụn'),
(4362, 43, 38, 'حَتّٰىٓ اِذَا جَاۤءَنَا قَالَ يٰلَيْتَ بَيْنِيْ وَبَيْنَكَ بُعْدَ الْمَشْرِقَيْنِ فَبِئْسَ الْقَرِيْنُ', 'ḥattā iżā jā`anā qāla yā laita bainī wa bainaka bu\'dal-masyriqaini fa bi`sal-qarīn'),
(4363, 43, 39, 'وَلَنْ يَّنْفَعَكُمُ الْيَوْمَ اِذْ ظَّلَمْتُمْ اَنَّكُمْ فِى الْعَذَابِ مُشْتَرِكُوْنَ', 'wa lay yanfa\'akumul-yauma iẓ ẓalamtum annakum fil-\'ażābi musytarikụn'),
(4364, 43, 40, 'اَفَاَنْتَ تُسْمِعُ الصُّمَّ اَوْ تَهْدِى الْعُمْيَ وَمَنْ كَانَ فِيْ ضَلٰلٍ مُّبِيْنٍ', 'a fa anta tusmi\'uṣ-ṣumma au tahdil-\'umya wa mang kāna fī ḍalālim mubīn'),
(4365, 43, 41, 'فَاِمَّا نَذْهَبَنَّ بِكَ فَاِنَّا مِنْهُمْ مُّنْتَقِمُوْنَۙ', 'fa immā naż-habanna bika fa innā min-hum muntaqimụn'),
(4366, 43, 42, 'اَوْ نُرِيَنَّكَ الَّذِيْ وَعَدْنٰهُمْ فَاِنَّا عَلَيْهِمْ مُّقْتَدِرُوْنَ', 'au nuriyannakallażī wa\'adnāhum fa innā \'alaihim muqtadirụn'),
(4367, 43, 43, 'فَاسْتَمْسِكْ بِالَّذِيْٓ اُوْحِيَ اِلَيْكَ ۚاِنَّكَ عَلٰى صِرَاطٍ مُّسْتَقِيْمٍ', 'fastamsik billażī ụḥiya ilaīk, innaka \'alā ṣirāṭim mustaqīm'),
(4368, 43, 44, 'وَاِنَّهٗ لَذِكْرٌ لَّكَ وَلِقَوْمِكَ ۚوَسَوْفَ تُسْٔـَلُوْنَ', 'wa innahụ lażikrul laka wa liqaumik, wa saufa tus`alụn'),
(4369, 43, 45, 'وَسْٔـَلْ مَنْ اَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رُّسُلِنَآ ۖ اَجَعَلْنَا مِنْ دُوْنِ الرَّحْمٰنِ اٰلِهَةً يُّعْبَدُوْنَ', 'was`al man arsalnā ming qablika mir rusulinā a ja\'alnā min dụnir-raḥmāni ālihatay yu\'badụn'),
(4370, 43, 46, 'وَلَقَدْ اَرْسَلْنَا مُوْسٰى بِاٰيٰتِنَآ اِلٰى فِرْعَوْنَ وَمَلَا۟ىِٕهٖ فَقَالَ اِنِّيْ رَسُوْلُ رَبِّ الْعٰلَمِيْنَ', 'wa laqad arsalnā mụsā bi`āyātinā ilā fir\'auna wa mala`ihī fa qāla innī rasụlu rabbil-\'ālamīn'),
(4371, 43, 47, 'فَلَمَّا جَاۤءَهُمْ بِاٰيٰتِنَآ اِذَا هُمْ مِّنْهَا يَضْحَكُوْنَ', 'fa lammā jā`ahum bi`āyātinā iżā hum min-hā yaḍ-ḥakụn'),
(4372, 43, 48, 'وَمَا نُرِيْهِمْ مِّنْ اٰيَةٍ اِلَّا هِيَ اَكْبَرُ مِنْ اُخْتِهَاۗ وَاَخَذْنٰهُمْ بِالْعَذَابِ لَعَلَّهُمْ يَرْجِعُوْنَ', 'wa mā nurīhim min āyatin illā hiya akbaru min ukhtihā, wa akhażnāhum bil-\'ażābi la\'allahum yarji\'ụn'),
(4373, 43, 49, 'وَقَالُوْا يٰٓاَيُّهَ السّٰحِرُ ادْعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِنْدَكَۚ اِنَّنَا لَمُهْتَدُوْنَ', 'wa qālụ yā ayyuhas-sāḥirud\'u lanā rabbaka bimā \'ahida \'indak, innanā lamuhtadụn'),
(4374, 43, 50, 'فَلَمَّا كَشَفْنَا عَنْهُمُ الْعَذَابَ اِذَا هُمْ يَنْكُثُوْنَ', 'fa lammā kasyafnā \'an-humul-\'ażāba iżā hum yangkuṡụn'),
(4375, 43, 51, 'وَنَادٰى فِرْعَوْنُ فِيْ قَوْمِهٖ قَالَ يٰقَوْمِ اَلَيْسَ لِيْ مُلْكُ مِصْرَ وَهٰذِهِ الْاَنْهٰرُ تَجْرِيْ مِنْ تَحْتِيْۚ اَفَلَا تُبْصِرُوْنَۗ', 'wa nādā fir\'aunu fī qaumihī qāla yā qaumi a laisa lī mulku miṣra wa hāżihil-an-hāru tajrī min taḥtī, a fa lā tubṣirụn'),
(4376, 43, 52, 'اَمْ اَنَا۠ خَيْرٌ مِّنْ هٰذَا الَّذِيْ هُوَ مَهِيْنٌ ەۙ وَّلَا يَكَادُ يُبِيْنُ', 'am ana khairum min hāżallażī huwa mahīnuw wa lā yakādu yubīn'),
(4377, 43, 53, 'فَلَوْلَٓا اُلْقِيَ عَلَيْهِ اَسْوِرَةٌ مِّنْ ذَهَبٍ اَوْ جَاۤءَ مَعَهُ الْمَلٰۤىِٕكَةُ مُقْتَرِنِيْنَ', 'falau lā ulqiya \'alaihi aswiratum min żahabin au jā`a ma\'ahul-malā`ikatu muqtarinīn'),
(4378, 43, 54, 'فَاسْتَخَفَّ قَوْمَهٗ فَاَطَاعُوْهُ ۗاِنَّهُمْ كَانُوْا قَوْمًا فٰسِقِيْنَ', 'fastakhaffa qaumahụ fa aṭā\'ụh, innahum kānụ qauman fāsiqīn'),
(4379, 43, 55, 'فَلَمَّآ اٰسَفُوْنَا انْتَقَمْنَا مِنْهُمْ فَاَغْرَقْنٰهُمْ اَجْمَعِيْنَۙ', 'fa lammā āsafụnantaqamnā min-hum fa agraqnāhum ajma\'īn'),
(4380, 43, 56, 'فَجَعَلْنٰهُمْ سَلَفًا وَّمَثَلًا لِّلْاٰخِرِيْنَ', 'fa ja\'alnāhum salafaw wa maṡalal lil-ākhirīn'),
(4381, 43, 57, 'وَلَمَّا ضُرِبَ ابْنُ مَرْيَمَ مَثَلًا اِذَا قَوْمُكَ مِنْهُ يَصِدُّوْنَ', 'wa lammā ḍuribabnu maryama maṡalan iżā qaumuka min-hu yaṣiddụn'),
(4382, 43, 58, 'وَقَالُوْٓا ءَاٰلِهَتُنَا خَيْرٌ اَمْ هُوَ ۗمَا ضَرَبُوْهُ لَكَ اِلَّا جَدَلًا ۗبَلْ هُمْ قَوْمٌ خَصِمُوْنَ', 'wa qālū a ālihatunā khairun am huw, mā ḍarabụhu laka illā jadalā, bal hum qaumun khaṣimụn'),
(4383, 43, 59, 'اِنْ هُوَ اِلَّا عَبْدٌ اَنْعَمْنَا عَلَيْهِ وَجَعَلْنٰهُ مَثَلًا لِّبَنِيْٓ اِسْرَاۤءِيْلَ ۗ', 'in huwa illā \'abdun an\'amnā \'alaihi wa ja\'alnāhu maṡalal libanī isrā`īl'),
(4384, 43, 60, 'وَلَوْ نَشَاۤءُ لَجَعَلْنَا مِنْكُمْ مَّلٰۤىِٕكَةً فِى الْاَرْضِ يَخْلُفُوْنَ', 'walau nasyā`u laja\'alnā mingkum malā`ikatan fil-arḍi yakhlufụn'),
(4385, 43, 61, 'وَاِنَّهٗ لَعِلْمٌ لِّلسَّاعَةِ فَلَا تَمْتَرُنَّ بِهَا وَاتَّبِعُوْنِۗ هٰذَا صِرَاطٌ مُّسْتَقِيْمٌ', 'wa innahụ la\'ilmul lis-sā\'ati fa lā tamtarunna bihā wattabi\'ụn, hāżā ṣirāṭum mustaqīm'),
(4386, 43, 62, 'وَلَا يَصُدَّنَّكُمُ الشَّيْطٰنُۚ اِنَّهٗ لَكُمْ عَدُوٌّ مُّبِيْنٌ', 'wa lā yaṣuddannakumusy-syaiṭān, innahụ lakum \'aduwwum mubīn'),
(4387, 43, 63, 'وَلَمَّا جَاۤءَ عِيْسٰى بِالْبَيِّنٰتِ قَالَ قَدْ جِئْتُكُمْ بِالْحِكْمَةِ وَلِاُبَيِّنَ لَكُمْ بَعْضَ الَّذِيْ تَخْتَلِفُوْنَ فِيْهِۚ فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ', 'wa lammā jā`a \'īsā bil-bayyināti qāla qad ji`tukum bil-ḥikmati wa li`ubayyina lakum ba\'ḍallażī takhtalifụna fīh, fattaqullāha wa aṭī\'ụn'),
(4388, 43, 64, 'اِنَّ اللّٰهَ هُوَ رَبِّيْ وَرَبُّكُمْ فَاعْبُدُوْهُۗ هٰذَا صِرَاطٌ مُّسْتَقِيْمٌ', 'innallāha huwa rabbī wa rabbukum fa\'budụh, hāżā ṣirāṭum mustaqīm'),
(4389, 43, 65, 'فَاخْتَلَفَ الْاَحْزَابُ مِنْۢ بَيْنِهِمْ ۚفَوَيْلٌ لِّلَّذِيْنَ ظَلَمُوْا مِنْ عَذَابِ يَوْمٍ اَلِيْمٍ', 'fakhtalafal-aḥzābu mim bainihim, fa wailul lillażīna ẓalamụ min \'ażābi yaumin alīm'),
(4390, 43, 66, 'هَلْ يَنْظُرُوْنَ اِلَّا السَّاعَةَ اَنْ تَأْتِيَهُمْ بَغْتَةً وَّهُمْ لَا يَشْعُرُوْنَ', 'hal yanẓurụna illas-sā\'ata an ta`tiyahum bagtataw wa hum lā yasy\'urụn'),
(4391, 43, 67, 'اَلْاَخِلَّاۤءُ يَوْمَىِٕذٍۢ بَعْضُهُمْ لِبَعْضٍ عَدُوٌّ اِلَّا الْمُتَّقِيْنَ ۗ', 'al-akhillā`u yauma`iżim ba\'ḍuhum liba\'ḍin \'aduwwun illal-muttaqīn'),
(4392, 43, 68, 'يٰعِبَادِ لَاخَوْفٌ عَلَيْكُمُ الْيَوْمَ وَلَآ اَنْتُمْ تَحْزَنُوْنَۚ', 'yā \'ibādi lā khaufun \'alaikumul-yauma wa lā antum taḥzanụn'),
(4393, 43, 69, 'اَلَّذِيْنَ اٰمَنُوْا بِاٰيٰتِنَا وَكَانُوْا مُسْلِمِيْنَۚ', 'allażīna āmanụ bi`āyātinā wa kānụ muslimīn'),
(4394, 43, 70, 'اُدْخُلُوا الْجَنَّةَ اَنْتُمْ وَاَزْوَاجُكُمْ تُحْبَرُوْنَ', 'udkhulul-jannata antum wa azwājukum tuḥbarụn'),
(4395, 43, 71, 'يُطَافُ عَلَيْهِمْ بِصِحَافٍ مِّنْ ذَهَبٍ وَّاَكْوَابٍ ۚوَفِيْهَا مَا تَشْتَهِيْهِ الْاَنْفُسُ وَتَلَذُّ الْاَعْيُنُ ۚوَاَنْتُمْ فِيْهَا خٰلِدُوْنَۚ', 'yuṭāfu \'alaihim biṣiḥāfim min żahabiw wa akwāb, wa fīhā mā tasytahīhil-anfusu wa talażżul-a\'yun, wa antum fīhā khālidụn'),
(4396, 43, 72, 'وَتِلْكَ الْجَنَّةُ الَّتِيْٓ اُوْرِثْتُمُوْهَا بِمَا كُنْتُمْ تَعْمَلُوْنَ', 'wa tilkal-jannatullatī ụriṡtumụhā bimā kuntum ta\'malụn'),
(4397, 43, 73, 'لَكُمْ فِيْهَا فَاكِهَةٌ كَثِيْرَةٌ مِّنْهَا تَأْكُلُوْنَ', 'lakum fīhā fākihatung kaṡīratum min-hā ta`kulụn'),
(4398, 43, 74, 'اِنَّ الْمُجْرِمِيْنَ فِيْ عَذَابِ جَهَنَّمَ خٰلِدُوْنَۖ', 'innal-mujrimīna fī \'ażābi jahannama khālidụn'),
(4399, 43, 75, 'لَا يُفَتَّرُ عَنْهُمْ وَهُمْ فِيْهِ مُبْلِسُوْنَ ۚ', 'lā yufattaru \'an-hum wa hum fīhi mublisụn'),
(4400, 43, 76, 'وَمَا ظَلَمْنٰهُمْ وَلٰكِنْ كَانُوْا هُمُ الظّٰلِمِيْنَ', 'wa mā ẓalamnāhum wa lāking kānụ humuẓ-ẓālimīn'),
(4401, 43, 77, 'وَنَادَوْا يٰمٰلِكُ لِيَقْضِ عَلَيْنَا رَبُّكَۗ قَالَ اِنَّكُمْ مَّاكِثُوْنَ', 'wa nādau yā māliku liyaqḍi \'alainā rabbuk, qāla innakum mākiṡụn'),
(4402, 43, 78, 'لَقَدْ جِئْنٰكُمْ بِالْحَقِّ وَلٰكِنَّ اَكْثَرَكُمْ لِلْحَقِّ كٰرِهُوْنَ', 'laqad ji`nākum bil-ḥaqqi wa lākinna akṡarakum lil-ḥaqqi kārihụn'),
(4403, 43, 79, 'اَمْ اَبْرَمُوْٓا اَمْرًا فَاِنَّا مُبْرِمُوْنَۚ', 'am abramū amran fa innā mubrimụn'),
(4404, 43, 80, 'اَمْ يَحْسَبُوْنَ اَنَّا لَا نَسْمَعُ سِرَّهُمْ وَنَجْوٰىهُمْ ۗ بَلٰى وَرُسُلُنَا لَدَيْهِمْ يَكْتُبُوْنَ', 'am yaḥsabụna annā lā nasma\'u sirrahum wa najwāhum, balā wa rusulunā ladaihim yaktubụn'),
(4405, 43, 81, 'قُلْ اِنْ كَانَ لِلرَّحْمٰنِ وَلَدٌ ۖفَاَنَا۠ اَوَّلُ الْعٰبِدِيْنَ', 'qul ing kāna lir-raḥmāni waladun fa ana awwalul-\'ābidīn'),
(4406, 43, 82, 'سُبْحٰنَ رَبِّ السَّمٰوٰتِ وَالْاَرْضِ رَبِّ الْعَرْشِ عَمَّا يَصِفُوْنَ', 'sub-ḥāna rabbis-samāwāti wal-arḍi rabbil-\'arsyi \'ammā yaṣifụn'),
(4407, 43, 83, 'فَذَرْهُمْ يَخُوْضُوْا وَيَلْعَبُوْا حَتّٰى يُلٰقُوْا يَوْمَهُمُ الَّذِيْ يُوْعَدُوْنَ', 'fa żar-hum yakhụḍụ wa yal\'abụ ḥattā yulāqụ yaumahumullażī yụ\'adụn'),
(4408, 43, 84, 'وَهُوَ الَّذِيْ فِى السَّمَاۤءِ اِلٰهٌ وَّ فِى الْاَرْضِ اِلٰهٌ ۗوَهُوَ الْحَكِيْمُ الْعَلِيْمُ', 'wa huwallażī fis-samā`i ilāhuw wa fil-arḍi ilāh, wa huwal-ḥakīmul-\'alīm'),
(4409, 43, 85, 'وَتَبٰرَكَ الَّذِيْ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا ۚوَعِنْدَهٗ عِلْمُ السَّاعَةِۚ وَاِلَيْهِ تُرْجَعُوْنَ', 'wa tabārakallażī lahụ mulkus-samāwāti wal-arḍi wa mā bainahumā, wa \'indahụ \'ilmus-sā\'ah, wa ilaihi turja\'ụn'),
(4410, 43, 86, 'وَلَا يَمْلِكُ الَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِهِ الشَّفَاعَةَ اِلَّا مَنْ شَهِدَ بِالْحَقِّ وَهُمْ يَعْلَمُوْنَ', 'wa lā yamlikullażīna yad\'ụna min dụnihisy-syafā\'ata illā man syahida bil-ḥaqqi wa hum ya\'lamụn'),
(4411, 43, 87, 'وَلَىِٕنْ سَاَلْتَهُمْ مَّنْ خَلَقَهُمْ لَيَقُوْلُنَّ اللّٰهُ فَاَنّٰى يُؤْفَكُوْنَۙ', 'wa la`in sa`altahum man khalaqahum layaqụlunnallāhu fa annā yu`fakụn'),
(4412, 43, 88, 'وَقِيْلِهٖ يٰرَبِّ اِنَّ هٰٓؤُلَاۤءِ قَوْمٌ لَّا يُؤْمِنُوْنَۘ', 'wa qīlihī yā rabbi inna hā`ulā`i qaumul lā yu`minụn'),
(4413, 43, 89, 'فَاصْفَحْ عَنْهُمْ وَقُلْ سَلٰمٌۗ فَسَوْفَ يَعْلَمُوْنَ', 'faṣfaḥ \'an-hum wa qul salām, fa saufa ya\'lamụn'),
(4414, 44, 1, 'حٰمۤ ۚ', 'ḥā mīm'),
(4415, 44, 2, 'وَالْكِتٰبِ الْمُبِيْنِۙ', 'wal-kitābil-mubīn'),
(4416, 44, 3, 'اِنَّآ اَنْزَلْنٰهُ فِيْ لَيْلَةٍ مُّبٰرَكَةٍ اِنَّا كُنَّا مُنْذِرِيْنَ', 'innā anzalnāhu fī lailatim mubārakatin innā kunnā munżirīn'),
(4417, 44, 4, 'فِيْهَا يُفْرَقُ كُلُّ اَمْرٍ حَكِيْمٍۙ', 'fīhā yufraqu kullu amrin ḥakīm'),
(4418, 44, 5, 'اَمْرًا مِّنْ عِنْدِنَاۗ اِنَّا كُنَّا مُرْسِلِيْنَۖ', 'amram min \'indinā, innā kunnā mursilīn'),
(4419, 44, 6, 'رَحْمَةً مِّنْ رَّبِّكَ ۗاِنَّهٗ هُوَ السَّمِيْعُ الْعَلِيْمُۗ', 'raḥmatam mir rabbik, innahụ huwas-samī\'ul-\'alīm'),
(4420, 44, 7, 'رَبِّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَاۘ اِنْ كُنْتُمْ مُّوْقِنِيْنَ', 'rabbis-samāwāti wal-arḍi wa mā bainahumā, ing kuntum mụqinīn'),
(4421, 44, 8, 'لَآ اِلٰهَ اِلَّا هُوَ يُحْيٖ وَيُمِيْتُ ۗرَبُّكُمْ وَرَبُّ اٰبَاۤىِٕكُمُ الْاَوَّلِيْنَ', 'lā ilāha illā huwa yuḥyī wa yumīt, rabbukum wa rabbu ābā`ikumul-awwalīn'),
(4422, 44, 9, 'بَلْ هُمْ فِيْ شَكٍّ يَّلْعَبُوْنَ', 'bal hum fī syakkiy yal\'abụn'),
(4423, 44, 10, 'فَارْتَقِبْ يَوْمَ تَأْتِى السَّمَاۤءُ بِدُخَانٍ مُّبِيْنٍ', 'fartaqib yauma ta`tis-samā`u bidukhānim mubīn'),
(4424, 44, 11, 'يَغْشَى النَّاسَۗ هٰذَا عَذَابٌ اَلِيْمٌ', 'yagsyan-nās, hāżā \'ażābun alīm'),
(4425, 44, 12, 'رَبَّنَا اكْشِفْ عَنَّا الْعَذَابَ اِنَّا مُؤْمِنُوْنَ', 'rabbanaksyif \'annal-\'ażāba innā mu`minụn'),
(4426, 44, 13, 'اَنّٰى لَهُمُ الذِّكْرٰى وَقَدْ جَاۤءَهُمْ رَسُوْلٌ مُّبِيْنٌۙ', 'annā lahumuż-żikrā wa qad jā`ahum rasụlum mubīn'),
(4427, 44, 14, 'ثُمَّ تَوَلَّوْا عَنْهُ وَقَالُوْا مُعَلَّمٌ مَّجْنُوْنٌۘ', 'ṡumma tawallau \'an-hu wa qālụ mu\'allamum majnụn'),
(4428, 44, 15, 'اِنَّا كَاشِفُوا الْعَذَابِ قَلِيْلًا اِنَّكُمْ عَاۤىِٕدُوْنَۘ', 'innā kāsyiful-\'ażābi qalīlan innakum \'ā`idụn'),
(4429, 44, 16, 'يَوْمَ نَبْطِشُ الْبَطْشَةَ الْكُبْرٰىۚ اِنَّا مُنْتَقِمُوْنَ', 'yauma nabṭisyul-baṭsyatal-kubrā, innā muntaqimụn'),
(4430, 44, 17, '۞ وَلَقَدْ فَتَنَّا قَبْلَهُمْ قَوْمَ فِرْعَوْنَ وَجَاۤءَهُمْ رَسُوْلٌ كَرِيْمٌۙ', 'wa laqad fatannā qablahum qauma fir\'auna wa jā`ahum rasụlung karīm'),
(4431, 44, 18, 'اَنْ اَدُّوْٓا اِلَيَّ عِبَادَ اللّٰهِ ۗاِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌۙ', 'an addū ilayya \'ibādallāh, innī lakum rasụlun amīn'),
(4432, 44, 19, 'وَّاَنْ لَّا تَعْلُوْا عَلَى اللّٰهِ ۚاِنِّيْٓ اٰتِيْكُمْ بِسُلْطٰنٍ مُّبِيْنٍۚ', 'wa al lā ta\'lụ \'alallāh, innī ātīkum bisulṭānim mubīn'),
(4433, 44, 20, 'وَاِنِّيْ عُذْتُ بِرَبِّيْ وَرَبِّكُمْ اَنْ تَرْجُمُوْنِۚ', 'wa innī \'użtu birabbī wa rabbikum an tarjumụn'),
(4434, 44, 21, 'وَاِنْ لَّمْ تُؤْمِنُوْا لِيْ فَاعْتَزِلُوْنِ', 'wa il lam tu`minụ lī fa\'tazilụn'),
(4435, 44, 22, 'فَدَعَا رَبَّهٗٓ اَنَّ هٰٓؤُلَاۤءِ قَوْمٌ مُّجْرِمُوْنَ', 'fa da\'ā rabbahū anna hā`ulā`i qaumum mujrimụn'),
(4436, 44, 23, 'فَاَسْرِ بِعِبَادِيْ لَيْلًا اِنَّكُمْ مُّتَّبَعُوْنَۙ', 'fa asri bi\'ibādī lailan innakum muttaba\'ụn'),
(4437, 44, 24, 'وَاتْرُكِ الْبَحْرَ رَهْوًاۗ اِنَّهُمْ جُنْدٌ مُّغْرَقُوْنَ', 'watrukil-baḥra rahwā, innahum jundum mugraqụn'),
(4438, 44, 25, 'كَمْ تَرَكُوْا مِنْ جَنّٰتٍ وَّعُيُوْنٍۙ', 'kam tarakụ min jannātiw wa \'uyụn'),
(4439, 44, 26, 'وَّزُرُوْعٍ وَّمَقَامٍ كَرِيْمٍۙ', 'wa zurụ\'iw wa maqāming karīm'),
(4440, 44, 27, 'وَّنَعْمَةٍ كَانُوْا فِيْهَا فٰكِهِيْنَۙ', 'wa na\'mating kānụ fīhā fākihīn'),
(4441, 44, 28, 'كَذٰلِكَ ۗوَاَوْرَثْنٰهَا قَوْمًا اٰخَرِيْنَۚ', 'każālik, wa auraṡnāhā qauman ākharīn'),
(4442, 44, 29, 'فَمَا بَكَتْ عَلَيْهِمُ السَّمَاۤءُ وَالْاَرْضُۗ وَمَا كَانُوْا مُنْظَرِيْنَ', 'fa mā bakat \'alaihimus-samā`u wal-arḍ, wa mā kānụ munẓarīn'),
(4443, 44, 30, 'وَلَقَدْ نَجَّيْنَا بَنِيْٓ اِسْرَاۤءِيْلَ مِنَ الْعَذَابِ الْمُهِيْنِۙ', 'wa laqad najjainā banī isrā`īla minal-\'ażābil-muhīn'),
(4444, 44, 31, 'مِنْ فِرْعَوْنَ ۗاِنَّهٗ كَانَ عَالِيًا مِّنَ الْمُسْرِفِيْنَ', 'min fir\'aụn, innahụ kāna \'āliyam minal-musrifīn'),
(4445, 44, 32, 'وَلَقَدِ اخْتَرْنٰهُمْ عَلٰى عِلْمٍ عَلَى الْعٰلَمِيْنَ ۚ', 'wa laqadikhtarnāhum \'alā \'ilmin \'alal-\'ālamīn'),
(4446, 44, 33, 'وَاٰتَيْنٰهُمْ مِّنَ الْاٰيٰتِ مَا فِيْهِ بَلٰۤـؤٌا مُّبِيْنٌ', 'wa ātaināhum minal-āyāti mā fīhi balā`um mubīn'),
(4447, 44, 34, 'اِنَّ هٰٓؤُلَاۤءِ لَيَقُوْلُوْنَۙ', 'inna hā`ulā`i layaqụlụn'),
(4448, 44, 35, 'اِنْ هِيَ اِلَّا مَوْتَتُنَا الْاُوْلٰى وَمَا نَحْنُ بِمُنْشَرِيْنَ', 'in hiya illā mautatunal-ụlā wa mā naḥnu bimunsyarīn'),
(4449, 44, 36, 'فَأْتُوْا بِاٰبَاۤىِٕنَآ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'fa`tụ bi`ābā`inā ing kuntum ṣādiqīn'),
(4450, 44, 37, 'اَهُمْ خَيْرٌ اَمْ قَوْمُ تُبَّعٍۙ وَّالَّذِيْنَ مِنْ قَبْلِهِمْۗ اَهْلَكْنٰهُمْ اِنَّهُمْ كَانُوْا مُجْرِمِيْنَ', 'a hum khairun am qaumu tubba\'iw wallażīna ming qablihim, ahlaknāhum innahum kānụ mujrimīn'),
(4451, 44, 38, 'وَمَا خَلَقْنَا السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَا لٰعِبِيْنَ', 'wa mā khalaqnas-samāwāti wal-arḍa wa mā bainahumā lā\'ibīn'),
(4452, 44, 39, 'مَا خَلَقْنٰهُمَآ اِلَّا بِالْحَقِّ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ', 'mā khalaqnāhumā illā bil-ḥaqqi wa lākinna akṡarahum lā ya\'lamụn'),
(4453, 44, 40, 'اِنَّ يَوْمَ الْفَصْلِ مِيْقَاتُهُمْ اَجْمَعِيْنَ ۙ', 'inna yaumal-faṣli mīqātuhum ajma\'īn'),
(4454, 44, 41, 'يَوْمَ لَا يُغْنِيْ مَوْلًى عَنْ مَّوْلًى شَيْـًٔا وَّلَا هُمْ يُنْصَرُوْنَۙ', 'yauma lā yugnī maulan \'am maulan syai`aw wa lā hum yunṣarụn'),
(4455, 44, 42, 'اِلَّا مَنْ رَّحِمَ اللّٰهُ ۗاِنَّهٗ هُوَ الْعَزِيْزُ الرَّحِيْمُ', 'illā mar raḥimallāh, innahụ huwal-\'azīzur-raḥīm'),
(4456, 44, 43, 'اِنَّ شَجَرَتَ الزَّقُّوْمِۙ', 'inna syajarataz-zaqqụm'),
(4457, 44, 44, 'طَعَامُ الْاَثِيْمِ ۛ', 'ṭa\'āmul-aṡīm'),
(4458, 44, 45, 'كَالْمُهْلِ ۛ يَغْلِيْ فِى الْبُطُوْنِۙ', 'kal-muhli yaglī fil-buṭụn'),
(4459, 44, 46, 'كَغَلْيِ الْحَمِيْمِ ۗ', 'kagalyil-ḥamīm'),
(4460, 44, 47, 'خُذُوْهُ فَاعْتِلُوْهُ اِلٰى سَوَاۤءِ الْجَحِيْمِۙ', 'khużụhu fa\'tilụhu ilā sawā`il-jaḥīm'),
(4461, 44, 48, 'ثُمَّ صُبُّوْا فَوْقَ رَأْسِهٖ مِنْ عَذَابِ الْحَمِيْمِۗ', 'ṡumma ṣubbụ fauqa ra`sihī min \'ażābil-ḥamīm'),
(4462, 44, 49, 'ذُقْۚ اِنَّكَ اَنْتَ الْعَزِيْزُ الْكَرِيْمُ', 'żuq, innaka antal-\'azīzul-karīm'),
(4463, 44, 50, 'اِنَّ هٰذَا مَا كُنْتُمْ بِهٖ تَمْتَرُوْنَ', 'inna hāżā mā kuntum bihī tamtarụn'),
(4464, 44, 51, 'اِنَّ الْمُتَّقِيْنَ فِيْ مَقَامٍ اَمِيْنٍۙ', 'innal-muttaqīna fī maqāmin amīn'),
(4465, 44, 52, 'فِيْ جَنّٰتٍ وَّعُيُوْنٍ ۙ', 'fī jannātiw wa \'uyụn'),
(4466, 44, 53, 'يَّلْبَسُوْنَ مِنْ سُنْدُسٍ وَّاِسْتَبْرَقٍ مُّتَقٰبِلِيْنَۚ', 'yalbasụna min sundusiw wa istabraqim mutaqābilīn'),
(4467, 44, 54, 'كَذٰلِكَۗ وَزَوَّجْنٰهُمْ بِحُوْرٍ عِيْنٍۗ', 'każālik, wa zawwajnāhum biḥụrin \'īn'),
(4468, 44, 55, 'يَدْعُوْنَ فِيْهَا بِكُلِّ فَاكِهَةٍ اٰمِنِيْنَۙ', 'yad\'ụna fīhā bikulli fākihatin āminīn'),
(4469, 44, 56, 'لَا يَذُوْقُوْنَ فِيْهَا الْمَوْتَ اِلَّا الْمَوْتَةَ الْاُوْلٰىۚ وَوَقٰىهُمْ عَذَابَ الْجَحِيْمِۙ', 'lā yażụqụna fīhal-mauta illal-mautatal-ụlā, wa waqāhum \'ażābal-jaḥīm'),
(4470, 44, 57, 'فَضْلًا مِّنْ رَّبِّكَۚ ذٰلِكَ هُوَ الْفَوْزُ الْعَظِيْمُ', 'faḍlam mir rabbik, żālika huwal-fauzul-\'aẓīm'),
(4471, 44, 58, 'فَاِنَّمَا يَسَّرْنٰهُ بِلِسَانِكَ لَعَلَّهُمْ يَتَذَكَّرُوْنَ', 'fa innamā yassarnāhu bilisānika la\'allahum yatażakkarụn'),
(4472, 44, 59, 'فَارْتَقِبْ اِنَّهُمْ مُّرْتَقِبُوْنَࣖ', 'fartaqib innahum murtaqibụn'),
(4473, 45, 1, 'حٰمۤ', 'ḥā mīm'),
(4474, 45, 2, 'تَنْزِيْلُ الْكِتٰبِ مِنَ اللّٰهِ الْعَزِيْزِ الْحَكِيْمِ', 'tanzīlul-kitābi minallāhil-\'azīzil-ḥakīm'),
(4475, 45, 3, 'اِنَّ فِى السَّمٰوٰتِ وَالْاَرْضِ لَاٰيٰتٍ لِّلْمُؤْمِنِيْنَۗ', 'inna fis-samāwāti wal-arḍi la`āyātil lil-mu`minīn'),
(4476, 45, 4, 'وَفِيْ خَلْقِكُمْ وَمَا يَبُثُّ مِنْ دَاۤبَّةٍ اٰيٰتٌ لِّقَوْمٍ يُّوْقِنُوْنَۙ', 'wa fī khalqikum wa mā yabuṡṡu min dābbatin āyātul liqaumiy yụqinụn'),
(4477, 45, 5, 'وَاخْتِلَافِ الَّيْلِ وَالنَّهَارِ وَمَآ اَنْزَلَ اللّٰهُ مِنَ السَّمَاۤءِ مِنْ رِّزْقٍ فَاَحْيَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَا وَتَصْرِيْفِ الرِّيٰحِ اٰيٰتٌ لِّقَوْمٍ يَّعْقِلُوْنَ', 'wakhtilāfil-laili wan-nahāri wa mā anzalallāhu minas-samā`i mir rizqin fa aḥyā bihil-arḍa ba\'da mautihā wa taṣrīfir-riyāḥi āyātul liqaumiy ya\'qilụn'),
(4478, 45, 6, 'تِلْكَ اٰيٰتُ اللّٰهِ نَتْلُوْهَا عَلَيْكَ بِالْحَقِّۚ فَبِاَيِّ حَدِيْثٍۢ بَعْدَ اللّٰهِ وَاٰيٰتِهٖ يُؤْمِنُوْنَ', 'tilka āyātullāhi natlụhā \'alaika bil-ḥaqq, fa bi`ayyi ḥadīṡim ba\'dallāhi wa āyātihī yu`minụn'),
(4479, 45, 7, 'وَيْلٌ لِّكُلِّ اَفَّاكٍ اَثِيْمٍۙ', 'wailul likulli affākin aṡīm'),
(4480, 45, 8, 'يَّسْمَعُ اٰيٰتِ اللّٰهِ تُتْلٰى عَلَيْهِ ثُمَّ يُصِرُّ مُسْتَكْبِرًا كَاَنْ لَّمْ يَسْمَعْهَاۚ فَبَشِّرْهُ بِعَذَابٍ اَلِيْمٍ', 'yasma\'u āyātillāhi tutlā \'alaihi ṡumma yuṣirru mustakbirang ka`al lam yasma\'hā, fa basysyir-hu bi\'ażābin alīm'),
(4481, 45, 9, 'وَاِذَا عَلِمَ مِنْ اٰيٰتِنَا شَيْـًٔا ۨاتَّخَذَهَا هُزُوًاۗ اُولٰۤىِٕكَ لَهُمْ عَذَابٌ مُّهِيْنٌۗ', 'wa iżā \'alima min āyātinā syai`anittakhażahā huzuwā, ulā`ika lahum \'ażābum muhīn'),
(4482, 45, 10, 'مِنْ وَّرَاۤىِٕهِمْ جَهَنَّمُ ۚوَلَا يُغْنِيْ عَنْهُمْ مَّا كَسَبُوْا شَيْـًٔا وَّلَا مَا اتَّخَذُوْا مِنْ دُوْنِ اللّٰهِ اَوْلِيَاۤءَۚ وَلَهُمْ عَذَابٌ عَظِيْمٌۗ', 'miw warā`ihim jahannam, wa lā yugnī \'an-hum mā kasabụ syai`aw wa lā mattakhażụ min dụnillāhi auliyā`, wa lahum \'ażābun \'aẓīm'),
(4483, 45, 11, 'هٰذَا هُدًىۚ وَالَّذِيْنَ كَفَرُوْا بِاٰيٰتِ رَبِّهِمْ لَهُمْ عَذَابٌ مِّنْ رِّجْزٍ اَلِيْمٌ', 'hāżā hudā, wallażīna kafarụ bi`āyāti rabbihim lahum \'ażābum mir rijzin alīm'),
(4484, 45, 12, '۞ اَللّٰهُ الَّذِيْ سَخَّرَ لَكُمُ الْبَحْرَ لِتَجْرِيَ الْفُلْكُ فِيْهِ بِاَمْرِهٖ وَلِتَبْتَغُوْا مِنْ فَضْلِهٖ وَلَعَلَّكُمْ تَشْكُرُوْنَۚ', 'allāhullażī sakhkhara lakumul-baḥra litajriyal-fulku fīhi bi`amrihī wa litabtagụ min faḍlihī wa la\'allakum tasykurụn'),
(4485, 45, 13, 'وَسَخَّرَ لَكُمْ مَّا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ جَمِيْعًا مِّنْهُ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّتَفَكَّرُوْنَ', 'wa sakhkhara lakum mā fis-samāwāti wa mā fil-arḍi jamī\'am min-h, inna fī żālika la`āyātil liqaumiy yatafakkarụn'),
(4486, 45, 14, 'قُلْ لِّلَّذِيْنَ اٰمَنُوْا يَغْفِرُوْا لِلَّذِيْنَ لَا يَرْجُوْنَ اَيَّامَ اللّٰهِ لِيَجْزِيَ قَوْمًا ۢبِمَا كَانُوْا يَكْسِبُوْنَ', 'qul lillażīna āmanụ yagfirụ lillażīna lā yarjụna ayyāmallāhi liyajziya qaumam bimā kānụ yaksibụn'),
(4487, 45, 15, 'مَنْ عَمِلَ صَالِحًا فَلِنَفْسِهٖۚ وَمَنْ اَسَاۤءَ فَعَلَيْهَا ۖ ثُمَّ اِلٰى رَبِّكُمْ تُرْجَعُوْنَ', 'man \'amila ṣāliḥan fa linafsih, wa man asā`a fa \'alaihā ṡumma ilā rabbikum turja\'ụn'),
(4488, 45, 16, 'وَلَقَدْ اٰتَيْنَا بَنِيْٓ اِسْرَاۤءِيْلَ الْكِتٰبَ وَالْحُكْمَ وَالنُّبُوَّةَ وَرَزَقْنٰهُمْ مِّنَ الطَّيِّبٰتِ وَفَضَّلْنٰهُمْ عَلَى الْعٰلَمِيْنَ ۚ', 'wa laqad ātainā banī isrā`īlal-kitāba wal-ḥukma wan-nubuwwata wa razaqnāhum minaṭ-ṭayyibāti wa faḍḍalnāhum \'alal-\'ālamīn'),
(4489, 45, 17, 'وَاٰتَيْنٰهُمْ بَيِّنٰتٍ مِّنَ الْاَمْرِۚ فَمَا اخْتَلَفُوْٓا اِلَّا مِنْۢ بَعْدِ مَا جَاۤءَهُمُ الْعِلْمُ بَغْيًاۢ بَيْنَهُمْ ۗاِنَّ رَبَّكَ يَقْضِيْ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كَانُوْا فِيْهِ يَخْتَلِفُوْنَ', 'wa ātaināhum bayyinātim minal-amr, fa makhtalafū illā mim ba\'di mā jā`ahumul-\'ilmu bagyam bainahum, inna rabbaka yaqḍī bainahum yaumal-qiyāmati fīmā kānụ fīhi yakhtalifụn'),
(4490, 45, 18, 'ثُمَّ جَعَلْنٰكَ عَلٰى شَرِيْعَةٍ مِّنَ الْاَمْرِ فَاتَّبِعْهَا وَلَا تَتَّبِعْ اَهْوَاۤءَ الَّذِيْنَ لَا يَعْلَمُوْنَ', 'ṡumma ja\'alnāka \'alā syarī\'atim minal-amri fattabi\'hā wa lā tattabi\' ahwā`allażīna lā ya\'lamụn'),
(4491, 45, 19, 'اِنَّهُمْ لَنْ يُّغْنُوْا عَنْكَ مِنَ اللّٰهِ شَيْـًٔا ۗوَاِنَّ الظّٰلِمِيْنَ بَعْضُهُمْ اَوْلِيَاۤءُ بَعْضٍۚ وَاللّٰهُ وَلِيُّ الْمُتَّقِيْنَ', 'innahum lay yugnụ \'angka minallāhi syai`ā, wa innaẓ-ẓālimīna ba\'ḍuhum auliyā`u ba\'ḍ, wallāhu waliyyul-muttaqīn'),
(4492, 45, 20, 'هٰذَا بَصَاۤىِٕرُ لِلنَّاسِ وَهُدًى وَّرَحْمَةٌ لِّقَوْمٍ يُّوْقِنُوْنَ', 'hāżā baṣā`iru lin-nāsi wa hudaw wa raḥmatul liqaumiy yụqinụn'),
(4493, 45, 21, 'اَمْ حَسِبَ الَّذِيْنَ اجْتَرَحُوا السَّيِّاٰتِ اَنْ نَّجْعَلَهُمْ كَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ سَوَاۤءً مَّحْيَاهُمْ وَمَمَاتُهُمْ ۗسَاۤءَ مَا يَحْكُمُوْنَࣖ', 'am ḥasiballażīnajtaraḥus-sayyi`āti an naj\'alahum kallażīna āmanụ wa \'amiluṣ-ṣāliḥāti sawā`am maḥyāhum wa mamātuhum, sā`a mā yaḥkumụn'),
(4494, 45, 22, 'وَخَلَقَ اللّٰهُ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّ وَلِتُجْزٰى كُلُّ نَفْسٍۢ بِمَا كَسَبَتْ وَهُمْ لَا يُظْلَمُوْنَ', 'wa khalaqallāhus-samāwāti wal-arḍa bil-ḥaqqi wa litujzā kullu nafsim bimā kasabat wa hum lā yuẓlamụn'),
(4495, 45, 23, 'اَفَرَءَيْتَ مَنِ اتَّخَذَ اِلٰهَهٗ هَوٰىهُ وَاَضَلَّهُ اللّٰهُ عَلٰى عِلْمٍ وَّخَتَمَ عَلٰى سَمْعِهٖ وَقَلْبِهٖ وَجَعَلَ عَلٰى بَصَرِهٖ غِشٰوَةًۗ فَمَنْ يَّهْدِيْهِ مِنْۢ بَعْدِ اللّٰهِ ۗ اَفَلَا تَذَكَّرُوْنَ', 'a fa ra`aita manittakhaża ilāhahụ hawāhu wa aḍallahullāhu \'alā \'ilmiw wa khatama \'alā sam\'ihī wa qalbihī wa ja\'ala \'alā baṣarihī gisyāwah, fa may yahdīhi mim ba\'dillāh, a fa lā tażakkarụn'),
(4496, 45, 24, 'وَقَالُوْا مَا هِيَ اِلَّا حَيَاتُنَا الدُّنْيَا نَمُوْتُ وَنَحْيَا وَمَا يُهْلِكُنَآ اِلَّا الدَّهْرُۚ وَمَا لَهُمْ بِذٰلِكَ مِنْ عِلْمٍۚ اِنْ هُمْ اِلَّا يَظُنُّوْنَ', 'wa qālụ mā hiya illā ḥayātunad-dun-yā namụtu wa naḥyā wa mā yuhlikunā illad-dahr, wa mā lahum biżālika min \'ilm, in hum illā yaẓunnụn'),
(4497, 45, 25, 'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا بَيِّنٰتٍ مَّا كَانَ حُجَّتَهُمْ اِلَّآ اَنْ قَالُوا ائْتُوْا بِاٰبَاۤىِٕنَآ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'wa iżā tutlā \'alaihim āyātunā bayyinātim mā kāna ḥujjatahum illā ang qālu`tụ bi`ābā`inā ing kuntum ṣādiqīn'),
(4498, 45, 26, 'قُلِ اللّٰهُ يُحْيِيْكُمْ ثُمَّ يُمِيْتُكُمْ ثُمَّ يَجْمَعُكُمْ اِلٰى يَوْمِ الْقِيٰمَةِ لَارَيْبَ فِيْهِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ', 'qulillāhu yuḥyīkum ṡumma yumītukum ṡumma yajma\'ukum ilā yaumil-qiyāmati lā raiba fīhi wa lākinna akṡaran-nāsi lā ya\'lamụn'),
(4499, 45, 27, 'وَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ وَيَوْمَ تَقُوْمُ السَّاعَةُ يَوْمَىِٕذٍ يَّخْسَرُ الْمُبْطِلُوْنَ', 'wa lillāhi mulkus-samāwāti wal-arḍ, wa yauma taqụmus-sā\'atu yauma`iżiy yakhsarul-mubṭilụn'),
(4500, 45, 28, 'وَتَرٰى كُلَّ اُمَّةٍ جَاثِيَةً ۗ كُلُّ اُمَّةٍ تُدْعٰٓى اِلٰى كِتٰبِهَاۗ اَلْيَوْمَ تُجْزَوْنَ مَا كُنْتُمْ تَعْمَلُوْنَ', 'wa tarā kulla ummatin jāṡiyah, kullu ummatin tud\'ā ilā kitābihā, al-yauma tujzauna mā kuntum ta\'malụn'),
(4501, 45, 29, 'هٰذَا كِتٰبُنَا يَنْطِقُ عَلَيْكُمْ بِالْحَقِّ ۗاِنَّا كُنَّا نَسْتَنْسِخُ مَا كُنْتُمْ تَعْمَلُوْنَ', 'hāżā kitābunā yanṭiqu \'alaikum bil-ḥaqq, innā kunnā nastansikhu mā kuntum ta\'malụn'),
(4502, 45, 30, 'فَاَمَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَيُدْخِلُهُمْ رَبُّهُمْ فِيْ رَحْمَتِهٖۗ ذٰلِكَ هُوَ الْفَوْزُ الْمُبِيْنُ', 'fa ammallażīna āmanụ wa \'amiluṣ-ṣāliḥāti fa yudkhiluhum rabbuhum fī raḥmatih, żālika huwal-fauzul-mubīn'),
(4503, 45, 31, 'وَاَمَّا الَّذِيْنَ كَفَرُوْاۗ اَفَلَمْ تَكُنْ اٰيٰتِيْ تُتْلٰى عَلَيْكُمْ فَاسْتَكْبَرْتُمْ وَكُنْتُمْ قَوْمًا مُّجْرِمِيْنَ', 'wa ammallażīna kafarụ, a fa lam takun āyātī tutlā \'alaikum fastakbartum wa kuntum qaumam mujrimīn'),
(4504, 45, 32, 'وَاِذَا قِيْلَ اِنَّ وَعْدَ اللّٰهِ حَقٌّ وَّالسَّاعَةُ لَا رَيْبَ فِيْهَا قُلْتُمْ مَّا نَدْرِيْ مَا السَّاعَةُۙ اِنْ نَّظُنُّ اِلَّا ظَنًّا وَّمَا نَحْنُ بِمُسْتَيْقِنِيْنَ', 'wa iżā qīla inna wa\'dallāhi ḥaqquw was-sā\'atu lā raiba fīhā qultum mā nadrī mas-sā\'atu in naẓunnu illā ẓannaw wa mā naḥnu bimustaiqinīn'),
(4505, 45, 33, 'وَبَدَا لَهُمْ سَيِّاٰتُ مَا عَمِلُوْا وَحَاقَ بِهِمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ', 'wa badā lahum sayyi`ātu mā \'amilụ wa ḥāqa bihim mā kānụ bihī yastahzi`ụn'),
(4506, 45, 34, 'وَقِيْلَ الْيَوْمَ نَنْسٰىكُمْ كَمَا نَسِيْتُمْ لِقَاۤءَ يَوْمِكُمْ هٰذَاۙ وَمَأْوٰىكُمُ النَّارُ وَمَا لَكُمْ مِّنْ نّٰصِرِيْنَ', 'wa qīlal-yauma nansākum kamā nasītum liqā`a yaumikum hāżā wa ma`wākumun-nāru wa mā lakum min nāṣirīn'),
(4507, 45, 35, 'ذٰلِكُمْ بِاَنَّكُمُ اتَّخَذْتُمْ اٰيٰتِ اللّٰهِ هُزُوًا وَّغَرَّتْكُمُ الْحَيٰوةُ الدُّنْيَا ۚفَالْيَوْمَ لَا يُخْرَجُوْنَ مِنْهَا وَلَا هُمْ يُسْتَعْتَبُوْنَ', 'żālikum bi`annakumuttakhażtum āyātillāhi huzuwaw wa garratkumul-ḥayātud-dun-yā, fal-yauma lā yukhrajụna min-hā wa lā hum yusta\'tabụn'),
(4508, 45, 36, 'فَلِلّٰهِ الْحَمْدُ رَبِّ السَّمٰوٰتِ وَرَبِّ الْاَرْضِ رَبِّ الْعٰلَمِيْنَ', 'fa lillāhil-ḥamdu rabbis-samāwāti wa rabbil-arḍi rabbil-\'ālamīn'),
(4509, 45, 37, 'وَلَهُ الْكِبْرِيَاۤءُ فِى السَّمٰوٰتِ وَالْاَرْضِ ۗوَهُوَ الْعَزِيْزُ الْحَكِيْمُ ۔', 'wa lahul-kibriyā`u fis-samāwāti wal-arḍ, wa huwal-\'azīzul-ḥakīm'),
(4510, 46, 1, 'حٰمۤ ۚ', 'ḥā mīm'),
(4511, 46, 2, 'تَنْزِيْلُ الْكِتٰبِ مِنَ اللّٰهِ الْعَزِيْزِ الْحَكِيْمِ', 'tanzīlul-kitābi minallāhil-\'azīzil-ḥakīm'),
(4512, 46, 3, 'مَا خَلَقْنَا السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَآ اِلَّا بِالْحَقِّ وَاَجَلٍ مُّسَمًّىۗ وَالَّذِيْنَ كَفَرُوْا عَمَّآ اُنْذِرُوْا مُعْرِضُوْنَ', 'mā khalaqnas-samāwāti wal-arḍa wa mā bainahumā illā bil-ḥaqqi wa ajalim musamman, wallażīna kafarụ \'ammā unżirụ mu\'riḍụn'),
(4513, 46, 4, 'قُلْ اَرَءَيْتُمْ مَّا تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ اَرُوْنِيْ مَاذَا خَلَقُوْا مِنَ الْاَرْضِ اَمْ لَهُمْ شِرْكٌ فِى السَّمٰوٰتِ ۖائْتُوْنِيْ بِكِتٰبٍ مِّنْ قَبْلِ هٰذَآ اَوْ اَثٰرَةٍ مِّنْ عِلْمٍ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'qul a ra`aitum mā tad\'ụna min dụnillāhi arụnī māżā khalaqụ minal-arḍi am lahum syirkun fis-samāwāti`tụnī bikitābim ming qabli hāżā au aṡāratim min \'ilmin ing kuntum ṣādiqīn'),
(4514, 46, 5, 'وَمَنْ اَضَلُّ مِمَّنْ يَّدْعُوْا مِنْ دُوْنِ اللّٰهِ مَنْ لَّا يَسْتَجِيْبُ لَهٗٓ اِلٰى يَوْمِ الْقِيٰمَةِ وَهُمْ عَنْ دُعَاۤىِٕهِمْ غٰفِلُوْنَ', 'wa man aḍallu mim may yad\'ụ min dụnillāhi mal lā yastajību lahū ilā yaumil-qiyāmati wa hum \'an du\'ā`ihim gāfilụn'),
(4515, 46, 6, 'وَاِذَا حُشِرَ النَّاسُ كَانُوْا لَهُمْ اَعْدَاۤءً وَّكَانُوْا بِعِبَادَتِهِمْ كٰفِرِيْنَ', 'wa iżā ḥusyiran-nāsu kānụ lahum a\'dā`aw wa kānụ bi\'ibādatihim kāfirīn'),
(4516, 46, 7, 'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا بَيِّنٰتٍ قَالَ الَّذِيْنَ كَفَرُوْا لِلْحَقِّ لَمَّا جَاۤءَهُمْۙ هٰذَا سِحْرٌ مُّبِيْنٌۗ', 'wa iżā tutlā \'alaihim āyātunā bayyināting qālallażīna kafarụ lil-ḥaqqi lammā jā`ahum hāżā siḥrum mubīn'),
(4517, 46, 8, 'اَمْ يَقُوْلُوْنَ افْتَرٰىهُ ۗ قُلْ اِنِ افْتَرَيْتُهٗ فَلَا تَمْلِكُوْنَ لِيْ مِنَ اللّٰهِ شَيْـًٔا ۗهُوَ اَعْلَمُ بِمَا تُفِيْضُوْنَ فِيْهِۗ كَفٰى بِهٖ شَهِيْدًا ۢ بَيْنِيْ وَبَيْنَكُمْ ۗ وَهُوَ الْغَفُوْرُ الرَّحِيْمُ', 'am yaqụlụnaftarāh, qul iniftaraituhụ fa lā tamlikụna lī minallāhi syai`ā, huwa a\'lamu bimā tufīḍụna fīh, kafā bihī syahīdam bainī wa bainakum, wa huwal-gafụrur-raḥīm'),
(4518, 46, 9, 'قُلْ مَا كُنْتُ بِدْعًا مِّنَ الرُّسُلِ وَمَآ اَدْرِيْ مَا يُفْعَلُ بِيْ وَلَا بِكُمْۗ اِنْ اَتَّبِعُ اِلَّا مَا يُوْحٰٓى اِلَيَّ وَمَآ اَنَا۠ اِلَّا نَذِيْرٌ مُّبِيْنٌ', 'qul mā kuntu bid\'am minar-rusuli wa mā adrī mā yuf\'alu bī wa lā bikum, in attabi\'u illā mā yụḥā ilayya wa mā ana illā nażīrum mubīn'),
(4519, 46, 10, 'قُلْ اَرَءَيْتُمْ اِنْ كَانَ مِنْ عِنْدِ اللّٰهِ وَكَفَرْتُمْ بِهٖ وَشَهِدَ شَاهِدٌ مِّنْۢ بَنِيْٓ اِسْرَاۤءِيْلَ عَلٰى مِثْلِهٖ فَاٰمَنَ وَاسْتَكْبَرْتُمْۗ اِنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ', 'qul ara`aitum ing kāna min \'indillāhi wa kafartum bihī wa syahida syāhidum mim banī isrā`īla \'alā miṡlihī fa āmana wastakbartum, innallāha lā yahdil-qaumaẓ-ẓālimīn'),
(4520, 46, 11, 'وَقَالَ الَّذِيْنَ كَفَرُوْا لِلَّذِيْنَ اٰمَنُوْا لَوْ كَانَ خَيْرًا مَّا سَبَقُوْنَآ اِلَيْهِۗ وَاِذْ لَمْ يَهْتَدُوْا بِهٖ فَسَيَقُوْلُوْنَ هٰذَآ اِفْكٌ قَدِيْمٌ', 'wa qālallażīna kafarụ lillażīna āmanụ lau kāna khairam mā sabaqụnā ilaīh, wa iż lam yahtadụ bihī fa sayaqụlụna hāżā ifkung qadīm'),
(4521, 46, 12, 'وَمِنْ قَبْلِهٖ كِتٰبُ مُوْسٰٓى اِمَامًا وَّرَحْمَةً ۗوَهٰذَا كِتٰبٌ مُّصَدِّقٌ لِّسَانًا عَرَبِيًّا لِّيُنْذِرَ الَّذِيْنَ ظَلَمُوْا ۖوَبُشْرٰى لِلْمُحْسِنِيْنَ', 'wa ming qablihī kitābu mụsā imāmaw wa raḥmah, wa hāżā kitābum muṣaddiqul lisānan \'arabiyyal liyunżirallażīna ẓalamụ wa busyrā lil-muḥsinīn'),
(4522, 46, 13, 'اِنَّ الَّذِيْنَ قَالُوْا رَبُّنَا اللّٰهُ ثُمَّ اسْتَقَامُوْا فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَۚ', 'innallażīna qālụ rabbunallāhu ṡummastaqāmụ fa lā khaufun \'alaihim wa lā hum yaḥzanụn'),
(4523, 46, 14, 'اُولٰۤىِٕكَ اَصْحٰبُ الْجَنَّةِ خٰلِدِيْنَ فِيْهَاۚ جَزَاۤءً ۢبِمَا كَانُوْا يَعْمَلُوْنَ', 'ulā`ika aṣ-ḥābul-jannati khālidīna fīhā, jazā`am bimā kānụ ya\'malụn'),
(4524, 46, 15, 'وَوَصَّيْنَا الْاِنْسَانَ بِوَالِدَيْهِ اِحْسَانًا ۗحَمَلَتْهُ اُمُّهٗ كُرْهًا وَّوَضَعَتْهُ كُرْهًا ۗوَحَمْلُهٗ وَفِصٰلُهٗ ثَلٰثُوْنَ شَهْرًا ۗحَتّٰىٓ اِذَا بَلَغَ اَشُدَّهٗ وَبَلَغَ اَرْبَعِيْنَ سَنَةًۙ قَالَ رَبِّ اَوْزِعْنِيْٓ اَنْ اَشْكُرَ نِعْمَتَكَ الَّتِيْٓ اَنْعَمْتَ عَلَيَّ وَعَلٰى وَالِدَيَّ وَاَنْ اَعْمَلَ صَالِحًا تَرْضٰىهُ وَاَصْلِحْ لِيْ فِيْ ذُرِّيَّتِيْۗ اِنِّيْ تُبْتُ اِلَيْكَ وَاِنِّيْ مِنَ الْمُسْلِمِيْنَ', 'wa waṣṣainal-insāna biwālidaihi iḥsānā, ḥamalat-hu ummuhụ kurhaw wa waḍa\'at-hu kurhā, wa ḥamluhụ wa fiṣāluhụ ṡalāṡụna syahrā, ḥattā iżā balaga asyuddahụ wa balaga arba\'īna sanatang qāla rabbi auzi\'nī an asykura ni\'matakallatī an\'amta \'alayya wa \'alā wālidayya wa an a\'mala ṣāliḥan tarḍāhu wa aṣliḥ lī fī żurriyyatī, innī tubtu ilaika wa innī minal-muslimīn'),
(4525, 46, 16, 'اُولٰۤىِٕكَ الَّذِيْنَ نَتَقَبَّلُ عَنْهُمْ اَحْسَنَ مَا عَمِلُوْا وَنَتَجَاوَزُ عَنْ سَيِّاٰتِهِمْ فِيْٓ اَصْحٰبِ الْجَنَّةِۗ وَعْدَ الصِّدْقِ الَّذِيْ كَانُوْا يُوْعَدُوْنَ', 'ulā`ikallażīna nataqabbalu \'an-hum aḥsana mā \'amilụ wa natajāwazu \'an sayyi`ātihim fī aṣ-ḥābil-jannah, wa\'daṣ-ṣidqillażī kānụ yụ\'adụn'),
(4526, 46, 17, 'وَالَّذِيْ قَالَ لِوَالِدَيْهِ اُفٍّ لَّكُمَآ اَتَعِدَانِنِيْٓ اَنْ اُخْرَجَ وَقَدْ خَلَتِ الْقُرُوْنُ مِنْ قَبْلِيْۚ وَهُمَا يَسْتَغِيْثٰنِ اللّٰهَ وَيْلَكَ اٰمِنْ ۖاِنَّ وَعْدَ اللّٰهِ حَقٌّۚ فَيَقُوْلُ مَا هٰذَآ اِلَّآ اَسَاطِيْرُ الْاَوَّلِيْنَ', 'wallażī qāla liwālidaihi uffil lakumā ata\'idāninī an ukhraja wa qad khalatil-qurụnu ming qablī, wa humā yastagīṡānillāha wailaka āmin inna wa\'dallāhi ḥaqq, fa yaqụlu mā hāżā illā asāṭīrul-awwalīn'),
(4527, 46, 18, 'اُولٰۤىِٕكَ الَّذِيْنَ حَقَّ عَلَيْهِمُ الْقَوْلُ فِيْٓ اُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِهِمْ مِّنَ الْجِنِّ وَالْاِنْسِ ۗاِنَّهُمْ كَانُوْا خٰسِرِيْنَ', 'ulā`ikallażīna ḥaqqa \'alaihimul-qaulu fī umaming qad khalat ming qablihim minal-jinni wal-ins, innahum kānụ khāsirīn'),
(4528, 46, 19, 'وَلِكُلٍّ دَرَجٰتٌ مِّمَّا عَمِلُوْاۚ وَلِيُوَفِّيَهُمْ اَعْمَالَهُمْ وَهُمْ لَا يُظْلَمُوْنَ', 'wa likullin darajātum mimmā \'amilụ, wa liyuwaffiyahum a\'mālahum wa hum lā yuẓlamụn'),
(4529, 46, 20, 'وَيَوْمَ يُعْرَضُ الَّذِيْنَ كَفَرُوْا عَلَى النَّارِۗ اَذْهَبْتُمْ طَيِّبٰتِكُمْ فِيْ حَيَاتِكُمُ الدُّنْيَا وَاسْتَمْتَعْتُمْ بِهَاۚ فَالْيَوْمَ تُجْزَوْنَ عَذَابَ الْهُوْنِ بِمَا كُنْتُمْ تَسْتَكْبِرُوْنَ فِى الْاَرْضِ بِغَيْرِ الْحَقِّ وَبِمَا كُنْتُمْ تَفْسُقُوْنَ', 'wa yauma yu\'raḍullażīna kafarụ \'alan-nār, aż-habtum ṭayyibātikum fī ḥayātikumud-dun-yā wastamta\'tum bihā, fal-yauma tujzauna \'ażābal-hụni bimā kuntum tastakbirụna fil-arḍi bigairil-ḥaqqi wa bimā kuntum tafsuqụn'),
(4530, 46, 21, '۞ وَاذْكُرْ اَخَا عَادٍۗ اِذْ اَنْذَرَ قَوْمَهٗ بِالْاَحْقَافِ وَقَدْ خَلَتِ النُّذُرُ مِنْۢ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهٖٓ اَلَّا تَعْبُدُوْٓا اِلَّا اللّٰهَ ۗاِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظِيْمٍ', 'ważkur akhā \'ād, iż anżara qaumahụ bil-aḥqāfi wa qad khalatin-nużuru mim baini yadaihi wa min khalfihī allā ta\'budū illallāh, innī akhāfu \'alaikum \'ażāba yaumin \'aẓīm'),
(4531, 46, 22, 'قَالُوْٓا اَجِئْتَنَا لِتَأْفِكَنَا عَنْ اٰلِهَتِنَاۚ فَأْتِنَا بِمَا تَعِدُنَآ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ', 'qālū a ji`tanā lita`fikanā \'an ālihatinā, fa`tinā bimā ta\'idunā ing kunta minaṣ-ṣādiqīn'),
(4532, 46, 23, 'قَالَ اِنَّمَا الْعِلْمُ عِنْدَ اللّٰهِ ۖوَاُبَلِّغُكُمْ مَّآ اُرْسِلْتُ بِهٖ وَلٰكِنِّيْٓ اَرٰىكُمْ قَوْمًا تَجْهَلُوْنَ', 'qāla innamal-\'ilmu \'indallāhi wa uballigukum mā ursiltu bihī wa lākinnī arākum qauman taj-halụn'),
(4533, 46, 24, 'فَلَمَّا رَاَوْهُ عَارِضًا مُّسْتَقْبِلَ اَوْدِيَتِهِمْ قَالُوْا هٰذَا عَارِضٌ مُّمْطِرُنَا ۗبَلْ هُوَ مَا اسْتَعْجَلْتُمْ بِهٖ ۗرِيْحٌ فِيْهَا عَذَابٌ اَلِيْمٌۙ', 'fa lammā ra`auhu \'āriḍam mustaqbila audiyatihim qālụ hāżā \'āriḍum mumṭirunā, bal huwa masta\'jaltum bih, rīḥun fīhā \'ażābun alīm'),
(4534, 46, 25, 'تُدَمِّرُ كُلَّ شَيْءٍۢ بِاَمْرِ رَبِّهَا فَاَصْبَحُوْا لَا يُرٰىٓ اِلَّا مَسٰكِنُهُمْۗ كَذٰلِكَ نَجْزِى الْقَوْمَ الْمُجْرِمِيْنَ', 'tudammiru kulla syai`im bi`amri rabbihā fa`aṣbaḥụ lā yurā illā masākinuhum, każālika najzil-qaumal-mujrimīn'),
(4535, 46, 26, 'وَلَقَدْ مَكَّنّٰهُمْ فِيْمَآ اِنْ مَّكَّنّٰكُمْ فِيْهِ وَجَعَلْنَا لَهُمْ سَمْعًا وَّاَبْصَارًا وَّاَفْـِٕدَةًۖ فَمَآ اَغْنٰى عَنْهُمْ سَمْعُهُمْ وَلَآ اَبْصَارُهُمْ وَلَآ اَفْـِٕدَتُهُمْ مِّنْ شَيْءٍ اِذْ كَانُوْا يَجْحَدُوْنَ بِاٰيٰتِ اللّٰهِ وَحَاقَ بِهِمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ', 'wa laqad makkannāhum fīmā im makkannākum fīhi wa ja\'alnā lahum sam\'aw wa abṣāraw wa af`idatan fa mā agnā \'an-hum sam\'uhum wa lā abṣāruhum wa lā af`idatuhum min syai`in iż kānụ yaj-ḥadụna bi`āyātillāhi wa ḥāqa bihim mā kānụ bihī yastahzi`ụn'),
(4536, 46, 27, 'وَلَقَدْ اَهْلَكْنَا مَا حَوْلَكُمْ مِّنَ الْقُرٰى وَصَرَّفْنَا الْاٰيٰتِ لَعَلَّهُمْ يَرْجِعُوْنَ', 'wa laqad ahlaknā mā ḥaulakum minal-qurā wa ṣarrafnal-āyāti la\'allahum yarji\'ụn'),
(4537, 46, 28, 'فَلَوْلَا نَصَرَهُمُ الَّذِيْنَ اتَّخَذُوْا مِنْ دُوْنِ اللّٰهِ قُرْبَانًا اٰلِهَةً ۗبَلْ ضَلُّوْا عَنْهُمْۚ وَذٰلِكَ اِفْكُهُمْ وَمَا كَانُوْا يَفْتَرُوْنَ', 'falau lā naṣarahumullażīnattakhażụ min dụnillāhi qurbānan ālihah, bal ḍallụ \'an-hum, wa żālika ifkuhum wa mā kānụ yaftarụn'),
(4538, 46, 29, 'وَاِذْ صَرَفْنَآ اِلَيْكَ نَفَرًا مِّنَ الْجِنِّ يَسْتَمِعُوْنَ الْقُرْاٰنَۚ فَلَمَّا حَضَرُوْهُ قَالُوْٓا اَنْصِتُوْاۚ فَلَمَّا قُضِيَ وَلَّوْا اِلٰى قَوْمِهِمْ مُّنْذِرِيْنَ', 'wa iż ṣarafnā ilaika nafaram minal-jinni yastami\'ụnal-qur`ān, fa lammā ḥaḍarụhu qālū anṣitụ, fa lammā quḍiya wallau ilā qaumihim munżirīn'),
(4539, 46, 30, 'قَالُوْا يٰقَوْمَنَآ اِنَّا سَمِعْنَا كِتٰبًا اُنْزِلَ مِنْۢ بَعْدِ مُوْسٰى مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ يَهْدِيْٓ اِلَى الْحَقِّ وَاِلٰى طَرِيْقٍ مُّسْتَقِيْمٍ', 'qālụ yā qaumanā innā sami\'nā kitāban unzila mim ba\'di mụsā muṣaddiqal limā baina yadaihi yahdī ilal-ḥaqqi wa ilā ṭarīqim mustaqīm'),
(4540, 46, 31, 'يٰقَوْمَنَآ اَجِيْبُوْا دَاعِيَ اللّٰهِ وَاٰمِنُوْا بِهٖ يَغْفِرْ لَكُمْ مِّنْ ذُنُوْبِكُمْ وَيُجِرْكُمْ مِّنْ عَذَابٍ اَلِيْمٍ', 'yā qaumanā ajībụ dā\'iyallāhi wa āminụ bihī yagfir lakum min żunụbikum wa yujirkum min \'ażābin alīm'),
(4541, 46, 32, 'وَمَنْ لَّا يُجِبْ دَاعِيَ اللّٰهِ فَلَيْسَ بِمُعْجِزٍ فِى الْاَرْضِ وَلَيْسَ لَهٗ مِنْ دُوْنِهٖٓ اَوْلِيَاۤءُ ۗ اُولٰۤىِٕكَ فِيْ ضَلٰلٍ مُّبِيْنٍ', 'wa mal lā yujib dā\'iyallāhi fa laisa bimu\'jizin fil-arḍi wa laisa lahụ min dụnihī auliyā`, ulā`ika fī ḍalālim mubīn'),
(4542, 46, 33, 'اَوَلَمْ يَرَوْا اَنَّ اللّٰهَ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَلَمْ يَعْيَ بِخَلْقِهِنَّ بِقٰدِرٍ عَلٰٓى اَنْ يُّحْيِ َۧ الْمَوْتٰى ۗبَلٰٓى اِنَّهٗ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'a wa lam yarau annallāhallażī khalaqas-samāwāti wal-arḍa wa lam ya\'ya bikhalqihinna biqādirin \'alā ay yuḥyiyal-mautā, balā innahụ \'alā kulli syai`ing qadīr'),
(4543, 46, 34, 'وَيَوْمَ يُعْرَضُ الَّذِيْنَ كَفَرُوْا عَلَى النَّارِۗ اَلَيْسَ هٰذَا بِالْحَقِّ ۗ قَالُوْا بَلٰى وَرَبِّنَا ۗقَالَ فَذُوْقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُوْنَ', 'wa yauma yu\'raḍullażīna kafarụ \'alan-nār, a laisa hāżā bil-ḥaqq, qālụ balā wa rabbinā, qāla fa żụqul-\'ażāba bimā kuntum takfurụn'),
(4544, 46, 35, 'فَاصْبِرْ كَمَا صَبَرَ اُولُوا الْعَزْمِ مِنَ الرُّسُلِ وَلَا تَسْتَعْجِلْ لَّهُمْ ۗ كَاَنَّهُمْ يَوْمَ يَرَوْنَ مَا يُوْعَدُوْنَۙ لَمْ يَلْبَثُوْٓا اِلَّا سَاعَةً مِّنْ نَّهَارٍ ۗ بَلٰغٌ ۚفَهَلْ يُهْلَكُ اِلَّا الْقَوْمُ الْفٰسِقُوْنَ', 'faṣbir kamā ṣabara ulul-\'azmi minar-rusuli wa lā tasta\'jil lahum, ka`annahum yauma yarauna mā yụ\'adụna lam yalbaṡū illā sā\'atam min nahār, balāg, fa hal yuhlaku illal-qaumul-fāsiqụn'),
(4545, 47, 1, 'اَلَّذِيْنَ كَفَرُوْا وَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ اَضَلَّ اَعْمَالَهُمْ', 'allażīna kafarụ wa ṣaddụ \'an sabīlillāhi aḍalla a\'mālahum'),
(4546, 47, 2, 'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَاٰمَنُوْا بِمَا نُزِّلَ عَلٰى مُحَمَّدٍ وَّهُوَ الْحَقُّ مِنْ رَّبِّهِمْ ۚ كَفَّرَ عَنْهُمْ سَيِّاٰتِهِمْ وَاَصْلَحَ بَالَهُمْ', 'wallażīna āmanụ wa \'amiluṣ-ṣāliḥāti wa āmanụ bimā nuzzila \'alā muḥammadiw wa huwal-ḥaqqu mir rabbihim, kaffara \'an-hum sayyi`ātihim wa aṣlaḥa bālahum'),
(4547, 47, 3, 'ذٰلِكَ بِاَنَّ الَّذِيْنَ كَفَرُوا اتَّبَعُوا الْبَاطِلَ وَاَنَّ الَّذِيْنَ اٰمَنُوا اتَّبَعُوا الْحَقَّ مِنْ رَّبِّهِمْ ۗ كَذٰلِكَ يَضْرِبُ اللّٰهُ لِلنَّاسِ اَمْثَالَهُمْ', 'żālika bi`annallażīna kafaruttaba\'ul-bāṭila wa annallażīna āmanuttaba\'ul-ḥaqqa mir rabbihim, każālika yaḍribullāhu lin-nāsi amṡālahum'),
(4548, 47, 4, 'فَاِذَا لَقِيْتُمُ الَّذِيْنَ كَفَرُوْا فَضَرْبَ الرِّقَابِۗ حَتّٰٓى اِذَآ اَثْخَنْتُمُوْهُمْ فَشُدُّوا الْوَثَاقَۖ فَاِمَّا مَنًّاۢ بَعْدُ وَاِمَّا فِدَاۤءً حَتّٰى تَضَعَ الْحَرْبُ اَوْزَارَهَا ەۛ ذٰلِكَ ۛ وَلَوْ يَشَاۤءُ اللّٰهُ لَانْتَصَرَ مِنْهُمْ وَلٰكِنْ لِّيَبْلُوَا۟ بَعْضَكُمْ بِبَعْضٍۗ وَالَّذِيْنَ قُتِلُوْا فِيْ سَبِيْلِ اللّٰهِ فَلَنْ يُّضِلَّ اَعْمَالَهُمْ', 'fa iżā laqītumullażīna kafarụ fa ḍarbar-riqāb, ḥattā iżā aṡkhantumụhum fa syuddul-waṡāqa fa immā mannam ba\'du wa immā fidā`an ḥattā taḍa\'al-ḥarbu auzārahā, żālika walau yasyā`ullāhu lantaṣara min-hum wa lākil liyabluwa ba\'ḍakum biba\'ḍ, wallażīna qutilụ fī sabīlillāhi fa lay yuḍilla a\'mālahum'),
(4549, 47, 5, 'سَيَهْدِيْهِمْ وَيُصْلِحُ بَالَهُمْۚ', 'sayahdīhim wa yuṣliḥu bālahum'),
(4550, 47, 6, 'وَيُدْخِلُهُمُ الْجَنَّةَ عَرَّفَهَا لَهُمْ', 'wa yudkhiluhumul-jannata \'arrafahā lahum'),
(4551, 47, 7, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنْ تَنْصُرُوا اللّٰهَ يَنْصُرْكُمْ وَيُثَبِّتْ اَقْدَامَكُمْ', 'yā ayyuhallażīna āmanū in tanṣurullāha yanṣurkum wa yuṡabbit aqdāmakum'),
(4552, 47, 8, 'وَالَّذِيْنَ كَفَرُوْا فَتَعْسًا لَّهُمْ وَاَضَلَّ اَعْمَالَهُمْ', 'wallażīna kafarụ fa ta\'sal lahum wa aḍalla a\'mālahum'),
(4553, 47, 9, 'ذٰلِكَ بِاَنَّهُمْ كَرِهُوْا مَآ اَنْزَلَ اللّٰهُ فَاَحْبَطَ اَعْمَالَهُمْ', 'żālika bi`annahum karihụ mā anzalallāhu fa aḥbaṭa a\'mālahum'),
(4554, 47, 10, '۞ اَفَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَيَنْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلِهِمْ ۗ دَمَّرَ اللّٰهُ عَلَيْهِمْ ۖوَلِلْكٰفِرِيْنَ اَمْثَالُهَا', 'a fa lam yasīrụ fil-arḍi fa yanẓurụ kaifa kāna \'āqibatullażīna ming qablihim, dammarallāhu \'alaihim wa lil-kāfirīna amṡāluhā'),
(4555, 47, 11, 'ذٰلِكَ بِاَنَّ اللّٰهَ مَوْلَى الَّذِيْنَ اٰمَنُوْا وَاَنَّ الْكٰفِرِيْنَ لَا مَوْلٰى لَهُمْ', 'żālika bi`annallāha maulallażīna āmanụ wa annal-kāfirīna lā maulā lahum'),
(4556, 47, 12, 'اِنَّ اللّٰهَ يُدْخِلُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ ۗوَالَّذِيْنَ كَفَرُوْا يَتَمَتَّعُوْنَ وَيَأْكُلُوْنَ كَمَا تَأْكُلُ الْاَنْعَامُ وَالنَّارُ مَثْوًى لَّهُمْ', 'innallāha yudkhilullażīna āmanụ wa \'amiluṣ-ṣāliḥāti jannātin tajrī min taḥtihal-an-hār, wallażīna kafarụ yatamatta\'ụna wa ya`kulụna kamā ta`kulul-an\'āmu wan-nāru maṡwal lahum'),
(4557, 47, 13, 'وَكَاَيِّنْ مِّنْ قَرْيَةٍ هِيَ اَشَدُّ قُوَّةً مِّنْ قَرْيَتِكَ الَّتِيْٓ اَخْرَجَتْكَۚ اَهْلَكْنٰهُمْ فَلَا نَاصِرَ لَهُمْ', 'wa ka`ayyim ming qaryatin hiya asyaddu quwwatam ming qaryatikallatī akhrajatk, ahlaknāhum fa lā nāṣira lahum'),
(4558, 47, 14, 'اَفَمَنْ كَانَ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّهٖ كَمَنْ زُيِّنَ لَهٗ سُوْۤءُ عَمَلِهٖ وَاتَّبَعُوْٓا اَهْوَاۤءَهُمْ', 'a fa mang kāna \'alā bayyinatim mir rabbihī kaman zuyyina lahụ sū`u \'amalihī wattaba\'ū ahwā`ahum'),
(4559, 47, 15, 'مَثَلُ الْجَنَّةِ الَّتِيْ وُعِدَ الْمُتَّقُوْنَ ۗفِيْهَآ اَنْهٰرٌ مِّنْ مَّاۤءٍ غَيْرِ اٰسِنٍۚ وَاَنْهٰرٌ مِّنْ لَّبَنٍ لَّمْ يَتَغَيَّرْ طَعْمُهٗ ۚوَاَنْهٰرٌ مِّنْ خَمْرٍ لَّذَّةٍ لِّلشّٰرِبِيْنَ ەۚ وَاَنْهٰرٌ مِّنْ عَسَلٍ مُّصَفًّى ۗوَلَهُمْ فِيْهَا مِنْ كُلِّ الثَّمَرٰتِ وَمَغْفِرَةٌ مِّنْ رَّبِّهِمْ ۗ كَمَنْ هُوَ خَالِدٌ فِى النَّارِ وَسُقُوْا مَاۤءً حَمِيْمًا فَقَطَّعَ اَمْعَاۤءَهُمْ', 'maṡalul-jannatillatī wu\'idal-muttaqụn, fīhā an-hārum mim mā`in gairi āsin, wa an-hārum mil labanil lam yatagayyar ṭa\'muh, wa an-hārum min khamril lażżatil lisy-syāribīn, wa an-hārum min \'asalim muṣaffā, wa lahum fīhā ming kulliṡ-ṡamarāti wa magfiratum mir rabbihim, kaman huwa khālidun fin-nāri wa suqụ mā`an ḥamīman fa qaṭṭa\'a am\'ā`ahum');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(4560, 47, 16, 'وَمِنْهُمْ مَّنْ يَّسْتَمِعُ اِلَيْكَۚ حَتّٰىٓ اِذَا خَرَجُوْا مِنْ عِنْدِكَ قَالُوْا لِلَّذِيْنَ اُوْتُوا الْعِلْمَ مَاذَا قَالَ اٰنِفًا ۗ اُولٰۤىِٕكَ الَّذِيْنَ طَبَعَ اللّٰهُ عَلٰى قُلُوْبِهِمْ وَاتَّبَعُوْٓا اَهْوَاۤءَهُمْ', 'wa min-hum may yastami\'u ilaīk, ḥattā iżā kharajụ min \'indika qālụ lillażīna ụtul-\'ilma māżā qāla ānifā, ulā`ikallażīna ṭaba\'allāhu \'alā qulụbihim wattaba\'ū ahwā`ahum'),
(4561, 47, 17, 'وَالَّذِيْنَ اهْتَدَوْا زَادَهُمْ هُدًى وَّاٰتٰىهُمْ تَقْوٰىهُمْ', 'wallażīnahtadau zādahum hudaw wa ātāhum taqwāhum'),
(4562, 47, 18, 'فَهَلْ يَنْظُرُوْنَ اِلَّا السَّاعَةَ اَنْ تَأْتِيَهُمْ بَغْتَةً ۚ فَقَدْ جَاۤءَ اَشْرَاطُهَا ۚ فَاَنّٰى لَهُمْ اِذَا جَاۤءَتْهُمْ ذِكْرٰىهُمْ', 'fa hal yanẓurụna illas-sā\'ata an ta`tiyahum bagtah, fa qad jā`a asyrāṭuhā, fa annā lahum iżā jā`at-hum żikrāhum'),
(4563, 47, 19, 'فَاعْلَمْ اَنَّهٗ لَآ اِلٰهَ اِلَّا اللّٰهُ وَاسْتَغْفِرْ لِذَنْۢبِكَ وَلِلْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِۚ وَاللّٰهُ يَعْلَمُ مُتَقَلَّبَكُمْ وَمَثْوٰىكُمْ', 'fa\'lam annahụ lā ilāha illallāhu wastagfir liżambika wa lil-mu`minīna wal-mu`mināt, wallāhu ya\'lamu mutaqallabakum wa maṡwākum'),
(4564, 47, 20, 'وَيَقُوْلُ الَّذِيْنَ اٰمَنُوْا لَوْلَا نُزِّلَتْ سُوْرَةٌ ۚفَاِذَآ اُنْزِلَتْ سُوْرَةٌ مُّحْكَمَةٌ وَّذُكِرَ فِيْهَا الْقِتَالُ ۙرَاَيْتَ الَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ يَّنْظُرُوْنَ اِلَيْكَ نَظَرَ الْمَغْشِيِّ عَلَيْهِ مِنَ الْمَوْتِۗ فَاَوْلٰى لَهُمْۚ', 'wa yaqụlullażīna āmanụ lau lā nuzzilat sụrah, fa iżā unzilat sụratum muḥkamatuw wa żukira fīhal-qitālu ra`aitallażīna fī qulụbihim maraḍuy yanẓurụna ilaika naẓaral-magsyiyyi \'alaihi minal-maụt, fa aulā lahum'),
(4565, 47, 21, 'طَاعَةٌ وَّقَوْلٌ مَّعْرُوْفٌۗ فَاِذَا عَزَمَ الْاَمْرُۗ فَلَوْ صَدَقُوا اللّٰهَ لَكَانَ خَيْرًا لَّهُمْۚ', 'ṭā\'atuw wa qaulum ma\'rụf, fa iżā \'azamal-amr, falau ṣadaqullāha lakāna khairal lahum'),
(4566, 47, 22, 'فَهَلْ عَسَيْتُمْ اِنْ تَوَلَّيْتُمْ اَنْ تُفْسِدُوْا فِى الْاَرْضِ وَتُقَطِّعُوْٓا اَرْحَامَكُمْ', 'fa hal \'asaitum in tawallaitum an tufsidụ fil-arḍi wa tuqaṭṭi\'ū ar-ḥāmakum'),
(4567, 47, 23, 'اُولٰۤىِٕكَ الَّذِيْنَ لَعَنَهُمُ اللّٰهُ فَاَصَمَّهُمْ وَاَعْمٰٓى اَبْصَارَهُمْ', 'ulā`ikallażīna la\'anahumullāhu fa aṣammahum wa a\'mā abṣārahum'),
(4568, 47, 24, 'اَفَلَا يَتَدَبَّرُوْنَ الْقُرْاٰنَ اَمْ عَلٰى قُلُوْبٍ اَقْفَالُهَا', 'a fa lā yatadabbarụnal-qur`āna am \'alā qulụbin aqfāluhā'),
(4569, 47, 25, 'اِنَّ الَّذِيْنَ ارْتَدُّوْا عَلٰٓى اَدْبَارِهِمْ مِّنْۢ بَعْدِ مَا تَبَيَّنَ لَهُمُ الْهُدَى الشَّيْطٰنُ سَوَّلَ لَهُمْۗ وَاَمْلٰى لَهُمْ', 'innallażīnartaddụ \'alā adbārihim mim ba\'di mā tabayyana lahumul-hudasy-syaiṭānu sawwala lahum, wa amlā lahum'),
(4570, 47, 26, 'ذٰلِكَ بِاَنَّهُمْ قَالُوْا لِلَّذِيْنَ كَرِهُوْا مَا نَزَّلَ اللّٰهُ سَنُطِيْعُكُمْ فِيْ بَعْضِ الْاَمْرِۚ وَاللّٰهُ يَعْلَمُ اِسْرَارَهُمْ', 'żālika bi`annahum qālụ lillażīna karihụ mā nazzalallāhu sanuṭī\'ukum fī ba\'ḍil-amr, wallāhu ya\'lamu isrārahum'),
(4571, 47, 27, 'فَكَيْفَ اِذَا تَوَفَّتْهُمُ الْمَلٰۤىِٕكَةُ يَضْرِبُوْنَ وُجُوْهَهُمْ وَاَدْبَارَهُمْ', 'fa kaifa iżā tawaffat-humul-malā`ikatu yaḍribụna wujụhahum wa adbārahum'),
(4572, 47, 28, 'ذٰلِكَ بِاَنَّهُمُ اتَّبَعُوْا مَآ اَسْخَطَ اللّٰهَ وَكَرِهُوْا رِضْوَانَهٗ فَاَحْبَطَ اَعْمَالَهُمْ', 'żālika bi`annahumuttaba\'ụ mā askhaṭallāha wa karihụ riḍwānahụ fa aḥbaṭa a\'mālahum'),
(4573, 47, 29, 'اَمْ حَسِبَ الَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ اَنْ لَّنْ يُّخْرِجَ اللّٰهُ اَضْغَانَهُمْ', 'am ḥasiballażīna fī qulụbihim maraḍun al lay yukhrijallāhu aḍgānahum'),
(4574, 47, 30, 'وَلَوْ نَشَاۤءُ لَاَرَيْنٰكَهُمْ فَلَعَرَفْتَهُمْ بِسِيْمٰهُمْ ۗوَلَتَعْرِفَنَّهُمْ فِيْ لَحْنِ الْقَوْلِۗ وَاللّٰهُ يَعْلَمُ اَعْمَالَكُمْ', 'walau nasyā`u la`arainākahum fa la\'araftahum bisīmāhum, wa lata\'rifannahum fī laḥnil-qaụl, wallāhu ya\'lamu a\'mālakum'),
(4575, 47, 31, 'وَلَنَبْلُوَنَّكُمْ حَتّٰى نَعْلَمَ الْمُجٰهِدِيْنَ مِنْكُمْ وَالصّٰبِرِيْنَۙ وَنَبْلُوَا۟ اَخْبَارَكُمْ', 'wa lanabluwannakum ḥattā na\'lamal-mujāhidīna mingkum waṣ-ṣābirīna wa nabluwa akhbārakum'),
(4576, 47, 32, 'اِنَّ الَّذِيْنَ كَفَرُوْا وَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ وَشَاۤقُّوا الرَّسُوْلَ مِنْۢ بَعْدِ مَا تَبَيَّنَ لَهُمُ الْهُدٰى لَنْ يَّضُرُّوا اللّٰهَ شَيْـًٔاۗ وَسَيُحْبِطُ اَعْمَالَهُمْ', 'innallażīna kafarụ wa ṣaddụ \'an sabīlillāhi wa syāqqur-rasụla mim ba\'di mā tabayyana lahumul-hudā lay yaḍurrullāha syai`ā, wa sayuḥbiṭu a\'mālahum'),
(4577, 47, 33, '۞ يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَطِيْعُوا اللّٰهَ وَاَطِيْعُوا الرَّسُوْلَ وَلَا تُبْطِلُوْٓا اَعْمَالَكُمْ', 'yā ayyuhallażīna āmanū aṭī\'ullāha wa aṭī\'ur-rasụla wa lā tubṭilū a\'mālakum'),
(4578, 47, 34, 'اِنَّ الَّذِيْنَ كَفَرُوْا وَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ ثُمَّ مَاتُوْا وَهُمْ كُفَّارٌ فَلَنْ يَّغْفِرَ اللّٰهُ لَهُمْ', 'innallażīna kafarụ wa ṣaddụ \'an sabīlillāhi ṡumma mātụ wa hum kuffārun fa lay yagfirallāhu lahum'),
(4579, 47, 35, 'فَلَا تَهِنُوْا وَتَدْعُوْٓا اِلَى السَّلْمِۖ وَاَنْتُمُ الْاَعْلَوْنَۗ وَاللّٰهُ مَعَكُمْ وَلَنْ يَّتِرَكُمْ اَعْمَالَكُمْ', 'fa lā tahinụ wa tad\'ū ilas-salmi wa antumul-a\'laụn, wallāhu ma\'akum wa lay yatirakum a\'mālakum'),
(4580, 47, 36, 'اِنَّمَا الْحَيٰوةُ الدُّنْيَا لَعِبٌ وَّلَهْوٌ ۗوَاِنْ تُؤْمِنُوْا وَتَتَّقُوْا يُؤْتِكُمْ اُجُوْرَكُمْ وَلَا يَسْـَٔلْكُمْ اَمْوَالَكُمْ', 'innamal-ḥayātud-dun-yā la\'ibuw wa lahw, wa in tu`minụ wa tattaqụ yu`tikum ujụrakum wa lā yas`alkum amwālakum'),
(4581, 47, 37, 'اِنْ يَّسْـَٔلْكُمُوْهَا فَيُحْفِكُمْ تَبْخَلُوْا وَيُخْرِجْ اَضْغَانَكُمْ', 'iy yas`alkumụhā fa yuḥfikum tabkhalụ wa yukhrij aḍgānakum'),
(4582, 47, 38, 'هٰٓاَنْتُمْ هٰٓؤُلَاۤءِ تُدْعَوْنَ لِتُنْفِقُوْا فِيْ سَبِيْلِ اللّٰهِۚ فَمِنْكُمْ مَّنْ يَّبْخَلُ ۚوَمَنْ يَّبْخَلْ فَاِنَّمَا يَبْخَلُ عَنْ نَّفْسِهٖ ۗوَاللّٰهُ الْغَنِيُّ وَاَنْتُمُ الْفُقَرَاۤءُ ۗ وَاِنْ تَتَوَلَّوْا يَسْتَبْدِلْ قَوْمًا غَيْرَكُمْۙ ثُمَّ لَا يَكُوْنُوْٓا اَمْثَالَكُمْ', 'hā`antum hā`ulā`i tud\'auna litunfiqụ fī sabīlillāh, fa mingkum may yabkhal, wa may yabkhal fa innamā yabkhalu \'an nafsih, wallāhul-ganiyyu wa antumul-fuqarā`, wa in tatawallau yastabdil-qauman gairakum ṡumma lā yakụnū amṡālakum'),
(4583, 48, 1, ' اِنَّا فَتَحْنَا لَكَ فَتْحًا مُّبِيْنًاۙ', 'innā fataḥnā laka fat-ḥam mubīnā'),
(4584, 48, 2, 'لِّيَغْفِرَ لَكَ اللّٰهُ مَا تَقَدَّمَ مِنْ ذَنْۢبِكَ وَمَا تَاَخَّرَ وَيُتِمَّ نِعْمَتَهٗ عَلَيْكَ وَيَهْدِيَكَ صِرَاطًا مُّسْتَقِيْمًاۙ', 'liyagfira lakallāhu mā taqaddama min żambika wa mā ta`akhkhara wa yutimma ni\'matahụ \'alaika wa yahdiyaka ṣirāṭam mustaqīmā'),
(4585, 48, 3, 'وَّيَنْصُرَكَ اللّٰهُ نَصْرًا عَزِيْزًا', 'wa yanṣurakallāhu naṣran \'azīzā'),
(4586, 48, 4, 'هُوَ الَّذِيْٓ اَنْزَلَ السَّكِيْنَةَ فِيْ قُلُوْبِ الْمُؤْمِنِيْنَ لِيَزْدَادُوْٓا اِيْمَانًا مَّعَ اِيْمَانِهِمْ ۗ وَلِلّٰهِ جُنُوْدُ السَّمٰوٰتِ وَالْاَرْضِۗ وَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًاۙ', 'huwallażī anzalas-sakīnata fī qulụbil-mu`minīna liyazdādū īmānam ma\'a īmānihim, wa lillāhi junụdus-samāwāti wal-arḍ, wa kānallāhu \'alīman ḥakīmā'),
(4587, 48, 5, 'لِّيُدْخِلَ الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا وَيُكَفِّرَ عَنْهُمْ سَيِّاٰتِهِمْۗ وَكَانَ ذٰلِكَ عِنْدَ اللّٰهِ فَوْزًا عَظِيْمًاۙ', 'liyudkhilal-mu`minīna wal-mu`mināti jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā wa yukaffira \'an-hum sayyi`ātihim, wa kāna żālika \'indallāhi fauzan \'aẓīmā'),
(4588, 48, 6, 'وَّيُعَذِّبَ الْمُنٰفِقِيْنَ وَالْمُنٰفِقٰتِ وَالْمُشْرِكِيْنَ وَالْمُشْرِكٰتِ الظَّاۤنِّيْنَ بِاللّٰهِ ظَنَّ السَّوْءِۗ عَلَيْهِمْ دَاۤىِٕرَةُ السَّوْءِۚ وَغَضِبَ اللّٰهُ عَلَيْهِمْ وَلَعَنَهُمْ وَاَعَدَّ لَهُمْ جَهَنَّمَۗ وَسَاۤءَتْ مَصِيْرًا', 'wa yu\'ażżibal-munāfiqīna wal-munāfiqāti wal-musyrikīna wal-musyrikātiẓ-ẓānnīna billāhi ẓannas-saụ`, \'alaihim dā`iratus-saụ`, wa gaḍiballāhu \'alaihim wa la\'anahum wa a\'adda lahum jahannam, wa sā`at maṣīrā'),
(4589, 48, 7, 'وَلِلّٰهِ جُنُوْدُ السَّمٰوٰتِ وَالْاَرْضِۗ وَكَانَ اللّٰهُ عَزِيْزًا حَكِيْمًا', 'wa lillāhi junụdus-samāwāti wal-arḍ, wa kānallāhu \'azīzan ḥakīmā'),
(4590, 48, 8, 'اِنَّآ اَرْسَلْنٰكَ شَاهِدًا وَّمُبَشِّرًا وَّنَذِيْرًاۙ', 'innā arsalnāka syāhidaw wa mubasysyiraw wa nażīrā'),
(4591, 48, 9, 'لِّتُؤْمِنُوْا بِاللّٰهِ وَرَسُوْلِهٖ وَتُعَزِّرُوْهُ وَتُوَقِّرُوْهُۗ وَتُسَبِّحُوْهُ بُكْرَةً وَّاَصِيْلًا', 'litu`minụ billāhi wa rasụlihī wa tu\'azzirụhu wa tuwaqqirụh, wa tusabbiḥụhu bukrataw wa aṣīlā'),
(4592, 48, 10, 'اِنَّ الَّذِيْنَ يُبَايِعُوْنَكَ اِنَّمَا يُبَايِعُوْنَ اللّٰهَ ۗيَدُ اللّٰهِ فَوْقَ اَيْدِيْهِمْ ۚ فَمَنْ نَّكَثَ فَاِنَّمَا يَنْكُثُ عَلٰى نَفْسِهٖۚ وَمَنْ اَوْفٰى بِمَا عٰهَدَ عَلَيْهُ اللّٰهَ فَسَيُؤْتِيْهِ اَجْرًا عَظِيْمًا', 'innallażīna yubāyi\'ụnaka innamā yubāyi\'ụnallāh, yadullāhi fauqa aidīhim, fa man nakaṡa fa innamā yangkuṡu \'alā nafsih, wa man aufā bimā \'āhada \'alaihullāha fa sayu`tīhi ajran \'aẓīmā'),
(4593, 48, 11, 'سَيَقُوْلُ لَكَ الْمُخَلَّفُوْنَ مِنَ الْاَعْرَابِ شَغَلَتْنَآ اَمْوَالُنَا وَاَهْلُوْنَا فَاسْتَغْفِرْ لَنَا ۚيَقُوْلُوْنَ بِاَلْسِنَتِهِمْ مَّا لَيْسَ فِيْ قُلُوْبِهِمْۗ قُلْ فَمَنْ يَّمْلِكُ لَكُمْ مِّنَ اللّٰهِ شَيْـًٔا اِنْ اَرَادَ بِكُمْ ضَرًّا اَوْ اَرَادَ بِكُمْ نَفْعًا ۗبَلْ كَانَ اللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرًا', 'sayaqụlu lakal-mukhallafụna minal-a\'rābi syagalatnā amwālunā wa ahlụnā fastagfir lanā, yaqụlụna bi`alsinatihim mā laisa fī qulụbihim, qul fa may yamliku lakum minallāhi syai`an in arāda bikum ḍarran au arāda bikum naf\'ā, bal kānallāhu bimā ta\'malụna khabīrā'),
(4594, 48, 12, 'بَلْ ظَنَنْتُمْ اَنْ لَّنْ يَّنْقَلِبَ الرَّسُوْلُ وَالْمُؤْمِنُوْنَ اِلٰٓى اَهْلِيْهِمْ اَبَدًا وَّزُيِّنَ ذٰلِكَ فِيْ قُلُوْبِكُمْ وَظَنَنْتُمْ ظَنَّ السَّوْءِۚ وَكُنْتُمْ قَوْمًاۢ بُوْرًا', 'bal ẓanantum al lay yangqalibar-rasụlu wal-mu`minụna ilā ahlīhim abadaw wa zuyyina żālika fī qulụbikum wa ẓanantum ẓannas-saụ`, wa kuntum qaumam bụrā'),
(4595, 48, 13, 'وَمَنْ لَّمْ يُؤْمِنْۢ بِاللّٰهِ وَرَسُوْلِهٖ فَاِنَّآ اَعْتَدْنَا لِلْكٰفِرِيْنَ سَعِيْرًا', 'wa mal lam yu`mim billāhi wa rasụlihī fa innā a\'tadnā lil-kāfirīna sa\'īrā'),
(4596, 48, 14, 'وَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ يَغْفِرُ لِمَنْ يَّشَاۤءُ وَيُعَذِّبُ مَنْ يَّشَاۤءُ ۗوَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا', 'wa lillāhi mulkus-samāwāti wal-arḍ, yagfiru limay yasyā`u wa yu\'ażżibu may yasyā`, wa kānallāhu gafụrar raḥīmā'),
(4597, 48, 15, 'سَيَقُوْلُ الْمُخَلَّفُوْنَ اِذَا انْطَلَقْتُمْ اِلٰى مَغَانِمَ لِتَأْخُذُوْهَا ذَرُوْنَا نَتَّبِعْكُمْ ۚ يُرِيْدُوْنَ اَنْ يُّبَدِّلُوْا كَلٰمَ اللّٰهِ ۗ قُلْ لَّنْ تَتَّبِعُوْنَا كَذٰلِكُمْ قَالَ اللّٰهُ مِنْ قَبْلُ ۖفَسَيَقُوْلُوْنَ بَلْ تَحْسُدُوْنَنَا ۗ بَلْ كَانُوْا لَا يَفْقَهُوْنَ اِلَّا قَلِيْلًا', 'sayaqụlul-mukhallafụna iżanṭalaqtum ilā magānima lita`khużụhā żarụnā nattabi\'kum, yurīdụna ay yubaddilụ kalāmallāh, qul lan tattabi\'ụnā każālikum qālallāhu ming qabl, fa sayaqụlụna bal taḥsudụnanā, bal kānụ lā yafqahụna illā qalīlā'),
(4598, 48, 16, 'قُلْ لِّلْمُخَلَّفِيْنَ مِنَ الْاَعْرَابِ سَتُدْعَوْنَ اِلٰى قَوْمٍ اُولِيْ بَأْسٍ شَدِيْدٍ تُقَاتِلُوْنَهُمْ اَوْ يُسْلِمُوْنَ ۚ فَاِنْ تُطِيْعُوْا يُؤْتِكُمُ اللّٰهُ اَجْرًا حَسَنًا ۚ وَاِنْ تَتَوَلَّوْا كَمَا تَوَلَّيْتُمْ مِّنْ قَبْلُ يُعَذِّبْكُمْ عَذَابًا اَلِيْمًا', 'qul lil-mukhallafīna minal-a\'rābi satud\'auna ilā qaumin ulī ba`sin syadīdin tuqātilụnahum au yuslimụn, fa in tuṭī\'ụ yu`tikumullāhu ajran ḥasanā, wa in tatawallau kamā tawallaitum ming qablu yu\'ażżibkum \'ażāban alīmā'),
(4599, 48, 17, 'لَيْسَ عَلَى الْاَعْمٰى حَرَجٌ وَّلَا عَلَى الْاَعْرَجِ حَرَجٌ وَّلَا عَلَى الْمَرِيْضِ حَرَجٌ ۗ وَمَنْ يُّطِعِ اللّٰهَ وَرَسُوْلَهٗ يُدْخِلْهُ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ ۚ وَمَنْ يَّتَوَلَّ يُعَذِّبْهُ عَذَابًا اَلِيْمًا', 'laisa \'alal-a\'mā ḥarajuw wa lā \'alal-a\'raji ḥarajuw wa lā \'alal-marīḍi ḥaraj, wa may yuṭi\'illāha wa rasụlahụ yudkhil-hu jannātin tajrī min taḥtihal-an-hār, wa may yatawalla yu\'ażżib-hu \'ażāban alīmā'),
(4600, 48, 18, '۞ لَقَدْ رَضِيَ اللّٰهُ عَنِ الْمُؤْمِنِيْنَ اِذْ يُبَايِعُوْنَكَ تَحْتَ الشَّجَرَةِ فَعَلِمَ مَا فِيْ قُلُوْبِهِمْ فَاَنْزَلَ السَّكِيْنَةَ عَلَيْهِمْ وَاَثَابَهُمْ فَتْحًا قَرِيْبًاۙ', 'laqad raḍiyallāhu \'anil-mu`minīna iż yubāyi\'ụnaka taḥtasy-syajarati fa \'alima mā fī qulụbihim fa anzalas-sakīnata \'alaihim wa aṡābahum fat-ḥang qarībā'),
(4601, 48, 19, 'وَّمَغَانِمَ كَثِيْرَةً يَّأْخُذُوْنَهَا ۗ وَكَانَ اللّٰهُ عَزِيْزًا حَكِيْمًا', 'wa magānima kaṡīratay ya`khużụnahā, wa kānallāhu \'azīzan ḥakīmā'),
(4602, 48, 20, 'وَعَدَكُمُ اللّٰهُ مَغَانِمَ كَثِيْرَةً تَأْخُذُوْنَهَا فَعَجَّلَ لَكُمْ هٰذِهٖ وَكَفَّ اَيْدِيَ النَّاسِ عَنْكُمْۚ وَلِتَكُوْنَ اٰيَةً لِّلْمُؤْمِنِيْنَ وَيَهْدِيَكُمْ صِرَاطًا مُّسْتَقِيْمًاۙ', 'wa \'adakumullāhu magānima kaṡīratan ta`khużụnahā fa \'ajjala lakum hāżihī wa kaffa aidiyan-nāsi \'angkum, wa litakụna āyatal lil-mu`minīna wa yahdiyakum ṣirāṭam mustaqīmā'),
(4603, 48, 21, 'وَّاُخْرٰى لَمْ تَقْدِرُوْا عَلَيْهَا قَدْ اَحَاطَ اللّٰهُ بِهَا ۗوَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرًا', 'wa ukhrā lam taqdirụ \'alaihā qad aḥāṭallāhu bihā, wa kānallāhu \'alā kulli syai`ing qadīrā'),
(4604, 48, 22, 'وَلَوْ قَاتَلَكُمُ الَّذِيْنَ كَفَرُوْا لَوَلَّوُا الْاَدْبَارَ ثُمَّ لَا يَجِدُوْنَ وَلِيًّا وَّلَا نَصِيْرًا', 'walau qātalakumullażīna kafarụ lawallawul-adbāra ṡumma lā yajidụna waliyyaw wa lā naṣīrā'),
(4605, 48, 23, 'سُنَّةَ اللّٰهِ الَّتِيْ قَدْ خَلَتْ مِنْ قَبْلُ ۖوَلَنْ تَجِدَ لِسُنَّةِ اللّٰهِ تَبْدِيْلًا', 'sunnatallāhillatī qad khalat ming qabl, wa lan tajida lisunnatillāhi tabdīlā'),
(4606, 48, 24, 'وَهُوَ الَّذِيْ كَفَّ اَيْدِيَهُمْ عَنْكُمْ وَاَيْدِيَكُمْ عَنْهُمْ بِبَطْنِ مَكَّةَ مِنْۢ بَعْدِ اَنْ اَظْفَرَكُمْ عَلَيْهِمْ ۗوَكَانَ اللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرًا', 'wa huwallażī kaffa aidiyahum \'angkum wa aidiyakum \'an-hum bibaṭni makkata mim ba\'di an aẓfarakum \'alaihim, wa kānallāhu bimā ta\'malụna baṣīrā'),
(4607, 48, 25, 'هُمُ الَّذِيْنَ كَفَرُوْا وَصَدُّوْكُمْ عَنِ الْمَسْجِدِ الْحَرَامِ وَالْهَدْيَ مَعْكُوْفًا اَنْ يَّبْلُغَ مَحِلَّهٗ ۚوَلَوْلَا رِجَالٌ مُّؤْمِنُوْنَ وَنِسَاۤءٌ مُّؤْمِنٰتٌ لَّمْ تَعْلَمُوْهُمْ اَنْ تَطَـُٔوْهُمْ فَتُصِيْبَكُمْ مِّنْهُمْ مَّعَرَّةٌ ۢبِغَيْرِ عِلْمٍ ۚ لِيُدْخِلَ اللّٰهُ فِيْ رَحْمَتِهٖ مَنْ يَّشَاۤءُۚ لَوْ تَزَيَّلُوْا لَعَذَّبْنَا الَّذِيْنَ كَفَرُوْا مِنْهُمْ عَذَابًا اَلِيْمًا', 'humullażīna kafarụ wa ṣaddụkum \'anil-masjidil-ḥarāmi wal-hadya ma\'kụfan ay yabluga maḥillah, walau lā rijālum mu`minụna wa nisā`um mu`minātul lam ta\'lamụhum an taṭa\'ụhum fa tuṣībakum min-hum ma\'arratum bigairi \'ilm, liyudkhilallāhu fī raḥmatihī may yasyā`, lau tazayyalụ la\'ażżabnallażīna kafarụ min-hum \'ażāban alīmā'),
(4608, 48, 26, 'اِذْ جَعَلَ الَّذِيْنَ كَفَرُوْا فِيْ قُلُوْبِهِمُ الْحَمِيَّةَ حَمِيَّةَ الْجَاهِلِيَّةِ فَاَنْزَلَ اللّٰهُ سَكِيْنَتَهٗ عَلٰى رَسُوْلِهٖ وَعَلَى الْمُؤْمِنِيْنَ وَاَلْزَمَهُمْ كَلِمَةَ التَّقْوٰى وَكَانُوْٓا اَحَقَّ بِهَا وَاَهْلَهَا ۗوَكَانَ اللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمًا', 'iż ja\'alallażīna kafarụ fī qulụbihimul-ḥamiyyata ḥamiyyatal-jāhiliyyati fa anzalallāhu sakīnatahụ \'alā rasụlihī wa \'alal-mu`minīna wa alzamahum kalimatat-taqwā wa kānū aḥaqqa bihā wa ahlahā, wa kānallāhu bikulli syai`in \'alīmā'),
(4609, 48, 27, 'لَقَدْ صَدَقَ اللّٰهُ رَسُوْلَهُ الرُّءْيَا بِالْحَقِّ ۚ لَتَدْخُلُنَّ الْمَسْجِدَ الْحَرَامَ اِنْ شَاۤءَ اللّٰهُ اٰمِنِيْنَۙ مُحَلِّقِيْنَ رُءُوْسَكُمْ وَمُقَصِّرِيْنَۙ لَا تَخَافُوْنَ ۗفَعَلِمَ مَا لَمْ تَعْلَمُوْا فَجَعَلَ مِنْ دُوْنِ ذٰلِكَ فَتْحًا قَرِيْبًا', 'laqad ṣadaqallāhu rasụlahur-ru`yā bil-ḥaqq, latadkhulunnal-masjidal-ḥarāma in syā`allāhu āminīna muḥalliqīna ru`ụsakum wa muqaṣṣirīna lā takhāfụn, fa \'alima mā lam ta\'lamụ fa ja\'ala min dụni żālika fat-ḥang qarībā'),
(4610, 48, 28, 'هُوَ الَّذِيْٓ اَرْسَلَ رَسُوْلَهٗ بِالْهُدٰى وَدِيْنِ الْحَقِّ لِيُظْهِرَهٗ عَلَى الدِّيْنِ كُلِّهٖ ۗوَكَفٰى بِاللّٰهِ شَهِيْدًا', 'huwallażī arsala rasụlahụ bil-hudā wa dīnil-ḥaqqi liyuẓ-hirahụ \'alad-dīni kullih, wa kafā billāhi syahīdā'),
(4611, 48, 29, 'مُحَمَّدٌ رَّسُوْلُ اللّٰهِ ۗوَالَّذِيْنَ مَعَهٗٓ اَشِدَّاۤءُ عَلَى الْكُفَّارِ رُحَمَاۤءُ بَيْنَهُمْ تَرٰىهُمْ رُكَّعًا سُجَّدًا يَّبْتَغُوْنَ فَضْلًا مِّنَ اللّٰهِ وَرِضْوَانًا ۖ سِيْمَاهُمْ فِيْ وُجُوْهِهِمْ مِّنْ اَثَرِ السُّجُوْدِ ۗذٰلِكَ مَثَلُهُمْ فِى التَّوْرٰىةِ ۖوَمَثَلُهُمْ فِى الْاِنْجِيْلِۚ كَزَرْعٍ اَخْرَجَ شَطْـَٔهٗ فَاٰزَرَهٗ فَاسْتَغْلَظَ فَاسْتَوٰى عَلٰى سُوْقِهٖ يُعْجِبُ الزُّرَّاعَ لِيَغِيْظَ بِهِمُ الْكُفَّارَ ۗوَعَدَ اللّٰهُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ مِنْهُمْ مَّغْفِرَةً وَّاَجْرًا عَظِيْمًا', 'muḥammadur rasụlullāh, wallażīna ma\'ahū asyiddā`u \'alal-kuffāri ruḥamā`u bainahum tarāhum rukka\'an sujjaday yabtagụna faḍlam minallāhi wa riḍwānan sīmāhum fī wujụhihim min aṡaris-sujụd, żālika maṡaluhum fit-taurāti wa maṡaluhum fil-injīl, kazar\'in akhraja syaṭ`ahụ fa āzarahụ fastaglaẓa fastawā \'alā sụqihī yu\'jibuz-zurrā\'a liyagīẓa bihimul-kuffār, wa\'adallāhullażīna āmanụ wa \'amiluṣ-ṣāliḥāti min-hum magfirataw wa ajran \'aẓīmā'),
(4612, 49, 1, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تُقَدِّمُوْا بَيْنَ يَدَيِ اللّٰهِ وَرَسُوْلِهٖ وَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ سَمِيْعٌ عَلِيْمٌ', 'yā ayyuhallażīna āmanụ lā tuqaddimụ baina yadayillāhi wa rasụlihī wattaqullāh, innallāha samī\'un \'alīm'),
(4613, 49, 2, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَرْفَعُوْٓا اَصْوَاتَكُمْ فَوْقَ صَوْتِ النَّبِيِّ وَلَا تَجْهَرُوْا لَهٗ بِالْقَوْلِ كَجَهْرِ بَعْضِكُمْ لِبَعْضٍ اَنْ تَحْبَطَ اَعْمَالُكُمْ وَاَنْتُمْ لَا تَشْعُرُوْنَ', 'yā ayyuhallażīna āmanụ lā tarfa\'ū aṣwātakum fauqa ṣautin-nabiyyi wa lā taj-harụ lahụ bil-qauli kajahri ba\'ḍikum liba\'ḍin an taḥbaṭa a\'mālukum wa antum lā tasy\'urụn'),
(4614, 49, 3, 'اِنَّ الَّذِيْنَ يَغُضُّوْنَ اَصْوَاتَهُمْ عِنْدَ رَسُوْلِ اللّٰهِ اُولٰۤىِٕكَ الَّذِيْنَ امْتَحَنَ اللّٰهُ قُلُوْبَهُمْ لِلتَّقْوٰىۗ لَهُمْ مَّغْفِرَةٌ وَّاَجْرٌ عَظِيْمٌ', 'innallażīna yaguḍḍụna aṣwātahum \'inda rasụlillāhi ulā`ikallażīnamtaḥanallāhu qulụbahum lit-taqwā, lahum magfiratuw wa ajrun \'aẓīm'),
(4615, 49, 4, 'اِنَّ الَّذِيْنَ يُنَادُوْنَكَ مِنْ وَّرَاۤءِ الْحُجُرٰتِ اَكْثَرُهُمْ لَا يَعْقِلُوْنَ', 'innallażīna yunādụnaka miw warā`il-ḥujurāti akṡaruhum lā ya\'qilụn'),
(4616, 49, 5, 'وَلَوْ اَنَّهُمْ صَبَرُوْا حَتّٰى تَخْرُجَ اِلَيْهِمْ لَكَانَ خَيْرًا لَّهُمْ ۗوَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ', 'walau annahum ṣabarụ ḥattā takhruja ilaihim lakāna khairal lahum, wallāhu gafụrur raḥīm'),
(4617, 49, 6, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنْ جَاۤءَكُمْ فَاسِقٌۢ بِنَبَاٍ فَتَبَيَّنُوْٓا اَنْ تُصِيْبُوْا قَوْمًاۢ بِجَهَالَةٍ فَتُصْبِحُوْا عَلٰى مَا فَعَلْتُمْ نٰدِمِيْنَ', 'yā ayyuhallażīna āmanū in jā`akum fāsiqum binaba`in fa tabayyanū an tuṣībụ qaumam bijahālatin fa tuṣbiḥụ \'alā mā fa\'altum nādimīn'),
(4618, 49, 7, 'وَاعْلَمُوْٓا اَنَّ فِيْكُمْ رَسُوْلَ اللّٰهِ ۗ لَوْ يُطِيْعُكُمْ فِيْ كَثِيْرٍ مِّنَ الْاَمْرِ لَعَنِتُّمْ وَلٰكِنَّ اللّٰهَ حَبَّبَ اِلَيْكُمُ الْاِيْمَانَ وَزَيَّنَهٗ فِيْ قُلُوْبِكُمْ وَكَرَّهَ اِلَيْكُمُ الْكُفْرَ وَالْفُسُوْقَ وَالْعِصْيَانَ ۗ اُولٰۤىِٕكَ هُمُ الرَّاشِدُوْنَۙ', 'wa\'lamū anna fīkum rasụlallāh, lau yuṭī\'ukum fī kaṡīrim minal-amri la\'anittum wa lākinnallāha ḥabbaba ilaikumul-īmāna wa zayyanahụ fī qulụbikum wa karraha ilaikumul-kufra wal-fusụqa wal-\'iṣyān, ulā`ika humur-rāsyidụn'),
(4619, 49, 8, 'فَضْلًا مِّنَ اللّٰهِ وَنِعْمَةً ۗوَاللّٰهُ عَلِيْمٌ حَكِيْمٌ', 'faḍlam minallāhi wa ni\'mah, wallāhu \'alīmun ḥakīm'),
(4620, 49, 9, 'وَاِنْ طَاۤىِٕفَتٰنِ مِنَ الْمُؤْمِنِيْنَ اقْتَتَلُوْا فَاَصْلِحُوْا بَيْنَهُمَاۚ فَاِنْۢ بَغَتْ اِحْدٰىهُمَا عَلَى الْاُخْرٰى فَقَاتِلُوا الَّتِيْ تَبْغِيْ حَتّٰى تَفِيْۤءَ اِلٰٓى اَمْرِ اللّٰهِ ۖفَاِنْ فَاۤءَتْ فَاَصْلِحُوْا بَيْنَهُمَا بِالْعَدْلِ وَاَقْسِطُوْا ۗاِنَّ اللّٰهَ يُحِبُّ الْمُقْسِطِيْنَ', 'wa in ṭā`ifatāni minal-mu`minīnaqtatalụ fa aṣliḥụ bainahumā, fa im bagat iḥdāhumā \'alal-ukhrā fa qātilullatī tabgī ḥattā tafī`a ilā amrillāh, fa in fā`at fa aṣliḥụ bainahumā bil-\'adli wa aqsiṭụ, innallāha yuḥibbul-muqsiṭīn'),
(4621, 49, 10, 'اِنَّمَا الْمُؤْمِنُوْنَ اِخْوَةٌ فَاَصْلِحُوْا بَيْنَ اَخَوَيْكُمْ وَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُرْحَمُوْنَ', 'innamal-mu`minụna ikhwatun fa aṣliḥụ baina akhawaikum wattaqullāha la\'allakum tur-ḥamụn'),
(4622, 49, 11, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا يَسْخَرْ قَوْمٌ مِّنْ قَوْمٍ عَسٰٓى اَنْ يَّكُوْنُوْا خَيْرًا مِّنْهُمْ وَلَا نِسَاۤءٌ مِّنْ نِّسَاۤءٍ عَسٰٓى اَنْ يَّكُنَّ خَيْرًا مِّنْهُنَّۚ وَلَا تَلْمِزُوْٓا اَنْفُسَكُمْ وَلَا تَنَابَزُوْا بِالْاَلْقَابِۗ بِئْسَ الِاسْمُ الْفُسُوْقُ بَعْدَ الْاِيْمَانِۚ وَمَنْ لَّمْ يَتُبْ فَاُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ', 'yā ayyuhallażīna āmanụ lā yaskhar qaumum ming qaumin \'asā ay yakụnụ khairam min-hum wa lā nisā`um min nisā`in \'asā ay yakunna khairam min-hunn, wa lā talmizū anfusakum wa lā tanābazụ bil-alqāb, bi`sa lismul-fusụqu ba\'dal-īmān, wa mal lam yatub fa ulā`ika humuẓ-ẓālimụn'),
(4623, 49, 12, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اجْتَنِبُوْا كَثِيْرًا مِّنَ الظَّنِّۖ اِنَّ بَعْضَ الظَّنِّ اِثْمٌ وَّلَا تَجَسَّسُوْا وَلَا يَغْتَبْ بَّعْضُكُمْ بَعْضًاۗ اَيُحِبُّ اَحَدُكُمْ اَنْ يَّأْكُلَ لَحْمَ اَخِيْهِ مَيْتًا فَكَرِهْتُمُوْهُۗ وَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ تَوَّابٌ رَّحِيْمٌ', 'yā ayyuhallażīna āmanujtanibụ kaṡīram minaẓ-ẓanni inna ba\'ḍaẓ-ẓanni iṡmuw wa lā tajassasụ wa lā yagtab ba\'ḍukum ba\'ḍā, a yuḥibbu aḥadukum ay ya`kula laḥma akhīhi maitan fa karihtumụh, wattaqullāh, innallāha tawwābur raḥīm'),
(4624, 49, 13, 'يٰٓاَيُّهَا النَّاسُ اِنَّا خَلَقْنٰكُمْ مِّنْ ذَكَرٍ وَّاُنْثٰى وَجَعَلْنٰكُمْ شُعُوْبًا وَّقَبَاۤىِٕلَ لِتَعَارَفُوْا ۚ اِنَّ اَكْرَمَكُمْ عِنْدَ اللّٰهِ اَتْقٰىكُمْ ۗاِنَّ اللّٰهَ عَلِيْمٌ خَبِيْرٌ', 'yā ayyuhan-nāsu innā khalaqnākum min żakariw wa unṡā wa ja\'alnākum syu\'ụbaw wa qabā`ila lita\'ārafụ, inna akramakum \'indallāhi atqākum, innallāha \'alīmun khabīr'),
(4625, 49, 14, '۞ قَالَتِ الْاَعْرَابُ اٰمَنَّا ۗ قُلْ لَّمْ تُؤْمِنُوْا وَلٰكِنْ قُوْلُوْٓا اَسْلَمْنَا وَلَمَّا يَدْخُلِ الْاِيْمَانُ فِيْ قُلُوْبِكُمْ ۗوَاِنْ تُطِيْعُوا اللّٰهَ وَرَسُوْلَهٗ لَا يَلِتْكُمْ مِّنْ اَعْمَالِكُمْ شَيْـًٔا ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'qālatil-a\'rābu āmannā, qul lam tu`minụ wa lāking qụlū aslamnā wa lammā yadkhulil-īmānu fī qulụbikum, wa in tuṭī\'ullāha wa rasụlahụ lā yalitkum min a\'mālikum syai`ā, innallāha gafụrur raḥīm'),
(4626, 49, 15, 'اِنَّمَا الْمُؤْمِنُوْنَ الَّذِيْنَ اٰمَنُوْا بِاللّٰهِ وَرَسُوْلِهٖ ثُمَّ لَمْ يَرْتَابُوْا وَجَاهَدُوْا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ فِيْ سَبِيْلِ اللّٰهِ ۗ اُولٰۤىِٕكَ هُمُ الصّٰدِقُوْنَ', 'innamal-mu`minụnallażīna āmanụ billāhi wa rasụlihī ṡumma lam yartābụ wa jāhadụ bi`amwālihim wa anfusihim fī sabīlillāh, ulā`ika humuṣ-ṣādiqụn'),
(4627, 49, 16, 'قُلْ اَتُعَلِّمُوْنَ اللّٰهَ بِدِيْنِكُمْۗ وَاللّٰهُ يَعْلَمُ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَاللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمٌ', 'qul a tu\'allimụnallāha bidīnikum, wallāhu ya\'lamu mā fis-samāwāti wa mā fil-arḍ, wallāhu bikulli syai`in \'alīm'),
(4628, 49, 17, 'يَمُنُّوْنَ عَلَيْكَ اَنْ اَسْلَمُوْا ۗ قُلْ لَّا تَمُنُّوْا عَلَيَّ اِسْلَامَكُمْ ۚبَلِ اللّٰهُ يَمُنُّ عَلَيْكُمْ اَنْ هَدٰىكُمْ لِلْاِيْمَانِ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'yamunnụna \'alaika an aslamụ, qul lā tamunnụ \'alayya islāmakum, balillāhu yamunnu \'alaikum an hadākum lil-īmāni ing kuntum ṣādiqīn'),
(4629, 49, 18, 'اِنَّ اللّٰهَ يَعْلَمُ غَيْبَ السَّمٰوٰتِ وَالْاَرْضِۗ وَاللّٰهُ بَصِيْرٌۢ بِمَا تَعْمَلُوْنَ', 'innallāha ya\'lamu gaibas-samāwāti wal-arḍ, wallāhu baṣīrum bimā ta\'malụn'),
(4630, 50, 1, ' قۤ ۗوَالْقُرْاٰنِ الْمَجِيْدِ ۖ', 'qāf, wal-qur`ānil-majīd'),
(4631, 50, 2, 'بَلْ عَجِبُوْٓا اَنْ جَاۤءَهُمْ مُّنْذِرٌ مِّنْهُمْ فَقَالَ الْكٰفِرُوْنَ هٰذَا شَيْءٌ عَجِيْبٌ ۚ', 'bal \'ajibū an jā`ahum munżirum min-hum fa qālal-kāfirụna hāżā syai`un \'ajīb'),
(4632, 50, 3, 'ءَاِذَا مِتْنَا وَكُنَّا تُرَابًا ۚ ذٰلِكَ رَجْعٌۢ بَعِيْدٌ', 'a iżā mitnā wa kunnā turābā, żālika raj\'um ba\'īd'),
(4633, 50, 4, 'قَدْ عَلِمْنَا مَا تَنْقُصُ الْاَرْضُ مِنْهُمْ ۚوَعِنْدَنَا كِتٰبٌ حَفِيْظٌ', 'qad \'alimnā mā tangquṣul-arḍu min-hum, wa \'indanā kitābun ḥafīẓ'),
(4634, 50, 5, 'بَلْ كَذَّبُوْا بِالْحَقِّ لَمَّا جَاۤءَهُمْ فَهُمْ فِيْٓ اَمْرٍ مَّرِيْجٍ', 'bal każżabụ bil-ḥaqqi lammā jā`ahum fa hum fī amrim marīj'),
(4635, 50, 6, 'اَفَلَمْ يَنْظُرُوْٓا اِلَى السَّمَاۤءِ فَوْقَهُمْ كَيْفَ بَنَيْنٰهَا وَزَيَّنّٰهَا وَمَا لَهَا مِنْ فُرُوْجٍ', 'a fa lam yanẓurū ilas-samā`i fauqahum kaifa banaināhā wa zayyannāhā wa mā lahā min furụj'),
(4636, 50, 7, 'وَالْاَرْضَ مَدَدْنٰهَا وَاَلْقَيْنَا فِيْهَا رَوَاسِيَ وَاَنْۢبَتْنَا فِيْهَا مِنْ كُلِّ زَوْجٍۢ بَهِيْجٍۙ', 'wal-arḍa madadnāhā wa alqainā fīhā rawāsiya wa ambatnā fīhā ming kulli zaujim bahīj'),
(4637, 50, 8, 'تَبْصِرَةً وَّذِكْرٰى لِكُلِّ عَبْدٍ مُّنِيْبٍ', 'tabṣirataw wa żikrā likulli \'abdim munīb'),
(4638, 50, 9, 'وَنَزَّلْنَا مِنَ السَّمَاۤءِ مَاۤءً مُّبٰرَكًا فَاَنْۢبَتْنَا بِهٖ جَنّٰتٍ وَّحَبَّ الْحَصِيْدِۙ', 'wa nazzalnā minas-samā`i mā`am mubārakan fa ambatnā bihī jannātiw wa ḥabbal-ḥaṣīd'),
(4639, 50, 10, 'وَالنَّخْلَ بٰسِقٰتٍ لَّهَا طَلْعٌ نَّضِيْدٌۙ', 'wan-nakhla bāsiqātil lahā ṭal\'un naḍīd'),
(4640, 50, 11, 'رِّزْقًا لِّلْعِبَادِۙ وَاَحْيَيْنَا بِهٖ بَلْدَةً مَّيْتًاۗ كَذٰلِكَ الْخُرُوْجُ', 'rizqal lil-\'ibādi wa aḥyainā bihī baldatam maitā, każālikal-khurụj'),
(4641, 50, 12, 'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوْحٍ وَّاَصْحٰبُ الرَّسِّ وَثَمُوْدُ', 'każżabat qablahum qaumu nụḥiw wa aṣ-ḥābur-rassi wa ṡamụd'),
(4642, 50, 13, 'وَعَادٌ وَّفِرْعَوْنُ وَاِخْوَانُ لُوْطٍۙ', 'wa \'āduw wa fir\'aunu wa ikhwānu lụṭ'),
(4643, 50, 14, 'وَّاَصْحٰبُ الْاَيْكَةِ وَقَوْمُ تُبَّعٍۗ كُلٌّ كَذَّبَ الرُّسُلَ فَحَقَّ وَعِيْدِ', 'wa aṣ-ḥābul-aikati wa qaumu tubba\', kullung każżabar-rusula fa ḥaqqa wa\'īd'),
(4644, 50, 15, 'اَفَعَيِيْنَا بِالْخَلْقِ الْاَوَّلِۗ بَلْ هُمْ فِيْ لَبْسٍ مِّنْ خَلْقٍ جَدِيْدٍ', 'a fa \'ayīnā bil-khalqil-awwal, bal hum fī labsim min khalqin jadīd'),
(4645, 50, 16, 'وَلَقَدْ خَلَقْنَا الْاِنْسَانَ وَنَعْلَمُ مَا تُوَسْوِسُ بِهٖ نَفْسُهٗ ۖوَنَحْنُ اَقْرَبُ اِلَيْهِ مِنْ حَبْلِ الْوَرِيْدِ', 'wa laqad khalaqnal-insāna wa na\'lamu mā tuwaswisu bihī nafsuh, wa naḥnu aqrabu ilaihi min ḥablil-warīd'),
(4646, 50, 17, 'اِذْ يَتَلَقَّى الْمُتَلَقِّيٰنِ عَنِ الْيَمِيْنِ وَعَنِ الشِّمَالِ قَعِيْدٌ', 'iż yatalaqqal-mutalaqqiyāni \'anil-yamīni wa \'anisy-syimāli qa\'īd'),
(4647, 50, 18, 'مَا يَلْفِظُ مِنْ قَوْلٍ اِلَّا لَدَيْهِ رَقِيْبٌ عَتِيْدٌ', 'mā yalfiẓu ming qaulin illā ladaihi raqībun \'atīd'),
(4648, 50, 19, 'وَجَاۤءَتْ سَكْرَةُ الْمَوْتِ بِالْحَقِّ ۗذٰلِكَ مَا كُنْتَ مِنْهُ تَحِيْدُ', 'wa jā`at sakratul-mauti bil-ḥaqq, żālika mā kunta min-hu taḥīd'),
(4649, 50, 20, 'وَنُفِخَ فِى الصُّوْرِۗ ذٰلِكَ يَوْمُ الْوَعِيْدِ', 'wa nufikha fiṣ-ṣụr, żālika yaumul-wa\'īd'),
(4650, 50, 21, 'وَجَاۤءَتْ كُلُّ نَفْسٍ مَّعَهَا سَاۤىِٕقٌ وَّشَهِيْدٌ', 'wa jā`at kullu nafsim ma\'ahā sā`iquw wa syahīd'),
(4651, 50, 22, 'لَقَدْ كُنْتَ فِيْ غَفْلَةٍ مِّنْ هٰذَا فَكَشَفْنَا عَنْكَ غِطَاۤءَكَ فَبَصَرُكَ الْيَوْمَ حَدِيْدٌ', 'laqad kunta fī gaflatim min hāżā fa kasyafnā \'angka giṭā`aka fa baṣarukal-yauma ḥadīd'),
(4652, 50, 23, 'وَقَالَ قَرِيْنُهٗ هٰذَا مَا لَدَيَّ عَتِيْدٌۗ', 'wa qāla qarīnuhụ hāżā mā ladayya \'atīd'),
(4653, 50, 24, 'اَلْقِيَا فِيْ جَهَنَّمَ كُلَّ كَفَّارٍ عَنِيْدٍ', 'alqiyā fī jahannama kulla kaffārin \'anīd'),
(4654, 50, 25, 'مَنَّاعٍ لِّلْخَيْرِ مُعْتَدٍ مُّرِيْبٍۙ', 'mannā\'il lil-khairi mu\'tadim murīb'),
(4655, 50, 26, ' ۨالَّذِيْ جَعَلَ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ فَاَلْقِيٰهُ فِى الْعَذَابِ الشَّدِيْدِ', 'allażī ja\'ala ma\'allāhi ilāhan ākhara fa alqiyāhu fil-\'ażābisy-syadīd'),
(4656, 50, 27, '۞ قَالَ قَرِيْنُهٗ رَبَّنَا مَآ اَطْغَيْتُهٗ وَلٰكِنْ كَانَ فِيْ ضَلٰلٍۢ بَعِيْدٍ', 'qāla qarīnuhụ rabbanā mā aṭgaituhụ wa lāking kāna fī ḍalālim ba\'īd'),
(4657, 50, 28, 'قَالَ لَا تَخْتَصِمُوْا لَدَيَّ وَقَدْ قَدَّمْتُ اِلَيْكُمْ بِالْوَعِيْدِ', 'qāla lā takhtaṣimụ ladayya wa qad qaddamtu ilaikum bil-wa\'īd'),
(4658, 50, 29, 'مَا يُبَدَّلُ الْقَوْلُ لَدَيَّ وَمَآ اَنَا۠ بِظَلَّامٍ لِّلْعَبِيْدِ', 'mā yubaddalul-qaulu ladayya wa mā ana biẓallāmil lil-\'abīd'),
(4659, 50, 30, 'يَوْمَ نَقُوْلُ لِجَهَنَّمَ هَلِ امْتَلَـْٔتِ وَتَقُوْلُ هَلْ مِنْ مَّزِيْدٍ', 'yauma naqụlu lijahannama halimtala`ti wa taqụlu hal mim mazīd'),
(4660, 50, 31, 'وَاُزْلِفَتِ الْجَنَّةُ لِلْمُتَّقِيْنَ غَيْرَ بَعِيْدٍ', 'wa uzlifatil-jannatu lil-muttaqīna gaira ba\'īd'),
(4661, 50, 32, 'هٰذَا مَا تُوْعَدُوْنَ لِكُلِّ اَوَّابٍ حَفِيْظٍۚ', 'hāżā mā tụ\'adụna likulli awwābin ḥafīẓ'),
(4662, 50, 33, 'مَنْ خَشِيَ الرَّحْمٰنَ بِالْغَيْبِ وَجَاۤءَ بِقَلْبٍ مُّنِيْبٍۙ', 'man khasyiyar-raḥmāna bil-gaibi wa jā`a biqalbim munīb'),
(4663, 50, 34, ' ۨادْخُلُوْهَا بِسَلٰمٍ ۗذٰلِكَ يَوْمُ الْخُلُوْدِ', 'udkhulụhā bisalām, żālika yaumul-khulụd'),
(4664, 50, 35, 'لَهُمْ مَّا يَشَاۤءُوْنَ فِيْهَا وَلَدَيْنَا مَزِيْدٌ', 'lahum mā yasyā`ụna fīhā wa ladainā mazīd'),
(4665, 50, 36, 'وَكَمْ اَهْلَكْنَا قَبْلَهُمْ مِّنْ قَرْنٍ هُمْ اَشَدُّ مِنْهُمْ بَطْشًا فَنَقَّبُوْا فِى الْبِلَادِۗ هَلْ مِنْ مَّحِيْصٍ', 'wa kam ahlaknā qablahum ming qarnin hum asyaddu min-hum baṭsyan fa naqqabụ fil-bilād, hal mim maḥīṣ'),
(4666, 50, 37, 'اِنَّ فِيْ ذٰلِكَ لَذِكْرٰى لِمَنْ كَانَ لَهٗ قَلْبٌ اَوْ اَلْقَى السَّمْعَ وَهُوَ شَهِيْدٌ', 'inna fī żālika lażikrā limang kāna lahụ qalbun au alqas-sam\'a wa huwa syahīd'),
(4667, 50, 38, 'وَلَقَدْ خَلَقْنَا السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَا فِيْ سِتَّةِ اَيَّامٍۖ وَّمَا مَسَّنَا مِنْ لُّغُوْبٍ', 'wa laqad khalaqnas-samāwāti wal-arḍa wa mā bainahumā fī sittati ayyāmiw wa mā massanā mil lugụb'),
(4668, 50, 39, 'فَاصْبِرْ عَلٰى مَا يَقُوْلُوْنَ وَسَبِّحْ بِحَمْدِ رَبِّكَ قَبْلَ طُلُوْعِ الشَّمْسِ وَقَبْلَ الْغُرُوْبِ', 'faṣbir \'alā mā yaqụlụna wa sabbiḥ biḥamdi rabbika qabla ṭulụ\'isy-syamsi wa qablal-gurụb'),
(4669, 50, 40, 'وَمِنَ الَّيْلِ فَسَبِّحْهُ وَاَدْبَارَ السُّجُوْدِ', 'wa minal-laili fa sabbiḥ-hu wa adbāras-sujụd'),
(4670, 50, 41, 'وَاسْتَمِعْ يَوْمَ يُنَادِ الْمُنَادِ مِنْ مَّكَانٍ قَرِيْبٍ', 'wastami\' yauma yunādil-munādi mim makāning qarīb'),
(4671, 50, 42, 'يَوْمَ يَسْمَعُوْنَ الصَّيْحَةَ بِالْحَقِّ ۗذٰلِكَ يَوْمُ الْخُرُوْجِ', 'yauma yasma\'ụnaṣ-ṣaiḥata bil-ḥaqq, żālika yaumul-khurụj'),
(4672, 50, 43, 'اِنَّا نَحْنُ نُحْيٖ وَنُمِيْتُ وَاِلَيْنَا الْمَصِيْرُۙ', 'innā naḥnu nuḥyī wa numītu wa ilainal-maṣīr'),
(4673, 50, 44, 'يَوْمَ تَشَقَّقُ الْاَرْضُ عَنْهُمْ سِرَاعًا ۗذٰلِكَ حَشْرٌ عَلَيْنَا يَسِيْرٌ', 'yauma tasyaqqaqul-arḍu \'an-hum sirā\'ā, żālika ḥasyrun \'alainā yasīr'),
(4674, 50, 45, 'نَحْنُ اَعْلَمُ بِمَا يَقُوْلُوْنَ وَمَآ اَنْتَ عَلَيْهِمْ بِجَبَّارٍۗ فَذَكِّرْ بِالْقُرْاٰنِ مَنْ يَّخَافُ وَعِيْدِ', 'naḥnu a\'lamu bimā yaqụlụna wa mā anta \'alaihim bijabbār, fażakkir bil-qur`āni may yakhāfu wa\'īd'),
(4675, 51, 1, ' وَالذّٰرِيٰتِ ذَرْوًاۙ', 'waż-żāriyāti żarwā'),
(4676, 51, 2, 'فَالْحٰمِلٰتِ وِقْرًاۙ', 'fal-ḥāmilāti wiqrā'),
(4677, 51, 3, 'فَالْجٰرِيٰتِ يُسْرًاۙ', 'fal-jāriyāti yusrā'),
(4678, 51, 4, 'فَالْمُقَسِّمٰتِ اَمْرًاۙ', 'fal-muqassimāti amrā'),
(4679, 51, 5, 'اِنَّمَا تُوْعَدُوْنَ لَصَادِقٌۙ', 'innamā tụ\'adụna laṣādiq'),
(4680, 51, 6, 'وَّاِنَّ الدِّيْنَ لَوَاقِعٌۗ', 'wa innad-dīna lawāqi\''),
(4681, 51, 7, 'وَالسَّمَاۤءِ ذَاتِ الْحُبُكِۙ', 'was-samā`i żātil-ḥubuk'),
(4682, 51, 8, 'اِنَّكُمْ لَفِيْ قَوْلٍ مُّخْتَلِفٍۙ', 'innakum lafī qaulim mukhtalif'),
(4683, 51, 9, 'يُّؤْفَكُ عَنْهُ مَنْ اُفِكَۗ', 'yu`faku \'an-hu man ufik'),
(4684, 51, 10, 'قُتِلَ الْخَرَّاصُوْنَۙ', 'qutilal-kharrāṣụn'),
(4685, 51, 11, 'الَّذِيْنَ هُمْ فِيْ غَمْرَةٍ سَاهُوْنَۙ', 'allażīna hum fī gamratin sāhụn'),
(4686, 51, 12, 'يَسْـَٔلُوْنَ اَيَّانَ يَوْمُ الدِّيْنِۗ', 'yas`alụna ayyāna yaumud-dīn'),
(4687, 51, 13, 'يَوْمَ هُمْ عَلَى النَّارِ يُفْتَنُوْنَ', 'yauma hum \'alan-nāri yuftanụn'),
(4688, 51, 14, 'ذُوْقُوْا فِتْنَتَكُمْۗ هٰذَا الَّذِيْ كُنْتُمْ بِهٖ تَسْتَعْجِلُوْنَ', 'żụqụ fitnatakum, hāżallażī kuntum bihī tasta\'jilụn'),
(4689, 51, 15, 'اِنَّ الْمُتَّقِيْنَ فِيْ جَنّٰتٍ وَّعُيُوْنٍۙ', 'innal-muttaqīna fī jannātiw wa \'uyụn'),
(4690, 51, 16, 'اٰخِذِيْنَ مَآ اٰتٰىهُمْ رَبُّهُمْ ۗ اِنَّهُمْ كَانُوْا قَبْلَ ذٰلِكَ مُحْسِنِيْنَۗ', 'ākhiżīna mā ātāhum rabbuhum, innahum kānụ qabla żālika muḥsinīn'),
(4691, 51, 17, 'كَانُوْا قَلِيْلًا مِّنَ الَّيْلِ مَا يَهْجَعُوْنَ', 'kānụ qalīlam minal-laili mā yahja\'ụn'),
(4692, 51, 18, 'وَبِالْاَسْحَارِ هُمْ يَسْتَغْفِرُوْنَ', 'wa bil-as-ḥāri hum yastagfirụn'),
(4693, 51, 19, 'وَفِيْٓ اَمْوَالِهِمْ حَقٌّ لِّلسَّاۤىِٕلِ وَالْمَحْرُوْمِ', 'wa fī amwālihim ḥaqqul lis-sā`ili wal-maḥrụm'),
(4694, 51, 20, 'وَفِى الْاَرْضِ اٰيٰتٌ لِّلْمُوْقِنِيْنَۙ', 'wa fil-arḍi āyātul lil-mụqinīn'),
(4695, 51, 21, 'وَفِيْٓ اَنْفُسِكُمْ ۗ اَفَلَا تُبْصِرُوْنَ', 'wa fī anfusikum, a fa lā tubṣirụn'),
(4696, 51, 22, 'وَفِى السَّمَاۤءِ رِزْقُكُمْ وَمَا تُوْعَدُوْنَ', 'wa fis-samā`i rizqukum wa mā tụ\'adụn'),
(4697, 51, 23, 'فَوَرَبِّ السَّمَاۤءِ وَالْاَرْضِ اِنَّهٗ لَحَقٌّ مِّثْلَ مَآ اَنَّكُمْ تَنْطِقُوْنَ', 'fa wa rabbis-samā`i wal-arḍi innahụ laḥaqqum miṡla mā annakum tanṭiqụn'),
(4698, 51, 24, 'هَلْ اَتٰىكَ حَدِيْثُ ضَيْفِ اِبْرٰهِيْمَ الْمُكْرَمِيْنَۘ', 'hal atāka ḥadīṡu ḍaifi ibrāhīmal-mukramīn'),
(4699, 51, 25, 'اِذْ دَخَلُوْا عَلَيْهِ فَقَالُوْا سَلٰمًا ۗقَالَ سَلٰمٌۚ قَوْمٌ مُّنْكَرُوْنَ', 'iż dakhalụ \'alaihi fa qālụ salāmā, qāla salām, qaumum mungkarụn'),
(4700, 51, 26, 'فَرَاغَ اِلٰٓى اَهْلِهٖ فَجَاۤءَ بِعِجْلٍ سَمِيْنٍۙ', 'fa rāga ilā ahlihī fa jā`a bi\'ijlin samīn'),
(4701, 51, 27, 'فَقَرَّبَهٗٓ اِلَيْهِمْۚ قَالَ اَلَا تَأْكُلُوْنَ', 'fa qarrabahū ilaihim, qāla alā ta`kulụn'),
(4702, 51, 28, 'فَاَوْجَسَ مِنْهُمْ خِيْفَةً ۗقَالُوْا لَا تَخَفْۗ وَبَشَّرُوْهُ بِغُلٰمٍ عَلِيْمٍ', 'fa aujasa min-hum khīfah, qālụ lā takhaf, wa basysyarụhu bigulāmin \'alīm'),
(4703, 51, 29, 'فَاَقْبَلَتِ امْرَاَتُهٗ فِيْ صَرَّةٍ فَصَكَّتْ وَجْهَهَا وَقَالَتْ عَجُوْزٌ عَقِيْمٌ', 'fa aqbalatimra`atuhụ fī ṣarratin fa ṣakkat waj-hahā wa qālat \'ajụzun \'aqīm'),
(4704, 51, 30, 'قَالُوْا كَذٰلِكِۙ قَالَ رَبُّكِ ۗاِنَّهٗ هُوَ الْحَكِيْمُ الْعَلِيْمُ ۔', 'qālụ każāliki qāla rabbuk, innahụ huwal-ḥakīmul-\'alīm'),
(4705, 51, 31, 'قَالَ فَمَا خَطْبُكُمْ اَيُّهَا الْمُرْسَلُوْنَۚ', 'qāla fa mā khaṭbukum ayyuhal-mursalụn'),
(4706, 51, 32, 'قَالُوْٓ اِنَّآ اُرْسِلْنَآ اِلٰى قَوْمٍ مُّجْرِمِيْنَۙ', 'qālū innā ursilnā ilā qaumim mujrimīn'),
(4707, 51, 33, 'لِنُرْسِلَ عَلَيْهِمْ حِجَارَةً مِّنْ طِيْنٍۙ', 'linursila \'alaihim ḥijāratam min ṭīn'),
(4708, 51, 34, 'مُّسَوَّمَةً عِنْدَ رَبِّكَ لِلْمُسْرِفِيْنَ', 'musawwamatan \'inda rabbika lil-musrifīn'),
(4709, 51, 35, 'فَاَخْرَجْنَا مَنْ كَانَ فِيْهَا مِنَ الْمُؤْمِنِيْنَۚ', 'fa akhrajnā mang kāna fīhā minal-mu`minīn'),
(4710, 51, 36, 'فَمَا وَجَدْنَا فِيْهَا غَيْرَ بَيْتٍ مِّنَ الْمُسْلِمِيْنَۚ', 'fa mā wajadnā fīhā gaira baitim minal-muslimīn'),
(4711, 51, 37, 'وَتَرَكْنَا فِيْهَآ اٰيَةً لِّلَّذِيْنَ يَخَافُوْنَ الْعَذَابَ الْاَلِيْمَۗ', 'wa taraknā fīhā āyatal lillażīna yakhāfụnal-\'ażābal-alīm'),
(4712, 51, 38, 'وَفِيْ مُوْسٰىٓ اِذْ اَرْسَلْنٰهُ اِلٰى فِرْعَوْنَ بِسُلْطٰنٍ مُّبِيْنٍ', 'wa fī mụsā iż arsalnāhu ilā fir\'auna bisulṭānim mubīn'),
(4713, 51, 39, 'فَتَوَلّٰى بِرُكْنِهٖ وَقَالَ سٰحِرٌ اَوْ مَجْنُوْنٌ', 'fa tawallā biruknihī wa qāla sāḥirun au majnụn'),
(4714, 51, 40, 'فَاَخَذْنٰهُ وَجُنُوْدَهٗ فَنَبَذْنٰهُمْ فِى الْيَمِّ وَهُوَ مُلِيْمٌۗ', 'fa akhażnāhu wa junụdahụ fa nabażnāhum fil-yammi wa huwa mulīm'),
(4715, 51, 41, 'وَفِيْ عَادٍ اِذْ اَرْسَلْنَا عَلَيْهِمُ الرِّيْحَ الْعَقِيْمَۚ', 'wa fī \'ādin iż arsalnā \'alaihimur-rīḥal-\'aqīm'),
(4716, 51, 42, 'مَا تَذَرُ مِنْ شَيْءٍ اَتَتْ عَلَيْهِ اِلَّا جَعَلَتْهُ كَالرَّمِيْمِۗ', 'mā tażaru min syai`in atat \'alaihi illā ja\'alat-hu kar-ramīm'),
(4717, 51, 43, 'وَفِيْ ثَمُوْدَ اِذْ قِيْلَ لَهُمْ تَمَتَّعُوْا حَتّٰى حِيْنٍ', 'wa fī ṡamụda iż qīla lahum tamatta\'ụ ḥattā ḥīn'),
(4718, 51, 44, 'فَعَتَوْا عَنْ اَمْرِ رَبِّهِمْ فَاَخَذَتْهُمُ الصّٰعِقَةُ وَهُمْ يَنْظُرُوْنَ', 'fa \'atau \'an amri rabbihim fa akhażat-humuṣ-ṣā\'iqatu wa hum yanẓurụn'),
(4719, 51, 45, 'فَمَا اسْتَطَاعُوْا مِنْ قِيَامٍ وَّمَا كَانُوْا مُنْتَصِرِيْنَۙ', 'fa mastaṭā\'ụ ming qiyāmiw wa mā kānụ muntaṣirīn'),
(4720, 51, 46, 'وَقَوْمَ نُوْحٍ مِّنْ قَبْلُ ۗ اِنَّهُمْ كَانُوْا قَوْمًا فٰسِقِيْنَ', 'wa qauma nụḥim ming qabl, innahum kānụ qauman fāsiqīn'),
(4721, 51, 47, 'وَالسَّمَاۤءَ بَنَيْنٰهَا بِاَيْىدٍ وَّاِنَّا لَمُوْسِعُوْنَ', 'was-samā`a banaināhā bi`aidiw wa innā lamụsi\'ụn'),
(4722, 51, 48, 'وَالْاَرْضَ فَرَشْنٰهَا فَنِعْمَ الْمَاهِدُوْنَ', 'wal-arḍa farasynāhā fa ni\'mal-māhidụn'),
(4723, 51, 49, 'وَمِنْ كُلِّ شَيْءٍ خَلَقْنَا زَوْجَيْنِ لَعَلَّكُمْ تَذَكَّرُوْنَ', 'wa ming kulli syai`in khalaqnā zaujaini la\'allakum tażakkarụn'),
(4724, 51, 50, 'فَفِرُّوْٓا اِلَى اللّٰهِ ۗاِنِّيْ لَكُمْ مِّنْهُ نَذِيْرٌ مُّبِيْنٌۚ', 'fa firrū ilallāh, innī lakum min-hu nażīrum mubīn'),
(4725, 51, 51, 'وَلَا تَجْعَلُوْا مَعَ اللّٰهِ اِلٰهًا اٰخَرَۗ اِنِّيْ لَكُمْ مِّنْهُ نَذِيْرٌ مُّبِيْنٌ', 'wa lā taj\'alụ ma\'allāhi ilāhan ākhar, innī lakum min-hu nażīrum mubīn'),
(4726, 51, 52, 'كَذٰلِكَ مَآ اَتَى الَّذِيْنَ مِنْ قَبْلِهِمْ مِّنْ رَّسُوْلٍ اِلَّا قَالُوْا سَاحِرٌ اَوْ مَجْنُوْنٌ', 'każālika mā atallażīna ming qablihim mir rasụlin illā qālụ sāḥirun au majnụn'),
(4727, 51, 53, 'اَتَوَاصَوْا بِهٖۚ بَلْ هُمْ قَوْمٌ طَاغُوْنَۚ', 'a tawāṣau bih, bal hum qaumun ṭāgụn'),
(4728, 51, 54, 'فَتَوَلَّ عَنْهُمْ فَمَآ اَنْتَ بِمَلُوْمٍ', 'fa tawalla \'an-hum fa mā anta bimalụm'),
(4729, 51, 55, 'وَذَكِّرْ فَاِنَّ الذِّكْرٰى تَنْفَعُ الْمُؤْمِنِيْنَ', 'wa żakkir fa innaż-żikrā tanfa\'ul-mu`minīn'),
(4730, 51, 56, 'وَمَا خَلَقْتُ الْجِنَّ وَالْاِنْسَ اِلَّا لِيَعْبُدُوْنِ', 'wa mā khalaqtul-jinna wal-insa illā liya\'budụn'),
(4731, 51, 57, 'مَآ اُرِيْدُ مِنْهُمْ مِّنْ رِّزْقٍ وَّمَآ اُرِيْدُ اَنْ يُّطْعِمُوْنِ', 'mā urīdu min-hum mir rizqiw wa mā urīdu ay yuṭ\'imụn'),
(4732, 51, 58, 'اِنَّ اللّٰهَ هُوَ الرَّزَّاقُ ذُو الْقُوَّةِ الْمَتِيْنُ', 'innallāha huwar-razzāqu żul-quwwatil-matīn'),
(4733, 51, 59, 'فَاِنَّ لِلَّذِيْنَ ظَلَمُوْا ذَنُوْبًا مِّثْلَ ذَنُوْبِ اَصْحٰبِهِمْ فَلَا يَسْتَعْجِلُوْنِ', 'fa inna lillażīna ẓalamụ żanụbam miṡla żanụbi aṣ-ḥābihim fa lā yasta\'jilụn'),
(4734, 51, 60, 'فَوَيْلٌ لِّلَّذِيْنَ كَفَرُوْا مِنْ يَّوْمِهِمُ الَّذِيْ يُوْعَدُوْنَ', 'fa wailul lillażīna kafarụ miy yaumihimullażī yụ\'adụn'),
(4735, 52, 1, 'وَالطُّوْرِۙ', 'waṭ-ṭụr'),
(4736, 52, 2, 'وَكِتٰبٍ مَّسْطُوْرٍۙ', 'wa kitābim masṭụr'),
(4737, 52, 3, 'فِيْ رَقٍّ مَّنْشُوْرٍۙ', 'fī raqqim mansyụr'),
(4738, 52, 4, 'وَّالْبَيْتِ الْمَعْمُوْرِۙ', 'wal-baitil-ma\'mụr'),
(4739, 52, 5, 'وَالسَّقْفِ الْمَرْفُوْعِۙ', 'was-saqfil-marfụ\''),
(4740, 52, 6, 'وَالْبَحْرِ الْمَسْجُوْرِۙ', 'wal-baḥril-masjụr'),
(4741, 52, 7, 'اِنَّ عَذَابَ رَبِّكَ لَوَاقِعٌۙ', 'inna \'ażāba rabbika lawāqi\''),
(4742, 52, 8, 'مَّا لَهٗ مِنْ دَافِعٍۙ', 'mā lahụ min dāfi\''),
(4743, 52, 9, 'يَّوْمَ تَمُوْرُ السَّمَاۤءُ مَوْرًاۙ', 'yauma tamụrus-samā`u maurā'),
(4744, 52, 10, 'وَّتَسِيْرُ الْجِبَالُ سَيْرًاۗ', 'wa tasīrul-jibālu sairā'),
(4745, 52, 11, 'فَوَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَۙ', 'fa wailuy yauma`iżil lil-mukażżibīn'),
(4746, 52, 12, 'الَّذِيْنَ هُمْ فِيْ خَوْضٍ يَّلْعَبُوْنَۘ', 'allażīna hum fī khauḍiy yal\'abụn'),
(4747, 52, 13, 'يَوْمَ يُدَعُّوْنَ اِلٰى نَارِ جَهَنَّمَ دَعًّاۗ', 'yauma yuda\'\'ụna ilā nāri jahannama da\'\'ā'),
(4748, 52, 14, 'هٰذِهِ النَّارُ الَّتِيْ كُنْتُمْ بِهَا تُكَذِّبُوْنَ', 'hāżihin-nārullatī kuntum bihā tukażżibụn'),
(4749, 52, 15, 'اَفَسِحْرٌ هٰذَآ اَمْ اَنْتُمْ لَا تُبْصِرُوْنَ', 'a fa siḥrun hāżā am antum lā tubṣirụn'),
(4750, 52, 16, 'اِصْلَوْهَا فَاصْبِرُوْٓا اَوْ لَا تَصْبِرُوْاۚ سَوَاۤءٌ عَلَيْكُمْۗ اِنَّمَا تُجْزَوْنَ مَا كُنْتُمْ تَعْمَلُوْنَ', 'iṣlauhā faṣbirū au lā taṣbirụ, sawā`un \'alaikum, innamā tujzauna mā kuntum ta\'malụn'),
(4751, 52, 17, 'اِنَّ الْمُتَّقِيْنَ فِيْ جَنّٰتٍ وَّنَعِيْمٍۙ', 'innal-muttaqīna fī jannātiw wa na\'īm'),
(4752, 52, 18, 'فَاكِهِيْنَ بِمَآ اٰتٰىهُمْ رَبُّهُمْۚ وَوَقٰىهُمْ رَبُّهُمْ عَذَابَ الْجَحِيْمِ', 'fākihīna bimā ātāhum rabbuhum, wa waqāhum rabbuhum \'ażābal-jaḥīm'),
(4753, 52, 19, 'كُلُوْا وَاشْرَبُوْا هَنِيْۤئًا ۢبِمَا كُنْتُمْ تَعْمَلُوْنَۙ', 'kulụ wasyrabụ hanī`am bimā kuntum ta\'malụn'),
(4754, 52, 20, 'مُتَّكِـِٕيْنَ عَلٰى سُرُرٍ مَّصْفُوْفَةٍۚ وَزَوَّجْنٰهُمْ بِحُوْرٍ عِيْنٍ', 'muttaki`īna \'alā sururim maṣfụfah, wa zawwajnāhum biḥụrin \'īn'),
(4755, 52, 21, 'وَالَّذِيْنَ اٰمَنُوْا وَاتَّبَعَتْهُمْ ذُرِّيَّتُهُمْ بِاِيْمَانٍ اَلْحَقْنَا بِهِمْ ذُرِّيَّتَهُمْ وَمَآ اَلَتْنٰهُمْ مِّنْ عَمَلِهِمْ مِّنْ شَيْءٍۚ كُلُّ امْرِئٍ ۢبِمَا كَسَبَ رَهِيْنٌ', 'wallażīna āmanụ wattaba\'at-hum żurriyyatuhum bi`īmānin alḥaqnā bihim żurriyyatahum wa mā alatnāhum min \'amalihim min syaī`, kullumri`im bimā kasaba rahīn'),
(4756, 52, 22, 'وَاَمْدَدْنٰهُمْ بِفَاكِهَةٍ وَّلَحْمٍ مِّمَّا يَشْتَهُوْنَ', 'wa amdadnāhum bifākihatiw wa laḥmim mimmā yasytahụn'),
(4757, 52, 23, 'يَتَنَازَعُوْنَ فِيْهَا كَأْسًا لَّا لَغْوٌ فِيْهَا وَلَا تَأْثِيْمٌ', 'yatanāza\'ụna fīhā ka`sal lā lagwun fīhā wa lā ta`ṡīm'),
(4758, 52, 24, 'وَيَطُوْفُ عَلَيْهِمْ غِلْمَانٌ لَّهُمْ كَاَنَّهُمْ لُؤْلُؤٌ مَّكْنُوْنٌۚ', 'wa yaṭụfu \'alaihim gilmānul lahum ka`annahum lu`lu`um maknụn'),
(4759, 52, 25, 'وَاَقْبَلَ بَعْضُهُمْ عَلٰى بَعْضٍ يَّتَسَاۤءَلُوْنَ', 'wa aqbala ba\'ḍuhum \'alā ba\'ḍiy yatasā`alụn'),
(4760, 52, 26, 'قَالُوْٓا اِنَّا كُنَّا قَبْلُ فِيْٓ اَهْلِنَا مُشْفِقِيْنَ', 'qālū innā kunnā qablu fī ahlinā musyfiqīn'),
(4761, 52, 27, 'فَمَنَّ اللّٰهُ عَلَيْنَا وَوَقٰىنَا عَذَابَ السَّمُوْمِ', 'fa mannallāhu \'alainā wa waqānā \'ażābas-samụm'),
(4762, 52, 28, 'اِنَّا كُنَّا مِنْ قَبْلُ نَدْعُوْهُۗ اِنَّهٗ هُوَ الْبَرُّ الرَّحِيْمُ', 'innā kunnā ming qablu nad\'ụh, innahụ huwal-barrur-raḥīm'),
(4763, 52, 29, 'فَذَكِّرْ فَمَآ اَنْتَ بِنِعْمَتِ رَبِّكَ بِكَاهِنٍ وَّلَا مَجْنُوْنٍۗ', 'fa żakkir fa mā anta bini\'mati rabbika bikāhiniw wa lā majnụn'),
(4764, 52, 30, 'اَمْ يَقُوْلُوْنَ شَاعِرٌ نَّتَرَبَّصُ بِهٖ رَيْبَ الْمَنُوْنِ', 'am yaqụlụna syā\'irun natarabbaṣu bihī raibal-manụn'),
(4765, 52, 31, 'قُلْ تَرَبَّصُوْا فَاِنِّيْ مَعَكُمْ مِّنَ الْمُتَرَبِّصِيْنَۗ', 'qul tarabbaṣụ fa innī ma\'akum minal-mutarabbiṣīn'),
(4766, 52, 32, 'اَمْ تَأْمُرُهُمْ اَحْلَامُهُمْ بِهٰذَآ اَمْ هُمْ قَوْمٌ طَاغُوْنَۚ', 'am ta`muruhum aḥlāmuhum bihāżā am hum qaumun ṭāgụn'),
(4767, 52, 33, 'اَمْ يَقُوْلُوْنَ تَقَوَّلَهٗۚ بَلْ لَّا يُؤْمِنُوْنَۚ', 'am yaqụlụna taqawwalah, bal lā yu`minụn'),
(4768, 52, 34, 'فَلْيَأْتُوْا بِحَدِيْثٍ مِّثْلِهٖٓ اِنْ كَانُوْا صٰدِقِيْنَۗ', 'falya`tụ biḥadīṡim miṡlihī ing kānụ ṣādiqīn'),
(4769, 52, 35, 'اَمْ خُلِقُوْا مِنْ غَيْرِ شَيْءٍ اَمْ هُمُ الْخَالِقُوْنَۗ', 'am khuliqụ min gairi syai`in am humul-khāliqụn'),
(4770, 52, 36, 'اَمْ خَلَقُوا السَّمٰوٰتِ وَالْاَرْضَۚ بَلْ لَّا يُوْقِنُوْنَۗ', 'am khalaqus-samāwāti wal-arḍ, bal lā yụqinụn'),
(4771, 52, 37, 'اَمْ عِنْدَهُمْ خَزَاۤىِٕنُ رَبِّكَ اَمْ هُمُ الْمُصَۣيْطِرُوْنَۗ', 'am \'indahum khazā`inu rabbika am humul-muṣaiṭirụn'),
(4772, 52, 38, 'اَمْ لَهُمْ سُلَّمٌ يَّسْتَمِعُوْنَ فِيْهِۚ فَلْيَأْتِ مُسْتَمِعُهُمْ بِسُلْطٰنٍ مُّبِيْنٍۗ', 'am lahum sullamuy yastami\'ụna fīh, falya`ti mustami\'uhum bisulṭānim mubīn'),
(4773, 52, 39, 'اَمْ لَهُ الْبَنٰتُ وَلَكُمُ الْبَنُوْنَۗ', 'am lahul-banātu wa lakumul-banụn'),
(4774, 52, 40, 'اَمْ تَسْـَٔلُهُمْ اَجْرًا فَهُمْ مِّنْ مَّغْرَمٍ مُّثْقَلُوْنَۗ', 'am tas`aluhum ajran fa hum mim magramim muṡqalụn'),
(4775, 52, 41, 'اَمْ عِنْدَهُمُ الْغَيْبُ فَهُمْ يَكْتُبُوْنَۗ', 'am \'indahumul-gaibu fa hum yaktubụn'),
(4776, 52, 42, 'اَمْ يُرِيْدُوْنَ كَيْدًاۗ فَالَّذِيْنَ كَفَرُوْا هُمُ الْمَكِيْدُوْنَۗ', 'am yurīdụna kaidā, fallażīna kafarụ humul-makīdụn'),
(4777, 52, 43, 'اَمْ لَهُمْ اِلٰهٌ غَيْرُ اللّٰهِ ۗسُبْحٰنَ اللّٰهِ عَمَّا يُشْرِكُوْنَ', 'am lahum ilāhun gairullāh, sub-ḥānallāhi \'ammā yusyrikụn'),
(4778, 52, 44, 'وَاِنْ يَّرَوْا كِسْفًا مِّنَ السَّمَاۤءِ سَاقِطًا يَّقُوْلُوْا سَحَابٌ مَّرْكُوْمٌ', 'wa iy yarau kisfam minas-samā`i sāqiṭay yaqụlụ saḥābum markụm'),
(4779, 52, 45, 'فَذَرْهُمْ حَتّٰى يُلٰقُوْا يَوْمَهُمُ الَّذِيْ فِيْهِ يُصْعَقُوْنَۙ', 'fażar-hum ḥattā yulāqụ yaumahumullażī fīhi yuṣ\'aqụn'),
(4780, 52, 46, 'يَوْمَ لَا يُغْنِيْ عَنْهُمْ كَيْدُهُمْ شَيْـًٔا وَّلَا هُمْ يُنْصَرُوْنَۗ', 'yauma lā yugnī \'an-hum kaiduhum syai`aw wa lā hum yunṣarụn'),
(4781, 52, 47, 'وَاِنَّ لِلَّذِيْنَ ظَلَمُوْا عَذَابًا دُوْنَ ذٰلِكَ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ', 'wa inna lillażīna ẓalamụ \'ażāban dụna żālika wa lākinna akṡarahum lā ya\'lamụn'),
(4782, 52, 48, 'وَاصْبِرْ لِحُكْمِ رَبِّكَ فَاِنَّكَ بِاَعْيُنِنَا وَسَبِّحْ بِحَمْدِ رَبِّكَ حِيْنَ تَقُوْمُۙ', 'waṣbir liḥukmi rabbika fa innaka bi`a\'yuninā wa sabbiḥ biḥamdi rabbika ḥīna taqụm'),
(4783, 52, 49, 'وَمِنَ الَّيْلِ فَسَبِّحْهُ وَاِدْبَارَ النُّجُوْمِ', 'wa minal-laili fa sabbiḥ-hu wa idbāran-nujụm'),
(4784, 53, 1, 'وَالنَّجْمِ اِذَا هَوٰىۙ', 'wan-najmi iżā hawā'),
(4785, 53, 2, 'مَا ضَلَّ صَاحِبُكُمْ وَمَا غَوٰىۚ', 'mā ḍalla ṣāḥibukum wa mā gawā'),
(4786, 53, 3, 'وَمَا يَنْطِقُ عَنِ الْهَوٰى', 'wa mā yanṭiqu \'anil-hawā'),
(4787, 53, 4, 'اِنْ هُوَ اِلَّا وَحْيٌ يُّوْحٰىۙ', 'in huwa illā waḥyuy yụḥā'),
(4788, 53, 5, 'عَلَّمَهٗ شَدِيْدُ الْقُوٰىۙ', '\'allamahụ syadīdul-quwā'),
(4789, 53, 6, 'ذُوْ مِرَّةٍۗ فَاسْتَوٰىۙ', 'żụ mirrah, fastawā'),
(4790, 53, 7, 'وَهُوَ بِالْاُفُقِ الْاَعْلٰىۗ', 'wa huwa bil-ufuqil-a\'lā'),
(4791, 53, 8, 'ثُمَّ دَنَا فَتَدَلّٰىۙ', 'ṡumma danā fa tadallā'),
(4792, 53, 9, 'فَكَانَ قَابَ قَوْسَيْنِ اَوْ اَدْنٰىۚ', 'fa kāna qāba qausaini au adnā'),
(4793, 53, 10, 'فَاَوْحٰىٓ اِلٰى عَبْدِهٖ مَآ اَوْحٰىۗ', 'fa auḥā ilā \'abdihī mā auḥā'),
(4794, 53, 11, 'مَا كَذَبَ الْفُؤَادُ مَا رَاٰى', 'mā każabal-fu`ādu mā ra`ā'),
(4795, 53, 12, 'اَفَتُمٰرُوْنَهٗ عَلٰى مَا يَرٰى', 'a fa tumārụnahụ \'alā mā yarā'),
(4796, 53, 13, 'وَلَقَدْ رَاٰهُ نَزْلَةً اُخْرٰىۙ', 'wa laqad ra`āhu nazlatan ukhrā'),
(4797, 53, 14, 'عِنْدَ سِدْرَةِ الْمُنْتَهٰى', '\'inda sidratil-muntahā'),
(4798, 53, 15, 'عِنْدَهَا جَنَّةُ الْمَأْوٰىۗ', '\'indahā jannatul-ma`wā'),
(4799, 53, 16, 'اِذْ يَغْشَى السِّدْرَةَ مَا يَغْشٰىۙ', 'iż yagsyas-sidrata mā yagsyā'),
(4800, 53, 17, 'مَا زَاغَ الْبَصَرُ وَمَا طَغٰى', 'mā zāgal-baṣaru wa mā ṭagā'),
(4801, 53, 18, 'لَقَدْ رَاٰى مِنْ اٰيٰتِ رَبِّهِ الْكُبْرٰى', 'laqad ra`ā min āyāti rabbihil-kubrā'),
(4802, 53, 19, 'اَفَرَءَيْتُمُ اللّٰتَ وَالْعُزّٰى', 'a fa ra`aitumul-lāta wal-\'uzzā'),
(4803, 53, 20, 'وَمَنٰوةَ الثَّالِثَةَ الْاُخْرٰى', 'wa manātaṡ-ṡāliṡatal-ukhrā'),
(4804, 53, 21, 'اَلَكُمُ الذَّكَرُ وَلَهُ الْاُنْثٰى', 'a lakumuż-żakaru wa lahul-unṡā'),
(4805, 53, 22, 'تِلْكَ اِذًا قِسْمَةٌ ضِيْزٰى', 'tilka iżang qismatun ḍīzā'),
(4806, 53, 23, 'اِنْ هِيَ اِلَّآ اَسْمَاۤءٌ سَمَّيْتُمُوْهَآ اَنْتُمْ وَاٰبَاۤؤُكُمْ مَّآ اَنْزَلَ اللّٰهُ بِهَا مِنْ سُلْطٰنٍۗ اِنْ يَّتَّبِعُوْنَ اِلَّا الظَّنَّ وَمَا تَهْوَى الْاَنْفُسُۚ وَلَقَدْ جَاۤءَهُمْ مِّنْ رَّبِّهِمُ الْهُدٰىۗ', 'in hiya illā asmā`un sammaitumụhā antum wa ābā`ukum mā anzalallāhu bihā min sulṭān, iy yattabi\'ụna illaẓ-ẓanna wa mā tahwal-anfus, wa laqad jā`ahum mir rabbihimul-hudā'),
(4807, 53, 24, 'اَمْ لِلْاِنْسَانِ مَا تَمَنّٰىۖ', 'am lil-insāni mā tamannā'),
(4808, 53, 25, 'فَلِلّٰهِ الْاٰخِرَةُ وَالْاُوْلٰى', 'fa lillāhil-ākhiratu wal-ụlā'),
(4809, 53, 26, 'وَكَمْ مِّنْ مَّلَكٍ فِى السَّمٰوٰتِ لَا تُغْنِيْ شَفَاعَتُهُمْ شَيْـًٔا اِلَّا مِنْۢ بَعْدِ اَنْ يَّأْذَنَ اللّٰهُ لِمَنْ يَّشَاۤءُ وَيَرْضٰى', 'wa kam mim malakin fis-samāwāti lā tugnī syafā\'atuhum syai`an illā mim ba\'di ay ya`żanallāhu limay yasyā`u wa yarḍā'),
(4810, 53, 27, 'اِنَّ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ لَيُسَمُّوْنَ الْمَلٰۤىِٕكَةَ تَسْمِيَةَ الْاُنْثٰى', 'innallażīna lā yu`minụna bil-ākhirati layusammụnal-malā`ikata tasmiyatal-unṡā'),
(4811, 53, 28, 'وَمَا لَهُمْ بِهٖ مِنْ عِلْمٍۗ اِنْ يَّتَّبِعُوْنَ اِلَّا الظَّنَّ وَاِنَّ الظَّنَّ لَا يُغْنِيْ مِنَ الْحَقِّ شَيْـًٔاۚ', 'wa mā lahum bihī min \'ilm, iy yattabi\'ụna illaẓ-ẓanna wa innaẓ-ẓanna lā yugnī minal-ḥaqqi syai`ā'),
(4812, 53, 29, 'فَاَعْرِضْ عَنْ مَّنْ تَوَلّٰىۙ عَنْ ذِكْرِنَا وَلَمْ يُرِدْ اِلَّا الْحَيٰوةَ الدُّنْيَاۗ', 'fa a\'riḍ \'am man tawallā \'an żikrinā wa lam yurid illal-ḥayātad-dun-yā'),
(4813, 53, 30, 'ذٰلِكَ مَبْلَغُهُمْ مِّنَ الْعِلْمِۗ اِنَّ رَبَّكَ هُوَ اَعْلَمُ بِمَنْ ضَلَّ عَنْ سَبِيْلِهٖۙ وَهُوَ اَعْلَمُ بِمَنِ اهْتَدٰى', 'żālika mablaguhum minal-\'ilm, inna rabbaka huwa a\'lamu biman ḍalla \'an sabīlihī wa huwa a\'lamu bimanihtadā'),
(4814, 53, 31, 'وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ لِيَجْزِيَ الَّذِيْنَ اَسَاۤءُوْا بِمَا عَمِلُوْا وَيَجْزِيَ الَّذِيْنَ اَحْسَنُوْا بِالْحُسْنٰىۚ', 'wa lillāhi mā fis-samāwāti wa mā fil-arḍ, liyajziyallażīna asā`ụ bimā \'amilụ wa yajziyallażīna aḥsanụ bil-ḥusnā'),
(4815, 53, 32, 'اَلَّذِيْنَ يَجْتَنِبُوْنَ كَبٰۤىِٕرَ الْاِثْمِ وَالْفَوَاحِشَ اِلَّا اللَّمَمَۙ اِنَّ رَبَّكَ وَاسِعُ الْمَغْفِرَةِۗ هُوَ اَعْلَمُ بِكُمْ اِذْ اَنْشَاَكُمْ مِّنَ الْاَرْضِ وَاِذْ اَنْتُمْ اَجِنَّةٌ فِيْ بُطُوْنِ اُمَّهٰتِكُمْۗ فَلَا تُزَكُّوْٓا اَنْفُسَكُمْۗ هُوَ اَعْلَمُ بِمَنِ اتَّقٰى', 'allażīna yajtanibụna kabā`iral-iṡmi wal-fawāḥisya illal-lamama inna rabbaka wāsi\'ul-magfirah, huwa a\'lamu bikum iż ansya`akum minal-arḍi wa iż antum ajinnatun fī buṭụni ummahātikum, fa lā tuzakkū anfusakum, huwa a\'lamu bimanittaqā'),
(4816, 53, 33, 'اَفَرَءَيْتَ الَّذِيْ تَوَلّٰىۙ', 'a fa ra`aitallażī tawallā'),
(4817, 53, 34, 'وَاَعْطٰى قَلِيْلًا وَّاَكْدٰى', 'wa a\'ṭā qalīlaw wa akdā'),
(4818, 53, 35, 'اَعِنْدَهٗ عِلْمُ الْغَيْبِ فَهُوَ يَرٰى', 'a \'indahụ \'ilmul-gaibi fa huwa yarā'),
(4819, 53, 36, 'اَمْ لَمْ يُنَبَّأْ بِمَا فِيْ صُحُفِ مُوْسٰى', 'am lam yunabba` bimā fī ṣuḥufi mụsā'),
(4820, 53, 37, 'وَاِبْرٰهِيْمَ الَّذِيْ وَفّٰىٓ ۙ', 'wa ibrāhīmallażī waffā'),
(4821, 53, 38, 'اَلَّا تَزِرُ وَازِرَةٌ وِّزْرَ اُخْرٰىۙ', 'allā taziru wāziratuw wizra ukhrā'),
(4822, 53, 39, 'وَاَنْ لَّيْسَ لِلْاِنْسَانِ اِلَّا مَا سَعٰىۙ', 'wa al laisa lil-insāni illā mā sa\'ā'),
(4823, 53, 40, 'وَاَنَّ سَعْيَهٗ سَوْفَ يُرٰىۖ', 'wa anna sa\'yahụ saufa yurā'),
(4824, 53, 41, 'ثُمَّ يُجْزٰىهُ الْجَزَاۤءَ الْاَوْفٰىۙ', 'ṡumma yujzāhul-jazā`al-aufā'),
(4825, 53, 42, 'وَاَنَّ اِلٰى رَبِّكَ الْمُنْتَهٰىۙ', 'wa anna ilā rabbikal-muntahā'),
(4826, 53, 43, 'وَاَنَّهٗ هُوَ اَضْحَكَ وَاَبْكٰى', 'wa annahụ huwa aḍ-ḥaka wa abkā'),
(4827, 53, 44, 'وَاَنَّهٗ هُوَ اَمَاتَ وَاَحْيَاۙ', 'wa annahụ huwa amāta wa aḥyā'),
(4828, 53, 45, 'وَاَنَّهٗ خَلَقَ الزَّوْجَيْنِ الذَّكَرَ وَالْاُنْثٰى', 'wa annahụ khalaqaz-zaujainiż-żakara wal-unṡā'),
(4829, 53, 46, 'مِنْ نُّطْفَةٍ اِذَا تُمْنٰىۙ', 'min nuṭfatin iżā tumnā'),
(4830, 53, 47, 'وَاَنَّ عَلَيْهِ النَّشْاَةَ الْاُخْرٰىۙ', 'wa anna \'alaihin-nasy`atal-ukhrā'),
(4831, 53, 48, 'وَاَنَّهٗ هُوَ اَغْنٰى وَاَقْنٰىۙ', 'wa annahụ huwa agnā wa aqnā'),
(4832, 53, 49, 'وَاَنَّهٗ هُوَ رَبُّ الشِّعْرٰىۙ', 'wa annahụ huwa rabbusy-syi\'rā'),
(4833, 53, 50, 'وَاَنَّهٗٓ اَهْلَكَ عَادًا ۨالْاُوْلٰىۙ', 'wa annahū ahlaka \'ādanil-ụlā'),
(4834, 53, 51, 'وَثَمُوْدَا۟ فَمَآ اَبْقٰىۙ', 'wa ṡamụda fa mā abqā'),
(4835, 53, 52, 'وَقَوْمَ نُوْحٍ مِّنْ قَبْلُۗ اِنَّهُمْ كَانُوْا هُمْ اَظْلَمَ وَاَطْغٰىۗ', 'wa qauma nụḥim ming qabl, innahum kānụ hum aẓlama wa aṭgā'),
(4836, 53, 53, 'وَالْمُؤْتَفِكَةَ اَهْوٰىۙ', 'wal-mu`tafikata ahwā'),
(4837, 53, 54, 'فَغَشّٰىهَا مَا غَشّٰىۚ', 'fa gasysyāhā mā gasysyā'),
(4838, 53, 55, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكَ تَتَمَارٰى', 'fa bi`ayyi ālā`i rabbika tatamārā'),
(4839, 53, 56, 'هٰذَا نَذِيْرٌ مِّنَ النُّذُرِ الْاُوْلٰى', 'hāżā nażīrum minan-nużuril-ụlā'),
(4840, 53, 57, 'اَزِفَتِ الْاٰزِفةُ ۚ', 'azifatil-āzifah'),
(4841, 53, 58, 'لَيْسَ لَهَا مِنْ دُوْنِ اللّٰهِ كَاشِفَةٌ ۗ', 'laisa lahā min dụnillāhi kāsyifah');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(4842, 53, 59, 'اَفَمِنْ هٰذَا الْحَدِيْثِ تَعْجَبُوْنَۙ', 'a fa min hāżal-ḥadīṡi ta\'jabụn'),
(4843, 53, 60, 'وَتَضْحَكُوْنَ وَلَا تَبْكُوْنَۙ', 'wa taḍ-ḥakụna wa lā tabkụn'),
(4844, 53, 61, 'وَاَنْتُمْ سَامِدُوْنَ', 'wa antum sāmidụn'),
(4845, 53, 62, 'فَاسْجُدُوْا لِلّٰهِ وَاعْبُدُوْا ۩', 'fasjudụ lillāhi wa\'budụ'),
(4846, 54, 1, 'اِقْتَرَبَتِ السَّاعَةُ وَانْشَقَّ الْقَمَرُ', 'iqtarabatis-sā\'atu wansyaqqal-qamar'),
(4847, 54, 2, 'وَاِنْ يَّرَوْا اٰيَةً يُّعْرِضُوْا وَيَقُوْلُوْا سِحْرٌ مُّسْتَمِرٌّ', 'wa iy yarau āyatay yu\'riḍụ wa yaqụlụ siḥrum mustamirr'),
(4848, 54, 3, 'وَكَذَّبُوْا وَاتَّبَعُوْٓا اَهْوَاۤءَهُمْ وَكُلُّ اَمْرٍ مُّسْتَقِرٌّ', 'wa każżabụ wattaba\'ū ahwā`ahum wa kullu amrim mustaqirr'),
(4849, 54, 4, 'وَلَقَدْ جَاۤءَهُمْ مِّنَ الْاَنْبَاۤءِ مَا فِيْهِ مُزْدَجَرٌۙ', 'wa laqad jā`ahum minal-ambā`i mā fīhi muzdajar'),
(4850, 54, 5, 'حِكْمَةٌ ۢ بَالِغَةٌ فَمَا تُغْنِ النُّذُرُۙ', 'ḥikmatum bāligatun fa mā tugnin-nużur'),
(4851, 54, 6, 'فَتَوَلَّ عَنْهُمْ ۘ يَوْمَ يَدْعُ الدَّاعِ اِلٰى شَيْءٍ نُّكُرٍۙ', 'fa tawalla \'an-hum, yauma yad\'ud-dā\'i ilā syai`in nukur'),
(4852, 54, 7, 'خُشَّعًا اَبْصَارُهُمْ يَخْرُجُوْنَ مِنَ الْاَجْدَاثِ كَاَنَّهُمْ جَرَادٌ مُّنْتَشِرٌۙ', 'khusysya\'an abṣāruhum yakhrujụna minal-ajdāṡi ka`annahum jarādum muntasyir'),
(4853, 54, 8, 'مُّهْطِعِيْنَ اِلَى الدَّاعِۗ يَقُوْلُ الْكٰفِرُوْنَ هٰذَا يَوْمٌ عَسِرٌ', 'muhṭi\'īna ilad-dā\', yaqụlul-kāfirụna hāżā yaumun \'asir'),
(4854, 54, 9, '۞ كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوْحٍ فَكَذَّبُوْا عَبْدَنَا وَقَالُوْا مَجْنُوْنٌ وَّازْدُجِرَ', 'każżabat qablahum qaumu nụḥin fa każżabụ \'abdanā wa qālụ majnụnuw wazdujir'),
(4855, 54, 10, 'فَدَعَا رَبَّهٗٓ اَنِّيْ مَغْلُوْبٌ فَانْتَصِرْ', 'fa da\'ā rabbahū annī maglụbun fantaṣir'),
(4856, 54, 11, 'فَفَتَحْنَآ اَبْوَابَ السَّمَاۤءِ بِمَاۤءٍ مُّنْهَمِرٍۖ', 'fa fataḥnā abwābas-samā`i bimā`im mun-hamir'),
(4857, 54, 12, 'وَّفَجَّرْنَا الْاَرْضَ عُيُوْنًا فَالْتَقَى الْمَاۤءُ عَلٰٓى اَمْرٍ قَدْ قُدِرَ ۚ', 'wa fajjarnal-arḍa \'uyụnan faltaqal-mā`u \'alā amring qad qudir'),
(4858, 54, 13, 'وَحَمَلْنٰهُ عَلٰى ذَاتِ اَلْوَاحٍ وَّدُسُرٍۙ', 'wa ḥamalnāhu \'alā żāti alwāḥiw wa dusur'),
(4859, 54, 14, 'تَجْرِيْ بِاَعْيُنِنَاۚ جَزَاۤءً لِّمَنْ كَانَ كُفِرَ', 'tajrī bi`a\'yuninā, jazā`al limang kāna kufir'),
(4860, 54, 15, 'وَلَقَدْ تَّرَكْنٰهَآ اٰيَةً فَهَلْ مِنْ مُّدَّكِرٍ', 'wa laqat taraknāhā āyatan fa hal mim muddakir'),
(4861, 54, 16, 'فَكَيْفَ كَانَ عَذَابِيْ وَنُذُرِ', 'fa kaifa kāna \'ażābī wa nużur'),
(4862, 54, 17, 'وَلَقَدْ يَسَّرْنَا الْقُرْاٰنَ لِلذِّكْرِ فَهَلْ مِنْ مُّدَّكِرٍ', 'wa laqad yassarnal-qur`āna liż-żikri fa hal mim muddakir'),
(4863, 54, 18, 'كَذَّبَتْ عَادٌ فَكَيْفَ كَانَ عَذَابِيْ وَنُذُرِ', 'każżabat \'ādun fa kaifa kāna \'ażābī wa nużur'),
(4864, 54, 19, 'اِنَّآ اَرْسَلْنَا عَلَيْهِمْ رِيْحًا صَرْصَرًا فِيْ يَوْمِ نَحْسٍ مُّسْتَمِرٍّۙ', 'innā arsalnā \'alaihim rīḥan ṣarṣaran fī yaumi naḥsim mustamirr'),
(4865, 54, 20, 'تَنْزِعُ النَّاسَۙ كَاَنَّهُمْ اَعْجَازُ نَخْلٍ مُّنْقَعِرٍ', 'tanzi\'un-nāsa ka`annahum a\'jāzu nakhlim mungqa\'ir'),
(4866, 54, 21, 'فَكَيْفَ كَانَ عَذَابِيْ وَنُذُرِ', 'fa kaifa kāna \'ażābī wa nużur'),
(4867, 54, 22, 'وَلَقَدْ يَسَّرْنَا الْقُرْاٰنَ لِلذِّكْرِ فَهَلْ مِنْ مُّدَّكِرٍ', 'wa laqad yassarnal-qur`āna liż-żikri fa hal mim muddakir'),
(4868, 54, 23, 'كَذَّبَتْ ثَمُوْدُ بِالنُّذُرِ', 'każżabaṡ ṡamụdu bin-nużur'),
(4869, 54, 24, 'فَقَالُوْٓا اَبَشَرًا مِّنَّا وَاحِدًا نَّتَّبِعُهٗٓ ۙاِنَّآ اِذًا لَّفِيْ ضَلٰلٍ وَّسُعُرٍ', 'fa qālū abasyaram minnā wāḥidan nattabi\'uhū innā iżal lafī ḍalāliw wa su\'ur'),
(4870, 54, 25, 'ءَاُلْقِيَ الذِّكْرُ عَلَيْهِ مِنْۢ بَيْنِنَا بَلْ هُوَ كَذَّابٌ اَشِرٌ', 'a ulqiyaż-żikru \'alaihi mim baininā bal huwa każżābun asyir'),
(4871, 54, 26, 'سَيَعْلَمُوْنَ غَدًا مَّنِ الْكَذَّابُ الْاَشِرُ', 'saya\'lamụna gadam manil-każżābul-asyir'),
(4872, 54, 27, 'اِنَّا مُرْسِلُوا النَّاقَةِ فِتْنَةً لَّهُمْ فَارْتَقِبْهُمْ وَاصْطَبِرْۖ', 'innā mursilun-nāqati fitnatal lahum fartaqib-hum waṣṭabir'),
(4873, 54, 28, 'وَنَبِّئْهُمْ اَنَّ الْمَاۤءَ قِسْمَةٌ ۢ بَيْنَهُمْۚ كُلُّ شِرْبٍ مُّحْتَضَرٌ', 'wa nabbi`hum annal-mā`a qismatum bainahum, kullu syirbim muḥtaḍar'),
(4874, 54, 29, 'فَنَادَوْا صَاحِبَهُمْ فَتَعَاطٰى فَعَقَرَ', 'fa nādau ṣāḥibahum fa ta\'āṭā fa \'aqar'),
(4875, 54, 30, 'فَكَيْفَ كَانَ عَذَابِيْ وَنُذُرِ', 'fa kaifa kāna \'ażābī wa nużur'),
(4876, 54, 31, 'اِنَّآ اَرْسَلْنَا عَلَيْهِمْ صَيْحَةً وَّاحِدَةً فَكَانُوْا كَهَشِيْمِ الْمُحْتَظِرِ', 'innā arsalnā \'alaihim ṣaiḥataw wāḥidatan fa kānụ kahasyīmil-muḥtaẓir'),
(4877, 54, 32, 'وَلَقَدْ يَسَّرْنَا الْقُرْاٰنَ لِلذِّكْرِ فَهَلْ مِنْ مُّدَّكِرٍ', 'wa laqad yassarnal-qur`āna liż-żikri fa hal mim muddakir'),
(4878, 54, 33, 'كَذَّبَتْ قَوْمُ لُوْطٍ ۢبِالنُّذُرِ', 'każżabat qaumu lụṭim bin-nużur'),
(4879, 54, 34, 'اِنَّآ اَرْسَلْنَا عَلَيْهِمْ حَاصِبًا اِلَّآ اٰلَ لُوْطٍ ۗنَجَّيْنٰهُمْ بِسَحَرٍۙ', 'innā arsalnā \'alaihim ḥāṣiban illā āla lụṭ, najjaināhum bisaḥar'),
(4880, 54, 35, 'نِّعْمَةً مِّنْ عِنْدِنَاۗ كَذٰلِكَ نَجْزِيْ مَنْ شَكَرَ', 'ni\'matam min \'indinā, każālika najzī man syakar'),
(4881, 54, 36, 'وَلَقَدْ اَنْذَرَهُمْ بَطْشَتَنَا فَتَمَارَوْا بِالنُّذُرِ', 'wa laqad anżarahum baṭsyatanā fa tamārau bin-nużur'),
(4882, 54, 37, 'وَلَقَدْ رَاوَدُوْهُ عَنْ ضَيْفِهٖ فَطَمَسْنَآ اَعْيُنَهُمْ فَذُوْقُوْا عَذَابِيْ وَنُذُرِ', 'wa laqad rāwadụhu \'an ḍaifihī fa ṭamasnā a\'yunahum fa żụqụ \'ażābī wa nużur'),
(4883, 54, 38, 'وَلَقَدْ صَبَّحَهُمْ بُكْرَةً عَذَابٌ مُّسْتَقِرٌّۚ', 'wa laqad ṣabbaḥahum bukratan \'ażābum mustaqirr'),
(4884, 54, 39, 'فَذُوْقُوْا عَذَابِيْ وَنُذُرِ', 'fa żụqụ \'ażābī wa nużur'),
(4885, 54, 40, 'وَلَقَدْ يَسَّرْنَا الْقُرْاٰنَ لِلذِّكْرِ فَهَلْ مِنْ مُّدَّكِرٍ', 'wa laqad yassarnal-qur`āna liż-żikri fa hal mim muddakir'),
(4886, 54, 41, 'وَلَقَدْ جَاۤءَ اٰلَ فِرْعَوْنَ النُّذُرُۚ', 'wa laqad jā`a āla fir\'aunan-nużur'),
(4887, 54, 42, 'كَذَّبُوْا بِاٰيٰتِنَا كُلِّهَا فَاَخَذْنٰهُمْ اَخْذَ عَزِيْزٍ مُّقْتَدِرٍ', 'każżabụ bi`āyātinā kullihā fa`akhażnāhum akhża \'azīzim muqtadir'),
(4888, 54, 43, 'اَكُفَّارُكُمْ خَيْرٌ مِّنْ اُولٰۤىِٕكُمْ اَمْ لَكُمْ بَرَاۤءَةٌ فِى الزُّبُرِۚ', 'a kuffārukum khairum min ulā`ikum am lakum barā`atun fiz-zubur'),
(4889, 54, 44, 'اَمْ يَقُوْلُوْنَ نَحْنُ جَمِيْعٌ مُّنْتَصِرٌ', 'am yaqụlụna naḥnu jamī\'um muntaṣir'),
(4890, 54, 45, 'سَيُهْزَمُ الْجَمْعُ وَيُوَلُّوْنَ الدُّبُرَ', 'sayuhzamul-jam\'u wa yuwallụnad-dubur'),
(4891, 54, 46, 'بَلِ السَّاعَةُ مَوْعِدُهُمْ وَالسَّاعَةُ اَدْهٰى وَاَمَرُّ', 'balis-sā\'atu mau\'iduhum was-sā\'atu ad-hā wa amarr'),
(4892, 54, 47, 'اِنَّ الْمُجْرِمِيْنَ فِيْ ضَلٰلٍ وَّسُعُرٍۘ', 'innal-mujrimīna fī ḍalāliw wa su\'ur'),
(4893, 54, 48, 'يَوْمَ يُسْحَبُوْنَ فِى النَّارِ عَلٰى وُجُوْهِهِمْۗ ذُوْقُوْا مَسَّ سَقَرَ', 'yauma yus-ḥabụna fin-nāri \'alā wujụhihim, żụqụ massa saqar'),
(4894, 54, 49, 'اِنَّا كُلَّ شَيْءٍ خَلَقْنٰهُ بِقَدَرٍ', 'innā kulla syai`in khalaqnāhu biqadar'),
(4895, 54, 50, 'وَمَآ اَمْرُنَآ اِلَّا وَاحِدَةٌ كَلَمْحٍ ۢبِالْبَصَرِ', 'wa mā amrunā illā wāḥidatung kalam-ḥim bil-baṣar'),
(4896, 54, 51, 'وَلَقَدْ اَهْلَكْنَآ اَشْيَاعَكُمْ فَهَلْ مِنْ مُّدَّكِرٍ', 'wa laqad ahlaknā asy-yā\'akum fa hal mim muddakir'),
(4897, 54, 52, 'وَكُلُّ شَيْءٍ فَعَلُوْهُ فِى الزُّبُرِ', 'wa kullu syai`in fa\'alụhu fiz-zubur'),
(4898, 54, 53, 'وَكُلُّ صَغِيْرٍ وَّكَبِيْرٍ مُّسْتَطَرٌ', 'wa kullu ṣagīriw wa kabīrim mustaṭar'),
(4899, 54, 54, 'اِنَّ الْمُتَّقِيْنَ فِيْ جَنّٰتٍ وَّنَهَرٍۙ', 'innal-muttaqīna fī jannātiw wa nahar'),
(4900, 54, 55, 'فِيْ مَقْعَدِ صِدْقٍ عِنْدَ مَلِيْكٍ مُّقْتَدِرٍ', 'fī maq\'adi ṣidqin \'inda malīkim muqtadir'),
(4901, 55, 1, 'اَلرَّحْمٰنُۙ', 'ar-raḥmān'),
(4902, 55, 2, 'عَلَّمَ الْقُرْاٰنَۗ', '\'allamal-qur`ān'),
(4903, 55, 3, 'خَلَقَ الْاِنْسَانَۙ', 'khalaqal-insān'),
(4904, 55, 4, 'عَلَّمَهُ الْبَيَانَ', '\'allamahul-bayān'),
(4905, 55, 5, 'اَلشَّمْسُ وَالْقَمَرُ بِحُسْبَانٍۙ', 'asy-syamsu wal-qamaru biḥusbān'),
(4906, 55, 6, 'وَّالنَّجْمُ وَالشَّجَرُ يَسْجُدَانِ', 'wan-najmu wasy-syajaru yasjudān'),
(4907, 55, 7, 'وَالسَّمَاۤءَ رَفَعَهَا وَوَضَعَ الْمِيْزَانَۙ', 'was-samā`a rafa\'ahā wa waḍa\'al-mīzān'),
(4908, 55, 8, 'اَلَّا تَطْغَوْا فِى الْمِيْزَانِ', 'allā taṭgau fil-mīzān'),
(4909, 55, 9, 'وَاَقِيْمُوا الْوَزْنَ بِالْقِسْطِ وَلَا تُخْسِرُوا الْمِيْزَانَ', 'wa aqīmul-wazna bil-qisṭi wa lā tukhsirul-mīzān'),
(4910, 55, 10, 'وَالْاَرْضَ وَضَعَهَا لِلْاَنَامِۙ', 'wal-arḍa waḍa\'ahā lil-anām'),
(4911, 55, 11, 'فِيْهَا فَاكِهَةٌ وَّالنَّخْلُ ذَاتُ الْاَكْمَامِۖ', 'fīhā fākihatuw wan-nakhlu żātul-akmām'),
(4912, 55, 12, 'وَالْحَبُّ ذُو الْعَصْفِ وَالرَّيْحَانُۚ', 'wal-ḥabbu żul-\'aṣfi war-raiḥān'),
(4913, 55, 13, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4914, 55, 14, 'خَلَقَ الْاِنْسَانَ مِنْ صَلْصَالٍ كَالْفَخَّارِ', 'khalaqal-insāna min ṣalṣāling kal-fakhkhār'),
(4915, 55, 15, 'وَخَلَقَ الْجَاۤنَّ مِنْ مَّارِجٍ مِّنْ نَّارٍۚ', 'wa khalaqal-jānna mim mārijim min nār'),
(4916, 55, 16, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4917, 55, 17, 'رَبُّ الْمَشْرِقَيْنِ وَرَبُّ الْمَغْرِبَيْنِۚ', 'rabbul-masyriqaini wa rabbul-magribaīn'),
(4918, 55, 18, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4919, 55, 19, 'مَرَجَ الْبَحْرَيْنِ يَلْتَقِيٰنِۙ', 'marajal-baḥraini yaltaqiyān'),
(4920, 55, 20, 'بَيْنَهُمَا بَرْزَخٌ لَّا يَبْغِيٰنِۚ', 'bainahumā barzakhul lā yabgiyān'),
(4921, 55, 21, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4922, 55, 22, 'يَخْرُجُ مِنْهُمَا اللُّؤْلُؤُ وَالْمَرْجَانُۚ', 'yakhruju min-humal-lu`lu`u wal-marjān'),
(4923, 55, 23, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4924, 55, 24, 'وَلَهُ الْجَوَارِ الْمُنْشَاٰتُ فِى الْبَحْرِ كَالْاَعْلَامِۚ', 'wa lahul-jawāril-munsya`ātu fil-baḥri kal-a\'lām'),
(4925, 55, 25, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4926, 55, 26, 'كُلُّ مَنْ عَلَيْهَا فَانٍۖ', 'kullu man \'alaihā fān'),
(4927, 55, 27, 'وَّيَبْقٰى وَجْهُ رَبِّكَ ذُو الْجَلٰلِ وَالْاِكْرَامِۚ', 'wa yabqā waj-hu rabbika żul-jalāli wal-ikrām'),
(4928, 55, 28, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4929, 55, 29, 'يَسْـَٔلُهٗ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِۗ كُلَّ يَوْمٍ هُوَ فِيْ شَأْنٍۚ', 'yas`aluhụ man fis-samāwāti wal-arḍ, kulla yaumin huwa fī sya`n'),
(4930, 55, 30, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4931, 55, 31, 'سَنَفْرُغُ لَكُمْ اَيُّهَ الثَّقَلٰنِۚ', 'sanafrugu lakum ayyuhaṡ-ṡaqalān'),
(4932, 55, 32, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4933, 55, 33, 'يٰمَعْشَرَ الْجِنِّ وَالْاِنْسِ اِنِ اسْتَطَعْتُمْ اَنْ تَنْفُذُوْا مِنْ اَقْطَارِ السَّمٰوٰتِ وَالْاَرْضِ فَانْفُذُوْاۗ لَا تَنْفُذُوْنَ اِلَّا بِسُلْطٰنٍۚ', 'yā ma\'syaral-jinni wal-insi inistaṭa\'tum an tanfużụ min aqṭāris-samāwāti wal-arḍi fanfużụ, lā tanfużụna illā bisulṭān'),
(4934, 55, 34, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4935, 55, 35, 'يُرْسَلُ عَلَيْكُمَا شُوَاظٌ مِّنْ نَّارٍۙ وَّنُحَاسٌ فَلَا تَنْتَصِرَانِۚ', 'yursalu \'alaikumā syuwāẓum min nāriw wa nuḥāsun fa lā tantaṣirān'),
(4936, 55, 36, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4937, 55, 37, 'فَاِذَا انْشَقَّتِ السَّمَاۤءُ فَكَانَتْ وَرْدَةً كَالدِّهَانِۚ', 'fa iżansyaqqatis-samā`u fa kānat wardatang kad-dihān'),
(4938, 55, 38, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4939, 55, 39, 'فَيَوْمَئِذٍ لَّا يُسْـَٔلُ عَنْ ذَنْۢبِهٖٓ اِنْسٌ وَّلَا جَاۤنٌّۚ', 'fa yauma`iżil lā yus`alu \'an żambihī insuw wa lā jānn'),
(4940, 55, 40, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4941, 55, 41, 'يُعْرَفُ الْمُجْرِمُوْنَ بِسِيْمٰهُمْ فَيُؤْخَذُ بِالنَّوَاصِيْ وَالْاَقْدَامِۚ', 'yu\'raful-mujrimụna bisīmāhum fa yu`khażu bin-nawāṣī wal-aqdām'),
(4942, 55, 42, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4943, 55, 43, 'هٰذِهٖ جَهَنَّمُ الَّتِيْ يُكَذِّبُ بِهَا الْمُجْرِمُوْنَۘ', 'hāżihī jahannamullatī yukażżibu bihal-mujrimụn'),
(4944, 55, 44, 'يَطُوْفُوْنَ بَيْنَهَا وَبَيْنَ حَمِيْمٍ اٰنٍۚ', 'yaṭụfụna bainahā wa baina ḥamīmin ān'),
(4945, 55, 45, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4946, 55, 46, 'وَلِمَنْ خَافَ مَقَامَ رَبِّهٖ جَنَّتٰنِۚ', 'wa liman khāfa maqāma rabbihī jannatān'),
(4947, 55, 47, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۙ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4948, 55, 48, 'ذَوَاتَآ اَفْنَانٍۚ', 'żawātā afnān'),
(4949, 55, 49, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4950, 55, 50, 'فِيْهِمَا عَيْنٰنِ تَجْرِيٰنِۚ', 'fīhimā \'aināni tajriyān'),
(4951, 55, 51, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4952, 55, 52, 'فِيْهِمَا مِنْ كُلِّ فَاكِهَةٍ زَوْجٰنِۚ', 'fīhimā ming kulli fākihatin zaujān'),
(4953, 55, 53, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4954, 55, 54, 'مُتَّكِـِٕيْنَ عَلٰى فُرُشٍۢ بَطَاۤىِٕنُهَا مِنْ اِسْتَبْرَقٍۗ وَجَنَا الْجَنَّتَيْنِ دَانٍۚ', 'muttaki`īna \'alā furusyim baṭā`inuhā min istabraq, wa janal-jannataini dān'),
(4955, 55, 55, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4956, 55, 56, 'فِيْهِنَّ قٰصِرٰتُ الطَّرْفِۙ لَمْ يَطْمِثْهُنَّ اِنْسٌ قَبْلَهُمْ وَلَا جَاۤنٌّۚ', 'fīhinna qāṣirātuṭ-ṭarfi lam yaṭmiṡ-hunna insung qablahum wa lā jānn'),
(4957, 55, 57, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4958, 55, 58, 'كَاَنَّهُنَّ الْيَاقُوْتُ وَالْمَرْجَانُۚ', 'ka`annahunnal-yāqụtu wal-marjān'),
(4959, 55, 59, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4960, 55, 60, 'هَلْ جَزَاۤءُ الْاِحْسَانِ اِلَّا الْاِحْسَانُۚ', 'hal jazā`ul-iḥsāni illal-iḥsān'),
(4961, 55, 61, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4962, 55, 62, 'وَمِنْ دُوْنِهِمَا جَنَّتٰنِۚ', 'wa min dụnihimā jannatān'),
(4963, 55, 63, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۙ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4964, 55, 64, 'مُدْهَاۤمَّتٰنِۚ', 'mud-hāmmatān'),
(4965, 55, 65, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4966, 55, 66, 'فِيْهِمَا عَيْنٰنِ نَضَّاخَتٰنِۚ', 'fīhimā \'aināni naḍḍākhatān'),
(4967, 55, 67, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4968, 55, 68, 'فِيْهِمَا فَاكِهَةٌ وَّنَخْلٌ وَّرُمَّانٌۚ', 'fīhimā fākihatuw wa nakhluw wa rummān'),
(4969, 55, 69, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4970, 55, 70, 'فِيْهِنَّ خَيْرٰتٌ حِسَانٌۚ', 'fīhinna khairātun ḥisān'),
(4971, 55, 71, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4972, 55, 72, 'حُوْرٌ مَّقْصُوْرٰتٌ فِى الْخِيَامِۚ', 'ḥụrum maqṣụrātun fil-khiyām'),
(4973, 55, 73, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4974, 55, 74, 'لَمْ يَطْمِثْهُنَّ اِنْسٌ قَبْلَهُمْ وَلَا جَاۤنٌّۚ', 'lam yaṭmiṡ-hunna insung qablahum wa lā jānn'),
(4975, 55, 75, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4976, 55, 76, 'مُتَّكِـِٕيْنَ عَلٰى رَفْرَفٍ خُضْرٍ وَّعَبْقَرِيٍّ حِسَانٍۚ', 'muttaki`īna \'alā rafrafin khuḍriw wa \'abqariyyin ḥisān'),
(4977, 55, 77, 'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ', 'fa bi`ayyi ālā`i rabbikumā tukażżibān'),
(4978, 55, 78, 'تَبٰرَكَ اسْمُ رَبِّكَ ذِى الْجَلٰلِ وَالْاِكْرَامِ', 'tabārakasmu rabbika żil-jalāli wal-ikrām'),
(4979, 56, 1, 'اِذَا وَقَعَتِ الْوَاقِعَةُۙ', 'iżā waqa\'atil-wāqi\'ah'),
(4980, 56, 2, 'لَيْسَ لِوَقْعَتِهَا كَاذِبَةٌ ۘ', 'laisa liwaq\'atihā kāżibah'),
(4981, 56, 3, 'خَافِضَةٌ رَّافِعَةٌ', 'khāfiḍatur rāfi\'ah'),
(4982, 56, 4, 'اِذَا رُجَّتِ الْاَرْضُ رَجًّاۙ', 'iżā rujjatil-arḍu rajjā'),
(4983, 56, 5, 'وَّبُسَّتِ الْجِبَالُ بَسًّاۙ', 'wa bussatil-jibālu bassā'),
(4984, 56, 6, 'فَكَانَتْ هَبَاۤءً مُّنْۢبَثًّاۙ', 'fa kānat habā`am mumbaṡṡā'),
(4985, 56, 7, 'وَّكُنْتُمْ اَزْوَاجًا ثَلٰثَةً ۗ', 'wa kuntum azwājan ṡalāṡah'),
(4986, 56, 8, 'فَاَصْحٰبُ الْمَيْمَنَةِ ەۙ مَآ اَصْحٰبُ الْمَيْمَنَةِ ۗ', 'fa aṣ-ḥābul-maimanati mā aṣ-ḥābul-maimanah'),
(4987, 56, 9, 'وَاَصْحٰبُ الْمَشْـَٔمَةِ ەۙ مَآ اَصْحٰبُ الْمَشْـَٔمَةِ ۗ', 'wa aṣ-ḥābul-masy`amati mā aṣ-ḥābul-masy`amah'),
(4988, 56, 10, 'وَالسّٰبِقُوْنَ السّٰبِقُوْنَۙ', 'was-sābiqụnas-sābiqụn'),
(4989, 56, 11, 'اُولٰۤىِٕكَ الْمُقَرَّبُوْنَۚ', 'ulā`ikal-muqarrabụn'),
(4990, 56, 12, 'فِيْ جَنّٰتِ النَّعِيْمِ', 'fī jannātin-na\'īm'),
(4991, 56, 13, 'ثُلَّةٌ مِّنَ الْاَوَّلِيْنَۙ', 'ṡullatum minal-awwalīn'),
(4992, 56, 14, 'وَقَلِيْلٌ مِّنَ الْاٰخِرِيْنَۗ', 'wa qalīlum minal-ākhirīn'),
(4993, 56, 15, 'عَلٰى سُرُرٍ مَّوْضُوْنَةٍۙ', '\'alā sururim mauḍụnah'),
(4994, 56, 16, 'مُّتَّكِـِٕيْنَ عَلَيْهَا مُتَقٰبِلِيْنَ', 'muttaki`īna \'alaihā mutaqābilīn'),
(4995, 56, 17, 'يَطُوْفُ عَلَيْهِمْ وِلْدَانٌ مُّخَلَّدُوْنَۙ', 'yaṭụfu \'alaihim wildānum mukhalladụn'),
(4996, 56, 18, 'بِاَكْوَابٍ وَّاَبَارِيْقَۙ وَكَأْسٍ مِّنْ مَّعِيْنٍۙ', 'bi`akwābiw wa abārīqa wa ka`sim mim ma\'īn'),
(4997, 56, 19, 'لَّا يُصَدَّعُوْنَ عَنْهَا وَلَا يُنْزِفُوْنَۙ', 'lā yuṣadda\'ụna \'an-hā wa lā yunzifụn'),
(4998, 56, 20, 'وَفَاكِهَةٍ مِّمَّا يَتَخَيَّرُوْنَۙ', 'wa fākihatim mimmā yatakhayyarụn'),
(4999, 56, 21, 'وَلَحْمِ طَيْرٍ مِّمَّا يَشْتَهُوْنَۗ', 'wa laḥmi ṭairim mimmā yasytahụn'),
(5000, 56, 22, 'وَحُوْرٌ عِيْنٌۙ', 'wa ḥụrun \'īn'),
(5001, 56, 23, 'كَاَمْثَالِ اللُّؤْلُؤِ الْمَكْنُوْنِۚ', 'ka`amṡālil-lu`lu`il-maknụn'),
(5002, 56, 24, 'جَزَاۤءًۢ بِمَا كَانُوْا يَعْمَلُوْنَ', 'jazā`am bimā kānụ ya\'malụn'),
(5003, 56, 25, 'لَا يَسْمَعُوْنَ فِيْهَا لَغْوًا وَّلَا تَأْثِيْمًاۙ', 'lā yasma\'ụna fīhā lagwaw wa lā ta`ṡīmā'),
(5004, 56, 26, 'اِلَّا قِيْلًا سَلٰمًا سَلٰمًا', 'illā qīlan salāman salāmā'),
(5005, 56, 27, 'وَاَصْحٰبُ الْيَمِينِ ەۙ مَآ اَصْحٰبُ الْيَمِيْنِۗ', 'wa aṣ-ḥābul-yamīni mā aṣ-ḥābul-yamīn'),
(5006, 56, 28, 'فِيْ سِدْرٍ مَّخْضُوْدٍۙ', 'fī sidrim makhḍụd'),
(5007, 56, 29, 'وَّطَلْحٍ مَّنْضُوْدٍۙ', 'wa ṭal-ḥim manḍụd'),
(5008, 56, 30, 'وَّظِلٍّ مَّمْدُوْدٍۙ', 'wa ẓillim mamdụd'),
(5009, 56, 31, 'وَّمَاۤءٍ مَّسْكُوْبٍۙ', 'wa mā`im maskụb'),
(5010, 56, 32, 'وَّفَاكِهَةٍ كَثِيْرَةٍۙ', 'wa fākihating kaṡīrah'),
(5011, 56, 33, 'لَّا مَقْطُوْعَةٍ وَّلَا مَمْنُوْعَةٍۙ', 'lā maqṭụ\'atiw wa lā mamnụ\'ah'),
(5012, 56, 34, 'وَّفُرُشٍ مَّرْفُوْعَةٍۗ', 'wa furusyim marfụ\'ah'),
(5013, 56, 35, 'اِنَّآ اَنْشَأْنٰهُنَّ اِنْشَاۤءًۙ', 'innā ansya`nāhunna insyā`ā'),
(5014, 56, 36, 'فَجَعَلْنٰهُنَّ اَبْكَارًاۙ', 'fa ja\'alnāhunna abkārā'),
(5015, 56, 37, 'عُرُبًا اَتْرَابًاۙ', '\'uruban atrābā'),
(5016, 56, 38, 'لِّاَصْحٰبِ الْيَمِيْنِۗ', 'li`aṣ-ḥābil-yamīn'),
(5017, 56, 39, 'ثُلَّةٌ مِّنَ الْاَوَّلِيْنَۙ', 'ṡullatum minal-awwalīn'),
(5018, 56, 40, 'وَثُلَّةٌ مِّنَ الْاٰخِرِيْنَۗ', 'wa ṡullatum minal-ākhirīn'),
(5019, 56, 41, 'وَاَصْحٰبُ الشِّمَالِ ەۙ مَآ اَصْحٰبُ الشِّمَالِۗ', 'wa aṣ-ḥābusy-syimāli mā aṣ-ḥābusy-syimāl'),
(5020, 56, 42, 'فِيْ سَمُوْمٍ وَّحَمِيْمٍۙ', 'fī samụmiw wa ḥamīm'),
(5021, 56, 43, 'وَّظِلٍّ مِّنْ يَّحْمُوْمٍۙ', 'wa ẓillim miy yaḥmụm'),
(5022, 56, 44, 'لَّا بَارِدٍ وَّلَا كَرِيْمٍ', 'lā bāridiw wa lā karīm'),
(5023, 56, 45, 'اِنَّهُمْ كَانُوْا قَبْلَ ذٰلِكَ مُتْرَفِيْنَۚ', 'innahum kānụ qabla żālika mutrafīn'),
(5024, 56, 46, 'وَكَانُوْا يُصِرُّوْنَ عَلَى الْحِنْثِ الْعَظِيْمِۚ', 'wa kānụ yuṣirrụna \'alal-ḥinṡil-\'aẓīm'),
(5025, 56, 47, 'وَكَانُوْا يَقُوْلُوْنَ ەۙ اَىِٕذَا مِتْنَا وَكُنَّا تُرَابًا وَّعِظَامًا ءَاِنَّا لَمَبْعُوْثُوْنَۙ', 'wa kānụ yaqụlụna a iżā mitnā wa kunnā turābaw wa \'iẓāman a innā lamab\'ụṡụn'),
(5026, 56, 48, 'اَوَاٰبَاۤؤُنَا الْاَوَّلُوْنَ', 'a wa ābā`unal-awwalụn'),
(5027, 56, 49, 'قُلْ اِنَّ الْاَوَّلِيْنَ وَالْاٰخِرِيْنَۙ', 'qul innal-awwalīna wal-ākhirīn'),
(5028, 56, 50, 'لَمَجْمُوْعُوْنَۙ اِلٰى مِيْقَاتِ يَوْمٍ مَّعْلُوْمٍ', 'lamajmụ\'ụna ilā mīqāti yaumim ma\'lụm'),
(5029, 56, 51, 'ثُمَّ اِنَّكُمْ اَيُّهَا الضَّاۤ لُّوْنَ الْمُكَذِّبُوْنَۙ', 'ṡumma innakum ayyuhaḍ-ḍāllụnal-mukażżibụn'),
(5030, 56, 52, 'لَاٰكِلُوْنَ مِنْ شَجَرٍ مِّنْ زَقُّوْمٍۙ', 'la`ākilụna min syajarim min zaqqụm'),
(5031, 56, 53, 'فَمَالِـُٔوْنَ مِنْهَا الْبُطُوْنَۚ', 'fa māli`ụna min-hal-buṭụn'),
(5032, 56, 54, 'فَشَارِبُوْنَ عَلَيْهِ مِنَ الْحَمِيْمِۚ', 'fa syāribụna \'alaihi minal-ḥamīm'),
(5033, 56, 55, 'فَشَارِبُوْنَ شُرْبَ الْهِيْمِۗ', 'fa syāribụna syurbal-hīm'),
(5034, 56, 56, 'هٰذَا نُزُلُهُمْ يَوْمَ الدِّيْنِۗ', 'hāżā nuzuluhum yaumad-dīn'),
(5035, 56, 57, 'نَحْنُ خَلَقْنٰكُمْ فَلَوْلَا تُصَدِّقُوْنَ', 'naḥnu khalaqnākum falau lā tuṣaddiqụn'),
(5036, 56, 58, 'اَفَرَءَيْتُمْ مَّا تُمْنُوْنَۗ', 'a fa ra`aitum mā tumnụn'),
(5037, 56, 59, 'ءَاَنْتُمْ تَخْلُقُوْنَهٗٓ اَمْ نَحْنُ الْخَالِقُوْنَ', 'a antum takhluqụnahū am naḥnul-khāliqụn'),
(5038, 56, 60, 'نَحْنُ قَدَّرْنَا بَيْنَكُمُ الْمَوْتَ وَمَا نَحْنُ بِمَسْبُوْقِيْنَۙ', 'naḥnu qaddarnā bainakumul-mauta wa mā naḥnu bimasbụqīn'),
(5039, 56, 61, 'عَلٰٓى اَنْ نُّبَدِّلَ اَمْثَالَكُمْ وَنُنْشِئَكُمْ فِيْ مَا لَا تَعْلَمُوْنَ', '\'alā an nubaddila amṡālakum wa nunsyi`akum fī mā lā ta\'lamụn'),
(5040, 56, 62, 'وَلَقَدْ عَلِمْتُمُ النَّشْاَةَ الْاُوْلٰى فَلَوْلَا تَذَكَّرُوْنَ', 'wa laqad \'alimtumun-nasy`atal-ụlā falau lā tażakkarụn'),
(5041, 56, 63, 'اَفَرَءَيْتُمْ مَّا تَحْرُثُوْنَۗ', 'a fa ra`aitum mā taḥruṡụn'),
(5042, 56, 64, 'ءَاَنْتُمْ تَزْرَعُوْنَهٗٓ اَمْ نَحْنُ الزَّارِعُوْنَ', 'a antum tazra\'ụnahū am naḥnuz-zāri\'ụn'),
(5043, 56, 65, 'لَوْ نَشَاۤءُ لَجَعَلْنٰهُ حُطَامًا فَظَلْتُمْ تَفَكَّهُوْنَۙ', 'lau nasyā`u laja\'alnāhu huṭāman fa ẓaltum tafakkahụn'),
(5044, 56, 66, 'اِنَّا لَمُغْرَمُوْنَۙ', 'innā lamugramụn'),
(5045, 56, 67, 'بَلْ نَحْنُ مَحْرُوْمُوْنَ', 'bal naḥnu mahrụmụn'),
(5046, 56, 68, 'اَفَرَءَيْتُمُ الْمَاۤءَ الَّذِيْ تَشْرَبُوْنَۗ', 'a fa ra`aitumul-mā`allażī tasyrabụn'),
(5047, 56, 69, 'ءَاَنْتُمْ اَنْزَلْتُمُوْهُ مِنَ الْمُزْنِ اَمْ نَحْنُ الْمُنْزِلُوْنَ', 'a antum anzaltumụhu minal-muzni am naḥnul-munzilụn'),
(5048, 56, 70, 'لَوْ نَشَاۤءُ جَعَلْنٰهُ اُجَاجًا فَلَوْلَا تَشْكُرُوْنَ', 'lau nasyā`u ja\'alnāhu ujājan falau lā tasykurụn'),
(5049, 56, 71, 'اَفَرَءَيْتُمُ النَّارَ الَّتِيْ تُوْرُوْنَۗ', 'a fa ra`aitumun-nārallatī tụrụn'),
(5050, 56, 72, 'ءَاَنْتُمْ اَنْشَأْتُمْ شَجَرَتَهَآ اَمْ نَحْنُ الْمُنْشِـُٔوْنَ', 'a antum ansya`tum syajaratahā am naḥnul-munsyi`ụn'),
(5051, 56, 73, 'نَحْنُ جَعَلْنٰهَا تَذْكِرَةً وَّمَتَاعًا لِّلْمُقْوِيْنَۚ', 'naḥnu ja\'alnāhā tażkirataw wa matā\'al lil-muqwīn'),
(5052, 56, 74, 'فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيْمِ', 'fa sabbiḥ bismi rabbikal-\'aẓīm'),
(5053, 56, 75, 'فَلَآ اُقْسِمُ بِمَوٰقِعِ النُّجُوْمِ', 'fa lā uqsimu bimawāqi\'in-nujụm'),
(5054, 56, 76, 'وَاِنَّهٗ لَقَسَمٌ لَّوْ تَعْلَمُوْنَ عَظِيْمٌۙ', 'wa innahụ laqasamul lau ta\'lamụna \'aẓīm'),
(5055, 56, 77, 'اِنَّهٗ لَقُرْاٰنٌ كَرِيْمٌۙ', 'innahụ laqur`ānung karīm'),
(5056, 56, 78, 'فِيْ كِتٰبٍ مَّكْنُوْنٍۙ', 'fī kitābim maknụn'),
(5057, 56, 79, 'لَّا يَمَسُّهٗٓ اِلَّا الْمُطَهَّرُوْنَۙ', 'lā yamassuhū illal-muṭahharụn'),
(5058, 56, 80, 'تَنْزِيْلٌ مِّنْ رَّبِّ الْعٰلَمِيْنَ', 'tanzīlum mir rabbil-\'ālamīn'),
(5059, 56, 81, 'اَفَبِهٰذَا الْحَدِيْثِ اَنْتُمْ مُّدْهِنُوْنَ', 'a fa bihāżal-ḥadīṡi antum mud-hinụn'),
(5060, 56, 82, 'وَتَجْعَلُوْنَ رِزْقَكُمْ اَنَّكُمْ تُكَذِّبُوْنَ', 'wa taj\'alụna rizqakum annakum tukażżibụn'),
(5061, 56, 83, 'فَلَوْلَآ اِذَا بَلَغَتِ الْحُلْقُوْمَۙ', 'falau lā iżā balagatil-ḥulqụm'),
(5062, 56, 84, 'وَاَنْتُمْ حِيْنَىِٕذٍ تَنْظُرُوْنَۙ', 'wa antum ḥīna`iżin tanẓurụn'),
(5063, 56, 85, 'وَنَحْنُ اَقْرَبُ اِلَيْهِ مِنْكُمْ وَلٰكِنْ لَّا تُبْصِرُوْنَ', 'wa naḥnu aqrabu ilaihi mingkum wa lākil lā tubṣirụn'),
(5064, 56, 86, 'فَلَوْلَآ اِنْ كُنْتُمْ غَيْرَ مَدِيْنِيْنَۙ', 'falau lā ing kuntum gaira madīnīn'),
(5065, 56, 87, 'تَرْجِعُوْنَهَآ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'tarji\'ụnahā ing kuntum ṣādiqīn'),
(5066, 56, 88, 'فَاَمَّآ اِنْ كَانَ مِنَ الْمُقَرَّبِيْنَۙ', 'fa ammā ing kāna minal-muqarrabīn'),
(5067, 56, 89, 'فَرَوْحٌ وَّرَيْحَانٌ ەۙ وَّجَنَّتُ نَعِيْمٍ', 'fa rauḥuw wa raiḥānuw wa jannatu na\'īm'),
(5068, 56, 90, 'وَاَمَّآ اِنْ كَانَ مِنْ اَصْحٰبِ الْيَمِيْنِۙ', 'wa ammā ing kāna min aṣ-ḥābil-yamīn'),
(5069, 56, 91, 'فَسَلٰمٌ لَّكَ مِنْ اَصْحٰبِ الْيَمِيْنِۗ', 'fa salāmul laka min aṣ-ḥābil-yamīn'),
(5070, 56, 92, 'وَاَمَّآ اِنْ كَانَ مِنَ الْمُكَذِّبِيْنَ الضَّاۤلِّيْنَۙ', 'wa ammā ing kāna minal-mukażżibīnaḍ-ḍāllīn'),
(5071, 56, 93, 'فَنُزُلٌ مِّنْ حَمِيْمٍۙ', 'fa nuzulum min ḥamīm'),
(5072, 56, 94, 'وَّتَصْلِيَةُ جَحِيْمٍ', 'wa taṣliyatu jaḥīm'),
(5073, 56, 95, 'اِنَّ هٰذَا لَهُوَ حَقُّ الْيَقِيْنِۚ', 'inna hāżā lahuwa ḥaqqul-yaqīn'),
(5074, 56, 96, 'فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيْمِ', 'fa sabbiḥ bismi rabbikal-\'aẓīm'),
(5075, 57, 1, 'سَبَّحَ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۚ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ', 'sabbaḥa lillāhi mā fis-samāwāti wal-arḍ, wa huwal-\'azīzul-ḥakīm'),
(5076, 57, 2, 'لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۚ يُحْيٖ وَيُمِيْتُۚ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'lahụ mulkus-samāwāti wal-arḍ, yuḥyī wa yumīt, wa huwa \'alā kulli syai`ing qadīr'),
(5077, 57, 3, 'هُوَ الْاَوَّلُ وَالْاٰخِرُ وَالظَّاهِرُ وَالْبَاطِنُۚ وَهُوَ بِكُلِّ شَيْءٍ عَلِيْمٌ', 'huwal-awwalu wal-ākhiru waẓ-ẓāhiru wal-bāṭin, wa huwa bikulli syai`in \'alīm'),
(5078, 57, 4, 'هُوَ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ فِيْ سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِۚ يَعْلَمُ مَا يَلِجُ فِى الْاَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنْزِلُ مِنَ السَّمَاۤءِ وَمَا يَعْرُجُ فِيْهَاۗ وَهُوَ مَعَكُمْ اَيْنَ مَا كُنْتُمْۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرٌۗ', 'huwallażī khalaqas-samāwāti wal-arḍa fī sittati ayyāmin ṡummastawā \'alal-\'arsy, ya\'lamu mā yaliju fil-arḍi wa mā yakhruju min-hā wa mā yanzilu minas-samā`i wa mā ya\'ruju fīhā, wa huwa ma\'akum aina mā kuntum, wallāhu bimā ta\'malụna baṣīr'),
(5079, 57, 5, 'لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ', 'lahụ mulkus-samāwāti wal-arḍ, wa ilallāhi turja\'ul-umụr'),
(5080, 57, 6, 'يُوْلِجُ الَّيْلَ فِى النَّهَارِ وَيُوْلِجُ النَّهَارَ فِى الَّيْلِۗ وَهُوَ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ', 'yụlijul-laila fin-nahāri wa yụlijun-nahāra fil-laīl, wa huwa \'alīmum biżātiṣ-ṣudụr'),
(5081, 57, 7, 'اٰمِنُوْا بِاللّٰهِ وَرَسُوْلِهٖ وَاَنْفِقُوْا مِمَّا جَعَلَكُمْ مُّسْتَخْلَفِيْنَ فِيْهِۗ فَالَّذِيْنَ اٰمَنُوْا مِنْكُمْ وَاَنْفَقُوْا لَهُمْ اَجْرٌ كَبِيْرٌ', 'āminụ billāhi wa rasụlihī wa anfiqụ mimmā ja\'alakum mustakhlafīna fīh, fallażīna āmanụ mingkum wa anfaqụ lahum ajrung kabīr'),
(5082, 57, 8, 'وَمَا لَكُمْ لَا تُؤْمِنُوْنَ بِاللّٰهِ ۚوَالرَّسُوْلُ يَدْعُوْكُمْ لِتُؤْمِنُوْا بِرَبِّكُمْ وَقَدْ اَخَذَ مِيْثَاقَكُمْ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ', 'wa mā lakum lā tu`minụna billāh, war-rasụlu yad\'ụkum litu`minụ birabbikum wa qad akhaża mīṡāqakum ing kuntum mu`minīn'),
(5083, 57, 9, 'هُوَ الَّذِيْ يُنَزِّلُ عَلٰى عَبْدِهٖٓ اٰيٰتٍۢ بَيِّنٰتٍ لِّيُخْرِجَكُمْ مِّنَ الظُّلُمٰتِ اِلَى النُّوْرِۗ وَاِنَّ اللّٰهَ بِكُمْ لَرَءُوْفٌ رَّحِيْمٌ', 'huwallażī yunazzilu \'alā \'abdihī āyātim bayyinātil liyukhrijakum minaẓ-ẓulumāti ilan-nụr, wa innallāha bikum lara`ụfur raḥīm'),
(5084, 57, 10, 'وَمَا لَكُمْ اَلَّا تُنْفِقُوْا فِيْ سَبِيْلِ اللّٰهِ وَلِلّٰهِ مِيْرَاثُ السَّمٰوٰتِ وَالْاَرْضِۗ لَا يَسْتَوِيْ مِنْكُمْ مَّنْ اَنْفَقَ مِنْ قَبْلِ الْفَتْحِ وَقَاتَلَۗ اُولٰۤىِٕكَ اَعْظَمُ دَرَجَةً مِّنَ الَّذِيْنَ اَنْفَقُوْا مِنْۢ بَعْدُ وَقَاتَلُوْاۗ وَكُلًّا وَّعَدَ اللّٰهُ الْحُسْنٰىۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ', 'wa mā lakum allā tunfiqụ fī sabīlillāhi wa lillāhi mīrāṡus-samāwāti wal-arḍ, lā yastawī mingkum man anfaqa ming qablil-fat-ḥi wa qātal, ulā`ika a\'ẓamu darajatam minallażīna anfaqụ mim ba\'du wa qātalụ, wa kullaw wa\'adallāhul-ḥusnā, wallāhu bimā ta\'malụna khabīr'),
(5085, 57, 11, 'مَنْ ذَا الَّذِيْ يُقْرِضُ اللّٰهَ قَرْضًا حَسَنًا فَيُضٰعِفَهٗ لَهٗ وَلَهٗٓ اَجْرٌ كَرِيْمٌ', 'man żallażī yuqriḍullāha qarḍan ḥasanan fa yuḍā\'ifahụ lahụ wa lahū ajrung karīm'),
(5086, 57, 12, 'يَوْمَ تَرَى الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ يَسْعٰى نُوْرُهُمْ بَيْنَ اَيْدِيْهِمْ وَبِاَيْمَانِهِمْ بُشْرٰىكُمُ الْيَوْمَ جَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَاۗ ذٰلِكَ هُوَ الْفَوْزُ الْعَظِيْمُۚ', 'yauma taral-mu`minīna wal-mu`mināti yas\'ā nụruhum baina aidīhim wa bi`aimānihim busyrākumul-yauma jannātun tajrī min taḥtihal-an-hāru khālidīna fīhā, żālika huwal-fauzul-\'aẓīm'),
(5087, 57, 13, 'يَوْمَ يَقُوْلُ الْمُنٰفِقُوْنَ وَالْمُنٰفِقٰتُ لِلَّذِيْنَ اٰمَنُوا انْظُرُوْنَا نَقْتَبِسْ مِنْ نُّوْرِكُمْۚ قِيْلَ ارْجِعُوْا وَرَاۤءَكُمْ فَالْتَمِسُوْا نُوْرًاۗ فَضُرِبَ بَيْنَهُمْ بِسُوْرٍ لَّهٗ بَابٌۗ بَاطِنُهٗ فِيْهِ الرَّحْمَةُ وَظَاهِرُهٗ مِنْ قِبَلِهِ الْعَذَابُۗ', 'yauma yaqụlul-munāfiqụna wal-munāfiqātu lillażīna āmanunẓurụnā naqtabis min nụrikum, qīlarji\'ụ warā`akum faltamisụ nụrā, fa ḍuriba bainahum bisụril lahụ bāb, bāṭinuhụ fīhir-raḥmatu wa ẓāhiruhụ ming qibalihil-\'ażāb'),
(5088, 57, 14, 'يُنَادُوْنَهُمْ اَلَمْ نَكُنْ مَّعَكُمْۗ قَالُوْا بَلٰى وَلٰكِنَّكُمْ فَتَنْتُمْ اَنْفُسَكُمْ وَتَرَبَّصْتُمْ وَارْتَبْتُمْ وَغَرَّتْكُمُ الْاَمَانِيُّ حَتّٰى جَاۤءَ اَمْرُ اللّٰهِ وَغَرَّكُمْ بِاللّٰهِ الْغَرُوْرُ', 'yunādụnahum a lam nakum ma\'akum, qālụ balā wa lākinnakum fatantum anfusakum wa tarabbaṣtum wartabtum wa garratkumul-amāniyyu ḥattā jā`a amrullāhi wa garrakum billāhil-garụr'),
(5089, 57, 15, 'فَالْيَوْمَ لَا يُؤْخَذُ مِنْكُمْ فِدْيَةٌ وَّلَا مِنَ الَّذِيْنَ كَفَرُوْاۗ مَأْوٰىكُمُ النَّارُۗ هِيَ مَوْلٰىكُمْۗ وَبِئْسَ الْمَصِيْرُ', 'fal-yauma lā yu`khażu mingkum fidyatuw wa lā minallażīna kafarụ, ma`wākumun-nār, hiya maulākum, wa bi`sal-maṣīr'),
(5090, 57, 16, 'اَلَمْ يَأْنِ لِلَّذِيْنَ اٰمَنُوْٓا اَنْ تَخْشَعَ قُلُوْبُهُمْ لِذِكْرِ اللّٰهِ وَمَا نَزَلَ مِنَ الْحَقِّۙ وَلَا يَكُوْنُوْا كَالَّذِيْنَ اُوْتُوا الْكِتٰبَ مِنْ قَبْلُ فَطَالَ عَلَيْهِمُ الْاَمَدُ فَقَسَتْ قُلُوْبُهُمْۗ وَكَثِيْرٌ مِّنْهُمْ فٰسِقُوْنَ', 'a lam ya`ni lillażīna āmanū an takhsya\'a qulụbuhum liżikrillāhi wa mā nazala minal-ḥaqqi wa lā yakụnụ kallażīna ụtul-kitāba ming qablu fa ṭāla \'alaihimul-amadu fa qasat qulụbuhum, wa kaṡīrum min-hum fāsiqụn'),
(5091, 57, 17, 'اِعْلَمُوْٓا اَنَّ اللّٰهَ يُحْيِ الْاَرْضَ بَعْدَ مَوْتِهَاۗ قَدْ بَيَّنَّا لَكُمُ الْاٰيٰتِ لَعَلَّكُمْ تَعْقِلُوْنَ', 'i\'lamū annallāha yuḥyil-arḍa ba\'da mautihā, qad bayyannā lakumul-āyāti la\'allakum ta\'qilụn'),
(5092, 57, 18, 'اِنَّ الْمُصَّدِّقِيْنَ وَالْمُصَّدِّقٰتِ وَاَقْرَضُوا اللّٰهَ قَرْضًا حَسَنًا يُّضٰعَفُ لَهُمْ وَلَهُمْ اَجْرٌ كَرِيْمٌ', 'innal-muṣṣaddiqīna wal-muṣṣaddiqāti wa aqraḍullāha qarḍan ḥasanay yuḍā\'afu lahum wa lahum ajrung karīm'),
(5093, 57, 19, 'وَالَّذِيْنَ اٰمَنُوْا بِاللّٰهِ وَرُسُلِهٖٓ اُولٰۤىِٕكَ هُمُ الصِّدِّيْقُوْنَ ۖوَالشُّهَدَاۤءُ عِنْدَ رَبِّهِمْۗ لَهُمْ اَجْرُهُمْ وَنُوْرُهُمْۗ وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَآ اُولٰۤىِٕكَ اَصْحٰبُ الْجَحِيْمِ', 'wallażīna āmanụ billāhi wa rusulihī ulā`ika humuṣ-ṣiddīqụna wasy-syuhadā`u \'inda rabbihim, lahum ajruhum wa nụruhum, wallażīna kafarụ wa każżabụ bi`āyātinā ulā`ika aṣ-ḥābul-jaḥīm'),
(5094, 57, 20, 'اِعْلَمُوْٓا اَنَّمَا الْحَيٰوةُ الدُّنْيَا لَعِبٌ وَّلَهْوٌ وَّزِيْنَةٌ وَّتَفَاخُرٌۢ بَيْنَكُمْ وَتَكَاثُرٌ فِى الْاَمْوَالِ وَالْاَوْلَادِۗ كَمَثَلِ غَيْثٍ اَعْجَبَ الْكُفَّارَ نَبَاتُهٗ ثُمَّ يَهِيْجُ فَتَرٰىهُ مُصْفَرًّا ثُمَّ يَكُوْنُ حُطَامًاۗ وَفِى الْاٰخِرَةِ عَذَابٌ شَدِيْدٌۙ وَّمَغْفِرَةٌ مِّنَ اللّٰهِ وَرِضْوَانٌ ۗوَمَا الْحَيٰوةُ الدُّنْيَآ اِلَّا مَتَاعُ الْغُرُوْرِ', 'i\'lamū annamal-ḥayātud-dun-yā la\'ibuw wa lahwuw wa zīnatuw wa tafākhurum bainakum wa takāṡurun fil-amwāli wal-aulād, kamaṡali gaiṡin a\'jabal-kuffāra nabātuhụ ṡumma yahīju fa tarāhu muṣfarran ṡumma yakụnu huṭāmā, wa fil-ākhirati \'ażābun syadīduw wa magfiratum minallāhi wa riḍwān, wa mal-ḥayātud-dun-yā illā matā\'ul-gurụr'),
(5095, 57, 21, 'سَابِقُوْٓا اِلٰى مَغْفِرَةٍ مِّنْ رَّبِّكُمْ وَجَنَّةٍ عَرْضُهَا كَعَرْضِ السَّمَاۤءِ وَالْاَرْضِۙ اُعِدَّتْ لِلَّذِيْنَ اٰمَنُوْا بِاللّٰهِ وَرُسُلِهٖۗ ذٰلِكَ فَضْلُ اللّٰهِ يُؤْتِيْهِ مَنْ يَّشَاۤءُ ۚوَاللّٰهُ ذُو الْفَضْلِ الْعَظِيْمِ', 'sābiqū ilā magfiratim mir rabbikum wa jannatin \'arḍuhā ka\'arḍis-samā`i wal-arḍi u\'iddat lillażīna āmanụ billāhi wa rusulih, żālika faḍlullāhi yu`tīhi may yasyā`, wallāhu żul-faḍlil-\'aẓīm'),
(5096, 57, 22, 'مَآ اَصَابَ مِنْ مُّصِيْبَةٍ فِى الْاَرْضِ وَلَا فِيْٓ اَنْفُسِكُمْ اِلَّا فِيْ كِتٰبٍ مِّنْ قَبْلِ اَنْ نَّبْرَاَهَا ۗاِنَّ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرٌۖ', 'mā aṣāba mim muṣībatin fil-arḍi wa lā fī anfusikum illā fī kitābim ming qabli an nabra`ahā, inna żālika \'alallāhi yasīr'),
(5097, 57, 23, 'لِّكَيْلَا تَأْسَوْا عَلٰى مَا فَاتَكُمْ وَلَا تَفْرَحُوْا بِمَآ اٰتٰىكُمْ ۗوَاللّٰهُ لَا يُحِبُّ كُلَّ مُخْتَالٍ فَخُوْرٍۙ', 'likai lā ta`sau \'alā mā fātakum wa lā tafraḥụ bimā ātākum, wallāhu lā yuḥibbu kulla mukhtālin fakhụr'),
(5098, 57, 24, ' ۨالَّذِيْنَ يَبْخَلُوْنَ وَيَأْمُرُوْنَ النَّاسَ بِالْبُخْلِ ۗوَمَنْ يَّتَوَلَّ فَاِنَّ اللّٰهَ هُوَ الْغَنِيُّ الْحَمِيْدُ', 'allażīna yabkhalụna wa ya`murụnan-nāsa bil-bukhl, wa may yatawalla fa innallāha huwal-ganiyyul-ḥamīd'),
(5099, 57, 25, 'لَقَدْ اَرْسَلْنَا رُسُلَنَا بِالْبَيِّنٰتِ وَاَنْزَلْنَا مَعَهُمُ الْكِتٰبَ وَالْمِيْزَانَ لِيَقُوْمَ النَّاسُ بِالْقِسْطِۚ وَاَنْزَلْنَا الْحَدِيْدَ فِيْهِ بَأْسٌ شَدِيْدٌ وَّمَنَافِعُ لِلنَّاسِ وَلِيَعْلَمَ اللّٰهُ مَنْ يَّنْصُرُهٗ وَرُسُلَهٗ بِالْغَيْبِۗ اِنَّ اللّٰهَ قَوِيٌّ عَزِيْزٌ', 'laqad arsalnā rusulanā bil-bayyināti wa anzalnā ma\'ahumul-kitāba wal-mīzāna liyaqụman-nāsu bil-qisṭ, wa anzalnal-ḥadīda fīhi ba`sun syadīduw wa manāfi\'u lin-nāsi wa liya\'lamallāhu may yanṣuruhụ wa rusulahụ bil-gaīb, innallāha qawiyyun \'azīz'),
(5100, 57, 26, 'وَلَقَدْ اَرْسَلْنَا نُوْحًا وَّاِبْرٰهِيْمَ وَجَعَلْنَا فِيْ ذُرِّيَّتِهِمَا النُّبُوَّةَ وَالْكِتٰبَ فَمِنْهُمْ مُّهْتَدٍۚ وَكَثِيْرٌ مِّنْهُمْ فٰسِقُوْنَ', 'wa laqad arsalnā nụḥaw wa ibrāhīma wa ja\'alnā fī żurriyyatihiman-nubuwwata wal-kitāba fa min-hum muhtad, wa kaṡīrum min-hum fāsiqụn'),
(5101, 57, 27, 'ثُمَّ قَفَّيْنَا عَلٰٓى اٰثَارِهِمْ بِرُسُلِنَا وَقَفَّيْنَا بِعِيْسَى ابْنِ مَرْيَمَ وَاٰتَيْنٰهُ الْاِنْجِيْلَ ەۙ وَجَعَلْنَا فِيْ قُلُوْبِ الَّذِيْنَ اتَّبَعُوْهُ رَأْفَةً وَّرَحْمَةً ۗوَرَهْبَانِيَّةَ ِۨابْتَدَعُوْهَا مَا كَتَبْنٰهَا عَلَيْهِمْ اِلَّا ابْتِغَاۤءَ رِضْوَانِ اللّٰهِ فَمَا رَعَوْهَا حَقَّ رِعَايَتِهَا ۚفَاٰتَيْنَا الَّذِيْنَ اٰمَنُوْا مِنْهُمْ اَجْرَهُمْ ۚ وَكَثِيْرٌ مِّنْهُمْ فٰسِقُوْنَ', 'ṡumma qaffainā \'alā āṡārihim birusulinā wa qaffainā bi\'īsabni maryama wa ātaināhul-injīla wa ja\'alnā fī qulụbillażīnattaba\'ụhu ra`fataw wa raḥmah, wa rahbāniyyatanibtada\'ụhā mā katabnāhā \'alaihim illabtigā`a riḍwānillāhi fa mā ra\'auhā ḥaqqa ri\'āyatihā, fa ātainallażīna āmanụ min-hum ajrahum, wa kaṡīrum min-hum fāsiqụn'),
(5102, 57, 28, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَاٰمِنُوْا بِرَسُوْلِهٖ يُؤْتِكُمْ كِفْلَيْنِ مِنْ رَّحْمَتِهٖ وَيَجْعَلْ لَّكُمْ نُوْرًا تَمْشُوْنَ بِهٖ وَيَغْفِرْ لَكُمْۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌۙ', 'yā ayyuhallażīna āmanuttaqullāha wa āminụ birasụlihī yu`tikum kiflaini mir raḥmatihī wa yaj\'al lakum nụran tamsyụna bihī wa yagfir lakum, wallāhu gafụrur raḥīm'),
(5103, 57, 29, 'لِّئَلَّا يَعْلَمَ اَهْلُ الْكِتٰبِ اَلَّا يَقْدِرُوْنَ عَلٰى شَيْءٍ مِّنْ فَضْلِ اللّٰهِ وَاَنَّ الْفَضْلَ بِيَدِ اللّٰهِ يُؤْتِيْهِ مَنْ يَّشَاۤءُ ۗوَاللّٰهُ ذُو الْفَضْلِ الْعَظِيْمِ ۔', 'li`allā ya\'lama ahlul-kitābi allā yaqdirụna \'alā syai`im min faḍlillāhi wa annal-faḍla biyadillāhi yu`tīhi may yasyā`, wallāhu żul-faḍlil-\'aẓīm'),
(5104, 58, 1, 'قَدْ سَمِعَ اللّٰهُ قَوْلَ الَّتِيْ تُجَادِلُكَ فِيْ زَوْجِهَا وَتَشْتَكِيْٓ اِلَى اللّٰهِ ۖوَاللّٰهُ يَسْمَعُ تَحَاوُرَكُمَاۗ اِنَّ اللّٰهَ سَمِيْعٌۢ بَصِيْرٌ', 'qad sami\'allāhu qaulallatī tujādiluka fī zaujihā wa tasytakī ilallāhi wallāhu yasma\'u taḥāwurakumā, innallāha samī\'um baṣīr'),
(5105, 58, 2, 'اَلَّذِيْنَ يُظٰهِرُوْنَ مِنْكُمْ مِّنْ نِّسَاۤىِٕهِمْ مَّا هُنَّ اُمَّهٰتِهِمْۗ اِنْ اُمَّهٰتُهُمْ اِلَّا الّٰۤـِٔيْ وَلَدْنَهُمْۗ وَاِنَّهُمْ لَيَقُوْلُوْنَ مُنْكَرًا مِّنَ الْقَوْلِ وَزُوْرًاۗ وَاِنَّ اللّٰهَ لَعَفُوٌّ غَفُوْرٌ', 'allażīna yuẓāhirụna mingkum min nisā`ihim mā hunna ummahātihim, in ummahātuhum illal-lā`ī waladnahum, wa innahum layaqụlụna mungkaram minal-qauli wazụrā, wa innallāha la\'afuwwun gafụr'),
(5106, 58, 3, 'وَالَّذِيْنَ يُظٰهِرُوْنَ مِنْ نِّسَاۤىِٕهِمْ ثُمَّ يَعُوْدُوْنَ لِمَا قَالُوْا فَتَحْرِيْرُ رَقَبَةٍ مِّنْ قَبْلِ اَنْ يَّتَمَاۤسَّاۗ ذٰلِكُمْ تُوْعَظُوْنَ بِهٖۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ', 'wallażīna yuẓāhirụna min nisā`ihim ṡumma ya\'ụdụna limā qālụ fa taḥrīru raqabatim ming qabli ay yatamāssā, żālikum tụ\'aẓụna bih, wallāhu bimā ta\'malụna khabīr'),
(5107, 58, 4, 'فَمَنْ لَّمْ يَجِدْ فَصِيَامُ شَهْرَيْنِ مُتَتَابِعَيْنِ مِنْ قَبْلِ اَنْ يَّتَمَاۤسَّاۗ فَمَنْ لَّمْ يَسْتَطِعْ فَاِطْعَامُ سِتِّيْنَ مِسْكِيْنًاۗ ذٰلِكَ لِتُؤْمِنُوْا بِاللّٰهِ وَرَسُوْلِهٖۗ وَتِلْكَ حُدُوْدُ اللّٰهِ ۗوَلِلْكٰفِرِيْنَ عَذَابٌ اَلِيْمٌ', 'fa mal lam yajid fa ṣiyāmu syahraini mutatābi\'aini ming qabli ay yatamāssā, fa mal lam yastaṭi\' fa iṭ\'āmu sittīna miskīnā, żālika litu`minụ billāhi wa rasụlih, wa tilka ḥudụdullāh, wa lil-kāfirīna \'ażābun alīm'),
(5108, 58, 5, 'اِنَّ الَّذِيْنَ يُحَاۤدُّوْنَ اللّٰهَ وَرَسُوْلَهٗ كُبِتُوْا كَمَا كُبِتَ الَّذِيْنَ مِنْ قَبْلِهِمْ وَقَدْ اَنْزَلْنَآ اٰيٰتٍۢ بَيِّنٰتٍۗ وَلِلْكٰفِرِيْنَ عَذَابٌ مُّهِيْنٌۚ', 'innallażīna yuḥāddụnallāha wa rasụlahụ kubitụ kamā kubitallażīna ming qablihim wa qad anzalnā āyātim bayyināt, wa lil-kāfirīna \'ażābum muhīn'),
(5109, 58, 6, 'يَوْمَ يَبْعَثُهُمُ اللّٰهُ جَمِيْعًا فَيُنَبِّئُهُمْ بِمَا عَمِلُوْاۗ اَحْصٰىهُ اللّٰهُ وَنَسُوْهُۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ', 'yauma yab\'aṡuhumullāhu jamī\'an fa yunabbi`uhum bimā \'amilụ, aḥṣāhullāhu wa nasụh, wallāhu \'alā kulli syai`in syahīd'),
(5110, 58, 7, 'اَلَمْ تَرَ اَنَّ اللّٰهَ يَعْلَمُ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ مَا يَكُوْنُ مِنْ نَّجْوٰى ثَلٰثَةٍ اِلَّا هُوَ رَابِعُهُمْ وَلَا خَمْسَةٍ اِلَّا هُوَ سَادِسُهُمْ وَلَآ اَدْنٰى مِنْ ذٰلِكَ وَلَآ اَكْثَرَ اِلَّا هُوَ مَعَهُمْ اَيْنَ مَا كَانُوْاۚ ثُمَّ يُنَبِّئُهُمْ بِمَا عَمِلُوْا يَوْمَ الْقِيٰمَةِۗ اِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ', 'a lam tara annallāha ya\'lamu mā fis-samāwāti wa mā fil-arḍ, mā yakụnu min najwā ṡalāṡatin illā huwa rābi\'uhum wa lā khamsatin illā huwa sādisuhum wa lā adnā min żālika wa lā akṡara illā huwa ma\'ahum aina mā kānụ, ṡumma yunabbi`uhum bimā \'amilụ yaumal-qiyāmah, innallāha bikulli syai`in \'alīm'),
(5111, 58, 8, 'اَلَمْ تَرَ اِلَى الَّذِيْنَ نُهُوْا عَنِ النَّجْوٰى ثُمَّ يَعُوْدُوْنَ لِمَا نُهُوْا عَنْهُ وَيَتَنٰجَوْنَ بِالْاِثْمِ وَالْعُدْوَانِ وَمَعْصِيَتِ الرَّسُوْلِۖ وَاِذَا جَاۤءُوْكَ حَيَّوْكَ بِمَا لَمْ يُحَيِّكَ بِهِ اللّٰهُ ۙوَيَقُوْلُوْنَ فِيْٓ اَنْفُسِهِمْ لَوْلَا يُعَذِّبُنَا اللّٰهُ بِمَا نَقُوْلُۗ حَسْبُهُمْ جَهَنَّمُۚ يَصْلَوْنَهَاۚ فَبِئْسَ الْمَصِيْرُ', 'a lam tara ilallażīna nuhụ \'anin-najwā ṡumma ya\'ụdụna limā nuhụ \'an-hu wa yatanājauna bil-iṡmi wal-\'udwāni wa ma\'ṣiyatir-rasụli wa iżā jā`ụka ḥayyauka bimā lam yuḥayyika bihillāhu wa yaqụlụna fī anfusihim lau lā yu\'ażżibunallāhu bimā naqụl, ḥasbuhum jahannam, yaṣlaunahā, fa bi`sal-maṣīr'),
(5112, 58, 9, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا تَنَاجَيْتُمْ فَلَا تَتَنَاجَوْا بِالْاِثْمِ وَالْعُدْوَانِ وَمَعْصِيَتِ الرَّسُوْلِ وَتَنَاجَوْا بِالْبِرِّ وَالتَّقْوٰىۗ وَاتَّقُوا اللّٰهَ الَّذِيْٓ اِلَيْهِ تُحْشَرُوْنَ', 'yā ayyuhallażīna āmanū iżā tanājaitum fa lā tatanājau bil-iṡmi wal-\'udwāni wa ma\'ṣiyatir-rasụli wa tanājau bil-birri wat-taqwā, wattaqullāhallażī ilaihi tuḥsyarụn'),
(5113, 58, 10, 'اِنَّمَا النَّجْوٰى مِنَ الشَّيْطٰنِ لِيَحْزُنَ الَّذِيْنَ اٰمَنُوْا وَلَيْسَ بِضَاۤرِّهِمْ شَيْـًٔا اِلَّا بِاِذْنِ اللّٰهِ ۗوَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ', 'innaman-najwā minasy-syaiṭāni liyaḥzunallażīna āmanụ wa laisa biḍārrihim syai`an illā bi`iżnillāh, wa \'alallāhi falyatawakkalil-mu`minụn'),
(5114, 58, 11, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا قِيْلَ لَكُمْ تَفَسَّحُوْا فِى الْمَجٰلِسِ فَافْسَحُوْا يَفْسَحِ اللّٰهُ لَكُمْۚ وَاِذَا قِيْلَ انْشُزُوْا فَانْشُزُوْا يَرْفَعِ اللّٰهُ الَّذِيْنَ اٰمَنُوْا مِنْكُمْۙ وَالَّذِيْنَ اُوْتُوا الْعِلْمَ دَرَجٰتٍۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ', 'yā ayyuhallażīna āmanū iżā qīla lakum tafassaḥụ fil-majālisi fafsaḥụ yafsaḥillāhu lakum, wa iżā qīlansyuzụ fansyuzụ yarfa\'illāhullażīna āmanụ mingkum wallażīna ụtul-\'ilma darajāt, wallāhu bimā ta\'malụna khabīr'),
(5115, 58, 12, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا نَاجَيْتُمُ الرَّسُوْلَ فَقَدِّمُوْا بَيْنَ يَدَيْ نَجْوٰىكُمْ صَدَقَةً ۗذٰلِكَ خَيْرٌ لَّكُمْ وَاَطْهَرُۗ فَاِنْ لَّمْ تَجِدُوْا فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'yā ayyuhallażīna āmanū iżā nājaitumur-rasụla fa qaddimụ baina yadai najwākum ṣadaqah, żālika khairul lakum wa aṭ-har, fa il lam tajidụ fa innallāha gafụrur raḥīm'),
(5116, 58, 13, 'ءَاَشْفَقْتُمْ اَنْ تُقَدِّمُوْا بَيْنَ يَدَيْ نَجْوٰىكُمْ صَدَقٰتٍۗ فَاِذْ لَمْ تَفْعَلُوْا وَتَابَ اللّٰهُ عَلَيْكُمْ فَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَاَطِيْعُوا اللّٰهَ وَرَسُوْلَهٗ ۗوَاللّٰهُ خَبِيْرٌ ۢبِمَا تَعْمَلُوْنَ', 'a asyfaqtum an tuqaddimụ baina yadai najwākum ṣadaqāt, fa iż lam taf\'alụ wa tāballāhu \'alaikum fa aqīmuṣ-ṣalāta wa ātuz-zakāta wa aṭī\'ullāha wa rasụlah, wallāhu khabīrum bimā ta\'malụn'),
(5117, 58, 14, '۞ اَلَمْ تَرَ اِلَى الَّذِيْنَ تَوَلَّوْا قَوْمًا غَضِبَ اللّٰهُ عَلَيْهِمْۗ مَا هُمْ مِّنْكُمْ وَلَا مِنْهُمْۙ وَيَحْلِفُوْنَ عَلَى الْكَذِبِ وَهُمْ يَعْلَمُوْنَ', 'a lam tara ilallażīna tawallau qauman gaḍiballāhu \'alaihim, mā hum mingkum wa lā min-hum wa yaḥlifụna \'alal-każibi wa hum ya\'lamụn'),
(5118, 58, 15, 'اَعَدَّ اللّٰهُ لَهُمْ عَذَابًا شَدِيْدًاۗ اِنَّهُمْ سَاۤءَ مَا كَانُوْا يَعْمَلُوْنَ', 'a\'addallāhu lahum \'ażāban syadīdā, innahum sā`a mā kānụ ya\'malụn'),
(5119, 58, 16, 'اِتَّخَذُوْٓا اَيْمَانَهُمْ جُنَّةً فَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ فَلَهُمْ عَذَابٌ مُّهِيْنٌ', 'ittakhażū aimānahum junnatan fa ṣaddụ \'an sabīlillāhi fa lahum \'ażābum muhīn'),
(5120, 58, 17, 'لَنْ تُغْنِيَ عَنْهُمْ اَمْوَالُهُمْ وَلَآ اَوْلَادُهُمْ مِّنَ اللّٰهِ شَيْـًٔاۗ اُولٰۤىِٕكَ اَصْحٰبُ النَّارِۗ هُمْ فِيْهَا خٰلِدُوْنَ', 'lan tugniya \'an-hum amwāluhum wa lā aulāduhum minallāhi syai`ā, ulā`ika aṣ-ḥābun-nār, hum fīhā khālidụn'),
(5121, 58, 18, 'يَوْمَ يَبْعَثُهُمُ اللّٰهُ جَمِيْعًا فَيَحْلِفُوْنَ لَهٗ كَمَا يَحْلِفُوْنَ لَكُمْ وَيَحْسَبُوْنَ اَنَّهُمْ عَلٰى شَيْءٍۗ اَلَآ اِنَّهُمْ هُمُ الْكٰذِبُوْنَ', 'yauma yab\'aṡuhumullāhu jamī\'an fa yaḥlifụna lahụ kamā yaḥlifụna lakum wa yaḥsabụna annahum \'alā syaī`, alā innahum humul-kāżibụn'),
(5122, 58, 19, 'اِسْتَحْوَذَ عَلَيْهِمُ الشَّيْطٰنُ فَاَنْسٰىهُمْ ذِكْرَ اللّٰهِ ۗ اُولٰۤىِٕكَ حِزْبُ الشَّيْطٰنِۗ اَلَآ اِنَّ حِزْبَ الشَّيْطٰنِ هُمُ الْخٰسِرُوْنَ', 'istaḥważa \'alaihimusy-syaiṭānu fa ansāhum żikrallāh, ulā`ika ḥizbusy-syaiṭān, alā inna ḥizbasy-syaiṭāni humul-khāsirụn'),
(5123, 58, 20, 'اِنَّ الَّذِيْنَ يُحَاۤدُّوْنَ اللّٰهَ وَرَسُوْلَهٗٓ اُولٰۤىِٕكَ فِى الْاَذَلِّيْنَ', 'innallażīna yuḥāddụnallāha wa rasụlahū ulā`ika fil-ażallīn'),
(5124, 58, 21, 'كَتَبَ اللّٰهُ لَاَغْلِبَنَّ اَنَا۠ وَرُسُلِيْۗ اِنَّ اللّٰهَ قَوِيٌّ عَزِيْزٌ', 'kataballāhu la`aglibanna ana wa rusulī, innallāha qawiyyun \'azīz'),
(5125, 58, 22, 'لَا تَجِدُ قَوْمًا يُّؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ يُوَاۤدُّوْنَ مَنْ حَاۤدَّ اللّٰهَ وَرَسُوْلَهٗ وَلَوْ كَانُوْٓا اٰبَاۤءَهُمْ اَوْ اَبْنَاۤءَهُمْ اَوْ اِخْوَانَهُمْ اَوْ عَشِيْرَتَهُمْۗ اُولٰۤىِٕكَ كَتَبَ فِيْ قُلُوْبِهِمُ الْاِيْمَانَ وَاَيَّدَهُمْ بِرُوْحٍ مِّنْهُ ۗوَيُدْخِلُهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَاۗ رَضِيَ اللّٰهُ عَنْهُمْ وَرَضُوْا عَنْهُۗ اُولٰۤىِٕكَ حِزْبُ اللّٰهِ ۗ اَلَآ اِنَّ حِزْبَ اللّٰهِ هُمُ الْمُفْلِحُوْنَ', 'lā tajidu qaumay yu`minụna billāhi wal-yaumil-ākhiri yuwāddụna man ḥāddallāha wa rasụlahụ walau kānū ābā`ahum au abnā`ahum au ikhwānahum au \'asyīratahum, ulā`ika kataba fī qulụbihimul-īmāna wa ayyadahum birụḥim min-h, wa yudkhiluhum jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā, raḍiyallāhu \'an-hum wa raḍụ \'an-h, ulā`ika ḥizbullāh, alā inna ḥizballāhi humul-mufliḥụn'),
(5126, 59, 1, 'سَبَّحَ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۚ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ', 'sabbaḥa lillāhi mā fis-samāwāti wa mā fil-arḍ, wa huwal-\'azīzul-ḥakīm'),
(5127, 59, 2, 'هُوَ الَّذِيْٓ اَخْرَجَ الَّذِيْنَ كَفَرُوْا مِنْ اَهْلِ الْكِتٰبِ مِنْ دِيَارِهِمْ لِاَوَّلِ الْحَشْرِۗ مَا ظَنَنْتُمْ اَنْ يَّخْرُجُوْا وَظَنُّوْٓا اَنَّهُمْ مَّانِعَتُهُمْ حُصُوْنُهُمْ مِّنَ اللّٰهِ فَاَتٰىهُمُ اللّٰهُ مِنْ حَيْثُ لَمْ يَحْتَسِبُوْا وَقَذَفَ فِيْ قُلُوْبِهِمُ الرُّعْبَ يُخْرِبُوْنَ بُيُوْتَهُمْ بِاَيْدِيْهِمْ وَاَيْدِى الْمُؤْمِنِيْنَۙ فَاعْتَبِرُوْا يٰٓاُولِى الْاَبْصَارِ', 'huwallażī akhrajallażīna kafarụ min ahlil-kitābi min diyārihim li`awwalil-ḥasyr, mā ẓanantum ay yakhrujụ wa ẓannū annahum māni\'atuhum ḥuṣụnuhum minallāhi fa atāhumullāhu min ḥaiṡu lam yaḥtasibụ wa qażafa fī qulụbihimur-ru\'ba yukhribụna buyụtahum bi`aidīhim wa aidil-mu`minīna fa\'tabirụ yā ulil-abṣār'),
(5128, 59, 3, 'وَلَوْلَآ اَنْ كَتَبَ اللّٰهُ عَلَيْهِمُ الْجَلَاۤءَ لَعَذَّبَهُمْ فِى الدُّنْيَاۗ وَلَهُمْ فِى الْاٰخِرَةِ عَذَابُ النَّارِ', 'walau lā ang kataballāhu \'alaihimul-jalā`a la\'ażżabahum fid-dun-yā, wa lahum fil-ākhirati \'ażābun-nār'),
(5129, 59, 4, 'ذٰلِكَ بِاَنَّهُمْ شَاۤقُّوا اللّٰهَ وَرَسُوْلَهٗ ۖوَمَنْ يُّشَاۤقِّ اللّٰهَ فَاِنَّ اللّٰهَ شَدِيْدُ الْعِقَابِ', 'żālika bi`annahum syāqqullāha wa rasụlahụ wa may yusyāqqillāha fa innallāha syadīdul-\'iqāb'),
(5130, 59, 5, 'مَا قَطَعْتُمْ مِّنْ لِّيْنَةٍ اَوْ تَرَكْتُمُوْهَا قَاۤىِٕمَةً عَلٰٓى اُصُوْلِهَا فَبِاِذْنِ اللّٰهِ وَلِيُخْزِيَ الْفٰسِقِيْنَ', 'mā qaṭa\'tum mil līnatin au taraktumụhā qā`imatan \'alā uṣụlihā fa bi`iżnillāhi wa liyukhziyal-fāsiqīn'),
(5131, 59, 6, 'وَمَآ اَفَاۤءَ اللّٰهُ عَلٰى رَسُوْلِهٖ مِنْهُمْ فَمَآ اَوْجَفْتُمْ عَلَيْهِ مِنْ خَيْلٍ وَّلَا رِكَابٍ وَّلٰكِنَّ اللّٰهَ يُسَلِّطُ رُسُلَهٗ عَلٰى مَنْ يَّشَاۤءُۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'wa mā afā`allāhu \'alā rasụlihī min-hum fa mā aujaftum \'alaihi min khailiw wa lā rikābiw wa lākinnallāha yusalliṭu rusulahụ \'alā may yasyā`, wallāhu \'alā kulli syai`ing qadīr'),
(5132, 59, 7, 'مَآ اَفَاۤءَ اللّٰهُ عَلٰى رَسُوْلِهٖ مِنْ اَهْلِ الْقُرٰى فَلِلّٰهِ وَلِلرَّسُوْلِ وَلِذِى الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنِ وَابْنِ السَّبِيْلِۙ كَيْ لَا يَكُوْنَ دُوْلَةً ۢ بَيْنَ الْاَغْنِيَاۤءِ مِنْكُمْۗ وَمَآ اٰتٰىكُمُ الرَّسُوْلُ فَخُذُوْهُ وَمَا نَهٰىكُمْ عَنْهُ فَانْتَهُوْاۚ وَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ شَدِيْدُ الْعِقَابِۘ', 'mā afā`allāhu \'alā rasụlihī min ahlil-qurā fa lillāhi wa lir-rasụli wa liżil-qurbā wal-yatāmā wal-masākīni wabnis-sabīli kai lā yakụna dụlatam bainal-agniyā`i mingkum, wa mā ātākumur-rasụlu fa khużụhu wa mā nahākum \'an-hu fantahụ, wattaqullāh, innallāha syadīdul-\'iqāb'),
(5133, 59, 8, 'لِلْفُقَرَاۤءِ الْمُهٰجِرِيْنَ الَّذِيْنَ اُخْرِجُوْا مِنْ دِيَارِهِمْ وَاَمْوَالِهِمْ يَبْتَغُوْنَ فَضْلًا مِّنَ اللّٰهِ وَرِضْوَانًا وَّيَنْصُرُوْنَ اللّٰهَ وَرَسُوْلَهٗ ۗ اُولٰۤىِٕكَ هُمُ الصّٰدِقُوْنَۚ', 'lil-fuqarā`il-muhājirīnallażīna ukhrijụ min diyārihim wa amwālihim yabtagụna faḍlam minallāhi wa riḍwānaw wa yanṣurụnallāha wa rasụlah, ulā`ika humuṣ-ṣādiqụn'),
(5134, 59, 9, 'وَالَّذِيْنَ تَبَوَّءُو الدَّارَ وَالْاِيْمَانَ مِنْ قَبْلِهِمْ يُحِبُّوْنَ مَنْ هَاجَرَ اِلَيْهِمْ وَلَا يَجِدُوْنَ فِيْ صُدُوْرِهِمْ حَاجَةً مِّمَّآ اُوْتُوْا وَيُؤْثِرُوْنَ عَلٰٓى اَنْفُسِهِمْ وَلَوْ كَانَ بِهِمْ خَصَاصَةٌ ۗوَمَنْ يُّوْقَ شُحَّ نَفْسِهٖ فَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَۚ', 'wallażīna tabawwa`ud-dāra wal-īmāna ming qablihim yuḥibbụna man hājara ilaihim wa lā yajidụna fī ṣudụrihim ḥājatam mimmā ụtụ wa yu`ṡirụna \'alā anfusihim walau kāna bihim khaṣāṣah, wa may yụqa syuḥḥa nafsihī fa ulā`ika humul-mufliḥụn'),
(5135, 59, 10, 'وَالَّذِيْنَ جَاۤءُوْ مِنْۢ بَعْدِهِمْ يَقُوْلُوْنَ رَبَّنَا اغْفِرْ لَنَا وَلِاِخْوَانِنَا الَّذِيْنَ سَبَقُوْنَا بِالْاِيْمَانِ وَلَا تَجْعَلْ فِيْ قُلُوْبِنَا غِلًّا لِّلَّذِيْنَ اٰمَنُوْا رَبَّنَآ اِنَّكَ رَءُوْفٌ رَّحِيْمٌ', 'wallażīna jā`ụ mim ba\'dihim yaqụlụna rabbanagfir lanā wa li`ikhwāninallażīna sabaqụnā bil-īmāni wa lā taj\'al fī qulụbinā gillal lillażīna āmanụ rabbanā innaka ra`ụfur raḥīm'),
(5136, 59, 11, '۞ اَلَمْ تَرَ اِلَى الَّذِيْنَ نَافَقُوْا يَقُوْلُوْنَ لِاِخْوَانِهِمُ الَّذِيْنَ كَفَرُوْا مِنْ اَهْلِ الْكِتٰبِ لَىِٕنْ اُخْرِجْتُمْ لَنَخْرُجَنَّ مَعَكُمْ وَلَا نُطِيْعُ فِيْكُمْ اَحَدًا اَبَدًاۙ وَّاِنْ قُوْتِلْتُمْ لَنَنْصُرَنَّكُمْۗ وَاللّٰهُ يَشْهَدُ اِنَّهُمْ لَكٰذِبُوْنَ', 'a lam tara ilallażīna nāfaqụ yaqụlụna li`ikhwānihimullażīna kafarụ min ahlil-kitābi la`in ukhrijtum lanakhrujanna ma\'akum wa lā nuṭī\'u fīkum aḥadan abadaw wa ing qụtiltum lananṣurannakum, wallāhu yasy-hadu innahum lakāżibụn'),
(5137, 59, 12, 'لَىِٕنْ اُخْرِجُوْا لَا يَخْرُجُوْنَ مَعَهُمْۚ وَلَىِٕنْ قُوْتِلُوْا لَا يَنْصُرُوْنَهُمْۚ وَلَىِٕنْ نَّصَرُوْهُمْ لَيُوَلُّنَّ الْاَدْبَارَۙ ثُمَّ لَا يُنْصَرُوْنَ', 'la`in ukhrijụ lā yakhrujụna ma\'ahum, wa la`ing qụtilụ lā yanṣurụnahum, wa la`in naṣarụhum layuwallunnal-adbāra ṡumma lā yunṣarụn'),
(5138, 59, 13, 'لَاَنْتُمْ اَشَدُّ رَهْبَةً فِيْ صُدُوْرِهِمْ مِّنَ اللّٰهِ ۗذٰلِكَ بِاَنَّهُمْ قَوْمٌ لَّا يَفْقَهُوْنَ', 'la`antum asyaddu rahbatan fī ṣudụrihim minallāh, żālika bi`annahum qaumul lā yafqahụn'),
(5139, 59, 14, 'لَا يُقَاتِلُوْنَكُمْ جَمِيْعًا اِلَّا فِيْ قُرًى مُّحَصَّنَةٍ اَوْ مِنْ وَّرَاۤءِ جُدُرٍۗ بَأْسُهُمْ بَيْنَهُمْ شَدِيْدٌ ۗ تَحْسَبُهُمْ جَمِيْعًا وَّقُلُوْبُهُمْ شَتّٰىۗ ذٰلِكَ بِاَنَّهُمْ قَوْمٌ لَّا يَعْقِلُوْنَۚ', 'lā yuqātilụnakum jamī\'an illā fī quram muḥaṣṣanatin au miw warā`i judur, ba`suhum bainahum syadīd, taḥsabuhum jamī\'aw wa qulụbuhum syattā, żālika bi`annahum qaumul lā ya\'qilụn'),
(5140, 59, 15, 'كَمَثَلِ الَّذِيْنَ مِنْ قَبْلِهِمْ قَرِيْبًا ذَاقُوْا وَبَالَ اَمْرِهِمْۚ وَلَهُمْ عَذَابٌ اَلِيْمٌۚ', 'kamaṡalillażīna ming qablihim qarīban żāqụ wa bāla amrihim, wa lahum \'ażābun alīm'),
(5141, 59, 16, 'كَمَثَلِ الشَّيْطٰنِ اِذْ قَالَ لِلْاِنْسَانِ اكْفُرْۚ فَلَمَّا كَفَرَ قَالَ اِنِّيْ بَرِيْۤءٌ مِّنْكَ اِنِّيْٓ اَخَافُ اللّٰهَ رَبَّ الْعٰلَمِيْنَ', 'kamaṡalisy-syaiṭāni iż qāla lil-insānikfur, fa lammā kafara qāla innī barī`um mingka innī akhāfullāha rabbal-\'ālamīn'),
(5142, 59, 17, 'فَكَانَ عَاقِبَتَهُمَآ اَنَّهُمَا فِى النَّارِ خَالِدَيْنِ فِيْهَاۗ وَذٰلِكَ جَزٰۤؤُا الظّٰلِمِيْنَ', 'fa kāna \'āqibatahumā annahumā fin-nāri khālidaini fīhā, wa żālika jazā`uẓ-ẓālimīn'),
(5143, 59, 18, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَلْتَنْظُرْ نَفْسٌ مَّا قَدَّمَتْ لِغَدٍۚ وَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ خَبِيْرٌ ۢبِمَا تَعْمَلُوْنَ', 'yā ayyuhallażīna āmanuttaqullāha waltanẓur nafsum mā qaddamat ligad, wattaqullāh, innallāha khabīrum bimā ta\'malụn');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(5144, 59, 19, 'وَلَا تَكُوْنُوْا كَالَّذِيْنَ نَسُوا اللّٰهَ فَاَنْسٰىهُمْ اَنْفُسَهُمْۗ اُولٰۤىِٕكَ هُمُ الْفٰسِقُوْنَ', 'wa lā takụnụ kallażīna nasullāha fa ansāhum anfusahum, ulā`ika humul-fāsiqụn'),
(5145, 59, 20, 'لَا يَسْتَوِيْٓ اَصْحٰبُ النَّارِ وَاَصْحٰبُ الْجَنَّةِۗ اَصْحٰبُ الْجَنَّةِ هُمُ الْفَاۤىِٕزُوْنَ', 'lā yastawī aṣ-ḥābun-nāri wa aṣ-ḥābul-jannah, aṣ-ḥābul-jannati humul-fā`izụn'),
(5146, 59, 21, 'لَوْ اَنْزَلْنَا هٰذَا الْقُرْاٰنَ عَلٰى جَبَلٍ لَّرَاَيْتَهٗ خَاشِعًا مُّتَصَدِّعًا مِّنْ خَشْيَةِ اللّٰهِ ۗوَتِلْكَ الْاَمْثَالُ نَضْرِبُهَا لِلنَّاسِ لَعَلَّهُمْ يَتَفَكَّرُوْنَ', 'lau anzalnā hāżal-qur`āna \'alā jabalil lara`aitahụ khāsyi\'am mutaṣaddi\'am min khasy-yatillāh, wa tilkal-amṡālu naḍribuhā lin-nāsi la\'allahum yatafakkarụn'),
(5147, 59, 22, 'هُوَ اللّٰهُ الَّذِيْ لَآ اِلٰهَ اِلَّا هُوَۚ عَالِمُ الْغَيْبِ وَالشَّهَادَةِۚ هُوَ الرَّحْمٰنُ الرَّحِيْمُ', 'huwallāhullażī lā ilāha illā huw, \'ālimul-gaibi wasy-syahādah, huwar-raḥmānur-raḥīm'),
(5148, 59, 23, 'هُوَ اللّٰهُ الَّذِيْ لَآ اِلٰهَ اِلَّا هُوَ ۚ اَلْمَلِكُ الْقُدُّوْسُ السَّلٰمُ الْمُؤْمِنُ الْمُهَيْمِنُ الْعَزِيْزُ الْجَبَّارُ الْمُتَكَبِّرُۗ سُبْحٰنَ اللّٰهِ عَمَّا يُشْرِكُوْنَ', 'huwallāhullażī lā ilāha illā huw, al-malikul-quddụsus-salāmul-mu`minul-muhaiminul-\'azīzul-jabbārul-mutakabbir, sub-ḥānallāhi \'ammā yusyrikụn'),
(5149, 59, 24, 'هُوَ اللّٰهُ الْخَالِقُ الْبَارِئُ الْمُصَوِّرُ لَهُ الْاَسْمَاۤءُ الْحُسْنٰىۗ يُسَبِّحُ لَهٗ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۚ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ', 'huwallāhul-khāliqul-bāri`ul-muṣawwiru lahul-asmā`ul-ḥusnā, yusabbiḥu lahụ mā fis-samāwāti wal-arḍ, wa huwal-\'azīzul-ḥakīm'),
(5150, 60, 1, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوْا عَدُوِّيْ وَعَدُوَّكُمْ اَوْلِيَاۤءَ تُلْقُوْنَ اِلَيْهِمْ بِالْمَوَدَّةِ وَقَدْ كَفَرُوْا بِمَا جَاۤءَكُمْ مِّنَ الْحَقِّۚ يُخْرِجُوْنَ الرَّسُوْلَ وَاِيَّاكُمْ اَنْ تُؤْمِنُوْا بِاللّٰهِ رَبِّكُمْۗ اِنْ كُنْتُمْ خَرَجْتُمْ جِهَادًا فِيْ سَبِيْلِيْ وَابْتِغَاۤءَ مَرْضَاتِيْ تُسِرُّوْنَ اِلَيْهِمْ بِالْمَوَدَّةِ وَاَنَا۠ اَعْلَمُ بِمَآ اَخْفَيْتُمْ وَمَآ اَعْلَنْتُمْۗ وَمَنْ يَّفْعَلْهُ مِنْكُمْ فَقَدْ ضَلَّ سَوَاۤءَ السَّبِيْلِ', 'yā ayyuhallażīna āmanụ lā tattakhiżụ \'aduwwī wa \'aduwwakum auliyā`a tulqụna ilaihim bil-mawaddati wa qad kafarụ bimā jā`akum minal-ḥaqq, yukhrijụnar-rasụla wa iyyākum an tu`minụ billāhi rabbikum, ing kuntum kharajtum jihādan fī sabīlī wabtigā`a marḍātī tusirrụna ilaihim bil-mawaddati wa ana a\'lamu bimā akhfaitum wa mā a\'lantum, wa may yaf\'al-hu mingkum fa qad ḍalla sawā`as-sabīl'),
(5151, 60, 2, 'اِنْ يَّثْقَفُوْكُمْ يَكُوْنُوْا لَكُمْ اَعْدَاۤءً وَّيَبْسُطُوْٓا اِلَيْكُمْ اَيْدِيَهُمْ وَاَلْسِنَتَهُمْ بِالسُّوْۤءِ وَوَدُّوْا لَوْ تَكْفُرُوْنَۗ', 'iy yaṡqafụkum yakụnụ lakum a\'dā`aw wa yabsuṭū ilaikum aidiyahum wa alsinatahum bis-sū`i wa waddụ lau takfurụn'),
(5152, 60, 3, 'لَنْ تَنْفَعَكُمْ اَرْحَامُكُمْ وَلَآ اَوْلَادُكُمْ ۛيَوْمَ الْقِيٰمَةِ ۛيَفْصِلُ بَيْنَكُمْۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرٌ', 'lan tanfa\'akum ar-ḥāmukum wa lā aulādukum, yaumal-qiyāmati yafṣilu bainakum, wallāhu bimā ta\'malụna baṣīr'),
(5153, 60, 4, 'قَدْ كَانَتْ لَكُمْ اُسْوَةٌ حَسَنَةٌ فِيْٓ اِبْرٰهِيْمَ وَالَّذِيْنَ مَعَهٗۚ اِذْ قَالُوْا لِقَوْمِهِمْ اِنَّا بُرَءٰۤؤُا مِنْكُمْ وَمِمَّا تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ ۖ كَفَرْنَا بِكُمْ وَبَدَا بَيْنَنَا وَبَيْنَكُمُ الْعَدَاوَةُ وَالْبَغْضَاۤءُ اَبَدًا حَتّٰى تُؤْمِنُوْا بِاللّٰهِ وَحْدَهٗٓ اِلَّا قَوْلَ اِبْرٰهِيْمَ لِاَبِيْهِ لَاَسْتَغْفِرَنَّ لَكَ وَمَآ اَمْلِكُ لَكَ مِنَ اللّٰهِ مِنْ شَيْءٍۗ رَبَّنَا عَلَيْكَ تَوَكَّلْنَا وَاِلَيْكَ اَنَبْنَا وَاِلَيْكَ الْمَصِيْرُ', 'qad kānat lakum uswatun ḥasanatun fī ibrāhīma wallażīna ma\'ah, iż qālụ liqaumihim innā bura`ā`u mingkum wa mimmā ta\'budụna min dụnillāhi kafarnā bikum wa badā bainanā wa bainakumul-\'adāwatu wal-bagḍā`u abadan ḥattā tu`minụ billāhi waḥdahū illā qaula ibrāhīma li`abīhi la`astagfiranna laka wa mā amliku laka minallāhi min syaī`, rabbanā \'alaika tawakkalnā wa ilaika anabnā wa ilaikal-maṣīr'),
(5154, 60, 5, 'رَبَّنَا لَا تَجْعَلْنَا فِتْنَةً لِّلَّذِيْنَ كَفَرُوْا وَاغْفِرْ لَنَا رَبَّنَاۚ اِنَّكَ اَنْتَ الْعَزِيْزُ الْحَكِيْمُ', 'rabbanā lā taj\'alnā fitnatal lillażīna kafarụ wagfir lanā rabbanā, innaka antal-\'azīzul-ḥakīm'),
(5155, 60, 6, 'لَقَدْ كَانَ لَكُمْ فِيْهِمْ اُسْوَةٌ حَسَنَةٌ لِّمَنْ كَانَ يَرْجُو اللّٰهَ وَالْيَوْمَ الْاٰخِرَۗ وَمَنْ يَّتَوَلَّ فَاِنَّ اللّٰهَ هُوَ الْغَنِيُّ الْحَمِيْدُ', 'laqad kāna lakum fīhim uswatun ḥasanatul limang kāna yarjullāha wal-yaumal-ākhir, wa may yatawalla fa innallāha huwal-ganiyyul-ḥamīd'),
(5156, 60, 7, '۞ عَسَى اللّٰهُ اَنْ يَّجْعَلَ بَيْنَكُمْ وَبَيْنَ الَّذِيْنَ عَادَيْتُمْ مِّنْهُمْ مَّوَدَّةًۗ وَاللّٰهُ قَدِيْرٌۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ', '\'asallāhu ay yaj\'ala bainakum wa bainallażīna \'ādaitum min-hum mawaddah, wallāhu qadīrun, wallāhu gafụrur raḥīm'),
(5157, 60, 8, 'لَا يَنْهٰىكُمُ اللّٰهُ عَنِ الَّذِيْنَ لَمْ يُقَاتِلُوْكُمْ فِى الدِّيْنِ وَلَمْ يُخْرِجُوْكُمْ مِّنْ دِيَارِكُمْ اَنْ تَبَرُّوْهُمْ وَتُقْسِطُوْٓا اِلَيْهِمْۗ اِنَّ اللّٰهَ يُحِبُّ الْمُقْسِطِيْنَ', 'lā yan-hākumullāhu \'anillażīna lam yuqātilụkum fid-dīni wa lam yukhrijụkum min diyārikum an tabarrụhum wa tuqsiṭū ilaihim, innallāha yuḥibbul-muqsiṭīn'),
(5158, 60, 9, 'اِنَّمَا يَنْهٰىكُمُ اللّٰهُ عَنِ الَّذِيْنَ قَاتَلُوْكُمْ فِى الدِّيْنِ وَاَخْرَجُوْكُمْ مِّنْ دِيَارِكُمْ وَظَاهَرُوْا عَلٰٓى اِخْرَاجِكُمْ اَنْ تَوَلَّوْهُمْۚ وَمَنْ يَّتَوَلَّهُمْ فَاُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ', 'innamā yan-hākumullāhu \'anillażīna qātalụkum fid-dīni wa akhrajụkum min diyārikum wa ẓāharụ \'alā ikhrājikum an tawallauhum, wa may yatawallahum fa ulā`ika humuẓ-ẓālimụn'),
(5159, 60, 10, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا جَاۤءَكُمُ الْمُؤْمِنٰتُ مُهٰجِرٰتٍ فَامْتَحِنُوْهُنَّۗ اَللّٰهُ اَعْلَمُ بِاِيْمَانِهِنَّ فَاِنْ عَلِمْتُمُوْهُنَّ مُؤْمِنٰتٍ فَلَا تَرْجِعُوْهُنَّ اِلَى الْكُفَّارِۗ لَا هُنَّ حِلٌّ لَّهُمْ وَلَا هُمْ يَحِلُّوْنَ لَهُنَّۗ وَاٰتُوْهُمْ مَّآ اَنْفَقُوْاۗ وَلَا جُنَاحَ عَلَيْكُمْ اَنْ تَنْكِحُوْهُنَّ اِذَآ اٰتَيْتُمُوْهُنَّ اُجُوْرَهُنَّۗ وَلَا تُمْسِكُوْا بِعِصَمِ الْكَوَافِرِ وَسْـَٔلُوْا مَآ اَنْفَقْتُمْ وَلْيَسْـَٔلُوْا مَآ اَنْفَقُوْاۗ ذٰلِكُمْ حُكْمُ اللّٰهِ ۗيَحْكُمُ بَيْنَكُمْۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ', 'yā ayyuhallażīna āmanū iżā jā`akumul-mu`minātu muhājirātin famtaḥinụhunn, allāhu a\'lamu bi`īmānihinna fa in \'alimtumụhunna mu`minātin fa lā tarji\'ụhunna ilal-kuffār, lā hunna ḥillul lahum wa lā hum yaḥillụna lahunn, wa ātụhum mā anfaqụ, wa lā junāḥa \'alaikum an tangkiḥụhunna iżā ātaitumụhunna ujụrahunn, wa lā tumsikụ bi\'iṣamil-kawāfiri was`alụ mā anfaqtum walyas`alụ mā anfaqụ, żālikum ḥukmullāh, yaḥkumu bainakum, wallāhu \'alīmun ḥakīm'),
(5160, 60, 11, 'وَاِنْ فَاتَكُمْ شَيْءٌ مِّنْ اَزْوَاجِكُمْ اِلَى الْكُفَّارِ فَعَاقَبْتُمْ فَاٰتُوا الَّذِيْنَ ذَهَبَتْ اَزْوَاجُهُمْ مِّثْلَ مَآ اَنْفَقُوْاۗ وَاتَّقُوا اللّٰهَ الَّذِيْٓ اَنْتُمْ بِهٖ مُؤْمِنُوْنَ', 'wa in fātakum syai`um min azwājikum ilal-kuffāri fa āqabtum fa ātullażīna żahabat azwājuhum miṡla mā anfaqụ, wattaqullāhallażī antum bihī mu`minụn'),
(5161, 60, 12, 'يٰٓاَيُّهَا النَّبِيُّ اِذَا جَاۤءَكَ الْمُؤْمِنٰتُ يُبَايِعْنَكَ عَلٰٓى اَنْ لَّا يُشْرِكْنَ بِاللّٰهِ شَيْـًٔا وَّلَا يَسْرِقْنَ وَلَا يَزْنِيْنَ وَلَا يَقْتُلْنَ اَوْلَادَهُنَّ وَلَا يَأْتِيْنَ بِبُهْتَانٍ يَّفْتَرِيْنَهٗ بَيْنَ اَيْدِيْهِنَّ وَاَرْجُلِهِنَّ وَلَا يَعْصِيْنَكَ فِيْ مَعْرُوْفٍ فَبَايِعْهُنَّ وَاسْتَغْفِرْ لَهُنَّ اللّٰهَ ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'yā ayyuhan-nabiyyu iżā jā`akal-mu`minātu yubāyi\'naka \'alā al lā yusyrikna billāhi syai`aw wa lā yasriqna wa lā yaznīna wa lā yaqtulna aulādahunna wa lā ya`tīna bibuhtāniy yaftarīnahụ baina aidīhinna wa arjulihinna wa lā ya\'ṣīnaka fī ma\'rụfin fa bāyi\'hunna wastagfir lahunnallāh, innallāha gafụrur raḥīm'),
(5162, 60, 13, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَوَلَّوْا قَوْمًا غَضِبَ اللّٰهُ عَلَيْهِمْ قَدْ يَىِٕسُوْا مِنَ الْاٰخِرَةِ كَمَا يَىِٕسَ الْكُفَّارُ مِنْ اَصْحٰبِ الْقُبُوْرِ', 'yā ayyuhallażīna āmanụ lā tatawallau qauman gaḍiballāhu \'alaihim qad ya`isụ minal-ākhirati kamā ya`isal-kuffāru min aṣ-ḥābil-qubụr'),
(5163, 61, 1, 'سَبَّحَ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۚ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ', 'sabbaḥa lillāhi mā fis-samāwāti wa mā fil-arḍ, wa huwal-\'azīzul-ḥakīm'),
(5164, 61, 2, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لِمَ تَقُوْلُوْنَ مَا لَا تَفْعَلُوْنَ', 'yā ayyuhallażīna āmanụ lima taqụlụna mā lā taf\'alụn'),
(5165, 61, 3, 'كَبُرَ مَقْتًا عِنْدَ اللّٰهِ اَنْ تَقُوْلُوْا مَا لَا تَفْعَلُوْنَ', 'kabura maqtan \'indallāhi an taqụlụ mā lā taf\'alụn'),
(5166, 61, 4, 'اِنَّ اللّٰهَ يُحِبُّ الَّذِيْنَ يُقَاتِلُوْنَ فِيْ سَبِيْلِهٖ صَفًّا كَاَنَّهُمْ بُنْيَانٌ مَّرْصُوْصٌ', 'innallāha yuḥibbullażīna yuqātilụna fī sabīlihī ṣaffang ka`annahum bun-yānum marṣụṣ'),
(5167, 61, 5, 'وَاِذْ قَالَ مُوْسٰى لِقَوْمِهٖ يٰقَوْمِ لِمَ تُؤْذُوْنَنِيْ وَقَدْ تَّعْلَمُوْنَ اَنِّيْ رَسُوْلُ اللّٰهِ اِلَيْكُمْۗ فَلَمَّا زَاغُوْٓا اَزَاغَ اللّٰهُ قُلُوْبَهُمْۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الْفٰسِقِيْنَ', 'wa iż qāla mụsā liqaumihī yā qaumi lima tu`żụnanī wa qat ta\'lamụna annī rasụlullāhi ilaikum, fa lammā zāgū azāgallāhu qulụbahum, wallāhu lā yahdil-qaumal-fāsiqīn'),
(5168, 61, 6, 'وَاِذْ قَالَ عِيْسَى ابْنُ مَرْيَمَ يٰبَنِيْٓ اِسْرَاۤءِيْلَ اِنِّيْ رَسُوْلُ اللّٰهِ اِلَيْكُمْ مُّصَدِّقًا لِّمَا بَيْنَ يَدَيَّ مِنَ التَّوْرٰىةِ وَمُبَشِّرًاۢ بِرَسُوْلٍ يَّأْتِيْ مِنْۢ بَعْدِى اسْمُهٗٓ اَحْمَدُۗ فَلَمَّا جَاۤءَهُمْ بِالْبَيِّنٰتِ قَالُوْا هٰذَا سِحْرٌ مُّبِيْنٌ', 'wa iż qāla \'īsabnu maryama yā banī isrā`īla innī rasụlullāhi ilaikum muṣaddiqal limā baina yadayya minat-taurāti wa mubasysyiram birasụliy ya`tī mim ba\'dismuhū aḥmad, fa lammā jā`ahum bil-bayyināti qālụ hāżā siḥrum mubīn'),
(5169, 61, 7, 'وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ الْكَذِبَ وَهُوَ يُدْعٰىٓ اِلَى الْاِسْلَامِۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ', 'wa man aẓlamu mim maniftarā \'alallāhil-każiba wa huwa yud\'ā ilal-islām, wallāhu lā yahdil-qaumaẓ-ẓālimīn'),
(5170, 61, 8, 'يُرِيْدُوْنَ لِيُطْفِـُٔوْا نُوْرَ اللّٰهِ بِاَفْوَاهِهِمْۗ وَاللّٰهُ مُتِمُّ نُوْرِهٖ وَلَوْ كَرِهَ الْكٰفِرُوْنَ', 'yurīdụna liyuṭfi`ụ nụrallāhi bi`afwāhihim, wallāhu mutimmu nụrihī walau karihal-kāfirụn'),
(5171, 61, 9, 'هُوَ الَّذِيْٓ اَرْسَلَ رَسُوْلَهٗ بِالْهُدٰى وَدِيْنِ الْحَقِّ لِيُظْهِرَهٗ عَلَى الدِّيْنِ كُلِّهٖۙ وَلَوْ كَرِهَ الْمُشْرِكُوْنَ', 'huwallażī arsala rasụlahụ bil-hudā wa dīnil-ḥaqqi liyuẓ-hirahụ \'alad-dīni kullihī walau karihal-musyrikụn'),
(5172, 61, 10, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا هَلْ اَدُلُّكُمْ عَلٰى تِجَارَةٍ تُنْجِيْكُمْ مِّنْ عَذَابٍ اَلِيْمٍ', 'yā ayyuhallażīna āmanụ hal adullukum \'alā tijāratin tunjīkum min \'ażābin alīm'),
(5173, 61, 11, 'تُؤْمِنُوْنَ بِاللّٰهِ وَرَسُوْلِهٖ وَتُجَاهِدُوْنَ فِيْ سَبِيْلِ اللّٰهِ بِاَمْوَالِكُمْ وَاَنْفُسِكُمْۗ ذٰلِكُمْ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَۙ', 'tu`minụna billāhi wa rasụlihī wa tujāhidụna fī sabīlillāhi bi`amwālikum wa anfusikum, żālikum khairul lakum ing kuntum ta\'lamụn'),
(5174, 61, 12, 'يَغْفِرْ لَكُمْ ذُنُوْبَكُمْ وَيُدْخِلْكُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ وَمَسٰكِنَ طَيِّبَةً فِيْ جَنّٰتِ عَدْنٍۗ ذٰلِكَ الْفَوْزُ الْعَظِيْمُۙ', 'yagfir lakum żunụbakum wa yudkhilkum jannātin tajrī min taḥtihal-an-hāru wa masākina ṭayyibatan fī jannāti \'adn, żālikal-fauzul-\'aẓīm'),
(5175, 61, 13, 'وَاُخْرٰى تُحِبُّوْنَهَاۗ نَصْرٌ مِّنَ اللّٰهِ وَفَتْحٌ قَرِيْبٌۗ وَبَشِّرِ الْمُؤْمِنِيْنَ', 'wa ukhrā tuḥibbụnahā, naṣrum minallāhi wa fat-ḥung qarīb, wa basysyiril-mu`minīn'),
(5176, 61, 14, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُوْنُوْٓا اَنْصَارَ اللّٰهِ كَمَا قَالَ عِيْسَى ابْنُ مَرْيَمَ لِلْحَوَارِيّٖنَ مَنْ اَنْصَارِيْٓ اِلَى اللّٰهِ ۗقَالَ الْحَوَارِيُّوْنَ نَحْنُ اَنْصَارُ اللّٰهِ فَاٰمَنَتْ طَّاۤىِٕفَةٌ مِّنْۢ بَنِيْٓ اِسْرَاۤءِيْلَ وَكَفَرَتْ طَّاۤىِٕفَةٌ ۚفَاَيَّدْنَا الَّذِيْنَ اٰمَنُوْا عَلٰى عَدُوِّهِمْ فَاَصْبَحُوْا ظَاهِرِيْنَ', 'yā ayyuhallażīna āmanụ kụnū anṣārallāhi kamā qāla \'īsabnu maryama lil-ḥawāriyyīna man anṣārī ilallāh, qālal-ḥawāriyyụna naḥnu anṣārullāhi fa āmanat ṭā`ifatum mim banī isrā`īla wa kafarat ṭā`ifah, fa ayyadnallażīna āmanụ \'alā \'aduwwihim fa aṣbaḥụ ẓāhirīn'),
(5177, 62, 1, 'يُسَبِّحُ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ الْمَلِكِ الْقُدُّوْسِ الْعَزِيْزِ الْحَكِيْمِ', 'yusabbiḥu lillāhi mā fis-samāwāti wa mā fil-arḍil-malikil-quddụsil-\'azīzil-ḥakīm'),
(5178, 62, 2, 'هُوَ الَّذِيْ بَعَثَ فِى الْاُمِّيّٖنَ رَسُوْلًا مِّنْهُمْ يَتْلُوْا عَلَيْهِمْ اٰيٰتِهٖ وَيُزَكِّيْهِمْ وَيُعَلِّمُهُمُ الْكِتٰبَ وَالْحِكْمَةَ وَاِنْ كَانُوْا مِنْ قَبْلُ لَفِيْ ضَلٰلٍ مُّبِيْنٍۙ', 'huwallażī ba\'aṡa fil-ummiyyīna rasụlam min-hum yatlụ \'alaihim āyātihī wa yuzakkīhim wa yu\'allimuhumul-kitāba wal-ḥikmata wa ing kānụ ming qablu lafī ḍalālim mubīn'),
(5179, 62, 3, 'وَّاٰخَرِيْنَ مِنْهُمْ لَمَّا يَلْحَقُوْا بِهِمْۗ وَهُوَ الْعَزِيْزُ الْحَكِيْمُۙ', 'wa ākharīna min-hum lammā yal-ḥaqụ bihim, wa huwal-\'azīzul-ḥakīm'),
(5180, 62, 4, 'ذٰلِكَ فَضْلُ اللّٰهِ يُؤْتِيْهِ مَنْ يَّشَاۤءُۗ وَاللّٰهُ ذُو الْفَضْلِ الْعَظِيْمِ', 'żālika faḍlullāhi yu`tīhi may yasyā`, wallāhu żul-faḍlil-\'aẓīm'),
(5181, 62, 5, 'مَثَلُ الَّذِيْنَ حُمِّلُوا التَّوْرٰىةَ ثُمَّ لَمْ يَحْمِلُوْهَا كَمَثَلِ الْحِمَارِ يَحْمِلُ اَسْفَارًاۗ بِئْسَ مَثَلُ الْقَوْمِ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِ اللّٰهِ ۗوَاللّٰهُ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ', 'maṡalullażīna ḥummilut-taurāta ṡumma lam yaḥmilụhā kamaṡalil-ḥimāri yaḥmilu asfārā, bi`sa maṡalul-qaumillażīna każżabụ bi`āyātillāh, wallāhu lā yahdil-qaumaẓ-ẓālimīn'),
(5182, 62, 6, 'قُلْ يٰٓاَيُّهَا الَّذِيْنَ هَادُوْٓا اِنْ زَعَمْتُمْ اَنَّكُمْ اَوْلِيَاۤءُ لِلّٰهِ مِنْ دُوْنِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'qul yā ayyuhallażīna hādū in za\'amtum annakum auliyā`u lillāhi min dụnin-nāsi fa tamannawul-mauta ing kuntum ṣādiqīn'),
(5183, 62, 7, 'وَلَا يَتَمَنَّوْنَهٗٓ اَبَدًاۢ بِمَا قَدَّمَتْ اَيْدِيْهِمْۗ وَاللّٰهُ عَلِيْمٌۢ بِالظّٰلِمِيْنَ', 'wa lā yatamannaunahū abadam bimā qaddamat aidīhim, wallāhu \'alīmum biẓ-ẓālimīn'),
(5184, 62, 8, 'قُلْ اِنَّ الْمَوْتَ الَّذِيْ تَفِرُّوْنَ مِنْهُ فَاِنَّهٗ مُلٰقِيْكُمْ ثُمَّ تُرَدُّوْنَ اِلٰى عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ', 'qul innal-mautallażī tafirrụna min-hu fa innahụ mulāqīkum ṡumma turaddụna ilā \'ālimil-gaibi wasy-syahādati fa yunabbi`ukum bimā kuntum ta\'malụn'),
(5185, 62, 9, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا نُوْدِيَ لِلصَّلٰوةِ مِنْ يَّوْمِ الْجُمُعَةِ فَاسْعَوْا اِلٰى ذِكْرِ اللّٰهِ وَذَرُوا الْبَيْعَۗ ذٰلِكُمْ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ', 'yā ayyuhallażīna āmanū iżā nụdiya liṣ-ṣalāti miy yaumil-jumu\'ati fas\'au ilā żikrillāhi wa żarul baī\', żālikum khairul lakum ing kuntum ta\'lamụn'),
(5186, 62, 10, 'فَاِذَا قُضِيَتِ الصَّلٰوةُ فَانْتَشِرُوْا فِى الْاَرْضِ وَابْتَغُوْا مِنْ فَضْلِ اللّٰهِ وَاذْكُرُوا اللّٰهَ كَثِيْرًا لَّعَلَّكُمْ تُفْلِحُوْنَ', 'fa iżā quḍiyatiṣ-ṣalātu fantasyirụ fil-arḍi wabtagụ min faḍlillāhi ważkurullāha kaṡīral la\'allakum tufliḥụn'),
(5187, 62, 11, 'وَاِذَا رَاَوْا تِجَارَةً اَوْ لَهْوًا ۨانْفَضُّوْٓا اِلَيْهَا وَتَرَكُوْكَ قَاۤىِٕمًاۗ قُلْ مَا عِنْدَ اللّٰهِ خَيْرٌ مِّنَ اللَّهْوِ وَمِنَ التِّجَارَةِۗ وَاللّٰهُ خَيْرُ الرّٰزِقِيْنَ', 'wa iżā ra`au tijāratan au lahwaninfaḍḍū ilaihā wa tarakụka qā`imā, qul mā \'indallāhi khairum minal-lahwi wa minat-tijārah, wallāhu khairur-rāziqīn'),
(5188, 63, 1, 'اِذَا جَاۤءَكَ الْمُنٰفِقُوْنَ قَالُوْا نَشْهَدُ اِنَّكَ لَرَسُوْلُ اللّٰهِ ۘوَاللّٰهُ يَعْلَمُ اِنَّكَ لَرَسُوْلُهٗ ۗوَاللّٰهُ يَشْهَدُ اِنَّ الْمُنٰفِقِيْنَ لَكٰذِبُوْنَۚ', 'iżā jā`akal munāfiqụna qālụ nasy-hadu innaka larasụlullāh, wallāhu ya\'lamu innaka larasụluh, wallāhu yasy-hadu innal-munāfiqīna lakāżibụn'),
(5189, 63, 2, 'اِتَّخَذُوْٓا اَيْمَانَهُمْ جُنَّةً فَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ ۗاِنَّهُمْ سَاۤءَ مَا كَانُوْا يَعْمَلُوْنَ', 'ittakhażū aimānahum junnatan fa ṣaddụ \'an sabīlillāh, innahum sā`a mā kānụ ya\'malụn'),
(5190, 63, 3, 'ذٰلِكَ بِاَنَّهُمْ اٰمَنُوْا ثُمَّ كَفَرُوْا فَطُبِعَ عَلٰى قُلُوْبِهِمْ فَهُمْ لَا يَفْقَهُوْنَ', 'żālika bi`annahum āmanụ ṡumma kafarụ fa ṭubi\'a \'alā qulụbihim fa hum lā yafqahụn'),
(5191, 63, 4, '۞ وَاِذَا رَاَيْتَهُمْ تُعْجِبُكَ اَجْسَامُهُمْۗ وَاِنْ يَّقُوْلُوْا تَسْمَعْ لِقَوْلِهِمْۗ كَاَنَّهُمْ خُشُبٌ مُّسَنَّدَةٌ ۗيَحْسَبُوْنَ كُلَّ صَيْحَةٍ عَلَيْهِمْۗ هُمُ الْعَدُوُّ فَاحْذَرْهُمْۗ قَاتَلَهُمُ اللّٰهُ ۖاَنّٰى يُؤْفَكُوْنَ', 'wa iżā ra`aitahum tu\'jibuka ajsāmuhum, wa iy yaqụlụ tasma\' liqaulihim, ka`annahum khusyubum musannadah, yaḥsabụna kulla ṣaiḥatin \'alaihim, humul-\'aduwwu faḥżar-hum, qātalahumullāhu annā yu`fakụn'),
(5192, 63, 5, 'وَاِذَا قِيْلَ لَهُمْ تَعَالَوْا يَسْتَغْفِرْ لَكُمْ رَسُوْلُ اللّٰهِ لَوَّوْا رُءُوْسَهُمْ وَرَاَيْتَهُمْ يَصُدُّوْنَ وَهُمْ مُّسْتَكْبِرُوْنَ', 'wa iżā qīla lahum ta\'ālau yastagfir lakum rasụlullāhi lawwau ru`ụsahum wa ra`aitahum yaṣuddụna wa hum mustakbirụn'),
(5193, 63, 6, 'سَوَاۤءٌ عَلَيْهِمْ اَسْتَغْفَرْتَ لَهُمْ اَمْ لَمْ تَسْتَغْفِرْ لَهُمْۗ لَنْ يَّغْفِرَ اللّٰهُ لَهُمْۗ اِنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الْفٰسِقِيْنَ', 'sawā`un \'alaihim astagfarta lahum am lam tastagfir lahum, lay yagfirallāhu lahum, innallāha lā yahdil-qaumal-fāsiqīn'),
(5194, 63, 7, 'هُمُ الَّذِيْنَ يَقُوْلُوْنَ لَا تُنْفِقُوْا عَلٰى مَنْ عِنْدَ رَسُوْلِ اللّٰهِ حَتّٰى يَنْفَضُّوْاۗ وَلِلّٰهِ خَزَاۤىِٕنُ السَّمٰوٰتِ وَالْاَرْضِۙ وَلٰكِنَّ الْمُنٰفِقِيْنَ لَا يَفْقَهُوْنَ', 'humullażīna yaqụlụna lā tunfiqụ \'alā man \'inda rasụlillāhi ḥattā yanfaḍḍụ, wa lillāhi khazā`inus-samāwāti wal-arḍi wa lākinnal-munāfiqīna lā yafqahụn'),
(5195, 63, 8, 'يَقُوْلُوْنَ لَىِٕنْ رَّجَعْنَآ اِلَى الْمَدِيْنَةِ لَيُخْرِجَنَّ الْاَعَزُّ مِنْهَا الْاَذَلَّ ۗوَلِلّٰهِ الْعِزَّةُ وَلِرَسُوْلِهٖ وَلِلْمُؤْمِنِيْنَ وَلٰكِنَّ الْمُنٰفِقِيْنَ لَا يَعْلَمُوْنَ', 'yaqụlụna la`ir raja\'nā ilal-madīnati layukhrijannal-a\'azzu min-hal-ażall, wa lillāhil-\'izzatu wa lirasụlihī wa lil-mu`minīna wa lākinnal-munāfiqīna lā ya\'lamụn'),
(5196, 63, 9, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تُلْهِكُمْ اَمْوَالُكُمْ وَلَآ اَوْلَادُكُمْ عَنْ ذِكْرِ اللّٰهِ ۚوَمَنْ يَّفْعَلْ ذٰلِكَ فَاُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ', 'yā ayyuhallażīna āmanụ lā tul-hikum amwālukum wa lā aulādukum \'an żikrillāh, wa may yaf\'al żālika fa ulā`ika humul-khāsirụn'),
(5197, 63, 10, 'وَاَنْفِقُوْا مِنْ مَّا رَزَقْنٰكُمْ مِّنْ قَبْلِ اَنْ يَّأْتِيَ اَحَدَكُمُ الْمَوْتُ فَيَقُوْلَ رَبِّ لَوْلَآ اَخَّرْتَنِيْٓ اِلٰٓى اَجَلٍ قَرِيْبٍۚ فَاَصَّدَّقَ وَاَكُنْ مِّنَ الصّٰلِحِيْنَ', 'wa anfiqụ mimmā razaqnākum ming qabli ay ya`tiya aḥadakumul-mautu fa yaqụla rabbi lau lā akhkhartanī ilā ajaling qarībin fa aṣṣaddaqa wa akum minaṣ-ṣāliḥīn'),
(5198, 63, 11, 'وَلَنْ يُّؤَخِّرَ اللّٰهُ نَفْسًا اِذَا جَاۤءَ اَجَلُهَاۗ وَاللّٰهُ خَبِيْرٌۢ بِمَا تَعْمَلُوْنَ', 'wa lay yu`akhkhirallāhu nafsan iżā jā`a ajaluhā, wallāhu khabīrum bimā ta\'malụn'),
(5199, 64, 1, 'يُسَبِّحُ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ لَهُ الْمُلْكُ وَلَهُ الْحَمْدُۖ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'yusabbiḥu lillāhi mā fis-samāwāti wa mā fil-arḍ, lahul-mulku wa lahul-ḥamdu wa huwa \'alā kulli syai`ing qadīr'),
(5200, 64, 2, 'هُوَ الَّذِيْ خَلَقَكُمْ فَمِنْكُمْ كَافِرٌ وَّمِنْكُمْ مُّؤْمِنٌۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرٌ', 'huwallażī khalaqakum fa mingkum kāfiruw wa mingkum mu`min, wallāhu bimā ta\'malụna baṣīr'),
(5201, 64, 3, 'خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّ وَصَوَّرَكُمْ فَاَحْسَنَ صُوَرَكُمْۚ وَاِلَيْهِ الْمَصِيْرُ', 'khalaqas-samāwāti wal-arḍa bil-ḥaqqi wa ṣawwarakum fa aḥsana ṣuwarakum, wa ilaihil-maṣīr'),
(5202, 64, 4, 'يَعْلَمُ مَا فِى السَّمٰوٰتِ وَالْاَرْضِ وَيَعْلَمُ مَا تُسِرُّوْنَ وَمَا تُعْلِنُوْنَۗ وَاللّٰهُ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ', 'ya\'lamu mā fis-samāwāti wal-arḍi wa ya\'lamu mā tusirrụna wa mā tu\'linụn, wallāhu \'alīmum biżātiṣ-ṣudụr'),
(5203, 64, 5, 'اَلَمْ يَأْتِكُمْ نَبَؤُا الَّذِيْنَ كَفَرُوْا مِنْ قَبْلُ ۖفَذَاقُوْا وَبَالَ اَمْرِهِمْ وَلَهُمْ عَذَابٌ اَلِيْمٌ', 'a lam ya`tikum naba`ullażīna kafarụ ming qablu fa żāqụ wa bāla amrihim wa lahum \'ażābun alīm'),
(5204, 64, 6, 'ذٰلِكَ بِاَنَّهٗ كَانَتْ تَّأْتِيْهِمْ رُسُلُهُمْ بِالْبَيِّنٰتِ فَقَالُوْٓا اَبَشَرٌ يَّهْدُوْنَنَاۖ فَكَفَرُوْا وَتَوَلَّوْا وَّاسْتَغْنَى اللّٰهُ ۗوَاللّٰهُ غَنِيٌّ حَمِيْدٌ', 'żālika bi`annahụ kānat ta`tīhim rusuluhum bil-bayyināti fa qālū abasyaruy yahdụnanā fa kafarụ wa tawallaw wastagnallāh, wallāhu ganiyyun ḥamīd'),
(5205, 64, 7, 'زَعَمَ الَّذِيْنَ كَفَرُوْٓا اَنْ لَّنْ يُّبْعَثُوْاۗ قُلْ بَلٰى وَرَبِّيْ لَتُبْعَثُنَّ ثُمَّ لَتُنَبَّؤُنَّ بِمَا عَمِلْتُمْۗ وَذٰلِكَ عَلَى اللّٰهِ يَسِيْرٌ', 'za\'amallażīna kafarū al lay yub\'aṡụ, qul balā wa rabbī latub\'aṡunna ṡumma latunabba`unna bimā \'amiltum, wa żālika \'alallāhi yasīr'),
(5206, 64, 8, 'فَاٰمِنُوْا بِاللّٰهِ وَرَسُوْلِهٖ وَالنُّوْرِ الَّذِيْٓ اَنْزَلْنَاۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ', 'fa āminụ billāhi wa rasụlihī wan-nụrillażī anzalnā, wallāhu bimā ta\'malụna khabīr'),
(5207, 64, 9, 'يَوْمَ يَجْمَعُكُمْ لِيَوْمِ الْجَمْعِ ذٰلِكَ يَوْمُ التَّغَابُنِۗ وَمَنْ يُّؤْمِنْۢ بِاللّٰهِ وَيَعْمَلْ صَالِحًا يُّكَفِّرْ عَنْهُ سَيِّاٰتِهٖ وَيُدْخِلْهُ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًاۗ ذٰلِكَ الْفَوْزُ الْعَظِيْمُ', 'yauma yajma\'ukum liyaumil-jam\'i żālika yaumut-tagābun, wa may yu`mim billāhi wa ya\'mal ṣāliḥay yukaffir \'an-hu sayyi`ātihī wa yudkhil-hu jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā abadā, żālikal-fauzul-\'aẓīm'),
(5208, 64, 10, 'وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَآ اُولٰۤىِٕكَ اَصْحٰبُ النَّارِ خٰلِدِيْنَ فِيْهَاۗ وَبِئْسَ الْمَصِيْرُ', 'wallażīna kafarụ wa każżabụ bi`āyātinā ulā`ika aṣ-ḥābun-nāri khālidīna fīhā, wa bi`sal-maṣīr'),
(5209, 64, 11, 'مَآ اَصَابَ مِنْ مُّصِيْبَةٍ اِلَّا بِاِذْنِ اللّٰهِ ۗوَمَنْ يُّؤْمِنْۢ بِاللّٰهِ يَهْدِ قَلْبَهٗ ۗوَاللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمٌ', 'mā aṣāba mim muṣībatin illā bi`iżnillāh, wa may yu`mim billāhi yahdi qalbah, wallāhu bikulli syai`in \'alīm'),
(5210, 64, 12, 'وَاَطِيْعُوا اللّٰهَ وَاَطِيْعُوا الرَّسُوْلَۚ فَاِنْ تَوَلَّيْتُمْ فَاِنَّمَا عَلٰى رَسُوْلِنَا الْبَلٰغُ الْمُبِيْنُ', 'wa aṭī\'ullāha wa aṭī\'ur-rasụl, fa in tawallaitum fa innamā \'alā rasụlinal-balāgul-mubīn'),
(5211, 64, 13, 'اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَۗ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ', 'allāhu lā ilāha illā huw, wa \'alallāhi falyatawakkalil-mu`minụn'),
(5212, 64, 14, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنَّ مِنْ اَزْوَاجِكُمْ وَاَوْلَادِكُمْ عَدُوًّا لَّكُمْ فَاحْذَرُوْهُمْۚ وَاِنْ تَعْفُوْا وَتَصْفَحُوْا وَتَغْفِرُوْا فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'yā ayyuhallażīna āmanū inna min azwājikum wa aulādikum \'aduwwal lakum faḥżarụhum, wa in ta\'fụ wa taṣfaḥụ wa tagfirụ fa innallāha gafụrur raḥīm'),
(5213, 64, 15, 'اِنَّمَآ اَمْوَالُكُمْ وَاَوْلَادُكُمْ فِتْنَةٌ ۗوَاللّٰهُ عِنْدَهٗٓ اَجْرٌ عَظِيْمٌ', 'innamā amwālukum wa aulādukum fitnah, wallāhu \'indahū ajrun \'aẓīm'),
(5214, 64, 16, 'فَاتَّقُوا اللّٰهَ مَا اسْتَطَعْتُمْ وَاسْمَعُوْا وَاَطِيْعُوْا وَاَنْفِقُوْا خَيْرًا لِّاَنْفُسِكُمْۗ وَمَنْ يُّوْقَ شُحَّ نَفْسِهٖ فَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ', 'fattaqullāha mastaṭa\'tum wasma\'ụ wa aṭī\'ụ wa anfiqụ khairal li`anfusikum, wa may yụqa syuḥḥa nafsihī fa ulā`ika humul-mufliḥụn'),
(5215, 64, 17, 'اِنْ تُقْرِضُوا اللّٰهَ قَرْضًا حَسَنًا يُّضٰعِفْهُ لَكُمْ وَيَغْفِرْ لَكُمْۗ وَاللّٰهُ شَكُوْرٌ حَلِيْمٌۙ', 'in tuqriḍullāha qarḍan ḥasanay yuḍā\'if-hu lakum wa yagfir lakum, wallāhu syakụrun ḥalīm'),
(5216, 64, 18, 'عٰلِمُ الْغَيْبِ وَالشَّهَادَةِ الْعَزِيْزُ الْحَكِيْمُ', '\'ālimul-gaibi wasy-syahādatil-\'azīzul-ḥakīm'),
(5217, 65, 1, 'يٰٓاَيُّهَا النَّبِيُّ اِذَا طَلَّقْتُمُ النِّسَاۤءَ فَطَلِّقُوْهُنَّ لِعِدَّتِهِنَّ وَاَحْصُوا الْعِدَّةَۚ وَاتَّقُوا اللّٰهَ رَبَّكُمْۚ لَا تُخْرِجُوْهُنَّ مِنْۢ بُيُوْتِهِنَّ وَلَا يَخْرُجْنَ اِلَّآ اَنْ يَّأْتِيْنَ بِفَاحِشَةٍ مُّبَيِّنَةٍۗ وَتِلْكَ حُدُوْدُ اللّٰهِ ۗوَمَنْ يَّتَعَدَّ حُدُوْدَ اللّٰهِ فَقَدْ ظَلَمَ نَفْسَهٗ ۗ لَا تَدْرِيْ لَعَلَّ اللّٰهَ يُحْدِثُ بَعْدَ ذٰلِكَ اَمْرًا', 'yā ayyuhan-nabiyyu iżā ṭallaqtumun-nisā`a fa ṭalliqụhunna li\'iddatihinna wa aḥṣul-\'iddah, wattaqullāha rabbakum, lā tukhrijụhunna mim buyụtihinna wa lā yakhrujna illā ay ya`tīna bifāḥisyatim mubayyinah, wa tilka ḥudụdullāh, wa may yata\'adda ḥudụdallāhi fa qad ẓalama nafsah, lā tadrī la\'allallāha yuḥdiṡu ba\'da żālika amrā'),
(5218, 65, 2, 'فَاِذَا بَلَغْنَ اَجَلَهُنَّ فَاَمْسِكُوْهُنَّ بِمَعْرُوْفٍ اَوْ فَارِقُوْهُنَّ بِمَعْرُوْفٍ وَّاَشْهِدُوْا ذَوَيْ عَدْلٍ مِّنْكُمْ وَاَقِيْمُوا الشَّهَادَةَ لِلّٰهِ ۗذٰلِكُمْ يُوْعَظُ بِهٖ مَنْ كَانَ يُؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ ەۗ وَمَنْ يَّتَّقِ اللّٰهَ يَجْعَلْ لَّهٗ مَخْرَجًا ۙ', 'fa iżā balagna ajalahunna fa amsikụhunna bima\'rụfin au fāriqụhunna bima\'rụfiw wa asy-hidụ żawai \'adlim mingkum wa aqīmusy-syahādata lillāh, żālikum yụ\'aẓu bihī mang kāna yu`minu billāhi wal-yaumil-ākhir, wa may yattaqillāha yaj\'al lahụ makhrajā'),
(5219, 65, 3, 'وَّيَرْزُقْهُ مِنْ حَيْثُ لَا يَحْتَسِبُۗ وَمَنْ يَّتَوَكَّلْ عَلَى اللّٰهِ فَهُوَ حَسْبُهٗ ۗاِنَّ اللّٰهَ بَالِغُ اَمْرِهٖۗ قَدْ جَعَلَ اللّٰهُ لِكُلِّ شَيْءٍ قَدْرًا', 'wa yarzuq-hu min ḥaiṡu lā yaḥtasib, wa may yatawakkal \'alallāhi fa huwa ḥasbuh, innallāha bāligu amrih, qad ja\'alallāhu likulli syai`ing qadrā'),
(5220, 65, 4, 'وَالّٰۤـِٔيْ يَىِٕسْنَ مِنَ الْمَحِيْضِ مِنْ نِّسَاۤىِٕكُمْ اِنِ ارْتَبْتُمْ فَعِدَّتُهُنَّ ثَلٰثَةُ اَشْهُرٍۙ وَّالّٰۤـِٔيْ لَمْ يَحِضْنَۗ وَاُولَاتُ الْاَحْمَالِ اَجَلُهُنَّ اَنْ يَّضَعْنَ حَمْلَهُنَّۗ وَمَنْ يَّتَّقِ اللّٰهَ يَجْعَلْ لَّهٗ مِنْ اَمْرِهٖ يُسْرًا', 'wal-lā`i ya`isna minal-maḥīḍi min nisā`ikum inirtabtum fa \'iddatuhunna ṡalāṡatu asy-huriw wal-lā`i lam yahiḍn, wa ulātul-aḥmāli ajaluhunna ay yaḍa\'na ḥamlahunn, wa may yattaqillāha yaj\'al lahụ min amrihī yusrā'),
(5221, 65, 5, 'ذٰلِكَ اَمْرُ اللّٰهِ اَنْزَلَهٗٓ اِلَيْكُمْۗ وَمَنْ يَّتَّقِ اللّٰهَ يُكَفِّرْ عَنْهُ سَيِّاٰتِهٖ وَيُعْظِمْ لَهٗٓ اَجْرًا', 'żālika amrullāhi anzalahū ilaikum, wa may yattaqillāha yukaffir \'an-hu sayyi`ātihī wa yu\'ẓim lahū ajrā'),
(5222, 65, 6, 'اَسْكِنُوْهُنَّ مِنْ حَيْثُ سَكَنْتُمْ مِّنْ وُّجْدِكُمْ وَلَا تُضَاۤرُّوْهُنَّ لِتُضَيِّقُوْا عَلَيْهِنَّۗ وَاِنْ كُنَّ اُولَاتِ حَمْلٍ فَاَنْفِقُوْا عَلَيْهِنَّ حَتّٰى يَضَعْنَ حَمْلَهُنَّۚ فَاِنْ اَرْضَعْنَ لَكُمْ فَاٰتُوْهُنَّ اُجُوْرَهُنَّۚ وَأْتَمِرُوْا بَيْنَكُمْ بِمَعْرُوْفٍۚ وَاِنْ تَعَاسَرْتُمْ فَسَتُرْضِعُ لَهٗٓ اُخْرٰىۗ', 'askinụhunna min ḥaiṡu sakantum miw wujdikum wa lā tuḍārrụhunna lituḍayyiqụ \'alaihinn, wa ing kunna ulāti ḥamlin fa anfiqụ \'alaihinna ḥattā yaḍa\'na ḥamlahunn, fa in arḍa\'na lakum fa ātụhunna ujụrahunn, wa`tamirụ bainakum bima\'rụf, wa in ta\'āsartum fa saturḍi\'u lahū ukhrā'),
(5223, 65, 7, 'لِيُنْفِقْ ذُوْ سَعَةٍ مِّنْ سَعَتِهٖۗ وَمَنْ قُدِرَ عَلَيْهِ رِزْقُهٗ فَلْيُنْفِقْ مِمَّآ اٰتٰىهُ اللّٰهُ ۗ لَا يُكَلِّفُ اللّٰهُ نَفْسًا اِلَّا مَآ اٰتٰىهَاۗ سَيَجْعَلُ اللّٰهُ بَعْدَ عُسْرٍ يُّسْرًا', 'liyunfiq żụ sa\'atim min sa\'atih, wa mang qudira \'alaihi rizquhụ falyunfiq mimmā ātāhullāh, lā yukallifullāhu nafsan illā mā ātāhā, sayaj\'alullāhu ba\'da \'usriy yusrā'),
(5224, 65, 8, 'وَكَاَيِّنْ مِّنْ قَرْيَةٍ عَتَتْ عَنْ اَمْرِ رَبِّهَا وَرُسُلِهٖ فَحَاسَبْنٰهَا حِسَابًا شَدِيْدًاۙ وَّعَذَّبْنٰهَا عَذَابًا نُّكْرًا', 'wa ka`ayyim ming qaryatin \'atat \'an amri rabbihā wa rusulihī fa ḥāsabnāhā ḥisāban syadīdaw wa \'ażżabnāhā \'ażāban nukrā'),
(5225, 65, 9, 'فَذَاقَتْ وَبَالَ اَمْرِهَا وَكَانَ عَاقِبَةُ اَمْرِهَا خُسْرًا', 'fa żāqat wa bāla amrihā wa kāna \'āqibatu amrihā khusrā'),
(5226, 65, 10, 'اَعَدَّ اللّٰهُ لَهُمْ عَذَابًا شَدِيْدًا ۖفَاتَّقُوا اللّٰهَ يٰٓاُولِى الْاَلْبَابِۛ الَّذِيْنَ اٰمَنُوْا ۛ قَدْ اَنْزَلَ اللّٰهُ اِلَيْكُمْ ذِكْرًاۙ', 'a\'addallāhu lahum \'ażāban syadīdan fattaqullāha yā ulil-albāb, allażīna āmanụ qad anzalallāhu ilaikum żikrā'),
(5227, 65, 11, 'رَّسُوْلًا يَّتْلُوْا عَلَيْكُمْ اٰيٰتِ اللّٰهِ مُبَيِّنٰتٍ لِّيُخْرِجَ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ مِنَ الظُّلُمٰتِ اِلَى النُّوْرِۗ وَمَنْ يُّؤْمِنْۢ بِاللّٰهِ وَيَعْمَلْ صَالِحًا يُّدْخِلْهُ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًاۗ قَدْ اَحْسَنَ اللّٰهُ لَهٗ رِزْقًا', 'rasụlay yatlụ \'alaikum āyātillāhi mubayyinātil liyukhrijallażīna āmanụ wa \'amiluṣ-ṣāliḥāti minaẓ-ẓulumāti ilan-nụr, wa may yu`mim billāhi wa ya\'mal ṣāliḥay yudkhil-hu jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā abadā, qad aḥsanallāhu lahụ rizqā'),
(5228, 65, 12, 'اَللّٰهُ الَّذِيْ خَلَقَ سَبْعَ سَمٰوٰتٍ وَّمِنَ الْاَرْضِ مِثْلَهُنَّۗ يَتَنَزَّلُ الْاَمْرُ بَيْنَهُنَّ لِتَعْلَمُوْٓا اَنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ەۙ وَّاَنَّ اللّٰهَ قَدْ اَحَاطَ بِكُلِّ شَيْءٍ عِلْمًا', 'allāhullażī khalaqa sab\'a samāwātiw wa minal-arḍi miṡlahunn, yatanazzalul-amru bainahunna lita\'lamū annallāha \'alā kulli syai`ing qadīruw wa annallāha qad aḥāṭa bikulli syai`in \'ilmā'),
(5229, 66, 1, 'يٰٓاَيُّهَا النَّبِيُّ لِمَ تُحَرِّمُ مَآ اَحَلَّ اللّٰهُ لَكَۚ تَبْتَغِيْ مَرْضَاتَ اَزْوَاجِكَۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ', 'yā ayyuhan-nabiyyu lima tuḥarrimu mā aḥallallāhu lak, tabtagī marḍāta azwājik, wallāhu gafụrur raḥīm'),
(5230, 66, 2, 'قَدْ فَرَضَ اللّٰهُ لَكُمْ تَحِلَّةَ اَيْمَانِكُمْۚ وَاللّٰهُ مَوْلٰىكُمْۚ وَهُوَ الْعَلِيْمُ الْحَكِيْمُ', 'qad faraḍallāhu lakum taḥillata aimānikum, wallāhu maulākum, wa huwal-\'alīmul-ḥakīm'),
(5231, 66, 3, 'وَاِذْ اَسَرَّ النَّبِيُّ اِلٰى بَعْضِ اَزْوَاجِهٖ حَدِيْثًاۚ فَلَمَّا نَبَّاَتْ بِهٖ وَاَظْهَرَهُ اللّٰهُ عَلَيْهِ عَرَّفَ بَعْضَهٗ وَاَعْرَضَ عَنْۢ بَعْضٍۚ فَلَمَّا نَبَّاَهَا بِهٖ قَالَتْ مَنْ اَنْۢبَاَكَ هٰذَاۗ قَالَ نَبَّاَنِيَ الْعَلِيْمُ الْخَبِيْرُ', 'wa iż asarran-nabiyyu ilā ba\'ḍi azwājihī ḥadīṡā, fa lammā nabba`at bihī wa aẓ-harahullāhu \'alaihi \'arrafa ba\'ḍahụ wa a\'raḍa \'am ba\'ḍ, fa lammā nabba`ahā bihī qālat man amba`aka hāżā, qāla nabba`aniyal-\'alīmul-khabīr'),
(5232, 66, 4, 'اِنْ تَتُوْبَآ اِلَى اللّٰهِ فَقَدْ صَغَتْ قُلُوْبُكُمَاۚ وَاِنْ تَظٰهَرَا عَلَيْهِ فَاِنَّ اللّٰهَ هُوَ مَوْلٰىهُ وَجِبْرِيْلُ وَصَالِحُ الْمُؤْمِنِيْنَۚ وَالْمَلٰۤىِٕكَةُ بَعْدَ ذٰلِكَ ظَهِيْرٌ', 'in tatụbā ilallāhi fa qad ṣagat qulụbukumā, wa in taẓāharā \'alaihi fa innallāha huwa maulāhu wa jibrīlu wa ṣāliḥul-mu`minīn, wal-malā`ikatu ba\'da żālika ẓahīr'),
(5233, 66, 5, 'عَسٰى رَبُّهٗٓ اِنْ طَلَّقَكُنَّ اَنْ يُّبْدِلَهٗٓ اَزْوَاجًا خَيْرًا مِّنْكُنَّ مُسْلِمٰتٍ مُّؤْمِنٰتٍ قٰنِتٰتٍ تٰۤىِٕبٰتٍ عٰبِدٰتٍ سٰۤىِٕحٰتٍ ثَيِّبٰتٍ وَّاَبْكَارًا', '\'asā rabbuhū in ṭallaqakunna ay yubdilahū azwājan khairam mingkunna muslimātim mu`mināting qānitātin tā`ibātin \'ābidātin sā`iḥātin ṡayyibātiw wa abkārā'),
(5234, 66, 6, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا قُوْٓا اَنْفُسَكُمْ وَاَهْلِيْكُمْ نَارًا وَّقُوْدُهَا النَّاسُ وَالْحِجَارَةُ عَلَيْهَا مَلٰۤىِٕكَةٌ غِلَاظٌ شِدَادٌ لَّا يَعْصُوْنَ اللّٰهَ مَآ اَمَرَهُمْ وَيَفْعَلُوْنَ مَا يُؤْمَرُوْنَ', 'yā ayyuhallażīna āmanụ qū anfusakum wa ahlīkum nāraw wa qụduhan-nāsu wal-ḥijāratu \'alaihā malā`ikatun gilāẓun syidādul lā ya\'ṣụnallāha mā amarahum wa yaf\'alụna mā yu`marụn'),
(5235, 66, 7, 'يٰٓاَيُّهَا الَّذِيْنَ كَفَرُوْا لَا تَعْتَذِرُوا الْيَوْمَۗ اِنَّمَا تُجْزَوْنَ مَا كُنْتُمْ تَعْمَلُوْنَ', 'yā ayyuhallażīna kafarụ lā ta\'tażirul-yaụm, innamā tujzauna mā kuntum ta\'malụn'),
(5236, 66, 8, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا تُوْبُوْٓا اِلَى اللّٰهِ تَوْبَةً نَّصُوْحًاۗ عَسٰى رَبُّكُمْ اَنْ يُّكَفِّرَ عَنْكُمْ سَيِّاٰتِكُمْ وَيُدْخِلَكُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۙ يَوْمَ لَا يُخْزِى اللّٰهُ النَّبِيَّ وَالَّذِيْنَ اٰمَنُوْا مَعَهٗۚ نُوْرُهُمْ يَسْعٰى بَيْنَ اَيْدِيْهِمْ وَبِاَيْمَانِهِمْ يَقُوْلُوْنَ رَبَّنَآ اَتْمِمْ لَنَا نُوْرَنَا وَاغْفِرْ لَنَاۚ اِنَّكَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'yā ayyuhallażīna āmanụ tụbū ilallāhi taubatan naṣụḥā, \'asā rabbukum ay yukaffira \'angkum sayyi`ātikum wa yudkhilakum jannātin tajrī min taḥtihal-an-hāru yauma lā yukhzillāhun-nabiyya wallażīna āmanụ ma\'ah, nụruhum yas\'ā baina aidīhim wa bi`aimānihim yaqụlụna rabbanā atmim lanā nụranā wagfir lanā, innaka \'alā kulli syai`ing qadīr'),
(5237, 66, 9, 'يٰٓاَيُّهَا النَّبِيُّ جَاهِدِ الْكُفَّارَ وَالْمُنٰفِقِيْنَ وَاغْلُظْ عَلَيْهِمْۗ وَمَأْوٰىهُمْ جَهَنَّمُۗ وَبِئْسَ الْمَصِيْرُ', 'yā ayyuhan-nabiyyu jāhidil-kuffāra wal-munāfiqīna wagluẓ \'alaihim, wa ma`wāhum jahannam, wa bi`sal-maṣīr'),
(5238, 66, 10, 'ضَرَبَ اللّٰهُ مَثَلًا لِّلَّذِيْنَ كَفَرُوا امْرَاَتَ نُوْحٍ وَّامْرَاَتَ لُوْطٍۗ كَانَتَا تَحْتَ عَبْدَيْنِ مِنْ عِبَادِنَا صَالِحَيْنِ فَخَانَتٰهُمَا فَلَمْ يُغْنِيَا عَنْهُمَا مِنَ اللّٰهِ شَيْـًٔا وَّقِيْلَ ادْخُلَا النَّارَ مَعَ الدَّاخِلِيْنَ', 'ḍaraballāhu maṡalal lillażīna kafarumra`ata nụḥiw wamra`ata lụṭ, kānatā taḥta \'abdaini min \'ibādinā ṣāliḥaini fa khānatāhumā fa lam yugniyā \'an-humā minallāhi syai`aw wa qīladkhulan-nāra ma\'ad-dākhilīn'),
(5239, 66, 11, 'وَضَرَبَ اللّٰهُ مَثَلًا لِّلَّذِيْنَ اٰمَنُوا امْرَاَتَ فِرْعَوْنَۘ اِذْ قَالَتْ رَبِّ ابْنِ لِيْ عِنْدَكَ بَيْتًا فِى الْجَنَّةِ وَنَجِّنِيْ مِنْ فِرْعَوْنَ وَعَمَلِهٖ وَنَجِّنِيْ مِنَ الْقَوْمِ الظّٰلِمِيْنَۙ', 'wa ḍaraballāhu maṡalal lillażīna āmanumra`ata fir\'aụn, iż qālat rabbibni lī \'indaka baitan fil-jannati wa najjinī min fir\'auna wa \'amalihī wa najjinī minal-qaumiẓ-ẓālimīn'),
(5240, 66, 12, 'وَمَرْيَمَ ابْنَتَ عِمْرٰنَ الَّتِيْٓ اَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا فِيْهِ مِنْ رُّوْحِنَا وَصَدَّقَتْ بِكَلِمٰتِ رَبِّهَا وَكُتُبِهٖ وَكَانَتْ مِنَ الْقٰنِتِيْنَ ۔', 'wa maryamabnata \'imrānallatī aḥṣanat farjahā fa nafakhnā fīhi mir rụḥinā wa ṣaddaqat bikalimāti rabbihā wa kutubihī wa kānat minal-qānitīn'),
(5241, 67, 1, 'تَبٰرَكَ الَّذِيْ بِيَدِهِ الْمُلْكُۖ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌۙ', 'tabārakallażī biyadihil-mulku wa huwa \'alā kulli syai`ing qadīr'),
(5242, 67, 2, ' ۨالَّذِيْ خَلَقَ الْمَوْتَ وَالْحَيٰوةَ لِيَبْلُوَكُمْ اَيُّكُمْ اَحْسَنُ عَمَلًاۗ وَهُوَ الْعَزِيْزُ الْغَفُوْرُۙ', 'allażī khalaqal-mauta wal-ḥayāta liyabluwakum ayyukum aḥsanu \'amalā, wa huwal-\'azīzul-gafụr'),
(5243, 67, 3, 'الَّذِيْ خَلَقَ سَبْعَ سَمٰوٰتٍ طِبَاقًاۗ مَا تَرٰى فِيْ خَلْقِ الرَّحْمٰنِ مِنْ تَفٰوُتٍۗ فَارْجِعِ الْبَصَرَۙ هَلْ تَرٰى مِنْ فُطُوْرٍ', 'allażī khalaqa sab\'a samāwātin ṭibāqā, mā tarā fī khalqir-raḥmāni min tafāwut, farji\'il-baṣara hal tarā min fuṭụr'),
(5244, 67, 4, 'ثُمَّ ارْجِعِ الْبَصَرَ كَرَّتَيْنِ يَنْقَلِبْ اِلَيْكَ الْبَصَرُ خَاسِئًا وَّهُوَ حَسِيْرٌ', 'ṡummarji\'il-baṣara karrataini yangqalib ilaikal-baṣaru khāsi`aw wa huwa ḥasīr'),
(5245, 67, 5, 'وَلَقَدْ زَيَّنَّا السَّمَاۤءَ الدُّنْيَا بِمَصَابِيْحَ وَجَعَلْنٰهَا رُجُوْمًا لِّلشَّيٰطِيْنِ وَاَعْتَدْنَا لَهُمْ عَذَابَ السَّعِيْرِ', 'wa laqad zayyannas-samā`ad-dun-yā bimaṣābīḥa wa ja\'alnāhā rujụmal lisy-syayāṭīni wa a\'tadnā lahum \'ażābas-sa\'īr'),
(5246, 67, 6, 'وَلِلَّذِيْنَ كَفَرُوْا بِرَبِّهِمْ عَذَابُ جَهَنَّمَۗ وَبِئْسَ الْمَصِيْرُ', 'wa lillażīna kafarụ birabbihim \'ażābu jahannam, wa bi`sal-maṣīr'),
(5247, 67, 7, 'اِذَآ اُلْقُوْا فِيْهَا سَمِعُوْا لَهَا شَهِيْقًا وَّهِيَ تَفُوْرُۙ', 'iżā ulqụ fīhā sami\'ụ lahā syahīqaw wa hiya tafụr'),
(5248, 67, 8, 'تَكَادُ تَمَيَّزُ مِنَ الْغَيْظِۗ كُلَّمَآ اُلْقِيَ فِيْهَا فَوْجٌ سَاَلَهُمْ خَزَنَتُهَآ اَلَمْ يَأْتِكُمْ نَذِيْرٌۙ', 'takādu tamayyazu minal-gaīẓ, kullamā ulqiya fīhā faujun sa`alahum khazanatuhā a lam ya`tikum nażīr'),
(5249, 67, 9, 'قَالُوْا بَلٰى قَدْ جَاۤءَنَا نَذِيْرٌ ەۙ فَكَذَّبْنَا وَقُلْنَا مَا نَزَّلَ اللّٰهُ مِنْ شَيْءٍۖ اِنْ اَنْتُمْ اِلَّا فِيْ ضَلٰلٍ كَبِيْرٍ', 'qālụ balā qad jā`anā nażīrun fa każżabnā wa qulnā mā nazzalallāhu min syai`in in antum illā fī ḍalāling kabīr'),
(5250, 67, 10, 'وَقَالُوْا لَوْ كُنَّا نَسْمَعُ اَوْ نَعْقِلُ مَا كُنَّا فِيْٓ اَصْحٰبِ السَّعِيْرِ', 'wa qālụ lau kunnā nasma\'u au na\'qilu mā kunnā fī aṣ-ḥābis-sa\'īr'),
(5251, 67, 11, 'فَاعْتَرَفُوْا بِذَنْۢبِهِمْۚ فَسُحْقًا لِّاَصْحٰبِ السَّعِيْرِ', 'fa\'tarafụ biżambihim, fa suḥqal li`aṣ-ḥābis-sa\'īr'),
(5252, 67, 12, 'اِنَّ الَّذِيْنَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ لَهُمْ مَّغْفِرَةٌ وَّاَجْرٌ كَبِيْرٌ', 'innallażīna yakhsyauna rabbahum bil-gaibi lahum magfiratuw wa ajrung kabīr'),
(5253, 67, 13, 'وَاَسِرُّوْا قَوْلَكُمْ اَوِ اجْهَرُوْا بِهٖۗ اِنَّهٗ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ', 'wa asirrụ qaulakum awij-harụ bih, innahụ \'alīmum biżātiṣ-ṣudụr'),
(5254, 67, 14, 'اَلَا يَعْلَمُ مَنْ خَلَقَۗ وَهُوَ اللَّطِيْفُ الْخَبِيْرُ', 'alā ya\'lamu man khalaq, wa huwal-laṭīful-khabīr'),
(5255, 67, 15, 'هُوَ الَّذِيْ جَعَلَ لَكُمُ الْاَرْضَ ذَلُوْلًا فَامْشُوْا فِيْ مَنَاكِبِهَا وَكُلُوْا مِنْ رِّزْقِهٖۗ وَاِلَيْهِ النُّشُوْرُ', 'huwallażī ja\'ala lakumul-arḍa żalụlan famsyụ fī manākibihā wa kulụ mir rizqih, wa ilaihin-nusyụr'),
(5256, 67, 16, 'ءَاَمِنْتُمْ مَّنْ فِى السَّمَاۤءِ اَنْ يَّخْسِفَ بِكُمُ الْاَرْضَ فَاِذَا هِيَ تَمُوْرُۙ', 'a amintum man fis-samā`i ay yakhsifa bikumul-arḍa fa iżā hiya tamụr'),
(5257, 67, 17, 'اَمْ اَمِنْتُمْ مَّنْ فِى السَّمَاۤءِ اَنْ يُّرْسِلَ عَلَيْكُمْ حَاصِبًاۗ فَسَتَعْلَمُوْنَ كَيْفَ نَذِيْرِ', 'am amintum man fis-samā`i ay yursila \'alaikum ḥāṣibā, fa sata\'lamụna kaifa nażīr'),
(5258, 67, 18, 'وَلَقَدْ كَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْ فَكَيْفَ كَانَ نَكِيْرِ', 'wa laqad każżaballażīna ming qablihim fa kaifa kāna nakīr'),
(5259, 67, 19, 'اَوَلَمْ يَرَوْا اِلَى الطَّيْرِ فَوْقَهُمْ صٰۤفّٰتٍ وَّيَقْبِضْنَۘ مَا يُمْسِكُهُنَّ اِلَّا الرَّحْمٰنُۗ اِنَّهٗ بِكُلِّ شَيْءٍۢ بَصِيْرٌ', 'a wa lam yarau ilaṭ-ṭairi fauqahum ṣāffātiw wa yaqbiḍn, mā yumsikuhunna illar-raḥmān, innahụ bikulli syai`im baṣīr'),
(5260, 67, 20, 'اَمَّنْ هٰذَا الَّذِيْ هُوَ جُنْدٌ لَّكُمْ يَنْصُرُكُمْ مِّنْ دُوْنِ الرَّحْمٰنِۗ اِنِ الْكٰفِرُوْنَ اِلَّا فِيْ غُرُوْرٍۚ', 'am man hāżallażī huwa jundul lakum yanṣurukum min dụnir-raḥmān, inil-kāfirụna illā fī gurụr'),
(5261, 67, 21, 'اَمَّنْ هٰذَا الَّذِيْ يَرْزُقُكُمْ اِنْ اَمْسَكَ رِزْقَهٗ ۚ بَلْ لَّجُّوْا فِيْ عُتُوٍّ وَّنُفُوْرٍ', 'am man hāżallażī yarzuqukum in amsaka rizqah, bal lajjụ fī \'utuwwiw wa nufụr'),
(5262, 67, 22, 'اَفَمَنْ يَّمْشِيْ مُكِبًّا عَلٰى وَجْهِهٖٓ اَهْدٰىٓ اَمَّنْ يَّمْشِيْ سَوِيًّا عَلٰى صِرَاطٍ مُّسْتَقِيْمٍ', 'a fa may yamsyī mukibban \'alā waj-hihī ahdā am may yamsyī sawiyyan \'alā ṣirāṭim mustaqīm'),
(5263, 67, 23, 'قُلْ هُوَ الَّذِيْٓ اَنْشَاَكُمْ وَجَعَلَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٕدَةَۗ قَلِيْلًا مَّا تَشْكُرُوْنَ', 'qul huwallażī ansya`akum wa ja\'ala lakumus-sam\'a wal-abṣāra wal-af`idah, qalīlam mā tasykurụn'),
(5264, 67, 24, 'قُلْ هُوَ الَّذِيْ ذَرَاَكُمْ فِى الْاَرْضِ وَاِلَيْهِ تُحْشَرُوْنَ', 'qul huwallażī żara`akum fil-arḍi wa ilaihi tuḥsyarụn'),
(5265, 67, 25, 'وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'wa yaqụlụna matā hāżal-wa\'du ing kuntum ṣādiqīn'),
(5266, 67, 26, 'قُلْ اِنَّمَا الْعِلْمُ عِنْدَ اللّٰهِ ۖوَاِنَّمَآ اَنَا۠ نَذِيْرٌ مُّبِيْنٌ', 'qul innamal-\'ilmu \'indallāhi wa innamā ana nażīrum mubīn'),
(5267, 67, 27, 'فَلَمَّا رَاَوْهُ زُلْفَةً سِيْۤـَٔتْ وُجُوْهُ الَّذِيْنَ كَفَرُوْا وَقِيْلَ هٰذَا الَّذِيْ كُنْتُمْ بِهٖ تَدَّعُوْنَ', 'fa lammā ra`auhu zulfatan sī`at wujụhullażīna kafarụ wa qīla hāżallażī kuntum bihī tadda\'ụn'),
(5268, 67, 28, 'قُلْ اَرَءَيْتُمْ اِنْ اَهْلَكَنِيَ اللّٰهُ وَمَنْ مَّعِيَ اَوْ رَحِمَنَاۙ فَمَنْ يُّجِيْرُ الْكٰفِرِيْنَ مِنْ عَذَابٍ اَلِيْمٍ', 'qul ara`aitum in ahlakaniyallāhu wa mam ma\'iya au raḥimanā fa may yujīrul-kāfirīna min \'ażābin alīm'),
(5269, 67, 29, 'قُلْ هُوَ الرَّحْمٰنُ اٰمَنَّا بِهٖ وَعَلَيْهِ تَوَكَّلْنَاۚ فَسَتَعْلَمُوْنَ مَنْ هُوَ فِيْ ضَلٰلٍ مُّبِيْنٍ', 'qul huwar-raḥmānu āmannā bihī wa \'alaihi tawakkalnā, fa sata\'lamụna man huwa fī ḍalālim mubīn'),
(5270, 67, 30, 'قُلْ اَرَءَيْتُمْ اِنْ اَصْبَحَ مَاۤؤُكُمْ غَوْرًا فَمَنْ يَّأْتِيْكُمْ بِمَاۤءٍ مَّعِيْنٍ', 'qul ara`aitum in aṣbaḥa mā`ukum gauran fa may ya`tīkum bimā`im ma\'īn'),
(5271, 68, 1, 'نۤ ۚوَالْقَلَمِ وَمَا يَسْطُرُوْنَۙ', 'nūn, wal-qalami wa mā yasṭurụn'),
(5272, 68, 2, 'مَآ اَنْتَ بِنِعْمَةِ رَبِّكَ بِمَجْنُوْنٍ', 'mā anta bini\'mati rabbika bimajnụn'),
(5273, 68, 3, 'وَاِنَّ لَكَ لَاَجْرًا غَيْرَ مَمْنُوْنٍۚ', 'wa inna laka la`ajran gaira mamnụn'),
(5274, 68, 4, 'وَاِنَّكَ لَعَلٰى خُلُقٍ عَظِيْمٍ', 'wa innaka la\'alā khuluqin \'aẓīm'),
(5275, 68, 5, 'فَسَتُبْصِرُ وَيُبْصِرُوْنَۙ', 'fa satubṣiru wa yubṣirụn'),
(5276, 68, 6, 'بِاَيِّىكُمُ الْمَفْتُوْنُ', 'bi`ayyikumul-maftụn'),
(5277, 68, 7, 'اِنَّ رَبَّكَ هُوَ اَعْلَمُ بِمَنْ ضَلَّ عَنْ سَبِيْلِهٖۖ وَهُوَ اَعْلَمُ بِالْمُهْتَدِيْنَ', 'inna rabbaka huwa a\'lamu biman ḍalla \'an sabīlihī wa huwa a\'lamu bil-muhtadīn'),
(5278, 68, 8, 'فَلَا تُطِعِ الْمُكَذِّبِيْنَ', 'fa lā tuṭi\'il-mukażżibīn'),
(5279, 68, 9, 'وَدُّوْا لَوْ تُدْهِنُ فَيُدْهِنُوْنَۚ', 'waddụ lau tud-hinu fa yud-hinụn'),
(5280, 68, 10, 'وَلَا تُطِعْ كُلَّ حَلَّافٍ مَّهِيْنٍۙ', 'wa lā tuṭi\' kulla ḥallāfim mahīn'),
(5281, 68, 11, 'هَمَّازٍ مَّشَّاۤءٍۢ بِنَمِيْمٍۙ', 'hammāzim masysyā`im binamīm'),
(5282, 68, 12, 'مَّنَّاعٍ لِّلْخَيْرِ مُعْتَدٍ اَثِيْمٍۙ', 'mannā\'il lil-khairi mu\'tadin aṡīm'),
(5283, 68, 13, 'عُتُلٍّۢ بَعْدَ ذٰلِكَ زَنِيْمٍۙ', '\'utullim ba\'da żālika zanīm'),
(5284, 68, 14, 'اَنْ كَانَ ذَا مَالٍ وَّبَنِيْنَۗ', 'ang kāna żā māliw wa banīn'),
(5285, 68, 15, 'اِذَا تُتْلٰى عَلَيْهِ اٰيٰتُنَا قَالَ اَسَاطِيْرُ الْاَوَّلِيْنَۗ', 'iżā tutlā \'alaihi āyātunā qāla asāṭīrul-awwalīn'),
(5286, 68, 16, 'سَنَسِمُهٗ عَلَى الْخُرْطُوْمِ', 'sanasimuhụ \'alal-khurṭụm'),
(5287, 68, 17, 'اِنَّا بَلَوْنٰهُمْ كَمَا بَلَوْنَآ اَصْحٰبَ الْجَنَّةِۚ اِذْ اَقْسَمُوْا لَيَصْرِمُنَّهَا مُصْبِحِيْنَۙ', 'innā balaunāhum kamā balaunā aṣ-ḥābal-jannah, iż aqsamụ layaṣrimunnahā muṣbiḥīn'),
(5288, 68, 18, 'وَلَا يَسْتَثْنُوْنَ', 'wa lā yastaṡnụn'),
(5289, 68, 19, 'فَطَافَ عَلَيْهَا طَاۤىِٕفٌ مِّنْ رَّبِّكَ وَهُمْ نَاۤىِٕمُوْنَ', 'fa ṭāfa \'alaihā ṭā`ifum mir rabbika wa hum nā`imụn'),
(5290, 68, 20, 'فَاَصْبَحَتْ كَالصَّرِيْمِۙ', 'fa aṣbaḥat kaṣ-ṣarīm'),
(5291, 68, 21, 'فَتَنَادَوْا مُصْبِحِيْنَۙ', 'fa tanādau muṣbiḥīn'),
(5292, 68, 22, 'اَنِ اغْدُوْا عَلٰى حَرْثِكُمْ اِنْ كُنْتُمْ صَارِمِيْنَ', 'anigdụ \'alā ḥarṡikum ing kuntum ṣārimīn'),
(5293, 68, 23, 'فَانْطَلَقُوْا وَهُمْ يَتَخَافَتُوْنَۙ', 'fanṭalaqụ wa hum yatakhāfatụn'),
(5294, 68, 24, 'اَنْ لَّا يَدْخُلَنَّهَا الْيَوْمَ عَلَيْكُمْ مِّسْكِيْنٌۙ', 'al lā yadkhulannahal-yauma \'alaikum miskīn'),
(5295, 68, 25, 'وَّغَدَوْا عَلٰى حَرْدٍ قَادِرِيْنَ', 'wa gadau \'alā ḥarding qādirīn'),
(5296, 68, 26, 'فَلَمَّا رَاَوْهَا قَالُوْٓا اِنَّا لَضَاۤلُّوْنَۙ', 'fa lammā ra`auhā qālū innā laḍāllụn'),
(5297, 68, 27, 'بَلْ نَحْنُ مَحْرُوْمُوْنَ', 'bal naḥnu maḥrụmụn'),
(5298, 68, 28, 'قَالَ اَوْسَطُهُمْ اَلَمْ اَقُلْ لَّكُمْ لَوْلَا تُسَبِّحُوْنَ', 'qāla ausaṭuhum a lam aqul lakum lau lā tusabbiḥụn'),
(5299, 68, 29, 'قَالُوْا سُبْحٰنَ رَبِّنَآ اِنَّا كُنَّا ظٰلِمِيْنَ', 'qālụ sub-ḥāna rabbinā innā kunnā ẓālimīn'),
(5300, 68, 30, 'فَاَقْبَلَ بَعْضُهُمْ عَلٰى بَعْضٍ يَّتَلَاوَمُوْنَ', 'fa aqbala ba\'ḍuhum \'alā ba\'ḍiy yatalāwamụn'),
(5301, 68, 31, 'قَالُوْا يٰوَيْلَنَآ اِنَّا كُنَّا طٰغِيْنَ', 'qālụ yā wailanā innā kunnā ṭāgīn'),
(5302, 68, 32, 'عَسٰى رَبُّنَآ اَنْ يُّبْدِلَنَا خَيْرًا مِّنْهَآ اِنَّآ اِلٰى رَبِّنَا رَاغِبُوْنَ', '\'asā rabbunā ay yubdilanā khairam min-hā innā ilā rabbinā rāgibụn'),
(5303, 68, 33, 'كَذٰلِكَ الْعَذَابُۗ وَلَعَذَابُ الْاٰخِرَةِ اَكْبَرُۘ لَوْ كَانُوْا يَعْلَمُوْنَ', 'każālikal-\'ażāb, wa la\'ażābul-ākhirati akbar, lau kānụ ya\'lamụn'),
(5304, 68, 34, 'اِنَّ لِلْمُتَّقِيْنَ عِنْدَ رَبِّهِمْ جَنّٰتِ النَّعِيْمِ', 'inna lil-muttaqīna \'inda rabbihim jannātin na\'īm'),
(5305, 68, 35, 'اَفَنَجْعَلُ الْمُسْلِمِيْنَ كَالْمُجْرِمِيْنَۗ', 'a fa naj\'alul-muslimīna kal-mujrimīn'),
(5306, 68, 36, 'مَا لَكُمْۗ كَيْفَ تَحْكُمُوْنَۚ', 'mā lakum, kaifa taḥkumụn'),
(5307, 68, 37, 'اَمْ لَكُمْ كِتٰبٌ فِيْهِ تَدْرُسُوْنَۙ', 'am lakum kitābun fīhi tadrusụn'),
(5308, 68, 38, 'اِنَّ لَكُمْ فِيْهِ لَمَا تَخَيَّرُوْنَۚ', 'inna lakum fīhi lamā takhayyarụn'),
(5309, 68, 39, 'اَمْ لَكُمْ اَيْمَانٌ عَلَيْنَا بَالِغَةٌ اِلٰى يَوْمِ الْقِيٰمَةِۙ اِنَّ لَكُمْ لَمَا تَحْكُمُوْنَۚ', 'am lakum aimānun \'alainā bāligatun ilā yaumil-qiyāmati inna lakum lamā taḥkumụn'),
(5310, 68, 40, 'سَلْهُمْ اَيُّهُمْ بِذٰلِكَ زَعِيْمٌۚ', 'sal-hum ayyuhum biżālika za\'īm'),
(5311, 68, 41, 'اَمْ لَهُمْ شُرَكَاۤءُۚ فَلْيَأْتُوْا بِشُرَكَاۤىِٕهِمْ اِنْ كَانُوْا صٰدِقِيْنَ', 'am lahum syurakā`, falya`tụ bisyurakā`ihim ing kānụ ṣādiqīn'),
(5312, 68, 42, 'يَوْمَ يُكْشَفُ عَنْ سَاقٍ وَّيُدْعَوْنَ اِلَى السُّجُوْدِ فَلَا يَسْتَطِيْعُوْنَۙ', 'yauma yuksyafu \'an sāqiw wa yud\'auna ilas-sujụdi fa lā yastaṭī\'ụn'),
(5313, 68, 43, 'خَاشِعَةً اَبْصَارُهُمْ تَرْهَقُهُمْ ذِلَّةٌ ۗوَقَدْ كَانُوْا يُدْعَوْنَ اِلَى السُّجُوْدِ وَهُمْ سَالِمُوْنَ', 'khāsyi\'atan abṣāruhum tar-haquhum żillah, wa qad kānụ yud\'auna ilas-sujụdi wa hum sālimụn'),
(5314, 68, 44, 'فَذَرْنِيْ وَمَنْ يُّكَذِّبُ بِهٰذَا الْحَدِيْثِۗ سَنَسْتَدْرِجُهُمْ مِّنْ حَيْثُ لَا يَعْلَمُوْنَۙ', 'fa żarnī wa may yukażżibu bihāżal-ḥadīṡ, sanastadrijuhum min ḥaiṡu lā ya\'lamụn'),
(5315, 68, 45, 'وَاُمْلِيْ لَهُمْۗ اِنَّ كَيْدِيْ مَتِيْنٌ', 'wa umlī lahum, inna kaidī matīn'),
(5316, 68, 46, 'اَمْ تَسْـَٔلُهُمْ اَجْرًا فَهُمْ مِّنْ مَّغْرَمٍ مُّثْقَلُوْنَۚ', 'am tas`aluhum ajran fa hum mim magramim muṡqalụn'),
(5317, 68, 47, 'اَمْ عِنْدَهُمُ الْغَيْبُ فَهُمْ يَكْتُبُوْنَ', 'am \'indahumul-gaibu fa hum yaktubụn'),
(5318, 68, 48, 'فَاصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تَكُنْ كَصَاحِبِ الْحُوْتِۘ اِذْ نَادٰى وَهُوَ مَكْظُوْمٌۗ', 'faṣbir liḥukmi rabbika wa lā takung kaṣāḥibil-ḥụt, iż nādā wa huwa makẓụm'),
(5319, 68, 49, 'لَوْلَآ اَنْ تَدَارَكَهٗ نِعْمَةٌ مِّنْ رَّبِّهٖ لَنُبِذَ بِالْعَرَاۤءِ وَهُوَ مَذْمُوْمٌ', 'lau lā an tadārakahụ ni\'matum mir rabbihī lanubiża bil-\'arā`i wa huwa mażmụm'),
(5320, 68, 50, 'فَاجْتَبٰىهُ رَبُّهٗ فَجَعَلَهٗ مِنَ الصّٰلِحِيْنَ', 'fajtabāhu rabbuhụ fa ja\'alahụ minaṣ-ṣāliḥīn'),
(5321, 68, 51, 'وَاِنْ يَّكَادُ الَّذِيْنَ كَفَرُوْا لَيُزْلِقُوْنَكَ بِاَبْصَارِهِمْ لَمَّا سَمِعُوا الذِّكْرَ وَيَقُوْلُوْنَ اِنَّهٗ لَمَجْنُوْنٌ ۘ', 'wa iy yakādullażīna kafarụ layuzliqụnaka bi`abṣārihim lammā sami\'uż-żikra wa yaqụlụna innahụ lamajnụn'),
(5322, 68, 52, 'وَمَا هُوَ اِلَّا ذِكْرٌ لِّلْعٰلَمِيْنَ', 'wa mā huwa illā żikrul lil-\'ālamīn'),
(5323, 69, 1, 'اَلْحَاۤقَّةُۙ', 'al-ḥāqqah'),
(5324, 69, 2, 'مَا الْحَاۤقَّةُ ۚ', 'mal-ḥāqqah'),
(5325, 69, 3, 'وَمَآ اَدْرٰىكَ مَا الْحَاۤقَّةُ ۗ', 'wa mā adrāka mal-ḥāqqah'),
(5326, 69, 4, 'كَذَّبَتْ ثَمُوْدُ وَعَادٌ ۢبِالْقَارِعَةِ', 'każżabaṡ ṡamụdu wa \'ādum bil-qāri\'ah'),
(5327, 69, 5, 'فَاَمَّا ثَمُوْدُ فَاُهْلِكُوْا بِالطَّاغِيَةِ', 'fa ammā ṡamụdu fa uhlikụ biṭ-ṭāgiyah'),
(5328, 69, 6, 'وَاَمَّا عَادٌ فَاُهْلِكُوْا بِرِيْحٍ صَرْصَرٍ عَاتِيَةٍۙ', 'wa ammā \'ādun fa uhlikụ birīḥin ṣarṣarin \'ātiyah'),
(5329, 69, 7, 'سَخَّرَهَا عَلَيْهِمْ سَبْعَ لَيَالٍ وَّثَمٰنِيَةَ اَيَّامٍۙ حُسُوْمًا فَتَرَى الْقَوْمَ فِيْهَا صَرْعٰىۙ كَاَنَّهُمْ اَعْجَازُ نَخْلٍ خَاوِيَةٍۚ', 'sakhkharahā \'alaihim sab\'a layāliw wa ṡamāniyata ayyāmin ḥusụman fa taral-qauma fīhā ṣar\'ā ka`annahum a\'jāzu nakhlin khāwiyah'),
(5330, 69, 8, 'فَهَلْ تَرٰى لَهُمْ مِّنْۢ بَاقِيَةٍ', 'fa hal tarā lahum mim bāqiyah'),
(5331, 69, 9, 'وَجَاۤءَ فِرْعَوْنُ وَمَنْ قَبْلَهٗ وَالْمُؤْتَفِكٰتُ بِالْخَاطِئَةِۚ', 'wa jā`a fir\'aunu wa mang qablahụ wal-mu`tafikātu bil-khāṭi`ah'),
(5332, 69, 10, 'فَعَصَوْا رَسُوْلَ رَبِّهِمْ فَاَخَذَهُمْ اَخْذَةً رَّابِيَةً', 'fa \'aṣau rasụla rabbihim fa akhażahum akhżatar rābiyah'),
(5333, 69, 11, 'اِنَّا لَمَّا طَغَا الْمَاۤءُ حَمَلْنٰكُمْ فِى الْجَارِيَةِۙ', 'innā lammā ṭagal-mā`u ḥamalnākum fil-jāriyah'),
(5334, 69, 12, 'لِنَجْعَلَهَا لَكُمْ تَذْكِرَةً وَّتَعِيَهَآ اُذُنٌ وَّاعِيَةٌ', 'linaj\'alahā lakum tażkirataw wa ta\'iyahā użunuw wā\'iyah'),
(5335, 69, 13, 'فَاِذَا نُفِخَ فِى الصُّوْرِ نَفْخَةٌ وَّاحِدَةٌ ۙ', 'fa iżā nufikha fiṣ-ṣụri nafkhatuw wāḥidah'),
(5336, 69, 14, 'وَّحُمِلَتِ الْاَرْضُ وَالْجِبَالُ فَدُكَّتَا دَكَّةً وَّاحِدَةًۙ', 'wa ḥumilatil-arḍu wal-jibālu fa dukkatā dakkataw wāḥidah'),
(5337, 69, 15, 'فَيَوْمَىِٕذٍ وَّقَعَتِ الْوَاقِعَةُۙ', 'fa yauma`iżiw waqa\'atil-wāqi\'ah'),
(5338, 69, 16, 'وَانْشَقَّتِ السَّمَاۤءُ فَهِيَ يَوْمَىِٕذٍ وَّاهِيَةٌۙ', 'wansyaqqatis-samā`u fa hiya yauma`iżiw wāhiyah'),
(5339, 69, 17, 'وَّالْمَلَكُ عَلٰٓى اَرْجَاۤىِٕهَاۗ وَيَحْمِلُ عَرْشَ رَبِّكَ فَوْقَهُمْ يَوْمَىِٕذٍ ثَمٰنِيَةٌ ۗ', 'wal-malaku \'alā arjā`ihā, wa yaḥmilu \'arsya rabbika fauqahum yauma`iżin ṡamāniyah'),
(5340, 69, 18, 'يَوْمَىِٕذٍ تُعْرَضُوْنَ لَا تَخْفٰى مِنْكُمْ خَافِيَةٌ', 'yauma`iżin tu\'raḍụna lā takhfā mingkum khāfiyah'),
(5341, 69, 19, 'فَاَمَّا مَنْ اُوْتِيَ كِتٰبَهٗ بِيَمِيْنِهٖ فَيَقُوْلُ هَاۤؤُمُ اقْرَءُوْا كِتٰبِيَهْۚ', 'fa ammā man ụtiya kitābahụ biyamīnihī fa yaqụlu hā`umuqra`ụ kitābiyah'),
(5342, 69, 20, 'اِنِّيْ ظَنَنْتُ اَنِّيْ مُلٰقٍ حِسَابِيَهْۚ', 'innī ẓanantu annī mulāqin ḥisābiyah'),
(5343, 69, 21, 'فَهُوَ فِيْ عِيْشَةٍ رَّاضِيَةٍۚ', 'fa huwa fī \'īsyatir rāḍiyah'),
(5344, 69, 22, 'فِيْ جَنَّةٍ عَالِيَةٍۙ', 'fī jannatin \'āliyah'),
(5345, 69, 23, 'قُطُوْفُهَا دَانِيَةٌ', 'quṭụfuhā dāniyah'),
(5346, 69, 24, 'كُلُوْا وَاشْرَبُوْا هَنِيْۤـًٔا ۢبِمَآ اَسْلَفْتُمْ فِى الْاَيَّامِ الْخَالِيَةِ', 'kulụ wasyrabụ hanī`am bimā aslaftum fil-ayyāmil-khāliyah'),
(5347, 69, 25, 'وَاَمَّا مَنْ اُوْتِيَ كِتٰبَهٗ بِشِمَالِهٖ ەۙ فَيَقُوْلُ يٰلَيْتَنِيْ لَمْ اُوْتَ كِتٰبِيَهْۚ', 'wa ammā man ụtiya kitābahụ bisyimālihī fa yaqụlu yā laitanī lam ụta kitābiyah'),
(5348, 69, 26, 'وَلَمْ اَدْرِ مَا حِسَابِيَهْۚ', 'wa lam adri mā ḥisābiyah'),
(5349, 69, 27, 'يٰلَيْتَهَا كَانَتِ الْقَاضِيَةَۚ', 'yā laitahā kānatil-qāḍiyah'),
(5350, 69, 28, 'مَآ اَغْنٰى عَنِّيْ مَالِيَهْۚ', 'mā agnā \'annī māliyah'),
(5351, 69, 29, 'هَلَكَ عَنِّيْ سُلْطٰنِيَهْۚ', 'halaka \'annī sulṭāniyah'),
(5352, 69, 30, 'خُذُوْهُ فَغُلُّوْهُۙ', 'khużụhu fa gullụh'),
(5353, 69, 31, 'ثُمَّ الْجَحِيْمَ صَلُّوْهُۙ', 'ṡummal-jaḥīma ṣallụh'),
(5354, 69, 32, 'ثُمَّ فِيْ سِلْسِلَةٍ ذَرْعُهَا سَبْعُوْنَ ذِرَاعًا فَاسْلُكُوْهُۗ', 'ṡumma fī silsilatin żar\'uhā sab\'ụna żirā\'an faslukụh'),
(5355, 69, 33, 'اِنَّهٗ كَانَ لَا يُؤْمِنُ بِاللّٰهِ الْعَظِيْمِۙ', 'innahụ kāna lā yu`minu billāhil-\'aẓīm'),
(5356, 69, 34, 'وَلَا يَحُضُّ عَلٰى طَعَامِ الْمِسْكِيْنِۗ', 'wa lā yaḥuḍḍu \'alā ṭa\'āmil-miskīn'),
(5357, 69, 35, 'فَلَيْسَ لَهُ الْيَوْمَ هٰهُنَا حَمِيْمٌۙ', 'fa laisa lahul-yauma hāhunā ḥamīm'),
(5358, 69, 36, 'وَّلَا طَعَامٌ اِلَّا مِنْ غِسْلِيْنٍۙ', 'wa lā ṭa\'āmun illā min gislīn'),
(5359, 69, 37, 'لَّا يَأْكُلُهٗٓ اِلَّا الْخَاطِـُٔوْنَ', 'lā ya`kuluhū illal-khāṭi`ụn'),
(5360, 69, 38, 'فَلَآ اُقْسِمُ بِمَا تُبْصِرُوْنَۙ', 'fa lā uqsimu bimā tubṣirụn'),
(5361, 69, 39, 'وَمَا لَا تُبْصِرُوْنَۙ', 'wa mā lā tubṣirụn'),
(5362, 69, 40, 'اِنَّهٗ لَقَوْلُ رَسُوْلٍ كَرِيْمٍۙ', 'innahụ laqaulu rasụling karīm'),
(5363, 69, 41, 'وَّمَا هُوَ بِقَوْلِ شَاعِرٍۗ قَلِيْلًا مَّا تُؤْمِنُوْنَۙ', 'wa mā huwa biqauli syā\'ir, qalīlam mā tu`minụn'),
(5364, 69, 42, 'وَلَا بِقَوْلِ كَاهِنٍۗ قَلِيْلًا مَّا تَذَكَّرُوْنَۗ', 'wa lā biqauli kāhin, qalīlam mā tażakkarụn'),
(5365, 69, 43, 'تَنْزِيْلٌ مِّنْ رَّبِّ الْعٰلَمِيْنَ', 'tanzīlum mir rabbil-\'ālamīn'),
(5366, 69, 44, 'وَلَوْ تَقَوَّلَ عَلَيْنَا بَعْضَ الْاَقَاوِيْلِۙ', 'walau taqawwala \'alainā ba\'ḍal-aqāwīl'),
(5367, 69, 45, 'لَاَخَذْنَا مِنْهُ بِالْيَمِيْنِۙ', 'la`akhażnā min-hu bil-yamīn'),
(5368, 69, 46, 'ثُمَّ لَقَطَعْنَا مِنْهُ الْوَتِيْنَۖ', 'ṡumma laqaṭa\'nā min-hul-watīn');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(5369, 69, 47, 'فَمَا مِنْكُمْ مِّنْ اَحَدٍ عَنْهُ حَاجِزِيْنَۙ', 'fa mā mingkum min aḥadin \'an-hu ḥājizīn'),
(5370, 69, 48, 'وَاِنَّهٗ لَتَذْكِرَةٌ لِّلْمُتَّقِيْنَ', 'wa innahụ latażkiratul lil-muttaqīn'),
(5371, 69, 49, 'وَاِنَّا لَنَعْلَمُ اَنَّ مِنْكُمْ مُّكَذِّبِيْنَۗ', 'wa innā lana\'lamu anna mingkum mukażżibīn'),
(5372, 69, 50, 'وَاِنَّهٗ لَحَسْرَةٌ عَلَى الْكٰفِرِيْنَۚ', 'wa innahụ laḥasratun \'alal-kāfirīn'),
(5373, 69, 51, 'وَاِنَّهٗ لَحَقُّ الْيَقِيْنِ', 'wa innahụ laḥaqqul-yaqīn'),
(5374, 69, 52, 'فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيْمِ', 'fa sabbiḥ bismi rabbikal-\'aẓīm'),
(5375, 70, 1, 'سَاَلَ سَاۤىِٕلٌۢ بِعَذَابٍ وَّاقِعٍۙ', 'sa`ala sā`ilum bi\'ażābiw wāqi\''),
(5376, 70, 2, 'لِّلْكٰفِرِيْنَ لَيْسَ لَهٗ دَافِعٌۙ', 'lil-kāfirīna laisa lahụ dāfi\''),
(5377, 70, 3, 'مِّنَ اللّٰهِ ذِى الْمَعَارِجِۗ', 'minallāhi żil-ma\'ārij'),
(5378, 70, 4, 'تَعْرُجُ الْمَلٰۤىِٕكَةُ وَالرُّوْحُ اِلَيْهِ فِيْ يَوْمٍ كَانَ مِقْدَارُهٗ خَمْسِيْنَ اَلْفَ سَنَةٍۚ', 'ta\'rujul-malā`ikatu war-rụḥu ilaihi fī yauming kāna miqdāruhụ khamsīna alfa sanah'),
(5379, 70, 5, 'فَاصْبِرْ صَبْرًا جَمِيْلًا', 'faṣbir ṣabran jamīlā'),
(5380, 70, 6, 'اِنَّهُمْ يَرَوْنَهٗ بَعِيْدًاۙ', 'innahum yaraunahụ ba\'īdā'),
(5381, 70, 7, 'وَّنَرٰىهُ قَرِيْبًاۗ', 'wa narāhu qarībā'),
(5382, 70, 8, 'يَوْمَ تَكُوْنُ السَّمَاۤءُ كَالْمُهْلِۙ', 'yauma takụnus-samā`u kal-muhl'),
(5383, 70, 9, 'وَتَكُوْنُ الْجِبَالُ كَالْعِهْنِۙ', 'wa takụnul-jibālu kal-\'ihn'),
(5384, 70, 10, 'وَلَا يَسْـَٔلُ حَمِيْمٌ حَمِيْمًاۚ', 'wa lā yas`alu ḥamīmun ḥamīmā'),
(5385, 70, 11, 'يُبَصَّرُوْنَهُمْۗ يَوَدُّ الْمُجْرِمُ لَوْ يَفْتَدِيْ مِنْ عَذَابِ يَوْمِىِٕذٍۢ بِبَنِيْهِۙ', 'yubaṣṣarụnahum, yawaddul-mujrimu lau yaftadī min \'ażābi yaumi`iżim bibanīh'),
(5386, 70, 12, 'وَصَاحِبَتِهٖ وَاَخِيْهِۙ', 'wa ṣāḥibatihī wa akhīh'),
(5387, 70, 13, 'وَفَصِيْلَتِهِ الَّتِيْ تُـْٔوِيْهِۙ', 'wa faṣīlatihillatī tu`wīh'),
(5388, 70, 14, 'وَمَنْ فِى الْاَرْضِ جَمِيْعًاۙ ثُمَّ يُنْجِيْهِۙ', 'wa man fil-arḍi jamī\'an ṡumma yunjīh'),
(5389, 70, 15, 'كَلَّاۗ اِنَّهَا لَظٰىۙ', 'kallā, innahā laẓā'),
(5390, 70, 16, 'نَزَّاعَةً لِّلشَّوٰىۚ', 'nazzā\'atal lisy-syawā'),
(5391, 70, 17, 'تَدْعُوْا مَنْ اَدْبَرَ وَتَوَلّٰىۙ', 'tad\'ụ man adbara wa tawallā'),
(5392, 70, 18, 'وَجَمَعَ فَاَوْعٰى', 'wa jama\'a fa au\'ā'),
(5393, 70, 19, '۞ اِنَّ الْاِنْسَانَ خُلِقَ هَلُوْعًاۙ', 'innal-insāna khuliqa halụ\'ā'),
(5394, 70, 20, 'اِذَا مَسَّهُ الشَّرُّ جَزُوْعًاۙ', 'iżā massahusy-syarru jazụ\'ā'),
(5395, 70, 21, 'وَّاِذَا مَسَّهُ الْخَيْرُ مَنُوْعًاۙ', 'wa iżā massahul-khairu manụ\'ā'),
(5396, 70, 22, 'اِلَّا الْمُصَلِّيْنَۙ', 'illal-muṣallīn'),
(5397, 70, 23, 'الَّذِيْنَ هُمْ عَلٰى صَلَاتِهِمْ دَاۤىِٕمُوْنَۖ', 'allażīna hum \'alā ṣalātihim dā`imụn'),
(5398, 70, 24, 'وَالَّذِيْنَ فِيْٓ اَمْوَالِهِمْ حَقٌّ مَّعْلُوْمٌۖ', 'wallażīna fī amwālihim ḥaqqum ma\'lụm'),
(5399, 70, 25, 'لِّلسَّاۤىِٕلِ وَالْمَحْرُوْمِۖ', 'lis-sā`ili wal-maḥrụm'),
(5400, 70, 26, 'وَالَّذِيْنَ يُصَدِّقُوْنَ بِيَوْمِ الدِّيْنِۖ', 'wallażīna yuṣaddiqụna biyaumid-dīn'),
(5401, 70, 27, 'وَالَّذِيْنَ هُمْ مِّنْ عَذَابِ رَبِّهِمْ مُّشْفِقُوْنَۚ', 'wallażīna hum min \'ażābi rabbihim musyfiqụn'),
(5402, 70, 28, 'اِنَّ عَذَابَ رَبِّهِمْ غَيْرُ مَأْمُوْنٍۖ', 'inna \'ażāba rabbihim gairu ma`mụn'),
(5403, 70, 29, 'وَّالَّذِيْنَ هُمْ لِفُرُوْجِهِمْ حٰفِظُوْنَۙ', 'wallażīna hum lifurụjihim ḥāfiẓụn'),
(5404, 70, 30, 'اِلَّا عَلٰٓى اَزْوَاجِهِمْ اَوْ مَا مَلَكَتْ اَيْمَانُهُمْ فَاِنَّهُمْ غَيْرُ مَلُوْمِيْنَۚ', 'illā \'alā azwājihim au mā malakat aimānuhum fa innahum gairu malụmīn'),
(5405, 70, 31, 'فَمَنِ ابْتَغٰى وَرَاۤءَ ذٰلِكَ فَاُولٰۤىِٕكَ هُمُ الْعٰدُوْنَۚ', 'fa manibtagā warā`a żālika fa ulā`ika humul-\'ādụn'),
(5406, 70, 32, 'وَالَّذِيْنَ هُمْ لِاَمٰنٰتِهِمْ وَعَهْدِهِمْ رَاعُوْنَۖ', 'wallażīna hum li`amānātihim wa \'ahdihim rā\'ụn'),
(5407, 70, 33, 'وَالَّذِيْنَ هُمْ بِشَهٰدٰتِهِمْ قَاۤىِٕمُوْنَۖ', 'wallażīna hum bisyahādātihim qā`imụn'),
(5408, 70, 34, 'وَالَّذِيْنَ هُمْ عَلٰى صَلَاتِهِمْ يُحَافِظُوْنَۖ', 'wallażīna hum \'alā ṣalātihim yuḥāfiẓụn'),
(5409, 70, 35, 'اُولٰۤىِٕكَ فِيْ جَنّٰتٍ مُّكْرَمُوْنَ ۗ', 'ulā`ika fī jannātim mukramụn'),
(5410, 70, 36, 'فَمَالِ الَّذِيْنَ كَفَرُوْا قِبَلَكَ مُهْطِعِيْنَۙ', 'fa mālillażīna kafarụ qibalaka muhṭi\'īn'),
(5411, 70, 37, 'عَنِ الْيَمِيْنِ وَعَنِ الشِّمَالِ عِزِيْنَ', '\'anil-yamīni wa \'anisy-syimāli \'izīn'),
(5412, 70, 38, 'اَيَطْمَعُ كُلُّ امْرِئٍ مِّنْهُمْ اَنْ يُّدْخَلَ جَنَّةَ نَعِيْمٍۙ', 'a yaṭma\'u kullumri`im min-hum ay yudkhala jannata na\'īm'),
(5413, 70, 39, 'كَلَّاۗ اِنَّا خَلَقْنٰهُمْ مِّمَّا يَعْلَمُوْنَ', 'kallā, innā khalaqnāhum mimmā ya\'lamụn'),
(5414, 70, 40, 'فَلَآ اُقْسِمُ بِرَبِّ الْمَشَارِقِ وَالْمَغٰرِبِ اِنَّا لَقٰدِرُوْنَۙ', 'fa lā uqsimu birabbil-masyāriqi wal-magāribi innā laqādirụn'),
(5415, 70, 41, 'عَلٰٓى اَنْ نُّبَدِّلَ خَيْرًا مِّنْهُمْۙ وَمَا نَحْنُ بِمَسْبُوْقِيْنَ', '\'alā an nubaddila khairam min-hum wa mā naḥnu bimasbụqīn'),
(5416, 70, 42, 'فَذَرْهُمْ يَخُوْضُوْا وَيَلْعَبُوْا حَتّٰى يُلٰقُوْا يَوْمَهُمُ الَّذِيْ يُوْعَدُوْنَۙ', 'fa żar-hum yakhụḍụ wa yal\'abụ ḥattā yulāqụ yaumahumullażī yụ\'adụn'),
(5417, 70, 43, 'يَوْمَ يَخْرُجُوْنَ مِنَ الْاَجْدَاثِ سِرَاعًا كَاَنَّهُمْ اِلٰى نُصُبٍ يُّوْفِضُوْنَۙ', 'yauma yakhrujụna minal-ajdāṡi sirā\'ang ka`annahum ilā nuṣubiy yụfiḍụn'),
(5418, 70, 44, 'خَاشِعَةً اَبْصَارُهُمْ تَرْهَقُهُمْ ذِلَّةٌ ۗذٰلِكَ الْيَوْمُ الَّذِيْ كَانُوْا يُوْعَدُوْنَ', 'khāsyi\'atan abṣāruhum tar-haquhum żillah, żālikal-yaumullażī kānụ yụ\'adụn'),
(5419, 71, 1, 'اِنَّآ اَرْسَلْنَا نُوْحًا اِلٰى قَوْمِهٖٓ اَنْ اَنْذِرْ قَوْمَكَ مِنْ قَبْلِ اَنْ يَّأْتِيَهُمْ عَذَابٌ اَلِيْمٌ', 'innā arsalnā nụhan ilā qaumihī an anżir qaumaka ming qabli ay ya`tiyahum \'ażābun alīm'),
(5420, 71, 2, 'قَالَ يٰقَوْمِ اِنِّيْ لَكُمْ نَذِيْرٌ مُّبِيْنٌۙ', 'qāla yā qaumi innī lakum nażīrum mubīn'),
(5421, 71, 3, 'اَنِ اعْبُدُوا اللّٰهَ وَاتَّقُوْهُ وَاَطِيْعُوْنِۙ', 'ani\'budullāha wattaqụhu wa aṭī\'ụn'),
(5422, 71, 4, 'يَغْفِرْ لَكُمْ مِّنْ ذُنُوْبِكُمْ وَيُؤَخِّرْكُمْ اِلٰٓى اَجَلٍ مُّسَمًّىۗ اِنَّ اَجَلَ اللّٰهِ اِذَا جَاۤءَ لَا يُؤَخَّرُۘ لَوْ كُنْتُمْ تَعْلَمُوْنَ', 'yagfir lakum min żunụbikum wa yu`akhkhirkum ilā ajalim musammā, inna ajalallāhi iżā jā`a lā yu`akhkhar, lau kuntum ta\'lamụn'),
(5423, 71, 5, 'قَالَ رَبِّ اِنِّيْ دَعَوْتُ قَوْمِيْ لَيْلًا وَّنَهَارًاۙ', 'qāla rabbi innī da\'autu qaumī lailaw wa nahārā'),
(5424, 71, 6, 'فَلَمْ يَزِدْهُمْ دُعَاۤءِيْٓ اِلَّا فِرَارًا', 'fa lam yazid-hum du\'ā`ī illā firārā'),
(5425, 71, 7, 'وَاِنِّيْ كُلَّمَا دَعَوْتُهُمْ لِتَغْفِرَ لَهُمْ جَعَلُوْٓا اَصَابِعَهُمْ فِيْٓ اٰذَانِهِمْ وَاسْتَغْشَوْا ثِيَابَهُمْ وَاَصَرُّوْا وَاسْتَكْبَرُوا اسْتِكْبَارًاۚ', 'wa innī kullamā da\'autuhum litagfira lahum ja\'alū aṣābi\'ahum fī āżānihim wastagsyau ṡiyābahum wa aṣarrụ wastakbarustikbārā'),
(5426, 71, 8, 'ثُمَّ اِنِّيْ دَعَوْتُهُمْ جِهَارًاۙ', 'ṡumma innī da\'autuhum jihārā'),
(5427, 71, 9, 'ثُمَّ اِنِّيْٓ اَعْلَنْتُ لَهُمْ وَاَسْرَرْتُ لَهُمْ اِسْرَارًاۙ', 'ṡumma innī a\'lantu lahum wa asrartu lahum isrārā'),
(5428, 71, 10, 'فَقُلْتُ اسْتَغْفِرُوْا رَبَّكُمْ اِنَّهٗ كَانَ غَفَّارًاۙ', 'fa qultustagfirụ rabbakum innahụ kāna gaffārā'),
(5429, 71, 11, 'يُّرْسِلِ السَّمَاۤءَ عَلَيْكُمْ مِّدْرَارًاۙ', 'yursilis-samā`a \'alaikum midrārā'),
(5430, 71, 12, 'وَّيُمْدِدْكُمْ بِاَمْوَالٍ وَّبَنِيْنَ وَيَجْعَلْ لَّكُمْ جَنّٰتٍ وَّيَجْعَلْ لَّكُمْ اَنْهٰرًاۗ', 'wa yumdidkum bi`amwāliw wa banīna wa yaj\'al lakum jannātiw wa yaj\'al lakum an-hārā'),
(5431, 71, 13, 'مَا لَكُمْ لَا تَرْجُوْنَ لِلّٰهِ وَقَارًاۚ', 'mā lakum lā tarjụna lillāhi waqārā'),
(5432, 71, 14, 'وَقَدْ خَلَقَكُمْ اَطْوَارًا', 'wa qad khalaqakum aṭwārā'),
(5433, 71, 15, 'اَلَمْ تَرَوْا كَيْفَ خَلَقَ اللّٰهُ سَبْعَ سَمٰوٰتٍ طِبَاقًاۙ', 'a lam tarau kaifa khalaqallāhu sab\'a samāwātin ṭibāqā'),
(5434, 71, 16, 'وَّجَعَلَ الْقَمَرَ فِيْهِنَّ نُوْرًا وَّجَعَلَ الشَّمْسَ سِرَاجًا', 'wa ja\'alal-qamara fīhinna nụraw wa ja\'alasy-syamsa sirājā'),
(5435, 71, 17, 'وَاللّٰهُ اَنْۢبَتَكُمْ مِّنَ الْاَرْضِ نَبَاتًاۙ', 'wallāhu ambatakum minal-arḍi nabātā'),
(5436, 71, 18, 'ثُمَّ يُعِيْدُكُمْ فِيْهَا وَيُخْرِجُكُمْ اِخْرَاجًا', 'ṡumma yu\'īdukum fīhā wa yukhrijukum ikhrājā'),
(5437, 71, 19, 'وَاللّٰهُ جَعَلَ لَكُمُ الْاَرْضَ بِسَاطًاۙ', 'wallāhu ja\'ala lakumul-arḍa bisāṭā'),
(5438, 71, 20, 'لِّتَسْلُكُوْا مِنْهَا سُبُلًا فِجَاجًا', 'litaslukụ min-hā subulan fijājā'),
(5439, 71, 21, 'قَالَ نُوْحٌ رَّبِّ اِنَّهُمْ عَصَوْنِيْ وَاتَّبَعُوْا مَنْ لَّمْ يَزِدْهُ مَالُهٗ وَوَلَدُهٗٓ اِلَّا خَسَارًاۚ', 'qāla nụḥur rabbi innahum \'aṣaunī wattaba\'ụ mal lam yazid-hu māluhụ wa waladuhū illā khasārā'),
(5440, 71, 22, 'وَمَكَرُوْا مَكْرًا كُبَّارًاۚ', 'wa makarụ makrang kubbārā'),
(5441, 71, 23, 'وَقَالُوْا لَا تَذَرُنَّ اٰلِهَتَكُمْ وَلَا تَذَرُنَّ وَدًّا وَّلَا سُوَاعًا ەۙ وَّلَا يَغُوْثَ وَيَعُوْقَ وَنَسْرًاۚ', 'wa qālụ lā tażarunna ālihatakum wa lā tażarunna waddaw wa lā suwā\'aw wa lā yagụṡa wa ya\'ụqa wa nasrā'),
(5442, 71, 24, 'وَقَدْ اَضَلُّوْا كَثِيْرًا ەۚ وَلَا تَزِدِ الظّٰلِمِيْنَ اِلَّا ضَلٰلًا', 'wa qad aḍallụ kaṡīrā, wa lā tazidiẓ-ẓālimīna illā ḍalālā'),
(5443, 71, 25, 'مِمَّا خَطِيْۤـٰٔتِهِمْ اُغْرِقُوْا فَاُدْخِلُوْا نَارًا ەۙ فَلَمْ يَجِدُوْا لَهُمْ مِّنْ دُوْنِ اللّٰهِ اَنْصَارًا', 'mimmā khaṭī`ātihim ugriqụ fa udkhilụ nāran fa lam yajidụ lahum min dụnillāhi anṣārā'),
(5444, 71, 26, 'وَقَالَ نُوْحٌ رَّبِّ لَا تَذَرْ عَلَى الْاَرْضِ مِنَ الْكٰفِرِيْنَ دَيَّارًا', 'wa qāla nụḥur rabbi lā tażar \'alal-arḍi minal-kāfirīna dayyārā'),
(5445, 71, 27, 'اِنَّكَ اِنْ تَذَرْهُمْ يُضِلُّوْا عِبَادَكَ وَلَا يَلِدُوْٓا اِلَّا فَاجِرًا كَفَّارًا', 'innaka in tażar-hum yuḍillụ \'ibādaka wa lā yalidū illā fājirang kaffārā'),
(5446, 71, 28, 'رَبِّ اغْفِرْ لِيْ وَلِوَالِدَيَّ وَلِمَنْ دَخَلَ بَيْتِيَ مُؤْمِنًا وَّلِلْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِۗ وَلَا تَزِدِ الظّٰلِمِيْنَ اِلَّا تَبَارًا', 'rabbigfir lī wa liwālidayya wa liman dakhala baitiya mu`minaw wa lil-mu`minīna wal-mu`mināt, wa lā tazidiẓ-ẓālimīna illā tabārā'),
(5447, 72, 1, 'قُلْ اُوْحِيَ اِلَيَّ اَنَّهُ اسْتَمَعَ نَفَرٌ مِّنَ الْجِنِّ فَقَالُوْٓا اِنَّا سَمِعْنَا قُرْاٰنًا عَجَبًاۙ', 'qul ụḥiya ilayya annahustama\'a nafarum minal-jinni fa qālū innā sami\'nā qur`ānan \'ajabā'),
(5448, 72, 2, 'يَّهْدِيْٓ اِلَى الرُّشْدِ فَاٰمَنَّا بِهٖۗ وَلَنْ نُّشْرِكَ بِرَبِّنَآ اَحَدًاۖ', 'yahdī ilar-rusydi fa āmannā bih, wa lan nusyrika birabbinā aḥadā'),
(5449, 72, 3, 'وَّاَنَّهٗ تَعٰلٰى جَدُّ رَبِّنَا مَا اتَّخَذَ صَاحِبَةً وَّلَا وَلَدًاۖ', 'wa annahụ ta\'ālā jaddu rabbinā mattakhaża ṣāḥibataw wa lā waladā'),
(5450, 72, 4, 'وَّاَنَّهٗ كَانَ يَقُوْلُ سَفِيْهُنَا عَلَى اللّٰهِ شَطَطًاۖ', 'wa annahụ kāna yaqụlu safīhunā \'alallāhi syaṭaṭā'),
(5451, 72, 5, 'وَّاَنَّا ظَنَنَّآ اَنْ لَّنْ تَقُوْلَ الْاِنْسُ وَالْجِنُّ عَلَى اللّٰهِ كَذِبًاۙ', 'wa annā ẓanannā al lan taqụlal-insu wal-jinnu \'alallāhi każibā'),
(5452, 72, 6, 'وَّاَنَّهٗ كَانَ رِجَالٌ مِّنَ الْاِنْسِ يَعُوْذُوْنَ بِرِجَالٍ مِّنَ الْجِنِّ فَزَادُوْهُمْ رَهَقًاۖ', 'wa annahụ kāna rijālum minal-insi ya\'ụżụna birijālim minal-jinni fa zādụhum rahaqā'),
(5453, 72, 7, 'وَّاَنَّهُمْ ظَنُّوْا كَمَا ظَنَنْتُمْ اَنْ لَّنْ يَّبْعَثَ اللّٰهُ اَحَدًاۖ', 'wa annahum ẓannụ kamā ẓanantum al lay yab\'aṡallāhu aḥadā'),
(5454, 72, 8, 'وَّاَنَّا لَمَسْنَا السَّمَاۤءَ فَوَجَدْنٰهَا مُلِئَتْ حَرَسًا شَدِيْدًا وَّشُهُبًاۖ', 'wa annā lamasnas-samā`a fa wajadnāhā muli`at ḥarasan syadīdaw wa syuhubā'),
(5455, 72, 9, 'وَّاَنَّا كُنَّا نَقْعُدُ مِنْهَا مَقَاعِدَ لِلسَّمْعِۗ فَمَنْ يَّسْتَمِعِ الْاٰنَ يَجِدْ لَهٗ شِهَابًا رَّصَدًاۖ', 'wa annā kunnā naq\'udu min-hā maqā\'ida lis-sam\', fa may yastami\'il-āna yajid lahụ syihābar raṣadā'),
(5456, 72, 10, 'وَّاَنَّا لَا نَدْرِيْٓ اَشَرٌّ اُرِيْدَ بِمَنْ فِى الْاَرْضِ اَمْ اَرَادَ بِهِمْ رَبُّهُمْ رَشَدًاۙ', 'wa annā lā nadrī asyarrun urīda biman fil-arḍi am arāda bihim rabbuhum rasyadā'),
(5457, 72, 11, 'وَّاَنَّا مِنَّا الصّٰلِحُوْنَ وَمِنَّا دُوْنَ ذٰلِكَۗ كُنَّا طَرَاۤىِٕقَ قِدَدًاۙ', 'wa annā minnaṣ-ṣāliḥụna wa minnā dụna żālik, kunnā ṭarā`iqa qidadā'),
(5458, 72, 12, 'وَّاَنَّا ظَنَنَّآ اَنْ لَّنْ نُّعْجِزَ اللّٰهَ فِى الْاَرْضِ وَلَنْ نُّعْجِزَهٗ هَرَبًاۖ', 'wa annā ẓanannā al lan nu\'jizallāha fil-arḍi wa lan nu\'jizahụ harabā'),
(5459, 72, 13, 'وَّاَنَّا لَمَّا سَمِعْنَا الْهُدٰىٓ اٰمَنَّا بِهٖۗ فَمَنْ يُّؤْمِنْۢ بِرَبِّهٖ فَلَا يَخَافُ بَخْسًا وَّلَا رَهَقًاۖ', 'wa annā lammā sami\'nal-hudā āmannā bih, fa may yu`mim birabbihī fa lā yakhāfu bakhsaw wa lā rahaqā'),
(5460, 72, 14, 'وَّاَنَّا مِنَّا الْمُسْلِمُوْنَ وَمِنَّا الْقَاسِطُوْنَۗ فَمَنْ اَسْلَمَ فَاُولٰۤىِٕكَ تَحَرَّوْا رَشَدًا', 'wa annā minnal-muslimụna wa minnal-qāsiṭụn, fa man aslama fa ulā`ika taḥarrau rasyadā'),
(5461, 72, 15, 'وَاَمَّا الْقَاسِطُوْنَ فَكَانُوْا لِجَهَنَّمَ حَطَبًاۙ', 'wa ammal-qāsiṭụna fa kānụ lijahannama ḥaṭabā'),
(5462, 72, 16, 'وَّاَنْ لَّوِ اسْتَقَامُوْا عَلَى الطَّرِيْقَةِ لَاَسْقَيْنٰهُمْ مَّاۤءً غَدَقًاۙ', 'wa al lawistaqāmụ \'alaṭ-ṭarīqati la`asqaināhum mā`an gadaqā'),
(5463, 72, 17, 'لِّنَفْتِنَهُمْ فِيْهِۗ وَمَنْ يُّعْرِضْ عَنْ ذِكْرِ رَبِّهٖ يَسْلُكْهُ عَذَابًا صَعَدًاۙ', 'linaftinahum fīh, wa may yu\'riḍ \'an żikri rabbihī yasluk-hu \'ażāban ṣa\'adā'),
(5464, 72, 18, 'وَّاَنَّ الْمَسٰجِدَ لِلّٰهِ فَلَا تَدْعُوْا مَعَ اللّٰهِ اَحَدًاۖ', 'wa annal-masājida lillāhi fa lā tad\'ụ ma\'allāhi aḥadā'),
(5465, 72, 19, 'وَّاَنَّهٗ لَمَّا قَامَ عَبْدُ اللّٰهِ يَدْعُوْهُ كَادُوْا يَكُوْنُوْنَ عَلَيْهِ لِبَدًاۗ', 'wa annahụ lammā qāma \'abdullāhi yad\'ụhu kādụ yakụnụna \'alaihi libadā'),
(5466, 72, 20, 'قُلْ اِنَّمَآ اَدْعُوْا رَبِّيْ وَلَآ اُشْرِكُ بِهٖٓ اَحَدًا', 'qul innamā ad\'ụ rabbī wa lā usyriku bihī aḥadā'),
(5467, 72, 21, 'قُلْ اِنِّيْ لَآ اَمْلِكُ لَكُمْ ضَرًّا وَّلَا رَشَدًا', 'qul innī lā amliku lakum ḍarraw wa lā rasyadā'),
(5468, 72, 22, 'قُلْ اِنِّيْ لَنْ يُّجِيْرَنِيْ مِنَ اللّٰهِ اَحَدٌ ەۙ وَّلَنْ اَجِدَ مِنْ دُوْنِهٖ مُلْتَحَدًا ۙ', 'qul innī lay yujīranī minallāhi aḥaduw wa lan ajida min dụnihī multaḥadā'),
(5469, 72, 23, 'اِلَّا بَلٰغًا مِّنَ اللّٰهِ وَرِسٰلٰتِهٖۗ وَمَنْ يَّعْصِ اللّٰهَ وَرَسُوْلَهٗ فَاِنَّ لَهٗ نَارَ جَهَنَّمَ خٰلِدِيْنَ فِيْهَآ اَبَدًاۗ', 'illā balāgam minallāhi wa risālātih, wa may ya\'ṣillāha wa rasụlahụ fa inna lahụ nāra jahannama khālidīna fīhā abadā'),
(5470, 72, 24, 'حَتّٰىٓ اِذَا رَاَوْا مَا يُوْعَدُوْنَ فَسَيَعْلَمُوْنَ مَنْ اَضْعَفُ نَاصِرًا وَّاَقَلُّ عَدَدًاۗ', 'ḥattā iżā ra`au mā yụ\'adụna fa saya\'lamụna man aḍ\'afu nāṣiraw wa aqallu \'adadā'),
(5471, 72, 25, 'قُلْ اِنْ اَدْرِيْٓ اَقَرِيْبٌ مَّا تُوْعَدُوْنَ اَمْ يَجْعَلُ لَهٗ رَبِّيْٓ اَمَدًا', 'qul in adrī a qarībum mā tụ\'adụna am yaj\'alu lahụ rabbī amadā'),
(5472, 72, 26, 'عٰلِمُ الْغَيْبِ فَلَا يُظْهِرُ عَلٰى غَيْبِهٖٓ اَحَدًاۙ', '\'ālimul-gaibi fa lā yuẓ-hiru \'alā gaibihī aḥadā'),
(5473, 72, 27, 'اِلَّا مَنِ ارْتَضٰى مِنْ رَّسُوْلٍ فَاِنَّهٗ يَسْلُكُ مِنْۢ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهٖ رَصَدًاۙ', 'illā manirtaḍā mir rasụlin fa innahụ yasluku mim baini yadaihi wa min khalfihī raṣadā'),
(5474, 72, 28, 'لِّيَعْلَمَ اَنْ قَدْ اَبْلَغُوْا رِسٰلٰتِ رَبِّهِمْ وَاَحَاطَ بِمَا لَدَيْهِمْ وَاَحْصٰى كُلَّ شَيْءٍ عَدَدًا', 'liya\'lama ang qad ablagụ risālāti rabbihim wa aḥāṭa bimā ladaihim wa aḥṣā kulla syai`in \'adadā'),
(5475, 73, 1, 'يٰٓاَيُّهَا الْمُزَّمِّلُۙ', 'yā ayyuhal-muzzammil'),
(5476, 73, 2, 'قُمِ الَّيْلَ اِلَّا قَلِيْلًاۙ', 'qumil-laila illā qalīlā'),
(5477, 73, 3, 'نِّصْفَهٗٓ اَوِ انْقُصْ مِنْهُ قَلِيْلًاۙ', 'niṣfahū awingquṣ min-hu qalīlā'),
(5478, 73, 4, 'اَوْ زِدْ عَلَيْهِ وَرَتِّلِ الْقُرْاٰنَ تَرْتِيْلًاۗ', 'au zid \'alaihi wa rattilil-qur`āna tartīlā'),
(5479, 73, 5, 'اِنَّا سَنُلْقِيْ عَلَيْكَ قَوْلًا ثَقِيْلًا', 'innā sanulqī \'alaika qaulan ṡaqīlā'),
(5480, 73, 6, 'اِنَّ نَاشِئَةَ الَّيْلِ هِيَ اَشَدُّ وَطْـًٔا وَّاَقْوَمُ قِيْلًاۗ', 'inna nāsyi`atal-laili hiya asyaddu waṭ`aw wa aqwamu qīlā'),
(5481, 73, 7, 'اِنَّ لَكَ فِى النَّهَارِ سَبْحًا طَوِيْلًاۗ', 'inna laka fin-nahāri sab-ḥan ṭawīlā'),
(5482, 73, 8, 'وَاذْكُرِ اسْمَ رَبِّكَ وَتَبَتَّلْ اِلَيْهِ تَبْتِيْلًاۗ', 'ważkurisma rabbika wa tabattal ilaihi tabtīlā'),
(5483, 73, 9, 'رَبُّ الْمَشْرِقِ وَالْمَغْرِبِ لَآ اِلٰهَ اِلَّا هُوَ فَاتَّخِذْهُ وَكِيْلًا', 'rabbul-masyriqi wal-magribi lā ilāha illā huwa fattakhiż-hu wakīlā'),
(5484, 73, 10, 'وَاصْبِرْ عَلٰى مَا يَقُوْلُوْنَ وَاهْجُرْهُمْ هَجْرًا جَمِيْلًا', 'waṣbir \'alā mā yaqụlụna wahjur-hum hajran jamīlā'),
(5485, 73, 11, 'وَذَرْنِيْ وَالْمُكَذِّبِيْنَ اُولِى النَّعْمَةِ وَمَهِّلْهُمْ قَلِيْلًا', 'wa żarnī wal-mukażżibīna ulin-na\'mati wa mahhil-hum qalīlā'),
(5486, 73, 12, 'اِنَّ لَدَيْنَآ اَنْكَالًا وَّجَحِيْمًاۙ', 'inna ladainā angkālaw wa jaḥīmā'),
(5487, 73, 13, 'وَّطَعَامًا ذَا غُصَّةٍ وَّعَذَابًا اَلِيْمًا', 'wa ṭa\'āman żā guṣṣatiw wa \'ażāban alīmā'),
(5488, 73, 14, 'يَوْمَ تَرْجُفُ الْاَرْضُ وَالْجِبَالُ وَكَانَتِ الْجِبَالُ كَثِيْبًا مَّهِيْلًا', 'yauma tarjuful-arḍu wal-jibālu wa kānatil-jibālu kaṡībam mahīlā'),
(5489, 73, 15, 'اِنَّآ اَرْسَلْنَآ اِلَيْكُمْ رَسُوْلًا ەۙ شَاهِدًا عَلَيْكُمْ كَمَآ اَرْسَلْنَآ اِلٰى فِرْعَوْنَ رَسُوْلًا ۗ', 'innā arsalnā ilaikum rasụlan syāhidan \'alaikum kamā arsalnā ilā fir\'auna rasụlā'),
(5490, 73, 16, 'فَعَصٰى فِرْعَوْنُ الرَّسُوْلَ فَاَخَذْنٰهُ اَخْذًا وَّبِيْلًاۚ', 'fa \'aṣā fir\'aunur-rasụla fa akhażnāhu akhżaw wabīlā'),
(5491, 73, 17, 'فَكَيْفَ تَتَّقُوْنَ اِنْ كَفَرْتُمْ يَوْمًا يَّجْعَلُ الْوِلْدَانَ شِيْبًاۖ', 'fa kaifa tattaqụna ing kafartum yaumay yaj\'alul-wildāna syībā'),
(5492, 73, 18, ' ۨالسَّمَاۤءُ مُنْفَطِرٌۢ بِهٖۗ كَانَ وَعْدُهٗ مَفْعُوْلًا', 'as-samā`u munfaṭirum bih, kāna wa\'duhụ maf\'ụlā'),
(5493, 73, 19, 'اِنَّ هٰذِهٖ تَذْكِرَةٌ ۚ فَمَنْ شَاۤءَ اتَّخَذَ اِلٰى رَبِّهٖ سَبِيْلًا', 'inna hāżihī tażkirah, fa man syā`attakhaża ilā rabbihī sabīlā'),
(5494, 73, 20, '۞ اِنَّ رَبَّكَ يَعْلَمُ اَنَّكَ تَقُوْمُ اَدْنٰى مِنْ ثُلُثَيِ الَّيْلِ وَنِصْفَهٗ وَثُلُثَهٗ وَطَاۤىِٕفَةٌ مِّنَ الَّذِيْنَ مَعَكَۗ وَاللّٰهُ يُقَدِّرُ الَّيْلَ وَالنَّهَارَۗ عَلِمَ اَنْ لَّنْ تُحْصُوْهُ فَتَابَ عَلَيْكُمْ فَاقْرَءُوْا مَا تَيَسَّرَ مِنَ الْقُرْاٰنِۗ عَلِمَ اَنْ سَيَكُوْنُ مِنْكُمْ مَّرْضٰىۙ وَاٰخَرُوْنَ يَضْرِبُوْنَ فِى الْاَرْضِ يَبْتَغُوْنَ مِنْ فَضْلِ اللّٰهِ ۙوَاٰخَرُوْنَ يُقَاتِلُوْنَ فِيْ سَبِيْلِ اللّٰهِ ۖفَاقْرَءُوْا مَا تَيَسَّرَ مِنْهُۙ وَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَاَقْرِضُوا اللّٰهَ قَرْضًا حَسَنًاۗ وَمَا تُقَدِّمُوْا لِاَنْفُسِكُمْ مِّنْ خَيْرٍ تَجِدُوْهُ عِنْدَ اللّٰهِ ۙهُوَ خَيْرًا وَّاَعْظَمَ اَجْرًاۗ وَاسْتَغْفِرُوا اللّٰهَ ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'inna rabbaka ya\'lamu annaka taqụmu adnā min ṡuluṡayil-laili wa niṣfahụ wa ṡuluṡahụ wa ṭā`ifatum minallażīna ma\'ak, wallāhu yuqaddirul-laila wan-nahār, \'alima al lan tuḥṣụhu fa tāba \'alaikum faqra`ụ mā tayassara minal-qur`ān, \'alima an sayakụnu mingkum marḍā wa ākharụna yaḍribụna fil-arḍi yabtagụna min faḍlillāhi wa ākharụna yuqātilụna fī sabīlillāhi faqra`ụ mā tayassara min-hu wa aqīmuṣ-ṣalāta wa ātuz-zakāta wa aqriḍullāha qarḍan ḥasanā, wa mā tuqaddimụ li`anfusikum min khairin tajidụhu \'indallāhi huwa khairaw wa a\'ẓama ajrā, wastagfirullāh, innallāha gafụrur raḥīm'),
(5495, 74, 1, 'يٰٓاَيُّهَا الْمُدَّثِّرُۙ', 'yā ayyuhal-muddaṡṡir'),
(5496, 74, 2, 'قُمْ فَاَنْذِرْۖ', 'qum fa anżir'),
(5497, 74, 3, 'وَرَبَّكَ فَكَبِّرْۖ', 'wa rabbaka fa kabbir'),
(5498, 74, 4, 'وَثِيَابَكَ فَطَهِّرْۖ', 'wa ṡiyābaka fa ṭahhir'),
(5499, 74, 5, 'وَالرُّجْزَ فَاهْجُرْۖ', 'war-rujza fahjur'),
(5500, 74, 6, 'وَلَا تَمْنُنْ تَسْتَكْثِرُۖ', 'wa lā tamnun tastakṡir'),
(5501, 74, 7, 'وَلِرَبِّكَ فَاصْبِرْۗ', 'wa lirabbika faṣbir'),
(5502, 74, 8, 'فَاِذَا نُقِرَ فِى النَّاقُوْرِۙ', 'fa iżā nuqira fin-nāqụr'),
(5503, 74, 9, 'فَذٰلِكَ يَوْمَىِٕذٍ يَّوْمٌ عَسِيْرٌۙ', 'fa żālika yauma`iżiy yaumun \'asīr'),
(5504, 74, 10, 'عَلَى الْكٰفِرِيْنَ غَيْرُ يَسِيْرٍ', '\'alal-kāfirīna gairu yasīr'),
(5505, 74, 11, 'ذَرْنِيْ وَمَنْ خَلَقْتُ وَحِيْدًاۙ', 'żarnī wa man khalaqtu waḥīdā'),
(5506, 74, 12, 'وَّجَعَلْتُ لَهٗ مَالًا مَّمْدُوْدًاۙ', 'wa ja\'altu lahụ mālam mamdụdā'),
(5507, 74, 13, 'وَّبَنِيْنَ شُهُوْدًاۙ', 'wa banīna syuhụdā'),
(5508, 74, 14, 'وَّمَهَّدْتُّ لَهٗ تَمْهِيْدًاۙ', 'wa mahhattu lahụ tamhīdā'),
(5509, 74, 15, 'ثُمَّ يَطْمَعُ اَنْ اَزِيْدَۙ', 'ṡumma yaṭma\'u an azīd'),
(5510, 74, 16, 'كَلَّاۗ اِنَّهٗ كَانَ لِاٰيٰتِنَا عَنِيْدًاۗ', 'kallā, innahụ kāna li`āyātinā \'anīdā'),
(5511, 74, 17, 'سَاُرْهِقُهٗ صَعُوْدًاۗ', 'sa`ur-hiquhụ ṣa\'ụdā'),
(5512, 74, 18, 'اِنَّهٗ فَكَّرَ وَقَدَّرَۙ', 'innahụ fakkara wa qaddar'),
(5513, 74, 19, 'فَقُتِلَ كَيْفَ قَدَّرَۙ', 'fa qutila kaifa qaddar'),
(5514, 74, 20, 'ثُمَّ قُتِلَ كَيْفَ قَدَّرَۙ', 'ṡumma qutila kaifa qaddar'),
(5515, 74, 21, 'ثُمَّ نَظَرَۙ', 'ṡumma naẓar'),
(5516, 74, 22, 'ثُمَّ عَبَسَ وَبَسَرَۙ', 'ṡumma \'abasa wa basar'),
(5517, 74, 23, 'ثُمَّ اَدْبَرَ وَاسْتَكْبَرَۙ', 'ṡumma adbara wastakbar'),
(5518, 74, 24, 'فَقَالَ اِنْ هٰذَآ اِلَّا سِحْرٌ يُّؤْثَرُۙ', 'fa qāla in hāżā illā siḥruy yu`ṡar'),
(5519, 74, 25, 'اِنْ هٰذَآ اِلَّا قَوْلُ الْبَشَرِۗ', 'in hāżā illā qaulul-basyar'),
(5520, 74, 26, 'سَاُصْلِيْهِ سَقَرَ', 'sa`uṣlīhi saqar'),
(5521, 74, 27, 'وَمَآ اَدْرٰىكَ مَا سَقَرُۗ', 'wa mā adrāka mā saqar'),
(5522, 74, 28, 'لَا تُبْقِيْ وَلَا تَذَرُۚ', 'lā tubqī wa lā tażar'),
(5523, 74, 29, 'لَوَّاحَةٌ لِّلْبَشَرِۚ', 'lawwāḥatul lil-basyar'),
(5524, 74, 30, 'عَلَيْهَا تِسْعَةَ عَشَرَۗ', '\'alaihā tis\'ata \'asyar'),
(5525, 74, 31, 'وَمَا جَعَلْنَآ اَصْحٰبَ النَّارِ اِلَّا مَلٰۤىِٕكَةً ۖوَّمَا جَعَلْنَا عِدَّتَهُمْ اِلَّا فِتْنَةً لِّلَّذِيْنَ كَفَرُوْاۙ لِيَسْتَيْقِنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ وَيَزْدَادَ الَّذِيْنَ اٰمَنُوْٓا اِيْمَانًا وَّلَا يَرْتَابَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ وَالْمُؤْمِنُوْنَۙ وَلِيَقُوْلَ الَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ وَّالْكٰفِرُوْنَ مَاذَآ اَرَادَ اللّٰهُ بِهٰذَا مَثَلًاۗ كَذٰلِكَ يُضِلُّ اللّٰهُ مَنْ يَّشَاۤءُ وَيَهْدِيْ مَنْ يَّشَاۤءُۗ وَمَا يَعْلَمُ جُنُوْدَ رَبِّكَ اِلَّا هُوَۗ وَمَا هِيَ اِلَّا ذِكْرٰى لِلْبَشَرِ', 'wa mā ja\'alnā aṣ-ḥāban-nāri illā malā`ikataw wa mā ja\'alnā \'iddatahum illā fitnatal lillażīna kafarụ liyastaiqinallażīna ụtul-kitāba wa yazdādallażīna āmanū īmānaw wa lā yartāballażīna ụtul-kitāba wal-mu`minụna wa liyaqụlallażīna fī qulụbihim maraḍuw wal-kāfirụna māżā arādallāhu bihāżā maṡalā, każālika yuḍillullāhu may yasyā`u wa yahdī may yasyā`, wa mā ya\'lamu junụda rabbika illā huw, wa mā hiya illā żikrā lil-basyar'),
(5526, 74, 32, 'كَلَّا وَالْقَمَرِۙ', 'kallā wal-qamar'),
(5527, 74, 33, 'وَالَّيْلِ اِذْ اَدْبَرَۙ', 'wal-laili iż adbar'),
(5528, 74, 34, 'وَالصُّبْحِ اِذَآ اَسْفَرَۙ', 'waṣ-ṣub-ḥi iżā asfar'),
(5529, 74, 35, 'اِنَّهَا لَاِحْدَى الْكُبَرِۙ', 'innahā la`iḥdal-kubar'),
(5530, 74, 36, 'نَذِيْرًا لِّلْبَشَرِۙ', 'nażīral lil-basyar'),
(5531, 74, 37, 'لِمَنْ شَاۤءَ مِنْكُمْ اَنْ يَّتَقَدَّمَ اَوْ يَتَاَخَّرَۗ', 'liman syā`a mingkum ay yataqaddama au yata`akhkhar'),
(5532, 74, 38, 'كُلُّ نَفْسٍۢ بِمَا كَسَبَتْ رَهِيْنَةٌۙ', 'kullu nafsim bimā kasabat rahīnah'),
(5533, 74, 39, 'اِلَّآ اَصْحٰبَ الْيَمِيْنِ ۛ', 'illā aṣ-ḥābal-yamīn'),
(5534, 74, 40, 'فِيْ جَنّٰتٍ ۛ يَتَسَاۤءَلُوْنَۙ', 'fī jannātiy yatasā`alụn'),
(5535, 74, 41, 'عَنِ الْمُجْرِمِيْنَۙ', '\'anil-mujrimīn'),
(5536, 74, 42, 'مَا سَلَكَكُمْ فِيْ سَقَرَ', 'mā salakakum fī saqar'),
(5537, 74, 43, 'قَالُوْا لَمْ نَكُ مِنَ الْمُصَلِّيْنَۙ', 'qālụ lam naku minal-muṣallīn'),
(5538, 74, 44, 'وَلَمْ نَكُ نُطْعِمُ الْمِسْكِيْنَۙ', 'wa lam naku nuṭ\'imul-miskīn'),
(5539, 74, 45, 'وَكُنَّا نَخُوْضُ مَعَ الْخَاۤىِٕضِيْنَۙ', 'wa kunnā nakhụḍu ma\'al-khā`iḍīn'),
(5540, 74, 46, 'وَكُنَّا نُكَذِّبُ بِيَوْمِ الدِّيْنِۙ', 'wa kunnā nukażżibu biyaumid-dīn'),
(5541, 74, 47, 'حَتّٰىٓ اَتٰىنَا الْيَقِيْنُۗ', 'ḥattā atānal-yaqīn'),
(5542, 74, 48, 'فَمَا تَنْفَعُهُمْ شَفَاعَةُ الشَّافِعِيْنَۗ', 'fa mā tanfa\'uhum syafā\'atusy-syāfi\'īn'),
(5543, 74, 49, 'فَمَا لَهُمْ عَنِ التَّذْكِرَةِ مُعْرِضِيْنَۙ', 'fa mā lahum \'anit-tażkirati mu\'riḍīn'),
(5544, 74, 50, 'كَاَنَّهُمْ حُمُرٌ مُّسْتَنْفِرَةٌۙ', 'ka`annahum ḥumurum mustanfirah'),
(5545, 74, 51, 'فَرَّتْ مِنْ قَسْوَرَةٍۗ', 'farrat ming qaswarah'),
(5546, 74, 52, 'بَلْ يُرِيْدُ كُلُّ امْرِئٍ مِّنْهُمْ اَنْ يُّؤْتٰى صُحُفًا مُّنَشَّرَةًۙ', 'bal yurīdu kullumri`im min-hum ay yu`tā ṣuḥufam munasysyarah'),
(5547, 74, 53, 'كَلَّاۗ بَلْ لَّا يَخَافُوْنَ الْاٰخِرَةَۗ', 'kallā, bal lā yakhāfụnal-ākhirah'),
(5548, 74, 54, 'كَلَّآ اِنَّهٗ تَذْكِرَةٌ ۚ', 'kallā innahụ tażkirah'),
(5549, 74, 55, 'فَمَنْ شَاۤءَ ذَكَرَهٗۗ', 'fa man syā`a żakarah'),
(5550, 74, 56, 'وَمَا يَذْكُرُوْنَ اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ ۗهُوَ اَهْلُ التَّقْوٰى وَاَهْلُ الْمَغْفِرَةِ', 'wa mā yażkurụna illā ay yasyā`allāh, huwa ahlut-taqwā wa ahlul-magfirah'),
(5551, 75, 1, 'لَآ اُقْسِمُ بِيَوْمِ الْقِيٰمَةِۙ', 'lā uqsimu biyaumil-qiyāmah'),
(5552, 75, 2, 'وَلَآ اُقْسِمُ بِالنَّفْسِ اللَّوَّامَةِ', 'wa lā uqsimu bin-nafsil-lawwāmah'),
(5553, 75, 3, 'اَيَحْسَبُ الْاِنْسَانُ اَلَّنْ نَّجْمَعَ عِظَامَهٗ ۗ', 'a yaḥsabul-insānu allan najma\'a \'iẓāmah'),
(5554, 75, 4, 'بَلٰى قَادِرِيْنَ عَلٰٓى اَنْ نُّسَوِّيَ بَنَانَهٗ', 'balā qādirīna \'alā an nusawwiya banānah'),
(5555, 75, 5, 'بَلْ يُرِيْدُ الْاِنْسَانُ لِيَفْجُرَ اَمَامَهٗۚ', 'bal yurīdul-insānu liyafjura amāmah'),
(5556, 75, 6, 'يَسْـَٔلُ اَيَّانَ يَوْمُ الْقِيٰمَةِۗ', 'yas`alu ayyāna yaumul-qiyāmah'),
(5557, 75, 7, 'فَاِذَا بَرِقَ الْبَصَرُۙ', 'fa iżā bariqal-baṣar'),
(5558, 75, 8, 'وَخَسَفَ الْقَمَرُۙ', 'wa khasafal-qamar'),
(5559, 75, 9, 'وَجُمِعَ الشَّمْسُ وَالْقَمَرُۙ', 'wa jumi\'asy-syamsu wal-qamar'),
(5560, 75, 10, 'يَقُوْلُ الْاِنْسَانُ يَوْمَىِٕذٍ اَيْنَ الْمَفَرُّۚ', 'yaqụlul-insānu yauma`iżin ainal-mafarr'),
(5561, 75, 11, 'كَلَّا لَا وَزَرَۗ', 'kallā lā wazar'),
(5562, 75, 12, 'اِلٰى رَبِّكَ يَوْمَىِٕذِ ِۨالْمُسْتَقَرُّۗ', 'ilā rabbika yauma`iżinil-mustaqarr'),
(5563, 75, 13, 'يُنَبَّؤُا الْاِنْسَانُ يَوْمَىِٕذٍۢ بِمَا قَدَّمَ وَاَخَّرَۗ', 'yunabba`ul-insānu yauma`iżim bimā qaddama wa akhkhar'),
(5564, 75, 14, 'بَلِ الْاِنْسَانُ عَلٰى نَفْسِهٖ بَصِيْرَةٌۙ', 'balil-insānu \'alā nafsihī baṣīrah'),
(5565, 75, 15, 'وَّلَوْ اَلْقٰى مَعَاذِيْرَهٗۗ', 'walau alqā ma\'āżīrah'),
(5566, 75, 16, 'لَا تُحَرِّكْ بِهٖ لِسَانَكَ لِتَعْجَلَ بِهٖۗ', 'lā tuḥarrik bihī lisānaka lita\'jala bih'),
(5567, 75, 17, 'اِنَّ عَلَيْنَا جَمْعَهٗ وَقُرْاٰنَهٗ ۚ', 'inna \'alainā jam\'ahụ wa qur`ānah'),
(5568, 75, 18, 'فَاِذَا قَرَأْنٰهُ فَاتَّبِعْ قُرْاٰنَهٗ ۚ', 'fa iżā qara`nāhu fattabi\' qur`ānah'),
(5569, 75, 19, 'ثُمَّ اِنَّ عَلَيْنَا بَيَانَهٗ ۗ', 'ṡumma inna \'alainā bayānah'),
(5570, 75, 20, 'كَلَّا بَلْ تُحِبُّوْنَ الْعَاجِلَةَۙ', 'kallā bal tuḥibbụnal-\'ājilah'),
(5571, 75, 21, 'وَتَذَرُوْنَ الْاٰخِرَةَۗ', 'wa tażarụnal-ākhirah'),
(5572, 75, 22, 'وُجُوْهٌ يَّوْمَىِٕذٍ نَّاضِرَةٌۙ', 'wujụhuy yauma`iżin nāḍirah'),
(5573, 75, 23, 'اِلٰى رَبِّهَا نَاظِرَةٌ ۚ', 'ilā rabbihā nāẓirah'),
(5574, 75, 24, 'وَوُجُوْهٌ يَّوْمَىِٕذٍۢ بَاسِرَةٌۙ', 'wa wujụhuy yauma`iżim bāsirah'),
(5575, 75, 25, 'تَظُنُّ اَنْ يُّفْعَلَ بِهَا فَاقِرَةٌ ۗ', 'taẓunnu ay yuf\'ala bihā fāqirah'),
(5576, 75, 26, 'كَلَّآ اِذَا بَلَغَتِ التَّرَاقِيَۙ', 'kallā iżā balagatit-tarāqī'),
(5577, 75, 27, 'وَقِيْلَ مَنْ ۜرَاقٍۙ', 'wa qīla man rāq'),
(5578, 75, 28, 'وَّظَنَّ اَنَّهُ الْفِرَاقُۙ', 'wa ẓanna annahul-firāq'),
(5579, 75, 29, 'وَالْتَفَّتِ السَّاقُ بِالسَّاقِۙ', 'waltaffatis-sāqu bis-sāq'),
(5580, 75, 30, 'اِلٰى رَبِّكَ يَوْمَىِٕذِ ِۨالْمَسَاقُ ۗ', 'ilā rabbika yauma`iżinil-masāq'),
(5581, 75, 31, 'فَلَا صَدَّقَ وَلَا صَلّٰىۙ', 'fa lā ṣaddaqa wa lā ṣallā'),
(5582, 75, 32, 'وَلٰكِنْ كَذَّبَ وَتَوَلّٰىۙ', 'wa lāking każżaba wa tawallā'),
(5583, 75, 33, 'ثُمَّ ذَهَبَ اِلٰٓى اَهْلِهٖ يَتَمَطّٰىۗ', 'ṡumma żahaba ilā ahlihī yatamaṭṭā'),
(5584, 75, 34, 'اَوْلٰى لَكَ فَاَوْلٰىۙ', 'aulā laka fa aulā'),
(5585, 75, 35, 'ثُمَّ اَوْلٰى لَكَ فَاَوْلٰىۗ', 'ṡumma aulā laka fa aulā'),
(5586, 75, 36, 'اَيَحْسَبُ الْاِنْسَانُ اَنْ يُّتْرَكَ سُدًىۗ', 'a yaḥsabul-insānu ay yutraka sudā'),
(5587, 75, 37, 'اَلَمْ يَكُ نُطْفَةً مِّنْ مَّنِيٍّ يُّمْنٰى', 'a lam yaku nuṭfatam mim maniyyiy yumnā'),
(5588, 75, 38, 'ثُمَّ كَانَ عَلَقَةً فَخَلَقَ فَسَوّٰىۙ', 'ṡumma kāna \'alaqatan fa khalaqa fa sawwā'),
(5589, 75, 39, 'فَجَعَلَ مِنْهُ الزَّوْجَيْنِ الذَّكَرَ وَالْاُنْثٰىۗ', 'fa ja\'ala min-huz-zaujainiż-żakara wal-unṡā'),
(5590, 75, 40, 'اَلَيْسَ ذٰلِكَ بِقٰدِرٍ عَلٰٓى اَنْ يُّحْيِ َۧ الْمَوْتٰى', 'a laisa żālika biqādirin \'alā ay yuḥyiyal-mautā'),
(5591, 76, 1, 'هَلْ اَتٰى عَلَى الْاِنْسَانِ حِيْنٌ مِّنَ الدَّهْرِ لَمْ يَكُنْ شَيْـًٔا مَّذْكُوْرًا', 'hal atā \'alal-insāni ḥīnum minad-dahri lam yakun syai`am mażkụrā'),
(5592, 76, 2, 'اِنَّا خَلَقْنَا الْاِنْسَانَ مِنْ نُّطْفَةٍ اَمْشَاجٍۖ نَّبْتَلِيْهِ فَجَعَلْنٰهُ سَمِيْعًاۢ بَصِيْرًا', 'innā khalaqnal-insāna min nuṭfatin amsyājin nabtalīhi fa ja\'alnāhu samī\'an baṣīrā'),
(5593, 76, 3, 'اِنَّا هَدَيْنٰهُ السَّبِيْلَ اِمَّا شَاكِرًا وَّاِمَّا كَفُوْرًا', 'innā hadaināhus-sabīla immā syākiraw wa immā kafụrā'),
(5594, 76, 4, 'اِنَّآ اَعْتَدْنَا لِلْكٰفِرِيْنَ سَلٰسِلَا۟ وَاَغْلٰلًا وَّسَعِيْرًا', 'innā a\'tadnā lil-kāfirīna salāsila wa aglālaw wa sa\'īrā'),
(5595, 76, 5, 'اِنَّ الْاَبْرَارَ يَشْرَبُوْنَ مِنْ كَأْسٍ كَانَ مِزَاجُهَا كَافُوْرًاۚ', 'innal-abrāra yasyrabụna ming ka`sing kāna mizājuhā kāfụrā'),
(5596, 76, 6, 'عَيْنًا يَّشْرَبُ بِهَا عِبَادُ اللّٰهِ يُفَجِّرُوْنَهَا تَفْجِيْرًا', '\'ainay yasyrabu bihā \'ibādullāhi yufajjirụnahā tafjīrā'),
(5597, 76, 7, 'يُوْفُوْنَ بِالنَّذْرِ وَيَخَافُوْنَ يَوْمًا كَانَ شَرُّهٗ مُسْتَطِيْرًا', 'yụfụna bin-nażri wa yakhāfụna yaumang kāna syarruhụ mustaṭīrā'),
(5598, 76, 8, 'وَيُطْعِمُوْنَ الطَّعَامَ عَلٰى حُبِّهٖ مِسْكِيْنًا وَّيَتِيْمًا وَّاَسِيْرًا', 'wa yuṭ\'imụnaṭ-ṭa\'āma \'alā ḥubbihī miskīnaw wa yatīmaw wa asīrā'),
(5599, 76, 9, 'اِنَّمَا نُطْعِمُكُمْ لِوَجْهِ اللّٰهِ لَا نُرِيْدُ مِنْكُمْ جَزَاۤءً وَّلَا شُكُوْرًا', 'innamā nuṭ\'imukum liwaj-hillāhi lā nurīdu mingkum jazā`aw wa lā syukụrā'),
(5600, 76, 10, 'اِنَّا نَخَافُ مِنْ رَّبِّنَا يَوْمًا عَبُوْسًا قَمْطَرِيْرًا', 'innā nakhāfu mir rabbinā yauman \'abụsang qamṭarīrā'),
(5601, 76, 11, 'فَوَقٰىهُمُ اللّٰهُ شَرَّ ذٰلِكَ الْيَوْمِ وَلَقّٰىهُمْ نَضْرَةً وَّسُرُوْرًاۚ', 'fa waqāhumullāhu syarra żālikal-yaumi wa laqqāhum naḍrataw wa surụrā'),
(5602, 76, 12, 'وَجَزٰىهُمْ بِمَا صَبَرُوْا جَنَّةً وَّحَرِيْرًاۙ', 'wa jazāhum bimā ṣabarụ jannataw wa ḥarīrā'),
(5603, 76, 13, 'مُّتَّكِـِٕيْنَ فِيْهَا عَلَى الْاَرَاۤىِٕكِۚ لَا يَرَوْنَ فِيْهَا شَمْسًا وَّلَا زَمْهَرِيْرًاۚ', 'muttaki`īna fīhā \'alal-arā`ik, lā yarauna fīhā syamsaw wa lā zamharīrā'),
(5604, 76, 14, 'وَدَانِيَةً عَلَيْهِمْ ظِلٰلُهَا وَذُلِّلَتْ قُطُوْفُهَا تَذْلِيْلًا', 'wa dāniyatan \'alaihim ẓilāluhā wa żullilat quṭụfuhā tażlīlā'),
(5605, 76, 15, 'وَيُطَافُ عَلَيْهِمْ بِاٰنِيَةٍ مِّنْ فِضَّةٍ وَّاَكْوَابٍ كَانَتْ قَوَارِيْرَا۠', 'wa yuṭāfu \'alaihim bi`āniyatim min fiḍḍatiw wa akwābing kānat qawārīrā'),
(5606, 76, 16, 'قَوَارِيْرَا۟ مِنْ فِضَّةٍ قَدَّرُوْهَا تَقْدِيْرًا', 'qawārīra min fiḍḍating qaddarụhā taqdīrā'),
(5607, 76, 17, 'وَيُسْقَوْنَ فِيْهَا كَأْسًا كَانَ مِزَاجُهَا زَنْجَبِيْلًاۚ', 'wa yusqauna fīhā ka`sang kāna mizājuhā zanjabīlā'),
(5608, 76, 18, 'عَيْنًا فِيْهَا تُسَمّٰى سَلْسَبِيْلًا', '\'ainan fīhā tusammā salsabīlā'),
(5609, 76, 19, '۞ وَيَطُوْفُ عَلَيْهِمْ وِلْدَانٌ مُّخَلَّدُوْنَۚ اِذَا رَاَيْتَهُمْ حَسِبْتَهُمْ لُؤْلُؤًا مَّنْثُوْرًا', 'wa yaṭụfu \'alaihim wildānum mukhalladụn, iżā ra`aitahum ḥasibtahum lu`lu`am manṡụrā'),
(5610, 76, 20, 'وَاِذَا رَاَيْتَ ثَمَّ رَاَيْتَ نَعِيْمًا وَّمُلْكًا كَبِيْرًا', 'wa iżā ra`aita ṡamma ra`aita na\'īmaw wa mulkang kabīrā'),
(5611, 76, 21, 'عٰلِيَهُمْ ثِيَابُ سُنْدُسٍ خُضْرٌ وَّاِسْتَبْرَقٌۖ وَّحُلُّوْٓا اَسَاوِرَ مِنْ فِضَّةٍۚ وَسَقٰىهُمْ رَبُّهُمْ شَرَابًا طَهُوْرًا', '\'āliyahum ṡiyābu sundusin khuḍruw wa istabraquw wa ḥullū asāwira min fiḍḍah, wa saqāhum rabbuhum syarāban ṭahụrā'),
(5612, 76, 22, 'اِنَّ هٰذَا كَانَ لَكُمْ جَزَاۤءً وَّكَانَ سَعْيُكُمْ مَّشْكُوْرًا', 'inna hāżā kāna lakum jazā`aw wa kāna sa\'yukum masykụrā'),
(5613, 76, 23, 'اِنَّا نَحْنُ نَزَّلْنَا عَلَيْكَ الْقُرْاٰنَ تَنْزِيْلًاۚ', 'innā naḥnu nazzalnā \'alaikal-qur`āna tanzīlā'),
(5614, 76, 24, 'فَاصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تُطِعْ مِنْهُمْ اٰثِمًا اَوْ كَفُوْرًاۚ', 'faṣbir liḥukmi rabbika wa lā tuṭi\' min-hum āṡiman au kafụrā'),
(5615, 76, 25, 'وَاذْكُرِ اسْمَ رَبِّكَ بُكْرَةً وَّاَصِيْلًاۚ', 'ważkurisma rabbika bukrataw wa aṣīlā'),
(5616, 76, 26, 'وَمِنَ الَّيْلِ فَاسْجُدْ لَهٗ وَسَبِّحْهُ لَيْلًا طَوِيْلًا', 'wa minal-laili fasjud lahụ wa sabbiḥ-hu lailan ṭawīlā'),
(5617, 76, 27, 'اِنَّ هٰٓؤُلَاۤءِ يُحِبُّوْنَ الْعَاجِلَةَ وَيَذَرُوْنَ وَرَاۤءَهُمْ يَوْمًا ثَقِيْلًا', 'inna hā`ulā`i yuḥibbụnal-\'ājilata wa yażarụna warā`ahum yauman ṡaqīlā'),
(5618, 76, 28, 'نَحْنُ خَلَقْنٰهُمْ وَشَدَدْنَآ اَسْرَهُمْۚ وَاِذَا شِئْنَا بَدَّلْنَآ اَمْثَالَهُمْ تَبْدِيْلًا', 'naḥnu khalaqnāhum wa syadadnā asrahum, wa iżā syi`nā baddalnā amṡālahum tabdīlā'),
(5619, 76, 29, 'اِنَّ هٰذِهٖ تَذْكِرَةٌ ۚ فَمَنْ شَاۤءَ اتَّخَذَ اِلٰى رَبِّهٖ سَبِيْلًا', 'inna hāżihī tażkirah, fa man syā`attakhaża ilā rabbihī sabīlā'),
(5620, 76, 30, 'وَمَا تَشَاۤءُوْنَ اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ ۗاِنَّ اللّٰهَ كَانَ عَلِيْمًا حَكِيْمًاۖ', 'wa mā tasyā`ụna illā ay yasyā`allāh, innallāha kāna \'alīman ḥakīmā'),
(5621, 76, 31, 'يُّدْخِلُ مَنْ يَّشَاۤءُ فِيْ رَحْمَتِهٖۗ وَالظّٰلِمِيْنَ اَعَدَّ لَهُمْ عَذَابًا اَلِيْمًا', 'yudkhilu may yasyā`u fī raḥmatih, waẓ-ẓālimīna a\'adda lahum \'ażāban alīmā'),
(5622, 77, 1, 'وَالْمُرْسَلٰتِ عُرْفًاۙ', 'wal-mursalāti \'urfā'),
(5623, 77, 2, 'فَالْعٰصِفٰتِ عَصْفًاۙ', 'fal-\'āṣifāti \'aṣfā'),
(5624, 77, 3, 'وَّالنّٰشِرٰتِ نَشْرًاۙ', 'wan-nāsyirāti nasyrā'),
(5625, 77, 4, 'فَالْفٰرِقٰتِ فَرْقًاۙ', 'fal-fāriqāti farqā'),
(5626, 77, 5, 'فَالْمُلْقِيٰتِ ذِكْرًاۙ', 'fal-mulqiyāti żikrā'),
(5627, 77, 6, 'عُذْرًا اَوْ نُذْرًاۙ', '\'użran au nużrā'),
(5628, 77, 7, 'اِنَّمَا تُوْعَدُوْنَ لَوَاقِعٌۗ', 'innamā tụ\'adụna lawāqi\''),
(5629, 77, 8, 'فَاِذَا النُّجُوْمُ طُمِسَتْۙ', 'fa iżan-nujụmu ṭumisat'),
(5630, 77, 9, 'وَاِذَا السَّمَاۤءُ فُرِجَتْۙ', 'wa iżas-samā`u furijat'),
(5631, 77, 10, 'وَاِذَا الْجِبَالُ نُسِفَتْۙ', 'wa iżal-jibālu nusifat'),
(5632, 77, 11, 'وَاِذَا الرُّسُلُ اُقِّتَتْۗ', 'wa iżar-rusulu uqqitat'),
(5633, 77, 12, 'لِاَيِّ يَوْمٍ اُجِّلَتْۗ', 'li`ayyi yaumin ujjilat'),
(5634, 77, 13, 'لِيَوْمِ الْفَصْلِۚ', 'liyaumil-faṣl'),
(5635, 77, 14, 'وَمَآ اَدْرٰىكَ مَا يَوْمُ الْفَصْلِۗ', 'wa mā adrāka mā yaumul-faṣl'),
(5636, 77, 15, 'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ', 'wailuy yauma`iżil lil-mukażżibīn'),
(5637, 77, 16, 'اَلَمْ نُهْلِكِ الْاَوَّلِيْنَۗ', 'a lam nuhlikil-awwalīn'),
(5638, 77, 17, 'ثُمَّ نُتْبِعُهُمُ الْاٰخِرِيْنَ', 'ṡumma nutbi\'uhumul-ākhirīn'),
(5639, 77, 18, 'كَذٰلِكَ نَفْعَلُ بِالْمُجْرِمِيْنَ', 'każālika naf\'alu bil-mujrimīn'),
(5640, 77, 19, 'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ', 'wailuy yauma`iżil lil-mukażżibīn'),
(5641, 77, 20, 'اَلَمْ نَخْلُقْكُّمْ مِّنْ مَّاۤءٍ مَّهِيْنٍۙ', 'a lam nakhlukkum mim mā`im mahīn'),
(5642, 77, 21, 'فَجَعَلْنٰهُ فِيْ قَرَارٍ مَّكِيْنٍ', 'fa ja\'alnāhu fī qarārim makīn'),
(5643, 77, 22, 'اِلٰى قَدَرٍ مَّعْلُوْمٍۙ', 'ilā qadarim ma\'lụm'),
(5644, 77, 23, 'فَقَدَرْنَاۖ فَنِعْمَ الْقٰدِرُوْنَ', 'fa qadarnā fa ni\'mal-qādirụn'),
(5645, 77, 24, 'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ', 'wailuy yauma`iżil lil-mukażżibīn'),
(5646, 77, 25, 'اَلَمْ نَجْعَلِ الْاَرْضَ كِفَاتًاۙ', 'a lam naj\'alil-arḍa kifātā'),
(5647, 77, 26, 'اَحْيَاۤءً وَّاَمْوَاتًاۙ', 'aḥyā`aw wa amwātā'),
(5648, 77, 27, 'وَّجَعَلْنَا فِيْهَا رَوَاسِيَ شٰمِخٰتٍ وَّاَسْقَيْنٰكُمْ مَّاۤءً فُرَاتًاۗ', 'wa ja\'alnā fīhā rawāsiya syāmikhātiw wa asqainākum mā`an furātā'),
(5649, 77, 28, 'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ', 'wailuy yauma`iżil lil-mukażżibīn'),
(5650, 77, 29, 'اِنْطَلِقُوْٓا اِلٰى مَا كُنْتُمْ بِهٖ تُكَذِّبُوْنَۚ', 'inṭaliqū ilā mā kuntum bihī tukażżibụn'),
(5651, 77, 30, 'اِنْطَلِقُوْٓا اِلٰى ظِلٍّ ذِيْ ثَلٰثِ شُعَبٍ', 'inṭaliqū ilā ẓillin żī ṡalāṡi syu\'ab'),
(5652, 77, 31, 'لَا ظَلِيْلٍ وَّلَا يُغْنِيْ مِنَ اللَّهَبِۗ', 'lā ẓalīliw wa lā yugnī minal-lahab'),
(5653, 77, 32, 'اِنَّهَا تَرْمِيْ بِشَرَرٍ كَالْقَصْرِۚ', 'innahā tarmī bisyararing kal-qaṣr'),
(5654, 77, 33, 'كَاَنَّهٗ جِمٰلَتٌ صُفْرٌۗ', 'ka`annahụ jimālatun ṣufr'),
(5655, 77, 34, 'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ', 'wailuy yauma`iżil lil-mukażżibīn'),
(5656, 77, 35, 'هٰذَا يَوْمُ لَا يَنْطِقُوْنَۙ', 'hāżā yaumu lā yanṭiqụn'),
(5657, 77, 36, 'وَلَا يُؤْذَنُ لَهُمْ فَيَعْتَذِرُوْنَ', 'wa lā yu`żanu lahum fa ya\'tażirụn'),
(5658, 77, 37, 'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ', 'wailuy yauma`iżil lil-mukażżibīn'),
(5659, 77, 38, 'هٰذَا يَوْمُ الْفَصْلِ جَمَعْنٰكُمْ وَالْاَوَّلِيْنَ', 'hāżā yaumul-faṣli jama\'nākum wal-awwalīn'),
(5660, 77, 39, 'فَاِنْ كَانَ لَكُمْ كَيْدٌ فَكِيْدُوْنِ', 'fa ing kāna lakum kaidun fa kīdụn'),
(5661, 77, 40, 'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ', 'wailuy yauma`iżil lil-mukażżibīn'),
(5662, 77, 41, 'اِنَّ الْمُتَّقِيْنَ فِيْ ظِلٰلٍ وَّعُيُوْنٍۙ', 'innal-muttaqīna fī ẓilāliw wa \'uyụn'),
(5663, 77, 42, 'وَّفَوَاكِهَ مِمَّا يَشْتَهُوْنَۗ', 'wa fawākiha mimmā yasytahụn'),
(5664, 77, 43, 'كُلُوْا وَاشْرَبُوْا هَنِيْۤـًٔا ۢبِمَا كُنْتُمْ تَعْمَلُوْنَ', 'kulụ wasyrabụ hanī`am bimā kuntum ta\'malụn'),
(5665, 77, 44, 'اِنَّا كَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ', 'innā każālika najzil-muḥsinīn'),
(5666, 77, 45, 'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ', 'wailuy yauma`iżil lil-mukażżibīn'),
(5667, 77, 46, 'كُلُوْا وَتَمَتَّعُوْا قَلِيْلًا اِنَّكُمْ مُّجْرِمُوْنَ', 'kulụ wa tamatta\'ụ qalīlan innakum mujrimụn'),
(5668, 77, 47, 'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ', 'wailuy yauma`iżil lil-mukażżibīn'),
(5669, 77, 48, 'وَاِذَا قِيْلَ لَهُمُ ارْكَعُوْا لَا يَرْكَعُوْنَ', 'wa iżā qīla lahumurka\'ụ lā yarka\'ụn'),
(5670, 77, 49, 'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ', 'wailuy yauma`iżil lil-mukażżibīn'),
(5671, 77, 50, 'فَبِاَيِّ حَدِيْثٍۢ بَعْدَهٗ يُؤْمِنُوْنَ ۔', 'fa bi`ayyi ḥadīṡim ba\'dahụ yu`minụn'),
(5672, 78, 1, 'عَمَّ يَتَسَاۤءَلُوْنَۚ', '\'amma yatasā`alụn'),
(5673, 78, 2, 'عَنِ النَّبَاِ الْعَظِيْمِۙ', '\'anin-naba`il-\'aẓīm'),
(5674, 78, 3, 'الَّذِيْ هُمْ فِيْهِ مُخْتَلِفُوْنَۗ', 'allażī hum fīhi mukhtalifụn'),
(5675, 78, 4, 'كَلَّا سَيَعْلَمُوْنَۙ', 'kallā saya\'lamụn'),
(5676, 78, 5, 'ثُمَّ كَلَّا سَيَعْلَمُوْنَ', 'ṡumma kallā saya\'lamụn'),
(5677, 78, 6, 'اَلَمْ نَجْعَلِ الْاَرْضَ مِهٰدًاۙ', 'a lam naj\'alil-arḍa mihādā'),
(5678, 78, 7, 'وَّالْجِبَالَ اَوْتَادًاۖ', 'wal-jibāla autādā'),
(5679, 78, 8, 'وَّخَلَقْنٰكُمْ اَزْوَاجًاۙ', 'wa khalaqnākum azwājā'),
(5680, 78, 9, 'وَّجَعَلْنَا نَوْمَكُمْ سُبَاتًاۙ', 'wa ja\'alnā naumakum subātā'),
(5681, 78, 10, 'وَّجَعَلْنَا الَّيْلَ لِبَاسًاۙ', 'wa ja\'alnal-laila libāsā'),
(5682, 78, 11, 'وَّجَعَلْنَا النَّهَارَ مَعَاشًاۚ', 'wa ja\'alnan-nahāra ma\'āsyā'),
(5683, 78, 12, 'وَبَنَيْنَا فَوْقَكُمْ سَبْعًا شِدَادًاۙ', 'wa banainā fauqakum sab\'an syidādā'),
(5684, 78, 13, 'وَّجَعَلْنَا سِرَاجًا وَّهَّاجًاۖ', 'wa ja\'alnā sirājaw wahhājā'),
(5685, 78, 14, 'وَّاَنْزَلْنَا مِنَ الْمُعْصِرٰتِ مَاۤءً ثَجَّاجًاۙ', 'wa anzalnā minal-mu\'ṣirāti mā`an ṡajjājā'),
(5686, 78, 15, 'لِّنُخْرِجَ بِهٖ حَبًّا وَّنَبَاتًاۙ', 'linukhrija bihī ḥabbaw wa nabātā'),
(5687, 78, 16, 'وَّجَنّٰتٍ اَلْفَافًاۗ', 'wa jannātin alfāfā'),
(5688, 78, 17, 'اِنَّ يَوْمَ الْفَصْلِ كَانَ مِيْقَاتًاۙ', 'inna yaumal-faṣli kāna mīqātā'),
(5689, 78, 18, 'يَّوْمَ يُنْفَخُ فِى الصُّوْرِ فَتَأْتُوْنَ اَفْوَاجًاۙ', 'yauma yunfakhu fiṣ-ṣụri fa ta`tụna afwājā'),
(5690, 78, 19, 'وَّفُتِحَتِ السَّمَاۤءُ فَكَانَتْ اَبْوَابًاۙ', 'wa futiḥatis-samā`u fa kānat abwābā'),
(5691, 78, 20, 'وَّسُيِّرَتِ الْجِبَالُ فَكَانَتْ سَرَابًاۗ', 'wa suyyiratil-jibālu fa kānat sarābā'),
(5692, 78, 21, 'اِنَّ جَهَنَّمَ كَانَتْ مِرْصَادًاۙ', 'inna jahannama kānat mirṣādā'),
(5693, 78, 22, 'لِّلطّٰغِيْنَ مَاٰبًاۙ', 'liṭ-ṭāgīna ma`ābā'),
(5694, 78, 23, 'لّٰبِثِيْنَ فِيْهَآ اَحْقَابًاۚ', 'lābiṡīna fīhā aḥqābā'),
(5695, 78, 24, 'لَا يَذُوْقُوْنَ فِيْهَا بَرْدًا وَّلَا شَرَابًاۙ', 'lā yażụqụna fīhā bardaw wa lā syarābā'),
(5696, 78, 25, 'اِلَّا حَمِيْمًا وَّغَسَّاقًاۙ', 'illā ḥamīmaw wa gassāqā'),
(5697, 78, 26, 'جَزَاۤءً وِّفَاقًاۗ', 'jazā`aw wifāqā'),
(5698, 78, 27, 'اِنَّهُمْ كَانُوْا لَا يَرْجُوْنَ حِسَابًاۙ', 'innahum kānụ lā yarjụna ḥisābā'),
(5699, 78, 28, 'وَّكَذَّبُوْا بِاٰيٰتِنَا كِذَّابًاۗ', 'wa każżabụ bi`āyātinā kiżżābā'),
(5700, 78, 29, 'وَكُلَّ شَيْءٍ اَحْصَيْنٰهُ كِتٰبًاۙ', 'wa kulla syai`in aḥṣaināhu kitābā'),
(5701, 78, 30, 'فَذُوْقُوْا فَلَنْ نَّزِيْدَكُمْ اِلَّا عَذَابًا', 'fa żụqụ fa lan nazīdakum illā \'ażābā'),
(5702, 78, 31, 'اِنَّ لِلْمُتَّقِيْنَ مَفَازًاۙ', 'inna lil-muttaqīna mafāzā'),
(5703, 78, 32, 'حَدَاۤىِٕقَ وَاَعْنَابًاۙ', 'ḥadā`iqa wa a\'nābā'),
(5704, 78, 33, 'وَّكَوَاعِبَ اَتْرَابًاۙ', 'wa kawā\'iba atrābā'),
(5705, 78, 34, 'وَّكَأْسًا دِهَاقًاۗ', 'wa ka`san dihāqā'),
(5706, 78, 35, 'لَا يَسْمَعُوْنَ فِيْهَا لَغْوًا وَّلَا كِذَّابًا', 'lā yasma\'ụna fīhā lagwaw wa lā kiżżābā'),
(5707, 78, 36, 'جَزَاۤءً مِّنْ رَّبِّكَ عَطَاۤءً حِسَابًاۙ', 'jazā`am mir rabbika \'aṭā`an ḥisābā'),
(5708, 78, 37, 'رَّبِّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا الرَّحْمٰنِ لَا يَمْلِكُوْنَ مِنْهُ خِطَابًاۚ', 'rabbis-samāwāti wal-arḍi wa mā bainahumar-raḥmāni lā yamlikụna min-hu khiṭābā'),
(5709, 78, 38, 'يَوْمَ يَقُوْمُ الرُّوْحُ وَالْمَلٰۤىِٕكَةُ صَفًّاۙ لَّا يَتَكَلَّمُوْنَ اِلَّا مَنْ اَذِنَ لَهُ الرَّحْمٰنُ وَقَالَ صَوَابًا', 'yauma yaqụmur-rụḥu wal-malā`ikatu ṣaffal lā yatakallamụna illā man ażina lahur-raḥmānu wa qāla ṣawābā'),
(5710, 78, 39, 'ذٰلِكَ الْيَوْمُ الْحَقُّۚ فَمَنْ شَاۤءَ اتَّخَذَ اِلٰى رَبِّهٖ مَاٰبًا', 'żālikal-yaumul-ḥaqq, fa man syā`attakhaża ilā rabbihī ma`ābā'),
(5711, 78, 40, 'اِنَّآ اَنْذَرْنٰكُمْ عَذَابًا قَرِيْبًا ەۙ يَّوْمَ يَنْظُرُ الْمَرْءُ مَا قَدَّمَتْ يَدَاهُ وَيَقُوْلُ الْكٰفِرُ يٰلَيْتَنِيْ كُنْتُ تُرَابًا', 'innā anżarnākum \'ażābang qarībay yauma yanẓurul-mar`u mā qaddamat yadāhu wa yaqụlul-kāfiru yā laitanī kuntu turābā'),
(5712, 79, 1, 'وَالنّٰزِعٰتِ غَرْقًاۙ', 'wan-nāzi\'āti garqā'),
(5713, 79, 2, 'وَّالنّٰشِطٰتِ نَشْطًاۙ', 'wan-nāsyiṭāti nasyṭā'),
(5714, 79, 3, 'وَّالسّٰبِحٰتِ سَبْحًاۙ', 'was-sābiḥāti sab-ḥā'),
(5715, 79, 4, 'فَالسّٰبِقٰتِ سَبْقًاۙ', 'fas-sābiqāti sabqā'),
(5716, 79, 5, 'فَالْمُدَبِّرٰتِ اَمْرًاۘ', 'fal-mudabbirāti amrā'),
(5717, 79, 6, 'يَوْمَ تَرْجُفُ الرَّاجِفَةُۙ', 'yauma tarjufur-rājifah'),
(5718, 79, 7, 'تَتْبَعُهَا الرَّادِفَةُ ۗ', 'tatba\'uhar-rādifah'),
(5719, 79, 8, 'قُلُوْبٌ يَّوْمَىِٕذٍ وَّاجِفَةٌۙ', 'qulụbuy yauma`iżiw wājifah'),
(5720, 79, 9, 'اَبْصَارُهَا خَاشِعَةٌ ۘ', 'abṣāruhā khāsyi\'ah'),
(5721, 79, 10, 'يَقُوْلُوْنَ ءَاِنَّا لَمَرْدُوْدُوْنَ فِى الْحَافِرَةِۗ', 'yaqụlụna a innā lamardụdụna fil-ḥāfirah'),
(5722, 79, 11, 'ءَاِذَا كُنَّا عِظَامًا نَّخِرَةً ۗ', 'a iżā kunnā \'iẓāman nakhirah'),
(5723, 79, 12, 'قَالُوْا تِلْكَ اِذًا كَرَّةٌ خَاسِرَةٌ ۘ', 'qālụ tilka iżang karratun khāsirah'),
(5724, 79, 13, 'فَاِنَّمَا هِيَ زَجْرَةٌ وَّاحِدَةٌۙ', 'fa innamā hiya zajratuw wāḥidah'),
(5725, 79, 14, 'فَاِذَا هُمْ بِالسَّاهِرَةِۗ', 'fa iżā hum bis-sāhirah'),
(5726, 79, 15, 'هَلْ اَتٰىكَ حَدِيْثُ مُوْسٰىۘ', 'hal atāka ḥadīṡu mụsā'),
(5727, 79, 16, 'اِذْ نَادٰىهُ رَبُّهٗ بِالْوَادِ الْمُقَدَّسِ طُوًىۚ', 'iż nādāhu rabbuhụ bil-wādil-muqaddasi ṭuwā'),
(5728, 79, 17, 'اِذْهَبْ اِلٰى فِرْعَوْنَ اِنَّهٗ طَغٰىۖ', 'iż-hab ilā fir\'auna innahụ ṭagā'),
(5729, 79, 18, 'فَقُلْ هَلْ لَّكَ اِلٰٓى اَنْ تَزَكّٰىۙ', 'fa qul hal laka ilā an tazakkā'),
(5730, 79, 19, 'وَاَهْدِيَكَ اِلٰى رَبِّكَ فَتَخْشٰىۚ', 'wa ahdiyaka ilā rabbika fa takhsyā'),
(5731, 79, 20, 'فَاَرٰىهُ الْاٰيَةَ الْكُبْرٰىۖ', 'fa arāhul-āyatal-kubrā'),
(5732, 79, 21, 'فَكَذَّبَ وَعَصٰىۖ', 'fa każżaba wa \'aṣā'),
(5733, 79, 22, 'ثُمَّ اَدْبَرَ يَسْعٰىۖ', 'ṡumma adbara yas\'ā'),
(5734, 79, 23, 'فَحَشَرَ فَنَادٰىۖ', 'fa ḥasyara fa nādā'),
(5735, 79, 24, 'فَقَالَ اَنَا۠ رَبُّكُمُ الْاَعْلٰىۖ', 'fa qāla ana rabbukumul-a\'lā'),
(5736, 79, 25, 'فَاَخَذَهُ اللّٰهُ نَكَالَ الْاٰخِرَةِ وَالْاُوْلٰىۗ', 'fa akhażahullāhu nakālal-ākhirati wal-ụlā'),
(5737, 79, 26, 'اِنَّ فِيْ ذٰلِكَ لَعِبْرَةً لِّمَنْ يَّخْشٰى ۗ', 'inna fī żālika la\'ibratal limay yakhsyā'),
(5738, 79, 27, 'ءَاَنْتُمْ اَشَدُّ خَلْقًا اَمِ السَّمَاۤءُ ۚ بَنٰىهَاۗ', 'a antum asyaddu khalqan amis-samā`, banāhā'),
(5739, 79, 28, 'رَفَعَ سَمْكَهَا فَسَوّٰىهَاۙ', 'rafa\'a samkahā fa sawwāhā'),
(5740, 79, 29, 'وَاَغْطَشَ لَيْلَهَا وَاَخْرَجَ ضُحٰىهَاۖ', 'wa agṭasya lailahā wa akhraja ḍuḥāhā'),
(5741, 79, 30, 'وَالْاَرْضَ بَعْدَ ذٰلِكَ دَحٰىهَاۗ', 'wal-arḍa ba\'da żālika daḥāhā'),
(5742, 79, 31, 'اَخْرَجَ مِنْهَا مَاۤءَهَا وَمَرْعٰىهَاۖ', 'akhraja min-hā mā`ahā wa mar\'āhā'),
(5743, 79, 32, 'وَالْجِبَالَ اَرْسٰىهَاۙ', 'wal-jibāla arsāhā'),
(5744, 79, 33, 'مَتَاعًا لَّكُمْ وَلِاَنْعَامِكُمْۗ', 'matā\'al lakum wa li`an\'āmikum'),
(5745, 79, 34, 'فَاِذَا جَاۤءَتِ الطَّاۤمَّةُ الْكُبْرٰىۖ', 'fa iżā jā`atiṭ-ṭāmmatul-kubrā'),
(5746, 79, 35, 'يَوْمَ يَتَذَكَّرُ الْاِنْسَانُ مَا سَعٰىۙ', 'yauma yatażakkarul-insānu mā sa\'ā'),
(5747, 79, 36, 'وَبُرِّزَتِ الْجَحِيْمُ لِمَنْ يَّرٰى', 'wa burrizatil-jaḥīmu limay yarā'),
(5748, 79, 37, 'فَاَمَّا مَنْ طَغٰىۖ', 'fa ammā man ṭagā'),
(5749, 79, 38, 'وَاٰثَرَ الْحَيٰوةَ الدُّنْيَاۙ', 'wa āṡaral-ḥayātad-dun-yā'),
(5750, 79, 39, 'فَاِنَّ الْجَحِيْمَ هِيَ الْمَأْوٰىۗ', 'fa innal-jaḥīma hiyal-ma`wā'),
(5751, 79, 40, 'وَاَمَّا مَنْ خَافَ مَقَامَ رَبِّهٖ وَنَهَى النَّفْسَ عَنِ الْهَوٰىۙ', 'wa ammā man khāfa maqāma rabbihī wa nahan-nafsa \'anil-hawā'),
(5752, 79, 41, 'فَاِنَّ الْجَنَّةَ هِيَ الْمَأْوٰىۗ', 'fa innal-jannata hiyal-ma`wā'),
(5753, 79, 42, 'يَسْـَٔلُوْنَكَ عَنِ السَّاعَةِ اَيَّانَ مُرْسٰىهَاۗ', 'yas`alụnaka \'anis-sā\'ati ayyāna mursāhā'),
(5754, 79, 43, 'فِيْمَ اَنْتَ مِنْ ذِكْرٰىهَاۗ', 'fīma anta min żikrāhā'),
(5755, 79, 44, 'اِلٰى رَبِّكَ مُنْتَهٰىهَاۗ', 'ilā rabbika muntahāhā'),
(5756, 79, 45, 'اِنَّمَآ اَنْتَ مُنْذِرُ مَنْ يَّخْشٰىهَاۗ', 'innamā anta munżiru may yakhsyāhā'),
(5757, 79, 46, 'كَاَنَّهُمْ يَوْمَ يَرَوْنَهَا لَمْ يَلْبَثُوْٓا اِلَّا عَشِيَّةً اَوْ ضُحٰىهَا', 'ka`annahum yauma yaraunahā lam yalbaṡū illā \'asyiyyatan au ḍuḥāhā'),
(5758, 80, 1, 'عَبَسَ وَتَوَلّٰىٓۙ', '\'abasa wa tawallā'),
(5759, 80, 2, 'اَنْ جَاۤءَهُ الْاَعْمٰىۗ', 'an jā`ahul-a\'mā'),
(5760, 80, 3, 'وَمَا يُدْرِيْكَ لَعَلَّهٗ يَزَّكّٰىٓۙ', 'wa mā yudrīka la\'allahụ yazzakkā'),
(5761, 80, 4, 'اَوْ يَذَّكَّرُ فَتَنْفَعَهُ الذِّكْرٰىۗ', 'au yażżakkaru fa tanfa\'ahuż-żikrā'),
(5762, 80, 5, 'اَمَّا مَنِ اسْتَغْنٰىۙ', 'ammā manistagnā'),
(5763, 80, 6, 'فَاَنْتَ لَهٗ تَصَدّٰىۗ', 'fa anta lahụ taṣaddā'),
(5764, 80, 7, 'وَمَا عَلَيْكَ اَلَّا يَزَّكّٰىۗ', 'wa mā \'alaika allā yazzakkā'),
(5765, 80, 8, 'وَاَمَّا مَنْ جَاۤءَكَ يَسْعٰىۙ', 'wa ammā man jā`aka yas\'ā'),
(5766, 80, 9, 'وَهُوَ يَخْشٰىۙ', 'wa huwa yakhsyā'),
(5767, 80, 10, 'فَاَنْتَ عَنْهُ تَلَهّٰىۚ', 'fa anta \'an-hu talahhā'),
(5768, 80, 11, 'كَلَّآ اِنَّهَا تَذْكِرَةٌ ۚ', 'kallā innahā tażkirah'),
(5769, 80, 12, 'فَمَنْ شَاۤءَ ذَكَرَهٗ ۘ', 'fa man syā`a żakarah'),
(5770, 80, 13, 'فِيْ صُحُفٍ مُّكَرَّمَةٍۙ', 'fī ṣuḥufim mukarramah'),
(5771, 80, 14, 'مَّرْفُوْعَةٍ مُّطَهَّرَةٍ ۢ ۙ', 'marfụ\'atim muṭahharah'),
(5772, 80, 15, 'بِاَيْدِيْ سَفَرَةٍۙ', 'bi`aidī safarah'),
(5773, 80, 16, 'كِرَامٍۢ بَرَرَةٍۗ', 'kirāmim bararah'),
(5774, 80, 17, 'قُتِلَ الْاِنْسَانُ مَآ اَكْفَرَهٗۗ', 'qutilal-insānu mā akfarah'),
(5775, 80, 18, 'مِنْ اَيِّ شَيْءٍ خَلَقَهٗۗ', 'min ayyi syai`in khalaqah'),
(5776, 80, 19, 'مِنْ نُّطْفَةٍۗ خَلَقَهٗ فَقَدَّرَهٗۗ', 'min nuṭfah, khalaqahụ fa qaddarah'),
(5777, 80, 20, 'ثُمَّ السَّبِيْلَ يَسَّرَهٗۙ', 'ṡummas-sabīla yassarah'),
(5778, 80, 21, 'ثُمَّ اَمَاتَهٗ فَاَقْبَرَهٗۙ', 'ṡumma amātahụ fa aqbarah'),
(5779, 80, 22, 'ثُمَّ اِذَا شَاۤءَ اَنْشَرَهٗۗ', 'ṡumma iżā syā`a ansyarah'),
(5780, 80, 23, 'كَلَّا لَمَّا يَقْضِ مَآ اَمَرَهٗۗ', 'kallā lammā yaqḍi mā amarah'),
(5781, 80, 24, 'فَلْيَنْظُرِ الْاِنْسَانُ اِلٰى طَعَامِهٖٓ ۙ', 'falyanẓuril-insānu ilā ṭa\'āmih'),
(5782, 80, 25, 'اَنَّا صَبَبْنَا الْمَاۤءَ صَبًّاۙ', 'annā ṣababnal-mā`a ṣabbā'),
(5783, 80, 26, 'ثُمَّ شَقَقْنَا الْاَرْضَ شَقًّاۙ', 'ṡumma syaqaqnal-arḍa syaqqā'),
(5784, 80, 27, 'فَاَنْۢبَتْنَا فِيْهَا حَبًّاۙ', 'fa ambatnā fīhā ḥabbā'),
(5785, 80, 28, 'وَّعِنَبًا وَّقَضْبًاۙ', 'wa \'inabaw wa qaḍbā'),
(5786, 80, 29, 'وَّزَيْتُوْنًا وَّنَخْلًاۙ', 'wa zaitụnaw wa nakhlā'),
(5787, 80, 30, 'وَّحَدَاۤئِقَ غُلْبًا', 'wa ḥadā`iqa gulbā'),
(5788, 80, 31, 'وَفَاكِهَةً وَّاَبًّا', 'wa fākihataw wa abbā'),
(5789, 80, 32, 'مَتَاعًا لَّكُمْ وَلِاَنْعَامِكُمْۗ', 'matā\'al lakum wa li`an\'āmikum'),
(5790, 80, 33, 'فَاِذَا جَاۤءَتِ الصَّاۤخَّةُ ۖ', 'fa iżā jā`atiṣ-ṣākhkhah'),
(5791, 80, 34, 'يَوْمَ يَفِرُّ الْمَرْءُ مِنْ اَخِيْهِۙ', 'yauma yafirrul-mar`u min akhīh'),
(5792, 80, 35, 'وَاُمِّهٖ وَاَبِيْهِۙ', 'wa ummihī wa abīh'),
(5793, 80, 36, 'وَصَاحِبَتِهٖ وَبَنِيْهِۗ', 'wa ṣāḥibatihī wa banīh'),
(5794, 80, 37, 'لِكُلِّ امْرِئٍ مِّنْهُمْ يَوْمَىِٕذٍ شَأْنٌ يُّغْنِيْهِۗ', 'likullimri`im min-hum yauma`iżin sya`nuy yugnīh'),
(5795, 80, 38, 'وُجُوْهٌ يَّوْمَىِٕذٍ مُّسْفِرَةٌۙ', 'wujụhuy yauma`iżim musfirah'),
(5796, 80, 39, 'ضَاحِكَةٌ مُّسْتَبْشِرَةٌ ۚ', 'ḍāḥikatum mustabsyirah'),
(5797, 80, 40, 'وَوُجُوْهٌ يَّوْمَىِٕذٍ عَلَيْهَا غَبَرَةٌۙ', 'wa wujụhuy yauma`iżin \'alaihā gabarah'),
(5798, 80, 41, 'تَرْهَقُهَا قَتَرَةٌ ۗ', 'tarhaquhā qatarah'),
(5799, 80, 42, 'اُولٰۤىِٕكَ هُمُ الْكَفَرَةُ الْفَجَرَةُ', 'ulā`ika humul-kafaratul-fajarah'),
(5800, 81, 1, 'اِذَا الشَّمْسُ كُوِّرَتْۖ', 'iżasy-syamsu kuwwirat'),
(5801, 81, 2, 'وَاِذَا النُّجُوْمُ انْكَدَرَتْۖ', 'wa iżan-nujụmungkadarat'),
(5802, 81, 3, 'وَاِذَا الْجِبَالُ سُيِّرَتْۖ', 'wa iżal-jibālu suyyirat'),
(5803, 81, 4, 'وَاِذَا الْعِشَارُ عُطِّلَتْۖ', 'wa iżal-\'isyāru \'uṭṭilat'),
(5804, 81, 5, 'وَاِذَا الْوُحُوْشُ حُشِرَتْۖ', 'wa iżal-wuḥụsyu ḥusyirat'),
(5805, 81, 6, 'وَاِذَا الْبِحَارُ سُجِّرَتْۖ', 'wa iżal-biḥāru sujjirat'),
(5806, 81, 7, 'وَاِذَا النُّفُوْسُ زُوِّجَتْۖ', 'wa iżan-nufụsu zuwwijat'),
(5807, 81, 8, 'وَاِذَا الْمَوْءٗدَةُ سُىِٕلَتْۖ', 'wa iżal-mau`ụdatu su`ilat'),
(5808, 81, 9, 'بِاَيِّ ذَنْۢبٍ قُتِلَتْۚ', 'bi`ayyi żambing qutilat'),
(5809, 81, 10, 'وَاِذَا الصُّحُفُ نُشِرَتْۖ', 'wa iżaṣ-ṣuḥufu nusyirat'),
(5810, 81, 11, 'وَاِذَا السَّمَاۤءُ كُشِطَتْۖ', 'wa iżas-samā`u kusyiṭat'),
(5811, 81, 12, 'وَاِذَا الْجَحِيْمُ سُعِّرَتْۖ', 'wa iżal-jaḥīmu su\'\'irat'),
(5812, 81, 13, 'وَاِذَا الْجَنَّةُ اُزْلِفَتْۖ', 'wa iżal-jannatu uzlifat'),
(5813, 81, 14, 'عَلِمَتْ نَفْسٌ مَّآ اَحْضَرَتْۗ', '\'alimat nafsum mā aḥḍarat'),
(5814, 81, 15, 'فَلَآ اُقْسِمُ بِالْخُنَّسِۙ', 'fa lā uqsimu bil-khunnas'),
(5815, 81, 16, 'الْجَوَارِ الْكُنَّسِۙ', 'al-jawāril-kunnas'),
(5816, 81, 17, 'وَالَّيْلِ اِذَا عَسْعَسَۙ', 'wal-laili iżā \'as\'as'),
(5817, 81, 18, 'وَالصُّبْحِ اِذَا تَنَفَّسَۙ', 'waṣ-ṣub-ḥi iżā tanaffas'),
(5818, 81, 19, 'اِنَّهٗ لَقَوْلُ رَسُوْلٍ كَرِيْمٍۙ', 'innahụ laqaulu rasụling karīm'),
(5819, 81, 20, 'ذِيْ قُوَّةٍ عِنْدَ ذِى الْعَرْشِ مَكِيْنٍۙ', 'żī quwwatin \'inda żil-\'arsyi makīn'),
(5820, 81, 21, 'مُّطَاعٍ ثَمَّ اَمِيْنٍۗ', 'muṭā\'in ṡamma amīn'),
(5821, 81, 22, 'وَمَا صَاحِبُكُمْ بِمَجْنُوْنٍۚ', 'wa mā ṣāḥibukum bimajnụn'),
(5822, 81, 23, 'وَلَقَدْ رَاٰهُ بِالْاُفُقِ الْمُبِيْنِۚ', 'wa laqad ra`āhu bil-ufuqil-mubīn'),
(5823, 81, 24, 'وَمَا هُوَ عَلَى الْغَيْبِ بِضَنِيْنٍۚ', 'wa mā huwa \'alal-gaibi biḍanīn'),
(5824, 81, 25, 'وَمَا هُوَ بِقَوْلِ شَيْطٰنٍ رَّجِيْمٍۚ', 'wa mā huwa biqauli syaiṭānir rajīm'),
(5825, 81, 26, 'فَاَيْنَ تَذْهَبُوْنَۗ', 'fa aina taż-habụn'),
(5826, 81, 27, 'اِنْ هُوَ اِلَّا ذِكْرٌ لِّلْعٰلَمِيْنَۙ', 'in huwa illā żikrul lil-\'ālamīn'),
(5827, 81, 28, 'لِمَنْ شَاۤءَ مِنْكُمْ اَنْ يَّسْتَقِيْمَۗ', 'liman syā`a mingkum ay yastaqīm');
INSERT INTO `quran_id` (`id`, `suraId`, `verseID`, `ayahText`, `readText`) VALUES
(5828, 81, 29, 'وَمَا تَشَاۤءُوْنَ اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ رَبُّ الْعٰلَمِيْنَ', 'wa mā tasyā`ụna illā ay yasyā`allāhu rabbul-\'ālamīn'),
(5829, 82, 1, 'اِذَا السَّمَاۤءُ انْفَطَرَتْۙ', 'iżas-samā`unfaṭarat'),
(5830, 82, 2, 'وَاِذَا الْكَوَاكِبُ انْتَثَرَتْۙ', 'wa iżal-kawākibuntaṡarat'),
(5831, 82, 3, 'وَاِذَا الْبِحَارُ فُجِّرَتْۙ', 'wa iżal-biḥāru fujjirat'),
(5832, 82, 4, 'وَاِذَا الْقُبُوْرُ بُعْثِرَتْۙ', 'wa iżal-qubụru bu\'ṡirat'),
(5833, 82, 5, 'عَلِمَتْ نَفْسٌ مَّا قَدَّمَتْ وَاَخَّرَتْۗ', '\'alimat nafsum mā qaddamat wa akhkharat'),
(5834, 82, 6, 'يٰٓاَيُّهَا الْاِنْسَانُ مَا غَرَّكَ بِرَبِّكَ الْكَرِيْمِۙ', 'yā ayyuhal-insānu mā garraka birabbikal-karīm'),
(5835, 82, 7, 'الَّذِيْ خَلَقَكَ فَسَوّٰىكَ فَعَدَلَكَۙ', 'allażī khalaqaka fa sawwāka fa \'adalak'),
(5836, 82, 8, 'فِيْٓ اَيِّ صُوْرَةٍ مَّا شَاۤءَ رَكَّبَكَۗ', 'fī ayyi ṣụratim mā syā`a rakkabak'),
(5837, 82, 9, 'كَلَّا بَلْ تُكَذِّبُوْنَ بِالدِّيْنِۙ', 'kallā bal tukażżibụna bid-dīn'),
(5838, 82, 10, 'وَاِنَّ عَلَيْكُمْ لَحٰفِظِيْنَۙ', 'wa inna \'alaikum laḥāfiẓīn'),
(5839, 82, 11, 'كِرَامًا كَاتِبِيْنَۙ', 'kirāmang kātibīn'),
(5840, 82, 12, 'يَعْلَمُوْنَ مَا تَفْعَلُوْنَ', 'ya\'lamụna mā taf\'alụn'),
(5841, 82, 13, 'اِنَّ الْاَبْرَارَ لَفِيْ نَعِيْمٍۙ', 'innal-abrāra lafī na\'īm'),
(5842, 82, 14, 'وَّاِنَّ الْفُجَّارَ لَفِيْ جَحِيْمٍ', 'wa innal-fujjāra lafī jaḥīm'),
(5843, 82, 15, 'يَصْلَوْنَهَا يَوْمَ الدِّيْنِ', 'yaṣlaunahā yaumad-dīn'),
(5844, 82, 16, 'وَمَا هُمْ عَنْهَا بِغَاۤىِٕبِيْنَۗ', 'wa mā hum \'an-hā bigā`ibīn'),
(5845, 82, 17, 'وَمَآ اَدْرٰىكَ مَا يَوْمُ الدِّيْنِۙ', 'wa mā adrāka mā yaumud-dīn'),
(5846, 82, 18, 'ثُمَّ مَآ اَدْرٰىكَ مَا يَوْمُ الدِّيْنِۗ', 'ṡumma mā adrāka mā yaumud-dīn'),
(5847, 82, 19, 'يَوْمَ لَا تَمْلِكُ نَفْسٌ لِّنَفْسٍ شَيْـًٔا ۗوَالْاَمْرُ يَوْمَىِٕذٍ لِّلّٰهِ', 'yauma lā tamliku nafsul linafsin syai`ā, wal-amru yauma`iżil lillāh'),
(5848, 83, 1, 'وَيْلٌ لِّلْمُطَفِّفِيْنَۙ', 'wailul lil-muṭaffifīn'),
(5849, 83, 2, 'الَّذِيْنَ اِذَا اكْتَالُوْا عَلَى النَّاسِ يَسْتَوْفُوْنَۖ', 'allażīna iżaktālụ \'alan-nāsi yastaufụn'),
(5850, 83, 3, 'وَاِذَا كَالُوْهُمْ اَوْ وَّزَنُوْهُمْ يُخْسِرُوْنَۗ', 'wa iżā kālụhum aw wazanụhum yukhsirụn'),
(5851, 83, 4, 'اَلَا يَظُنُّ اُولٰۤىِٕكَ اَنَّهُمْ مَّبْعُوْثُوْنَۙ', 'alā yaẓunnu ulā`ika annahum mab\'ụṡụn'),
(5852, 83, 5, 'لِيَوْمٍ عَظِيْمٍۙ', 'liyaumin \'aẓīm'),
(5853, 83, 6, 'يَّوْمَ يَقُوْمُ النَّاسُ لِرَبِّ الْعٰلَمِيْنَۗ', 'yauma yaqụmun-nāsu lirabbil-\'ālamīn'),
(5854, 83, 7, 'كَلَّآ اِنَّ كِتٰبَ الْفُجَّارِ لَفِيْ سِجِّيْنٍۗ', 'kallā inna kitābal-fujjāri lafī sijjīn'),
(5855, 83, 8, 'وَمَآ اَدْرٰىكَ مَا سِجِّيْنٌۗ', 'wa mā adrāka mā sijjīn'),
(5856, 83, 9, 'كِتٰبٌ مَّرْقُوْمٌۗ', 'kitābum marqụm'),
(5857, 83, 10, 'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَۙ', 'wailuy yauma`iżil lil-mukażżibīn'),
(5858, 83, 11, 'الَّذِيْنَ يُكَذِّبُوْنَ بِيَوْمِ الدِّيْنِۗ', 'allażīna yukażżibụna biyaumid-dīn'),
(5859, 83, 12, 'وَمَا يُكَذِّبُ بِهٖٓ اِلَّا كُلُّ مُعْتَدٍ اَثِيْمٍۙ', 'wa mā yukażżibu bihī illā kullu mu\'tadin aṡīm'),
(5860, 83, 13, 'اِذَا تُتْلٰى عَلَيْهِ اٰيٰتُنَا قَالَ اَسَاطِيْرُ الْاَوَّلِيْنَۗ', 'iżā tutlā \'alaihi āyātunā qāla asāṭīrul-awwalīn'),
(5861, 83, 14, 'كَلَّا بَلْ ۜرَانَ عَلٰى قُلُوْبِهِمْ مَّا كَانُوْا يَكْسِبُوْنَ', 'kallā bal rāna \'alā qulụbihim mā kānụ yaksibụn'),
(5862, 83, 15, 'كَلَّآ اِنَّهُمْ عَنْ رَّبِّهِمْ يَوْمَىِٕذٍ لَّمَحْجُوْبُوْنَۗ', 'kallā innahum \'ar rabbihim yauma`iżil lamaḥjụbụn'),
(5863, 83, 16, 'ثُمَّ اِنَّهُمْ لَصَالُوا الْجَحِيْمِۗ', 'ṡumma innahum laṣālul-jaḥīm'),
(5864, 83, 17, 'ثُمَّ يُقَالُ هٰذَا الَّذِيْ كُنْتُمْ بِهٖ تُكَذِّبُوْنَۗ', 'ṡumma yuqālu hāżallażī kuntum bihī tukażżibụn'),
(5865, 83, 18, 'كَلَّآ اِنَّ كِتٰبَ الْاَبْرَارِ لَفِيْ عِلِّيِّيْنَۗ', 'kallā inna kitābal-abrāri lafī \'illiyyīn'),
(5866, 83, 19, 'وَمَآ اَدْرٰىكَ مَا عِلِّيُّوْنَۗ', 'wa mā adrāka mā \'illiyyụn'),
(5867, 83, 20, 'كِتٰبٌ مَّرْقُوْمٌۙ', 'kitābum marqụm'),
(5868, 83, 21, 'يَّشْهَدُهُ الْمُقَرَّبُوْنَۗ', 'yasy-haduhul-muqarrabụn'),
(5869, 83, 22, 'اِنَّ الْاَبْرَارَ لَفِيْ نَعِيْمٍۙ', 'innal-abrāra lafī na\'īm'),
(5870, 83, 23, 'عَلَى الْاَرَاۤىِٕكِ يَنْظُرُوْنَۙ', '\'alal-arā`iki yanẓurụn'),
(5871, 83, 24, 'تَعْرِفُ فِيْ وُجُوْهِهِمْ نَضْرَةَ النَّعِيْمِۚ', 'ta\'rifu fī wujụhihim naḍratan na\'īm'),
(5872, 83, 25, 'يُسْقَوْنَ مِنْ رَّحِيْقٍ مَّخْتُوْمٍۙ', 'yusqauna mir raḥīqim makhtụm'),
(5873, 83, 26, 'خِتٰمُهٗ مِسْكٌ ۗوَفِيْ ذٰلِكَ فَلْيَتَنَافَسِ الْمُتَنَافِسُوْنَۗ', 'khitāmuhụ misk, wa fī żālika falyatanāfasil-mutanāfisụn'),
(5874, 83, 27, 'وَمِزَاجُهٗ مِنْ تَسْنِيْمٍۙ', 'wa mizājuhụ min tasnīm'),
(5875, 83, 28, 'عَيْنًا يَّشْرَبُ بِهَا الْمُقَرَّبُوْنَۗ', '\'ainay yasyrabu bihal-muqarrabụn'),
(5876, 83, 29, 'اِنَّ الَّذِيْنَ اَجْرَمُوْا كَانُوْا مِنَ الَّذِيْنَ اٰمَنُوْا يَضْحَكُوْنَۖ', 'innallażīna ajramụ kānụ minallażīna āmanụ yaḍ-ḥakụn'),
(5877, 83, 30, 'وَاِذَا مَرُّوْا بِهِمْ يَتَغَامَزُوْنَۖ', 'wa iżā marrụ bihim yatagāmazụn'),
(5878, 83, 31, 'وَاِذَا انْقَلَبُوْٓا اِلٰٓى اَهْلِهِمُ انْقَلَبُوْا فَكِهِيْنَۖ', 'wa iżangqalabū ilā ahlihimungqalabụ fakihīn'),
(5879, 83, 32, 'وَاِذَا رَاَوْهُمْ قَالُوْٓا اِنَّ هٰٓؤُلَاۤءِ لَضَاۤلُّوْنَۙ', 'wa iżā ra`auhum qālū inna hā`ulā`i laḍāllụn'),
(5880, 83, 33, 'وَمَآ اُرْسِلُوْا عَلَيْهِمْ حٰفِظِيْنَۗ', 'wa mā ursilụ \'alaihim ḥāfiẓīn'),
(5881, 83, 34, 'فَالْيَوْمَ الَّذِيْنَ اٰمَنُوْا مِنَ الْكُفَّارِ يَضْحَكُوْنَۙ', 'fal-yaumallażīna āmanụ minal-kuffāri yaḍ-ḥakụn'),
(5882, 83, 35, 'عَلَى الْاَرَاۤىِٕكِ يَنْظُرُوْنَۗ', '\'alal-arā`iki yanẓurụn'),
(5883, 83, 36, 'هَلْ ثُوِّبَ الْكُفَّارُ مَا كَانُوْا يَفْعَلُوْنَ', 'hal ṡuwwibal-kuffāru mā kānụ yaf\'alụn'),
(5884, 84, 1, 'اِذَا السَّمَاۤءُ انْشَقَّتْۙ', 'iżas-samā`unsyaqqat'),
(5885, 84, 2, 'وَاَذِنَتْ لِرَبِّهَا وَحُقَّتْۙ', 'wa ażinat lirabbihā wa ḥuqqat'),
(5886, 84, 3, 'وَاِذَا الْاَرْضُ مُدَّتْۙ', 'wa iżal-arḍu muddat'),
(5887, 84, 4, 'وَاَلْقَتْ مَا فِيْهَا وَتَخَلَّتْۙ', 'wa alqat mā fīhā wa takhallat'),
(5888, 84, 5, 'وَاَذِنَتْ لِرَبِّهَا وَحُقَّتْۗ', 'wa ażinat lirabbihā wa ḥuqqat'),
(5889, 84, 6, 'يٰٓاَيُّهَا الْاِنْسَانُ اِنَّكَ كَادِحٌ اِلٰى رَبِّكَ كَدْحًا فَمُلٰقِيْهِۚ', 'yā ayyuhal-insānu innaka kādiḥun ilā rabbika kad-ḥan fa mulāqīh'),
(5890, 84, 7, 'فَاَمَّا مَنْ اُوْتِيَ كِتٰبَهٗ بِيَمِيْنِهٖۙ', 'fa ammā man ụtiya kitābahụ biyamīnih'),
(5891, 84, 8, 'فَسَوْفَ يُحَاسَبُ حِسَابًا يَّسِيْرًاۙ', 'fa saufa yuḥāsabu ḥisābay yasīrā'),
(5892, 84, 9, 'وَّيَنْقَلِبُ اِلٰٓى اَهْلِهٖ مَسْرُوْرًاۗ', 'wa yangqalibu ilā ahlihī masrụrā'),
(5893, 84, 10, 'وَاَمَّا مَنْ اُوْتِيَ كِتٰبَهٗ وَرَاۤءَ ظَهْرِهٖۙ', 'wa ammā man ụtiya kitābahụ warā`a ẓahrih'),
(5894, 84, 11, 'فَسَوْفَ يَدْعُوْ ثُبُوْرًاۙ', 'fa saufa yad\'ụ ṡubụrā'),
(5895, 84, 12, 'وَّيَصْلٰى سَعِيْرًاۗ', 'wa yaṣlā sa\'īrā'),
(5896, 84, 13, 'اِنَّهٗ كَانَ فِيْٓ اَهْلِهٖ مَسْرُوْرًاۗ', 'innahụ kāna fī ahlihī masrụrā'),
(5897, 84, 14, 'اِنَّهٗ ظَنَّ اَنْ لَّنْ يَّحُوْرَ ۛ', 'innahụ ẓanna al lay yaḥụr'),
(5898, 84, 15, 'بَلٰىۛ اِنَّ رَبَّهٗ كَانَ بِهٖ بَصِيْرًاۗ', 'balā inna rabbahụ kāna bihī baṣīrā'),
(5899, 84, 16, 'فَلَآ اُقْسِمُ بِالشَّفَقِۙ', 'fa lā uqsimu bisy-syafaq'),
(5900, 84, 17, 'وَالَّيْلِ وَمَا وَسَقَۙ', 'wal-laili wa mā wasaq'),
(5901, 84, 18, 'وَالْقَمَرِ اِذَا اتَّسَقَۙ', 'wal-qamari iżattasaq'),
(5902, 84, 19, 'لَتَرْكَبُنَّ طَبَقًا عَنْ طَبَقٍۗ', 'latarkabunna ṭabaqan \'an ṭabaq'),
(5903, 84, 20, 'فَمَا لَهُمْ لَا يُؤْمِنُوْنَۙ', 'fa mā lahum lā yu`minụn'),
(5904, 84, 21, 'وَاِذَا قُرِئَ عَلَيْهِمُ الْقُرْاٰنُ لَا يَسْجُدُوْنَ ۗ ۩', 'wa iżā quri`a \'alaihimul-qur`ānu lā yasjudụn'),
(5905, 84, 22, 'بَلِ الَّذِيْنَ كَفَرُوْا يُكَذِّبُوْنَۖ', 'balillażīna kafarụ yukażżibụn'),
(5906, 84, 23, 'وَاللّٰهُ اَعْلَمُ بِمَا يُوْعُوْنَۖ', 'wallāhu a\'lamu bimā yụ\'ụn'),
(5907, 84, 24, 'فَبَشِّرْهُمْ بِعَذَابٍ اَلِيْمٍۙ', 'fa basysyir-hum bi\'ażābin alīm'),
(5908, 84, 25, 'اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ اَجْرٌ غَيْرُ مَمْنُوْنٍ', 'illallażīna āmanụ wa \'amiluṣ-ṣāliḥāti lahum ajrun gairu mamnụn'),
(5909, 85, 1, 'وَالسَّمَاۤءِ ذَاتِ الْبُرُوْجِۙ', 'was-samā`i żātil-burụj'),
(5910, 85, 2, 'وَالْيَوْمِ الْمَوْعُوْدِۙ', 'wal-yaumil-mau\'ụd'),
(5911, 85, 3, 'وَشَاهِدٍ وَّمَشْهُوْدٍۗ', 'wa syāhidiw wa masy-hụd'),
(5912, 85, 4, 'قُتِلَ اَصْحٰبُ الْاُخْدُوْدِۙ', 'qutila aṣ-ḥābul-ukhdụd'),
(5913, 85, 5, 'النَّارِ ذَاتِ الْوَقُوْدِۙ', 'an-nāri żātil-waqụd'),
(5914, 85, 6, 'اِذْ هُمْ عَلَيْهَا قُعُوْدٌۙ', 'iż hum \'alaihā qu\'ụd'),
(5915, 85, 7, 'وَّهُمْ عَلٰى مَا يَفْعَلُوْنَ بِالْمُؤْمِنِيْنَ شُهُوْدٌ ۗ', 'wa hum \'alā mā yaf\'alụna bil-mu`minīna syuhụd'),
(5916, 85, 8, 'وَمَا نَقَمُوْا مِنْهُمْ اِلَّآ اَنْ يُّؤْمِنُوْا بِاللّٰهِ الْعَزِيْزِ الْحَمِيْدِۙ', 'wa mā naqamụ min-hum illā ay yu`minụ billāhil-\'azīzil-ḥamīd'),
(5917, 85, 9, 'الَّذِيْ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ ۗوَاللّٰهُ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ ۗ', 'allażī lahụ mulkus-samāwāti wal-arḍ, wallāhu \'alā kulli syai`in syahīd'),
(5918, 85, 10, 'اِنَّ الَّذِيْنَ فَتَنُوا الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ ثُمَّ لَمْ يَتُوْبُوْا فَلَهُمْ عَذَابُ جَهَنَّمَ وَلَهُمْ عَذَابُ الْحَرِيْقِۗ', 'innallażīna fatanul-mu`minīna wal-mu`mināti ṡumma lam yatụbụ fa lahum \'ażābu jahannama wa lahum \'ażābul-ḥarīq'),
(5919, 85, 11, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ جَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ ەۗ ذٰلِكَ الْفَوْزُ الْكَبِيْرُۗ', 'innallażīna āmanụ wa \'amiluṣ-ṣāliḥāti lahum jannātun tajrī min taḥtihal-an-hār, żālikal-fauzul-kabīr'),
(5920, 85, 12, 'اِنَّ بَطْشَ رَبِّكَ لَشَدِيْدٌ ۗ', 'inna baṭsya rabbika lasyadīd'),
(5921, 85, 13, 'اِنَّهٗ هُوَ يُبْدِئُ وَيُعِيْدُۚ', 'innahụ huwa yubdi`u wa yu\'īd'),
(5922, 85, 14, 'وَهُوَ الْغَفُوْرُ الْوَدُوْدُۙ', 'wa huwal-gafụrul-wadụd'),
(5923, 85, 15, 'ذُو الْعَرْشِ الْمَجِيْدُۙ', 'żul-\'arsyil-majīd'),
(5924, 85, 16, 'فَعَّالٌ لِّمَا يُرِيْدُۗ', 'fa\'\'ālul limā yurīd'),
(5925, 85, 17, 'هَلْ اَتٰىكَ حَدِيْثُ الْجُنُوْدِۙ', 'hal atāka ḥadīṡul-junụd'),
(5926, 85, 18, 'فِرْعَوْنَ وَثَمُوْدَۗ', 'fir\'auna wa ṡamụd'),
(5927, 85, 19, 'بَلِ الَّذِيْنَ كَفَرُوْا فِيْ تَكْذِيْبٍۙ', 'balillażīna kafarụ fī takżīb'),
(5928, 85, 20, 'وَّاللّٰهُ مِنْ وَّرَاۤىِٕهِمْ مُّحِيْطٌۚ', 'wallāhu miw warā`ihim muḥīṭ'),
(5929, 85, 21, 'بَلْ هُوَ قُرْاٰنٌ مَّجِيْدٌۙ', 'bal huwa qur`ānum majīd'),
(5930, 85, 22, 'فِيْ لَوْحٍ مَّحْفُوْظٍ', 'fī lauḥim maḥfụẓ'),
(5931, 86, 1, 'وَالسَّمَاۤءِ وَالطَّارِقِۙ', 'was-samā`i waṭ-ṭāriq'),
(5932, 86, 2, 'وَمَآ اَدْرٰىكَ مَا الطَّارِقُۙ', 'wa mā adrāka maṭ-ṭāriq'),
(5933, 86, 3, 'النَّجْمُ الثَّاقِبُۙ', 'an-najmuṡ-ṡāqib'),
(5934, 86, 4, 'اِنْ كُلُّ نَفْسٍ لَّمَّا عَلَيْهَا حَافِظٌۗ', 'ing kullu nafsil lammā \'alaihā ḥāfiẓ'),
(5935, 86, 5, 'فَلْيَنْظُرِ الْاِنْسَانُ مِمَّ خُلِقَ', 'falyanẓuril-insānu mimma khuliq'),
(5936, 86, 6, 'خُلِقَ مِنْ مَّاۤءٍ دَافِقٍۙ', 'khuliqa mim mā`in dāfiq'),
(5937, 86, 7, 'يَّخْرُجُ مِنْۢ بَيْنِ الصُّلْبِ وَالتَّرَاۤىِٕبِۗ', 'yakhruju mim bainiṣ-ṣulbi wat-tarā`ib'),
(5938, 86, 8, 'اِنَّهٗ عَلٰى رَجْعِهٖ لَقَادِرٌۗ', 'innahụ \'alā raj\'ihī laqādir'),
(5939, 86, 9, 'يَوْمَ تُبْلَى السَّرَاۤىِٕرُۙ', 'yauma tublas-sarā`ir'),
(5940, 86, 10, 'فَمَا لَهٗ مِنْ قُوَّةٍ وَّلَا نَاصِرٍۗ', 'fa mā lahụ ming quwwatiw wa lā nāṣir'),
(5941, 86, 11, 'وَالسَّمَاۤءِ ذَاتِ الرَّجْعِۙ', 'was-samā`i żātir-raj\''),
(5942, 86, 12, 'وَالْاَرْضِ ذَاتِ الصَّدْعِۙ', 'wal-arḍi żātiṣ-ṣad\''),
(5943, 86, 13, 'اِنَّهٗ لَقَوْلٌ فَصْلٌۙ', 'innahụ laqaulun faṣl'),
(5944, 86, 14, 'وَّمَا هُوَ بِالْهَزْلِۗ', 'wa mā huwa bil-hazl'),
(5945, 86, 15, 'اِنَّهُمْ يَكِيْدُوْنَ كَيْدًاۙ', 'innahum yakīdụna kaidā'),
(5946, 86, 16, 'وَّاَكِيْدُ كَيْدًاۖ', 'wa akīdu kaidā'),
(5947, 86, 17, 'فَمَهِّلِ الْكٰفِرِيْنَ اَمْهِلْهُمْ رُوَيْدًا', 'fa mahhilil-kāfirīna am-hil-hum ruwaidā'),
(5948, 87, 1, 'سَبِّحِ اسْمَ رَبِّكَ الْاَعْلَىۙ', 'sabbiḥisma rabbikal-a\'lā'),
(5949, 87, 2, 'الَّذِيْ خَلَقَ فَسَوّٰىۖ', 'allażī khalaqa fa sawwā'),
(5950, 87, 3, 'وَالَّذِيْ قَدَّرَ فَهَدٰىۖ', 'wallażī qaddara fa hadā'),
(5951, 87, 4, 'وَالَّذِيْٓ اَخْرَجَ الْمَرْعٰىۖ', 'wallażī akhrajal-mar\'ā'),
(5952, 87, 5, 'فَجَعَلَهٗ غُثَاۤءً اَحْوٰىۖ', 'fa ja\'alahụ guṡā`an aḥwā'),
(5953, 87, 6, 'سَنُقْرِئُكَ فَلَا تَنْسٰىٓ ۖ', 'sanuqri`uka fa lā tansā'),
(5954, 87, 7, 'اِلَّا مَا شَاۤءَ اللّٰهُ ۗاِنَّهٗ يَعْلَمُ الْجَهْرَ وَمَا يَخْفٰىۗ', 'illā mā syā`allāh, innahụ ya\'lamul-jahra wa mā yakhfā'),
(5955, 87, 8, 'وَنُيَسِّرُكَ لِلْيُسْرٰىۖ', 'wa nuyassiruka lil-yusrā'),
(5956, 87, 9, 'فَذَكِّرْ اِنْ نَّفَعَتِ الذِّكْرٰىۗ', 'fa żakkir in nafa\'atiż-żikrā'),
(5957, 87, 10, 'سَيَذَّكَّرُ مَنْ يَّخْشٰىۙ', 'sayażżakkaru may yakhsyā'),
(5958, 87, 11, 'وَيَتَجَنَّبُهَا الْاَشْقَىۙ', 'wa yatajannabuhal-asyqā'),
(5959, 87, 12, 'الَّذِيْ يَصْلَى النَّارَ الْكُبْرٰىۚ', 'allażī yaṣlan-nāral-kubrā'),
(5960, 87, 13, 'ثُمَّ لَا يَمُوْتُ فِيْهَا وَلَا يَحْيٰىۗ', 'ṡumma lā yamụtu fīhā wa lā yaḥyā'),
(5961, 87, 14, 'قَدْ اَفْلَحَ مَنْ تَزَكّٰىۙ', 'qad aflaḥa man tazakkā'),
(5962, 87, 15, 'وَذَكَرَ اسْمَ رَبِّهٖ فَصَلّٰىۗ', 'wa żakarasma rabbihī fa ṣallā'),
(5963, 87, 16, 'بَلْ تُؤْثِرُوْنَ الْحَيٰوةَ الدُّنْيَاۖ', 'bal tu`ṡirụnal-ḥayātad-dun-yā'),
(5964, 87, 17, 'وَالْاٰخِرَةُ خَيْرٌ وَّاَبْقٰىۗ', 'wal-ākhiratu khairuw wa abqā'),
(5965, 87, 18, 'اِنَّ هٰذَا لَفِى الصُّحُفِ الْاُوْلٰىۙ', 'inna hāżā lafiṣ-ṣuḥufil-ụlā'),
(5966, 87, 19, 'صُحُفِ اِبْرٰهِيْمَ وَمُوْسٰى', 'ṣuḥufi ibrāhīma wa mụsā'),
(5967, 88, 1, 'هَلْ اَتٰىكَ حَدِيْثُ الْغَاشِيَةِۗ', 'hal atāka ḥadīṡul-gāsyiyah'),
(5968, 88, 2, 'وُجُوْهٌ يَّوْمَىِٕذٍ خَاشِعَةٌ ۙ', 'wujụhuy yauma`iżin khāsyi\'ah'),
(5969, 88, 3, 'عَامِلَةٌ نَّاصِبَةٌ ۙ', '\'āmilatun nāṣibah'),
(5970, 88, 4, 'تَصْلٰى نَارًا حَامِيَةً ۙ', 'taṣlā nāran ḥāmiyah'),
(5971, 88, 5, 'تُسْقٰى مِنْ عَيْنٍ اٰنِيَةٍ ۗ', 'tusqā min \'ainin āniyah'),
(5972, 88, 6, 'لَيْسَ لَهُمْ طَعَامٌ اِلَّا مِنْ ضَرِيْعٍۙ', 'laisa lahum ṭa\'āmun illā min ḍarī\''),
(5973, 88, 7, 'لَّا يُسْمِنُ وَلَا يُغْنِيْ مِنْ جُوْعٍۗ', 'lā yusminu wa lā yugnī min jụ\''),
(5974, 88, 8, 'وُجُوْهٌ يَّوْمَىِٕذٍ نَّاعِمَةٌ ۙ', 'wujụhuy yauma`iżin nā\'imah'),
(5975, 88, 9, 'لِّسَعْيِهَا رَاضِيَةٌ ۙ', 'lisa\'yihā rāḍiyah'),
(5976, 88, 10, 'فِيْ جَنَّةٍ عَالِيَةٍۙ', 'fī jannatin \'āliyah'),
(5977, 88, 11, 'لَّا تَسْمَعُ فِيْهَا لَاغِيَةً ۗ', 'lā tasma\'u fīhā lāgiyah'),
(5978, 88, 12, 'فِيْهَا عَيْنٌ جَارِيَةٌ ۘ', 'fīhā \'ainun jāriyah'),
(5979, 88, 13, 'فِيْهَا سُرُرٌ مَّرْفُوْعَةٌ ۙ', 'fīhā sururum marfụ\'ah'),
(5980, 88, 14, 'وَّاَكْوَابٌ مَّوْضُوْعَةٌ ۙ', 'wa akwābum mauḍụ\'ah'),
(5981, 88, 15, 'وَّنَمَارِقُ مَصْفُوْفَةٌ ۙ', 'wa namāriqu maṣfụfah'),
(5982, 88, 16, 'وَّزَرَابِيُّ مَبْثُوْثَةٌ ۗ', 'wa zarābiyyu mabṡụṡah'),
(5983, 88, 17, 'اَفَلَا يَنْظُرُوْنَ اِلَى الْاِبِلِ كَيْفَ خُلِقَتْۗ', 'a fa lā yanẓurụna ilal-ibili kaifa khuliqat'),
(5984, 88, 18, 'وَاِلَى السَّمَاۤءِ كَيْفَ رُفِعَتْۗ', 'wa ilas-samā`i kaifa rufi\'at'),
(5985, 88, 19, 'وَاِلَى الْجِبَالِ كَيْفَ نُصِبَتْۗ', 'wa ilal-jibāli kaifa nuṣibat'),
(5986, 88, 20, 'وَاِلَى الْاَرْضِ كَيْفَ سُطِحَتْۗ', 'wa ilal-arḍi kaifa suṭiḥat'),
(5987, 88, 21, 'فَذَكِّرْۗ اِنَّمَآ اَنْتَ مُذَكِّرٌۙ', 'fa żakkir, innamā anta mużakkir'),
(5988, 88, 22, 'لَّسْتَ عَلَيْهِمْ بِمُصَيْطِرٍۙ', 'lasta \'alaihim bimuṣaiṭir'),
(5989, 88, 23, 'اِلَّا مَنْ تَوَلّٰى وَكَفَرَۙ', 'illā man tawallā wa kafar'),
(5990, 88, 24, 'فَيُعَذِّبُهُ اللّٰهُ الْعَذَابَ الْاَكْبَرَۗ', 'fa yu\'ażżibuhullāhul-\'ażābal-akbar'),
(5991, 88, 25, 'اِنَّ اِلَيْنَآ اِيَابَهُمْ', 'inna ilainā iyābahum'),
(5992, 88, 26, 'ثُمَّ اِنَّ عَلَيْنَا حِسَابَهُمْ', 'ṡumma inna \'alainā ḥisābahum'),
(5993, 89, 1, 'وَالْفَجْرِۙ', 'wal-fajr'),
(5994, 89, 2, 'وَلَيَالٍ عَشْرٍۙ', 'wa layālin \'asyr'),
(5995, 89, 3, 'وَّالشَّفْعِ وَالْوَتْرِۙ', 'wasy-syaf\'i wal-watr'),
(5996, 89, 4, 'وَالَّيْلِ اِذَا يَسْرِۚ', 'wal-laili iżā yasr'),
(5997, 89, 5, 'هَلْ فِيْ ذٰلِكَ قَسَمٌ لِّذِيْ حِجْرٍۗ', 'hal fī żālika qasamul liżī ḥijr'),
(5998, 89, 6, 'اَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِعَادٍۖ', 'a lam tara kaifa fa\'ala rabbuka bi\'ād'),
(5999, 89, 7, 'اِرَمَ ذَاتِ الْعِمَادِۖ', 'irama żātil-\'imād'),
(6000, 89, 8, 'الَّتِيْ لَمْ يُخْلَقْ مِثْلُهَا فِى الْبِلَادِۖ', 'allatī lam yukhlaq miṡluhā fil-bilād'),
(6001, 89, 9, 'وَثَمُوْدَ الَّذِيْنَ جَابُوا الصَّخْرَ بِالْوَادِۖ', 'wa ṡamụdallażīna jābuṣ-ṣakhra bil-wād'),
(6002, 89, 10, 'وَفِرْعَوْنَ ذِى الْاَوْتَادِۖ', 'wa fir\'auna żil-autād'),
(6003, 89, 11, 'الَّذِيْنَ طَغَوْا فِى الْبِلَادِۖ', 'allażīna ṭagau fil-bilād'),
(6004, 89, 12, 'فَاَكْثَرُوْا فِيْهَا الْفَسَادَۖ', 'fa akṡarụ fīhal-fasād'),
(6005, 89, 13, 'فَصَبَّ عَلَيْهِمْ رَبُّكَ سَوْطَ عَذَابٍۖ', 'fa ṣabba \'alaihim rabbuka sauṭa \'ażāb'),
(6006, 89, 14, 'اِنَّ رَبَّكَ لَبِالْمِرْصَادِۗ', 'inna rabbaka labil-mirṣād'),
(6007, 89, 15, 'فَاَمَّا الْاِنْسَانُ اِذَا مَا ابْتَلٰىهُ رَبُّهٗ فَاَكْرَمَهٗ وَنَعَّمَهٗۙ فَيَقُوْلُ رَبِّيْٓ اَكْرَمَنِۗ', 'fa ammal-insānu iżā mabtalāhu rabbuhụ fa akramahụ wa na\'\'amahụ fa yaqụlu rabbī akraman'),
(6008, 89, 16, 'وَاَمَّآ اِذَا مَا ابْتَلٰىهُ فَقَدَرَ عَلَيْهِ رِزْقَهٗ ەۙ فَيَقُوْلُ رَبِّيْٓ اَهَانَنِۚ', 'wa ammā iżā mabtalāhu fa qadara \'alaihi rizqahụ fa yaqụlu rabbī ahānan'),
(6009, 89, 17, 'كَلَّا بَلْ لَّا تُكْرِمُوْنَ الْيَتِيْمَۙ', 'kallā bal lā tukrimụnal-yatīm'),
(6010, 89, 18, 'وَلَا تَحٰۤضُّوْنَ عَلٰى طَعَامِ الْمِسْكِيْنِۙ', 'wa lā tahāḍḍụna \'alā ṭa\'āmil-miskīn'),
(6011, 89, 19, 'وَتَأْكُلُوْنَ التُّرَاثَ اَكْلًا لَّمًّاۙ', 'wa ta`kulụnat-turāṡa aklal lammā'),
(6012, 89, 20, 'وَّتُحِبُّوْنَ الْمَالَ حُبًّا جَمًّاۗ', 'wa tuḥibbụnal-māla ḥubban jammā'),
(6013, 89, 21, 'كَلَّآ اِذَا دُكَّتِ الْاَرْضُ دَكًّا دَكًّاۙ', 'kallā iżā dukkatil-arḍu dakkan dakkā'),
(6014, 89, 22, 'وَّجَآءَ رَبُّكَ وَالْمَلَكُ صَفًّا صَفًّاۚ', 'wa jā`a rabbuka wal-malaku ṣaffan ṣaffā'),
(6015, 89, 23, 'وَجِايْۤءَ يَوْمَىِٕذٍۢ بِجَهَنَّمَۙ يَوْمَىِٕذٍ يَّتَذَكَّرُ الْاِنْسَانُ وَاَنّٰى لَهُ الذِّكْرٰىۗ', 'wa jī`a yauma`iżim bijahannama yauma`iżiy yatażakkarul-insānu wa annā lahuż-żikrā'),
(6016, 89, 24, 'يَقُوْلُ يٰلَيْتَنِيْ قَدَّمْتُ لِحَيَاتِيْۚ', 'yaqụlu yā laitanī qaddamtu liḥayātī'),
(6017, 89, 25, 'فَيَوْمَىِٕذٍ لَّا يُعَذِّبُ عَذَابَهٗٓ اَحَدٌ ۙ', 'fa yauma`iżil lā yu\'ażżibu \'ażābahū aḥad'),
(6018, 89, 26, 'وَّلَا يُوْثِقُ وَثَاقَهٗٓ اَحَدٌ ۗ', 'wa lā yụṡiqu waṡāqahū aḥad'),
(6019, 89, 27, 'يٰٓاَيَّتُهَا النَّفْسُ الْمُطْمَىِٕنَّةُۙ', 'yā ayyatuhan-nafsul-muṭma`innah'),
(6020, 89, 28, 'ارْجِعِيْٓ اِلٰى رَبِّكِ رَاضِيَةً مَّرْضِيَّةً ۚ', 'irji\'ī ilā rabbiki rāḍiyatam marḍiyyah'),
(6021, 89, 29, 'فَادْخُلِيْ فِيْ عِبٰدِيْۙ', 'fadkhulī fī \'ibādī'),
(6022, 89, 30, 'وَادْخُلِيْ جَنَّتِيْࣖ', 'wadkhulī jannatī'),
(6023, 90, 1, 'لَآ اُقْسِمُ بِهٰذَا الْبَلَدِۙ', 'lā uqsimu bihāżal-balad'),
(6024, 90, 2, 'وَاَنْتَ حِلٌّۢ بِهٰذَا الْبَلَدِۙ', 'wa anta ḥillum bihāżal-balad'),
(6025, 90, 3, 'وَوَالِدٍ وَّمَا وَلَدَۙ', 'wa wālidiw wa mā walad'),
(6026, 90, 4, 'لَقَدْ خَلَقْنَا الْاِنْسَانَ فِيْ كَبَدٍۗ', 'laqad khalaqnal-insāna fī kabad'),
(6027, 90, 5, 'اَيَحْسَبُ اَنْ لَّنْ يَّقْدِرَ عَلَيْهِ اَحَدٌ ۘ', 'a yaḥsabu al lay yaqdira \'alaihi aḥad'),
(6028, 90, 6, 'يَقُوْلُ اَهْلَكْتُ مَالًا لُّبَدًاۗ', 'yaqụlu ahlaktu mālal lubadā'),
(6029, 90, 7, 'اَيَحْسَبُ اَنْ لَّمْ يَرَهٗٓ اَحَدٌۗ', 'a yaḥsabu al lam yarahū aḥad'),
(6030, 90, 8, 'اَلَمْ نَجْعَلْ لَّهٗ عَيْنَيْنِۙ', 'a lam naj\'al lahụ \'ainaīn'),
(6031, 90, 9, 'وَلِسَانًا وَّشَفَتَيْنِۙ', 'wa lisānaw wa syafataīn'),
(6032, 90, 10, 'وَهَدَيْنٰهُ النَّجْدَيْنِۙ', 'wa hadaināhun-najdaīn'),
(6033, 90, 11, 'فَلَا اقْتَحَمَ الْعَقَبَةَ ۖ', 'fa laqtaḥamal-\'aqabah'),
(6034, 90, 12, 'وَمَآ اَدْرٰىكَ مَا الْعَقَبَةُ ۗ', 'wa mā adrāka mal-\'aqabah'),
(6035, 90, 13, 'فَكُّ رَقَبَةٍۙ', 'fakku raqabah'),
(6036, 90, 14, 'اَوْ اِطْعَامٌ فِيْ يَوْمٍ ذِيْ مَسْغَبَةٍۙ', 'au iṭ\'āmun fī yaumin żī masgabah'),
(6037, 90, 15, 'يَّتِيْمًا ذَا مَقْرَبَةٍۙ', 'yatīman żā maqrabah'),
(6038, 90, 16, 'اَوْ مِسْكِيْنًا ذَا مَتْرَبَةٍۗ', 'au miskīnan żā matrabah'),
(6039, 90, 17, 'ثُمَّ كَانَ مِنَ الَّذِيْنَ اٰمَنُوْا وَتَوَاصَوْا بِالصَّبْرِ وَتَوَاصَوْا بِالْمَرْحَمَةِۗ', 'ṡumma kāna minallażīna āmanụ wa tawāṣau biṣ-ṣabri wa tawāṣau bil-mar-ḥamah'),
(6040, 90, 18, 'اُولٰۤىِٕكَ اَصْحٰبُ الْمَيْمَنَةِۗ', 'ulā`ika aṣ-ḥābul-maimanah'),
(6041, 90, 19, 'وَالَّذِيْنَ كَفَرُوْا بِاٰيٰتِنَا هُمْ اَصْحٰبُ الْمَشْئَمَةِۗ', 'wallażīna kafarụ bi`āyātinā hum aṣ-ḥābul-masy`amah'),
(6042, 90, 20, 'عَلَيْهِمْ نَارٌ مُّؤْصَدَةٌ', '\'alaihim nārum mu`ṣadah'),
(6043, 91, 1, 'وَالشَّمْسِ وَضُحٰىهَاۖ', 'wasy-syamsi wa ḍuḥāhā'),
(6044, 91, 2, 'وَالْقَمَرِ اِذَا تَلٰىهَاۖ', 'wal-qamari iżā talāhā'),
(6045, 91, 3, 'وَالنَّهَارِ اِذَا جَلّٰىهَاۖ', 'wan-nahāri iżā jallāhā'),
(6046, 91, 4, 'وَالَّيْلِ اِذَا يَغْشٰىهَاۖ', 'wal-laili iżā yagsyāhā'),
(6047, 91, 5, 'وَالسَّمَاۤءِ وَمَا بَنٰىهَاۖ', 'was-samā`i wa mā banāhā'),
(6048, 91, 6, 'وَالْاَرْضِ وَمَا طَحٰىهَاۖ', 'wal-arḍi wa mā ṭaḥāhā'),
(6049, 91, 7, 'وَنَفْسٍ وَّمَا سَوّٰىهَاۖ', 'wa nafsiw wa mā sawwāhā'),
(6050, 91, 8, 'فَاَلْهَمَهَا فُجُوْرَهَا وَتَقْوٰىهَاۖ', 'fa al-hamahā fujụrahā wa taqwāhā'),
(6051, 91, 9, 'قَدْ اَفْلَحَ مَنْ زَكّٰىهَاۖ', 'qad aflaḥa man zakkāhā'),
(6052, 91, 10, 'وَقَدْ خَابَ مَنْ دَسّٰىهَاۗ', 'wa qad khāba man dassāhā'),
(6053, 91, 11, 'كَذَّبَتْ ثَمُوْدُ بِطَغْوٰىهَآ ۖ', 'każżabaṡ ṡamụdu biṭagwāhā'),
(6054, 91, 12, 'اِذِ انْۢبَعَثَ اَشْقٰىهَاۖ', 'iżimba\'aṡa asyqāhā'),
(6055, 91, 13, 'فَقَالَ لَهُمْ رَسُوْلُ اللّٰهِ نَاقَةَ اللّٰهِ وَسُقْيٰهَاۗ', 'fa qāla lahum rasụlullāhi nāqatallāhi wa suqyāhā'),
(6056, 91, 14, 'فَكَذَّبُوْهُ فَعَقَرُوْهَاۖ فَدَمْدَمَ عَلَيْهِمْ رَبُّهُمْ بِذَنْۢبِهِمْ فَسَوّٰىهَاۖ', 'fa każżabụhu fa \'aqarụhā fa damdama \'alaihim rabbuhum biżambihim fa sawwāhā'),
(6057, 91, 15, 'وَلَا يَخَافُ عُقْبٰهَا', 'wa lā yakhāfu \'uqbāhā'),
(6058, 92, 1, 'وَالَّيْلِ اِذَا يَغْشٰىۙ', 'wal-laili iżā yagsyā'),
(6059, 92, 2, 'وَالنَّهَارِ اِذَا تَجَلّٰىۙ', 'wan-nahāri iżā tajallā'),
(6060, 92, 3, 'وَمَا خَلَقَ الذَّكَرَ وَالْاُنْثٰىٓ ۙ', 'wa mā khalaqaż-żakara wal-unṡā'),
(6061, 92, 4, 'اِنَّ سَعْيَكُمْ لَشَتّٰىۗ', 'inna sa\'yakum lasyattā'),
(6062, 92, 5, 'فَاَمَّا مَنْ اَعْطٰى وَاتَّقٰىۙ', 'fa ammā man a\'ṭā wattaqā'),
(6063, 92, 6, 'وَصَدَّقَ بِالْحُسْنٰىۙ', 'wa ṣaddaqa bil-ḥusnā'),
(6064, 92, 7, 'فَسَنُيَسِّرُهٗ لِلْيُسْرٰىۗ', 'fa sanuyassiruhụ lil-yusrā'),
(6065, 92, 8, 'وَاَمَّا مَنْۢ بَخِلَ وَاسْتَغْنٰىۙ', 'wa ammā mam bakhila wastagnā'),
(6066, 92, 9, 'وَكَذَّبَ بِالْحُسْنٰىۙ', 'wa każżaba bil-ḥusnā'),
(6067, 92, 10, 'فَسَنُيَسِّرُهٗ لِلْعُسْرٰىۗ', 'fa sanuyassiruhụ lil-\'usrā'),
(6068, 92, 11, 'وَمَا يُغْنِيْ عَنْهُ مَالُهٗٓ اِذَا تَرَدّٰىٓۙ', 'wa mā yugnī \'an-hu māluhū iżā taraddā'),
(6069, 92, 12, 'اِنَّ عَلَيْنَا لَلْهُدٰىۖ', 'inna \'alainā lal-hudā'),
(6070, 92, 13, 'وَاِنَّ لَنَا لَلْاٰخِرَةَ وَالْاُوْلٰىۗ', 'wa inna lanā lal-ākhirata wal-ụlā'),
(6071, 92, 14, 'فَاَنْذَرْتُكُمْ نَارًا تَلَظّٰىۚ', 'fa anżartukum nāran talaẓẓā'),
(6072, 92, 15, 'لَا يَصْلٰىهَآ اِلَّا الْاَشْقَىۙ', 'lā yaṣlāhā illal-asyqā'),
(6073, 92, 16, 'الَّذِيْ كَذَّبَ وَتَوَلّٰىۗ', 'allażī każżaba wa tawallā'),
(6074, 92, 17, 'وَسَيُجَنَّبُهَا الْاَتْقَىۙ', 'wa sayujannabuhal-atqā'),
(6075, 92, 18, 'الَّذِيْ يُؤْتِيْ مَالَهٗ يَتَزَكّٰىۚ', 'allażī yu`tī mālahụ yatazakkā'),
(6076, 92, 19, 'وَمَا لِاَحَدٍ عِنْدَهٗ مِنْ نِّعْمَةٍ تُجْزٰىٓۙ', 'wa mā li`aḥadin \'indahụ min ni\'matin tujzā'),
(6077, 92, 20, 'اِلَّا ابْتِغَاۤءَ وَجْهِ رَبِّهِ الْاَعْلٰىۚ', 'illabtigā`a waj-hi rabbihil-a\'lā'),
(6078, 92, 21, 'وَلَسَوْفَ يَرْضٰى', 'wa lasaufa yarḍā'),
(6079, 93, 1, 'وَالضُّحٰىۙ', 'waḍ-ḍuḥā'),
(6080, 93, 2, 'وَالَّيْلِ اِذَا سَجٰىۙ', 'wal-laili iżā sajā'),
(6081, 93, 3, 'مَا وَدَّعَكَ رَبُّكَ وَمَا قَلٰىۗ', 'mā wadda\'aka rabbuka wa mā qalā'),
(6082, 93, 4, 'وَلَلْاٰخِرَةُ خَيْرٌ لَّكَ مِنَ الْاُوْلٰىۗ', 'wa lal-ākhiratu khairul laka minal-ụlā'),
(6083, 93, 5, 'وَلَسَوْفَ يُعْطِيْكَ رَبُّكَ فَتَرْضٰىۗ', 'wa lasaufa yu\'ṭīka rabbuka fa tarḍā'),
(6084, 93, 6, 'اَلَمْ يَجِدْكَ يَتِيْمًا فَاٰوٰىۖ', 'a lam yajidka yatīman fa āwā'),
(6085, 93, 7, 'وَوَجَدَكَ ضَاۤلًّا فَهَدٰىۖ', 'wa wajadaka ḍāllan fa hadā'),
(6086, 93, 8, 'وَوَجَدَكَ عَاۤىِٕلًا فَاَغْنٰىۗ', 'wa wajadaka \'ā`ilan fa agnā'),
(6087, 93, 9, 'فَاَمَّا الْيَتِيْمَ فَلَا تَقْهَرْۗ', 'fa ammal-yatīma fa lā taq-har'),
(6088, 93, 10, 'وَاَمَّا السَّاۤىِٕلَ فَلَا تَنْهَرْ', 'wa ammas-sā`ila fa lā tan-har'),
(6089, 93, 11, 'وَاَمَّا بِنِعْمَةِ رَبِّكَ فَحَدِّثْ', 'wa ammā bini\'mati rabbika fa ḥaddiṡ'),
(6090, 94, 1, 'اَلَمْ نَشْرَحْ لَكَ صَدْرَكَۙ', 'a lam nasyraḥ laka ṣadrak'),
(6091, 94, 2, 'وَوَضَعْنَا عَنْكَ وِزْرَكَۙ', 'wa waḍa\'nā \'angka wizrak'),
(6092, 94, 3, 'الَّذِيْٓ اَنْقَضَ ظَهْرَكَۙ', 'allażī angqaḍa ẓahrak'),
(6093, 94, 4, 'وَرَفَعْنَا لَكَ ذِكْرَكَۗ', 'wa rafa\'nā laka żikrak'),
(6094, 94, 5, 'فَاِنَّ مَعَ الْعُسْرِ يُسْرًاۙ', 'fa inna ma\'al-\'usri yusrā'),
(6095, 94, 6, 'اِنَّ مَعَ الْعُسْرِ يُسْرًاۗ', 'inna ma\'al-\'usri yusrā'),
(6096, 94, 7, 'فَاِذَا فَرَغْتَ فَانْصَبْۙ', 'fa iżā faragta fanṣab'),
(6097, 94, 8, 'وَاِلٰى رَبِّكَ فَارْغَبْ', 'wa ilā rabbika fargab'),
(6098, 95, 1, 'وَالتِّيْنِ وَالزَّيْتُوْنِۙ', 'wat-tīni waz-zaitụn'),
(6099, 95, 2, 'وَطُوْرِ سِيْنِيْنَۙ', 'wa ṭụri sīnīn'),
(6100, 95, 3, 'وَهٰذَا الْبَلَدِ الْاَمِيْنِۙ', 'wa hāżal-baladil-amīn'),
(6101, 95, 4, 'لَقَدْ خَلَقْنَا الْاِنْسَانَ فِيْٓ اَحْسَنِ تَقْوِيْمٍۖ', 'laqad khalaqnal-insāna fī aḥsani taqwīm'),
(6102, 95, 5, 'ثُمَّ رَدَدْنٰهُ اَسْفَلَ سَافِلِيْنَۙ', 'ṡumma radadnāhu asfala sāfilīn'),
(6103, 95, 6, 'اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَلَهُمْ اَجْرٌ غَيْرُ مَمْنُوْنٍۗ', 'illallażīna āmanụ wa \'amiluṣ-ṣāliḥāti fa lahum ajrun gairu mamnụn'),
(6104, 95, 7, 'فَمَا يُكَذِّبُكَ بَعْدُ بِالدِّيْنِۗ', 'fa mā yukażżibuka ba\'du bid-dīn'),
(6105, 95, 8, 'اَلَيْسَ اللّٰهُ بِاَحْكَمِ الْحٰكِمِيْنَ', 'a laisallāhu bi`aḥkamil-ḥākimīn'),
(6106, 96, 1, 'اِقْرَأْ بِاسْمِ رَبِّكَ الَّذِيْ خَلَقَۚ', 'iqra` bismi rabbikallażī khalaq'),
(6107, 96, 2, 'خَلَقَ الْاِنْسَانَ مِنْ عَلَقٍۚ', 'khalaqal-insāna min \'alaq'),
(6108, 96, 3, 'اِقْرَأْ وَرَبُّكَ الْاَكْرَمُۙ', 'iqra` wa rabbukal-akram'),
(6109, 96, 4, 'الَّذِيْ عَلَّمَ بِالْقَلَمِۙ', 'allażī \'allama bil-qalam'),
(6110, 96, 5, 'عَلَّمَ الْاِنْسَانَ مَا لَمْ يَعْلَمْۗ', '\'allamal-insāna mā lam ya\'lam'),
(6111, 96, 6, 'كَلَّآ اِنَّ الْاِنْسَانَ لَيَطْغٰىٓ ۙ', 'kallā innal-insāna layaṭgā'),
(6112, 96, 7, 'اَنْ رَّاٰهُ اسْتَغْنٰىۗ', 'ar ra`āhustagnā'),
(6113, 96, 8, 'اِنَّ اِلٰى رَبِّكَ الرُّجْعٰىۗ', 'inna ilā rabbikar-ruj\'ā'),
(6114, 96, 9, 'اَرَاَيْتَ الَّذِيْ يَنْهٰىۙ', 'a ra`aitallażī yan-hā'),
(6115, 96, 10, 'عَبْدًا اِذَا صَلّٰىۗ', '\'abdan iżā ṣallā'),
(6116, 96, 11, 'اَرَاَيْتَ اِنْ كَانَ عَلَى الْهُدٰىٓۙ', 'a ra`aita ing kāna \'alal-hudā'),
(6117, 96, 12, 'اَوْ اَمَرَ بِالتَّقْوٰىۗ', 'au amara bit-taqwā'),
(6118, 96, 13, 'اَرَاَيْتَ اِنْ كَذَّبَ وَتَوَلّٰىۗ', 'a ra`aita ing każżaba wa tawallā'),
(6119, 96, 14, 'اَلَمْ يَعْلَمْ بِاَنَّ اللّٰهَ يَرٰىۗ', 'a lam ya\'lam bi`annallāha yarā'),
(6120, 96, 15, 'كَلَّا لَىِٕنْ لَّمْ يَنْتَهِ ەۙ لَنَسْفَعًاۢ بِالنَّاصِيَةِۙ', 'kallā la`il lam yantahi lanasfa\'am bin-nāṣiyah'),
(6121, 96, 16, 'نَاصِيَةٍ كَاذِبَةٍ خَاطِئَةٍۚ', 'nāṣiyating kāżibatin khāṭi`ah'),
(6122, 96, 17, 'فَلْيَدْعُ نَادِيَهٗۙ', 'falyad\'u nādiyah'),
(6123, 96, 18, 'سَنَدْعُ الزَّبَانِيَةَۙ', 'sanad\'uz-zabāniyah'),
(6124, 96, 19, 'كَلَّاۗ لَا تُطِعْهُ وَاسْجُدْ وَاقْتَرِبْ ۩', 'kallā, lā tuṭi\'hu wasjud waqtarib'),
(6125, 97, 1, 'اِنَّآ اَنْزَلْنٰهُ فِيْ لَيْلَةِ الْقَدْرِ', 'innā anzalnāhu fī lailatil-qadr'),
(6126, 97, 2, 'وَمَآ اَدْرٰىكَ مَا لَيْلَةُ الْقَدْرِۗ', 'wa mā adrāka mā lailatul-qadr'),
(6127, 97, 3, 'لَيْلَةُ الْقَدْرِ ەۙ خَيْرٌ مِّنْ اَلْفِ شَهْرٍۗ', 'lailatul-qadri khairum min alfi syahr'),
(6128, 97, 4, 'تَنَزَّلُ الْمَلٰۤىِٕكَةُ وَالرُّوْحُ فِيْهَا بِاِذْنِ رَبِّهِمْۚ مِنْ كُلِّ اَمْرٍۛ', 'tanazzalul-malā`ikatu war-rụḥu fīhā bi`iżni rabbihim, ming kulli amr'),
(6129, 97, 5, 'سَلٰمٌ ۛهِيَ حَتّٰى مَطْلَعِ الْفَجْرِ', 'salāmun hiya ḥattā maṭla\'il-fajr'),
(6130, 98, 1, 'لَمْ يَكُنِ الَّذِيْنَ كَفَرُوْا مِنْ اَهْلِ الْكِتٰبِ وَالْمُشْرِكِيْنَ مُنْفَكِّيْنَ حَتّٰى تَأْتِيَهُمُ الْبَيِّنَةُۙ', 'lam yakunillażīna kafarụ min ahlil-kitābi wal-musyrikīna munfakkīna ḥattā ta`tiyahumul-bayyinah'),
(6131, 98, 2, 'رَسُوْلٌ مِّنَ اللّٰهِ يَتْلُوْا صُحُفًا مُّطَهَّرَةًۙ', 'rasụlum minallāhi yatlụ ṣuḥufam muṭahharah'),
(6132, 98, 3, 'فِيْهَا كُتُبٌ قَيِّمَةٌ ۗ', 'fīhā kutubung qayyimah'),
(6133, 98, 4, 'وَمَا تَفَرَّقَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ اِلَّا مِنْۢ بَعْدِ مَا جَاۤءَتْهُمُ الْبَيِّنَةُ ۗ', 'wa mā tafarraqallażīna ụtul-kitāba illā mim ba\'di mā jā`at-humul-bayyinah'),
(6134, 98, 5, 'وَمَآ اُمِرُوْٓا اِلَّا لِيَعْبُدُوا اللّٰهَ مُخْلِصِيْنَ لَهُ الدِّيْنَ ەۙ حُنَفَاۤءَ وَيُقِيْمُوا الصَّلٰوةَ وَيُؤْتُوا الزَّكٰوةَ وَذٰلِكَ دِيْنُ الْقَيِّمَةِۗ', 'wa mā umirū illā liya\'budullāha mukhliṣīna lahud-dīna ḥunafā`a wa yuqīmuṣ-ṣalāta wa yu`tuz-zakāta wa żālika dīnul-qayyimah'),
(6135, 98, 6, 'اِنَّ الَّذِيْنَ كَفَرُوْا مِنْ اَهْلِ الْكِتٰبِ وَالْمُشْرِكِيْنَ فِيْ نَارِ جَهَنَّمَ خٰلِدِيْنَ فِيْهَاۗ اُولٰۤىِٕكَ هُمْ شَرُّ الْبَرِيَّةِۗ', 'innallażīna kafarụ min ahlil-kitābi wal-musyrikīna fī nāri jahannama khālidīna fīhā, ulā`ika hum syarrul-bariyyah'),
(6136, 98, 7, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ اُولٰۤىِٕكَ هُمْ خَيْرُ الْبَرِيَّةِۗ', 'innallażīna āmanụ wa \'amiluṣ-ṣāliḥāti ulā`ika hum khairul-bariyyah'),
(6137, 98, 8, 'جَزَاۤؤُهُمْ عِنْدَ رَبِّهِمْ جَنّٰتُ عَدْنٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًا ۗرَضِيَ اللّٰهُ عَنْهُمْ وَرَضُوْا عَنْهُ ۗ ذٰلِكَ لِمَنْ خَشِيَ رَبَّهٗ', 'jazā`uhum \'inda rabbihim jannātu \'adnin tajrī min taḥtihal-an-hāru khālidīna fīhā abadā, raḍiyallāhu \'an-hum wa raḍụ \'an-h, żālika liman khasyiya rabbah'),
(6138, 99, 1, 'اِذَا زُلْزِلَتِ الْاَرْضُ زِلْزَالَهَاۙ', 'iżā zulzilatil-arḍu zilzālahā'),
(6139, 99, 2, 'وَاَخْرَجَتِ الْاَرْضُ اَثْقَالَهَاۙ', 'wa akhrajatil-arḍu aṡqālahā'),
(6140, 99, 3, 'وَقَالَ الْاِنْسَانُ مَا لَهَاۚ', 'wa qālal-insānu mā lahā'),
(6141, 99, 4, 'يَوْمَىِٕذٍ تُحَدِّثُ اَخْبَارَهَاۙ', 'yauma`iżin tuḥaddiṡu akhbārahā'),
(6142, 99, 5, 'بِاَنَّ رَبَّكَ اَوْحٰى لَهَاۗ', 'bi`anna rabbaka auḥā lahā'),
(6143, 99, 6, 'يَوْمَىِٕذٍ يَّصْدُرُ النَّاسُ اَشْتَاتًا ەۙ لِّيُرَوْا اَعْمَالَهُمْۗ', 'yauma`iżiy yaṣdurun-nāsu asytātal liyurau a\'mālahum'),
(6144, 99, 7, 'فَمَنْ يَّعْمَلْ مِثْقَالَ ذَرَّةٍ خَيْرًا يَّرَهٗۚ', 'fa may ya\'mal miṡqāla żarratin khairay yarah'),
(6145, 99, 8, 'وَمَنْ يَّعْمَلْ مِثْقَالَ ذَرَّةٍ شَرًّا يَّرَهٗ', 'wa may ya\'mal miṡqāla żarratin syarray yarah'),
(6146, 100, 1, 'وَالْعٰدِيٰتِ ضَبْحًاۙ', 'wal-\'ādiyāti ḍab-ḥā'),
(6147, 100, 2, 'فَالْمُوْرِيٰتِ قَدْحًاۙ', 'fal-mụriyāti qad-ḥā'),
(6148, 100, 3, 'فَالْمُغِيْرٰتِ صُبْحًاۙ', 'fal-mugīrāti ṣub-ḥā'),
(6149, 100, 4, 'فَاَثَرْنَ بِهٖ نَقْعًاۙ', 'fa aṡarna bihī naq\'ā'),
(6150, 100, 5, 'فَوَسَطْنَ بِهٖ جَمْعًاۙ', 'fa wasaṭna bihī jam\'ā'),
(6151, 100, 6, 'اِنَّ الْاِنْسَانَ لِرَبِّهٖ لَكَنُوْدٌ ۚ', 'innal-insāna lirabbihī lakanụd'),
(6152, 100, 7, 'وَاِنَّهٗ عَلٰى ذٰلِكَ لَشَهِيْدٌۚ', 'wa innahụ \'alā żālika lasyahīd'),
(6153, 100, 8, 'وَاِنَّهٗ لِحُبِّ الْخَيْرِ لَشَدِيْدٌ ۗ', 'wa innahụ liḥubbil-khairi lasyadīd'),
(6154, 100, 9, '۞ اَفَلَا يَعْلَمُ اِذَا بُعْثِرَ مَا فِى الْقُبُوْرِۙ', 'a fa lā ya\'lamu iżā bu\'ṡira mā fil-qubụr'),
(6155, 100, 10, 'وَحُصِّلَ مَا فِى الصُّدُوْرِۙ', 'wa huṣṣila mā fiṣ-ṣudụr'),
(6156, 100, 11, 'اِنَّ رَبَّهُمْ بِهِمْ يَوْمَىِٕذٍ لَّخَبِيْرٌ', 'inna rabbahum bihim yauma`iżil lakhabīr'),
(6157, 101, 1, 'اَلْقَارِعَةُۙ', 'al-qāri\'ah'),
(6158, 101, 2, 'مَا الْقَارِعَةُ ۚ', 'mal-qāri\'ah'),
(6159, 101, 3, 'وَمَآ اَدْرٰىكَ مَا الْقَارِعَةُ ۗ', 'wa mā adrāka mal-qāri\'ah'),
(6160, 101, 4, 'يَوْمَ يَكُوْنُ النَّاسُ كَالْفَرَاشِ الْمَبْثُوْثِۙ', 'yauma yakụnun-nāsu kal-farāsyil-mabṡụṡ'),
(6161, 101, 5, 'وَتَكُوْنُ الْجِبَالُ كَالْعِهْنِ الْمَنْفُوْشِۗ', 'wa takụnul-jibālu kal-\'ihnil-manfụsy'),
(6162, 101, 6, 'فَاَمَّا مَنْ ثَقُلَتْ مَوَازِينُهٗۙ', 'fa ammā man ṡaqulat mawāzīnuh'),
(6163, 101, 7, 'فَهُوَ فِيْ عِيْشَةٍ رَّاضِيَةٍۗ', 'fa huwa fī \'īsyatir rāḍiyah'),
(6164, 101, 8, 'وَاَمَّا مَنْ خَفَّتْ مَوَازِيْنُهٗۙ', 'wa ammā man khaffat mawāzīnuh'),
(6165, 101, 9, 'فَاُمُّهُ هَاوِيَةٌ ۗ', 'fa ummuhụ hāwiyah'),
(6166, 101, 10, 'وَمَآ اَدْرٰىكَ مَا هِيَهْۗ', 'wa mā adrāka mā hiyah'),
(6167, 101, 11, 'نَارٌ حَامِيَةٌ', 'nārun ḥāmiyah'),
(6168, 102, 1, 'اَلْهٰىكُمُ التَّكَاثُرُۙ', 'al-hākumut-takāṡur'),
(6169, 102, 2, 'حَتّٰى زُرْتُمُ الْمَقَابِرَۗ', 'ḥattā zurtumul-maqābir'),
(6170, 102, 3, 'كَلَّا سَوْفَ تَعْلَمُوْنَۙ', 'kallā saufa ta\'lamụn'),
(6171, 102, 4, 'ثُمَّ كَلَّا سَوْفَ تَعْلَمُوْنَ', 'ṡumma kallā saufa ta\'lamụn'),
(6172, 102, 5, 'كَلَّا لَوْ تَعْلَمُوْنَ عِلْمَ الْيَقِيْنِۗ', 'kallā lau ta\'lamụna \'ilmal-yaqīn'),
(6173, 102, 6, 'لَتَرَوُنَّ الْجَحِيْمَۙ', 'latarawunnal-jaḥīm'),
(6174, 102, 7, 'ثُمَّ لَتَرَوُنَّهَا عَيْنَ الْيَقِيْنِۙ', 'ṡumma latarawunnahā \'ainal-yaqīn'),
(6175, 102, 8, 'ثُمَّ لَتُسْـَٔلُنَّ يَوْمَىِٕذٍ عَنِ النَّعِيْمِ', 'ṡumma latus`alunna yauma`iżin \'anin-na\'īm'),
(6176, 103, 1, 'وَالْعَصْرِۙ', 'wal-\'aṣr'),
(6177, 103, 2, 'اِنَّ الْاِنْسَانَ لَفِيْ خُسْرٍۙ', 'innal-insāna lafī khusr'),
(6178, 103, 3, 'اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَتَوَاصَوْا بِالْحَقِّ ەۙ وَتَوَاصَوْا بِالصَّبْرِ', 'illallażīna āmanụ wa \'amiluṣ-ṣāliḥāti wa tawāṣau bil-ḥaqqi wa tawāṣau biṣ-ṣabr'),
(6179, 104, 1, 'وَيْلٌ لِّكُلِّ هُمَزَةٍ لُّمَزَةٍۙ', 'wailul likulli humazatil lumazah'),
(6180, 104, 2, ' ۨالَّذِيْ جَمَعَ مَالًا وَّعَدَّدَهٗۙ', 'allażī jama\'a mālaw wa \'addadah'),
(6181, 104, 3, 'يَحْسَبُ اَنَّ مَالَهٗٓ اَخْلَدَهٗۚ', 'yaḥsabu anna mālahū akhladah'),
(6182, 104, 4, 'كَلَّا لَيُنْۢبَذَنَّ فِى الْحُطَمَةِۖ', 'kallā layumbażanna fil-ḥuṭamah'),
(6183, 104, 5, 'وَمَآ اَدْرٰىكَ مَا الْحُطَمَةُ ۗ', 'wa mā adrāka mal-ḥuṭamah'),
(6184, 104, 6, 'نَارُ اللّٰهِ الْمُوْقَدَةُۙ', 'nārullāhil-mụqadah'),
(6185, 104, 7, 'الَّتِيْ تَطَّلِعُ عَلَى الْاَفْـِٕدَةِۗ', 'allatī taṭṭali\'u \'alal-af`idah'),
(6186, 104, 8, 'اِنَّهَا عَلَيْهِمْ مُّؤْصَدَةٌۙ', 'innahā \'alaihim mu`ṣadah'),
(6187, 104, 9, 'فِيْ عَمَدٍ مُّمَدَّدَةٍ', 'fī \'amadim mumaddadah'),
(6188, 105, 1, 'اَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِاَصْحٰبِ الْفِيْلِۗ', 'a lam tara kaifa fa\'ala rabbuka bi`aṣ-ḥābil-fīl'),
(6189, 105, 2, 'اَلَمْ يَجْعَلْ كَيْدَهُمْ فِيْ تَضْلِيْلٍۙ', 'a lam yaj\'al kaidahum fī taḍlīl'),
(6190, 105, 3, 'وَّاَرْسَلَ عَلَيْهِمْ طَيْرًا اَبَابِيْلَۙ', 'wa arsala \'alaihim ṭairan abābīl'),
(6191, 105, 4, 'تَرْمِيْهِمْ بِحِجَارَةٍ مِّنْ سِجِّيْلٍۙ', 'tarmīhim biḥijāratim min sijjīl'),
(6192, 105, 5, 'فَجَعَلَهُمْ كَعَصْفٍ مَّأْكُوْلٍ', 'fa ja\'alahum ka\'aṣfim ma`kụl'),
(6193, 106, 1, 'لِاِيْلٰفِ قُرَيْشٍۙ', 'li`īlāfi quraīsy'),
(6194, 106, 2, 'اٖلٰفِهِمْ رِحْلَةَ الشِّتَاۤءِ وَالصَّيْفِۚ', 'īlāfihim riḥlatasy-syitā`i waṣ-ṣaīf'),
(6195, 106, 3, 'فَلْيَعْبُدُوْا رَبَّ هٰذَا الْبَيْتِۙ', 'falya\'budụ rabba hāżal-baīt'),
(6196, 106, 4, 'الَّذِيْٓ اَطْعَمَهُمْ مِّنْ جُوْعٍ ەۙ وَّاٰمَنَهُمْ مِّنْ خَوْفٍ', 'allażī aṭ\'amahum min jụ\'iw wa āmanahum min khaụf'),
(6197, 107, 1, 'اَرَءَيْتَ الَّذِيْ يُكَذِّبُ بِالدِّيْنِۗ', 'a ra`aitallażī yukażżibu bid-dīn'),
(6198, 107, 2, 'فَذٰلِكَ الَّذِيْ يَدُعُّ الْيَتِيْمَۙ', 'fa żālikallażī yadu\'\'ul-yatīm'),
(6199, 107, 3, 'وَلَا يَحُضُّ عَلٰى طَعَامِ الْمِسْكِيْنِۗ', 'wa lā yaḥuḍḍu \'alā ṭa\'āmil-miskīn'),
(6200, 107, 4, 'فَوَيْلٌ لِّلْمُصَلِّيْنَۙ', 'fa wailul lil-muṣallīn'),
(6201, 107, 5, 'الَّذِيْنَ هُمْ عَنْ صَلَاتِهِمْ سَاهُوْنَۙ', 'allażīna hum \'an ṣalātihim sāhụn'),
(6202, 107, 6, 'الَّذِيْنَ هُمْ يُرَاۤءُوْنَۙ', 'allażīna hum yurā`ụn'),
(6203, 107, 7, 'وَيَمْنَعُوْنَ الْمَاعُوْنَ', 'wa yamna\'ụnal-mā\'ụn'),
(6204, 108, 1, 'اِنَّآ اَعْطَيْنٰكَ الْكَوْثَرَۗ', 'innā a\'ṭainākal-kauṡar'),
(6205, 108, 2, 'فَصَلِّ لِرَبِّكَ وَانْحَرْۗ', 'fa ṣalli lirabbika wan-ḥar'),
(6206, 108, 3, 'اِنَّ شَانِئَكَ هُوَ الْاَبْتَرُ', 'inna syāni`aka huwal-abtar'),
(6207, 109, 1, 'قُلْ يٰٓاَيُّهَا الْكٰفِرُوْنَۙ', 'qul yā ayyuhal-kāfirụn'),
(6208, 109, 2, 'لَآ اَعْبُدُ مَا تَعْبُدُوْنَۙ', 'lā a\'budu mā ta\'budụn'),
(6209, 109, 3, 'وَلَآ اَنْتُمْ عٰبِدُوْنَ مَآ اَعْبُدُۚ', 'wa lā antum \'ābidụna mā a\'bud'),
(6210, 109, 4, 'وَلَآ اَنَا۠ عَابِدٌ مَّا عَبَدْتُّمْۙ', 'wa lā ana \'ābidum mā \'abattum'),
(6211, 109, 5, 'وَلَآ اَنْتُمْ عٰبِدُوْنَ مَآ اَعْبُدُۗ', 'wa lā antum \'ābidụna mā a\'bud'),
(6212, 109, 6, 'لَكُمْ دِيْنُكُمْ وَلِيَ دِيْنِ', 'lakum dīnukum wa liya dīn'),
(6213, 110, 1, 'اِذَا جَاۤءَ نَصْرُ اللّٰهِ وَالْفَتْحُۙ', 'iżā jā`a naṣrullāhi wal-fat-ḥ'),
(6214, 110, 2, 'وَرَاَيْتَ النَّاسَ يَدْخُلُوْنَ فِيْ دِيْنِ اللّٰهِ اَفْوَاجًاۙ', 'wa ra`aitan-nāsa yadkhulụna fī dīnillāhi afwājā'),
(6215, 110, 3, 'فَسَبِّحْ بِحَمْدِ رَبِّكَ وَاسْتَغْفِرْهُۗ اِنَّهٗ كَانَ تَوَّابًا', 'fa sabbiḥ biḥamdi rabbika wastagfir-h, innahụ kāna tawwābā'),
(6216, 111, 1, 'تَبَّتْ يَدَآ اَبِيْ لَهَبٍ وَّتَبَّۗ', 'tabbat yadā abī lahabiw wa tabb'),
(6217, 111, 2, 'مَآ اَغْنٰى عَنْهُ مَالُهٗ وَمَا كَسَبَۗ', 'mā agnā \'an-hu māluhụ wa mā kasab'),
(6218, 111, 3, 'سَيَصْلٰى نَارًا ذَاتَ لَهَبٍۙ', 'sayaṣlā nāran żāta lahab'),
(6219, 111, 4, 'وَّامْرَاَتُهٗ ۗحَمَّالَةَ الْحَطَبِۚ', 'wamra`atuh, ḥammālatal-ḥaṭab'),
(6220, 111, 5, 'فِيْ جِيْدِهَا حَبْلٌ مِّنْ مَّسَدٍ', 'fī jīdihā ḥablum mim masad'),
(6221, 112, 1, 'قُلْ هُوَ اللّٰهُ اَحَدٌۚ', 'qul huwallāhu aḥad'),
(6222, 112, 2, 'اَللّٰهُ الصَّمَدُۚ', 'allāhuṣ-ṣamad'),
(6223, 112, 3, 'لَمْ يَلِدْ وَلَمْ يُوْلَدْۙ', 'lam yalid wa lam yụlad'),
(6224, 112, 4, 'وَلَمْ يَكُنْ لَّهٗ كُفُوًا اَحَدٌ', 'wa lam yakul lahụ kufuwan aḥad'),
(6225, 113, 1, 'قُلْ اَعُوْذُ بِرَبِّ الْفَلَقِۙ', 'qul a\'ụżu birabbil-falaq'),
(6226, 113, 2, 'مِنْ شَرِّ مَا خَلَقَۙ', 'min syarri mā khalaq'),
(6227, 113, 3, 'وَمِنْ شَرِّ غَاسِقٍ اِذَا وَقَبَۙ', 'wa min syarri gāsiqin iżā waqab'),
(6228, 113, 4, 'وَمِنْ شَرِّ النَّفّٰثٰتِ فِى الْعُقَدِۙ', 'wa min syarrin-naffāṡāti fil-\'uqad'),
(6229, 113, 5, 'وَمِنْ شَرِّ حَاسِدٍ اِذَا حَسَدَ', 'wa min syarri ḥāsidin iżā ḥasad'),
(6230, 114, 1, 'قُلْ اَعُوْذُ بِرَبِّ النَّاسِۙ', 'qul a\'ụżu birabbin-nās'),
(6231, 114, 2, 'مَلِكِ النَّاسِۙ', 'malikin-nās'),
(6232, 114, 3, 'اِلٰهِ النَّاسِۙ', 'ilāhin-nās'),
(6233, 114, 4, 'مِنْ شَرِّ الْوَسْوَاسِ ەۙ الْخَنَّاسِۖ', 'min syarril-waswāsil-khannās'),
(6234, 114, 5, 'الَّذِيْ يُوَسْوِسُ فِيْ صُدُوْرِ النَّاسِۙ', 'allażī yuwaswisu fī ṣudụrin-nās'),
(6235, 114, 6, 'مِنَ الْجِنَّةِ وَالنَّاسِ', 'minal-jinnati wan-nās');
